$B[37883] = array( 'title' => 'The Rites of Hajj and `Umrah: A Pictorial Guide', 'excerpt' => 'Hajj means going to Mecca to perform tawaf (circumambulating the Ka&#039;bah) and the rest of the rites during the months of hajj in response to the command of Allah and seeking His pleasure.', 'url' => $url.'/english/the-rites-of-hajj-and-umrah-a-pictorial-guide/', 'image' => $url.'/wp-content/uploads/2018/06/The-Rites-of-Hajj-and-Umrah-Booklet-240x312.jpg', 'author_id' => '7026', 'author_name' => 'Dar al-Ifta al Misriyyah ', 'book_url' => $url.'/books/2018/06/en_The_Rites_of_Hajj_and_Umrah_a_Pictorial_Guide.pdf', 'publisher' => 'Dar al-Iftaa al-Misriyyah' );
