$t[5] = '&quot;وقالوا&quot; للنبي &quot;قلوبنا في أكنة&quot; أغطية &quot;مما تدعونا إليه وفي آذاننا وقر&quot; ثقل &quot;ومن بيننا وبينك حجاب&quot; خلاف في الدين &quot;فاعمل&quot; على دينك &quot;إننا عاملون&quot; على ديننا';
$t[6] = '&quot;قل إنما أنا بشر مثلكم يوحى إلي إنما إلهكم إله واحد فاستقيموا إليه&quot; بالإيمان والطاعة &quot;واستغفروه وويل&quot; كلمة عذاب';
$t[7] = '&quot;الذين لا يؤتون الزكاة وهم بالآخرة هم&quot; تأكيد';
$t[8] = '&quot;إن الذين آمنوا وعملوا الصالحات لهم أجر غير ممنون&quot; مقطوع';
$t[9] = '&quot;قل أإنكم&quot; بتحقيق الهمزة الثانية وتسهيلها وإدخال ألف بينها بوجهيها وبين الأولى &quot;لتكفرون بالذي خلق الأرض في يومين&quot; الأحد والاثنين &quot;وتجعلون له أندادا&quot; شركاء &quot;ذلك رب&quot; أي مالك &quot;العالمين&quot; جمع عالم, وهو ما سوى الله وجمع لاختلاف أنواعه بالياء والنون, تغليبا للعقلاء';
$t[10] = '&quot;وجعل&quot; مستأنف ولا يجوز عطفه على صلة الذي للفاصل الأجنبي &quot;فيها رواسي&quot; جبالا ثوابت &quot;من فوقها وبارك فيها&quot; بكثرة المياه والزروع والضروع &quot;وقدر&quot; قسم &quot;فيها أقواتها&quot; للناس والبهائم &quot;في&quot; تمام &quot;أربعة أيام&quot; أي الجعل وما ذكر معه في يوم الثلاثاء والأربعاء &quot;سواء&quot; منصوب على المصدر, أي استوت الأربعة استواء لا تزيد ولا تنقص &quot;للسائلين&quot; عن خلق الأرض بما فيها';
$t[11] = '&quot;ثم استوى&quot; قصد &quot;إلى السماء وهي دخان&quot; بخار مرتفع &quot;فقال لها وللأرض ائتيا&quot; إلى مرادي منكما &quot;طوعا أو كرها&quot; في موضع الحال, أي طائعتين أو مكرهتين &quot;قالتا أتينا&quot; بمن فينا &quot;طائعين&quot; فيه تغليب المذكر العاقل أو نزلتا لخطابهما منزلته';
$t[12] = '&quot;فقضاهن&quot; الضمير يرجع إلى السماء لأنها في معنى الجمع الآيلة إليه , أي صيرها &quot;سبع سماوات في يومين&quot; الخميس والجمعة فرغ منها في آخر ساعة منه, وفيها خلق آدم ولذلك لم يقل هنا سواء, ووافق ما هنا آيات خلق السماوات والأرض في ستة أيام &quot;وأوحى في كل سماء أمرها&quot; الذي أمر به من فيها من الطاعة والعبادة &quot;وزينا السماء الدنيا بمصابيح&quot; بنجوم &quot;وحفظا&quot; منصوب بفعله المقدر, أي حفظناها من استراق الشياطين السمع بالشهب &quot;ذلك تقدير العزيز&quot; في ملكه &quot;العليم&quot; بخلقه';
$t[13] = '&quot;فإن أعرضوا&quot; أي كفار مكة عن الإيمان بعد هذا البيان &quot;فقل أنذرتكم&quot; خوفتكم &quot;صاعقة مثل صاعقة عاد وثمود&quot; عذابا يهلككم مثل الذي أهلكهم';
$t[14] = '&quot;إذ جاءتهم الرسل من بين أيديهم ومن خلفهم&quot; أي مقبلين عليهم ومدبرين عنهم فكفروا كما سيأتي, والإهلاك في زمنه فقط &quot;أن&quot; أي بأن &quot;لا تعبدوا إلا الله قالوا لو شاء ربنا لأنزل&quot; علينا &quot;ملائكة فإنا بما أرسلتم به&quot; على زعمكم';
$t[15] = '&quot;فأما عاد فاستكبروا في الأرض بغير الحق وقالوا&quot; لما خوفوا بالعذاب &quot;من أشد منا قوة&quot; أي لا أحد, كان واحدهم يقلع الصخرة العظيمة من الجبل يجعلها حيث يشاء &quot;أولم يروا&quot; يعلموا &quot;أن الله الذي خلقهم هو أشد منهم قوة وكانوا بآياتنا&quot; المعجزات';
$t[16] = '&quot;فأرسلنا عليهم ريحا صرصرا&quot; باردة شديدة الصوت بلا مطر &quot;في أيام نحسات&quot; بكسر الحاء وسكونها مشؤومات عليهم &quot;لنذيقهم عذاب الخزي&quot; الذل &quot;في الحياة الدنيا ولعذاب الآخرة أخزى&quot; أشد &quot;وهم لا ينصرون&quot; بمنعه عنهم';
$t[17] = '&quot;وأما ثمود فهديناهم&quot; بينا لهم طريق الهدى &quot;فاستحبوا العمى&quot; اختاروا الكفر &quot;على الهدى فأخذتهم صاعقة العذاب الهون&quot; المهين';
$t[18] = '&quot;ونجينا&quot; منها &quot;الذين آمنوا وكانوا يتقون&quot; الله';
$t[19] = '&quot;و&quot; اذكر &quot;يوم يحشر&quot; بالياء والنون المفتوحة وضم الشين وفتح الهمزة &quot;أعداء الله إلى النار فهم يوزعون&quot; يساقون';
$t[20] = '&quot;حتى إذا ما&quot; زائدة';
$t[21] = '&quot;وقالوا لجلودهم لم شهدتم علينا قالوا أنطقنا الله الذي أنطق كل شيء&quot; أي أراد نطقه &quot;وهو خلقكم أول مرة وإليه ترجعون&quot; قيل: هو من كلام الجلود, وقيل: هو من كلام الله تعالى كالذي بعده وموقعه قريب مما قبله بأن القادر على إنشائكم ابتداء وإعادتكم بعد الموت أحياء قادر على إنطاق جلودكم وأعضائكم';
$t[22] = '&quot;وما كنتم تستترون&quot; عن ارتكابكم الفواحش من &quot;أن يشهد عليكم سمعكم ولا أبصاركم ولا جلودكم&quot; لأنكم لم توقنوا بالبعث &quot;ولكن ظننتم&quot; عند استتاركم';
$t[23] = '&quot;وذلكم&quot; مبتدأ &quot;ظنكم&quot; بدل منه &quot;الذي ظننتم بربكم&quot; نعت والخبر &quot;أرداكم&quot; أي أهلككم';
$t[24] = '&quot;فإن يصبروا&quot; على العذاب &quot;فالنار مثوى&quot; مأوى &quot;لهم وإن يستعتبوا&quot; يطلبوا العتبى, أي الرضا &quot;فما هم من المعتبين&quot; المرضيين';
$t[25] = '&quot;وقيضنا&quot; سببنا &quot;لهم قرناء&quot; من الشياطين &quot;فزينوا لهم ما بين أيديهم&quot; من أمر الدنيا واتباع الشهوات &quot;وما خلفهم&quot; من أمر الآخرة بقولهم لا بعث ولا حساب &quot;وحق عليهم القول&quot; بالعذاب وهو &quot;لأملأن جهنم&quot; الآية &quot;في&quot; جملة &quot;أمم قد خلت&quot; هلكت';
$t[26] = '&quot;وقال الذين كفروا&quot; عند قراءة النبي صلى الله عليه وسلم &quot;لا تسمعوا لهذا القرآن والغوا فيه&quot; ائتوا باللغط ونحوه وصيحوا في زمن قراءته &quot;لعلكم تغلبون&quot; فيسكت عن القراءة';
$t[27] = '&quot;فلنذيقن الذين كفروا عذابا شديدا ولنجزينهم أسوأ الذي كانوا يعملون&quot; أي أقبح جزاء عملهم';
$t[28] = '&quot;ذلك&quot; العذاب الشديد وأسوأ الجزاء &quot;جزاء أعداء الله&quot; بتحقيق الهمزة الثانية وإبدالها واوا &quot;النار&quot; عطف بيان للجزاء المخبر به عن ذلك &quot;لهم فيها دار الخلد&quot; أي إقامة لا انتقال منها &quot;جزاء&quot; منصوب على المصدر بفعله المقدر &quot;بما كانوا بآياتنا&quot; القرآن';
$t[29] = '&quot;وقال الذين كفروا&quot; في النار &quot;ربنا أرنا الذين أضلانا من الجن والإنس&quot; أي إبليس وقابيل سنا الكفر والقتل &quot;نجعلهما تحت أقدامنا&quot; في النار &quot;ليكونا من الأسفلين&quot; أي أشد عذابا منا';
$t[30] = '&quot;إن الذين قالوا ربنا الله ثم استقاموا&quot; على التوحيد وغيره مما وجب عليهم &quot;تتنزل عليهم الملائكة&quot; عند الموت &quot;أن&quot; بأن &quot;لا تخافوا&quot; من الموت وما بعده &quot;ولا تحزنوا&quot; على ما خلفتم من أهل وولد فنحن نخلفكم فيه';
$t[31] = '&quot;نحن أولياؤكم في الحياة الدنيا&quot; أي نحفظكم فيها &quot;وفي الآخرة&quot; أي نكون معكم فيها حتى تدخلوا الجنة &quot;ولكم فيها ما تشتهي أنفسكم ولكم فيها ما تدعون&quot; تطلبون';
$t[32] = '&quot;نزلا&quot; رزقا مهيئا منصوب بجعل مقدرا &quot;من غفور رحيم&quot; أي الله';
$t[33] = '&quot;ومن أحسن قولا&quot; أي لا أحد أحسن قولا &quot;ممن دعا إلى الله&quot; بالتوحيد';
$t[34] = '&quot;ولا تستوي الحسنة ولا السيئة&quot; في جزئياتهما لأن بعضهما فوق بعض &quot;ادفع&quot; السيئة &quot;بالتي&quot; أي بالخصلة التي &quot;هي أحسن&quot; كالغضب بالصبر والجهل بالحلم والإساءة بالعفو &quot;فإذا الذي بينك وبينه عداوة كأنه ولي حميم&quot; أي فيصير عدوك كالصديق القريب في محبته إذا فعلت ذلك فالذي مبتدأ وكأنه الخبر وإذا ظرف لمعنى التشبيه';
$t[35] = '&quot;وما يلقاها&quot; أي يؤتي الخصلة التي هي أحسن &quot; إلا الذين صبروا وما يلقها إلا ذو حظ&quot; ثواب';
$t[36] = '&quot;وإما&quot; فيه إدغام نون إن الشرطية في ما الزائدة &quot;ينزغنك من الشيطان نزغ&quot; أي يصرفك عن الخصلة وغيرها من الخير صارف &quot;فاستعذ بالله&quot; جواب الشرط وجواب الأمر محذوف, أي يدفعه عنك &quot;إنه هو السميع&quot; للقول &quot;العليم&quot; بالفعل';
$t[37] = '&quot;ومن آياته الليل والنهار والشمس والقمر لا تسجدوا للشمس ولا للقمر واسجدوا لله الذي خلقهن&quot; أي الآيات الأربع';
$t[38] = '&quot;فإن استكبروا&quot; عن السجود لله وحده &quot;فالذين عند ربك&quot; أي فالملائكة &quot;يسبحون&quot; يصلون &quot;له بالليل والنهار وهم لا يسأمون&quot; لا يملون';
$t[39] = '&quot;ومن آياته أنك ترى الأرض خاشعة&quot; يابسة لا نبات فيها &quot;فإذا أنزلنا عليها الماء اهتزت&quot; تحركت &quot;وربت&quot; انتفخت وعلت';
$t[40] = '&quot;إن الذين يلحدون&quot; من ألحد ولحد &quot;في آياتنا&quot; القرآن بالتكذيب &quot;لا يخفون علينا&quot; فنجازيهم &quot;أفمن يلقى في النار خير أم من يأتي آمنا يوم القيامة اعملوا ما شئتم إنه بما تعملون بصير&quot; تهديد لهم';
$t[41] = '&quot;إن الذين كفروا بالذكر&quot; القرآن &quot;لما جاءهم&quot; نجازيهم &quot;وإنه لكتاب عزيز&quot; منيع';
$t[42] = '&quot;لا يأتيه الباطل من بين يديه ولا من خلفه&quot; أي ليس قبله كتاب يكذبه ولا بعده &quot;تنزيل من حكيم حميد&quot; أي الله المحمود في أمره';
$t[43] = '&quot;ما يقال لك&quot; من التكذيب &quot;إلا&quot; مثل &quot;ما قد قيل للرسل من قبلك إن ربك لذو مغفرة&quot; للمؤمنين &quot;وذو عقاب أليم&quot; للكافرين';
$t[44] = '&quot;ولو جعلناه&quot; أي الذكر &quot;قرآنا أعجميا لقالوا لولا&quot; هلا &quot;فصلت&quot; بينت &quot;آياته&quot; حتى نفهمها &quot;أ&quot; قرآن &quot;أعجمي و&quot; نبي &quot;عربي&quot; استفهام إنكار منهم بتحقيق الهمزة الثانية وقلبها ألف بإشباع ودونه &quot;قل هو للذين آمنوا هدى&quot; من الضلالة &quot;وشفاء&quot; من الجهل &quot;والذين لا يؤمنون في آذانهم وقر&quot; ثقل فلا يسمعونه &quot;وهو عليهم عمى&quot; فلا يفهمونه &quot;أولئك ينادون من مكان بعيد&quot; أي هم كالمنادى من مكان بعيد لا يسمع ولا يفهم ما ينادى به';
$t[45] = '&quot;ولقد آتينا موسى الكتاب&quot; التوراة &quot;فاختلف فيه&quot; بالتصديق والتكذيب كالقرآن &quot;ولولا كلمة سبقت من ربك&quot; بتأخير الحساب والجزاء للخلائق إلى يوم القيامة &quot;لقضي بينهم&quot; في الدنيا فيما اختلفوا فيه &quot;وإنهم&quot; أي المكذبين به &quot;لفي شك منه مريب&quot; موقع في الريبة';
$t[46] = '&quot;من عمل صالحا فلنفسه&quot; عمل &quot;ومن أساء فعليها&quot; أي فضرر إساءته على نفسه &quot;وما ربك بظلام للعبيد&quot; أي بذي ظلم لقوله تعالى &quot;إن الله لا يظلم مثقال ذرة&quot;';
$t[47] = '&quot;إليه يرد علم الساعة&quot; متى تكون لا يعلمها غيره &quot;وما تخرج من ثمرات&quot; وفي قراءة ثمرات &quot;من أكمامها&quot; أوعيتها جمع كم بكسر الكاف إلا بعلمه &quot;وما تحمل من أنثى ولا تضع إلا بعلمه ويوم يناديهم أين شركاءي قالوا آذناك&quot; أعلمناك الآن &quot;ما منا من شهيد&quot; أي شاهد بأن لك شريكا';
$t[48] = '&quot;وضل&quot; غاب &quot;عنهم ما كانوا يدعون&quot; يعبدون &quot;من قبل&quot; في الدنيا من الأصنام &quot;وظنوا&quot; أيقنوا &quot;ما لهم من محيص&quot; مهرب من العذاب والنفي في الموضعين معلق عن العمل وجملة النفي سدت مسد المفعولين';
$t[49] = '&quot;لا يسأم الإنسان من دعاء الخير&quot; أي لا يزال يسأل ربه المال والصحة وغيرهما &quot;وإن مسه الشر&quot; الفقر والشدة &quot;فيئوس قنوط&quot; من رحمة الله, وهذا وما بعده في الكافرين';
$t[50] = '&quot;ولئن&quot; لام قسم &quot;أذقناه&quot; آتيناه &quot;رحمة&quot; غنى وصحة &quot;منا من بعد ضراء&quot; شدة وبلاء &quot;مسته ليقولن هذا لي&quot; أي بعملي &quot;وما أظن الساعة قائمة ولئن&quot; لام قسم &quot;رجعت إلى ربي إن لي عنده للحسنى&quot; أي الجنة &quot;فلننبئن الذين كفروا بما عملوا ولنذيقنهم من عذاب غليظ&quot; شديد, واللام في الفعل لام قسم';
$t[51] = '&quot;وإذا أنعمنا على الإنسان&quot; الجنس &quot;أعرض&quot; عن الشكر &quot;ونأى بجانبه&quot; ثنى عطفه متبخترا , وفي قراءة بتقديم الهمزة &quot;وإذا مسه الشر فذو دعاء عريض&quot; كثير';
$t[52] = '&quot;قل أرأيتم إن كان&quot; أي القرآن &quot;من عند الله&quot; كما قال النبي &quot;ثم كفرتم به من&quot; أي لا أحد &quot;أضل ممن هو في شقاق&quot; خلاف &quot;بعيد&quot; عن الحق أوقع هذا موقع منكم بيانا لحالهم';
$t[53] = '&quot;سنريهم آياتنا في الآفاق&quot; أقطار السماوات والأرض من النيرات والنبات والأشجار &quot;وفي أنفسهم&quot; من لطيف الصنعة وبديع الحكمة &quot;حتى يتبين لهم أنه&quot; أي القرآن &quot;الحق&quot; المنزل من الله بالبعث والحساب والعقاب, فيعاقبون على كفرهم به وبالجائي به &quot;أو لم يكف بربك&quot; فاعل يكف &quot;أنه على كل شيء شهيد&quot; بدل منه , أي أو لم يكفهم في صدقك أن ربك لا يغيب عنه شيء ما';
$t[54] = '&quot;ألا إنهم في مرية&quot; شك &quot;من لقاء ربهم&quot; لإنكارهم البعث &quot;ألا إنه&quot; تعالى &quot;بكل شيء محيط&quot; علما وقدرة فيجازيهم بكفرهم';
?>