$t[15] = 'Məgər Biz ilk yaradılışdan acizmi qaldıq?! Xeyr! (Eləcə də qiyamət g&uuml;n&uuml; &ouml;l&uuml;ləri diriltməkdən aciz qalmayacağıq!) Onlar yeni yaradılış (təkrar dirilmə) barəsində şəkk-ş&uuml;bhə i&ccedil;indədirlər!';
$t[16] = 'And olsun ki, insanı Biz yaratdıq və nəfsinin ona nə vəsvəsə etdiyini də Biz bilirik. Biz ona şah damarından da yaxınıq! (İnsanın adi hərəkətlərindən tutmuş &uuml;rəyindən ke&ccedil;ən ən gizli hisslər, duyğular belə Allaha mə&rsquo;lumdur!)';
$t[17] = 'Xatırla ki, (insanın) sağında və solunda (onun b&uuml;t&uuml;n əməllərini) təsbit edən (qeydə alan) iki mələk oturmuşdur!';
$t[18] = 'Dediyi hər s&ouml;z&uuml; (yazmaq &uuml;&ccedil;&uuml;n) onun yanında hazır durub g&ouml;zləyən (iki mələk) vardır!';
$t[19] = '&Ouml;l&uuml;m (&ouml;l&uuml;m bihuşluğu), həqiqətən, gələcəkdir. (Ey insan!) Bu sənin qorxub qa&ccedil;dığın (c&uuml;rbəc&uuml;r vasitələrlə canını qurtarmaq istədiyin) şeydir!';
$t[20] = '(&Ouml;ləndən sonra yenidən dirilmək &uuml;&ccedil;&uuml;n) sur &ccedil;alınacaqdır. Bu və&rsquo;d olunmuş g&uuml;nd&uuml;r (əzab g&uuml;n&uuml;d&uuml;r)!';
$t[21] = 'Hər bir kəs (Rəbbinin h&uuml;zuruna) bir s&uuml;r&uuml;yən (s&uuml;r&uuml;y&uuml;b məhşərə gətirən) və bir də şahidlə (iki mələklə) gələcəkdir.';
$t[22] = '(Ona belə deyiləcəkdir:) &ldquo;Sən (d&uuml;nyada) bundan (bu m&uuml;dhiş g&uuml;nə uğrayacağından) qafil idin. Artıq bu g&uuml;n g&ouml;z&uuml;ndən pərdəni g&ouml;t&uuml;rd&uuml;k. Sən bu g&uuml;n sərrast g&ouml;r&uuml;rsən!&rdquo;';
$t[23] = 'Yoldaşı (ona təhkim olunmuş mələk) deyəcəkdir: &ldquo;Bu yanımdakı (Cəhənnəm &uuml;&ccedil;&uuml;n) hazırdır!&rdquo;';
$t[24] = '(Allah o iki mələyə buyuracaqdır:) &ldquo;Atın Cəhənnəmə hər bir inadcıl kafiri;';
$t[25] = 'Xeyrə mane olanı, (z&uuml;lm etməklə, g&uuml;nah t&ouml;rətməklə) həddi aşanı, (Allahın birliyinə) şəkk edəni!';
$t[26] = 'O kimsəni ki, Allahla yanaşı &ouml;z&uuml;nə başqa bir tanrı qəbul etdi. Atın onu şiddətli əzabın i&ccedil;inə!&rdquo;';
$t[27] = 'Yoldaşı (Şeytan və ya &ouml;z nəfsi) deyəcəkdir: &ldquo;Ey Rəbbimiz! Onu mən azdırmadım. Fəqət o &ouml;z&uuml; haqq yoldan &ccedil;ox azmışdı!&rdquo;';
$t[28] = '(Allah) buyuracaqdır: &ldquo;Mənim h&uuml;zurumda m&uuml;bahisə etməyin. Mən sizə (d&uuml;nyada peyğəmbər vasitəsilə) bu əzabı bildirmişdim.';
$t[29] = 'Mənim h&uuml;zurumda s&ouml;z g&uuml;ləşdirməyin. Mən bəndələrə (əbəs yerə) z&uuml;lm edən deyiləm!&rdquo;';
$t[30] = 'O g&uuml;n Biz Cəhənnəmə: &ldquo;Doldunmu?&rdquo; &ndash; deyəcək, o isə: &ldquo;Yenə varmı?&rdquo; &ndash; deyə cavab verəcəkdir.';
$t[31] = 'Cənnət də m&uuml;ttəqilər &uuml;&ccedil;&uuml;n yaxın bir yerə gətiriləcəkdir.';
$t[32] = '(Onlara deyiləcəkdir:) &ldquo;Bu, və&rsquo;d olunduğunuz (Cənnətdir). O, hər bir (t&ouml;vbə edib Allaha tərəf) qayıdan, (nəfsini haramdan, &ouml;z&uuml;n&uuml; g&uuml;nahdan) qoruyan;';
$t[33] = 'Rəhmandan (Onu) g&ouml;rmədən qorxan və (Rəbbinin h&uuml;zuruna) haqqa d&ouml;nm&uuml;ş qəlblə gələn bəndə &uuml;&ccedil;&uuml;nd&uuml;r!';
$t[34] = 'Ora sağlıqla (əmin-amanlıqla) daxil olun. Bu, əbədiyyət g&uuml;n&uuml;d&uuml;r!&rdquo;';
$t[35] = 'Orada onlar &uuml;&ccedil;&uuml;n istədikləri hər şey vardır. Dərgahımızda isə ondan artığı (m&ouml;&rsquo;minlərin təsəvv&uuml;r edə bilmədikləri ne&rsquo;mətlər, Allahın rizasını və s.) m&ouml;vcuddur!';
$t[36] = 'Biz onlardan (Məkkə m&uuml;şriklərindən) əvvəl &ouml;zlərindən daha q&uuml;vvətli ne&ccedil;ə-ne&ccedil;ə nəsilləri məhv etdik. (Onlar:) &ldquo;G&ouml;rəsən, (&ouml;l&uuml;mdən qa&ccedil;ıb) can qurtarmağa bir yer varmı?&rdquo; (deyərək) &ouml;lkə-&ouml;lkə gəzib dolaşmışdılar.';
$t[37] = 'Ş&uuml;bhəsiz ki, bunda qəlb (ağıl) sahibi, yaxud (moizə edildiyi zaman b&uuml;t&uuml;n varlığı ilə) hazır olub (ona cani-dildən) qulaq asan kimsə &uuml;&ccedil;&uuml;n &ouml;y&uuml;d-nəsihət vardır!';
$t[38] = 'And olsun ki, Biz g&ouml;yləri, yeri və onların arasında olanları altı g&uuml;ndə yaratdıq və Bizə he&ccedil; bir yorğunluq da &uuml;z vermədi.';
$t[39] = '(Ya Peyğəmbər!) Artıq onların dediklərinə (batil s&ouml;zlərinə) səbr et. G&uuml;n doğmamışdan əvvəl, batmamışdan əvvəl Rəbbini təqdis edib şə&rsquo;ninə təriflər de!';
$t[40] = 'Gecənin bir vaxtında və səcdələrdən sonra da (Onu ş&uuml;kr-səna ilə təqdis et)!';
$t[41] = '(Ya Peyğəmbər!) Car&ccedil;ının yaxın bir yerdən car &ccedil;əkəcəyi g&uuml;n (deyilən s&ouml;z&uuml;, surun səsini) dinlə! (O g&uuml;n İsrafil uca səslə: &ldquo;Ey k&ouml;hnəlmiş s&uuml;m&uuml;klər, ey bir-birindən ayrılmış oynaqlar, ey əprimiş ətlər, ey dağılmış sa&ccedil;lar! Allah sizə haqq-hesab g&uuml;n&uuml;nə gəlmək &uuml;&ccedil;&uuml;n yenidən birləşməyi buyurur!&rdquo; &ndash; deyəcək və suru ikinci dəfə &uuml;f&uuml;rəcəkdir).';
$t[42] = 'O g&uuml;n (b&uuml;t&uuml;n yaranmışlar) dəhşətli (t&uuml;k&uuml;rpədici) səsi doğrudan-doğruya eşidəcəklər. O g&uuml;n (qəbirlərdən) &ccedil;ıxma g&uuml;n&uuml;d&uuml;r!';
$t[43] = 'Ş&uuml;bhəsiz ki, dirildən də, &ouml;ld&uuml;rən də Bizim. (Axır) d&ouml;n&uuml;ş də Bizim h&uuml;zurumuzadır!';
$t[44] = 'O g&uuml;n yer onlardan ayrılıb par&ccedil;alanacaq, onlar s&uuml;r&rsquo;ətlə (torpağın altından, qəbirlərindən &ccedil;ıxacaqlar). O bizim &uuml;&ccedil;&uuml;n asan olan həşrdir (B&uuml;t&uuml;n yaranmışların bir yerə toplanacaqları məhşər g&uuml;n&uuml;d&uuml;r).';
$t[45] = 'Biz onların nə dediklərini &ccedil;ox g&ouml;zəl bilirik. (Ya Peyğəmbər!) Sən onlara zor edən (onları zorla imana gətirən) deyilsən. Sən Mənim təhdidimdən (əzabımdan) qorxablara Qur&rsquo;anla &ouml;y&uuml;d-nəsihət ver!';
