$t[21] = 'Sizin &ouml;z&uuml;n&uuml;z də (sizi yaradanın birliyini s&uuml;but edən) əlamətlər vardır. Məgər g&ouml;rm&uuml;rs&uuml;n&uuml;z?';
$t[22] = 'G&ouml;ydə də ruzinin (yağış, qar) və və&rsquo;d olunduğunuz şey (m&uuml;kafat, cəza) vardır! (Sizə və&rsquo;d olunan m&uuml;kafat, yaxud cəza Allah dərgahındakı l&ouml;vhi-məhfuzda yazılmışdır).';
$t[23] = 'G&ouml;y&uuml;n və yerin Rəbbinə (sahibinə) and olsun ki, bu (deyilənlər) sizin danışığınız (dediyiniz s&ouml;z&uuml;n &ouml;z s&ouml;z&uuml;n&uuml;z olması) kimi həqiqətdir.';
$t[24] = '(Ya Rəsulum!) İbrahimin m&ouml;htərəm qonaqlarının (mələklərin) s&ouml;hbəti sənə gəlib &ccedil;atdımı?';
$t[25] = 'Onlar (İbrahimin) yanına gəlib salam verdikdə (İbrahim) salamı alıb (&ouml;z-&ouml;z&uuml;nə): &ldquo;Bunlar tanımadığım kimsələrdir!&rdquo; &ndash;dedi.';
$t[26] = 'O (bir bəhanə ilə) ailəsinin yanına getdi və bir buzov (kəsib qızardaraq) gətirdi.';
$t[27] = 'Onu qabaqlarına (qonaqların qabağına) qoyub: &ldquo;Bəlkə, yeyəsiniz!&rdquo; &ndash; dedi.';
$t[28] = '(İbrahim qonaqların yeməyə əl uzatmadıqlarını g&ouml;rd&uuml;kdə) onlardan qorxuya d&uuml;şd&uuml;. Onlar: &ldquo;Qorxma!&rdquo; &ndash; dedilər və ona &ccedil;ox bilikli bir oğlan uşağı (İshaqın d&uuml;nyaya gələcəyi) ilə m&uuml;jdə verdilər.';
$t[29] = '(Bunu eşidən) z&ouml;vcəsi (Sara) &ccedil;ağır-bağır salıb (təəcc&uuml;b&uuml;ndən əlini) &uuml;z&uuml;nə &ccedil;ırparaq dedi: &ldquo;Mən qoca, qısır bir qarıyam! (Mənim necə uşağım ola bilər?)&rdquo;';
$t[30] = 'Onlar: &ldquo;Sənin Rəbbin belə buyurmuşdur. Ş&uuml;bhəsiz ki, O, hikmət sahibidir, (hər şeyi) biləndir!&rdquo; &ndash; dedilər.';
$t[31] = '(Sonra İbrahim) dedi: &ldquo;Ey el&ccedil;ilər! Sizin işiniz nədir?&rdquo;';
$t[32] = 'Onlar dedilər: &ldquo;Biz g&uuml;nahkar bir q&ouml;vmə (Lut tayfasına cəza verməyə) g&ouml;ndərilmişik.';
$t[33] = 'Onların başına gildən daşlar yağdırmaq &uuml;&ccedil;&uuml;n (gəlmişik).';
$t[34] = '(Elə daşlar ki, onlara pozğunluqla məşğul olub) həddi aşanlar &uuml;&ccedil;&uuml;n sənin Rəbbinin dərgahında x&uuml;susi nişan qoyulmuşdur!&rdquo;';
$t[35] = 'Nəhayət, (Lut q&ouml;vm&uuml;n&uuml; məhv etmək &uuml;&ccedil;&uuml;n) orada (Sədumda) olan m&ouml;&rsquo;minləri &ccedil;ıxartdıq.';
$t[36] = 'Lakin orada bir evdən (Lutun ailəsindən) başqa m&uuml;səlman (t&ouml;vhid dinində olan) tapmadıq.';
$t[37] = 'Və orada (axirətdəki) şiddətli əzabdan qorxanlar &uuml;&ccedil;&uuml;n bir nişanə qoyduq. (Lut q&ouml;vm&uuml;n&uuml;n Şamla Hicaz arasında yerləşən yurdunu xaraba qoyub altını &uuml;st&uuml;nə &ccedil;evirdik. Bu, Məkkə m&uuml;şrikləri &uuml;&ccedil;&uuml;n də bir ibrət dərsi olmalıdır).';
$t[38] = 'Musanın hekayətində də (bir ibrət dərsi vardır). O zaman Biz onu Fir&rsquo;onun yanına a&ccedil;ıq-aşkar bir dəlillə g&ouml;ndərmişdik.';
$t[39] = 'O, b&uuml;t&uuml;n ə&rsquo;yanları (və qoşunu) ilə birlikdə (imandan) &uuml;z d&ouml;ndərib (Musa barəsində): &ldquo;Bu sehrbazdır, yaxud da dəlidir!&rdquo; &ndash; demişdi.';
$t[40] = 'Nəhayət, onu və əsgərlərini tutub dənizə atdıq. O, məzəmmətə layiq bir iş g&ouml;rm&uuml;şd&uuml; (allahlıq iddiasına d&uuml;şm&uuml;şd&uuml;).';
$t[41] = 'Adın (Ad q&ouml;vm&uuml;n&uuml;n) başına gələnlərdə də (bir ibrət dərsi vardır). O zaman Biz onların &uuml;st&uuml;nə hər şeyi qurudub məhv edən (xeyirsiz) bir k&uuml;lək g&ouml;ndərmişdik.';
$t[42] = 'O, qarşısına &ccedil;ıxan he&ccedil; bir şeyi buraxmır, onu sovurub k&uuml;lə (&ccedil;&ouml;r-&ccedil;&ouml;pə) d&ouml;ndərirdi.';
$t[43] = 'Səmudun (Səmud q&ouml;vm&uuml;n&uuml;n) başına gələnlərdə də (bir ibrət dərsi vardır). O zaman onlara: &ldquo;Hələ bir m&uuml;ddət (&uuml;&ccedil; g&uuml;n) kef &ccedil;əkib ləzzət alın!&rdquo; &ndash; deyilmişdi.';
$t[44] = 'Onlar &ouml;z Rəbbinin əmrinə (tabe olmağa) təkəbb&uuml;r g&ouml;stərmiş və onları g&ouml;zləri baxa-baxa ildırım vurmuşdu.';
$t[45] = 'Onların nə (yıxıldıqları yerdən) qalxmağa g&uuml;cləri &ccedil;atmış, nə də (bir kimsədən &ouml;zlərinə) k&ouml;mək istəyə bilmişdilər (yaxud nə də &ouml;zlərinə cəza verəndən intiqam ala bilmişdilər).';
$t[46] = 'Daha əvvəl Nuh q&ouml;vm&uuml;n&uuml; (məhv etmişdik). &Ccedil;&uuml;nki onlar (Allahın itaətindən &ccedil;ıxmış) fasiqlər idilər.';
$t[47] = 'Biz g&ouml;y&uuml; q&uuml;drətimizlə (q&uuml;drət əlimizlə) yaratdıq və Biz (istədiyimiz hər şeyi yaratmağa) qadirik. (Və ya: Biz g&ouml;y&uuml; q&uuml;drətimizlə yaratdıq və Biz onu genişləndirməkdəyik).';
$t[48] = 'Yeri də Biz d&ouml;şədik. (Onu) d&ouml;şəyənlər necə də g&ouml;zəldir! (Biz necə də g&ouml;zəl d&ouml;şəyənlərik!)';
$t[49] = 'Biz hər şeydən (erkək və dişi, bir-birinin əksi olmaqla) c&uuml;t-c&uuml;t yaratdıq. Bəlkə, bir d&uuml;ş&uuml;n&uuml;b ibrət alasınız!';
$t[50] = '(Ya Rəsulum! Bu insanlara de:) &ldquo;Allaha tərəf qa&ccedil;ın! (Allahın qəzəbindən qa&ccedil;ıb Onun mərhəmətinə sığının, k&uuml;fr&uuml; atıb imana gəlin!) Mən sizi Ondan (Allahın əzabından) a&ccedil;ıq-aşkar qorxudan bir peyğəmbərəm!';
$t[51] = 'Allahla yanaşı başqa bir tanrı qəbul etməyin. Mən sizi Ondan (Allahın əzabından) a&ccedil;ıq-aşkar qorxudan bir peyğəmbərəm!&rdquo;';
$t[52] = '(Ya Rəsulum! Sənin q&ouml;vm&uuml;n sənə şair, sehrbaz, divanə dediyi kimi) eləcə də onlardan əvvəlkilər &ouml;zlərinə bir peyğəmbər gələr-gəlməz ona sehrbaz, divanə dedilər.';
$t[53] = 'G&ouml;rəsən, onlar bunu (bu dediklərini) bir-birinə vəsiyyətmi etmişlər?! (Hamısı eyni şeyi deyir). Xeyr, onlar azğın bir q&ouml;vmd&uuml;rlər!';
$t[54] = '(Ya Rəsulum!) Artıq onlardan &uuml;z d&ouml;ndər! Sən buna g&ouml;rə qınanası deyilsən (&ccedil;&uuml;nki sənə həvalə olunmuş risaləti layiqincə təbliğ etdin).';
$t[55] = 'Sən (onlara Qur&rsquo;anla) &ouml;y&uuml;d-nəsihət ver. &Ccedil;&uuml;nki &ouml;y&uuml;d-nəsihət m&ouml;&rsquo;minlərə fayda verir.';
$t[56] = 'Mən cinləri və insanları yalnız Mənə ibadət etmək &uuml;&ccedil;&uuml;n yaratdım!';
$t[57] = 'Mən onlardan ruzi istəmirəm. Mən onlardan Məni (ehtiyacı olan bəndələrimi) yedirtmələrini də istəmirəm!';
$t[58] = 'Ş&uuml;bhəsiz ki, ruzi verən də, q&uuml;vvət sahibi də, yenilməz olan da Allahdır!';
$t[59] = 'Həqiqət, z&uuml;lm edənlərin (&ouml;zlərindən əvvəlki) yoldaşlarının qisməti kimi bir qisməti vardır! (Allaha şərik qoşmaqla &ouml;zlərinə z&uuml;lm edən Məkkə m&uuml;şriklərini də əvvəlki &uuml;mmətlər kimi şiddətli bir əzab g&ouml;zləyir!) Qoy onlar Məni (&ouml;zlərinə əzab verməyə) tələsdirməsinlər!';
$t[60] = 'Və&rsquo;d olunduqları g&uuml;ndən (qiyamət g&uuml;n&uuml;ndən) dolayı vay kafirlərin halına!';
