$t[59] = 'neochvějn&iacute; byli a na P&aacute;na sv&eacute;ho spol&eacute;hali!';
$t[60] = 'Kolik je zv&iacute;řat, jež neopatřuj&iacute; si obživu svou, ale Bůh jim ji u&scaron;tědřuje stejně jako v&aacute;m - a On sly&scaron;&iacute;c&iacute; je i v&scaron;evědouc&iacute;.';
$t[61] = 'Když se jich zept&aacute;&scaron;: &quot;Kdo stvořil nebesa a zemi a podmanil si slunce a měs&iacute;c?&quot;, odpov&iacute;daj&iacute;: &quot;Bůh!&quot; Jak se tedy mohou tak odvracet?';
$t[62] = 'Bůh &scaron;tědře d&aacute;v&aacute; obživu, komu chce ze služebn&iacute;ků Sv&yacute;ch, a odměřuje ji tak&eacute;, komu chce a Bůh věru je v&scaron;evědouc&iacute; o každ&eacute; věci.';
$t[63] = 'A když se zept&aacute;&scaron;: &quot;Kdo ses&iacute;l&aacute; z nebe vodu, jež oživuje zemi po smrti jej&iacute;?&quot;, tehdy odpov&iacute;daj&iacute;: &quot;Bůh.&quot; Rci: &quot;Chv&aacute;la Bohu!&quot; Ale vět&scaron;ina z nich nem&aacute; rozum.';
$t[64] = 'Tento život pozemsk&yacute; nen&iacute; leč z&aacute;bava a hra, zat&iacute;mco pobyt v životě budouc&iacute;m je t&iacute;m životem prav&yacute;m - k&eacute;ž by to jen věděli!';
$t[65] = 'A když pluj&iacute; na lodi, vz&yacute;vaj&iacute; Boha a upř&iacute;mně mu zasvěcuj&iacute; svou v&iacute;ru, když v&scaron;ak je potom na sou&scaron;i zachr&aacute;n&iacute;, hle, modloslužebn&iacute;ky se st&aacute;vaj&iacute;,';
$t[66] = 'aby uk&aacute;zali svůj nevděk za to, co jsme jim dali, a aby si jen už&iacute;vali - v&scaron;ak z&aacute;hy se dozv&iacute;!';
