$t[1] = 'فرمان خدا (برای مجازات مشرکان و مجرمان،) فرا رسیده است؛ برای آن عجله نکنید! منزّه و برتر است خداوند از آنچه همتای او قرارمی&zwnj;دهند!';
$t[2] = 'فرشتگان را با روح (الهی) بفرمانش بر هر کس از بندگانش بخواهد نازل می&zwnj;کند؛ (و به آنها دستور می&zwnj;دهد) که مردم را انذار کنید؛ (و بگویید:) معبودی جز من نیست؛ از (مخالفت دستور) من، بپرهیزید!';
$t[3] = 'آسمانها و زمین را بحق آفرید؛ او برتر است از اینکه همتایی برای او قرار می&zwnj;دهند!';
$t[4] = 'انسان را از نطفه بی&zwnj;ارزشی آفرید؛ و سرانجام (او موجودی فصیح، و) مدافع آشکار از خویشتن گردید!';
$t[5] = 'و چهارپایان را آفرید؛ در حالی که در آنها، برای شما وسیله پوشش، و منافع دیگری است؛ و از گوشت آنها می&zwnj;خورید!';
$t[6] = 'و در آنها برای شما زینت و شکوه است به هنگامی که آنها را به استراحتگاهشان بازمی&zwnj;گردانید، و هنگامی که (صبحگاهان) به صحرا می&zwnj;فرستید!';
$t[7] = 'آنها بارهای سنگین شما را به شهری حمل می&zwnj;کنند که جز با مشقّت زیاد، به آن نمی&zwnj;رسیدید؛ پروردگارتان رؤوف و رحیم است (که این وسایل حیات را در اختیارتان قرار داده)!';
$t[8] = 'همچنین اسبها و استرها و الاغها را آفرید؛ تا بر آنها سوار شوید و زینت شما باشد، و چیزهایی می&zwnj;آفریند که نمی&zwnj;دانید.';
$t[9] = 'و بر خداست که راه راست را (به بندگان) نشان دهد؛ امّا بعضی از راه&zwnj;ها بیراهه است! و اگر خدا بخواهد، همه شما را (به اجبار) هدایت می&zwnj;کند (؛ ولی اجبار سودی ندارد).';
$t[10] = 'او کسی است که از آسمان، آبی فرستاد، که نوشیدن شما از آن است؛ و (همچنین) گیاهان و درختانی که حیوانات خود را در آن به چرا می&zwnj;برید، نیز از آن است.';
$t[11] = 'خداوند با آن (آب باران)، برای شما زراعت و زیتون و نخل و انگور، و از همه میوه&zwnj;ها می&zwnj;رویاند؛ مسلماً در این، نشانه روشنی برای اندیشمندان است.';
$t[12] = 'او شب و روز و خورشید و ماه را مسخّر شما ساخت؛ و ستارگان نیز به فرمان او مسخّر شمایند؛ در این، نشانه&zwnj;هایی است (از عظمت خدا،) برای گروهی که عقل خود را به کار می&zwnj;گیرند!';
$t[13] = '(علاوه بر این،) مخلوقاتی را که در زمین به رنگهای گوناگون آفریده نیز مسخّر (فرمان شما) ساخت؛ در این، نشانه روشنی است برای گروهی که متذکّر می&zwnj;شوند!';
$t[14] = 'او کسی است که دریا را مسخّر (شما) ساخت تا از آن، گوشت تازه بخورید؛ و زیوری برای پوشیدن (مانند مروارید) از آن استخراج کنید؛ و کشتیها را می&zwnj;بینی که سینه دریا را می&zwnj;شکافند تا شما (به تجارت پردازید و) از فضل خدا بهره گیرید؛ شاید شکر نعمتهای او را بجا آورید!';
$t[15] = 'و در زمین، کوه&zwnj;های ثابت و محکمی افکند تا لرزش آن را نسبت به شما بگیرد؛ و نهرها و راه&zwnj;هایی ایجاد کرد، تا هدایت شوید.';
$t[16] = 'و (نیز) علاماتی قرار داد؛ و (شب هنگام) به وسیله ستارگان هدایت می&zwnj;شوند.';
$t[17] = 'آیا کسی که (این گونه مخلوقات را) می&zwnj;آفریند، همچون کسی است که نمی&zwnj;آفریند؟! آیا متذکّر نمی&zwnj;شوید؟!';
$t[18] = 'و اگر نعمتهای خدا را بشمارید، هرگز نمی&zwnj;توانید آنها را احصا کنید؛ خداوند بخشنده و مهربان است!';
$t[19] = 'خداوند آنچه را پنهان می&zwnj;دارید و آنچه را آشکار می&zwnj;سازید، می&zwnj;داند.';
$t[20] = 'معبودهایی را که غیر از خدا می&zwnj;خوانند، چیزی را خلق نمی&zwnj;کنند؛ بلکه خودشان هم مخلوقند!';
$t[21] = 'آنها مردگانی هستند که هرگز استعداد حیات ندارند؛ و نمی&zwnj;دانند (عبادت&zwnj;کنندگانشان) در چه زمانی محشور می&zwnj;شوند!';
$t[22] = 'معبود شما خداوند یگانه است؛ امّا کسانی که به آخرت ایمان نمی&zwnj;آورند، دلهایشان (حق را) انکار می&zwnj;کند و مستکبرند.';
$t[23] = 'قطعاً خداوند از آنچه پنهان می&zwnj;دارند و آنچه آشکار می&zwnj;سازند با خبر است؛ او مستکبران را دوست نمی&zwnj;دارد!';
$t[24] = 'و هنگامی که به آنها گفته شود: &laquo;پروردگار شما چه نازل کرده است؟&raquo; می&zwnj;گویند: &laquo;اینها (وحی الهی نیست؛) همان افسانه&zwnj;های دروغین پیشینیان است!&raquo;';
$t[25] = 'آنها باید روز قیامت، (هم) بار گناهان خود را بطور کامل بر دوش کشند؛ و هم سهمی از گناهان کسانی که بخاطر جهل، گمراهشان می&zwnj;سازند! بدانید آنها بار سنگین بدی بر دوش می&zwnj;کشند!';
$t[26] = 'کسانی که قبل از ایشان بودند (نیز) از این توطئه&zwnj;ها داشتند؛ ولی خداوند به سراغ شالوده (زندگی) آنها رفت؛ و آن را از اساس ویران کرد؛ و سقف از بالا بر سرشان فرو ریخت؛ و عذاب (الهی) از آن جایی که نمی&zwnj;دانستند به سراغشان آمد!';
$t[27] = 'سپس روز قیامت خدا آنها را رسوا می&zwnj;سازد؛ و می&zwnj;گوید: &laquo;شریکانی که شما برای من ساختید، و بخاطر آنها با دیگران دشمنی می&zwnj;کردید، کجا هستید؟!&raquo; (در این هنگام،) کسانی که به آنها علم داده شده می&zwnj;گویند: &laquo;رسوایی و بدبختی، امروز بر کافران است!&raquo;';
$t[28] = 'همانها که فرشتگان (مرگ) روحشان را می&zwnj;گیرند در حالی که به خود ظلم کرده بودند! در این موقع آنها تسلیم می&zwnj;شوند (و بدروغ می&zwnj;گویند:) ما کار بدی انجام نمی&zwnj;دادیم! آری، خداوند به آنچه انجام می&zwnj;دادید عالم است!';
$t[29] = '(به آنها گفته می&zwnj;شود:) اکنون از درهای جهنم وارد شوید در حالی که جاودانه در آن خواهید بود! چه جای بدی است جایگاه مستکبران!';
$t[30] = '(ولی هنگامی که) به پرهیزگاران گفته می&zwnj;شد: &laquo;پروردگار شما چه چیز نازل کرده است؟&raquo; می&zwnj;گفتند: &laquo;خیر (و سعادت)&raquo; (آری،) برای کسانی که نیکی کردند، در این دنیا نیکی است؛ و سرای آخرت از آن هم بهتر است؛ و چه خوب است سرای پرهیزگاران!';
$t[31] = 'باغهایی از بهشت جاویدان است که همگی وارد آن می&zwnj;شوند؛ نهرها از زیر درختانش می&zwnj;گذرد؛ هر چه بخواهند در آنجا هست؛ خداوند پرهیزگاران را چنین پاداش می&zwnj;دهد!';
$t[32] = 'همانها که فرشتگان (مرگ) روحشان را می&zwnj;گیرند در حالی که پاک و پاکیزه&zwnj;اند؛ به آنها می&zwnj;گویند: &laquo;سلام بر شما! وارد بهشت شوید به خاطر اعمالی که انجام می&zwnj;دادید!&raquo;';
$t[33] = 'آیا آنها انتظاری جز این دارند که فرشتگان (قبض ارواح) به سراغشان بیایند، یا فرمان پروردگارت (برای مجازاتشان) فرا رسد (آنگاه توبه کنند؟! ولی توبه آنها در آن زمان بی اثر است! آری،) کسانی که پیش از ایشان بودند نیز چنین کردند! خداوند به آنها ستم نکرد؛ ولی آنان به خویشتن ستم می&zwnj;نمودند!';
$t[34] = 'و سرانجام بدیهای اعمالشان به آنها رسید؛ و آنچه را (از وعده&zwnj;های عذاب) استهزا می&zwnj;کردند، بر آنان وارد شد.';
$t[35] = 'مشرکان گفتند: &laquo;اگر خدا می&zwnj;خواست، نه ما و نه پدران ما، غیر او را پرستش نمی&zwnj;کردیم؛ و چیزی را بدون اجازه او حرام نمی&zwnj;ساختیم!&raquo; (آری،) کسانی که پیش از ایشان بودند نیز همین کارها را انجام دادند؛ ولی آیا پیامبران وظیفه&zwnj;ای جز ابلاغ آشکار دارند؟!';
$t[36] = 'ما در هر امتی رسولی برانگیختیم که: &laquo;خدای یکتا را بپرستید؛ و از طاغوت اجتناب کنید!&raquo; خداوند گروهی را هدایت کرد؛ و گروهی ضلالت و گمراهی دامانشان را گرفت؛ پس در روی زمین بگردید و ببینید عاقبت تکذیب&zwnj;کنندگان چگونه بود!';
$t[37] = 'هر قدر بر هدایت آنها حریص باشی، (سودی ندارد؛ چرا) که خداوند کسی را که گمراه ساخت، هدایت نمی&zwnj;کند؛ و آنها یاورانی نخواهند داشت!';
$t[38] = 'آنها سوگندهای شدید به خدا یاد کردند که: &laquo;هرگز خداوند کسی را که می&zwnj;میرد، برنمی&zwnj;انگیزد!&raquo; آری، این وعده قطعی خداست (که همه مردگان را برای جزا بازمی&zwnj;گرداند)؛ ولی بیشتر مردم نمی&zwnj;دانند!';
$t[39] = 'هدف این است که آنچه را در آن اختلاف داشتند، برای آنها روشن سازد؛ و کسانی که منکر شدند، بدانند دروغ می&zwnj;گفتند!';
$t[40] = '(رستاخیز مردگان برای ما مشکل نیست؛ زیرا) وقتی چیزی را اراده می&zwnj;کنیم، فقط به آن می&zwnj;گوییم: &laquo;موجود باش!&raquo; بلافاصله موجود می&zwnj;شود.';
$t[41] = 'آنها که پس از ستم دیدن در راه خدا، هجرت کردند، در این دنیا جایگاه (و مقام) خوبی به آنها می&zwnj;دهیم؛ و پاداش آخرت، از آن هم بزرگتر است اگر می&zwnj;دانستند!';
$t[42] = 'آنها کسانی هستند که صبر و استقامت پیشه کردند، و تنها بر پروردگارشان توکّل می&zwnj;کنند.';
$t[43] = 'و پیش از تو، جز مردانی که به آنها وحی می&zwnj;کردیم، نفرستادیم! اگر نمی&zwnj;دانید، از آگاهان بپرسید (تا تعجب نکنید از اینکه پیامبر اسلام از میان همین مردان برانگیخته شده است)!';
$t[44] = '(از آنها بپرسید که) از دلایل روشن و کتب (پیامبران پیشین آگاهند!) و ما این ذکر [= قرآن&zwnj;] را بر تو نازل کردیم، تا آنچه به سوی مردم نازل شده است برای آنها روشن سازی؛ و شاید اندیشه کنند!';
$t[45] = 'آیا توطئه&zwnj;گران از این ایمن گشتند که ممکن است خدا آنها را در زمین فروبرد، و یا مجازات (الهی)، از آن جا که انتظارش را ندارند، به سراغشان آید؟!...';
$t[46] = 'یا به هنگامی (که برای کسب مال و ثروت افزونتر) در رفت و آمدند، دامانشان را بگیرد در حالی که قادر به فرار نیستند؟!...';
$t[47] = 'یا بطور تدریجی، با هشدارهای خوف&zwnj;انگیز آنان را گرفتار سازد؟! چرا که پروردگار شما، رؤوف و رحیم است.';
$t[48] = 'آیا آنها مخلوقات خدا را ندیدند که سایه&zwnj;هایشان از راست و چپ حرکت دارند، و با خضوع برای خدا سجده می&zwnj;کنند؟!';
$t[49] = '(نه تنها سایه&zwnj;ها، بلکه) تمام آنچه در آسمانها و زمین از جنبندگان وجود دارد، و همچنین فرشتگان، برای خدا سجده می&zwnj;کنند و تکبّر نمی&zwnj;ورزند.';
$t[50] = 'آنها (تنها) از (مخالفت) پروردگارشان، که حاکم بر آنهاست، می&zwnj;ترسند؛ و آنچه را مأموریت دارند انجام می&zwnj;دهند.';
$t[51] = 'خداوند فرمان داده: &laquo;دو معبود (برای خود) انتخاب نکنید؛ معبود (شما) همان خدای یگانه است؛ تنها از (کیفر) من بترسید!&raquo;';
$t[52] = 'آنچه در آسمانها و زمین است، از آن اوست؛ و دین خالص (نیز) همواره از آن او می&zwnj;باشد؛ آیا از غیر او می&zwnj;ترسید؟!';
$t[53] = 'آنچه از نعمتها دارید، همه از سوی خداست! و هنگامی که ناراحتی به شما رسد، فقط او را می&zwnj;خوانید!';
$t[54] = '(امّا) هنگامی که ناراحتی و رنج را از شما برطرف می&zwnj;سازد، ناگاه گروهی از شما برای پروردگارشان همتا قائل می&zwnj;شوند.';
$t[55] = '(بگذار) تا نعمتهایی را که به آنها داده&zwnj;ایم کفران کنند! (اکنون) چند روزی (از متاع دنیا) بهره گیرید، امّا بزودی خواهید دانست (سرانجام کارتان به کجا خواهد کشید)!';
$t[56] = 'آنان برای بتهایی که هیچ گونه سود و زیانی از آنها سراغ ندارند، سهمی از آنچه به آنان روزی داده&zwnj;ایم قرارمی&zwnj;دهند؛ به خدا سوگند، (در دادگاه قیامت،) از این افتراها که می&zwnj;بندید، بازپرسی خواهید شد!';
$t[57] = 'آنها (در پندار خود،) برای خداوند دختران قرارمی&zwnj;دهند؛ -منزّه است (از اینکه فرزندی داشته باشد)- ولی برای خودشان، آنچه را میل دارند قائل می&zwnj;شوند...';
$t[58] = 'در حالی که هرگاه به یکی از آنها بشارت دهند دختر نصیب تو شده، صورتش (از فرط ناراحتی) سیاه می&zwnj;شود؛ و به شدّت خشمگین می&zwnj;گردد؛...';
$t[59] = 'بخاطر بشارت بدی که به او داده شده، از قوم و قبیله خود متواری می&zwnj;گردد؛ (و نمی&zwnj;داند) آیا او را با قبول ننگ نگهدارد، یا در خاک پنهانش کند؟! چه بد حکم می&zwnj;کنند!';
$t[60] = 'برای آنها که به سرای آخرت ایمان ندارند، صفات زشت است؛ و برای خدا، صفات عالی است؛ و او قدرتمند و حکیم است.';
$t[61] = 'و اگر خداوند مردم را بخاطر ظلمشان مجازات می&zwnj;کرد، جنبنده&zwnj;ای را بر پشت زمین باقی نمی&zwnj;گذارد؛ ولی آنها را تا زمان معیّنی به تأخیر می&zwnj;اندازد. و هنگامی که اجلشان فرا رسد، نه ساعتی تأخیر می&zwnj;کنند، و نه ساعتی پیشی می&zwnj;گیرند.';
$t[62] = 'آنها برای خدا چیزهایی قرارمی&zwnj;دهند که خودشان از آن کراهت دارند [= فرزندان دختر]؛ با این حال زبانشان به دروغ می&zwnj;گوید سرانجام نیکی دارند! از این رو برای آنان آتش است؛ و آنها از پیشگامان (دوزخ) اند.';
$t[63] = 'به خدا سوگند، به سوی امتهای پیش از تو پیامبرانی فرستادیم؛ امّا شیطان اعمالشان را در نظرشان آراست؛ و امروز او ولیّ و سرپرستشان است؛ و مجازات دردناکی برای آنهاست!';
$t[64] = 'ما قرآن را بر تو نازل نکردیم مگر برای اینکه آنچه را در آن اختلاف دارند، برای آنها روشن کنی؛ و (این قرآن) مایه هدایت و رحمت است برای قومی که ایمان می&zwnj;آورند!';
$t[65] = 'خداوند از آسمان، آبی فرستاد؛ و زمین را، پس از آنکه مرده بود، حیات بخشید! در این، نشانه روشنی است برای جمعیّتی که گوش شنوا دارند!';
$t[66] = 'و در وجود چهارپایان، برای شما (درسهای) عبرتی است: از درون شکم آنها، از میان غذاهای هضم&zwnj;شده و خون، شیر خالص و گوارا به شما می&zwnj;نوشانیم!';
$t[67] = 'و از میوه&zwnj;های درختان نخل و انگور، مسکرات (ناپاک) و روزی خوب و پاکیزه می&zwnj;گیرید؛ در این، نشانه روشنی است برای جمعیّتی که اندیشه می&zwnj;کنند!';
$t[68] = 'و پروردگار تو به زنبور عسل &laquo;وحی&raquo; (و الهام غریزی) نمود که: &laquo;از کوه&zwnj;ها و درختان و داربستهایی که مردم می&zwnj;سازند، خانه&zwnj;هایی برگزین!';
$t[69] = 'سپس از تمام ثمرات (و شیره گلها) بخور و راه&zwnj;هایی را که پروردگارت برای تو تعیین کرده است، براحتی بپیما! &laquo;از درون شکم آنها، نوشیدنی با رنگهای مختلف خارج می&zwnj;شود که در آن، شفا برای مردم است؛ به یقین در این امر، نشانه روشنی است برای جمعیّتی که می&zwnj;اندیشند.';
$t[70] = 'خداوند شما را آفرید؛ سپس شما را می&zwnj;میراند؛ بعضی از شما به نامطلوب&zwnj;ترین سنین بالای عمر می&zwnj;رسند، تا بعد از علم و آگاهی، چیزی ندانند (و همه چیز را فراموش کنند)؛ خداوند دانا و تواناست!';
$t[71] = 'خداوند بعضی از شما را بر بعضی دیگر از نظر روزی برتری داد (چرا که استعدادها و تلاشهایتان متفاوت است)! اما آنها که برتری داده شده&zwnj;اند، حاضر نیستند از روزی خود به بردگانشان بدهند و همگی در آن مساوی گردند؛ آیا آنان نعمت خدا را انکار می&zwnj;نمایند (که شکر او را ادا نمی&zwnj;کنند)؟!';
$t[72] = 'خداوند برای شما از جنس خودتان همسرانی قرار داد؛ و از همسرانتان برای شما فرزندان و نوه&zwnj;هایی به وجود آورد؛ و از پاکیزه&zwnj;ها به شما روزی داد؛ آیا به باطل ایمان می&zwnj;آورند، و نعمت خدا را انکار می&zwnj;کنند؟!';
$t[73] = 'آنها غیر از خدا، موجوداتی را می&zwnj;پرستند که هیچ رزقی را برای آنان از آسمانها و زمین در اختیار ندارند؛ و توان این کار را نیز ندارند.';
$t[74] = 'پس، برای خدا امثال (و شبیه&zwnj;ها) قائل نشوید! خدا می&zwnj;داند، و شما نمی&zwnj;دانید.';
$t[75] = 'خداوند مثالی زده: برده مملوکی را که قادر بر هیچ چیز نیست؛ و انسان (با ایمانی) را که از جانب خود، رزقی نیکو به او بخشیده&zwnj;ایم، و او پنهان و آشکار از آنچه خدا به او داده، انفاق می&zwnj;کند؛ آیا این دو نفر یکسانند؟! شکر مخصوص خداست، ولی اکثر آنها نمی&zwnj;دانند!';
$t[76] = 'خداوند مثالی (دیگر) زده است: دو نفر را، که یکی از آن دو، گنگ مادرزاد است؛ و قادر بر هیچ کاری نیست؛ و سربار صاحبش می&zwnj;باشد؛ او را در پی هر کاری بفرستد، خوب انجام نمی&zwnj;دهد؛ آیا چنین انسانی، با کسی که امر به عدل و داد می&zwnj;کند، و بر راهی راست قرار دارد، برابر است؟!';
$t[77] = 'غیب آسمانها و زمین، مخصوص خداست (و او همه را می&zwnj;داند)؛ و امر قیامت (بقدری نزدیک و آسان است) درست همانند چشم برهم زدن، و یا از آن هم نزدیکتر؛ چرا که خدا بر هر چیزی تواناست!';
$t[78] = 'و خداوند شما را از شکم مادرانتان خارج نمود در حالی که هیچ چیز نمی&zwnj;دانستید؛ و برای شما، گوش و چشم و عقل قرار داد، تا شکر نعمت او را بجا آورید!';
$t[79] = 'آیا آنها به پرندگانی که بر فراز آسمانها نگه&zwnj;داشته شده، نظر نیفکندند؟ هیچ کس جز خدا آنها را نگاه نمی&zwnj;دارد؛ در این امر، نشانه&zwnj;هایی (از عظمت و قدرت خدا) است برای کسانی که ایمان می&zwnj;آورند!';
$t[80] = 'و خدا برای شما از خانه&zwnj;هایتان محل سکونت (و آرامش) قرار داد؛ و از پوست چهارپایان نیز برای شما خانه&zwnj;هایی قرار داد که روز کوچ کردن و روز اقامتتان، به آسانی می&zwnj;توانید آنها را جا به جا کنید؛ و از پشم و کرک و موی آنها، برای شما اثاث و متاع (و وسایل مختلف زندگی) تا زمان معیّنی قرار داد.';
$t[81] = 'و (نیز) خداوند از آنچه آفریده است سایه&zwnj;هایی برای شما قرار داده؛ و از کوه&zwnj;ها پناهگاه&zwnj;هایی؛ و برای شما پیراهنهایی آفریده، که شما را از گرما (و سرما) حفظ می&zwnj;کند؛ و پیراهنهایی که به هنگام جنگ، حافظ شماست؛ این گونه نعمتهایش را بر شما کامل می&zwnj;کند، شاید تسلیم فرمان او شوید!';
$t[82] = '(با این همه،) اگر روی برتابند، (نگران مباش؛) تو فقط وظیفه ابلاغ آشکار داری.';
$t[83] = 'آنها نعمت خدا را می&zwnj;شناسند؛ سپس آن را انکار می&zwnj;کنند؛ و اکثرشان کافرند!';
$t[84] = '(به خاطر بیاورید) روزی را که از هر امّتی گواهی بر آنان برمی&zwnj;انگیزیم؛ سپس به آنان که کفر ورزیدند، اجازه (سخن گفتن) داده نمی&zwnj;شود؛ (بلکه دست و پا و گوش و چشم، حتّی پوست تن آنها گواهی می&zwnj;دهند!) و (نیز) اجازه عذرخواهی و تقاضای عفو به آنان نمی&zwnj;دهند!';
$t[85] = 'و هنگامی که ظالمان عذاب را ببینند، نه به آنها تخفیف داده می&zwnj;شود، و نه مهلت!';
$t[86] = 'و هنگامی که مشرکان معبودهایی را که همتای خدا قرار دادند می&zwnj;بینند، می&zwnj;گویند: &laquo;پروردگارا! اینها همتایانی هستند که ما به جای تو، آنها را می&zwnj;خواندیم! &laquo;در این هنگام، معبودان به آنها می&zwnj;گویند: &laquo;شما دروغگو هستید! (شما هوای نفس خود را پرستش می&zwnj;کردید!)&raquo;';
$t[87] = 'و در آن روز، همگی (ناگزیر) در پیشگاه خدا تسلیم می&zwnj;شوند؛ و تمام آنچه را (نسبت به خدا) دروغ می&zwnj;بستند، گم و نابود می&zwnj;شود!';
$t[88] = 'کسانی که کافر شدند و (مردم را) از راه خدا بازداشتند، بخاطر فسادی که می&zwnj;کردند، عذابی بر عذابشان می&zwnj;افزاییم!';
$t[89] = '(به یاد آورید) روزی را که از هر امتی، گواهی از خودشان بر آنها برمی&zwnj;انگیزیم؛ و تو را گواه بر آنان قرارمی&zwnj;دهیم! و ما این کتاب را بر تو نازل کردیم که بیانگر همه چیز، و مایه هدایت و رحمت و بشارت برای مسلمانان است!';
$t[90] = 'خداوند به عدل و احسان و بخشش به نزدیکان فرمان می&zwnj;دهد؛ و از فحشا و منکر و ستم، نهی می&zwnj;کند؛ خداوند به شما اندرز می&zwnj;دهد، شاید متذکّر شوید!';
$t[91] = 'و هنگامی که با خدا عهد بستید، به عهد او وفا کنید! و سوگندها را بعد از محکم ساختن نشکنید، در حالی که خدا را کفیل و ضامن بر (سوگند) خود قرار داده&zwnj;اید، به یقین خداوند از آنچه انجام می&zwnj;دهید، آگاه است!';
$t[92] = 'همانند آن زن (سبک مغز) نباشید که پشمهای تابیده خود را، پس از استحکام، وامی&zwnj;تابید! در حالی که (سوگند و پیمان) خود را وسیله خیانت و فساد قرار می&zwnj;دهید؛ بخاطر اینکه گروهی، جمعیّتشان از گروه دیگر بیشتر است (و کثرت دشمن را بهانه&zwnj;ای برای شکستن بیعت با پیامبر می&zwnj;شمرید)! خدا فقط شما را با این وسیله آزمایش می&zwnj;کند؛ و به یقین روز قیامت، آنچه را در آن اختلاف داشتید، برای شما روشن می&zwnj;سازد!';
$t[93] = 'و اگر خدا می&zwnj;خواست، همه شما را امت واحدی قرارمی&zwnj;داد؛ (و همه را به اجبار وادار به ایمان می&zwnj;کرد؛ امّا ایمان اجباری فایده&zwnj;ای ندارد!) ولی خدا هر کس را بخواهد (و شایسته بداند) گمراه، و هر کس را بخواهد (و لایق بداند) هدایت می&zwnj;کند! (به گروهی توفیق هدایت داده، و از گروهی سلب می&zwnj;کند!) و یقیناً شما از آنچه انجام می&zwnj;دادید، بازپرسی خواهید شد!';
$t[94] = 'سوگندهایتان را وسیله تقلّب و خیانت در میان خود قرار ندهید، مبادا گامی بعد از ثابت&zwnj;گشتن (بر ایمان) متزلزل شود؛ و به خاطر بازداشتن (مردم) از راه خدا، آثار سوء آن را بچشید! و برای شما، عذاب عظیمی خواهد بود!';
$t[95] = 'و (هرگز) پیمان الهی را با بهای کمی مبادله نکنید (و هر بهایی در برابر آن ناچیز است!) آنچه نزد خداست، برای شما بهتر است اگر می&zwnj;دانستید.';
$t[96] = 'آنچه نزد شماست فانی می&zwnj;شود؛ امّا آنچه نزد خداست باقی است؛ و به کسانی که صبر و استقامت پیشه کنند، مطابق بهترین اعمالی که انجام می&zwnj;دادند پاداش خواهیم داد.';
$t[97] = 'هر کس کار شایسته&zwnj;ای انجام دهد، خواه مرد باشد یا زن، در حالی که مؤمن است، او را به حیاتی پاک زنده می&zwnj;داریم؛ و پاداش آنها را به بهترین اعمالی که انجام می&zwnj;دادند، خواهیم داد.';
$t[98] = 'هنگامی که قرآن می&zwnj;خوانی، از شرّ شیطان مطرود، به خدا پناه بر!';
$t[99] = 'چرا که او، بر کسانی که ایمان دارند و بر پروردگارشان توکّل می&zwnj;کنند، تسلّطی ندارد.';
$t[100] = 'تسلّط او تنها بر کسانی است که او را به سرپرستی خود برگزیده&zwnj;اند، و آنها که نسبت به او [= خدا] شرک می&zwnj;ورزند (و فرمان شیطان را به جای فرمان خدا، گردن می&zwnj;نهند)';
$t[101] = 'و هنگامی که آیه&zwnj;ای را به آیه دیگر مبدّل کنیم [= حکمی را نسخ نماییم&zwnj;] -و خدا بهتر می&zwnj;داند چه حکمی را نازل کند- آنها می&zwnj;گویند: &laquo;تو افترا می&zwnj;بندی!&raquo; امّا بیشترشان (حقیقت را) نمی&zwnj;دانند!';
$t[102] = 'بگو، روح القدس آن را از جانب پروردگارت بحقّ نازل کرده، تا افراد باایمان را ثابت&zwnj;قدم گرداند؛ و هدایت و بشارتی است برای عموم مسلمانان!';
$t[103] = 'ما می&zwnj;دانیم که آنها می&zwnj;گویند: &laquo;این آیات را انسانی به او تعلیم می&zwnj;دهد!&raquo; در حالی که زبان کسی که اینها را به او نسبت می&zwnj;دهند عجمی است؛ ولی این (قرآن)، زبان عربی آشکار است!';
$t[104] = 'به یقین، کسانی که به آیات الهی ایمان نمی&zwnj;آورند، خدا آنها را هدایت نمی&zwnj;کند؛ و برای آنان عذاب دردناکی است.';
$t[105] = 'تنها کسانی دروغ می&zwnj;بندند که به آیات خدا ایمان ندارند؛ (آری،) دروغگویان واقعی آنها هستند!';
$t[106] = 'کسانی که بعد از ایمان کافر شوند -بجز آنها که تحت فشار واقع شده&zwnj;اند در حالی که قلبشان آرام و با ایمان است- آری، آنها که سینه خود را برای پذیرش کفر گشوده&zwnj;اند، غضب خدا بر آنهاست؛ و عذاب عظیمی در انتظارشان!';
$t[107] = 'این به خاطر آن است که زندگی دنیا (و پست را) بر آخرت ترجیح دادند؛ و خداوند افراد بی&zwnj;ایمان (لجوج) را هدایت نمی&zwnj;کند.';
$t[108] = 'آنها کسانی هستند که (بر اثر فزونی گناه،) خدا بر قلب و گوش و چشمانشان مهر نهاده؛ (به همین دلیل نمی&zwnj;فهمند،) و غافلان واقعی همانها هستند!';
$t[109] = 'و ناچار آنها در آخرت زیانکارند.';
$t[110] = 'امّا پروردگار تو نسبت به کسانی که بعد از فریب&zwnj;خوردن، (به ایمان بازگشتند و) هجرت کردند؛ سپس جهاد کردند و در راه خدا استقامت نمودند؛ پروردگارت، بعد از انجام این کارها، بخشنده و مهربان است (و آنها را مشمول رحمت خود می&zwnj;سازد).';
$t[111] = '(به یاد آورید) روزی را که هر کس (در فکر خویشتن است؛ و تنها) به دفاع از خود برمی&zwnj;خیزد؛ و نتیجه اعمال هر کسی، بی&zwnj;کم و کاست، به او داده می&zwnj;شود؛ و به آنها ظلم نخواهد شد!';
$t[112] = 'خداوند (برای آنان که کفران نعمت می&zwnj;کنند،) مثلی زده است: منطقه آبادی که امن و آرام و مطمئن بود؛ و همواره روزیش از هر جا می&zwnj;رسید؛ امّا به نعمتهای خدا ناسپاسی کردند؛ و خداوند به خاطر اعمالی که انجام می&zwnj;دادند، لباس گرسنگی و ترس را بر اندامشان پوشانید!';
$t[113] = 'پیامبری از خودشان به سراغ آنها آمد، او را تکذیب کردند؛ از این رو عذاب الهی آنها را فراگرفت در حالی که ظالم بودند!';
$t[114] = 'پس، از آنچه خدا روزیتان کرده است، حلال و پاکیزه بخورید؛ و شکر نعمت خدا را بجا آورید اگر او را می&zwnj;پرستید!';
$t[115] = 'خداوند، تنها مردار، خون، گوشت خوک و آنچه را با نام غیر خدا سر بریده&zwnj;اند، بر شما حرام کرده است؛ امّا کسانی که ناچار شوند، در حالی که تجاوز و تعدّی از حدّ ننمایند، (خدا آنها را می&zwnj;بخشد؛ چرا که) خدا بخشنده و مهربان است.';
$t[116] = 'به خاطر دروغی که بر زبانتان جاری می&zwnj;شود (و چیزی را مجاز و چیزی را ممنوع می&zwnj;کنید،) نگویید: &laquo;این حلال است و آن حرام&raquo;، تا بر خدا افترا ببندید به یقین کسانی که به خدا دروغ می&zwnj;بندند، رستگار نخواهند شد!';
$t[117] = 'بهره کمی است (که در این دنیا نصیبشان می&zwnj;شود)؛ و عذاب دردناکی در انتظار آنان است!';
$t[118] = 'چیزهایی را که پیش از این برای تو شرح دادیم، بر یهود حرام کردیم؛ ما به آنها ستم نکردیم، اما آنها به خودشان ظلم و ستم می&zwnj;کردند!';
$t[119] = 'امّا پروردگارت نسبت به آنها که از روی جهالت، بدی کرده&zwnj;اند، سپس توبه کرده و در مقام جبران برآمده&zwnj;اند، پروردگارت بعد از آن آمرزنده و مهربان است.';
$t[120] = 'ابراهیم (به تنهایی) امّتی بود مطیع فرمان خدا؛ خالی از هر گونه انحراف؛ و از مشرکان نبود؛';
$t[121] = 'شکرگزار نعمتهای پروردگار بود؛ خدا او را برگزید؛ و به راهی راست هدایت نمود!';
$t[122] = 'ما در دنیا به او (همّت) نیکویی دادیم؛ و در آخرت از نیکان است!';
$t[123] = 'سپس به تو وحی فرستادیم که از آیین ابراهیم -که ایمانی خالص داشت و از مشرکان نبود- پیروی کن!';
$t[124] = '(تحریمهای) روز شنبه (برای یهود) فقط بعنوان یک مجازات بود، که در آن هم اختلاف کردند؛ و پروردگارت روز قیامت، در آنچه اختلاف داشتند، میان آنها داوری می&zwnj;کند!';
$t[125] = 'با حکمت و اندرز نیکو، به راه پروردگارت دعوت نما! و با آنها به روشی که نیکوتر است، استدلال و مناظره کن! پروردگارت، از هر کسی بهتر می&zwnj;داند چه کسی از راه او گمراه شده است؛ و او به هدایت&zwnj;یافتگان داناتر است.';
$t[126] = 'و هر گاه خواستید مجازات کنید، تنها بمقداری که به شما تعدّی شده کیفر دهید! و اگر شکیبایی کنید، این کار برای شکیبایان بهتر است.';
$t[127] = 'صبر کن، و صبر تو فقط برای خدا و به توفیق خدا باشد! و بخاطر (کارهای) آنها، اندوهگین و دلسرد مشو! و از توطئه&zwnj;های آنها، در تنگنا قرار مگیر!';
$t[128] = 'خداوند با کسانی است که تقوا پیشه کرده&zwnj;اند، و کسانی که نیکوکارند.';
?>