$t[3] = 'این گونه خداوند عزیز و حکیم به تو و پیامبرانی که پیش از تو بودند وحی می&zwnj;کند.';
$t[4] = 'آنچه در آسمانها و آنچه در زمین است از آن اوست؛ و او بلندمرتبه و بزرگ است!';
$t[5] = 'نزدیک است آسمانها (بخاطر نسبتهای ناروای مشرکان) از بالا متلاشی شوند و فرشتگان پیوسته تسبیح و حمد پروردگارشان را بجا می&zwnj;آورند و برای کسانی که در زمین هستند استغفار می&zwnj;کنند؛ آگاه باشید خداوند آمرزنده و مهربان است.';
$t[6] = 'کسانی که غیر خدا را ولیّ خود انتخاب کردند، خداوند حساب همه اعمال آنها را نگه می&zwnj;دارد؛ و تو مأمور نیستی که آنان را مجبور به قبول حقّ کنی!';
$t[7] = 'و این گونه قرآنی عربی [= فصیح و گویا] را بر تو وحی کردیم تا &laquo;امّ&zwnj;القری&raquo; [= مکّه&zwnj;] و مردم پیرامون آن را انذار کنی و آنها را از روزی که همه خلایق در آن روز جمع می&zwnj;شوند و شکّ و تردید در آن نیست بترسانی؛ گروهی در بهشتند و گروهی در آتش سوزان!';
$t[8] = 'و اگر خدا می&zwnj;خواست همه آنها را امّت واحدی قرار می&zwnj;داد (و به زور هدایت می&zwnj;کرد، ولی هدایت اجباری سودی ندارد)؛ امّا خداوند هر کس را بخواهد در رحمتش وارد می&zwnj;کند، و برای ظالمان ولیّ و یاوری نیست.';
$t[9] = 'آیا آنها غیر از خدا را ولیّ خود برگزیدند؟! در حالی که &laquo;ولیّ&raquo; فقط خداوند است و اوست که مردگان را زنده می&zwnj;کند، و اوست که بر هر چیزی تواناست!';
$t[10] = 'در هر چیز اختلاف کنید، داوریش با خداست؛ این است خداوند، پروردگار من، بر او توکّل کرده&zwnj;ام و به سوی او بازمی&zwnj;گردم!';
$t[11] = 'او آفریننده آسمانها و زمین است و از جنس شما همسرانی برای شما قرار داد و جفتهایی از چهارپایان آفرید؛ و شما را به این وسیله [= بوسیله همسران&zwnj;] زیاد می کند؛ هیچ چیز همانند او نیست و او شنوا و بیناست!';
$t[12] = 'کلیدهای آسمانها و زمین از آن اوست؛ روزی را برای هر کس بخواهد گسترش می دهد یا محدود می&zwnj;سازد؛ او به همه چیز داناست.';
$t[13] = 'آیینی را برای شما تشریع کرد که به نوح توصیه کرده بود؛ و آنچه را بر تو وحی فرستادیم و به ابراهیم و موسی و عیسی سفارش کردیم این بود که: دین را برپا دارید و در آن تفرقه ایجاد نکنید! و بر مشرکان گران است آنچه شما آنان را به سویش دعوت می کنید! خداوند هر کس را بخواهد برمی&zwnj;گزیند، و کسی را که به سوی او بازگردد هدایت می&zwnj;کند.';
$t[14] = 'آنان پراکنده نشدند مگر بعد از آنکه علم و آگاهی به سراغشان آمد؛ و این تفرقه جویی بخاطر انحراف از حق (و عداوت و حسد) بود؛ و اگر فرمانی از سوی پروردگارت صادر نشده بود که تا سرآمد معیّنی (زنده و آزاد) باشند، در میان آنها داوری می&zwnj;شد؛ و کسانی که بعد از آنها وارثان کتاب شدند نسبت به آن در شک و تردیدند، شکی همراه با بدبینی!';
$t[15] = 'پس به همین خاطر تو نیز آنان را به سوی این آیین واحد الهی دعوت کن و آنچنان که مأمور شده&zwnj;ای استقامت نما، و از هوی و هوسهای آنان پیروی مکن، و بگو: &laquo;به هر کتابی که خدا نازل کرده ایمان آورده&zwnj;ام و مأمورم در میان شما عدالت کنم؛ خداوند پروردگار ما و شماست؛ نتیجه اعمال ما از آن ما است و نتیجه اعمال شما از آن شما، خصومت شخصی در میان ما نیست؛ و خداوند ما و شما را در یکجا جمع می&zwnj;کند، و بازگشت (همه) به سوی اوست!&raquo;';
$t[16] = 'کسانی که (از روی لجاجت) درباره خدا بعد از پذیرفتن (و ایمان به) او، محاجّه می کنند، دلیلشان نزد پروردگارشان باطل و بی&zwnj;پایه است؛ و غضب بر آنهاست و عذابی شدید دارند.';
$t[17] = 'خداوند کسی است که کتاب را بحق نازل کرد و میزان (سنجش حق و باطل و خبر قیامت) را نیز؛ تو چه می&zwnj;دانی شاید ساعت (قیام قیامت) نزدیک باشد!';
$t[18] = 'کسانی که به قیامت ایمان ندارند درباره آن شتاب می&zwnj;کنند؛ ولی آنها که ایمان آورده&zwnj;اند پیوسته از آن هراسانند، و می&zwnj;دانند آن حق است؛ آگاه باشید کسانی که در قیامت تردید می&zwnj;کنند، در گمراهی عمیقی هستند.';
$t[19] = 'خداوند نسبت به بندگانش لطف (و آگاهی) دارد؛ هر کس را بخواهد روزی می&zwnj;دهد و او قوّی و شکست&zwnj;ناپذیر است!';
$t[20] = 'کسی که زراعت آخرت را بخواهد، به کشت او برکت و افزایش می&zwnj;دهیم و بر محصولش می&zwnj;افزاییم؛ و کسی که فقط کشت دنیا را بطلبد، کمی از آن به او می&zwnj;دهیم امّا در آخرت هیچ بهره&zwnj;ای ندارد!';
$t[21] = 'آیا معبودانی دارند که بی&zwnj;اذن خداوند آیینی برای آنها ساخته&zwnj;اند؟! اگر مهلت معیّنی برای آنها نبود، در میانشان داوری می&zwnj;شد (و دستور عذاب صادر می&zwnj;گشت) و برای ظالمان عذاب دردناکی است!';
$t[22] = '(در آن روز) ستمگران را می&zwnj;بینی که از اعمالی که انجام داده&zwnj;اند سخت بیمناکند، ولی آنها را فرامی&zwnj;گیرد! امّا کسانی که ایمان آورده و کارهای شایسته انجام داده&zwnj;اند در باغهای بهشتند و هر چه بخواهند نزد پروردگارشان برای آنها فراهم است؛ این است فضل (و بخشش) بزرگ!';
$t[23] = 'این همان چیزی است که خداوند بندگانش را که ایمان آورده و اعمال صالح انجام داده&zwnj;اند به آن نوید می&zwnj;دهد! بگو: &laquo;من هیچ پاداشی از شما بر رسالتم درخواست نمی&zwnj;کنم جز دوست&zwnj;داشتن نزدیکانم [= اهل بیتم&zwnj;]؛ و هر کس کار نیکی انجام دهد، بر نیکی&zwnj;اش می&zwnj;افزاییم؛ چرا که خداوند آمرزنده و سپاسگزار است.';
$t[24] = 'آیا می&zwnj;گویند: &laquo;او بر خدا دروغ بسته است&raquo;؟! در حالی که اگر خدا بخواهد بر قلب تو مُهر می&zwnj;نهد (و اگر خلاف بگوئی قدرت اظهار این آیات را از تو می&zwnj;گیرد) و باطل را محو می&zwnj;کند و حقّ را بفرمانش پابرجا می&zwnj;سازد؛ چرا که او از آنچه درون سینه&zwnj;هاست آگاه است.';
$t[25] = 'او کسی است که توبه را از بندگانش می&zwnj;پذیرد و بدیها را می&zwnj;بخشد، و آنچه را انجام می&zwnj;دهید می&zwnj;داند.';
$t[26] = 'و درخواست کسانی را که ایمان آورده و کارهای نیک انجام داده&zwnj;اند می&zwnj;پذیرد و از فضل خود بر آنها می&zwnj;افزاید؛ امّا برای کافران عذاب شدیدی است!';
$t[27] = 'هرگاه خداوند روزی را برای بندگانش وسعت بخشد، در زمین طغیان و ستم می&zwnj;کنند؛ از این&zwnj;رو بمقداری که می&zwnj;خواهد (و مصلحت می&zwnj;داند) نازل می&zwnj;کند، که نسبه به بندگانش آگاه و بیناست!';
$t[28] = 'او کسی است که باران سودمند را پس از آنکه مأیوس شدند نازل می&zwnj;کند و رحمت خویش را می&zwnj;گستراند؛ و او ولیّ و (سرپرست) و ستوده است!';
$t[29] = 'و از آیات اوست آفرینش آسمانها و زمین و آنچه از جنبندگان در آنها منتشر نموده؛ و او هرگاه بخواهد بر جمع آنها تواناست!';
$t[30] = 'هر مصیبتی به شما رسد بخاطر اعمالی است که انجام داده&zwnj;اید، و بسیاری را نیز عفو می&zwnj;کند!';
$t[31] = 'و شما هرگز نمی&zwnj;توانید در زمین از قدرت خداوند فرار کنید؛ و غیر از خدا هیچ ولیّ و یاوری برای شما نیست.';
$t[32] = 'از نشانه&zwnj;های او کشتیهایی است که در دریا همچون کوه&zwnj;ها به نظر می&zwnj;رسند!';
$t[33] = 'اگر او اراده کند، باد را ساکن می&zwnj;سازد تا آنها بر پشت دریا بی&zwnj;حرکت بمانند؛ در این نشانه&zwnj;هایی است برای هر صبرکننده شکرگزار!';
$t[34] = 'یا اگر بخواهد آنها را بخاطر اعمالی که سرنشینانش مرتکب شده&zwnj;اند نابود می&zwnj;سازد؛ و در عین حال بسیاری را می&zwnj;بخشد.';
$t[35] = 'کسانی که در آیات ما مجادله می&zwnj;کنند بدانند هیچ گریزگاهی ندارند!';
$t[36] = 'آنچه به شما عطا شده متاع زودگذر زندگی دنیاست، و آنچه نزد خداست برای کسانی که ایمان آورده و بر پروردگارشان توکّل می&zwnj;کنند بهتر و پایدارتر است.';
$t[37] = 'همان کسانی که از گناهان بزرگ و اعمال زشت اجتناب می&zwnj;ورزند، و هنگامی که خشمگین شوند عفو می&zwnj;کنند.';
$t[38] = 'و کسانی که دعوت پروردگارشان را اجابت کرده و نماز را برپا می&zwnj;دارند و کارهایشان به صورت مشورت در میان آنهاست و از آنچه به آنها روزی داده&zwnj;ایم انفاق می&zwnj;کنند،';
$t[39] = 'و کسانی که هرگاه ستمی به آنها رسد، (تسلیم ظلم نمی&zwnj;شوند و) یاری می&zwnj;طلبند!';
$t[40] = 'کیفر بدی، مجازاتی است همانند آن؛ و هر کس عفو و اصلاح کند، پاداش او با خداست؛ خداوند ظالمان را دوست ندارد!';
$t[41] = 'و کسی که بعد از مظلوم&zwnj;شدن یاری طلبد، ایرادی بر او نیست؛';
$t[42] = 'ایراد و مجازات بر کسانی است که به مردم ستم می&zwnj;کنند و در زمین بناحق ظلم روا می&zwnj;دارند؛ برای آنان عذاب دردناکی است!';
$t[43] = 'امّا کسانی که شکیبایی و عفو کنند، این از کارهای پرارزش است!';
$t[44] = 'کسی را که خدا گمراه کند، ولیّ و یاوری جز او نخواهد داشت؛ و ظالمان را (روز قیامت) می&zwnj;بینی هنگامی که عذاب الهی را مشاهده می&zwnj;کنند میگویند: &laquo;آیا راهی به سوی بازگشت (و جبران) وجود دارد؟!&raquo;';
$t[45] = 'و آنها را می&zwnj;بینی که بر آتش عرضه می&zwnj;شوند در حالی که از شدّت مذلّت خاشعند و زیر چشمی (به آن) نگاه می&zwnj;کنند؛ و کسانی که ایمان آورده&zwnj;اند می&zwnj;گویند: &laquo;زیانکاران واقعی آنانند که خود و خانواده خویش را روز قیامت از دست داده&zwnj;اند؛ آگاه باشید که ظالمان (آن روز) در عذاب دائمند!&raquo;';
$t[46] = 'آنها جز خدا اولیا و یاورانی ندارند که یاریشان کنند؛ و هر کس را خدا گمراه سازد، هیچ راه نجاتی برای او نیست!';
$t[47] = 'اجابت کنید دعوت پروردگار خود را پیش از آنکه روزی فرا رسد که بازگشتی برای آن در برابر اراده خدا نیست؛ و در آن روز، نه پناهگاهی دارید و نه مدافعی!';
$t[48] = 'و اگر روی&zwnj;گردان شوند (غمگین مباش)، ما تو را حافظ آنان (و مأمور اجبارشان) قرار نداده&zwnj;ایم؛ وظیف&zwnj;ه تو تنها ابلاغ رسالت است! و هنگامی که ما رحمتی از سوی خود به انسان بچشانیم به آن دلخوش می&zwnj;شود، و اگر بلایی بخاطر اعمالی که انجام داده&zwnj;اند به آنها رسد (به کفران می&zwnj;پردازند)، چرا که انسان بسیار کفران&zwnj;کننده است!';
$t[49] = 'مالکیّت و حاکمیّت آسمانها و زمین از آن خداست؛ هر چه را بخواهد می&zwnj;آفریند؛ به هر کس اراده کند دختر می&zwnj;بخشد و به هر کس بخواهد پسر،';
$t[50] = 'یا (اگر بخواهد) پسر و دختر -هر دو- را برای آنان جمع میکند و هر کس را بخواهد عقیم می&zwnj;گذارد؛ زیرا که او دانا و قادر است.';
$t[51] = 'و شایسته هیچ انسانی نیست که خدا با او سخن گوید، مگر از راه وحی یا از پشت حجاب، یا رسولی می&zwnj;فرستد و بفرمان او آنچه را بخواهد وحی می&zwnj;کند؛ چرا که او بلندمقام و حکیم است!';
$t[52] = 'همان گونه (که بر پیامبران پیشین وحی فرستادیم) بر تو نیز روحی را بفرمان خود وحی کردیم؛ تو پیش از این نمی&zwnj;دانستی کتاب و ایمان چیست (و از محتوای قرآن آگاه نبودی)؛ ولی ما آن را نوری قرار دادیم که بوسیله آن هر کس از بندگان خویش را بخواهیم هدایت می&zwnj;کنیم؛ و تو مسلّماً به سوی راه راست هدایت می&zwnj;کنی.';
$t[53] = 'راه خداوندی که تمامی آنچه در آسمانها و آنچه در زمین است از آن اوست؛ آگاه باشید که همه کارها تنها بسوی خدا بازمی&zwnj;گردد!';
?>