$t = array();
$t[1] = 'A. L̃. M̃.';
$t[2] = 'An rinj&atilde;yi Rũmawa.';
$t[3] = 'A cikin mafi kusantar ƙasarsu, kuma sũ, a b&atilde;yan rinj&atilde;yarsu, z&atilde; sn rinj&atilde;ya.';
$t[4] = 'A cikin\'yan shẽkaru. Al\'amari na Allah ne a gab&atilde;nin k&otilde;me da b&atilde;yansa, kuma a r&atilde;nar nan mũminai z&atilde; su yi farin ciki.';
$t[5] = 'Da taimakon Allah Yan&atilde; taimakon wanda Yake so. Kuma Shĩ ne Mabuw&atilde;yi, Mai jin ƙai.';
$t[6] = 'Wa\'adin Allah, Allah b&atilde; ya s&atilde;ɓ&atilde;wa ga wa\'adinsa, kuma amma mafi yawan mut&atilde;ne ba su sani ba.';
$t[7] = 'Sun&atilde; sanin bayyanannar r&atilde;yuwar dũniya, alh&atilde;li kuwa sũ shagalallu ne daga r&atilde;yuwar L&atilde;hira.';
$t[8] = 'Shin, ba su yi tun&atilde;ni ba a cikin zuk&atilde;tansu cẽwa Allah bai halitta sammai da ƙasa ba da abin da ke tsak&atilde;ninsu, f&atilde;ce da gaskiya da ajali ambatacce? Kuma lalle m&atilde;su yawa daga mut&atilde;ne k&atilde;firai ne ga gamuwa da Ubangijinsu?';
$t[9] = 'Shin, kuma ba su yi tafiya ba, a cikin ƙas&atilde; d&otilde;min su gani yadda aƙibar waɗanda ke a gab&atilde;ninsu ta kasance? Sun kasance mafiya ƙarfi daga gare su kuma suka n&otilde;mi ƙasa suka r&atilde;ya ta fiye da yadda suka raya ta, kuma manzanninsu suka je musu da hujjoji bayyanannu. Sabo da haka Allah ba Ya yiwuwa Ya z&atilde;lunce su, amma kansu suka kasance sun&atilde; z&atilde;lunta.';
$t[10] = 'Sa\'an nan &atilde;ƙibar waɗannan da suka aikata mũgun aiki ta kasance mafi muni, w&atilde;tau sun ƙaryata game da &atilde;y&otilde;yin Allah, kuma suka kasance sun&atilde; izgili da su.';
$t[11] = 'Allah ne ke f&atilde;ra yin halitta, sa\'an nan Ya s&atilde;ke ta sa\'an nan zuwa gare Shi ake mayar da ku.';
$t[12] = 'Kuma a r&atilde;nar da sa\'a ke tsayuwa, m&atilde;su laifi z&atilde; su k&atilde;sa magana.';
$t[13] = 'Kuma b&atilde; su da m&atilde;su cẽto daga abũbuwan shirkinsu, kuma snn kasance m&atilde;su k&atilde;fircẽwadaga abũbuwan shirkinsu.';
$t[14] = 'Kuma a r&atilde;nar da Sa\'a ke tsayuwa, a r&atilde;nar nan mut&atilde;ne ke farraba.';
$t[15] = 'To, amma waɗanda suka yi ĩm&atilde;ni, kuma suka aikata ayyukan ƙwarai to sũ an&atilde; faranta musu r&atilde;yuka, a cikin wani lambu.';
$t[16] = 'Kuma waɗanda suka k&atilde;firta, kuma suka ƙaryata, game da &atilde;y&otilde;yinMu da kuma haɗuwa da R&atilde;nar L&atilde;hira, to, waɗancan an&atilde; halartar da su a cikin az&atilde;ba.';
$t[17] = 'Sab&otilde;da haka tsarkakẽwa ta tabbata ga Allah a l&otilde;kacin da kuke shiga maraice, da l&otilde;kacin da kuke shiga s&atilde;fiya.';
$t[18] = 'Kuma Shĩ ne da g&otilde;diya, a cikin sammai da ƙas&atilde; kuma da l&otilde;kacin yamma da l&otilde;kacin zaw&atilde;li.';
$t[19] = 'Yan&atilde; fitar da mai rai daga matacce kuma Yan&atilde; fitar da matacce daga mai rai, kuma Yan&atilde; r&atilde;yar da ƙasa a b&atilde;yan mutuwarta. Kuma haka ake fitar da ku.';
$t[20] = 'Kuma akwai daga &atilde;y&otilde;yinSa, Ya halitta ku daga turɓ&atilde;ya, sai g&atilde; ku kun zama mutum, kun&atilde; w&atilde;tsuwa.';
$t[21] = 'Kuma akwai daga &atilde;y&otilde;yinsa, Ya halitta muku m&atilde;tan aure daga kanku, d&otilde;min ku natsu zuwa gare ta, kuma Ya sanya s&otilde;yayya da rahama a tsak&atilde;ninku. Lalle a cikin wancan akwai &atilde;y&otilde;yi ga mut&atilde;ne m&atilde;su yin tun&atilde;ni.';
$t[22] = 'Kuma akwai daga &atilde;y&otilde;yinSa halittar sammai da ƙas&atilde; da s&atilde;ɓ&atilde;, war harsunanku, da launukanku. Lalle a cikin wannan akwai &atilde;y&otilde;yi ga m&atilde;su ilmi.';
$t[23] = 'Kuma akwai daga cikin &atilde;y&otilde;yinSa, barcinku a cikin dare da r&atilde;na, da nẽmanku ga falalarsa. Lalle a cikin wannan akwai &atilde;y&otilde;yi ga mut&atilde;ne m&atilde;su saur&atilde;r&atilde;wa.';
$t[24] = 'Kuma akwai daga cikin &atilde;y&otilde;yinSa, Ya nũna muku walƙiya a kan ts&otilde;ro da ɗamm&atilde;ni kuma Ya dinga saukar da ruwa daga sama Sa\'an nan Ya r&atilde;yar da ƙas&atilde; game da shi a b&atilde;yan mutuwarta. Lalle a cikin wannan akwai &atilde;y&otilde;yi ga mut&atilde;ne m&atilde;su hankaltawa.';
$t[25] = 'Kuma akwai daga &atilde;y&otilde;yinsa, tsayuwar sama da asƙ&atilde; bisa umurninsa, sa\'an nan idan Ya kira ku, kira ɗaya, daga ƙas&atilde;, sai g&atilde; ku kun&atilde; fita.';
$t[26] = 'Kuma wanda ke cikin sammai da ƙas&atilde; N&atilde;sa ne shi kaɗai. Dukansu m&atilde;su taw&atilde;li\'u ne a gare Shi.';
$t[27] = 'Kuma Shĩ ne Wanda Ya f&atilde;ra halitta, sa\'an nan Ya s&atilde;ke ta kuma sakẽwarta t&atilde; fi sauƙi a gare shi. Kuma Yan&atilde; da mis&atilde;li wanda ya fi ɗaukaka a cikin sammai da ƙas&atilde; kuma Shĩ ne Mabuw&atilde;yi, Mai jin ƙai.';
$t[28] = 'Ya bũga muku wani mis&atilde;li daga kanku. K&otilde; kun&atilde; da ab&otilde;kan tarẽwa, daga cikin b&atilde;yin da hann&atilde;yenku na d&atilde;ma suka mallaka, a cikin arzikinku, watau ku zama daidai a ciki kun&atilde; ts&otilde;ron su kamar tS&otilde;ronku ga kanku? Haka dai Muke bayyana &atilde;y&otilde;yi, daki-daki, ga mut&atilde;ne m&atilde;su hankaltawa.';
$t[29] = '&Atilde;\'aha! waɗanda suka yi z&atilde;1unci sun bi son zũciy&otilde;yinsu, b&atilde; t&atilde;re da wani ilmi ba. To w&atilde;ne ne zai shiryar da wanda Allah Ya ɓatar, kuma b&atilde; su da waɗansu mataimaka?';
$t[30] = 'Sab&otilde;da haka, ka tsayar da fuskarka ga addini, kan&atilde; mai karkata zuwa ga gaskiya, halittar Allah da Ya halitta mut&atilde;ne a kanta. B&atilde;bu musany&atilde;wa ga halittar Allah, wannan shĩ ne addini madaidaici kuma amma mafi yawan mut&atilde;ne ba su sani ba.';
$t[31] = 'Kun&atilde; m&atilde;su mai da al\'amari gare Shi, kuma ku bĩ Shi dataƙawa, kuma ku tsayar da salla, kuma kada ku kasance daga mushirikai.';
$t[32] = 'W&atilde;tau waɗanda suka rarrabe addininsu kuma suka kasance ƙungiya-ƙungiya, k&otilde;wace ƙungiya tan&atilde; mai farin ciki da abin da ke a gare ta kawai.';
$t[33] = 'Kuma idan cũta ta sh&atilde;fi mut&atilde;ne, sai su kir&atilde;yi Ubangijinsu, sun&atilde; m&atilde;su mai da al\'amari gare Shi, sa\'an nan idan Ya ɗanɗana musu wata rahama daga gare Shi, sai g&atilde; wani ɓangare daga gare su sun&atilde; shirki da Ubangijinsu.';
$t[34] = 'D&otilde;min su k&atilde;firta dl abin da Muka b&atilde; su. To ku ji d&atilde;ɗi kaɗan Sa\'an nan z&atilde; ku, sani.';
$t[35] = 'K&otilde; Mun saukar da wani dalĩ li a gare su? Shi kuwa yan&atilde; magana da abin da suka kasance sun&atilde; shirkin da shi?';
$t[36] = 'Kuma idan Muka ɗanɗana wa mut&atilde;ne wata rahama, sai su yi farin ciki da ita kuma idan cũta ta s&atilde;me su, sab&otilde;da abin da hannayensu suka gab&atilde;tar, sai g&atilde; su sun&atilde; yanke ƙauna,';
$t[37] = 'Shin, kuma ba su gani ba cẽwa Allah na shimfida arziki ga wanda Yake so kuma Yan&atilde; ƙuntat&atilde;wa? Lalle a cikin wancan akwai &atilde;y&otilde;yi ga mut&atilde;ne m&atilde;su yin ĩm&atilde;ni.';
$t[38] = 'Sab&otilde;da haka ka bai wa zumu hakkinsa da miskĩnai da ɗan hanya, wannan shĩ ne alhẽri ga waɗanda ke nufin yardar Allah kuma waɗancan sũ ne m&atilde;su s&atilde;mun babban rabo.';
$t[39] = 'Kuma abin da kuka b&atilde;yar na riba d&otilde;min ya ƙ&atilde;ru a cikin dũkiyar mut&atilde;ne to, b&atilde; zai ƙ&atilde;ru ba, a wurin Allah. Kuma abin da kuka b&atilde;yar na zakka, kan&atilde; nufin yardar Allah to (m&atilde;su yin haka) waɗancan sũ ne m&atilde;su nink&atilde;wa (ga dũkiyarsu).';
$t[40] = 'Allah ne Wanda Ya halitta ku, sa\'an nan Ya azurta ku, sa\'an nan Ya matar da ku, sa\'an nan Ya r&atilde;yar da ku. Ashe, daga cikin abũbuwan shirkinku akwai wanda ke aikata wani abu daga waɗannan ahũbuwa? Tsarki ya tabbata ga Allah, kuma Ya ɗaukaka bisa ga abin da suke yi na shirki.';
$t[41] = '&yen;arn&atilde; t&atilde; bayyana a cikin ƙasa da tẽku, sab&otilde;da abin da hann&atilde;yen mut&atilde;ne suka aikata. D&otilde;min Allah Ya ɗanɗana musu s&atilde;shin abin da suka aikata, ɗamm&atilde;ninsu z&atilde; su k&otilde;mo.';
$t[42] = 'Ka ce: &quot;Ku tafi a cikin ƙas&atilde; sa\'an nan ku dũbi yadda &atilde;ƙibar waɗanda suka kasance a gab&atilde;ninku ta kasance. Mafi yawansu sun kasance m&atilde;su yin shirki.&quot;';
$t[43] = 'Sab&otilde;da haka, ka tsayar da fuskarka ga addini madaidaici a gab&atilde;nin wani yini ya zo, b&atilde;bu makawa gare Shi daga Allah, a r&atilde;nar nan mut&atilde;ne sun&atilde; ts&atilde;gẽwa (su rabu biyu).';
$t[44] = 'Wanda ya k&atilde;firta, to, k&atilde;fircinsa na kansa, kuma wanda ya aikata aikin ƙwarai, to, sab&otilde;da kansu suke yin shimfiɗa.';
$t[45] = 'D&otilde;min Ya s&atilde;k&atilde; wa waɗanda suka yi ĩm&atilde;ni, kuma suka aikata ayyukan ƙwarai, daga falalarSa. Lalle Allah b&atilde; Ya son kafirai.';
$t[46] = 'Kuma akwai daga &atilde;y&otilde;yinSa, ya aika isk&otilde;ki m&atilde;su b&atilde;yar da bush&atilde;ra kuma d&otilde;min Ya ɗanɗana muku daga rahamarSa, kuma d&otilde;min jir&atilde;gen ruwa su gud&atilde;na da umurninSa, kuma d&otilde;min ku nẽma daga falalarSa, f&atilde;tanku z&atilde; ku gode.';
$t[47] = 'Kuma lalle Mun aiki waɗansu Manzanni a gab&atilde;ninka, zuwa ga mut&atilde;nensu sai suka je musu da hujj&otilde;ji, bayyanannu, sa\'an nan Muka azabtar da waɗanda suka yi laifi, kuma ya kasance tabbatacce taimakon mũminai wajibi ne a kan mu.';
$t[48] = 'Allah ne Wanda ke aika isk&otilde;ki, sai su m&otilde;tsar da girgije, sa\'an nan Ya shimfiɗa shi a cikin sama yadda Yake so, kuma Ya sanya shi wani ɓaɓɓake, sa\'an nan ka ga ruwa na fita daga tsak&atilde;ninsa. Sa\'an nan idan Allah Ya s&atilde;mi waɗanda Ya so daga b&atilde;yinsa, Sai g&atilde; su suna bush&atilde;ra da shi.';
$t[49] = 'Kuma k&otilde; da sun kasance a gab&atilde;nin a saukar da shi a kansu, kusa-kusa, sun&atilde; baƙin ciki hai b&atilde; su iya magana.';
$t[50] = 'Sai ka dũbi al&atilde;m&otilde;min rahamar Allah yadda Yake r&atilde;yar da ƙas&atilde; a b&atilde;yan mutuwarta. Lalle wannan (Mai wannan aiki), tabbas, Mai r&atilde;yar da halitta ne, kuma Shi Mai ikon yi ne a kan k&otilde;me.';
$t[51] = 'Kuma lalle idan Mun aika wata iska, suka ganta fatsifatsi, lalle z&atilde; su yini a b&atilde;yansa sun&atilde; k&atilde;firta.';
$t[52] = 'Sab&otilde;da haka, kai, b&atilde; ka jiyar da matattu kira, kuma b&atilde; ka jiyar da kur&atilde;me kira idan sun jũya b&atilde;ya sun&atilde; gudu.';
$t[53] = 'Kuma ba ka zamo mai shiryar da mak&atilde;fi ba daga ɓatarsu, b&atilde; ka jiyarwa f&atilde;ce wanda ke yin ĩm&atilde;ni da &atilde;y&otilde;yinMu, watau sũ ne m&atilde;su mĩƙa wuya, su sallama.';
$t[54] = 'Allah ne Ya halitta ku daga rauni, sa\'an nan Ya sanya wani ƙarfi a bayan wani rauni, sa\'an nan Ya sanya wani rauni da furfura a b&atilde;yan wani ƙarfi, Allah na halitta abin da Ya so, kuma Shĩ ne Mai ilmi, Mai ĩkon yi,';
$t[55] = 'Kuma a r&atilde;nar da Sa\'a ke tsayuwa, m&atilde;su laifi na rantsuw&atilde;: Ba su zauna a cikin kabari ba f&atilde;ce s&atilde;\'a guda. Kamar haka suka kasance an&atilde; karkatar da su.';
$t[56] = 'Kuma waɗanda aka bai wa ilmi da ĩm&atilde;ni suka ce: &quot;Lalle, haƙĩƙa kun zauna a cikin Litt&atilde;fin Allah, har zuwa r&atilde;nar t&atilde;yarwa, to, kuma wannan ita ce r&atilde;nar t&atilde;yarwar, kuma amma kũ, kun kasance ba ku sani ba.&quot;';
$t[57] = 'To, a r&atilde;nar da uzurin waɗanda suka yi z&atilde;lunci b&atilde; ya amfaninsu, kuma b&atilde; a neman yardarsu.';
$t[58] = 'Kuma lalle tabbas Mun buga k&otilde;wane irin mis&atilde;li ga mut&atilde;ne a cikin wannan Alƙur\'&atilde;ni, kuma lalle idan k&atilde; je musu da k&otilde;wace &atilde;y&atilde;, lalle, waɗanda suka k&atilde;firta z&atilde; su ce &quot;Kũ, b&atilde; k&otilde;me kuke ba f&atilde;ce m&atilde;su barua.&quot;';
$t[59] = 'Kamar haka Allah Yake sh&atilde;fe haske a kan zuk&atilde;tan waɗanda ba su sani ba.';
$t[60] = 'Sab&otilde;da haka ka yi haƙuri lalle wa\'adin Allah gaskiya ne, kuma kada waɗanda b&atilde; su da yaƙĩni su sassabce maka hankali.';
