<?php
$t = array();
$t[1] = 'سه&zwnj;رنجی سه&zwnj;ره&zwnj;تای سووره&zwnj;تی (البقرة&zwnj;) بده&zwnj;، ئه&zwnj;و ئایه&zwnj;ت و فه&zwnj;رمانانه&zwnj;ی (له&zwnj;م سووره&zwnj;ته&zwnj;دا هه&zwnj;یه&zwnj;) چه&zwnj;ند ئایه&zwnj;ت و فه&zwnj;رمانێکی ئه&zwnj;و قورئانه&zwnj;پڕ له&zwnj;دانایی یه&zwnj;یه&zwnj;.';
$t[2] = 'ئایا چ سه&zwnj;ر سوڕمانێکی بۆ خه&zwnj;ڵکی تێدایه&zwnj;که&zwnj;وه&zwnj;حی و نیگامان نارد بۆ پیاوێک له&zwnj;خۆیان که: خه&zwnj;ڵکی&zwnj;بێدار بکه&zwnj;ره&zwnj;وه&zwnj;و دایان بچڵه&zwnj;کێنه (تا خواناس و خواپه&zwnj;ره&zwnj;ست بن) و مژده&zwnj;بده&zwnj;به&zwnj;وانه&zwnj;ی باوه&zwnj;ڕیان هێناوه&zwnj;که&zwnj;بێگومان پله&zwnj;و پایه&zwnj;ی به&zwnj;رزو بڵندیان بۆ هه&zwnj;یه&zwnj;لای په&zwnj;روه&zwnj;ردگاریان وتیان: به&zwnj;ڕاستی ئه&zwnj;م (محمد ناوه&zwnj;) جادووگه&zwnj;رێکی ئاشکرایه&zwnj;.';
$t[3] = 'به&zwnj;ڕاستی په&zwnj;روه&zwnj;ردگاری ئێوه (الله&zwnj;) یه&zwnj;، ئه&zwnj;و زاته&zwnj;یه که&zwnj;ئاسمانه&zwnj;کان و زه&zwnj;وی له&zwnj;شه&zwnj;ش ڕۆژدا دروست کردووه&zwnj;، له&zwnj;وه&zwnj;ودوا له&zwnj;سه&zwnj;ر ته&zwnj;ختی فه&zwnj;رمانڕه&zwnj;وایی وه&zwnj;ستا (ئه&zwnj;ڵبه&zwnj;ته&zwnj;چۆنێتی وه&zwnj;ستان و ته&zwnj;ختی فه&zwnj;رمانڕه&zwnj;وایه&zwnj;تی و گه&zwnj;لێ شتی له&zwnj;و بابه&zwnj;تانه له&zwnj;ڕاده&zwnj;ی بۆچوونی ئێمه به&zwnj;ده&zwnj;ره&zwnj;، ته&zwnj;فسیری ئه&zwnj;م ئایه&zwnj;ته&zwnj;یان له&zwnj;پێشه&zwnj;وا مالیک و ئه&zwnj;حمه&zwnj;د پرسیووه&zwnj;، له&zwnj;وه&zwnj;ڵامدا وتوویانه&zwnj;: وه&zwnj;ستانه&zwnj;که حه&zwnj;قیقه&zwnj;تی هه&zwnj;یه&zwnj;، چۆنیه&zwnj;تیه&zwnj;که&zwnj;ی شاراوه&zwnj;یه&zwnj;، پرسیار کردن ده&zwnj;رباره&zwnj;ی بیدعه&zwnj;یه&zwnj;، باوه&zwnj;ڕ بوون پێی واجبه&zwnj;)، هه&zwnj;ر خۆیشی پلانی هه&zwnj;موو شتێک دائه&zwnj;نێت و هه&zwnj;موو شتێک ڕێک ئه&zwnj;خات، هیچ که&zwnj;س نیه شه&zwnj;فاعه&zwnj;تخواز و تکا کار بێت، مه&zwnj;گه&zwnj;ر دوای مۆڵه&zwnj;تی ئه&zwnj;و زاته&zwnj;، ئه&zwnj;و خوایه په&zwnj;روه&zwnj;ردگارتانه&zwnj;، که&zwnj;وابوو بیپه&zwnj;رستن، جا ئایا بۆچی یاده&zwnj;وه&zwnj;ری وه&zwnj;رناگرن و بیر ناکه&zwnj;نه&zwnj;وه؟!.';
$t[4] = 'گه&zwnj;ڕانه&zwnj;وه&zwnj;ی هه&zwnj;ر هه&zwnj;مووتان بۆ لای ئه&zwnj;و زاته&zwnj;یه&zwnj;، به&zwnj;ڵێنی خوا هه&zwnj;میشه ڕاسته&zwnj;و دروسته&zwnj;و نه&zwnj;گۆڕه&zwnj;، به&zwnj;ڕاستی خۆی سه&zwnj;ره&zwnj;تا دروست کراوان به&zwnj;دی ده&zwnj;هێنێت و دوایش وه&zwnj;ک خۆیان لێده&zwnj;کاته&zwnj;وه (تا له&zwnj;قیامه&zwnj;تدا) ئه&zwnj;وانه&zwnj;ی که&zwnj;باوه&zwnj;ڕیان هێناوه&zwnj;و کارو کرده&zwnj;وه چاکه&zwnj;کانیان ئه&zwnj;نجامداوه&zwnj;به&zwnj;دادپه&zwnj;روه&zwnj;ری پاداشتیان بداته&zwnj;وه&zwnj;، ئه&zwnj;وانه&zwnj;ش که&zwnj;بێ باوه&zwnj;ڕ بوون خواردنه&zwnj;وه&zwnj;ی له&zwnj;کوڵ و سزای به&zwnj;ئێش و ئازاریان بۆ ئاماده&zwnj;یه به&zwnj;هۆی ئه&zwnj;وه&zwnj;ی که&zwnj;باوه&zwnj;ڕیان پێ نه&zwnj;ئه&zwnj;کرد.';
$t[5] = 'خوا ئه&zwnj;و زاته&zwnj;یه&zwnj;که&zwnj;خۆری کردووه&zwnj;به&zwnj;سه&zwnj;رچاوه&zwnj;ی تیشک و ڕووناکیه&zwnj;کی پرشنگدار، مانگیشی کردووه به&zwnj;سه&zwnj;رچاوه&zwnj;ی ڕووناکی، چه&zwnj;نده&zwnj;ها جێگه&zwnj;ی هه&zwnj;ڵهاتن و ئاوابوونیشی بۆ دیاری کردووه (جگه له&zwnj;وه&zwnj;ی هه&zwnj;رشه&zwnj;وه به&zwnj;شێوه&zwnj;یه&zwnj;ک خۆی ده&zwnj;نوێنێت) تا بزانن و بیکه&zwnj;نه بنه&zwnj;مای ساڵژمێری و حساب ڕاگرتن، خوای گه&zwnj;وره ئه&zwnj;وه&zwnj;ی به&zwnj;بێ مه&zwnj;به&zwnj;ست دروست نه&zwnj;کردووه&zwnj;، به&zwnj;ڵکو هه&zwnj;مووی له&zwnj;سه&zwnj;ر بنچینه&zwnj;ی حه&zwnj;ق و ڕاستی به&zwnj;دیهێناوه&zwnj;، به&zwnj;و شێوه&zwnj;یه&zwnj;ئه&zwnj;و زاته ئایه&zwnj;ت و به&zwnj;ڵگه&zwnj;و موعجیزه&zwnj;کانی ڕوون ده&zwnj;کاته&zwnj;وه بۆ که&zwnj;سانێک که&zwnj;بیانزانن و تێی بگه&zwnj;ن.';
$t[6] = 'به&zwnj;ڕاستی له&zwnj;جیاوازی شه&zwnj;وو ڕۆژو ئه&zwnj;و شتانه&zwnj;دا که&zwnj;خوا له&zwnj;ئاسمانه&zwnj;کان و زه&zwnj;ویدا دروستی کردوون، به&zwnj;ڵگه&zwnj;و نیشانه&zwnj;ی زۆرن بۆ که&zwnj;سانێک که&zwnj;له&zwnj;خوا بترسن و دینداری بکه&zwnj;ن.';
$t[7] = 'به&zwnj;ڕاستی ئه&zwnj;وانه&zwnj;ی که&zwnj;به&zwnj;ته&zwnj;مای ئاماده&zwnj;بوونی به&zwnj;ر باره&zwnj;گای ئێمه&zwnj;نین و (باوه&zwnj;ڕیان پێی نیه&zwnj;) و ڕازی بوون به&zwnj;ژیانی ئه&zwnj;م دنیایه&zwnj;و دڵنیا بوون پێی و دڵیان پێی خۆشبوو و ئه&zwnj;وانه&zwnj;ش که&zwnj;خۆیان غافڵ و بێ ئاگان له&zwnj;ئایه&zwnj;ت و نهێنی دروست کراوه&zwnj;کانی ئێمه&zwnj;، (سه&zwnj;رنجی ناده&zwnj;ن و بیری لێناکه&zwnj;نه&zwnj;وه&zwnj;)...';
$t[8] = 'ئا ئه&zwnj;وانه&zwnj;شوێن و جێگه&zwnj;یان ئاگره به هۆی ئه&zwnj;و کار و کرده&zwnj;وانه&zwnj;ی که ده&zwnj;یانکرد.';
$t[9] = 'به&zwnj;ڕاستی ئه&zwnj;وانه&zwnj;ی که&zwnj;باوه&zwnj;ڕیان هێناوه&zwnj;و کارو کرده&zwnj;وه&zwnj;چاکه&zwnj;کانیان ئه&zwnj;نجامداوه&zwnj;، په&zwnj;روه&zwnj;ردگاریان به&zwnj;هۆی ئیمان و باوه&zwnj;ڕی (ڕێک و پێک و دامه&zwnj;زراویانه&zwnj;وه&zwnj;) هیدایه&zwnj;ت و ڕێنموییان ده&zwnj;کات (بۆ ئه&zwnj;نجامدانی هه&zwnj;موو کارو کرده&zwnj;وه&zwnj;یه&zwnj;کی چاک، له&zwnj;و جیهانیشدا) له&zwnj;ناو به&zwnj;هه&zwnj;شته جۆراو جۆره&zwnj;پڕ له&zwnj;نازو نیعمه&zwnj;ته&zwnj;کاندا چه&zwnj;نده&zwnj;ها ڕوبار به&zwnj;به&zwnj;رده&zwnj;میاندا ده&zwnj;ڕوات.';
$t[10] = 'دوعاو پاڕانه&zwnj;وه&zwnj;یان (له&zwnj;به&zwnj;هه&zwnj;شدا ئه&zwnj;مه&zwnj;یه&zwnj;: په&zwnj;روه&zwnj;ردگارا، پاکی و بێگه&zwnj;ردی و ستایش شایسته&zwnj;ی تۆیه ئه&zwnj;ی خوای میهره&zwnj;بان، چاک و چۆنیشیان له&zwnj;وێ سه&zwnj;لامکردنه&zwnj;، دواهه&zwnj;مین گوفتاریشیان دوای هه&zwnj;موو نازو نیعمه&zwnj;تێک، دوای هه&zwnj;موو به&zwnj;خششێک ئه&zwnj;وه&zwnj;یه&zwnj;که&zwnj;: پڕ به&zwnj;دڵ، له&zwnj;ناخی ده&zwnj;روونیانه&zwnj;وه به&zwnj;ڕاستی ده&zwnj;ڵێن: الحمد لله رب العالمین.';
$t[11] = 'ئه&zwnj;گه&zwnj;ر خوا خه&zwnj;ڵکی تووشی شه&zwnj;ڕو ناخۆشی به&zwnj;زوویی بکردایه (له&zwnj;سه&zwnj;ر داخوازی خۆیان کاتێک ته&zwnj;نگه&zwnj;تاو ده&zwnj;بن) هه&zwnj;روه&zwnj;کو چۆن بۆ داخوازی خێر په&zwnj;له&zwnj;یانه&zwnj;، ئه&zwnj;وه هه&zwnj;ر هه&zwnj;موویان کۆتایی به&zwnj;ژیانیان ده&zwnj;هێنرا (به&zwnj;ڵام خوای گه&zwnj;وره واناکات، به&zwnj;ڵکو ده&zwnj;فه&zwnj;رموێت) جا ئه&zwnj;وانه&zwnj;ی به&zwnj;ته&zwnj;مانین ئاماده&zwnj;ی به&zwnj;رده&zwnj;م باره&zwnj;گای ئێمه&zwnj;ببنه&zwnj;وه (باوه&zwnj;ڕیان پێی نیه&zwnj;)، ماوه&zwnj;یه&zwnj;ک وازیان لێده&zwnj;هێنین تا له&zwnj;سته&zwnj;میاندا ڕۆبچن و سه&zwnj;رگه&zwnj;ردان بن.';
$t[12] = 'کاتێکیش ئاده&zwnj;میزاد (ئه&zwnj;وانه&zwnj;یان که&zwnj;باوه&zwnj;ڕ له&zwnj;ده&zwnj;روونیاندا به&zwnj;چاکی دانه&zwnj;مه&zwnj;زراوه&zwnj;) زیان و ناخۆشیه&zwnj;کی تووش بێت، ئه&zwnj;وه هاناو هاوار بۆ ئێمه ده&zwnj;هێنێت بۆ لابردنی، له&zwnj;کاتی ڕاکشاندا، یان به&zwnj;دانیشتنه&zwnj;وه&zwnj;، یان به&zwnj;پێوه&zwnj;بێت، جا کاتێک زه&zwnj;ره&zwnj;ر و زیانه&zwnj;که&zwnj;ی لێ دوور ده&zwnj;خه&zwnj;ینه&zwnj;وه&zwnj;و ده&zwnj;که&zwnj;وێته&zwnj;وه باری ئاسایی، هه&zwnj;روا ده&zwnj;ڕوات به&zwnj;ڕێدا و بارودۆخی ئه&zwnj;وسای فه&zwnj;رامۆش ده&zwnj;کات، هه&zwnj;روه&zwnj;ک ڕۆژێ له&zwnj;ڕۆژان هاواری له ئێمه نه&zwnj;کردبێت و ل ه&zwnj;ئێمه نه&zwnj;پاڕابێته&zwnj;وه تا ناخۆشی و زیانێک که&zwnj;تووشیبووه لێی دوور بخه&zwnj;ینه&zwnj;وه&zwnj;، ئا به&zwnj;و شێوه&zwnj;یه بۆ له&zwnj;سنوور ده&zwnj;رچووان ئه&zwnj;و کارو کرده&zwnj;وانه&zwnj;ی که&zwnj;ده&zwnj;یانکرد ڕازێنراوه&zwnj;ته&zwnj;وه&zwnj;.';
$t[13] = 'سوێند به&zwnj;خوا بێگومان ئێمه له&zwnj;سه&zwnj;ده&zwnj;کانی پێش ئێوه&zwnj;دا خه&zwnj;ڵکێکی زۆرمان له&zwnj;ناو بردووه کاتێک سته&zwnj;میان کردووه&zwnj;و ڕۆچوون تیایدا، له&zwnj;کاتێکیشدا پێغه&zwnj;مبه&zwnj;ره&zwnj;کانیان به&zwnj;به&zwnj;ڵگه&zwnj;ی زۆره&zwnj;وه بۆ هات و (په&zwnj;یامی خوایان پێ ڕاگه&zwnj;یاندن) وه&zwnj;نه&zwnj;بوو باوه&zwnj;ڕیشیان پێبهێنن، (ئێمه&zwnj;ش تۆڵه&zwnj;مان لێسه&zwnj;ندن) ئا به&zwnj;و شێوه&zwnj;یه تۆڵه له&zwnj;قه&zwnj;وم و گه&zwnj;لانی تاوانبار ده&zwnj;که&zwnj;ینه&zwnj;وه.';
$t[14] = 'پاشان ئێمه ئێوه&zwnj;مان کرده جێنشین له&zwnj;زه&zwnj;ویدا له&zwnj;دوای له&zwnj;ناوبردنی ئه&zwnj;وان، تا ببینین ئێوه چی ده&zwnj;که&zwnj;ن و چۆن ده&zwnj;که&zwnj;ن!';
$t[15] = 'کاتێکیش ئایه&zwnj;ته&zwnj;کانی ئێمه که ڕوون و ئاشکرایه به&zwnj;سه&zwnj;ریاندا بخوێنرێته&zwnj;وه&zwnj;، ئه&zwnj;وه ئه&zwnj;وانه&zwnj;ی که به&zwnj;ته&zwnj;مانین ئاماده&zwnj;ی به&zwnj;رده&zwnj;م باره&zwnj;گای ئێمه ببن ده&zwnj;ڵێن: قورئانێکی جگه له&zwnj;مه بهێنه&zwnj;و بیخوێنه&zwnj;ره&zwnj;وه&zwnj;، یان بیگۆڕه (به&zwnj;شێوه&zwnj;یه&zwnj;کی تر)، تۆیش ئه&zwnj;ی محمد (صلی الله علیه وسلم) پێیان بڵێ: من بۆم نیه له&zwnj;خۆمه&zwnj;وه ئه&zwnj;وه بیگۆڕم، من شوێنی هیچ شتێک ناکه&zwnj;وم جگه ئه&zwnj;وه نه&zwnj;بێت که به&zwnj;وه&zwnj;حی پێم ده&zwnj;گات، به&zwnj;ڕاستی من ده&zwnj;ترسم ئه&zwnj;گه&zwnj;ر یاخی بم له په&zwnj;روه&zwnj;ردگارم و نا فه&zwnj;رمانی بکه&zwnj;م گیرۆده&zwnj;ی سزایه&zwnj;کی سه&zwnj;ختی ڕۆژێکی زۆر گه&zwnj;وره&zwnj;ببم.';
$t[16] = 'پێیان بڵێ (ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر صلی الله علیه وسلم) ئه&zwnj;گه&zwnj;ر خوا بیویستایه ئه&zwnj;وه قورئانم به&zwnj;سه&zwnj;ردا نه&zwnj;ده&zwnj;خوێنده&zwnj;وه&zwnj;، خوایش ئاگاداری نه&zwnj;ده&zwnj;کردن پێی، چونکه به&zwnj;ڕاستی ئاشکرایه که&zwnj;من ته&zwnj;مه&zwnj;نێکی دوورو درێژم له&zwnj;ناوتاندا به&zwnj;سه&zwnj;ر بردووه پێش هاتنی قورئان (بێ ئه&zwnj;وه&zwnj;ی وشه&zwnj;یه&zwnj;ک له&zwnj;م قورئانه بزانم) که&zwnj;واته بۆچی عه&zwnj;قڵ وبیرو هۆشتان ناخه&zwnj;نه&zwnj;کار (که&zwnj;سێکی نه&zwnj;خوێنده&zwnj;وار تا ته&zwnj;مه&zwnj;نی چل ساڵی هیچ نه&zwnj;ڵێت، به&zwnj;مه&zwnj;رجێک ناو بانگی ده&zwnj;رکرد بێت به&zwnj;ڕاستگۆیی و ئه&zwnj;مینداری، که&zwnj;چی له&zwnj;و ته&zwnj;مه&zwnj;نه به&zwnj;دواوه ئه&zwnj;م قورئانه ڕێک و پێکه ڕابگه&zwnj;یه&zwnj;نێت، ئایا ڕه&zwnj;وا نیه هه&zwnj;ر که&zwnj;س ده&zwnj;یبیستێت خێرا بڕوا بهێنێت به&zwnj;نێره&zwnj;ری قورئان و ئه&zwnj;و که&zwnj;سه&zwnj;ش که&zwnj;بۆی ڕه&zwnj;وانه&zwnj;کراوه&zwnj;؟)';
$t[17] = 'جا کێ له&zwnj;وه سته&zwnj;مکارتره که درۆ به ده&zwnj;م خواوه هه&zwnj;ڵده&zwnj;به&zwnj;ستێت، یاخود ئایاته&zwnj;کانی به درۆ بزانێت و بڕوای پێی نه&zwnj;بێت، به&zwnj;ڕاستی تاوانباران سه&zwnj;رفراز نابن.';
$t[18] = '(بێ باوه&zwnj;ڕان وخوانه&zwnj;ناسان) شتێک ده&zwnj;په&zwnj;رستن له&zwnj;جیاتی خوا نه زیانیان پێده&zwnj;گه&zwnj;یه&zwnj;نێت نه قازانج، ده&zwnj;شڵێن: ئا ئه&zwnj;و بت و شتانه تکا کارمانن لای خوا، پێیان بڵێ ئه&zwnj;ی محمد (صلی الله علیه وسلم): باشه ئایا ئێوه خوا ئاگادار ده&zwnj;که&zwnj;نه&zwnj;وه به&zwnj;شتێک که له&zwnj;ئاسمانه&zwnj;کان و له&zwnj;زه&zwnj;ویدا هه&zwnj;بێت و خوا نه&zwnj;یزانێت؟! پاک و بێگه&zwnj;رده&zwnj;و به&zwnj;رزو بڵنده&zwnj;و دووره له&zwnj;وه&zwnj;ی که&zwnj;نه&zwnj;فامان ده&zwnj;یکه&zwnj;ن به&zwnj;هاوه&zwnj;ڵی.';
$t[19] = 'خه&zwnj;ڵکی له&zwnj;سه&zwnj;ره&zwnj;تای په&zwnj;یدا بوونیاندا به&zwnj;ش به&zwnj;ش نه&zwnj;بوون به&zwnj;ڵکو ته&zwnj;نها یه&zwnj;ک پارچه&zwnj;بوون، یه&zwnj;ک ئاین بوون، یه&zwnj;ک بیرو باوه&zwnj;ڕ بوون، جا که (زه&zwnj;مان تێپه&zwnj;ڕی و زۆر بوون و له&zwnj;سه&zwnj;رچاوه&zwnj;ی نووری پێغه&zwnj;مبه&zwnj;رایه&zwnj;تی دوورکه&zwnj;وتنه&zwnj;وه&zwnj;) ناکۆکی و جیاوازی له&zwnj;نێوانیاندا په&zwnj;یدا بوو به&zwnj;ش به&zwnj;ش بوون، خۆ ئه&zwnj;گه&zwnj;ر بڕیارداده&zwnj;ی پێشتر له&zwnj;لایه&zwnj;ن په&zwnj;روه&zwnj;ردگارته&zwnj;وه نه&zwnj;بوایه&zwnj;، ئه&zwnj;وه دادپه&zwnj;روه&zwnj;ری هه&zwnj;ر له&zwnj;دنیادا له&zwnj;نێوانیاندا ئه&zwnj;نجامده&zwnj;درا، (حه&zwnj;ق و ڕاستی ڕوون ده&zwnj;کرایه&zwnj;وه&zwnj;) له&zwnj;و شتانه&zwnj;دا که&zwnj;کێشه&zwnj;و جیاوازی تێدا ده&zwnj;که&zwnj;ن.';
$t[20] = '(هه&zwnj;روه&zwnj;ها بێ باوه&zwnj;ڕان) ده&zwnj;ڵێن: بۆ موعجیزه&zwnj;و به&zwnj;ڵگه&zwnj;یه&zwnj;ک دانه&zwnj;به&zwnj;زێنرایه سه&zwnj;ری له&zwnj;لایه&zwnj;ن په&zwnj;روه&zwnj;ردگاریه&zwnj;وه، ئه&zwnj;وه له&zwnj;وه&zwnj;ڵامیاندا بڵێ: به&zwnj;ڕاستی موعجیزه&zwnj;و نهێنی و شاراوه&zwnj;کان ته&zwnj;نها به&zwnj;ده&zwnj;ست خوایه&zwnj;، جا ئێوه چاوه&zwnj;ڕوانی بکه&zwnj;ن، بێگومان منیش له&zwnj;گه&zwnj;ڵتاندا له&zwnj;چاوه&zwnj;ڕوانانم.';
$t[21] = 'کاتێکیش خه&zwnj;ڵکی له&zwnj;دوای ناخۆشیه&zwnj;ک که بۆیان پێش دێت له&zwnj;ڕه&zwnj;حمه&zwnj;ت و به&zwnj;خششی خۆمان به&zwnj;هره&zwnj;وریان ده&zwnj;که&zwnj;ین، ئه&zwnj;وه یه&zwnj;کسه&zwnj;ر پیلانگێڕی کاریانه دژ به&zwnj;ئاین و فه&zwnj;رمانه&zwnj;کانی ئێمه&zwnj;، ئه&zwnj;ی محمد (صلی الله علیه وسلم) پێیان بڵێ: خوا خێراتره له&zwnj;وان له نه&zwnj;خشه&zwnj;و پلاندا، به&zwnj;ڕاستی فریشته&zwnj;کانمان هه&zwnj;موو پیلان و فێڵێک که&zwnj;ده&zwnj;یکه&zwnj;ن تۆماری ده&zwnj;که&zwnj;ن.';
$t[22] = '(ئه&zwnj;ی هاوه&zwnj;ڵگه&zwnj;ڕان) خوا ئه&zwnj;و زاته&zwnj;یه که له&zwnj;وشکانی و ده&zwnj;ریادا هاتو چۆتان پێده&zwnj;کات، هه&zwnj;تا ئه&zwnj;و کاته&zwnj;ی که له&zwnj;که&zwnj;شتیه&zwnj;کاندا ده&zwnj;بن و ده&zwnj;یانگوێزنه&zwnj;وه به&zwnj;هۆی باو شه&zwnj;ماڵێکی پاک و ئارامه&zwnj;وه&zwnj;و هه&zwnj;مووان دڵخۆشن پێی، ئه&zwnj;وه بایه&zwnj;کی توندو به&zwnj;هێز و سه&zwnj;خت به&zwnj;ره&zwnj;و که&zwnj;شتیه&zwnj;که بێت و هه&zwnj;ڵبکات، له&zwnj;هه&zwnj;موو لایه&zwnj;که&zwnj;وه شه&zwnj;پۆل بۆیان بێت، ئیتر وابزانن که ده&zwnj;وره&zwnj;دراون و (ڕزگار بوونیان ئه&zwnj;سته&zwnj;مه&zwnj;، ئا له&zwnj;و کاته&zwnj;دا خه&zwnj;ڵکه&zwnj;که به&zwnj;سۆزه&zwnj;وه&zwnj;) هاناو هاوار بۆ خوا ده&zwnj;به&zwnj;ن، دڵنیان خوا نه&zwnj;بێت که&zwnj;س ناتوانێت فریایان بکه&zwnj;وێت، له&zwnj;کاتێکدا بیرو باوه&zwnj;ڕو ئاینیان به&zwnj;ته&zwnj;واویی پاک و پوخت و یه&zwnj;کلایی ده&zwnj;که&zwnj;نه&zwnj;وه بۆ خوا، بۆیه به&zwnj;سۆزه&zwnj;وه ده&zwnj;ڵێن: سوێند به&zwnj;خوا ئه&zwnj;گه&zwnj;ر له&zwnj;م به&zwnj;ڵایه&zwnj;، له&zwnj;م ته&zwnj;نگانه&zwnj;یه&zwnj;، ڕزگارمان بکه&zwnj;یت، ئه&zwnj;وه به&zwnj;ڕاستی ده&zwnj;چینه ڕیزی سوپاسگوزارانته&zwnj;وه&zwnj;.';
$t[23] = 'جاکاتێک خوا ڕزگاری کردن، خێرا ئه&zwnj;وان به&zwnj;ناحه&zwnj;ق ده&zwnj;ست ده&zwnj;که&zwnj;نه&zwnj;وه به&zwnj;سته&zwnj;مکاری له&zwnj;زه&zwnj;ویدا، ئه&zwnj;ی خه&zwnj;ڵکینه&zwnj;: به&zwnj;ڕاستی سته&zwnj;م کردنتان ته&zwnj;نها له&zwnj;سه&zwnj;ر خۆتان ده&zwnj;که&zwnj;وێت و به&zwnj;زیانی خۆتان ته&zwnj;واو ده&zwnj;بێت، ئێوه به&zwnj;و سته&zwnj;مکاریه خۆشی و ڕابواردنی ژیانی دنیاتان ده&zwnj;وێت، له&zwnj;وه&zwnj;ودوا گه&zwnj;ڕانه&zwnj;وه&zwnj;تان هه&zwnj;ر بۆ لای ئێمه&zwnj;یه&zwnj;و ئه&zwnj;وسا ئاگادارتان ده&zwnj;که&zwnj;ینه&zwnj;وه به&zwnj;هه&zwnj;موو ئه&zwnj;و کارو کرده&zwnj;وانه&zwnj;ی که&zwnj;ده&zwnj;تانکرد.';
$t[24] = 'بێگومان نموونه&zwnj;ی ژیانی ئه&zwnj;م دنیایه&zwnj;، هه&zwnj;ر له&zwnj;و بارانه ده&zwnj;چێت له&zwnj;ئاسمانه&zwnj;وه دامانباراندووه جا به&zwnj;هۆیه&zwnj;وه ڕووه&zwnj;کی زه&zwnj;وی گه&zwnj;شه&zwnj;ده&zwnj;کات و به&zwnj;یه&zwnj;کدا چوون (به&zwnj;رو بوومی خۆی پێشکه&zwnj;ش ده&zwnj;کات) له&zwnj;و&zwnj;خۆراکانه&zwnj;ی که&zwnj;هه&zwnj;م خه&zwnj;ڵکی و هه&zwnj;م ئاژه&zwnj;ڵیش لێی ده&zwnj;خۆن، هه&zwnj;تا کاتێک وای لێ دێت زه&zwnj;وی، به&zwnj;جوانی نه&zwnj;خش و نیگاری خۆی ده&zwnj;رده&zwnj;خات و خۆی ده&zwnj;ڕازێنێته&zwnj;وه&zwnj;، خه&zwnj;ڵکه&zwnj;که&zwnj;ی وا گومان ده&zwnj;به&zwnj;ن به&zwnj;ڕاستی هه&zwnj;میشه ده&zwnj;سه&zwnj;ڵاتداری ته&zwnj;واون به&zwnj;سه&zwnj;ریدا (ئا له&zwnj;وکاته&zwnj;دا که&zwnj;خه&zwnj;ڵکی خراپ و غافڵن و دڵیان به&zwnj;دنیا خۆشه&zwnj;و هه&zwnj;رچی گوناهو تاوانه ده&zwnj;یکه&zwnj;ن) ئه&zwnj;وه فه&zwnj;رمانی ئێمه&zwnj;یان بۆ ده&zwnj;رده&zwnj;چێت له&zwnj;شه&zwnj;ودا یان له&zwnj;ڕۆژدا، ئه&zwnj;و&zwnj;سا وه&zwnj;ک کشتوکاڵی دروێنه&zwnj;کراوی لێ ده&zwnj;که&zwnj;ین، هه&zwnj;روه&zwnj;کو دوێنێ نه&zwnj;بووبێت، ئابه&zwnj;و شێوه&zwnj;یه ئایه&zwnj;ته&zwnj;کانی خۆمان ڕوون ده&zwnj;که&zwnj;ینه&zwnj;وه بۆ که&zwnj;سانێک بیر بکه&zwnj;نه&zwnj;وه&zwnj;و تێفکرن (وا دیاره ڕۆژێک دێت پێشکه&zwnj;وتنی زانستی ده&zwnj;گاته ئاستێکی زۆر باڵا، خه&zwnj;ڵکی ئه&zwnj;و سه&zwnj;رده&zwnj;مه زۆر غه&zwnj;ڕا ده&zwnj;بن، به&zwnj;ڵام له&zwnj;پڕێکدا خوا کۆتایی به&zwnj;هه&zwnj;موو شتێک ده&zwnj;هێنێت).';
$t[25] = 'خوای گه&zwnj;وره&zwnj;ش بانگه&zwnj;واز ده&zwnj;کات بۆ به&zwnj;هه&zwnj;شتی پڕ له&zwnj;شادی و ئاشتی و ئاسایش، هه&zwnj;ر که&zwnj;سێک ئه&zwnj;و زاته بیه&zwnj;وێت (خۆیشی هه&zwnj;وڵ بدات بۆ به&zwnj;ده&zwnj;ستهێنانی) ڕێنموویی ده&zwnj;کات بۆ ڕێگه&zwnj;و ڕێبازی ڕاست و دروست.';
$t[26] = 'بۆ ئه&zwnj;وانه&zwnj;ی چاکه&zwnj;یان کردووه&zwnj;، پاداشتی چاکترو به&zwnj;هه&zwnj;شتی به&zwnj;رین و زیاتریش ئاماده&zwnj;یه به&zwnj;چه&zwnj;نده&zwnj;ها قات، (له&zwnj;ڕیوایه&zwnj;تێکدا که&zwnj;پێشه&zwnj;ویان ئه&zwnj;حمه&zwnj;د و موسلیم ڕیوایه&zwnj;تیان کردووه&zwnj;، ئه&zwnj;و زیاده&zwnj;یه&zwnj;، ته&zwnj;ماشا کردنی ڕوخساری پڕ له&zwnj;نوورو قه&zwnj;شه&zwnj;نگی زاتی په&zwnj;روه&zwnj;ردگاره&zwnj;) ڕووخساریشیان ڕه&zwnj;شی و تاڵی و ڕیسوایی و زه&zwnj;لیلی و ماندوێتی پێوه دیارنییه، ئا ئه&zwnj;وانه نیشته&zwnj;جێی به&zwnj;هه&zwnj;شتن و بۆ هه&zwnj;میشه ژیانی تیادا ده&zwnj;به&zwnj;نه&zwnj;سه&zwnj;ر.';
$t[27] = 'ئه&zwnj;وانه&zwnj;ش گوناهو تاوانه&zwnj;کا&zwnj;نیان ئه&zwnj;نجامداوه&zwnj;، هه&zwnj;رچه&zwnj;نده پاداشتی هه&zwnj;ر گوناهو تاوانێک به&zwnj;قه&zwnj;ده&zwnj;ر خۆی ده&zwnj;درێته&zwnj;وه (به&zwnj;ڵام هه&zwnj;ندێک ئه&zwnj;وه&zwnj;نده تاوانبارن) خه&zwnj;جاڵه&zwnj;تی و سه&zwnj;رشۆڕی و ڕوو ڕه&zwnj;شی ماندووی کردوون، که&zwnj;س نیه په&zwnj;نایان بدات و بیانپارێزێت له (خه&zwnj;شم و قینی) خوا، ئه&zwnj;وه&zwnj;نده ڕوو ڕه&zwnj;شن هه&zwnj;ر ده&zwnj;ڵێی پارچه&zwnj;یه&zwnj;ک له&zwnj;شه&zwnj;وی تاریک ڕووخساری داپۆشیوون، ئائه&zwnj;وانه نیشته&zwnj;جێی ناو ئاگری دۆزه&zwnj;خن و بۆ هه&zwnj;میشه ژیان له&zwnj;ناویدا ده&zwnj;به&zwnj;نه&zwnj;سه&zwnj;ر.';
$t[28] = 'ئه&zwnj;مانه&zwnj;ش له&zwnj;ڕۆژێکدا ده&zwnj;بێت که&zwnj;هه&zwnj;ر هه&zwnj;موو خه&zwnj;ڵکی کۆده&zwnj;که&zwnj;ینه&zwnj;وه بۆ لێپرسینه&zwnj;وه&zwnj;، پاشان به&zwnj;وانه ده&zwnj;ڵێین: که&zwnj;شه&zwnj;ریک و هاوه&zwnj;ڵیان بۆ خوا بڕیارداوه&zwnj;: خۆتان و ئه&zwnj;و شتانه&zwnj;ی که&zwnj;ده&zwnj;تانپه&zwnj;رست، له&zwnj;جێی خۆتان بوه&zwnj;ستن و مه&zwnj;جوڵێن، ئه&zwnj;وسا موشریکه&zwnj;کان له&zwnj;په&zwnj;رستراوه&zwnj;کانیان جیا ده&zwnj;که&zwnj;ینه&zwnj;وه، خوا بت و په&zwnj;رستراوه&zwnj;کان، ده&zwnj;هێنێته قسه&zwnj;و ئه&zwnj;وانیش به یه&zwnj;ک ده&zwnj;نگ ده&zwnj;ڵێن: ئێوه ئێمه&zwnj;تان نه&zwnj;ئه&zwnj;په&zwnj;رست!!';
$t[29] = 'ئینجا خوا به&zwnj;سه که&zwnj;شایه&zwnj;ت بێت له&zwnj;نێوان ئێمه و ئێوه&zwnj;دا، به&zwnj;ڕاستی ئێمه ئاگاداری په&zwnj;رستن و پاڕانه&zwnj;وه&zwnj;ی ئێوه نه&zwnj;بووین.';
$t[30] = 'ئا له&zwnj;وێدا (له&zwnj;گۆڕه&zwnj;پانی مه&zwnj;حشه&zwnj;رو لێپرسینه&zwnj;وه&zwnj;دا) هه&zwnj;موو که&zwnj;س ده&zwnj;رئه&zwnj;نجامی کاروکرده&zwnj;وه&zwnj;ی ڕابووردووی هه&zwnj;ست پێ ده&zwnj;کات که&zwnj;پێش خۆی ڕه&zwnj;وانه&zwnj;ی کردووه&zwnj;، ئه&zwnj;وسا هه&zwnj;مووان ده&zwnj;گه&zwnj;ڕێنرێنه&zwnj;وه بۆ لای خوا، خاوه&zwnj;نی ڕاسته&zwnj;قینه&zwnj;و په&zwnj;روه&zwnj;ردگاری حه&zwnj;ق، هه&zwnj;ر چیه&zwnj;کیش به&zwnj;ناحه&zwnj;ق ده&zwnj;یانپه&zwnj;رست و هه&zwnj;ڵیانده&zwnj;به&zwnj;ست و پێی هه&zwnj;ڵخه&zwnj;ڵه&zwnj;تاو بوون، ون ده&zwnj;بێت و له&zwnj;ناو ده&zwnj;چێت..';
$t[31] = '(ئه&zwnj;ی پێعه&zwnj;مبه&zwnj;ر صلی الله علیه وسلم پێیان بڵێ): کێ هه&zwnj;یه له&zwnj;ئاسمانه&zwnj;وه (به&zwnj;هۆی تیشکی خۆرو بارانه&zwnj;وه&zwnj;) و له&zwnj;زه&zwnj;ویه&zwnj;وه (به&zwnj;هۆی خاک و ئاوو هه&zwnj;واوه&zwnj;) ڕزق و ڕۆزیتان بۆ فه&zwnj;راهه&zwnj;م بهێنێت و پێتان ببه&zwnj;خشێت، ئه&zwnj;ی کێ هه&zwnj;یه&zwnj;خاوه&zwnj;نی ده&zwnj;زگاکانی بیستن و بینین بێت (توانای بیستن و بینینیان پێ ببه&zwnj;خشێت) ئه&zwnj;ی کێ زیندوو له&zwnj;مردوو ده&zwnj;رده&zwnj;هێنێت و مردوو له&zwnj;زیندوو ده&zwnj;رده&zwnj;هێنێت، هه&zwnj;رواها کێ&zwnj;یه که&zwnj;هه&zwnj;موو کارو فرمانه&zwnj;کان جێبه&zwnj;جێ ده&zwnj;کات و گۆڕانکاریان به&zwnj;سه&zwnj;ردا ده&zwnj;هێنێت (له&zwnj;به&zwnj;رامبه&zwnj;ر هه&zwnj;موو ئه&zwnj;و پرسیارانه&zwnj;وه&zwnj;) خه&zwnj;ڵکی ده&zwnj;ڵێن: الله&zwnj;، خوا (هه&zwnj;موو ئه&zwnj;و کارانه&zwnj;و زیاتریش زۆر به&zwnj;ئاسانی ئه&zwnj;نجام ده&zwnj;دات له&zwnj;ڕێگه&zwnj;ی یاساو به&zwnj;رنامه&zwnj;یه&zwnj;که&zwnj;وه&zwnj;که&zwnj;هه&zwnj;ر خۆی دایڕشتووه&zwnj;)، که&zwnj;واته&zwnj;پێیان بڵێ: ئایا ناترسن له&zwnj;سه&zwnj;ره&zwnj;نجامی یاخی بوونتان، ئایا ناترسن له&zwnj;خه&zwnj;شم و قینی خوا؟!';
$t[32] = 'جا هه&zwnj;ر ئه&zwnj;و زاته&zwnj;یه په&zwnj;روه&zwnj;ردگاری ڕاست و دروستتان، (باشه&zwnj;) دوای حه&zwnj;ق و ڕاستی چی هه&zwnj;یه جگه له&zwnj;گومڕایی و سه&zwnj;ر گه&zwnj;ردانی؟ (ئیتر ئێوه بۆ بیرناکه&zwnj;نه&zwnj;وه&zwnj;) چۆن لاده&zwnj;درێن (له&zwnj;ڕێبازی حه&zwnj;قه&zwnj;وه بۆ ڕێبازی به&zwnj;تاڵ)؟!';
$t[33] = 'ئا به&zwnj;و شێوه&zwnj;یه (چونکه له&zwnj;گه&zwnj;ڵ ئه&zwnj;و هه&zwnj;موو به&zwnj;ڵگه&zwnj;یه&zwnj;دا هه&zwnj;ر تاوانبارن و سه&zwnj;ر که&zwnj;شی ده&zwnj;که&zwnj;ن) بڕیاری په&zwnj;روه&zwnj;ردگارت له&zwnj;سه&zwnj;ر ئه&zwnj;وانه&zwnj;ی که له&zwnj;سنوور ده&zwnj;رچوون بڕاوه&zwnj;ته&zwnj;وه&zwnj;، که ئه&zwnj;وانه به&zwnj;ڕاستی هه&zwnj;ر ئیمان و باوه&zwnj;ڕناهێنن.';
$t[34] = '(ئه&zwnj;ی محمد صلی الله علیه وسلم) پێیان بڵێ: باشه ئایا له&zwnj;و شتانه&zwnj;ی که ئێوه له&zwnj;جیاتی خوا (ده&zwnj;یپه&zwnj;رستن) که&zwnj;سێک هه&zwnj;یه که بتوانێت شتێک دروست بکات له&zwnj;سه&zwnj;ره&zwnj;تاوه&zwnj;، یان ئه&zwnj;گه&zwnj;ر دروستی کردوو دوایی نه&zwnj;ما، ئایا ده&zwnj;توانێت سه&zwnj;ر له&zwnj;نوێ بیهێنێته&zwnj;وه کایه&zwnj;؟! ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر (صلی الله علیه وسلم) تۆ بڵێ: هه&zwnj;ر خوا، الله&zwnj;، هه&zwnj;ر ئه&zwnj;و ده&zwnj;توانێت له&zwnj;سه&zwnj;ره&zwnj;تاو نه&zwnj;بوونه&zwnj;وه خه&zwnj;ڵکی و هه&zwnj;موو شت دروست بکات و پاشان دوای مردن و نه&zwnj;مان دووباره&zwnj;ی بکاته&zwnj;ه&zwnj;وه&zwnj;، که&zwnj;وابوو چۆن لائه&zwnj;درێن له&zwnj;حه&zwnj;ق به&zwnj;هۆی درۆو پڕوپاگه&zwnj;نده&zwnj;وه&zwnj;؟!';
$t[35] = 'هه&zwnj;روه&zwnj;ها پێیان بڵێ: ئایا که&zwnj;سێک یان شتێک هه&zwnj;یه له په&zwnj;رستراوه&zwnj;کانتان، هیدایه&zwnj;ت و ڕێنموویی بکات بۆ ڕێبازی حه&zwnj;ق و ڕاستی (بێگومان نه&zwnj;خێر) ده&zwnj;ی پێیان بڵێ: خوا ڕێنموویی بۆ حه&zwnj;ق و ڕاستی ده&zwnj;کات، جا ئایا که&zwnj;سێک ڕێنموویی بکات بۆ حه&zwnj;ق و ڕاستی شایسته&zwnj;تره که&zwnj;په&zwnj;یڕه&zwnj;وی بکرێ یان که&zwnj;سێک ڕێنمووی وه&zwnj;رناگرێت مه&zwnj;گه&zwnj;ر ڕێنموویی بکرێ؟ ئه&zwnj;وه چیتانه چۆن بڕیاری وا ده&zwnj;ده&zwnj;ن؟ فه&zwnj;رمانی وا ده&zwnj;رده&zwnj;که&zwnj;ن.؟';
$t[36] = 'زۆر به&zwnj;یان شوێنی هیچ شتێک ناکه&zwnj;ون ته&zwnj;نها گومان نه&zwnj;بێت، به&zwnj;ڕاستی گومانیش هیچ سوودێک به&zwnj;خاوه&zwnj;نه&zwnj;که&zwnj;ی ناگه&zwnj;یه&zwnj;نێت له&zwnj;باره&zwnj;ی حه&zwnj;ق و ڕاستیه&zwnj;وه&zwnj;، به&zwnj;ڕاستی خوا زانایه به&zwnj;هه&zwnj;موو ئه&zwnj;و کارو کرده&zwnj;وانه&zwnj;ی که&zwnj;ئه&zwnj;نجامی ده&zwnj;ده&zwnj;ن.';
$t[37] = 'وه&zwnj;نه&zwnj;بێت ئه&zwnj;م قورئانه له&zwnj;لایه&zwnj;ن که&zwnj;سێکه&zwnj;وه داڕێژرا بێت و هه&zwnj;ڵبه&zwnj;سترا بێت جگه له&zwnj;خوا، به&zwnj;ڵکو ڕاستی و ڕه&zwnj;وانی کتێبه ئاسمانیه&zwnj;کانی پێش خۆشی دووپات ده&zwnj;کاته&zwnj;وه&zwnj;، هه&zwnj;روه&zwnj;ها ڕوونکه&zwnj;ره&zwnj;وه&zwnj;ی بیرو باوه&zwnj;ڕو ئه&zwnj;رکه&zwnj;کانه&zwnj;، به&zwnj;ڕاستی هیچ گومان له&zwnj;وه&zwnj;دا نیه که ئه&zwnj;م قورئانه&zwnj;له&zwnj;لایه&zwnj;ن په&zwnj;روه&zwnj;ردگاری هه&zwnj;موو جیهانه&zwnj;وه ڕه&zwnj;وانه&zwnj;کراوه&zwnj;.';
$t[38] = 'به&zwnj;ڵکو (له&zwnj;گه&zwnj;ڵ ئه&zwnj;و هه&zwnj;موو به&zwnj;ڵگانه&zwnj;دا) ئه&zwnj;وانه ده&zwnj;ڵێن که محمد (صلی الله علیه وسلم) قورئانی هه&zwnj;ڵبه&zwnj;ستووه&zwnj;، بڵێ: ده&zwnj;ئێوه&zwnj;ش سووره&zwnj;تێک وه&zwnj;ک سووره&zwnj;ته&zwnj;کانی داڕێژن و بیهێنن، داوا بکه&zwnj;ن له&zwnj;هه&zwnj;ر که&zwnj;سێکی تر که ده&zwnj;توانن جگه له&zwnj;خوا (که&zwnj;یارمه&zwnj;تیتان بدات) درێغی مه&zwnj;که&zwnj;ن ئه&zwnj;گه&zwnj;ر ڕاستگۆن.';
$t[39] = 'به&zwnj;ڵکو سه&zwnj;ره&zwnj;ڕای ئه&zwnj;و هه&zwnj;موو به&zwnj;ڵگانه ئه&zwnj;وانه بڕوایان به&zwnj;قورئان نه&zwnj;کردووه که&zwnj;هیچ شتێکیان نه&zwnj;زانیووه ده&zwnj;رباره&zwnj;ی، هێشتا بۆیان نه&zwnj;هاتووه سه&zwnj;رئه&zwnj;نجامی ئه&zwnj;و به&zwnj;ڵێنانه&zwnj;ی که به&zwnj;ڵێنی پێداون، هه&zwnj;ر به&zwnj;و شێوه&zwnj;یه ئه&zwnj;وانه&zwnj;ی پێش ئه&zwnj;مان به&zwnj;رنامه&zwnj;ی خوایان به&zwnj;درۆزانی و بڕوایان پێی نه&zwnj;بوو، ده&zwnj;ته&zwnj;ماشا بکه&zwnj;و سه&zwnj;رنج بده بزانه سه&zwnj;رئه&zwnj;نجامی سته&zwnj;مکاران چۆن بووه&zwnj;.';
$t[40] = 'له&zwnj;و خه&zwnj;ڵکه&zwnj;دا که&zwnj;سانێک هه&zwnj;ن ئیمان و باوه&zwnj;ڕ به&zwnj;قورئان ئه&zwnj;هێنن و هه&zwnj;شیانه ئیمان و باوه&zwnj;ڕی پێناهێنێت، په&zwnj;روه&zwnj;ردگاری تۆیش هه&zwnj;میشه ئاگاداره به&zwnj;وانه&zwnj;ی که&zwnj;تۆوی خراپه ده&zwnj;چێنن.';
$t[41] = 'ئه&zwnj;گه&zwnj;ر بڕواشیان پێنه&zwnj;کردیت و به&zwnj;رنامه&zwnj;که&zwnj;ی تۆیان به&zwnj;درۆزانی، ئه&zwnj;وه تۆش بڵێ: کارو کرده&zwnj;وه&zwnj;ی خۆم هه&zwnj;ر بۆ خۆمه&zwnj;و کارو کرده&zwnj;وه&zwnj;ی خۆتان هه&zwnj;ر بۆ خۆتانه&zwnj;، ئێوه به&zwnj;رین و لێپرسراو نین له&zwnj;هه&zwnj;رچی من ده&zwnj;یکه&zwnj;م، منیش به&zwnj;ریم و لێپرسراو نیم له&zwnj;هه&zwnj;رچی که ئێوه ده&zwnj;یکه&zwnj;ن.';
$t[42] = '(له&zwnj;و بێ باوه&zwnj;ڕانه&zwnj;) که&zwnj;سانێک هه&zwnj;یه گوێت بۆ هه&zwnj;ڵده&zwnj;خات (کاتێک قورئان ده&zwnj;خوێنیت)، جا ئایا تۆ ده&zwnj;زانیت قسه&zwnj;و گوفتاری خۆت بده&zwnj;ی به&zwnj;گوێی که&zwnj;سانێکدا که که&zwnj;ڕه&zwnj;واڵه&zwnj;ن نایبیستن و ئه&zwnj;گه&zwnj;ر چی تێ نه&zwnj;ئه&zwnj;گه&zwnj;یشتن و عه&zwnj;قڵ و ژیریان نه&zwnj;ده&zwnj;خسته&zwnj;کار.';
$t[43] = 'هه&zwnj;شیانه سه&zwnj;رنجی تۆ ده&zwnj;دات و له هه&zwnj;ڵس و که&zwnj;وت و گوفتارو ڕه&zwnj;فتاری تۆ ورد ده&zwnj;بێته&zwnj;وه&zwnj;، جا ئایا تۆ هیدایه&zwnj;تی کوێرو نابینایان ده&zwnj;ده&zwnj;یت؟! ئه&zwnj;گه&zwnj;ر کوێراییان داهات بێت و نه&zwnj;بینن؟! (دیاره که ئه&zwnj;وانه دڵیان کوێره&zwnj;و ده&zwnj;روونیان تاریکه و نایانه&zwnj;وێت نووری ئیسلام دڵ و ده&zwnj;روونیان ڕووناک بکاته&zwnj;وه&zwnj;).';
$t[44] = 'به&zwnj;ڕاستی خوا هیچ جۆره سته&zwnj;مێک ناکات له خه&zwnj;ڵکی، به&zwnj;ڵکو خه&zwnj;ڵکی خۆیان سته&zwnj;م له&zwnj;خۆیان ده&zwnj;که&zwnj;ن.';
$t[45] = 'ڕۆژێک دێت خوا هه&zwnj;ر هه&zwnj;مووانیان کۆده&zwnj;کاته&zwnj;وه&zwnj;، وا هه&zwnj;ست ده&zwnj;که&zwnj;ن ته&zwnj;نها سه&zwnj;عاتێک له ڕۆژیان پێچووه (خزمان و ناسیاوان) هه&zwnj;مووان یه&zwnj;کتر ده&zwnj;ناسنه&zwnj;وه&zwnj;، بێگومان ئه&zwnj;وانه&zwnj;ی که بڕوایان نه&zwnj;بوو به ئاماده بوونی به&zwnj;رده&zwnj;م دادگای خوا، خه&zwnj;ساره&zwnj;ت مه&zwnj;ندی و زه&zwnj;ره&zwnj;رمه&zwnj;ندیه&zwnj;کی زۆر ڕویان تێده&zwnj;کات، به&zwnj;ڕاستی ئه&zwnj;وانه هیدایه&zwnj;ت وه&zwnj;رگر نه&zwnj;بوون.';
$t[46] = 'خۆ ئه&zwnj;گه&zwnj;ر هه&zwnj;ندێک له هه&zwnj;ڕه&zwnj;شه&zwnj;کانی خۆمان (بۆ خوا نه&zwnj;ناسان) پێش بهێنین و نیشانی تۆی بده&zwnj;ین، یاخود پێشتر گیانت بکێشین و ئاکامیان نه&zwnj;بینیت، ئه&zwnj;وه گه&zwnj;ڕانه&zwnj;وه&zwnj;یان هه&zwnj;ر بۆ لای ئێمه&zwnj;یه (له&zwnj;ده&zwnj;ستمان ده&zwnj;رباز نابن)، له&zwnj;وه&zwnj;و دواش خوا خۆی شایه&zwnj;ته له&zwnj;سه&zwnj;ر هه&zwnj;موو ئه&zwnj;و کارو کرده&zwnj;وانه&zwnj;ی که ئه&zwnj;نجامی ده&zwnj;ده&zwnj;ن.';
$t[47] = 'بۆ هه&zwnj;موو قه&zwnj;وم و گه&zwnj;لێک پێغه&zwnj;مبه&zwnj;رێک هه&zwnj;بووه&zwnj;، جا کاتێک (له&zwnj;گۆڕه&zwnj;پانی مه&zwnj;حشه&zwnj;ردا) ئه&zwnj;و پێغه&zwnj;مبه&zwnj;ره&zwnj;یان ئاماده ده&zwnj;بێت، له&zwnj;سه&zwnj;ر بنچینه&zwnj;ی دادپه&zwnj;روه&zwnj;ری داوه&zwnj;ری ده&zwnj;کرێت له نێوانیاندا و هیچ جۆره سته&zwnj;مێکیان لێناکرێت.';
$t[48] = '(بێباوه&zwnj;ڕان) ده&zwnj;ڵێن: کوا، که&zwnj;ی ئه&zwnj;و به&zwnj;ڵێنه پێش دێت؟ که&zwnj;ی ڕۆژی قیامه&zwnj;ت به&zwnj;رپا ده&zwnj;بێت؟! ئه&zwnj;گه&zwnj;ر ئێوه ڕاست ده&zwnj;که&zwnj;ن!';
$t[49] = 'پێیان بڵێ (من ته&zwnj;نها پێغه&zwnj;مبه&zwnj;رم) توانای ئه&zwnj;وه&zwnj;شم به&zwnj;ر ده&zwnj;ست نیه که زه&zwnj;ره&zwnj;رێک یان قازانجێک به خۆم بگه&zwnj;یه&zwnj;نم مه&zwnj;گه&zwnj;ر ئه&zwnj;وه&zwnj;ی که ویستی خوای له&zwnj;سه&zwnj;ر بێت، هه&zwnj;موو قه&zwnj;ومێک کاتی دیاری کراوی خۆی هه&zwnj;یه بۆ کۆتایی هاتنی، هه&zwnj;ر کاتێک ئه&zwnj;و ساته گه&zwnj;یشت، ئه&zwnj;وا نه&zwnj;تاوێک دواده&zwnj;که&zwnj;ون، نه&zwnj;ساتێکیش پێش ده&zwnj;که&zwnj;ون.';
$t[50] = 'بڵێ: هه&zwnj;واڵم بده&zwnj;نێ باشه ئه&zwnj;گه&zwnj;ر سزاو تۆڵه&zwnj;ی خوا له&zwnj;شه&zwnj;وگاردا یان له ڕۆژگاردا یه&zwnj;خه&zwnj;ی پێگرتن، تاوانباران (به&zwnj;ته&zwnj;مای چین!) ئاخۆ بۆچی په&zwnj;له ده&zwnj;که&zwnj;ن؟!';
$t[51] = 'دوای ئه&zwnj;وه ئایا که (قیامه&zwnj;ت) ڕوویداو به&zwnj;رپا بوو باوه&zwnj;ڕی پێ ده&zwnj;هێنن؟!. ئه&zwnj;وسا پێیان ده&zwnj;وترێت: ئا ئێستا باوه&zwnj;ڕی پێ ده&zwnj;هێنن؟! خۆ به&zwnj;ڕاستی کاتی خۆی (به&zwnj;گاڵته پێ کردنه&zwnj;وه&zwnj;) په&zwnj;له&zwnj;تان ده&zwnj;کرد له پێش هاتنی و (بڕواتان پێی نه&zwnj;بوو)!';
$t[52] = 'ئه&zwnj;وسا به&zwnj;وانه&zwnj;ی که سته&zwnj;میان له خۆیان کرد ده&zwnj;وترێت: ده&zwnj;بچێژن سزاو ئازاری نه&zwnj;بڕاوه&zwnj;، مه&zwnj;گه&zwnj;ر پاداشتان له&zwnj;به&zwnj;رامبه&zwnj;ری کارو کرده&zwnj;وه و هه&zwnj;وڵه&zwnj;کانی خۆتانه&zwnj;وه نادرێته&zwnj;وه که ده&zwnj;تانکرد؟!';
$t[53] = '(بێباوه&zwnj;ڕان به&zwnj;گاڵته&zwnj;وه لێت ده&zwnj;پرسن): ئایا به&zwnj;رپا بوونی قیامه&zwnj;ت ڕاسته&zwnj;؟ (شتی وا پێش دێت؟!). بڵێ: به&zwnj;ڵێ، ڕاسته&zwnj;، سوێند به&zwnj;په&zwnj;روه&zwnj;ردگارم به&zwnj;ڕاستی ئه&zwnj;وه حه&zwnj;قیقه&zwnj;تێکی و (هه&zwnj;ر پێش دێت و) ئێوه ناتوانن خوا ده&zwnj;سته&zwnj;وسان بکه&zwnj;ن.';
$t[54] = 'ئه&zwnj;گه&zwnj;ر هه&zwnj;رچی له&zwnj;زه&zwnj;ویدا هه&zwnj;یه که&zwnj;سێک که سته&zwnj;می کردووه خاوه&zwnj;نی بێت (له قیامه&zwnj;تدا) ئاماده&zwnj;یه هه&zwnj;ر هه&zwnj;مووی بکاته قوربانی خۆی و بیبه&zwnj;خشێت، ئه&zwnj;وانه له ده&zwnj;روونه&zwnj;وه زۆر په&zwnj;شیمانن کاتێک سزاو ئازاری چاوه&zwnj;ڕوان نه&zwnj;کراو ده&zwnj;بینن، ئه&zwnj;وسا داوه&zwnj;ری له نێوانیاندا له&zwnj;سه&zwnj;ر بنچینه&zwnj;ی دادپه&zwnj;روه&zwnj;ری ئه&zwnj;نجام ده&zwnj;درێت و ئه&zwnj;وانه هیچ جۆره سته&zwnj;مێکیان لێناکرێت.';
$t[55] = '(خه&zwnj;ڵکینه&zwnj;) ئاگاداربن و بیرتان نه&zwnj;چێت که به&zwnj;ڕاستی هه&zwnj;رچی له ئاسمانه&zwnj;کان و زه&zwnj;ویدا هه&zwnj;یه هه&zwnj;ر هه&zwnj;مووی بۆ خوایه&zwnj;، ئاگاداربن که به&zwnj;ڵێنی خوا ڕاست و دروسته (قیامه&zwnj;ت به&zwnj;رپا ده&zwnj;کات و پاداشت و سزا له&zwnj;سه&zwnj;ر بنچینه&zwnj;ی ئیمان و کرده&zwnj;وه دیاری ده&zwnj;کات) به&zwnj;ڵام زۆربه&zwnj;ی خه&zwnj;ڵکی له&zwnj;م ڕاستیه بێ ئاگان و نایزانن.';
$t[56] = 'هه&zwnj;ر خوا ژیان ده&zwnj;به&zwnj;خشێت و مردن پێش ده&zwnj;هێنێت، هه&zwnj;ر بۆ لای ئه&zwnj;ویش ده&zwnj;برێنه&zwnj;وه&zwnj;.';
$t[57] = 'ئه&zwnj;ی خه&zwnj;ڵکینه&zwnj;، به&zwnj;ڕاستی ئێوه ئامۆژگاریه&zwnj;کی گه&zwnj;وره و گرنگتان له لایه&zwnj;ن په&zwnj;روه&zwnj;ردگارتانه&zwnj;وه بۆ هاتووه که (قورئانه&zwnj;) شیفا و چاره&zwnj;سه&zwnj;ریشه بۆ نه&zwnj;خۆشی دڵ و ده&zwnj;روونه&zwnj;کان و ڕێ نیشانده&zwnj;رو ڕه&zwnj;حمه&zwnj;تیشه بۆ ئیمانداران.';
$t[58] = '(ئه&zwnj;ی محمد صلی الله علیه وسلم) به ئیماندارن بڵێ: با دڵشاد و شادومان بن به&zwnj;فه&zwnj;زڵ و به&zwnj;خشش و ڕه&zwnj;حمه&zwnj;تی خوایی (که قورئان و به&zwnj;رنابه&zwnj;ی خوایه&zwnj;)، که&zwnj;وا بوو با هه&zwnj;ر به&zwnj;وه دڵ خۆش بن، هه&zwnj;ر ئه&zwnj;وه&zwnj;ش چاکتره بۆیان له هه&zwnj;موو ئه&zwnj;و شتانه&zwnj;ی که خه&zwnj;ڵکی خه&zwnj;ریکن کۆی ده&zwnj;که&zwnj;نه&zwnj;وه&zwnj;و که&zwnj;ڵه&zwnj;که&zwnj;ی ده&zwnj;که&zwnj;ن.';
$t[59] = 'بڵێ پێیان: هه&zwnj;واڵم بده&zwnj;نێ، ئێوه چۆن له خۆتانه&zwnj;وه ئه&zwnj;و ڕزق و ڕۆزیه&zwnj;ی که خوا پێی به&zwnj;خشیوون دابه&zwnj;شی ده&zwnj;که&zwnj;ن و هه&zwnj;ندێکی به حه&zwnj;رام داده&zwnj;نێن و هه&zwnj;ندێکی به حه&zwnj;ڵاڵ (که چی باوه&zwnj;ڕتان به به&zwnj;رنامه&zwnj;ی خوا نیه)، پێیان بڵێ: ئایا خوا خۆی مۆڵه&zwnj;تی ئه&zwnj;و کاره&zwnj;ی پێداون؟! یا خود له خۆتانه&zwnj;وه شت به&zwnj;ده&zwnj;م خواوه هه&zwnj;ڵده&zwnj;به&zwnj;ستن؟!';
$t[60] = 'باشه&zwnj;، ئاخۆ له ڕۆژی قیامه&zwnj;تدا گومان و بۆچوونی ئه&zwnj;و که&zwnj;سانه چۆن بێت که درۆ به&zwnj;ده&zwnj;م خواوه هه&zwnj;ڵده&zwnj;به&zwnj;ستن؟ (ئایا واده&zwnj;زانن لێ پرسینه&zwnj;وه نیه&zwnj;؟) به&zwnj;ڕاستی (له&zwnj;گه&zwnj;ڵ لاری زۆربه&zwnj;ی خه&zwnj;ڵکیدا) خوا هه&zwnj;ر خاوه&zwnj;نی فه&zwnj;زڵه به&zwnj;سه&zwnj;ریانه&zwnj;وه&zwnj;، هه&zwnj;ر چه&zwnj;نده زۆربه&zwnj;یان سوپاس ناپه&zwnj;زێرو بێ ئه&zwnj;مه&zwnj;کن.';
$t[61] = '(ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر صلی الله علیه وسلم) تۆ به&zwnj;هیچ کارێک هه&zwnj;ڵناسی، هیچ به&zwnj;شێک له قورئان ناخوێنیته&zwnj;وه (که ئێمه ئاگادار نه&zwnj;بین، ئێوه&zwnj;ش ئه&zwnj;ی خه&zwnj;ڵکینه&zwnj;) به هیچ هه&zwnj;ڵسوکه&zwnj;وتێک هه&zwnj;ڵناسن که ئێمه شایه&zwnj;ت نه&zwnj;بین به&zwnj;سه&zwnj;ریه&zwnj;وه له&zwnj;کاتێکدا ئه&zwnj;نجامی ده&zwnj;ده&zwnj;ن و تیایدا ڕۆده&zwnj;چن، هیچ شتێکیش له په&zwnj;روه&zwnj;ردگاری تۆ، ون نابێت له پارچه ئه&zwnj;تۆمێکه&zwnj;وه بگره له زه&zwnj;وی یان له ئاسماندا، بچوک تر بێت یان گه&zwnj;وره تر، که له دۆسیه&zwnj;یه&zwnj;کی تایبه&zwnj;تی ئاشکرادا لای ئێمه تۆمار نه&zwnj;کرا بێت.';
$t[62] = 'ئاگادار بن که به&zwnj;ڕاستی چاکان و خۆ شه&zwnj;ویستانی خوای میهره&zwnj;بان نه&zwnj;ترس و بیم ڕوویان تێده&zwnj;کات (له سه&zwnj;ره مه&zwnj;رگ و له ڕۆژی لێ پرسینه&zwnj;وه&zwnj;دا)، نه&zwnj;غه&zwnj;م و په&zwnj;ژاره و دڵته&zwnj;نگی دایان ده&zwnj;گرێت (به&zwnj;ڵکو فریشتوه&zwnj;کان به&zwnj;ڕوو خۆشیه&zwnj;وه مژده&zwnj;ی سه&zwnj;رفرازی و خۆشنوودیان ده&zwnj;ده&zwnj;نێ)...';
$t[63] = '(ئه&zwnj;و به&zwnj;خته&zwnj;وه&zwnj;رانه&zwnj;) ئه&zwnj;و که&zwnj;سانه&zwnj;ن باوه&zwnj;ڕی به&zwnj;تین و دامه&zwnj;زراوو پڕ سۆزیان هێناوه به په&zwnj;روه&zwnj;وردگاریان و خۆشیان ده&zwnj;پارێزن له خه&zwnj;شم و قینی خوا (به هۆی ئه&zwnj;نجامدانی فه&zwnj;رمانه&zwnj;کانی خوا و ته&zwnj;رێز کردنیان له حه&zwnj;رام)...';
$t[64] = 'هه&zwnj;ر بۆ ئه&zwnj;وانه مژده&zwnj;ی (کامه&zwnj;رانی و به&zwnj;خته&zwnj;وه&zwnj;ری) له&zwnj;ژیانی دنیادا (که مژده&zwnj;ی قورئان و فه&zwnj;رمووده&zwnj;یه&zwnj;) له قیامه&zwnj;تیشدا (مژده&zwnj;کان هه&zwnj;موو ده&zwnj;بنه واقع و، هه&zwnj;موو مژده قورئانیه&zwnj;کان به زیاده&zwnj;وه دینه دی)، بێگومان ئه&zwnj;و به&zwnj;ڵێنانه&zwnj;ی خوا هه&zwnj;ر دێنه&zwnj;دی و هیچ گۆڕانێکیان به&zwnj;سه&zwnj;ردا نایه&zwnj;ت، ئا ئه&zwnj;وه&zwnj;یه سه&zwnj;رفرازی و کامه&zwnj;رانی گه&zwnj;وره&zwnj;و مه&zwnj;زن.';
$t[65] = 'گوفتاری نابه&zwnj;جێ و نادروستی خوانه&zwnj;ناسان خه&zwnj;فه&zwnj;ت بارو غه&zwnj;مگینت نه&zwnj;کات، چونکه به&zwnj;ڕاستی عیزه&zwnj;ت و به&zwnj;رزی و بڵندی هه&zwnj;ر هه&zwnj;مووی هی خوایه&zwnj;، ئه&zwnj;و زاته هه&zwnj;میشه بیسه&zwnj;ر و زانایه&zwnj;.';
$t[66] = 'ئاگادار بن که به&zwnj;ڕاستی هه&zwnj;ر که&zwnj;س له ئاسمانه&zwnj;کان و له زه&zwnj;ویدا هه&zwnj;یه خوا خاوه&zwnj;نیانه&zwnj;، ئه&zwnj;وانه&zwnj;ی جگه له خوا شتی تر ده&zwnj;په&zwnj;رستن و په&zwnj;یڕه&zwnj;وی ڕێبازی تر ده&zwnj;که&zwnj;ن (وه&zwnj;نه&zwnj;بێت شوێن ڕێبازێکی چاک که&zwnj;وت بن) ئه&zwnj;وانه ته&zwnj;نها شوێنی گومان که&zwnj;وتوون، ئه&zwnj;وانه جگه له درۆ کردن و گۆتره&zwnj;کاری هیچی تر ناکه&zwnj;ن.';
$t[67] = 'هه&zwnj;ر زاتی خوایه که شه&zwnj;وی بۆ به&zwnj;دیهێناون تاوه&zwnj;کو تیایدا بحه&zwnj;وێنه&zwnj;وه&zwnj;، ڕۆژیشی کردووه به&zwnj;ڕووناککه&zwnj;ره&zwnj;وه&zwnj;، (تا کاره کاسپی تیادا ئه&zwnj;نجام بده&zwnj;ن)، به&zwnj;ڕاستی ئا له&zwnj;و دیاردانه&zwnj;دا به&zwnj;ڵگه و نیشانه&zwnj;ی زۆر هه&zwnj;ن (له&zwnj;سه&zwnj;ر ده&zwnj;سه&zwnj;ڵات و توانایی په&zwnj;رووه&zwnj;ردگار) بۆ که&zwnj;سانێک که ببیستن وتێبگه&zwnj;ن.';
$t[68] = '(خوانه&zwnj;ناسان) ده&zwnj;ڵێن: خوا منداڵی بۆ خۆی بڕیارداوه&zwnj;! پاکی و بێگه&zwnj;ردی شایسته&zwnj;ی ئه&zwnj;و خوایه&zwnj;یه&zwnj;، ئه&zwnj;و زاته بێ نیازه (پێویستی به کوڕو کچ نیه&zwnj;)، هه&zwnj;رچی له ئاسمانه&zwnj;کان و له زه&zwnj;ویدا هه&zwnj;یه هه&zwnj;ر خوا خاوه&zwnj;نیانه&zwnj;، بێگومان هیچ به&zwnj;ڵگه و نیشانه&zwnj;یه&zwnj;کتان نیه بۆ ئه&zwnj;م بوختانه ناڕه&zwnj;وایه&zwnj;، ئایا شتێک هه&zwnj;ڵده&zwnj;به&zwnj;ستن بۆ خواو (بێ باکانه&zwnj;) ده&zwnj;یڵێن، که هیچی لێ نازانن و هیچ بنه&zwnj;مایه&zwnj;کی نیه&zwnj;؟!';
$t[69] = '(ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر صلی الله علیه وسلم تۆ) بڵێ: به&zwnj;ڕاستی ئه&zwnj;وانه&zwnj;ی درۆو بوختان به&zwnj;ده&zwnj;م خواوه هه&zwnj;ڵده&zwnj;به&zwnj;ستن، ئه&zwnj;وه سه&zwnj;رفرازو ڕزگار نابن.';
$t[70] = '(به&zwnj;ڵکو هه&zwnj;موو ده&zwnj;سکه&zwnj;وته&zwnj;که&zwnj;یان) ژیانی دنیاو ڕابواردنی دنیایه&zwnj;، له&zwnj;وه&zwnj;و دوا گه&zwnj;ڕانه&zwnj;وه&zwnj;یان بۆ لای ئێمه&zwnj;یه&zwnj;، ئه&zwnj;وسا سزای به ئێشیان ده&zwnj;ده&zwnj;ین و تاڵاو ده&zwnj;که&zwnj;ین به&zwnj;گه&zwnj;روویاندا به&zwnj;هۆی ئه&zwnj;وه&zwnj;ی که بێباوه&zwnj;ڕ ئه&zwnj;بوون پێی.';
$t[71] = '(ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر صلی الله علیه وسلم) به&zwnj;سه&zwnj;رهات و هه&zwnj;واڵی نوح به&zwnj;سه&zwnj;ر ئه&zwnj;و خه&zwnj;ڵکه&zwnj;دا بخوێنه&zwnj;ره&zwnj;وه&zwnj;، کاتێک به قه&zwnj;ومه&zwnj;که&zwnj;ی خۆی وت: ئه&zwnj;ی قه&zwnj;وم و که&zwnj;سم، ئه&zwnj;گه&zwnj;ر ئێوه سه&zwnj;غڵه&zwnj;تن، به&zwnj;مانه&zwnj;وه&zwnj;ی من له&zwnj;ناوتاندا و یادخستنه&zwnj;وه&zwnj;ی ئایه&zwnj;ته&zwnj;کانی خواتان لاکرانه ئه&zwnj;وه دڵنیابن که من کۆڵ ناده&zwnj;م، پشتم به خوا به&zwnj;ستووه له&zwnj;و کاره&zwnj;مدا، (ئیتر ئێوه چیتان له&zwnj;ده&zwnj;ست دێت درێغی مه&zwnj;که&zwnj;ن) خۆتان و بته&zwnj;کانتان و هاوبیرو باوه&zwnj;ڕه&zwnj;کانتان بکه&zwnj;ونه خۆو ڕای خۆتان کۆبکه&zwnj;نه&zwnj;وه&zwnj;، پاشان به&zwnj;ئاشکرا چی ده&zwnj;که&zwnj;ن بیکه&zwnj;ن، هه&zwnj;رچیان له ده&zwnj;ست دێت دژ به من ئه&zwnj;نجامی بده&zwnj;ن (منه&zwnj;تتان نه&zwnj;بێت)، مۆڵه&zwnj;تم مه&zwnj;ده&zwnj;ن.';
$t[72] = 'جا ئه&zwnj;گه&zwnj;ر پشتم تێبکه&zwnj;ن و باوه&zwnj;ڕم پێنه&zwnj;که&zwnj;ن (پێم سه&zwnj;یره چونکه&zwnj;): ئه&zwnj;وه من هیچ پاداشتێک و هیچ شتێکم له ئێوه داوا نه&zwnj;کردووه&zwnj;، به&zwnj;ڵکو پاداشتم ته&zwnj;نها له&zwnj;سه&zwnj;ر خوایه و من فه&zwnj;رمانم پێ دراوه که له ڕێزی موسڵماناندا بم.';
$t[73] = 'که&zwnj;چی بانگه&zwnj;وازه&zwnj;که&zwnj;یان به&zwnj;درۆ زانی، ئینجا ئێمه&zwnj;ش نوح و هه&zwnj;موو ئه&zwnj;وانه&zwnj;ی که له&zwnj;گه&zwnj;ڵیدا بوون له که&zwnj;شتیه&zwnj;که&zwnj;دا ڕزگارمان کردن و کردیشمانن به جێنشین و هه&zwnj;موو ئه&zwnj;وانه&zwnj;مان نوقمی ڕه&zwnj;هێڵ و زریانه&zwnj;که کرد که بڕوایان به ئایه&zwnj;ت و فه&zwnj;رمانه&zwnj;کانی ئێمه نه&zwnj;بوو، ئینجا ته&zwnj;ماشاکه&zwnj;و سه&zwnj;رنج بده&zwnj;و بزانه سه&zwnj;رئه&zwnj;نجامی ئه&zwnj;و که&zwnj;سانه چۆن بوو که بێدار کرابوونه&zwnj;وه و هۆشیاریان پێدرابوو.';
$t[74] = 'پاشان دوای نوح، پێغه&zwnj;به&zwnj;رانی ترمان نارد بۆ سه&zwnj;ر قه&zwnj;ومه&zwnj;که&zwnj;یان، جا ئه&zwnj;وانیش به چه&zwnj;نده&zwnj;ها به&zwnj;ڵگه&zwnj;و نیشانه&zwnj;و موعجیزه&zwnj;ی زۆره&zwnj;وه هاتن، که&zwnj;چی ئه&zwnj;و خه&zwnj;ڵکه باوه&zwnj;ڕیان نه&zwnj;ده&zwnj;هێنا به&zwnj;وه&zwnj;ی که پێشتر باوه&zwnj;ڕیسن پێی نه&zwnj;بوو، ئابه&zwnj;و شێوه&zwnj;یه ئێمه مۆر ده&zwnj;نێین به&zwnj;سه&zwnj;ر دڵی ده&zwnj;ستدرێژکاراندا.';
$t[75] = 'جا دوای ئه&zwnj;وان موسا و هاروونمان ڕه&zwnj;وانه کرد بۆ فیرعه&zwnj;ون و داروده&zwnj;سته&zwnj;که&zwnj;ی، هاوڕێ له&zwnj;گه&zwnj;ل موعجیزه&zwnj;ی زۆردا که چی ئه&zwnj;وان خۆیان به گه&zwnj;وره زانی و بڕوایان پێ نه&zwnj;کردن، ئه&zwnj;وانه که&zwnj;سانێکی تاوانبار و گوناهباربوون.';
$t[76] = 'جا کاتێک حه&zwnj;قیقه&zwnj;تیان له&zwnj;لایه&zwnj;ن ئێمه&zwnj;وه (له&zwnj;ڕێگه&zwnj;ی موساوه&zwnj;) بۆ هات وتیان: به&zwnj;ڕاستی ئه&zwnj;مه جادوویه&zwnj;کی ئاشکرایه&zwnj;!!';
$t[77] = 'موسا پێی وتن: باشه ئێوه به&zwnj;م حه&zwnj;قیقه&zwnj;ت و ڕاستیه&zwnj;ی که بۆم هێناون ده&zwnj;ڵێن: (جادوو)؟! ئایا ئه&zwnj;م موعجیزه جۆراو جۆرانه جادوون؟! خۆ جادووگه&zwnj;ران سه&zwnj;رفراز نابن؟!';
$t[78] = '(فیرعه&zwnj;ون و دارو ده&zwnj;سته&zwnj;&zwnj;ی) وتیان: باشه تۆ بۆ ئه&zwnj;وه هاتوویت که وێڵمان بکه&zwnj;یت له&zwnj;و بیروباوه&zwnj;ڕو به&zwnj;رنامه&zwnj;یه&zwnj;ی که باوو باپیرانمان له&zwnj;سه&zwnj;ری بوون و له&zwnj;وانه&zwnj;وه بۆ ئێمه ماوه&zwnj;ته&zwnj;وه&zwnj;؟ (ئایا به&zwnj;م ڕێبازه&zwnj;) ده&zwnj;تانه&zwnj;وێت ده&zwnj;سه&zwnj;ڵاتداری و گه&zwnj;وره&zwnj;یی بۆ خۆتان له&zwnj;سه&zwnj;ر زه&zwnj;ویدا به&zwnj;ده&zwnj;ست بهێنن؟! ئێمه بڕواتان پێ ناکه&zwnj;ین!!';
$t[79] = '(ئینجا فیرعه&zwnj;ون فه&zwnj;رمانیدا) وتی: ئاده&zwnj;ی هه&zwnj;رچی جادووگه&zwnj;ری زاناو شاره&zwnj;زا هه&zwnj;یه بهێنن و بۆم کۆبکه&zwnj;نه&zwnj;وه&zwnj;.';
$t[80] = 'جا کاتێک جادووگه&zwnj;ران هاتن و (بڕیاردرا جادووگه&zwnj;ریی خۆیان بنوێنن و موعجیزه&zwnj;ی موسا به&zwnj;تاڵ بکه&zwnj;نه&zwnj;وه&zwnj;) موسا پێی وتن: ده&zwnj;ی چیتان هه&zwnj;یه فڕێی بده&zwnj;ن و بیخه&zwnj;نه ڕوو!!';
$t[81] = 'ئه&zwnj;وسا (جادووگه&zwnj;ران، جادووی خۆیان نواند) موسا وتی: هه&zwnj;رچی کردووتانه و نیشانی ئه&zwnj;و خه&zwnj;ڵکه&zwnj;تانداوه&zwnj;هه&zwnj;مووی جادووه و هیچی تر، بێگومان خوا دوای تاوێکی تر پووچی ده&zwnj;کاته&zwnj;وه&zwnj;، به&zwnj;ڕاستی خوا کرده&zwnj;وه&zwnj;ی ئه&zwnj;وانه&zwnj;ی که تۆوی خراپه ده&zwnj;چێنن چاک و سه&zwnj;رکه&zwnj;وتوو ناکات.';
$t[82] = 'به&zwnj;ڵکو خوا حه&zwnj;ق و ڕاستی ده&zwnj;چه&zwnj;سپێنێت به&zwnj;فه&zwnj;رمان و ویسته&zwnj;کانی خۆی ئه&zwnj;گه&zwnj;ر چی تاوانکارو تاوانباران پێیان ناخۆش بێت.';
$t[83] = 'سه&zwnj;رئه&zwnj;نجام که&zwnj;س باوه&zwnj;ڕی به موسا نه&zwnj;هێنا جگه له چه&zwnj;ند لاوێک (چونکه زۆربه&zwnj;ی خه&zwnj;ڵکه&zwnj;که&zwnj;) ده&zwnj;ترسان له فیرعه&zwnj;ن و داروده&zwnj;سته&zwnj;که&zwnj;ی که به&zwnj;ڵایان به&zwnj;سه&zwnj;ربهێنن و تووشی ئازارو ئه&zwnj;شکه&zwnj;نجه&zwnj;یان بکه&zwnj;ن، به&zwnj;ڕاستی فیرعه&zwnj;ون خۆی به&zwnj;گه&zwnj;وره&zwnj;و بڵند ده&zwnj;زانی له زه&zwnj;ویدا، به&zwnj;ڕاستی ئه&zwnj;و له سنوور ترازاوان بوو.';
$t[84] = 'موساش به قه&zwnj;ومه&zwnj;که&zwnj;ی وت: ئه&zwnj;ی قه&zwnj;وم و عه&zwnj;شره&zwnj;تم، ئه&zwnj;گه&zwnj;ر ئێوه به&zwnj;ڕاستی باوه&zwnj;ڕتان به&zwnj;خوا هێناوه&zwnj;، ئه&zwnj;وه ئیتر پشت به&zwnj;و ببه&zwnj;ستن ئه&zwnj;گه&zwnj;ر موسڵمان و ملکه&zwnj;چن.';
$t[85] = 'ئه&zwnj;وسا (ئیمانداران) وتیان: ئێمه ئیتر هه&zwnj;ر پشتمان به&zwnj;خوا به&zwnj;وستووه&zwnj;، په&zwnj;روه&zwnj;ردگارا مه&zwnj;مانگێڕه به&zwnj;هۆکاری سه&zwnj;رگه&zwnj;ردانی بۆ قه&zwnj;ومی سته&zwnj;مکاران (که هه&zwnj;م ئێمه ئازار بده&zwnj;ن هه&zwnj;م خۆشیان گوناهبار ببن و دوور بکه&zwnj;ونه&zwnj;وه له ئیمان).';
$t[86] = 'هه&zwnj;روه&zwnj;ها به&zwnj;ڕه&zwnj;حمه&zwnj;ت و میهره&zwnj;بانی خۆت (ده&zwnj;رووی خێرمان لێ بکه&zwnj;ره&zwnj;وه&zwnj;) ڕزگارمان بکه له ده&zwnj;ست هۆزو ده&zwnj;سته&zwnj;ی بێ&zwnj;باوه&zwnj;ڕان.';
$t[87] = 'ئێمه وه&zwnj;حیشمان نارد بۆ موسا (علیه السلام) و براکه&zwnj;ی تا له میسردا چه&zwnj;ند ژوورێک ئاماده بکه&zwnj;ن و ئه&zwnj;و ژوورانه&zwnj;ش بکه&zwnj;نه شوێنی خواپه&zwnj;رستی و ڕووی تێبکه&zwnj;ن و پێمان وتن نوێژه&zwnj;کانتان به&zwnj;چاکی ئه&zwnj;نجام بده&zwnj;ن، مژده&zwnj;ش بده به ئیمانداران.';
$t[88] = '(جا کاتێک سته&zwnj;می فیرعه&zwnj;ون له سنوور ده&zwnj;رچوو) موسا نزای کردوو وتی: په&zwnj;روه&zwnj;ردگارا، به&zwnj;ڕاستی تۆ له ژیانی دنیادا ناز و نیعمه&zwnj;ت و ماڵ و حاڵ و جێگه و ڕێگه&zwnj;ی خۆش و ڕازاوه&zwnj;ت به&zwnj;خشیووه به&zwnj;فیرعه&zwnj;ون و داروده&zwnj;سته&zwnj;که&zwnj;ی، په&zwnj;روه&zwnj;ردگارا، ئه&zwnj;و شتانه بووه&zwnj;ته هۆی ئه&zwnj;وه&zwnj;ی که (خه&zwnj;ڵکی) له ڕێبازو به&zwnj;رنامه&zwnj;ی تۆ وێڵ و گومڕابن، په&zwnj;وه&zwnj;ردگارا: ماڵ و سامانیان ته&zwnj;فرو تونا بکه&zwnj;و کوێری بکه&zwnj;ره&zwnj;وه&zwnj;، دڵیان بگوشه&zwnj;، (تا هه&zwnj;ست به&zwnj;خۆشی نه&zwnj;که&zwnj;ن)، ئه&zwnj;مانه باوه&zwnj;ڕ ناهێنن هه&zwnj;تا سزاو ئازاری به&zwnj;ئێش ئه&zwnj;بینن.';
$t[89] = 'په&zwnj;وه&zwnj;ردگار فه&zwnj;رمووی: بێگومان دوعای هه&zwnj;ردووکتان وه&zwnj;رگیرا (وادیاره هاروون (علیه السلام) به&zwnj;کوڵ ئامینی کردوه&zwnj;)، به&zwnj;ڵام ئێوه هه&zwnj;ر به&zwnj;رده&zwnj;وام بن له&zwnj;سه&zwnj;ر ڕێبازی ڕاست و دروستی هیدایه&zwnj;ت و شوێن ڕێبازی ئه&zwnj;وانه مه&zwnj;که&zwnj;ون که حه&zwnj;ق نازانن و نافامن.';
$t[90] = 'ئه&zwnj;وسا نه&zwnj;وه&zwnj;ی ئیسرائیلمان به&zwnj;ده&zwnj;ریادا په&zwnj;ڕانده&zwnj;وه&zwnj;، فیرعه&zwnj;ون و سه&zwnj;ربازانی بۆ سته&zwnj;م و ده&zwnj;ستدرێژی شوێنیان که&zwnj;وتن، تا ئه&zwnj;و کاته&zwnj;ی (که ئاوه&zwnj;که چوه&zwnj;وه باری ئاسایی خۆی) فیرعه&zwnj;ون خه&zwnj;ریک بوو ده&zwnj;خنکا ئا له&zwnj;و کاته&zwnj;دا وتی: وا ئیتر باوه&zwnj;ڕم هێنا به&zwnj;وه&zwnj;ی که هیچ خوایه&zwnj;ک نیه جگه له&zwnj;و خوایه&zwnj;ی که نه&zwnj;وه&zwnj;ی ئیسرائیل باوه&zwnj;ڕیان پێهێناوه و من ئیتر له موسڵمان و ملکه&zwnj;چانم.';
$t[91] = '(ئه&zwnj;وسا پێی وترا) ئایا ئێسته&zwnj;، (باوه&zwnj;ڕ ده&zwnj;هێنیت!) له&zwnj;کاتێکدا به&zwnj;ڕاستی پێشتر یاخی بوویت و له ڕیزی ئه&zwnj;وانه&zwnj;دا بوویت که تۆوی خراپه&zwnj;یان ده&zwnj;چاند؟!';
$t[92] = 'ئه&zwnj;مڕۆ ئیتر لاشه&zwnj;ی تۆ ڕزگار ده&zwnj;که&zwnj;ین تا ببێته موعجیزه و په&zwnj;ند و ئامۆژگاری بۆ ئه&zwnj;وانه&zwnj;ی دوایتۆ دێن (دیاره خوای گه&zwnj;وره پاڕاستویه&zwnj;تی له&zwnj;وه&zwnj;ی که ماسی بیخوات، شه&zwnj;پۆلی ده&zwnj;ریا گه&zwnj;یاندیه که&zwnj;نار، تا ئه&zwnj;وانه&zwnj;ی که به&zwnj;خوایان ده&zwnj;زانی به&zwnj;چاوی خۆیان ببینن که فیرعه&zwnj;ون ئاده&zwnj;میزادێکه وه&zwnj;ک ئه&zwnj;و خه&zwnj;ڵکه و ببێته په&zwnj;ند، بۆ هه&zwnj;موو پاشاو زۆرداره&zwnj;کانی سه&zwnj;ر زه&zwnj;وی که ئه&zwnj;وه سه&zwnj;رئه&zwnj;نجامی مسۆگه&zwnj;ری هه&zwnj;موویانه&zwnj;)، به&zwnj;ڵام به&zwnj;ڕاستی زۆربه&zwnj;ی خه&zwnj;ڵکی له ئایه&zwnj;ت و موعجیزه&zwnj;کانی ئێمه غافڵ و بێئاگان.';
$t[93] = '(له&zwnj;وه&zwnj;ودوا) ئێمه ده&zwnj;روومان له نه&zwnj;وه&zwnj;ی ئیسرائیل کرده&zwnj;وه و شوێن و جێگه&zwnj;و ڕێگه&zwnj;ی خۆشمان پێبه&zwnj;خشین و له چه&zwnj;نده&zwnj;ها ڕزق و ڕۆزی چاک به&zwnj;هره&zwnj;وه&zwnj;رمان کردن، جا ئه&zwnj;وان جیاوازی و ناکۆکیان نه&zwnj;بوو هه&zwnj;تا زانست و زانیاری ته&zwnj;واویان بۆ هات (که&zwnj;چی ئه&zwnj;وان دینی خوا که هۆی یه&zwnj;کبوون و یه&zwnj;کگرتووییه کردیانه هۆی دووبه&zwnj;ره&zwnj;کی و جیاوازی هه&zwnj;رچه&zwnj;نده زانست و زانیاریشیان له&zwnj;و باره&zwnj;یه&zwnj;وه هه&zwnj;بوو)، به&zwnj;ڕاستی په&zwnj;روه&zwnj;ردگاری تۆ (ئه&zwnj;ی محمد صلی اله علیه وسلم) داوه&zwnj;ی ده&zwnj;کات له نێوانیاندا له ڕۆژی قیامه&zwnj;تدا له&zwnj;وکێشه&zwnj;و شتانه&zwnj;دا که ئه&zwnj;وان جیاوازی و ناکۆکی تێدا ده&zwnj;که&zwnj;ن.';
$t[94] = 'جا ئه&zwnj;گه&zwnj;ر تۆ (ده&zwnj;رباره&zwnj;ی ئه&zwnj;و شتانه&zwnj;ی که بۆمان دابه&zwnj;زاندوویت له&zwnj;باره&zwnj;ی نه&zwnj;وه&zwnj;ی ئیسرائیله&zwnj;وه له شک و گوماندایت، ئه&zwnj;وه پرسیار بکه له&zwnj;و که&zwnj;سانه&zwnj;ی که پێش تۆ له ته&zwnj;ورات شاره&zwnj;زان و ده&zwnj;یخوێننه&zwnj;وه&zwnj;، سوێند به&zwnj;خوا به&zwnj;ڕاستی حه&zwnj;ق و ڕاستیت له&zwnj;لایه&zwnj;ن په&zwnj;روه&zwnj;ردگارته&zwnj;وه بۆ هاتووه&zwnj;، که&zwnj;وابوو به&zwnj;هیچ شێوه&zwnj;یه&zwnj;ک گومانت نه&zwnj;بێت و مه&zwnj;چۆره ڕیزی گوماندارانه&zwnj;وه&zwnj;.';
$t[95] = '(هه&zwnj;روه&zwnj;ها) نه&zwnj;که&zwnj;یت بچیته ڕیزی که&zwnj;سانێکه&zwnj;وه که باوه&zwnj;ڕیان به موعجیزه و نیشانه&zwnj;کانی (ده&zwnj;سه&zwnj;ڵاتدارێتی) خوا نی&zwnj;یه&zwnj;، چونکه ئه&zwnj;گه&zwnj;ر وابیت، ده&zwnj;چیته ڕیزی خه&zwnj;ساره&zwnj;تمه&zwnj;ند و زه&zwnj;ره&zwnj;رمه&zwnj;ندانه&zwnj;وه&zwnj;.';
$t[96] = 'به&zwnj;ڕاستی ئه&zwnj;وانه&zwnj;ی که وه&zwnj;ک فیرعه&zwnj;ون و دارو ده&zwnj;سته&zwnj;ی سته&zwnj;مکارو تاوانبارو گوناهکارن، بڕیارداده&zwnj;ی په&zwnj;روه&zwnj;ردگارت له&zwnj;سه&zwnj;ریان چه&zwnj;سپا، ئه&zwnj;وانه&zwnj;: باوه&zwnj;ڕ ناهێنن.';
$t[97] = 'ئه&zwnj;گه&zwnj;ر چی هه&zwnj;رچی به&zwnj;ڵگه&zwnj;و موعجیزه هه&zwnj;یه بۆیان پێش بێت، ئه&zwnj;وانه هه&zwnj;ر باوه&zwnj;ڕ ناهێنن هه&zwnj;تا سزای به&zwnj;ئێش و ئازار ده&zwnj;بینن.';
$t[98] = 'جا ده&zwnj;بوایه خه&zwnj;ڵکی ئه&zwnj;و شارو شارۆچکانه&zwnj;ی هه&zwnj;ڕه&zwnj;شه&zwnj;یان لێکرابوو باوه&zwnj;ڕیان بهێنایه و باوه&zwnj;ڕه&zwnj;که&zwnj;یان سوود به&zwnj;خش بوایه بۆیان، به&zwnj;ڵام باوه&zwnj;ڕی هیچ کامیان سوودی پێنه&zwnj;گه&zwnj;یاندن جگه له قه&zwnj;ومی یونس (علیه السلام) نه&zwnj;بێت کاتێک که باوه&zwnj;ڕیان هێنا سزای سه&zwnj;رشۆڕیمان له ژیانی دنیادا له&zwnj;سه&zwnj;ر هه&zwnj;ڵگرتن و تا ماوه&zwnj;یه&zwnj;ک له نازو نیعمه&zwnj;ت به&zwnj;هره&zwnj;وه&zwnj;رمان کردن.';
$t[99] = 'خۆ ئه&zwnj;گه&zwnj;ر په&zwnj;روه&zwnj;ردگارت بیویستایه (ده&zwnj;یتوانی به&zwnj;زۆرو به&zwnj;ناچاری) هه&zwnj;ر هه&zwnj;موو دانیشتوانی سه&zwnj;ر زه&zwnj;وی باوه&zwnj;ڕ بهێنن (به&zwnj;ڵام په&zwnj;روه&zwnj;ردگاری مه&zwnj;زن ڕێزی بۆ ئاده&zwnj;میزاد داناوه و سه&zwnj;رپشکی کردووه&zwnj;)، جا ئایا تۆ (ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر صلی الله علیه وسلم، ئه&zwnj;ی ئیماندار) ئه&zwnj;ته&zwnj;وێ زۆر له&zwnj;خه&zwnj;ڵکی بکه&zwnj;ی بۆ ئه&zwnj;وه&zwnj;ی بڕواداربن؟!';
$t[100] = 'هیچ که&zwnj;س بۆی نیه باوه&zwnj;ڕ بهێنێت (هه&zwnj;تا هه&zwnj;وڵ نه&zwnj;دات و خۆی ماندوو نه&zwnj;کات و بیر نه&zwnj;کاته&zwnj;وه و) ویستی خوایشی له&zwnj;سه&zwnj;ر نه&zwnj;بێت و سزای (توند و ڕیسواکه&zwnj;ر) ده&zwnj;خاته سه&zwnj;ر ئه&zwnj;و که&zwnj;سانه&zwnj;ی که عه&zwnj;قڵ و ژیری به&zwnj;کار ناهێنن.';
$t[101] = 'پێیان بڵێ: ته&zwnj;ماشا بکه&zwnj;ن و سه&zwnj;رنج بده&zwnj;ن: خوا چی دروست کردووه له ئاسمانه&zwnj;کان و زه&zwnj;ویدا، که&zwnj;چی به&zwnj;ڵگه و نیشانه&zwnj;و ئاگاداریه&zwnj;کان سوودیان نیه بۆ ئه&zwnj;و که&zwnj;سانه&zwnj;ی که باوه&zwnj;ڕ ناهێنن و (ڕێبازی ئیمان ناگرنه&zwnj;به&zwnj;ر).';
$t[102] = '(ئه&zwnj;و بێ&zwnj;باوه&zwnj;ڕانه&zwnj;) چاوه&zwnj;ڕێی چی ده&zwnj;که&zwnj;ن، مه&zwnj;گه&zwnj;ر چاوه&zwnj;ڕێی ئه&zwnj;وه ناکه&zwnj;ن که ڕۆژگارێکیان به&zwnj;سه&zwnj;ر بهێنین وه&zwnj;ک ئه&zwnj;وه&zwnj;ی به&zwnj;سه&zwnj;ر ئه&zwnj;وانه&zwnj;ی پێشیاندا هێنامان؟. بڵێ: جا ئێوه چاوه&zwnj;ڕێ بن، به&zwnj;ڕاستی منیش له&zwnj;گه&zwnj;ڵ ئێوه&zwnj;دا له چاوه&zwnj;ڕوانانم.';
$t[103] = 'له&zwnj;وه&zwnj;ودوا (کاتێک بڕیارماندا خوانه&zwnj;ناسان له&zwnj;ناو به&zwnj;رین) پێغه&zwnj;مبه&zwnj;رانمان و ئه&zwnj;وانه&zwnj;ی که باوه&zwnj;ڕیان هێناوه ڕزگار ده&zwnj;که&zwnj;ین، هه&zwnj;ر به&zwnj;و شێوه&zwnj;یه به ئه&zwnj;رکی خۆمانی ده&zwnj;زانین که ئیمانداران ڕزگار بکه&zwnj;ین.';
$t[104] = 'بڵێ: ئه&zwnj;ی خه&zwnj;ڵکینه ئه&zwnj;گه&zwnj;ر ئێوه گومانتان هه&zwnj;یه له ئاین و به&zwnj;رنامه&zwnj;ی من، ئه&zwnj;وه چاک بزانن که&zwnj;: من هه&zwnj;رگیز ئه&zwnj;و شتانه ناپه&zwnj;رستم که ئێوه له جیاتی خوا ده&zwnj;یانپه&zwnj;رستن، به&zwnj;ڵکو ئه&zwnj;و خوایه ده&zwnj;په&zwnj;رستم، که مردنی ئێوه&zwnj;ی به&zwnj;ده&zwnj;سته و من فه&zwnj;رمانم پێدراوه که له ده&zwnj;سته&zwnj;ی باوه&zwnj;ڕداران بم.';
$t[105] = 'هه&zwnj;روه&zwnj;ها فه&zwnj;رمانم پێدراوه که به&zwnj;ڕاست و ڕه&zwnj;وان ڕووت بکه&zwnj;ره ئاین و دینداری به&zwnj;وپه&zwnj;ڕی ملکه&zwnj;چی و دڵسۆزیه&zwnj;وه&zwnj;، هه&zwnj;روه&zwnj;ها فه&zwnj;رمانم پێدراوه که&zwnj;: نه&zwnj;که&zwnj;یت له ده&zwnj;سته&zwnj;ی موشریک و هاوه&zwnj;ڵگه&zwnj;ڕان بیت.';
$t[106] = 'هاناو هاواریش نه&zwnj;به&zwnj;یت بۆ که&zwnj;سێک یان شتێک له جیاتی خوا، که نه قازانجت پێ ده&zwnj;گه&zwnj;یه&zwnj;نێت نه زیان (چونکه قازانج و زیان هه&zwnj;ر به&zwnj;ده&zwnj;ست خوایه&zwnj;)، خۆ ئه&zwnj;گه&zwnj;ر کاری وا ئه&zwnj;نجام بده&zwnj;یت، ئه&zwnj;وه به&zwnj;ڕاستی تۆ له سته&zwnj;مکارانیت.';
$t[107] = '(ئه&zwnj;ی ئینسان) ئه&zwnj;گه&zwnj;ر خوا تووشی ناخۆشی و زیانێکت بکات، ئه&zwnj;وه هیچ که&zwnj;س ناتوانێت فریات بکه&zwnj;وێت و له&zwnj;سه&zwnj;رتی لابه&zwnj;رێت جگه له&zwnj;و زاته&zwnj;، یاخود ئه&zwnj;گه&zwnj;ر بیه&zwnj;وێت تووشی خێر و خۆشیه&zwnj;کت بکات، ئه&zwnj;وه هیچ که&zwnj;س ناتوانێت به&zwnj;ری فه&zwnj;زڵ و به&zwnj;خششی خوا بگرێت، هه&zwnj;ر کام که بیه&zwnj;وێت له به&zwnj;نده&zwnj;کانی به&zwnj;هره&zwnj;وه&zwnj;ر ده&zwnj;کات (له نازو نیعمه&zwnj;تانی بێ&zwnj;شومار) ئه&zwnj;و زاته هه&zwnj;میشه لێخۆشبوو میهره&zwnj;بانه&zwnj;.';
$t[108] = '(ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر صلی الله علیه وسلم) بڵێ: ئه&zwnj;ی خه&zwnj;ڵکینه به&zwnj;ڕاستی حه&zwnj;ق و ڕاستیتان له&zwnj;لایه&zwnj;ن په&zwnj;روه&zwnj;ردگارتانه&zwnj;وه بۆ هاتووه&zwnj;، ئه&zwnj;وه&zwnj;ی ڕێبازی هیدایه&zwnj;تی وه&zwnj;رگرتبێت ئه&zwnj;وه سوودی بۆ خۆیه&zwnj;تی، ئه&zwnj;وه&zwnj;ش گومڕا بووبێت و ڕێی ون کردبێت، ئه&zwnj;وه گومڕاییه&zwnj;که&zwnj;ی له&zwnj;سه&zwnj;ر خۆی ده&zwnj;که&zwnj;وێت و من لێپرسراوو چاودێر نیم به&zwnj;سه&zwnj;ر کارو کرده&zwnj;وه&zwnj;ی ئێوه&zwnj;وه&zwnj;.';
$t[109] = 'تۆ (ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر صلی الله علیه وسلم) هه&zwnj;ر شوێنی ئه&zwnj;و (به&zwnj;رنامه&zwnj;یه&zwnj;) بکه&zwnj;وه که له ڕێگه&zwnj;ی وه&zwnj;حی و نیگاوه به تۆ ده&zwnj;گات و ئارامگرو خۆگربه (له&zwnj;به&zwnj;رامبه&zwnj;ر هه&zwnj;ر کێشه&zwnj;یه&zwnj;که&zwnj;وه که خوانه&zwnj;ناسان بۆت پێش ده&zwnj;هێنن) هه&zwnj;تا خوا خۆی داوه&zwnj;ری ده&zwnj;کات و ئه&zwnj;و زاته چاکترین و دادپه&zwnj;روه&zwnj;رترینی دادوه&zwnj;رانه&zwnj;.';
?>