<?php
$t = array();
$t[1] = '(الر) له&zwnj;سه&zwnj;ره&zwnj;تای سوره&zwnj;تی (البقرة) ڕاڤه کراوه&zwnj;. ئه&zwnj;مه کتێب و په&zwnj;یامێکه (له&zwnj;و پیتانه پێکهاتووه&zwnj;) که ئایه&zwnj;ت و یاساکانی به&zwnj;شێوه&zwnj;یه&zwnj;کی ڕاست و دروست پێکه&zwnj;وه په&zwnj;یوه&zwnj;ست کراون و جۆشدراون، پاشان درێژه&zwnj;یان پێدراوه و ڕوون کراونه&zwnj;ته&zwnj;وه له&zwnj;لایه&zwnj;ن زاتێکه&zwnj;وه که داناو به ئاگاو لێزانه&zwnj;.';
$t[2] = '(گرنگترین په&zwnj;یامی ئه&zwnj;وه&zwnj;یه&zwnj;&zwnj;) که&zwnj;: جگه له خوا که&zwnj;س مه&zwnj;په&zwnj;رستن، به&zwnj;ڕاستی من (واته محمد صلی الله علیه وسلم) له&zwnj;لایه&zwnj;ن خواوه ترسێنه&zwnj;رو مژده&zwnj;ده&zwnj;رم بۆتان.';
$t[3] = 'هه&zwnj;روه&zwnj;ها (خه&zwnj;ڵکینه&zwnj;)! داوای لێخۆشبوون له په&zwnj;روه&zwnj;ردگارتان بکه&zwnj;ن، له&zwnj;وه&zwnj;ودوا بگه&zwnj;ڕێنه&zwnj;وه بۆ لای، ئه&zwnj;وکاته به&zwnj;چاکی ده&zwnj;تانژێنێت به&zwnj;ژیانێکی چاک، تا کاتێکی دیاریکراو، هه&zwnj;روه&zwnj;ها پاداشتی هه&zwnj;موو خاوه&zwnj;ن چاکه&zwnj;یه&zwnj;ک به&zwnj;زیاده&zwnj;وه ده&zwnj;داته&zwnj;وه&zwnj;، خۆ ئه&zwnj;گه&zwnj;ر (ئێوه&zwnj;) پشت هه&zwnj;ڵکه&zwnj;ن و گوێڕایه&zwnj;ڵی فه&zwnj;رمانی خوا نه&zwnj;بن، ئه&zwnj;وه به&zwnj;ڕاستی من ده&zwnj;ترسم که سزای ڕۆژێکی گه&zwnj;وره و سه&zwnj;خت یه&zwnj;خه&zwnj;تان پێ بگرێت (که ڕۆژی قیامه&zwnj;ته&zwnj;، یان ئه&zwnj;و ڕۆژه&zwnj;ی خوای گه&zwnj;وره له ناوتان ده&zwnj;بات).';
$t[4] = 'گه&zwnj;ڕانه&zwnj;وه&zwnj;تان هه&zwnj;ر بۆ لای خوایه&zwnj;، هه&zwnj;ر ئه&zwnj;ویش ده&zwnj;سه&zwnj;ڵاتی به&zwnj;سه&zwnj;ر هه&zwnj;موو شتێکدا هه&zwnj;یه&zwnj;.';
$t[5] = 'ئاگاداربن به&zwnj;ڕاستی ئه&zwnj;و (خوانه&zwnj;ناسانه&zwnj;) خۆیان داده&zwnj;نه&zwnj;وێنن، بۆ ئه&zwnj;وه&zwnj;ی (به&zwnj;خه&zwnj;یاڵی خۆیان) به&zwnj;وه خۆیان له&zwnj;خوای گه&zwnj;وره یان له پێغه&zwnj;مبه&zwnj;ر بشارنه&zwnj;وه&zwnj;، ئاگاداربن که خوا ئاگای لێیانه کاتێک ده&zwnj;چنه ناو جێگه&zwnj;وه و خۆیان داده&zwnj;پۆشن، هه&zwnj;روه&zwnj;ها ده&zwnj;زانێت چی ده&zwnj;شارنه&zwnj;وه و چی ئاشکرا ده&zwnj;که&zwnj;ن (له کردارو گوفتارو نیه&zwnj;تیان) چونکه به&zwnj;ڕاستی ئه&zwnj;و خوایه زانایه به&zwnj;وه&zwnj;ی له دڵ و ده&zwnj;روون و سینه&zwnj;کاندا حه&zwnj;شار دراوه&zwnj;.';
$t[6] = 'هیچ زینده&zwnj;وه&zwnj;رێك نیه له&zwnj;م زه&zwnj;ویه&zwnj;دا ڕزق و ڕۆزیه&zwnj;که&zwnj;ی له&zwnj;سه&zwnj;ر خوا نه&zwnj;بێت و ئاگاداره به&zwnj;شوێنی حه&zwnj;وانه&zwnj;وه و شوێنی ده&zwnj;رچوونیان، هه&zwnj;موو شتێك تۆماره له دۆسیه&zwnj;ی ئاشکرادا له (لوح المحفوظ) دا.';
$t[7] = 'ئه&zwnj;و خوایه زاتێکه ئاسمانه&zwnj;کان و زه&zwnj;وی له ماوه&zwnj;ی شه&zwnj;ش ڕۆژدا دروستکرد، ته&zwnj;ختی فه&zwnj;رمانڕه&zwnj;وایه&zwnj;تی (که دروستکراوێکی زۆر گه&zwnj;وره و شکۆمه&zwnj;نده&zwnj;) له&zwnj;سه&zwnj;ر ئاو بوو، تا تاقیتان بکاته&zwnj;وه که کێتان کاری چاکتر ئه&zwnj;نجام ده&zwnj;دات (فه&zwnj;رمانبه&zwnj;ردارێکی چاکه&zwnj;) و سوێند به&zwnj;خوا ئه&zwnj;گه&zwnj;ر بڵێت به&zwnj;ڕاستی ئێوه زیندوو ده&zwnj;کرێنه&zwnj;وه دوای مردن، بێگومان ئه&zwnj;وانه&zwnj;ی که بێ باوه&zwnj;ڕ بوون ده&zwnj;ڵێن: ئه&zwnj;م قسه و باسه هیچ نیه&zwnj;، ته&zwnj;نها جادوویه&zwnj;کی ئاشکرایه&zwnj;.';
$t[8] = 'سوێند به&zwnj;خوا ئه&zwnj;گه&zwnj;ر سزادانیان دوا بخه&zwnj;ین تا چه&zwnj;ند کاتێکی دیاریکراو، ئه&zwnj;وه بێگومان (به گاڵته&zwnj;وه&zwnj;) ده&zwnj;ڵێن: چی ئه&zwnj;و سزایه&zwnj;ی به&zwnj;ند کردووه و دوای خستووه و کوا بۆ پێش نایه&zwnj;ت؟ ئاگاداربن! ئه&zwnj;و ڕۆژه&zwnj;ی یه&zwnj;خه&zwnj;یان پێده&zwnj;گرێت که&zwnj;سیان لێی ده&zwnj;رباز ناکرێت و له&zwnj;کۆڵیان نابێته&zwnj;وه&zwnj;، ئه&zwnj;و سزایه&zwnj;ش که ئه&zwnj;وان گا&zwnj;ڵته&zwnj;یان پێده&zwnj;کرد، ده&zwnj;یانگرێته&zwnj;وه&zwnj;.';
$t[9] = 'سوێند به&zwnj;خوا ئه&zwnj;گه&zwnj;ر خێر و خۆشی و ئاسووده&zwnj;ییه&zwnj;کمان به ئاده&zwnj;میزاد بچێژین، له&zwnj;وه&zwnj;ودوا (بۆ تاقیکردنه&zwnj;وه&zwnj;ی) لێی بسێنینه&zwnj;وه&zwnj;، ئه&zwnj;وه به&zwnj;ڕاستی ده&zwnj;بینیت ئه&zwnj;و که&zwnj;سه نائومێدو بێ هیوا و سوپاس ناپه&zwnj;زێره و قسه&zwnj;و گوفتاری بۆنی بێ باوه&zwnj;ڕی و خوانه&zwnj;ناسی لێ دێت.';
$t[10] = 'هه&zwnj;روه&zwnj;ها ئه&zwnj;گه&zwnj;ر دوای ئه ته&zwnj;نگانه و ناخۆشیه&zwnj;ی که تووشی بوو، نازو نیعمه&zwnj;تی پێ بچێژین، ئه&zwnj;وه بێگومان ده&zwnj;ڵێت: ئیتر هه&zwnj;رچی هه&zwnj;ژاری و ناخۆشیه له کۆڵم بۆته&zwnj;وه و به&zwnj;سه&zwnj;ر چووه&zwnj;، چونکه به&zwnj;ڕاستی زۆر خۆشحاڵه و شانازی به&zwnj;خۆیه&zwnj;وه ده&zwnj;کات و له خۆی بایی ده&zwnj;بێت (وا ده&zwnj;زانێت هه&zwnj;روا ژیانی به&zwnj;رده&zwnj;وام ده&zwnj;بێت).';
$t[11] = 'جگه له&zwnj;و که&zwnj;سانه&zwnj;ی که خۆگرو ئارامگر بوون و کارو و کرده&zwnj;وه چاکه&zwnj;کانیان ئه&zwnj;نجامداوه (له خۆشی و ناخۆشیدا سروشتی خواناسیان ناگۆڕێت) ئا ئه&zwnj;وانه لێخۆش بوون و پاداشتی گه&zwnj;وره و فراوانیان بۆ هه&zwnj;یه&zwnj;.';
$t[12] = 'جا له&zwnj;وانه&zwnj;یه تۆ (ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر) واز بهێنیت له هه&zwnj;ندێ له&zwnj;و باسانه&zwnj;ی که به (وحی) بۆت ڕه&zwnj;وانه&zwnj;کراوه و دڵته&zwnj;نگ و سه&zwnj;غڵه&zwnj;ت بیت نه&zwnj;وه&zwnj;کو بڵێن: ئه&zwnj;وه بۆ گه&zwnj;نجینه&zwnj;یه&zwnj;ك (له زێڕو زیو) دانه&zwnj;به&zwnj;زیوه&zwnj;ته سه&zwnj;ری، یان فریشته&zwnj;یه&zwnj;ك بۆ گه&zwnj;یاندنی په&zwnj;یامه&zwnj;که نه&zwnj;هاتووه له&zwnj;گه&zwnj;ڵیدا، (تۆ خه&zwnj;فه&zwnj;ت مه&zwnj;خۆ) چونکه به&zwnj;ڕاستی تۆ ته&zwnj;نها ترسێنه&zwnj;ری، خوا خۆیشی ئاگادارو چاودێره به&zwnj;سه&zwnj;ر هه&zwnj;موو شتێکدا (حیسابیان بۆ ده&zwnj;کات).';
$t[13] = 'به&zwnj;ڵکو (له&zwnj;گه&zwnj;ڵ ئه&zwnj;و هه&zwnj;موو به&zwnj;ڵگانه&zwnj;دا) ئه&zwnj;وانه ده&zwnj;ڵێن که محمد قورئانی هه&zwnj;ڵبه&zwnj;ستووه&zwnj;، پێیان بڵێ: جا ئێوه&zwnj;ش ده سووره&zwnj;تی هه&zwnj;ڵباستراو وێنه&zwnj;ی ئه&zwnj;م قورئانه بهێنن، هه&zwnj;ر که&zwnj;سیش له خوا گه&zwnj;وره به&zwnj;ده&zwnj;ر، ده&zwnj;توانن، بانگ بکه&zwnj;ن (بۆ هاوکاریتان په&zwnj;نای بۆ به&zwnj;رن) ئه&zwnj;گه&zwnj;ر ڕاست ده&zwnj;که&zwnj;ن.';
$t[14] = 'جا ئه&zwnj;گه&zwnj;ر (خوانه&zwnj;ناسان) نه&zwnj;هاتن به&zwnj;ده&zwnj;م ئه&zwnj;م داوایه&zwnj;تانه&zwnj;وه&zwnj;، ئه&zwnj;وه ئیتر چاك بزانن و دڵنیابن که به&zwnj;ڕاستی ئه&zwnj;م قورئانه هه&zwnj;ر به ئاگاداری خوای گه&zwnj;وره دابه&zwnj;زێنراوه و بشزانن جگه له&zwnj;ویش خوایه&zwnj;کی تر نیه&zwnj;، ئایا ئیتر ئێوه (ئه&zwnj;ی بێ باوه&zwnj;ڕان) ملکه&zwnj;چ ده&zwnj;بن؟';
$t[15] = 'هه&zwnj;ر که&zwnj;سێك ته&zwnj;نها ژیانی دنیاو ڕازاوه&zwnj;یی و (خۆشگوزه&zwnj;رانی) یه&zwnj;که&zwnj;ی بوێت. ئه&zwnj;وه پاداشتی هه&zwnj;وڵ و کۆششی ئه&zwnj;و جۆره که&zwnj;سانه ده&zwnj;ده&zwnj;ینه&zwnj;وه له دنیادا، ئه&zwnj;وانیش تێیدا هیچیان لێکه&zwnj;م ناکرێته&zwnj;وه&zwnj;.';
$t[16] = 'ئا ئه&zwnj;وانه ئه&zwnj;وانه&zwnj;ن که له ڕۆژی دوایدا جگه له ئاگری دۆزه&zwnj;خ هیچی تریان بۆ نیه و ئه&zwnj;وه&zwnj;ی که کردیان له دنیادا پووچه&zwnj;ڵ بۆوه و بێ نرخه&zwnj;، هه&zwnj;روه&zwnj;ها ئه&zwnj;و کاروکرده&zwnj;وه&zwnj;ی که ده&zwnj;یانکرد به&zwnj;تاڵ و پووچ و بۆشه&zwnj;.';
$t[17] = '(ئایه ئه&zwnj;و جۆره که&zwnj;سانه&zwnj;ی که باس کران) وه&zwnj;کو ئه&zwnj;و که&zwnj;سه وایه که له&zwnj;لایه&zwnj;ن په&zwnj;روه&zwnj;ردگاریه&zwnj;وه هه&zwnj;موو شتێکی بۆ ڕوون کراوه&zwnj;ته&zwnj;وه&zwnj;، (ئه&zwnj;میش باوه&zwnj;ڕی پێیه&zwnj;تی چونکه&zwnj;) پێغه&zwnj;مبه&zwnj;رێك به&zwnj;ڵگه&zwnj;نامه&zwnj;ی خوا ده&zwnj;خوێنێته&zwnj;وه و له پێش ئه&zwnj;میشه&zwnj;وه په&zwnj;یامه&zwnj;که&zwnj;ی موسا (که ته&zwnj;وراته&zwnj;) پێشه&zwnj;واو ڕه&zwnj;حمه&zwnj;ت بوووه&zwnj;، ئه&zwnj;و جۆره که&zwnj;سانه&zwnj;ی (که ژیرو هۆشمه&zwnj;دن) باوه&zwnj;ڕی پێ ده&zwnj;هێنن، جا ئه&zwnj;و ده&zwnj;سته و کۆمه&zwnj;ڵانه&zwnj;ی که باوه&zwnj;ڕ به&zwnj;م په&zwnj;یامه ناکه&zwnj;ن، ئه&zwnj;وه ئاگری دۆزه&zwnj;خ چاوه&zwnj;ڕێیانه&zwnj;، نه&zwnj;که&zwnj;یت تۆ له&zwnj;جۆره گومانێکدا بیت له&zwnj;م قورئانه&zwnj;و له&zwnj;م په&zwnj;یامه&zwnj;، چونکه به&zwnj;ڕاستی ئه&zwnj;وه حه&zwnj;قیقه&zwnj;ته و له&zwnj;لایه&zwnj;ن په&zwnj;روه&zwnj;ردگارته&zwnj;وه بۆت ڕه&zwnj;وانه کراوه&zwnj;، به&zwnj;ڵام زۆربه&zwnj;ی خه&zwnj;ڵکی باوه&zwnj;ڕ ناهێنن.';
$t[18] = 'کێ له&zwnj;وه سته&zwnj;مکارتره که درۆ به&zwnj;ناوی خواوه هه&zwnj;ڵببه&zwnj;ستێت (به&zwnj;وه&zwnj;ی که بڵێ خوا هاوه&zwnj;ڵ یان کوڕی هه&zwnj;یه&zwnj;) ئه&zwnj;وانه نیشانی په&zwnj;روه&zwnj;ردگاریان ده&zwnj;درێن و شایه&zwnj;ته&zwnj;کانی قیامه&zwnj;ت، که فریشته و پێغه&zwnj;مبه&zwnj;ران و بانگخوازانن ده&zwnj;ڵێن: ئا ئه&zwnj;وانه ئه&zwnj;وانه&zwnj;ن درۆیان کرد به&zwnj;ده&zwnj;م په&zwnj;روه&zwnj;ردگاریانه&zwnj;وه&zwnj;، ئاگاداربن! نه&zwnj;فره&zwnj;تی خوا له&zwnj;سه&zwnj;ر سته&zwnj;مکارانه&zwnj;...';
$t[19] = 'ئه&zwnj;وانه&zwnj;ی به&zwnj;نه&zwnj;رم و نیانی و له&zwnj;سه&zwnj;رخۆ به&zwnj;رهه&zwnj;ڵستی په&zwnj;یامی خوا ده&zwnj;که&zwnj;ن و به&zwnj;لاری ده&zwnj;یانه&zwnj;وێت و ده&zwnj;یانه&zwnj;وێت به هه&zwnj;ڵه&zwnj;و ناته&zwnj;واوی بیناسێنن به&zwnj;خه&zwnj;ڵکی و ئه&zwnj;وانه به&zwnj;رۆژی دوایی بێ بڕوان.';
$t[20] = 'ئا ئه&zwnj;وانه خۆیان ده&zwnj;سته&zwnj;وستان نه&zwnj;کردووه له زه&zwnj;ویداو له ده&zwnj;ستی خوا ده&zwnj;رچن، بێجگه له خوا هیچ په&zwnj;ناو یارمه&zwnj;تیده&zwnj;رێکیان نیه (که&zwnj;س فریایان ناکه&zwnj;وێت) و سزایان بۆ چه&zwnj;ند به&zwnj;رابه&zwnj;ر ده&zwnj;کرێت، ئه&zwnj;وانه له دنیادا ئه&zwnj;وه&zwnj;نده ڕقیان له حه&zwnj;ق و ڕێگه&zwnj;ی خوا بوو، ئه&zwnj;وانه نه&zwnj;یانده&zwnj;توانی گوێ بگرن و ڕاستی ببینن.';
$t[21] = 'ئا ئه&zwnj;وانه ئه&zwnj;وانه&zwnj;ن که خۆیان فه&zwnj;وتاند و ئه&zwnj;و باس و درۆیانه&zwnj;ی که له دنیادا هه&zwnj;ڵیانده&zwnj;به&zwnj;ست لێیان ون بوو.';
$t[22] = 'گومانی تێدا نیه که به&zwnj;ڕاستی ئه&zwnj;وانه له قیامه&zwnj;تدا زۆر خه&zwnj;ساره&zwnj;تمه&zwnj;ند و دۆڕاوترن.';
$t[23] = 'به&zwnj;ڕاستی ئه&zwnj;وانه&zwnj;ی بڕوایان هێناوه به&zwnj;خواو ڕاستی به&zwnj;رنامه&zwnj;که&zwnj;ی، کرده&zwnj;وه چاکه&zwnj;کانیشیان ئه&zwnj;نجامداوه&zwnj;و گه&zwnj;ڕاونه&zwnj;ته&zwnj;وه بۆ لای په&zwnj;روه&zwnj;ردگاریان و ملکه&zwnj;چ و دڵ ئارام بوون له به&zwnj;رامبه&zwnj;ر فه&zwnj;رمانه&zwnj;کانی، ئا ئه&zwnj;وانه خاوه&zwnj;ن و نیشته&zwnj;جێی به&zwnj;هه&zwnj;شتن و به هه&zwnj;تا هه&zwnj;تایی تیایدا ده&zwnj;مێننه&zwnj;وه&zwnj;.';
$t[24] = 'وێنه&zwnj;و ئه&zwnj;م دوو کۆمه&zwnj;ڵه (کۆمه&zwnj;ڵی ئیمانداران و کۆمه&zwnj;ڵی بێ باوه&zwnj;ڕان) وه&zwnj;ك کوێرو که&zwnj;ڕ، چاوساغ و بیسه&zwnj;ر وایه&zwnj;، ئایا ئه&zwnj;م دووانه وێنه&zwnj;یان وه&zwnj;ك یه&zwnj;که&zwnj;، ئه&zwnj;وه بۆ بیر ناکه&zwnj;نه&zwnj;وه&zwnj;؟!';
$t[25] = 'سوێند به&zwnj;خوا به&zwnj;ڕاستی ئێمه نوحمان نارد بۆ لای قه&zwnj;وم و خزمه&zwnj;کانی، (پێی وتن) بێگومان من بۆ ئێوه داچڵه&zwnj;کێنه&zwnj;رێکی ئاشکرام (ئه&zwnj;گه&zwnj;ر له فه&zwnj;رمانی خوا ده&zwnj;رچن تووشی سزا ده&zwnj;بن، خۆ ئێوه باش من ده&zwnj;ناسن).';
$t[26] = 'که جگه له خوا (که&zwnj;س و هیچی تر) نه&zwnj;په&zwnj;رستن، چونکه من به&zwnj;ڕاستی ده&zwnj;ترسم سزای ڕۆژێکی به&zwnj;ئێش یه&zwnj;خه&zwnj;تان پێ بگرێت (هه&zwnj;مووتان له&zwnj;ناو بچن).';
$t[27] = 'جا ئه&zwnj;و ده&zwnj;سته ده&zwnj;سه&zwnj;ڵاتداره&zwnj;ی که بێ باوه&zwnj;ڕ بوون له هۆزی نوح وتیان: ئێمه تۆ ته&zwnj;نها وه&zwnj;ك یه&zwnj;کێك له خۆمان ده&zwnj;بینین و نابینین ئه&zwnj;وانه&zwnj;ی شوێنی تۆ که&zwnj;وتوون جگه له&zwnj;وانه&zwnj;ی که هه&zwnj;ژارو داماوه&zwnj;کانمانن و دووربین نین و ژیریان که&zwnj;&zwnj;مه و ساده&zwnj;ن، هه&zwnj;روه&zwnj;ها نابینین ئێوه هیچ شتێکی زیاترتان به&zwnj;سه&zwnj;ر ئێمه&zwnj;دا هه&zwnj;بێت، به&zwnj;ڵکو ئێمه وا گومان ده&zwnj;به&zwnj;ین ئێوه درۆزنن.';
$t[28] = 'نوح وتی: ئه&zwnj;ی خزمه&zwnj;کانم هه&zwnj;واڵم بده&zwnj;نێ: ئه&zwnj;گه&zwnj;ر من له&zwnj;سه&zwnj;ر ڕێگه&zwnj;و به&zwnj;رنامه&zwnj;یه&zwnj;کی ڕاست و دروستبم له&zwnj;لایه&zwnj;ن په&zwnj;روه&zwnj;ردگارمه&zwnj;وه و خێرو چاکه&zwnj;و به&zwnj;زه&zwnj;یی خۆی پێ به&zwnj;خشیبم له&zwnj;لایه&zwnj;ن خۆیه&zwnj;وه&zwnj;، ئینجا ئه&zwnj;مانه له ئێوه ساراوه&zwnj;ته&zwnj;وه و نایبینن؟! ئایا به&zwnj;زۆر ئیمان له دڵتاندا دروست بکه&zwnj;ین و بتانخه&zwnj;ینه سه&zwnj;ر ئه&zwnj;م ڕێگه&zwnj;یه&zwnj;، له&zwnj;کاتێکدا ئێوه لێی بێزارن و خوازیاری نین؟!!';
$t[29] = 'ئه&zwnj;ی خزمه&zwnj;کانم (خۆ من) له بری ئه&zwnj;م بانگه&zwnj;واز و ئامۆژگاریانه&zwnj;م داوای هیچ ماڵ و خه&zwnj;رجیتان لێناکه&zwnj;م، کرێ و پاداشتم ته&zwnj;نها لای خوایه&zwnj;، من هه&zwnj;رگیز ئه&zwnj;وانه&zwnj;ی بڕوایان هێناوه له خۆم دوور ناخه&zwnj;مه&zwnj;وه و ده&zwnj;ریان ناکه&zwnj;م، چونکه بێگومان ئه&zwnj;وان ئه&zwnj;گهن به&zwnj;خوای خۆیان، حیسابیان لای خوایه&zwnj;، به&zwnj;ڵام به&zwnj;ڕاستی من ئێوه ئه&zwnj;بینم هۆزێکن خۆتان گێل و نه&zwnj;فام ده&zwnj;که&zwnj;ن (به&zwnj;وه&zwnj;ی به که&zwnj;م سه&zwnj;یری ئیمانداوانی ده&zwnj;وری من ده&zwnj;که&zwnj;ن له&zwnj;به&zwnj;ر بێ که&zwnj;سی و هه&zwnj;ژاریان).';
$t[30] = 'ئه&zwnj;ی خزمه&zwnj;کانم کێ ئه&zwnj;توانێت یارمه&zwnj;تیم بدات به&zwnj;رامبه&zwnj;ر خوای گه&zwnj;وره (له سزای خوا بمپارێزێت) ئه&zwnj;گه&zwnj;ر ئه&zwnj;و ئیماندارو دۆستانه&zwnj;ی خوا ده&zwnj;ربکه&zwnj;م و له خۆمیان دوور بخه&zwnj;مه&zwnj;وه و ئایا بیرناکه&zwnj;ناوه (که شتی وا ناشێت و دروست نیه بۆ که&zwnj;سێك خواناس بێت)؟';
$t[31] = 'خۆ من پێتان ناڵێم گه&zwnj;نجینه&zwnj;کانی خوا لای منه و من شتی نادیارو غه&zwnj;یبیش نازانم، ناشڵێم به&zwnj;ڕاستی من فریشته&zwnj;م، هه&zwnj;روه&zwnj;ها ناڵێم به&zwnj;و ئیماندارانه&zwnj;ی له&zwnj;به&zwnj;ر چاوی ئێوه که&zwnj;م نرخن، خوا گه&zwnj;وره هه&zwnj;رگیز خێریان به&zwnj;سه&zwnj;ردا نارژێنێت، خوا خۆی ئاگاداره به&zwnj;وه&zwnj;ی له ده&zwnj;روونیاندا هه&zwnj;یه&zwnj;، بێگومان من ئه&zwnj;و کاته (ئه&zwnj;گه&zwnj;ر وابکه&zwnj;م) له سته&zwnj;مکارانم.';
$t[32] = '(خه&zwnj;ڵکه&zwnj;که&zwnj;) وتیان: ئه&zwnj;ی نو&zwnj;ح به&zwnj;ڕاستی زۆر له&zwnj;سه&zwnj;ری چوویت و زیاد له پێویست به&zwnj;ربه&zwnj;ره&zwnj;کانی و شه&zwnj;ڕه قسه&zwnj;ت له&zwnj;گه&zwnj;ڵدا کردین ده&zwnj;ی ئه&zwnj;و سزایه&zwnj;مان بۆ بهێنه که هه&zwnj;ڕه&zwnj;شه&zwnj;ی پێده&zwnj;که&zwnj;یت و هه&zwnj;میشه باسی ده&zwnj;که&zwnj;یت و ده&zwnj;مانترسێنی پێی، ئه&zwnj;گه&zwnj;ر تۆ له ڕاستگۆیانیت.';
$t[33] = 'نوح له وه&zwnj;ڵامدا وتی: ئه&zwnj;وه به ده&zwnj;ست خوایه و به&zwnj;ڕاستی ته&zwnj;نها ئه&zwnj;و بۆتان پێش ده&zwnj;هێنێت ئه&zwnj;گه&zwnj;ر بیه&zwnj;وێت و ئێوه ده&zwnj;سته&zwnj;وسانکه&zwnj;ری خوا نین ناتوانن (ئه&zwnj;سته&zwnj;مه ده&zwnj;رباز بوونتان).';
$t[34] = '(وادیاره&zwnj;) ئامۆژگاری و دڵسۆزیم سوودتان پێناگه&zwnj;یه&zwnj;نێت ئه&zwnj;گه&zwnj;رچی بمه&zwnj;وێت به&zwnj;رده&zwnj;وامیشبم له&zwnj;سه&zwnj;ر ئامۆژگاریتان، ئه&zwnj;گه&zwnj;ر خوا بیه&zwnj;وێت سه&zwnj;رگه&zwnj;ردانتان بکات ئه&zwnj;وه ئه&zwnj;و زاته په&zwnj;روه&zwnj;ردگارتانه و هه&zwnj;ر بۆ لای ئه&zwnj;ویش ده&zwnj;برێنه&zwnj;وه&zwnj;.';
$t[35] = 'به&zwnj;ڵکو (بێ باوه&zwnj;ڕانی مه&zwnj;ککه&zwnj;) ده&zwnj;ڵێن: ئه&zwnj;م باس و بابه&zwnj;تانه (محمد) خۆی هه&zwnj;ڵی به&zwnj;ستووه&zwnj;، تۆیش بڵێ: ئه&zwnj;گه&zwnj;ر من هه&zwnj;ڵیبه&zwnj;ستم ئه&zwnj;وه تاوانه&zwnj;که&zwnj;ی له&zwnj;سه&zwnj;ر خۆمه و منیش دووره په&zwnj;رێزو به&zwnj;ریم له&zwnj;و تاوانه&zwnj;ی که ده&zwnj;یکه&zwnj;م.';
$t[36] = '(سه&zwnj;ره&zwnj;&zwnj;نجام) وه&zwnj;حی و په&zwnj;یامی خوا ڕه&zwnj;وانه&zwnj;کرا بۆ نوح که چاك بزانه به&zwnj;ڕاستی هه&zwnj;رگیز که&zwnj;سی تر بڕوا ناهێنێت له قه&zwnj;ومه&zwnj;که&zwnj;ت جگه له&zwnj;وه&zwnj;ی که به&zwnj;ڕاستی بڕوای هێناوه&zwnj;، کوابوو خه&zwnj;فه&zwnj;تبار مه&zwnj;به به&zwnj;و کرده&zwnj;وه ناشیرین و ناپه&zwnj;سه&zwnj;ندانه&zwnj;ی ئه&zwnj;نجامیان ده&zwnj;دا.';
$t[37] = 'که&zwnj;شتییه&zwnj;که&zwnj;ش دروست بکه به&zwnj;چاودێری و فه&zwnj;رانی ئێمه&zwnj;، (کاتی تۆفانه&zwnj;که&zwnj;) تکامان لێ نه&zwnj;که&zwnj;یت بۆ ئه&zwnj;وانه&zwnj;ی له سنووری ڕاستی ده&zwnj;رچوون و تاوانبارن، چونکه به&zwnj;ڕاستی ئه&zwnj;وانه بڕیاری خنکاندن و تیاچوونیان بۆ دراوه&zwnj;.';
$t[38] = '(نوح) یش ده&zwnj;ستی کرد به دروستکردنی که&zwnj;شتیه&zwnj;که&zwnj;، هه&zwnj;ر جارێك تاقمێك له هۆزه&zwnj;که&zwnj;ی به&zwnj;لایدا تێده&zwnj;په&zwnj;رین، گاڵته&zwnj;یان پێ ده&zwnj;کرد، (نوح) وتی ئه&zwnj;گه&zwnj;ر ئێسته ئێوه گا&zwnj;ڵته به ئێمه ده&zwnj;که&zwnj;ن، ئه&zwnj;وه به&zwnj;ڕاستی ئێمه&zwnj;ش ڕۆژێك دێت گا&zwnj;ڵته به ئێوه بکه&zwnj;ین وه&zwnj;ك چۆن ئێوه گا&zwnj;ڵته به ئێمه ده&zwnj;که&zwnj;ن.';
$t[39] = 'جا له داهاتوودا ده&zwnj;زانن کێ سزایه&zwnj;کی بۆ دێت که ڕیسوای بکات، پاشان له قیامه&zwnj;تدا دووچاری سزایه&zwnj;کی به&zwnj;رده&zwnj;وام و هه&zwnj;میشه ده&zwnj;بێت.';
$t[40] = '(ئه&zwnj;م کێشه&zwnj;یه به&zwnj;رده&zwnj;وام بوو) هه&zwnj;تا ئه&zwnj;و کاته&zwnj;ی بڕیارمان هات و فه&zwnj;رمان ده&zwnj;رچوو (به له&zwnj;ناوبردنی بێ باوه&zwnj;ڕه&zwnj;کان) و له ته&zwnj;نووره پڕ له ئاگره&zwnj;که&zwnj;وه ئاو فواره&zwnj;ی کرد (که ئه&zwnj;وه نیشانه&zwnj;ی ده&zwnj;ست پێکردنی تۆڵه خواییه&zwnj;که بوو) ئه&zwnj;وسا وتمان: ئه&zwnj;ی نوح، له هه&zwnj;موو نێرو و مێیه&zwnj;ك دووان هه&zwnj;ڵبگره له که&zwnj;شتیه&zwnj;که&zwnj;داو ماڵ و خێزانت، جگه له&zwnj;وانه&zwnj;ی که بڕیاریان له&zwnj;سه&zwnj;ر دراوه تووشی تۆڵه ببن به&zwnj;هۆی بێ باوه&zwnj;ڕیانه&zwnj;وه&zwnj;، ئه&zwnj;وانه&zwnj;ش هه&zwnj;ڵگره که باوه&zwnj;ڕیان هێناوه (شایا&zwnj;نی باسه&zwnj;) که&zwnj;سیش بڕوای به&zwnj;نوح نه&zwnj;کردو له&zwnj;گه&zwnj;ڵیدا نه&zwnj;بوو، جگه له&zwnj;که&zwnj;سانێکی که&zwnj;م نه&zwnj;بێت.';
$t[41] = 'نوح وتی: سواربن و برۆنه ناو که&zwnj;شتیه&zwnj;که&zwnj;وه&zwnj;، به&zwnj;ناوی خواوه&zwnj;یه ڕۆیشتنی و وه&zwnj;ستانی، بێگومان په&zwnj;روه&zwnj;ردگارم زۆر لێ خۆشبووه و زۆر به به&zwnj;زه&zwnj;ییه&zwnj;.';
$t[42] = 'که&zwnj;شتیه&zwnj;که&zwnj;ش ده&zwnj;یبردن به&zwnj;ناو شه&zwnj;پۆلی وه&zwnj;ك چیادا، نوح بانگی کرده کوڕه&zwnj;که&zwnj;ی (ئه&zwnj;وه&zwnj;یان که بێ باوه&zwnj;ڕ بوو) له شوێنێکی چه&zwnj;په&zwnj;کدا گیری خوارد بوو پێی وت: ڕۆڵه گیان، له&zwnj;گه&zwnj;ڵ ئێمه&zwnj;دا سواربه و له&zwnj;گه&zwnj;ڵ بێ باوه&zwnj;ڕاندا مه&zwnj;به&zwnj;.';
$t[43] = 'کوڕه&zwnj;که&zwnj;ی وتی: په&zwnj;نا ئه&zwnj;به&zwnj;مه لای ئه&zwnj;و چیایه&zwnj;و ده&zwnj;مپارێزیت له&zwnj;م زریان و ئاوه&zwnj;، (نوح) وتی: ئه&zwnj;مڕۆ هیچ په&zwnj;ناده&zwnj;رێك نیه له تۆڵه و خه&zwnj;شمی خوا که&zwnj;س بپارێزێت مه&zwnj;گه&zwnj;ر که&zwnj;سێك خوا خۆی ڕه&zwnj;حمی پێ بکات، ئه&zwnj;وسا (دوای ئه&zwnj;م وت و وێژه&zwnj;) شه&zwnj;پۆلی ئاو دای به&zwnj;نێوانیاندا و جا چووه ڕیزی خنکاوه&zwnj;کانه&zwnj;وه&zwnj;.';
$t[44] = 'پاشان فه&zwnj;رماندرا به زه&zwnj;وی: ئاوه&zwnj;که&zwnj;ت با ڕۆبچێت به&zwnj;ناختدا، ئه&zwnj;ی ئاسمان: تۆش با به&zwnj;س بێت و باران مه&zwnj;بارێنه&zwnj;، ئیتر ئاوه&zwnj;که ڕۆچوو، فه&zwnj;رمانیش به&zwnj;ڕێکی و ته&zwnj;واوی ئه&zwnj;نجامدرا، که&zwnj;شتیه&zwnj;که&zwnj;ش له&zwnj;سه&zwnj;ر کێوه جودی (که له کوردستانی باکووره&zwnj;) وه&zwnj;ستاو له&zwnj;نگه&zwnj;ری گرت و وترا: ده&zwnj;ك دووری له به&zwnj;زه&zwnj;یی خواو تیاچوون بۆ به&zwnj;ره&zwnj;ی سته&zwnj;مکاران.';
$t[45] = '(پاش هێور بوونه&zwnj;وه&zwnj;ی زریانه&zwnj;که&zwnj;) نوح هاواری له خوا کردو جا وتی: ئه&zwnj;ی په&zwnj;روه&zwnj;ردگارم کوڕه&zwnj;که&zwnj;م له خێزانی من بوو، بێگومان په&zwnj;یمانی تۆش ڕاسته و (کاتی خۆی فه&zwnj;رمووت خێزانی تۆ ڕزگارن له تیاچوون) و تۆیش زاناترین و دادپه&zwnj;روه&zwnj;رترینی فه&zwnj;رمانڕه&zwnj;وایانیت.';
$t[46] = 'خوای گه&zwnj;وره&zwnj;ش فه&zwnj;رمووی: ئه&zwnj;ی نوح به&zwnj;ڕاستی ئه&zwnj;و له خێزانی تۆ نه&zwnj;بوو، چونکه به&zwnj;ڕاستی ئه&zwnj;و کرداری چاك نه&zwnj;بوو، که&zwnj;وابوو داوای شتێکم لێ مه&zwnj;که که زانیاریت ده&zwnj;رباره&zwnj;ی نیه&zwnj;، بێگومان من ئامۆژگاریت ده&zwnj;که&zwnj;م که کارێك نه&zwnj;که&zwnj;یت خۆت بخه&zwnj;یته ڕیزی نه&zwnj;فامانه&zwnj;وه&zwnj;.';
$t[47] = '(ئینجا نوح) وتی: ئه&zwnj;ی په&zwnj;روه&zwnj;ردگارم، به&zwnj;ڕاستی من په&zwnj;نا ده&zwnj;گرم بۆ تۆ که داوای شتێك بکه&zwnj;م زانیاریم پێی نه&zwnj;بێت، خۆ ئه&zwnj;گه&zwnj;ر لێم خۆش نه&zwnj;بیت و به&zwnj;زه&zwnj;ییت پێمدا نه&zwnj;یه&zwnj;ته&zwnj;وه ئه&zwnj;وه ده&zwnj;چمه ڕیزی خه&zwnj;ساره&zwnj;تمه&zwnj;ندانه&zwnj;وه&zwnj;.';
$t[48] = '(ئه&zwnj;وسا له&zwnj;لایه&zwnj;ن خوای گه&zwnj;وره&zwnj;وه&zwnj;) وترا: ئه&zwnj;ی نوح وه&zwnj;ره خواره&zwnj;وه و دابه&zwnj;زه له که&zwnj;شتیه&zwnj;که&zwnj;و له چیای جودی بۆ ده&zwnj;شتایی به&zwnj;بێوه&zwnj;یی و سه&zwnj;لامه&zwnj;تی له&zwnj;لایه&zwnj;ن ئێمه&zwnj;وه&zwnj;، به&zwnj;ره&zwnj;که&zwnj;تی زۆریش له&zwnj;سه&zwnj;ر تۆو ئه&zwnj;و کۆمه&zwnj;ڵ و خه&zwnj;ڵکانه&zwnj;ی له&zwnj;گه&zwnj;ڵ تۆدان، خه&zwnj;ڵکانێکی تریش له&zwnj;مه&zwnj;و دوا دێن و له نازو نیعمه&zwnj;ت به&zwnj;هره&zwnj;وه&zwnj;ریان ده&zwnj;که&zwnj;ین، پاشان (به&zwnj;هۆی لاری و بێ برواییانه&zwnj;وه&zwnj;) سزای به&zwnj;سۆیان تووش ده&zwnj;بێت له&zwnj;لایه&zwnj;ن ئێمه&zwnj;وه&zwnj;.';
$t[49] = 'ئه&zwnj;ی محمد (صلی الله علیه وسلم) ئه&zwnj;و باسانه&zwnj;ی بۆمان کردیت له هه&zwnj;واڵه نادیاره&zwnj;کانه که بۆت ده&zwnj;نێرین، نه&zwnj;تۆ ده&zwnj;تزانی و نه هۆز و نه&zwnj;ته&zwnj;وه&zwnj;که&zwnj;شت پێش ئه&zwnj;م باسه&zwnj;ی ئێمه&zwnj;، جا خۆگرو به ئارام به&zwnj;، چونکه به&zwnj;ڕاستی پا&zwnj;شه&zwnj;رۆژ بۆ خواناس و پارێزکارانه&zwnj;.';
$t[50] = 'بۆ لای هۆزی (عاد) یش، هودی برایانمان نارد، وتی: ئه&zwnj;ی خزمه&zwnj;کانم، خوا بپه&zwnj;ر&zwnj;ستن و فه&zwnj;رمانبه&zwnj;رداری ئه&zwnj;و بن، هیچ خوایه&zwnj;کی ترتان نیه جگه له&zwnj;و، ئێوه هیچ ڕاستیه&zwnj;کتان نیه جگه له درۆ هه&zwnj;ڵبه&zwnj;ستن.';
$t[51] = '(هود وتی) ئه&zwnj;ی خزمه&zwnj;کانم من له&zwnj;سه&zwnj;ر ئه&zwnj;م کارو بانگه&zwnj;وازه خواییه&zwnj;ی که به&zwnj;ئێوه&zwnj;ی ڕاده&zwnj;گه&zwnj;یه&zwnj;نم هیچ کرێ و پاداشتێکتان لێ داوا ناکه&zwnj;م، کرێ و پاداشتم ته&zwnj;نها له&zwnj;سه&zwnj;ر ئه&zwnj;و زاته&zwnj;یه که به&zwnj;دی هێناوم، باشه ئه&zwnj;وه بیر ناکه&zwnj;نه&zwnj;وه و ژیرنابن؟!';
$t[52] = 'ئه&zwnj;ی خزمه&zwnj;کانم داوای لێخۆسبوون له په&zwnj;روه&zwnj;ردگارتان بکه&zwnj;ن، پاشان بگه&zwnj;رێنه&zwnj;وه بۆ لای ئه&zwnj;و، ئه&zwnj;وه له ئاسمانه&zwnj;وه بارانی زۆری پر به&zwnj;ره&zwnj;که&zwnj;تتان بۆ ده&zwnj;بارێنێت، هێزو ده&zwnj;سه&zwnj;ڵاتتان بۆ زیاد ده&zwnj;کات، زیاد له هێزو ده&zwnj;سه&zwnj;ڵاتی ئاسایی خۆتان، ڕوو وه&zwnj;رمه&zwnj;گێرن له&zwnj;م باس و ئامۆژگاریانه له&zwnj;کاتێکدا که هه&zwnj;میشه تاوانبارن.';
$t[53] = 'وتیان ئه&zwnj;ی هود: به&zwnj;ڵگه&zwnj;یه&zwnj;کی زۆر ڕۆشنت بۆ نه&zwnj;هێناوین تا به ته&zwnj;واوی ئێمه قه&zwnj;ناعه&zwnj;ت بکه&zwnj;ین (که تۆ پێغه&zwnj;مبه&zwnj;ری خوایت) و ئێمه&zwnj;ش واز له خواکانی خۆمان ناهێنین به قسه&zwnj;ی تۆ، ئێمه ئیمان و بڕوامان نیه به&zwnj;ڕاستێتی تۆ.';
$t[54] = 'ئێمه هیچ ناڵێین ئه&zwnj;وه نه&zwnj;بێت: وا دیاره هه&zwnj;ندێ له خواکانمان به&zwnj;خراپ ده&zwnj;ستیان لێ وه&zwnj;شاندوویت!! هود وتی: ده&zwnj;ی من به&zwnj;ڕاستی خوا ده&zwnj;که&zwnj;م به&zwnj;شایه&zwnj;ت و ئێوه&zwnj;ش به&zwnj;شایه&zwnj;ت بن که بێگومان من به&zwnj;ریم له&zwnj;وه&zwnj;ی ئێوه ده&zwnj;یکه&zwnj;نه شه&zwnj;ریك و هاوه&zwnj;ڵ بۆ خوا...';
$t[55] = 'جگه له خوا خۆتان به په&zwnj;رستراوه&zwnj;کانتانه&zwnj;وه فێڵ و پیلانم لێ بکه&zwnj;ن هه&zwnj;ر هه&zwnj;مووتان، که&zwnj;وابوو وه&zwnj;رن هه&zwnj;مووتان کۆببنه&zwnj;وه و نه&zwnj;خشه بکێشن بۆ له&zwnj;ناو بردنم، پاشان مۆڵه&zwnj;تیشم مه&zwnj;ده&zwnj;ن!...';
$t[56] = 'چونکه به&zwnj;ڕاستی من پشتم به&zwnj;خوا به&zwnj;ستووه&zwnj;، که په&zwnj;روه&zwnj;ردگاری من و په&zwnj;روه&zwnj;ردگاری ئێوه&zwnj;یشه&zwnj;، هیچ گیانله&zwnj;به&zwnj;رێك نیه جڵه&zwnj;وی به&zwnj;ده&zwnj;ست خوای گه&zwnj;وره&zwnj;وه نه&zwnj;بێت، بێگومان په&zwnj;روه&zwnj;ردگارم له&zwnj;سه&zwnj;ر به&zwnj;رنامه&zwnj;و ڕێبازێکی ڕاست و دروسته&zwnj;.';
$t[57] = 'جا ئه&zwnj;گه&zwnj;ر ئێوه گوێم بۆ نه&zwnj;گرن و پشت بکه&zwnj;نه ئه&zwnj;م به&zwnj;رنامه&zwnj;یه&zwnj;، ئه&zwnj;وه بێگومان من پێم ڕاگه&zwnj;یاندن ئه&zwnj;و به&zwnj;رنامه و په&zwnj;یامه خواییه&zwnj;ی به مندا نێرراوه بۆتان و په&zwnj;روه&zwnj;ردگارم (ئه&zwnj;گه&zwnj;ر ملکه&zwnj;چ نه&zwnj;بن) ئێوه له&zwnj;ناو ده&zwnj;بات و هۆزێکی تر ده&zwnj;خاته شوێنی ئێوه&zwnj;، ئێوه ناتوانن هیچ زیانێك به&zwnj;و بگه&zwnj;یه&zwnj;نن به&zwnj;ڕاستی په&zwnj;روه&zwnj;ردگاری من به&zwnj;سه&zwnj;ر هه&zwnj;موو شتێکدا چاودێرو به توانایه&zwnj;.';
$t[58] = 'کاتێکیش فه&zwnj;رمانی ئێمه هات (که بڕیاری تیاچوونی هۆزی عاد بوو) و ئه&zwnj;وه هود و ئه&zwnj;وانه&zwnj;ی بڕوایان هێنابوو له&zwnj;گه&zwnj;ڵ ئه&zwnj;ودا بوون ڕزگاریشمان کردن به&zwnj;هۆی به&zwnj;زه&zwnj;یی و لێبورده&zwnj;یی خۆمانه&zwnj;وه له سزایه&zwnj;کی زۆر قورس و گران.';
$t[59] = 'به&zwnj;ڵێ! ئه&zwnj;وه هۆزی عاد بوو، بێ باوه&zwnj;ڕ بوون و ئیمانیان نه&zwnj;هێنا به فه&zwnj;رمانه&zwnj;کانی په&zwnj;روه&zwnj;ردگاریان، لاشیاندا له به&zwnj;رنامه&zwnj;ی پێغه&zwnj;مبه&zwnj;رانی خواو شوێن فه&zwnj;رمانی هه&zwnj;موو زۆردارو سه&zwnj;رسه&zwnj;خت و خوانه&zwnj;ناسێك که&zwnj;وتن.';
$t[60] = 'جا هه&zwnj;ر له&zwnj;م جیهانه&zwnj;دا نه&zwnj;فرین لێکراو بوون و دوور بوون له به&zwnj;زه&zwnj;یی خوا، له ڕۆژی قیامه&zwnj;تیشدا (هه&zwnj;ر نه&zwnj;فرین لێکراو ده&zwnj;بن) و ئاگاداربن که بێگومان هۆزی عاد بێ باوه&zwnj;ڕ بوون و بێ وه&zwnj;فاییان ده&zwnj;ربڕی به&zwnj;رامبه&zwnj;ر په&zwnj;روه&zwnj;ردگاریان، ئاگاداربن که دووریی و بێ نرخی بۆ هۆزی عادی قه&zwnj;ومی هود.';
$t[61] = 'بۆ هۆزی ثه&zwnj;مودیش، صالح ی برایانمان ڕه&zwnj;وانه کرد، وتی: ئه&zwnj;ی خزمه&zwnj;کانم گوێڕایه&zwnj;ڵی خوای گه&zwnj;وره بکه&zwnj;ن جگه له&zwnj;و زاته هیچ خوایه&zwnj;کی ترتان نیه&zwnj;، ئه&zwnj;و به&zwnj;رپای کردوون له زه&zwnj;ویداو داوای لێکردوون که ئاوه&zwnj;دانی بکه&zwnj;نه&zwnj;وه&zwnj;، جا که&zwnj;وابوو داوای لێ خۆشبوونی لێ بکه&zwnj;ن پاشان بگه&zwnj;رێنه&zwnj;وه بۆ لای و فه&zwnj;رمانه&zwnj;کانی به&zwnj;جێ بهێنن، چونکه به&zwnj;ڕاستی په&zwnj;روه&zwnj;ردگاری من زۆر نزیکه و نزا وه&zwnj;رگره (له&zwnj;و که&zwnj;سانه&zwnj;ی لێی ده&zwnj;پارێنه&zwnj;وه و داوای لێ خۆشبوونی لێده&zwnj;که&zwnj;ن).';
$t[62] = 'خه&zwnj;ڵکه&zwnj;که وتیان: ئه&zwnj;ی صالح، به&zwnj;ڕاستی پێش ئه&zwnj;م باسانه&zwnj;ت تۆ له&zwnj;ناو ئێمه&zwnj;دا شوێنی هیوامان بوویت، ئایا به&zwnj;رگری ئه&zwnj;وه&zwnj;مان لێده&zwnj;که&zwnj;یت که شوێنی شتێك بکه&zwnj;وین که باوو باپیرانمان شوێنی که&zwnj;وتوون و په&zwnj;رستویانن؟! ئێمه به&zwnj;ڕاستی له گومانداین له&zwnj;وه&zwnj;ی که تۆ بانگی ئێمه ده&zwnj;که&zwnj;ی بۆلای و به&zwnj;دبینین لێی!!';
$t[63] = 'صالح وتی: ئه&zwnj;ی خزمه&zwnj;کانم هه&zwnj;واڵم بده&zwnj;نێ: ئه&zwnj;گه&zwnj;ر من له&zwnj;سه&zwnj;ر ڕێگه و به&zwnj;رنامه&zwnj;یه&zwnj;کی ڕاست و دروست بم له&zwnj;لایه&zwnj;ن په&zwnj;روه&zwnj;ردگارمه&zwnj;وه و خێرو چاکه&zwnj;و به&zwnj;زه&zwnj;یی خۆی پێ به&zwnj;خشی بم له خۆیه&zwnj;وه و ڕه&zwnj;حمه&zwnj;تی تایبه&zwnj;تیشی پێ به&zwnj;خشیووم، باشه کێ یارمه&zwnj;تیم ده&zwnj;دات و له سزای خوا ڕزگارم ده&zwnj;کات ئه&zwnj;گه&zwnj;ر له فه&zwnj;رمانی لابده&zwnj;م و په&zwnj;یامه&zwnj;که&zwnj;یم به ڕێك و ته&zwnj;واوی نه&zwnj;گه&zwnj;یاند، جا ئێوه به&zwnj;م هه&zwnj;ڵوێسته&zwnj;تان جگه له خه&zwnj;ساره&zwnj;تمه&zwnj;ندیی بۆ من هیچ سوودێکی ترم پێناگه&zwnj;یه&zwnj;نن.';
$t[64] = 'ئه&zwnj;ی خزمه&zwnj;کانم ئه&zwnj;مه وشتره&zwnj;که&zwnj;یه که له&zwnj;لایه&zwnj;ن خواوه کراوه به موعجیزه و نیشانه&zwnj;یه&zwnj;کی تایبه&zwnj;ت (له&zwnj;سه&zwnj;ر ده&zwnj;سه&zwnj;ڵاتی خۆی و ڕاستی پێغه&zwnj;مبه&zwnj;رێتی من) و جا وازی لێ بهێنن با له زه&zwnj;وی خوادا بله&zwnj;وه&zwnj;ڕێت، به&zwnj;نیازی خراپه&zwnj;وه ده&zwnj;ستی بۆ نه&zwnj;به&zwnj;ن، ئه&zwnj;گینا سزایه&zwnj;کی نزیك ده&zwnj;تانگرێت.';
$t[65] = 'یه&zwnj;کسه&zwnj;رچوون سه&zwnj;ریان بڕی، جا (صالح) یش پێی وتن: ڕابوێرن له ماڵ و مه&zwnj;نزڵی خۆتاندا بۆ ماوه&zwnj;ی سێ ڕۆژ، (دوای ئه&zwnj;و ماوه دیاری کراوه تیاده&zwnj;چن) و ئه&zwnj;وه&zwnj;ش په&zwnj;یمانێکه و هه&zwnj;رگیز به&zwnj;درۆ ناخرێته&zwnj;وه&zwnj;.';
$t[66] = 'جا کاتێك فه&zwnj;رمانی ئێمه هات و بڕیاردرا له&zwnj;کاتی دیاری کراودا و ئه&zwnj;وه صالح و ئه&zwnj;وانه&zwnj;ی بڕوایان هێنابوو له&zwnj;گه&zwnj;ڵیدا ڕزگارمان کردن به میهره&zwnj;بانی و به&zwnj;زه&zwnj;یی خۆمان، له سه&zwnj;رشۆڕیی ئه&zwnj;و ڕۆژه&zwnj;ش په&zwnj;ناماندان، به&zwnj;ڕاستی په&zwnj;روه&zwnj;ردگارت هه&zwnj;ر خۆی خاوه&zwnj;نی هێزه و باڵاده&zwnj;سته&zwnj;.';
$t[67] = 'جا ئه&zwnj;و ده&zwnj;نگه سامناکه&zwnj;ی بووه هۆی تیاچوونیان هه&zwnj;موو ئه&zwnj;وانه&zwnj;ی گرت که تاوان و سته&zwnj;میان کردو فه&zwnj;رمانبه&zwnj;رداری په&zwnj;روه&zwnj;ردگاریان نه&zwnj;بوون، سه&zwnj;ره&zwnj;نجام هه&zwnj;موویان له شوێنه&zwnj;کانی خۆیاندا که&zwnj;وتن به&zwnj;ڕووی زه&zwnj;ویدا و تیاچوون له به&zwnj;ره&zwnj;به&zwnj;یاندا...';
$t[68] = 'وه&zwnj;ك ئه&zwnj;وه&zwnj;ی که هه&zwnj;ر له&zwnj;و شوێنه&zwnj;دا نه&zwnj;بووبن و تیایدا نه&zwnj;ژیابن، ئاگادارین، به&zwnj;ڕاستی هۆزی ثمود بێ بڕوا بوون به په&zwnj;روه&zwnj;ردگاریان و بێ وه&zwnj;فا بوون به&zwnj;رامبه&zwnj;ری، ئاگارداربن بڕیاری دووری له به&zwnj;زه&zwnj;یی خوا، درا بۆ هۆزی ثمودیش.';
$t[69] = 'سوێند به&zwnj;خوا بێگومان وه&zwnj;فدێکی ڕه&zwnj;وانه کراوی ئێمه (له فریشته&zwnj;) هاتنه لای ئیبراهیم به مژده&zwnj;وه (مژده&zwnj;ی بوونی منداڵێك) و وتیان: سڵاوی خوات لێ بێت، ئه&zwnj;میش وتی: سڵاوی خوا له ئێوه&zwnj;ش، دوای به&zwnj;خێرهاتنیان ڕۆیشت و جا زۆری پێ نه&zwnj;چوو گه&zwnj;ڕایه&zwnj;وه به&zwnj;گۆشتی گوێره&zwnj;که&zwnj;یه&zwnj;کی برژاوه&zwnj;وه&zwnj;.';
$t[70] = 'جا کاتێك (ئیبراهیم) بینی ده&zwnj;ستی بۆ نابه&zwnj;ن، کاره&zwnj;که&zwnj;یانی به نائاسایی داناو له&zwnj;دڵی خۆیدا لێیان ترسا، ئه&zwnj;وانیش هه&zwnj;ستیان پێکردو وتیان: مه&zwnj;ترسه&zwnj;، به&zwnj;ڕاستی ئێمه نێردراوین بۆ لای قه&zwnj;ومه&zwnj;که&zwnj;ی لوط.';
$t[71] = 'له&zwnj;و کاته&zwnj;دا خێزانه&zwnj;که&zwnj;ی وه&zwnj;ستابوو، یه&zwnj;کسه&zwnj;ر پێکه&zwnj;نی، جا (خوای گه&zwnj;وره فه&zwnj;رمووی): ئێمه&zwnj;ش مژده&zwnj;مان دایه که&zwnj;ئیسحاقت پێده&zwnj;به&zwnj;خشین و له دوای ئه&zwnj;ویش یه&zwnj;عقوب، له نه&zwnj;وه&zwnj;ی ئیسحاق دێته دنیاوه&zwnj;.';
$t[72] = 'خێزانی ئیبراهیم وتی: وه&zwnj;ی ڕۆ (شه&zwnj;رمه&zwnj;زار خۆم)! چۆن منداڵم ده&zwnj;بێت من پیرم و ئه&zwnj;مه&zwnj;ش مێرده&zwnj;که&zwnj;مه&zwnj;، پیر و به&zwnj;ساڵا چووه&zwnj;، به&zwnj;ڕاستی ئه&zwnj;مه شتێکی سه&zwnj;ر سوڕهێنه&zwnj;ره&zwnj;!';
$t[73] = 'فریشته&zwnj;کان وتیان: ئایا سه&zwnj;رسام ده&zwnj;بیت له فه&zwnj;رمان؟ کاری خوا؟؟ ڕه&zwnj;حمه&zwnj;ت و به&zwnj;ره&zwnj;که&zwnj;تی خواتان له&zwnj;سه&zwnj;ربێت ئه&zwnj;ی خێزانی ئه&zwnj;م ماڵه پیرۆزه&zwnj;، (بێگومان خوای گه&zwnj;وره کاری وا ده&zwnj;کات) و چونکه هه&zwnj;میشه ئه&zwnj;و زاته سوپاسکراوه و خاوه&zwnj;نی به&zwnj;رزی و ده&zwnj;سه&zwnj;ڵاتیشه&zwnj;.';
$t[74] = 'جاکاتێك ترسه&zwnj;که له دڵی ئیبراهیم ده&zwnj;رچوو (هێمن بۆوه&zwnj;) و مژده&zwnj;که&zwnj;شی بۆ هاتبوو، ئینجا ده&zwnj;ستی کرد به باس کردن و موجاده&zwnj;له ده&zwnj;رباره&zwnj;ی قه&zwnj;ومی (لوط).';
$t[75] = 'به&zwnj;ڕاستی ئیبراهیم زۆر له&zwnj;سه&zwnj;رخۆ و به ئارام و خه&zwnj;مخۆر و گه&zwnj;ڕاوه&zwnj;یه بۆ لای خوا.';
$t[76] = '(فریشته&zwnj;کان وتیان): ئه&zwnj;ی ئیبراهیم واز بهێنه له&zwnj;و باس و کێشه&zwnj;یه&zwnj;، دڵنیابه به&zwnj;ڕاستی په&zwnj;روه&zwnj;ردگارت فه&zwnj;رمانی داوه و (کار ته&zwnj;واو بووه&zwnj;) و بێگومان سزایه&zwnj;کیان بۆ هاتووه که ناگه&zwnj;ڕێته&zwnj;وه&zwnj;.';
$t[77] = 'کاتێکیس وه&zwnj;فدی فریشته&zwnj;کانی ئێمه هاتنه لای لوط، ناڕه&zwnj;حه&zwnj;ت بوو پێیان، زۆر دڵته&zwnj;نگ بوو (چونکه له شێوه&zwnj;ی چه&zwnj;ند لاوێکی پێکه&zwnj;وتوودا هاتبوون، له&zwnj;دڵی خۆیدا) وتی: ئه&zwnj;مه ڕۆژێکی سه&zwnj;خته (منی تێکه&zwnj;وتووم).';
$t[78] = 'هۆزه&zwnj;که&zwnj;شی به&zwnj;بیستنی هاتنی ئه&zwnj;م میوانانه&zwnj;، به&zwnj;په&zwnj;له&zwnj;و بێ شه&zwnj;رمانه کشان بۆ لای، پێشتریش کرداره خراپه&zwnj;کانیان ده&zwnj;کرد، (کاتێك لوط ئه&zwnj;م دیمه&zwnj;نه ناشیرینه&zwnj;ی بینی) وتی: ئه&zwnj;ی خزمه&zwnj;کانم ئائه&zwnj;وانه کچه&zwnj;کانمن و ئه&zwnj;وان پاك و خاوێنن بۆ ئێوه (وه&zwnj;رن له&zwnj;ناو هۆزدا کێتان به دڵه به&zwnj;شێوه&zwnj;یه&zwnj;کی شه&zwnj;رعی و ڕه&zwnj;وا لێتان ماره بکه&zwnj;م) جا له خوا بترسن و شه&zwnj;رمه&zwnj;زارم مه&zwnj;که&zwnj;ن له&zwnj;ناو میوانه&zwnj;کانمدا، ئایا پیاوێکی ژیرو مه&zwnj;ردتان تێدا نیه (که له کاری ناڕه&zwnj;وای ئاوا جڵه&zwnj;وگیری بکات)؟!';
$t[79] = 'ئه&zwnj;وانیش بێ شه&zwnj;رمانه وتیان: سوێند به&zwnj;خوا به&zwnj;ڕاستی تۆ ده&zwnj;زانیت ئێمه هیچ حه&zwnj;قێکمان نیه به&zwnj;کچه&zwnj;کانی تۆوه&zwnj;، بێگومان تۆش ده&zwnj;زانیت ئێمه چیمان ده&zwnj;وێت!';
$t[80] = 'لوط وتی: خۆزگه به&zwnj;ڕاستی هێزێکم ده&zwnj;بوو له خۆتان (بۆ به&zwnj;رامبه&zwnj;ری ناپاکه&zwnj;کانتان) یان په&zwnj;نایه&zwnj;کی به&zwnj;هێزم ده&zwnj;ست ده&zwnj;که&zwnj;وێت و ده&zwnj;ڕۆیشتم بۆی (تا تۆڵه&zwnj;تان لێ بکه&zwnj;مه&zwnj;وه و ته&zwnj;مێتان بکه&zwnj;م).';
$t[81] = 'میوانه&zwnj;کان وتیان: ئه&zwnj;ی لوط به&zwnj;ڕاستی ئێمه فریشته&zwnj;ی نێرراوی په&zwnj;روه&zwnj;ردگاری تۆین، ئه&zwnj;و تاوانبارانه هه&zwnj;رگیز ناگه&zwnj;نه لات (تابتوانن ده&zwnj;ستدرێژی بکه&zwnj;نه سه&zwnj;ر ئێمه&zwnj;) و جا تۆ خاوو خێزانت له&zwnj;به&zwnj;شێکی شه&zwnj;ودا ده&zwnj;ربکه&zwnj;و که&zwnj;س له ئێوه لانه&zwnj;کاته&zwnj;وه و ئاوڕ نه&zwnj;داته&zwnj;وه&zwnj;، جگه هاوسه&zwnj;ره&zwnj;که&zwnj;ت (که لاساره&zwnj;و به&zwnj;گوێت ناکات) چونکه به&zwnj;ڕاستی ئه&zwnj;و سزایه&zwnj;ی که تاوانبارانی گرتۆته&zwnj;وه ئه&zwnj;ویش ده&zwnj;گرێته&zwnj;وه&zwnj;، به&zwnj;ڕاستی کاتی سزاکه&zwnj;شیان له به&zwnj;ره&zwnj;به&zwnj;یاندایه&zwnj;، ئایا به&zwnj;ره&zwnj;به&zwnj;یانی ئه&zwnj;مشه&zwnj;و نزیك نیه&zwnj;؟!';
$t[82] = 'جا کاتێك فه&zwnj;رمانی ئێمه بۆ تیاچوونیان هات، شارۆچکه&zwnj;که&zwnj;یانمان ژێره و ژوور کرد و به قوڕی سووره&zwnj;وه کراوی وه&zwnj;ك به&zwnj;رد، چین له&zwnj;سه&zwnj;ر چین به&zwnj;رد بارانمان کرد.';
$t[83] = 'هه&zwnj;موو ئه&zwnj;و به&zwnj;ردانه دیاری کراو بوون، له لایه&zwnj;ن په&zwnj;روه&zwnj;ردگارته&zwnj;وه (که بدات به&zwnj;سه&zwnj;ر ته&zwnj;وقه سه&zwnj;ری کێدا) ئه&zwnj;م کاره&zwnj;سات و ئه&zwnj;م به&zwnj;رد بارانه&zwnj;ش وه&zwnj;نه&zwnj;بێت زۆر دوور بێت له سته&zwnj;مکارانه&zwnj;وه (له هه&zwnj;موو سه&zwnj;رده&zwnj;مێکدا، کاتێك ڕۆده&zwnj;چن له تاوانکاریدا).';
$t[84] = 'بۆ لای خه&zwnj;ڵکی مه&zwnj;دیه&zwnj;نیش براو که&zwnj;سی خۆتان (شوعه&zwnj;یب) مان به پێغه&zwnj;مبه&zwnj;رێتی ڕه&zwnj;وانه&zwnj;کرد، ئه&zwnj;ویش وتی: ئه&zwnj;ی خزمه&zwnj;کانم به&zwnj;ندایه&zwnj;تی خوا بکه&zwnj;ن، هیچ خوایه&zwnj;کی ترتان نیه جگه له&zwnj;و، هه&zwnj;روه&zwnj;ها که&zwnj;م و کووڕیش مه&zwnj;که&zwnj;ن له پێوانه&zwnj;و کێشاندا، به&zwnj;ڕاستی من به&zwnj;چاك ده&zwnj;تانبینم و ڕزق و ڕۆزیتان باشه&zwnj;و له خۆشیدان، بێگومان من ده&zwnj;ترسم ئه&zwnj;گه&zwnj;ر له فه&zwnj;رمانی خوا ده&zwnj;رچن ئێوه تووشی سزای ڕۆژیك ببن که گه&zwnj;مارۆتان بدات و لێی ڕزگار نه&zwnj;بن.';
$t[85] = 'ئه&zwnj;ی قه&zwnj;وم و خزمه&zwnj;کانم، با پێوان و کێشانتان تێرو ته&zwnj;واوبێت و به دادپه&zwnj;روه&zwnj;رانه ئه&zwnj;نجامی بده&zwnj;ن، شتومه&zwnj;کی خه&zwnj;ڵکی که&zwnj;م نرخ مه&zwnj;که&zwnj;ن و (فێڵیان لێ مه&zwnj;که&zwnj;ن) و خراپه&zwnj;کاری مه&zwnj;که&zwnj;ن له زه&zwnj;ویدا و تۆوی خراپه مه&zwnj;چێنن.';
$t[86] = 'ئه&zwnj;وه&zwnj;ی خوای گه&zwnj;وره بۆتان ده&zwnj;هێڵێته&zwnj;وه له قازانج و حه&zwnj;ڵالی کردووه بۆتان، ئه&zwnj;وه چاکتره له زۆرێکی حه&zwnj;رام، ئه&zwnj;گه&zwnj;ر ئێوه ئیماندار بن، منیش لێپرسراو نیم به&zwnj;رامبه&zwnj;ر ئێوه (به&zwnj;ڵکو حه&zwnj;ق و ڕاستیتان پێ ڕاده&zwnj;گه&zwnj;یه&zwnj;نم).';
$t[87] = 'خه&zwnj;ڵکه&zwnj;که وتیان: ئه&zwnj;ی شوعه&zwnj;یب، ئایا نوێژه&zwnj;که&zwnj;ت فه&zwnj;رمانت پێده&zwnj;کات که ئێمه واز بهێنین له&zwnj;وه&zwnj;ی باوو باپیرانمان په&zwnj;رستوویانن، یاخود له ماڵ و سامانماندا به&zwnj;ئاره&zwnj;زووی خۆمان چیمان بوێت بیکه&zwnj;ین و به&zwnj;ڕاستی تۆ زۆرهێمن و خاوه&zwnj;ن ژیریت؟! (وادیاره گا&zwnj;ڵته&zwnj;یان پێکردووه&zwnj;، یان قه&zwnj;ناعه&zwnj;تیان به&zwnj;ژیری و له&zwnj;سه&zwnj;رخۆیی بووه و سه&zwnj;ریان سوڕماوه له&zwnj;وه&zwnj;ی که ده&zwnj;یه&zwnj;وێت له داب و نه&zwnj;ریتی باوو باپیران ده&zwnj;ربچێت).';
$t[88] = 'شوعه&zwnj;یت وتی: ئه&zwnj;ی قه&zwnj;وم و خزمه&zwnj;کانم هه&zwnj;واڵم بده&zwnj;نێ ئه&zwnj;گه&zwnj;ر منتان بینی و بۆتان ده&zwnj;رکه&zwnj;وت که له&zwnj;سه&zwnj;ر به&zwnj;رنامه&zwnj;یه&zwnj;کی ڕێك و ڕۆشنم له&zwnj;لایه&zwnj;ن په&zwnj;روه&zwnj;ردگارمه&zwnj;وه و هه&zwnj;ر له&zwnj;لایه&zwnj;ن خۆشیه&zwnj;وه ڕزق و ڕۆزی جوان و باشی داومه&zwnj;تێ، منیش نامه&zwnj;وێت قه&zwnj;ده&zwnj;غه&zwnj;ی شتێکتان لێ بکه&zwnj;م، که&zwnj;چی خۆم وه&zwnj;ك ئێوه نه&zwnj;که&zwnj;م (چی به ئێوه بگه&zwnj;یه&zwnj;نم خۆم له پێشدا ئه&zwnj;نجامی ده&zwnj;ده&zwnj;م) من هیچم ناوێت ته&zwnj;نها چاکسازی نه&zwnj;بێت، به&zwnj;گوێره&zwnj;ی توانام، ڕێکخستنی کارم به&zwnj;ده&zwnj;ستی که&zwnj;س نیه به&zwnj;ده&zwnj;ستی خوا نه&zwnj;بێت، پشت و په&zwnj;نام هه&zwnj;ر به&zwnj;و به&zwnj;ستووه و هه&zwnj;ر بۆ لای ئه&zwnj;ویش ده&zwnj;گه&zwnj;ڕێمه&zwnj;وه&zwnj;.';
$t[89] = 'ئه&zwnj;ی قه&zwnj;وم و خزمانم ناکۆکی و دژایه&zwnj;تیتان له&zwnj;گه&zwnj;ڵ مندا، واتان لێ ناکات (ئه&zwnj;و ئایین و بیرو باوه&zwnj;ڕه&zwnj;ی هێناومه له&zwnj;لایه&zwnj;ن خواوه به&zwnj;درۆی بزانن و) جا ئه&zwnj;و کاته ئه&zwnj;وه&zwnj;ی تووشی هۆزی نوح و هود و صالح بوو تووشی ئێوه&zwnj;ش ببێت، خۆ هۆزی لوط لێتانه&zwnj;وه زۆر دوور نیه&zwnj;.';
$t[90] = 'داوای لێخۆشبوونیش له په&zwnj;روه&zwnj;ردگارتان بکه&zwnj;ن و واز بهێنن له تاوان و خراپه&zwnj;کاری و پاشان بگه&zwnj;ڕێنه&zwnj;وه بۆ لای خوا، بیگومان په&zwnj;روه&zwnj;ردگاری من زۆر به&zwnj;به&zwnj;زه&zwnj;یی و میهره&zwnj;بانه&zwnj;، زۆر به&zwnj;سۆز و دلۆڤانه&zwnj;.';
$t[91] = 'خزمانی ناله&zwnj;با&zwnj;ر وتیان: ئه&zwnj;ی شوعه&zwnj;یب، ئێمه تێناگه&zwnj;ین له زۆربه&zwnj;ی ئه&zwnj;و باسانه&zwnj;ی که تۆ ده&zwnj;یڵێیت، ئێمه&zwnj;ش به&zwnj;ڕاستی تۆ به&zwnj;لاواز ده&zwnj;بینین له&zwnj;ناو خۆماندا (واده&zwnj;زانین ناتوانیت به&zwnj;ره&zwnj;نگاری ئه&zwnj;م هه&zwnj;موو خه&zwnj;ڵکه بکه&zwnj;یت، ئه&zwnj;مه&zwnj;ی تۆ باسی ده&zwnj;که&zwnj;یت پێویستی به&zwnj;هێزو توانایه&zwnj;کی زۆر هه&zwnj;یه&zwnj;) و خۆ ئه&zwnj;گه&zwnj;ر خزمه&zwnj;کانت نه&zwnj;بوایه به&zwnj;رد بارانمان ده&zwnj;کردیت، وه&zwnj;نه&zwnj;بێت تۆ هیچ تواناو ده&zwnj;سه&zwnj;ڵاتێکت هه&zwnj;بێت به&zwnj;سه&zwnj;رماندا.';
$t[92] = 'شوعه&zwnj;یب وتی: ئه&zwnj;ی هۆزو خزمانم ئایا که&zwnj;س و کارم به&zwnj;لای ئێوه&zwnj;وه به تواناو ده&zwnj;سه&zwnj;ڵاتدارتره له خوا؟! له&zwnj;کاتێکدا ئه&zwnj;و زاته&zwnj;تان فه&zwnj;رامۆش کردووه و گوێ ناده&zwnj;ن به فه&zwnj;رمانه&zwnj;کانی، به&zwnj;ڕاستی په&zwnj;روه&zwnj;ردگارم ئاگاداره به&zwnj;وه&zwnj;ی ئێوه ئه&zwnj;نجامی ده&zwnj;ده&zwnj;ن.';
$t[93] = 'ئه&zwnj;ی هۆزو خزمانم ئێوه کاری خۆتان بکه&zwnj;ن چه&zwnj;نده تواناتان هه&zwnj;یه&zwnj;، منیش کاری خۆم له&zwnj;سه&zwnj;ر به&zwnj;رنامه&zwnj;ی خۆم ئه&zwnj;نجام ده&zwnj;ده&zwnj;م، له&zwnj;مه&zwnj;ولا ده&zwnj;رده&zwnj;که&zwnj;وێت و دازانن کێ سزای بۆ دێت و سه&zwnj;رشۆڕی ده&zwnj;کات، کێش درۆزنه&zwnj;، ئێوه چاوه&zwnj;رێ بن به&zwnj;ڕاستی منیش له&zwnj;گه&zwnj;ڵتاندا چاوه&zwnj;ڕێم.';
$t[94] = 'کاتێکیش فه&zwnj;رمانی ئێمه هات بۆ تیاچوونیان، شوعه&zwnj;یب و ئه&zwnj;وانه&zwnj;ی بڕوایان هێنابوو له&zwnj;گه&zwnj;ڵیدا به&zwnj;هۆی به&zwnj;زه&zwnj;یی و لوطفی خۆمان ڕزگارمان کردن و ده&zwnj;نگ و نرکه&zwnj;ی تیاچوون تاوانکارو بێ بڕواکانی گرته&zwnj;وه و جا هه&zwnj;موویان له شوێن و جێگه&zwnj;ی خۆیاندا تیاچوون و به&zwnj;چۆکدا هاتن له به&zwnj;ره&zwnj;به&zwnj;یاندا.';
$t[95] = 'هه&zwnj;روه&zwnj;ک له&zwnj;وێ نه&zwnj;ژیابن، ئاگاداربن، ده&zwnj;ك مه&zwnj;دیه&zwnj;ن له&zwnj;وه&zwnj;ولاتر بچێت هه&zwnj;روه&zwnj;ك ثمود له&zwnj;وه&zwnj;ولاتر چوو.';
$t[96] = 'سوێندیش بێت بێگومان موسا پێغه&zwnj;مبه&zwnj;رمان نارد به&zwnj;چه&zwnj;نده&zwnj;ها به&zwnj;ڵگه و فه&zwnj;رمانی دروسته&zwnj;وه&zwnj;، هاوڕێ له&zwnj;گه&zwnj;ڵ مه&zwnj;عجیزه و شتی سه&zwnj;رسوڕهێنه&zwnj;ری ئاشکرادا...';
$t[97] = 'بۆ لای فیرعه&zwnj;ون و دارو ده&zwnj;سته&zwnj;ی، که&zwnj;چی خه&zwnj;ڵکه&zwnj;که شوێن فه&zwnj;رمانی فیرعه&zwnj;ون که&zwnj;وتن، له&zwnj;کاتێکیشدا که کارو فه&zwnj;رمانی فیرعه&zwnj;ون هه&zwnj;رگیز ڕاست و دروست و ژیرانه نه&zwnj;بوو (به&zwnj;ڵکو سته&zwnj;مکار بوو خۆی به&zwnj;خوا ده&zwnj;دایه قه&zwnj;ڵه&zwnj;م)!...';
$t[98] = 'له ڕۆژی قیامه&zwnj;تیشدا فیرعه&zwnj;ون پێش قه&zwnj;وم و شوێنکه&zwnj;وتووانی ده&zwnj;که&zwnj;وێت و ئینجا ده&zwnj;یانباته ناو ئاگری دۆزه&zwnj;خ، ئای چه&zwnj;ند خراپه ئه&zwnj;و چوونه ژووره&zwnj;وه&zwnj;یه و ئه&zwnj;و شوێنه&zwnj;ی ده&zwnj;چنه ناوی.';
$t[99] = 'ئه&zwnj;وانه له&zwnj;م دنیادا نه&zwnj;فرین و ده&zwnj;نگوباسی خراپ شوێنیشیان خرا، له ڕۆژی قیامه&zwnj;تیشدا، (به&zwnj;شیان هه&zwnj;ر دووریه له&zwnj;ره&zwnj;حم و به&zwnj;زه&zwnj;یی خوا) و ئای چه&zwnj;ند خراپه ئه&zwnj;و به&zwnj;خشینه&zwnj;ی پێیان به&zwnj;خشراوه&zwnj;.';
$t[100] = 'ئه&zwnj;وه&zwnj;ی که باسکرا هه&zwnj;ندێك بوو له هه&zwnj;واڵی خه&zwnj;ڵکی ئه&zwnj;و شارو شارۆچکانه&zwnj;، بۆت ده&zwnj;گێڕینه&zwnj;وه&zwnj;، هه&zwnj;ندێك له&zwnj;و شارو دێهاتانه ماون و هه&zwnj;ندێکی تری به&zwnj;ته&zwnj;واوی وێران بوون.';
$t[101] = 'ئێمه سته&zwnj;ممان لێنه&zwnj;کردن و به&zwnj;ڵکو هه&zwnj;ر خۆیان سته&zwnj;میان له خۆیان کرد، ئه&zwnj;وسا ئه&zwnj;و په&zwnj;رستراوانه&zwnj;یان که له&zwnj;جیاتی خوا هاواریان لێ ده&zwnj;کردن، فریای هیچ شتێکیان نه&zwnj;که&zwnj;وتن کاتێك فه&zwnj;رمانی په&zwnj;روه&zwnj;ردگاری تۆ هات، هیچیان بۆ زیاد نه&zwnj;کردن جگه له تیاچوون نه&zwnj;بێت.';
$t[102] = 'ئا به&zwnj;و شوێوه&zwnj;یه&zwnj;ش تۆڵه&zwnj;سه&zwnj;ندنی په&zwnj;روه&zwnj;ردگاری تۆ، کاتێك تۆڵه ده&zwnj;سێنێت له خه&zwnj;ڵکی شارو دێهاته&zwnj;کان له&zwnj;حاڵێکدا که یاخی و سته&zwnj;م پیشه بوون، به&zwnj;ڕاستی تۆڵه&zwnj;سه&zwnj;ندنه&zwnj;که&zwnj;ی زۆر به&zwnj;ئێش و به&zwnj;هێزو توونده&zwnj;.';
$t[103] = 'بێگومان له&zwnj;و به&zwnj;سه&zwnj;رهاته&zwnj;دا نیشان و په&zwnj;ندو ئامۆژگاری هه&zwnj;یه بۆ که&zwnj;سێك له سزای ڕۆژی دوایی بترسێت، ئه&zwnj;وه ڕۆژێكه خه&zwnj;ڵکی بۆی کۆ ده&zwnj;کرێنه&zwnj;وه و ئه&zwnj;وه ڕۆژێکه خه&zwnj;ڵکی هه&zwnj;موو ئاماده کراون و هه&zwnj;مووان له&zwnj;به&zwnj;رچاون ده&zwnj;بن تیایدا.';
$t[104] = 'ئێمه به&zwnj;رپابوونی ئه&zwnj;و ڕۆژه دواناخه&zwnj;ین مه&zwnj;گه&zwnj;ر بۆ کاتێکی که&zwnj;م و سنووردار نه&zwnj;بێت.';
$t[105] = 'ئه&zwnj;و ڕۆژه&zwnj;ی که ئه&zwnj;و ڕووداوه پێش دێت مه&zwnj;گه&zwnj;ر خوای گه&zwnj;وره مۆڵه&zwnj;ت بدات، ئه&zwnj;گینا که&zwnj;س بۆی نیه قسه بکات، ئه&zwnj;وسا خه&zwnj;ڵکه&zwnj;که ده&zwnj;بنه دوو کۆمه&zwnj;ڵ، کۆمه&zwnj;ڵێك به&zwnj;دبه&zwnj;خت و چاره&zwnj;ڕه&zwnj;ش، کۆمه&zwnj;ڵێکیش به&zwnj;خته&zwnj;وه&zwnj;رو کامه&zwnj;ران.';
$t[106] = 'جا ئه&zwnj;وانه&zwnj;ی به&zwnj;دبه&zwnj;خت و چاره ڕه&zwnj;ش بوون، ئه&zwnj;وه له&zwnj;ناو ئاگردان، هه&zwnj;ناسه هه&zwnj;ڵمژین و هه&zwnj;ناسه دانه&zwnj;وه&zwnj;یان زۆر سه&zwnj;خت و دژواره&zwnj;.';
$t[107] = 'بۆ هه&zwnj;میشه&zwnj;ش تیایدا ده&zwnj;میننه&zwnj;وه تا ئاسمانه&zwnj;کان و زه&zwnj;وی هه&zwnj;بێت (که ئه&zwnj;ویش بۆ هه&zwnj;تاهه&zwnj;تایی دروست کراوه له قیامه&zwnj;تدا) و مه&zwnj;گه&zwnj;ر په&zwnj;روه&zwnj;ردگارت ویستی (گۆڕانکاری) هه&zwnj;بێت، به&zwnj;ڕاستی په&zwnj;روه&zwnj;ردگارت کارایه بۆ هه&zwnj;رشتێك بیه&zwnj;وێت.';
$t[108] = 'ئه&zwnj;وانه&zwnj;ش که کامه&zwnj;ران و به&zwnj;ختیار کراون ئه&zwnj;وه له به&zwnj;هه&zwnj;شتدان، بۆ هه&zwnj;تاهه&zwnj;تایی تیایدا ده&zwnj;مێننه&zwnj;وه هه&zwnj;تا ئاسمانه&zwnj;کان و زه&zwnj;وی هه&zwnj;بێت (که ئه&zwnj;ویش بۆ هه&zwnj;تاهه&zwnj;تایی دروست کراوه له قیامه&zwnj;تدا) و مه&zwnj;گه&zwnj;ر په&zwnj;روه&zwnj;ردگارت ویستی (گۆرانکاری) هه&zwnj;بێت، ئه&zwnj;مه&zwnj;ش به&zwnj;خششێکه هه&zwnj;رگیز کۆتایی نایه&zwnj;ت.';
$t[109] = 'جا هیج له گوماندا مه&zwnj;به له پووچه&zwnj;ڵی و بێ سوودی ئه&zwnj;وه&zwnj;ی ئه&zwnj;و بێ بڕوایانه ده&zwnj;یپه&zwnj;رستن و شوێنی که&zwnj;وتوون، ئه&zwnj;وه&zwnj;ی ده&zwnj;یپه&zwnj;رستن هیچ نیه جگه وێنه&zwnj;ی ئه&zwnj;وه نه&zwnj;بێت که باوو باپیریان پێشتر ده&zwnj;یانپه&zwnj;رست، بێگومان ئێمه&zwnj;ش به&zwnj;تێرو ته&zwnj;واو بێ که&zwnj;م و کوڕی پاداشتی کارو کرده&zwnj;وه&zwnj;یان ده&zwnj;ده&zwnj;ینه&zwnj;وه&zwnj;.';
$t[110] = 'سوێند به&zwnj;خوا به&zwnj;ڕاستی ئێمه به&zwnj;رنامه&zwnj;مان به موسا به&zwnj;خشی، که&zwnj;چی جیاوازی تێدا دروست کرا، جا له&zwnj;به&zwnj;رئه&zwnj;وه نه&zwnj;بوایه که خوای تۆ بڕیاریداوه (دادگاییان دوا بخات) هه&zwnj;ر ئێسته دادگایی ده&zwnj;کردن و تۆڵه&zwnj;ی لێ ده&zwnj;سه&zwnj;ندن، که&zwnj;چی به&zwnj;ڕاستی ئه&zwnj;و بێ بڕوایانه هه&zwnj;ر له&zwnj;گوماندان لێی و خه&zwnj;ڵکیش تووشی گومان ده&zwnj;که&zwnj;ن.';
$t[111] = 'به&zwnj;ڕاستی هه&zwnj;موو ئه&zwnj;وانه په&zwnj;روه&zwnj;ردگارت پاداشتی کرده&zwnj;وه&zwnj;کانیان به&zwnj;ته&zwnj;واوی ده&zwnj;داته&zwnj;وه&zwnj;، بێگومان ئه&zwnj;و زاته زۆر ئاگاداره به&zwnj;وه&zwnj;ی ئه&zwnj;وان ئه&zwnj;نجامی ده&zwnj;ده&zwnj;ن و ده&zwnj;یکه&zwnj;ن.';
$t[112] = 'ئه&zwnj;ی محمد (صلی الله علیه وسلم) که&zwnj;وابوو چۆن فه&zwnj;رمانت پێدراوه&zwnj;، هه&zwnj;روا به&zwnj;ڕاست و به&zwnj;ڕێکی فه&zwnj;رمانه&zwnj;کان به&zwnj;جێ بهێنه&zwnj;، هه&zwnj;روه&zwnj;ها ئه&zwnj;و که&zwnj;سانه&zwnj;ش له&zwnj;گه&zwnj;ڵ تۆدان و گه&zwnj;ڕاونه&zwnj;ته&zwnj;وه بۆ لای خواو ته&zwnj;وبه&zwnj;یان کردووه&zwnj;، نه&zwnj;که&zwnj;ن له سنووری فه&zwnj;رمانی خوا ده&zwnj;ربچن، چونکه به&zwnj;ڕاستی ئه&zwnj;و زاته زۆر بینایه به&zwnj;و کرده&zwnj;وانه&zwnj;ی که ده&zwnj;یکه&zwnj;ن.';
$t[113] = 'نه&zwnj;که&zwnj;ن مه&zwnj;یلی دڵتان له&zwnj;گه&zwnj;ڵ ئه&zwnj;وانه&zwnj;دا بێت که سته&zwnj;میان کردووه و له&zwnj;خوا یاخی بوون، چونکه ئه&zwnj;و کاته ئاگر ده&zwnj;تانسوتێنێت، که&zwnj;سیش جگه له خوا پشتیوانتان نابێت، له&zwnj;وه&zwnj;ودوایش یارمه&zwnj;تی نادرێن.';
$t[114] = 'نوێژیش به&zwnj;چاکی ئه&zwnj;نجام بده له&zwnj;هه&zwnj;ردوو سه&zwnj;ری ڕۆژه&zwnj;وه (که نیوه&zwnj;ڕۆ و عه&zwnj;سره&zwnj;) و له سێ کاته&zwnj;که&zwnj;ی شه&zwnj;ویشدا که (مه&zwnj;غریب و عیشاو به&zwnj;یانی) یه&zwnj;، نزیكن له (ئه&zwnj;مسه&zwnj;رو ئه&zwnj;وسه&zwnj;ری ڕۆژه&zwnj;وه&zwnj;، به&zwnj;ڕاستی کرده&zwnj;وه چاکه&zwnj;کان کرداره خراپه&zwnj;کان لاده&zwnj;بات و گوناهه&zwnj;کان ده&zwnj;شۆنه&zwnj;وه&zwnj;، ئه&zwnj;وه ئامۆژگاری و بیرخه&zwnj;ره&zwnj;وه&zwnj;یه بۆ ئه&zwnj;وانه&zwnj;ی یادی خوا ده&zwnj;که&zwnj;ن.';
$t[115] = 'خۆشت بگره&zwnj;و به ئارامبه&zwnj;، چونکه بێگومان خوای گه&zwnj;وره پاداشتی چاکه&zwnj;خوازان به&zwnj;زایه نادات.';
$t[116] = 'جا ئه&zwnj;وه بۆ له&zwnj;ناو خه&zwnj;ڵکی زه&zwnj;مانی پێش ئێوه&zwnj;دا که&zwnj;سانی ژیرو دیندار نه&zwnj;بوو، که به&zwnj;رگریان له خراپه&zwnj;و تاوان بکردایه له زه&zwnj;ویدا، مه&zwnj;گه&zwnj;ر که&zwnj;مێك نه&zwnj;بێت له&zwnj;وانه&zwnj;ی که ڕزگارمان کردن لێیان، چونکه زۆربه&zwnj;یان له سنووری فه&zwnj;رمانی خوا ده&zwnj;رچوون و شوێن ڕابواردنی دنیاو ئاره&zwnj;زوو که&zwnj;وتن، ئیتر به&zwnj;و کرده&zwnj;وه ناشیرینه&zwnj;یان چوونه ڕیزی تاوانبارو خراپکارانه&zwnj;وه&zwnj;.';
$t[117] = 'په&zwnj;روه&zwnj;ردگارت به سته&zwnj;م خه&zwnj;ڵکی شارو دێهاته&zwnj;کان له&zwnj;ناو نابات، له&zwnj;کاتێکدا خه&zwnj;ڵکه&zwnj;که&zwnj;ی چاکه&zwnj;خواز و چاکساز بن.';
$t[118] = 'خۆ ئه&zwnj;گه&zwnj;ر په&zwnj;روه&zwnj;ردگارت بیویستایه ئه&zwnj;وه هه&zwnj;موو خه&zwnj;ڵکی ده&zwnj;کرد به&zwnj;یه&zwnj;ك تاقم و کۆمه&zwnj;ڵ، (به&zwnj;ڵام) به&zwnj;رده&zwnj;وامیش ئه&zwnj;و خه&zwnj;ڵکه جیاوازن له&zwnj;نێوانی یه&zwnj;کتردا له ئایین و به&zwnj;رنامه و بۆچووندا...';
$t[119] = 'مه&zwnj;گه&zwnj;ر ئه&zwnj;وه&zwnj;ی به&zwnj;زه&zwnj;یی په&zwnj;روه&zwnj;ردگارت گرتبێتیه&zwnj;وه&zwnj;، هه&zwnj;ر بۆ ئه&zwnj;و مه&zwnj;به&zwnj;سته&zwnj;ش خوای گه&zwnj;وره دروستی کردوون، (واته&zwnj;: به&zwnj;شێوه&zwnj;یه&zwnj;ك دروستی کردوون بتوانن ئازادی بیروباوه&zwnj;ڕو بۆچوونیان هه&zwnj;بێت، ئینجا ڕێگه&zwnj;ی ڕاستیان پێ بڵێت و بۆیان ڕوون بکاته&zwnj;وه&zwnj;) و بڕیاری په&zwnj;روه&zwnj;ردگاری تۆ ده&zwnj;رچووه و ته&zwnj;واو بووه&zwnj;، که&zwnj;: دۆزه&zwnj;خ پڕ ده&zwnj;که&zwnj;م له&zwnj;گرۆی په&zwnj;ری و خه&zwnj;ڵکی (ئه&zwnj;وانه&zwnj;ی خۆیان شیاوی ئاگر ده&zwnj;که&zwnj;ن).';
$t[120] = 'ئه&zwnj;ی محمد (صلی الله علیه وسلم) هه&zwnj;موو ئه&zwnj;وانه&zwnj;شت بۆ ده&zwnj;گێڕینه&zwnj;وه له چیرۆك و هه&zwnj;واڵی پێغه&zwnj;مبه&zwnj;ران، ئه&zwnj;وه&zwnj;ی که دڵی تۆی پێ دابمه&zwnj;زرێنین و دڵنیات بکه&zwnj;ین، دڵنیاش به که له&zwnj;م هه&zwnj;واڵانه&zwnj;دا حه&zwnj;ق و ڕاستیت بۆ هات و بۆت ڕوونکرایه&zwnj;وه&zwnj;، هه&zwnj;روه&zwnj;ها ئامۆژگاری و بیرخسته&zwnj;وه&zwnj;ی باشی تێدابوو بۆ ئیمانداران.';
$t[121] = 'بڵێ به&zwnj;وانه&zwnj;ش ئیمان ناهێنن و نیازیان وایه فه&zwnj;رمانبه&zwnj;رداری تۆ نه&zwnj;بن، ئێوه له شوێنی خۆتاندا کاری خۆتان بکه&zwnj;ن و به&zwnj;ڕاستی ئێمه&zwnj;ش هه&zwnj;میشه خه&zwnj;ریك ده&zwnj;بین و کاری خۆمان ده&zwnj;که&zwnj;ین.';
$t[122] = 'به&zwnj;رده&zwnj;وامیش چاوه&zwnj;رێی ده&zwnj;رئه&zwnj;نجامی کاری هه&zwnj;ردوو لا بکه&zwnj;ن و ئێمه&zwnj;ش به&zwnj;ڕاستی چاوه&zwnj;ڕێین.';
$t[123] = 'هه&zwnj;ر بۆ خوایه نهێنی و نادیاره&zwnj;کانی ئاسمانه&zwnj;کان و زه&zwnj;وی، هه&zwnj;موو کارو فه&zwnj;رمانیش هه&zwnj;ر بۆ لای ئه&zwnj;و زاته ده&zwnj;گه&zwnj;ڕێته&zwnj;وه&zwnj;، ده&zwnj;ی که&zwnj;وابوو ئه&zwnj;و بپه&zwnj;ره&zwnj;سته و ته&zwnj;نها پشت به&zwnj;و ببه&zwnj;سته&zwnj;، هه&zwnj;رگیز خوای په&zwnj;روه&zwnj;ردگارت بێ ئاگا نیه له&zwnj;وه&zwnj;ی که ئێوه ده&zwnj;یکه&zwnj;ن و ئه&zwnj;نجامی ده&zwnj;ده&zwnj;ن.';
?>