$t = array();
$t[1] = 'سه&zwnj;رنجی سه&zwnj;ره&zwnj;تای سووره&zwnj;تی (البقرة) بده&zwnj;، ئه&zwnj;و ئایه&zwnj;تانه&zwnj;ی له&zwnj;م سووره&zwnj;ته&zwnj;دا هاتووه چه&zwnj;ند ئایه&zwnj;تێکی ئه&zwnj;و قورئانه ڕوون و ئاشکرایه&zwnj;یه&zwnj;.';
$t[2] = 'به&zwnj;ڕاستی ئێمه ئه&zwnj;م ئایه&zwnj;تانه&zwnj;مان دابه&zwnj;زاندووه له شێوه&zwnj;ی قورئانێکی پاراودا به زمانی عه&zwnj;ره&zwnj;بی بۆ ئه&zwnj;وه&zwnj;ی ئێوه لێی تێبگه&zwnj;ن و ژیریتان بخه&zwnj;نه&zwnj;کار.';
$t[3] = 'ئێمه چاکترین و جوانترین چیرۆك و به&zwnj;سه&zwnj;رهاتت بۆ ده&zwnj;گێرینه&zwnj;وه ئه&zwnj;ی محمد صلی الله علیه وسلم) به&zwnj;هۆی ئه&zwnj;م قورئانه&zwnj;ی که نیگامان کرد بۆ لای تۆ، له&zwnj;کاتێکدا بێگومان تۆ له پێش ئه&zwnj;م قورئانه&zwnj;دا له&zwnj;م به&zwnj;سه&zwnj;رهاته بێ ئاگا بوویت.';
$t[4] = 'کاتێك یوسف به&zwnj;باوکی وت: باوکی به&zwnj;ڕێزم بێگومان من له خه&zwnj;ومدا یازده ئه&zwnj;ستێره و خۆرو مانگم بینی کڕنووشیان بۆ ده&zwnj;بردم.';
$t[5] = 'باوکی وتی: ڕۆڵه گیان خه&zwnj;وه&zwnj;که&zwnj;ت مه&zwnj;گێڕه&zwnj;وه بۆ براکانت، نه&zwnj;وه&zwnj;کو به&zwnj;هۆی ئه&zwnj;وه&zwnj;وه فێڵێکت لێبکه&zwnj;ن و تووشی به&zwnj;ڵایه&zwnj;کت بکه&zwnj;ن (چونکه ئه&zwnj;م خه&zwnj;وه داهاتوویه&zwnj;کی بڵند بۆ تۆ ده&zwnj;گه&zwnj;یه&zwnj;نێت، ئه&zwnj;وانیش حه&zwnj;سوودیت پێده&zwnj;به&zwnj;ن) به&zwnj;ڕاستی شه&zwnj;یتانیش دوژمنێکی ئاشکرای ئاده&zwnj;میزاده&zwnj;.....';
$t[6] = 'به&zwnj;و شێوه&zwnj;یه&zwnj;ش خوای په&zwnj;روه&zwnj;ردگارت تۆ دیاری ده&zwnj;کات و هه&zwnj;ڵتده&zwnj;بژێردیت (بۆ پێغه&zwnj;مبه&zwnj;رێتی) و هه&zwnj;ندێك لێکدانه&zwnj;وه&zwnj;ی خه&zwnj;وت فێر ده&zwnj;کات ونیعمه&zwnj;ته&zwnj;کانی خۆیت به&zwnj;سه&zwnj;ردا ته&zwnj;واو ده&zwnj;کات و هه&zwnj;روه&zwnj;ها به&zwnj;سه&zwnj;ر نه&zwnj;وه&zwnj;کانی یه&zwnj;عقوبیشدا، هه&zwnj;روه&zwnj;ك چۆن پێشتر نیعمه&zwnj;ت و پله&zwnj;وپایه&zwnj;ی بڵندی به&zwnj;خشی به&zwnj;باپیرانت که ئیبراهیم و ئیسحاقن، به&zwnj;ڕاستی په&zwnj;روه&zwnj;ردگارت زانا و دانایه&zwnj;.';
$t[7] = 'سوێند به&zwnj;خوا بێگومان له&zwnj;م ڕووداوه&zwnj;ی یوسف و براکانیدا نیشانه&zwnj;ی زۆری (پێغه&zwnj;مبه&zwnj;رێتی محمدی) تێدایه بۆ که&zwnj;سانێك پرسیار ده&zwnj;که&zwnj;ن له&zwnj;و باره&zwnj;یه&zwnj;وه (به&zwnj;شوێن ڕاستێتی تۆدا ده&zwnj;گه&zwnj;ڕێن).';
$t[8] = 'کاتێك براکانی یوسف وتیان: به&zwnj;ڕاستی یوسف و براکه&zwnj;ی (بنیامین) لای باوکمان، له ئێمه نازدارو خۆشه&zwnj;ویستترن، له&zwnj;کاتێکدا ئێمه کۆمه&zwnj;ڵێکی به&zwnj;هێزین، به&zwnj;ڕاستی باوکمان له سه&zwnj;رلێشێواویه&zwnj;کی ئاشکرادایه&zwnj;.';
$t[9] = '(له&zwnj;نێوان خۆیاندا هه&zwnj;ندێکیان وتیان) یوسف بکوژن، یان فڕێی بده&zwnj;نه شوێن و زه&zwnj;ویه&zwnj;کی دوور، ئه&zwnj;وکاته ڕووی باوکتان یه&zwnj;کلایی ده&zwnj;بێته&zwnj;وه بۆ ئێوه و خۆشه&zwnj;ویستیه&zwnj;که&zwnj;ی هه&zwnj;مووی بۆ ئێوه ده&zwnj;بێت، ئینجا دوای نه&zwnj;مانی یوسف ده&zwnj;بنه پێڕو نه&zwnj;وه&zwnj;یه&zwnj;کی چاك و ڕێك و پێك.';
$t[10] = 'یه&zwnj;کێك له براکانی وتی: یوسف مه&zwnj;کوژن، به&zwnj;ڵکو بیخه&zwnj;ینه ناو بیرێکی قووڵه&zwnj;وه&zwnj;، دوایی کاروان دێت و ده&zwnj;یدۆزنه&zwnj;وه و ده&zwnj;یبه&zwnj;ن، ئه&zwnj;گه&zwnj;ر بڕیار بێت هه&zwnj;ر شتێکی لێ بکه&zwnj;ن.';
$t[11] = '(دوای ئه&zwnj;و بڕیاره هاتنه لای باوکیان و) وتیان: ئه&zwnj;ی باوکه ئه&zwnj;وه چیته له ئێمه دڵنیانیت له باره&zwnj;ی یوسفه&zwnj;وه، به&zwnj;ڕاستی ئێمه زۆر دڵسۆزی ئه&zwnj;وین و زۆر خۆشمان ده&zwnj;وێت.';
$t[12] = 'به&zwnj;یانی بینێره له&zwnj;گه&zwnj;ڵمان با بای باڵی بدات گه&zwnj;مه&zwnj;و یاری بکات و بێگومان زۆر پارێزگاری لێ ده&zwnj;که&zwnj;ین و ئاگامان لێی ده&zwnj;بێت.';
$t[13] = 'باوکیان وتی: من زۆر غه&zwnj;م و په&zwnj;ژاره ده&zwnj;مگرێت له&zwnj;وه&zwnj;ی ئێوه بیبه&zwnj;ن، ده&zwnj;ترسم گورگ بیخوات له&zwnj;کاتێکدا که ئێوه لێی بێ ئاگان (له&zwnj;به&zwnj;ر یاری و گه&zwnj;مه&zwnj;ی خۆتان).';
$t[14] = 'کوڕه&zwnj;کان وتیان: سوێند به&zwnj;خوا ئه&zwnj;گه&zwnj;ر بێت و یوسف گورگه&zwnj;که بیخوات، له&zwnj;کاتێکدا ئێمه&zwnj;ش کۆمه&zwnj;ڵێکی به&zwnj;هێزین، به&zwnj;ڕاستی ئه&zwnj;وکاته ئێمه خه&zwnj;ساره&zwnj;تمه&zwnj;ندین (زه&zwnj;ره&zwnj;رمان له خۆمانداوه و شه&zwnj;رمه&zwnj;زاری لای تۆ ده&zwnj;بین).';
$t[15] = 'جا کاتێك (سه&zwnj;ره&zwnj;&zwnj;نجام باوکیان ڕازی کردو) یوسفیان بردو هه&zwnj;موو هاتنه سه&zwnj;ر ئه&zwnj;و بڕیاره&zwnj;ی که بیخه&zwnj;ینه ناو تاریکای بیره&zwnj;که&zwnj;وه&zwnj;، له&zwnj;و کاته&zwnj;دا نیگامان بۆ نارد که&zwnj;: هه&zwnj;واڵی ئه&zwnj;م به&zwnj;سه&zwnj;رهاته له&zwnj;ئاینده&zwnj;دا به براکانت ده&zwnj;ده&zwnj;یت، کاتێك ئه&zwnj;وان ئاگایان له خۆیان نابێت و له بیریان چۆته&zwnj;وه و هه&zwnj;ستی پێناکه&zwnj;ن.';
$t[16] = 'پاشان که تاریك داهات هاتنه لای باوکیان ده&zwnj;گریان.';
$t[17] = 'وتیان ئه&zwnj;ی باوکی به&zwnj;ڕێزمان: به&zwnj;ڕاستی ئێمه ڕۆشتین پێشبڕکێمان ده&zwnj;کردو یوسفمان لای که&zwnj;ل و په&zwnj;له&zwnj;که&zwnj;مان به&zwnj;جێ هێشت، ئه&zwnj;وسا گورگه&zwnj;که په&zwnj;لاماری داو خواردی، به&zwnj;ڵام تۆ هه&zwnj;میشه بڕوامان پێناکه&zwnj;یت ئه&zwnj;گه&zwnj;رچی ئێمه ڕاستگۆش بین....';
$t[18] = 'براکانیشی هاتن به&zwnj;هه&zwnj;ندێ خوێنی ساخته&zwnj;وه که به&zwnj;سه&zwnj;ر کراسه&zwnj;که&zwnj;یه&zwnj;وه بوو (هێنایان بۆ باوکیان) ئه&zwnj;ویش وتی: نه&zwnj;خێر وانیه&zwnj;، به&zwnj;ڵکو خۆتان بۆ خۆتان ئه&zwnj;م کاره&zwnj;تان سازداوه&zwnj;، جا چارم ته&zwnj;نها خۆگریی و ئارام گرتنه به&zwnj;جوانترین شێوه&zwnj;، هه&zwnj;ر خواش داوای کۆمه&zwnj;کی لێده&zwnj;کرێت له&zwnj;سه&zwnj;ر ئه&zwnj;و باسه&zwnj;ی ئێوه ده&zwnj;یکه&zwnj;ن.';
$t[19] = 'ئه&zwnj;وسا کاروانێك هات و ئینجا ئاو هێنه&zwnj;ره&zwnj;کایان نارد ئاو بهێنێت (ئه&zwnj;ویش چووه سه&zwnj;ربیره&zwnj;که&zwnj;) و سه&zwnj;تڵه&zwnj;که&zwnj;ی داهێشته ناو بیره&zwnj;که&zwnj;وه&zwnj;، کاتێك هێنایه سه&zwnj;ره&zwnj;وه له&zwnj;جیاتی ئاو یوسف هاته ده&zwnj;ره&zwnj;وه&zwnj;، کاتێك کابرا بینی، وتی: خه&zwnj;ڵکینه مژده بێت ئه&zwnj;مه لاوێکه (ئه&zwnj;وسا هه&zwnj;ر که&zwnj;سێك بدۆزرایه&zwnj;ته&zwnj;وه ده&zwnj;کرا به&zwnj;به&zwnj;نده&zwnj;و ده&zwnj;فرۆشرا) یوسفیان شارده&zwnj;وه و کردیانه که&zwnj;ره&zwnj;سه&zwnj;ی فرۆشتن، خوای گه&zwnj;وره&zwnj;ش زاناو ئاگایه به&zwnj;و کارو کرده&zwnj;وانه&zwnj;ی که ده&zwnj;یکه&zwnj;ن (چاودێری یوسف ده&zwnj;کات و کارێکی گه&zwnj;وره&zwnj;ی پێیه&zwnj;تی).';
$t[20] = 'سه&zwnj;رئه&zwnj;نجام (بردیان و) فرۆشتیان له بازاڕی کۆیله فرۆشه&zwnj;کاندا له میسر، به&zwnj;نرخێکی هه&zwnj;رزان، چه&zwnj;ند درهه&zwnj;مێکی که&zwnj;م، له فرۆشتنیدا هیچ گرنگییان پێنه&zwnj;داو که&zwnj;م ته&zwnj;ماع بوون (چونکه پشتیان پێوه&zwnj;ی نه&zwnj;ئێشا بوو).';
$t[21] = 'جا ئه&zwnj;و که&zwnj;سه&zwnj;ی یوسفی کڕی له میسر، به هاوسه&zwnj;ره&zwnj;که&zwnj;ی وت: ڕێز بگره له شوێن وجێگه&zwnj;ی ئه&zwnj;م لاوه&zwnj;، به&zwnj;ئومێدی ئه&zwnj;وه&zwnj;ی سوودمان پێبگه&zwnj;یه&zwnj;نێت، یان بیکه&zwnj;ینه کوڕی خۆمان، ئا به&zwnj;و شێوه&zwnj;یه یوسفمان جێگیرکرد له زه&zwnj;ویدا و پایه&zwnj;دارمان کرد، بۆ ئه&zwnj;وه&zwnj;ش فێری بکه&zwnj;ین چۆن خه&zwnj;و لێک بداته&zwnj;وه و ڕووداوه&zwnj;کانی چوار ده&zwnj;وری وه&zwnj;ك خۆی تێبگات، خوای گه&zwnj;وره زاڵه به&zwnj;سه&zwnj;ر کارو فه&zwnj;رمانه&zwnj;کانی خۆیدا، به&zwnj;ڵام زۆربه&zwnj;ی خه&zwnj;ڵکی به&zwnj;مه نازانن.';
$t[22] = 'کاتێکیش که یوسف پێگه&zwnj;یشت و گه&zwnj;یشته ئه&zwnj;وپه&zwnj;ڕی توند و تۆڵی (له ڕووی لاشه&zwnj;و ژیریی یه&zwnj;وه&zwnj;) و ئێمه&zwnj;ش پله&zwnj;ی فه&zwnj;رمانڕه&zwnj;وایی دانایی و زانیاری شاره&zwnj;زایی باشمان پێبه&zwnj;خشی، هه&zwnj;ر به&zwnj;و شێوه&zwnj;یه&zwnj;ش پاداشتی چاکه&zwnj;کاران ده&zwnj;ده&zwnj;ینه&zwnj;وه&zwnj;.';
$t[23] = 'جا ئه&zwnj;و ئافره&zwnj;ته&zwnj;ی که یوسف له ماڵیدا بوو هه&zwnj;وڵیدا یوسف له خشته ببات و دڵی ڕابکێشێت بۆ لای خۆی، هه&zwnj;موو ده&zwnj;رگاکانیشی داخست و وتی: وه&zwnj;ره پێشه&zwnj;وه و خۆت ئاماده بکه&zwnj;، یوسف وتی: پانا ده&zwnj;گرم به خوا کاری واناکه&zwnj;م، چونکه په&zwnj;روه&zwnj;ردگارم ده&zwnj;رووی لێ کردوومه&zwnj;ته&zwnj;وه&zwnj;، هه&zwnj;روه&zwnj;ها مێرده&zwnj;که&zwnj;شت چاك ڕه&zwnj;فتارر بووه له&zwnj;گه&zwnj;ڵمدا (چۆن خیانه&zwnj;تی لێ ده&zwnj;که&zwnj;م) و چونکه من دڵنیام سته&zwnj;مکاران سه&zwnj;رفراز نابن.';
$t[24] = 'سوێند به&zwnj;خوا بێگومان ژنه&zwnj;که په&zwnj;لاماری یوسفیداو ویستی لێی بدات، یوسفیش نیازی لێدانی هه&zwnj;بوو ئه&zwnj;گه&zwnj;ر به&zwnj;ڵگه&zwnj;ی خوایی نه&zwnj;زانیایه (که سه&zwnj;رئه&zwnj;نجامی لێدانه&zwnj;که&zwnj;ی به&zwnj;خراپ به&zwnj;سه&zwnj;ریدا ده&zwnj;شکێته&zwnj;وه&zwnj;)و ئا به&zwnj;و شێوه&zwnj;یه&zwnj;مان کرد (بۆ ئه&zwnj;وه&zwnj;ی هه&zwnj;رچی گوناهو تاوان و خراپه&zwnj;یه لێی دوور ده&zwnj;خه&zwnj;ینه&zwnj;وه&zwnj;) و به&zwnj;ڕاستی ئه&zwnj;و له به&zwnj;نده هه&zwnj;ڵبژارده وپاکه&zwnj;کانی ئێمه&zwnj;یه&zwnj;.';
$t[25] = 'سه&zwnj;رئه&zwnj;نجام هه&zwnj;ردووکیان پێشبڕکێیان کرد بۆ ده&zwnj;رگاکه (یوسف بۆ کردنه&zwnj;وه و ئه&zwnj;ویش بۆ داخستن) ژنه&zwnj;که توانی ده&zwnj;ستی له کراسه&zwnj;که&zwnj;ی گیر بکات و له پشته&zwnj;وه دڕاندی، ئه&zwnj;وسا هه&zwnj;ردووکیان له به&zwnj;رده&zwnj;می ده&zwnj;رگاکه&zwnj;دا تووشی مێرده&zwnj;که&zwnj;ی هاتن، خیرا هاته قسه&zwnj;و ویستی یوسف تاوانبار بکات بۆیه وتی: باشه ئاخۆ سزای ئه&zwnj;و که&zwnj;سه چییه که وستبیتی خراپه له&zwnj;گه&zwnj;ڵ خێزانه&zwnj;که&zwnj;تدا بکات، جگه له&zwnj;وه&zwnj;ی به&zwnj;نده بکرێت یان سزایه&zwnj;کی به&zwnj;ئێش بدرێت.';
$t[26] = 'یوسف وتی: (نه&zwnj;خێر وانیه&zwnj;) ئه&zwnj;و ویستی له خشته&zwnj;م به&zwnj;رێت، یه&zwnj;کێك له خزمانی ژنه&zwnj;که (که&zwnj;گوێی له به&zwnj;سه&zwnj;رهاته&zwnj;که بوو، دیار بووکه پیاوێکی زیره&zwnj;ك و دنیا دیده بوو) شایه&zwnj;تیداو وتی: ئه&zwnj;گه&zwnj;ر کراسه&zwnj;که&zwnj;ی یوسف له پێشه&zwnj;&zwnj;وه دڕابێت ئه&zwnj;وه ژنه&zwnj;که ڕاست ده&zwnj;کات و ئه&zwnj;و کاته یوسف یه&zwnj;کێکه له درۆزنان.';
$t[27] = 'خۆ ئه&zwnj;گه&zwnj;ر کراسه&zwnj;که&zwnj;ی له پشته&zwnj;وه دڕابێت ژنه&zwnj;که درۆ ده&zwnj;کات و یوسف یه&zwnj;کێکه له ڕاستگۆیان.';
$t[28] = 'کاتێك مێرده&zwnj;که&zwnj;ی بینی کراسه&zwnj;که&zwnj;ی یوسف له پشته&zwnj;وه دڕاوه&zwnj;، به ژنه&zwnj;که&zwnj;ی وت: به&zwnj;ڕاستی ئه&zwnj;مه پیلان و نه&zwnj;خشه&zwnj;ی خۆتانه&zwnj;، بێگومان فێڵ و ته&zwnj;ڵه&zwnj;که و نه&zwnj;خشه و پیلانی ئێوه زۆر گه&zwnj;وره&zwnj;یه (له&zwnj;م جۆره شتانه&zwnj;دا).';
$t[29] = 'ئینجا ڕووی کرده یوسف وتی: ئه&zwnj;ی یوسف، ئه&zwnj;م باسه فه&zwnj;رامۆش بکه و گوێی مه&zwnj;ده&zwnj;رێ، تۆش داوای لێخۆشبوون و لێبوردن بکه له هه&zwnj;ڵه و تاوانه&zwnj;که&zwnj;ت چونکه به&zwnj;ڕاستی تۆ له گوناهباران و به&zwnj;هه&zwnj;ڵه&zwnj;چواندایت.';
$t[30] = 'پاشان ژنانێك له ئافره&zwnj;تانی ناو شار ئه&zwnj;م ده&zwnj;نگ و باسه&zwnj;یان بیست بۆیه وتیشیان: سه&zwnj;یره&zwnj;! ژنه&zwnj;که&zwnj;ی عه&zwnj;زیزی میسر ده&zwnj;یه&zwnj;وێت به&zwnj;نه&zwnj;رم و نیانی سه&zwnj;رنجی لاوه&zwnj;که&zwnj;ی خۆی ڕابکێشێت بۆلای خۆی، به&zwnj;ڕاستی ئه&zwnj;وینداریه&zwnj;که&zwnj;ی بۆی هه&zwnj;موو په&zwnj;ره&zwnj;کانی دڵی داگیرکردووه&zwnj;، بێگومان ئێمه ئه&zwnj;و ژنه سه&zwnj;رگه&zwnj;ردانێکی ئاشکرادا ده&zwnj;بینین.';
$t[31] = 'جا کاتێك ژنه&zwnj;که&zwnj;ی عه&zwnj;زیزی میسر توانج و پلاره&zwnj;که&zwnj;ی ئه&zwnj;وانی بیست، ئه&zwnj;ویش ناردی به شوێنیانداو جێگه&zwnj;یه&zwnj;کی خۆشی بۆ ئاماده کردن، چه&zwnj;قۆشی دایه ده&zwnj;ستی هه&zwnj;ریه&zwnj;که&zwnj;یان، ئینجا فه&zwnj;رمانیدا به یوسف و وتی: ئاده&zwnj;ی وه&zwnj;ره ده&zwnj;ره&zwnj;وه بۆ لایان (با بتبینن) و جا کاتێك بینیان به گه&zwnj;وره&zwnj;و گرنگیان زانیو (الله اکبر) یان بۆ کردو به بێئاگایی ده&zwnj;ستی خۆیان بڕی وتیشیان: پاکی و بێگه&zwnj;ردی ودووری له هه&zwnj;موو ناته&zwnj;واویه&zwnj;ك شایسته&zwnj;ی خوایه که ئه&zwnj;مه&zwnj;ی دروست کردووه&zwnj;! ئه&zwnj;مه به&zwnj;شه&zwnj;ر نیه&zwnj;، ته&zwnj;نها فریشته&zwnj;یه&zwnj;کی جوان و به&zwnj;ڕێزه و هیچی تر نیه&zwnj;.';
$t[32] = 'ئینجا ژنه&zwnj;که وتی: ئه&zwnj;وه ئه&zwnj;و که&zwnj;سه&zwnj;یه که ئێوه لۆمه&zwnj;و سه&zwnj;رزه&zwnj;نشتی منتان ده&zwnj;کرد له باره&zwnj;یه&zwnj;وه و سوێند به&zwnj;خوا بێگومان من هه&zwnj;وڵمدا له خشته&zwnj;ی به&zwnj;رم، که&zwnj;چی ئه&zwnj;و هه&zwnj;ر خۆی گرت و پاك وخاوێنی ویست، سوێند به خوا ئه&zwnj;گه&zwnj;ر ئه&zwnj;وه&zwnj;ی من فه&zwnj;رمانی پێده&zwnj;ده&zwnj;م نه&zwnj;یکات، ئه&zwnj;وه به&zwnj;ڕاستی به&zwnj;ند ده&zwnj;کرێت و زه&zwnj;لیل ده&zwnj;بێت و به&zwnj;که&zwnj;م ته&zwnj;ماشا ده&zwnj;کرێت.';
$t[33] = 'یوسف که&zwnj;ئه&zwnj;مه&zwnj;ی بیست وتی: په&zwnj;روه&zwnj;ردگارم من زیندانم له&zwnj;لا خۆشه&zwnj;ویستره له&zwnj;وه&zwnj;ی که ئه&zwnj;وان بانگم ده&zwnj;که&zwnj;ن بۆی، خۆ ئه&zwnj;گه&zwnj;ر له فێڵ و ته&zwnj;ڵه&zwnj;که&zwnj;ی ئه&zwnj;وانه ڕزگارم نه&zwnj;که&zwnj;یت و پیلان و نه&zwnj;خشه&zwnj;کانیان ده&zwnj;ربازم نه&zwnj;که&zwnj;یت و لام نه&zwnj;ده&zwnj;یت، له&zwnj;وانه&zwnj;یه منیش مه&zwnj;یلم بۆ دروست ببێت و ئاره&zwnj;زوویان بکه&zwnj;م پاشان بچمه ڕیزی نه&zwnj;فام و گوناهبارانه&zwnj;وه&zwnj;.';
$t[34] = 'جا په&zwnj;روه&zwnj;ردگاری به هانایه&zwnj;وه چوو نزاکه&zwnj;ی گیرا کردو له ته&zwnj;ڵه&zwnj;که و فێڵی ژنان پاڕاستی و لایدا، چونکه به&zwnj;ڕاستی هه&zwnj;ر ئه&zwnj;وزاته بیسه&zwnj;ری نزاکانه و زانایه (به دڵ و ده&zwnj;روونه&zwnj;کان).';
$t[35] = 'پاشان، له دوای ئه&zwnj;وه&zwnj;ی که به&zwnj;ڵگه&zwnj;ی زۆریان بینی له&zwnj;سه&zwnj;ر یوسف، بۆیان ده&zwnj;رکه&zwnj;وت و وایان به چاك زانی تا ماوه&zwnj;یه&zwnj;ك هه&zwnj;ر به&zwnj;ندی بکه&zwnj;ن.';
$t[36] = 'ئه&zwnj;وسا دوو لاوی تریش به&zwnj;تاوانی جیا جیا له&zwnj;گه&zwnj;ڵ یوسفدا خرانه به&zwnj;ندیخانه&zwnj;وه&zwnj;، (هه&zwnj;ریه&zwnj;که&zwnj;یان خه&zwnj;وێکی بینی بوو) یه&zwnj;کێکیان وتی: بێگومان من خۆمم ده&zwnj;بینی له خه&zwnj;ومدا ترێم ده&zwnj;گوشی و شه&zwnj;رابم لێ دروست ده&zwnj;کرد، ئه&zwnj;وی تریشیان وتی: به&zwnj;ڕاستی من خۆمم ده&zwnj;بینی له خه&zwnj;ومدا که نانم به&zwnj;سه&zwnj;ر سه&zwnj;رمه&zwnj;وه هه&zwnj;ڵده&zwnj;گرت باڵنده لێی ده&zwnj;خوارد، ئاگادارمان بکه به&zwnj;لێکدانه&zwnj;وه&zwnj;ی ئه&zwnj;م خه&zwnj;ونه&zwnj;، چونکه به&zwnj;ڕاستی ئێمه ده&zwnj;بینین تۆ له چاکه خوازانیت و (شاره&zwnj;زای له لێکدانه&zwnj;وه&zwnj;ی خه&zwnj;ودا).....';
$t[37] = 'یوسف وتی: (من ته&zwnj;نها لێکدانه&zwnj;وه&zwnj;ی خه&zwnj;ون نازانم به&zwnj;ڵکو جگه له&zwnj;وه&zwnj;ش) هیچ خواردنێکتان بۆ نایه&zwnj;ت که ده&zwnj;رخواردتان بدرێت که من نه&zwnj;زانم چیه و چۆنه پێش ئه&zwnj;وه&zwnj;ی بگاته لاتان (ئه&zwnj;مه&zwnj;ش ئازایه&zwnj;تی خۆم نیه&zwnj;) به&zwnj;ڵکو ئه&zwnj;وه هه&zwnj;ندێکه له&zwnj;وه&zwnj;ی په&zwnj;روه&zwnj;ردگارم فێری کردووم و بزانن به&zwnj;ڕاستی من به&zwnj;رنامه&zwnj;ی قه&zwnj;ومێکم واز لێهێناوه که باوه&zwnj;ڕ به خوا ناهینن و هه&zwnj;میشه بڕوایان به قیامه&zwnj;تیش نی یه&zwnj;.';
$t[38] = 'به&zwnj;ڵکو شوێنی ئایین و به&zwnj;رنامه&zwnj;ی باو و باپیرانم ئیبراهیم و ئیسحاق و یه&zwnj;عقوب که&zwnj;وتووم، هه&zwnj;رگیز ڕه&zwnj;وا نی یه بۆ ئێمه هیچ جۆره شتێك بکه&zwnj;ینه هاوه&zwnj;ڵ و شه&zwnj;ریك بۆ خوا، ئه&zwnj;وه&zwnj;ش له فه&zwnj;زڵ و ڕێزی تایبه&zwnj;تی خوایی یه له&zwnj;سه&zwnj;ر ئێمه و له&zwnj;سه&zwnj;ر خه&zwnj;ڵکیش، به&zwnj;ڵام به&zwnj;داخه&zwnj;وه زۆربه&zwnj;ی خه&zwnj;ڵکی سوپاسگوزاری ناکه&zwnj;ن.';
$t[39] = '(ئه&zwnj;وسا ڕووی تێکردن وتی): ئه&zwnj;ی هاوه&zwnj;ڵانی زیندانم، ئایا چه&zwnj;ند خوایه&zwnj;کی هه&zwnj;مه جۆر و جیاوازی (بپه&zwnj;رسترێن)چاکترن، یاخود خوایه&zwnj;کی تاك و ته&zwnj;نها و زاڵ به&zwnj;سه&zwnj;ر هه&zwnj;موو شتێکدا؟';
$t[40] = 'جا هه&zwnj;رچی ئێوه ده&zwnj;یپه&zwnj;رستن جگه له خوا، ته&zwnj;نها خۆتان و باو و باپیرانتان ناوتان لێناون (ته&zwnj;نها ناون و ناوه&zwnj;ڕۆکیان نی یه&zwnj;)، هه&zwnj;رگیز خوا به&zwnj;وه ڕازی نی یه و، هیچ به&zwnj;ڵگه&zwnj;یه&zwnj;کیشی ڕه&zwnj;وانه نه&zwnj;کردووه له&zwnj;سه&zwnj;ر په&zwnj;سه&zwnj;ند کردنیان، به&zwnj;ڕاستی حوکم و فه&zwnj;رمانڕه&zwnj;وایی جگه له خوا شایسته&zwnj;ی که&zwnj;سی تر نی یه&zwnj;، فه&zwnj;رمانیشی داوه جگه له خۆی که&zwnj;سی تر نه&zwnj;په&zwnj;رستن، هه&zwnj;ر ئه&zwnj;وه&zwnj;ش ئایین و به&zwnj;رنامه&zwnj;ی په&zwnj;سه&zwnj;ند و ڕاست و دروست و به&zwnj;نرخ، به&zwnj;ڵام زۆربه&zwnj;ی خه&zwnj;ڵکی ئه&zwnj;م ڕاستیانه نازانن و لێی بێ ئاگان.';
$t[41] = 'ئه&zwnj;ی هاوه&zwnj;ڵانی زیندانیم، ئه&zwnj;وه&zwnj;تان له خه&zwnj;ونیدا ترێی ده&zwnj;گوشی ئه&zwnj;وه ده&zwnj;بێته ساقی پاشا و ڕزگاری ده&zwnj;بێت و شه&zwnj;رابی پێشکه&zwnj;ش ده&zwnj;کات، ئه&zwnj;وی تریشیان له خاچ ده&zwnj;درێت و باڵنده له&zwnj;سه&zwnj;رو ڕووخساری ده&zwnj;خوات، ئیتر ئه&zwnj;مه&zwnj;ش وه&zwnj;ڵامی لێکدانه&zwnj;وه&zwnj;ی خه&zwnj;ونه&zwnj;کانتانه و هه&zwnj;ر ئه&zwnj;وه&zwnj;ش بڕیار دراوه&zwnj;.';
$t[42] = 'ئه&zwnj;وسا یوسف به&zwnj;و که&zwnj;سایانی وت: که&zwnj;وای ده&zwnj;زانی ڕزگاری ده&zwnj;بێت و ده&zwnj;بێته ساقی پاشا، باسم بکه لای پاشات (که به&zwnj;بێ تاوان من به&zwnj;ند کراوم)، به&zwnj;ڵام شه&zwnj;یتان بیری کابرای بردووه که له&zwnj;لای پاشا باسی بکات، ئیتر چه&zwnj;ند ساڵێك (یوسف به بێ تاوان) له به&zwnj;ندیخانه&zwnj;دا مایه&zwnj;وه&zwnj;.';
$t[43] = '(شه&zwnj;وێك پاشا خه&zwnj;ونێکی سه&zwnj;یری بینی و بوو به مه&zwnj;راقی و له کۆبوونه&zwnj;وه&zwnj;یه&zwnj;کدا) وتی: به&zwnj;ڕاستی من له خه&zwnj;ومدا بینیم که حه&zwnj;وت مانگای قه&zwnj;ڵه&zwnj;و له&zwnj;لایه&zwnj;ن حه&zwnj;وت مه&zwnj;نگای له&zwnj;ڕو لاوازه&zwnj;وه خوران، حه&zwnj;وت گوڵه گه&zwnj;نمی سه&zwnj;وز و جوان و حه&zwnj;وتی وشکیشم بینی، ئه&zwnj;ی زاناو شاره&zwnj;زایان ئه&zwnj;م خه&zwnj;وه&zwnj;م بۆ لێك بده&zwnj;نه&zwnj;وه ئه&zwnj;گه&zwnj;ر خه&zwnj;ون لێك ده&zwnj;ده&zwnj;نه&zwnj;وه&zwnj;...';
$t[44] = 'ئه&zwnj;وانیش وتیان: ئه&zwnj;م خه&zwnj;ونه بێ سه&zwnj;رو بنه و هیچ نی یه&zwnj;، ئێمه له لێکدانه&zwnj;وه&zwnj;ی خه&zwnj;ونی بێ سه&zwnj;رو بن شاره&zwnj;زا نین.';
$t[45] = 'ئینجا ئه&zwnj;وه&zwnj;یان که ڕزگاری بوو بوو، بیری که&zwnj;وته&zwnj;وه دوای ئه&zwnj;و هه&zwnj;موو ماوه&zwnj;یه وتی: داواکارم بمنێرن (بۆ لای یوسف) من هه&zwnj;واڵی (لێکدانه&zwnj;وه&zwnj;ی ئه&zwnj;و خه&zwnj;ونه&zwnj;تان) پێ ڕاده&zwnj;گه&zwnj;یه&zwnj;نم.';
$t[46] = '(ساقی چوو بۆ لای زیندانه&zwnj;که بۆ دیداری یوسف وتی): یوسف ئه&zwnj;ی هاوه&zwnj;ڵی ڕاستگۆم، ئه&zwnj;م خه&zwnj;ونه&zwnj;مان بۆ لێك بده&zwnj;ره&zwnj;وه (له خه&zwnj;ونێکی پاشادا) حه&zwnj;وت مانگای قه&zwnj;ڵه&zwnj;و له&zwnj;لایه&zwnj;ن حه&zwnj;وت مانگای له&zwnj;ڕو لاوازه&zwnj;وه خوراون، هه&zwnj;روه&zwnj;ها له باره&zwnj;ی حه&zwnj;وت گوڵه گه&zwnj;نمی سه&zwnj;وزو جوان و حه&zwnj;وتی تری وشك بۆمان لێك بده&zwnj;ره&zwnj;وه&zwnj;، بۆ ئه&zwnj;وه&zwnj;ی بگه&zwnj;ڕێمه&zwnj;وه بۆ لای خه&zwnj;ڵکی تا وه&zwnj;ڵام بزانن.';
$t[47] = '(یوسف) وتی: حه&zwnj;وت ساڵی به&zwnj;رده&zwnj;وام کشتوکاڵ ده&zwnj;که&zwnj;ن و دانه&zwnj;وێڵه ده&zwnj;چێنن، ئه&zwnj;وه&zwnj;ی که دروێنه&zwnj;تان کردوو، لێتان زیاد بوو به&zwnj;گوڵه&zwnj;که&zwnj;یه&zwnj;وه بیهێڵنه&zwnj;وه&zwnj;، مه&zwnj;گه&zwnj;ر ئه&zwnj;و که&zwnj;مه&zwnj;ی که ده&zwnj;یخۆن (دانه&zwnj;وێڵه به گوڵه&zwnj;که&zwnj;یه&zwnj;وه هه&zwnj;ڵبگیرێت له کرم و ئه&zwnj;سپێ و زایه بوون به&zwnj;دوور ده&zwnj;بێت).';
$t[48] = 'پاشان له دوای ئه&zwnj;وه حه&zwnj;وت ساڵ گرانیی و سه&zwnj;ختی پێش دێت، ئه&zwnj;وه&zwnj;ی که زه&zwnj;خیره&zwnj;تان کردووه بۆ ئه&zwnj;و ساڵانه هه&zwnj;مووی ده&zwnj;خۆن، مه&zwnj;گه&zwnj;ر که&zwnj;مێکی نه&zwnj;بێت که هه&zwnj;ڵی ده&zwnj;گرن (بۆ تۆو).';
$t[49] = 'له&zwnj;وه&zwnj;ودوا ساڵێکی باراناوی و پڕ به&zwnj;ره&zwnj;که&zwnj;ت دێت که خه&zwnj;ڵکی به&zwnj;رو بوومی زۆریان پێ ده&zwnj;به&zwnj;خشرێت، میوه ده&zwnj;گوشن و شه&zwnj;ربه&zwnj;تی لێ دروست ده&zwnj;که&zwnj;ن.';
$t[50] = '(ساقی گه&zwnj;ڕایه&zwnj;وه بۆ لای پاشا ولێکدانه&zwnj;وه&zwnj;که&zwnj;ی یوسفی بۆ باس کرد، زۆری به&zwnj;دڵ بوو، بۆیه پاشاش وتی): ئاده&zwnj;ی بچن بۆم بهێنن، جا کاتێك که ڕه&zwnj;وانه&zwnj;کراوه&zwnj;که&zwnj;ی پاشا گه&zwnj;یشته لای یوسف و (وتی: پاشا داوات ده&zwnj;کات، یوسف) وتی: بگه&zwnj;ڕێره&zwnj;وه بۆ لای پاشا و جا پێی بڵێ: هۆی چی بوو ئه&zwnj;و ئافره&zwnj;تانه کاتی خۆی ده&zwnj;ستی خۆیان بڕی و برینداریان کرد (ئایا من هیچ تاوانێکم له&zwnj;وه&zwnj;دا هه&zwnj;یه&zwnj;؟!) به&zwnj;ڕاستی هه&zwnj;ر په&zwnj;روه&zwnj;ردگارم به ئاگایه له فێڵ و ته&zwnj;ڵه&zwnj;که و پیلانیان.';
$t[51] = '(پاشا ئه&zwnj;و ئافره&zwnj;تانه&zwnj;ی هه&zwnj;موو کۆکرده&zwnj;وه&zwnj;) پێی وتن: ئه&zwnj;وکاره گرنگه&zwnj;تان چی بوو کاتێك که&zwnj;وا ئێوه ویستتان یوسف له خشته به&zwnj;رن (ئایا ئه&zwnj;و هیچ تاوانێکی هه&zwnj;بوو؟) هه&zwnj;موو وتیان: په&zwnj;نا به&zwnj;خوا (ئێمه بوختانی بۆ بکه&zwnj;ین) ئێمه هیچ جۆره هه&zwnj;ڵه&zwnj;و ناشرینیه&zwnj;کمان لێ نه&zwnj;دیووه و نه&zwnj;زانیووه&zwnj;، (ژنه&zwnj;که&zwnj;ی عه&zwnj;زیزی میسر) وتی: ئا ئێسته ئیتر ڕاستی ڕوون و ئاشکرابوو (دان به&zwnj;ڕاستیدا ده&zwnj;نێم)، خه&zwnj;تای من بوو ده&zwnj;مویست له خشته&zwnj;ی به&zwnj;رم و به&zwnj;ڕاستی یوسف یه&zwnj;کێکه له ڕاستگۆیان.';
$t[52] = '(ئینجا یوسف) وتی: ئه&zwnj;وه (بۆیه وام کرد) تا (پاشا) چاك بزانێت و دڵنیابێت به&zwnj;ڕاستی من له نهێنیشدا خیانه&zwnj;تم لێ نه&zwnj;کردووه&zwnj;، بێگومانیش خوا فێڵ و ته&zwnj;ڵه&zwnj;ی خیانه&zwnj;تکاران به&zwnj;ئه&zwnj;نجام ناگه&zwnj;یه&zwnj;نێت. (یاخود ژنه&zwnj;که وتی): تا یوسف دڵنیابێت که له نهێنیداو (ئێسته ئه&zwnj;و لێره نی یه&zwnj;) خیانه&zwnj;تی لێ ناکه&zwnj;م و بوختانی بۆ هه&zwnj;ڵنابه&zwnj;ستم، چونکه (بۆم ده&zwnj;رکه&zwnj;وتووه&zwnj;) که خوا فێڵ و ته&zwnj;ڵه&zwnj;که&zwnj;ی خیانه&zwnj;تکاران به ئه&zwnj;نجام ناگه&zwnj;یه&zwnj;نێت';
$t[53] = '(ده&zwnj;شێت یوسف یان ژنه&zwnj;که&zwnj;ی عه&zwnj;زیزی میسر وتبێتی) منیش نه&zwnj;فسی خۆم به&zwnj;ری ناکه&zwnj;م، چونکه به&zwnj;ڕاستی نه&zwnj;فس (ئه&zwnj;گه&zwnj;ر به نووری خواناسی ئاوه&zwnj;دان نه&zwnj;بێت) زۆر فه&zwnj;رمانده&zwnj;ره به&zwnj;گوناهو هه&zwnj;ڵه&zwnj;و تاوان، مه&zwnj;گه&zwnj;ر نه&zwnj;فسێك که په&zwnj;روه&zwnj;ردگارم میهره&zwnj;بانی پێ کردبێت، به&zwnj;ڕاستی په&zwnj;روه&zwnj;ردگارم لێ خۆشبوو میهره&zwnj;بانه&zwnj;.';
$t[54] = '(ئه&zwnj;وسا پاشا که ڕاستیه&zwnj;کانی به ته&zwnj;واویی بۆ ده&zwnj;رکه&zwnj;وت ئه&zwnj;وه&zwnj;نده&zwnj;ی تر یوسفی لا به&zwnj;ڕێزو به&zwnj;نرخ بوو بۆیه&zwnj;) فه&zwnj;رمانیدا وتی: ئاده&zwnj;ی بچن بۆم بهێنن، ئه&zwnj;و (لاوه پاکه ژیره زانایه&zwnj;) تایبه&zwnj;تی ده&zwnj;که&zwnj;م بۆخۆم و له نزیکانی من بێت، جا کاتێك (هێنایان) و پاشا قسه&zwnj;ی له&zwnj;گه&zwnj;ڵ کرد، وتی: ئه&zwnj;مڕۆ ئیتر به&zwnj;ڕاستی تۆ لای ئێمه خاوه&zwnj;نی پله&zwnj;و جێگه&zwnj;ی متمانه و باوه&zwnj;ڕی.';
$t[55] = 'یوسف وتی: پێم باشه بمکه&zwnj;یت به&zwnj;لێپرسراو به&zwnj;سه&zwnj;ر گه&zwnj;نجینه&zwnj;کان و سامانی سه&zwnj;ر زه&zwnj;وی ئه&zwnj;م وڵاته&zwnj;، (میسر) به&zwnj;ڕاستی من پارێزه&zwnj;رێکی زانام (له&zwnj;خۆم ڕاده&zwnj;بینم که شت بپارێزم و زاناشم که چۆن ده&zwnj;یپارێزم).';
$t[56] = '(خوا ده&zwnj;فه&zwnj;رموێت) ئا به&zwnj;و شێوه&zwnj;یه ده&zwnj;سه&zwnj;ڵاتمان دا به یوسف و جێگیر وپایه&zwnj;دارمان کرد له خاکی میسردا، هه&zwnj;ر پله&zwnj;و پایه&zwnj;یه&zwnj;کی ده&zwnj;ویست له&zwnj;و وڵاته&zwnj;دا ده&zwnj;ستی ده&zwnj;که&zwnj;وت بۆی ساز ده&zwnj;بوو، هه&zwnj;ر که&zwnj;سێك، هه&zwnj;ر ده&zwnj;سته&zwnj;یه&zwnj;ك بمانه&zwnj;وێت به&zwnj;هره&zwnj;وه&zwnj;ری ده&zwnj;که&zwnj;ین له ڕه&zwnj;حمه&zwnj;ت و به&zwnj;خششه&zwnj;کانمان و پاداشتی چاکه&zwnj;کاران به&zwnj;زایه ناده&zwnj;ین.';
$t[57] = 'بێگومان پاداشتی ئه&zwnj;و جیهان (له به&zwnj;هه&zwnj;شتی به&zwnj;ریندا) چاکتره بۆ ئه&zwnj;وانه&zwnj;ی که ئیمان و باوه&zwnj;ڕیان هێناوه و خۆیان ده&zwnj;پاڕاست.';
$t[58] = '(ساڵانی گرانی هاته پێشه&zwnj;وه&zwnj;) براکانی یوسف (هاتن بۆ میسر) جا چوونه دیوانی، ئه&zwnj;و ئه&zwnj;وانی ناسێوه&zwnj;، به&zwnj;ڵام ئه&zwnj;وان ئه&zwnj;میان نه&zwnj;ناسێوه (چونکه پۆشاکی وه&zwnj;زیری له به&zwnj;ردا بوو، هه&zwnj;رگیز شتی وایان چاوه&zwnj;ڕوان نه&zwnj;ده&zwnj;کرد).';
$t[59] = '(ئه&zwnj;ویش ڕێزی گرتن و دواندنی و پێی ده&zwnj;رخستن که برایه&zwnj;کی باوکییان هه&zwnj;یه و نه&zwnj;یانهێناوه له&zwnj;به&zwnj;ئه&zwnj;وه&zwnj;) له کاتێکدا ڕێکی خستن و بارگه و بنه&zwnj;ی بۆ پێچانه&zwnj;وه وتی: (من حه&zwnj;ز ده&zwnj;که&zwnj;م) برایه&zwnj;کی باوکیتان هه&zwnj;یه بۆم بهێنن، ئایا نابینن به&zwnj;ڕاستی من چۆن کێشانه و پێوانه&zwnj;م تێرو ته&zwnj;واوه و، چۆن ڕێز له میوان ده&zwnj;گرم (یارمه&zwnj;تی هه&zwnj;ژاران ده&zwnj;ده&zwnj;م).';
$t[60] = 'خۆ ئه&zwnj;گه&zwnj;ر ئه&zwnj;و برایه&zwnj;تانم بۆ نه&zwnj;هێنن، ئه&zwnj;وه ئیتر هیچ موعامه&zwnj;له و کێشانه و پێوانه&zwnj;ی ئیوه لای من نی یه و نزیکم مه&zwnj;که&zwnj;ونه&zwnj;وه&zwnj;.';
$t[61] = '(له وه&zwnj;ڵامیدا) وتیان: ئێمه به هه&zwnj;موو شێوه&zwnj;یه&zwnj;ك هه&zwnj;وڵ ده&zwnj;ده&zwnj;ین و نه&zwnj;خشه ده&zwnj;کێشین بۆ ئه&zwnj;و مه&zwnj;به&zwnj;سته (هه&zwnj;تا) باوکی ڕازی بکه&zwnj;ین (ئه&zwnj;م جاره بیهێنین)، بێگومان ئێمه ئه&zwnj;و کاره ده&zwnj;که&zwnj;ین.';
$t[62] = '(له فرسه&zwnj;تێکدا) یوسف به لاوه&zwnj;کانی به&zwnj;رده&zwnj;ستی وت: هه&zwnj;رچی شتومه&zwnj;ك و که&zwnj;لو په&zwnj;لێکیان هێناوه بیخه&zwnj;نه&zwnj;وه ناو باره&zwnj;کانیان، بۆ ئه&zwnj;وه&zwnj;ی بیناسنه&zwnj;وه کاتێك ده&zwnj;گه&zwnj;ڕێنه&zwnj;وه بۆ لای که&zwnj;س و کاریان بۆ ئه&zwnj;وه&zwnj;ی (ببێته هانده&zwnj;رێك و) بێنه&zwnj;وه&zwnj;.';
$t[63] = 'جا کاتێك گه&zwnj;ڕانه&zwnj;وه بۆ لای باوکیان وتیان: باوکی به&zwnj;ڕێزمان، (کێشان و) پێوانمان لێ قه&zwnj;ده&zwnj;غه کراوه (تا بنیامینی برامان له&zwnj;گه&zwnj;ڵ خۆماندا نه&zwnj;به&zwnj;ین)، که&zwnj;واته براکه&zwnj;مان له&zwnj;گه&zwnj;ڵ بنێره تاپێوانه بکه&zwnj;ین (به&zwnj;ده&zwnj;ست به&zwnj;تاڵی نه&zwnj;گه&zwnj;ڕێنه&zwnj;وه&zwnj;)، به&zwnj;ڵێن بێت پارێزه&zwnj;رین بۆی.';
$t[64] = 'یه&zwnj;عقوبی باوکیان وتی: (ئه&zwnj;مه داخوازیه&zwnj;کی سه&zwnj;یره&zwnj;) ئایا هه&zwnj;روا متمانه&zwnj;تان پێ بکه&zwnj;م له&zwnj;سه&zwnj;ری مه&zwnj;گه&zwnj;ر هه&zwnj;روه&zwnj;ك پێشتر متمانه&zwnj;م پێکردن له&zwnj;سه&zwnj;ر یوسفی برای؟! (سه&zwnj;رئه&zwnj;نجام ڕازیان کرد و، ئه&zwnj;ویش وتی) که&zwnj;واته خوا چاکترین پارێزه&zwnj;ره هه&zwnj;ر ئه&zwnj;و میهره&zwnj;بانترینی میهره&zwnj;بانانه&zwnj;.';
$t[65] = 'کاتێکیش که&zwnj;لوپه&zwnj;ل و بارگه و بنه&zwnj;کانیان کرده&zwnj;وه بینیان هه&zwnj;رچی شتومه&zwnj;کیانه هه&zwnj;ر ئه&zwnj;وه خراوه&zwnj;ته&zwnj;وه باره&zwnj;کانیان و گه&zwnj;ڕێنراوه&zwnj;ته&zwnj;وه بۆیان، (ئینجا باوکیان بانگ کردو) وتیان: باوکی به&zwnj;ڕێزمان ئێمه چی بکرین، چی بڵێین (له به&zwnj;رامبه&zwnj;ر ئه&zwnj;و پاشا به&zwnj;ڕێزه&zwnj;وه&zwnj;) ئه&zwnj;وه&zwnj;ته هه&zwnj;رچی که&zwnj;ل و په&zwnj;لمان هه&zwnj;یه گه&zwnj;ڕێنراوه&zwnj;ته&zwnj;وه بۆمان و (تۆ ئه&zwnj;گه&zwnj;ر مۆڵه&zwnj;تی بنیامین بده&zwnj;ی) ڕزق و ڕۆزیی زۆر ده&zwnj;هێنین بۆ که&zwnj;س و کارمان، براکه&zwnj;شمان ده&zwnj;پارێزین و باری وشترێکیش زیاتر ده&zwnj;هینین، ئه&zwnj;و باره&zwnj;ش شتێکی ئاسانه&zwnj;.';
$t[66] = 'یه&zwnj;عقوب وتی: هه&zwnj;رگیز له&zwnj;گه&zwnj;ڵتاندا ناینێرم هه&zwnj;تا په&zwnj;یمانێکی خواییم پێ نه&zwnj;ده&zwnj;ن (سوێند نه&zwnj;خۆن) که بێگومان بیهێننه&zwnj;وه بۆ لام، مه&zwnj;گه&zwnj;ر ده&zwnj;وره بدرێن و هیچ ده&zwnj;سه&zwnj;ڵاتتان نه&zwnj;مێنێت، جا کاتێك په&zwnj;یمانه&zwnj;که&zwnj;یان دایه و سوێندیان بۆ خوارد، یه&zwnj;عقوب وتی: خوا خۆی ئاگادارو چاودێره به&zwnj;و په&zwnj;یمانه&zwnj;ی که بڕیارمان له&zwnj;سه&zwnj;ر داوه&zwnj;.';
$t[67] = 'هوروه&zwnj;ها وتی: کوڕه&zwnj;کانم، هه&zwnj;مووتان له یه&zwnj;ك ده&zwnj;روازه&zwnj;وه مه&zwnj;چنه ژووره&zwnj;وه و له چه&zwnj;ند ده&zwnj;روازه&zwnj;یه&zwnj;کی جیا جیاوه بچنه ژووره&zwnj;وه (خۆتان بکه&zwnj;ن به شاردا)، دڵنیاش بن که من ناتوانم هیچ شتێک له&zwnj;و به&zwnj;ڵاو پێشهاتانه&zwnj;ی که بڕیاری خوای له&zwnj;سه&zwnj;ر بگێڕمه&zwnj;وه له ئێوه&zwnj;، هیچ فه&zwnj;رمان و بڕیارێک بۆ که&zwnj;س نی یه&zwnj;، ته&zwnj;نها بۆ خوا نه&zwnj;بێت، پشتم هه&zwnj;ر به&zwnj;و به&zwnj;ستووه و پشت و په&zwnj;نام هه&zwnj;ر ئه&zwnj;وه&zwnj;، جا ده&zwnj;با ئه&zwnj;وانه&zwnj;ی که پاڵپشت و پشتیوانیان ده&zwnj;وێت هه&zwnj;ر پشت به&zwnj;و ببه&zwnj;ستن.';
$t[68] = 'کاتیك چوونه ناوه&zwnj;وه به&zwnj;و شێوه&zwnj;یه&zwnj;ی که باوکیان فه&zwnj;رمانی پێدابوون، جا وه&zwnj;نه&zwnj;بێت باوکیان توانیبێتی فریایان بکه&zwnj;وێت و له هیچ پێشهاتێك ڕزگاریان بکات که بڕیارداده&zwnj;ی خوای له&zwnj;سه&zwnj;ره&zwnj;، (ئه&zwnj;و ڕێنمووییه) ته&zwnj;نها پێداویستیه&zwnj;کی ده&zwnj;روونی یه&zwnj;عقوب خۆی بوو، ڕایگه&zwnj;یاند، به&zwnj;ڕاستی یه&zwnj;عقوب خاوه&zwnj;نی زانستێك بوو، چونکه ئێمه فێرمان کردبوو و به&zwnj;ڵام زۆربه&zwnj;ی خه&zwnj;ڵکی ڕاسته&zwnj;یه&zwnj;کان نازانن.';
$t[69] = 'کاتێکیش که کوڕه&zwnj;کان چونه ژوره&zwnj;وه بۆ لای یوسف، بنیامینی برای نزیك کرده&zwnj;وه له خۆی و (له فرسه&zwnj;تێکدا) وتی: به&zwnj;ڕاستی من یوسفی براتم، جا خه&zwnj;فه&zwnj;ت مه&zwnj;خۆو خه&zwnj;مت نه&zwnj;بێت به&zwnj;و هه&zwnj;ڵس و که&zwnj;وته&zwnj;ی ئه&zwnj;وان ده&zwnj;یانکرد (چونکه من نه&zwnj;خشه&zwnj;م کێشاوه تۆ لێره بهێڵمه&zwnj;وه&zwnj;، تا له لای خۆم ژیان به&zwnj;سه&zwnj;ر به&zwnj;ریت).';
$t[70] = 'جا کاتێك یوسف بارگه و بنه&zwnj;ی بۆ پێچانه&zwnj;وه و سازی کردن (خۆراك و دانه&zwnj;وێڵه&zwnj;ی پێویستی) پێبه&zwnj;خشین جامولکه&zwnj;ی کێشان و پێوانی خسته ناو باری بنیامینی برایه&zwnj;وه&zwnj;، پاشان جارچیه&zwnj;ك که به&zwnj;ده&zwnj;نگی به&zwnj;رز وتی: ئه&zwnj;ی کاروانچیان ئێوه به&zwnj;ڕاستی دزن؟!';
$t[71] = 'ئه&zwnj;وانیش به&zwnj;په&zwnj;له گه&zwnj;ڕانه&zwnj;وه و هاتن به&zwnj;ره&zwnj;و ڕوویان وتیان: چی دیار نیه&zwnj;؟ چیتان لێ ون بووه&zwnj;؟';
$t[72] = 'پاساوانه&zwnj;کان وتیان: جامولکه و ده&zwnj;فری پاشامان لێ ون بووه&zwnj;و بۆی ده&zwnj;گه&zwnj;ڕێن، هه&zwnj;ر که&zwnj;سێك هێنای باری وشترێکی بۆ هه&zwnj;یه (لێپرسراوه&zwnj;که&zwnj;یان وتی): من ده&zwnj;بمه زامنی ئه&zwnj;و به&zwnj;ڵینه&zwnj;.';
$t[73] = 'کوڕه&zwnj;کان وتیان: سوێند به خوا، به&zwnj;ڕاستی ئێوه چاك ده&zwnj;زانن که ئێمه بۆ ئه&zwnj;وه نه&zwnj;هاتووین فه&zwnj;سادو خراپه له زه&zwnj;ویدا بکه&zwnj;ین و ئێمه دزنین.';
$t[74] = 'پاسه&zwnj;وانه&zwnj;کان وتیان: ئه&zwnj;گه&zwnj;ر (ده&zwnj;رکه&zwnj;وت) ئێوه درۆزن بوون، که&zwnj;وابوو پاداشتی ئه&zwnj;ودزه چیه&zwnj;؟';
$t[75] = 'کوڕه&zwnj;کان وتیان: پاداشته&zwnj;که&zwnj;ی ئه&zwnj;و که&zwnj;سه&zwnj;ی که ده&zwnj;فره دزراوه&zwnj;که له&zwnj;باره&zwnj;که&zwnj;یدا ده&zwnj;دۆزرێته&zwnj;وه&zwnj;، ئه&zwnj;وه پاداشته&zwnj;که&zwnj;یه&zwnj;تی (که بریتیه له&zwnj;وه&zwnj;ی بارمته بێت لای خاوه&zwnj;نی شته دزراوه&zwnj;که&zwnj;)، ئێمه ئا به&zwnj;و شێوه&zwnj;یه تۆڵه له سته&zwnj;مکاران ده&zwnj;سێنین.';
$t[76] = 'ئینجا یوسف خۆی ده&zwnj;ستی کرد به&zwnj;گه&zwnj;ڕان به&zwnj;باره&zwnj;کاندا پێش ئه&zwnj;وه&zwnj;ی باری بنیامینی برای بپشکنێت، له&zwnj;وه&zwnj;ودوا له&zwnj;باری براکه&zwnj;یدا ده&zwnj;ری هێنا، ئا به&zwnj;و شێوه&zwnj;یه یوسفمان فێری نه&zwnj;خشه&zwnj;ی گلدانه&zwnj;وه&zwnj;ی براکه&zwnj;ی کرد، نه&zwnj;ده&zwnj;بوو به&zwnj;ده&zwnj;ستووری پاشا براکه&zwnj;ی گل بداته&zwnj;وه&zwnj;، مه&zwnj;گه&zwnj;ر ویستی خوای گه&zwnj;وره&zwnj;ی له&zwnj;سه&zwnj;ر بێت، به&zwnj;و شێوه&zwnj;یه چه&zwnj;نده&zwnj;ها جار پله&zwnj;و پاییه&zwnj;ی هه&zwnj;ر که&zwnj;س که بمانه&zwnj;وێت به&zwnj;رزو بڵند ده&zwnj;که&zwnj;ینه&zwnj;وه و له سه&zwnj;روو هه&zwnj;موو خاوه&zwnj;ن زانست و زانیارێکه&zwnj;وه زانا و شاره&zwnj;زاتر هه&zwnj;یه&zwnj;.';
$t[77] = 'براکانی وتیان: ئه&zwnj;گه&zwnj;ر ئه&zwnj;و دزی بکات، ئه&zwnj;وه براکه&zwnj;شی بێگومان دزی کردووه له&zwnj;مه&zwnj;وپێش، ئینجا یوسف ئه&zwnj;و قسه ناڕه&zwnj;واو تۆمه&zwnj;ته ناقۆڵایه&zwnj;ی له&zwnj;دڵی خۆیدا هێشته&zwnj;وه و نه&zwnj;یخسته ڕوو بۆ ئه&zwnj;وان، وتی: ئێوه له که&zwnj;سانێکی باش ناچن، جا خوا خۆی له هه&zwnj;موو که&zwnj;س زاناتره به&zwnj;وه&zwnj;ی که ده&zwnj;یڵێن و باسی ده&zwnj;که&zwnj;ن.';
$t[78] = 'براکانی وتیان: جه&zwnj;نابی پاشا، به&zwnj;ڕاستی ئه&zwnj;م کوڕه باوکێکی پیری به&zwnj;ساڵاچووی هه&zwnj;یه&zwnj;، که&zwnj;واته یه&zwnj;کێك له ئێمه له&zwnj;جیاتی ئه&zwnj;و گل بده&zwnj;ره&zwnj;وه&zwnj;، چونکه بێگومان ئێمه تۆ به پیاو چاك و چاکه&zwnj;خواز ده&zwnj;زانین.';
$t[79] = 'یوسف وتی: په&zwnj;نا به&zwnj;خوا، ئه&zwnj;گه&zwnj;ر هه&zwnj;ڵه&zwnj;ی وا بکه&zwnj;ین ته&zwnj;نها ئه&zwnj;و که&zwnj;سه گل ئه&zwnj;ده&zwnj;ینه&zwnj;وه که شتومه&zwnj;که&zwnj;کانمان له&zwnj;لای دۆزیوه&zwnj;ته&zwnj;وه&zwnj;، خۆ ئه&zwnj;گه&zwnj;ر وا نه&zwnj;که&zwnj;ین، ئه&zwnj;وه ئیتر ئێمه به&zwnj;ڕاستی سته&zwnj;مکارین.';
$t[80] = 'ئینجا کاتێك که نائومێد بوون له پاشا، له که&zwnj;نارێکه&zwnj;وه بۆ خۆیان ده&zwnj;ستیان کرد به&zwnj;چپه&zwnj;و ڕا گۆڕینه&zwnj;وه گه&zwnj;وره&zwnj;که&zwnj;یان وتی: باشه&zwnj;، ئه&zwnj;ی نه&zwnj;تان زانی که بێگومان باوکتان به&zwnj;ڕاستی په&zwnj;یمانێکی لێ وه&zwnj;رگرتوون و خوایشی کردووه به شایه&zwnj;ت به&zwnj;سه&zwnj;رتانه&zwnj;وه&zwnj;، پێشتریش ئاشکرایه که چیتان کرد به یوسف و پیلانتان گێڕا دژی، ئیتر من هه&zwnj;رگیز ئه&zwnj;م سه&zwnj;ر زه&zwnj;مین و وڵاته به&zwnj;جێ ناهێڵم هه&zwnj;تا باوکم مۆڵه&zwnj;تم ده&zwnj;دات، یاخود خوا فه&zwnj;رمانێك بدات له قازانجم بێت، ئه&zwnj;و زاته&zwnj;ش چاکترینی دادوه&zwnj;رانه.';
$t[81] = 'بگه&zwnj;ڕێنه&zwnj;وه بۆ لای باوکتان و پێ بڵێن: ئه&zwnj;ی باوکی به&zwnj;ڕێزمان، بێگومان کوڕه&zwnj;که&zwnj;ت دزی کردوو ئێمه ئاگامان لێ نه&zwnj;بووه&zwnj;، جگه له&zwnj;وه&zwnj;ی که زانیومانه&zwnj;، ئێمه له شاراوه و نهێنیه&zwnj;کان شاره&zwnj;زا نین (نازانین ئه&zwnj;و کوڕه بۆ وای کرد).';
$t[82] = '(حه&zwnj;ز ده&zwnj;که&zwnj;یت، له خه&zwnj;ڵکی) ئه&zwnj;و شاره&zwnj;ش بپرسه که ئێمه&zwnj;ی لێ بووین، له&zwnj;و کاروانه&zwnj;ش بپرسه که ئێمه&zwnj;ی تێدا بووین و پیکه&zwnj;وه هاتینه&zwnj;وه ئێمه به&zwnj;دڵنیایه&zwnj;وه ڕاست ده&zwnj;ڵێن و ڕاستگۆشین.';
$t[83] = '(کاتێك کوڕه&zwnj;کان گه&zwnj;یشتنه&zwnj;وه لای باوکیان و به&zwnj;سه&zwnj;رهاته&zwnj;که&zwnj;یان بۆ گێڕایه&zwnj;وه&zwnj;) وتی: (وانیه&zwnj;) به&zwnj;ڵکو دیسانه&zwnj;وه خۆتان پیلانێکی ترتان ڕێك خستووه&zwnj;، جا چارم نیه خۆگریه&zwnj;کی جوان نه&zwnj;بێت، ئومێده په&zwnj;روه&zwnj;ردگار هه&zwnj;موویانم بۆ بهێنێته&zwnj;وه و به&zwnj;هه&zwnj;موویان شاد ببم، به&zwnj;ڕاستی ئه&zwnj;و زاته زاناو دانایه&zwnj;.';
$t[84] = '(ئه&zwnj;وسا چووه که&zwnj;نارێکه&zwnj;وه&zwnj;) و لێیان دوور که&zwnj;وته&zwnj;وه&zwnj;و (به&zwnj;ده&zwnj;م هه&zwnj;ناسه&zwnj;ی ساردو ئه&zwnj;سرینه&zwnj;وه&zwnj;) وتی: ئه&zwnj;ی داخم بۆ یوسفی نازدار، (له دڵم ده&zwnj;رناچێت یوسفی جوان خاس و خوا ناسم ئه&zwnj;وه&zwnj;نده گریاو فرمێسکی هه&zwnj;ڵوه&zwnj;ران)، چاوه&zwnj;کانی سپی هه&zwnj;ڵگه&zwnj;ڕاو ئاوی سپی تێزاو بینینی نه&zwnj;ما له ئاخ و داخ و خه&zwnj;فه&zwnj;ت و ئێشدا، هه&zwnj;ر خه&zwnj;م و په&zwnj;ژاره&zwnj;ی خۆی ده&zwnj;خوارده&zwnj;وه&zwnj;.';
$t[85] = '(کوڕه&zwnj;کان به&zwnj;زه&zwnj;ییان به باوکیاندا هاته&zwnj;وه&zwnj;و) وتیان: سوێند به خوا تۆ هه&zwnj;ر واز ناهێنیت یادی یوسف ده&zwnj;که&zwnj;یت و ناوی ده&zwnj;به&zwnj;یت تا وات لێ دێت په&zwnj;کت ده&zwnj;که&zwnj;وێت یاخود تیا له تیاچوون ده&zwnj;بیت.';
$t[86] = '(له وه&zwnj;ڵامیاندا) وتی: به&zwnj;ڕاستی من باسی سکاڵا و غه&zwnj;م و په&zwnj;ژاره&zwnj;م هه&zwnj;ر بۆ لای خوا ده&zwnj;به&zwnj;م، ئه&zwnj;وه&zwnj;ی من ده&zwnj;یزانم له لایه&zwnj;ن خواوه (له نه&zwnj;خشه و حیکمه&zwnj;ته&zwnj;کانی، له پێشهات و ڕووداوه&zwnj;کاندا) ئێوه نایزانن.';
$t[87] = '(دوای چه&zwnj;ند ڕۆژێك یه&zwnj;عقوب فه&zwnj;رمانیدا به&zwnj;کوڕه&zwnj;کانی) و وتی: کوڕه&zwnj;کانم بچنه&zwnj;وه و گوێ هه&zwnj;ڵخه&zwnj;ن بۆ یوسف و براکه&zwnj;ی، له سۆزو میهره&zwnj;بانی خوایش نائومێد مه&zwnj;بن، چونکه به&zwnj;ڕاستی که&zwnj;س نائومێد نابێت له سۆزو میهره&zwnj;بانی خوا جگه له که&zwnj;سانی خوانه&zwnj;ناس و بێ باوه&zwnj;ڕ.';
$t[88] = 'کاتێك (گه&zwnj;شتنه میسر) و چوونه باره&zwnj;گای یوسف وتیان: جه&zwnj;نابی پاشا ئێمه و که&zwnj;س و کارمان نه&zwnj;هاتی و گرانی و کوێره&zwnj;وه&zwnj;ریی زۆرمان تووش هاتووه وکه&zwnj;ل و په&zwnj;لێکی تێکه&zwnj;ڵ و پێکه&zwnj;ڵ و بێ نرخیشمان هێناوه&zwnj;، که&zwnj;واته چیمان پێویسته بۆمان بپێوه و خێریشمان پێ بکه&zwnj;، بێگومان خوا پاداشتی خێر خوازان ده&zwnj;داته&zwnj;وه&zwnj;.';
$t[89] = '(ئینجا یوسف زانی کاتی ئه&zwnj;وه هاتووه که خۆی بناسێنێت بۆیه&zwnj;) وتی: ئایا زانیتان و له بیرتانه چیتان کرد به یوسف و به&zwnj;براکه&zwnj;ی کاتێك ئێوه نه&zwnj;فام بوون و ترسی خواتان له دڵدا نه&zwnj;بوو؟';
$t[90] = '(هه&zwnj;موو به&zwnj;سه&zwnj;رسوڕمانه&zwnj;وه چاویان ئه&zwnj;بڵه&zwnj;ق بوو سه&zwnj;رنجیان داو) وتیان: ئایا به&zwnj;ڕاست، بۆچی تۆ یوسفیت؟! وتی: به&zwnj;ڵێ، من یوسفم و ئه&zwnj;مه&zwnj;ش (بنیامین) ی برامه، به&zwnj;ڕاستی خوای میهره&zwnj;بان ڕێزی لێناوین و به&zwnj;هره&zwnj;مه&zwnj;ندی کردووین، بێگومان ئه&zwnj;وه&zwnj;ی خۆپارێز بێت له گوناهو خۆگر بێت (له به&zwnj;رامبه&zwnj;ر پێشهاته&zwnj;کانه&zwnj;وه&zwnj;، خوای گه&zwnj;وره ده&zwnj;رووی لێ ده&zwnj;کاته&zwnj;وه&zwnj;) ئه&zwnj;وه به&zwnj;ڕاستی خوا پاداشتی چاکه&zwnj;کاران و چاکه&zwnj;خوازان به&zwnj;زایه نادات.';
$t[91] = 'ئینجا براکانی وتیان: سوێندمان به&zwnj;خوا به&zwnj;ڕاستی خوای گه&zwnj;وره ڕێزی تۆی داوه به&zwnj;سه&zwnj;ر ئێمه&zwnj;دا، ئێمه به&zwnj;ڕاستی کاتی خۆی به&zwnj;هه&zwnj;ڵه&zwnj;دا چووین و گوناهبارین.';
$t[92] = 'یوسف وتی: خه&zwnj;متان نه&zwnj;بێت ئه&zwnj;مڕۆ ئیتر هیچ سه&zwnj;ر زه&zwnj;نشتێکتان له&zwnj;سه&zwnj;ر نی یه&zwnj;، خوا لێتان خۆش بێت، بێگومان ئه&zwnj;و زاته له هه&zwnj;موو میهره&zwnj;بانان میهره&zwnj;بانتره&zwnj;.';
$t[93] = '(یوسف هه&zwnj;واڵی باوکی پرسی، که زانی نابینا بووه وتی): بڕۆن ئه&zwnj;م کراسه&zwnj;م به&zwnj;رن و بیده&zwnj;ن به&zwnj;سه&zwnj;رو ڕووی باوکمدا بینایی بۆ ده&zwnj;گه&zwnj;ڕێته&zwnj;وه و چاوی چاك ده&zwnj;بێته&zwnj;وه&zwnj;، پاشان هه&zwnj;موو که&zwnj;س و کارتانم بۆ بهێنن به&zwnj;گشتی.';
$t[94] = 'کاتێك کاروان ئاوه&zwnj;دانی به&zwnj;جێهێشت و که&zwnj;وته ڕێ، باوکیان وتی: به&zwnj;ڕاستی من هه&zwnj;ست به بۆنی یوسف ده&zwnj;که&zwnj;م ئه&zwnj;گه&zwnj;ر به&zwnj;بێ هۆش و خه&zwnj;ڵه&zwnj;فاوم نه&zwnj;زانن و (بڕوام پێبکه&zwnj;ن).';
$t[95] = 'ئه&zwnj;و که&zwnj;سانه&zwnj;ی که له لای بوون وتیان: سوێند به&zwnj;خوا بێگومان تۆ هه&zwnj;ر له ناو سه&zwnj;رگه&zwnj;ردانی و خه&zwnj;یاڵه کۆنه&zwnj;که&zwnj;ی خۆتدایت!!';
$t[96] = 'ئینجا که مژده هێنه&zwnj;ره&zwnj;که گه&zwnj;یشت و کراسه&zwnj;که&zwnj;یدا به&zwnj;سه&zwnj;ر ده&zwnj;م و چاویدا، خێرا بینای بۆ گه&zwnj;ڕایه&zwnj;وه&zwnj;، چاوه&zwnj;کانی گه&zwnj;شایه&zwnj;وه&zwnj;، وتی: ئایا من پێم نه&zwnj;وتن، به&zwnj;ڕاستی ئه&zwnj;وه&zwnj;ی من ده&zwnj;یزانم له لایه&zwnj;ن خواوه&zwnj;، ئێوه نایزانن؟!';
$t[97] = '(دوای ماوه&zwnj;یه&zwnj;ك کوڕه&zwnj;کان گه&zwnj;یشتن وهه&zwnj;موو شتێك ئاشکرا بوو) وتیان: ئه&zwnj;ی باوکی به&zwnj;ڕێزمان داوای لێخۆشبوونی گوناهه&zwnj;کانمان بۆ بکه (له خوای میهره&zwnj;بان) به&zwnj;ڕاستی ئێمه گوناهکار بووین و به&zwnj;هه&zwnj;ڵه&zwnj;دا چوو بووین.';
$t[98] = 'باوکیان وتی: له ئاینده&zwnj;دا داوای لێ خۆشبوونتان له په&zwnj;روه&zwnj;ردگارم بۆ ده&zwnj;که&zwnj;م، به&zwnj;ڕاستی ئه&zwnj;و زاته لێخۆشبوو میهره&zwnj;بانه&zwnj;.';
$t[99] = 'ئینجا (که هه&zwnj;موویان گه&zwnj;یشتنه میسر) چوونه لای یوسف، باوك و دایکی هێنایه نزیکی خۆی و (ڕێزی زۆری بۆ ده&zwnj;ڕبڕین، ئاخۆ چه&zwnj;نده دڵخۆش و کامه&zwnj;ران بووبن، دوای ئه&zwnj;و ماوه دوور و درێژه به کوڕی جوان کردارو خواناسیان شاد بوون، ئیستاش پاشای میسره&zwnj;) و پێی وتن: انشاءالله، ئه&zwnj;گه&zwnj;ر خوا ویستی بێت به&zwnj;یه&zwnj;کجاریی وه&zwnj;رن بۆ میسر، به&zwnj;وپه&zwnj;ڕی شادی و ئارامی و کامه&zwnj;رانی نیشته&zwnj;جێ ببن.';
$t[100] = 'ئه&zwnj;وسا دایك وباوکی به&zwnj;رز کرده&zwnj;وه بۆ سه&zwnj;ر ته&zwnj;ختی پاشایه&zwnj;تی و حوکمڕانی خۆی، هه&zwnj;مووان کڕنووشیان بۆ برد (دیاره که ئه&zwnj;وسا دروست بووه و نیشانه&zwnj;ی ڕێزو پێزانین بووه&zwnj;)، یوسف که ئه&zwnj;مه&zwnj;ی دی وتی: باوکی به&zwnj;ڕێزم ئه&zwnj;مه لێکدانه&zwnj;وه&zwnj;ی خه&zwnj;ونه&zwnj;که&zwnj;ی ئه&zwnj;وسامه که به&zwnj;منداڵی بینیم، ئه&zwnj;وه&zwnj;ته بێگومان په&zwnj;روه&zwnj;ردگارم هێنایه&zwnj;دی و به&zwnj;ڕاست ده&zwnj;رچوو، به&zwnj;ڕاستی ئه&zwnj;و زاته چاکه&zwnj;ی له&zwnj;گه&zwnj;ڵدا کردم و به&zwnj;سۆز بوو بۆم، کاتێك له زیندان ڕزگاری کردم، ئه&zwnj;وه&zwnj;ته ئێوه&zwnj;ی له بیابانه&zwnj;وه هێنایه&zwnj;وه&zwnj;و (به&zwnj;یه&zwnj;ك شادی کردینه&zwnj;وه&zwnj;) دوای ئه&zwnj;وه&zwnj;ی شه&zwnj;یتان نێوانی من و براکانمی تێکدابوو، به&zwnj;ڕاستی په&zwnj;روه&zwnj;ردگارم به&zwnj;سۆزو به&zwnj;لوطفه بۆ هه&zwnj;رشتێك که ده&zwnj;یه&zwnj;وێت (په&zwnj;له&zwnj;ی نیه&zwnj;و ڕێگه&zwnj;ی به&zwnj;شێنه&zwnj;یی بۆخۆش ده&zwnj;کات)، بێگومان ئه&zwnj;و زاته زانایه (به&zwnj;نهێنی و ئاشکرای دروستکراوانی) و دانایه (به&zwnj;نه&zwnj;خشه&zwnj;ی ورد، هه&zwnj;رچی بیه&zwnj;وێت ئه&zwnj;نجامی ده&zwnj;دات).';
$t[101] = '(ئینجا یوسف پێغه&zwnj;مبه&zwnj;ر چووه که&zwnj;ناره&zwnj;وه و ده&zwnj;سته&zwnj;کانی هه&zwnj;ڵبڕی و نزای کردو وتی): په&zwnj;روه&zwnj;ردگارا به&zwnj;ڕاستی تۆ پاشایه&zwnj;تیت پێ به&zwnj;خشیم و له لێکدانه&zwnj;وه&zwnj;ی خه&zwnj;و شاره&zwnj;زات کردم، ئه&zwnj;ی به&zwnj;دیهێنه&zwnj;ری ئاسمانه&zwnj;کان و زه&zwnj;وی، تۆ پشت و په&zwnj;نا و یارو یاوه&zwnj;ری منیت له دنیاو قیامه&zwnj;تدا، داواکارم که به موسڵمانی بم مرێنیت و بمخه&zwnj;یته ڕیزی پاکانه&zwnj;وه و بمگه&zwnj;یه&zwnj;نیت به&zwnj;کاروانی چاکان.';
$t[102] = 'ئه&zwnj;ی محمد ئه&zwnj;م به&zwnj;سه&zwnj;ر هات و سه&zwnj;رگوزشته&zwnj;یه&zwnj;ی یوسف که بۆت نیگا ده&zwnj;که&zwnj;ین له هه&zwnj;واڵ و به&zwnj;سه&zwnj;رهاته شاراوه&zwnj;کانه&zwnj;، خۆ تۆ له لای براکانی یوسف نه&zwnj;بوویت کاتێك که هه&zwnj;موویان بڕیاریاندا و پیلانیان ده&zwnj;گێڕا....';
$t[103] = '(دڵنیاش به&zwnj;) که زۆربه&zwnj;ی خه&zwnj;ڵکی باوه&zwnj;ڕدار نین، هه&zwnj;رچه&zwnj;نده تۆ زۆر به&zwnj;په&zwnj;رۆش بیت و هه&zwnj;وڵ بده&zwnj;یت و تێ بکۆشیت.';
$t[104] = '(ئه&zwnj;ی محمد صلی الله علیه وسلم) خۆ تۆ هیچ پاداشت و به&zwnj;خششێكت لێیان ناوێت له&zwnj;سه&zwnj;ر گه&zwnj;یاندنی په&zwnj;یامه&zwnj;که&zwnj;، ئه&zwnj;م قورئانه هیچی تر نی یه جگه له&zwnj;وه&zwnj;ی که یادخه&zwnj;ره&zwnj;وه&zwnj;یه بۆ هه&zwnj;موو خه&zwnj;ڵکی جیهان.';
$t[105] = 'چه&zwnj;نده&zwnj;ها به&zwnj;ڵگه&zwnj;و نیشانه له ئاسمانه&zwnj;کان و زه&zwnj;ویدا هه&zwnj;ن (له&zwnj;سه&zwnj;ر ده&zwnj;سه&zwnj;ڵاتدارێتی و جوانکاری خوای په&zwnj;روه&zwnj;ردگار) که&zwnj;چی زۆربه&zwnj;ی خه&zwnj;ڵکی به&zwnj;لایدا تێده&zwnj;په&zwnj;ڕن و تێنافکرن و پشتی تێده&zwnj;که&zwnj;ن.';
$t[106] = 'جا زۆربه&zwnj;ی زۆری خه&zwnj;ڵکی باوه&zwnj;ڕناهێنن به خوای تاك و ته&zwnj;نها، بێ ئه&zwnj;وه&zwnj;ی هاوه&zwnj;ڵی بۆ بڕیار نه&zwnj;ده&zwnj;ن (بێگومان ئیمان و باوه&zwnj;ڕی وا هیچ نرخی نیه و خوای گه&zwnj;وره له&zwnj;و تاوانه خۆش نابێت).';
$t[107] = 'ئایا ئه&zwnj;وانه له&zwnj;وه ئه&zwnj;مینن و ناترسن که سزایه&zwnj;کی خوایی هه&zwnj;موویان بگرێته&zwnj;وه و (له&zwnj;ناویان به&zwnj;رێت)، یاخود کوتوپڕ قیامه&zwnj;ت به&zwnj;رپا ببێت له کاتێکدا ئه&zwnj;وان هه&zwnj;ست به&zwnj;نزیکی به&zwnj;رپابوونی نه&zwnj;که&zwnj;ن.';
$t[108] = '(ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر صلی الله علیه وسلم! تۆ) بڵێ: ئا ئه&zwnj;مه ڕێگه&zwnj;و ڕێبازمه&zwnj;: من و شوێنکه&zwnj;وتوانم بانگ ده&zwnj;که&zwnj;ین بۆ لای خوای په&zwnj;روه&zwnj;ردگار له&zwnj;سه&zwnj;ر بنچینه&zwnj;و بناغه&zwnj;یه&zwnj;کی ڕوون و ئاشکرا، پاکی و بێگه&zwnj;ردیش بۆ زاتی په&zwnj;روه&zwnj;ردگاره&zwnj;، من هیچ کاتێك له موشریك و هاوه&zwnj;ڵگه&zwnj;ران نیم.';
$t[109] = 'ئێمه که&zwnj;سمان نه&zwnj;ناردووه له پێش تۆدا جگه له پیاوانێك که هه&zwnj;ڵمان بژاردوون له خه&zwnj;ڵکی شارو شوێنه&zwnj;کان و نیگایان بۆ ده&zwnj;نێرین (تا په&zwnj;یامی ئێمه به&zwnj;ڕوونی بگه&zwnj;یه&zwnj;نن)، ئایا نه&zwnj;گه&zwnj;ڕاون له زه&zwnj;ویدا، تا ته&zwnj;ماشا بکه&zwnj;ن و سه&zwnj;رنج بده&zwnj;ن که&zwnj;: چۆن بوو سه&zwnj;رئه&zwnj;نجامی ئه&zwnj;وانه&zwnj;ی پێش ئه&zwnj;مان (کاتێك یاخی بوون)، بێگومان جێگه&zwnj;و ڕێگه&zwnj;و خانووبه&zwnj;ره&zwnj;ی به&zwnj;هه&zwnj;شت له قیامه&zwnj;تدا چاکتره بۆ ئه&zwnj;وانه&zwnj;ی که خواناس بوون و پارێزگاریان کردووه&zwnj;، ئایا ئه&zwnj;وه عه&zwnj;قڵ و ژیریتان نا&zwnj;خه&zwnj;نه&zwnj;کار؟!.';
$t[110] = '(ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر صلی اللله علیه وسلم وا مه&zwnj;زانه که ئه&zwnj;م ڕێگه&zwnj;یه به&zwnj;گوڵ و گوڵزار چێنراوه&zwnj;، به&zwnj;ڵکو پڕه له ناخۆشی و ئازارو گه&zwnj;یشتووه&zwnj;ته ڕاده&zwnj;یه&zwnj;ك که&zwnj;): هه&zwnj;تا بگره کاتێك پێغه&zwnj;مبه&zwnj;ران تووشی نائومێدی و بێ هیوایی ده&zwnj;بن و گومان واده&zwnj;به&zwnj;ن که به ڕاستی ئه&zwnj;وان (به&zwnj;رنامه&zwnj;که&zwnj;یان) به درۆ زانراوه&zwnj;، ئه&zwnj;وه ئا له&zwnj;و کاتی بێ هیواییه&zwnj;دا، سه&zwnj;رکه&zwnj;وتنی تایبه&zwnj;تی ئێمه&zwnj;یان بۆ دێت، ئه&zwnj;وسا ئه&zwnj;وه&zwnj;ی بمانه&zwnj;وێت و شایسته&zwnj;بێت ڕزگاری ده&zwnj;که&zwnj;ین، جا که&zwnj;س نیه بتوانێت تۆڵه&zwnj;ی توندو تیژی ئێمه له که&zwnj;سانی تاوانبارو تاوانکار بگێڕێته&zwnj;وه و به&zwnj;رپه&zwnj;رچی بداته&zwnj;وه&zwnj;.';
$t[111] = 'سوێند بێت به&zwnj;خوا به&zwnj;ڕاستی له سه&zwnj;رگوزه&zwnj;شته و به&zwnj;سه&zwnj;رهاتی پێغه&zwnj;مبه&zwnj;ران په&zwnj;ندو ئامۆژگاری هه&zwnj;یه بۆ که&zwnj;سانی ژیرو هۆشمه&zwnj;ند، وه&zwnj;نه&zwnj;بێت ئه&zwnj;م قورئانه قسه&zwnj;و باسێك بێت هه&zwnj;ڵبه&zwnj;سترابێت، به&zwnj;ڵکو به&zwnj;ڕاست دانه&zwnj;ری ئه&zwnj;وه&zwnj;یه که له پێش خۆیه&zwnj;وه هاتووه (ته&zwnj;ورات و ئینجیل) و ڕوونکه&zwnj;ره&zwnj;وه و جیاکردنه&zwnj;وه&zwnj;ی هه&zwnj;موو شتێکی پێویسته له&zwnj;ئایندا، ڕێنمومیی و ڕه&zwnj;حمه&zwnj;تیشه بۆ که&zwnj;سانێك باوه&zwnj;ڕ ده&zwnj;هێنن.';
?>