$t = array();
$t[1] = 'سه&zwnj;رنجی سه&zwnj;ره&zwnj;تای سووره&zwnj;تی (البقرة) بده&zwnj;، ئه&zwnj;وه&zwnj;ی له&zwnj;م کتێبه&zwnj;دا هه&zwnj;یه ئایاته&zwnj;کانی قورئانی پیرۆزه و ئه&zwnj;وه&zwnj;ی که له سه&zwnj;رانسه&zwnj;ری ئه&zwnj;م قورئانه&zwnj;دا بۆ تۆ ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر صلی الله&zwnj;علیه وسلم دابه&zwnj;زێنراوه له لایه&zwnj;ن په&zwnj;روه&zwnj;ردگارته&zwnj;وه&zwnj;، ڕاستی و حه&zwnj;قیقه&zwnj;ته&zwnj;، به&zwnj;ڵام زۆربه&zwnj;ی خه&zwnj;ڵکی باوه&zwnj;ڕ ناهێنن.';
$t[2] = 'خوا ئه&zwnj;و زاته&zwnj;یه که به&zwnj;چاوی خۆتان ده&zwnj;بینن چۆن ئاسمانه&zwnj;کانی به&zwnj;بێ کۆڵه&zwnj;که و ستوون به&zwnj;رز کردۆته&zwnj;وه و ڕایگرتووه&zwnj;، له&zwnj;وه&zwnj;ودوا له&zwnj;سه&zwnj;ر ته&zwnj;ختی فه&zwnj;رمانڕه&zwnj;وایی وه&zwnj;ستاوه (ئه&zwnj;ڵبه&zwnj;ته چونیه&zwnj;تی وه&zwnj;ستان و ته&zwnj;ختی فه&zwnj;رمانڕه&zwnj;وایی و گه&zwnj;لێ شتی له&zwnj;و بابه&zwnj;تانه له ڕاده&zwnj;ی بۆچوونی ئێمه به&zwnj;ده&zwnj;ره&zwnj;، ته&zwnj;فسیری ئه&zwnj;م ئایه&zwnj;ته&zwnj;یان له پێشه&zwnj;وا مه&zwnj;لیك پرسیووه&zwnj;، له وه&zwnj;ڵامدا وتوویه&zwnj;تی: وه&zwnj;ستانه&zwnj;که حه&zwnj;قیقه&zwnj;تی هه&zwnj;یه&zwnj;، چونیه&zwnj;تیه&zwnj;که&zwnj;ی شاراوه&zwnj;یه&zwnj;، پرسیارکردن ده&zwnj;رباره&zwnj;ی بیدعه&zwnj;یه&zwnj;، باوه&zwnj;ڕ بوون پێی واجبه&zwnj;)، خۆرو مانگیشی ڕام هێناوه&zwnj;، هه&zwnj;ر یه&zwnj;که&zwnj;یان له چه&zwnj;رخ و خۆلی خۆیدا بێ وچان تا ماوه&zwnj;یه&zwnj;کی دیاریکراو ده&zwnj;سوڕێنه&zwnj;وه&zwnj;، هه&zwnj;ر ئه&zwnj;و زاته کاروباری جیهان هه&zwnj;ر هه&zwnj;مووی به&zwnj;ڕێ ده&zwnj;کات و ڕێکی ده&zwnj;خات، هه&zwnj;روه&zwnj;ها ئایه&zwnj;ته&zwnj;کانی قورئان به&zwnj;وردی و دوورو درێژی ده&zwnj;خاته به&zwnj;رچاو بۆ ئه&zwnj;وه&zwnj;ی دڵنیابن له به خزمه&zwnj;ت گه&zwnj;یشتن و ئاماده بوونی به&zwnj;رده&zwnj;م دادگای په&zwnj;روه&zwnj;ردگارتان (بۆ لێپرسینه&zwnj;وه&zwnj;).';
$t[3] = 'هه&zwnj;ر ئه&zwnj;و زاته&zwnj;شه که زه&zwnj;وی پان و به&zwnj;رین کردوه و (هه&zwnj;رچه&zwnj;نده له شێوه&zwnj;ی گۆدایه&zwnj;)، که&zwnj;ژو چیاو ڕووباری زۆری تێدا دابین کردووه&zwnj;، له هه&zwnj;موو به&zwnj;رو بوومێك جووتی فه&zwnj;راهه&zwnj;م هێناوه له هه&zwnj;ردوو جۆره&zwnj;، شه&zwnj;وی والێکردووه که ڕۆژ داپۆشێت، به&zwnj;ڕاستی ئا له&zwnj;و شتانه&zwnj;دا به&zwnj;ڵگه و نیشانه&zwnj;ی زۆر هه&zwnj;ن بۆ که&zwnj;سانێك بیربکه&zwnj;نه&zwnj;وه&zwnj;.';
$t[4] = 'له&zwnj;سه&zwnj;ر ڕووکاری زه&zwnj;وی پارچه&zwnj;ی جۆراوجۆر هه&zwnj;یه له پاڵ یه&zwnj;کدا (که کارکردیان هه&zwnj;یه له&zwnj;سه&zwnj;ر چۆنێتی و به&zwnj;رهه&zwnj;می ڕووه&zwnj;ك، جگه له&zwnj;وه&zwnj;ی جیاوازیان نیشانه&zwnj;ن بۆ کانزا جۆراو جۆره&zwnj;کان)، هه&zwnj;ره&zwnj;ها باخی ڕه&zwnj;زی ترێ، کشتوکاڵی هه&zwnj;مه جۆر له دانه&zwnj;وێڵه و بێستانه&zwnj;کاندا، دارخورمای یه&zwnj;ك قه&zwnj;دو دوو قه&zwnj;د، هه&zwnj;موو ئه&zwnj;مانه به&zwnj;یه&zwnj;ك ئاو ئاودێر ده&zwnj;کرێن و له یه&zwnj;ك جۆر ئاو ده&zwnj;خۆنه&zwnj;وه (به&zwnj;ڵام سه&zwnj;وئه&zwnj;نجام ئه&zwnj;و هه&zwnj;موو به&zwnj;روبومه جیاوازه له قه&zwnj;باره&zwnj;و تام و بۆن و ڕه&zwnj;نگ و شێوازدا، په&zwnj;یدا ده&zwnj;بن) و هه&zwnj;ندێکیانمان له هه&zwnj;ندێکی تر بۆ خۆراکی ئاده&zwnj;میزاد نازدارتر و په&zwnj;سه&zwnj;ندتر کردووه&zwnj;، به&zwnj;ڕاستی ئا له&zwnj;و شتانه&zwnj;شدا به&zwnj;ڵگه و نیشانه&zwnj;ی زۆر هه&zwnj;ن بۆ که&zwnj;سانێك عه&zwnj;قڵ و ژیری خۆیان بخه&zwnj;نه&zwnj;کار.';
$t[5] = 'خۆ ئه&zwnj;گه&zwnj;ر تۆ به&zwnj;شتێك سه&zwnj;رسام بیت و پێت سه&zwnj;یربێت، ئه&zwnj;وه قسه&zwnj;ی بێ باوه&zwnj;ڕان جێگه&zwnj;ی سه&zwnj;رسامییه که ده&zwnj;ڵێن: ئایا کاتێك بووینه&zwnj;وه به خاك و خۆڵ، سه&zwnj;رله&zwnj;نوێ ئایا زیندوو ده&zwnj;کرێینه&zwnj;وه&zwnj;و دروست ده&zwnj;بینه&zwnj;وه&zwnj;؟ ئا ئه&zwnj;وانه (که ئه&zwnj;و قسه&zwnj;یه ده&zwnj;که&zwnj;ن) ئه&zwnj;وانه&zwnj;ن که بێ باوه&zwnj;ڕ بوون به په&zwnj;روه&zwnj;ردگاریان، ئا ئه&zwnj;وانه زنجیره&zwnj;کان له گه&zwnj;ردنیاندان و ئه&zwnj;وانه نیشته&zwnj;جێی ناو ئاگری دۆزه&zwnj;خن و هه&zwnj;میشه ده&zwnj;بێ تیایدا بمێنه&zwnj;وه&zwnj;.';
$t[6] = '(لارو وێری خوانه&zwnj;ناسان گه&zwnj;یشتۆته ڕاده&zwnj;یه&zwnj;ك که&zwnj;): په&zwnj;له&zwnj;ت لێده&zwnj;که&zwnj;ن له پێشهاتنی به&zwnj;ڵاو ناخۆشی پێش ئه&zwnj;وه&zwnj;ی داوای چاکه و خۆشی بکه&zwnj;ن، له&zwnj;کاتێکدا بێگومان له پێشینانه&zwnj;وه نموونه&zwnj;ی تیاچوون و ڕیسوا بوونی خوانه&zwnj;ناسان تێپه&zwnj;ڕ بووه&zwnj;، بێگومان په&zwnj;روه&zwnj;ردگاری تۆ (ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر صلی الله علیه وسلم)خاوه&zwnj;نی چاوپۆشی و لێخۆشبوونه بۆ خه&zwnj;ڵکی، سه&zwnj;ره&zwnj;ڕای سته&zwnj;میان (ئه&zwnj;گه&zwnj;ر بگه&zwnj;ڕێنه&zwnj;وه بۆ لای ئیمان و خواناسی)، به&zwnj;ڕاستی په&zwnj;روه&zwnj;ردگاری تۆ زۆر تۆڵه سێنه&zwnj;رێکی به&zwnj;هێزه (له&zwnj;و که&zwnj;سانه&zwnj;ی که دژایه&zwnj;تی به&zwnj;رنامه به نرخه&zwnj;که&zwnj;ی ده&zwnj;که&zwnj;ن.';
$t[7] = 'ئه&zwnj;وانه&zwnj;ی که بێ باوه&zwnj;ڕ بوون ده&zwnj;ڵێن: ئه&zwnj;وه بۆ به&zwnj;ڵگه&zwnj;و نیشانه&zwnj;یه&zwnj;ك له&zwnj;لایه&zwnj;ن په&zwnj;روه&zwnj;ردگاریه&zwnj;وه دانه&zwnj;به&zwnj;زێنرایه سه&zwnj;ری (که پێغه&zwnj;مبه&zwnj;ری خوایه&zwnj;) بێگومان، ئه&zwnj;ی محمد صلی الله علیه وسلم) تۆ ته&zwnj;نها ترسێنه&zwnj;رو بێدارکه&zwnj;ره&zwnj;وه&zwnj;ی ئه&zwnj;و خه&zwnj;ڵکه&zwnj;یت، بۆ هه&zwnj;ر میلله&zwnj;تێك پێغه&zwnj;مبه&zwnj;رو چاوساغ و ڕێپیشانده&zwnj;رێك هه&zwnj;یه&zwnj;.';
$t[8] = 'خوا چاك ده&zwnj;زانێت به&zwnj;وه&zwnj;ی هه&zwnj;موو مێینه&zwnj;یه&zwnj;ك چی له سکیدا هه&zwnj;ڵگرتووه (نێره یان مێ، ته&zwnj;واوه&zwnj;، یان ناته&zwnj;واو، ڕه&zwnj;نگی ڕووخساری، قه&zwnj;باره&zwnj;ی.... تد) هه&zwnj;روه&zwnj;ها ده&zwnj;زانێت به&zwnj;وه&zwnj;ش که ده&zwnj;پوکێته&zwnj;وه&zwnj;و له&zwnj;بار ده&zwnj;چێت و به&zwnj;وه&zwnj;ش که زیاد ده&zwnj;کات و گه&zwnj;وره ده&zwnj;بێت، هه&zwnj;موو شتێك لای ئه&zwnj;و زاته به&zwnj;ئه&zwnj;ندازه و پێوانه&zwnj;یه&zwnj;.';
$t[9] = 'ئه&zwnj;و زاته زانای نهێنی و شاراوه&zwnj;کانه هه&zwnj;روه&zwnj;ها زانای ئاشکراو بینراوه&zwnj;کانیشه&zwnj;، زاتێکی زۆر گه&zwnj;وره&zwnj;و به&zwnj;شکۆو بڵندو پایه&zwnj;داره.';
$t[10] = 'چونیه&zwnj;که لای ئه&zwnj;و زاته&zwnj;، ئه&zwnj;گه&zwnj;ر که&zwnj;سێکتان قسه بشارێته&zwnj;وه&zwnj;، یان بیڵێت و ئاشکرای بکات، هه&zwnj;روه&zwnj;ها ئاگاداری ئه&zwnj;و که&zwnj;سه&zwnj;یه که له تاریکی شه&zwnj;ودا خۆی ده&zwnj;شارێته&zwnj;وه&zwnj;، ئه&zwnj;وه&zwnj;ش که به&zwnj;ڕۆژدا ده&zwnj;ڕواو ئاشکراو دیاره&zwnj;.';
$t[11] = 'بۆ خوا هه&zwnj;یه فریشته&zwnj;ی بێ شومار که به نۆره به&zwnj;دوای یه&zwnj;کدا دێن تا چاودێری ئاده&zwnj;میزاد بکه&zwnj;ن له به&zwnj;رو پشت و له هه&zwnj;موو لایه&zwnj;که&zwnj;وه بیپارێزن به فه&zwnj;رمانی خوا، بێگومان خوا بارودۆخی هیچ قه&zwnj;وم و گه&zwnj;لێك ناگۆڕێت (له خۆشیه&zwnj;وه بۆ ناخۆشی، یان له زه&zwnj;لیلیه&zwnj;وه بۆ سه&zwnj;ربه&zwnj;رزی، یان له گرانیه&zwnj;وه بۆ هه&zwnj;رزانی)هه&zwnj;تا ئه&zwnj;وان ئه&zwnj;وه&zwnj;ی به خۆیان ده&zwnj;کرێت نه&zwnj;یگۆڕن و نه&zwnj;یکه&zwnj;ن، کاتێکیش خوا بیه&zwnj;وێت به&zwnj;ڵایه&zwnj;ك به&zwnj;سه&zwnj;ر هه&zwnj;ر میلله&zwnj;تێکدا بهێنێت (له ده&zwnj;رئه&zwnj;نجامی سه&zwnj;رکه&zwnj;شی و یاخی بوونیان)، ئه&zwnj;وه هیچ هێزێك ناتوانێت به&zwnj;ری بگرێت، هیچ که&zwnj;سیش ناتوانێت جگه له خوا فریایان بکه&zwnj;وێت و ڕزگاریان بکات.';
$t[12] = 'خوا ئه&zwnj;و زاته&zwnj;یه که هه&zwnj;وره بروسکه&zwnj;تان نیشان ده&zwnj;دات، هاوڕێ له&zwnj;گه&zwnj;ڵ ترسدا (به&zwnj;هۆی ڕووناکی به&zwnj;هێزو ده&zwnj;نگی سامناکی، که ده&zwnj;وریان هه&zwnj;یه له به&zwnj;پێز کردنی خاکی زه&zwnj;وی و ته&zwnj;قاندنه&zwnj;وه&zwnj;ی تۆوه&zwnj;کاندا) هه&zwnj;روه&zwnj;ها ئومێد و هیوا به&zwnj;خشیشه (به ساڵێکی پڕ به&zwnj;ره&zwnj;که&zwnj;ت)، هه&zwnj;ر ئه&zwnj;و زاته هه&zwnj;وری قورس و سه&zwnj;نگین و ئاودار به&zwnj;دی ده&zwnj;هێنێت.';
$t[13] = 'هه&zwnj;وره تریشقه (به&zwnj;ده&zwnj;نگی گه&zwnj;وره و سامناكی) ته&zwnj;سبیحات و سوپاس و ستایشی (به&zwnj;دیهێنه&zwnj;ری ده&zwnj;کات) و فریشته&zwnj;کانیش له ترسی ئه&zwnj;و زاته (سه&zwnj;ر گه&zwnj;رمی ته&zwnj;سبیحات و ستایش و سوپاسگوزارین)، هه&zwnj;روه&zwnj;ها (جاری وا هه&zwnj;یه&zwnj;) هه&zwnj;وره بروسکه ده&zwnj;نێرێت و ده&zwnj;یماڵێت به&zwnj;که&zwnj;سێکدا که خوا ویستی له&zwnj;سه&zwnj;ره و ڕۆژی ته&zwnj;واو بووه (جا له&zwnj;گه&zwnj;ڵ ئه&zwnj;م هه&zwnj;موو به&zwnj;ڵگه&zwnj;و نیشانه به&zwnj;هێزه&zwnj;دا) ئه&zwnj;وانه هه&zwnj;ر له ده&zwnj;مه&zwnj;ده&zwnj;مێ و مشتومڕدان ده&zwnj;رباره&zwnj;ی خوا (دوور له بنه&zwnj;مای زانستی) له&zwnj;کاتێکدا ئه&zwnj;و زاته ده&zwnj;سه&zwnj;ڵاتی زۆر به&zwnj;هیزه&zwnj;و تۆڵه&zwnj;سه&zwnj;ندنی (له خوانه&zwnj;ناسان) توندو تیژه&zwnj;.';
$t[14] = 'هه&zwnj;ر بۆ خوایه بانگه&zwnj;وازی حه&zwnj;ق و ڕاستی، (به&zwnj;ڵام) ئه&zwnj;وانه&zwnj;ی هاناو هاوار بۆ که&zwnj;سێکی تر ده&zwnj;به&zwnj;ن جگه له&zwnj;و زاته&zwnj;، هاوار لێکراوان به هیچ شێوه&zwnj;یه&zwnj;ك به&zwnj;هانایانه&zwnj;وه نایه&zwnj;ن و وه&zwnj;ڵامیان ناده&zwnj;نه&zwnj;وه&zwnj;، مه&zwnj;گه&zwnj;ر وه&zwnj;کو که&zwnj;سێك (تینووی بێت له دووره&zwnj;وه&zwnj;) ده&zwnj;سته&zwnj;کانی درێژه بکات بۆ ئاو تا بیگه&zwnj;یه&zwnj;نێته ده&zwnj;می، که&zwnj;چی بێسووده&zwnj;و ناگاته ده&zwnj;می، بێگومان دوعا نزای بێ باوه&zwnj;ڕان ته&zwnj;نها له&zwnj;ناو تاریکی و گومڕایدایه و هیچی تر نی یه&zwnj;.';
$t[15] = 'هه&zwnj;رچی له ئاسمانه&zwnj;کان و زه&zwnj;ویدا هه&zwnj;یه&zwnj;، له&zwnj;سه&zwnj;ر ڕه&zwnj;زامه&zwnj;ندیی خۆیان بێ یان به ناچاریی سوژده بۆ خوای به&zwnj;دیهێنه&zwnj;ر ده&zwnj;به&zwnj;ن هاوڕێ له&zwnj;گه&zwnj;ڵ سێبه&zwnj;ره&zwnj;کانیاندا له به&zwnj;ره&zwnj;به&zwnj;یان و ده&zwnj;مه&zwnj;و ئێواراندا.';
$t[16] = '(ئه&zwnj;ی محمد صلی الله علیه وسلم به خوانه&zwnj;ناسان) بڵێ: کێ په&zwnj;روه&zwnj;ردگاری ئاسمانه&zwnj;کان و زه&zwnj;ویه&zwnj;؟ (ئه&zwnj;گه&zwnj;ر ئه&zwnj;وان بێ ده&zwnj;نگ بوون) تۆ بڵێ: الله په&zwnj;روه&zwnj;ردگاریانه&zwnj;، (چونکه ئه&zwnj;وانه که&zwnj;ڕ کوێرو ڵاڵن له&zwnj;وانه&zwnj;یه دان به&zwnj;و ڕاستیه&zwnj;دا نه&zwnj;نێن) پاشان پێیان بڵێ: ئایا ڕه&zwnj;وایه جگه له&zwnj;و خوایه پشت به شتانی تر ببه&zwnj;ستن و بیانکه&zwnj;نه پشتیوان و خۆشه&zwnj;ویست، که ئه&zwnj;وانه ته&zwnj;نانه&zwnj;ت بۆ خۆشییان قازانج و زه&zwnj;ره&zwnj;ریان به&zwnj;ده&zwnj;ست نیه&zwnj;؟! هه&zwnj;روه&zwnj;ها بڵێ: باشه ئاخر کوێر و چاوساغ وه&zwnj;ك یه&zwnj;کن؟! تاریکی و ڕووناکی چوونیه&zwnj;کن؟! (دیاره خوانه&zwnj;ناسی و بێ دینی کوێری و تاریکیه&zwnj;، ئیمان و باوه&zwnj;ڕو خواناسیش چاو ساغی و ڕووناکیه&zwnj;)، به&zwnj;ڵکو چه&zwnj;ند هاوبه&zwnj;شێکیان داناوه بۆ خوا، که&zwnj;شتیان دروستکردووه وه&zwnj;ك دروستکراوه&zwnj;کانی خواو جا دروستکراوه&zwnj;که&zwnj;یان لێ شێواوه&zwnj;، پێیان بڵێ: الله به&zwnj;دیهێنه&zwnj;رو دروستکاری هه&zwnj;موو شتێکه و ئه&zwnj;وزاته تاکوته&zwnj;نهاو به&zwnj;ده&zwnj;سه&zwnj;ڵات و به&zwnj;هێزو به توانایه&zwnj;.';
$t[17] = 'هه&zwnj;ر ئه&zwnj;و زاته له ئاسمانه&zwnj;وه بارانی باراندووه&zwnj;، به&zwnj;ئه&zwnj;ندازه&zwnj;ی ئه&zwnj;و دۆڵ و نشێوه به&zwnj;هۆی ئه&zwnj;و بارانه پڕ ده&zwnj;بن (ئینجا لافاوێك دروست ده&zwnj;بێت)، ئه&zwnj;و لافاوه که&zwnj;ف و کوڵی به&zwnj;رز هه&zwnj;ڵگرتووه&zwnj;، (ئه&zwnj;مه نموونه&zwnj;ی یه&zwnj;که&zwnj;می حه&zwnj;ق و ناحه&zwnj;قه&zwnj;، بۆ نمونه&zwnj;ی دووه&zwnj;م ده&zwnj;فه&zwnj;رمووێت) هه&zwnj;روه&zwnj;ها له&zwnj;سه&zwnj;ر ڕووی ئه&zwnj;و کانزایانه&zwnj;ی که ئاگری تێبه&zwnj;رده&zwnj;ده&zwnj;ن و ده&zwnj;یتوێننه&zwnj;وه به&zwnj;ئاگر بۆ دروستکردنی خشڵ وه&zwnj;کو (ئاڵتوون و زیوو پلاتین) یاخود بۆ پێویستی تر (وه&zwnj;کو ئاسن و مس و قوڕقوشم) که&zwnj;ف و کوڵ دروست ده&zwnj;بێت وه&zwnj;ك که&zwnj;ف و کوڵی سه&zwnj;ر ئاوه&zwnj;که&zwnj;، ئا به&zwnj;و شێوه&zwnj;یه خوا نموونه ده&zwnj;هێنێته&zwnj;وه بۆ حه&zwnj;ق و ناحه&zwnj;ق، بۆ ڕاست و ناڕاست، جا ئه&zwnj;وه&zwnj;ی که&zwnj;ف و کوڵه وبێسوود و بێ که&zwnj;ڵکه ده&zwnj;ڕوات و ده&zwnj;پوکێته&zwnj;وه&zwnj;، به&zwnj;ڵام ئه&zwnj;وه&zwnj;ی که سوود به&zwnj;خش و به&zwnj;که&zwnj;ڵکه بۆ خه&zwnj;ڵکی له&zwnj;سه&zwnj;ر زه&zwnj;ویدا هه&zwnj;ر ئه&zwnj;وه ده&zwnj;مێنێته&zwnj;وه&zwnj;، ئا به&zwnj;و شێوه&zwnj;یه خوا نموونه جۆراوجۆره&zwnj;کان ده&zwnj;هێنێته&zwnj;وه&zwnj;، (دیاره که به&zwnj;تاڵ و ناحه&zwnj;قی که&zwnj;ف و کوڵن و ته&zwnj;مه&zwnj;ن کۆتان، حه&zwnj;ق و &lrm;ڕاستیش ئاوه پاکه&zwnj;که&zwnj;یه&zwnj;و و کانزا به&zwnj;سووده&zwnj;کانن ده&zwnj;مێننه&zwnj;وه له زه&zwnj;ویدا).';
$t[18] = '(هه&zwnj;ڵویستی ئه&zwnj;و خه&zwnj;ڵکه له به&zwnj;رامبه&zwnj;ر با&zwnj;نگه&zwnj;وازی په&zwnj;روه&zwnj;ردگاریانه&zwnj;وه دوو جۆره&zwnj;) بۆ ئه&zwnj;وانه&zwnj;ی که به&zwnj;ده&zwnj;م بانگه&zwnj;وازی په&zwnj;روه&zwnj;ردگاریانه&zwnj;وه چوون، هه&zwnj;موو خۆشی و شادی و کامه&zwnj;رانی و ئاسووده&zwnj;ییه&zwnj;کان بۆ ئاماده&zwnj;یه&zwnj;، ئه&zwnj;وانه&zwnj;ش که به&zwnj;ده&zwnj;م بانگه&zwnj;وازه&zwnj;که&zwnj;یه&zwnj;وه نه&zwnj;چوون، ئه&zwnj;گه&zwnj;ر به&zwnj;ڕاستی خاوه&zwnj;نی هه&zwnj;ر هه&zwnj;موو شتێك بن له زه&zwnj;ویداو ئه&zwnj;وه&zwnj;نده&zwnj;ی تریشی له&zwnj;گه&zwnj;ڵدا بێت، ئه&zwnj;وه ده&zwnj;یانکرده قوربانی خۆیان، له پێناوی ڕزگاری خۆیاندا ده&zwnj;یانبه&zwnj;خشی، (چونکه&zwnj;) ئا ئه&zwnj;و جۆره که&zwnj;سانه به خراپترین شێوه حسابیان له&zwnj;گه&zwnj;ڵدا ده&zwnj;کرێت، سه&zwnj;رئه&zwnj;نجامیش، جێگه&zwnj;یان دۆزه&zwnj;خه&zwnj;، ئای که شوێنێکی خراپه&zwnj;.';
$t[19] = 'جا ئایا ئه&zwnj;و که&zwnj;سه&zwnj;ی که دڵنیایه و ده&zwnj;زانێت بێگومان ئه&zwnj;م قورئانه&zwnj;ی که له&zwnj;لایه&zwnj;ن په&zwnj;روه&zwnj;ردگارته&zwnj;وه بۆت دابه&zwnj;زێنراوه حه&zwnj;ق و ڕاسته&zwnj;قینه&zwnj;یه&zwnj;، وه&zwnj;کو ئه&zwnj;و که&zwnj;سه وایه که کوێره له ئاستیدا؟، به&zwnj;ڕاستی هه&zwnj;ر خاوه&zwnj;ن بیرو هۆشه&zwnj;کان له&zwnj;ڕاستی تێده&zwnj;گه&zwnj;ن و تێده&zwnj;فکرن.....';
$t[20] = 'ئه&zwnj;و که&zwnj;سانه&zwnj;ن وه&zwnj;فا ده&zwnj;که&zwnj;ن و به&zwnj;ئه&zwnj;مه&zwnj;کن له به&zwnj;رامبه&zwnj;ر ئه&zwnj;و په&zwnj;یمانه&zwnj;وه که هه&zwnj;یانه له&zwnj;گه&zwnj;ڵ خوای په&zwnj;روه&zwnj;ردگارداو، ئه&zwnj;و په&zwnj;یمانه&zwnj;ش ناشکێن و به&zwnj;ڵێن هه&zwnj;ڵناوه&zwnj;شێننه&zwnj;وه&zwnj;....';
$t[21] = 'ئه&zwnj;وانه&zwnj;شن که هه&zwnj;رچی خوا فه&zwnj;رمانی به گه&zwnj;یاندنی داوه ده&zwnj;یگه&zwnj;یه&zwnj;نن و به&zwnj;دڵ له په&zwnj;روه&zwnj;ردگاریان ده&zwnj;ترسن (نه&zwnj;وه&zwnj;کو لێیان بره&zwnj;نجێت) و له لێپرسینه&zwnj;وه&zwnj;و حسابی توند و تیژی قیامه&zwnj;تیش سڵ ده&zwnj;که&zwnj;نه&zwnj;وه&zwnj;.';
$t[22] = 'ئه&zwnj;وانه&zwnj;شن که دانیان به&zwnj;خۆدا گرت و به ئارام بوون بۆ به&zwnj;ده&zwnj;ستهێنانی ڕه&zwnj;زامه&zwnj;ندیی په&zwnj;روه&zwnj;ردگاریان و نوێژه&zwnj;که&zwnj;شیان به چاکی ئه&zwnj;نجام داوه&zwnj;و له&zwnj;و ڕزق و ڕۆزیه&zwnj;ش که پێمان به&zwnj;خشیوون به&zwnj;خشیویانه&zwnj;، به&zwnj;نهێنی و به ئاشکراو به&zwnj;چاکه&zwnj;کاری و شێوازی جوان پاڵ به نادروستی و هه&zwnj;ڵه و خراپکارایه&zwnj;وه ده&zwnj;نێن، ئا ئه&zwnj;وانه سه&zwnj;رئه&zwnj;نجامه&zwnj;که&zwnj;یان ئه&zwnj;و خانووبه&zwnj;ره (ڕازاوه&zwnj;یه&zwnj;یه له به&zwnj;هه&zwnj;شتدا)....';
$t[23] = 'که باخه&zwnj;کانی به&zwnj;هه&zwnj;شتی_عه&zwnj;دنه&zwnj;_ بۆیان ڕازاوه&zwnj;ته&zwnj;وه&zwnj;، به شادیه&zwnj;وه ده&zwnj;چنه ناوی، هاوڕێ له&zwnj;گه&zwnj;ڵ هه&zwnj;ر که&zwnj;سێك چاك و پاك و خواناسه له باووباپیران و هاوسه&zwnj;رانیان و نه&zwnj;وه&zwnj;کانیان، جا فریشته&zwnj;کان ده&zwnj;چن بۆ سه&zwnj;ردانیان له هه&zwnj;موو ده&zwnj;روازه&zwnj;کانه&zwnj;وه&zwnj;....';
$t[24] = 'ده&zwnj;ڵێن: سڵاوتان لێ بێت به هۆی خۆگرتنتانه&zwnj;وه (له به&zwnj;رامبه&zwnj;ر کێشه&zwnj;کانی ژیان، له&zwnj;به&zwnj;رامبه&zwnj;ر گاڵته&zwnj;جاڕیی خوانه&zwnj;ناسان...) که&zwnj;وابوو ئای چه&zwnj;نده جێگه و ڕێگه&zwnj;یه&zwnj;کی خۆشتان بۆ خۆتان مسۆگه&zwnj;ر کرد.';
$t[25] = '(به&zwnj;ڵام) ئه&zwnj;وانه&zwnj;ی که په&zwnj;یمانی خوا ده&zwnj;شکێنن دوای ئه&zwnj;وه&zwnj;ی که پێشتر توندو تۆڵ بوو (له سه&zwnj;روو هه&zwnj;موویانه&zwnj;وه په&zwnj;یمانی فیتره&zwnj;ت و به&zwnj;ندایه&zwnj;تی)، هه&zwnj;رچی په&zwnj;یوه&zwnj;ندیه&zwnj;کیش خوا فه&zwnj;رمانیدا به&zwnj;گه&zwnj;یاندنی ئه&zwnj;وان ده&zwnj;یبڕن و ده&zwnj;یپچڕێنن، هه&zwnj;روه&zwnj;ها تۆوی فه&zwnj;سادو گوناهو تاوان ده&zwnj;چێنن له زه&zwnj;ویدا، ئا ئه&zwnj;وانه نه&zwnj;فره&zwnj;ت و خه&zwnj;شمی خوایان له&zwnj;سه&zwnj;ره&zwnj;و هه&zwnj;روه&zwnj;ها جیگه و ڕێگه&zwnj;ی ناخۆش و نا&zwnj;له&zwnj;بار (له&zwnj;دۆزه&zwnj;خدا) بۆ ئه&zwnj;وان هه&zwnj;یه&zwnj;.';
$t[26] = 'هه&zwnj;ر خوایه&zwnj;، ڕزق و ڕۆزی فراوان ده&zwnj;به&zwnj;خشێت به هه&zwnj;ر که&zwnj;س که بیه&zwnj;وێت و ته&zwnj;نگ و که&zwnj;می ده&zwnj;کاته&zwnj;وه (به&zwnj;شێوه&zwnj;یه&zwnj;ك که حیکمه&zwnj;ت و دانای تێدا به&zwnj;دی ده&zwnj;کرێت) که&zwnj;چی زۆربه&zwnj;ی خه&zwnj;ڵکی دڵخۆشن به&zwnj;ژیانی دنیا، له&zwnj;کاتێکدا که ژیانی دنیا له به&zwnj;رامبه&zwnj;ر ژیانی قیامه&zwnj;ته&zwnj;وه (له&zwnj;به&zwnj;هه&zwnj;شتی به&zwnj;ریندا) ته&zwnj;نها ڕابواردنێکی که&zwnj;م نه&zwnj;بێت هیچی تر نی یه&zwnj;.';
$t[27] = 'خوانه&zwnj;ناسان ده&zwnj;ڵێن: ئه&zwnj;وه بۆچی موعجیزه&zwnj;یه&zwnj;ك بۆ محمد صلی الله علیه وسلم له&zwnj;لایه&zwnj;ن په&zwnj;روه&zwnj;ردگاریه&zwnj;وه دانه&zwnj;به&zwnj;زێنرایه سه&zwnj;ری، (به&zwnj;مه&zwnj;رجێك_موعجیزه&zwnj;_ هیچ کات نه&zwnj;بووه&zwnj;ته هۆی ئیمان هێنانی خه&zwnj;ڵکی، به&zwnj;ڵکو بیانویه&zwnj;که و هه&zwnj;میشه خوانه&zwnj;ناسان وڕکیان لێ گرتووه&zwnj;) تۆش پێیان بڵێ: به&zwnj;ڕاستی خوا، هه&zwnj;رکه&zwnj;سێك که&zwnj;بیه&zwnj;وێت گومڕاو سه&zwnj;رلێشێواوی ده&zwnj;کات، له&zwnj;هه&zwnj;مان کاتدا هیدایه&zwnj;تی ئه&zwnj;و که&zwnj;سانه ده&zwnj;دات که ڕوو له په&zwnj;یامه&zwnj;که&zwnj;ی ده&zwnj;که&zwnj;ن و به&zwnj;ندایه&zwnj;تی ده&zwnj;که&zwnj;ن و گه&zwnj;ڕاونه&zwnj;ته&zwnj;وه بۆ لای...';
$t[28] = 'ئه&zwnj;وانه&zwnj;ی باوه&zwnj;ڕیان هیناوه&zwnj;و دڵ و ده&zwnj;روونیان ئارام ده&zwnj;بێت به قورئان و په&zwnj;یامه&zwnj;که&zwnj;ی خوای په&zwnj;روه&zwnj;ردگار، ئاگاداربن دڵه&zwnj;کان: هه&zwnj;ر به قورئان و یادی خواو (پاپه&zwnj;ندبوون به ئاینه&zwnj;که&zwnj;یه&zwnj;وه&zwnj;) خۆشنوود ده&zwnj;بن و ده&zwnj;حه&zwnj;وێنه&zwnj;وه&zwnj;.';
$t[29] = 'ئه&zwnj;و که&zwnj;سانه&zwnj;ی که باوه&zwnj;ڕیان هیناوه و کارو کرده&zwnj;وه چاکه&zwnj;کانیان ئه&zwnj;نجامداوه&zwnj;، به&zwnj;هه&zwnj;شت و نازو نیعمه&zwnj;تی جوان و ڕازاوه و جێگه و ڕێگه&zwnj;ی خۆش بۆیان ئاماده&zwnj;یه&zwnj;.';
$t[30] = 'ئا به&zwnj;و شێوه&zwnj;یه تۆمان ڕه&zwnj;وانه کردووه بۆ سه&zwnj;ر ئو&zwnj;ممه&zwnj;تێك که پێش ئه&zwnj;ما&zwnj;ن میلله&zwnj;تانی تریش هه&zwnj;بوون، تا ئه&zwnj;م قورئانه&zwnj;ی که به&zwnj;وه&zwnj;حی بۆمان ڕه&zwnj;وانه کردویت به&zwnj;سه&zwnj;ریاندا بخوێنیته&zwnj;وه&zwnj;، که&zwnj;چی زۆربه&zwnj;یان باوه&zwnj;ڕ به&zwnj;خوای میهره&zwnj;بان ناهێنن، ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر صلی الله علیه وسلم تۆ بڵێ: ئه&zwnj;و زاته په&zwnj;روه&zwnj;ردگاری منه و بێجگه له&zwnj;و خوایه&zwnj;کی تر نیه&zwnj;، من هه&zwnj;ر پشتم به&zwnj;و زاته به&zwnj;ستووه&zwnj;، گه&zwnj;ڕانه&zwnj;وه&zwnj;شم هه&zwnj;ر بۆ لای ئه&zwnj;وه&zwnj;.';
$t[31] = 'خۆ ئه&zwnj;گه&zwnj;ر به&zwnj;ڕاستی له جیاتی ئه&zwnj;م قورئانه&zwnj;، خوا قورئانێکی ڕه&zwnj;وانه بکردایه که کێوه&zwnj;کان پێی بڕۆشتنایه&zwnj;، یان زه&zwnj;وی پێ هه&zwnj;ڵکه&zwnj;نرایه و له&zwnj;ت بکرایه&zwnj;، یان مردووه&zwnj;کانی پێ بهاتایه قسه&zwnj;و گفتوگۆی له&zwnj;گه&zwnj;ڵدا بکرایه&zwnj;، خوانه&zwnj;ناسان هه&zwnj;ر بڕوایان نه&zwnj;ده&zwnj;هێنا، به&zwnj;ڵام دڵنیابن که هه&zwnj;موو کارێك و هه&zwnj;موو شتێك به&zwnj;ده&zwnj;ست خوایه&zwnj;، باشه&zwnj;، ئایا ئه&zwnj;وانه&zwnj;ی ئیمانیان هێناوه نائومێد نه&zwnj;بوون (له بێ دین و خوانه&zwnj;ناسان، خۆ هیدایه&zwnj;ت به زۆر نیه&zwnj;؟) جا ئه&zwnj;گه&zwnj;ر خوا بیوستایه هه&zwnj;موو خه&zwnj;ڵکی ناچار ده&zwnj;کرد که هیدایه&zwnj;ت وه&zwnj;ربگرن (خوانه&zwnj;ناسان وه&zwnj;نه&zwnj;بێت ژیانی دنیا به&zwnj;خۆشی به&zwnj;رنه سه&zwnj;ر، به&zwnj;ڵکو) به&zwnj;رده&zwnj;وام ئه&zwnj;وانه&zwnj;ی که بێ باوه&zwnj;ڕ بوون به&zwnj;ڵای سه&zwnj;خت و ناخۆشیان تووش ده&zwnj;بێت به&zwnj;هۆی ئه&zwnj;و کارو پیشه&zwnj;یه&zwnj;وه که ئه&zwnj;نجامیان داوه&zwnj;، یاخود ئه&zwnj;و به&zwnj;ڵا سه&zwnj;ختانه له نزیك ما&zwnj;ڵه&zwnj;کانیانه&zwnj;وه داده&zwnj;به&zwnj;زێت و سه&zwnj;رهه&zwnj;ڵده&zwnj;دات، هه&zwnj;تا به&zwnj;ڵێنی خوا دێته دی، به&zwnj;ڕاستی خوا په&zwnj;یمانی ناشکێنێت و ده&zwnj;یهێنێته دی.';
$t[32] = '(ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر صلی الله علیه وسلم ئه&zwnj;گه&zwnj;ر خوا نه&zwnj;ناسان گاڵته ده&zwnj;که&zwnj;ن به تۆ یان به ئایینه&zwnj;که&zwnj;ی تۆ، خه&zwnj;فه&zwnj;ت مه&zwnj;خۆ چونکه&zwnj;) سوێند به&zwnj;خوا به&zwnj;ڕاستی گاڵته&zwnj;کراوه به پێغه&zwnj;مبه&zwnj;رانی پێش تۆش، جا من ماوه&zwnj;یه&zwnj;ك مۆڵه&zwnj;تمدا به&zwnj;وانه&zwnj;ی که بێ باوه&zwnj;ڕ بوون پاشان خه&zwnj;شم و قینی خۆمم لێگرتن، جا چۆن تۆڵه&zwnj;یه&zwnj;ك بوو تۆڵه&zwnj;که&zwnj;م؟ (له مێژوو بپرسه و سه&zwnj;رنجی ئاسه&zwnj;واریان بده&zwnj;)!';
$t[33] = 'جا ئایا ئه&zwnj;و زاته&zwnj;ی که سه&zwnj;رپه&zwnj;رشتیاری کارو کرده&zwnj;وه&zwnj;ی هه&zwnj;موو که&zwnj;سێك و ده&zwnj;زانێت چیان کردووه (وه&zwnj;کو په&zwnj;رستراو و بته&zwnj;کانی تر وایه که خه&zwnj;ڵکی هاناو هاواری بۆ ده&zwnj;به&zwnj;ن؟)، له&zwnj;گه&zwnj;ڵ گه&zwnj;ورویی و باڵاده&zwnj;ستی ئه&zwnj;و په&zwnj;روه&zwnj;ردگاره&zwnj;دا نه&zwnj;فامان شه&zwnj;ریکیان بۆ خوا بڕیارداوه&zwnj;، تۆش بڵێ: ئاده&zwnj;ی ئاوی ئه&zwnj;و بت و هاوه&zwnj;ڵانه&zwnj;م بۆ بنێن که له&zwnj;جیاتی خوا ده&zwnj;یانپه&zwnj;رستن؟ یاخود (به&zwnj;هه&zwnj;موو عه&zwnj;قلێکیانه&zwnj;وه&zwnj;) ده&zwnj;تانه&zwnj;وێت خوا ئاگادار بکه&zwnj;نه&zwnj;وه به&zwnj;شتێك که ئه&zwnj;و زاته نایزانێت له زه&zwnj;ویدا؟!!، یان هه&zwnj;ر ئه&zwnj;تانه&zwnj;وێت ئاگاداری بکه&zwnj;نه&zwnj;وه به&zwnj;قسه&zwnj;ی سه&zwnj;رزاره&zwnj;کیتان، نه&zwnj;خێر ڕاستی هه&zwnj;ر ئه&zwnj;وه&zwnj;یه که فێڵ و ته&zwnj;ڵه&zwnj;که و پیلانی ئه&zwnj;وانه&zwnj;ی که بێ باوه&zwnj;ڕ بوون بۆیان ڕازێنراوه&zwnj;ته&zwnj;وه و له ڕێبازی ڕاست وێڵ کراون، جا هه&zwnj;ر که&zwnj;س به&zwnj;هۆی کارو کرده&zwnj;وه&zwnj;ی خراپیه&zwnj;وه ڕێبازی خوا ون بکات، ئیتر که&zwnj;س نیه هیدایه&zwnj;تی بدات.';
$t[34] = 'ئه&zwnj;وانه هه&zwnj;ر له دنیادا تووشی سزایه&zwnj;کیان ده&zwnj;که&zwnj;ین، بێگومان سزای قیامه&zwnj;تیشیان سه&zwnj;ختتره و هیچ شتێك نیه له سزای خوایی ڕزگاریان بکات و په&zwnj;نایان بدات.';
$t[35] = 'وێنه&zwnj;ی نمونه&zwnj;یی ئه&zwnj;و به&zwnj;هه&zwnj;شته&zwnj;ی به&zwnj;ڵێن دراوه به پارێزگاران (که جوان و ڕازاوه&zwnj;یه&zwnj;) چه&zwnj;نده&zwnj;ها ڕووبار ڕه&zwnj;وان و جاریه (به&zwnj;ژێر دره&zwnj;خته&zwnj;کان و به به&zwnj;رده&zwnj;م کۆشکه&zwnj;کانیدا)، خواردن و خواردنه&zwnj;وه&zwnj;ی (هه&zwnj;مه&zwnj;جۆر و هه&zwnj;مه&zwnj;ڕه&zwnj;نگی) به&zwnj;رده&zwnj;وامه هاوڕێ له&zwnj;گه&zwnj;ڵ سێبه&zwnj;ری خۆش و (هه&zwnj;وای سازگارو شنه&zwnj;ی شه&zwnj;ماڵدا) ئه&zwnj;وه&zwnj;یه سه&zwnj;ر ئه&zwnj;نجامی ئه&zwnj;وانه&zwnj;ی له&zwnj;توڕه&zwnj;یی خوا خۆیان پاڕاست، سه&zwnj;ر ئه&zwnj;نجامی بێ باوه&zwnj;ڕو خوانه&zwnj;ناسانیش ئاگری دۆزه&zwnj;خه&zwnj;.';
$t[36] = 'ده&zwnj;سته&zwnj;یه&zwnj;ك له&zwnj;وانه&zwnj;ی که کتێبی ئاسمانیان پێ به&zwnj;خشیوون (شوینکه&zwnj;وته&zwnj;ی حه&zwnj;ق و ڕاستین) دڵخۆش و شادمان ده&zwnj;بن به&zwnj;و قورئانه&zwnj;ی که بۆ تۆ دابه&zwnj;زێنراوه&zwnj;، هه&zwnj;ندێ گرۆ ده&zwnj;سته&zwnj;ی تریش هه&zwnj;یه ئینکاری هه&zwnj;ندێکی ده&zwnj;کات.. پێیان بڵێ: به&zwnj;ڕاستی من فه&zwnj;رمانی ئه&zwnj;وه&zwnj;م پێ دراوه که ته&zwnj;نها خوا بپه&zwnj;رستم و هیچ شه&zwnj;ریك و هاوه&zwnj;ڵیشی بۆ بڕیار نه&zwnj;ده&zwnj;م، هه&zwnj;ر بۆ لای ئه&zwnj;و خه&zwnj;ڵك بانگ بکه&zwnj;م، گه&zwnj;ڕانه&zwnj;وه&zwnj;ش هه&zwnj;ر بۆ لای ئه&zwnj;وه&zwnj;.';
$t[37] = 'هه&zwnj;ر به&zwnj;و شێوه&zwnj;یه (پێشتر کتێبی ئاسمانیمان به زمانی جۆراو جۆر ڕه&zwnj;وانه کردووه&zwnj;) قورئانمان وه&zwnj;کو داوه&zwnj;رێك به&zwnj;عه&zwnj;ره&zwnj;بیه&zwnj;کی پاراو دابه&zwnj;زاندووه&zwnj;، سوێند به&zwnj;خوا ئه&zwnj;گه&zwnj;ر شوێنی ئاره&zwnj;زووه&zwnj;کانی ئه&zwnj;وان بکه&zwnj;ویت، دوای ئه&zwnj;وه&zwnj;ی زانستی و زانیاری ته&zwnj;واوت (له&zwnj;لایه&zwnj;ن خواوه&zwnj;) بۆ هات، ئه&zwnj;وه هیچ پشتیوان و پارێزه&zwnj;رێکت نی یه له سزای خوا بتپارێزێت.';
$t[38] = 'سوێند به&zwnj;خوا بێگومان پێش تۆ پێغه&zwnj;مبه&zwnj;رانی ترمان ڕه&zwnj;وانه کردووه و هاوسه&zwnj;رو نه&zwnj;وه&zwnj;شمان پێداون، هیچ پێغه&zwnj;مبه&zwnj;رێکیش بۆی نه&zwnj;بووه به&zwnj;ویستی خۆی موعجیزه نیشان بدات ئه&zwnj;گه&zwnj;ر ویستی خوای له&zwnj;سه&zwnj;ر نه&zwnj;بووبێت، بۆ هه&zwnj;موو کارو ڕووداوێکیش بڕیارێکی تایبه&zwnj;ت هه&zwnj;یه&zwnj;.';
$t[39] = 'خوا ویستی نه&zwnj;مانی له&zwnj;سه&zwnj;ر هه&zwnj;رچییه&zwnj;ك بێت، له&zwnj;ناوی ده&zwnj;بات و ده&zwnj;یکوژێنێته&zwnj;وه&zwnj;، یان ده&zwnj;یهێڵێته&zwnj;وه و پایه&zwnj;داری ده&zwnj;کات، سه&zwnj;رچاوه&zwnj;ی تۆمارکراوی هه&zwnj;موو ڕووداوه&zwnj;کان لای ئه&zwnj;و زاته&zwnj;یه&zwnj;، (یاخود خوا ئایینه پێشووه&zwnj;کان له مه&zwnj;یداندا ناهێڵێت و دواهه&zwnj;مین ئاین و به&zwnj;رنامه&zwnj;ی که ئایینی ئیسلامه ده&zwnj;یچه&zwnj;سپێنێت و پایه&zwnj;داری ده&zwnj;کات).';
$t[40] = 'جا ئه&zwnj;گه&zwnj;ر هه&zwnj;ندێ له هه&zwnj;ڕه&zwnj;شه&zwnj;کانی خۆمانیان بۆ پێش بهێنین و نیشانت بده&zwnj;ین، یاخود بتمرێنین و سه&zwnj;رئه&zwnj;نجامی ئه&zwnj;وان نه&zwnj;بینیت، خه&zwnj;فه&zwnj;ت مه&zwnj;خۆ چونکه به&zwnj;ڕاستی تۆ ته&zwnj;نها گه&zwnj;یاندنی په&zwnj;یامه&zwnj;که&zwnj;ت له&zwnj;سه&zwnj;ره&zwnj;، لێپرسینه&zwnj;وه&zwnj;ش له&zwnj;سه&zwnj;ر ئێمه&zwnj;یه&zwnj;.';
$t[41] = 'ئایا خوا نه&zwnj;ناسان نه&zwnj;یانبینیوه&zwnj;، ئێمه چۆن ئه&zwnj;م لاو ئه&zwnj;و لای زه&zwnj;وی که&zwnj;م ده&zwnj;که&zwnj;ینه&zwnj;وه (ده&zwnj;سه&zwnj;ڵاتی خوانه&zwnj;ناسان ناهێڵین له ڕۆژهه&zwnj;ڵات و ڕۆژئاوای زه&zwnj;ویدا، سه&zwnj;رئه&zwnj;نجام به&zwnj;رنامه و ئوممه&zwnj;تی میانه&zwnj;ڕه و سه&zwnj;ر ده&zwnj;خه&zwnj;ین) خوا خۆی داوه&zwnj;&zwnj;ریی ده&zwnj;کات و (گۆڕانکاریه&zwnj;کان تاوتۆی ده&zwnj;کات)، که&zwnj;س ناتوانێت موحاسه&zwnj;به&zwnj;ی بکات و کۆسپ و ته&zwnj;گه&zwnj;ره بهێنێته ڕێی وئه&zwnj;وزاته زۆر به&zwnj;خێراییهه&zwnj;موو شتێك تاوتۆی ده&zwnj;کات و له هه&zwnj;مووان ده&zwnj;پرسێته&zwnj;وه&zwnj;.';
$t[42] = 'بێگومان خوا نه&zwnj;ناسان پێش ئه&zwnj;مانیش پیلانیان گێڕا، به&zwnj;مه&zwnj;رجێك پیلانه&zwnj;کانیان هه&zwnj;ر هه&zwnj;مووی لای خوا ڕوون و ئاشکرایه&zwnj;، ده&zwnj;زانێت هه&zwnj;ر که&zwnj;س چی ده&zwnj;کات و چی ده&zwnj;ستپێشخه&zwnj;ری ده&zwnj;کات، بێ باوه&zwnj;ڕ خوانه&zwnj;ناسانیش له&zwnj;ئاینده&zwnj;دا ده&zwnj;زانن که سه&zwnj;رئه&zwnj;نجامی ئه&zwnj;م جیهانه و ئه&zwnj;و جیهانیش بۆ کێ ده&zwnj;بێت.';
$t[43] = 'ئه&zwnj;وانه&zwnj;ش که بێ باوه&zwnj;ڕ بوون ده&zwnj;ڵێن: تۆ پێغه&zwnj;مبه&zwnj;رو ڕه&zwnj;وانه کراوی خوا نیت، له وه&zwnj;ڵامیاندا بڵێ: هه&zwnj;ر خوا به&zwnj;سه که شایه&zwnj;ت بێت له نێوان منیش و ئێوه&zwnj;شدا، هه&zwnj;روه&zwnj;ها ئه&zwnj;و که&zwnj;سه&zwnj;ش که شاره&zwnj;زای قورئان و کتێبه ئاسمانیه&zwnj;کانه&zwnj;.';
?>