<?php
$t = array();
$t[1] = 'سه&zwnj;رنجی سه&zwnj;ره&zwnj;تای سوره&zwnj;تی (البقرة) بده&zwnj;، ئه&zwnj;م قورئانه کتێبێکه دامانبه&zwnj;زاندووه بۆت ئه&zwnj;ی محمد صلی الله علیه وسلم تا خه&zwnj;ڵکی له تاریکیه&zwnj;کانی (بێ باوه&zwnj;ڕی و نه&zwnj;زانی و سه&zwnj;رلێشێواوی) ده&zwnj;ربهێنێت، به&zwnj;ره&zwnj;و نوورو ڕێبازی ڕۆشنایی ئیمان، به&zwnj;ویستی په&zwnj;روه&zwnj;ردگاریان، تا ده&zwnj;گه&zwnj;نه ڕێبازی ڕاستی خوای باڵاده&zwnj;ست و شایسته&zwnj;ی سوپاسگوزاری.';
$t[2] = 'ئه&zwnj;و خوایه&zwnj;ی که هه&zwnj;رچی له ئاسمانه&zwnj;کان و هه&zwnj;رچی له زه&zwnj;ویدا هه&zwnj;یه هه&zwnj;ر ئه&zwnj;و خاوه&zwnj;نیانه&zwnj;، هاوار و ئاهو نا&zwnj;ڵه بۆ ئه&zwnj;و که&zwnj;سانه&zwnj;ی که بێ باوه&zwnj;ڕن له سزایه&zwnj;کی زۆر توندو تیژ....';
$t[3] = 'ئه&zwnj;وانه&zwnj;ی که ژیانی دنیایان له به نرخترو خۆشه&zwnj;ویستره له ژیانی جیهانی دوایی، هه&zwnj;میشه به&zwnj;ربه&zwnj;ست ده&zwnj;خه&zwnj;نه به&zwnj;ر ڕێبازی خواو ده&zwnj;یانه&zwnj;وێت له ڕێڕه&zwnj;وی خۆی ده&zwnj;ربچێت و بترازێت، (یاخود په&zwnj;سه&zwnj;نده لایان، به&zwnj;ڵام به&zwnj;مه&zwnj;رجێك له خزمه&zwnj;ت پلان و نه&zwnj;خشه&zwnj;ی ئه&zwnj;واندا بێت) ئا ئه&zwnj;وانه له گومڕاییه&zwnj;کی قووڵ و بێ سه&zwnj;رو بندا سه&zwnj;رگه&zwnj;ردانن و ده&zwnj;مێننه&zwnj;وه&zwnj;.';
$t[4] = 'ئێمه هیچ فرستادیه&zwnj;کمان ڕه&zwnj;وانه نه&zwnj;کردووه ته&zwnj;نها به&zwnj;زمانی قه&zwnj;ومه&zwnj;که&zwnj;ی نه&zwnj;بێت، (یه&zwnj;کێك بووه له خۆیان و به زمانی ئه&zwnj;وان دواوه&zwnj;) تا هه&zwnj;موو شتێکیان بۆ ڕوون بکاته&zwnj;وه&zwnj;، ئینجا خوای گه&zwnj;وره ئه&zwnj;و که&zwnj;سه&zwnj;ی گومڕا کردووه که وستوویه&zwnj;تی (چونکه گوێی بۆ با&zwnj;نگه&zwnj;وازی حه&zwnj;ق نه&zwnj;گرتووه&zwnj;)، ئه&zwnj;وه&zwnj;ش که وستویه&zwnj;تی هیدایه&zwnj;تی داوه (په&zwnj;روه&zwnj;ردگار پاڵپشتی ژیرو هۆشمه&zwnj;ندانه&zwnj;)، ئه&zwnj;و زاته&zwnj;ش باڵا ده&zwnj;سته (ویستی ئه&zwnj;و له سه&zwnj;روو هه&zwnj;موو ویسته&zwnj;کانه&zwnj;وه&zwnj;یه&zwnj;)، دانایه و (ده&zwnj;زانێت کێ شایسته&zwnj;ی ئه&zwnj;و ڕه&zwnj;حمه&zwnj;ته به&zwnj;نرخه&zwnj;یه&zwnj;).';
$t[5] = 'سوێند به&zwnj;خوا به&zwnj;ڕاستی ئێمه موسامان ڕه&zwnj;وانه کرد، هاوڕێ له&zwnj;گه&zwnj;ڵ موعجیزه&zwnj;ی زۆردا (فه&zwnj;رمانما پێدا) که قه&zwnj;وم و عه&zwnj;شیرتت له تاریکیه&zwnj;کان ڕزگار بکه&zwnj;و به&zwnj;ره&zwnj;و نوورو ڕووناکی بیانبه&zwnj;، ڕۆژه&zwnj;کانی خوایان بهێنه&zwnj;ره&zwnj;وه یاد (ئه&zwnj;و ڕۆژانه&zwnj;ی که تۆڵه&zwnj;ی له بێ باوه&zwnj;ڕان سه&zwnj;ند و ئیماندارانی سه&zwnj;رسه&zwnj;خت)، بێگومان ئا له&zwnj;و به&zwnj;سه&zwnj;رهاتانه&zwnj;دا به&zwnj;ڵگه و نیشانه&zwnj;ی زۆر هه&zwnj;ن، بۆ هه&zwnj;موو ئه&zwnj;و که&zwnj;سانه&zwnj;ی که زۆر خۆگرن (له&zwnj;کاتی ته&zwnj;نگانه&zwnj;دا)، زۆر سوپاسگوزاریشن (له&zwnj;کاتی خۆشیدا).';
$t[6] = '(یادیان بهێنه&zwnj;ره&zwnj;وه&zwnj;) کاتێك موسا به قه&zwnj;ومه&zwnj;که&zwnj;ی وت: یادی نازو نیعمه&zwnj;ته&zwnj;کانی خوا بکه&zwnj;نه&zwnj;وه له&zwnj;سه&zwnj;رتان، کاتێك له دارو ده&zwnj;سته&zwnj;ی فیرعه&zwnj;ون ڕزگاری کردن که به&zwnj;ناخۆشترین شێوه سزای ده&zwnj;دات، کوڕانی ئێوه&zwnj;یان سه&zwnj;ر ده&zwnj;بڕی و ئافره&zwnj;تانی ئێوه&zwnj;یان ده&zwnj;هێشته&zwnj;وه (بۆ کاره&zwnj;که&zwnj;ری)، بێگومان ئا له&zwnj;و پێشهاتانه&zwnj;دا به&zwnj;ڵاو تاقی کردنه&zwnj;وه&zwnj;یه&zwnj;کی گه&zwnj;وره له&zwnj;لایه&zwnj;ن په&zwnj;روه&zwnj;ردگارتانه&zwnj;وه بۆتان ساز درابوو.';
$t[7] = 'له&zwnj;یادتان بێت که&zwnj;: په&zwnj;روه&zwnj;ردگارتان بڕیاری داوه&zwnj;، سوێند به&zwnj;خوا ئه&zwnj;گه&zwnj;ر سوپاسگوزارو شوکرانه بژێر بن، ڕزق و ڕۆزی و نازو نیعمه&zwnj;تتان به&zwnj;سه&zwnj;ردا ده&zwnj;ڕێژێنم و زیادی ده&zwnj;که&zwnj;م، به&zwnj;ڵام: ئه&zwnj;گه&zwnj;ر ناشوکرو سوپاس ناپه&zwnj;زێڕ بن، به&zwnj;ڕاستی ئه&zwnj;و کاته ئیتر تۆڵه و سزای من زۆر به ئازار ده&zwnj;بێت.';
$t[8] = 'موسا وتی: خه&zwnj;ڵکینه ئه&zwnj;گه&zwnj;ر ئێوه و هه&zwnj;موو دانیشتوانی سه&zwnj;ر زه&zwnj;وی به&zwnj;گشتی خوانه&zwnj;ناس و بێ باوه&zwnj;ڕ بن، ئه&zwnj;وه بێگومان هه&zwnj;ر خۆتان زه&zwnj;ره&zwnj;ر ده&zwnj;که&zwnj;ن، چونکه خوا هه&zwnj;ر خۆی بێ نیازه و شایسته&zwnj;ی سوپاسه&zwnj;.';
$t[9] = 'باشه&zwnj;، ئایا هه&zwnj;واڵی قه&zwnj;ومه پێشووه&zwnj;کانتان پێ نه&zwnj;گه&zwnj;شتووه&zwnj;، قه&zwnj;ومی (نوح و عاد و ثمود)، ئه&zwnj;وانه&zwnj;ش که له دوای ئه&zwnj;وان هاتوون، ته&zwnj;نها خوا نه&zwnj;بێت که&zwnj;س نازانێت چه&zwnj;نده بوون و چیان به&zwnj;سه&zwnj;ر هاتووه&zwnj;، ئه&zwnj;وانه فرستاده&zwnj;کانیان به به&zwnj;ڵگه&zwnj;ی زۆره&zwnj;وه هاتن دووپاتیان ده&zwnj;کرده&zwnj;وه که پێغه&zwnj;مبه&zwnj;ری په&zwnj;روه&zwnj;ردگارن، که&zwnj;چی خوانه&zwnj;ناسان یان به&zwnj;ده&zwnj;ستیان ئاماژه&zwnj;یان ده&zwnj;کرد بۆ پێغه&zwnj;مبه&zwnj;ران که بێده&zwnj;نگ بن، یاخود ده&zwnj;ستیان ده&zwnj;برد بۆ ده&zwnj;می خۆیان و (ده&zwnj;یانکرده فیکه و هۆسه و گاڵته&zwnj;جاڕی) و ده&zwnj;یانوت: بێگومان ئێمه باوه&zwnj;ڕمان به&zwnj;م په&zwnj;یام و به&zwnj;رنامه&zwnj;یه نیه که به ئێوه&zwnj;دا ڕه&zwnj;وانه کراوه&zwnj;، به&zwnj;ڕاستی ئێمه&zwnj;ش له دوو دڵیه&zwnj;کدان که خه&zwnj;ڵکیش ده&zwnj;خاته گومانه&zwnj;وه له&zwnj;وه&zwnj;ی که ئێوه به&zwnj;نگه&zwnj;وازی ئێمه&zwnj;ی بۆ ده&zwnj;که&zwnj;ن.';
$t[10] = 'پێغه&zwnj;مبه&zwnj;رانیان وتیان: با&zwnj;شه&zwnj;، ئاخر گومان له چی ده&zwnj;که&zwnj;ن؟ ئایا له خوای په&zwnj;روه&zwnj;ردگاردا گومان هه&zwnj;یه&zwnj;؟! به&zwnj;دیهێنه&zwnj;ری ئاسمانه&zwnj;کان و زه&zwnj;وی؟! ئه&zwnj;و زاته بانگتان ده&zwnj;کات بۆ ئه&zwnj;وه&zwnj;ی لێتان خۆش ببێت، بۆ ئه&zwnj;وه&zwnj;ی هه&zwnj;ڵه&zwnj;و گوناهه&zwnj;کانتان چاوپۆشی بکات و مۆڵه&zwnj;تیشیتان ده&zwnj;دات تا کاتێکی دیاریکراو، که&zwnj;چی (خوا نه&zwnj;ناسان ڕوو به&zwnj;پێغه&zwnj;مبه&zwnj;ران) وتیان: ئێوه ته&zwnj;نها به&zwnj;شه&zwnj;رن وه&zwnj;کو ئێمه هیچی تر نین (له&zwnj;باتی سوپاسی خوا بکه&zwnj;ن که ئه&zwnj;وه&zwnj;نده ئاده&zwnj;میزاد ڕێزداره&zwnj;، نوێنه&zwnj;ر له خۆیان هه&zwnj;ڵده&zwnj;بژێرێت)، که&zwnj;چی ڕه&zwnj;خنه&zwnj;یان گرت و وتیان: ئێوه ده&zwnj;تانه&zwnj;وێت ڕێگرو دژ به&zwnj;وه بن که باو و باپیرانمان ده&zwnj;یانپه&zwnj;رست (ده&zwnj;ی ئه&zwnj;گه&zwnj;ر ڕاست ده&zwnj;که&zwnj;ن) به&zwnj;ڵگه&zwnj;یه&zwnj;کی ئاشکرامان بۆ بهێنن و نیشانمان بده&zwnj;ن؟!';
$t[11] = 'پێغه&zwnj;مبه&zwnj;رانیان پییان وتن: ئێمه ته&zwnj;نها به&zwnj;شه&zwnj;رێکین وه&zwnj;کو ئێوه و هیچی تر، به&zwnj;ڵام خوا ویستی هه&zwnj;ر که&zwnj;سێکی ببێت له به&zwnj;نده&zwnj;کانی خۆی منه&zwnj;ت ده&zwnj;خاته&zwnj;سه&zwnj;ری (هه&zwnj;ڵیانده&zwnj;بژێرێ بۆ گه&zwnj;یاندنی په&zwnj;یامه پیرۆزه&zwnj;که&zwnj;ی)، ئێمه بۆمان نه&zwnj;بووه هیچ به&zwnj;ڵگه و موعجیزه&zwnj;یه&zwnj;کتان بۆ بهێنین مه&zwnj;گه&zwnj;ر به ویست و مۆڵه&zwnj;تی خوا نه&zwnj;بێت، ده&zwnj;با ئه&zwnj;وانه&zwnj;ی که ئیماندارن هه&zwnj;ر پشت به&zwnj;خوا ببه&zwnj;ستن.';
$t[12] = 'جا ئه&zwnj;وه چیمان هه&zwnj;یه ئێمه ئه&zwnj;گه&zwnj;ر پشت به په&zwnj;روه&zwnj;ردگاری مه&zwnj;زن نه&zwnj;به&zwnj;ستین، له&zwnj;کاتێکدا که به&zwnj;ڕاستی ئه&zwnj;و زاته هیدایه&zwnj;ت و ڕێنموویی کردووین بۆ هه&zwnj;موو ڕێگه&zwnj;و ڕێبازێکی چاك و به&zwnj;سوود، مه&zwnj;رج بێت له به&zwnj;رامبه&zwnj;ر ئه&zwnj;و هه&zwnj;موو نه&zwnj;خۆشی و ئازاره&zwnj;وه که ئێوه بۆ ئێمه&zwnj;تان پێش هێنه&zwnj;، خۆگرو به&zwnj;ئارام بین، ده&zwnj;با ئه&zwnj;وانه&zwnj;ی که پشتیوانیان پێویسته هه&zwnj;ر پشت به&zwnj;خوا ببه&zwnj;ستن.';
$t[13] = 'ئه&zwnj;وانه&zwnj;ش که بێ باوه&zwnj;ڕ بوون به پێغه&zwnj;مبه&zwnj;رانی خۆیانیان وت: (ئه&zwnj;گه&zwnj;ر کۆڵ نه&zwnj;ده&zwnj;ن) به&zwnj;ڕاستی له وڵاتمان ده&zwnj;رتان ده&zwnj;که&zwnj;ین، یان ده&zwnj;بێت بێنه&zwnj;وه ڕیزی ئاینه&zwnj;که&zwnj;ی ئێمه&zwnj;، ئه&zwnj;وسا په&zwnj;روه&zwnj;ردگاریان په&zwnj;یامی بۆ ناردن که&zwnj;: دڵنیابن ئێمه سته&zwnj;مکاران (به&zwnj;م زووانه&zwnj;) له&zwnj;ناو ده&zwnj;به&zwnj;ین و ده&zwnj;یانفه&zwnj;وتێنین...';
$t[14] = 'بێگومان نیشته&zwnj;جێتان ده&zwnj;که&zwnj;ین له&zwnj;و وڵاته&zwnj;دا له دوای ئه&zwnj;وان، ئه&zwnj;وه&zwnj;ش بۆ هه&zwnj;موو ئه&zwnj;و که&zwnj;سانه&zwnj;یه که له وه&zwnj;ستانیان له به&zwnj;رده&zwnj;م ده&zwnj;سه&zwnj;ڵاتی مندا ده&zwnj;ترسن و هه&zwnj;ڕه&zwnj;شه&zwnj;کانی من بیم و ترسیان هه&zwnj;یه&zwnj;.';
$t[15] = 'ئه&zwnj;وسا ئیتر پێغه&zwnj;مبه&zwnj;ران داوای سه&zwnj;رکه&zwnj;وتنیان له په&zwnj;روه&zwnj;ردگاریان کردو هه&zwnj;موو زۆردارو سته&zwnj;مکارو سه&zwnj;رسه&zwnj;خت و دڵ ڕه&zwnj;شێك نائومێد و مایه&zwnj;پووچ بوو.';
$t[16] = '(هه&zwnj;ریه&zwnj;ك له&zwnj;وانه&zwnj;) له داهاتوودا دۆزه&zwnj;خ چاوه&zwnj;ڕێیه&zwnj;تی و تیایدا کێم و زووخاوی ده&zwnj;رخوارد ده&zwnj;درێت....';
$t[17] = 'جا ئه&zwnj;و کێم و زووخاوه قوم قوم به&zwnj;ناچاری ده&zwnj;خواته&zwnj;وه و به&zwnj;ئاسانی بۆی قوت نادرێت، مه&zwnj;رگیش له هه&zwnj;موو لایه&zwnj;که&zwnj;وه هێرشی بۆ ده&zwnj;هێنێت، که&zwnj;چی ناشمرێت، له&zwnj;وه&zwnj;ودواش سزاو ئازاری هه&zwnj;مه&zwnj;جۆرو ناخۆش و گران یه&zwnj;خه&zwnj;ی به&zwnj;رنادات.';
$t[18] = 'نموونه&zwnj;ی ئه&zwnj;وانه&zwnj;ی که باوه&zwnj;ڕیان نه&zwnj;هێناوه به&zwnj;په&zwnj;روه&zwnj;ردگاریان، کرده&zwnj;وه&zwnj;کانیان وه&zwnj;کو خۆڵه&zwnj;مێشێك وایه که له ڕه&zwnj;شه&zwnj;بایه&zwnj;کی سه&zwnj;خت له ڕۆژێکی گه&zwnj;رده&zwnj;لوودا به&zwnj;توندی لێی بدات، له&zwnj;و هه&zwnj;وڵ و کۆششه&zwnj;ی که داویانه بۆ دنیا هیچیان ده&zwnj;ست ناکه&zwnj;وێت، ئا ئه&zwnj;وه&zwnj;یه گومڕایه&zwnj;ك که له حه&zwnj;ق و ڕاستیه&zwnj;وه دووره&zwnj;.';
$t[19] = 'ئایا نه&zwnj;تبینیوه و نه&zwnj;تزانیوه که بێگومان خوا ئاسمانه&zwnj;کان و زه&zwnj;وی له&zwnj;سه&zwnj;ر بنچینه&zwnj;ی حه&zwnj;ق و ڕاستی به&zwnj;دیهێناوه&zwnj;، ئه&zwnj;گه&zwnj;ر بیه&zwnj;وێت ئه&zwnj;وه ئێوه له&zwnj;ناوده&zwnj;بات و ناتانهێڵێت و که&zwnj;سانی تازه ده&zwnj;هێنێته کایه&zwnj;وه&zwnj;.';
$t[20] = 'ئه&zwnj;و گۆڕانکاری و ئه&zwnj;و جۆره شتانه لای خوای به&zwnj;ده&zwnj;سه&zwnj;ڵات گران نیه و زۆر ئاسانه&zwnj;.';
$t[21] = '(ئه&zwnj;و ڕۆژه&zwnj;ی که قیامه&zwnj;ت به&zwnj;رپا ده&zwnj;بێت) هه&zwnj;مووان له&zwnj;به&zwnj;رده&zwnj;م ده&zwnj;سه&zwnj;ڵاتی خوادا ده&zwnj;رده&zwnj;که&zwnj;ون، ئینجا لاوازو بێ ده&zwnj;سه&zwnj;ڵاته&zwnj;کان (ئه&zwnj;وانه&zwnj;ی کوێرانه شوێن ملهوڕو سته&zwnj;مکاران که&zwnj;وتبوون) ده&zwnj;ڵێن: به&zwnj;وانه&zwnj;ی که خۆیان به&zwnj;زل و گه&zwnj;وره دانابوو، بێگومان خۆ ئێمه شوێن ئێوه که&zwnj;وتبووین! (ئێمه گوێڕایه&zwnj;ڵ و فه&zwnj;رمانبه&zwnj;رداری ئێوه بووین!) جا ئایا ئێوه ئه&zwnj;مڕۆ ناتوانن هه&zwnj;وڵێك بده&zwnj;ن تائه&zwnj;ندازه&zwnj;یه&zwnj;ك سزای خوا له&zwnj;سه&zwnj;رمان که&zwnj;م ببێته&zwnj;وه&zwnj;، له&zwnj;وه&zwnj;ڵامیاندا وتیان: ئه&zwnj;گه&zwnj;ر خوا هیدایه&zwnj;تی ئێمه&zwnj;ی بدایه&zwnj;، ئێمه&zwnj;ش هیدایه&zwnj;تی ئێوه&zwnj;مان ده&zwnj;دا، (ئه&zwnj;ڵبه&zwnj;ت ڕوون و ئاشکرایه که&zwnj;ئه&zwnj;وانه به ده&zwnj;سه&zwnj;ڵاتدارو بێ ده&zwnj;سه&zwnj;ڵاتیانه&zwnj;وه له دنیادا نه&zwnj;ك هیدایه&zwnj;تیان نه&zwnj;ده&zwnj;ویست به&zwnj;ڵکو دژی ده&zwnj;وه&zwnj;ستان و پیلانیان ده&zwnj;گێڕا، ئیتر چۆن خوای گه&zwnj;روره هیدایه&zwnj;تی ئه&zwnj;و جۆره که&zwnj;سانه ده&zwnj;دات، خۆ هیدایه&zwnj;ت به&zwnj;زۆر نیه&zwnj;) ئێسته&zwnj;ش بۆ ئێمه چوونیه&zwnj;كه&zwnj;، هاوارو ئاهو ناڵه بکه&zwnj;ین، یان دان به&zwnj;خۆدا بگرین، چارمان نیه و ڕزگار بوونمان ئه&zwnj;سته&zwnj;مه&zwnj;.';
$t[22] = '(کاتێ هه&zwnj;مووشت ته&zwnj;واو ده&zwnj;بێت به&zwnj;هه&zwnj;شتیه&zwnj;کان ده&zwnj;چنه به&zwnj;هه&zwnj;شته&zwnj;وه و دۆزه&zwnj;خیه&zwnj;کان ده&zwnj;خرێنه دۆزه&zwnj;خه&zwnj;وه&zwnj;) شه&zwnj;یتان (به&zwnj;فه&zwnj;رمانی خوا) دێته قسه&zwnj;و (ڕوو به په&zwnj;یڕه&zwnj;وانی) ده&zwnj;ڵێت: به&zwnj;ڕاستی خوا به&zwnj;ڵێنی ڕاست و دروستی پێ ڕاگه&zwnj;یاندبوون منیش به&zwnj;ڵێنی درۆو نادروستم پێدان و جا توانیم گومڕاتان بکه&zwnj;م و له ڕێبازی ڕاست و وێڵتان بکه&zwnj;م، وه&zwnj;نه&zwnj;بێت من هیچ زۆرو ده&zwnj;سه&zwnj;ڵاتێکم به&zwnj;سه&zwnj;ر ئێوه&zwnj;دا هه&zwnj;بووبێت، جگه له&zwnj;وه&zwnj;ی (به&zwnj;وه&zwnj;سوه سه&zwnj;وخه&zwnj;ته&zwnj;ره&zwnj;و خه&zwnj;یاڵ) با&zwnj;نگم کردن (بۆ خوانه&zwnj;ناسی و تاوان) ئێوه&zwnj;ش به&zwnj;ده&zwnj;ممه&zwnj;وه هاتن و به&zwnj;قسه&zwnj;تان کردم، ئیتر سه&zwnj;ر&zwnj;زه&zwnj;نشت و لۆمه&zwnj;ی من مه&zwnj;که&zwnj;ن، به&zwnj;ڵکو سه&zwnj;رزه&zwnj;نشت و لۆمه&zwnj;ی خۆتان بکه&zwnj;ن (که ئاوا گێلانه&zwnj;، بێ بیرکردنه&zwnj;وه به&zwnj;قسه&zwnj;تان کردم، ئه&zwnj;مڕۆ) من ناتوانم به&zwnj;هاناو هاواری ئێوه&zwnj;وه بێم، ئێوه&zwnj;ش ناتوانن به هاناو هاواری منه&zwnj;وه بێن، بێگومان من باوه&zwnj;ڕم نه&zwnj;بوو به&zwnj;وه&zwnj;ی که کاتی خۆی منتان کردبوو به&zwnj;شه&zwnj;رێك و هاوبه&zwnj;ش له پێشدا، به&zwnj;ڕاستی ئه&zwnj;مڕۆ سته&zwnj;مکاران سزای زۆر به&zwnj;ئێش ده&zwnj;درێن.';
$t[23] = '(له&zwnj;ولاشه&zwnj;وه&zwnj;) ئه&zwnj;وانه&zwnj;ی ئیمان و باوه&zwnj;ڕیان هێنابوو، کارو کرده&zwnj;وه چاکه&zwnj;کانیان ئه&zwnj;نجام دابوو به&zwnj;ره&zwnj;و به&zwnj;هه&zwnj;شت به&zwnj;ڕێ کران، چه&zwnj;نده&zwnj;ها ڕووبار به&zwnj;ژێر دره&zwnj;خته&zwnj;کان و به&zwnj;به&zwnj;رده&zwnj;م کۆشکه&zwnj;کانیدا ده&zwnj;ڕوات، له&zwnj;وجێگه&zwnj;و ڕێگه خۆشه&zwnj;دا ژیانی هه&zwnj;میشه&zwnj;یی و به&zwnj;رده&zwnj;وام ده&zwnj;به&zwnj;نه سه&zwnj;ر به&zwnj;فه&zwnj;رمان و ڕه&zwnj;زامه&zwnj;ندی په&zwnj;روه&zwnj;ردگاریان و چاك و چۆنیان له&zwnj;و به&zwnj;هه&zwnj;شته&zwnj;دا _سڵاوکردنه&zwnj;_.';
$t[24] = 'ئایا نه&zwnj;تانبینیوه و نه&zwnj;تانزانیوه چۆن خوا نموونه ده&zwnj;هێنێته&zwnj;وه به&zwnj;گوفتاری چاك و پاك، که&zwnj;وه&zwnj;ك دره&zwnj;ختی چاك و به&zwnj;ردار وایه&zwnj;، ڕه&zwnj;گی به&zwnj;توندی داکوتاوه له زه&zwnj;ویدا، لق و پۆپه&zwnj;کانیشی به&zwnj;ئاسماندا بڵاوبونه&zwnj;ته&zwnj;وه&zwnj;.';
$t[25] = 'ئه&zwnj;و دره&zwnj;خته هه&zwnj;موو کات و ده&zwnj;مێك به&zwnj;رو بوومی (پاكو جوان و بێگه&zwnj;ردی) ده&zwnj;به&zwnj;خشێت به ویستی په&zwnj;روه&zwnj;ردگاری، جا خوای په&zwnj;روه&zwnj;ردگار ئه&zwnj;م نموونانه ده&zwnj;هێنێته&zwnj;وه بۆ خه&zwnj;ڵکی، به&zwnj;ڵکو یاداوه&zwnj;ری وه&zwnj;رگرن (له&zwnj;ڕاستیدا دین و به&zwnj;رنامه&zwnj;ی خوا دره&zwnj;ختێکی ئاوا پیرۆزه&zwnj;، ڕه&zwnj;گ و ڕیشاڵی خێر به&zwnj;خشه&zwnj;، چونکه خاکی زه&zwnj;وی له داماڵین ده&zwnj;پارێزێت، لق و پۆپی به&zwnj;سووده&zwnj;، سێبه&zwnj;ر ده&zwnj;کات، گه&zwnj;ڵاوگوڵ بۆ داوو ده&zwnj;رمان و ماڵات، به&zwnj;روبوومی بۆ ئاده&zwnj;میزاد، باڵنده&zwnj;کان به&zwnj;ئاوازی خۆش له&zwnj;سه&zwnj;ری ده&zwnj;خوێنن و ته&zwnj;سبیحات ده&zwnj;که&zwnj;ن و دڵخۆشمان ده&zwnj;که&zwnj;ن، ئۆکسجین ده&zwnj;به&zwnj;خشێت... هتد).';
$t[26] = 'نموونه&zwnj;ی قسه&zwnj;و گوفتاری پیس و ناپوختیش وه&zwnj;کو دره&zwnj;ختی زرو پیس و بێ سوود وایه&zwnj;، که هه&zwnj;ڵکێشرابێت و له&zwnj;سه&zwnj;ر ڕووکاری زه&zwnj;وی فڕێ درابێت، به&zwnj;هیچ بارێکدا خۆی نه&zwnj;گرێت (بۆ سووتاندن نه&zwnj;بێت بۆ هیچی تر چاك نیه&zwnj;، ئه&zwnj;م نموونه&zwnj;یه&zwnj;ش ئاماژه&zwnj;یه بۆ هه&zwnj;موو سه&zwnj;رگه&zwnj;ردانێك که له دنیادا خێر نه&zwnj;داته&zwnj;وه&zwnj;، له قیامه&zwnj;تیشدا شوێنکه&zwnj;وتوانیان له دۆزه&zwnj;خه&zwnj;وه سه&zwnj;ریان ده&zwnj;رده&zwnj;چێت).';
$t[27] = 'خوای گه&zwnj;وره ئه&zwnj;وانه&zwnj;ی باوه&zwnj;ڕیان هێناوه پایه&zwnj;دار ده&zwnj;کات له&zwnj;سه&zwnj;ر گوفتاری به&zwnj;جێ و دروستیان له ژیانی دنیادا، له&zwnj;سه&zwnj;ره&zwnj;مه&zwnj;رگ و له قیامه&zwnj;تیشدا، (باوه&zwnj;ڕی دامه&zwnj;زراو فریایان ده&zwnj;که&zwnj;وێت و) گوفتاریان ڕێك و پێك و دامه&zwnj;زراو ده&zwnj;بێت و به&zwnj;ئیمانه&zwnj;وه ده&zwnj;مرن، له&zwnj;ولاشه&zwnj;وه هه&zwnj;ر ئه&zwnj;و خوایه سته&zwnj;مکاران گومڕا ده&zwnj;کات و ئه&zwnj;وه&zwnj;ی په&zwnj;روه&zwnj;ردگار بیه&zwnj;و&lrm;ێت هه&zwnj;ر ئه&zwnj;وه پێش دێت و ده&zwnj;کرێت.';
$t[28] = 'ئایا سه&zwnj;رنجی ئه&zwnj;وانه ناده&zwnj;یت که نازو نیعمه&zwnj;ته&zwnj;کانی خوایان به حه&zwnj;رام گێڕاوه (بۆ گوناهو خراپه باکاری ده&zwnj;هێنن) و بوونه&zwnj;ته هۆی سه&zwnj;رگه&zwnj;ردانی و تیاچوونی قه&zwnj;ومه&zwnj;که&zwnj;ی خۆیان.';
$t[29] = '(ئه&zwnj;وانه&zwnj;) سه&zwnj;رئه&zwnj;نجام ده&zwnj;چنه ناو دۆزه&zwnj;خ، که جێگه&zwnj;یه&zwnj;کی زۆر ناخۆشه بۆ نیشته&zwnj;جێ بوون.';
$t[30] = 'خوانه&zwnj;ناسان هاوه&zwnj;ڵ و شه&zwnj;ریکیان بۆ خوا بڕیاردا، بۆ ئه&zwnj;وه&zwnj;ی خه&zwnj;ڵك وێڵ بکه&zwnj;ن له به&zwnj;رنامه&zwnj;و ئاین و ڕێبازی خوا، پێیان بڵێ: ڕابوێرن و ژیان به&zwnj;سه&zwnj;ربه&zwnj;رن، چونکه سه&zwnj;رئه&zwnj;نجامتان به&zwnj;ره&zwnj;و دۆزه&zwnj;خه&zwnj;.';
$t[31] = '(ئه&zwnj;ی محمد صلی الله علیه وسلم) به&zwnj;و به&zwnj;ندانه&zwnj;م که باوه&zwnj;ڕیان هێناوه بڵێ: با نوێژه&zwnj;کانیان به چاکی ئه&zwnj;نجام بده&zwnj;ن و له&zwnj;و ڕزق و ڕۆزیه&zwnj;ی که پێمان به&zwnj;خشیوون ببه&zwnj;خشن به&zwnj;نهێنی (بۆ ئه&zwnj;وه&zwnj;ی ڕیاو ڕووپامایی تێکه&zwnj;ڵ نه&zwnj;بێت) و به&zwnj;ئاشکرا (بۆ هاندانی که&zwnj;سانی تر)، پێش ئه&zwnj;وه&zwnj;ی ڕۆژێك بێت نه کڕینی تیادایه&zwnj;، نه&zwnj;فرۆشتن (تا به&zwnj;هۆی پاره&zwnj;و سامانه&zwnj;وه خۆتان ده&zwnj;رباز بکه&zwnj;ن) نه دۆستایه&zwnj;تیشی تێدایه (تا دۆستان و هاوه&zwnj;ڵان هه&zwnj;وڵێك بده&zwnj;ن).';
$t[32] = 'خوا ئه&zwnj;و زاته&zwnj;یه که ئاسمانه&zwnj;کان و زه&zwnj;وی به&zwnj;دیهێناوه&zwnj;، له ئاسمانه&zwnj;وه بارانی باراندووه به&zwnj;هۆیه&zwnj;وه به&zwnj;روبوومی له&zwnj;ناخی زه&zwnj;وی بۆ ئێوه ده&zwnj;رهێناوه&zwnj;، هه&zwnj;روه&zwnj;ها که&zwnj;شتی له ده&zwnj;ریادا بۆ ئێوه بار هێناوه تا فه&zwnj;رمانی ئه&zwnj;و زاته بڕوات (به&zwnj;سه&zwnj;ریه&zwnj;وه&zwnj;، هه&zwnj;ر خوایه به&zwnj;دیهێنه&zwnj;ری ئه&zwnj;و ماده سه&zwnj;ره&zwnj;تاییانه&zwnj;ی که که&zwnj;شتی لێ دروست ده&zwnj;کرێت، هه&zwnj;ر ئه&zwnj;وزاته&zwnj;یه به&zwnj;دیهێنه&zwnj;ری وزه&zwnj;و ئاوو هه&zwnj;وا... هتد)، هه&zwnj;روه&zwnj;ها ڕووباره&zwnj;کانیشی بۆ ڕام کردوون (که جۆره&zwnj;ها سوودیان هه&zwnj;یه بۆتان).';
$t[33] = 'خۆرو مانگیشی بۆ ڕام هێناون که به&zwnj;رده&zwnj;وام له چه&zwnj;رخ و خولدان، شه&zwnj;و و ڕۆژیشی بۆ خزمه&zwnj;تگوزاری ئێوه ڕام هێنا.';
$t[34] = 'له&zwnj;هه&zwnj;رچی شتێك داواتان کردبێت و پێوستان بێت، پێی به&zwnj;خشیوون، خۆ ئه&zwnj;گه&zwnj;ر نازو نیعمه&zwnj;ت و به&zwnj;خششه&zwnj;کانی خوا ئاماژه بکه&zwnj;ن، ناتوانن بیانژمێرن (به&zwnj;ڵکو یه&zwnj;ك نیعمه&zwnj;ت ناژمێردرێت، ئه&zwnj;وه&zwnj;نده لق و پۆپی لێ ده&zwnj;بێته&zwnj;وه&zwnj;)، به&zwnj;ڕاستی زۆربه&zwnj;ی ئینسانه&zwnj;کان زۆر سته&zwnj;مکارو خوانه&zwnj;ناس و سپڵه&zwnj;ن.';
$t[35] = '(به&zwnj;یاد بێنه&zwnj;) کاتێك ئیبراهیم نزای کردو وتی: په&zwnj;روه&zwnj;ردگارا، باڵی هێمنی و ئاسایش بکێشه به&zwnj;سه&zwnj;ر ئه&zwnj;م شارو وڵاته&zwnj;داو خۆم و نه&zwnj;وه&zwnj;کانیشم بپارێزه له&zwnj;وه&zwnj;ی که بته&zwnj;کان بپه&zwnj;ره&zwnj;ستین.';
$t[36] = 'په&zwnj;روه&zwnj;ردگارا، به&zwnj;ڕاستی ئه&zwnj;و بتانه زۆر که&zwnj;سیان گومڕا کردو، زۆر خه&zwnj;ڵکیان سه&zwnj;رلێشێواو کرد، جا ئه&zwnj;وه&zwnj;ی شوێنی من بکه&zwnj;وێته و به&zwnj;گوێی من بکات، ئه&zwnj;وه بێگومان له په&zwnj;یڕه&zwnj;وانی منه&zwnj;، ئه&zwnj;وه&zwnj;ش که لێم یاخی ده&zwnj;بێت، ئه&zwnj;وه بێگومان تۆ زۆر لێخۆش بوو میهره&zwnj;بانیت.';
$t[37] = 'په&zwnj;روه&zwnj;ردگارا به&zwnj;ڕاستی من هه&zwnj;ندێ له وه&zwnj;چه و خێزانم له دۆڵێکی وشکی بێ کشتوکاڵدا جێنشین کردووه&zwnj;، له&zwnj;پاڵ ماڵه پیرۆزه&zwnj;که&zwnj;تدا، له پاڵ (بیت الحرام) دا، په&zwnj;روه&zwnj;ردگارا، بۆیه لێره نیشته&zwnj;جێم کردن تا به&zwnj;چاکی نوێژو خواپه&zwnj;رستی ئه&zwnj;نجام بده&zwnj;ن، جاداواکارم دڵی هه&zwnj;ندێك له&zwnj;و خه&zwnj;ڵکه که&zwnj;مه&zwnj;ندکێش بکه&zwnj;یت بۆ لایان و له ڕزق و ڕۆزی جۆراو جۆر به&zwnj;هره&zwnj;مه&zwnj;ندیان بکه&zwnj;یت، بۆئه&zwnj;وه&zwnj;ی سوپاسگوزاری بکه&zwnj;ن.';
$t[38] = 'په&zwnj;روه&zwnj;ردگارا، به&zwnj;ڕاستی تۆ ده&zwnj;زانیت چی ده&zwnj;شارینه&zwnj;وه و چی ئاشکرا ده&zwnj;که&zwnj;ین (له کارو کرده&zwnj;وه&zwnj;مان) و هیچ شتێك له زه&zwnj;وی و له ئاسماندا له خوای گه&zwnj;وره په&zwnj;نهان نابێت.';
$t[39] = 'سوپاس و ستایش بۆ ئه&zwnj;و خوایه&zwnj;ی که له ته&zwnj;مه&zwnj;نی پیریدا ئیسماعیل و ئیسحاقی پێ به&zwnj;خشیم، به&zwnj;ڕاستی په&zwnj;روه&zwnj;ردگارم بیسه&zwnj;ری دوعا و نزایه&zwnj;.';
$t[40] = 'په&zwnj;روه&zwnj;ردگارا، خۆم و نه&zwnj;وه&zwnj;کانم بگێڕه له&zwnj;و که&zwnj;سانه&zwnj;ی که ئه&zwnj;نجامده&zwnj;ری نوێژ بین به&zwnj;چاکی، په&zwnj;روه&zwnj;ردگارا، دوعاو نزام گیرا بکه&zwnj;.';
$t[41] = 'په&zwnj;روه&zwnj;ردگارا، له&zwnj;خۆم و دایک و باوکم و ئیمانداران خۆش ببه&zwnj;، له&zwnj;و ڕۆژه&zwnj;دا که لێپرسینه&zwnj;وه به&zwnj;رپا ده&zwnj;بێت.';
$t[42] = '(ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر صلی الله علیه وسلم) واگومان مه&zwnj;به که خوا بێ ئاگایه له&zwnj;و کاروکرده&zwnj;وه&zwnj;یه&zwnj;ی سته&zwnj;مکاران ده&zwnj;یکه&zwnj;ن، به&zwnj;ڕاستی خوا سزاکه&zwnj;یان دوا ده&zwnj;خات بۆ ڕۆژیك که چاوه&zwnj;کانیان تیایدا ئه&zwnj;بڵه&zwnj;ق ده&zwnj;بن (جاری وایش هه&zwnj;یه هه&zwnj;ر له دنیادا سته&zwnj;مکاران ڕیسوا ده&zwnj;کات و تۆڵه&zwnj;یان لێده&zwnj;سێنێت).';
$t[43] = 'به په&zwnj;له و شپرزه&zwnj;ییه&zwnj;وه دێن، سه&zwnj;ریان به&zwnj;رز کردۆته&zwnj;وه و له ترس و سامناکی ئه&zwnj;و ڕۆژه&zwnj;دا چاویان ناتروکێت، دڵه ڕاوکێیانه&zwnj;و دڵ و ده&zwnj;روونیان به&zwnj;تاڵه و هیچ حاڵی نین.';
$t[44] = 'ئه&zwnj;ی محمد صلی الله علیه وسلم خه&zwnj;ڵك بترسێنه و بێداریان بکه&zwnj;ره&zwnj;وه له&zwnj;و ڕۆژه&zwnj;ی که سزاو ئازار یه&zwnj;خه&zwnj;یان پێ ده&zwnj;گرێت، جا ئه&zwnj;وانه&zwnj;ی که سته&zwnj;میان کردووه ده&zwnj;ڵێن: په&zwnj;روه&zwnj;ردگارا بۆ ماوه&zwnj;یه&zwnj;کی که&zwnj;م و نزیك مۆڵه&zwnj;تمان بدا (بمانگێڕه&zwnj;وه بۆ دنیا) تا به ده&zwnj;م بانگه&zwnj;وازه&zwnj;که&zwnj;ته&zwnj;وه بچین و شوێنی پێغه&zwnj;مبه&zwnj;ران بکه&zwnj;ین، (به&zwnj;ڵام تازه کار له&zwnj;کار ترازاوه&zwnj;و گه&zwnj;ڕانه&zwnj;وه ئه&zwnj;سته&zwnj;مه&zwnj;، بۆیه پێیان ده&zwnj;وترێت) مه&zwnj;گه&zwnj;ر کاتی خۆی سوێندتان نه&zwnj;ده&zwnj;خوارد که دنیا به&zwnj;رده&zwnj;وامه&zwnj;و ئێوه تیاچونتان نابێت؟!.';
$t[45] = 'ئێوه کاتی خۆیشی نیشته&zwnj;جێ بوون له ماڵ و حاڵ و شوێنی ئه&zwnj;وانه&zwnj;دا که سته&zwnj;میان له خۆیان کردو (ڕێگه&zwnj;ی ئیمان و خواناسیان نه&zwnj;گرت) بۆتان ده&zwnj;رکه&zwnj;وت که چیمان پێکردن و چیمان به&zwnj;سه&zwnj;ر هێنان و نموونه و باسی زۆرمان بۆ هێناونه&zwnj;ته&zwnj;وه (ده&zwnj;رباره&zwnj;ی سه&zwnj;رئه&zwnj;نجامیان).';
$t[46] = 'بێگومان (خوانه&zwnj;ناسان) پیلان و ته&zwnj;ڵه&zwnj;که&zwnj;ی خۆیانیان گێڕا، فێڵ و فڕی زۆریان کرد، به&zwnj;ڵام هه&zwnj;موو پیلان و فێڵیان لای خوا ئاشکراو دیاره (به&zwnj;سه&zwnj;ر خۆیاندا ده&zwnj;شکێته&zwnj;وه&zwnj;) هه&zwnj;رچه&zwnj;نده پیلان و فێڵیان کێوه&zwnj;کانیش له بن بهێنێت.';
$t[47] = 'که&zwnj;واته&zwnj;، وا حساب مه&zwnj;که (ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر صلی الله علیه وسلم، ئه&zwnj;ی ئیماندار) خوای گه&zwnj;وره به&zwnj;ڵێنی خۆی بۆ پێغه&zwnj;مبه&zwnj;ران ناباته سه&zwnj;ر (به سه&zwnj;رخستن و مانه&zwnj;وه&zwnj;ی به&zwnj;رنامه&zwnj;یان له دنیاداو سه&zwnj;رفرازی هه&zwnj;میشه&zwnj;ییان له به&zwnj;هه&zwnj;شتی به&zwnj;ریندا) چونکه به&zwnj;ڕاستی خوا باڵاده&zwnj;سته و خاوه&zwnj;نی تۆڵه&zwnj;یه (له سته&zwnj;مکارو خوانه&zwnj;ناسان).';
$t[48] = 'له ڕۆژێکدا که ئه&zwnj;م زه&zwnj;ویه ده&zwnj;گۆڕێت به زه&zwnj;ویه&zwnj;کی ترو هه&zwnj;روه&zwnj;ها ئاسمانه&zwnj;کانیش ده&zwnj;گۆڕێن به شێوه&zwnj;و شێوازێکی ترو هه&zwnj;موو ئه&zwnj;و خه&zwnj;ڵکه ده&zwnj;رده&zwnj;که&zwnj;ون و ئاماده&zwnj;ن له به&zwnj;رده&zwnj;م دادگای خوای تاك و ته&zwnj;نهاو به&zwnj;ده&zwnj;سه&zwnj;ڵات و خه&zwnj;شیمگیر له کافران.';
$t[49] = 'له&zwnj;و ڕۆژه&zwnj;دا تاوانباران و گوناهکاران ده&zwnj;بینێت کۆت و زنجیر کراوه&zwnj;ته ده&zwnj;ست و پێ و گه&zwnj;ردنیان و به&zwnj;یه&zwnj;که&zwnj;وه به&zwnj;ستراون.';
$t[50] = 'کراسه&zwnj;کانیان له قه&zwnj;ترانه (که ما&zwnj;دده&zwnj;یه&zwnj;کی ڕه&zwnj;شی بۆن ناخۆشه و به&zwnj;خێرایش ده&zwnj;سووتێت) و بڵێسه&zwnj;ی ئاگریش ڕووخساریانی داپۆشیوه (زۆر له سه&zwnj;غڵه&zwnj;تیدان).';
$t[51] = '(ئه&zwnj;م تۆڵه سه&zwnj;ندنه له سته&zwnj;مکاران) بۆ ئه&zwnj;وه&zwnj;یه که خوا پاداشتی هه&zwnj;ر که&zwnj;سێك به&zwnj;گوێره&zwnj;ی ئه&zwnj;وه&zwnj;ی کردویه&zwnj;تی بداته&zwnj;وه&zwnj;، چونکه به&zwnj;ڕاستی خوای گه&zwnj;وره به&zwnj;خێرایی لێپرسینه&zwnj;وه&zwnj;کان ئه&zwnj;نجام ده&zwnj;دات.';
$t[52] = 'ئه&zwnj;م (قورئانه&zwnj;) ڕاگه&zwnj;یاندنێکه بۆ خه&zwnj;ڵکی به&zwnj;گشتی تا به&zwnj;هۆیه&zwnj;وه وریا بکرێنه&zwnj;وه و ئامۆژگاری پێ وه&zwnj;ربگرن، بۆ ئه&zwnj;وه&zwnj;ی بزانن بێگومان ئه&zwnj;و زاته خوایه&zwnj;کی تاك و ته&zwnj;نیایه&zwnj;، (هه&zwnj;روه&zwnj;ها بۆ ئه&zwnj;وه&zwnj;یه که&zwnj;) خاوه&zwnj;نی بیرو هۆشه&zwnj;کان یادا&zwnj;وه&zwnj;ری وه&zwnj;ربگرن و تێفکرن (له هه&zwnj;وڵی ئه&zwnj;وه&zwnj;دا بن خۆشیان و خه&zwnj;ڵکیش له سزای دۆزه&zwnj;خ ڕزگار بکه&zwnj;ن).';
?>