<?php
$t = array();
$t[1] = '&zwnj;چه&zwnj;ند پیتێك له سه&zwnj;ره&zwnj;تای هه&zwnj;ندێ له سووره&zwnj;ته&zwnj;كانی قورئاندا هاتوون ژماره&zwnj;یان (14) پیته&zwnj;، نیوه&zwnj;ی كۆی پیته&zwnj;كانی زمانی عه&zwnj;ره&zwnj;بین، بۆ سه&zwnj;لماندنی (اعجاز) و مه&zwnj;زنی و گرنگی قورئان كه له توانای هیچ كه&zwnj;سدا نیه به&zwnj;و پیتانه كتێبێكی ئاوا بێ وێنه دابنێت، به&zwnj;راده&zwnj;یه&zwnj;ك هه&zwnj;تا ئه&zwnj;گه&zwnj;ر هه&zwnj;موو گرۆی ئاده&zwnj;میزاد و په&zwnj;ری كۆببنه&zwnj;وه بۆ ئه&zwnj;و مه&zwnj;به&zwnj;سته بێگومان ناتوانن، هه&zwnj;روه&zwnj;ها چه&zwnj;نده&zwnj;ها نهێنی تریشی تێدایه&zwnj;، زاناكان له هه&zwnj;ندێكی دواون، هه&zwnj;ندێكیشیان وتوویانه&zwnj;: هه&zwnj;ر خوا خۆی زانایه به نهێنی ئه&zwnj;و پیتانه&zwnj;.';
$t[2] = '&zwnj;ئه&zwnj;و قورئانه كتێبێكه هیچ گومانێكی تێدا نیه&zwnj;، (كه له لایه&zwnj;ن خوای په&zwnj;روه&zwnj;ردگاره&zwnj;وه ڕه&zwnj;وانه كراوه به جبره&zwnj;ئیلدا بۆ پێغه&zwnj;مبه&zwnj;ر (صلی الله علیه وسلم) كه مایه&zwnj;ی ڕێنمووی و سه&zwnj;رچاوه&zwnj;ی هیدایه&zwnj;ته بۆ خواناسان و پارێزكاران (كه ئه&zwnj;وانه&zwnj;ن فه&zwnj;رزه&zwnj;كان جێبه&zwnj;جێ ده&zwnj;كه&zwnj;ن و له حه&zwnj;رام خۆیان ده&zwnj;پارێزن، له ڕووی بیروباوه&zwnj;ڕو كردارو گوفتره&zwnj;وه&zwnj;).';
$t[3] = 'ئه&zwnj;وانه&zwnj;ی باوه&zwnj;ڕ به نه&zwnj;بینراوه&zwnj;كان ده&zwnj;هێنن (وه&zwnj;ك خوا و فریشته و ئه&zwnj;وانه&zwnj;ی خوا باسی كردوون و ئێمه نایانبینین)، هه&zwnj;روه&zwnj;ها نوێژه&zwnj;كانیان به&zwnj;چاكی ئه&zwnj;نجام ده&zwnj;ده&zwnj;ن و له&zwnj;و ڕزق و ڕۆزییه (حه&zwnj;ڵاڵه&zwnj;ی) پێمان به&zwnj;خشیوون، ده&zwnj;به&zwnj;خشن.';
$t[4] = '(له هه&zwnj;مان كاتدا) ئه&zwnj;وانه باوه&zwnj;ڕ ده&zwnj;هێنن به&zwnj;و (په&zwnj;یامه&zwnj;ی) كه بۆ تۆ (ئه&zwnj;ی محمد (&lrm;&lrm;&lrm;&lrm;صلی الله علیه وسلم) دابه&zwnj;زێنراوه و به&zwnj;و په&zwnj;یامانه&zwnj;ش كه بۆ (پێغه&zwnj;مبه&zwnj;رانی&lrm; (سه&zwnj;لامی خوایان لێ بێت) پێش تۆ دابه&zwnj;زێنراوه&zwnj;، دڵنیاش ده&zwnj;بن كه ڕۆژی دوایی پێش دێت و قیامه&zwnj;ت هه&zwnj;ر به&zwnj;رپا ده&zwnj;بێت.';
$t[5] = 'ئه&zwnj;وانه له سه&zwnj;ر شا ڕێگه&zwnj;ی هیدایه&zwnj;تی په&zwnj;روه&zwnj;ردگاریانن و هه&zwnj;ر ئه&zwnj;وانیش سه&zwnj;رفراز و سه&zwnj;رکه&zwnj;وتوو (ئه&zwnj;مانه بنه&zwnj;ما سه&zwnj;ره&zwnj;کیه&zwnj;کانی ئیمان و ئیسلامن).';
$t[6] = 'به&zwnj;ڕاستی ئه&zwnj;وانه&zwnj;ی که بێ باوه&zwnj;ڕ بوون ئاگاداریان بکه&zwnj;یت یان ئاگاداریان نه&zwnj;که&zwnj;یت یه&zwnj;کسانه بۆیان، باوه&zwnj;ڕ و ئیمان ناهێنن...';
$t[7] = '(چونکه&zwnj;) خوا مۆری ناوه به&zwnj;سه&zwnj;ر دڵ و گوێیاندا و په&zwnj;رده&zwnj;ی به&zwnj;سه&zwnj;ر چاویاندا هێناوه&zwnj;، (نه بیرده&zwnj;که&zwnj;نه&zwnj;وه&zwnj;، نه ئاماده&zwnj;ن حه&zwnj;ق ببیستن و ڕاستییه&zwnj;کان نابینن)، هه&zwnj;ر بۆیه سزایه&zwnj;کی سه&zwnj;خت چاوه&zwnj;ڕێیانه &lrm;&lrm;&lrm; (ئه&zwnj;مانه&zwnj;ش ده&zwnj;سته&zwnj;یه&zwnj;کن له بێ باوه&zwnj;ڕان که هه&zwnj;ر خوا خۆی ده&zwnj;یانناسێت).';
$t[8] = 'له ناو خه&zwnj;ڵکیدا که&zwnj;سانێک هه&zwnj;ن که ده&zwnj;ڵێن: ئیمان و باوه&zwnj;ڕمان به خوا و به ڕۆژی قیامه&zwnj;ت هێناوه (له&zwnj;ڕاستیدا ئه&zwnj;وانه درۆ ده&zwnj;که&zwnj;ن) ئه&zwnj;وانه هه&zwnj;میشه باوه&zwnj;ڕدار نین).';
$t[9] = 'ئه&zwnj;وانه (به&zwnj;خه&zwnj;یاڵی خۆیان وا ده&zwnj;زانن) خوا و ئه&zwnj;و که&zwnj;سانه ده&zwnj;خه&zwnj;ڵه&zwnj;تێنن که باوه&zwnj;ڕداری ڕاسته&zwnj;قینه&zwnj;ن!! که&zwnj;چی جگه له خۆیان که&zwnj;سیان پێ ناخه&zwnj;ڵه&zwnj;تێنرێ، به&zwnj;ڵام هه&zwnj;ست ناکه&zwnj;ن و تێناگه&zwnj;ن و بیرناکه&zwnj;نه&zwnj;وه&zwnj;.';
$t[10] = 'ئه&zwnj;وانه له دڵ و ده&zwnj;روونیاندا نه&zwnj;خۆشی بێ باوه&zwnj;ڕی و حه&zwnj;سوودی و کینه هه&zwnj;یه&zwnj;، خوایش ده&zwnj;ردیان گران و کاریگه&zwnj;ر ده&zwnj;کات و سزایه&zwnj;کی سه&zwnj;ختیش (له دینا و قیامه&zwnj;تدا) چاوه&zwnj;ڕێنانه به هۆی درۆکردن و (دژایه&zwnj;تییان بۆ ئیسلام).';
$t[11] = 'ئه&zwnj;وانه کاتێک پێیان ده&zwnj;وترێت تۆوی گوناهو تاوان و خراپه&zwnj;کاری مه&zwnj;چێنن له زه&zwnj;ویدا، ده&zwnj;ڵێن: به&zwnj;ڕاستی ئێمه ته&zwnj;نها چاکسازی ده&zwnj;که&zwnj;ین!!';
$t[12] = 'ئاگادار بن، که ڕاستی هه&zwnj;ر ئه&zwnj;وانه تۆوی خراپه ده&zwnj;چێنن، به&zwnj;ڵام (به&zwnj;هۆی لووت به&zwnj;رزی و ده&zwnj;رده&zwnj;دارییانه&zwnj;وه&zwnj;) هه&zwnj;ستی پێ ناکه&zwnj;ن و (خۆیانی لێ گێل ده&zwnj;که&zwnj;ن).';
$t[13] = 'کاتێک پێیان ده&zwnj;وترێت ئیمان و باوه&zwnj;ڕ بهێنن وه&zwnj;کو ئه&zwnj;وه هه&zwnj;موو خه&zwnj;ڵکه (بێ فێڵ و ڕاستگۆیانه&zwnj;) که باوه&zwnj;ڕیان هێناوه&zwnj;، ده&zwnj;ڵێن: چۆن ئێمه وه&zwnj;کو ئه&zwnj;و خه&zwnj;ڵکه عه&zwnj;قڵ و گێلانه باوه&zwnj;ڕ ده&zwnj;هێنین، (خه&zwnj;ڵکینه&zwnj;) چاک بزانن و ئاگاداربن که هه&zwnj;ر ئه&zwnj;وانه خۆیان که&zwnj;م عه&zwnj;قڵ و گێل و نه&zwnj;فامن، به&zwnj;ڵام بۆ خۆیان نازانن.';
$t[14] = 'كاتێكیش (دووڕووه&zwnj;كان) ده&zwnj;گه&zwnj;ن به&zwnj;وانه&zwnj;ی باوه&zwnj;ڕیان هێناوه ده&zwnj;ڵێن: ئێمه ئیماندارین و باوه&zwnj;ڕمان هێناوه&zwnj;!!، كه&zwnj;چی كاتێك به ته&zwnj;نها ده&zwnj;مێننه&zwnj;وه له لای شه&zwnj;یتانه گه&zwnj;وره و فێڵبازه&zwnj;كانیان ده&zwnj;ڵێن: به&zwnj;ڕاستی ئێمه له&zwnj;گه&zwnj;ڵ ئێوه&zwnj;ین، بێگومان ئێمه ته&zwnj;نها گاڵته&zwnj;یان پێده&zwnj;كه&zwnj;ین و پێیان ڕاده&zwnj;بوێرین!!';
$t[15] = '(به&zwnj;ڵام خه&zwnj;یاڵیان خاوه چونكه&zwnj;) خوا سزای گاڵته&zwnj;جارییه&zwnj;كه&zwnj;یان لێده&zwnj;ستێنێت و (ده&zwnj;یانكاته په&zwnj;ندو گاڵته&zwnj;جاڕ)، ئه&zwnj;وه جارێ به&zwnj;ره&zwnj;ڵای كردوون تا كوێرانه مل بنێن و درێژه به&zwnj;گومڕایی خۆیان بده&zwnj;ن.';
$t[16] = 'ئه&zwnj;وانه گومڕاییان كڕی به&zwnj;ڕێنموویی و هیدایه&zwnj;ت، بازرگانیه&zwnj;كه&zwnj;یان قازانجی نه&zwnj;كرد، سه&zwnj;رمایه&zwnj;كه&zwnj;شیان كه هیدایه&zwnj;ته له ده&zwnj;ستیانداو مایه&zwnj;پووچ بوون و وه&zwnj;رگری ڕێبازی هیدایه&zwnj;ت نه&zwnj;بوون...';
$t[17] = 'نموونه و وێنه&zwnj;ی ئه&zwnj;وانه وه&zwnj;ك كه&zwnj;سێك وایه&zwnj;: ئاگرێك دابگیرسێنێت (بۆ سوودی خۆی و هاوڕێكانی) كه&zwnj;چی كاتێك هه&zwnj;ڵگیرساو ده&zwnj;وروبه&zwnj;ری خۆی ڕووناك كرده&zwnj;وه&zwnj;، خوا ڕووناكیه&zwnj;كه&zwnj;یان لێ بكوژێنێته&zwnj;وه و له چه&zwnj;نده&zwnj;ها تاریكیدا جێیان بهێڵێت و هیچ تروسكاییه&zwnj;ك نابینین و ڕێ ده&zwnj;رناكه&zwnj;ن.';
$t[18] = 'ئه&zwnj;وانه كه&zwnj;ڕو لاڵ و كوێرن (له ئاستی حه&zwnj;ق و ڕاستیداو)، ناشگه&zwnj;ڕێنه&zwnj;وه (بۆ سه&zwnj;ر هیدایه&zwnj;ت).';
$t[19] = 'یاخود وێنه&zwnj;یان وه&zwnj;ك كه&zwnj;سانێك وایه به ده&zwnj;ست بارانێكی لێزمه&zwnj;وه گیریان خواردبێت، كه په&zwnj;ڵه هه&zwnj;وری تاریك و هه&zwnj;وره تریشقه و بروسكه&zwnj;ی هه&zwnj;بێت، له تاو ده&zwnj;نگی بروسكه&zwnj;كه په&zwnj;نجه&zwnj;یان ده&zwnj;ئاخنن به&zwnj;گوێچكه&zwnj;یاندا له ترسی مردن، خوایش گه&zwnj;مارۆی كافرانی داوه (بارانه&zwnj;كه قورئانه و بروسكه&zwnj;كان، ترساندنی دووڕووه&zwnj;كانه كه حه&zwnj;ز به&zwnj;بیستنی ناكه&zwnj;ن).';
$t[20] = 'خه&zwnj;ریكه تیشكی بروسكه&zwnj;كه بینایی چاویان به&zwnj;رێت، هه&zwnj;ر كاتێك به&zwnj;ر پێیان ڕۆشن ده&zwnj;كاته&zwnj;وه&zwnj;، ده&zwnj;ڕۆن، هه&zwnj;ر كه تیشكه&zwnj;كه نه&zwnj;ماو تاریك بوو لێیان، (ده&zwnj;سڵه&zwnj;مێنه&zwnj;وه و) هه&zwnj;ڵوێسته ده&zwnj;كه&zwnj;ن، خۆ ئه&zwnj;گه&zwnj;ر خوا بیویستایه بیستن و بینینیانی له&zwnj;ناو ده&zwnj;برد، به&zwnj;ڕاستی خوا ده&zwnj;سه&zwnj;ڵاتی به&zwnj;سه&zwnj;ر هه&zwnj;موو شتێكدا هه&zwnj;یه&zwnj;.';
$t[21] = 'ئه&zwnj;ی خه&zwnj;ڵكینه په&zwnj;روه&zwnj;ردگاری خۆتان بپه&zwnj;رستن، ئه&zwnj;و زاته&zwnj;ی كه ئێوه و پێشینانی ئێوه&zwnj;شی به&zwnj;دی هێناوه&zwnj;، بۆ ئه&zwnj;وه&zwnj;ی ڕێبازی خواناسی و ته&zwnj;قوا و پارێزكاری بگرنه به&zwnj;ر.';
$t[22] = 'هه&zwnj;ر ئه&zwnj;وزاته زه&zwnj;وی كردووه به فه&zwnj;رش و جێگای نیشته&zwnj;جێ بوون و ژیان بۆتان، ئاسمانی وه&zwnj;كو سه&zwnj;قفێك به&zwnj;سه&zwnj;رتانه&zwnj;وه ڕاگیر كردووه&zwnj;، له ئاسمانه&zwnj;وه ئاوی بۆ باراندوون، جا به&zwnj;هۆیه&zwnj;وه جۆره&zwnj;ها به&zwnj;رهه&zwnj;می (دره&zwnj;خت و گژوگیای) به&zwnj;ده&zwnj;رهێناوه تا ببێته ڕزق و ڕۆزی بۆتان، كه&zwnj;واته شه&zwnj;ریك و هاوتا بۆ خوا بڕیار مه&zwnj;ده&zwnj;ن، كه خۆشتان ده&zwnj;زانن په&zwnj;رستراو ده&zwnj;بێت به&zwnj;دیهێنه&zwnj;ر بێت.';
$t[23] = 'جا ئه&zwnj;گه&zwnj;ر ئێوه له دوو دڵیدان و گومانتان له&zwnj;م (قورئانه پیرۆزه&zwnj;) هه&zwnj;یه كه دامانبه&zwnj;زاندووه بۆ به&zwnj;نده&zwnj;ی خۆمان محمد (صلی الله علیه وسلم) ئه&zwnj;گه&zwnj;ر بۆتان ده&zwnj;كرێت ئێوه&zwnj;ش هه&zwnj;وڵ بده&zwnj;ن سووره&zwnj;تێكی وه&zwnj;ك ئه&zwnj;و بهێنن، هاوپه&zwnj;یمان و هاوكاره&zwnj;كانیشتان جگه له خوا (بۆ ئه&zwnj;و مه&zwnj;به&zwnj;سته&zwnj;) بانگ بكه&zwnj;ن ئه&zwnj;گه&zwnj;ر ئێوه ڕاست ده&zwnj;كه&zwnj;ن.';
$t[24] = 'خۆ ئه&zwnj;گه&zwnj;ر نه&zwnj;تانتوانی و هه&zwnj;رگیز نایشتوانن (كه&zwnj;واته باوه&zwnj;ڕبهێنن و) خۆتان بپارێزن له&zwnj;و ئاگره&zwnj;ی: سووته&zwnj;مه&zwnj;نیه&zwnj;كه&zwnj;ی خڵكی و به&zwnj;رده&zwnj;، كه بۆ بێ باوه&zwnj;ڕ و خوانه&zwnj;ناسان ئاماده كراوه&zwnj;.';
$t[25] = '(ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر (صلی الله علیه وسلم) مژده بده به&zwnj;وانه&zwnj;ی كه باوه&zwnj;ڕیان هێناوه و كارو كرده&zwnj;وه چاكه&zwnj;كانیان ئه&zwnj;نجامداوه به&zwnj;وه&zwnj;ی باخه&zwnj;كانی به&zwnj;هه&zwnj;شت كه چه&zwnj;نده&zwnj;ها ڕوبار به&zwnj;ژێردره&zwnj;خت و به&zwnj;ناو باخه&zwnj;كانیدا ده&zwnj;ڕوات، به&zwnj;ڕاستی بۆیان ئاماده&zwnj;یه&zwnj;، هه&zwnj;ركاتێك له میوه&zwnj;و به&zwnj;رهه&zwnj;مێكی ئه&zwnj;و باخانه&zwnj;یان بۆ ده&zwnj;هێنن، ده&zwnj;ڵێن: ئێستا له&zwnj;وه&zwnj;تان هێنا، تاوێك له&zwnj;مه&zwnj;و پێش خواردمان، هه&zwnj;ر ده&zwnj;یهێنین و له&zwnj;یه&zwnj;كتریش ده&zwnj;چن، (له ڕه&zwnj;نگ و ڕواڵه&zwnj;تدا، به&zwnj;ڵام له&zwnj;تام و بۆندا زۆر جیاوازن)، هه&zwnj;روه&zwnj;ها له&zwnj;وێدا هاوسه&zwnj;رانی پاك و بێ گه&zwnj;ردیان بۆ ئاماده&zwnj;كراوه (ئه&zwnj;و به&zwnj;خته&zwnj;وه&zwnj;رانه&zwnj;) ژیانی هه&zwnj;میشه&zwnj;یی و نه&zwnj;بڕاوه له&zwnj;و به&zwnj;هه&zwnj;شته&zwnj;دا ده&zwnj;به&zwnj;نه سه&zwnj;ر.';
$t[26] = 'به&zwnj;ڕاستی خوا باكی نیه&zwnj;و نه&zwnj;نگ نیه به&zwnj;لایه&zwnj;وه هه&zwnj;ر نموونه&zwnj;یه&zwnj;ك بهێنێته&zwnj;وه به مێشوله&zwnj;یه&zwnj;ك، له&zwnj;ویش بچووكتر بێت یان گه&zwnj;وره&zwnj;تر، جا ئه&zwnj;وانه&zwnj;ی ئیمان و باوه&zwnj;ڕیان هێناوه&zwnj;، ئه&zwnj;وه چاك ده&zwnj;زانن ئه&zwnj;م نمونه&zwnj;یه ڕاسته و له لایه&zwnj;ن په&zwnj;روه&zwnj;ردگاریانه&zwnj;وه&zwnj;یه&zwnj;، ئه&zwnj;وانه&zwnj;ش كافرو بێ باوه&zwnj;ڕن ده&zwnj;ڵێن: خوا مه&zwnj;به&zwnj;ستی چی بوو له&zwnj;م نموونه&zwnj;یه&zwnj;دا؟ (خوای گه&zwnj;وره&zwnj;ش ده&zwnj;فه&zwnj;رموێت) زۆرێكی پێ گومڕا ده&zwnj;كات و زۆرێكی تریشی پێ هیدایه&zwnj;ت و ڕێنمووی ده&zwnj;كات، به&zwnj;ڵام دیاره جگه له لارو وێرو یاخی و تاوانباران كه&zwnj;سی تر گومڕا ناكات...';
$t[27] = 'ئه&zwnj;وانه&zwnj;ی په&zwnj;یمانی خوا (كه له ڕووی فیتره&zwnj;ت و سروشت و په&zwnj;یامه&zwnj;كانی خواوه&zwnj;) هه&zwnj;یانه&zwnj;، (كه ده&zwnj;بێت خواناس بن) هه&zwnj;ڵده&zwnj;وه&zwnj;شێننه&zwnj;وه و په&zwnj;یڕه&zwnj;وی ناكه&zwnj;ن له&zwnj;دوای به&zwnj;هێزو پته&zwnj;و بوونی، ئه&zwnj;و په&zwnj;یوه&zwnj;ندییانه&zwnj;ش ده&zwnj;پسێنن كه خوا فه&zwnj;رمانی گه&zwnj;یاندنی داوه&zwnj;، له&zwnj;زه&zwnj;ویشدا تاوان و خراپه ده&zwnj;چێنن، ئا ئه&zwnj;وانه خۆیان زه&zwnj;ره&zwnj;رمه&zwnj;ند و خه&zwnj;ساره&zwnj;تمه&zwnj;ندن.';
$t[28] = '(خه&zwnj;ڵكینه&zwnj;)، چۆن بێ بڕوا ده&zwnj;بن به خوا له&zwnj;كاتێكدا ئێوه بێ گیان بوون (له توێی خاكدا) خوا (دروستی كردوون) و گیانی به&zwnj;به&zwnj;ردا كردن، پاشان ده&zwnj;تانمرێنێت و دوایی زیندووتان ده&zwnj;كاته&zwnj;وه&zwnj;، ئه&zwnj;وسا هه&zwnj;ر بۆ لای ئه&zwnj;ویش ده&zwnj;برێنه&zwnj;وه&zwnj;.';
$t[29] = 'هه&zwnj;ر ئه&zwnj;وزاته هه&zwnj;موو شتێكی له زه&zwnj;ویدا بۆ ئێوه دروست كردووه&zwnj;، پاشان ویستی ئاسمان دروست بكات، بۆیه به حه&zwnj;وت چین به&zwnj;دی هێناو ڕێكی خستن، هه&zwnj;ر خۆیشی به هه&zwnj;موو شتێك زاناو ئاگاداره&zwnj;.';
$t[30] = '(ئه&zwnj;ی محمد (صلی الله علیه وسلم) باسی ئه&zwnj;وه بكه له دێرین زه&zwnj;ماندا) په&zwnj;روه&zwnj;ردگارت به فریشته&zwnj;كانی وت: به&zwnj;ڕاستی من ده&zwnj;مه&zwnj;وێت له زه&zwnj;ویدا جێنشینێك دابنێم (بۆ ئاوه&zwnj;دانی و دینداری)، فریشته&zwnj;كان وتیان: ئایا كه&zwnj;سێكی تیادا ده&zwnj;كه&zwnj;یته جێنشین كه خراپه&zwnj;و تاوانی تیادا بچێنێت و خوێنرێژی تێدا ئه&zwnj;نجام بدات؟! له&zwnj;كاتێكدا ئێمه ته&zwnj;سبیحات و سوپاس و ستایشت ده&zwnj;كه&zwnj;ین و قه&zwnj;درو ڕێزی تۆ چاك ده&zwnj;زانین و به&zwnj;دوورت ده&zwnj;گرین له&zwnj;و شتانه&zwnj;ی شایسته&zwnj;ی تۆ نین، (له&zwnj;وه&zwnj;ڵامیاندا) خوا فه&zwnj;رمووی: بێگومان ئه&zwnj;وه&zwnj;ی ئێوه نایزانن من ده&zwnj;یزانم.';
$t[31] = '(ئینجا ئاده&zwnj;می درووست كرد، ویستی توانایی و لێهاتووی بۆ فریشته&zwnj;كان ڕوون بكاته&zwnj;وه&zwnj;) ناوی هه&zwnj;رچی پێویستی ده&zwnj;وروبه&zwnj;ر هه&zwnj;یه فێری كرد، له&zwnj;وه&zwnj;ودوا به فریشته&zwnj;كانی ڕانواند و پێی فه&zwnj;رموون: ئاده&zwnj;ی ئێوه ناوی ئه&zwnj;و شتانه&zwnj;م پێ بڵێن ئه&zwnj;گه&zwnj;ر ڕاست ده&zwnj;كه&zwnj;ن و (توانای ئاوه&zwnj;دانكردنه&zwnj;وه&zwnj;ی زه&zwnj;ویتان هه&zwnj;یه&zwnj;؟).';
$t[32] = 'فریشته&zwnj;كان وتیان: پاكی و بێگه&zwnj;ردی و ستایش هه&zwnj;ر شایسته&zwnj;ی تۆیه&zwnj;، ئێمه هیچ زانستێكمان نیه&zwnj;، ته&zwnj;نها ئه&zwnj;وه ده&zwnj;زانین كه تۆ فێرت كردووین، به&zwnj;ڕاستی تۆ په&zwnj;روه&zwnj;ردگارێكی زاناو دانایت.';
$t[33] = 'ئه&zwnj;وسا خوا فه&zwnj;رمووی ئه&zwnj;ی ئاده&zwnj;م ئاده&zwnj;ی تۆ ئاگاداریان بكه به ناوه&zwnj;كانیان، جا كاتێك ئاده&zwnj;م هه&zwnj;واڵی ناوه&zwnj;كانی پێدان.. خوا فه&zwnj;رمووی: ئه&zwnj;ی پێم نه&zwnj;وتن به&zwnj;ڕاستی من خۆم ئاگادارم به نهێنی ئاسمانه&zwnj;كان و زه&zwnj;وی، ده&zwnj;شزانم ئێوه چی ده&zwnj;رده&zwnj;خه&zwnj;ن و چی ده&zwnj;شارنه&zwnj;وه&zwnj;؟!';
$t[34] = 'كاتێكیش به&zwnj;فریشته&zwnj;كانمان وت: سوژده به&zwnj;رن بۆ ئاده&zwnj;م (سوژده&zwnj;ی ڕێزو به&zwnj;فه&zwnj;رمانی خوا، وه&zwnj;كو ڕووگه&zwnj;یه&zwnj;ك) سوژده&zwnj;یان برد، جگه ئیبلیس نه&zwnj;بێت كه سه&zwnj;رپێچی كردو خۆی به گه&zwnj;وره&zwnj;زانی و خۆی خسته ڕیزی كافره&zwnj;كانه&zwnj;وه&zwnj;.';
$t[35] = 'ئینجا وتمان: ئه&zwnj;ی ئاده&zwnj;م خۆت و هاوسه&zwnj;رت له&zwnj;م به&zwnj;هه&zwnj;شته&zwnj;دا ژیان به&zwnj;رنه&zwnj;سه&zwnj;رو نیشته&zwnj;جێ بن له&zwnj;هه&zwnj;ر كوێ حه&zwnj;زده&zwnj;كه&zwnj;ن بخۆن و بخۆنه&zwnj;وه به&zwnj;تێرو ته&zwnj;سه&zwnj;لی، به&zwnj;ڵام نزیكی ئه&zwnj;م دره&zwnj;خته مه&zwnj;كه&zwnj;ون (دره&zwnj;ختێكی دیاری كراو بۆ تاقی كردنه&zwnj;وه لێیان قه&zwnj;ده&zwnj;غه كرا) ئه&zwnj;گینا ده&zwnj;چنه ڕیزی سته&zwnj;مكارانه&zwnj;وه&zwnj;.';
$t[36] = 'شه&zwnj;یتان (فرسه&zwnj;تی لێهێنان) و له خشته&zwnj;ی بردن (وای لێكردن له به&zwnj;رو بوومی دره&zwnj;خته قه&zwnj;ده&zwnj;غه كراوه&zwnj;كه بخۆن) و له&zwnj;و شوێنه (خۆش و پڕ له نازو نیعمه&zwnj;ته&zwnj;) به&zwnj;ده&zwnj;ركردنی دان، ئه&zwnj;وسا پێمان وتن: (ماده&zwnj;م سه&zwnj;رپێچیتان كرد ئیتر لێره جێتان نابێته&zwnj;وه&zwnj;)، ده&zwnj;بێت دابه&zwnj;زنه خواره&zwnj;وه بۆ سه&zwnj;ر زه&zwnj;وی، جا (له&zwnj;وێ) هه&zwnj;نێدكتان دوژمنی هه&zwnj;ندێكتانن (شه&zwnj;یتان دوژمنی ئێوه&zwnj;یه و به&zwnj;رده&zwnj;وام هه&zwnj;وڵ ده&zwnj;دات دوژمنایه&zwnj;تی له نێوانتاندا به&zwnj;رپا بكات)، تاماوه&zwnj;یه&zwnj;كی دیاری كراو نیشته&zwnj;جێ ده&zwnj;بن له&zwnj;زه&zwnj;ویداو له نازو نیعمه&zwnj;ته&zwnj;كان سوود وه&zwnj;رده&zwnj;گرن و به&zwnj;هره&zwnj;وه&zwnj;ر ده&zwnj;بن.';
$t[37] = 'پاشان ئاده&zwnj;م له&zwnj;لایه&zwnj;ن په&zwnj;روه&zwnj;ردگاریه&zwnj;وه چه&zwnj;ند وشه&zwnj;و نزایه&zwnj;كی وه&zwnj;رگرت (به&zwnj;دڵ و به&zwnj;كوڵ پاڕایه&zwnj;وه خوای میهره&zwnj;بانیش) نزاكه&zwnj;ی وه&zwnj;رگرت و لێی خۆش بوو، چونكه به&zwnj;ڕاستی ته&zwnj;نها ئه&zwnj;و، زاتێكی ئێجگار میهره&zwnj;بان و تۆبه وه&zwnj;رگره&zwnj;. (نزاكه&zwnj;ش له ئایه&zwnj;تی 23 ی سووره&zwnj;تی ئه&zwnj;عراف دا هاتووه&zwnj;).';
$t[38] = 'ئنجا وتمان: هه&zwnj;مووتان ده&zwnj;بێ دابه&zwnj;زن (بۆ سه&zwnj;ر زه&zwnj;وی) جا ئه&zwnj;گه&zwnj;ر هه&zwnj;ركاتێك له لایه&zwnj;ن منه&zwnj;وه ڕێنوماییتان بۆهات، هه&zwnj;ركه&zwnj;س شوێن ڕێنوومایی من بكه&zwnj;وێت، ئه&zwnj;وه نه ترس و بیمیان له&zwnj;سه&zwnj;ر ده&zwnj;بێت، نه غه&zwnj;مگین و دڵته&zwnj;نگ ده&zwnj;بن.';
$t[39] = 'ئه&zwnj;وانه&zwnj;ش بێ باوه&zwnj;ڕ بوون و باوه&zwnj;ڕیان به ئایه&zwnj;ته&zwnj;كانی ئێمه نه&zwnj;بوو، ئه&zwnj;وانه نیشته&zwnj;جێی دۆزه&zwnj;خن و بۆ هه&zwnj;تا هه&zwnj;تایی تیایدا ده&zwnj;مێننه&zwnj;وه&zwnj;.';
$t[40] = 'ئه&zwnj;ی نه&zwnj;وه&zwnj;ی ئیسرائیل (نه&zwnj;وه&zwnj;ی حه&zwnj;زره&zwnj;تی یه&zwnj;عقوب (سه&zwnj;لامی خوای له&zwnj;سه&zwnj;ر بێت) یادی نازو نیعمه&zwnj;تی من بكه&zwnj;نه&zwnj;وه كه به&zwnj;سه&zwnj;رتاندا ڕژاندومه&zwnj;و به&zwnj;وه&zwnj;فاو به&zwnj;ئه&zwnj;مه&zwnj;ك بن و په&zwnj;یمانه&zwnj;كه&zwnj;م جێبه&zwnj;جێ بكه&zwnj;ن (كه ئیمان و كرده&zwnj;وه&zwnj;ی چاك و باوه&zwnj;ڕكردنه به&zwnj;هه&zwnj;موو پێغه&zwnj;مبه&zwnj;ران به&zwnj;بێ جیاوازی) ئه&zwnj;وسا منیش په&zwnj;یمانی خۆم به&zwnj;جێ ده&zwnj;هێنم (كه جێنشین كردنتانه له دنیاداو، به&zwnj;هه&zwnj;شتی به&zwnj;رینه له قیامه&zwnj;تدا) باترس و بیمتان ته&zwnj;نها له من هه&zwnj;بێت (تا ئاسووده&zwnj;یی ڕه&zwnj;هاتان پێ ببه&zwnj;خشم)...';
$t[41] = 'هه&zwnj;روه&zwnj;ها ئیمان بهێنن به&zwnj;و قورئانه&zwnj;ی كه ناردومه&zwnj;ته خواره كه پاڵپشتی په&zwnj;یامه&zwnj;كه&zwnj;ی لای ئێوه&zwnj;یه (ته&zwnj;ورات و ئینجیلی ده&zwnj;ستكاری نه&zwnj;كراو) مه&zwnj;بنه یه&zwnj;كه&zwnj;م بێ بڕوا پێی (له جیاتی یه&zwnj;كه&zwnj;م باوه&zwnj;ڕدار) و ئایه&zwnj;ته&zwnj;كانم به نرخێكی كه&zwnj;م مه&zwnj;گۆڕنه&zwnj;وه (له پێناوی به&zwnj;ده&zwnj;ستهێنانی هه&zwnj;ندێ مه&zwnj;رامی كه&zwnj;م نرخی دنیادا)، ته&zwnj;نها له من بترسن و خۆتان له خه&zwnj;شمی من بپارێزن.';
$t[42] = 'نه&zwnj;كه&zwnj;ن ڕاستی و به&zwnj;تاڵ، حه&zwnj;ق و ناحه&zwnj;ق به&zwnj;یه&zwnj;كه&zwnj;وه بئاڵێنن و حه&zwnj;ق مه&zwnj;شارنه&zwnj;وه&zwnj;، درۆ دامه&zwnj;پۆشن به&zwnj;ڕاست له&zwnj;كاتێكدا خۆشتان حه&zwnj;ق و ڕاستی ده&zwnj;زانن كامه&zwnj;یه&zwnj;.';
$t[43] = 'نوێژه&zwnj;كانتان به&zwnj;چاكی ئه&zwnj;نجام بده&zwnj;ن به دروشمه&zwnj;كانیه&zwnj;وه&zwnj;و زه&zwnj;كاتیش بده&zwnj;ن، له&zwnj;گه&zwnj;ڵ كڕنووش به&zwnj;راندا كڕنووش ببه&zwnj;ن (بۆ په&zwnj;روه&zwnj;ردگارتان).';
$t[44] = '(كه&zwnj;ی ئه&zwnj;وه ڕاسته&zwnj;) فه&zwnj;رمانی چاكه به&zwnj;خه&zwnj;ڵكی بده&zwnj;ن و خۆتان فه&zwnj;رامۆش بكه&zwnj;ن؟! له&zwnj;كاتێكدا خۆ ئێوه كتێبی خوا ـ ته&zwnj;ورات ـ ده&zwnj;خوێننه&zwnj;وه&zwnj;، (ده&zwnj;زانن سزای گوفتاری بێ ڕه&zwnj;فتار چۆنه&zwnj;) جا ئایا ژیریتان ڕێتان لێناگرێت له&zwnj;و هه&zwnj;ڵوێسته ناشرینه&zwnj;؟!';
$t[45] = '(ئێوه هه&zwnj;وڵ بده&zwnj;ن) پشت ببه&zwnj;ستن به&zwnj;ئارامگرتن و نوێژكردن (بۆ گه&zwnj;یشتن به ئامانج) به&zwnj;ڕاستی نوێژ ئه&zwnj;ركێكی گه&zwnj;وره&zwnj;و گرانه&zwnj;، مه&zwnj;گه&zwnj;ر له&zwnj;سه&zwnj;ر ئه&zwnj;و كه&zwnj;سانه&zwnj;ی كه به خۆشه&zwnj;ویستی و سۆزه&zwnj;وه خواپه&zwnj;رستی ئه&zwnj;نجام ده&zwnj;ده&zwnj;ن و (باوه&zwnj;ڕی دامه&zwnj;زراویان هه&zwnj;یه&zwnj;)...';
$t[46] = 'ئه&zwnj;وانه&zwnj;ی ده&zwnj;زانن بێگومان به دیداری په&zwnj;روه&zwnj;ردگاریان شاد ده&zwnj;بن و باوه&zwnj;ڕی پته&zwnj;ویان هه&zwnj;یه و كه بۆ لای ئه&zwnj;و ده&zwnj;گه&zwnj;ڕێنه&zwnj;وه&zwnj;.';
$t[47] = 'ئه&zwnj;ی نه&zwnj;وه&zwnj;ی ئیسرائیل: یادی نازو نیعمه&zwnj;ته&zwnj;كانم بكه&zwnj;نه&zwnj;وه كه ڕشتومه به&zwnj;سه&zwnj;رتاندا بێگومان من گرنگی زۆرم پێداون و نازونیعمه&zwnj;تی زۆرم پێبه&zwnj;خشیوون (له&zwnj;خواردن و خواردنه&zwnj;وه له موعجیزه&zwnj;ی جۆراوجۆر، له&zwnj;چاره&zwnj;سه&zwnj;ركردنی كێشه&zwnj;كانیان،... هتد) من زیاده ڕێزی ئێوه&zwnj;م داوه به&zwnj;سه&zwnj;ر هه&zwnj;موو خه&zwnj;ڵكیدا (ئه&zwnj;ڵبه&zwnj;ته له&zwnj;سه&zwnj;رده&zwnj;می خۆیاندا و ئه&zwnj;وانه&zwnj;ی كه پابه&zwnj;ندی ئاینی خوا بوون، به تایبه&zwnj;تی پێغه&zwnj;مبه&zwnj;ران و پیاوچاكان)...';
$t[48] = 'خۆتان بپارێزن له ڕۆژێك كه كه&zwnj;س فریای كه&zwnj;س ناكه&zwnj;وێت و كه&zwnj;س هیچی له&zwnj;ده&zwnj;ست نایه&zwnj;ت و تكاكاری كه&zwnj;سیش وه&zwnj;رناگیرێت و بارمته&zwnj;و گۆڕانكاری تیادا ئه&zwnj;نجام نادرێت و ئه&zwnj;و كه&zwnj;سانه&zwnj;ی خوانه&zwnj;ناسن یارمه&zwnj;تی نادرێن و سه&zwnj;رناخرێن.';
$t[49] = 'بیری نیعمه&zwnj;تی ڕزگاربونیشتان بكه&zwnj;نه&zwnj;وه كاتێك ڕزگارمان كردن و له&zwnj;ده&zwnj;ست دارو ده&zwnj;سته&zwnj;ی فیرعه&zwnj;ون كه خراپترین سزاو ئازاریان ده&zwnj;دان، كوڕه&zwnj;كانتانیان سه&zwnj;رده&zwnj;بڕین و ئافره&zwnj;ته&zwnj;كانتانیان ده&zwnj;هێشتنه&zwnj;وه (بۆ كاره&zwnj;كه&zwnj;ری و خزمه&zwnj;ت) ئه&zwnj;وه&zwnj;ش بۆ ئێوه توشهات و تاقیكردنه&zwnj;وه&zwnj;یه&zwnj;كی گه&zwnj;وره بوو بۆتان له&zwnj;لایه&zwnj;ن په&zwnj;روه&zwnj;ردگارتانه&zwnj;وه (تا پشت به&zwnj;و زاته ببه&zwnj;ستن و بڕوای ته&zwnj;واوتان پێی هه&zwnj;بێت).';
$t[50] = 'یادی ئه&zwnj;و نیعمه&zwnj;ته بكه&zwnj;نه&zwnj;وه كه له&zwnj;به&zwnj;ر خاتری ئێوه ده&zwnj;ریاكه&zwnj;مان له&zwnj;ت كرد، ئینجا ڕزگارمان كردن، دارو ده&zwnj;سته&zwnj;ی فیرعه&zwnj;ونمان نغرۆ كرد، له&zwnj;كاتێكدا ئێوه به&zwnj;چاوی خۆتان بینیتان و ته&zwnj;ماشاتان ده&zwnj;كردن.';
$t[51] = 'یادی ئه&zwnj;و (نیعمه&zwnj;ته&zwnj;) بكه&zwnj;نه&zwnj;وه كاتێك چل شه&zwnj;و واده&zwnj;مان دانا بۆ موسا (بۆ پاڕانه&zwnj;وه&zwnj;و نزا) پاشان گوێره&zwnj;كه&zwnj;كه&zwnj;تان كرده خواتان، بێگومان ئێوه سته&zwnj;مكار بوون.';
$t[52] = 'پاشان (له&zwnj;و هه&zwnj;موو هه&zwnj;ڵانه&zwnj;تان) خۆشبووین بۆ ئه&zwnj;وه&zwnj;ی سوپاسگوزاریی بكه&zwnj;ن.';
$t[53] = 'یادی ئه&zwnj;ونیعمه&zwnj;ته بكه&zwnj;نه&zwnj;وه كاتێك كتێبی (ته&zwnj;ورات) و جیاكه&zwnj;ره&zwnj;وه&zwnj;ی (ره&zwnj;واو، ناڕه&zwnj;وامان) به&zwnj;خشی به موسا بۆ ئه&zwnj;وه&zwnj;ی ڕێبازی هیدایه&zwnj;ت بگرنه&zwnj;به&zwnj;ر (له گومڕایی ڕزگارتان ببێت).';
$t[54] = 'یادی ئه&zwnj;و نیعمه&zwnj;ته بكه&zwnj;نه&zwnj;وه كاتێك موسا به قه&zwnj;وم و هۆزی خۆی وت: ئه&zwnj;ی قه&zwnj;وم و هۆزه&zwnj;كه&zwnj;م به&zwnj;ڕاستی ئێوه به&zwnj;و گوێره&zwnj;كه په&zwnj;رستی یه سته&zwnj;متان له خۆتان كردووه&zwnj;، ده&zwnj;ی بگه&zwnj;ڕێنه&zwnj;وه بۆ لای به&zwnj;دیهێنه&zwnj;رتان و ته&zwnj;وبه بكه&zwnj;ن و ئینجا خۆتان بكوژن (بێ تاوان تاوانبان بكوژێت)، ئه&zwnj;وه باشتره بۆتان (چونكه تاوانه&zwnj;كه&zwnj;تان زۆر گه&zwnj;وره&zwnj;یه&zwnj;) پاشان ئه&zwnj;ویش (واته خوا دوای له&zwnj;ناوبردنی هه&zwnj;ندێك له تاوانباران) تۆبه&zwnj;ی وه&zwnj;رگرت (له&zwnj;وانه&zwnj;ی مابوون)، له ڕاستیدا ته&zwnj;نها ئه&zwnj;و زاته هێجگار ته&zwnj;وبه وه&zwnj;رگرو به ڕه&zwnj;حم و دلۆڤانه&zwnj;.';
$t[55] = 'یادی ئه&zwnj;و نیعمه&zwnj;ته بكه&zwnj;نه&zwnj;وه كه وتتان: ئه&zwnj;ی موسا هه&zwnj;رگیز باوه&zwnj;ڕت پێناهێنین تاوه&zwnj;كو به ئاشكراو به چاوی خۆمان خوا نه&zwnj;بینین، ئیتر یه&zwnj;كسه&zwnj;ر هه&zwnj;وره&zwnj;بروسكه&zwnj;كه لێی دان، له كاتێكدا كه ته&zwnj;ماشاتان ده&zwnj;كرد و به&zwnj;ده&zwnj;ورو به&zwnj;ری خۆتاندا ده&zwnj;تانڕوانی (به&zwnj;سه&zwnj;رسامیه&zwnj;وه بیرتان ده&zwnj;كرده&zwnj;وه&zwnj;).';
$t[56] = 'پاشان زیندوومان كردنه&zwnj;وه&zwnj;و ژیانمان پێبه&zwnj;خشینه&zwnj;وه له&zwnj;دوای ئه&zwnj;و مه&zwnj;رگه&zwnj;تان بۆ ئه&zwnj;وه&zwnj;ی سوپاسگوزاریی بكه&zwnj;ن.';
$t[57] = '(هه&zwnj;روه&zwnj;ها) له نازو نیعمه&zwnj;ته&zwnj;كانی خوا بۆتان ئه&zwnj;وه&zwnj;یه&zwnj;: ئێمه هه&zwnj;ورمان بۆ كردن به سێبه&zwnj;رو گه&zwnj;زۆمان بۆ باراندن و په&zwnj;له&zwnj;وه&zwnj;ری شه&zwnj;لاقه&zwnj;مان بۆ ناردن، ده بخۆن له&zwnj;و ڕزق و ڕۆزییه چاكانه&zwnj;ی كه پێمان به&zwnj;خشیوون (به&zwnj;ڵام له&zwnj;به&zwnj;ر بێ دینی و سته&zwnj;میان تۆڵه&zwnj;مان لێسه&zwnj;ندن)، جا وه&zwnj;نه&zwnj;بێت ئه&zwnj;وانه سته&zwnj;میان له ئێمه كرد بێت به&zwnj;ڵكو هه&zwnj;ر سته&zwnj;میان له خۆیان ده&zwnj;كرد.';
$t[58] = 'یادی ئه&zwnj;و نیعمه&zwnj;ته بكه&zwnj;نه&zwnj;وه كاتێك وتمان: بچنه ئه&zwnj;م شاره&zwnj;وه (كه قودسه&zwnj;)، جا هه&zwnj;رچی حه&zwnj;زی لێده&zwnj;كه&zwnj;ن له&zwnj;وێ له هه&zwnj;ر جێییه&zwnj;كی به&zwnj;فراوانی و خۆشی بیخۆن، له ده&zwnj;روازه&zwnj;ی شاره&zwnj;كه به&zwnj;رێزو كڕنووشی (پێزانینیه&zwnj;وه&zwnj;) بچه&zwnj;نه ژوره&zwnj;وه و بڵێن: خوایه له تاوانه&zwnj;كانمان ببووره&zwnj;، ئه&zwnj;و كاته ئێمه&zwnj;ش له هه&zwnj;ڵه و تاوانه&zwnj;كانتان ده&zwnj;بوورین، ئێمه پاداشتی چاكه&zwnj;كاران له ئاینده&zwnj;دا زۆر به زیاده&zwnj;وه ده&zwnj;ده&zwnj;ینه&zwnj;وه&zwnj;.';
$t[59] = 'كه&zwnj;چی ئه&zwnj;وانه&zwnj;ی سته&zwnj;میان كردبوو یه&zwnj;كسه&zwnj;ر گوفتاره&zwnj;كه&zwnj;یان گۆڕی به&zwnj;شتێك بێجگه له&zwnj;وه&zwnj;ی پێیان وترا بوو (واته به لووت به&zwnj;رزیه&zwnj;وه چوونه ژووره&zwnj;وه&zwnj;)، جا ئێمه&zwnj;ش له ئاسمانه&zwnj;وه سزایه&zwnj;كمان دابه&zwnj;زانده سه&zwnj;ر ئه&zwnj;وانه&zwnj;ی سته&zwnj;میان كرد به&zwnj;هۆی ئه&zwnj;وه&zwnj;ی له فه&zwnj;رمانی خوا ده&zwnj;رده&zwnj;چوون.';
$t[60] = 'یادی ئه&zwnj;و نیعمه&zwnj;ته بكه&zwnj;نه&zwnj;وه كاتێك موسا بۆ قه&zwnj;وم و گه&zwnj;له&zwnj;كه&zwnj;ی خۆی داوای بارینی بارانی كرد، ئێمه&zwnj;ش پێمان وت: به گۆچانه&zwnj;كه&zwnj;ت بده به (تاشه&zwnj;) به&zwnj;رده&zwnj;كه&zwnj;دا (جا هه&zwnj;ر ئه&zwnj;و كاره&zwnj;ی ئه&zwnj;نجامدا) به&zwnj;رده&zwnj;كه دوانزه كانی لێ هه&zwnj;ڵقوڵا، بێگومان هه&zwnj;ر هۆزێك چاوگه&zwnj;ی خۆی ناسی (ئینجا پێمان وتن) ده بخۆن و بخۆنه&zwnj;وه له ڕزق و ڕۆزی و به&zwnj;خششی خواو تاوان و خراپه مه&zwnj;كه&zwnj;ن له&zwnj;سه&zwnj;ر زه&zwnj;ویدا له كاتێكدا كه به&zwnj;چاندنی تۆوی خراپه&zwnj;وه خه&zwnj;ریكن.';
$t[61] = 'یادی ئه&zwnj;و نیعمه&zwnj;ته بكه&zwnj;نه&zwnj;وه كاتێك به موساتان وت: ئێمه هه&zwnj;رگیز له&zwnj;سه&zwnj;ر یه&zwnj;ك جۆر خۆراك ئۆقره ناگرین و به&zwnj;ته&zwnj;نها جۆرێك خوارده&zwnj;مه&zwnj;نی ڕازی نین، له&zwnj;به&zwnj;رئه&zwnj;وه داوا له په&zwnj;روه&zwnj;ردگارت بكه له ڕزق و ڕۆزی و به&zwnj;رو بوومی زه&zwnj;ویمان بۆ به&zwnj;رهه&zwnj;م بهێنێت له ڕوه&zwnj;كی پاقله&zwnj;یی وخه&zwnj;یارو سیرو نیسك و پیاز، موسا پێی وتن: باشه چۆن ئێوه شتی كه&zwnj;م و ئایه&zwnj;خ و ناته&zwnj;واو وه&zwnj;رده&zwnj;گرن له&zwnj;جیاتی شتی له&zwnj;و چاكترو باشتر؟ ده دابه&zwnj;زن بۆ شارێك (بیابانی سینا، به&zwnj;جێبهێڵن) ئینجا به&zwnj;ڕاستی ئه&zwnj;وه&zwnj;ی داوای ده&zwnj;كه&zwnj;ن ده&zwnj;ستتان ده&zwnj;كه&zwnj;وێت. (جا به&zwnj;هۆی ئه&zwnj;و بیروباوه&zwnj;ڕو بیانوو گرتن و لاسارییه&zwnj;یانه&zwnj;وه&zwnj;) سوكیی و ڕیسوایی و لاتی و زه&zwnj;بوونی درا به&zwnj;سه&zwnj;ریاندا و شایانی خه&zwnj;شم و قینی خوایش بوون، چونكه ئه&zwnj;وان به&zwnj;ڕاستی باوه&zwnj;ڕیان نه&zwnj;ده&zwnj;كرد به&zwnj;ئایه&zwnj;ته&zwnj;كانی خواو، پێغه&zwnj;مبه&zwnj;ره&zwnj;كانیان به&zwnj;ناحه&zwnj;ق ده&zwnj;كوشت، ئه&zwnj;وه&zwnj;ش به&zwnj;هۆی ڕۆچوونیانه&zwnj;وه له&zwnj;یاخی بوون و نافه&zwnj;رمانیدا، هه&zwnj;میشه ده&zwnj;ستدرێژیان ده&zwnj;كرد (ئێسته&zwnj;ش به&zwnj;رده&zwnj;وامن له&zwnj;سه&zwnj;ر به&zwnj;رنامه&zwnj;ی باوو باپیرانی خوانه&zwnj;ناسیان).';
$t[62] = 'بێگومان ئه&zwnj;وانه&zwnj;ی باوه&zwnj;ڕیان هێناوه (له ئوممه&zwnj;تی محمد (صلی الله علیه وسلم)، ئه&zwnj;وانه&zwnj;ش كه بوونه جووله&zwnj;كه (شوێنكه&zwnj;وته&zwnj;ی ڕاسته&zwnj;قینه&zwnj;ی موسان)، گاوره&zwnj;كانیش (كه شوێنكه&zwnj;وته&zwnj;ی ڕاسته&zwnj;قینه&zwnj;ی عیسان)، (ێابئه)ش كه شوێنكه&zwnj;وته&zwnj;ی ڕاسته&zwnj;قینه&zwnj;ی داودن)، ئه&zwnj;وانه هه&zwnj;ر ده&zwnj;سته&zwnj;یه&zwnj;كیان باوه&zwnj;ڕی دامه&zwnj;زراوی هێنا بێت به خواو ڕۆژی دوایی و كارو كرده&zwnj;وه&zwnj;ی چاكه&zwnj;ی ئه&zwnj;نجام دابێت، جا ئه&zwnj;وانه پاداشتیان لای په&zwnj;روه&zwnj;ردگاریانه&zwnj;، نه ترس و بیمیان ده&zwnj;بێت، نه غه&zwnj;م و په&zwnj;ژاره&zwnj;ش ڕوویان تێده&zwnj;كات.';
$t[63] = 'یادی ئه&zwnj;و نیعمه&zwnj;ته&zwnj;ش بكه&zwnj;نه&zwnj;وه كاتێك په&zwnj;یمانمان لێوه&zwnj;رگرتن (كه په&zwnj;یڕه&zwnj;وی ته&zwnj;ورات بكه&zwnj;ن) و كێوی گورمان به&zwnj;ڕاسه&zwnj;رتانه&zwnj;وه ڕاگرت و پێمان وتن: ئه&zwnj;و به&zwnj;رنامه&zwnj;یه&zwnj;ی پێمانداون به توندی بیگرن (ئه&zwnj;گینا ئه&zwnj;م چیایه ده&zwnj;ده&zwnj;ین به&zwnj;سه&zwnj;رتاندا)، هه&zwnj;میشه&zwnj;ش با له بیرتان بێت چی تێدایه&zwnj;و چیتان لێده&zwnj;خوازێت، بۆ ئه&zwnj;وه&zwnj;ی له خوا ترس و پارێزكاربن';
$t[64] = 'كه&zwnj;چی پاش ئه&zwnj;وانه هه&zwnj;مووی ڕووتان وه&zwnj;رچه&zwnj;رخاند له ڕێبازی خوا، (په&zwnj;یمانه&zwnj;كه&zwnj;تان شكاند)، ئه&zwnj;وكاته ئه&zwnj;گه&zwnj;ر به&zwnj;زه&zwnj;یی و یارمه&zwnj;تی و فه&zwnj;زڵی خوا نه&zwnj;بوایه له سه&zwnj;رتان، ئه&zwnj;وه ده&zwnj;چوونه ڕیزی خه&zwnj;ساره&zwnj;تمه&zwnj;ندانه&zwnj;وه&zwnj;.';
$t[65] = 'سوێند به خوا بێگومان زانیووتانه و ئاگادارن چیمان به&zwnj;سه&zwnj;ر ئه&zwnj;وانه&zwnj;تاندا هێنا ڕۆژانی شه&zwnj;ممه ده&zwnj;ستدرێژییان ده&zwnj;كردو له سنوور ده&zwnj;رده&zwnj;چوون، (له&zwnj;به&zwnj;ر ئه&zwnj;وه بڕیارمانداو) وتمان: ببنه مه&zwnj;یموونی دوورخراوه له ڕه&zwnj;حمه&zwnj;تی خوا (بڕیار بوو جووله&zwnj;كه&zwnj;كانی شاره&zwnj;كه&zwnj;ی قه&zwnj;راغ ده&zwnj;ریا ـ ئیلیه ـ ڕۆژانی شه&zwnj;ممه ته&zwnj;رخان بكه&zwnj;ن بۆ خواپه&zwnj;رستی و ڕاوه&zwnj;ماسی تیادانه&zwnj;كه&zwnj;ن، دیاره ماسییه&zwnj;كانیش هه&zwnj;ستیان به&zwnj;وه كردووه&zwnj;، بۆیه له&zwnj;و ڕۆژه&zwnj;دا خۆیان نواندووه&zwnj;، جوله&zwnj;كه&zwnj;ش به&zwnj;سروشت زۆر به&zwnj;ته&zwnj;ماعن، بیریان كرده&zwnj;وه له ته&zwnj;گبیرو فێڵێك، ئه&zwnj;ویش به&zwnj;وه&zwnj;ی كه له ڕۆژی هه&zwnj;ینیدا داوو تۆڕیان بۆ بنێنه&zwnj;وه تا له شه&zwnj;ممه&zwnj;دا تێی بكه&zwnj;ون، ڕۆژی یه&zwnj;ك شه&zwnj;ممه چی كه&zwnj;وتۆته داوو تۆڕه&zwnj;كانه&zwnj;وه ده&zwnj;ری بهێنن (له ته&zwnj;فسیری ئایه&zwnj;تی ١٦٣ تا ١٦٦، له سووره&zwnj;تی ـ الأعراف ـ دا ئه&zwnj;م ڕووداوه باسكراوه&zwnj;).';
$t[66] = 'ئینجا ئه&zwnj;و به مه&zwnj;یموون كردنه&zwnj;مان گێڕا به په&zwnj;ندو عیبره&zwnj;ت بۆ ئه&zwnj;وانه&zwnj;ی كه&zwnj;: هاوچه&zwnj;رخی ڕووداوه&zwnj;كه بوون و بۆ ئه&zwnj;وانه&zwnj;ش له دوای ئه&zwnj;وان دێن، هه&zwnj;روه&zwnj;ها كردمانه ئامۆژگاری بۆ خواناس و پارێزكاران.';
$t[67] = 'یادی ئه&zwnj;و نیعمه&zwnj;ته بكه&zwnj;نه&zwnj;وه كاتێك موسا به قه&zwnj;ومه&zwnj;كه&zwnj;ی وت: بێگومان خوا فه&zwnj;رمانتان پێده&zwnj;دات كه مانگایه&zwnj;ك سه&zwnj;ر ببڕن (كاتێك یه&zwnj;كێكیان لێ كوژرا بوو، بكوژه&zwnj;كه&zwnj;ش لای زۆربه&zwnj;یان نادیاربوو، هاناو هاواریان برد بۆموسا) وتیان: ئایا ده&zwnj;مانكه&zwnj;یته گاڵته جاڕی خۆت؟ موسا وتی: په&zwnj;نا ده&zwnj;گرم به&zwnj;خوا بچمه ڕیزی نه&zwnj;فامانه&zwnj;وه (گاڵته به خه&zwnj;ڵكی بكه&zwnj;م).';
$t[68] = 'ئینجا وتیان: (جا كه&zwnj;وایه&zwnj;) له په&zwnj;روه&zwnj;ردگارت داوا بكه بۆمان ڕوون بكاته&zwnj;وه ئه&zwnj;و مانگایه كامه&zwnj;یه&zwnj;؟ موسا وتی: به&zwnj;ڕاستی خوا ده&zwnj;فه&zwnj;رموێت: ئه&zwnj;و مانگایه نه پیره&zwnj;و نه پارێنه به&zwnj;ڵكو میانه ساڵه&zwnj;، (ئیتر ئه&zwnj;وه&zwnj;نده قووڵی مه&zwnj;كه&zwnj;نه&zwnj;وه&zwnj;و له&zwnj;سه&zwnj;ری مه&zwnj;رۆن) چ فه&zwnj;رمانێكتان پێدراوه جێ به&zwnj;جێی بكه&zwnj;ن.';
$t[69] = 'كه&zwnj;چی وتیان: له په&zwnj;روه&zwnj;ردگارت داوا بكه بزانین ڕه&zwnj;نگی چۆنه&zwnj;؟ موسا وتی: بێگومان خوا ده&zwnj;فه&zwnj;رموێت: ئه&zwnj;و مانگایه مانگایه&zwnj;كی ڕه&zwnj;نگ زه&zwnj;ردی تۆخه&zwnj;، ته&zwnj;ماشاكارانی دڵخۆش ده&zwnj;كات.';
$t[70] = 'كه&zwnj;چی هه&zwnj;ر وتیان: له په&zwnj;روه&zwnj;ردگارت داوا بكه تا به&zwnj;ته&zwnj;واوی بۆمان ڕوون بكاته&zwnj;وه بزانین كامه&zwnj;یه&zwnj;؟! چونكه به&zwnj;ڕاستی ئه&zwnj;م گا گه&zwnj;له سه&zwnj;ری لێ تێك داوین و ڕه&zwnj;نگی زۆربه&zwnj;یان له یه&zwnj;ك ده&zwnj;چێت، ئه&zwnj;و مانگایه&zwnj;مان بۆ نادۆزیرێته&zwnj;وه&zwnj;، به&zwnj;ڕاستی ئێمه ـ ئه&zwnj;گه&zwnj;ر خوا ویستی له&zwnj;سه&zwnj;ر بێت ـ ڕێنموویی وه&zwnj;رده&zwnj;گرین.';
$t[71] = 'موسا وتی: به&zwnj;ڕاستی خوا ده&zwnj;فه&zwnj;رموێت: ئه&zwnj;وه مانگایه&zwnj;كه هێشتا ڕانه&zwnj;هێنراوه زه&zwnj;وی بكێڵێت و جووت بكات، ئاودێری كشتوكاڵیش ناكات، بێ عه&zwnj;یبه&zwnj;و یه&zwnj;ك ڕه&zwnj;نگه&zwnj;و هیچ په&zwnj;ڵه&zwnj;یه&zwnj;كی تێدانی یه&zwnj;، ئه&zwnj;وسا وتیان: ئا ئێسته ئیتر هه&zwnj;واڵی ڕاسته&zwnj;قینه&zwnj;ت پێداین (ئیتر دوای هه&zwnj;وڵ و كۆششێكی زۆر به&zwnj;ده&zwnj;ستیان هێنا) و سه&zwnj;ریان بڕی، هه&zwnj;رچه&zwnj;نده خه&zwnj;ریك بوو ئه&zwnj;و كاره&zwnj;ش نه&zwnj;كه&zwnj;ن.';
$t[72] = '(یادی نیعمه&zwnj;تی ئاشكرا بوونی بكوژه&zwnj;كه بكه&zwnj;نه&zwnj;وه&zwnj;) چۆن كاتێك كه&zwnj;سێكتان كوشت و یه&zwnj;كسه&zwnj;ر ئاژاوه له نێوانتاندا به&zwnj;رپابوو، خوایش ئه&zwnj;وه&zwnj;ی ده&zwnj;تانشارده&zwnj;وه ده&zwnj;ریخست و ئاشكرای كرد (كه بكوژ كێیه&zwnj;).';
$t[73] = 'ئینجا وتمان: به پارچه&zwnj;یه&zwnj;ك له&zwnj;و مانگا سه&zwnj;ربڕاوه بده&zwnj;ن به لاشه&zwnj;ی كوژراوه&zwnj;كه&zwnj;دا (دیاره به&zwnj;ویستی خوا وه&zwnj;ك موعجیزه&zwnj;یه&zwnj;ك كوژراوه&zwnj;كه زیندوو بۆته&zwnj;وه&zwnj;و ڕاستی بكوژه&zwnj;كه&zwnj;ی خۆی وتووه&zwnj;)، جا ئا به&zwnj;و شێوه&zwnj;یه&zwnj;و به ئاسانی، خوا مردوان زیندوو ده&zwnj;كاته&zwnj;وه&zwnj;و ده&zwnj;سه&zwnj;ڵاتی خۆیتان نیشان ده&zwnj;دات بۆ ئه&zwnj;وه&zwnj;ی تێبگه&zwnj;ن و ژیر بن.';
$t[74] = 'پاشان له&zwnj;وه&zwnj;ودوا دڵه&zwnj;كانتان ڕه&zwnj;ق بوو، (ئه&zwnj;ی جوله&zwnj;كه&zwnj;كان) هه&zwnj;ر وه&zwnj;ك به&zwnj;رد، به&zwnj;لچكو ڕه&zwnj;قتریش (چونكه&zwnj;) به&zwnj;ڕاستی به&zwnj;رد هه&zwnj;یه ڕووباری لێ هه&zwnj;ڵده&zwnj;قوڵێت، هه&zwnj;شیانه له&zwnj;ت له&zwnj;ت ده&zwnj;بێت و ئاوی لێ دێته ده&zwnj;ره&zwnj;وه&zwnj;، هه&zwnj;شیانه له ترسی خوا له لوطكه&zwnj;ی شاخه&zwnj;كان به&zwnj;ر ده&zwnj;بێته&zwnj;وه&zwnj;، خوایش بێئاگا نیه له&zwnj;و كارو كرده&zwnj;وانه&zwnj;ی ئه&zwnj;نجامی ده&zwnj;ده&zwnj;ن.';
$t[75] = 'ئایا ئێوه (ئه&zwnj;ی گرۆی ئیمانداران) به ته&zwnj;مان و ئومێده&zwnj;وارن كه جوله&zwnj;كه ئیمان و باوه&zwnj;ڕ بهێنن به ئاینه&zwnj;كه&zwnj;ی ئێوه&zwnj;، له كاتێكدا بێگومان ده&zwnj;سته&zwnj;یه&zwnj;ك له شاره&zwnj;زاكانیان گوفتاری خوایان ده&zwnj;بیست له (ته&zwnj;ورات)دا، له&zwnj;وه&zwnj;ودوا ده&zwnj;ستكاریان ده&zwnj;كرد دوای ئه&zwnj;وه&zwnj;ی كه تێی گه&zwnj;یشتبوون و ده&zwnj;یانزانی ڕاستیه&zwnj;كه&zwnj;ی كامه&zwnj;یه&zwnj;.';
$t[76] = '(له مه&zwnj;دینه&zwnj;دا جووه&zwnj;كان) هه&zwnj;ر كاتێك تووشی ئیمانداران ده&zwnj;هاتن ده&zwnj;یانوت: ئێمه&zwnj;ش ئیمانمان هێناوه&zwnj;و (موسوڵمان بووین)، به&zwnj;ڵام كاتێك پێكه&zwnj;وه كۆ ده&zwnj;بوونه&zwnj;وه له&zwnj;گه&zwnj;ڵ (به&zwnj;ناو زاناو دڵره&zwnj;قه&zwnj;كانیان) به یه&zwnj;كتریان ده&zwnj;وت: ئه&zwnj;وه بۆ ئێوه ئه&zwnj;و شتانه&zwnj;یان بۆ باس ده&zwnj;كه&zwnj;ن كه خوا بۆ ئێوه&zwnj;ی له (ته&zwnj;ورات)دا باسكردووه (ده&zwnj;رباره&zwnj;ی پێغه&zwnj;مبه&zwnj;رایه&zwnj;تی محمد (صلی الله علیه وسلم) تاوه&zwnj;كو بیكه&zwnj;نه به&zwnj;ڵگه به&zwnj;سه&zwnj;رتانه&zwnj;وه لای په&zwnj;روه&zwnj;ردگارتان!! ئایا ئێوه عه&zwnj;قڵتان به&zwnj;و شتانه ناشكێت و ژیریتان ناخه&zwnj;نه&zwnj;كار؟!';
$t[77] = 'ئایا ئه&zwnj;وانه نازانن كه به&zwnj;ڕاستی خوا ده&zwnj;زانێت چی په&zwnj;نهان ده&zwnj;كه&zwnj;ن و چیش ئاشكرا ده&zwnj;كه&zwnj;ن؟ (له گوفتارو كردارو نیه&zwnj;تیان).';
$t[78] = 'هه&zwnj;ندێ له&zwnj;و جووله&zwnj;كانه نه&zwnj;شاره&zwnj;زا و نه&zwnj;خوێنده&zwnj;وارن، واده&zwnj;زانن كتێبی (ته&zwnj;ورات) بێجگه له چه&zwnj;ند ئاوات و هیوایه&zwnj;ك هیچی تر نیه&zwnj;، ئه&zwnj;وانه هه&zwnj;ر گومانیان كردۆته پیشه&zwnj;ی خۆیان (وا ده&zwnj;زانن به&zwnj;هه&zwnj;شتیان بۆ خۆیان مسۆگه&zwnj;ر كردووه&zwnj;).';
$t[79] = 'سا واوه&zwnj;یلا بۆ ئه&zwnj;و كه&zwnj;سانه&zwnj;ی كتێب ده&zwnj;نووسن به&zwnj;ده&zwnj;ستی خۆیان و پاشان ده&zwnj;ڵێن: ئا ئه&zwnj;مه له&zwnj;لایه&zwnj;ن خواوه&zwnj;یه&zwnj;، تا كه&zwnj;مێك له ماڵی دنیا به&zwnj;ده&zwnj;ست بهێنن، جا وه&zwnj;یل بۆ ئه&zwnj;و ده&zwnj;ست و (قه&zwnj;ڵه&zwnj;مانه&zwnj;ی) كه شتی وا ده&zwnj;نووسن و وه&zwnj;یل بۆ ئه&zwnj;و كه&zwnj;سانه&zwnj;ی كه كه&zwnj;سابه&zwnj;تی ئاوا ئه&zwnj;نجام ده&zwnj;ده&zwnj;ن.';
$t[80] = 'جووله&zwnj;كه&zwnj;كان ده&zwnj;ڵێن: (چه&zwnj;نده گوناهباربین) ته&zwnj;نها چه&zwnj;ند ڕۆژیكی كه&zwnj;م له دۆزه&zwnj;خدا ده&zwnj;مێنینه&zwnj;وه&zwnj;!! پێیان بڵێ: باشه&zwnj;، ئێوه په&zwnj;یمانتان له خوا وه&zwnj;رگرتووه&zwnj;، تا هه&zwnj;رگیز خوای گه&zwnj;وره په&zwnj;یمان شكێنی نه&zwnj;كات؟! یان هه&zwnj;ر ده&zwnj;تانه&zwnj;وێت شتێك بڵێن به&zwnj;ده&zwnj;م خواوه كه هیچ بناغه&zwnj;یه&zwnj;كی نیه&zwnj;و هه&zwnj;ر نازانن (چی ده&zwnj;ڵێن)!!';
$t[81] = 'نه&zwnj;خێر وانییه&zwnj;، ئه&zwnj;و كه&zwnj;سه&zwnj;ی گوناهو تاوان ئه&zwnj;نجام بدات و تاوانه&zwnj;كانی هه&zwnj;موولایه&zwnj;كی دابگرێت (ئاشكراو نادیاری) ئه&zwnj;و جۆره كه&zwnj;سانه نیشته&zwnj;جێی ئاگری دۆزه&zwnj;خن و بۆ هه&zwnj;میشه&zwnj;یی تیایدا ده&zwnj;مێننه&zwnj;وه (له هه&zwnj;ر نه&zwnj;ته&zwnj;وه&zwnj;و هۆزو تیره&zwnj;و تایه&zwnj;فه&zwnj;یه&zwnj;ك بن).';
$t[82] = 'ئه&zwnj;و كه&zwnj;سانه&zwnj;ش باوه&zwnj;ڕیان هێناوه&zwnj;و كرده&zwnj;وه چاكه&zwnj;كان ئه&zwnj;نجام ده&zwnj;ده&zwnj;ن، هه&zwnj;ر ئه&zwnj;وانن نیشته&zwnj;جێی به&zwnj;هه&zwnj;شتن و تیایدا ژیانی هه&zwnj;تا هه&zwnj;تایی ده&zwnj;به&zwnj;نه سه&zwnj;ر (له هه&zwnj;ر چین و توێژو ڕه&zwnj;گه&zwnj;زێك بن).';
$t[83] = 'یادی ئه&zwnj;و نیعمه&zwnj;ته بكه&zwnj;نه&zwnj;وه كاتێك په&zwnj;یمانمان وه&zwnj;رگرت له&zwnj;نه&zwnj;وه&zwnj;ی ئیسرائیل كه&zwnj;جگه له&zwnj;خوا كه&zwnj;سی تر نه&zwnj;په&zwnj;رستن و له&zwnj;گه&zwnj;ڵ دایك و باوكدا چاكه&zwnj;كار بن، هه&zwnj;روه&zwnj;ها له&zwnj;گه&zwnj;ڵ خزمان و هه&zwnj;تیوان و هه&zwnj;ژارانیشداو گوفتاری چاك و جوان ڕوو به خه&zwnj;ڵكی بڵێن، هه&zwnj;روه&zwnj;ها نوێژه&zwnj;كانتان به&zwnj;چاكی ئه&zwnj;نجام بده&zwnj;ن و زه&zwnj;كاتیش بده&zwnj;ن، پاشان ئێوه (له&zwnj;جیاتی گوێڕایه&zwnj;ڵی) پشتتان تێكرد، جگه له&zwnj;كه&zwnj;مێكتان نه&zwnj;بێت، له&zwnj;كاتێكدا زۆربه&zwnj;تان (له&zwnj;و هه&zwnj;موو ئامۆژگارییه به&zwnj;سوودانه&zwnj;) ڕوو وه&zwnj;رده&zwnj;چه&zwnj;رخێنن و پشتی تێده&zwnj;كه&zwnj;ن.';
$t[84] = 'یادیئه&zwnj;و نیعمه&zwnj;ته بكه&zwnj;نه&zwnj;وه كاتێك په&zwnj;یمانمان له ئێوه وه&zwnj;رگرت كه ئیتر خوێنی یه&zwnj;كتر (به ناحه&zwnj;ق) نه&zwnj;ڕێژن و یه&zwnj;كتر له ماڵ و حاڵی خۆتان ده&zwnj;ربه&zwnj;ده&zwnj;ر نه&zwnj;كه&zwnj;ن، پاشان بڕیاریشتان له&zwnj;سه&zwnj;ر ئه&zwnj;وه&zwnj;داو له&zwnj;كاتێكدا خۆیشتان شایه&zwnj;تی له&zwnj;سه&zwnj;ر ئه&zwnj;مه ده&zwnj;ده&zwnj;ن.';
$t[85] = 'له&zwnj;وه&zwnj;ودوا ئێوه هه&zwnj;ر ئه&zwnj;وانه&zwnj;ن (ئاماژه&zwnj;یه بۆ جووله&zwnj;كه&zwnj;كانی سه&zwnj;رده&zwnj;می پێغه&zwnj;مبه&zwnj;ر (صلی الله علیه وسلم)خه&zwnj;ریكی یه&zwnj;كتركوشتنن، ده&zwnj;سته&zwnj;یه&zwnj;ك له (خزمانی) خۆتان له ماڵ و حاڵی خۆیان ده&zwnj;ربه&zwnj;ده&zwnj;ر ده&zwnj;كه&zwnj;ن پشتی یه&zwnj;كتر ده&zwnj;گرن دژ به ده&zwnj;سته&zwnj;یه&zwnj;كتان به تاوان و دوژمنایه&zwnj;تی، كه&zwnj;چی ئه&zwnj;گه&zwnj;ر به&zwnj;دیلی ده&zwnj;ستی دوژمن بكه&zwnj;وتنایه ئه&zwnj;وا ئێوه فیدیه&zwnj;تان بۆ ده&zwnj;دان و ئازادتان ده&zwnj;كردن، له حاڵێكدا ده&zwnj;ربه&zwnj;ده&zwnj;ركردنه&zwnj;كه&zwnj;یان له&zwnj;سه&zwnj;رتان حه&zwnj;رامه&zwnj;، كه&zwnj;وابوو چۆن ده&zwnj;بێت باوه&zwnj;ڕتان به به&zwnj;شێكی (ته&zwnj;ورات) هه&zwnj;بێت و به&zwnj;شێكی تری باوه&zwnj;ڕتان نه&zwnj;بێت؟ ئایا پاداشتی ئه&zwnj;وه&zwnj;تان كه ئه&zwnj;وكاره ده&zwnj;كات جگه له&zwnj;سه&zwnj;ر شۆڕیی و سووكی نه&zwnj;بێت له ژیانی دنیادا هیچی تر هه&zwnj;یه&zwnj;؟ له ڕۆژی قیامه&zwnj;تیشدا ده&zwnj;گه&zwnj;ڕێنرێنه&zwnj;وه بۆ ناو سه&zwnj;ختترین سزا، خوایش بێ ئاگانیه له&zwnj;و كارانه&zwnj;ی كه ئێوه ئه&zwnj;نجامی ده&zwnj;ده&zwnj;ن.';
$t[86] = 'ئه&zwnj;وانه&zwnj;، ئه&zwnj;وانه&zwnj;ن كه ژیانی دنیایان كڕی و گۆڕیویانه&zwnj;ته&zwnj;وه به ژیانی (به&zwnj;هه&zwnj;شت)و دوارۆژ، له&zwnj;به&zwnj;ر ئه&zwnj;وه سزایان له&zwnj;سه&zwnj;ر كه&zwnj;م ناكرێته&zwnj;وه&zwnj;و ئه&zwnj;وانه هه&zwnj;رگیز یارمه&zwnj;تی نادرێن و سه&zwnj;رناخرێن.';
$t[87] = 'سوێند بێت بێگومان ئێمه ته&zwnj;وراتمان به&zwnj;خشی به موساو به&zwnj;رده&zwnj;وام دوای ئه&zwnj;ویش پێغه&zwnj;مبه&zwnj;رانمان ڕه&zwnj;وانه كردو به عیسای كوری مه&zwnj;ریه&zwnj;م به&zwnj;ڵگه&zwnj;و نیشانه&zwnj;و موعجیزه&zwnj;ی جۆراو جۆرمان به&zwnj;خشی هه&zwnj;روه&zwnj;ها پشتگیریمان كرد به فریشته (جبرئیل)، ئایا چۆن ده&zwnj;بێت هه&zwnj;ر كاتێك پێغه&zwnj;مبه&zwnj;رێكمان ڕه&zwnj;وانه كردبێت، به&zwnj;ڵام له&zwnj;گه&zwnj;ڵ ئاره&zwnj;زووه&zwnj;كانی ئێوه&zwnj;دا نه&zwnj;گونجا بێت، خۆتان به&zwnj;زل زانیوه&zwnj;و لووتتان به&zwnj;رز كردۆته&zwnj;وه&zwnj;و بڕواتان نه&zwnj;هێناوه&zwnj;، ئه&zwnj;وسا ده&zwnj;سته&zwnj;یه&zwnj;كتان (به&zwnj;رنامه&zwnj;ی خواتان) به درۆ زانیووه&zwnj;و هه&zwnj;ندێكیشتان پێغه&zwnj;مبه&zwnj;رانتان ده&zwnj;كوشت.';
$t[88] = 'هه&zwnj;روه&zwnj;ها (جوله&zwnj;كه&zwnj;كانی سه&zwnj;رده&zwnj;می پێغه&zwnj;مبه&zwnj;ر (صلی الله علیه وسلم) وتیان: دڵه&zwnj;كانمان داخراوه (له گوفتاری تۆ، تێناگه&zwnj;ین) نه&zwnj;خێر وانی یه&zwnj;، ئه&zwnj;وانه خوا نفرینی لێكردوون به&zwnj;هۆی كوفرو خوانه&zwnj;ناسیانه&zwnj;وه&zwnj;، كه&zwnj;وابوو به كه&zwnj;م شت بڕوا ده&zwnj;كه&zwnj;ن.';
$t[89] = 'كاتێك له لایه&zwnj;ن خواوه كتێبێك (قورئان) هات بۆیان، كه&zwnj;: ئه&zwnj;و كتێبانه&zwnj;ی ئه&zwnj;وانیش په&zwnj;سه&zwnj;ند ده&zwnj;كات و به&zwnj;ڕاستی ده&zwnj;زانێت، ئه&zwnj;وان پێشتر داوای سه&zwnj;ركه&zwnj;وتن و زاڵ بونیان به&zwnj;سه&zwnj;ر ئه&zwnj;وانه&zwnj;ی بێ باوه&zwnj;ڕ بوون له خوا ده&zwnj;كرد به هۆی شوێنكه&zwnj;وتنیان بۆ پێغه&zwnj;مبه&zwnj;ر (صلی الله علیه وسلم)، كه&zwnj;چی كاتێك ئه&zwnj;و پێغه&zwnj;مبه&zwnj;ره&zwnj;یان بۆ هات كه ده&zwnj;یانناسی، بڕوایان پێ نه&zwnj;كرد، كه&zwnj;واته با نه&zwnj;فرین و له&zwnj;عنه&zwnj;تی خوا له&zwnj;سه&zwnj;ر كافران بێت.';
$t[90] = '(ئه&zwnj;و جووله&zwnj;كانه&zwnj;) خۆیان به&zwnj;شتێكی ناپه&zwnj;سه&zwnj;ند فرۆشت (هه&zwnj;ڵوێستێكی نادروستیان نواند) كاتێك كافر بوون به&zwnj;و قورئانه&zwnj;ی خوا ناردوویه&zwnj;تی، (له حه&zwnj;سوودی)و سه&zwnj;ركه&zwnj;شیدا (ره&zwnj;خنه&zwnj;یان گرت و وتیان): بۆچی خوای گه&zwnj;وره ده&zwnj;رگای فه&zwnj;زڵ و لای ڕه&zwnj;حمه&zwnj;ت ده&zwnj;كاته&zwnj;وه له هه&zwnj;ركه&zwnj;سێك بیه&zwnj;وێت له به&zwnj;نده&zwnj;كانی!! ئه&zwnj;وسا ئیتر دووچاری یه&zwnj;ك له دوای یه&zwnj;كی ڕق و خه&zwnj;شمی خوا بوون (چونكه وه&zwnj;نه&zwnj;بێت ته&zwnj;نها كافر بوو بن به محمد (صلی الله علیه وسلم) و به&zwnj;قورئان، به&zwnj;ڵكو كافربوون به موساو ته&zwnj;وراتیش كه داوای شوێنكه&zwnj;وتنی محمدیان لێده&zwnj;كات)و بۆ كافرو خوانه&zwnj;ناسان سزایه&zwnj;كی ڕیسواكه&zwnj;ر ئاماده&zwnj;یه&zwnj;.';
$t[91] = 'كاتێك پێیان ده&zwnj;وترێت باوه&zwnj;ڕ بهێنن به&zwnj;وه&zwnj;ی كه له لایه&zwnj;ن خواوه ڕه&zwnj;وانه&zwnj;كراوه&zwnj;، ده&zwnj;ڵێن: ئێمه ته&zwnj;نها باوه&zwnj;ڕمان به&zwnj;وه هه&zwnj;یه كه بۆ ئێمه ڕه&zwnj;وانه&zwnj;كراوه و باوه&zwnj;ڕ ناهێنن به هه&zwnj;موو ئه&zwnj;وانه&zwnj;ی كه دوای (ته&zwnj;ورات) هاتووه وه&zwnj;كو (ئینجیل و قورئان) له&zwnj;كاتێكدا كه هه&zwnj;ر ئه&zwnj;و قورئانه حه&zwnj;ق و ڕاستی یه&zwnj;و به&zwnj;ڕاست دانه&zwnj;ری (ته&zwnj;ورات)ی ئه&zwnj;وانیشه&zwnj;، پێیان بڵێ: ئه&zwnj;ی كه&zwnj;واته بۆچی پێشتر پێغه&zwnj;مبه&zwnj;ره&zwnj;كانی خواتان ده&zwnj;كوشت ئه&zwnj;گه&zwnj;ر ئێوه ئیماندارن به ته&zwnj;ورات؟!';
$t[92] = 'سوێند به خوا به&zwnj;ڕاستی موسا نیشانه&zwnj;ی فره&zwnj;و موعجیزه&zwnj;ی بۆ هێنان، كه&zwnj;چی (كاتێ كه&zwnj;چوو بۆ نزای په&zwnj;روه&zwnj;ردگار، له كێوی گور) ئێوه گوێره&zwnj;كه&zwnj;كه&zwnj;تان په&zwnj;رست، به&zwnj;ڕاستی ئێوه سته&zwnj;مكارن.';
$t[93] = '(یادیان بێنه&zwnj;ره&zwnj;وه ئه&zwnj;و سه&zwnj;رده&zwnj;مه&zwnj;ی) كه په&zwnj;یمانمان لێ وه&zwnj;رگرتن و كێوی گورمان به&zwnj;سه&zwnj;ردا به&zwnj;رزكردنه&zwnj;وه و (فه&zwnj;رمانمان پێدان): ئه&zwnj;وه&zwnj;ی پێمان به&zwnj;خشیوون (له ته&zwnj;ورات) به توندی بیگرن و لێی لامه&zwnj;ده&zwnj;ن و گوێ بیستبن، (به&zwnj;ده&zwnj;م) وتیان: گوێڕایه&zwnj;ڵین، (به&zwnj;ڵام به&zwnj;كرده&zwnj;وه&zwnj;) پێچه&zwnj;وانه&zwnj;یان كردو یاخی بوون و گوێره&zwnj;كه په&zwnj;رستی له دڵ و ده&zwnj;روونیاندا ڕوابوو به هۆی بێ باوه&zwnj;ڕییانه&zwnj;وه&zwnj;، پێیان بڵێ: ئیمانه&zwnj;كه&zwnj;تان شتی ناڕه&zwnj;واتان پێ ده&zwnj;كات ئه&zwnj;گه&zwnj;ر ئێوه ئیماندارن؟! (باوو باپیرانتان گوێره&zwnj;كه په&zwnj;رست و پێغه&zwnj;مبه&zwnj;ر كوژ، ئێوه&zwnj;ش سه&zwnj;رسه&zwnj;خت و بێ باوه&zwnj;ڕ).';
$t[94] = '(ئه&zwnj;ی محمد (صلی الله علیه وسلم) پێیان) بڵێ: ئه&zwnj;گه&zwnj;ر ڕاست ده&zwnj;كه&zwnj;ن و باوه&zwnj;ڕتان وایه كه ماڵی ئه&zwnj;و دنیا (به&zwnj;هه&zwnj;شت) لای خوا تایبه&zwnj;تمه&zwnj;نده به ئێوه نه&zwnj;ك خه&zwnj;ڵكی تر، ده ئاواتی مردن بخوازن ئه&zwnj;گه&zwnj;ر ڕاست ده&zwnj;كه&zwnj;ن (تا زوو بچنه ناو به&zwnj;هه&zwnj;شت)!!';
$t[95] = '(به&zwnj;ڵام ئه&zwnj;وانه&zwnj;) هه&zwnj;رگیز به&zwnj;هۆی ڕه&zwnj;فتاری نادروست و ده&zwnj;ست پێشخه&zwnj;ری ناپه&zwnj;سه&zwnj;ندیانه&zwnj;وه ئاواتی مه&zwnj;رگ ناخوازن، خوایش چاك ئاگاداره له حاڵی سته&zwnj;مكاران.';
$t[96] = 'سوێند بێت ئه&zwnj;و جووله&zwnj;كانه ده&zwnj;بینیت كه له هه&zwnj;موو خه&zwnj;ڵك به په&zwnj;رۆشترن له&zwnj;سه&zwnj;ر دنیاو ژیانی دنیا به&zwnj;هه&zwnj;ر جۆرێك بێت، هه&zwnj;تا بگره له هاوه&zwnj;ڵپه&zwnj;رست و موشریكه&zwnj;كانیش، هه&zwnj;ریه&zwnj;ك له&zwnj;وانه ئاوات ده&zwnj;خوازێت كه&zwnj;: هه&zwnj;زار ساڵ ته&zwnj;مه&zwnj;نی ببێت، هه&zwnj;ر چه&zwnj;نده وه&zwnj;نه&zwnj;بێت ته&zwnj;مه&zwnj;نی درێژیان له سزا ڕزگاریان بكات، خوایش زۆر بینایه به&zwnj;و كرده&zwnj;وانه&zwnj;ی كه ئه&zwnj;نجامی ده&zwnj;ده&zwnj;ن.';
$t[97] = '(ئه&zwnj;ی محمد (صلی الله علیه وسلم) پێیان بڵێ: هه&zwnj;ر كه&zwnj;سێك دوژمنی جوبره&zwnj;ئیله&zwnj;، (با بزانێت) كه هه&zwnj;ر ئه&zwnj;و جوبره&zwnj;ئیله به فه&zwnj;رمانی خوا ئه&zwnj;و قورئانه&zwnj;ی دابه&zwnj;زاندۆته سه&zwnj;ر دڵت كه كتێبه پێشووه&zwnj;كان به&zwnj;ڕاست داده&zwnj;نێت و ڕێنموویی (بۆ چاكه&zwnj;) ده&zwnj;كات و مژده به&zwnj;خشیشه به ئیمانداران.';
$t[98] = 'ئه&zwnj;و كه&zwnj;سه&zwnj;ی دوژمن بێت به خواو فریشته&zwnj;كانی و پێغه&zwnj;مبه&zwnj;ره&zwnj;كانی، به&zwnj;تایبه&zwnj;ت جوبره&zwnj;ئیل و میكائیل، ئه&zwnj;وه با بزانێت و دڵنیا بێت كه خوای گه&zwnj;وره دوژمنی كافرو بێباوه&zwnj;ڕانه&zwnj;.';
$t[99] = 'سوێند بێت به خوا ئێمه بێگومان نیشانه&zwnj;و ئایه&zwnj;تی ڕوونمان بۆ تۆ ناردووه (ئه&zwnj;ی محمد (صلی الله علیه وسلم) كه&zwnj;س باوه&zwnj;ڕی پێی له&zwnj;ق نابێت، جگه له ده&zwnj;روون نه&zwnj;خۆش و یاخی بووه&zwnj;كان.';
$t[100] = 'ئه&zwnj;وه بۆ هه&zwnj;ر جارێك په&zwnj;یمانێكیان به&zwnj;ستبێت (له&zwnj;گه&zwnj;ڵ خواو پێغه&zwnj;مبه&zwnj;رو موسوڵماناندا) خێرا ده&zwnj;سته&zwnj;یه&zwnj;كیان په&zwnj;یمانه&zwnj;كه&zwnj;یان شكاندووه&zwnj;، به&zwnj;ڵكو زۆربه&zwnj;ی زۆریان باوه&zwnj;ڕ ناهێنن (ڕێزو قه&zwnj;دری په&zwnj;یمان و ئیمان ناگرن).';
$t[101] = 'كاتێكیش كه پێغه&zwnj;مبه&zwnj;رێكیان له لایه&zwnj;ن خواوه بۆ هات (مه&zwnj;به&zwnj;ست حه&zwnj;زره&zwnj;تی محمد (صلی الله علیه وسلم) له&zwnj;گه&zwnj;ڵ ئه&zwnj;وه&zwnj;شدا كه پشتگیری له ڕاستی ته&zwnj;ورات ده&zwnj;كات، ده&zwnj;سته&zwnj;یه&zwnj;ك له خاوه&zwnj;ن كتێبه&zwnj;كان (جووه&zwnj;كان) كتێبی خوایان پشتگوێ خست و خۆیان لێ گێل كرد، هه&zwnj;روه&zwnj;ك هه&zwnj;واڵی وا نه&zwnj;زانن و (نه&zwnj;یان بیستبێت).';
$t[102] = 'جا جووه&zwnj;كان (وازیان له كتێبی خوا ـ ته&zwnj;ورات ـ هێناو) شوێنی ئه&zwnj;وه كه&zwnj;وتن، كه شه&zwnj;یتانه&zwnj;كان ده&zwnj;یانخوێنده&zwnj;وه ده&zwnj;رباره&zwnj;ی ده&zwnj;سه&zwnj;ڵات و حكومه&zwnj;تی سوله&zwnj;یمان، (ئاشكرایه كه&zwnj;) هه&zwnj;رگیز سوله&zwnj;یمان كافر نه&zwnj;بووه&zwnj;، به&zwnj;ڵكو شه&zwnj;یتانه&zwnj;كان كافربوون و خه&zwnj;ڵكیان فێری سیحرو جادوو ده&zwnj;كرد، هه&zwnj;روه&zwnj;ها مه&zwnj;ردومیان فێری ئه&zwnj;وه ده&zwnj;كرد كه بۆ هاروت و ماروت دابه&zwnj;زی بوو (كه دوو فریشته بوون) له بابل (مه&zwnj;به&zwnj;ستیشیان تێگه&zwnj;یاندنی خه&zwnj;ڵكی بوو له سیحرو جادوو). ئه&zwnj;وانیش كه&zwnj;سیان فێری هیچ شتێك نه&zwnj;ده&zwnj;كرد تا پێیان نه&zwnj;وتایه&zwnj;: ئه&zwnj;م كاره&zwnj;ی ئێمه بۆ تاقی كردنه&zwnj;وه&zwnj;یه&zwnj;و خۆت كافر مه&zwnj;كه (كه&zwnj;چی هه&zwnj;ندێك هه&zwnj;ر كۆڵیان نه&zwnj;داو) له&zwnj;و فریشتانه&zwnj;وه شتی وافێر ده&zwnj;بوون كه ژن و مێردی پێ له یه&zwnj;ك جوودا بكه&zwnj;ن، (هه&zwnj;ر چه&zwnj;نده&zwnj;) ئه&zwnj;و جادووگه&zwnj;رانه ناتوانن هیچ زیانێك به كه&zwnj;س بگه&zwnj;یه&zwnj;نن، مه&zwnj;گه&zwnj;ر ویستی خوای له&zwnj;سه&zwnj;ر بێت، ئه&zwnj;و خه&zwnj;ڵكه سه&zwnj;رگه&zwnj;ردانه هه&zwnj;میشه فێری شتێك ده&zwnj;بن كه زیانیان لێده&zwnj;داو سوودی نیه بۆیان، بێگومان ده&zwnj;شزانن ئه&zwnj;وه&zwnj;ی كاڵای وای كڕی بێت و شتی وافێر ببێت له&zwnj;و دونیا هیچ به&zwnj;هره&zwnj;یه&zwnj;كی ده&zwnj;ست ناكه&zwnj;وێت، (به&zwnj;ڕاستی) ئه&zwnj;و شته&zwnj;ی ئه&zwnj;وان خۆیان پێ فرۆشتووه و كه&zwnj;وتونه&zwnj;ته داوییه&zwnj;وه شتێكی زۆر ناپه&zwnj;سه&zwnj;نده ئه&zwnj;گه&zwnj;ر بزانن و تێبگه&zwnj;ن (چونكه سیحرو جادوو چه&zwnj;واشه كاریه و كارێكی نادروسته&zwnj;).';
$t[103] = 'خۆ ئه&zwnj;گه&zwnj;ر به&zwnj;ڕاستی ئه&zwnj;و جووله&zwnj;كانه ئیمان و باوه&zwnj;ڕیان بهێنایه و خۆیان بپاڕاستایه و جوانكار بوونایه (كه بێگومان ئه&zwnj;مه&zwnj;ش نایه&zwnj;ته دی) ئه&zwnj;وا پاداشتێكیان له لایه&zwnj;ن خوای گه&zwnj;وره&zwnj;وه كه چاكترو به&zwnj;فه&zwnj;ڕتر بوو ده&zwnj;ست ده&zwnj;كه&zwnj;وت، ئه&zwnj;گه&zwnj;ر بیانزانیایه (بیرو هۆشیان به&zwnj;كار بهێنایه&zwnj;).';
$t[104] = 'ئه&zwnj;ی ئه&zwnj;وانه&zwnj;ی باوه&zwnj;ڕتان هێناوه مه&zwnj;ڵێن (راعِنا) (چونكه له زمانی عیبریدا واتاكه&zwnj;ی ناشرینه بۆ توانج و پلار به&zwnj;كار دێت، به&zwnj;ڵكو) بڵێن: (انڤرنا) چاوه&zwnj;ڕێ و سه&zwnj;یرمان بكه و به چاكی ئه&zwnj;و فه&zwnj;رمانه له&zwnj;گوێ بگرن، (چونكه&zwnj;) بۆ كافرو بێباوه&zwnj;ران سزایه&zwnj;كی به ئێشمان ئاماده كردووه (به&zwnj;تایبه&zwnj;ت ئه&zwnj;وانه&zwnj;ی گاڵته به&zwnj;دین و ئیمان و موقه&zwnj;دده&zwnj;سات ده&zwnj;كه&zwnj;ن).';
$t[105] = 'چ ئه&zwnj;وانه&zwnj;ی كه كافرن له خاوه&zwnj;ن كتێبه ئاسمانیه&zwnj;كان، چ موشریك و هاوه&zwnj;ڵگه&zwnj;ران حه&zwnj;ز ناكه&zwnj;ن له&zwnj;لایه&zwnj;ن په&zwnj;روه&zwnj;ردگارتانه&zwnj;وه هیچ خێرو به&zwnj;ره&zwnj;كه&zwnj;تێك دابارێت بۆتان و ڕوتان تێبكات، له&zwnj;گه&zwnj;ڵ ئه&zwnj;وه&zwnj;شدا خوا ڕه&zwnj;حمه&zwnj;تی خۆی تایبه&zwnj;ت ده&zwnj;كات به هه&zwnj;ر كه&zwnj;سێك (بیه&zwnj;وێت و شایسته بێت) هه&zwnj;ر خوایش خاوه&zwnj;نی فه&zwnj;زڵ و به&zwnj;خششی گه&zwnj;وره&zwnj;یه&zwnj;.';
$t[106] = 'هه&zwnj;ر ئایه&zwnj;تێكبسڕینه&zwnj;وه&zwnj;، یان بیخه&zwnj;ینه بارودۆخی فه&zwnj;رامۆشییه&zwnj;وه و پشتگوێی بخه&zwnj;ین، چاكتر له&zwnj;و، یان وێنه&zwnj;ی ئه&zwnj;و ده&zwnj;خه&zwnj;ینه ڕوو، مه&zwnj;گه&zwnj;ر نه&zwnj;تزانیوه كه خوا ده&zwnj;سه&zwnj;ڵاتی به&zwnj;سه&zwnj;ر هه&zwnj;موو شتێكدا هه&zwnj;یه&zwnj;؟';
$t[107] = 'ئایا نه&zwnj;تزانیوه (ئه&zwnj;ی محمد (صلی الله علیه وسلم)، ئه&zwnj;ی ئیماندار) كه به&zwnj;ڕاستی هه&zwnj;ر خوا خاوه&zwnj;نی هه&zwnj;موو ئاسمانه&zwnj;كان و زه&zwnj;وییه&zwnj;، جگه له&zwnj;و خوایه هیچ پشتیوان و یاریده&zwnj;ده&zwnj;رێكتان نیه&zwnj;؟';
$t[108] = '(ئه&zwnj;ی هاوه&zwnj;ڵان) ئاخۆ ئێوه&zwnj;ش ده&zwnj;تانه&zwnj;وێت داخوازی (گه&zwnj;وره&zwnj;و ناقۆڵا) له پێغه&zwnj;مبه&zwnj;ره&zwnj;كه&zwnj;تان (محمد (صلی الله علیه وسلم) بكه&zwnj;ن، هه&zwnj;ر وه&zwnj;ك كاتی خۆی داخوازی له موسا كرا، جا هه&zwnj;ر كه&zwnj;سێك ئیمان و باوه&zwnj;ڕ بگۆرێته&zwnj;وه به كوفر، ئه&zwnj;وه بێگومان ڕێبازی ڕاست و دروستی ون كردووه&zwnj;.';
$t[109] = 'زۆرێك له خاوه&zwnj;ن كتێبه&zwnj;كان ئاواته خوازن له&zwnj;به&zwnj;ر حه&zwnj;سوودی و ناپاكی خۆیان ئه&zwnj;گه&zwnj;ر بۆیان بكرێت بتانگێڕنه&zwnj;وه ڕیزی بێ باوه&zwnj;ڕان ئه&zwnj;مه&zwnj;ش دوای ئه&zwnj;وه&zwnj;ی كه حه&zwnj;ق و ڕاستییان به ته&zwnj;واوی بۆ ڕوون بۆته&zwnj;وه&zwnj;، به&zwnj;ڵام ئێوه هه&zwnj;ر لێیان ببورن و لێیان مه&zwnj;گرن و چاوپۆشییان لێ بكه&zwnj;ن، تا خوا كاری خۆی ئه&zwnj;نجام ده&zwnj;دات و ویستی خۆی جێبه&zwnj;جێ ده&zwnj;كات، بێگومان خوا ده&zwnj;سه&zwnj;ڵاتی به&zwnj;سه&zwnj;ر هه&zwnj;موو شتێكدا هه&zwnj;یه&zwnj;.';
$t[110] = '(كه&zwnj;واته ئه&zwnj;ی ئیمانداران) نوێژه&zwnj;كانتان به&zwnj;چاكی ئه&zwnj;نجام بده&zwnj;ن و زه&zwnj;كاتیش بده&zwnj;ن (به خاوه&zwnj;ن مافه&zwnj;كان) و دڵنیا بن كه هه&zwnj;ر كارێكی چاكه بۆ خۆتانی ده&zwnj;كه&zwnj;ن، لای خوا بزر نابێت و ده&zwnj;ستتان ده&zwnj;كه&zwnj;وێته&zwnj;وه چونكه به&zwnj;ڕاستی خوا بیناو به ئاگایه به&zwnj;و كارو كرده&zwnj;وانه&zwnj;ی كه ئه&zwnj;نجامی ده&zwnj;ده&zwnj;ن.';
$t[111] = '(جووله&zwnj;كه و گاوره&zwnj;كان) ده&zwnj;ڵێن: هه&zwnj;رگیز كه&zwnj;س ناچێته به&zwnj;هه&zwnj;شته&zwnj;وه بێجگه له جووله&zwnj;كه یان گاور، جا ئه&zwnj;وه ئاوات و خه&zwnj;یاڵی خۆیانه (تۆش ئه&zwnj;ی محمد (صلی الله علیه وسلم)، یان ئه&zwnj;ی ئیماندار) پێیان بڵێ: ئه&zwnj;گه&zwnj;ر ڕاستگۆن، به&zwnj;ڵگه&zwnj;تان له&zwnj;سه&zwnj;ر ئه&zwnj;م قسه&zwnj;یه بهێننه مه&zwnj;یدانه&zwnj;وه (ئه&zwnj;گه&zwnj;ر ئێوه به&zwnj;هه&zwnj;شتی بن، ئه&zwnj;وا موسوڵمانانیش به&zwnj;هه&zwnj;شتیه چونكه باوه&zwnj;ڕیان به موسا و عیسا هه&zwnj;یه&zwnj;).';
$t[112] = 'نه&zwnj;خێر وانی یه&zwnj;، هه&zwnj;ركه&zwnj;س به&zwnj;ڕاستی خۆی بداته ده&zwnj;ست خواو له&zwnj;هه&zwnj;مان كاتدا چاكه&zwnj;كارو موسوڵمان بێت، ئه&zwnj;وه پاداشتی خۆی لای په&zwnj;روه&zwnj;ردگاری ده&zwnj;ست ده&zwnj;كه&zwnj;وێت و نه&zwnj;ترسیان ده&zwnj;بێت و نه&zwnj;غه&zwnj;م و په&zwnj;ژاره دایان ده&zwnj;گرێت.';
$t[113] = 'جوله&zwnj;كه ده&zwnj;ڵێن: گاوره&zwnj;كان له&zwnj;سه&zwnj;ر هیچ بنه&zwnj;ماو ڕاستییه&zwnj;ك نین، گاوره&zwnj;كانیش ده&zwnj;ڵێن جووله&zwnj;كه له&zwnj;سه&zwnj;ر هیچ بنه&zwnj;ماو ڕاستییه&zwnj;ك نین، له كاتێكدا ئه&zwnj;وانه كتێبی (ته&zwnj;ورات و ئینجیلیش ده&zwnj;خوێننه&zwnj;وه&zwnj;) هه&zwnj;روه&zwnj;ها ئه&zwnj;وانه&zwnj;ش كه هیچ نازانن (له بتپه&zwnj;رستانی عه&zwnj;ره&zwnj;ب) هه&zwnj;روا ده&zwnj;ڵێن (بڕوایان به&zwnj;ئایینی ئیسلام و ئه&zwnj;وانه&zwnj;ش نیه&zwnj;) خوایش له ڕۆژی قیامه&zwnj;تدا دادگایی نێوانیان ده&zwnj;كات له&zwnj;وه&zwnj;ی كه ناكۆكن تیایدا.';
$t[114] = 'جا كێ له&zwnj;وه تاوانبارترو سته&zwnj;مكارتره مزگه&zwnj;وته&zwnj;كانی خوا قه&zwnj;ده&zwnj;غه ده&zwnj;كات له&zwnj;وه&zwnj;ی ناوی پیرۆزی ئه&zwnj;وی تیادا ببرێت باسی ئاینی ئه&zwnj;وی تێدا بكرێت و هه&zwnj;وڵ بدات بۆ وێران كردنیان، ئه&zwnj;وانه بۆیان نیه بچنه مزگه&zwnj;وته&zwnj;كانه&zwnj;وه مه&zwnj;گه&zwnj;ر ترسی خوایان له&zwnj;دڵدا بێت، ئه&zwnj;وانه ته&zwnj;نها سوكی و ڕیسوایی به&zwnj;شیانه له دنیاداو له قیامه&zwnj;تیشدا سزای گه&zwnj;وره&zwnj;یان بۆ هه&zwnj;یه&zwnj;.';
$t[115] = 'ڕۆژهه&zwnj;ڵات و ڕۆژئاواو (هه&zwnj;موولایه&zwnj;ك) هه&zwnj;ر خوا خاوه&zwnj;نیه&zwnj;تی، جا ڕوو بكه&zwnj;نه هه&zwnj;ر لایه&zwnj;ك خوا له&zwnj;و جێ یه ئاگای لێتانه&zwnj;، به&zwnj;ڕاستی خوا فراوانگیرو زانایه&zwnj;.';
$t[116] = '(جووله&zwnj;كه&zwnj;و گاورو بت په&zwnj;رستان) ده&zwnj;ڵێن: خوا منداڵی بۆ خۆی داناوه&zwnj;و فه&zwnj;راهه&zwnj;می هێناوه (له&zwnj;كاتێكدا خوا پێویستی به&zwnj;كه&zwnj;س نیه&zwnj;) نه&zwnj;خێر وانی یه&zwnj;، بێگومان هه&zwnj;رچی له ئاسمانه&zwnj;كان و زه&zwnj;ویدا هه&zwnj;یه هه&zwnj;ر خۆی خاوه&zwnj;نیه&zwnj;تی و هه&zwnj;ر هه&zwnj;مووشیان ملكه&zwnj;چ و فه&zwnj;رمانبه&zwnj;رداری ئه&zwnj;وزاته&zwnj;ن.';
$t[117] = '(هه&zwnj;ر ئه&zwnj;و زاته&zwnj;) به&zwnj;دیهێنه&zwnj;ری ئاسمانه&zwnj;كان و زه&zwnj;ویه به&zwnj;جوانترین شێوه&zwnj;، جا هه&zwnj;ر كاتێك بڕیاری هه&zwnj;ر شتێك بدات، ته&zwnj;نها ئه&zwnj;وه&zwnj;نده&zwnj;ی به&zwnj;سه كه فه&zwnj;رمانی بوون یان پێشهاتنی بدات، ئیتر خێرا ئه&zwnj;و شته&zwnj;، یان ئه&zwnj;و كاره&zwnj;، ده&zwnj;بێت و ئه&zwnj;نجام ده&zwnj;درێت.';
$t[118] = 'جا ئه&zwnj;وانه&zwnj;ی نه&zwnj;فام و نه&zwnj;زانن ده&zwnj;یانگوت: بۆ خوا له&zwnj;گه&zwnj;ڵ ئێمه گفتوگۆ ناكات؟! یان موعجیزه&zwnj;و نیشانه&zwnj;یه&zwnj;كمان بۆ نایه&zwnj;ت؟!! (شایانی باسه&zwnj;) كه&zwnj;سانی تریش پێش ئه&zwnj;مان هه&zwnj;مان شتیان ده&zwnj;وت، ئه&zwnj;مانه دڵه&zwnj;كانیان چوونیه&zwnj;كه&zwnj;و له&zwnj;یه&zwnj;ك ده&zwnj;چێت (شێوازی ده&zwnj;ربڕینیان جیاوازه&zwnj;)، بێگومان ئێمه چه&zwnj;ننده&zwnj;ها به&zwnj;ڵگه&zwnj;و نیشانه&zwnj;ی ئاشكراو ڕوونمان دیاری كردووه بۆ ئه&zwnj;و كه&zwnj;سانه&zwnj;ی كه ڕاستی خوازن و به شوێن حه&zwnj;قیقه&zwnj;تدا ده&zwnj;گه&zwnj;ڕێن.';
$t[119] = 'به&zwnj;ڕاستی (ئه&zwnj;ی محمد (صلی الله علیه وسلم) ئێمه تۆمان هاوڕێ له&zwnj;گه&zwnj;ڵ حه&zwnj;ق و ڕاسته&zwnj;قینه&zwnj;كاندا ڕه&zwnj;وانه كردووه تا مژده&zwnj;ده&zwnj;ر بیت به ئیمانداران و ترسێنه&zwnj;ر بیت بۆ بێ باوه&zwnj;ران، بێگومان تۆ هه&zwnj;رگیز به&zwnj;رپرسیار نیت له نیشته جێكانی دۆزه&zwnj;خ.';
$t[120] = 'هه&zwnj;رگیز جوله&zwnj;كه و گاوره&zwnj;كان له تۆ (ئه&zwnj;ی محمد (صلی الله علیه وسلم)، ئه&zwnj;ی ئیماندار) ڕازی نابن، هه&zwnj;تا به ته&zwnj;واوی شوێن ئاین و به&zwnj;رنامه&zwnj;یان نه&zwnj;كه&zwnj;ویت، پێیان بڵێ: به&zwnj;ڕاستی هه&zwnj;ر به&zwnj;رنامه&zwnj;ی خوا (ئاینی ئیسلام، شایسته&zwnj;ی ئه&zwnj;وه&zwnj;یه كه هه&zwnj;موولایه&zwnj;كمان شوێنی بكه&zwnj;وین) هیدایه&zwnj;تی ڕاست و دروست هه&zwnj;ر لای ئه&zwnj;وه&zwnj;، خۆ ئه&zwnj;گه&zwnj;ر شوێن ئاره&zwnj;زووه&zwnj;كانی ئه&zwnj;وان بكه&zwnj;ویت پاش ئه&zwnj;وه&zwnj;ی زانیاری و زانستیت پێگه&zwnj;یشتووه&zwnj;، ئه&zwnj;وه بزانه كه له لایه&zwnj;ن خواوه هیچ سه&zwnj;رپه&zwnj;رشتیاری و یارمه&zwnj;تییه&zwnj;كت ده&zwnj;ست ناكه&zwnj;وێت.';
$t[121] = 'ده&zwnj;سته&zwnj;یه&zwnj;ك له&zwnj;وانه&zwnj;ی كتێبمان پێبه&zwnj;خشیون (قورئان) به&zwnj;چاكی و به&zwnj;وردی ده&zwnj;وری ده&zwnj;كه&zwnj;نه&zwnj;وه&zwnj;، ئه&zwnj;وانه باوه&zwnj;ڕیان پێی هه&zwnj;یه&zwnj;و باوه&zwnj;ڕی پێ ده&zwnj;هێنن به&zwnj;ڵام ئه&zwnj;وه&zwnj;ی بڕوای پێی نه&zwnj;بێت، ئا ئه&zwnj;و جۆره كه&zwnj;سانه له خه&zwnj;ساره&zwnj;تمه&zwnj;ندو زه&zwnj;ره&zwnj;رمه&zwnj;نده&zwnj;كانن.';
$t[122] = 'ئه&zwnj;ی نه&zwnj;وه&zwnj;ی ئیسرائیل یادی ئه&zwnj;و نازو نیعمه&zwnj;تانه بكه&zwnj;ن كه به&zwnj;سه&zwnj;رتاندا ڕژاندوومه و من كاتی خۆی ڕێزی ئێوه&zwnj;م دابوو به&zwnj;سه&zwnj;ر هه&zwnj;موو دانیشتوانی ئه&zwnj;م جیهانه&zwnj;دا.';
$t[123] = 'خۆتان بپارێزن له ڕۆژێك كه كه&zwnj;س له جیاتی كه&zwnj;س تۆڵه&zwnj;و پاداشت ناداته&zwnj;وه&zwnj;، به هیچ شێوه&zwnj;یه&zwnj;ك كه&zwnj;س فریای كه&zwnj;س ناكه&zwnj;وێت، كه&zwnj;س بارمته&zwnj;ی لێوه&zwnj;رناگیرێت و، تكاو پاڕانه&zwnj;وه&zwnj;ی كه&zwnj;س بۆ كه&zwnj;سانی تر سوودی نابێت (یاخی و سته&zwnj;مكارانیش) سه&zwnj;ركه&zwnj;وتوونابن و سه&zwnj;رناخرێن...';
$t[124] = '(یادی ئه&zwnj;وه بكه&zwnj;نه&zwnj;وه&zwnj;) په&zwnj;روه&zwnj;ردگاری ئیبراهیم، ئیبراهیمی تاقیكرده&zwnj;وه به چه&zwnj;ند فه&zwnj;رمان و بڕیارێك، ئه&zwnj;ویش زۆر به جوانی ئه&zwnj;نجامیدان، دوایی (كه له هه&zwnj;موو تاقیكردنه&zwnj;وه&zwnj;كاندا سه&zwnj;ركه&zwnj;وت) خوا فه&zwnj;رمووی: من بڕیارمداوه بتكه&zwnj;مه پێشه&zwnj;وای خه&zwnj;ڵكی، ئیبراهیم وتی: حه&zwnj;ز ده&zwnj;كه&zwnj;م نه&zwnj;وه&zwnj;كانیشم له&zwnj;وكاره پیرۆزه بێ به&zwnj;هره نه&zwnj;بن، خوا فه&zwnj;رمووی: په&zwnj;یام و په&zwnj;یمانی من بۆ سته&zwnj;مكاران نابێت (با نه&zwnj;وه&zwnj;ی تۆش بن)';
$t[125] = '(یادی ئه&zwnj;وه&zwnj;ش بكه&zwnj;نه&zwnj;وه&zwnj;) چۆن ئه&zwnj;م ماڵه&zwnj;مان (كه&zwnj;عبه&zwnj;) كرده جێگای كۆبوونه&zwnj;وه&zwnj;ی خه&zwnj;ڵكی، كه به&zwnj;وپه&zwnj;ڕی هێمنی و ئاسایش و ئاسووده&zwnj;ییه&zwnj;وه له&zwnj;وێ به&zwnj;یه&zwnj;ك بگه&zwnj;ن (ده ئێوه&zwnj;ش، خه&zwnj;ڵكینه&zwnj;) مه&zwnj;قامی ئیبراهیم كه شوێنی تایبه&zwnj;تی خواپه&zwnj;رستی ئه&zwnj;و بوو، بیكه&zwnj;نه جێگای خواپه&zwnj;رستی و نوێژ، ئینجا فه&zwnj;رمانماندا به ئیبراهیم و ئیسماعیل كه ئه&zwnj;و ماڵه&zwnj;م خاوێن بكه&zwnj;نه&zwnj;وه (له چه&zwnj;په&zwnj;ڵی ماددی و مه&zwnj;عنه&zwnj;وی) بۆ ئه&zwnj;وانه&zwnj;ی به&zwnj;ده&zwnj;وریدا ده&zwnj;سوڕێنه&zwnj;وه&zwnj;و (طواف) ده&zwnj;كه&zwnj;ن و تیایدا ده&zwnj;مێننه&zwnj;وه&zwnj;و (اعتكاف) ده&zwnj;كه&zwnj;ن هه&zwnj;روه&zwnj;ها بۆ كڕنووشبه&zwnj;ران و سووژده&zwnj;به&zwnj;ران.';
$t[126] = '(یادی ئه&zwnj;وه بكه&zwnj;نه&zwnj;وه&zwnj;) كاتێك ئیبراهیم دۆعای كردو وتی: په&zwnj;روه&zwnj;ردگارا ده&zwnj;وروبه&zwnj;ری ئه&zwnj;م ماڵه&zwnj;ت بگێڕه به&zwnj;شارێكی پڕ له ئاسایش و ڕزق و ڕۆزی هه&zwnj;مه&zwnj;جۆر ببه&zwnj;خشه به&zwnj;دانیشتوانی، (به&zwnj;تایبه&zwnj;ت) به&zwnj;وانه&zwnj;ی باوه&zwnj;ڕیان به&zwnj;خواو ڕۆژیی دوایی هێناوه&zwnj;. (خوای گه&zwnj;وره وه&zwnj;ڵامی دایه&zwnj;وه كه دۆعات گیرایه&zwnj;و) فه&zwnj;رمووی: به&zwnj;ڵام ئه&zwnj;وه&zwnj;ش كافره بێ به&zwnj;شی ناكه&zwnj;م له به&zwnj;رهه&zwnj;م و نازو نیعمه&zwnj;تی كه&zwnj;م ته&zwnj;مه&zwnj;نی ئه&zwnj;م دنیایه&zwnj;، به&zwnj;ڵام له&zwnj;وه&zwnj;ودوا به ناچاریی تووشی سزای ئاگری ده&zwnj;كه&zwnj;م كه سه&zwnj;رئه&zwnj;نجام و چاره&zwnj;نووسێكی خراپه&zwnj;.';
$t[127] = 'به یاد بهێنن ئه&zwnj;و كاته&zwnj;ی ئیبراهیم پایه&zwnj;كانی كه&zwnj;عبه&zwnj;ی به&zwnj;رز ده&zwnj;كرده&zwnj;وه له&zwnj;گه&zwnj;ڵ ئیسماعیلدا و (ده&zwnj;یانوت): په&zwnj;روه&zwnj;ردگارا ئه&zwnj;م كاره&zwnj;مان لێ وه&zwnj;ربگره&zwnj;و له ڕیزی چاكه&zwnj;كانماندا تۆماری بكه&zwnj;، چونكه به&zwnj;ڕاستی تۆ خوایه&zwnj;كی زۆر بیسه&zwnj;رو زانایت.';
$t[128] = 'په&zwnj;روه&zwnj;ردگارا، هه&zwnj;ردووكمان وا لێ بكه ته&zwnj;نها فه&zwnj;رمانبه&zwnj;ردارو ملكه&zwnj;چی تۆبین و له نه&zwnj;وه&zwnj;كانیشمان ئوممه&zwnj;تێكی موسوڵمان و فه&zwnj;رمانبه&zwnj;ردار بۆ خۆت فه&zwnj;راهه&zwnj;م بهێنه و چۆنیه&zwnj;تی ڕێبازی به&zwnj;ندایه&zwnj;تیمان فێربكه و نیشانمان بده&zwnj;و ته&zwnj;وبه و په&zwnj;شیمانیمان لێ وه&zwnj;ربگره&zwnj;، بێگومان تۆ خوایه&zwnj;كی زۆر ته&zwnj;وبه وه&zwnj;رگرو میهره&zwnj;بان و دلۆڤانیت.';
$t[129] = 'په&zwnj;روه&zwnj;ردگارا، بۆ ئه&zwnj;و خه&zwnj;ڵكه&zwnj;ی كه له ئاینده&zwnj;دا دێن پێغه&zwnj;مبه&zwnj;رێك له خۆیان ڕه&zwnj;وانه بكه&zwnj;، تا ئایه&zwnj;ته&zwnj;كانی تۆیان به&zwnj;سه&zwnj;ردا بخوێنێته&zwnj;وه&zwnj;و فێری كتێبه پیرۆزه&zwnj;كه&zwnj;ی تۆ (واته قورئان) و داناییان بكات، تا دڵ و ده&zwnj;روونیان له ژه&zwnj;نگ و كرده&zwnj;وه&zwnj;یان له ڕه&zwnj;وشتی ناپه&zwnj;سه&zwnj;ند پاك بكاته&zwnj;وه&zwnj;، به&zwnj;ڕاستی هه&zwnj;ر تۆ خوایه&zwnj;كی باڵاده&zwnj;ست و دانایت.';
$t[130] = 'ئایا كێ له ئاینی ئیبراهیم پشت هه&zwnj;ڵ ده&zwnj;كات، جگه له&zwnj;و كه&zwnj;سه&zwnj;ی كه&zwnj;خۆی گێل و نه&zwnj;فام بكات، سوێندبێت بێگومان ئێمه ئیبراهیممان له&zwnj;م دنیایه&zwnj;دا هه&zwnj;ڵبژاردوه&zwnj;و (كردوومانه به&zwnj;پێشه&zwnj;وا) به&zwnj;ڕاستی له&zwnj;و دنیاش له ڕیزی پیاو چاكاندایه&zwnj;.';
$t[131] = 'كاتێك په&zwnj;روه&zwnj;ردگاری پێی وت: ملكه&zwnj;چ و فه&zwnj;رمانبه&zwnj;ردار به&zwnj;، (ئه&zwnj;ویش خێرا) وتی: ته&zwnj;سلیمی په&zwnj;روه&zwnj;ردگاری هه&zwnj;موو جیهان بووم (باوه&zwnj;ڕی پته&zwnj;وم پێی هه&zwnj;یه&zwnj;).';
$t[132] = 'جا ئیبراهیم و یه&zwnj;عقوب ئه&zwnj;م وه&zwnj;سێته&zwnj;یان كرد بۆ كوڕه&zwnj;كان و نه&zwnj;وه&zwnj;كانیان، (هه&zwnj;ریه&zwnj;ك له&zwnj;وان فه&zwnj;رموویه&zwnj;تی): ڕۆڵه&zwnj;كانم؛ به&zwnj;ڕاستی خوا هه&zwnj;ر ئه&zwnj;م ئاینه&zwnj;ی بۆ ئێوه بڕیارداوه&zwnj;و هه&zwnj;ڵی بژاردووه&zwnj;، ئیتر ئێوه&zwnj;ش هه&zwnj;وڵ بده&zwnj;ن به موسوڵمانی نه&zwnj;بێت مه&zwnj;مرن (تا دوا هه&zwnj;ناسه&zwnj;تان په&zwnj;یڕه&zwnj;وی بكه&zwnj;ن).';
$t[133] = 'ئایا ئێوه (ئه&zwnj;ی جووله&zwnj;كه&zwnj;و گاورانێك كه باوه&zwnj;ڕ به&zwnj;محمد ناكه&zwnj;ن و خۆشتان به&zwnj;شوێنكه&zwnj;وته&zwnj;ی ئیبراهیم و یه&zwnj;عقوب ده&zwnj;زانن) ئاماده&zwnj;ی سه&zwnj;ره&zwnj;مه&zwnj;رگی یه&zwnj;عقوب بوون؟ ئه&zwnj;و كاته&zwnj;ی به&zwnj;كوڕه&zwnj;كانی وت: دوای مردنی من چی ده&zwnj;په&zwnj;رستن؟! هه&zwnj;موو وتیان: خوای تۆ و باوو باپیرانت ده&zwnj;په&zwnj;رستین، كه ئیبراهیم و ئیسماعیل و ئیسحاقن، خوایه&zwnj;كی تاك و ته&zwnj;نهایه&zwnj;و ئێمه هه&zwnj;موو ته&zwnj;سلیم و فه&zwnj;رمانبه&zwnj;رداری ئه&zwnj;وین.';
$t[134] = 'ئه&zwnj;وانه ئوممه&zwnj;تێك بوون ڕۆشتن به&zwnj;خۆیان و كرده&zwnj;وه&zwnj;ی چاكیانه&zwnj;وه&zwnj;، ئێوه&zwnj;ش هه&zwnj;رچی ده&zwnj;یكه&zwnj;ن له چاكه بۆ خۆتانه و به&zwnj;رپرس نابن له كارو كرده&zwnj;وه&zwnj;ی ئه&zwnj;وان.';
$t[135] = '(كه&zwnj;چی هێشتا ئه&zwnj;وان به موسوڵمانان) ده&zwnj;ڵێن: وه&zwnj;رن ببنه جوله&zwnj;كه یان گاور تا بكه&zwnj;ونه سه&zwnj;ر ڕێگه&zwnj;ی ڕاست!! (ئه&zwnj;ی محمد (صلی الله علیه وسلم) پێیان) بڵێ: (نه&zwnj;خێر وانابین) به&zwnj;ڵكو شوێن ئاین و به&zwnj;رنامه&zwnj;ی ئیبراهیم ده&zwnj;كه&zwnj;وین كه پاك و دروسته&zwnj;و هه&zwnj;رگیز له ڕیزی بت په&zwnj;رستان و هاوه&zwnj;ڵگه&zwnj;راندا نه&zwnj;بووه&zwnj;.';
$t[136] = '(ئه&zwnj;ی په&zwnj;یڕه&zwnj;وانی محمد (صلی الله علیه وسلم) ئێوه&zwnj;) بڵێن: ئێمه باوه&zwnj;ڕمان به خوا هێناوه&zwnj;و به&zwnj;و قورئانه&zwnj;ش كه دابه&zwnj;زیوه بۆمان و به&zwnj;وه&zwnj;ش كه دابه&zwnj;زیوه بۆ ئیبراهیم و ئیسماعیل و ئیسحاق و یه&zwnj;عقوب و نه&zwnj;وه&zwnj;كانی یه&zwnj;عقوب، هه&zwnj;روه&zwnj;ها به&zwnj;و په&zwnj;یامه&zwnj;ی كه بۆ موساو عیسا هاتووه و به&zwnj;و په&zwnj;یامانه&zwnj;ی كه بۆ پێغه&zwnj;مبه&zwnj;ران به&zwnj;گشتی ڕه&zwnj;وانه كراوه له لایه&zwnj;ن په&zwnj;روه&zwnj;ردگاریانه&zwnj;وه و ئێمه جیاوازی ناكه&zwnj;ین له نێوان هیچ كام له&zwnj;و پێغه&zwnj;مبه&zwnj;رانه&zwnj;دا، ئێمه هه&zwnj;موومان ته&zwnj;نها ته&zwnj;سلیمی فه&zwnj;رمانی په&zwnj;روه&zwnj;ردگارین.';
$t[137] = 'جا ئه&zwnj;گه&zwnj;ر ئه&zwnj;وان (جوله&zwnj;كه&zwnj;و گاوره&zwnj;كان) باوه&zwnj;ڕیان هێنا، وه&zwnj;ك ئه&zwnj;وه&zwnj;ی ئێوه باوه&zwnj;ڕتان پێ هێناوه&zwnj;، ئه&zwnj;وه به&zwnj;ڕاستی مانای وایه ڕێبازی هیدایه&zwnj;تیان دۆزیوه&zwnj;ته&zwnj;وه&zwnj;، به&zwnj;ڵام ئه&zwnj;گه&zwnj;ر پشتیان تێكرد، ئه&zwnj;وه بێگومان ده&zwnj;كه&zwnj;ونه دژایه&zwnj;تی و دوبه&zwnj;ره&zwnj;كی یه&zwnj;وه (به&zwnj;ڵام دڵنیابه ئه&zwnj;ی محمد (صلی الله علیه وسلم)، ئه&zwnj;ی ئیماندار) خوا تۆ ده&zwnj;پارێزێت له پیلان و ته&zwnj;ڵه&zwnj;كه&zwnj;یان چونكه ئه&zwnj;و خوایه زۆر بیسه&zwnj;رو زانایه&zwnj;.';
$t[138] = '(ئه&zwnj;م ئاین و به&zwnj;رنامه&zwnj;یه&zwnj;) ئه&zwnj;و ڕه&zwnj;نگ و نه&zwnj;خشه&zwnj;یه&zwnj;یه كه خوا دایڕشتووه&zwnj;، كێ هه&zwnj;یه له خوا چاكتر ڕه&zwnj;نگ و نه&zwnj;خشه&zwnj;ی جوان بكێشێت و دایڕێژێت، له هه&zwnj;موو بارو دۆخێكدا ئێمه&zwnj;ی ئیماندار هه&zwnj;ر ئه&zwnj;و ده&zwnj;په&zwnj;رستین.';
$t[139] = '(به&zwnj;و جووله&zwnj;كانه&zwnj;) بڵێ: ئایا ڕه&zwnj;وایه ئێوه ده&zwnj;رباره&zwnj;ی ئاینی خوا قڕه&zwnj;و گاڵه&zwnj;و كێشه&zwnj;مان له&zwnj;گه&zwnj;ڵ ده&zwnj;كه&zwnj;ن (ده&zwnj;تانه&zwnj;وێت پێغه&zwnj;مبه&zwnj;رایه&zwnj;تی هه&zwnj;ر له&zwnj;ناو ئێوه&zwnj;دا بێت! له&zwnj;كاتێكدا كه قه&zwnj;دری خوایش نازانن و كوڕو هاوسه&zwnj;ری بۆ بڕیار ده&zwnj;ده&zwnj;ن!!)، خۆ خۆتان چاك ده&zwnj;زانن كه ئه&zwnj;و خوایه په&zwnj;روه&zwnj;ردگاری ئێمه و ئێوه&zwnj;شه&zwnj;، كاروكرده&zwnj;وه&zwnj;ی خۆمان بۆ خۆمان و كارو كرده&zwnj;وه&zwnj;ی ئێوه&zwnj;ش بۆ خۆتانه (ئێمه له بۆچوون و كاروكرده&zwnj;وه&zwnj;تان به&zwnj;رین)، ئێمه ته&zwnj;نها دڵسۆزو ملكه&zwnj;چی ئه&zwnj;و په&zwnj;روه&zwnj;ردگاره&zwnj;ین.';
$t[140] = 'چۆن ده&zwnj;ڵێن كه&zwnj;: ئیبراهیم و ئیسماعیل و ئیسحاق و یه&zwnj;عقوب و كوڕه&zwnj;كانی، جوله&zwnj;كه یان گاور بوون؟!! پێیان بڵێ: باشه ئایا ئێوه ده&zwnj;زانن یان خوا؟ جا كێ له&zwnj;وه سته&zwnj;مكارتره كه شایه&zwnj;تیه&zwnj;كی خوایی له&zwnj;لابێت و شاردبێتیه&zwnj;وه&zwnj;، بێگومان خوا بێ ئاگانیه له&zwnj;و كاروكرده&zwnj;وانه&zwnj;ی ئه&zwnj;نجامی ده&zwnj;ده&zwnj;ن.';
$t[141] = 'ئه&zwnj;وانه ئوممه&zwnj;تێك بوون ڕۆشتن به&zwnj;خۆیان و كرده&zwnj;وه&zwnj;ی چاكیانه&zwnj;وه&zwnj;، ئێوه&zwnj;ش هه&zwnj;رچی ده&zwnj;یكه&zwnj;ن له چاكه بۆ خۆتانه و به&zwnj;پرس نابن له كاروكرده&zwnj;وه&zwnj;ی ئه&zwnj;وان.';
$t[142] = 'نه&zwnj;فام و ده&zwnj;م به&zwnj;تاڵه&zwnj;كان له خه&zwnj;ڵكی (دوای گۆڕینی ڕووگه له قودسه&zwnj;وه بۆ كه&zwnj;عبه&zwnj;) ده&zwnj;ڵێن: باشه چی وای لێكردن كه واز له ڕووگه پێشوه&zwnj;كه بهێنن؟! كه جاران ڕوویان تێده&zwnj;كرد، پێیان بڵێ: ڕۆژهه&zwnj;ڵات و ڕۆژئاوا و (هه&zwnj;موولایه&zwnj;ك) خوا خاوه&zwnj;نیه&zwnj;تی، هه&zwnj;ر كه&zwnj;سێكی بوێت ڕێنموویی ده&zwnj;كات بۆ ڕێگه&zwnj;و ڕێبازی ڕاست و ڕه&zwnj;وان.';
$t[143] = 'به&zwnj;و شێوه&zwnj;یه&zwnj;ش ئێوه&zwnj;مان به&zwnj;گه&zwnj;لێكی میانه&zwnj;ڕه&zwnj;و گێڕاوه (له هه&zwnj;موو ڕوویه&zwnj;كه&zwnj;وه&zwnj;، له ڕووی عیباده&zwnj;ت و خواپه&zwnj;رستیه&zwnj;وه&zwnj;، له ڕووی دنیاو قیامه&zwnj;ته&zwnj;وه&zwnj;، له ڕووی داخوازیه&zwnj;كانی ڕۆح و نه&zwnj;فسه&zwnj;وه&zwnj;.. هتد) تا ببنه شایه&zwnj;ت به&zwnj;سه&zwnj;ر خه&zwnj;ڵكیه&zwnj;وه (تا بزانن ئه&zwnj;م ئاین و به&zwnj;رنامه&zwnj;یه هه&zwnj;مووی مایه&zwnj;ی خێرو سه&zwnj;ربه&zwnj;رزیه بۆ تاك و كۆمه&zwnj;ڵ له هه&zwnj;موو سه&zwnj;رده&zwnj;مێكدا و له هه&zwnj;موو بواره&zwnj;كانی ژیاندا) و بۆ ئه&zwnj;وه&zwnj;ی پێغه&zwnj;مبه&zwnj;ریش (له هه&zwnj;موو ئه&zwnj;و بوارانه&zwnj;دا مامۆستاو ڕابه&zwnj;ری ئێوه بێت و له ڕۆژی قیامه&zwnj;تدا) شایه&zwnj;تی بدات (بۆ دڵسۆزانی ئوممه&zwnj;تی له&zwnj;سه&zwnj;ر ئه&zwnj;وه&zwnj;ی به&zwnj;ئه&zwnj;ركی سه&zwnj;رشانی خۆیان هه&zwnj;ستاون) و ئه&zwnj;و قیبله&zwnj;و ڕووگه&zwnj;یه&zwnj;ش ڕووت تێكردبوو (كه بیت المقدس بوو) گۆڕانكاریشمان به&zwnj;سه&zwnj;ردا هێنا ته&zwnj;نها بۆ ئه&zwnj;وه بوو بزانین و ده&zwnj;ربكه&zwnj;وێت، كێ شوێنكه&zwnj;وته&zwnj;ی پێغه&zwnj;مبه&zwnj;ر ده&zwnj;بێت و كێش پاشه&zwnj;و پاش هه&zwnj;ڵده&zwnj;گه&zwnj;ڕێته&zwnj;وه&zwnj;، هه&zwnj;رچه&zwnj;نده (گۆڕینی قیبله&zwnj;) بوو به&zwnj;هۆی ناڕه&zwnj;حه&zwnj;تییه&zwnj;كی گه&zwnj;وره&zwnj;، مه&zwnj;گه&zwnj;ر له&zwnj;سه&zwnj;ر ئه&zwnj;وانه&zwnj;ی خوا دڵیانی دامه&zwnj;زراندبوو و ڕێنموویی كردبوون، بێگومان خوا ئیمان و باوه&zwnj;ڕداریی و (نوێژتان) زایه ناكات، چونكه به&zwnj;ڕاستی خوا بۆ خه&zwnj;ڵكی، زۆر به&zwnj;سۆزو به به&zwnj;زه&zwnj;یی یه&zwnj;.';
$t[144] = '(ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر (صلی الله علیه وسلم)) به&zwnj;ڕاستی چاوگێڕان و هه&zwnj;ڵسورانی ڕووخسارت ڕوو به&zwnj;ئاسمان ده&zwnj;بینین (كه به هیوای ڕووگه&zwnj;یه&zwnj;كی تایبه&zwnj;تیت، ئێمه&zwnj;ش ئاگاداری نیازی دڵت بووین) سوێند به&zwnj;خوا ئێمه&zwnj;ش ڕووت وه&zwnj;رده&zwnj;چه&zwnj;رخێنین بۆ ڕووگه&zwnj;یه&zwnj;ك پێی ڕازی و خۆشنوود بیت، ده ڕووت وه&zwnj;ربچه&zwnj;رخێنه به&zwnj;ره&zwnj;و لای كه&zwnj;عبه (المسجد الحرام) و ئێوه&zwnj;ش ئه&zwnj;ی موسوڵمانان له هه&zwnj;ر كوێ بوون، به&zwnj;ره&zwnj;و لای ئه&zwnj;وێ ڕووتان وه&zwnj;ربچه&zwnj;رخێنن، به&zwnj;ڕاستی ئه&zwnj;وانه&zwnj;ش كتێبیان پێ گه&zwnj;یشتووه (له گاورو جوو) چاك ده&zwnj;زانن كه (ئه&zwnj;م گۆڕانی ڕووگه&zwnj;یه&zwnj;) حه&zwnj;قیقه&zwnj;ت و ڕاستیه و فه&zwnj;رمانی په&zwnj;روه&zwnj;ردگاریانه (له ته&zwnj;ورات و ئینجیلدا ئاماژه&zwnj;ی بۆ كراوه&zwnj;) و خوا هه&zwnj;رگیز بێ ئاگا نیه له&zwnj;و كارو كرده&zwnj;وانه&zwnj;ی كه ئه&zwnj;نجامی ده&zwnj;ده&zwnj;ن.';
$t[145] = 'سوێند به&zwnj;خوا ئه&zwnj;گه&zwnj;ر بچیت بۆ لای ئه&zwnj;وانه&zwnj;ی كتێبیان بۆ هاتووه&zwnj;، به هه&zwnj;ر به&zwnj;ڵگه&zwnj;یه&zwnj;ك بته&zwnj;وێت شوێنی ڕاستی بكه&zwnj;ون و ڕووبكه&zwnj;نه ڕووگه&zwnj;ی تۆ، ڕووی تێناكه&zwnj;ن و به&zwnj;قسه&zwnj;ت ناكه&zwnj;ن، تۆش هه&zwnj;رگیز شوێنكه&zwnj;وته&zwnj;ی ڕووگه&zwnj;ی ئه&zwnj;وان نابیت، ئه&zwnj;وانیش هه&zwnj;ندێكیان شوێنكه&zwnj;وته&zwnj;ی ڕووگه&zwnj;ی هه&zwnj;ندێكی تریان نین، سوێند به&zwnj;خوا ئه&zwnj;گه&zwnj;ر تۆ شوێن هه&zwnj;واو ئاره&zwnj;زووی ئه&zwnj;وان بكه&zwnj;ویت، دوای ئه&zwnj;وه&zwnj;ی زانیاری ته&zwnj;واوت پێڕاگه&zwnj;یه&zwnj;نرا له&zwnj;م باره&zwnj;یه&zwnj;وه&zwnj;، بێگومان تۆ ده&zwnj;چیته ڕیزی سته&zwnj;مكارانه&zwnj;وه (ئه&zwnj;سته&zwnj;مه تۆ وابیت، ئه&zwnj;ی فرستاده&zwnj;ی خوا).';
$t[146] = 'ئه&zwnj;وانه&zwnj;یكتێبمان پێ به&zwnj;خشیوون پێغه&zwnj;مبه&zwnj;ر (صلی الله علیه وسلم) ده&zwnj;ناسن هه&zwnj;روه&zwnj;ك چۆن نه&zwnj;وه&zwnj;كانی خۆیان ده&zwnj;ناسن، بێگومان ده&zwnj;سته&zwnj;یه&zwnj;ك له&zwnj;وان ئه&zwnj;و ڕاستییه ده&zwnj;شارنه&zwnj;وه&zwnj;، له&zwnj;كاتێكدا ده&zwnj;شزانن محمد (صلی الله علیه وسلم) فرستاده&zwnj;ی خوایه&zwnj;.';
$t[147] = 'حه&zwnj;ق و ڕاستی هه&zwnj;ر ئه&zwnj;وه&zwnj;یه له&zwnj;لایه&zwnj;ن په&zwnj;روه&zwnj;ردگارته&zwnj;وه پێت ڕاگه&zwnj;یه&zwnj;نراوه&zwnj;، نه&zwnj;كه&zwnj;ی له ڕیزی ئه&zwnj;و كه&zwnj;سانه&zwnj;دا بیت گومان و دوودڵی (بۆ خه&zwnj;ڵكی) دروست ده&zwnj;كه&zwnj;ن.';
$t[148] = 'بۆ هه&zwnj;ر گه&zwnj;ل و میلله&zwnj;تێك ڕوگه&zwnj;و قیبله&zwnj;یه&zwnj;ك هه&zwnj;یه&zwnj;، خوا ڕوویان وه&zwnj;رده&zwnj;چه&zwnj;رخێنێت بۆ لای، (ده ئێوه&zwnj;ی موسوڵمان) پێشبڕكێ بكه&zwnj;ن له چاكه&zwnj;كاندا، ڕوو له هه&zwnj;ركوێ بن خوا هه&zwnj;مووتان كۆ ده&zwnj;كاته&zwnj;وه چونكه به&zwnj;ڕاستی ئه&zwnj;و خوایه به&zwnj;توانایه به&zwnj;سه&zwnj;ر هه&zwnj;موو شتێكدا.';
$t[149] = 'له هه&zwnj;ر شوێنێكه&zwnj;وه ده&zwnj;رچویته ده&zwnj;ره&zwnj;وه (له هه&zwnj;ركوێ بوویت له ماڵ یان سه&zwnj;فه&zwnj;ر، له&zwnj;كاتی نوێژه&zwnj;كاندا) ڕوو بكه&zwnj;ره كه&zwnj;عبه&zwnj;ی پیرۆز، بێگومان ئه&zwnj;و فه&zwnj;رمانه حه&zwnj;ق و ڕاستیه و له&zwnj;لایه&zwnj;ن په&zwnj;روه&zwnj;ردگارته&zwnj;وه&zwnj;یه&zwnj;، خوا غافل و بێ ئاگا نیه له&zwnj;و كارو كرده&zwnj;وانه&zwnj;ی كه ئه&zwnj;نجامی ده&zwnj;ده&zwnj;ن.';
$t[150] = 'له هه&zwnj;ر لایه&zwnj;كه&zwnj;وه ڕۆیشتیته ده&zwnj;ره&zwnj;وه و له هه&zwnj;ركوێ بوویت (له&zwnj;كاتی نوێژه&zwnj;كاندا) ڕوو بكه&zwnj;ره كه&zwnj;عبه&zwnj;ی پیرۆز و ئێوه&zwnj;ش (ئه&zwnj;ی موسوڵمانان) له هه&zwnj;ر كوێ بوون و هه&zwnj;ن ڕووتان وه&zwnj;رچه&zwnj;رخێنن به&zwnj;لای ئه&zwnj;ودا، تا خه&zwnj;ڵكی و میلله&zwnj;تانی تر هیچ به&zwnj;ڵگه&zwnj;یه&zwnj;كیان نه&zwnj;بێت به&zwnj;سه&zwnj;رتانه&zwnj;وه&zwnj;، مه&zwnj;گه&zwnj;ر سته&zwnj;مكارانیان (كه به&zwnj;هیچ به&zwnj;ڵگه&zwnj;یه&zwnj;ك قایل نابن و هه&zwnj;ر ئاشووب و پیلان ده&zwnj;گێڕن)، جا له&zwnj;و تاوانبارو پیلانگێڕانه مه&zwnj;ترسن، به&zwnj;ڵكو ته&zwnj;نها له من بترسن، تا نازو نیعمه&zwnj;تی خۆمتان به&zwnj;ته&zwnj;واوی به&zwnj;سه&zwnj;ردا بڕێژم (كه مانه&zwnj;وه&zwnj;یه له&zwnj;سه&zwnj;ر ڕێگه&zwnj;ی ڕاست له دنیادا و به&zwnj;هه&zwnj;شتی به&zwnj;رین و ڕه&zwnj;زامه&zwnj;ندی خوایه له دواڕۆژدا) بۆ ئه&zwnj;وه&zwnj;ی هه&zwnj;وڵ بده&zwnj;ن به&zwnj;رده&zwnj;وام ڕێبازی هیدایه&zwnj;ت بگرنه&zwnj;به&zwnj;ر.';
$t[151] = 'هه&zwnj;ر وه&zwnj;كو (بۆ ته&zwnj;واو كردنی نازو نیعمه&zwnj;تم) له نێوتاندا پێغه&zwnj;مبه&zwnj;رێكم له خۆتان بۆ ڕه&zwnj;وانه كردوون كه ئایه&zwnj;ته&zwnj;كانی منتان به&zwnj;سه&zwnj;ردا ده&zwnj;خوێنێته&zwnj;وه&zwnj;، دڵ و ده&zwnj;روون و ڕواڵه&zwnj;ت و ئاشكراتان خاوێن ده&zwnj;كاته&zwnj;وه&zwnj;، فێری قورئان و حیكمه&zwnj;ت و داناییتان ده&zwnj;كات، ئه&zwnj;و شتانه&zwnj;تان فێر ده&zwnj;كات كه هه&zwnj;رگیز خۆتان نه&zwnj;تانده&zwnj;زانی.';
$t[152] = '(ده&zwnj;ی كه&zwnj;واته&zwnj;) ئه&zwnj;ی ئیمانداران ئه&zwnj;گه&zwnj;ر یادی من بكه&zwnj;ن، منیش یادی ئێوه ده&zwnj;كه&zwnj;م و سوپاسگوزاری من بن و سوپاس ناپه&zwnj;زێرو سپڵه&zwnj;یی به&zwnj;رامبه&zwnj;رم مه&zwnj;كه&zwnj;ن.';
$t[153] = 'ئه&zwnj;ی ئه&zwnj;وانه&zwnj;ی ئیمان و باوه&zwnj;ڕتان هێناوه داوای كۆمه&zwnj;ك و یارمه&zwnj;تی له خوا بكه&zwnj;ن به&zwnj;هۆی ئارامگرتن و نوێژ كردنه&zwnj;وو، دڵنیابن كه خوا یارو یاوه&zwnj;ری خۆگرو ئارامگرانه&zwnj;.';
$t[154] = 'جا هه&zwnj;رگیز به&zwnj;و كه&zwnj;سانه&zwnj;ی له ڕێگه&zwnj;ی خوادا ده&zwnj;كوژرێن و شه&zwnj;هید ده&zwnj;بن، مه&zwnj;ڵێن: مردوون، نه&zwnj;خێر ئه&zwnj;وانه زیندوون و به&zwnj;ڵام ئێوه هه&zwnj;ست به زیندوویه&zwnj;تی ئه&zwnj;وان ناكه&zwnj;ن.';
$t[155] = 'سوێند به&zwnj;خوا تاقیتان ده&zwnj;كه&zwnj;ینه&zwnj;وه به كه&zwnj;مێك له ترس و بیم و برسێتی و كه&zwnj;م بوونی ماڵ و سامان و مردنی كه&zwnj;س و كارو تیاچوونی به&zwnj;رو بوومی كشتوكاڵ، جا مژده بده به&zwnj;و كه&zwnj;سانه&zwnj;ی خۆگرو ئارامگرن...';
$t[156] = 'كه ئه&zwnj;وانه&zwnj;ن هه&zwnj;ر كاتێك تووشی به&zwnj;ڵاو ناخۆشیه&zwnj;ك ده&zwnj;بن ده&zwnj;ڵێن: ئێمه موڵكی خواین و هه&zwnj;رچی هه&zwnj;مانه به&zwnj;خششی خوایه&zwnj;، سه&zwnj;ر ئه&zwnj;نجامیش ته&zwnj;نها هه&zwnj;ر بۆ لای ئه&zwnj;و زاته&zwnj;یه گه&zwnj;ڕانه&zwnj;وه&zwnj;مان.';
$t[157] = 'ئا ئه&zwnj;وانه چه&zwnj;نده&zwnj;ها ستایش و ڕه&zwnj;حمه&zwnj;ت و میهره&zwnj;بانی هه&zwnj;ر به&zwnj;سه&zwnj;ر ئه&zwnj;واندا ده&zwnj;بارێت له&zwnj;لایه&zwnj;ن په&zwnj;روه&zwnj;ردگاریانه&zwnj;وه و ئه&zwnj;وانه ئه&zwnj;و كه&zwnj;سانه&zwnj;ن ڕێبازی هیدایه&zwnj;تیان وه&zwnj;رگرتووه&zwnj;.';
$t[158] = 'به&zwnj;ڕاستی سه&zwnj;فا و مه&zwnj;روه (كه دوو جێگه&zwnj;ن له پاڵ كه&zwnj;عبه&zwnj;دا) له دروشمه&zwnj;كانی خوای په&zwnj;روه&zwnj;ردگارن، جا ئه&zwnj;وه&zwnj;ی حه&zwnj;جی ماڵی خوا ده&zwnj;كات، یاخود عه&zwnj;مره ئه&zwnj;نجام ده&zwnj;دات (كه له هه&zwnj;موو كاتێكی ساڵدا ده&zwnj;كرێت) ئه&zwnj;وه هیچ گوناهێكی له&zwnj;سه&zwnj;ر نییه ئه&zwnj;گه&zwnj;ر هاتوچۆو بكات له نێوانیاندا، ئه&zwnj;وه&zwnj;ش خۆبه&zwnj;خشی ده&zwnj;كات و (عه&zwnj;مره و ته&zwnj;واف)ی زیاتر ده&zwnj;كات با بزانێت كه خوا سوپاسگوزارو زانایه&zwnj;.';
$t[159] = 'به&zwnj;ڕاستی ئه&zwnj;وانه&zwnj;ی كه به&zwnj;رنامه و ڕێنمووییه&zwnj;كانی ئاشكرای ڕه&zwnj;وانه كراوی ئێمه ده&zwnj;شارنه&zwnj;وه كه ناردوومانه&zwnj;، دوای ئه&zwnj;وه&zwnj;ی بۆ خه&zwnj;ڵكیمان ڕوون كردۆته&zwnj;وه له كتێبه&zwnj;كاندا، ئا ئه&zwnj;وانه خوا نه&zwnj;فرینیان لێده&zwnj;كات و نه&zwnj;فرین كارانیش نه&zwnj;فره&zwnj;تیان لێده&zwnj;كه&zwnj;ن...';
$t[160] = 'جگه له&zwnj;وانه&zwnj;ی كه ته&zwnj;وبه&zwnj;یان كردووه و چاكسازیان ئه&zwnj;نجامداوه&zwnj;و ئه&zwnj;و حه&zwnj;قیقه&zwnj;ت و ڕاستیان ڕوون كردۆته&zwnj;وه كه جاران شاردبوویانه&zwnj;وه&zwnj;، ئا ئه&zwnj;وانه گه&zwnj;ڕانه&zwnj;وه&zwnj;كه&zwnj;یان لێ وه&zwnj;رده&zwnj;گرم و ته&zwnj;نها منیشم زۆر ته&zwnj;وبه وه&zwnj;رگرو دلۆڤان و میهره&zwnj;بانم.';
$t[161] = 'به&zwnj;ڕاستی ئه&zwnj;وانه&zwnj;ی كه بێ باوه&zwnj;ڕ بوون و به بێ باوه&zwnj;ڕی مردوون، ئا ئه&zwnj;وانه له&zwnj;عنه&zwnj;ت و نه&zwnj;فرینی خواو فریشته&zwnj;كان و هه&zwnj;موو خه&zwnj;ڵكیان به&zwnj;گشتی به&zwnj;سه&zwnj;ردا داده&zwnj;بارێت...';
$t[162] = 'ئه&zwnj;وانه به&zwnj;و نه&zwnj;فرینه&zwnj;وه له ئاگری دۆزه&zwnj;خدا بۆ هه&zwnj;میشه ده&zwnj;مێننه&zwnj;وه و ئه&zwnj;و سزایه&zwnj;یان له&zwnj;سه&zwnj;ر سووك ناكرێت و به چاوی سۆزه&zwnj;وه ته&zwnj;ماشا ناكرێن.';
$t[163] = 'خوای ئێوه خوایه&zwnj;كی تاك و ته&zwnj;نهایه&zwnj;، جگه له&zwnj;و زاته خوایه&zwnj;كی تر نیه&zwnj;، هه&zwnj;ر ئه&zwnj;و خوایه&zwnj;ش به&zwnj;خشنده&zwnj;و میهره&zwnj;بان و به&zwnj;به&zwnj;زه&zwnj;ییه&zwnj;.';
$t[164] = 'به&zwnj;ڕاستی له دروستكردنی ئاسمانه&zwnj;كان و زه&zwnj;وی و ئاڵو گۆڕی شه&zwnj;وو ڕۆژدا و له&zwnj;و كه&zwnj;شتیانه&zwnj;دا كه ده&zwnj;گه&zwnj;ڕێن به ده&zwnj;ریادا و به&zwnj;هۆیه&zwnj;وه سوود به خه&zwnj;ڵكی ده&zwnj;گه&zwnj;یه&zwnj;نن و له&zwnj;و ئاوه&zwnj;ی كه خوا له ئاسمانه&zwnj;وه ده&zwnj;یبارێنێت و ئینجا به&zwnj;هۆیه&zwnj;وه زه&zwnj;وی پێ زیندوو ده&zwnj;كاته&zwnj;وه دوای وشك بوون و مردنی، هه&zwnj;روه&zwnj;ها جۆره&zwnj;ها زینده&zwnj;وه&zwnj;ر و گیانله&zwnj;به&zwnj;ر و مارو مێرووی له&zwnj;سه&zwnj;ر ڕووكاری زه&zwnj;ویدا بڵاو كردۆته&zwnj;وه و گۆڕاندن و هاتووچۆ پێكردنی باو شه&zwnj;ماڵ و ڕاگیر كردنی هه&zwnj;وره&zwnj;كان له نێوان ئاسمان و زه&zwnj;ویدا، ئه&zwnj;مانه هه&zwnj;موو به&zwnj;ڵگه&zwnj;و نیشانه&zwnj;ی به&zwnj;هێزن بۆ ئه&zwnj;وانه&zwnj;ی كه عه&zwnj;قڵ و بیروهۆشیان ده&zwnj;خه&zwnj;نه&zwnj;كارو بیر له ده&zwnj;سه&zwnj;ڵاتی بێ سنووری خوا ده&zwnj;كه&zwnj;نه&zwnj;وه&zwnj;.';
$t[165] = '(له&zwnj;گه&zwnj;ڵ ئه&zwnj;و هه&zwnj;موو به&zwnj;ڵگه ئاشكرایانه&zwnj;دا) هه&zwnj;ندێك كه&zwnj;س هه&zwnj;یه له&zwnj;جیاتی ئه&zwnj;وه&zwnj;ی خواپه&zwnj;رست بێت، شتانی تر داده&zwnj;تاشێت بۆ خۆی و (شوێن ڕێنمووییه&zwnj;كانی خوا ناكه&zwnj;وێت و) خۆشه&zwnj;ویستی و سۆزی ده&zwnj;دات به&zwnj;و بت و به&zwnj;رنامۆلكه&zwnj;و تووله ڕێگه&zwnj;یانه و خۆشی ده&zwnj;وێن، وه&zwnj;ك چۆن ئیمانداران خوایان خۆش ده&zwnj;وێت، به&zwnj;ڵام ئه&zwnj;وانه&zwnj;ی ئیمان و باوه&zwnj;ڕیان هێناوه زۆر زیاتر خوایان خۆش ده&zwnj;وێت (هه&zwnj;ر به&zwnj;راورد ناكرێت) جا ئه&zwnj;وانه&zwnj;ی كه سته&zwnj;میان كردووه گه&zwnj;ر ده&zwnj;یانزانی له كاتێكدا كه به&zwnj;چاوی خۆیان ڕووبه&zwnj;ڕووی ئه&zwnj;و سزایه ده&zwnj;بنه&zwnj;وه&zwnj;، كه به&zwnj;ڕاستی بۆیان ده&zwnj;رده&zwnj;كه&zwnj;وێت كه هێزو تواناو ده&zwnj;سه&zwnj;ڵات هه&zwnj;ر هه&zwnj;مووی به ده&zwnj;ست خوایه و دڵنیا ده&zwnj;بوون كه ئه&zwnj;و خوایه سزاكه&zwnj;ی زۆر به ئازاره (بۆ سته&zwnj;مكاران).';
$t[166] = 'له&zwnj;و كاته&zwnj;دا سزای ڕۆژی دوایی ده&zwnj;بینن، ئه&zwnj;وانه&zwnj;ی كه خه&zwnj;ڵكی شوێنیان كه&zwnj;وتبوون خۆیان به&zwnj;ریی ده&zwnj;كه&zwnj;ن و له شوێنكه&zwnj;وته&zwnj;كانیان حاشا ده&zwnj;كه&zwnj;ن، به&zwnj;تایبه&zwnj;ت كاتێك ئه&zwnj;و سزا توندو تیژه ده&zwnj;بینن، هه&zwnj;موو په&zwnj;یوه&zwnj;ندیه&zwnj;كان ده&zwnj;بچڕێت كه له نێوانیاندا هه&zwnj;بوو و هیچ به&zwnj;ڵگه&zwnj;و بیانویه&zwnj;كیان به&zwnj;ده&zwnj;سته&zwnj;وه نامێنێت كه خۆیانی پێ ڕزگار بكه&zwnj;ن.';
$t[167] = 'ئه&zwnj;وانه&zwnj;ی كه شوێنكه&zwnj;وته بوون ده&zwnj;ڵێن: خۆزگه نۆره&zwnj;یه&zwnj;كمان بۆ بلوایه ئێمه&zwnj;ش ئاوا خۆمان به&zwnj;ری ده&zwnj;كرد له&zwnj;وان، هه&zwnj;روه&zwnj;ك ئه&zwnj;وان خۆیان به&zwnj;ریی كرد له ئێمه&zwnj;، ئا به&zwnj;و شێوه&zwnj;یه خوا سه&zwnj;رئه&zwnj;نجامی كارو كرده&zwnj;وه&zwnj;كانیان نیشان ده&zwnj;دات و بۆیان ده&zwnj;كاته ئاخ و حه&zwnj;سره&zwnj;ت له&zwnj;سه&zwnj;ریان، به&zwnj;ڕاستی ئه&zwnj;وانه هه&zwnj;رگیز له دۆزه&zwnj;خ ده&zwnj;رچوونیان نییه&zwnj;.';
$t[168] = 'ئه&zwnj;ی خه&zwnj;ڵكینه&zwnj;! له ڕزق و ڕۆزی حه&zwnj;ڵاڵ و پاكی زه&zwnj;وی بخۆن (توخنی قه&zwnj;ده&zwnj;غه كراوه&zwnj;كان مه&zwnj;كه&zwnj;ون) و شوێن هه&zwnj;نگاوه&zwnj;كانی شه&zwnj;یتان مه&zwnj;كه&zwnj;ون، چونكه بێگومان ئه&zwnj;و دوژمنێكی ئاشكراتانه&zwnj;.';
$t[169] = 'بێگومان ئه&zwnj;و شه&zwnj;یتانه هه&zwnj;ر فه&zwnj;رمان به خراپه&zwnj;و گوناهو تاوان ده&zwnj;كات و هه&zwnj;روه&zwnj;ها هانتان ده&zwnj;دات بۆ وتنی هه&zwnj;ندێك شت یان هه&zwnj;ڵبه&zwnj;ستنی هه&zwnj;ندێك گوفتار به&zwnj;ده&zwnj;م خواوه بێ ئه&zwnj;وه&zwnj;ی بزانن (كه سه&zwnj;رئه&zwnj;نجامی ئه&zwnj;و كاره&zwnj;تان چه&zwnj;نده مه&zwnj;ترسیداره&zwnj;).';
$t[170] = 'ئه&zwnj;گه&zwnj;ر به&zwnj;و خه&zwnj;ڵكه نادانه بوترێت: شوێنی ئه&zwnj;و به&zwnj;رنامه&zwnj;یه بكه&zwnj;ون كه له&zwnj;لایه&zwnj;ن خواوه ڕه&zwnj;وانه كراوه&zwnj;، ده&zwnj;ڵێن: نه&zwnj;خێر، هه&zwnj;ر شوێنی ئه&zwnj;وه ده&zwnj;كه&zwnj;وین كه باوو باپیرانمان پێوه&zwnj;ی ئالووده بوون و له&zwnj;سه&zwnj;ری ڕاهاتوون، ئایا ئه&zwnj;گه&zwnj;ر باوو باپیرانیشیان تێ نه&zwnj;گه&zwnj;یشتوو هیچ نه&zwnj;زان و ناشاره&zwnj;زاو ڕێنمووییش وه&zwnj;رنه&zwnj;گرن (هه&zwnj;ر شوێنیان ده&zwnj;كه&zwnj;ون؟!).';
$t[171] = 'نموونه&zwnj;ی بێ باوه&zwnj;ڕان (له&zwnj;گه&zwnj;ڵ پێغه&zwnj;مبه&zwnj;ردا له&zwnj;وه&zwnj;ی كه سوود وه&zwnj;رناگرن له&zwnj;م قورئانه&zwnj;) وه&zwnj;كو ئه&zwnj;و شوانه وایه&zwnj;: كه هاوار ده&zwnj;كات به&zwnj;سه&zwnj;ر ئاژه&zwnj;ڵه&zwnj;كه&zwnj;یدا، به&zwnj;ڵام تێی ناگه&zwnj;ن، ته&zwnj;نها ئه&zwnj;وه&zwnj;یه كه گوێیان له هات و هاوارێكه&zwnj;، ئه&zwnj;وانه كه&zwnj;ڕو لاڵ و كوێرن و ژیری و هۆشمه&zwnj;ندیان له ده&zwnj;ستداوه&zwnj;.';
$t[172] = 'ئه&zwnj;ی ئه&zwnj;وانه&zwnj;ی باوه&zwnj;ڕتان هێناوه&zwnj;، بخۆن له&zwnj;و ڕزق و ڕۆزیه چاك و باشانه&zwnj;ی كه پێمان به&zwnj;خشیوون و به&zwnj;رده&zwnj;وام سوپاسگوزاری خوا بكه&zwnj;ن ئه&zwnj;گه&zwnj;ر ئێوه ته&zwnj;نها ئه&zwnj;و ده&zwnj;په&zwnj;رستن.';
$t[173] = '(خوای په&zwnj;روه&zwnj;ردگار) ته&zwnj;نها مرداره&zwnj;وه بوو و خوێن و گۆشتی به&zwnj;راز و ئه&zwnj;و ماڵاتانه&zwnj;ی كه ناوی غه&zwnj;یری خوایان له&zwnj;كاتی سه&zwnj;ربڕیندا له&zwnj;سه&zwnj;ر هێنراوه&zwnj;، حه&zwnj;رامی كردووه لێتان، جا ئه&zwnj;گه&zwnj;ر كه&zwnj;سێك پێویستی ناچاری كرد به&zwnj;خواردنیان بێ زیاده&zwnj;ڕه&zwnj;وی و له سنوور ده&zwnj;رچوون، ئه&zwnj;وه هیچ گوناهێكی له&zwnj;سه&zwnj;رنی یه&zwnj;، به&zwnj;ڕاستی خوا زۆر لێخۆشبوو به به&zwnj;زه&zwnj;یی و دلۆڤانه&zwnj;.';
$t[174] = 'بێگومان ئه&zwnj;وانه&zwnj;ی نیگای خوا ده&zwnj;شارنه&zwnj;وه كه له كتێبه&zwnj;كانیاندا ڕه&zwnj;وانه&zwnj;ی كردوون (ته&zwnj;ورات و ئینجیل و قورئان) و به&zwnj;و شاردنه&zwnj;وه&zwnj;یه نرخێكی كه&zwnj;م به&zwnj;ده&zwnj;ست ده&zwnj;هێنن (ئه&zwnj;گه&zwnj;ر هه&zwnj;موو دنیاشیان ده&zwnj;ستكه&zwnj;وێت له به&zwnj;رامبه&zwnj;ریه&zwnj;وه هه&zwnj;ركه&zwnj;مه&zwnj;)، ئا ئه&zwnj;وانه ته&zwnj;نها ئاگر ده&zwnj;خۆن و سكیانی لێ پڕ ده&zwnj;كه&zwnj;ن و له ڕۆژی قیامه&zwnj;تدا خوای گه&zwnj;وره نایاندوێنێت و خاوێنیان ناكاته&zwnj;وه (له چڵك و چه&zwnj;په&zwnj;ڵی گوناهیان) و هه&zwnj;ر سزای به&zwnj;ئێشیان بۆ هه&zwnj;یه&zwnj;...';
$t[175] = 'ئا ئه&zwnj;وانه كه&zwnj;سانێكن كه گومڕاییان وه&zwnj;رگرتووه له باتی هیدایه&zwnj;ت و سزایان كڕیوه له باتی چاوپۆشی و لێبووردن، ئای چه&zwnj;نده خۆگرن له&zwnj;سه&zwnj;ر ئاگری دۆزه&zwnj;خ!!';
$t[176] = '(ئه&zwnj;و هه&zwnj;ڕه&zwnj;شه&zwnj;یه&zwnj;) له&zwnj;به&zwnj;ر ئه&zwnj;وه&zwnj;یه به&zwnj;ڕاستی خوا كتێبه پیرۆزه&zwnj;كانی به&zwnj;حه&zwnj;ق و ڕاستی دابه&zwnj;زاندووه&zwnj;، بێگومان ئه&zwnj;وانه&zwnj;ش كه ناكۆكیان له&zwnj;سه&zwnj;ری هه&zwnj;یه&zwnj;و هه&zwnj;ر ڕایان جیاوازه له به&zwnj;رامبه&zwnj;ر كتێبه ئاسمانیه&zwnj;كانه&zwnj;وه&zwnj;، ئه&zwnj;وانه هه&zwnj;ر له كێشمه كێش و به&zwnj;ربه&zwnj;ره&zwnj;كانیه&zwnj;كی دوور له ڕاستیدان.';
$t[177] = 'كرداری چاك و كاری باش ته&zwnj;نها بریتی نیه له ڕووكردنتان به&zwnj;ره&zwnj;و ڕۆژهه&zwnj;ڵات و ڕۆژئاوا (چونكه جوله&zwnj;كه زۆر له&zwnj;سه&zwnj;ر گۆڕانی قیبله دووان و پڕو پاگه&zwnj;نده&zwnj;ی زۆریان كرد)، به&zwnj;ڵكو كرداری چاك و كاری باش بریتیه له&zwnj;وه&zwnj;ی كه ئاده&zwnj;میزاد باوه&zwnj;ڕی هێنابێت به خواو به ڕۆژی دوایی و به فریشته&zwnj;كان و به&zwnj;كتێبه&zwnj;كان و به پێغه&zwnj;مبه&zwnj;ران، هه&zwnj;روه&zwnj;ها ماڵ و سامانی به&zwnj;خشی بێت ـ له&zwnj;گه&zwnj;ڵ خۆشه&zwnj;ویستیدا بۆی ـ به&zwnj;خزمان و هه&zwnj;تیوان و هه&zwnj;ژاران و ڕێبواران و داواكاران و له پێناوی ئازادكرنی به&zwnj;نده&zwnj;كاندا؛ هه&zwnj;روه&zwnj;ها نوێژی به&zwnj;چاكی ئه&zwnj;نجام دابێت و زه&zwnj;كاتیشی له ماڵ و سامانی ده&zwnj;ركردبێت و ئه&zwnj;وانه&zwnj;ی وه&zwnj;فاداربن به&zwnj;په&zwnj;یمانه&zwnj;كانیان كاتێك په&zwnj;یمانیان دابێت، ئه&zwnj;وانه&zwnj;ش كه له كاتی نه&zwnj;داری و هه&zwnj;ژاری و نه&zwnj;خۆشی و ناخۆشی و كاتی جه&zwnj;نگ و شه&zwnj;ڕو شۆڕدا ئارامگرن، ئا ئه&zwnj;وانه كه&zwnj;سانێكن ڕاستیان كردووه و هه&zwnj;ر ئه&zwnj;وانه&zwnj;شن پارێزكارو خواناس و دیندارن.';
$t[178] = 'ئه&zwnj;ی ئه&zwnj;وانه&zwnj;ی باوه&zwnj;ڕتان هێناوه&zwnj;، پێویست كراوه له&zwnj;سه&zwnj;رتان: تۆڵه&zwnj;سه&zwnj;ندنه&zwnj;وه بۆ كوژراوه&zwnj;كان، كه&zwnj;سێكی ئازاد به&zwnj;كه&zwnj;سێكی ئازاد و به&zwnj;نده&zwnj;ش له جیاتی به&zwnj;نده و ئافره&zwnj;ت له جیاتی ئافره&zwnj;ت (واته هه&zwnj;ر كه&zwnj;سێك به تاوانی كوشتنی ناحه&zwnj;ق هه&zwnj;ستا ده&zwnj;بێت بكوژرێته&zwnj;وه&zwnj;)، جا ئه&zwnj;وه&zwnj;ی چاوپۆشی شتێكی لێكرا له&zwnj;لایه&zwnj;ن براكه&zwnj;یه&zwnj;وه&zwnj;، با حه&zwnj;قداری كوژراوه&zwnj;كه چاك ڕه&zwnj;فتاربێت له&zwnj;گه&zwnj;ڵ بكوژه&zwnj;كه&zwnj;داو بكوژه&zwnj;كه به&zwnj;جوانی خوێن به&zwnj;های كوژراوه&zwnj;كه بدات و ئه&zwnj;وه كار ئاسانكردن و سۆزو میهره&zwnj;بانیه&zwnj;كه له&zwnj;لایه&zwnj;ن په&zwnj;روه&zwnj;ردگارتانه&zwnj;وه (كه له&zwnj;جیاتی كوشتنه&zwnj;وه خوێنبایی وه&zwnj;ربگیرێت) ئه&zwnj;وسا ئیتر ئه&zwnj;وه&zwnj;ی له&zwnj;وه&zwnj;ودوا ده&zwnj;ستدرێژی بكات و له سنوور بترازێت سزای به&zwnj;ئێشی بۆ ئاماده&zwnj;كراوه&zwnj;.';
$t[179] = 'ئه&zwnj;ی هۆشمه&zwnj;نده&zwnj;كان ژیانتان ته&zwnj;نها له تۆڵه&zwnj;سه&zwnj;ندنه&zwnj;وه&zwnj;دایه (له&zwnj;و كه&zwnj;سانه&zwnj;ی خه&zwnj;ڵكی به&zwnj;ناحه&zwnj;ق ده&zwnj;كوژن)، بۆ ئه&zwnj;وه&zwnj;ی ته&zwnj;قواو له خوا ترسان ببێته پیشه&zwnj;تان و خۆ له ده&zwnj;ستدرێژی و تاوان به دوور بگرن.';
$t[180] = 'پێویستكراوه له&zwnj;سه&zwnj;رتان كاتێك یه&zwnj;كێكتان مه&zwnj;رگی نزیك بوه&zwnj;وه&zwnj;و سامان و ماڵ و داراییه&zwnj;كی به&zwnj;جێ ده&zwnj;هێشت، با وه&zwnj;سیه&zwnj;ت بكات كه هه&zwnj;ندێكی بدرێت به دایك و باوكی و خزمانی به&zwnj;شێوه&zwnj;یه&zwnj;كی جوان و له&zwnj;بارو ئه&zwnj;مه پێویست و ئه&zwnj;ركه له&zwnj;سه&zwnj;ر خواناس و پارێزكاران.';
$t[181] = 'جا ئه&zwnj;وه&zwnj;ی ئه&zwnj;و وه&zwnj;سیه&zwnj;ته ده&zwnj;گۆڕێت دوای ئه&zwnj;وه&zwnj;ی بیستی و به&zwnj;باشی وه&zwnj;ری گرت، گوناهی ته&zwnj;نها له&zwnj;سه&zwnj;ر ئه&zwnj;وانه&zwnj;یه كه ده&zwnj;یگۆڕن، به&zwnj;ڕاستی خوای په&zwnj;روه&zwnj;ردگار بیسه&zwnj;رو زانایه (به گوفتار و نیه&zwnj;تیان).';
$t[182] = 'جا هه&zwnj;ر كه&zwnj;س ده&zwnj;ترسا له وه&zwnj;سیه&zwnj;ت كاره&zwnj;كه هه&zwnj;ڵه یان گوناهێك یان ماف خۆرییه&zwnj;كی ئه&zwnj;نجام بدات (گیروگرفت بۆ وه&zwnj;ره&zwnj;سه دروست بكات) ئینجا ڕێكی خستن، ئه&zwnj;وه هیچ گوناهی له&zwnj;سه&zwnj;رنیه (به&zwnj;ڵكو به&zwnj;خێرخواز له قه&zwnj;ڵه&zwnj;م ده&zwnj;درێت) به&zwnj;ڕاستی خوا زۆر لێخۆشبوو میهره&zwnj;بانه&zwnj;.';
$t[183] = 'ئه&zwnj;ی ئه&zwnj;و كه&zwnj;سانه&zwnj;ی باوه&zwnj;ڕتان هێناوه ڕۆژووی ڕه&zwnj;مه&zwnj;زان له&zwnj;سه&zwnj;رتان پێویست كراوه هه&zwnj;ر وه&zwnj;كو له&zwnj;سه&zwnj;ر گه&zwnj;لانی پێش ئێوه پێویست كرابوو، بۆ ئه&zwnj;وه&zwnj;ی خواناسی و پارێزكاری بكه&zwnj;ن.';
$t[184] = 'چه&zwnj;ند ڕۆژێكی ژمێرراوی دیاریكراو (كه مانگی ڕه&zwnj;مه&zwnj;زانه&zwnj;) خۆ ئه&zwnj;گه&zwnj;ر كه&zwnj;سێك له ئێوه نه&zwnj;خۆش بوو یان له سه&zwnj;فه&zwnj;ردا بوو با به ئه&zwnj;ندازه&zwnj;ی ئه&zwnj;و ڕۆژانه&zwnj;ی بۆی نه&zwnj;گیراوه له ڕۆژانێكی تردا بیانگێڕێته&zwnj;وه&zwnj;و ئه&zwnj;وانه&zwnj;ش كه به زه&zwnj;حمه&zwnj;ت ده&zwnj;توانن به&zwnj;ڕۆژوو بن (وه&zwnj;كو پیره&zwnj;مێردو نه&zwnj;خۆشه به&zwnj;رده&zwnj;وامه&zwnj;كان) ئه&zwnj;وه با ژه&zwnj;مێك خواردن ببه&zwnj;خشن به هه&zwnj;ژارێك، ئه&zwnj;وه&zwnj;ی زیاتر ببه&zwnj;خشێت ئه&zwnj;وه باشتره بۆی، بێگومان ئه&zwnj;گه&zwnj;ر به&zwnj;ڕۆژوو بن چاكتره&zwnj;و پاداشتی زۆرتره ئه&zwnj;گه&zwnj;ر بزانن و (بتوانن).';
$t[185] = 'مانگی ڕه&zwnj;مه&zwnj;زان (ئه&zwnj;و مانگه&zwnj;یه&zwnj;) كه قورئانی تیادا هاتۆته خواره&zwnj;وه&zwnj;، له&zwnj;كاتێكدا ئه&zwnj;م قورئانه ڕێنموویی به&zwnj;خشه به خه&zwnj;ڵكی و به&zwnj;ڵگه&zwnj;ی زۆری تیادایه&zwnj;، له ڕێنموویی و جیاكردنه&zwnj;وه&zwnj;ی ڕاست و ناڕاست و حه&zwnj;ق و ناحه&zwnj;ق، جا كه&zwnj;سێك له ئێوه مانگی ڕه&zwnj;مه&zwnj;زانی بینی (یان هه&zwnj;واڵی بینینی پێدرا)، ده&zwnj;با ئه&zwnj;و مانگه به&zwnj;ڕۆژوو بێت، ئه&zwnj;وه&zwnj;ش كه نه&zwnj;خۆش بێت یان له سه&zwnj;فه&zwnj;ردا بێت با له&zwnj;ڕۆژانی تردا به&zwnj;و ئه&zwnj;ندازه بیگرێته&zwnj;وه&zwnj;، چونكه خوا ئاسانكاری بۆ ئێوه ده&zwnj;وێت و نایه&zwnj;وێت (په&zwnj;رستن و فه&zwnj;رزه&zwnj;كانتان) له&zwnj;سه&zwnj;ر قورس و گران بكات و بۆ ئه&zwnj;وه&zwnj;ی ئه&zwnj;و ماوه دیاریكراوه ته&zwnj;واو بكه&zwnj;ن و خوا به&zwnj;گه&zwnj;وره بگرن و سوپاسگوزاریشی بكه&zwnj;ن له&zwnj;سه&zwnj;ر ئه&zwnj;وه&zwnj;ی كه ڕێنموویی كردوون (سروودی جه&zwnj;ژن: الله اكبر الله اكبر الله اكبر، لا اله الا الله والله اكبر، الله اكبر ولله الحمد، به&zwnj;جێهێنانی ئه&zwnj;م فه&zwnj;رمانه خواییه&zwnj;یه&zwnj;).';
$t[186] = '(ئه&zwnj;ی محمد (صلی الله علیه وسلم) ئه&zwnj;گه&zwnj;ر به&zwnj;نده&zwnj;كانم پرسیارت لێبكه&zwnj;ن ده&zwnj;رباره&zwnj;م، ئه&zwnj;وه من نزیكم لێیانه&zwnj;وه&zwnj;، به هاناو هاواری دۆعا گۆیانه&zwnj;وه ده&zwnj;چم هه&zwnj;ر كاتێك لێم بپاڕێنه&zwnj;وه&zwnj;و نزا بكه&zwnj;ن، ده&zwnj;با ئه&zwnj;وانیش به&zwnj;ده&zwnj;م بانگه&zwnj;وازی منه&zwnj;وه بێن و باوه&zwnj;ڕی دامه&zwnj;زراو به من بهێنن، بۆ ئه&zwnj;وه&zwnj;ی ڕێگای هۆشیاری و ئاگایی بگرنه&zwnj;به&zwnj;ر (بۆ به&zwnj;ده&zwnj;ستهێنانی سه&zwnj;ربه&zwnj;رزی و سه&zwnj;رفرازی هه&zwnj;ردوو جیهان).';
$t[187] = 'له شه&zwnj;وگاری ڕه&zwnj;مه&zwnj;زاندا تێكه&zwnj;ڵ بوونتان له&zwnj;گه&zwnj;ڵ هاوسه&zwnj;رانتاندا حه&zwnj;ڵاڵكراوه&zwnj;، ئه&zwnj;وان پۆشاكن بۆ ئێوه و ئێوه&zwnj;ش پۆشاكن بۆ ئه&zwnj;وان، خوای گه&zwnj;وره ده&zwnj;یزانی كه ئێوه خۆتان ده&zwnj;خه&zwnj;ڵه&zwnj;تاند (له نزیك بوونه&zwnj;وه&zwnj;تان له هاوسه&zwnj;ره&zwnj;كانتانه&zwnj;وه&zwnj;، چونكه دوودڵ بوون و نه&zwnj;تانده&zwnj;زانی حه&zwnj;ڵاڵه یان نا)، به&zwnj;ڵام خوای میهره&zwnj;بان تۆبه&zwnj;ی لێ وه&zwnj;رگرتن و لێتان خۆشبوو، كه&zwnj;وابوو له ئێسته به&zwnj;دواوه (له شه&zwnj;وگاردا) نزیكییان بكه&zwnj;ن و ئه&zwnj;وه داوابكه&zwnj;ن خوا بۆی بڕیار داون (له نه&zwnj;وه&zwnj;ی چاك و خواناس)، ده بخۆن و بخۆنه&zwnj;وه هه&zwnj;تا به ڕوونی به&zwnj;ره&zwnj;به&zwnj;یان و سپێده&zwnj;تان بۆ ده&zwnj;رده&zwnj;كه&zwnj;وێت و له ڕۆژهه&zwnj;ڵاته&zwnj;وه دیارده&zwnj;دات و تاریكی شه&zwnj;و لاده&zwnj;بات، له&zwnj;وه&zwnj;ودوا ڕۆژووه&zwnj;كه&zwnj;تان ببه&zwnj;نه سه&zwnj;ر تا شه&zwnj;و دادێت و (خۆرئاوا ده&zwnj;بێت) و نزیكیی له&zwnj;گه&zwnj;ڵ هاوسه&zwnj;ره&zwnj;كانتاندا مه&zwnj;كه&zwnj;ن له كاتێكدا كه ئێوه بڕیاری مانه&zwnj;وه&zwnj;تان (بۆ خوا په&zwnj;رستی) له مزگه&zwnj;وته&zwnj;كاندا داوه&zwnj;، جا ئه&zwnj;وانه&zwnj;ی كه باسكران چه&zwnj;ند سنوورێكی خوان و مه&zwnj;یانبه&zwnj;زێنن و نزیكیان مه&zwnj;كه&zwnj;ون، ئا به&zwnj;وشێوه&zwnj;یه خوا ئایه&zwnj;ت و ڕێنمووییه&zwnj;كانی خۆی ڕوون ده&zwnj;كاته&zwnj;وه بۆ خه&zwnj;ڵكی بۆ ئه&zwnj;وه&zwnj;ی پارێزكاری بكه&zwnj;ن.';
$t[188] = 'نه&zwnj;كه&zwnj;ن ماڵ و دارایی یه&zwnj;كتر له نێوانتاندا به ناحه&zwnj;ق بخۆن (وه&zwnj;ك به&zwnj;رتیل و سوو، یان داگیركردن و فڕوفێڵ...) نه&zwnj;كه&zwnj;ن هه&zwnj;ندێكی بده&zwnj;ن به&zwnj;كاربه&zwnj;ده&zwnj;ستان بۆ ئه&zwnj;وه&zwnj;ی هه&zwnj;ندێك ماڵی خه&zwnj;ڵكی بخۆن له ڕێگه&zwnj;ی ناحه&zwnj;ق و گوناهه&zwnj;وه&zwnj;، له كاتێكدا خۆشتان ده&zwnj;زانن كه ئه&zwnj;و كاره ڕه&zwnj;وا نیه&zwnj;.';
$t[189] = 'پرسیارت لێده&zwnj;كه&zwnj;ن ده&zwnj;رباره&zwnj;ی نوێ بوونه&zwnj;وه&zwnj;ی مانگه&zwnj;كان، پێیان بڵێ ئه&zwnj;وه بۆ دیاریكردنی كاته&zwnj;كانه بۆ خه&zwnj;ڵكی و بۆ زانینی كاتی حه&zwnj;ج (خوای گه&zwnj;وره ڕێكی خستووه&zwnj;)، چاكه و چاكه&zwnj;كاریش ئه&zwnj;وه نیه كه له پشته&zwnj;وه خۆتان بكه&zwnj;ن به ماڵاندا (كه ئه&zwnj;مه كارێك بوو له سه&zwnj;رده&zwnj;می نه&zwnj;فامیدا ئه&zwnj;نجام ده&zwnj;درا دوای گه&zwnj;ڕانه&zwnj;وه له حه&zwnj;ج) به&zwnj;ڵكو چاكه ئه&zwnj;وه&zwnj;یه كه ئه&zwnj;و كه&zwnj;سه له خواترس و پارێزكار بێت، له ده&zwnj;رگای ماڵه&zwnj;كانه&zwnj;وه بچنه ژوره&zwnj;وه&zwnj;، له خوا بترسن بۆ ئه&zwnj;وه&zwnj;ی سه&zwnj;رفراز بن.';
$t[190] = 'بجه&zwnj;نگن له پێناوی ڕێبازی خوادا دژی ئه&zwnj;وانه&zwnj;ی كه دژتان ده&zwnj;جه&zwnj;نگن، نه&zwnj;كه&zwnj;ن ده&zwnj;ستدرێژی بكه&zwnj;ن (دژی ژن و مناڵ و ماڵات و... هتد) چونكه به&zwnj;ڕاستی خوا ئه&zwnj;و ده&zwnj;ستدرێژكه&zwnj;رانه&zwnj;ی خۆش ناوێت.';
$t[191] = '(ده&zwnj;ستدرێژكه&zwnj;ران) بكوژن له هه&zwnj;ر كوێدا ده&zwnj;ستان كه&zwnj;وتن و ده&zwnj;ستان به&zwnj;سه&zwnj;ریاندا ڕۆیشت و ده&zwnj;ریان بكه&zwnj;ن له&zwnj;و شوێنه&zwnj;ی كه ده&zwnj;ریان كردوون (كه شاری مه&zwnj;ككه بوو)، بێگومان هاوه&zwnj;ڵگه&zwnj;ری و زۆر كردن له ئیماندار تا پاشگه&zwnj;ز ببێته&zwnj;وه له ئاینه&zwnj;كه&zwnj;ی له كوشتن خراپتره&zwnj;، مه&zwnj;جه&zwnj;نگن دژی كافران له سنووری مزگه&zwnj;وتی حه&zwnj;رام و (كه&zwnj;عبه&zwnj;)دا هه&zwnj;تا ئه&zwnj;وان شه&zwnj;ڕتان پێ نه&zwnj;فرۆشن و نه جه&zwnj;نگن دژتان، خۆ ئه&zwnj;گه&zwnj;ر شه&zwnj;ڕیان پێ فرۆشتن و جه&zwnj;نگان دژتان ئێوه&zwnj;ش بجه&zwnj;نگن دژیان، چونكه هه&zwnj;ر ئاوایه پاداشتی ئه&zwnj;و بێباوه&zwnj;ڕانه&zwnj;.';
$t[192] = 'خۆ ئه&zwnj;گه&zwnj;ر ئه&zwnj;وانه كۆڵیانداو شه&zwnj;ڕیان وه&zwnj;ستان، ئه&zwnj;وه خوا زۆر لێبورده و میهره&zwnj;بانه&zwnj;.';
$t[193] = '(خۆ ئه&zwnj;گه&zwnj;ر كافران كۆڵیان نه&zwnj;داو هه&zwnj;ر جه&zwnj;نگان و پیلانیان گێڕا) ئێوه&zwnj;ش بجه&zwnj;نگن دژیان تا وه&zwnj;كو هاوه&zwnj;ڵگه&zwnj;ری و ئاشوب و ناخۆشی نه&zwnj;مێنێت، هه&zwnj;روه&zwnj;ها تا دین و ئاین و به&zwnj;رنامه&zwnj;و فه&zwnj;رمانڕه&zwnj;وایی هه&zwnj;ر بۆ خوای گه&zwnj;وره بێت، خۆ ئه&zwnj;گه&zwnj;ر كۆڵیانداو وازیان هێنا، ئه&zwnj;وا هیچ جۆره دژایه&zwnj;تیه&zwnj;كیان نامێنێت ته&zwnj;نها دژ به سته&zwnj;مكاره&zwnj;كانیان نه&zwnj;بێت.';
$t[194] = 'ڕێزی مانگی حه&zwnj;رام په&zwnj;یوه&zwnj;سته به ڕێز گرتنی ئه&zwnj;وان بۆ مانگی حه&zwnj;رام (واته&zwnj;: ئه&zwnj;گه&zwnj;ر له&zwnj;و مانگانه&zwnj;دا جه&zwnj;نگان دژتان كه جه&zwnj;نگ تیایدا حه&zwnj;رامه ئێوه حه&zwnj;قی به&zwnj;رگریتان هه&zwnj;یه&zwnj;) و ده&zwnj;ستدرێژی بۆ سه&zwnj;ر هه&zwnj;ر شتێكی حورمه&zwnj;تدار ده&zwnj;بێت تۆڵه&zwnj;ی بسه&zwnj;نرێت به&zwnj;بێ زیاده&zwnj;ڕه&zwnj;وی، جا ئه&zwnj;وه&zwnj;ی كه ده&zwnj;ستدرێژی ده&zwnj;كاته سه&zwnj;رتان، ئێوه&zwnj;ش مافتان هه&zwnj;یه به&zwnj;قه&zwnj;ده&zwnj;ر ده&zwnj;ست درێژییه&zwnj;كه&zwnj;ی ئه&zwnj;و، تۆڵه&zwnj;ی خۆتان بكه&zwnj;نه&zwnj;وه و له خوا بترسن و، چاك بزانن كه به&zwnj;ڕاستی خوا یارو یاوه&zwnj;ری پارێزكارانه&zwnj;.';
$t[195] = 'له ڕێ و ڕێبازی خوادا بۆ ڕه&zwnj;زامه&zwnj;ندی ئه&zwnj;و، ماڵ و سامانتان ببه&zwnj;خشن، به&zwnj;ده&zwnj;ستی خۆتان، خۆتان به&zwnj;ره&zwnj;و تیاچوون مه&zwnj;به&zwnj;ن (چونكه ڕه&zwnj;زیلی و ده&zwnj;ست نوقاوی مایه&zwnj;ی تیاچوونه&zwnj;)، هه&zwnj;میشه خێرو چاكه ئه&zwnj;نجام بده&zwnj;ن و چاكه&zwnj;خواز بن و (ماڵ و سامانتان له پێناوی خوادا ببه&zwnj;خشن) چونكه به&zwnj;ڕاستی خوا ئه&zwnj;و چاكه كارانه&zwnj;ی خۆشده&zwnj;وێت.';
$t[196] = 'حه&zwnj;ج و عه&zwnj;مره به&zwnj;چاكی و ته&zwnj;واوی بۆ به&zwnj;ده&zwnj;ستهێنانی ڕه&zwnj;زامه&zwnj;ندی خوا ئه&zwnj;نجام بده&zwnj;ن، جا ئه&zwnj;گه&zwnj;ر ڕێتان لێگیراو ئه&zwnj;ركه&zwnj;كانتان بۆ ئه&zwnj;نجام نه&zwnj;درا (به&zwnj;هۆی نه&zwnj;خۆشی یان هه&zwnj;ر هۆیه&zwnj;كی تر) ئه&zwnj;وه ده&zwnj;توانن ماڵاتێك بكه&zwnj;ن به&zwnj;قوربانی به&zwnj;گوێره&zwnj;ی توانا، سه&zwnj;رتان مه&zwnj;تاشن و (ئیحرام مه&zwnj;شكێنن) تا قوربانییه&zwnj;كه ئه&zwnj;نجام نه&zwnj;درێت و به&zwnj;جێی خۆی نه&zwnj;گات، جا هه&zwnj;ر كه&zwnj;سێكتان نه&zwnj;خۆش بوو یان برین و ئازارێك له سه&zwnj;ریدا هه&zwnj;بوو (پێویست بوو مووی سه&zwnj;ری یان هه&zwnj;ندێكی لاببات) با فیدیه بدات (كه بریتیه له&zwnj;وه&zwnj;ی) به&zwnj;ڕۆژوو بێت، یان خێرێك یان قوربانیه&zwnj;ك بكات، جا هه&zwnj;ر كاتێك كه له ئاسووده&zwnj;یی و ئاسایدا بوون، ئه&zwnj;وه&zwnj;ی كه ده&zwnj;یه&zwnj;وێت نێوان حه&zwnj;ج و عه&zwnj;مره (ئیحرام بشكێنێت و پۆشاكی ئاسایی له&zwnj;به&zwnj;ر بكات) ده&zwnj;بێت به گوێره&zwnj;ی توانای خۆی قوربانییه&zwnj;ك بكات، ئه&zwnj;وه&zwnj;ش كه نه&zwnj;یبوو قوربانی بكات، با سێ ڕۆژ له حه&zwnj;ج به&zwnj;رۆژوو بێت و حه&zwnj;وت ڕۆژیش كه گه&zwnj;ڕانه&zwnj;وه (ناو ماڵ و حاڵی خۆیان) ئه&zwnj;وه ده&zwnj;كاته&zwnj;، ده&zwnj;، ڕۆژی ته&zwnj;واو، جا ئه&zwnj;مه بۆ ئه&zwnj;و كه&zwnj;سانه&zwnj;یه كه خه&zwnj;ڵكی مه&zwnj;ككه&zwnj;و ده&zwnj;ورو به&zwnj;ری نین، له خوا بترسن و پارێزكاربن و بزانن كه به&zwnj;ڕاستی خوا سزاو تۆڵه&zwnj;ی زۆر سه&zwnj;خته (له&zwnj;وانه&zwnj;ی كه سه&zwnj;رپێچی ده&zwnj;كه&zwnj;ن).';
$t[197] = 'حه&zwnj;ج له چه&zwnj;ند مانگێکی دیاریکراودایه و (خۆی بۆ ئاماده ده&zwnj;کرێت و نیه&zwnj;تی لێده&zwnj;هێنرێت) جا ئه&zwnj;وه&zwnj;ی که له&zwnj;و مانگانه&zwnj;دا ده&zwnj;ستبه&zwnj;کار بوو و بڕیاریدا حه&zwnj;ج بکات، ئه&zwnj;وه ده&zwnj;بێت نزیکی هاوسه&zwnj;ری نه&zwnj;که&zwnj;وێت و شه&zwnj;ڕه چه&zwnj;پۆک و گوناهو موجاده&zwnj;له و گوفتاری نادروست ئه&zwnj;نجام نه&zwnj;دات، بێگومان هه&zwnj;ر خێرو چاکه&zwnj;یه&zwnj;ک ئه&zwnj;نجام بده&zwnj;ن خوا پێی ده&zwnj;زانێت و ئاگاداره لێی، به&zwnj;رده&zwnj;وام هه&zwnj;وڵ بده&zwnj;ن که زه&zwnj;خیره&zwnj;و توێشوو بخه&zwnj;ن، بێگومان چاکترین زه&zwnj;خیره&zwnj;ش ته&zwnj;قواو پارێزکاریه&zwnj;، به&zwnj;رده&zwnj;وامیش له من بترسن ئه&zwnj;ی خاوه&zwnj;ن بیرو هۆشه&zwnj;کان.';
$t[198] = 'هیچ گوناهبار نابن ئه&zwnj;گه&zwnj;ر له کاتی حه&zwnj;جدا له فه&zwnj;زڵی په&zwnj;روه&zwnj;ردگارتان، خۆتان به&zwnj;هره&zwnj;وه&zwnj;ر بکه&zwnj;ن (وه&zwnj;ک بازرگانی و شت کڕین) هه&zwnj;ر کاتێک له عه&zwnj;ره&zwnj;فات دابه&zwnj;زین و گه&zwnj;ڕانه&zwnj;وه&zwnj;، یادی خوا بکه&zwnj;ن له&zwnj;لای (مشعر الحرام که&zwnj;: کێوێکه له داوێنی مزدلفة دا) و یادیشی بکه&zwnj;ن وه&zwnj;ک چۆن ڕێنموویی کردوون و هیدایه&zwnj;تی داوون، چونکه به&zwnj;ڕاستی ئێوه پێش ئه&zwnj;م ئاین و ڕێنووماییه سه&zwnj;رگه&zwnj;ردان و گومڕا بوون.';
$t[199] = 'له&zwnj;وه&zwnj;ودوا (روو به قوڕه&zwnj;یش و سه&zwnj;رکرده و ده&zwnj;وڵه&zwnj;مه&zwnj;ندان ده&zwnj;فه&zwnj;رموێت): خه&zwnj;ڵکی له کوێوه دابه&zwnj;زین و گه&zwnj;ڕانه&zwnj;وه ئێوه&zwnj;ش له&zwnj;وێوه دابه&zwnj;زن و بگه&zwnj;ڕێنه&zwnj;وه&zwnj;و (خۆتان جیامه&zwnj;که&zwnj;نه&zwnj;وه) و داوای لێخۆشبوون له خوا بکه&zwnj;ن، چونکه به&zwnj;ڕاستی په&zwnj;روه&zwnj;ردگارتان زۆر لێخۆشبوو میهره&zwnj;بانه&zwnj;.';
$t[200] = 'جا هه&zwnj;ر کاتێک ئه&zwnj;رک و مه&zwnj;ناسیکه&zwnj;کانتان به&zwnj;جێ هێنا، ئه&zwnj;وکاته یادی خوا بکه&zwnj;ن هه&zwnj;روه&zwnj;کو چۆن یادی باو وباپیرانتان ده&zwnj;که&zwnj;ن (کاتی خۆی عه&zwnj;ره&zwnj;ب زۆر خۆیان هه&zwnj;ڵکێشاوه به باوو باپیریانه&zwnj;وه&zwnj;و باسیان کردوون) یاخود زۆر زیاتریش له&zwnj;وان، جا هه&zwnj;ندێک له خه&zwnj;ڵکی هه&zwnj;یه که ده&zwnj;ڵێت: په&zwnj;روه&zwnj;ردگارا هه&zwnj;رچیمان پێ ده&zwnj;به&zwnj;خشیت له دنیادا پێمان ببه&zwnj;خشه&zwnj;، بێگومان ئه&zwnj;و جۆره که&zwnj;سه له قیامه&zwnj;تدا بێ به&zwnj;هره&zwnj;و بێ به&zwnj;ش و بێ ڕێزه&zwnj;.';
$t[201] = 'هه&zwnj;شیانه که ده&zwnj;ڵێت: په&zwnj;روه&zwnj;ردگارا له دنیادا خێرو چاکه&zwnj;و نازو نیعمه&zwnj;تمان پێ ببه&zwnj;خشه&zwnj;و، له قیامه&zwnj;تیشدا له نازو نیعمه&zwnj;ته&zwnj;کانی به&zwnj;هه&zwnj;شت به&zwnj;هره&zwnj;مه&zwnj;ندمان بکه&zwnj;و، له سزای ئاگری دۆزه&zwnj;خ بمانپارێزه&zwnj;.';
$t[202] = 'ئا ئه&zwnj;وانه له هه&zwnj;ندێک له&zwnj;و کارو کرده&zwnj;وانه&zwnj;ی که ئه&zwnj;نجامیانداوه به&zwnj;هره&zwnj;وه&zwnj;ر ده&zwnj;بن له دنیاو قیامه&zwnj;تدا و خوا زۆر به خێرایی لێپرسینه&zwnj;وه ئه&zwnj;نجام ده&zwnj;دات (هه&zwnj;رکه&zwnj;سه به&zwnj;و شوێنه ده&zwnj;گه&zwnj;یه&zwnj;نێت که له دنیادا ده&zwnj;ست پێشخه&zwnj;ری بۆ کردووه&zwnj;).';
$t[203] = 'له ڕۆژانێکی دیاریکراودا (که ڕۆژانی جه&zwnj;ژنی قوربانه&zwnj;) یادی خوا بکه&zwnj;ن ئه&zwnj;وه&zwnj;ی که په&zwnj;له بکات و له دوو ڕۆژدا شه&zwnj;یتان ڕه&zwnj;جم بکات ئه&zwnj;وه هیچ گوناهی له&zwnj;سه&zwnj;رنییه&zwnj;، ئه&zwnj;وه&zwnj;ش دوای بخات و (به سێ ڕۆژ ئه&zwnj;نجامی بدات) هه&zwnj;رگوناهی له&zwnj;سه&zwnj;ر نییه&zwnj;، به&zwnj;تایبه&zwnj;ت بۆ ئه&zwnj;وانه&zwnj;ی که خۆیان له نافه&zwnj;رمانی خوا ده&zwnj;پارێزن، جا له خوا بترسن و بزانن سه&zwnj;رئه&zwnj;نجام ئێوه بۆ لای ئه&zwnj;و ده&zwnj;گه&zwnj;ڕێنه&zwnj;وه&zwnj;، (له&zwnj;به&zwnj;رده&zwnj;م دادگای ئه&zwnj;ودا) کۆ ده&zwnj;کرێنه&zwnj;وه&zwnj;.';
$t[204] = 'هه&zwnj;ندێ که&zwnj;س هه&zwnj;ن قسه&zwnj;و گوفتارو ده&zwnj;م پاراوییان له باره&zwnj;ی ژیانی ئه&zwnj;م دنیایه&zwnj;وه سه&zwnj;رسامت ده&zwnj;کات، خوایش به شایه&zwnj;ت ده&zwnj;گرێت و ده&zwnj;ڵێت: دڵ و زمانم یه&zwnj;کسانن، له کاتێکدا ئه&zwnj;و سه&zwnj;رسه&zwnj;ختترین که&zwnj;سه له هه&zwnj;موو جۆره دوژمنایه&zwnj;تیه&zwnj;کدا.';
$t[205] = 'هه&zwnj;ر کاتێک ئه&zwnj;و جۆره که&zwnj;سه پشتی هه&zwnj;ڵکردو ده&zwnj;سه&zwnj;ڵاتی په&zwnj;یدا کرد؛ هه&zwnj;وڵ و کۆشش ده&zwnj;دات له زه&zwnj;ویدا تا ئاشوب به&zwnj;رپا بکات و خراپه بڕوێنێت و کشتوکاڵ له&zwnj;ناو ببات و پاکتاو کردنی ڕه&zwnj;گه&zwnj;زی ئه&zwnj;نجام بدات، بێگومان خوا فه&zwnj;سادو خراپه و تاوانی ناوێت.';
$t[206] = 'خۆ ئه&zwnj;گه&zwnj;ر پێی بوترێت: له خوا بترسه&zwnj;، (هه&zwnj;روا بۆت نالوێت)، غه&zwnj;ڕڕایی ده&zwnj;یگرێت و شانازیی به گوناهو تاوانه&zwnj;وه ده&zwnj;کات و سوور ده&zwnj;بێت له&zwnj;سه&zwnj;ری، تۆڵه له ئیمانداره دڵسۆزه&zwnj;که ده&zwnj;سێنێت و تاوانباریشی ده&zwnj;کات، جا ئه&zwnj;و خوانه&zwnj;ناسه دۆزه&zwnj;خی به&zwnj;سه&zwnj;، سوێند به&zwnj;خوا ئه&zwnj;و دۆزه&zwnj;خه شوێن و جێگه&zwnj;و لانکه&zwnj;یه&zwnj;کی ناله&zwnj;باره&zwnj;.';
$t[207] = 'هه&zwnj;ندێ خه&zwnj;ڵکیش هه&zwnj;ن (پێچه&zwnj;وانه&zwnj;ی ئه&zwnj;وانه&zwnj;ی باسکران) خۆیان ده&zwnj;فرۆشن بۆ به&zwnj;ده&zwnj;ستهێنانی ڕه&zwnj;زامه&zwnj;ندی به&zwnj;خوا، خوای گه&zwnj;وره&zwnj;ش به&zwnj;سۆزو میهره&zwnj;بانه به&zwnj;رامبه&zwnj;ر به&zwnj;رامبه&zwnj;ر ئه&zwnj;و جۆره به&zwnj;ندانه&zwnj;ی.';
$t[208] = 'ئه&zwnj;ی ئه&zwnj;وانه&zwnj;ی ئیمان و باواڕتان هێناوه هه&zwnj;ر هه&zwnj;مووتان خۆتان بخه&zwnj;نه ژێر سایه&zwnj;ی ئیسلامه&zwnj;وه و به ته&zwnj;واوی په&zwnj;یڕه&zwnj;وی بکه&zwnj;ن و شوێنی هه&zwnj;نگاوه&zwnj;کانی شه&zwnj;یتان مه&zwnj;که&zwnj;ون، چونکه به&zwnj;ڕاستی ئه&zwnj;و دوژمنی ئاشکراتانه (هه&zwnj;میشه ده&zwnj;یه&zwnj;وێت شه&zwnj;ڕو جه&zwnj;نگ له نێوان خه&zwnj;ڵکیدا به&zwnj;رپا بکات و له ئیسلام دووریان بخاته&zwnj;وه&zwnj;).';
$t[209] = 'خۆ ئه&zwnj;گه&zwnj;ر لاتانداو هه&zwnj;ڵه&zwnj;و گوناهتان کرد دوای ئه&zwnj;وه&zwnj;ی که هه&zwnj;موو شتێکتان بۆ ڕوون کرابۆوه&zwnj;، ئه&zwnj;وه چاک بزانن که خوا به&zwnj;ده&zwnj;سه&zwnj;&zwnj;ڵاته (تۆڵه&zwnj;تان لێ بسێنێت)و دانایه (په&zwnj;له ناکات به&zwnj;ڵکو بگه&zwnj;ڕێنه&zwnj;وه بۆ سه&zwnj;ر ڕاسته شه&zwnj;قام).';
$t[210] = 'ئایا ئه&zwnj;وانه&zwnj;ی که ڕێبازی ئیسلامیان ناوێت چاوه&zwnj;ڕێی چی ده&zwnj;که&zwnj;ن؟ جگه له هاتنی خوای گه&zwnj;وره بۆ لایان له نێو سێبه&zwnj;ری په&zwnj;ڵه هه&zwnj;وری سپی له&zwnj;گه&zwnj;ڵ فریشته&zwnj;کاندا، ئه&zwnj;وکاته هه&zwnj;موو شتێک کۆتایی پێ هێنرا (ده&zwnj;رگای ته&zwnj;وبه داخراو کۆتایی به دنیا هات)، ئه&zwnj;وسا هه&zwnj;موو کارو فرمانێک هه&zwnj;ر بۆ لای خوای په&zwnj;روه&zwnj;ردگار ده&zwnj;گه&zwnj;ڕێته&zwnj;وه&zwnj;.';
$t[211] = '(ئه&zwnj;ی محمد (صلی الله علیه وسلم) له نه&zwnj;وه&zwnj;ی ئیسرائیل بپرسه&zwnj;: چه&zwnj;نده به&zwnj;ڵگه&zwnj;ی ڕوون و ئاشکرامان پێ به&zwnj;خشیوون؟ جا ئه&zwnj;وه&zwnj;ی نیعمه&zwnj;تی خوا بگۆڕێت، (له یه&zwnj;کخواناسی لابدات) دوای ئه&zwnj;وه&zwnj;ی بۆی هاتووه&zwnj;، ئه&zwnj;وه به&zwnj;ڕاستی خوا تۆڵه&zwnj;ی زۆر سه&zwnj;خت و به ئازاره (له&zwnj;و جۆره که&zwnj;سانه&zwnj;).';
$t[212] = 'ژیانی دنیا ڕازێنراوه&zwnj;ته&zwnj;وه بۆ بێ باوه&zwnj;ڕان (به&zwnj;ڵام له&zwnj;باتی سوپاسگوزاری) گاڵته ده&zwnj;که&zwnj;ن به باوه&zwnj;ڕداران (له&zwnj;به&zwnj;ر هه&zwnj;ژاری و نه&zwnj;داری و که&zwnj;م ده&zwnj;سه&zwnj;ڵاتی)، بێگومان ئه&zwnj;وانه&zwnj;ی که خاوه&zwnj;نی ته&zwnj;قواو له خوا ترسان بوون له ڕۆژی قیامه&zwnj;تدا له سه&zwnj;رویانه&zwnj;وه&zwnj;ن (له به&zwnj;هه&zwnj;شتی به&zwnj;رزو به&zwnj;ریندا، ئه&zwnj;وانیش له ناخی دۆزه&zwnj;خدان) و خوا (له دنیادا) ڕزق و ڕۆزی ده&zwnj;به&zwnj;خشێت به هه&zwnj;موو که&zwnj;س بێ ئه&zwnj;وه&zwnj;ی گوێ بداته بیرو باوه&zwnj;ڕیان.';
$t[213] = '(له سه&zwnj;ره&zwnj;تای په&zwnj;یدابونی خه&zwnj;ڵکیدا، پێش تۆفانه&zwnj;که&zwnj;ی نوح) خه&zwnj;ڵکی یه&zwnj;ک ئوممه&zwnj;ت بوون و خاوه&zwnj;نی ته&zwnj;نها بیروباوه&zwnj;ڕی یه&zwnj;کتاناسین بون، (به&zwnj;ڵام به&zwnj;ره&zwnj;به&zwnj;ره لایاندا) خوایش وێڵی نه&zwnj;کردن و پێغه&zwnj;مبه&zwnj;رانی ڕه&zwnj;وانه&zwnj;کرد، تا مژده&zwnj;ده&zwnj;رو ترسێنه&zwnj;ربن هاوڕێ له&zwnj;گه&zwnj;ڵ کتێبی ئاسمانیدا، بۆ ڕوون کردنه&zwnj;وه&zwnj;ی حه&zwnj;ق و ڕاستی، تا له&zwnj;نێوان خه&zwnj;ڵکیدا فه&zwnj;رمانڕه&zwnj;وایی و دادوه&zwnj;ری ئه&zwnj;نجام بدات له&zwnj;سه&zwnj;ر ئه&zwnj;و شتانه&zwnj;ی که بۆته کێشه&zwnj;یان و ڕاو بۆچوونیان له&zwnj;سه&zwnj;ری جیاوازه&zwnj;، که&zwnj;سیش جیاوازی و کێشه&zwnj;ی له&zwnj;سه&zwnj;ر به&zwnj;رپا نه&zwnj;کرد جگه له&zwnj;وانه&zwnj;ی که ئه&zwnj;و کتێبانه&zwnj;یان پێ به&zwnj;خشراوه (که گاورو جوله&zwnj;که&zwnj;ن) دوای ئه&zwnj;وه&zwnj;ی به&zwnj;ڵگه&zwnj;و نیشانه&zwnj;ی ڕوون و ئاشکرایان بۆ هات (که قورئانه&zwnj;، ئینجا کێشه و دووبه&zwnj;ره&zwnj;کیان تێدا دروست بوو)، ئه&zwnj;مه&zwnj;ش به&zwnj;هۆی حه&zwnj;ساده&zwnj;ت و سته&zwnj;م کردنیانه&zwnj;وه (بۆیه په&zwnj;یڕه&zwnj;وی حه&zwnj;ق و ڕاستیان نه&zwnj;کرد)، ئه&zwnj;وسا خوا هیدایه&zwnj;ت و ڕێنمووی ئیماندارانی کرد بۆ دۆزینه&zwnj;وه&zwnj;ی حه&zwnj;ق و په&zwnj;یڕه&zwnj;وی کردنی له نێوان ئه&zwnj;و مه&zwnj;سه&zwnj;لانه&zwnj;ی که ئه&zwnj;وان کێشه&zwnj;یان له سه&zwnj;ری هه&zwnj;بوو و خوا ڕێنموویی هه&zwnj;رکه&zwnj;سێک ده&zwnj;کات که بیه&zwnj;وێت و (شایسته بێت) بۆ ڕێگه و ڕێبازی ڕاست و دروست.';
$t[214] = 'ئایا لاتان وایه هه&zwnj;روا به ئاسانی ده&zwnj;چنه به&zwnj;هه&zwnj;شته&zwnj;وه (بێ ته&zwnj;نگانه و ناخۆشی)؟! مه&zwnj;گه&zwnj;ر نموونه&zwnj;ی ئیماندارانی پێشووتان بۆ باس نه&zwnj;کراوه&zwnj;، که تووشی شه&zwnj;ڕو ناخۆشی و ته&zwnj;نگانه&zwnj;و گرانی و نه&zwnj;داری بوون و ته&zwnj;کانیان خواردوه و زۆریان بۆ هاتووه به&zwnj;ڕادایه&zwnj;ک، هه&zwnj;تا پیغه&zwnj;مبه&zwnj;ره&zwnj;که&zwnj;یان و ئه&zwnj;وانه&zwnj;ی باوه&zwnj;ڕیان هێناوه له&zwnj;گه&zwnj;ڵیدا، ده&zwnj;یانوت: که&zwnj;ی په&zwnj;روه&zwnj;ردگار سه&zwnj;رکه&zwnj;وتن ده&zwnj;به&zwnj;خشێت؟! ئیتر که&zwnj;ی خوا یارمه&zwnj;تی ده&zwnj;نێرێت؟! ئاگاداربن و دڵنیابن به&zwnj;ڕاستی یارمه&zwnj;تی خوایی زۆر نزیکه&zwnj;. له تۆ ده&zwnj;پرسن (ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر&lrm;&lrm; (صلی الله علیه وسلم)که&zwnj;چی ببه&zwnj;خشن و به&zwnj;کێی ببه&zwnj;خشن؟ پێیان بڵێ: هه&zwnj;ر خێرێک ده&zwnj;که&zwnj;ن سه&zwnj;ره&zwnj;تا دایک و باوکتان فه&zwnj;رامۆش مه&zwnj;که&zwnj;ن، هه&zwnj;روه&zwnj;ها خزمان و هه&zwnj;تیوان و هه&zwnj;ژاران و ڕێبوارانی نه&zwnj;داریش، هه&zwnj;ر خێرو چاکه&zwnj;یه&zwnj;کیش ئه&zwnj;نجام ده&zwnj;ده&zwnj;ن، دڵنیابن که خوا زانا و ئاگاداره لێی.';
$t[215] = 'جه&zwnj;نگ له&zwnj;سه&zwnj;رتان بڕیار دراوه که&zwnj;شتێکی گرانه لاتان (ئیماندار به جه&zwnj;سته لای گرانه به&zwnj;ڵام ڕۆحی پێی ئاسووده&zwnj;یه بۆ به&zwnj;رگری له ئیسلام و لابردنی کۆسپه&zwnj;کان) به&zwnj;ڵام له&zwnj;وانه&zwnj;یه شتێکتان لا ناخۆش بێت که&zwnj;چی خێر له&zwnj;وه&zwnj;دا بێت، هه&zwnj;روه&zwnj;ها له&zwnj;وانه&zwnj;یه شتێکتان پێ خۆش بێت که&zwnj;چی شه&zwnj;ڕ له&zwnj;وه&zwnj;دا بێت، بێگومان هه&zwnj;ر خوا خۆی ده&zwnj;زانێت (خێر له چیدایه&zwnj;)، ئێوه نایزانن. (جه&zwnj;نگ له ئیسلامدا بۆ به&zwnj;رهه&zwnj;ڵستی کردنی دوژمنانی داگیرکه&zwnj;رو دوژمنانی دینه و بۆ لابردنی ئه&zwnj;و کۆسپانه&zwnj;یه که نایه&zwnj;ڵن خه&zwnj;ڵک ئازادانه بژین و ئازادانه ئه&zwnj;و ڕێبازه هه&zwnj;ڵبژێرن که خۆیان قه&zwnj;ناعه&zwnj;تیان پێیه&zwnj;تی، هه&zwnj;رگیز بۆ ئه&zwnj;وه نیه که به زۆر خه&zwnj;ڵک موسوڵمان ببێت و مێژووی ئیسلام شایه&zwnj;تی حاڵه&zwnj;و ئه&zwnj;گه&zwnj;ر کارێکی وا ڕووی دابێت ئه&zwnj;وه ئاینی ئیسلام لێی به&zwnj;رییه&zwnj;).';
$t[216] = 'جه&zwnj;نگ له&zwnj;سه&zwnj;رتان بڕیار دراوه که&zwnj;شتێکی گرانه لاتان (ئیماندار به جه&zwnj;سته لای گرانه به&zwnj;ڵام ڕۆحی پێی ئاسووده&zwnj;یه بۆ به&zwnj;رگری له ئیسلام و لابردنی کۆسپه&zwnj;کان) به&zwnj;ڵام له&zwnj;وانه&zwnj;یه شتێکتان لا ناخۆش بێت که&zwnj;چی خێر له&zwnj;وه&zwnj;دا بێت، هه&zwnj;روه&zwnj;ها له&zwnj;وانه&zwnj;یه شتێکتان پێ خۆش بێت که&zwnj;چی شه&zwnj;ڕ له&zwnj;وه&zwnj;دا بێت، بێگومان هه&zwnj;ر خوا خۆی ده&zwnj;زانێت (خێر له چیدایه&zwnj;)، ئێوه نایزانن. (جه&zwnj;نگ له ئیسلامدا بۆ به&zwnj;رهه&zwnj;ڵستی کردنی دوژمنانی داگیرکه&zwnj;رو دوژمنانی دینه و بۆ لابردنی ئه&zwnj;و کۆسپانه&zwnj;یه که نایه&zwnj;ڵن خه&zwnj;ڵک ئازادانه بژین و ئازادانه ئه&zwnj;و ڕێبازه هه&zwnj;ڵبژێرن که خۆیان قه&zwnj;ناعه&zwnj;تیان پێیه&zwnj;تی، هه&zwnj;رگیز بۆ ئه&zwnj;وه نیه که به زۆر خه&zwnj;ڵک موسوڵمان ببێت و مێژووی ئیسلام شایه&zwnj;تی حاڵه&zwnj;و ئه&zwnj;گه&zwnj;ر کارێکی وا ڕووی دابێت ئه&zwnj;وه ئاینی ئیسلام لێی به&zwnj;رییه&zwnj;).';
$t[217] = 'پرسیارت لێده&zwnj;که&zwnj;ن ده&zwnj;رباره&zwnj;ی جه&zwnj;نگ له مانگی حه&zwnj;رامدا (که بریتین له ـ ذوالعقدة ـ ذوالحجة ـ محرم ـ ڕجب)، بڵێ: جه&zwnj;نگ له&zwnj;و جۆره مانگا&zwnj;نه&zwnj;دا کاره&zwnj;سات و هه&zwnj;ڵه&zwnj;یه&zwnj;کی گه&zwnj;وره&zwnj;یه&zwnj;، به&zwnj;رهه&zwnj;ڵستی ڕێبازی خواو بێ باوه&zwnj;ڕی پێی، جه&zwnj;نگ له&zwnj;مزگه&zwnj;وتی حه&zwnj;رامدا و ده&zwnj;ربه&zwnj;ده&zwnj;ر کردنی خه&zwnj;ڵکه&zwnj;که&zwnj;ی زۆر گه&zwnj;وره&zwnj;تره لای خوا (له کوشتنی که&zwnj;سێک یان چه&zwnj;ند که&zwnj;سێک له مانگی حه&zwnj;رامدا) هه&zwnj;روه&zwnj;ها ئازاردانی ئیماندار تا هاوه&zwnj;ڵگه&zwnj;ر بێت پاشگه&zwnj;ز بێته&zwnj;وه تاوانێکه له کوشتن گه&zwnj;وره&zwnj;تره&zwnj;، (بێگومان بێ باوه&zwnj;ڕان) به&zwnj;رده&zwnj;وام دژتان ده&zwnj;جه&zwnj;نگن هه&zwnj;تا واتان لێبکه&zwnj;ن له دین و ئاینتان پاشگه&zwnj;ز ببنه&zwnj;وه&zwnj;، ئه&zwnj;گه&zwnj;ر بۆیان بکرێت، جا هه&zwnj;ر که&zwnj;س له ئێوه پاشگه&zwnj;ز بێته&zwnj;وه له ئاینه&zwnj;که&zwnj;ی و به کافریش بمرێت، ئا ئه&zwnj;و جۆره که&zwnj;سانه کاره&zwnj;کانیان پووچه&zwnj;ڵ ده&zwnj;بێت له دنیاو قیامه&zwnj;تدا و ئه&zwnj;وانه جێنشینی ناو ئاگری دۆزه&zwnj;خن و ژیانی هه&zwnj;میشه&zwnj;یی تیایدا ده&zwnj;به&zwnj;نه سه&zwnj;ر.';
$t[218] = 'به&zwnj;ڕاستی ئه&zwnj;وانه&zwnj;ی که باوه&zwnj;ڕیان هێناوه&zwnj;و ئه&zwnj;وانه&zwnj;ی کۆچ و جیهادو تێکۆشانیان کردووه له پێناو ڕێبازی خوادا، ئا ئه&zwnj;وانه به&zwnj;هیوا و به&zwnj;ته&zwnj;مای ڕه&zwnj;حمه&zwnj;ت و میهره&zwnj;بانی خوای په&zwnj;روه&zwnj;ردگارن، به&zwnj;ڕاستی خوایش زۆر لێخۆشبوو و دلۆڤانه&zwnj;.';
$t[219] = 'پرسیارت لێ ده&zwnj;که&zwnj;ن ده&zwnj;رباره&zwnj;ی عه&zwnj;ره&zwnj;ق و قومار، پێیان بڵێ: له&zwnj;و دووانه&zwnj;دا تاوان و گوناهێکی گه&zwnj;وره هه&zwnj;یه&zwnj;، هه&zwnj;ر چه&zwnj;نده چه&zwnj;ند سوودێکیشان تێدایه بۆ خه&zwnj;ڵکانێک، بێگومان گوناهه&zwnj;که&zwnj;یان گه&zwnj;وره&zwnj;تره له که&zwnj;ڵک و سوودیان، پرسیارت لێده&zwnj;که&zwnj;ن که چی ببه&zwnj;خشن؟ پێیان بڵێ: هه&zwnj;رچیتان لێ زیاده ده&zwnj;توانن بیبه&zwnj;خشن، ئا به&zwnj;و شێوه&zwnj;یه خوای گه&zwnj;وره ئه&zwnj;م به&zwnj;ڵگه&zwnj;و فه&zwnj;رمانانه&zwnj;تان بۆ ڕوون و ئاشکرا ده&zwnj;کات بۆ ئه&zwnj;وه&zwnj;ی بیر بکه&zwnj;نه&zwnj;وه&zwnj;و (واز له&zwnj;و شتانه بهێنن که زیانیان زۆرتره له سوودیان، وه&zwnj;چی بۆ هه&zwnj;ردوو جیهانتان چاکه ئه&zwnj;وه بکه&zwnj;ن)...';
$t[220] = '(ئه&zwnj;وه&zwnj;ی به&zwnj;رژه&zwnj;وه&zwnj;ندی ئێوه&zwnj;ی تێدایه&zwnj;) له دنیاو قیامه&zwnj;تدا (خوا ڕوونی کردۆته&zwnj;وه&zwnj;)، هه&zwnj;روه&zwnj;ها پرسیارت لێده&zwnj;که&zwnj;ن ده&zwnj;رباره&zwnj;ی هه&zwnj;تیوان (که چۆن بن بۆیان) پێیان بڵێ: چاکسازی و په&zwnj;روه&zwnj;رده&zwnj;کردنیان له هه&zwnj;مووشت چاکترو په&zwnj;سه&zwnj;ندتره&zwnj;، خۆ ئه&zwnj;گه&zwnj;ر تێکه&zwnj;ڵاویان بکه&zwnj;ن و بیانهێننه ماڵتان ئه&zwnj;وانه براتانن (ده&zwnj;بێت به&zwnj;چاکی چاودێری خۆیان و ماڵیان بکه&zwnj;ن) خوایش که&zwnj;سانی خراپساز (ئه&zwnj;وانه&zwnj;ی تۆوی خراپه ده&zwnj;چێنن) و چاکسازتان ده&zwnj;زانێ و جیای ده&zwnj;کاته&zwnj;وه&zwnj;، ئه&zwnj;گه&zwnj;ر خوا بیویستایه باری گران ده&zwnj;کردن و مه&zwnj;رجی سه&zwnj;ختی داده&zwnj;نا (ده&zwnj;رباره&zwnj;ی چاودێری هه&zwnj;تیوان)، چونکه به&zwnj;ڕاستی خوا باڵاده&zwnj;ست و داناو کاربه&zwnj;جێ یه&zwnj;.';
$t[221] = 'ئافره&zwnj;تانی موشریک و خواناس ماره مه&zwnj;که&zwnj;ن هه&zwnj;تا ئیمان و باوه&zwnj;ڕ ده&zwnj;هێنن، بێگومان که&zwnj;نیزه&zwnj;کێکی ئیماندار چاکتره له ئافره&zwnj;تێکی موشریک و خوانه&zwnj;ناس، هه&zwnj;رچه&zwnj;نده (جوانییه&zwnj;که&zwnj;ی، یان سامانه&zwnj;که&zwnj;ی، یان پله&zwnj;و پایه&zwnj;که&zwnj;ی) سه&zwnj;رسامی کردبن و ژن ماره مه&zwnj;بڕن له پیاوانی موشریک و خوانه&zwnj;ناس هه&zwnj;تا ئیمان و باوه&zwnj;ڕ ده&zwnj;هێنن و به&zwnj;ڕاستی به&zwnj;نده&zwnj;یه&zwnj;کی ئیماندار چاکتره له موشریک و خوانه&zwnj;ناسێک، هه&zwnj;رچه&zwnj;نده سه&zwnj;رسامی کردبن (قه&zwnj;دو قیافه&zwnj;و پله&zwnj;وپایه&zwnj;و سامانه&zwnj;که&zwnj;ی)، ئائه&zwnj;و خوانه&zwnj;ناسانه بانگه&zwnj;وازی مه&zwnj;ردوم ده&zwnj;که&zwnj;ن به&zwnj;ره&zwnj;و دۆزه&zwnj;خ، خوایش بانگهێشتی مه&zwnj;ردومان ده&zwnj;کات به&zwnj;ره&zwnj;و به&zwnj;هه&zwnj;شت و لێخۆشبوون به فه&zwnj;رمان و ئیزنی خۆی (که گوێڕایه&zwnj;ڵی ڕێبازه&zwnj;که&zwnj;یه&zwnj;تی) و خوا ئایه&zwnj;ته&zwnj;کانی (قورئانه&zwnj;که&zwnj;ی) به&zwnj;روونی ده&zwnj;خاته به&zwnj;رچاو بۆ خه&zwnj;ڵکی، بۆ ئه&zwnj;وه&zwnj;ی یاداوه&zwnj;ری وه&zwnj;ربگرن و تێبگه&zwnj;ن.';
$t[222] = '(ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر (صلی الله علیه وسلم) پرسیارت لێده&zwnj;که&zwnj;ن ده&zwnj;رباره&zwnj;ی (عاده&zwnj;تی مانگانه&zwnj;ی ئافره&zwnj;ت) پێیان بڵێ: ئه&zwnj;وه ئازارو زه&zwnj;ره&zwnj;ره&zwnj;، که&zwnj;وابوو خۆتان به دوور بگرن له هاوسه&zwnj;ره&zwnj;کانتان که له&zwnj;و حاڵه&zwnj;ته&zwnj;دا بن و نزیکیان مه&zwnj;که&zwnj;ون تا پاک ده&zwnj;بنه&zwnj;وه&zwnj;، جاکاتێک پاک و خاوێن بوونه&zwnj;&zwnj;وه&zwnj;و خۆیان شت بچنه لایان، به&zwnj;و شێوه&zwnj;یه&zwnj;ی که خوا فه&zwnj;رمانی داوه&zwnj;، به&zwnj;ڕاستی خوا ته&zwnj;وبه&zwnj;کاران و پاکانی خۆشده&zwnj;وێت (ئه&zwnj;وانه&zwnj;ی ڕواڵه&zwnj;ت و ناوه&zwnj;ڕۆکیان پاکه&zwnj;).';
$t[223] = 'ژنانی هاوسه&zwnj;رتان کێڵگه&zwnj;ن بۆتان و شوێنی چاندنی وه&zwnj;چه&zwnj;ن، جا به هه&zwnj;ر شێوه&zwnj;یه&zwnj;ک ده&zwnj;تانه&zwnj;وێت تۆو بوه&zwnj;شێننه ناو کێڵگه&zwnj;که&zwnj;تانه&zwnj;وه (به هیوای به&zwnj;رهه&zwnj;م) و بۆ خۆتان ده&zwnj;ست پێشکه&zwnj;ری بکه&zwnj;ن (سه&zwnj;ره&zwnj;تا هاوسه&zwnj;ری خواناس و چاک هه&zwnj;ڵبژێرن، له&zwnj;وه&zwnj;ودواش دوعاو نزاو خواپه&zwnj;رستی به&zwnj;رده&zwnj;وام فه&zwnj;رامۆش مه&zwnj;که&zwnj;ن)و له خوابترسن و خۆتان له خراپه بپارێزن و چاک بزانن و دڵنیابن که به دیداری خوا شاد ده&zwnj;بن و ئه&zwnj;م مژده&zwnj;یه&zwnj;ش بده به ئیمانداران.';
$t[224] = 'نه&zwnj;که&zwnj;ن سوێند خواردن به&zwnj;خوا بکه&zwnj;نه کۆسپ له&zwnj;به&zwnj;رامبه&zwnj;ر چاکه&zwnj;کاری و ئاینداری و ڕێکخستنی خه&zwnj;ڵکیدا (واته که&zwnj;فاره&zwnj;تی سوێنده&zwnj;کانتان بده&zwnj;ن و له چاکه&zwnj;و چاکه&zwnj;کاری درێغی مه&zwnj;که&zwnj;ن) چونکه خوا زۆر بیسه&zwnj;رو زانایه&zwnj;.';
$t[225] = 'خوای گه&zwnj;وره ئه&zwnj;و سوێندانه&zwnj;تان لێناگرێت که له ده&zwnj;متان ده&zwnj;رده&zwnj;چێت و له&zwnj;سه&zwnj;ری ڕاهاتوون، به&zwnj;ڵکو له&zwnj;سه&zwnj;رئه&zwnj;وه لێتان ده&zwnj;گرێت و موحاسه&zwnj;به&zwnj;تان ده&zwnj;کات که نیه&zwnj;تی دڵتانی له&zwnj;سه&zwnj;ره و خوا لێخۆشبووه (له گوناهه&zwnj;که&zwnj;تان) و ئارامگرو خۆڕاگره (زوو تۆڵه له یاخی و سوێند خۆران ناسێنێت).';
$t[226] = 'بۆ ئه&zwnj;وانه&zwnj;ی که سوێند ده&zwnj;خۆن نزیکی هاوسه&zwnj;ریان نه&zwnj;که&zwnj;ون، بۆیان هه&zwnj;یه تا چوار مانگ چاوه&zwnj;روانی بکه&zwnj;ن (ده&zwnj;بێت له&zwnj;و ماوه&zwnj;یه&zwnj;دا بڕیاری یه&zwnj;کجاری بده&zwnj;ن)، خۆ ئه&zwnj;گه&zwnj;ر ئاشت بوونه&zwnj;وه و ڕێککه&zwnj;وتن، ئه&zwnj;وه خوا لێخۆشبوو و میهره&zwnj;بانه&zwnj;.';
$t[227] = 'خۆ ئه&zwnj;گه&zwnj;ر سوور بوون له&zwnj;سه&zwnj;ر ته&zwnj;ڵاق دانیان، ئه&zwnj;وه به&zwnj;ڕاستی خوا بیسه&zwnj;ری (بڕیاریانه&zwnj;)و زانایه (به نیه&zwnj;تیان).';
$t[228] = 'ژنانی ته&zwnj;ڵاقدراو، ده&zwnj;بێت تا سێ جار عاده&zwnj;تی مانگانه چاوه&zwnj;ڕێ بکه&zwnj;ن و (شوونه&zwnj;که&zwnj;نه&zwnj;وه&zwnj;) و حه&zwnj;ڵاڵ نیه بۆیان ئه&zwnj;وه&zwnj;ی خوا له منداڵدانیاندا دروستی کردووه (منداڵ یان حه&zwnj;یز) بیشارنه&zwnj;وه&zwnj;، ئه&zwnj;گه&zwnj;ر باوه&zwnj;ڕیان به خواو به&zwnj;رۆژی دوایی دامه&zwnj;زراوه&zwnj;، (له&zwnj;و ماوه&zwnj;یه&zwnj;دا) هاوسه&zwnj;ره&zwnj;کانیان شایسته&zwnj;ن به&zwnj;گێڕانه&zwnj;وه&zwnj;یان له ماوه&zwnj;ی چاوه&zwnj;روانی و عیده&zwnj;دا، ئه&zwnj;گه&zwnj;ر ویستیان چاکسازی له ژیانی خۆیاندا بکه&zwnj;ن (ئه&zwnj;مه&zwnj;ش بۆ ته&zwnj;ڵاقی ڕه&zwnj;جعی یه&zwnj;)، ژنان مافیان هه&zwnj;یه به&zwnj;سه&zwnj;ر پیاوانه&zwnj;وه به&zwnj;وێنه&zwnj;ی ئه&zwnj;و مافانه&zwnj;ی که بۆ پیاوانه له&zwnj;سه&zwnj;ر ژنان به&zwnj;گوێره&zwnj;ی باو و نه&zwnj;ریتێک که په&zwnj;سه&zwnj;نده له ئیسلامدا و پیاوان به&zwnj;سه&zwnj;ر ئافره&zwnj;تانه&zwnj;وه یه&zwnj;ک پله&zwnj;یان هه&zwnj;یه (ئه&zwnj;ویش ده&zwnj;سه&zwnj;ڵاتی گێڕانه&zwnj;وه&zwnj;ی ژنه&zwnj;کانیانه له&zwnj;کاتی ته&zwnj;ڵاقی ڕه&zwnj;جعیدا)و خوا باڵاده&zwnj;ست و دانایه (باڵاده&zwnj;سته به&zwnj;سه&zwnj;ر هه&zwnj;موواندا و دانایه له دابه&zwnj;شکردنی ماف و ئه&zwnj;رکه&zwnj;کاندا).';
$t[229] = 'ته&zwnj;ڵاقێک (که پیاو بتوانێت هاوسه&zwnj;ره&zwnj;که&zwnj;ی تێدا بگێڕێته&zwnj;وه&zwnj;) دووجاره&zwnj;، دووکه&zwnj;ڕه&zwnj;ته (پیاو بۆی هه&zwnj;یه ته&zwnj;ڵاقی هاوسه&zwnj;ره&zwnj;که&zwnj;ی بدات و چاکترین شێوازی ئه&zwnj;وه&zwnj;یه که له سوننه&zwnj;تی پێغه&zwnj;مبه&zwnj;ردا (صلی الله علیه وسلم) ڕوون کراوه&zwnj;ته&zwnj;وه&zwnj;)، له&zwnj;وه&zwnj;ودوا واته بۆ سێهه&zwnj;م جار ئیتر ئه&zwnj;گه&zwnj;ر ده&zwnj;یه&zwnj;وێت ژیانی هاوسه&zwnj;ریی بباته سه&zwnj;ر (نابێت ته&zwnj;ڵاق بێت به&zwnj;ده&zwnj;میداو ئه&zwnj;گه&zwnj;ر هات) ده&zwnj;بێت به&zwnj;چاکی و به&zwnj;ڕێک و پێکی ده&zwnj;ست به&zwnj;رداری ببێت و بۆتان حه&zwnj;ڵاڵ نیه هیچ شتێکیان لێ بسێننه&zwnj;وه له ماره&zwnj;یی و ئه&zwnj;و شتانه&zwnj;ی پێتان به&zwnj;خشیوون، مه&zwnj;گه&zwnj;ر بترسن له&zwnj;وه&zwnj;ی که نه&zwnj;توانن سنووره&zwnj;کانی خوا چاودێری بکه&zwnj;ن و (سته&zwnj;م له یه&zwnj;ک بکه&zwnj;ن)، خۆ ئه&zwnj;گه&zwnj;ر (ئه&zwnj;ی قازییه&zwnj;کان، ئه&zwnj;ی خێرخوازان) ترسان له&zwnj;وه&zwnj;ی که ژن ومێرده&zwnj;که سنووری خوا ڕاناگرن و (خۆیان گوناهبار ده&zwnj;که&zwnj;ن) ئه&zwnj;وه ئێوه هیچ گوناهبار نابن کاتێک که دڵنیان ئافره&zwnj;ته&zwnj;که ده&zwnj;یه&zwnj;وێت (به هه&zwnj;ندێک سامان، یان ده&zwnj;ست هه&zwnj;ڵگرتن له هه&zwnj;ندێک ماره&zwnj;یی) خۆی ڕزگار بکات، دڵنیابن ئه&zwnj;و شتانه&zwnj;ی که باسکران سنووره&zwnj;کانی خوای په&zwnj;روه&zwnj;ردگارن، لێی مه&zwnj;ترازێن و سنوورشکێنی مه&zwnj;که&zwnj;ن، ئه&zwnj;وه&zwnj;ی ده&zwnj;ستدرێژی بکاته سه&zwnj;ر سنووره&zwnj;کانی خوا، ئا ئه&zwnj;و جۆره که&zwnj;سانه سته&zwnj;مکارن.';
$t[230] = 'خۆ ئه&zwnj;گه&zwnj;ر (بۆ جاری سێهه&zwnj;م، پیاو ژنه&zwnj;که&zwnj;ی خۆی) ته&zwnj;ڵاقدا، ئه&zwnj;وه ئیتر جارێکی تر بۆی حه&zwnj;ڵاڵ نابێت مه&zwnj;گه&zwnj;ر دوای ئه&zwnj;وه&zwnj;ی شوویه&zwnj;کی تر بکات و (هاوسه&zwnj;ری دووهه&zwnj;می به&zwnj;شێوه&zwnj;یه&zwnj;کی ئاسایی ته&zwnj;ڵاقی بدات، یان بمرێت)، ئه&zwnj;وه بۆیان هه&zwnj;یه بگه&zwnj;ڕێنه&zwnj;وه بۆ لای یه&zwnj;کترو (ژیانی ژن و مێردایه&zwnj;تی ده&zwnj;ست پێبکه&zwnj;نه&zwnj;وه&zwnj;)، ئه&zwnj;گه&zwnj;ر گومان و ڕایان وابوو ده&zwnj;توانن سنووره&zwnj;کانی خوا ڕاگرن و (ژیانێکی ئاسوده&zwnj;یی ببه&zwnj;نه سه&zwnj;ر)، جا ئه&zwnj;وه&zwnj;ی که باسکرا سنووره&zwnj;کانی خوای گه&zwnj;وره&zwnj;ن، ڕوونیان ده&zwnj;کاته&zwnj;وه بۆ گه&zwnj;لێک که بزانن و تێبگه&zwnj;ن.';
$t[231] = 'کاتێک ژنانی هاوسه&zwnj;رتان ته&zwnj;ڵاقداو ئه&zwnj;وانیش گه&zwnj;یشتنه کۆتایی (عیدده&zwnj;یان) ئه&zwnj;وسا یان بیانهێڵنه&zwnj;وه لای خۆتان به&zwnj;چاکه و ڕێزه&zwnj;وه&zwnj;، یان ده&zwnj;ست به&zwnj;رداریان ببن به چاکه و ڕێزه&zwnj;وه&zwnj;، مه&zwnj;یان هێڵنه&zwnj;وه به ئێش و ئازاره&zwnj;وه بۆ ئه&zwnj;وه&zwnj;ی ده&zwnj;ست درێژی و سته&zwnj;میان لێبکه&zwnj;ن، هه&zwnj;رکه&zwnj;س ئه&zwnj;وه بکات ئه&zwnj;وه سته&zwnj;می له خۆی کردووه&zwnj;، نه&zwnj;که&zwnj;ن ئایه&zwnj;ت و ڕێنمووییه&zwnj;کانی خوا به&zwnj;گاڵته بگرن، یادی نازو نیعمه&zwnj;تی خوا بکه&zwnj;نه&zwnj;وه له&zwnj;سه&zwnj;رتان، هه&zwnj;روه&zwnj;ها یادی ئه&zwnj;و شتانه بکه&zwnj;نه&zwnj;وه که له قورئاندا بۆتان ڕه&zwnj;وانه کراوه که پڕه له دانایی، له&zwnj;کاتێکدا ئامۆژگاریتان ده&zwnj;کات به&zwnj;وانه و هه&zwnj;وڵ بده&zwnj;ن خواناس و ئاین په&zwnj;روه&zwnj;ر بن و چاک بزانن که&zwnj;: به&zwnj;ڕاستی، خوا به هه&zwnj;موو شتێک زانایه&zwnj;.';
$t[232] = 'هه&zwnj;روه&zwnj;ها ئه&zwnj;گه&zwnj;ر ژنانتان ته&zwnj;ڵاقداو گه&zwnj;یشتنه کۆتایی عاده&zwnj;ی مانگانه&zwnj;یان، ئێوه (ئه&zwnj;ی که&zwnj;سوکاری ژن) مه&zwnj;بنه کۆسپ له ڕێگه&zwnj;یاندا ئه&zwnj;گه&zwnj;ر ویستیان شوو بکه&zwnj;نه&zwnj;وه به هاوسه&zwnj;ره&zwnj;کانیان، (یاخود ویستیان شوویه&zwnj;کی تر بکه&zwnj;ن)، کاتێک ئه&zwnj;وان له نێوان خۆیاندا ڕه&zwnj;زامه&zwnj;ندییان نواند به شێوه&zwnj;یه&zwnj;کی جوان، ئه&zwnj;م (فه&zwnj;رمان و ڕێنمووییانه&zwnj;) ئامۆژگاری ئه&zwnj;و که&zwnj;سانه&zwnj;ی پێ ده&zwnj;کرێت له ئێوه که باوه&zwnj;ڕی به خواو ڕۆژی قیامه&zwnj;ت هه&zwnj;بێت، ئه&zwnj;و جۆره ڕه&zwnj;فتار و کرده&zwnj;وانه&zwnj;تان جوانترو په&zwnj;سه&zwnj;ندترو خاوێنتره&zwnj;، خوای په&zwnj;روه&zwnj;ردگاریش چاک ده&zwnj;زانێرت (چی خێره بۆتان) و ئێوه نایزانن و ده&zwnj;رکی ناکه&zwnj;ن.';
$t[233] = 'دایکان (ته&zwnj;ڵاقدراو بن یان نه&zwnj;ء) دوو ساڵی ته&zwnj;واو با شیر به منداڵه&zwnj;کانیان بده&zwnj;ن بۆ هه&zwnj;ر که&zwnj;س که ده&zwnj;یه&zwnj;وێت شیری ته&zwnj;واو بدات و پێویسته له&zwnj;سه&zwnj;ر ئه&zwnj;و که&zwnj;سه&zwnj;ی خاوه&zwnj;نی منداڵه&zwnj;که&zwnj;یه (واته باوکی یان به&zwnj;خێوکه&zwnj;ری) ڕزق و ڕۆزی و پۆشاک به شێوه&zwnj;یه&zwnj;کی جوان و په&zwnj;سه&zwnj;ندی ئایین ئاماده بکات، ئه&zwnj;رک له&zwnj;سه&zwnj;ر هیچ که&zwnj;س دانانرێت جگه له توانای خۆی، نابێت هیچ دایکێک زیانی پێ بگه&zwnj;یه&zwnj;نرێت به&zwnj;هۆی منداڵه&zwnj;که&zwnj;یه&zwnj;وه&zwnj;، هه&zwnj;روه&zwnj;ها باوکیشی نابێت زیانی پێ بگه&zwnj;یه&zwnj;نرێت به&zwnj;هۆیمنداڵه&zwnj;که&zwnj;یه&zwnj;وه و له&zwnj;سه&zwnj;ر میراتگری منداڵه&zwnj;که&zwnj;شه وێنه&zwnj;ی ئه&zwnj;و مافانه&zwnj;ی که باسکران، خۆ ئه&zwnj;گه&zwnj;ر دایک و باوک هه&zwnj;ر کاتێک به ڕه&zwnj;زامه&zwnj;ندی و پرس و ڕای هه&zwnj;ردوولایان ویستیان منداڵه&zwnj;که&zwnj;یان له شیر ببڕنه&zwnj;وه&zwnj;، ئه&zwnj;وه قه&zwnj;یناکات و هیچ گوناهیان له&zwnj;سه&zwnj;ر نی یه له&zwnj;و باره&zwnj;یه&zwnj;وه&zwnj;، خۆ ئه&zwnj;گه&zwnj;ر ویستتان دایه&zwnj;ن بۆ شیر پێدانی منداڵه&zwnj;کانتان بگرن، ئه&zwnj;وه&zwnj;ش قه&zwnj;یناکات و هیچ گوناهتان له&zwnj;سه&zwnj;ر نی یه&zwnj;، به&zwnj;مه&zwnj;رجێک ماف و کرێیان به&zwnj;جوانی و شایسته پێ بده&zwnj;ن، له خوا بترسن و پارێزکاربن و چاک بزانن به&zwnj;ڕاستی خوا زۆر بینایه به&zwnj;و کارو کرده&zwnj;وانه&zwnj;ی که ئه&zwnj;نجامی ده&zwnj;ده&zwnj;ن.';
$t[234] = 'ئه&zwnj;و پیاوانه&zwnj;تان که ده&zwnj;مرن و هاوسه&zwnj;ران به&zwnj;جێ ده&zwnj;هێڵن، پێویسته ئه&zwnj;و هاوسه&zwnj;رانه چوارمانگ و ده شه&zwnj;و خۆیان بگرن و چاوه&zwnj;ڕێ بکه&zwnj;ن (شوو نه&zwnj;که&zwnj;نه&zwnj;وه&zwnj;)، جا که گه&zwnj;یشتنه کۆتایی ماوه&zwnj;ی عیدده&zwnj;که&zwnj;یان، ئه&zwnj;وه ئیتر هیچ گوناهتان له&zwnj;سه&zwnj;ر نی یه ئه&zwnj;ی که&zwnj;س و کاری ژن که ڕێ بده&zwnj;ن ئه&zwnj;و ئافره&zwnj;تانه بچنه&zwnj;وه دۆخی جارانیان له&zwnj;وه&zwnj;ی که شه&zwnj;رع حه&zwnj;ڵاڵی کردووه بۆیان پێش عیدده&zwnj;، خوای په&zwnj;روه&zwnj;ردگاریش زۆر ئاگاداره به&zwnj;و کارو کرده&zwnj;وانه&zwnj;ی ئه&zwnj;نجامی ده&zwnj;ده&zwnj;ن.';
$t[235] = 'هیچ گوناهێکیشتان له&zwnj;سه&zwnj;ر نی یه له&zwnj;وه&zwnj;ی به ناڕاسته&zwnj;وخۆ خوازبێنی ژنانی مێرد مردوو بکه&zwnj;ن (که هێشتا ماوه&zwnj;ی دیاریکراویان ته&zwnj;واو نه&zwnj;بووه و دروست نیه شوو بکه&zwnj;نه&zwnj;وه&zwnj;)، یاخود له دڵی خۆتاندا بڕیاری خوازبێنیتان دابێت، خوای گه&zwnj;وره به&zwnj;ڕاستی زانیویه&zwnj;تی که ئێوه له&zwnj;مه&zwnj;ودوا ناویان ده&zwnj;به&zwnj;ن و باسیان ده&zwnj;که&zwnj;ن، به&zwnj;ڵام نه&zwnj;که&zwnj;ن به نهێنی به&zwnj;ڵێنیان لێ واربگرن، مه&zwnj;گه&zwnj;ر به&zwnj;شێوازی گوفتارێکی چاک و دروست و باو نه&zwnj;بێت، نه&zwnj;که&zwnj;ن بڕیاری ته&zwnj;واوی ماره&zwnj;بڕینیان بده&zwnj;ن هه&zwnj;تا ماوه&zwnj;ی دیاری کراو ته&zwnj;واو نه&zwnj;بێت و چاکیش بزانن به&zwnj;ڕاستی خوا ده&zwnj;زانێت به&zwnj;وه&zwnj;ی که له دڵ و ده&zwnj;روونتاندایه&zwnj;، که&zwnj;واته خۆتان بپارێزن (له قه&zwnj;ده&zwnj;غه کراوه&zwnj;کانی خوا)، دڵنیابن و چاک بزانن که خوا زۆر لێخۆشبوو و خۆڕاگرو له&zwnj;سه&zwnj;رخۆیه&zwnj;.';
$t[236] = 'هیچ گوناهتان له&zwnj;سه&zwnj;رنی یه ئه&zwnj;گه&zwnj;ر ژنانێک ته&zwnj;ڵاق بده&zwnj;ن که هێشتا نه&zwnj;چووبنه لایان، یاخود ماره&zwnj;یتان بۆ دیاری نه&zwnj;کرد بوون، پێویسته له&zwnj;سه&zwnj;رتان هه&zwnj;ندێک سامانیان بده&zwnj;نێ، جا ئه&zwnj;وه&zwnj;ی ده&zwnj;وڵه&zwnj;مه&zwnj;نده&zwnj;و زۆری هه&zwnj;یه و ئه&zwnj;وه&zwnj;ی که هه&zwnj;ژارو که&zwnj;م ده&zwnj;سته (هه&zwnj;رکه&zwnj;س) به&zwnj;پێی توانای خۆی، ئه&zwnj;م دیاری و یارمه&zwnj;تییانه به&zwnj;شێوه&zwnj;یه&zwnj;ک بێت که باوو په&zwnj;سه&zwnj;ندی شه&zwnj;رع بێت، ئه&zwnj;مه&zwnj;ش ئه&zwnj;رکێکی پێویسته له&zwnj;سه&zwnj;ر چاکه&zwnj;کاران.';
$t[237] = 'خۆ ئه&zwnj;گه&zwnj;ر ژنانتان ته&zwnj;ڵاقدا پێش ئه&zwnj;وه&zwnj;ی بچنه لایان، له هه&zwnj;مان کاتیشدا ماره&zwnj;ییتان بۆ دیاری کردبوون، ئه&zwnj;وه ده&zwnj;بێت نیوه&zwnj;ی ماره&zwnj;ییه دیاری کراوه&zwnj;که&zwnj;یان بده&zwnj;نێ، مه&zwnj;گه&zwnj;ر خۆیان نه&zwnj;یانه&zwnj;وێت و چاوپۆشی لێبکه&zwnj;ن، یان ئه&zwnj;و که&zwnj;سه چاوپۆشی بکات که ماره بڕینه&zwnj;که&zwnj;ی به&zwnj;ده&zwnj;سته&zwnj;، چاوپۆشی و لێبووردنتان له مافی خۆتان له پارێزکاریه&zwnj;وه نزیکتره&zwnj;و چاکه&zwnj;و پیاوه&zwnj;تی نێوان یه&zwnj;کتر فه&zwnj;رامۆش مه&zwnj;که&zwnj;ن، به&zwnj;ڕاستی خوا بینایه به&zwnj;و کارو کرده&zwnj;وانه&zwnj;ی که ئه&zwnj;نجامی ده&zwnj;ده&zwnj;ن.';
$t[238] = 'که&zwnj;مته&zwnj;رخه&zwnj;می مه&zwnj;که&zwnj;ن و پارێزگاری له ئه&zwnj;نجامدانی نوێژه&zwnj;کاندا بکه&zwnj;ن به&zwnj;تایبه&zwnj;تی نوێژی ناوه&zwnj;ند (که نوێژی عه&zwnj;سره&zwnj;) هه&zwnj;میشه&zwnj;و به&zwnj;رده&zwnj;وامیش هه&zwnj;ستن به ئه&zwnj;نجامدانی فه&zwnj;رمانه&zwnj;کان بۆ خوا به&zwnj;گه&zwnj;ردن که&zwnj;چیه&zwnj;وه&zwnj;.';
$t[239] = 'ئینجا ئه&zwnj;گه&zwnj;ر ترسان (که نوێژه&zwnj;که&zwnj;تان وه&zwnj;کو پێویست ئه&zwnj;نجام نه&zwnj;ده&zwnj;ن به&zwnj;هۆی جه&zwnj;نگ یان مه&zwnj;ترسیه&zwnj;که&zwnj;وه&zwnj;) ده&zwnj;توانن به&zwnj;ده&zwnj;م ڕۆیشتنه&zwnj;وه یان به سواری ئه&zwnj;نجامی بده&zwnj;ن، هه&zwnj;رکاتێک ئه&zwnj;مین بوونه&zwnj;وه و هه&zwnj;ستتان به هێمنی کرد، ئینجا یادی خوا بکه&zwnj;ن و (نوێژه&zwnj;کانتان به&zwnj;چاکی ئه&zwnj;نجام بده&zwnj;ن) به&zwnj;و شێوه&zwnj;یه&zwnj;ی که فێری کردوون پێشتر نه&zwnj;تانده&zwnj;زانی.';
$t[240] = 'ئه&zwnj;و پیاوانه&zwnj;تان که ده&zwnj;مرن و هاوسه&zwnj;ران به&zwnj;جێ ده&zwnj;هێڵن، (ده&zwnj;بێت به&zwnj;فه&zwnj;رمانی خوا) وه&zwnj;سیه&zwnj;ت بکه&zwnj;ن بۆ هاوسه&zwnj;رانیان که تا ساڵێک له ماڵی هاوسه&zwnj;ریاندا ڕێزیان لێبگیرێت و به&zwnj;خێو بکرێن و ده&zwnj;رنه&zwnj;کرێن، خۆ ئه&zwnj;گه&zwnj;ر به&zwnj;ویستی خۆیان ده&zwnj;رچوون ئه&zwnj;وه ئیتر هیچ گوناهتان له&zwnj;سه&zwnj;رنی یه ئه&zwnj;ی که&zwnj;س و کاری ژن که ڕێ بده&zwnj;ن ئه&zwnj;و ئافره&zwnj;تانه بچنه&zwnj;وه دۆخی جارانیان له&zwnj;وه&zwnj;ی که شه&zwnj;رع حه&zwnj;ڵاڵی کردووه بۆیان پێش عیدده&zwnj;، دڵنیاش بن که خوا باڵاده&zwnj;سته (ئه&zwnj;گه&zwnj;ر سته&zwnj;میان لێبکه&zwnj;ن)و دانایه (له ڕێنموویی و دانانی سنووره&zwnj;کانیدا).';
$t[241] = 'بۆ ژنانی ته&zwnj;ڵاقدراو بڕێک سامان و پاره پێویسته له&zwnj;سه&zwnj;ر پیاوانی له خواترس و پارێزکار.';
$t[242] = 'ئا به&zwnj;و شێوه&zwnj;یه خوا فه&zwnj;رمانه&zwnj;کانی خۆیتان بۆ ڕوون ده&zwnj;کاته&zwnj;وه (ده&zwnj;رباره&zwnj;ی مافی ژنان) بۆ ئه&zwnj;وه&zwnj;ی ژیربن و بیرو هۆشتان بخه&zwnj;نه&zwnj;کار.';
$t[243] = 'ئایا نه&zwnj;تبیستووه و نه&zwnj;تڕوانیوه&zwnj;ته ئه&zwnj;وانه&zwnj;ی له ترسی مردن له ماڵ و شوێنه&zwnj;واریان ده&zwnj;رچوون، له&zwnj;کاتێکدا که هه&zwnj;زاره&zwnj;ها که&zwnj;سیش بوون، (ترسان له به&zwnj;ره&zwnj;نگار بوونه&zwnj;وه&zwnj;ی دوژمن)، خوایش فه&zwnj;رمووی پێیان: بمرن! (دوای ئه&zwnj;وه&zwnj;ی مردن) پاشان زیندووی کردنه&zwnj;وه (تا بزانن ڕاکردن و خۆشاردنه&zwnj;وه له جیهادو به&zwnj;ره&zwnj;نگار بوونه&zwnj;وه&zwnj;ی دوژمن مانای ڕزگار بوون له مردن نیه&zwnj;)، به&zwnj;ڕاستی خوا خاوه&zwnj;نی چاکه&zwnj;و به&zwnj;خشش و فه&zwnj;زڵی زۆره به&zwnj;سه&zwnj;ر خه&zwnj;ڵکییه&zwnj;وه (چاوپۆشی له که&zwnj;م ته&zwnj;رخه&zwnj;مییان ده&zwnj;کات و مۆڵه&zwnj;تیان ده&zwnj;دات)، هه&zwnj;رچه&zwnj;نده زۆربه&zwnj;ی خه&zwnj;ڵکی سوپاسی خوا ناکه&zwnj;ن و سپڵه&zwnj;ن.';
$t[244] = 'که&zwnj;واته ئیتر مه&zwnj;ترسن و) له ڕێگه&zwnj;ی خوادا بجه&zwnj;نگن، چاک بزانن و دڵنیابن به&zwnj;ڕاستی خوا بیسه&zwnj;رو زانایه (به هه&zwnj;موو گفتوگۆو هه&zwnj;وڵ و کۆشش و چالاکی و جیهادو خه&zwnj;باتتان).';
$t[245] = 'کێیه ئه&zwnj;و که&zwnj;سه قه&zwnj;رز ده&zwnj;دات به&zwnj;خوای په&zwnj;روه&zwnj;ردگار به قه&zwnj;رزێکی چاک (به&zwnj;خشراوه&zwnj;که له ماڵی حه&zwnj;ڵاڵ و بێ منه&zwnj;ت و نازدار بێت)، خوایش به&zwnj;و هۆیه&zwnj;وه بۆی چه&zwnj;ند به&zwnj;رامبه&zwnj;ر ده&zwnj;کات و پاداشتی بێ سنووری پێ ده&zwnj;به&zwnj;خشێت، (به&zwnj;ڕاستی خوایه&zwnj;کی به&zwnj;ڕێزو دلۆڤانمان هه&zwnj;یه&zwnj;، جێگه&zwnj;ی سه&zwnj;رسوڕمان و سه&zwnj;رنجه ئه&zwnj;و شێوازه&zwnj;ی که هانی ئیماندارانی پێ ده&zwnj;دات، بۆ به&zwnj;خشین به یه&zwnj;کترو بۆ به&zwnj;خشین بۆ ڕزگاری گرۆی ئاده&zwnj;میزاد)، خوایش ڕزق و ڕۆزیی و ده&zwnj;رامه&zwnj;ت ده&zwnj;گرێته&zwnj;وه و فراوانی ده&zwnj;کات (به حیکمه&zwnj;ت و ویستی خۆی) هه&zwnj;ر بۆ لای ئه&zwnj;و زاته&zwnj;ش ده&zwnj;گه&zwnj;ڕێنه&zwnj;وه&zwnj;.';
$t[246] = 'ئایا نه&zwnj;تبیستووه و نه&zwnj;تڕوانیوه&zwnj;ته به&zwnj;سه&zwnj;رهاتی ده&zwnj;سته&zwnj;یه&zwnj;ک له گه&zwnj;وره پیاوانی نه&zwnj;وه&zwnj;ی ئیسرائیل له دوای موسا له&zwnj;کاتێکدا به پێغه&zwnj;مبه&zwnj;ره&zwnj;که&zwnj;ی خۆیانیان وت: پاشایه&zwnj;ک، سه&zwnj;رکرده&zwnj;یه&zwnj;کمان بۆ دیاری بکه و بینێره تا له پێناوی خوادا بجه&zwnj;نگین، پێغه&zwnj;مبه&zwnj;ره&zwnj;که وتی: باشه&zwnj;؛ ئه&zwnj;ی ئه&zwnj;گه&zwnj;ر جه&zwnj;نگتان له&zwnj;سه&zwnj;ر بڕیاردراو خه&zwnj;ریک بوو یه&zwnj;خه&zwnj;تان بگرێت و په&zwnj;شیمان بوونه&zwnj;وه و نه&zwnj;جه&zwnj;نگان؟ (له وه&zwnj;ڵامدا) وتیان: چیمانه که له پێناوی خوادا نه&zwnj;جه&zwnj;نگین، له کاتێکیشدا ئه&zwnj;وه&zwnj;ته ده&zwnj;ربه&zwnj;ده&zwnj;رکراوین و له ماڵ و منداڵ و وڵاتمان بووین، که&zwnj;چی کاتێک جه&zwnj;نگیان له&zwnj;سه&zwnj;ر بڕیاردا، ته&zwnj;نها که&zwnj;مێکیان نه&zwnj;بێت ئه&zwnj;وانی تر هه&zwnj;موویان پشتیان هه&zwnj;ڵکردو په&zwnj;شیمان بوونه&zwnj;وه&zwnj;، خوایش زاناو به&zwnj;ئاگایه به سته&zwnj;مکاران.';
$t[247] = 'ئینجا پێغه&zwnj;مبه&zwnj;ره&zwnj;که&zwnj;یان (به&zwnj;وانه&zwnj;ی که ئاماده&zwnj;ی جه&zwnj;نگ بوون) وتی: ئه&zwnj;وه ئیتر خوا به ڕاستی بڕیاری داوه&zwnj;و طالوطی ڕه&zwnj;وانه کردووه که پاشا و سه&zwnj;رکرده&zwnj;تان بێت، که&zwnj;چی (هه&zwnj;ندێکیان) وتیان: چۆن ئه&zwnj;و ببێته سه&zwnj;رکرده به&zwnj;سه&zwnj;رمانه&zwnj;و، ئێمه شایسته&zwnj;ترین به سه&zwnj;رکرده&zwnj;یی له&zwnj;و، ئه&zwnj;و ماڵ و سامانێکی زۆری نیه&zwnj;! (جووله&zwnj;که هه&zwnj;میشه ماڵ و سامان ده&zwnj;که&zwnj;نه پێوه&zwnj;ر له هه&zwnj;موو شتێکدا)، (پێغه&zwnj;مبه&zwnj;ره&zwnj;که له وه&zwnj;ڵامیاندا) وتی: به&zwnj;ڕاستی خوا (طالوط)ی هه&zwnj;ڵبژاردووه و په&zwnj;سه&zwnj;ندی کردووه به&zwnj;سه&zwnj;رتانداو زانست و زانیاری زۆرو لاشه&zwnj;یه&zwnj;کی به&zwnj;هێزو گه&zwnj;وره&zwnj;شی داوه&zwnj;تێ، خوای گه&zwnj;وره&zwnj;ش پاشایه&zwnj;تی ده&zwnj;به&zwnj;خشێت به هه&zwnj;ر که&zwnj;س که بیه&zwnj;وێت و (شایسته بێت) و خوا فراوانگیرو زانایه&zwnj;.';
$t[248] = 'ئه&zwnj;وسا پێغه&zwnj;مبه&zwnj;ره&zwnj;که&zwnj;یان پێی وتن: به&zwnj;ڕاستی نیشانه&zwnj;ی پاشایه&zwnj;تی طالوط ئه&zwnj;وه&zwnj;یه&zwnj;: سندووقه&zwnj;که&zwnj;تان بۆ دێته&zwnj;وه&zwnj;و، ئاسووده&zwnj;یی و ئارامی تێدایه له لایه&zwnj;ن په&zwnj;روه&zwnj;ردگارتانه&zwnj;وه&zwnj;یه&zwnj;، هه&zwnj;روه&zwnj;ها هه&zwnj;ندێک پاشماوه&zwnj;ی به&zwnj;نرخ و که&zwnj;لپوری تێدایه له&zwnj;وه&zwnj;ی که بنه&zwnj;ماڵه&zwnj;ی موسا و بنه&zwnj;ماڵه&zwnj;ی هاروون به&zwnj;جێیان هێشتووه&zwnj;و له&zwnj;کاتێکدا که فریشته هه&zwnj;ڵیده&zwnj;گرن و ده&zwnj;یهێننه&zwnj;وه بۆتان، به&zwnj;ڕاستی هه&zwnj;ر له&zwnj;وه&zwnj;دا به&zwnj;ڵگه&zwnj;و نیشانه&zwnj;ی ته&zwnj;واو هه&zwnj;یه بۆتان ئه&zwnj;گه&zwnj;ر باوه&zwnj;ڕدارن.';
$t[249] = 'ئینجا له&zwnj;کاتێکدا طالوط سه&zwnj;ربازانی جیاکرده&zwnj;وه&zwnj;و پێی وتن (وا ئێمه ده&zwnj;ڕۆین)، بێگومان خوا (له ڕێ گوزه&zwnj;رماندا) تاقیتان ده&zwnj;کاته&zwnj;وه به ڕووبارێک (دوای ماندوو بوون و تینوویه&zwnj;تی)، جا ئه&zwnj;وه&zwnj;ی لێی بخواته&zwnj;وه (ئه&zwnj;وه سه&zwnj;ربازی) من نیه و (بگه&zwnj;ڕێته&zwnj;وه&zwnj;) و ئه&zwnj;وه&zwnj;ی ئه&zwnj;یچێژێت یان ته&zwnj;نها لوێچێک به&zwnj;ده&zwnj;ستی خۆی بخواته&zwnj;وه ئه&zwnj;وه به&zwnj;ڕاستی سه&zwnj;ربازی منه و (ده&zwnj;په&zwnj;ڕێته&zwnj;وه&zwnj;و ده&zwnj;ڕۆین، جا له&zwnj;گه&zwnj;ڵ ئه&zwnj;و ڕوون کردنه&zwnj;وه&zwnj;یه&zwnj;شدا) زۆربه&zwnj;یان (له ئاوی ڕووباره&zwnj;که&zwnj;یان) خوارده&zwnj;وه&zwnj;، که&zwnj;مێکیان نه&zwnj;بێت، جا کاتێک لێی په&zwnj;ڕینه&zwnj;وه خۆی و ئه&zwnj;وانه&zwnj;ی باوه&zwnj;ڕیان هێنابوو له&zwnj;گه&zwnj;ڵیدا (هه&zwnj;ندێکیان) وتیان: ئه&zwnj;مڕۆ (زه&zwnj;حمه&zwnj;ته به&zwnj;م ژماره که&zwnj;مه&zwnj;وه&zwnj;) ده&zwnj;ره&zwnj;قه&zwnj;تی جالوط و سه&zwnj;ربازانی بێین، (به&zwnj;ڵام) ئه&zwnj;وانه&zwnj;ی دڵنیا بوون به خزمه&zwnj;تی په&zwnj;روه&zwnj;ردگاریان ده&zwnj;گه&zwnj;ن وتیان: (مه&zwnj;ترسن) چونکه چه&zwnj;نده&zwnj;ها جار ده&zwnj;سته&zwnj;ی که&zwnj;م، زاڵ بوون به&zwnj;سه&zwnj;ر ده&zwnj;سته&zwnj;ی زۆردا به&zwnj;فه&zwnj;رمانی خواو خوا یارو یاوه&zwnj;ری خۆگرو ئارامگرانه&zwnj;.';
$t[250] = 'کاتێک له به&zwnj;رامبه&zwnj;ر جالوط و سه&zwnj;ربازانیه&zwnj;وه وه&zwnj;ستان (نزایان کردو) وتیان: په&zwnj;روه&zwnj;ردگارا خۆگری و ئارامگریمان پێ ببه&zwnj;خشه&zwnj;، (دڵ و ده&zwnj;روون و) پێکانمان دامه&zwnj;زراو بکه و سه&zwnj;رمان بخه به&zwnj;سه&zwnj;ر قه&zwnj;وم و هۆزی بێباوه&zwnj;ڕاندا.';
$t[251] = 'ئه&zwnj;وسا ئیتر به فه&zwnj;رمانی خوا له&zwnj;شکری جالوطیان به&zwnj;زاند و داود (که یه&zwnj;کێک بوو له سه&zwnj;ربازانی طالوط) جالوطی کوشت، له&zwnj;وه&zwnj;ودوا خوای گه&zwnj;وره پاشایه&zwnj;تی و دانایی پێبه&zwnj;خشی و هه&zwnj;رچی ویستی بوو فێری کرد، خۆ ئه&zwnj;گه&zwnj;ر خوا هه&zwnj;ندێک له خه&zwnj;ڵکانی به هه&zwnj;ندێکی تری له&zwnj;ناو نه&zwnj;بات، هه&zwnj;ر هه&zwnj;موو سه&zwnj;ر زه&zwnj;وی پڕ ده&zwnj;بێت له تاوان و خراپه&zwnj;، به&zwnj;ڵام خوا فه&zwnj;زڵ و ڕێزی زۆره بۆ هه&zwnj;موو گرۆی ئادامیزاد (به&zwnj;شێوه&zwnj;یه&zwnj;کی گشتی).';
$t[252] = 'ئا ئه&zwnj;وانه&zwnj;ی که باسکران ئایات و به&zwnj;ڵگه&zwnj;و موعجیزه&zwnj;ی خوای گه&zwnj;وره&zwnj;ن به&zwnj;ڕاستی و دروستی به&zwnj;سه&zwnj;ر تۆدا ده&zwnj;یخوێنینه&zwnj;وه و به&zwnj;ڕاستی تۆ (ئه&zwnj;ی محمد (صلی الله علیه وسلم) یه&zwnj;کێکیت له پێغه&zwnj;مبه&zwnj;رانی خاوه&zwnj;ن په&zwnj;یام.';
$t[253] = 'ئه&zwnj;و پێغه&zwnj;مبه&zwnj;رانه&zwnj;ی (باسکران) ڕێزی هه&zwnj;ندێکیانمان داوه به&zwnj;سه&zwnj;ر هاندێکی تریاندا، هه&zwnj;یانه خوا گفتوگۆی (یه&zwnj;کسه&zwnj;ری) له&zwnj;گه&zwnj;ڵدا کردووه (وه&zwnj;ک موسا) و پله&zwnj;وپایه&zwnj;ی هه&zwnj;ندێکیانی به&zwnj;رز کردۆته&zwnj;وه به چه&zwnj;نده&zwnj;ها پله (وه&zwnj;ک محمد (صلی الله علیه وسلم) و چه&zwnj;نده&zwnj;ها به&zwnj;ڵگه&zwnj;ی ڕوون و ئاشکرامان به&zwnj;خشیووه به&zwnj;عیسای کوڕی مه&zwnj;ریه&zwnj;م، (وه&zwnj;ک زیندووکردنه&zwnj;وه&zwnj;ی مردوو، چاکردنه&zwnj;وه&zwnj;ی نه&zwnj;خۆشی زگماک،.. هتد به&zwnj;ویستی خوا) و پشتگیریمان کردووه به فریشته&zwnj;ی سرووش که (جوبره&zwnj;ئیل)ه&zwnj;، خۆ ئه&zwnj;گه&zwnj;ر خوا ویستی له&zwnj;سه&zwnj;ر بوایه (ئه&zwnj;و خه&zwnj;ڵکه هه&zwnj;مووی باوه&zwnj;ڕدار ده&zwnj;بوون)، دوای هاتنی پێغه&zwnj;مبه&zwnj;ره&zwnj;کانیان جه&zwnj;نگیان دژبه&zwnj;یه&zwnj;ک نه&zwnj;ده&zwnj;کرد، پاش ئه&zwnj;وه&zwnj;ی چه&zwnj;نده&zwnj;ها به&zwnj;ڵگه&zwnj;ی ئاشکرایان (له&zwnj;سه&zwnj;رده&zwnj;ستی پێغه&zwnj;مبه&zwnj;ران) بۆ هاتبوو، به&zwnj;ڵام دووبه&zwnj;ره&zwnj;کی که&zwnj;وته نێوانیانه&zwnj;وه&zwnj;، هه&zwnj;ندێکیان ئیمان و باوه&zwnj;ڕی هێناو، هه&zwnj;ندێکیشیان بێ باوه&zwnj;ڕ بوو، ئه&zwnj;گه&zwnj;ر خوا ویستی له&zwnj;سه&zwnj;ر بوایه جه&zwnj;نگیان دژ به&zwnj;یه&zwnj;ک نه&zwnj;ده&zwnj;کردو یه&zwnj;کتریان نه&zwnj;ده&zwnj;کوشت، به&zwnj;ڵام خوا هه&zwnj;رچیه&zwnj;کی بوێت هه&zwnj;ر ئه&zwnj;وه ئه&zwnj;نجام ده&zwnj;دات هه&zwnj;ر ئه&zwnj;وه پێش ده&zwnj;هێنێت.';
$t[254] = 'ئه&zwnj;ی ئه&zwnj;وانه&zwnj;ی باوه&zwnj;ڕتان هێناوه له&zwnj;وماڵ و سامان ودارایی و (عیلم و زانست و تواناییه&zwnj;)ی که پێمان به&zwnj;خشیوون؛ ببه&zwnj;خشن، پێش ئه&zwnj;وه&zwnj;ی ڕۆژێک بێت که کڕین و فرۆشتن ودۆستایه&zwnj;تی و تکاکاری تیادانیه&zwnj;، (ئه&zwnj;و ڕۆژه&zwnj;) بێ باوه&zwnj;ڕان هه&zwnj;ر خۆیان له ڕیزی سته&zwnj;مکاراندان.';
$t[255] = 'خوا زاتێکه جگه ئه&zwnj;و هیچ خوایه&zwnj;کی ترنیه که شایه&zwnj;نی په&zwnj;رستن بێت و ئه&zwnj;و هه&zwnj;میشه زیندووه و ڕاگرو سه&zwnj;رپه&zwnj;رشتیاری (هه&zwnj;موو دروست کراوه&zwnj;کانیه&zwnj;تی)، نه وه&zwnj;نه&zwnj;وز ده&zwnj;یگرێت و نه خه&zwnj;و (چونکه ڕاگرو سه&zwnj;رپه&zwnj;رشتیاری بوونه&zwnj;وه&zwnj;ره&zwnj;)، هه&zwnj;رچی له ئاسمانه&zwnj;کان و هه&zwnj;رچی له زه&zwnj;ویدا هه&zwnj;یه هه&zwnj;ر ئه&zwnj;وزاته خاوه&zwnj;نیانه&zwnj;، کێ یه ئه&zwnj;وه&zwnj;ی (ده&zwnj;توانێت) تکا بکات له لای ئه&zwnj;و، به&zwnj;بێ مۆڵه&zwnj;تی خۆی، ده&zwnj;شزانێت چی له ئێستاو داهاتوو و ڕابووردودا ڕوویداوه&zwnj;و ڕووده&zwnj;دات، هیچ کام له دروستکراوانی زانست و زانیاری ته&zwnj;واویان نیه ده&zwnj;رباره&zwnj;ی زانیاری و زانسته&zwnj;کانی ئه&zwnj;و، مه&zwnj;گه&zwnj;ر به&zwnj;وه&zwnj;ی که خۆی بیه&zwnj;وێت فێریان بکات (ئه&zwnj;م پێشکه&zwnj;وتن و زانسته&zwnj;ی سه&zwnj;رده&zwnj;م به&zwnj;شێکی که&zwnj;من له به&zwnj;خششه&zwnj;کانی خوا)، فه&zwnj;رمانڕه&zwnj;وایی و ده&zwnj;سه&zwnj;ڵات و زانینی ئه&zwnj;وزاته هه&zwnj;موو ئاسمانه&zwnj;کان و زه&zwnj;وی گرتۆته&zwnj;وه و پارێزگاریان به&zwnj;لای خواوه هیچ گران نیه و ماندووی ناکات و هه&zwnj;ر ئه&zwnj;و خوایه&zwnj;کی به&zwnj;رزو بڵند و گه&zwnj;وره&zwnj;یه&zwnj;.';
$t[256] = 'به&zwnj;هیچ جۆرێک زۆر کردن نیه له وه&zwnj;رگرتنی بیروباوه&zwnj;ڕی ئیسلامدا (دینداری به&zwnj;زۆر نابێت بکرێت و دین به&zwnj;زۆر نادرێت به&zwnj;سه&zwnj;ر که&zwnj;سدا)، چونکه به&zwnj;ڕاستی ڕێبازی چاک و دروست؛ ڕوون و ئاشکرا بووه و جیابۆته&zwnj;وه له گومڕایی و سه&zwnj;رکه&zwnj;شی، جا ئه&zwnj;وه&zwnj;ی باوه&zwnj;ڕی نه&zwnj;بێت به ـ تاغوت ـ (که بریتیه له&zwnj;: هه&zwnj;موو ڕێگه&zwnj;و ڕێبازو هێزو به&zwnj;رنامه&zwnj;و لادان و بتێک)و باوه&zwnj;ڕی دامه&zwnj;زراو بهێنێت به&zwnj;خوا، جا بێگومان ئه&zwnj;وکه&zwnj;سه ده&zwnj;ستی گرتووه به به&zwnj;هێزترین هۆکاری ڕزگارییه&zwnj;وه&zwnj;، شوێن دامه&zwnj;زراوترین بیروباوه&zwnj;ڕ که&zwnj;وتووه&zwnj;، که هیچ پسان و هه&zwnj;ڵوه&zwnj;شاندنه&zwnj;وه&zwnj;یه&zwnj;کی بۆ نی یه و هه&zwnj;میشه پته&zwnj;و و دامه&zwnj;زراوه&zwnj;، خوای گه&zwnj;وره&zwnj;ش بیسه&zwnj;رو زانایه&zwnj;.';
$t[257] = 'خوا پشت و په&zwnj;نا و خاوه&zwnj;نی ئه&zwnj;و که&zwnj;سانه&zwnj;یه باوه&zwnj;ڕیان هێناوه&zwnj;، له تاریکاییه&zwnj;کانی (گومان و سته&zwnj;م) ده&zwnj;ریان ده&zwnj;هێنێت و ده&zwnj;یانبات به&zwnj;ره&zwnj;و نوورو ڕووناکی، ئه&zwnj;وانه&zwnj;ش که ڕێبازی کوفریان گرتۆته&zwnj;به&zwnj;ر، یارمه&zwnj;تی ده&zwnj;ره&zwnj;کانیان ته&zwnj;نها ـ تاغوته&zwnj;کانه&zwnj;، له نوورو ڕووناکی ده&zwnj;ریان ده&zwnj;هێنن و به&zwnj;ره&zwnj;و تاریکاییه&zwnj;کان ده&zwnj;یانبه&zwnj;ن، ئا ئه&zwnj;وانه نیشته&zwnj;جێی ناو ئاگری دۆزه&zwnj;خن و بۆ هه&zwnj;میشه تیایدا ده&zwnj;مێننه&zwnj;وه&zwnj;.';
$t[258] = 'ئایا نه&zwnj;تبیستووه و نه&zwnj;تڕوانیوه&zwnj;ته ئه&zwnj;و که&zwnj;سه&zwnj;ی ده&zwnj;مه&zwnj;قاڵه&zwnj;ی ده&zwnj;کرد له&zwnj;گه&zwnj;ڵ ئیبراهیمدا له باره&zwnj;ی په&zwnj;روه&zwnj;ردگاریه&zwnj;وه له&zwnj;به&zwnj;ر ئه&zwnj;وه&zwnj;ی خوا پاشایه&zwnj;تی پێبه&zwnj;خشی بوو، (له جیاتی باوه&zwnj;ڕهێنان و سوپاسگوزاری که&zwnj;چی ده&zwnj;مه ده&zwnj;مێی ده&zwnj;کرد له&zwnj;گه&zwnj;ڵ ئیبراهیمدا)، (ئه&zwnj;م لووت به&zwnj;رزیه&zwnj;شی کاتێک بوو به هه&zwnj;واو فیزاوه پرسیوویه&zwnj;تی له ئیبراهیم که په&zwnj;روه&zwnj;ردگاری تۆ کێیه&zwnj;؟ ئه&zwnj;ویش له وه&zwnj;ڵامدا وتی: په&zwnj;روه&zwnj;ردگاری من ئه&zwnj;وزاته&zwnj;یه که ژیان ده&zwnj;به&zwnj;خشێت و مردنیشی به&zwnj;ده&zwnj;سته&zwnj;، (نه&zwnj;مروود له وه&zwnj;ڵامیدا) وتی: جا منیش ژیان ده&zwnj;به&zwnj;خشم و مردنیشم به&zwnj;ده&zwnj;سته&zwnj;، (ئینجا فه&zwnj;رمانیدا هه&zwnj;ندێک له نه&zwnj;یارانی بکوژن گوایه ئه&zwnj;وه ده&zwnj;یانمرێنێت، هه&zwnj;ندێکیشی نه&zwnj;کوشت گوایه ئه&zwnj;وه ژیانی پێبه&zwnj;خشین!!)، ئه&zwnj;وسا ئیبراهیم وتی: به&zwnj;ڕاستی خوا خۆر له ڕۆژهه&zwnj;ڵاته&zwnj;وه ده&zwnj;هێنێت جا (ئه&zwnj;گه&zwnj;ر تۆ ده&zwnj;توانیت) له ڕۆژئاواوه بیهێنه&zwnj;، ئینجا ئه&zwnj;و بێ باوه&zwnj;ڕه ڕه&zwnj;نگی په&zwnj;ڕی و ده&zwnj;مکوت کراو وه&zwnj;ڵامی نه&zwnj;ما، بێگومان خوا هیدایه&zwnj;تی قه&zwnj;ومی زاڵم و سته&zwnj;مکار نادات.';
$t[259] = 'یاخود وه&zwnj;ک ئه&zwnj;و که&zwnj;سه&zwnj;ی تێپه&zwnj;ڕی به&zwnj;لای شارۆچکه&zwnj;یه&zwnj;کدا که هه&zwnj;موو خانووه&zwnj;کانی به&zwnj;سه&zwnj;ر سه&zwnj;قفه&zwnj;کانیدا ته&zwnj;پی بوون، له&zwnj;به&zwnj;ر خۆیه&zwnj;وه وتی: ئاخۆ خوای گه&zwnj;وره چۆن خه&zwnj;ڵکی ئه&zwnj;م شارۆچکه&zwnj;یه دوای مردنیان زیندوو ده&zwnj;کاته&zwnj;وه&zwnj;؟! (ئه&zwnj;و که&zwnj;سه عوزه&zwnj;یر پێغه&zwnj;مبه&zwnj;ر بوو (صلی الله علیه وسلم) یه&zwnj;کسه&zwnj;ر خوای گه&zwnj;وره سه&zwnj;د ساڵ مراندی و پاشان زیندووی کرده&zwnj;وه و پێی فه&zwnj;رموو: چه&zwnj;ند مایته&zwnj;وه&zwnj;؟ (عوزه&zwnj;یر) وتی: ڕۆژێک یان که&zwnj;متر له ڕۆژێک ماومه&zwnj;ته&zwnj;وه&zwnj;، (خوا) فه&zwnj;رمووی: نه&zwnj;خێر ئه&zwnj;وه سه&zwnj;د ساڵت پێچووه&zwnj;، جا ته&zwnj;ماشای خواردن و خواردنه&zwnj;وه&zwnj;که&zwnj;ت بکه (له&zwnj;و ماوه درێژه&zwnj;دا) نه&zwnj;گۆڕاوه و تێک نه&zwnj;چووه&zwnj;، ته&zwnj;ماشای گوێدرێژه&zwnj;که&zwnj;شت بکه&zwnj;، (بۆیه ئه&zwnj;مه&zwnj;مانکرد) تا به&zwnj;م به&zwnj;سه&zwnj;رهاته بتکه&zwnj;ینه موعجیزه بۆ خه&zwnj;ڵکی، جا سه&zwnj;یری ئێسکه&zwnj;کانی (گوێدرێژه&zwnj;که&zwnj;ت بکه&zwnj;) چۆن به&zwnj;رزیان ده&zwnj;که&zwnj;ینه&zwnj;وه و به&zwnj;یه&zwnj;که&zwnj;وه&zwnj;یان ده&zwnj;به&zwnj;ستین و پاشان به&zwnj;گۆشت دایانده&zwnj;پۆشین، کاتێک بینی و بۆی ده&zwnj;رکه&zwnj;وت وتی: چاک ده&zwnj;زانم و دڵنیام به&zwnj;ڕاستی خوا ده&zwnj;سه&zwnj;ڵاتی به&zwnj;سه&zwnj;ر هه&zwnj;موو شتێکدا هه&zwnj;یه&zwnj;.';
$t[260] = 'به یاد بهێنه&zwnj;، کاتێک ئیبراهیم وتی: په&zwnj;روه&zwnj;ردگارا نیشانم بده چۆن مردوو زیندوو ده&zwnj;که&zwnj;یته&zwnj;وه&zwnj;؟ خوا فه&zwnj;رمووی: ئایا باوه&zwnj;ڕت نه&zwnj;هێناوه&zwnj;؟ ئیبراهیم وتی: به&zwnj;ڵێ (باوه&zwnj;ڕم هه&zwnj;یه&zwnj;) به&zwnj;ڵام حه&zwnj;زده&zwnj;که&zwnj;م (به&zwnj;چاوی خۆم بیبینم) تا دڵم ئارام بگرێت، خوا فه&zwnj;رمووی: جا که&zwnj;واته چوار باڵنده بهێنه&zwnj;و جوان سه&zwnj;رنجیان بده&zwnj;و دوایی هه&zwnj;ر چواریان سه&zwnj;ربڕه&zwnj;و گۆشت و په&zwnj;ڕ و خوێنیان تێکه&zwnj;ڵ بکه&zwnj;، ئه&zwnj;وسا له&zwnj;سه&zwnj;ر لوطکه&zwnj;ی هه&zwnj;ر شاخێک هه&zwnj;ندێک له&zwnj;و تێکه&zwnj;ڵه&zwnj;یه دابنێ، پاشان بانگیان بکه&zwnj;، به&zwnj;گورجی و به&zwnj;په&zwnj;له هه&zwnj;موویان دێنه&zwnj;وه بۆ لات و چاک بزانه و دڵنیابه به&zwnj;ڕاستی خوا باڵاداست و کار به&zwnj;جێ یه&zwnj;.';
$t[261] = 'نمونه&zwnj;ی ئه&zwnj;وانه&zwnj;ی که ماڵ و سامانیان ده&zwnj;به&zwnj;خشن له پێناوی خوادا، وه&zwnj;کو تۆوێک وایه که بڕوێت و حه&zwnj;وت گوڵی گرتبێت، له هه&zwnj;ر گوڵێکدا سه&zwnj;د دانه&zwnj;ی تیادا بێت، خوایش بۆ هه&zwnj;ر که&zwnj;س که بیه&zwnj;وێت (به گوێره&zwnj;ی نیه&zwnj;تی و جێگرتنی خێره&zwnj;که&zwnj;ی) پاداشت چه&zwnj;ند به&zwnj;رامبه&zwnj;ر ده&zwnj;کات و خوا فراوانگیر و زانایه&zwnj;.';
$t[262] = 'ئه&zwnj;وانه&zwnj;ی ماڵ و داراییان ده&zwnj;به&zwnj;خشن له ڕێگه&zwnj;ی خوادا، به&zwnj;دوای ئه&zwnj;وه&zwnj;ی به&zwnj;خشیوویانه هیچ جۆره منه&zwnj;تێک نانێن و هیچ جۆره ئازارێک ناگه&zwnj;یه&zwnj;نن، (ئه&zwnj;وانه&zwnj;) ته&zwnj;نها پاداشتیان لای په&zwnj;روه&zwnj;ردگاریانه&zwnj;، هیچ ترس و بیمیان له&zwnj;سه&zwnj;رنیه (له که&zwnj;م و کووڕیی پاداشت و ته&zwnj;نگانه&zwnj;ی قیامه&zwnj;ت)، غه&zwnj;م و په&zwnj;ژاره&zwnj;ش ڕوویان تێ ناکات.';
$t[263] = 'قسه&zwnj;و گوفتاری جوان و چاوپۆشیه&zwnj;ک (له هه&zwnj;ڵه&zwnj;)، چاکتره له به&zwnj;خشینێک که ئازاری به&zwnj;دوادا بێت و منه&zwnj;ت بکرێت، خوایش ده&zwnj;وڵه&zwnj;مه&zwnj;ندو بێ نیازه (له به&zwnj;خشینی به&zwnj;منه&zwnj;ت) و خۆڕاگرو له&zwnj;سه&zwnj;رخۆیه (زوو تۆڵه ناسێنێت له سه&zwnj;رکه&zwnj;ش و یاخییه&zwnj;کان).';
$t[264] = 'ئه&zwnj;ی ئه&zwnj;وانه&zwnj;ی باوه&zwnj;ڕتان هێناوه&zwnj;، نه&zwnj;که&zwnj;ن خێرو پاداشتی چاکه&zwnj;کانتان پووچ بکه&zwnj;نه&zwnj;وه به منه&zwnj;ت نان و دڵ ئازاردانی (هه&zwnj;ژاران)، وه&zwnj;کو ئه&zwnj;و که&zwnj;سه&zwnj;ی ماڵ و سامانی خۆی له&zwnj;به&zwnj;ر چاوی خه&zwnj;ڵکی ببه&zwnj;خشێت بۆ ڕیاکاریی و خۆنواندن و ئیمان و باوه&zwnj;ڕی به&zwnj;خواو ڕۆژی دوایی نه&zwnj;بێت، جا نموونه&zwnj;ی ئه&zwnj;و جۆره که&zwnj;سانه وه&zwnj;ک به&zwnj;ردێکی ساف وایه که خۆڵێکی به سه&zwnj;ره&zwnj;وه بێت، له&zwnj;وه&zwnj;ودوا بارانێکی لێزمه ببارێت به&zwnj;سه&zwnj;ریداو هیچی به&zwnj;سه&zwnj;ره&zwnj;وه نه&zwnj;هێڵێت و به&zwnj;ره&zwnj;ق و ته&zwnj;قی به&zwnj;جێی بهێڵێت و حه&zwnj;قیقه&zwnj;تی ده&zwnj;ربکه&zwnj;وێت، (ئینجا ئه&zwnj;و جۆره ڕیابازو بێ باوه&zwnj;ڕ منه&zwnj;تنه&zwnj;رانه&zwnj;) هیچ جۆره به&zwnj;هره&zwnj;یه&zwnj;ک له&zwnj;و به&zwnj;خشینه&zwnj;یان به&zwnj;ده&zwnj;ست ناهێنن، بێگومان خوای گه&zwnj;وره ڕێنموویی قه&zwnj;ومی بێ باوه&zwnj;ڕو کافران ناکات.';
$t[265] = 'نموونه&zwnj;ی ئه&zwnj;وانه&zwnj;ی ماڵ و سامانیان ده&zwnj;به&zwnj;خشن له پێناوی به&zwnj;ده&zwnj;ستهێنانی ڕه&zwnj;زامه&zwnj;ندی خوادا و بۆ دامه&zwnj;زراندن و ڕاهێنانی خۆیان له&zwnj;سه&zwnj;ر چاکه&zwnj;کاری، وه&zwnj;کو باخچه&zwnj;یه&zwnj;ک وایه: له به&zwnj;رزاییه&zwnj;کدا بێت و بارانێکی دڵۆپ گه&zwnj;وره بیگرێته&zwnj;وه و ببێته هۆی ئه&zwnj;وه&zwnj;ی که دوو به&zwnj;رامبه&zwnj;ر به&zwnj;رهه&zwnj;می هه&zwnj;بێت، خۆ ئه&zwnj;گه&zwnj;ر بارانی دڵۆپ گه&zwnj;وره&zwnj;ش نه&zwnj;یگرێته&zwnj;وه&zwnj;، ئه&zwnj;وه نمه باران پاراوی ده&zwnj;کات، خوای گه&zwnj;وره&zwnj;ش بینایه به&zwnj;و کارو کرده&zwnj;وانه&zwnj;ی ده&zwnj;یکه&zwnj;ن.';
$t[266] = 'ئایا هیچ که&zwnj;س له ئێوه حه&zwnj;ز ده&zwnj;کات باخێکی هه&zwnj;بێت له دارخورماو چه&zwnj;نده&zwnj;ها جۆر ڕه&zwnj;زه ترێ پێک هاتبێت و چه&zwnj;ند جۆگه&zwnj;و ڕووبارێک به&zwnj;ژێر دره&zwnj;خته&zwnj;کانیدا بڕوات و هه&zwnj;موو جۆره به&zwnj;روبوومێکی تێدا بێت بۆ خاوه&zwnj;نه&zwnj;که&zwnj;ی و خۆی دووچاری پیری بووبێت و چه&zwnj;ند نه&zwnj;وه&zwnj;و منداڵێکی لاوازی هه&zwnj;بێت، که&zwnj;چی له&zwnj;ناکاو گه&zwnj;رده&zwnj;لولێک گڕوگه&zwnj;رمیه&zwnj;کی به&zwnj;هێزی تێدابێت و هه&zwnj;ڵکاته&zwnj;سه&zwnj;ری و یه&zwnj;کسه&zwnj;ربسووتێت؟! (ئه&zwnj;مه زۆر جێی داخ و نیگه&zwnj;رانیه چونکه نه&zwnj;خۆی له&zwnj;به&zwnj;ر پیری، نه&zwnj;منداڵی، له&zwnj;به&zwnj;ر بێ هێزی، ناتوانن باخه&zwnj;که ئاوه&zwnj;دان بکه&zwnj;نه&zwnj;وه&zwnj;)، ئا به&zwnj;و شێوه&zwnj;یه خوا ئایه&zwnj;ت و به&zwnj;ڵگه&zwnj;و نیشانه&zwnj;کان ده&zwnj;خاته به&zwnj;رچاوتان بۆ ئه&zwnj;وه&zwnj;ی تێفکرن و بیربکه&zwnj;نه&zwnj;وه (خۆتان مایه&zwnj;پووچ و ڕه&zwnj;نج به&zwnj;خه&zwnj;سار نه&zwnj;که&zwnj;ن).';
$t[267] = 'ئه&zwnj;ی ئه&zwnj;وانه&zwnj;ی باوه&zwnj;ڕتان هێناوه&zwnj;: ببه&zwnj;خشن له باشترین به&zwnj;روبووم و به&zwnj;رهه&zwnj;مێک که به&zwnj;ده&zwnj;ستان هێناوه و له&zwnj;و به&zwnj;روبوومانه&zwnj;ی که له زه&zwnj;ویدا بۆمان ده&zwnj;رهێناون و ڕواندوومانه&zwnj;، نه&zwnj;که&zwnj;ن نیازی به&zwnj;خشینی شتی ناپوخت و بێ که&zwnj;ڵک بکه&zwnj;ن و له&zwnj;و ببه&zwnj;خشن، له&zwnj;کاتێکدا خۆتان ئه&zwnj;و جۆره شتانه وه&zwnj;رناگرن و پێی ڕازی نابن (ئه&zwnj;گه&zwnj;ر بتاندرێتێ) مه&zwnj;گه&zwnj;ر چاوی لێ بنوقێنن و بزانن که خوا ده&zwnj;وڵه&zwnj;مه&zwnj;ندو بێ نیازه و شایسته&zwnj;ی سوپاسه (پێویستی به&zwnj;به&zwnj;خشینی بێ نرخ نیه&zwnj;).';
$t[268] = '(کاتێ ئێوه له ماڵ و داراییتان ده&zwnj;به&zwnj;خشن) شه&zwnj;یتان به&zwnj;ڵێنی هه&zwnj;ژاری و نه&zwnj;داریتان پێ ده&zwnj;دات (ده&zwnj;تانترسێنێت به هه&zwnj;ژاری) و فه&zwnj;رمانتان پێ ده&zwnj;دات به&zwnj;گوناهو تاوان و خراپه و نه&zwnj;به&zwnj;خشین، خوای گه&zwnj;وره&zwnj;ش به&zwnj;ڵێنی لێخۆشبوونێکی تایبه&zwnj;تی خۆیتان پێده&zwnj;دات له&zwnj;گه&zwnj;ڵ زۆری ڕۆزی و به&zwnj;هره&zwnj;ی فراوانداو خوا فراوانگیرو زانایه&zwnj;.';
$t[269] = 'ئه&zwnj;وزاته به&zwnj;هه&zwnj;ر که&zwnj;س بیه&zwnj;وێت و (شایسته بێت) حیکمه&zwnj;ت و دانایی پێ ده&zwnj;به&zwnj;خشێت و هه&zwnj;ر که&zwnj;س حیکمه&zwnj;تی پێ ببه&zwnj;خشرێت، ئه&zwnj;وه بێگومان خێرێکی زۆری پێدراوه&zwnj;، جا بێجگه له ژیر و هۆشمه&zwnj;نده&zwnj;کان که&zwnj;سانی تر له&zwnj;و به&zwnj;هره&zwnj;یه تێناگه&zwnj;ن و په&zwnj;ند وه&zwnj;رناگرن.';
$t[270] = 'هه&zwnj;رشتێکتان به&zwnj;خشی بێت یان هه&zwnj;ر نه&zwnj;زرو بڕیارێکتان دابێت، ئه&zwnj;وه خوا پێی ده&zwnj;زانێت و بۆ سته&zwnj;مکاران هیچ جۆره پشتیوانیه&zwnj;ک نیه (که بیانپارێزێ له تۆڵه&zwnj;ی خوا).';
$t[271] = 'ئه&zwnj;گه&zwnj;ر خێرو چاکه و به&zwnj;خشینه&zwnj;کانتان به ئاشکرا ئه&zwnj;نجام بده&zwnj;ن ئه&zwnj;وه شتێکی چاکه&zwnj;، خۆ ئه&zwnj;گه&zwnj;ر بیشارنه&zwnj;وه و بیده&zwnj;ن به هه&zwnj;ژاران به نهێنی ئه&zwnj;وه چاکتره بۆتان، جا (به&zwnj;و به&zwnj;خشینه&zwnj;) خوا هه&zwnj;ندێک له گوناهه&zwnj;کانتان ده&zwnj;سڕێته&zwnj;وه و خوا ئاگایه به&zwnj;و کارو کرده&zwnj;وانه&zwnj;ی که ئه&zwnj;نجامی ده&zwnj;ده&zwnj;ن.';
$t[272] = 'ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر (صلی الله علیه وسلم) هیدایه&zwnj;ت و ڕێنموویی و موسوڵمان کردنی ئه&zwnj;و خه&zwnj;ڵکه له&zwnj;سه&zwnj;ر تۆ نیه (چونکه تۆ ته&zwnj;نها گه&zwnj;یاندنی په&zwnj;یامه&zwnj;که&zwnj;ت له&zwnj;سه&zwnj;ره&zwnj;) به&zwnj;ڵام خوا هیدایه&zwnj;ت و ڕێنموویی هه&zwnj;ر که&zwnj;سێکی بوێت ده&zwnj;کات، (به مه&zwnj;رجێک ئه&zwnj;و که&zwnj;سه&zwnj;ش ڕووی دڵی بکاته ئیمان) هه&zwnj;ر خێرێکیش ئه&zwnj;نجام بده&zwnj;ن، ئه&zwnj;وه قازانجه&zwnj;که&zwnj;ی بۆ خۆتانه&zwnj;، ئێوه (ئه&zwnj;ی بڕواداران) هیچ شتێک نابه&zwnj;خشن مه&zwnj;گه&zwnj;ر بۆ ڕه&zwnj;زامه&zwnj;ندی خوا نه&zwnj;بێت و هه&zwnj;ر خێرو چاکه&zwnj;یه&zwnj;کیش ئه&zwnj;نجام بده&zwnj;ن پاداشته&zwnj;که&zwnj;یتان ده&zwnj;ستتان ده&zwnj;که&zwnj;وێته&zwnj;وه&zwnj;، ئه&zwnj;وسا ئێوه به&zwnj;هیچ شێوه&zwnj;یه&zwnj;ک سته&zwnj;متان لێناکرێت.';
$t[273] = '(ئه&zwnj;و جۆره به&zwnj;خشینانه پێشکه&zwnj;ش بکه&zwnj;ن) به&zwnj;و هه&zwnj;ژارانه&zwnj;ی له پێناوی خوادا لێبڕاون و ناتوانن بگه&zwnj;ڕێن به زه&zwnj;ویدا و بازرگانی و کاسبی بکه&zwnj;ن (به&zwnj;هۆی خه&zwnj;ریک بوونیان به&zwnj;گه&zwnj;یاندنی په&zwnj;یامی خوا)، ئه&zwnj;وه&zwnj;ی نه&zwnj;یانناسێت، (ئه&zwnj;و هه&zwnj;ژارانه ئه&zwnj;وه&zwnj;نده که&zwnj;سایه&zwnj;تی خۆیان ڕاده&zwnj;گرن و نه&zwnj;فس به&zwnj;رزن)، واده&zwnj;زانێت ده&zwnj;وڵه&zwnj;مه&zwnj;ندن، تۆ ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر (صلی الله علیه وسلم) (یا ئه&zwnj;ی ئیماندار) به ڕه&zwnj;نگ و ڕوخسارو سیمایاندا ده&zwnj;یانناسیت، داواناکه&zwnj;ن له خه&zwnj;ڵکی به&zwnj;زۆر شتی لێبسه&zwnj;نن و ڕووگیریان بکه&zwnj;ن، هه&zwnj;ر خێرێک ئه&zwnj;نجام بده&zwnj;ن ئه&zwnj;وه بێگومان خوا زانایه پێی.';
$t[274] = 'ئه&zwnj;وانه&zwnj;ی ماڵ و سامانیان ده&zwnj;به&zwnj;خشن، به&zwnj;شه&zwnj;و و به ڕۆژ، به نهێنی و به ئاشکرا، ئا ئه&zwnj;وانه هه&zwnj;ر پاداشتیان لای په&zwnj;روه&zwnj;ردگاریانه&zwnj;، ترس و بیمیان له&zwnj;سه&zwnj;ر نییه و ئه&zwnj;وان خه&zwnj;فه&zwnj;تبار نابن (له ڕۆژی قیامه&zwnj;ت و لێپرسینه&zwnj;وه&zwnj;دا).';
$t[275] = 'ئه&zwnj;وانه&zwnj;ی سوو ده&zwnj;خۆن، هه&zwnj;ڵسوکه&zwnj;وت ناکه&zwnj;ن و ڕاست نابنه&zwnj;وه (له گۆڕدا) مه&zwnj;گه&zwnj;ر وه&zwnj;ک ئه&zwnj;و که&zwnj;سه&zwnj;ی که شه&zwnj;یتان ده&zwnj;ستی لێوه&zwnj;شاندبێت (هه&zwnj;ر له دنیادا سروشتی نین، له قیامه&zwnj;تیشدا وه&zwnj;ک شێت ده&zwnj;بن) چونکه ئه&zwnj;وانه ده&zwnj;ڵێن: کڕین و فرۆشتن وه&zwnj;ک سوو وایه (له حاڵێکدا): خوا کڕین و فرۆشتنی حه&zwnj;ڵال کردوه&zwnj;و سووی حه&zwnj;رام کردووه&zwnj;، جا ئه&zwnj;وه&zwnj;ی ئامۆژگاری په&zwnj;روه&zwnj;ردگاری پێگه&zwnj;یشت و وازی هێنا، ئه&zwnj;وه&zwnj;ی ده&zwnj;ستی که&zwnj;وتووه&zwnj;، بۆ خۆی، کاری (له لێ پرسینه&zwnj;وه&zwnj;شی) ده&zwnj;که&zwnj;وێته&zwnj;لای خوا، به&zwnj;ڵام ئه&zwnj;وه&zwnj;ی ده&zwnj;ست بکاته&zwnj;وه&zwnj;به&zwnj;سوو خۆریی، ئا ئه&zwnj;وانه جێنشینی ناو ئاگری دۆزه&zwnj;خن و ژیانی هه&zwnj;میشه&zwnj;یی تیادا ده&zwnj;به&zwnj;نه&zwnj;سه&zwnj;ر.';
$t[276] = 'خوا، سوو، ته&zwnj;فروتونا ده&zwnj;کات و به&zwnj;ره&zwnj;که&zwnj;تی لێ هه&zwnj;ڵده&zwnj;گرێت، به&zwnj;ڵام پاداشتی خێر و سامان به&zwnj;خشین بره&zwnj;و پێ ده&zwnj;دات و به&zwnj;ره&zwnj;که&zwnj;تی تێ ده&zwnj;خات بێگومان خوا هیج کافرێکی تاوانباری خۆشناوێت.';
$t[277] = 'به&zwnj;ڕاستی ئه&zwnj;وانه&zwnj;ی که باوه&zwnj;ڕیان هێناوه و کرده&zwnj;وه چاکه&zwnj;کانیان ئه&zwnj;نجامداوه و نوێژه&zwnj;کانیان به&zwnj;چاکی به&zwnj;جێهێناوه و زه&zwnj;کاتیان داوه&zwnj;، ته&zwnj;نها پاداشتیان لای په&zwnj;روه&zwnj;ردگاریانه&zwnj;، ئه&zwnj;وسا نه ترس و بیمیان له&zwnj;سه&zwnj;ره&zwnj;، نه&zwnj;غه&zwnj;م و په&zwnj;ژاره ده&zwnj;یان گرێته&zwnj;وه&zwnj;.';
$t[278] = 'ئه&zwnj;ی ئه&zwnj;وانه&zwnj;ی باوه&zwnj;ڕتان هێناوه&zwnj;، پارێزکارو خواناس بن و واز له پاشماوه&zwnj;ی سوو بهێنن ئه&zwnj;گه&zwnj;ر ئیماندارن.';
$t[279] = 'خۆ ئه&zwnj;گه&zwnj;ر وانه&zwnj;که&zwnj;ن، ئه&zwnj;وه جه&zwnj;نگ له&zwnj;گه&zwnj;ڵ خواو پێغه&zwnj;مبه&zwnj;ره&zwnj;که&zwnj;یدا ڕابگه&zwnj;یه&zwnj;نن (که سه&zwnj;رئه&zwnj;نجامه&zwnj;که&zwnj;ی زۆر سامناکه&zwnj;)، خۆ ئه&zwnj;گه&zwnj;ر ته&zwnj;وبه بکه&zwnj;ن و (له سوو خۆری واز بهێنن) ئه&zwnj;وه ته&zwnj;نها سه&zwnj;رمایه&zwnj;کانتان بۆ هه&zwnj;یه وه&zwnj;ری بگرنه&zwnj;وه&zwnj;، نه سته&zwnj;م ده&zwnj;که&zwnj;ن (له قه&zwnj;رزاره&zwnj;کان) نه سته&zwnj;متان لێ ده&zwnj;کرێت.';
$t[280] = 'خۆ ئه&zwnj;گه&zwnj;ر قه&zwnj;رزاره&zwnj;که نه&zwnj;دار بوو، ئه&zwnj;وه مۆڵه&zwnj;تی بدرێ تا ده&zwnj;رووی لێ ده&zwnj;کرێته&zwnj;وه و ده&zwnj;یبێت، خێر کردنتان به قه&zwnj;رزاره&zwnj;که و ئازاد کردنی و به&zwnj;خشینی، چاکتره بۆتان ئه&zwnj;گه&zwnj;ر ئێوه بزانن (چه&zwnj;نده پاداشتی خوایی بێ سنووره&zwnj;).';
$t[281] = '(خه&zwnj;ڵکینه&zwnj;) له سزاو تۆڵه&zwnj;ی ڕۆژێک بترسن که تیایدا بۆ لای خوا ده&zwnj;گه&zwnj;ڕێنه&zwnj;وه&zwnj;، له&zwnj;وه&zwnj;ودوا هه&zwnj;موو که&zwnj;س پاداشتی کاروکرده&zwnj;وه&zwnj;ی خۆی وه&zwnj;رده&zwnj;گرێت و سته&zwnj;میان لێ ناکرێت.';
$t[282] = 'ئه&zwnj;ی ئه&zwnj;وانه&zwnj;ی باوه&zwnj;ڕتان هێناوه ئه&zwnj;گه&zwnj;ر قه&zwnj;رزێکتان کرد له یه&zwnj;کتر تا ماوه&zwnj;یه&zwnj;کی دیاری کراو، ئه&zwnj;وه بینوسن و با نووسه&zwnj;رێک که دادپه&zwnj;روه&zwnj;ربێت به&zwnj;ڕاستی و دروستی له نێوانتاندا بینوسێت، با هیچ نوسه&zwnj;رێک سه&zwnj;رپێچی نه&zwnj;کات له نوسین، هه&zwnj;ر وه&zwnj;ک چۆن خوا فێری کردووه با بنووسێت، (خۆ ئه&zwnj;گه&zwnj;ر که&zwnj;سێک خوێنده&zwnj;وار نه&zwnj;بێت) ئه&zwnj;وه&zwnj;ی که حه&zwnj;قه&zwnj;که&zwnj;ی به&zwnj;سه&zwnj;ره&zwnj;وه&zwnj;یه و قه&zwnj;رزه&zwnj;که&zwnj;ی لایه با به&zwnj;ده&zwnj;می خۆی (ئه&zwnj;ندازه&zwnj;ی قه&zwnj;رزه&zwnj;که&zwnj;ی) به کاتبه&zwnj;که بڵێت و له خوای په&zwnj;روه&zwnj;ردگاری بترسێت و چاوپۆشی له هیچی نه&zwnj;کات و لێی که&zwnj;م نه&zwnj;کاته&zwnj;وه&zwnj;، خۆ ئه&zwnj;گه&zwnj;ر ئه&zwnj;وه&zwnj;ی قه&zwnj;رزه&zwnj;که&zwnj;ی له&zwnj;سه&zwnj;ره هه&zwnj;ڵه&zwnj;شه بوو، یا لاواز بوو (منداڵ یان به&zwnj;ساڵاچوو بوو) یا نه&zwnj;یده&zwnj;توانی دان به حه&zwnj;قه&zwnj;که&zwnj;دا بنێت (که&zwnj;ڕوڵاڵ بوو) با به&zwnj;خێوکه&zwnj;ر، یا سه&zwnj;رپه&zwnj;رشتیاره&zwnj;که&zwnj;ی دادگه&zwnj;رانه حه&zwnj;قیقه&zwnj;ت و ڕاستی قه&zwnj;رزه&zwnj;که&zwnj;ی بڵێت و با دوو پیاو له خۆتان شایه&zwnj;تی له&zwnj;سه&zwnj;ر (قه&zwnj;رزو مامه&zwnj;ڵه&zwnj;)بگرن، ئه&zwnj;گه&zwnj;ر دووپیاو ده&zwnj;ستنه&zwnj;که&zwnj;وتن بۆ شایه&zwnj;تی، با پیاوێک و دوو ئافره&zwnj;ت شایه&zwnj;تییه&zwnj;که بده&zwnj;ن له&zwnj;و که&zwnj;سانه&zwnj;ی که ڕازین به شایه&zwnj;تیدانیان، نه&zwnj;وه&zwnj;کو یه&zwnj;کێک له (ئافره&zwnj;ته&zwnj;کان) بیری بچێت، با ئه&zwnj;وی تریان بیری بهێنێته&zwnj;وه و شایه&zwnj;ته&zwnj;کان نابێت که کاتێک داواکران خۆ وێڵ بکه&zwnj;ن و ئاماده نه&zwnj;بن، بێزار مه&zwnj;بن له&zwnj;وه&zwnj;ی که (قه&zwnj;رز) بنووسن، که&zwnj;م بێت یان زۆر و تا ماوه&zwnj;ی دیاری کراوی، به ڕوونی با یاداشت بکرێت، ئه&zwnj;نجامدانی مه&zwnj;سه&zwnj;له&zwnj;ی قه&zwnj;رزتان به&zwnj;و شێوه دادپه&zwnj;روه&zwnj;رانه&zwnj;یه و په&zwnj;سه&zwnj;ندتره لای خوا (له نه&zwnj;نووسین)، ئه&zwnj;وسا چاکتر ده&zwnj;توانن شایه&zwnj;تی ڕاگرن و نزیکترن له&zwnj;وه&zwnj;ی گومان و دوودڵی به یه&zwnj;کتر نه&zwnj;به&zwnj;ن (مافی که&zwnj;س تیا نه&zwnj;چێت)، مه&zwnj;گه&zwnj;ر بازرگانیه&zwnj;ک بێت له&zwnj;به&zwnj;ر ده&zwnj;ستانداو له نێو خۆتاندا بیگێڕن و کڕین و فرۆشتن ئه&zwnj;نجام بده&zwnj;ن، ئه&zwnj;وه گوناهتان ناگات که ناینووسن، (چونکه ئاسان نیه&zwnj;و مامه&zwnj;ڵه دواده&zwnj;خات، هه&zwnj;تا بۆتان ده&zwnj;کرێت) شایه&zwnj;ت بگرن کاتێک کڕین و فرۆشتنتان ئه&zwnj;نجامدا و نابێت نووسه&zwnj;رو شایه&zwnj;ت ئازار بدرێن و بێزار بکرێن، خۆ ئه&zwnj;گه&zwnj;ر شتی وابکه&zwnj;ن ئه&zwnj;وه به&zwnj;ڕاستی مانای وایه ئێوه له چه&zwnj;نده&zwnj;ها فه&zwnj;رمانبه&zwnj;رداری ده&zwnj;رچوون، جا له خوا بترسن و خۆ پارێزبن و خوا فێری ئه&zwnj;و شتانه&zwnj;تان ده&zwnj;کات (که خێری تیایه بۆ دنیاو پاداشتی قیامه&zwnj;تی له دوایه&zwnj;) و خوا به هه&zwnj;موو شتێک زانایه&zwnj;.';
$t[283] = 'خۆ ئه&zwnj;گه&zwnj;ر له سه&zwnj;فه&zwnj;ردا بوون و نووسه&zwnj;رێکتان ده&zwnj;ست نه&zwnj;که&zwnj;وت، ئه&zwnj;وه بارمته&zwnj;کان وه&zwnj;رده&zwnj;گیرێن (له&zwnj;باتی قه&zwnj;باڵه و سه&zwnj;نه&zwnj;ده&zwnj;که&zwnj;)، خۆ ئه&zwnj;گه&zwnj;ر له یه&zwnj;کتری دڵنیا بوون با ئه&zwnj;وه&zwnj;ی به ئه&zwnj;مین و ده&zwnj;ستپاک داده&zwnj;نرێت ئه&zwnj;مانه&zwnj;ته&zwnj;که&zwnj;ی (قه&zwnj;رزه&zwnj;که&zwnj;) بداته&zwnj;وه و با له خوای په&zwnj;روه&zwnj;ردگاری بترسێت، نه&zwnj;که&zwnj;ن شایه&zwnj;تی بشارنه&zwnj;وه&zwnj;، ئه&zwnj;وه&zwnj;ی شایه&zwnj;تی بشارێته&zwnj;وه ئه&zwnj;وه به&zwnj;ڕاستی دڵی ژه&zwnj;نگاوی و ناپوخت گوناهباره&zwnj;، خوایش به هه&zwnj;موو ئه&zwnj;و کرده&zwnj;وانه&zwnj;ی که ده&zwnj;یکه&zwnj;ن زانایه&zwnj;.';
$t[284] = 'هه&zwnj;رچی له ئاسمانه&zwnj;کان و هه&zwnj;رچی له زه&zwnj;ویدا هه&zwnj;یه هه&zwnj;ر خوا خاوه&zwnj;نیانه و ئه&zwnj;گه&zwnj;ر ئه&zwnj;وه&zwnj;ی له دڵ و ده&zwnj;رونتاندا جێگیر بووه&zwnj;، ده&zwnj;ری بخه&zwnj;ن، یان بیشارنه&zwnj;وه خوا ده&zwnj;رباره&zwnj;ی لێتان ده&zwnj;پرسێته&zwnj;وه&zwnj;، ئه&zwnj;وسا له هه&zwnj;ر که&zwnj;س بیه&zwnj;وێت خۆش ده&zwnj;بێت و هه&zwnj;رکه&zwnj;س بیه&zwnj;وێت سزای ده&zwnj;دات و خوایش به&zwnj;سه&zwnj;ر هه&zwnj;موو شتێکدا ده&zwnj;سه&zwnj;ڵاتی هه&zwnj;یه (ئه&zwnj;وه&zwnj;ی چرپه&zwnj;ی ده&zwnj;روونیه دێت و ده&zwnj;ڕوات و جێگیر نابێت لێپرسینه&zwnj;وه&zwnj;ی له&zwnj;سه&zwnj;ر نیه&zwnj;).';
$t[285] = 'پێغه&zwnj;مبه&zwnj;رو ئیمانداران باوه&zwnj;ڕیان هه&zwnj;یه به&zwnj;وه&zwnj;ی له لایه&zwnj;ن په&zwnj;روه&zwnj;ردگاریانه&zwnj;وه هاتۆته خواره&zwnj;وه&zwnj;، هه&zwnj;موویان باوه&zwnj;ڕیان هه&zwnj;یه به خواو فریشته&zwnj;کانی و کتێبه&zwnj;کانی و پێغه&zwnj;مبه&zwnj;ره&zwnj;کانی (ده&zwnj;ڵێن) هیچ جۆره جیاوازیه&zwnj;ک ناکه&zwnj;ین له نێوان هیچ پێغه&zwnj;مبه&zwnj;رێ له پێغه&zwnj;مبه&zwnj;ره&zwnj;کانی خوادا (له باوه&zwnj;ڕدا)، هه&zwnj;روه&zwnj;ها وتیان: گوێڕایه&zwnj;ڵ و ملکه&zwnj;چین (بۆ به&zwnj;دیهێنه&zwnj;رمان) په&zwnj;روه&zwnj;ردگارا لێخۆش بوونی تۆمان ده&zwnj;وێت و سه&zwnj;رئه&zwnj;نجام هه&zwnj;ر بۆ لای تۆیه گه&zwnj;ڕانه&zwnj;وه&zwnj;.';
$t[286] = 'خوای گه&zwnj;وره ئه&zwnj;رک ناخاته سه&zwnj;ر هیچ که&zwnj;سێک مه&zwnj;گه&zwnj;ر به قه&zwnj;ده&zwnj;ر توانایی خۆی نه&zwnj;بێت، هه&zwnj;ر کارێکی چاک ئه&zwnj;نجام بدات قازانجی ته&zwnj;نها بۆ خۆیه&zwnj;تی و هه&zwnj;ر خراپه&zwnj;یه&zwnj;کی کردبێت ته&zwnj;نها یه&zwnj;خه&zwnj;ی خۆی ده&zwnj;گرێت و له&zwnj;سه&zwnj;ر خۆیه&zwnj;تی (هه&zwnj;ر بۆیه ئیمانداران ده&zwnj;ڵێن): په&zwnj;روه&zwnj;ردگارا؛ لێمان مه&zwnj;گره ئه&zwnj;گه&zwnj;ر فه&zwnj;رامۆشیمان به&zwnj;سه&zwnj;ردا هات یان گوناهو هه&zwnj;ڵه&zwnj;یه&zwnj;کمان له ده&zwnj;ست قه&zwnj;وما، په&zwnj;روه&zwnj;ردگارا؛ ئه&zwnj;رکی سه&zwnj;خت و کاری گران مه&zwnj;ده به&zwnj;سه&zwnj;رماندا وه&zwnj;کو ئه&zwnj;وه&zwnj;ی داوته به&zwnj;سه&zwnj;ر ئه&zwnj;وانه&zwnj;ی پێش ئێمه&zwnj;دا، په&zwnj;روه&zwnj;ردگارا؛ ئه&zwnj;رکێک مه&zwnj;ده به&zwnj;سه&zwnj;رماندا له تواناماندا نه&zwnj;بێت، تووشی به&zwnj;ڵاو ناخۆشیه&zwnj;کمان مه&zwnj;که تواناو هێزو هه&zwnj;ناومان لێ ببڕێت، گوناهمان بسڕه&zwnj;وه و چاوپۆشی بکه له هه&zwnj;ڵه&zwnj;و تاوانه&zwnj;کانمان و دای پۆشه&zwnj;، (داواکارین) به&zwnj;زه&zwnj;ییت بێته&zwnj;وه پیاماندا، ڕه&zwnj;حممان پێ بکه&zwnj;، هه&zwnj;ر تۆ پشت و په&zwnj;ناو یاریداده&zwnj;ری ئێمه&zwnj;یت، جا بۆیه داوا ده&zwnj;که&zwnj;ین سه&zwnj;رمان بخه&zwnj;یت به&zwnj;سه&zwnj;ر قه&zwnj;ومی کافرو خوانه&zwnj;ناساندا، (ئا به&zwnj;و شێوه&zwnj;یه په&zwnj;روه&zwnj;ردگاری مه&zwnj;زن فێری نزای ڕێک و پێکمان ده&zwnj;کات، ئومێده که لێشمان وه&zwnj;ربگرێت..) کاتێک ئایه&zwnj;تی (٢٨٤) دابه&zwnj;زیووه هاوه&zwnj;ڵانی پێغه&zwnj;مبه&zwnj;ر (صلی الله علیه وسلم) سه&zwnj;غڵه&zwnj;ت بوون و چوون بۆ خزمه&zwnj;تی.. ئه&zwnj;ویش فه&zwnj;رموویه&zwnj;تی: بڵێن گوێڕایه&zwnj;ڵ و ملکه&zwnj;چین.. ئینجا خوای گه&zwnj;وره ئایه&zwnj;تی (٢٨٥، ٢٨٦)ی دابه&zwnj;زاند بۆ دڵدانه&zwnj;وه&zwnj;یان). خوای گه&zwnj;وره ئه&zwnj;رک ناخاته سه&zwnj;ر هیچ که&zwnj;سێک مه&zwnj;گه&zwnj;ر به قه&zwnj;ده&zwnj;ر توانایی خۆی نه&zwnj;بێت، هه&zwnj;ر کارێکی چاک ئه&zwnj;نجام بدات قازانجی ته&zwnj;نها بۆ خۆیه&zwnj;تی و هه&zwnj;ر خراپه&zwnj;یه&zwnj;کی کردبێت ته&zwnj;نها یه&zwnj;خه&zwnj;ی خۆی ده&zwnj;گرێت و له&zwnj;سه&zwnj;ر خۆیه&zwnj;تی (هه&zwnj;ر بۆیه ئیمانداران ده&zwnj;ڵێن): په&zwnj;روه&zwnj;ردگارا؛ لێمان مه&zwnj;گره ئه&zwnj;گه&zwnj;ر فه&zwnj;رامۆشیمان به&zwnj;سه&zwnj;ردا هات یان گوناهو هه&zwnj;ڵه&zwnj;یه&zwnj;کمان له ده&zwnj;ست قه&zwnj;وما، په&zwnj;روه&zwnj;ردگارا؛ ئه&zwnj;رکی سه&zwnj;خت و کاری گران مه&zwnj;ده به&zwnj;سه&zwnj;رماندا وه&zwnj;کو ئه&zwnj;وه&zwnj;ی داوته به&zwnj;سه&zwnj;ر ئه&zwnj;وانه&zwnj;ی پێش ئێمه&zwnj;دا، په&zwnj;روه&zwnj;ردگارا؛ ئه&zwnj;رکێک مه&zwnj;ده به&zwnj;سه&zwnj;رماندا له تواناماندا نه&zwnj;بێت، تووشی به&zwnj;ڵاو ناخۆشیه&zwnj;کمان مه&zwnj;که تواناو هێزو هه&zwnj;ناومان لێ ببڕێت، گوناهمان بسڕه&zwnj;وه و چاوپۆشی بکه له هه&zwnj;ڵه&zwnj;و تاوانه&zwnj;کانمان و دای پۆشه&zwnj;، (داواکارین) به&zwnj;زه&zwnj;ییت بێته&zwnj;وه پیاماندا، ڕه&zwnj;حممان پێ بکه&zwnj;، هه&zwnj;ر تۆ پشت و په&zwnj;ناو یاریداده&zwnj;ری ئێمه&zwnj;یت، جا بۆیه داوا ده&zwnj;که&zwnj;ین سه&zwnj;رمان بخه&zwnj;یت به&zwnj;سه&zwnj;ر قه&zwnj;ومی کافرو خوانه&zwnj;ناساندا، (ئا به&zwnj;و شێوه&zwnj;یه په&zwnj;روه&zwnj;ردگاری مه&zwnj;زن فێری نزای ڕێک و پێکمان ده&zwnj;کات، ئومێده که لێشمان وه&zwnj;ربگرێت..) کاتێک ئایه&zwnj;تی (٢٨٤) دابه&zwnj;زیووه هاوه&zwnj;ڵانی پێغه&zwnj;مبه&zwnj;ر (صلی الله علیه وسلم) سه&zwnj;غڵه&zwnj;ت بوون و چوون بۆ خزمه&zwnj;تی.. ئه&zwnj;ویش فه&zwnj;رموویه&zwnj;تی: بڵێن گوێڕایه&zwnj;ڵ و ملکه&zwnj;چین.. ئینجا خوای گه&zwnj;وره ئایه&zwnj;تی (٢٨٥، ٢٨٦)ی دابه&zwnj;زاند بۆ دڵدانه&zwnj;وه&zwnj;یان).';
?>