$t[3] = 'به&zwnj;ڵکو یادخه&zwnj;ره&zwnj;وه&zwnj;یه&zwnj;، بۆ ئه&zwnj;و که&zwnj;سه&zwnj;ی که له لێپرسینه&zwnj;وه&zwnj;ی په&zwnj;روه&zwnj;ردگار ده&zwnj;ترسێت.';
$t[4] = 'قورئان، له&zwnj;لایه&zwnj;ن زاتێکه&zwnj;وه به&zwnj;ش به&zwnj;ش دابه&zwnj;زیووه&zwnj;، که زه&zwnj;وی دروست کردووه و ئاسمانه بڵنده&zwnj;کانی به&zwnj;دی هێناوه&zwnj;.';
$t[5] = 'خوای میهره&zwnj;بان به&zwnj;سه&zwnj;ر ته&zwnj;ختی فه&zwnj;مانڕه&zwnj;وایه&zwnj;تی خۆیه&zwnj;وه وه&zwnj;ستاوه (عه&zwnj;رشی ئه&zwnj;و زاته له سه&zwnj;رو حه&zwnj;وت چین ئاسمانه&zwnj;وه&zwnj;یه و له&zwnj;وێوه سه&zwnj;رپه&zwnj;رشتی هه&zwnj;موو به دیهێنراوه&zwnj;کان ده&zwnj;کات، سه&zwnj;رنجی لاپه&zwnj;ڕه 249 ئایه&zwnj;تی ژماره /2 الرعد).';
$t[6] = 'هه&zwnj;رچی له ئاسمانه&zwnj;کان و هه&zwnj;رچی له زه&zwnj;ویداو هه&zwnj;رچی له نێوانیاندا هه&zwnj;یه و هه&zwnj;رچی له ژێر خاکدایه (هه&zwnj;ر هه&zwnj;مووی) خوا خاوه&zwnj;نیانه&zwnj;.';
$t[7] = 'خۆ ئه&zwnj;گه&zwnj;ر به&zwnj;ئاشکرا قسه بکه&zwnj;یت، ئه&zwnj;وه بێگومان ئه&zwnj;و زاته به&zwnj;نهێنی و له نهێنیش په&zwnj;نهانتر ده&zwnj;زانێت.';
$t[8] = 'الله زاتێکه که جگه له&zwnj;و خوایه&zwnj;کی تر نیه&zwnj;، هه&zwnj;رچی ناوه پیرۆزو سیفاته جوانه&zwnj;کان هه&zwnj;یه هه&zwnj;ر شایسته&zwnj;ی ئه&zwnj;وه&zwnj;.';
$t[9] = 'ئه ی پێغه&zwnj;مبه&zwnj;ر &lrm;صلی الله علیه وسلم ئایا هه&zwnj;واڵی به&zwnj;سه&zwnj;رهاتی موسات پێگه&zwnj;یشتووه&zwnj;؟!';
$t[10] = 'کاتێک ئاگرێکی بینی (له گه&zwnj;ڕانه&zwnj;وه&zwnj;یدا بۆ میسر، له شه&zwnj;وێکی ساردا، له بیابانی سینادا) به&zwnj;خێزانکه&zwnj;ی وت: ئێوه لێره بن، به&zwnj;ڕاستی من ئاگرێکم به&zwnj;دی کردووه&zwnj;، (با برۆم) به&zwnj;ڵکو سه&zwnj;لکه بزوتێکتان بۆ بهێنم، یان که&zwnj;سێکم ده&zwnj;ست بکه&zwnj;وێت ڕێنموویم بکات.';
$t[11] = 'جا کاتێک نزیکی ئاگره&zwnj;که بۆوه&zwnj;، بانگی لێکرا: ئه&zwnj;ی موسا..';
$t[12] = 'دڵنیابه من په&zwnj;روه&zwnj;ردگاری تۆم، که&zwnj;واته پێڵاوه&zwnj;کانت دا بکه&zwnj;نه&zwnj;، چونکه بێگومان تۆ له دۆڵی پیرۆزی - طوی- دایت.';
$t[13] = 'منیش هه&zwnj;ڵم بژاردوویت (بۆ ڕابه&zwnj;رایه&zwnj;تی و پێغه&zwnj;مبه&zwnj;رایه&zwnj;تی) جا گوێ بگره بۆ ئه&zwnj;و شتانه&zwnj;ی له ڕێگه&zwnj;ی وه&zwnj;حی و نیگاوه پێت ڕاده&zwnj;گه&zwnj;یه&zwnj;نرێت.';
$t[14] = 'به&zwnj;ڕاستی هه&zwnj;ر من (الله&zwnj;) م، من خوام، هیچ خوایه&zwnj;ک نیه جگه له من، که&zwnj;واته هه&zwnj;ر من بپه&zwnj;ر&zwnj;سته و نوێژه&zwnj;کانیشت به&zwnj;چاکی ئه&zwnj;نجام بده&zwnj;، تا هه&zwnj;میشه منت له یاد بێت.';
$t[15] = 'بێگومان ڕۆژی قیامه&zwnj;ت به&zwnj;ڕێوه&zwnj;یه و هه&zwnj;ر دێت، نزیکه بیشارمه&zwnj;وه&zwnj;، بۆ ئه&zwnj;وه&zwnj;ی هه&zwnj;موو که&zwnj;سێک به گوێره&zwnj;ی کارو کرده&zwnj;وه و هه&zwnj;وڵ و کۆششی پاداشت بدرێته&zwnj;وه&zwnj;.';
$t[16] = '(ئه&zwnj;ی موسا): نه&zwnj;یه&zwnj;ڵیت ئه&zwnj;و که&zwnj;سانه&zwnj;ی که باوه&zwnj;ڕیان به&zwnj;قیامه&zwnj;ت نیه و شوێنی ئاره&zwnj;زووی خۆیان که&zwnj;وتوون، (له&zwnj;و ڕاستیه&zwnj;) وێڵت بکه&zwnj;ن، چونکه ئه&zwnj;وسا تیاده&zwnj;چیت و زه&zwnj;ره&zwnj;ر ده&zwnj;که&zwnj;یت.';
$t[17] = '(ئینجا خوای گه&zwnj;وره پرسی) ئه&zwnj;ی موسا: ئه&zwnj;ی ئه&zwnj;وه چیه، به&zwnj;ده&zwnj;ستی ڕاسته&zwnj;وه (گرتووته&zwnj;).';
$t[18] = 'موسا وتی: ئه&zwnj;وه گۆچانه&zwnj;که&zwnj;مه&zwnj;، خۆمی به&zwnj;سه&zwnj;ردا ده&zwnj;ده&zwnj;م و (ده&zwnj;یماڵم به&zwnj;دره&zwnj;ختدا) تا گه&zwnj;ڵا بوه&zwnj;رێنم بۆ مه&zwnj;ڕه&zwnj;کانم، چه&zwnj;ند مه&zwnj;به&zwnj;ستێکی تریشم تێیدا هه&zwnj;یه&zwnj;.';
$t[19] = 'خوا فه&zwnj;رمووی: ده فرێی بده ئه&zwnj;ی موسا.';
$t[20] = 'جا هه&zwnj;ر که فرێی دا، یه&zwnj;کسه&zwnj;ر بوو به مارێکی زیندوو، به&zwnj;خێرایی ده&zwnj;جوڵایه&zwnj;وه&zwnj;.';
$t[21] = 'خوا فه&zwnj;رمووی: بیگره و مه&zwnj;ترسه&zwnj;، وه&zwnj;ک ئه&zwnj;وسای خۆی لێده&zwnj;که&zwnj;ینه&zwnj;وه&zwnj;.';
$t[22] = 'ده&zwnj;ستیشت بخه&zwnj;ره ژێر باڵته&zwnj;وه&zwnj;، ئه&zwnj;و کاته به&zwnj;ڕه&zwnj;نگی سپی و دره&zwnj;وشاوه&zwnj;یی دێته ده&zwnj;ره&zwnj;وه&zwnj;، بێ ئه&zwnj;وه&zwnj;ی عه&zwnj;یبدار ببێت، ئه&zwnj;وه&zwnj;ش نیشانه و به&zwnj;ڵگه&zwnj;یه&zwnj;کی تره&zwnj;.';
$t[23] = '(ئه&zwnj;م شتانه بۆ ئه&zwnj;وه&zwnj;یه&zwnj;) تا هه&zwnj;ندێک له موعجیزه گه&zwnj;وره&zwnj;کانی خۆمانت نیشان بده&zwnj;ین.';
$t[24] = 'ده بچۆ بۆ لای فیرعه&zwnj;ون چونکه بێگومان یاخی یه و له سنوور ده&zwnj;رچووه&zwnj;.';
$t[25] = '(موسا که&zwnj;وته نزا کردن) وتی: په&zwnj;روه&zwnj;ردگارا سینه و دڵ و ده&zwnj;روونم ئاسووده و گوشاد بکه&zwnj;.';
$t[26] = 'ئیش و کارو هه&zwnj;وڵ و کۆششم بۆ ئاسان بکه&zwnj;.';
$t[27] = 'گرێی زمانم بکه&zwnj;ره&zwnj;وه و زمانم گۆیا و پاراو بکه&zwnj;&zwnj;';
$t[28] = 'تا له قسه&zwnj;و گوفتارو مه&zwnj;رامم تێنگه&zwnj;ن.';
$t[29] = 'له که&zwnj;س و کاریشم هاوکار و یارمه&zwnj;تیده&zwnj;رێکم بۆ بڕیاربده&zwnj;.';
$t[30] = '(حه&zwnj;ز ده&zwnj;که&zwnj;م) هاروونی برام بێت،';
$t[31] = 'خوایه پشتمی پێ به&zwnj;هێز بکه&zwnj;،';
$t[32] = 'بیکه هاوبه&zwnj;ش و یاریده&zwnj;ده&zwnj;رم، له&zwnj;گه&zwnj;یاندنی (په&zwnj;یامه&zwnj;که&zwnj;تدا).';
$t[33] = 'تا ته&zwnj;سبیحات و ستایشی تۆ زۆر بکه&zwnj;ین،';
$t[34] = 'زۆر یادی تۆش بکه&zwnj;ین و ناوی تۆ به&zwnj;رین.';
$t[35] = 'چونکه به&zwnj;ڕاستی تۆ هه&zwnj;میشه بینای به حاڵی ئێمه&zwnj;، به هه&zwnj;ڵس و که&zwnj;وتی ئێمه&zwnj;، به هه&zwnj;موو شتێکی ئێمه&zwnj;.';
$t[36] = '(خوای گه&zwnj;وره&zwnj;ش) فه&zwnj;رمووی: ئه&zwnj;ی موسا هه&zwnj;موو داخوازیه&zwnj;کانت به&zwnj;جێ یه و په&zwnj;سه&zwnj;نده و هه&zwnj;موویانت پێدرا.';
$t[37] = 'سوێند به&zwnj;خوا بێگومان ئێمه جارێکی تریش منه&zwnj;تمان ناوه&zwnj;ته سه&zwnj;ر تۆو لای ڕه&zwnj;حمه&zwnj;تمان لێکردوویته&zwnj;وه&zwnj;..';
$t[38] = 'کاتێک ئێمه ئه&zwnj;وه&zwnj;ی پێوست بوو به&zwnj;دایکت ڕابگه&zwnj;یه&zwnj;نرێت، پێمان گه&zwnj;یاند..';
$t[39] = 'که بیخه&zwnj;ره ناو تابوتێکی تایبه&zwnj;ته&zwnj;وه&zwnj;، ئینجا بیخه&zwnj;ره ده&zwnj;ریاوه&zwnj;، با ده&zwnj;ریا به&zwnj;ره&zwnj;و که&zwnj;نار بیبات، تا سه&zwnj;ره&zwnj;نجام دوژمنه&zwnj;که&zwnj;ی من و دوژمنه&zwnj;که&zwnj;ی ئه&zwnj;و بیگرێته&zwnj;وه&zwnj;، خۆشه&zwnj;ویستیم ڕژاند به&zwnj;سه&zwnj;رتدا، تا له ژێر چاودێری مندا گه&zwnj;وره ببیت.';
$t[40] = '(له&zwnj;بیرت نه&zwnj;چێت) کاتێک خوشکه&zwnj;که&zwnj;ت ده&zwnj;ڕۆیشت و چاودێری تۆی ده&zwnj;کرد، (له نزیکی کۆشکی فیرعه&zwnj;ونه&zwnj;وه&zwnj;، بینی گیریان خواردووه به ده&zwnj;سته&zwnj;وه&zwnj;) وتی: باشه&zwnj;، ئایا که&zwnj;سێکتان پێ بڵێم تا بۆتان به&zwnj;خێو بکات، ئه&zwnj;وسا تۆمان گه&zwnj;ڕانده&zwnj;وه بۆ لای دایکت، تا چاوی ڕوون بێت و خه&zwnj;فه&zwnj;ت نه&zwnj;خوات، (ئه&zwnj;وه&zwnj;&zwnj;بوو که گه&zwnj;وره بوویت به&zwnj;هه&zwnj;ڵه&zwnj;) که&zwnj;سێکت کوشت، ئنجا ئێمه ڕزگارمان کردیت له غه&zwnj;م و په&zwnj;ژاره&zwnj;، به جۆره&zwnj;ها شێوه تۆمان تاقی کردۆته&zwnj;وه&zwnj;، ئیتر ئه&zwnj;وه&zwnj;بوو ساڵه&zwnj;ها له&zwnj;ناو خه&zwnj;ڵکی مه&zwnj;دینه&zwnj;دا مایته&zwnj;وه&zwnj;، (به&zwnj;شوانکاری ژیانی خۆتت برده سه&zwnj;ر) ئینجا ئه&zwnj;ی موسا له&zwnj;کاتی دیاری کراودا هاتوویت (تا په&zwnj;یامی ئێمه وه&zwnj;ربگریت و ڕای بگه&zwnj;یه&zwnj;نیت)...';
$t[41] = '(جا چاک بزانه&zwnj;) من تۆم دروست کردووه بۆ خۆم، (تا په&zwnj;یامی من ڕابگه&zwnj;یه&zwnj;نیت).';
$t[42] = 'ده&zwnj;ی بڕۆ خۆت و براکه&zwnj;ت هاوڕێ له&zwnj;گه&zwnj;ڵ موعجیزه&zwnj;کانمدا، نه&zwnj;که&zwnj;ن له&zwnj;گه&zwnj;یاندنی په&zwnj;یامی مندا سستی و خاوی بکه&zwnj;ن.';
$t[43] = 'هه&zwnj;ردووکتان بچن بۆ لای فیرعه&zwnj;ون، چونکه به&zwnj;ڕاستی ئه&zwnj;و له سنوور ده&zwnj;رچووه و له مه&zwnj;رزی به&zwnj;ندایه&zwnj;تی ترازاوه&zwnj;..';
$t[44] = 'هه&zwnj;ردووکتان به&zwnj;نه&zwnj;رمی و جوانی گفتوگۆی له&zwnj;گه&zwnj;ڵدا بکه&zwnj;ن، بۆ ئه&zwnj;وه&zwnj;ی یاداوه&zwnj;ری وه&zwnj;ربگرێت، یان ترسی (سزای ئێمه&zwnj;) له ده&zwnj;روونیدا جێ بگرێت.';
$t[45] = 'موساو هاروون وتیان: په&zwnj;روه&zwnj;ردگارا؛ ئێمه به&zwnj;ڕاستی ده&zwnj;ترسین په&zwnj;لامارمان بدات و له&zwnj;ناومان به&zwnj;رێت (پێش ئه&zwnj;وه&zwnj;ی په&zwnj;یامه&zwnj;که&zwnj;تی پێ ڕابگه&zwnj;یه&zwnj;نین)، یان زیاتر سه&zwnj;رکه&zwnj;شیی بکات.';
$t[46] = 'خوای گه&zwnj;وره فه&zwnj;رمووی: مه&zwnj;ترسن من به&zwnj;ڕاستی له&zwnj;گه&zwnj;ڵتاندام، ده&zwnj;بیسم و ده&zwnj;بینم (نایه&zwnj;ڵم ده&zwnj;ستدرێژی بکاته سه&zwnj;رتان).';
$t[47] = 'ئینجا بچن بۆ لای و پێی بڵێن: ئێمه هه&zwnj;ردووکامان له&zwnj;لایه&zwnj;ن په&zwnj;روه&zwnj;ردگاری تۆوه ڕه&zwnj;وانه کراوین، (باوه&zwnj;ڕی پێ بهێنه&zwnj;) و نه&zwnj;وه&zwnj;ی ئیسرائیلیش ئازاد بکه&zwnj;، با له گه&zwnj;ڵماندا بن، سزاو ئازاریان مه&zwnj;ده&zwnj;، به&zwnj;ڕاستی ئێمه&zwnj;، هاورێ له&zwnj;گه&zwnj;ڵ موعجیزه&zwnj;ی تایبه&zwnj;تی له لایه&zwnj;ن په&zwnj;روه&zwnj;ردگارته&zwnj;وه ڕه&zwnj;وانه&zwnj;کراوین، سڵاویش له&zwnj;سه&zwnj;ر ئه&zwnj;و که&zwnj;سانه بێت که شوێنی هیدایه&zwnj;ت و ڕێبازی ڕاسته&zwnj;قینه که&zwnj;وتوون.';
$t[48] = 'بێگومان ئێمه ئاگادارکراوین، که سزاو ئازارو به&zwnj;ڵا له&zwnj;سه&zwnj;ر ئه&zwnj;وانه&zwnj;یه که ڕاستیه&zwnj;کان به درۆ ده&zwnj;زانن و پشتی تێ ده&zwnj;که&zwnj;ن لێی یاخی ده&zwnj;بن.';
$t[49] = 'فیرعه&zwnj;ون به فیزه&zwnj;وه وتی: باسه ئه&zwnj;ی موسا په&zwnj;روه&zwnj;ردگاری ئێوه کێیه&zwnj;؟!';
$t[50] = 'موسا وتی: په&zwnj;روه&zwnj;ردگارمان ئه&zwnj;و زاته&zwnj;یه که هه&zwnj;موو شتێکی به&zwnj;تایبه&zwnj;تی و ڕێکو پێکی دروست کردووه&zwnj;، پاشان ڕێنموویی کردووه&zwnj;، تا به&zwnj;چاکی ده&zwnj;وری خۆی ببینێت و (له خزمه&zwnj;تی ئاده&zwnj;میزادا بێت).';
$t[51] = 'فیرعه&zwnj;ون وتی: ئه&zwnj;ی ئه&zwnj;و هه&zwnj;موو خه&zwnj;ڵکه&zwnj;ی که له&zwnj;وه&zwnj;و پێش تیاچوون، چیان لێهات و سه&zwnj;ره&zwnj;نجامیان چۆنه&zwnj;؟!';
$t[52] = 'موسا وتی: زانیاری ئه&zwnj;وانه&zwnj;، لای په&zwnj;روه&zwnj;ردگارمه&zwnj;و له دۆسیه&zwnj;کانی (لوح المحفوظ) دا پارێزراوه&zwnj;، په&zwnj;روه&zwnj;ردگاری من، هیچی لێ ون نابێت، هیچ شتێکیش فه&zwnj;رامۆش ناکات.';
$t[53] = 'ئه&zwnj;و زاته&zwnj;ی که زه&zwnj;وی بۆ ئێوه بار هێناوه&zwnj;، وه&zwnj;کو لانکه و بێشکه (له&zwnj;سه&zwnj;ری ده&zwnj;حه&zwnj;وێنه&zwnj;وه&zwnj;)، هه&zwnj;روه&zwnj;ها جۆره&zwnj;ها ڕێگه&zwnj;ی (زه&zwnj;مینی و ئاسمانی و هه&zwnj;وایی) تیادا بۆ فه&zwnj;راهه&zwnj;م هێناون، له ئاسمانیشه&zwnj;وه باران داده&zwnj;به&zwnj;زێنێت، که به&zwnj;هۆیه&zwnj;وه چانده&zwnj;ها جووت ڕووه&zwnj;کی پێ ڕواندووه&zwnj;.';
$t[54] = 'خه&zwnj;ڵکینه بخۆن (له&zwnj;و به&zwnj;رهه&zwnj;م و به&zwnj;ره&zwnj;که&zwnj;ته&zwnj;)، ماڵاته&zwnj;کانیشتان بله&zwnj;وه&zwnj;ڕێنن، به&zwnj;ڕاستی ئا له&zwnj;و شتانه&zwnj;دا به&zwnj;ڵگه&zwnj;ونیشانه&zwnj;ی زۆر هه&zwnj;یه بۆ که&zwnj;سانی ژیرو هۆشمه&zwnj;ند.';
$t[55] = 'ئێمه ئێوه&zwnj;مان هه&zwnj;ر له&zwnj;م زه&zwnj;ویه دروست کردووه&zwnj;، (دوای ماوه&zwnj;یه&zwnj;ک) ده&zwnj;تانخه&zwnj;ینه&zwnj;وه ناوی، پاشان جارێکی تر دووباره له&zwnj;و زه&zwnj;ویه ده&zwnj;رتان ده&zwnj;هێنینه&zwnj;وه (بۆ لێپرسینه&zwnj;وه و وه&zwnj;رگرتنی ئه&zwnj;و پاداشته&zwnj;ی که شایسته&zwnj;ن).';
$t[56] = 'سوێند به خوا به&zwnj;ڕاستی هه&zwnj;رچی به&zwnj;ڵگه و موعجیزه هه&zwnj;بوو نیشانی فیرعه&zwnj;ونماندا که&zwnj;چی هه&zwnj;ر هه&zwnj;مووی به&zwnj;درۆزانی و ئاماده نه&zwnj;بوو باوه&zwnj;ڕ بهێنێت، به&zwnj;ڵکو هه&zwnj;ر سه&zwnj;ری باداو یاخی بوو.';
$t[57] = 'فیرعه&zwnj;ون (به ناڕه&zwnj;زاییه&zwnj;وه&zwnj;) وتی: ئه&zwnj;ی موسا هاتووی بۆ لامان، تا له&zwnj;سه&zwnj;ر زه&zwnj;وی خۆمان به ده&zwnj;رکردنمان بده&zwnj;یت و ئاواره&zwnj;مان بکه&zwnj;یت، به&zwnj;هۆی جادووگه&zwnj;ریه&zwnj;که&zwnj;ته&zwnj;وه&zwnj;؟!';
$t[58] = 'که&zwnj;واته سوێند بێت ئێمه&zwnj;ش جادوویه&zwnj;کت بۆ ده&zwnj;هێنین وه&zwnj;ک ئه&zwnj;وه&zwnj;ی خۆت، جا شوێن و کاتێک بۆ ئێمه&zwnj;و بۆ خۆت دابنێ، نه ئێمه سه&zwnj;رپێچی لێ بکه&zwnj;ین نه تۆش، تا له یه&zwnj;ک شوێن و کاتدا ده&zwnj;ستبه&zwnj;کار ببین له شوێنێکی ڕێک و پێکدا.';
$t[59] = 'موسا وتی: کاتی دیاریکراومان ڕۆژی ڕازاندنه&zwnj;وه بێت له&zwnj;کاتی چێشته&zwnj;نگاودا خه&zwnj;ڵکی کۆبکرێته&zwnj;وه&zwnj;.';
$t[60] = 'فیرعه&zwnj;ون ئه&zwnj;و دانیشتنه&zwnj;ی به&zwnj;جێ هێشت و هه&zwnj;رچی فێڵ و ته&zwnj;ڵه&zwnj;که&zwnj;ی هه&zwnj;بوو خستیه&zwnj;کارو پاشان (له کاتی دیاری کراودا) هات (به له&zwnj;خۆبایی بوونه&zwnj;وه&zwnj;).';
$t[61] = 'موسا به جادووگه&zwnj;رانی وت: هاوار له ئێوه&zwnj;، نه&zwnj;که&zwnj;ن درۆ به ناوی خواوه هه&zwnj;ڵبه&zwnj;ستن، (هاوه&zwnj;ڵی بۆ بڕیار بده&zwnj;ن، باوه&zwnj;ڕ به پێغه&zwnj;مبه&zwnj;ران نه&zwnj;که&zwnj;ن) چونکه ئه&zwnj;و زاته به&zwnj;توانایه به&zwnj;به&zwnj;ڵایه&zwnj;ک هه&zwnj;مووتان له&zwnj;ناو ببات و بتانفه&zwnj;وتێنێت، چونکه به&zwnj;ڕاستی ئه&zwnj;و که&zwnj;سه&zwnj;ی که&zwnj;شتی ناڕه&zwnj;وای هه&zwnj;ڵبه&zwnj;ستووه و پڕوپاگه&zwnj;نده&zwnj;ی درۆی کردووه نائومێد و بێ هیواو ڕه&zwnj;نجه&zwnj;ڕۆ بووه&zwnj;.';
$t[62] = '(گوفتاری موسا کاری تێکردن) که&zwnj;وتنه باس و خواس له نێوان خۆیاندا و به&zwnj;نهێنی چپ و هوڕیان ده&zwnj;ست پێ کرد.';
$t[63] = '(هه&zwnj;ندێک له دارو ده&zwnj;سته&zwnj;ی فیرعه&zwnj;ون) وتیان به (جادووگه&zwnj;ران) به&zwnj;ڕاستی ئه&zwnj;م دوانه&zwnj;، دوو جادووگه&zwnj;رن و ده&zwnj;یانه&zwnj;وێت، له وڵات و نیشتمانی خۆتان ده&zwnj;رتانپه&zwnj;ڕێنن به&zwnj;هۆی جادووه&zwnj;&zwnj;که&zwnj;یانه&zwnj;وه و ئه&zwnj;م ڕێگه&zwnj;و به&zwnj;رنامه گرنگ و به&zwnj;رزه&zwnj;ی ئێوه له&zwnj;ناو به&zwnj;رن...';
$t[64] = 'جا هه&zwnj;رچی تواناو ده&zwnj;سه&zwnj;ڵات و جادوویه&zwnj;کتان هه&zwnj;یه بیخه&zwnj;نه&zwnj;کارو یه&zwnj;ک پارچه و یه&zwnj;ک ده&zwnj;ست، ده&zwnj;ستبه&zwnj;کار بن، بێگومان ئه&zwnj;وه&zwnj;ی ئه&zwnj;مڕۆ ده&zwnj;یباته&zwnj;وه سه&zwnj;رفرازو سه&zwnj;رکه&zwnj;توو به&zwnj;هره&zwnj;مه&zwnj;نده&zwnj;!!';
$t[65] = 'ئه&zwnj;وسا جادووگه&zwnj;ران وتیان: ئه&zwnj;ی موسا؛ یان تۆ ده&zwnj;ستبه&zwnj;کاربه و (دار عه&zwnj;سای خۆت) فڕێ بده&zwnj;، یان ئێمه سه&zwnj;ره&zwnj;تا ده&zwnj;ست پی ده&zwnj;که&zwnj;ین و فڕێی ده&zwnj;ده&zwnj;ین؟';
$t[66] = 'موسا وتی: نه&zwnj;خێر؛ ئێوه له پێشدا ده&zwnj;ست پێ بکه&zwnj;ن، جا هه&zwnj;ر ده&zwnj;ستیان پێکرد په&zwnj;ت و گوریس و دارو چێوه&zwnj;کانیان وا ده&zwnj;هێنرایه به&zwnj;رچاوی به&zwnj;هۆی جادوویانه&zwnj;وه&zwnj;، بێگومان وه&zwnj;ک مار به&zwnj;خێرایی ده&zwnj;ڕوات.';
$t[67] = 'ئه&zwnj;وسا موسا هه&zwnj;ستی کرد به ترسێک له دڵ و ده&zwnj;روونیدا.';
$t[68] = '(ئێمه&zwnj;ش نیگامان بۆ کرد) وتمان: مه&zwnj;ترسه و دڵنیابه چونکه به&zwnj;ڕاستی هه&zwnj;ر تۆ به&zwnj;رزو سه&zwnj;رکه&zwnj;توویت.';
$t[69] = 'ئه&zwnj;وه&zwnj;ش له&zwnj;ده&zwnj;ستی ڕاستتدایه (واته عساکه&zwnj;ت)فڕێی بده&zwnj;، هه&zwnj;رچی ئه&zwnj;وان کردوویانه ده&zwnj;یماشێته&zwnj;وه و قووتی ده&zwnj;دات، چونکه بێگومان ئه&zwnj;وه&zwnj;ی کردوویانه ته&zwnj;نها فێڵ و ته&zwnj;ڵه&zwnj;که&zwnj;ی جادووگه&zwnj;ره&zwnj;، جادووگه&zwnj;ریش بۆ هه&zwnj;ر کوێ بچێت و هه&zwnj;رچی بکات سه&zwnj;رکه&zwnj;توو نابێت.';
$t[70] = 'ئه&zwnj;وسا ئیتر جادووگه&zwnj;ران، خران به ڕووداو سوژده&zwnj;یان برد بۆ خوا (ڕاستییان بۆ ده&zwnj;رکه&zwnj;وت) وتیان: ئیتر ئێمه باوه&zwnj;ڕێکی به&zwnj;تینمان هێناوه به&zwnj;په&zwnj;روه&zwnj;ردگاری هاروون و موسا.';
$t[71] = 'فیرعه&zwnj;ون وتی: ئایا ئێوه هه&zwnj;ر ئاوا باوه&zwnj;ڕتان پێ هێنا پێش ئه&zwnj;وه&zwnj;ی مۆڵه&zwnj;تتان بده&zwnj;م و ڕازی بم؟ به&zwnj;ڕاستی دیاره موسا گه&zwnj;وره و مامۆستاتانه و هه&zwnj;ر ئه&zwnj;و فێری جادووگه&zwnj;ری کردوون، شه&zwnj;رت بێت ده&zwnj;ست و قاچتان به پێچه&zwnj;وانه&zwnj;وه بقرتێنم و هه&zwnj;ڵتانواسم به&zwnj;قه&zwnj;دی دارخورمادا ئه&zwnj;وسا چاک ده&zwnj;زانن که کاممان (من، یان خوای موسا) سزامان توندو تیژو به&zwnj;رده&zwnj;وامه&zwnj;!';
$t[72] = '(جادووگه&zwnj;رانی تاوێک له&zwnj;مه&zwnj;وبه&zwnj;ر، به&zwnj;باوه&zwnj;ڕێکی به&zwnj;تینه&zwnj;وه&zwnj;) وتیان: هه&zwnj;رگیز تازه باوی تۆ ناده&zwnj;ین به&zwnj;سه&zwnj;ر ئه&zwnj;م به&zwnj;ڵگه و نیشانانه&zwnj;دا که بۆمان هه&zwnj;تووه سوێند به&zwnj;و که&zwnj;سه&zwnj;ی که دروستی کردووین، چیت له&zwnj;ده&zwnj;ست دێت ئه&zwnj;نجامی بده و درێغی مه&zwnj;که&zwnj;، به&zwnj;ڕاستی تۆ هه&zwnj;ر ده&zwnj;توانیت ژیانی ئه&zwnj;م دنیایه&zwnj;مان لێ زه&zwnj;وت بکه&zwnj;یت.';
$t[73] = 'ئێمه ئیتر به&zwnj;ڕاستی باوه&zwnj;ڕمان هێناوه به په&zwnj;روه&zwnj;ردگارمان، تا له گوناهه&zwnj;کانمان خۆش ببێت و له&zwnj;و جادووگه&zwnj;ریه&zwnj;ش که به زۆر پێت کردین، هه&zwnj;میشه خوای گه&zwnj;وره له هه&zwnj;موو که&zwnj;س چاکتره و له هه&zwnj;موو که&zwnj;س پایه&zwnj;دارترو به&zwnj;رده&zwnj;وامتره&zwnj;.';
$t[74] = 'ئێمه دڵنیاین له&zwnj;وه&zwnj;ی، که ئه&zwnj;وه&zwnj;ی به تاوان و گوناهه&zwnj;وه بگه&zwnj;رێته&zwnj;وه بۆ لای په&zwnj;روه&zwnj;ردگاری، بێگومان ئه&zwnj;وه دۆزه&zwnj;خ بۆی ئاماده&zwnj;یه که نه&zwnj;ده&zwnj;مرێت تیایدا (تا له سزا ڕزگاری ببێت) نه ده&zwnj;ژی (به ئارامی و ئاسووده&zwnj;یی).';
$t[75] = 'ئه&zwnj;و که&zwnj;سه&zwnj;ش به ئیمانه&zwnj;وه بگه&zwnj;رێته&zwnj;وه بۆ لای و به&zwnj;ڕاستی کرده&zwnj;وه چاکه&zwnj;کانی ئه&zwnj;نجام دابێت، ئه&zwnj;وه بۆ ئه&zwnj;وانه پله&zwnj;و پایه&zwnj;ی به&zwnj;رزو بڵند ئاماده&zwnj;یه&zwnj;...';
$t[76] = 'به&zwnj;هه&zwnj;شتی -عه&zwnj;ده&zwnj;نه&zwnj;- که باخه&zwnj;که&zwnj;نی چڕو پڕه ده&zwnj;چنه ناوی، چه&zwnj;نده&zwnj;ها ڕووبار به&zwnj;ژێر دره&zwnj;خته&zwnj;کانیدا ده&zwnj;ڕوات، هاورێ له&zwnj;گه&zwnj;ڵ ژیانی هه&zwnj;میشه&zwnj;یی و نه&zwnj;بڕاوه&zwnj;دا، ئه&zwnj;وه&zwnj;ش پاداشتی که&zwnj;سێکه دڵ و ده&zwnj;روون و ڕه&zwnj;وشتی خۆی خاوێن کردبێت.';
$t[77] = '(دوای تێپه&zwnj;ڕ بوونی چه&zwnj;ند ساڵیک) سوێند به خوا به&zwnj;ڕاستی نیگامان بۆ موسا کرد که شه&zwnj;وڕه&zwnj;وی بکه به&zwnj;به&zwnj;نده&zwnj;کانم و ڕێگه&zwnj;یه&zwnj;کی وشکیان له ده&zwnj;ریادا بۆ دابین بکه و له&zwnj;کاتێکدا ترسی ئه&zwnj;وه&zwnj;ت نی یه که پێت بگه&zwnj;ن و خه&zwnj;می نوقم بوون تیاچوونیشت نی یه&zwnj;.';
$t[78] = 'ئه&zwnj;وسا فیرعه&zwnj;ون به سه&zwnj;ربازانیه&zwnj;وه شوێنیان که&zwnj;وتن، کوتوپڕ له&zwnj;ناو ئاوه&zwnj;که&zwnj;دا شه&zwnj;پۆلی ده&zwnj;ریا دای به&zwnj;سه&zwnj;ریانداو هه&zwnj;موویانی داپۆشی به&zwnj;چ داپۆشینێک!';
$t[79] = 'فیرعه&zwnj;ونیش قه&zwnj;وم و گه&zwnj;له&zwnj;که&zwnj;ی خۆی گومرا کردو ڕێنموویی نه&zwnj;کردن.';
$t[80] = '(پاشان خوا؛ ڕوو به نه&zwnj;وه&zwnj;ی ئیسرائیل ده&zwnj;فه&zwnj;رموێت): ئه&zwnj;ی نه&zwnj;وه&zwnj;ی ئیسرائیل بێگومان ئێمه ئێوه&zwnj;مان له ده&zwnj;ست دوژمنه&zwnj;که&zwnj;تان ڕزگار کردو به&zwnj;ڵێنیشمان دانێ که لای ڕاستی کێوی طوردا (په&zwnj;یمانتان لێ وه&zwnj;ربگرین)، گه&zwnj;زۆ و شه&zwnj;لاقه&zwnj;شمان بۆ دابین کردن.';
$t[81] = 'بخۆن له&zwnj;و ڕزق و ڕۆزیه پاک و چاکانه&zwnj;ی که پێمان به&zwnj;خشیوون و زیادڕه&zwnj;وی و گوناهکاری و فێڵ و ته&zwnj;ڵه&zwnj;که&zwnj;ی تێدا مه&zwnj;که&zwnj;ن، بۆ ئه&zwnj;وه&zwnj;ی خه&zwnj;شم و قینی من داتاننه&zwnj;گرێته&zwnj;وه&zwnj;، بێگومان ئه&zwnj;وه&zwnj;ی خه&zwnj;شم و قینی من دایبگرێت، ئه&zwnj;وه ئیتر که&zwnj;وتووه و تیاچووه&zwnj;.';
$t[82] = 'دڵنیاش بن به&zwnj;ڕاستی من زۆر لێخۆش بووم له که&zwnj;سه&zwnj;ی که ته&zwnj;وبه ده&zwnj;کات و باوه&zwnj;ڕ ده&zwnj;هێنێت و کاروکرده&zwnj;وه&zwnj;ی چاکه ده&zwnj;کات، له&zwnj;وه&zwnj;ودوا (تا دوا هه&zwnj;ناسه&zwnj;ی ژیانی) به&zwnj;رده&zwnj;وام ده&zwnj;بێت له&zwnj;سه&zwnj;ر ئه&zwnj;و ڕێبازه&zwnj;.';
$t[83] = '(له&zwnj;وه&zwnj;ودوا موسا خۆی گه&zwnj;یانده کێوی طور، خوای گه&zwnj;وره لێی پرسی:) ئه&zwnj;ی موسا بۆچی تۆ په&zwnj;له&zwnj;ت کردو قه&zwnj;ومه&zwnj;که&zwnj;ت به&zwnj;جێ هێشت.';
$t[84] = 'موسا وتی: ئه&zwnj;وان له&zwnj;سه&zwnj;ر ڕێبازی من ده&zwnj;ڕۆن، (به&zwnj;ڵام من ئاره&zwnj;زوومه&zwnj;ندی گفتوگۆت بووم) بۆیه به&zwnj;په&zwnj;له هاتم تا تۆ ئه&zwnj;ی په&zwnj;روه&zwnj;ردگارم لێم ڕازی بیت (فه&zwnj;رمانه پیرۆزه&zwnj;کانت وه&zwnj;ربگرم).';
$t[85] = 'په&zwnj;روه&zwnj;رردگار فه&zwnj;رمووی: بێگومان ئێمه قه&zwnj;ومه&zwnj;که&zwnj;تمان له دوای هاتنی تۆ تاقیکرده&zwnj;وه (که&zwnj;چی سه&zwnj;رکه&zwnj;توو نه&zwnj;بوون، باوه&zwnj;ڕ دامه&zwnj;زراو نه&zwnj;بوون) کابرای سامیری گومڕای کردن.';
$t[86] = 'پاشان موسا گه&zwnj;ڕایه&zwnj;وه بۆ ناو قه&zwnj;ومه&zwnj;که&zwnj;ی، به&zwnj;تووڕه&zwnj;یی و داخ و خه&zwnj;فه&zwnj;تێکی زۆره&zwnj;وه&zwnj;، وتی: ئه&zwnj;ی قه&zwnj;ومه&zwnj;که&zwnj;م، مه&zwnj;گه&zwnj;ر په&zwnj;روه&zwnj;ردگارتان به&zwnj;ڵێنی چاکی نه&zwnj;دابوونێ (که سه&zwnj;رفرازتان بکا&zwnj;ت له دنیاو قیامه&zwnj;تدا) ئایا ئه&zwnj;و به&zwnj;ڵێن و په&zwnj;یمانه زۆری پێچووه&zwnj;؟ یاخود ویستتان خه&zwnj;شم و قین له&zwnj;لایه&zwnj;ن په&zwnj;روه&zwnj;ردگارتانه&zwnj;وه دابارێت به&zwnj;سه&zwnj;رتاندا، بۆیه ئیتر ئه&zwnj;و به&zwnj;ڵێنه&zwnj;ی دابووتان به من نه&zwnj;تان برده&zwnj;سه&zwnj;ر (که پابه&zwnj;ندی یه&zwnj;کخواناسی بن؟!).';
$t[87] = 'خه&zwnj;ڵکه&zwnj;که وتیان: ئێمه سه&zwnj;رپێچی کردنمان له به&zwnj;ڵێنه&zwnj;که&zwnj;ی تۆ به&zwnj;ئاره&zwnj;زووی خۆمان نه&zwnj;بووه&zwnj;، حه&zwnj;زمان له سه&zwnj;رپێچی نه&zwnj;کردووه، به&zwnj;ڵام ئێمه زێرو زیوێکی زۆری - قبطی- یه&zwnj;کان درابوو به&zwnj;سه&zwnj;رماندا، جا ئیتر فرێمانداو سامیریش به&zwnj;و شێوه&zwnj;یه فرێیدا..';
$t[88] = 'به&zwnj;و زێرو زیوه په&zwnj;یکه&zwnj;ری گوێره&zwnj;که&zwnj;یه&zwnj;کی بۆ دروست کردن (جارجار) بۆڕه&zwnj;یه&zwnj;کی ده&zwnj;کرد، (سامیری و بێ دینه&zwnj;کان بێ شه&zwnj;رمانه&zwnj;) وتیان: ئه&zwnj;مه خوای ئێوه و خوای موسایه&zwnj;، ئه&zwnj;و له بیری چووه و (به&zwnj;شوێنیدا ده&zwnj;گه&zwnj;رێت).';
$t[89] = 'جا ئایا ئه&zwnj;و نه&zwnj;فا&zwnj;مانه نازانن ئه&zwnj;و گوێره&zwnj;که&zwnj;یه وه&zwnj;ڵامی هیچ قسه&zwnj;یه&zwnj;کیان ناداته&zwnj;وه&zwnj;؛ هیچ زه&zwnj;ره&zwnj;ر، یان قازانجێکی بۆتان نیه&zwnj;؟!';
$t[90] = 'بێگومان پێشتریش هاروون پێی وتن: ئه&zwnj;ی قه&zwnj;ومی من؛ ئێوه به&zwnj;م گوێره&zwnj;که زێرینه تووشی تاقیکردنه&zwnj;وه&zwnj;یه&zwnj;کی سه&zwnj;خت بوون و تیایدا ده&zwnj;رنه&zwnj;چوون، دڵنیابن که په&zwnj;روه&zwnj;ردگارتان خوایه&zwnj;کی میهره&zwnj;بانه&zwnj;، شوێنی من بکه&zwnj;ون و گوێڕایه&zwnj;ڵی فه&zwnj;رمانی من بن.';
$t[91] = 'که&zwnj;چی وتیان: هه&zwnj;رگیز وازی لێ ناهێنین و به&zwnj;ده&zwnj;وریدا دێین و ده&zwnj;یپه&zwnj;رستین هه&zwnj;تا موسا خۆی ده&zwnj;گه&zwnj;رێته&zwnj;وه&zwnj;!!';
$t[92] = '(موسا، که گه&zwnj;ڕایه&zwnj;وه به توڕه&zwnj;ییه&zwnj;وه&zwnj;) وتی: ئه&zwnj;ی هاروون، چی وای لێ کردیت (بێ ده&zwnj;نگ بیت) و هیچ نه&zwnj;که&zwnj;یت، کاتێک بینیتن ئاوا گومرا بوون؟';
$t[93] = 'چی وای لێ کردیت په&zwnj;یڕه&zwnj;وی من نه&zwnj;که&zwnj;یت؟! ئایا له فه&zwnj;رمانی من یاخی بوویت؟!';
$t[94] = 'هاروون وتی: کوڕی دایکم (بۆ ئه&zwnj;وه&zwnj;ی سۆزی بجوڵێنێت) ڕیشم مه&zwnj;گره و قژم ڕامه&zwnj;کێشه&zwnj;، (من بۆیه زۆر له&zwnj;سه&zwnj;ری نه&zwnj;چووم) بێگومان ترسام دوایی پێم بڵێیت: تۆ جیاوازیت خسته ناو نه&zwnj;ته&zwnj;وه&zwnj;ی ئیسرائیله&zwnj;وه و گوێت به&zwnj;قسه&zwnj;ی من نه&zwnj;دا.';
$t[95] = '(ئنجا موسا ڕووی کرده کابرای سامیری) وتی: ئه&zwnj;ی سامیری، ئه&zwnj;وکاره خه&zwnj;ته&zwnj;رناکه چی یه که ئه&zwnj;نجامت داوه&zwnj;؟ (ئه&zwnj;م پیلان و ته&zwnj;ڵه&zwnj;که و فێله چی یه دروستت کردووه&zwnj;)؟!';
$t[96] = 'سامیری وتی: ئه&zwnj;وه&zwnj;ی من بینیم، ئه&zwnj;وان نه&zwnj;یانبینی، من مشتێکم له شوێن پێی (ئه&zwnj;سپه&zwnj;که&zwnj;ی) جوبره&zwnj;ئیل هه&zwnj;ڵگرت و فڕێمدایه ناو په&zwnj;یکه&zwnj;ره&zwnj;که&zwnj;وه&zwnj;، به&zwnj;و شێوه&zwnj;یه نه&zwnj;فسی به&zwnj;دکردارم ئه&zwnj;و کاره&zwnj;ی پێکردم (یاخود ده&zwnj;گونجێت سامیری درۆ بکات، تا خۆی له خه&zwnj;شمی حه&zwnj;زره&zwnj;تی موسا ڕزگار بکات و جادوویه&zwnj;کی ئه&zwnj;نجام دابێت).';
$t[97] = '(موسا به سامیری) وت: ده&zwnj;بڕۆ تا ماوی له ژیاندا (هه&zwnj;ر که&zwnj;سێک نزیکت که&zwnj;وته&zwnj;وه ده&zwnj;بێت) بڵێیت: به هیچ شێوه&zwnj;یه&zwnj;ک به&zwnj;ریه&zwnj;ککه&zwnj;وتن نی یه (دیاره که ده&zwnj;ست هه&zwnj;رکه&zwnj;سی به&zwnj;ربکه&zwnj;وتبێت تووشی ئازارێکی زۆر بووه&zwnj;)، دڵنیاش به که کاتێکی تریشت بۆ دیاری کراوه&zwnj;، که هه&zwnj;رگیز ناتوانیت لێی ده&zwnj;رباز ببیت، سه&zwnj;رنجی خواکه&zwnj;شت بده که به&zwnj;ده&zwnj;وریدا ده&zwnj;سوڕایته&zwnj;وه و ده&zwnj;تپه&zwnj;رست، ده&zwnj;بێت بیسوتێنین و هه&zwnj;لا هه&zwnj;لای بکه&zwnj;ین و بیخه&zwnj;ینه ناو ده&zwnj;ریاوه&zwnj;.';
$t[98] = 'خه&zwnj;ڵکینه&zwnj;: ئێوه دڵنیابن که په&zwnj;روه&zwnj;ردگارتان ته&zwnj;نها زاتی (الله&zwnj;) یه&zwnj;، جگه له&zwnj;و خوایه&zwnj;کی تر نیه&zwnj;، زانیاری و زانستی ئه&zwnj;و زاته هه&zwnj;موو شتێکی گرتۆته&zwnj;وه&zwnj;.';
$t[99] = 'ئه&zwnj;ی محمد (&lrm;&lrm;صلی الله علیه وسلم): ئێمه بێگومان ئا به&zwnj;و شێوه&zwnj;یه هه&zwnj;ندێک له به&zwnj;سه&zwnj;رهاتی ئه&zwnj;وانه&zwnj;ی ڕابروود بۆت ده&zwnj;گێرینه&zwnj;وه&zwnj;، دڵنیابه ئێمه له&zwnj;لا&zwnj;یه&zwnj;ن خۆمانه&zwnj;وه قورئانمان پێ به&zwnj;خشیوویت که یاداوه&zwnj;ری بێت له هه&zwnj;موو ڕوویه&zwnj;که&zwnj;وه&zwnj;.';
$t[100] = 'ئه&zwnj;وه&zwnj;ی پشتی تێ کردبێت، ئه&zwnj;وه بێگومان له ڕۆژی قیامه&zwnj;تدا کۆڵیک گوناهـ هه&zwnj;ڵده&zwnj;گرێت.';
$t[101] = '(ئه&zwnj;و جۆره که&zwnj;سانه&zwnj;) له&zwnj;ناو سزادا بۆ هه&zwnj;میشه ده&zwnj;مێننه&zwnj;وه&zwnj;، ئای چه&zwnj;ند خراپه ئه&zwnj;و کۆڵه قورسه&zwnj;ی که له قیامه&zwnj;تدا به&zwnj;کۆڵیانه&zwnj;وه&zwnj;یه&zwnj;.';
$t[102] = 'ڕۆژێک دێت فوو ده&zwnj;کرێت به - صور- دا، ئه&zwnj;و ڕۆژه تاوانباران چاویان کوێره&zwnj;و شین هه&zwnj;ڵگه&zwnj;ڕاوه (دیمه&zwnj;نێکی ناشرین و سامناکیان هه&zwnj;یه&zwnj;).';
$t[103] = 'به&zwnj;چپه&zwnj;و سرته به&zwnj;یه&zwnj;ک ده&zwnj;ڵێن: ته&zwnj;نها ده شه&zwnj;و نه&zwnj;بێت؛ (له دنیادا یان له جیهانی به&zwnj;رزه&zwnj;خدا) هیچی تریان پێچووه&zwnj;.';
$t[104] = 'ئێمه چاک ده&zwnj;زانین سه&zwnj;ره&zwnj;نجام چی ده&zwnj;ڵێن چونکه ژیرترین و وریاترینیان ده&zwnj;ڵێت: ته&zwnj;نها ڕۆژێک نه&zwnj;بێت هیچی ترتان پێ نه&zwnj;چووه&zwnj;!!';
$t[105] = 'پرسیارت لێده&zwnj;که&zwnj;ن ده&zwnj;رباره&zwnj;ی کێوه&zwnj;کان (که له به&zwnj;رپا بوونی قیامه&zwnj;تدا چیان به&zwnj;سه&zwnj;ر دێت)، ئه&zwnj;ی محمد (صلی الله علیه وسلم) تۆ پێیان بڵێ: په&zwnj;روه&zwnj;ردگارم هه&zwnj;ڵی ده&zwnj;ته&zwnj;کێنێت و وردو خاشی ده&zwnj;کات...';
$t[106] = 'جا په&zwnj;رش و بڵاوی ده&zwnj;کاته&zwnj;وه و ده&zwnj;یکاته زه&zwnj;ویه&zwnj;کی پان و ته&zwnj;خت و به&zwnj;رین وازی لێ ده&zwnj;هێنێت و به&zwnj;جێ ده&zwnj;هێڵێت.';
$t[107] = 'هیچ خوارو خێچی و به&zwnj;رزی و نزمیه&zwnj;ک له&zwnj;سه&zwnj;ر ڕووکاری نابینیت.';
$t[108] = 'له&zwnj;و ڕۆژه&zwnj;دا هه&zwnj;موو خه&zwnj;ڵکی شوێنی جاڕده&zwnj;ری کۆکردنه&zwnj;وه ده&zwnj;که&zwnj;ون، هیچ سه&zwnj;رپێچی و لاریه&zwnj;ک ناکرێت، هه&zwnj;موو ده&zwnj;نگه&zwnj;کان له&zwnj;به&zwnj;ر په&zwnj;روه&zwnj;ردگاری میهره&zwnj;بان نزمن و به&zwnj;هێواشی ده&zwnj;دوێن، ته&zwnj;نها چپه و سرته&zwnj;یه&zwnj;کی که&zwnj;م نه&zwnj;بێت هیچی تر نابیستیت.';
$t[109] = 'له&zwnj;و ڕۆژه&zwnj;دا تکاو شه&zwnj;فاعه&zwnj;ت وه&zwnj;رناگیرێت و بێ سووده&zwnj;، مه&zwnj;گه&zwnj;ر له که&zwnj;سێک خوای میهره&zwnj;بان مۆڵه&zwnj;تی بدات و ڕازی بێت به&zwnj;وه&zwnj;ی قسه&zwnj;ی بۆ بکرێت و قسه&zwnj;ی لێ وه&zwnj;ربگرێت.';
$t[110] = 'خوا به هه&zwnj;موو ئه&zwnj;و شتانه ده&zwnj;زانێت (که له قیامه&zwnj;تدا) چاوه&zwnj;ڕێیانه&zwnj;و به هه&zwnj;موو ئه&zwnj;و شتانه&zwnj;ی (که له دنیادا) ئه&zwnj;نجامیان داوه و ئه&zwnj;و خه&zwnj;ڵکه هه&zwnj;رگیز ناتوانن ئاگاداری زانست و زانیاری بێ سنووری ئه&zwnj;و زاته ببه&zwnj;ن.';
$t[111] = 'هه&zwnj;رچی ڕووخساره&zwnj;کانه (له ڕۆژو قیامه&zwnj;تدا) ملکه&zwnj;ج و فه&zwnj;رمانبه&zwnj;ردارن، بۆ ئه&zwnj;و زاته&zwnj;ی که&zwnj;هه&zwnj;میشه زیندووه و ده&zwnj;سه&zwnj;ڵاتی بێ سنووری هه&zwnj;یه&zwnj;، بێگومان ئه&zwnj;وه&zwnj;ی سته&zwnj;می کردووه&zwnj;، ڕه&zwnj;نج به&zwnj;خه&zwnj;سارو نائومێدو بێ هیوایه&zwnj;.';
$t[112] = 'ئه&zwnj;وه&zwnj;ش که کارو کردوه&zwnj;وه چاکه&zwnj;کان ئانجام ده&zwnj;دات و له&zwnj;کاتێکدا ئیمانداریش بووبێت، ئه&zwnj;وه ئیتر ناترسێت له&zwnj;وه&zwnj;ی سته&zwnj;می لێ بکرێت یان مافی پێشێل بکرێت.';
$t[113] = 'ئا به&zwnj;و شێوه&zwnj;یه ئێمه قورئانێکی عه&zwnj;ره&zwnj;بی و پاراو و ڕه&zwnj;وانمان دابه&zwnj;زاندووه و جۆره&zwnj;ها هه&zwnj;ڕه&zwnj;شه&zwnj;شمان تیادا باس کردووه&zwnj;، بۆ ئه&zwnj;وه&zwnj;ی له خوا بترسن و یاخود ببێته هۆی ئه&zwnj;وه&zwnj;ی که یاداوه&zwnj;ریان تێدا به&zwnj;دی بهێنێت.';
$t[114] = 'خوای گه&zwnj;وره زۆر پایه به&zwnj;رزو بڵنده و پاشای به حه&zwnj;ق و ڕاسته&zwnj;قینه&zwnj;یه&zwnj;، (ئه&zwnj;ی محمد صلی الله علیه وسلم) نه&zwnj;که&zwnj;یت له خوێندنی قورئاندا په&zwnj;له بکه&zwnj;یت: له پێش ئه&zwnj;وه&zwnj;ی وه&zwnj;حی و نیگای ئه&zwnj;و قورئانه ته&zwnj;واو ببێت بۆ لای تۆ (چونکه ئێمه نایه&zwnj;ڵین فه&zwnj;رامۆشی بکه&zwnj;یت) نزا بکه و بشڵێ: په&zwnj;روه&zwnj;ردگارا، به&zwnj;رده&zwnj;وام زانست و زانیاری زۆرترو زیاترم پێ ببه&zwnj;خشه&zwnj;.';
$t[115] = 'سوێند به&zwnj;خوا بێگومان ئێمه پێشتر په&zwnj;یمانمان له ئاده&zwnj;م وه&zwnj;رگرت (که نزیکی دره&zwnj;ختی قه&zwnj;ده&zwnj;غه کراو نه&zwnj;که&zwnj;وێت) که&zwnj;چی بیری چوو، به مه&zwnj;رجێک نه&zwnj;مانبینی پێشتر بڕیاری کردنی ئه&zwnj;و سه&zwnj;رپێچییه بدات.';
$t[116] = 'سه&zwnj;رده&zwnj;مێک به&zwnj;فریشته&zwnj;کانمان وت: سوژده به&zwnj;رن بۆ ئاده&zwnj;م، هه&zwnj;موو سوژده&zwnj;یان برد، جگه له ئیبلیس که سه&zwnj;رسه&zwnj;خت و یاخی بوو.';
$t[117] = 'جا وتمان: ئه&zwnj;ی ئاده&zwnj;م، به&zwnj;ڕاستی ئه&zwnj;م (ئیبلیسه&zwnj;) دوژمنی خۆت و هاوسه&zwnj;ره&zwnj;که&zwnj;ته&zwnj;، وریابن له به&zwnj;هه&zwnj;شت به&zwnj;ده&zwnj;رکردنتان نه&zwnj;دات، به هۆیه&zwnj;وه تووشی ناخۆشی و نه&zwnj;گبه&zwnj;تی و ده&zwnj;ربه&zwnj;ده&zwnj;ری ببن.';
$t[118] = 'دڵنیاشبه تۆ له به&zwnj;هه&zwnj;شتدا نه&zwnj;برسیت ده&zwnj;بێت نه&zwnj;رووت و ڕه&zwnj;جاڵ ده&zwnj;بیت و...';
$t[119] = 'نه&zwnj;تینووشت ده&zwnj;بێت و نه&zwnj;گه&zwnj;رماش زۆرت بۆ ده&zwnj;هێنێت.';
$t[120] = 'جا شه&zwnj;یتان گومانی خسته دڵیه&zwnj;وه و پێی وت: ئه&zwnj;ی ئاده&zwnj;م پێت بڵێم و نیشانت بده&zwnj;م دره&zwnj;ختی نه&zwnj;مری و موڵک و داراییه&zwnj;ک که به&zwnj;سه&zwnj;رناچێت کامه&zwnj;یه&zwnj;؟!!';
$t[121] = '(شه&zwnj;یتان هه&zwnj;ر کۆڵی نه&zwnj;دا تا فریوی دان) هه&zwnj;ردووکیان له به&zwnj;روبوومی دره&zwnj;خته&zwnj;که&zwnj;یان خوارد (ئیتر جل و به&zwnj;رگ و پۆشاکیان داماڵرا)، عه&zwnj;یب و عاریان ده&zwnj;رکه&zwnj;وت، به&zwnj;په&zwnj;له هه&zwnj;وڵیاندا به&zwnj;گه&zwnj;ڵای دره&zwnj;خته&zwnj;کانی به&zwnj;هه&zwnj;شت (خۆیان داپۆشن) ئابه&zwnj;و شێوه&zwnj;یه ئاده&zwnj;م یاخی بوو له په&zwnj;روه&zwnj;ردگاری و فریوی خواردو ڕێگای لێ تێکچوو، پاشان په&zwnj;روه&zwnj;ردگاری هه&zwnj;ڵی بژارد و (کردی به پێغه&zwnj;مبه&zwnj;ر) ته&zwnj;وبه&zwnj;ی لێ وه&zwnj;رگرت و هیدایه&zwnj;ت و ڕێنموویی کرد.';
$t[122] = 'پاشان په&zwnj;روه&zwnj;ردگاری هه&zwnj;ڵیبژارد بۆ پێغه&zwnj;مبه&zwnj;رێتی و له&zwnj;خۆی نزیک خسته&zwnj;وه&zwnj;و ته&zwnj;وبه&zwnj;شی لێ وه&zwnj;رگرت و شاره&zwnj;زا و ڕێنمایی کرد، بۆ پارانه&zwnj;وه&zwnj;و داوا کردنی لێخۆشبوون.';
$t[123] = '(ئه&zwnj;وسا خوای گه&zwnj;وره فه&zwnj;رمانیدا) وتی: ئیتر هه&zwnj;ردووکتان له به&zwnj;هه&zwnj;شت دابه&zwnj;زن به&zwnj;گشتی، هه&zwnj;ندێکتان ده&zwnj;بنه دوژمنی هه&zwnj;ندێکتان، جا ئه&zwnj;گه&zwnj;ر هیدایه&zwnj;ت و ڕێنموویی من بۆ ئێوه هات، ئه&zwnj;وه&zwnj;ی شوێنی به&zwnj;رنامه&zwnj;که&zwnj;م بکه&zwnj;وێت، نه&zwnj;گومڕا ده&zwnj;بێت، نه ناخۆشی و ناڕه&zwnj;حه&zwnj;تی و ناسۆر دێته ڕێی.';
$t[124] = 'ئه&zwnj;وه&zwnj;یش ڕوو وه&zwnj;ربگێرێت له به&zwnj;رنامه و یادی من پشتی تێ بکات، بێگومان بۆ ئه&zwnj;و جۆره که&zwnj;سانه ژیانێکی ترش و تاڵ و ناخۆش پێش دێت، له ڕۆژی قیامه&zwnj;تیشدا به&zwnj;کوێری حه&zwnj;شری ده&zwnj;که&zwnj;ین..';
$t[125] = 'ئه&zwnj;و جۆره که&zwnj;سه ده&zwnj;ڵێت: ئه&zwnj;ی په&zwnj;روه&zwnj;ردگار بۆ به&zwnj;کوێری حه&zwnj;شرت کردم خۆ من کاتی خۆی چاوساغ و بینا بووم.';
$t[126] = '(له وه&zwnj;ڵامدا خوا) ده&zwnj;فه&zwnj;رموێت: تۆ ئا به&zwnj;و شێوه&zwnj;یه بوویت (کوێت بوویت له ئاستی) ئایه&zwnj;ت و به&zwnj;ڵگه&zwnj;کانی ئێمه&zwnj;دا کاتێک پێت ڕاده&zwnj;گه&zwnj;یه&zwnj;نرا، ئێمه&zwnj;ش ئه&zwnj;مڕۆ ئاوا مامه&zwnj;ڵه&zwnj;ی که&zwnj;سێکی له&zwnj;بیر کراوت له&zwnj;گاڵدا ده&zwnj;که&zwnj;ین (له سزاو ئازاردا ده&zwnj;هێڵرێیته&zwnj;وه&zwnj;).';
$t[127] = 'ئا به&zwnj;و شێوه&zwnj;یه&zwnj;، تۆڵه ده&zwnj;که&zwnj;ینه&zwnj;وه له&zwnj;و که&zwnj;سه&zwnj;ی که له سنوور ده&zwnj;ترازێت و باوه&zwnj;ڕ به&zwnj;ئایه&zwnj;ت و فه&zwnj;رمانه&zwnj;کانی په&zwnj;روه&zwnj;ردگاری ناهێنێت، بێگومان سزای قیامه&zwnj;ت زۆر سه&zwnj;خت ترو به&zwnj;رده&zwnj;وامتره (له سزای دنیا).';
$t[128] = 'ئایا بۆیان ڕوون نه&zwnj;بۆته&zwnj;وه که چه&zwnj;نده&zwnj;ها نه&zwnj;وه&zwnj;مان پێش ئه&zwnj;مان له&zwnj;ناو بردووه&zwnj;، به&zwnj;ناو شوێنه&zwnj;وارو ماڵ و حاڵیاندا هاتوچۆ ده&zwnj;که&zwnj;ن، به&zwnj;ڕاستی ئا له&zwnj;و به&zwnj;سه&zwnj;رهاتانه&zwnj;دا به&zwnj;ڵگه و نیشانه&zwnj;ی زۆر هه&zwnj;یه بۆ خاوه&zwnj;ن بیرو هۆشمه&zwnj;نده&zwnj;کان.';
$t[129] = 'خۆ ئه&zwnj;گه&zwnj;ر بڕیارێک له&zwnj;لایه&zwnj;ن په&zwnj;روه&zwnj;ردگارته&zwnj;وه پێش نه&zwnj;که&zwnj;وتایه&zwnj;و مۆڵه&zwnj;تی دیاریکراویش نه&zwnj;بوایه&zwnj;، ئه&zwnj;وه له&zwnj;ناو بردنی خوانه&zwnj;ناسان شتێکی ئه&zwnj;نجامدار ده&zwnj;بوو.';
$t[130] = 'که&zwnj;واته ئه&zwnj;ی محمد صلی الله علیه وسلم، ئه&zwnj;ی ئیماندار خۆڕاگر به به&zwnj;رامبه&zwnj;ر ئه&zwnj;و گوفتاره نابه&zwnj;جێیانه&zwnj;یان که ده&zwnj;یڵێن و ته&zwnj;سبیحات و ستایش و سوپاسی په&zwnj;روه&zwnj;ردگارت بکه&zwnj;، پێش هه&zwnj;ڵهاتنی خۆرو پێش ئاوابوونی، له هه&zwnj;ندێک کاته&zwnj;کانی شه&zwnj;ودا هه&zwnj;ر ته&zwnj;سبیحات و ستایشی بکه&zwnj;، له به&zwnj;شه جیا جیاکانی ڕۆژیشدا هه&zwnj;ر ته&zwnj;سبیحات و ستایشی بکه&zwnj;، بۆ ئه&zwnj;وه&zwnj;ی له ڕه&zwnj;زامه&zwnj;ندی په&zwnj;روه&zwnj;ردگار به&zwnj;هره&zwnj;ماند ببیت.';
$t[131] = '(ئه&zwnj;ی ئیماندار) تۆ چاوه&zwnj;کانت مه&zwnj;بڕه ئه&zwnj;و نازو نیعمه&zwnj;ته&zwnj;ی که هه&zwnj;ندێک خه&zwnj;ڵکمان لێ به&zwnj;هره&zwnj;وه&zwnj;ر کردووه&zwnj;، که زینه&zwnj;ت و ڕازاوه&zwnj;ی ژیانی دنیایه&zwnj;، بۆ ئه&zwnj;وه&zwnj;ی له&zwnj;و نازو نیعمه&zwnj;ته تاقیانبکه&zwnj;ینه&zwnj;وه&zwnj;، بێگومان ڕزق و ڕۆزی په&zwnj;روه&zwnj;ردگارت (له به&zwnj;هه&zwnj;شتدا) چاکترو به&zwnj;رده&zwnj;وامتره&zwnj;.';
$t[132] = '(ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر صلی الله علیه وسلم، ئه&zwnj;ی ئیماندار) فه&zwnj;رمان بده به هه&zwnj;موو تاکه&zwnj;کانی خێزانت، تا نوێژه&zwnj;که&zwnj;یان (به&zwnj;چاکی ئه&zwnj;نجام بده&zwnj;ن، چونکه خۆراکی ڕۆحه&zwnj;) و به&zwnj;رده&zwnj;وام و کۆڵنه&zwnj;ده&zwnj;ر و خۆڕاگربه له&zwnj;سه&zwnj;ری، ئێمه بێگومان داوای ڕزق و ڕۆزی له تۆ ناکه&zwnj;ین، به&zwnj;ڵکو ئێمه ڕزق و ڕۆزی به تۆ ده&zwnj;به&zwnj;خشین، سه&zwnj;ره&zwnj;نجامی چاک هه&zwnj;ر بۆ خواناسی و دینداریه&zwnj;.';
$t[133] = '(کافره&zwnj;کانی مه&zwnj;ککه&zwnj;) وتیان: باشه&zwnj;، ئه&zwnj;وه بۆ له&zwnj;لایه&zwnj;ن په&zwnj;روه&zwnj;ردگاریه&zwnj;وه مه&zwnj;عجیزه&zwnj;یه&zwnj;کی بۆ ئێمه نه&zwnj;هێناو (نیشانی نه&zwnj;داین)، مه&zwnj;گه&zwnj;ر باسی (ئه&zwnj;و مه&zwnj;عجیزانه&zwnj;) له کتێبه پێشووه&zwnj;کاندا ڕوون نه&zwnj;کراوه&zwnj;ته&zwnj;وه&zwnj;؟ (کاتی خۆی نیشانی قه&zwnj;ومه پێشووه&zwnj;کانمان داوه به&zwnj;ڵام خۆ باوه&zwnj;ڕیان نه&zwnj;هێناوه&zwnj;، بۆ ئامانیشی پێش بهێنین هه&zwnj;ر باوه&zwnj;ڕ نا&zwnj;هێنن).';
$t[134] = 'خۆ ئه&zwnj;گه&zwnj;ر به&zwnj;ڕاستی ئێمه&zwnj;ش پێش ناردنی قورئان و پێغه&zwnj;مبه&zwnj;ر له&zwnj;ناومان ببردنایه&zwnj;، ئه&zwnj;وه ده&zwnj;یانوت: په&zwnj;روه&zwnj;ردگارا، بۆچی پێغه&zwnj;مبه&zwnj;رێکت بۆ ڕه&zwnj;وانه نه&zwnj;کردین، تا شوێنی به&zwnj;رنامه و ئایه&zwnj;ته&zwnj;کانی تۆ بکه&zwnj;وین پێش ئه&zwnj;وه&zwnj;ی زه&zwnj;لیل و ڕیسوا ببین.';
$t[135] = '(ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر(صلی الله علیه وسلم) به دوژمنانی دین) بڵێ: هه&zwnj;موولایه&zwnj;ک چاوه&zwnj;ڕوانن، ده ئێوه&zwnj;ش چاوه&zwnj;ڕێ بن له ئاینده&zwnj;دا ده&zwnj;زانن که کێ خاوه&zwnj;نی به&zwnj;رنامه و ڕێبازی ڕاست و دروسته و کێش ڕێبازی هیدایه&zwnj;تی گرتۆته به&zwnj;ر.';
