<?php
$t = array();
$t[1] = 'به ڕاستى ئه&zwnj;و بڕوادارانه سه&zwnj;رکه&zwnj;وتوو سه&zwnj;رفراز بوون...';
$t[2] = 'ئه&zwnj;وانه&zwnj;ش ئه&zwnj;وانه&zwnj;ن که له نوێژه&zwnj;کانیاندا ملکه&zwnj;چ و دڵ ئارام و دڵ دامه&zwnj;زراون و دڵیان لاى خوایه&zwnj;...';
$t[3] = 'ئه&zwnj;وانه&zwnj;ش که له گوفتارى بێ سوودو کردارى نابه&zwnj;جێ و بێ هووده دووره په&zwnj;رێزن...';
$t[4] = 'هه&zwnj;روه&zwnj;ها ئه&zwnj;وانه&zwnj;ش که زه&zwnj;کاتى ماڵیان ده&zwnj;رده&zwnj;که&zwnj;ن...';
$t[5] = 'ئه&zwnj;وانه&zwnj;ش که پارێزه&zwnj;رى داوێنى خۆیانن...';
$t[6] = 'جگه له&zwnj;گه&zwnj;ڵ هاوسه&zwnj;ران و که&zwnj;نیزه&zwnj;که&zwnj;کانیاندا نه&zwnj;بێت، که ئه&zwnj;وه جێگه&zwnj;ى لۆمه&zwnj;و سه&zwnj;ر زه&zwnj;نشت نیه&zwnj;، (له بارودۆخى نائاسایدا به&zwnj;هۆى جه&zwnj;نگه&zwnj;وه ئافره&zwnj;تانى موسڵمان ده&zwnj;که&zwnj;ونه ده&zwnj;ست دوژمنان و ده&zwnj;ستدرێژى بێ سنوور ده&zwnj;کرێته سه&zwnj;ریان، دوور نیه ئافره&zwnj;تانى دوژمن نه&zwnj;که&zwnj;وێته ده&zwnj;ست ئیمانداران، ئه&zwnj;و کاته ته&zwnj;نها یه&zwnj;ك که&zwnj;س ده&zwnj;بێته هاوسه&zwnj;رىو، به ڕێزه&zwnj;وه ڕه&zwnj;فتارى له&zwnj;گه&zwnj;ڵ ده&zwnj;کات).';
$t[7] = 'جا ئه&zwnj;وه&zwnj;ى جگه له&zwnj;و سنووره دیارى کراوه&zwnj;، ڕێگه&zwnj;و ڕێبازى تر بگرێته&zwnj;به&zwnj;ر، ئه&zwnj;و جۆره که&zwnj;سانه به یاخى و له سنوور ده&zwnj;رچوو ده&zwnj;ژمێردرێن.';
$t[8] = 'هه&zwnj;روه&zwnj;ها ئه&zwnj;وانه&zwnj;ى که چاودێرى ئه&zwnj;مانه&zwnj;ت و په&zwnj;یمانه&zwnj;کانیان ده&zwnj;که&zwnj;ن (خیانه&zwnj;ت و فێڵ ناکه&zwnj;ن).';
$t[9] = 'ئه&zwnj;وانه&zwnj;ش که پابه&zwnj;ندى نوێژه&zwnj;کانن و له کاتى خۆیدا به ڕێکو پێکى ئه&zwnj;نجامى ده&zwnj;ده&zwnj;ن و ده&zwnj;یپارێزن.';
$t[10] = 'ئا ئه&zwnj;وانه میراتگران و خاوه&zwnj;نانى به&zwnj;هه&zwnj;شتن.';
$t[11] = 'ئه&zwnj;وانه ده&zwnj;بنه خاوه&zwnj;نى به&zwnj;هه&zwnj;شتى فیرده&zwnj;وس (که به&zwnj;رزترین و به نرخترین به&zwnj;هه&zwnj;شته&zwnj;) و بۆ هه&zwnj;میشه ژیانى نه&zwnj;بڕاوه&zwnj;ى تیادا ده&zwnj;به&zwnj;نه سه&zwnj;ر (له&zwnj;و په&zwnj;ڕى لاوێتى و ته&zwnj;ندروستى ئاسووده&zwnj;ییدا).';
$t[12] = 'سوێند به خوا به ڕاستى ئێمه ئاده&zwnj;میزادمان له ئاوێته&zwnj;یه&zwnj;کى پێکهاتوو له قوڕ به&zwnj;دیهێناوه&zwnj;.';
$t[13] = 'له&zwnj;وه&zwnj;و دوا له جێگه&zwnj;یه&zwnj;کى پارێزراوو له باردا (که منداڵانى دایکه&zwnj;) له شێوه&zwnj;ى نوتفه&zwnj;یه&zwnj;کى زۆر بچووکدا دامان مه&zwnj;زراندووه&zwnj;.';
$t[14] = 'له&zwnj;وه&zwnj;و دوا نوتفه&zwnj;که&zwnj;مان کردووه به خۆ هه&zwnj;ڵواسه&zwnj;رێك (سه&zwnj;ره مێکوته&zwnj;یه&zwnj;ك) پاشان خۆهه&zwnj;ڵواسه&zwnj;ره&zwnj;که&zwnj;مان کردووه به گۆشتپاره&zwnj;یه&zwnj;ك، ئینجا (شێوازى) گۆشتپاره&zwnj;که&zwnj;مان به په&zwnj;یکه&zwnj;رى ئێسك گۆڕیوه&zwnj;و به گۆشت په&zwnj;یکه&zwnj;رى ئێسکه&zwnj;که&zwnj;مان داپۆشیوه&zwnj;، ئه&zwnj;وساله شێوه&zwnj;یه&zwnj;کى تردا ده&zwnj;رمان هێناوه (که شێوه&zwnj;ى ئاده&zwnj;میزادێکى ڕێکو پێکه و ڕۆحى پێ به&zwnj;خشراوه&zwnj;)، به ڕاستى خواى مه&zwnj;زن به&zwnj;رزو پیرۆزو موباره&zwnj;که&zwnj;و چاکترین به&zwnj;دیهێنه&zwnj;رو چاکترین دروستکاره&zwnj;.';
$t[15] = 'بێگومان سه&zwnj;ره&zwnj;نجام ئێوه هه&zwnj;ر ده&zwnj;بێت بمرن (له توێى خاکدا ده&zwnj;بێت مه&zwnj;نزڵ بگرن).';
$t[16] = 'پاشان ده&zwnj;بێت له ڕۆژى قیامه&zwnj;تدا زیندوو بکرێنه&zwnj;وه (بۆ پاداشت و لێپرسینه&zwnj;وه&zwnj;).';
$t[17] = 'سوێند به خوا به ڕاستى ئێمه له ڕاسه&zwnj;رتانه&zwnj;وه حه&zwnj;وت چین ئاسمانمان دروستکردووه&zwnj;و ئێمه هه&zwnj;رگیز له دروست کراوان غافڵ و بێ ئاگا نین (هیچ شتێکمان لێ بزر نابێت و هیچ شتێك پشتگوێ ناخه&zwnj;ین).';
$t[18] = 'هه ڕ ئێمه له ئاسمانه&zwnj;وه به ئه&zwnj;ندازه&zwnj;و نه&zwnj;خشه&zwnj;یه&zwnj;کى دیاریکراو بارانمان باراندووه&zwnj;و ئینجا له ناخى زه&zwnj;ویدا جێگیرمان کردووه&zwnj;، بێگومان ئێمه به جۆره&zwnj;ها شێوه ده&zwnj;توانین ئه&zwnj;و ئاوه له ناو به&zwnj;رین و ئه&zwnj;و سه&zwnj;رچاوانه وشك بکه&zwnj;ین.';
$t[19] = 'ئێمه به هۆى ئه&zwnj;و ئاوه&zwnj;وه چه&zwnj;نده&zwnj;ها باخ و باخاتى خورماو ڕه&zwnj;زمان به&zwnj;دى هێناوه بۆتان، (له&zwnj;و باخ و باخچانه&zwnj;دا) میوه&zwnj;هاتى جۆراو جۆرى زۆرو زه&zwnj;به&zwnj;ندمان بۆبه&zwnj;دیهێناون، له هه ندێکیان ده&zwnj;خۆن.';
$t[20] = 'دره&zwnj;ختێکمان بۆ ڕواندوون که له کێوى طور له بیابانى سینا سه&zwnj;وز ده&zwnj;بێت که ڕۆن و چه&zwnj;ورى لێ ده&zwnj;رده&zwnj;هێنرێت و ئاماده&zwnj;یه بۆ که&zwnj;سانێك که ده&zwnj;یخۆن (مه&zwnj;به&zwnj;ست دره&zwnj;ختى زه&zwnj;یتونه که هه&zwnj;میشه سه&zwnj;وزه&zwnj;و ته&zwnj;مه&zwnj;ن درێژه&zwnj;و بێ ئه&zwnj;رکه&zwnj;و کالیسیۆم و ئا&zwnj;سن و فۆسفۆڕو ڤیتامینى جۆراو جۆرو پڕۆتینى تێدایه&zwnj;، گه&zwnj;لێك داوو ده&zwnj;رمانیشى لێ ده&zwnj;رده&zwnj;هێنرێت).';
$t[21] = 'به ڕاستى له به&zwnj;دیهاتنى ماڵاتیشدا جێگه&zwnj;ى سه&zwnj;رنج و په&zwnj;نده بۆتان که ورد ببنه&zwnj;وه&zwnj;و به&zwnj;دیهێنه&zwnj;رى جوانکار بناسن) جا له&zwnj;وه&zwnj;ى که له سکیاندا به&zwnj;دى دێت (مه&zwnj;به&zwnj;ست شیره&zwnj;) ده&zwnj;رخوارتان ده&zwnj;ده&zwnj;ین و چه&zwnj;نده&zwnj;ها که&zwnj;ڵکى زۆرى ترى هه&zwnj;یه بۆتان (له پێستیان، له خورى و موویان... هتد) هه&zwnj;روه&zwnj;ها لێشیان ده&zwnj;خۆن (گۆشت و جگه&zwnj;رو سه&zwnj;رو پێیان به&zwnj;جۆره&zwnj;ها شێوه ئاماده ده&zwnj;که&zwnj;ن و خواردنى خۆشیان لێ دروست ده&zwnj;که&zwnj;ن).';
$t[22] = 'له&zwnj;سه&zwnj;ر پشتى هه&zwnj;ندێك له&zwnj;و گیاندارانه&zwnj;ش هه&zwnj;ڵده&zwnj;گیرێن و له&zwnj;سه&zwnj;ر که&zwnj;شتیه&zwnj;کانیش (ده&zwnj;گوێزرێنه&zwnj;وه له شوێنێکه&zwnj;وه بۆ شوێنێکى تر له&zwnj;گه&zwnj;ڵ که&zwnj;ل و په&zwnj;ل و پێداویستیه&zwnj;کانتاندا).';
$t[23] = 'سوێند بێت بێگومان ئێمه نوح پێغه&zwnj;مبه&zwnj;رمان ڕه&zwnj;وانه کرد بۆ سه&zwnj;ر قه&zwnj;ومه&zwnj;که&zwnj;ى، جا پێى وتن: ئه&zwnj;ى قه&zwnj;وم و گه&zwnj;لم هه&zwnj;ر خوا بپه&zwnj;رستن، چونکه هیچ خوایه&zwnj;کى ترتان نیه جگه له&zwnj;و، ئایا خۆتان له خه&zwnj;شم و قینى ناپارێزن؟!';
$t[24] = 'جا ئه&zwnj;وانه&zwnj;ى که بێ باوه&zwnj;ر بوون له ده&zwnj;سته&zwnj;ى ده&zwnj;سه&zwnj;ڵاتدارى قه&zwnj;ومه&zwnj;که&zwnj;ى وتیان: (گوێى بۆ مه&zwnj;گرن)ئه&zwnj;م پیاوه هه&zwnj;ر که&zwnj;سێکى وه&zwnj;ك ئێوه&zwnj;یه&zwnj;، زیاتر نیه&zwnj;، ده&zwnj;یه&zwnj;وێت خۆى بکاته گه&zwnj;وره&zwnj;تان!! ئه&zwnj;گه&zwnj;ر خوا بیویستایه پێغه&zwnj;مبه&zwnj;رێك بنێرێت. ئه&zwnj;وه فریشته&zwnj;ى داده&zwnj;به&zwnj;زاند، ئێمه ئه&zwnj;م هه&zwnj;واڵه&zwnj;مان له باوو باپیرانى پێشووه&zwnj;وه نه&zwnj;بیستووه&zwnj;.';
$t[25] = 'ئه&zwnj;مه جگه له&zwnj;وه&zwnj;ى که پیاوێکه شێتى سه&zwnj;رى لێداوه&zwnj;، هیچى تر نى یه&zwnj;، که&zwnj;واته تا ماوه&zwnj;یه&zwnj;ك مۆڵه&zwnj;تى بده&zwnj;ن و وازى لێ بهێنن، (یان کۆڵ ده&zwnj;دات، یان ده&zwnj;مرێت!!).';
$t[26] = 'ئه&zwnj;وسا نوح وتى: په&zwnj;روه&zwnj;ردگارا، سه&zwnj;رکه&zwnj;وتنم پێ ببه&zwnj;خشه چونکه بڕوایان پێ نه&zwnj;کردم.';
$t[27] = 'ئینجا ئێمه&zwnj;ش ئه&zwnj;گادارمان کرد که که&zwnj;شتیه&zwnj;که دروست بکه له ژێر چاودێرى و نیگاى ئێمه&zwnj;دا، جا هه&zwnj;ر کاتێك فه&zwnj;رمانى ئێمه ده&zwnj;رچوو، ته&zwnj;نووره&zwnj;که فواره&zwnj;ى کردو ئاوى لێ ده&zwnj;رهات، ئه&zwnj;وه له هه&zwnj;موو جۆرێك جووتێك سوار بکه&zwnj;، هه&zwnj;روه&zwnj;ها خاوو خێزانت جگه له&zwnj;وانه&zwnj;ى که پێشتر بڕیارى غه&zwnj;رق بوونیان له&zwnj;سه&zwnj;ر دراوه&zwnj;، جا ده&zwnj;رباره&zwnj;ى ئه&zwnj;وانه&zwnj;ی که سته&zwnj;میان کردو (باوه&zwnj;ڕیان نه&zwnj;هێنا) لێم مه&zwnj;پاڕێره&zwnj;وه&zwnj;، چونکه به ڕاستى ئه&zwnj;وانه هه&zwnj;موو نوقم کراون (له زریانه&zwnj;که&zwnj;دا).';
$t[28] = 'جا کاتێك خۆت و ئه&zwnj;وانه&zwnj;ى هاوڕێ و یاوه&zwnj;رتن له&zwnj;سه&zwnj;ر که&zwnj;شتیه&zwnj;که سوار بوون (ئه&zwnj;ی نوح تۆ) ئه&zwnj;وه بڵێ: سوپاس و ستایش بۆ ئه&zwnj;و خوایه&zwnj;ی که ڕزگاری کردین له ده&zwnj;ست قه&zwnj;ومی سته&zwnj;مکار.';
$t[29] = 'هه&zwnj;روه&zwnj;ها بڵێ: په&zwnj;روه&zwnj;ردگارا له شوێنێکى موباره&zwnj;ك و پیرۆز دامبه&zwnj;زێنه&zwnj;. چونکه تۆ چاکترین زاتێکى بۆ ئه&zwnj;وه&zwnj;ی له شوێنی موباره&zwnj;ك و پیرۆزدا دامانبه&zwnj;زێنیت (ئیتر که&zwnj;شتیه&zwnj;که له&zwnj;سه&zwnj;ر کێوی جودی له کوردستانی باکور له&zwnj;نگه&zwnj;ری گرت).';
$t[30] = 'به&zwnj;ڕاستی ئا له&zwnj;و به&zwnj;سه&zwnj;رهاته&zwnj;دا چه&zwnj;نده&zwnj;ها نیشانه&zwnj;و به&zwnj;ڵگه&zwnj;ی ئاشکرا هه&zwnj;یه&zwnj;، بێگومان ئێمه (به جۆره&zwnj;ها شێوه&zwnj;و له هه&zwnj;موو سه&zwnj;رده&zwnj;مێکدا) خه&zwnj;ڵکی تاقی ده&zwnj;که&zwnj;ینه&zwnj;وه&zwnj;.';
$t[31] = 'له&zwnj;وه&zwnj;ودوا له دوای ئه&zwnj;وان قه&zwnj;وم و نه&zwnj;وه&zwnj;یه&zwnj;کی ترمان به&zwnj;دیهێنا.';
$t[32] = 'ئینجا پێغه&zwnj;مبه&zwnj;رێکمان هه&zwnj;ر له خۆیان بۆ ڕه&zwnj;وانه کردن، (پێی وتن): ته&zwnj;نها خوا بپه&zwnj;رستن، جگه له&zwnj;و زاته خوایه&zwnj;کی ترتان نیه&zwnj;، جا ئه&zwnj;وه لێی ناترسن؟ ئه&zwnj;وه خۆتان له خه&zwnj;شم و قینی ناپارێزن؟!';
$t[33] = 'ئه&zwnj;وانه&zwnj;ی که بێ باوه&zwnj;ڕ بوون له ده&zwnj;سته&zwnj;ی ده&zwnj;سه&zwnj;ڵاتداری قه&zwnj;ومه&zwnj;که&zwnj;ی و بڕوایان به پێشهاتی قیامه&zwnj;ت و زیندوو بوونه&zwnj;وه&zwnj;و لێپرسینه&zwnj;وه نه&zwnj;بوو و له ژیانی دنیادا له نازو نیعمه&zwnj;تی زۆر به&zwnj;هره&zwnj;وه&zwnj;رمان کردبوون وتیان، ئه&zwnj;م پێغه&zwnj;مبه&zwnj;ره ته&zwnj;نها به&zwnj;شه&zwnj;رێکه وه&zwnj;ك ئێوه&zwnj;، له&zwnj;و شتانه ده&zwnj;خوات که ئێوه لێی ده&zwnj;خۆن و له&zwnj;و شتانه ده&zwnj;خواته&zwnj;وه که ئێوه لێی ده&zwnj;خۆنه&zwnj;وه&zwnj;.';
$t[34] = 'جا ئه&zwnj;گه&zwnj;ر ملکه&zwnj;چ و فه&zwnj;رمانبه&zwnj;رداری به&zwnj;شه&zwnj;رێکی وه&zwnj;کو خۆتان ببن، ئه&zwnj;وه به ڕاستی ئیتر ئێوه خه&zwnj;ساره&zwnj;تمه&zwnj;ندو زه&zwnj;ره&zwnj;رمه&zwnj;ندن!!';
$t[35] = 'ئایا هه&zwnj;ڕه&zwnj;شه&zwnj;تان لێده&zwnj;کات به&zwnj;وه&zwnj;ی کاتێك بمرن و ببنه&zwnj;وه به خاك و ئێسك، ئێوه ده&zwnj;رئه&zwnj;هێنرێنه&zwnj;وه و زیندوو ده&zwnj;کرێنه&zwnj;وه و له خاك ده&zwnj;ربهێنرێنه&zwnj;وه&zwnj;؟!';
$t[36] = 'دووره و زۆر دووره ئه&zwnj;و هه&zwnj;ڕه&zwnj;شه&zwnj;یه&zwnj;ی که لێتان ده&zwnj;کرێت بێته دی و (زیندوو بکرێنه&zwnj;وه&zwnj;)!!';
$t[37] = 'ژیان ته&zwnj;نها ژیانی ئه&zwnj;م دنیایه&zwnj;&zwnj;مانه&zwnj;و هیچی تر نی یه&zwnj;، ده&zwnj;ژین و ده&zwnj;مرین و ئێمه زیندوو ناکرێینه&zwnj;وه&zwnj;!!';
$t[38] = 'ئه&zwnj;م پیاوه (مه&zwnj;به&zwnj;ست پێغه&zwnj;مبه&zwnj;ره&zwnj;که&zwnj;یانه&zwnj;) درۆ بۆ خوا هه&zwnj;ڵده&zwnj;به&zwnj;ستێت، ئێمه&zwnj;ش بڕوای پێ ناکه&zwnj;ین!!';
$t[39] = '(ئه&zwnj;میش وه&zwnj;ك نوح) وتی: په&zwnj;روه&zwnj;ردگارا، سه&zwnj;رکه&zwnj;وتنم پێببه&zwnj;خشه چونکه ئه&zwnj;وانه منیان به&zwnj;درۆزانی و بڕوایان پێنه&zwnj;کردم.';
$t[40] = 'خوای گه&zwnj;وره فه&zwnj;رمووی: دڵنیابه به&zwnj;م زووانه په&zwnj;شیمان ده&zwnj;بنه&zwnj;وه&zwnj;.';
$t[41] = 'ئه&zwnj;وسا ده&zwnj;نگێکی به&zwnj;هێز (دڵی پچڕاندن و مێشکی ته&zwnj;قاندن) کردمانن به که&zwnj;ف و کوڵ و چیلکه&zwnj;و چه&zwnj;وێڵی سه&zwnj;ر ئاو، ده&zwnj;با له&zwnj;وه&zwnj;و لاترچن قه&zwnj;ومی سته&zwnj;مکار.';
$t[42] = 'پاشان له دوای ئه&zwnj;وان چه&zwnj;ند قه&zwnj;ومێکی ترمان هێنایه کایه&zwnj;وه (ساڵه&zwnj;ها ژیان و له نازو نیعمه&zwnj;ت به&zwnj;هره&zwnj;وه&zwnj;ر بوون).';
$t[43] = 'هه&zwnj;ر میلله&zwnj;تێك کاتێك بڕیاری له&zwnj;ناو بردنی ده&zwnj;درێت، ئه&zwnj;و بڕیاره نه پێش ده&zwnj;که&zwnj;وێت نه دوا ده&zwnj;خرێت به&zwnj;ڵکو له کات و ساتی خۆیدا ڕووده&zwnj;دات.';
$t[44] = 'پاشان پێغه&zwnj;مبه&zwnj;رانی خۆمانمان یه&zwnj;ك به دوای یه&zwnj;ك ڕه&zwnj;وانه کرد، هه&zwnj;ر ئوممه&zwnj;تێك کاتێك پێغه&zwnj;مبه&zwnj;ری له خۆی بۆ ڕه&zwnj;وانه ده&zwnj;کرا (زۆر به&zwnj;یان به&zwnj;رنامه&zwnj;که&zwnj;یان) به درۆ ده&zwnj;زانی. ئێمه&zwnj;ش ئه&zwnj;و میلله&zwnj;ته&zwnj;مان به شوێن یه&zwnj;کدا (له ناو برد) و کردمانن به باس و خواسی ناو خه&zwnj;ڵکی و (په&zwnj;ندو عیبره&zwnj;ت)، ده&zwnj;با ئه&zwnj;و که&zwnj;سانه&zwnj;ی باوه&zwnj;ڕ ناهێنن له&zwnj;وه&zwnj;و لاترچن.';
$t[45] = 'پاشان موساو هاروونی برایمان ڕه&zwnj;وانه کرد هاوڕێ له&zwnj;گه&zwnj;ڵ نیشانه&zwnj;و به&zwnj;ڵگه&zwnj;و موعجیزه&zwnj;کانماندا و به&zwnj;ڵگه&zwnj;ی به&zwnj;هێزمان پێبه&zwnj;خشین (له&zwnj;سه&zwnj;ر ڕاستی په&zwnj;یامه&zwnj;که&zwnj;یان)...';
$t[46] = 'بۆ سه&zwnj;ر فیرعه&zwnj;ون و دارو ده&zwnj;سته&zwnj;که&zwnj;ی، که&zwnj;چی خۆیان به&zwnj;گه&zwnj;وره زانی و قه&zwnj;ومێکی لووت به&zwnj;رز بوون';
$t[47] = '(فیرعه&zwnj;ون و دارو ده&zwnj;سته&zwnj;که&zwnj;ی) وتیان: ئایا ڕه&zwnj;وایه ئێمه باوه&zwnj;ڕ به دوو به&zwnj;شه&zwnj;ری وه&zwnj;ك خۆمان بکه&zwnj;ین، له کاتێکدا که قه&zwnj;ومه&zwnj;که&zwnj;یان ئێمه ده&zwnj;په&zwnj;رستن و (خزمه&zwnj;تکاری ئێمه&zwnj;ن).';
$t[48] = ')دوای چه&zwnj;نده&zwnj;ها ساڵ له هه&zwnj;وڵ و کۆششی ئه&zwnj;و دوو پێغه&zwnj;مبه&zwnj;ره&zwnj;) هه&zwnj;ر باوه&zwnj;ڕیان پێنه&zwnj;کردن و به درۆیان زانین، بۆیه چونه ڕیزی تیاچووه&zwnj;کانه&zwnj;وه&zwnj;.';
$t[49] = 'سوێند به&zwnj;خوا بێگومان ئێمه ته&zwnj;وراتمان به&zwnj;خشی به&zwnj;موسا بۆ ئه&zwnj;وه&zwnj;ی قه&zwnj;ومه&zwnj;که&zwnj;ی ڕێبازی هیدایه&zwnj;ت بگرنه&zwnj;به&zwnj;ر.';
$t[50] = 'هه&zwnj;روه&zwnj;ها کوڕه&zwnj;که&zwnj;ی مه&zwnj;ریه&zwnj;م و دایکیمان کرده به&zwnj;ڵگه&zwnj;و نیشانه (له&zwnj;سه&zwnj;ر توانایی خۆمان) و ئێمه هه&zwnj;ردووکیانمان حه&zwnj;وانه&zwnj;وه له شوێنێکی بڵندو خۆش و سازگارو ئاوداردا.';
$t[51] = 'ئینجا وتمان: ئه&zwnj;ی گرۆی پێغه&zwnj;مبه&zwnj;ران، بخۆن له ڕزق و ڕۆزیه چاك و پاکه&zwnj;کان، کارو کرده&zwnj;وه&zwnj;ی چاکیش ئه&zwnj;نجام بده&zwnj;ن، به ڕاستی من زانام به هه&zwnj;موو ئه&zwnj;و کارو کرده&zwnj;وانه&zwnj;ی که ده&zwnj;یکه&zwnj;ن.';
$t[52] = 'هه&zwnj;ر ئه&zwnj;مه&zwnj;شه دین و ئاین و به&zwnj;رنامه&zwnj;ی تاکو ته&zwnj;نهای ئێوه&zwnj;، (به&zwnj;رنامه&zwnj;ی یه&zwnj;کخواناسی)و من په&zwnj;روه&zwnj;ردگارتانم، پارێزکاربن و خۆتان له نافه&zwnj;رمانی من بپارێزن.';
$t[53] = 'به&zwnj;ڵام زۆربه&zwnj;ی خه&zwnj;ڵکی بیرو بۆچوونی خۆیان به&zwnj;ش به&zwnj;ش و پارچه پارچه کرد (له به&zwnj;رنامه&zwnj;ی یه&zwnj;کخواناسی لایاندا)، هه&zwnj;موو ده&zwnj;سته&zwnj;و تاقمێك به&zwnj;و به&zwnj;رنامه&zwnj;و نه&zwnj;خشه&zwnj;یه&zwnj;ی هه&zwnj;یانه دڵخۆشن.';
$t[54] = 'جا وازیان لێ بهێنه با تا ماوه&zwnj;یه&zwnj;ك له گومڕایی و سه&zwnj;ر لێ شێواویی و سه&zwnj;رگه&zwnj;ردانی خۆیاندا گیر بخۆن.';
$t[55] = 'ئایا ئه&zwnj;وانه وا ده&zwnj;زانن که ئه&zwnj;و ماڵ و سامان و نه&zwnj;وه&zwnj;یه&zwnj;ی پێیان ده&zwnj;به&zwnj;خشین؟! (هۆی ئه&zwnj;وه&zwnj;یه خۆشمان ده&zwnj;وێن)!!';
$t[56] = 'نه&zwnj;خێر، وانی یه&zwnj;، مه&zwnj;به&zwnj;ستمانه به&zwnj;په&zwnj;له له نازو نیعمه&zwnj;ت به&zwnj;هره&zwnj;وه&zwnj;ریان بکه&zwnj;ین؟ به&zwnj;ڵکو ئه&zwnj;وانه هه&zwnj;ستی پێ ناکه&zwnj;ن.';
$t[57] = 'به ڕاستی ئه&zwnj;وانه&zwnj;ی که ترسی په&zwnj;روه&zwnj;ردگاریان له دڵدایه&zwnj;و ده&zwnj;ترسن لێیان بڕه&zwnj;نجێت...';
$t[58] = 'ئه&zwnj;وانه&zwnj;ش بڕوا به ئایه&zwnj;ت و فه&zwnj;رمانه&zwnj;کانی په&zwnj;روه&zwnj;ردگاریان ده&zwnj;هێنن...';
$t[59] = 'ئه&zwnj;وانه&zwnj;ش که هاوه&zwnj;ڵ و هاوبه&zwnj;ش و شه&zwnj;ریك بۆ په&zwnj;روه&zwnj;ردگاریان بڕیار ناده&zwnj;ن...';
$t[60] = 'ئه&zwnj;وانه&zwnj;ش که ده&zwnj;به&zwnj;خشن له زه&zwnj;کات و خێر دڵیان ده&zwnj;ترسێت، نه&zwnj;وه&zwnj;کو لێیان وه&zwnj;رنه&zwnj;گیرێت، چونکه ئه&zwnj;وانه بێگومانن که بۆ لای په&zwnj;روه&zwnj;ردگاریان ده&zwnj;گه&zwnj;ڕێنه&zwnj;وه&zwnj;.';
$t[61] = 'ئا ئه&zwnj;وانه به په&zwnj;له&zwnj;ن بۆ ئه&zwnj;نجامدانی هه&zwnj;موو چاکه&zwnj;یه&zwnj;ك و پێشبڕکێش ده&zwnj;که&zwnj;ن له چاکه&zwnj;دا.';
$t[62] = 'ئێمه هیچ ئه&zwnj;رکێك ناده&zwnj;ین به&zwnj;سه&zwnj;ر هیچ که&zwnj;سێکدا مه&zwnj;گه&zwnj;ر له چوار چێوه&zwnj;ی توانای خۆیدا، جا ئێمه دۆسیه&zwnj;ی تایبه&zwnj;تی هه&zwnj;موو که&zwnj;سێکمان لایه که حه&zwnj;ق گۆیه (گوفتارو کرداره&zwnj;کانی تێدا تۆمار کراوه&zwnj;، له&zwnj;به&zwnj;ر ئه&zwnj;وه&zwnj;) ئه&zwnj;وانه هیچ جۆره سته&zwnj;مێکیان لێ ناکرێت.';
$t[63] = 'به&zwnj;ڵام خوا نه&zwnj;ناسان له&zwnj;م نه&zwnj;خشه&zwnj;و به&zwnj;رنامه&zwnj;و چاکه&zwnj;خوازیانه سه&zwnj;ریان ده&zwnj;رناچێت و تێی نا&zwnj;گه&zwnj;ن، کارو کرده&zwnj;وه&zwnj;ی تریشیان هه&zwnj;یه (جیایه له کرده&zwnj;ی ئیمانداران) به&zwnj;رده&zwnj;وام ئه&zwnj;نجامی ده&zwnj;ده&zwnj;ن.';
$t[64] = 'هه&zwnj;تا ئه&zwnj;و کاته&zwnj;ی به&zwnj;ڵا به&zwnj;سه&zwnj;ر خۆشگوزه&zwnj;ران و ده&zwnj;وڵه&zwnj;مه&zwnj;نده&zwnj;کانیاندا ده&zwnj;هێنن، ده&zwnj;ستبه&zwnj;جێ ئیتر ئاهو ناڵه&zwnj;و گریه&zwnj;و زاریان لێ به&zwnj;رز ده&zwnj;بێته&zwnj;وه&zwnj;.';
$t[65] = '(له قیامه&zwnj;تیشدا پێیان ده&zwnj;وترێت) ئه&zwnj;مڕۆ ئیتر ئێوه چرکه&zwnj;تان لێوه نه&zwnj;یه&zwnj;ت و هاوار نه&zwnj;که&zwnj;ن، چونکه بێگومان ئێوه له لایه&zwnj;ن ئێمه&zwnj;وه هیچ جۆره پشتگیریه&zwnj;کتان لێ ناکرێت.';
$t[66] = 'جا ئێوه کاتی خۆی ئایه&zwnj;ته&zwnj;کانی ئێمه&zwnj;تان به&zwnj;سه&zwnj;ردا ده&zwnj;خوێنرایه&zwnj;وه که چی پشتان تێده&zwnj;کردو به&zwnj;ره&zwnj;و دوا ده&zwnj;گه&zwnj;ڕانه&zwnj;وه&zwnj;و خۆتان لێ ده&zwnj;شارده&zwnj;وه و دژایه&zwnj;تیتان ده&zwnj;کرد.';
$t[67] = 'له کاتێکدا خۆتان به گه&zwnj;وره ده&zwnj;زانی له ئاستیدا شه&zwnj;و گاریشتان به باس و خواس و گاڵته پێکردنی ئایه&zwnj;ته&zwnj;کانی ئێمه ده&zwnj;برده سه&zwnj;ر.';
$t[68] = 'ئایا (بێ باوه&zwnj;ڕان) ئه&zwnj;م گوفتاره&zwnj;یان لێك نه&zwnj;داوه&zwnj;ته&zwnj;وه&zwnj;، یاخود واده&zwnj;زانن ئه&zwnj;م به&zwnj;رنامه&zwnj;یه&zwnj;ی که بۆ ئه&zwnj;وان ڕه&zwnj;وانه کراوه بۆ باوو باپیرانی دێرینیان ڕه&zwnj;وانه نه&zwnj;کراوه&zwnj;؟!';
$t[69] = 'یان ئه&zwnj;وه&zwnj;یه پێغه&zwnj;مبه&zwnj;ره&zwnj;که&zwnj;یان نه&zwnj;ناسیووه&zwnj;و ئه&zwnj;وه بۆته هۆی ئه&zwnj;وه&zwnj;ی بڕوا به په&zwnj;یامه&zwnj;که&zwnj;ی نه&zwnj;که&zwnj;ن و دژی بوه&zwnj;ستن!!';
$t[70] = 'یاخود (بێ شه&zwnj;رمانه&zwnj;) ده&zwnj;ڵێن: شێتی سه&zwnj;ری لێداوه&zwnj;، نه&zwnj;خێر، وانی یه&zwnj;، به&zwnj;ڵکو ڕاستی و حه&zwnj;قی بۆ هێناون، به&zwnj;ڵام زۆربه&zwnj;یان ڕقیان له ڕاستی یه&zwnj;و حه&zwnj;ز له حه&zwnj;ق ناکه&zwnj;ن و پێی سه&zwnj;غڵه&zwnj;تن.';
$t[71] = 'ئه&zwnj;گه&zwnj;ر حه&zwnj;ق و ڕاستی شوێنی ئاره&zwnj;زووی ئه&zwnj;وان بکه&zwnj;وێت، ئه&zwnj;وه ئاسمانه&zwnj;کان و زه&zwnj;وی و هه&zwnj;موو ئه&zwnj;وانه&zwnj;ی تیایاندایه ده&zwnj;شێواو تیا ده&zwnj;چوو سه&zwnj;ر گه&zwnj;ردان ده&zwnj;بوو، نه&zwnj;خێر (حه&zwnj;ق وایه ڕقیان لێی نه&zwnj;بێت)، چونکه ئێمه قورئانێکمان بۆ هێناون (که یادی هه&zwnj;موو چاکه&zwnj;یه&zwnj;کیان بخاته&zwnj;وه&zwnj;، که&zwnj;چی ئه&zwnj;وان له&zwnj;به&zwnj;رامبه&zwnj;ر ئه&zwnj;و یادخه&zwnj;ره&zwnj;وانه&zwnj;دا سه&zwnj;رکه&zwnj;ش و یاخین و پشتی تێده&zwnj;که&zwnj;ن.';
$t[72] = 'ئاخۆ داوای خه&zwnj;رجی و پاره له&zwnj;وانه ده&zwnj;که&zwnj;یت (له به&zwnj;رامبه&zwnj;ر هیدایه&zwnj;تیانه&zwnj;وه تا لایان سه&zwnj;نگین بێت؟ بۆیه باوه&zwnj;ڕ ناهێنن؟) خۆ به&zwnj;خششی په&zwnj;روه&zwnj;ردگارت به&zwnj;سوودترو چاکتره&zwnj;، ئه&zwnj;و زاته چاکترین زاتێکه بۆ به&zwnj;خشینی ڕزق و ڕۆزی.';
$t[73] = 'بێگومان تۆ بانگیان ده&zwnj;که&zwnj;یت بۆ ڕێگه&zwnj;و ڕێبازێکی ڕاست و دروست.';
$t[74] = 'به ڕاستی ئه&zwnj;وانه&zwnj;ی بڕوا ناهێنن به قیامه&zwnj;ت وێڵ و لاده&zwnj;رن له ڕێگه&zwnj;و ڕێبازی ڕاست.';
$t[75] = 'خۆ ئه&zwnj;گه&zwnj;ر به&zwnj;زه&zwnj;ییمان پیایاندا بێته&zwnj;وه&zwnj;و ڕه&zwnj;حمیان پێ بکه&zwnj;ین و ناخۆشی و سه&zwnj;غڵه&zwnj;تیان له&zwnj;سه&zwnj;ر لا به&zwnj;رین، ئه&zwnj;وه له سته&zwnj;م و سه&zwnj;رکه&zwnj;شیاندا ڕۆده&zwnj;چن و سه&zwnj;رگه&zwnj;ردان ده&zwnj;بن.';
$t[76] = 'سوێند به خوا به ڕاستی ئێمه تووشی سزاو ئازاری (جۆراو جۆرمان) کردن، که&zwnj;چی له&zwnj;گه&zwnj;ڵ ئه&zwnj;وه&zwnj;شدا نه ملکه&zwnj;چ بوون بۆ په&zwnj;روه&zwnj;ردگاریان و نه لێی ده&zwnj;پاڕێنه&zwnj;وه&zwnj;.';
$t[77] = 'هه&zwnj;تا ئه&zwnj;و کاته&zwnj;ی که ده&zwnj;روازه&zwnj;ی سزایه&zwnj;کی سه&zwnj;ختیان لێ ده&zwnj;که&zwnj;ینه&zwnj;وه&zwnj;، ئیتر ده&zwnj;ستبه&zwnj;جێ له&zwnj;ناویدا سه&zwnj;رگه&zwnj;ردان و سه&zwnj;ر لێشێواو نائومێد ده&zwnj;بن.';
$t[78] = 'هه&zwnj;ر ئه&zwnj;و زاته&zwnj;یه که (ده&zwnj;زگای) بیستن و بینین و لێکدانه&zwnj;وه&zwnj;ی پێ به&zwnj;خشیوون، که&zwnj;چی زۆر که&zwnj;متان سوپاسگوزارن.';
$t[79] = 'ئه&zwnj;و زاته&zwnj;یه له زه&zwnj;ویدا به&zwnj;رپای کردوون و بڵاوی کردوونه&zwnj;ته&zwnj;وه&zwnj;، سه&zwnj;ره&zwnj;نجام (له ڕۆژی قیامه&zwnj;تدا) هه&zwnj;ر بۆ لای ئه&zwnj;ویش کۆ ده&zwnj;کرێنه&zwnj;وه&zwnj;.';
$t[80] = 'ئه&zwnj;و زاته&zwnj;شه که ژیان ده&zwnj;به&zwnj;خشێت و مردن پێش ده&zwnj;هێنێت، گۆڕینی شه&zwnj;وو ڕۆژیش هه&zwnj;ر به&zwnj;ده&zwnj;ست ئه&zwnj;وه (له ڕووی تاریکی و ڕووناکی، کورتی و درێژی و... هتد)، جا ئایا ئێوه عه&zwnj;قڵ به&zwnj;کار ناهێنن؟ ژیریتان ناخه&zwnj;نه&zwnj;کار؟';
$t[81] = 'له&zwnj;گه&zwnj;ڵ ئه&zwnj;و هه&zwnj;موو به&zwnj;ڵگانه&zwnj;دا ئه&zwnj;وانه هه&zwnj;ر وه&zwnj;کو خه&zwnj;ڵکی پێشین قسه کۆنه&zwnj;کان ده&zwnj;ڵێنه&zwnj;وه&zwnj;و..';
$t[82] = 'ده&zwnj;ڵێن: باشه&zwnj;، ئه&zwnj;گه&zwnj;ر مردین و بووینه خاك و خۆڵ و ئێسك ئایا ئێمه دووباره زیندوو ده&zwnj;کرێینه&zwnj;وه&zwnj;؟!';
$t[83] = 'به ڕاستی ئێمه و باوو باپیرانمان له&zwnj;م جۆره به&zwnj;ڵێنانه&zwnj;مان پێشتر پێدرابوو، ئه&zwnj;م قسه&zwnj;و به&zwnj;ڵێنانه داستانی پێشینه&zwnj;کانه&zwnj;!!';
$t[84] = 'تۆش پێیان بڵێ: باشه زه&zwnj;وی و ئه&zwnj;و که&zwnj;سانه&zwnj;ی که له&zwnj;سه&zwnj;ری ده&zwnj;ژین کێ خاوه&zwnj;نیانه&zwnj;، ئه&zwnj;گه&zwnj;ر زیره&zwnj;ك و هۆشیارن؟ ئه&zwnj;گه&zwnj;ر شت ده&zwnj;زانن؟!';
$t[85] = 'سه&zwnj;رئه&zwnj;نجام هه&zwnj;ر دان به ڕاستیدا ده&zwnj;نێن و ده&zwnj;ڵێن: هه&zwnj;رخوا، خاوه&zwnj;نیانه&zwnj;، هه&zwnj;ر په&zwnj;روه&zwnj;ردگار به&zwnj;دیهێنه&zwnj;ریانه&zwnj;. ئه&zwnj;وسا پێیان بڵێ: جا ئه&zwnj;وه بۆچی یاداوه&zwnj;ری وه&zwnj;رناگرن؟';
$t[86] = 'پێیان بڵێ؛ ئه&zwnj;ی کێ په&zwnj;روه&zwnj;ردگاری حه&zwnj;وت ئاسمانه&zwnj;که&zwnj;یه&zwnj;و کێ په&zwnj;روه&zwnj;ردگاری عه&zwnj;رش و ته&zwnj;ختی فه&zwnj;رمانڕه&zwnj;وایی گه&zwnj;وره&zwnj;و فراوانه&zwnj;؟ (ئاسمانه&zwnj;کان و زه&zwnj;وی له به&zwnj;رامبه&zwnj;ر عه&zwnj;رشی په&zwnj;روه&zwnj;ردگاردا، وه&zwnj;کو ئه&zwnj;ڵقه&zwnj;یه&zwnj;کی په&zwnj;نجه وایه له پاڵ چیایه&zwnj;کی گه&zwnj;وره&zwnj;دا).';
$t[87] = 'سه&zwnj;ره&zwnj;نجام ده&zwnj;ڵێن: هه&zwnj;ر خوا په&zwnj;روه&zwnj;ردگاریانه&zwnj;، ئه&zwnj;وسا بڵێ: باشه ئاخر ئێوه لێی ناترسن؟ خۆتان ناپارێزن له خه&zwnj;شم و قینی؟ (ئاخر ئێوه له کێ یاخی ده&zwnj;بن؟)';
$t[88] = '(هه&zwnj;روه&zwnj;ها پێیان) بڵێ: کێ جڵه&zwnj;وی هه&zwnj;موو شتێکی به&zwnj;ده&zwnj;سته&zwnj;، له کاتێکدا هه&zwnj;ر ئه&zwnj;و په&zwnj;نا به&zwnj;خشه&zwnj;و که&zwnj;س ناتوانێت له سزای ئه&zwnj;و زاته&zwnj;، که&zwnj;س په&zwnj;نا بدات، ئه&zwnj;گه&zwnj;ر ئێوه بزانن و شاره&zwnj;زابن.';
$t[89] = '(سه&zwnj;ره&zwnj;نجام بۆ ئه&zwnj;مه&zwnj;ش) ده&zwnj;ڵێن: هه&zwnj;موو ئه&zwnj;وانه به ده&zwnj;ست خوایه&zwnj;، تۆش بڵێ: که&zwnj;واته چۆن جادووتان لێ ده&zwnj;کرێت؟ که ئه&zwnj;مه ده&zwnj;زانن.';
$t[90] = 'نه&zwnj;خێر وانیه&zwnj;، به&zwnj;ڵکو ئێمه حه&zwnj;قیقه&zwnj;ت و ڕاستیمان بۆ هێناون، به ڕاستی نه&zwnj;فامان هه&zwnj;میشه درۆزنن.';
$t[91] = 'هه&zwnj;رگیز خوا که&zwnj;سی نه&zwnj;کردووه به ڕۆڵه&zwnj;ی خۆی و هیچ خوایه&zwnj;کی له&zwnj;گه&zwnj;ڵدا نی یه&zwnj;، چونکه ئه&zwnj;و کاته هه&zwnj;ر خوایه&zwnj;ك ده&zwnj;چوو به&zwnj;لای دروستکراوانی خۆیه&zwnj;وه&zwnj;و هه&zwnj;ندێکی خۆی بڵند ده&zwnj;کرد به&zwnj;سه&zwnj;ر هه&zwnj;ندێکی تردا (ئه&zwnj;وسا بوونه&zwnj;وه&zwnj;ر تێکده&zwnj;چوو)، پاکی و بێگه&zwnj;ردی بۆ زاتی خوایه له به&zwnj;رامبه&zwnj;ر ئه&zwnj;و شتانه&zwnj;وه که (خوا نه&zwnj;ناسان) هه&zwnj;ڵی ده&zwnj;به&zwnj;ستن و ده&zwnj;یڵێن.';
$t[92] = ')ئه&zwnj;و زاته&zwnj;) زانای نهێنی و شاراوه&zwnj;کانه&zwnj;، هه&zwnj;روه&zwnj;ها زانای دیارو ئاشکراکانه&zwnj;، به&zwnj;رزی و بڵندی بۆ ئه&zwnj;وه له&zwnj;به&zwnj;رامبه&zwnj;ر ئه&zwnj;و شتانه&zwnj;وه که (نه&zwnj;فامان) ده&zwnj;یکه&zwnj;نه شه&zwnj;ریك و هاوه&zwnj;ڵی.';
$t[93] = 'بڵێ: په&zwnj;روه&zwnj;ردگارا: ئه&zwnj;گه&zwnj;ر ئه&zwnj;وه&zwnj;ت نیشاندام که له توێی هه&zwnj;ڕه&zwnj;شه&zwnj;کانتدا پێت ڕاگه&zwnj;یاندوون...';
$t[94] = 'ئه&zwnj;و کاته په&zwnj;روه&zwnj;ردگارا مه&zwnj;مخه&zwnj;ره ڕیزی قه&zwnj;ومی سته&zwnj;مکاره&zwnj;وه&zwnj;.';
$t[95] = 'بێگومان ئێمه ده&zwnj;سه&zwnj;ڵاتمان هه&zwnj;یه&zwnj;، ئه&zwnj;و هه&zwnj;ڕه&zwnj;شانه&zwnj;ی لێمان کردوون پێشی بهێنین و نیشانت بده&zwnj;ین.';
$t[96] = 'هه&zwnj;وڵ بده به&zwnj;چاکترین و جوانترین شێوه پاڵ به هه&zwnj;ڵه&zwnj;و ناڕێکی ناحه&zwnj;زانه&zwnj;وه بنێیت و خه&zwnj;مت نه&zwnj;بێت ئێمه چاك ئاگادارین له گوفتارو قسه&zwnj;ی ناڕه&zwnj;وایان.';
$t[97] = '(خۆت بسپێره به&zwnj;خواو) بڵێ: په&zwnj;روه&zwnj;ردگارا من په&zwnj;نا ده&zwnj;گرم به تۆ له&zwnj;هه&zwnj;موو خه&zwnj;ته&zwnj;ره&zwnj;و خه&zwnj;یاڵ و وه&zwnj;سوه&zwnj;سه&zwnj;ی شه&zwnj;یتانه&zwnj;کان.';
$t[98] = 'په&zwnj;نا ده&zwnj;گرم به تۆ ئه&zwnj;ی په&zwnj;روه&zwnj;ردگارم که شه&zwnj;یتانه&zwnj;کان به&zwnj;ده&zwnj;ورمدا کۆ ببنه&zwnj;وه&zwnj;و (سه&zwnj;رم لێ بشێوێنن).';
$t[99] = '(خوا نه&zwnj;ناسان به&zwnj;رده&zwnj;وام له&zwnj;سه&zwnj;ر ڕێبازی چه&zwnj;وتیانن)، هه&zwnj;تا ئه&zwnj;و کاته&zwnj;ی مردن یه&zwnj;خه&zwnj;یان ده&zwnj;گرێت، ئه&zwnj;وسا هه ڕیه&zwnj;که&zwnj;یان ده&zwnj;ڵێت: په&zwnj;روه&zwnj;ردگارا بمگێڕه&zwnj;ره&zwnj;وه بۆ دنیا...';
$t[100] = 'به&zwnj;ڵکو چاکه ئه&zwnj;نجام بده&zwnj;م و کاری باشه بکه&zwnj;م و ئه&zwnj;و هه&zwnj;ڵانه&zwnj;ی که له ده&zwnj;ستم چووه له&zwnj;مه&zwnj;ودوا له&zwnj;ده&zwnj;ستی نه&zwnj;ده&zwnj;م، (به&zwnj;ڵام تازه کار له کار ترازاوه&zwnj;، شتی وا مه&zwnj;حاڵه&zwnj;، بۆیه به توندی پێی ده&zwnj;وترێت): نه&zwnj;خێر، ئه&zwnj;وه قسه&zwnj;یه&zwnj;که ئه&zwnj;و بۆ خۆی ده&zwnj;یڵێت، به&zwnj;ڵکو ده&zwnj;بێت له جیهانی به&zwnj;رزه&zwnj;خدا، له نێوان مردن و زیندوو بوونه&zwnj;وه&zwnj;دا تا کاتێك زیندوو ده&zwnj;کرێنه&zwnj;وه چاوه&zwnj;ڕێ بێت.';
$t[101] = 'جا کاتێك فوو ده&zwnj;کرێت به _صور_دا ئه&zwnj;وه هیچ په&zwnj;یوه&zwnj;ندیه&zwnj;کی خزمایه&zwnj;تی له&zwnj;و ڕۆژه&zwnj;دا نی یه&zwnj;و که&zwnj;س له که&zwnj;س ناپرسێته&zwnj;وه&zwnj;.';
$t[102] = 'ئه&zwnj;و که&zwnj;سه&zwnj;ی ته&zwnj;رازووی چاکه&zwnj;کانی قورس بێت، ئا ئه&zwnj;وانه سه&zwnj;رفرازو سه&zwnj;رکه&zwnj;وتوون.';
$t[103] = 'به&zwnj;ڵام ئه&zwnj;و که&zwnj;سه&zwnj;ی ته&zwnj;رازووی چاکه&zwnj;کانی سووك بێت، ئه&zwnj;وانه ئه&zwnj;و که&zwnj;سانه&zwnj;ن خۆیان له ده&zwnj;ستداوه&zwnj;و خۆیان خه&zwnj;سارو ڕیسوا کردووه&zwnj;، ده&zwnj;بێت هه&zwnj;ر له ناو دۆزه&zwnj;خدا ژیانی نه&zwnj;بڕاوه ببه&zwnj;نه سه&zwnj;ر.';
$t[104] = 'بڵێسه&zwnj;ی ئاگر چڕو چاویان ده&zwnj;برژێنێت، هه&zwnj;موویان لێویان هه&zwnj;ڵقرچاوه&zwnj;و دانیان ڕیچ ده&zwnj;بێته&zwnj;وه (دیمه&zwnj;نیان سامناکه&zwnj;).';
$t[105] = '(ئه&zwnj;وسا خوا پرسیاریان لێ ده&zwnj;کات) ئایا کاتی خۆی ئایه&zwnj;ته&zwnj;کانی من به&zwnj;سه&zwnj;رتاندا نه ده&zwnj;خوێنرایه&zwnj;وه&zwnj;؟ که&zwnj;چی ئێوه بڕواتان پێی نه&zwnj;بوو، به درۆتان ده&zwnj;زانی؟';
$t[106] = '(سه&zwnj;ره&zwnj;نجام به که&zwnj;ساسیه&zwnj;وه&zwnj;) ده&zwnj;ڵێن: په&zwnj;روه&zwnj;ردگارا نه&zwnj;گبه&zwnj;تی و نه&zwnj;هامه&zwnj;تی سه&zwnj;رشانی گرتین و زاڵ بوو به&zwnj;سه&zwnj;رماندا ئێمه که&zwnj;سانێکی گومڕا بووین.';
$t[107] = 'په&zwnj;روه&zwnj;ردگارا له ناو ئاگری دۆزه&zwnj;خ و سزاکه&zwnj;یدا ده&zwnj;رمان بهێنه ئه&zwnj;گه&zwnj;ر جارێکی تر گه&zwnj;ڕاینه&zwnj;وه (بۆ گومڕایی) ئه&zwnj;وه ئیتر دیاره که ئێمه که&zwnj;سانێکی سته&zwnj;مکارین.';
$t[108] = '(خوای گه&zwnj;وره له وه&zwnj;ڵامیاندا به تووڕه&zwnj;ییه&zwnj;وه) ده&zwnj;فه&zwnj;رموێت: بێ ده&zwnj;نگ بن و ده&zwnj;متان داخه&zwnj;ن، قسه&zwnj;شم له گه&zwnj;ڵدا مه&zwnj;که&zwnj;ن.';
$t[109] = 'چونکه به ڕاستی کاتی خۆی ده&zwnj;سته&zwnj;یه&zwnj;ك له به&zwnj;نده ئیمانداره&zwnj;کانم ده&zwnj;یانوت؛ په&zwnj;روه&zwnj;ردگارا ئێمه ئیمان و باوه&zwnj;ڕی دامه&zwnj;زراومان به زاتی تۆ هێناوه&zwnj;، ده&zwnj;، لێمان خۆش ببه&zwnj;و ڕه&zwnj;حممان پێ بکه چونکه تۆ چاکترین زاتێکی که له هه&zwnj;موو خاوه&zwnj;ن ڕه&zwnj;حم و سۆزه&zwnj;کان میهره&zwnj;بانتریت.';
$t[110] = 'که&zwnj;چی ئێوه گاڵته&zwnj;تان پێ ده&zwnj;کردن، هه&zwnj;تا گه&zwnj;یشته ڕاده&zwnj;یه&zwnj;ك یادی منیان له بیر بردنه&zwnj;وه&zwnj;و ئێوه به&zwnj;رده&zwnj;وام به&zwnj;وان پێده&zwnj;که&zwnj;نین.';
$t[111] = '(جا با بزانن که&zwnj;) بێگومان ئه&zwnj;مڕۆ من پاداشتم داونه&zwnj;ته&zwnj;وه به&zwnj;هۆی ئه&zwnj;وه&zwnj;وه که خۆگر بوون، به ڕاستی هه&zwnj;ر ئه&zwnj;وانیش سه&zwnj;رکه&zwnj;وتوو سه&zwnj;رفرازن.';
$t[112] = '(ئه&zwnj;وسا خوای گه&zwnj;وره&zwnj;) لێیان ده&zwnj;پرسێت: ئه&zwnj;وه چه&zwnj;ند ساڵ له زه&zwnj;ویدا ژیانتان برده سه&zwnj;ر.';
$t[113] = '(دۆزه&zwnj;خییان) ده&zwnj;ڵێن: ڕۆژێك یان که&zwnj;متر له ڕۆژێك (دیاره که ژیانی دنیا له چاوی ژیانی نه&zwnj;بڕاوه و هه&zwnj;میشه&zwnj;یی دا زۆر کورت و کۆتاو که&zwnj;م دێته به&zwnj;ر چاو، یاخود جیهانی به&zwnj;رزه&zwnj;خ وه&zwnj;کو ڕۆژێك یان که&zwnj;متر هه&zwnj;ستی پێ ده&zwnj;کرێت)، له&zwnj;و که&zwnj;سانه بپرسن که بیرو هۆشیان لای خۆیانه&zwnj;و حسابی ژماره&zwnj;و کات ده&zwnj;که&zwnj;ن.';
$t[114] = '(خوای گه&zwnj;وره پێیان) ده&zwnj;فه&zwnj;رموێت: له ڕاستیدا مانه&zwnj;وه&zwnj;تان له دنیادا هه&zwnj;ر زۆر که&zwnj;م بووه&zwnj;، ئه&zwnj;گه&zwnj;ر به&zwnj;ڕاستی ده&zwnj;تانزانی؟';
$t[115] = 'ئایا گومانتان واده&zwnj;برد که به ڕاستی ئێمه ئێوه&zwnj;مان به بێ مه&zwnj;به&zwnj;ست دروست کردووه&zwnj;و ئێوه بۆ لای ئێمه ناهێنرێنه&zwnj;وه&zwnj;؟!';
$t[116] = 'به&zwnj;رزی و بڵندی بۆ خوا، پاشای ڕه&zwnj;وای حه&zwnj;ق، هیچ خوایه&zwnj;ك نیه جگه له&zwnj;و زاته&zwnj;، خاوه&zwnj;نی عه&zwnj;رشی شکۆدارو به&zwnj;ڕێزه&zwnj;.';
$t[117] = 'ئه&zwnj;و که&zwnj;سه&zwnj;ی خوایه&zwnj;کی تر بڕیار بدات و بیکاته شه&zwnj;ریکی خوا، له کاتێکدا که هیچ به&zwnj;ڵگه&zwnj;یه&zwnj;کی به ده&zwnj;سته&zwnj;وه نیه&zwnj;، ئه&zwnj;وه به ڕاستی حساب و لێپرسینه&zwnj;وه&zwnj;ی لای په&zwnj;روه&zwnj;ردگارێتی، بێگومان بێ باوه&zwnj;ڕو خوا نه&zwnj;ناسان سه&zwnj;رفراز نابن.';
$t[118] = '(ئه&zwnj;ی محمد صلی الله علیه وسلم، ئه&zwnj;ی ئیماندار! تۆ) بڵێ: په&zwnj;روه&zwnj;ردگارا: له گوناهو هه&zwnj;ڵه&zwnj;کانم خۆش ببه&zwnj;و ڕه&zwnj;حمم پێ بکه چونکه تۆ له هه&zwnj;موو میهره&zwnj;بانان میهره&zwnj;بانتریت.';
?>