<?php
$t = array();
$t[1] = 'ئه&zwnj;مه سووره&zwnj;تێکی گرنگه ئێمه دامانبه&zwnj;زاندووه هه&zwnj;ندێك شتمان تیادا فه&zwnj;رز کردووه&zwnj;، چه&zwnj;نده&zwnj;ها ئایه&zwnj;ت و فه&zwnj;رمانی ئاشکرامان له&zwnj;توێی دا دابه&zwnj;زاندووه&zwnj;، بۆ ئه&zwnj;وه&zwnj;ی یاداوه&zwnj;ریی وه&zwnj;ربگرن و تێفکرن.';
$t[2] = 'سه&zwnj;د قامچی له&zwnj;هه&zwnj;ریه&zwnj;ك له ئافره&zwnj;ت و پیاوی زیناکه&zwnj;ر بوه&zwnj;شێنن، به&zwnj;زه&zwnj;یی نه&zwnj;تانگرێت له به&zwnj;جێهێنانی فه&zwnj;رمانی خوادا بۆیان، ئه&zwnj;گه&zwnj;ر ئێوه باوه&zwnj;ڕتان به ڕۆژی دوایی هێناوه&zwnj;، با ده&zwnj;سته&zwnj;یه&zwnj;کیش له ئیمانداران ئاماده&zwnj;ی سزاکه&zwnj;یان بن.';
$t[3] = 'پیاوی زیناکه&zwnj;ر ته&zwnj;نها ئافره&zwnj;تی زیناکه&zwnj;ر یاخود موشریك ماره ده&zwnj;کات، ئافره&zwnj;تی زیناکه&zwnj;ریش ته&zwnj;نها پیاوی زیناکه&zwnj;ر یاخود موشریك ماره&zwnj;ی ده&zwnj;کات، (هاوسه&zwnj;رێتی پیاوان و ئافره&zwnj;تانێك که به&zwnj;رده&zwnj;وامن له&zwnj;سه&zwnj;ر زینا)، ئه&zwnj;وه له&zwnj;سه&zwnj;ر ئیمانداران حه&zwnj;رامکراوه&zwnj;.';
$t[4] = 'ئه&zwnj;وانه&zwnj;ی که بوختان ده&zwnj;که&zwnj;ن بۆ ئافره&zwnj;تانی داوێن پاك و خاوه&zwnj;ن مێرد، پاشان ناتوانن چوار که&zwnj;سیش به شایه&zwnj;ت بهێنن، ئه&zwnj;وانه هه&zwnj;شتا قامچیان لێ بده&zwnj;ن و هه&zwnj;رگیز شایه&zwnj;تیان لێ وه&zwnj;رمه&zwnj;گرن، ئه&zwnj;وانه له ڕیزی تاوانبارو له سنوور ده&zwnj;رچوو و یاخیه&zwnj;کاندان.';
$t[5] = 'جگه له&zwnj;وانه&zwnj;ی که ته&zwnj;وبه&zwnj;ی ڕاسته&zwnj;قینه&zwnj;یان کردووه دوای ته&zwnj;مێ کردنه&zwnj;که&zwnj;یان، چاکسازی خۆیشیان کردووه&zwnj;، ئه&zwnj;وه بێگومان خوا (به نیسبه&zwnj;ت ئه&zwnj;و جۆره که&zwnj;سانه&zwnj;وه&zwnj;) لێخۆشبوو و میهره&zwnj;بانه&zwnj;.';
$t[6] = 'ئه&zwnj;وانه&zwnj;ی که تۆمه&zwnj;تی زینا ده&zwnj;ده&zwnj;نه پاڵ هاوسه&zwnj;ره&zwnj;کانیان، هیچ شایه&zwnj;تێکیشیان نیه جگه له خۆیان، شایه&zwnj;تی ئه&zwnj;و جۆره که&zwnj;سانه به&zwnj;وه ده&zwnj;بێت که چوار جار سوێند به خوا بخوات که بێگومان له ڕاستگۆیانه (به چاوی خۆی تاوانه&zwnj;که&zwnj;ی بینیووه&zwnj;).';
$t[7] = 'پێنجه&zwnj;م جاریش بڵێت به ڕاستی نه&zwnj;فرینی خوای له&zwnj;سه&zwnj;ره ئه&zwnj;گه&zwnj;ر (بوختانچی بێت) له درۆزنان بێت.';
$t[8] = '(له لایه&zwnj;کی تریشه&zwnj;وه&zwnj;) سزا له&zwnj;سه&zwnj;ر ژنه&zwnj;که لا ده&zwnj;برێت کاتێ که چوار جار سوێند به خوا بخوات که مێرده&zwnj;که&zwnj;ی درۆ ده&zwnj;کات و (بوختانی بۆ هه&zwnj;ڵده&zwnj;به&zwnj;ستێت).';
$t[9] = 'پێنجه&zwnj;م جار ژنه&zwnj;که ده&zwnj;ڵێت که خه&zwnj;شم و قینی خوای له&zwnj;سه&zwnj;ره ئه&zwnj;گه&zwnj;ر مێرده&zwnj;که&zwnj;ی ڕاست بکات.';
$t[10] = 'خۆ ئه&zwnj;گه&zwnj;ر فه&zwnj;زڵ و به&zwnj;هره&zwnj;ی خواو ڕه&zwnj;حمه&zwnj;ت و میهره&zwnj;بانی ئه&zwnj;و نه&zwnj;بێت له&zwnj;سه&zwnj;رتان (تووشی کێشه&zwnj;ی زۆر ده&zwnj;بن) به ڕاستی خوا ته&zwnj;وبه وه&zwnj;رگرو دانایه (ئه&zwnj;گه&zwnj;ر ته&zwnj;وبه&zwnj;ی ڕاسته&zwnj;قینه بکه&zwnj;ن).';
$t[11] = '(لێره&zwnj;وه قورئان ده&zwnj;رباره&zwnj;ی ئه&zwnj;و بوختانه ده&zwnj;دوێت که بۆ حه&zwnj;زره&zwnj;تی عائیشه له لایه&zwnj;ن دوو ڕووه&zwnj;کانه&zwnj;وه هه&zwnj;ڵبه&zwnj;سترا، هه&zwnj;ندێك له ئیماندارانیش بێ لێکدانه&zwnj;وه&zwnj;و تێفکران ده&zwnj;یانوته&zwnj;وه&zwnj;). بێگومان ئه&zwnj;وانه&zwnj;ی بوختانه&zwnj;که&zwnj;یان هه&zwnj;ڵبه&zwnj;ست ده&zwnj;سته&zwnj;یه&zwnj;ك بوون له خۆتان (یاخود وا خۆیان نیشان ده&zwnj;دا که له خۆتانن) وامه&zwnj;زانن که ئه&zwnj;و بوختانه شه&zwnj;ڕو خراپ بوو بۆتان، به&zwnj;ڵکو خێر بوو بۆتان (تا ئیمانداران له دووڕووه&zwnj;کان جیا بێته&zwnj;وه&zwnj;، ئیماندارانی دڵپاك وریا ببنه&zwnj;وه&zwnj;)، بێگومان تاوانی هه&zwnj;رکه&zwnj;س له&zwnj;و بوختانچیانه له گه&zwnj;ردنی خۆیدایه&zwnj;تی، ئه&zwnj;و که&zwnj;سه&zwnj;ش سه&zwnj;رۆك و گه&zwnj;وره&zwnj;و هه&zwnj;ڵبه&zwnj;ستی بوختانه&zwnj;که&zwnj;یه&zwnj;، سزایه&zwnj;کی سه&zwnj;خت و گه&zwnj;وره بۆی ئاماده&zwnj;یه&zwnj;.';
$t[12] = 'ده&zwnj;بوا هه&zwnj;ر که ئه&zwnj;و بوختانه&zwnj;تان بیست ئیمانداران له پیاوان و له ئافره&zwnj;تان گومانی چاکیان به خۆیان ببوایه&zwnj;و دڵنیا بوونایه له پاکی خۆیان و بیانوتایه ئه&zwnj;مه بوختانێکی ئاشکرایه&zwnj;و (کاری ناڕه&zwnj;وای وا له دایکی موسوڵمانان و له هاوه&zwnj;ڵانی پێغه&zwnj;مبه&zwnj;ر صلی الله علیه وسلم ناوه&zwnj;شێته&zwnj;وه).';
$t[13] = 'ده&zwnj;بوا بوختانچیه&zwnj;کان چوار شایه&zwnj;تیان بۆ سه&zwnj;لماندنی بوختانه&zwnj;که&zwnj;یان بهێنایه&zwnj;، کاتێك چوار شایه&zwnj;تیان نه هێنا، ئه&zwnj;وه ئیتر دیاره ئه&zwnj;وانه لای خوایش له ڕیزی درۆزنه&zwnj;کاندان.';
$t[14] = 'خۆ ئه&zwnj;گه&zwnj;ر فه&zwnj;زڵ و به&zwnj;زه&zwnj;یی خواو به&zwnj;خشنده&zwnj;ییه&zwnj;که&zwnj;ی نه&zwnj;بوایه له سه&zwnj;رتان له دنیاو قیامه&zwnj;تتدا، به&zwnj;هۆی ئه&zwnj;و تاوانه&zwnj;تانه&zwnj;وه سزایه&zwnj;کی زۆر گه&zwnj;وره&zwnj;و سه&zwnj;ختتان تووش ده&zwnj;بوو، به&zwnj;هۆی ڕۆچوونتان له&zwnj;و ده&zwnj;نگ و باسه&zwnj;دا.';
$t[15] = 'ئه&zwnj;وه بۆ هه&zwnj;ر ده&zwnj;ماو ده&zwnj;م قسه له زمانی یه&zwnj;کتر وه&zwnj;رده&zwnj;گرن (بێ لێکدانه&zwnj;وه&zwnj;)، کاتێك به زارتان شتێك ده&zwnj;ڵێن که هیچ ئاگاییتان ده&zwnj;رباره&zwnj;ی نیه&zwnj;، ئێوه واده&zwnj;زانن ئه&zwnj;و بوختانه شتێکی ساده&zwnj;و ئاسانه&zwnj;؟ له کاتێکدا ئه&zwnj;وه لای خوا زۆر گه&zwnj;وره&zwnj;و ناقۆڵایه&zwnj;.';
$t[16] = 'ده&zwnj;بوا کاتێك گوێتان له&zwnj;و بوختانه بوو، بتانوتایه&zwnj;: ئێمه بۆمان نی یه قسه&zwnj;ی وا بکه&zwnj;ین، پاکی و بێگه&zwnj;ردی بۆ خوا، ئاخر ئه&zwnj;مه بوختانێکی زۆر گه&zwnj;وره&zwnj;و ناقۆڵایه (که&zwnj;ی ئه&zwnj;وه له دایکی ئیمانداران ده&zwnj;وه&zwnj;شێته&zwnj;وه&zwnj;).';
$t[17] = 'خوا ئامۆژگاریتان ده&zwnj;کات که نه&zwnj;که&zwnj;ن هه&zwnj;رگیز شتی ئاوا دووباره بکه&zwnj;نه&zwnj;وه ئه&zwnj;گه&zwnj;ر ئێوه ئیماندارن.';
$t[18] = 'خوا ئایه&zwnj;ت و فه&zwnj;رمان و ڕێنمووییه&zwnj;کانی ڕوون ده&zwnj;کاته&zwnj;وه بۆتان (تایه&zwnj;کتر له&zwnj;که&zwnj;دار نه&zwnj;که&zwnj;ن)، بێگومان خوا زاناو دانایه&zwnj;.';
$t[19] = 'به ڕاستی ئه&zwnj;وانه&zwnj;ی حه&zwnj;ز ده&zwnj;که&zwnj;ن زیناو تاوان له ناو ئه&zwnj;وانه&zwnj;دا باوه&zwnj;ڕیان هێناوه بڵاوبێته&zwnj;وه&zwnj;، سزای پڕ ئێش و ئازار له&zwnj;م جیهان و له&zwnj;و جیهان بۆیان ئاماده&zwnj;یه&zwnj;و خوا خۆی چاك ده&zwnj;زانێت (سه&zwnj;ره&zwnj;نجامی زیناو شه&zwnj;ڕواڵپیسی به&zwnj;ره&zwnj;و کوێ ده&zwnj;چێت) ئێوه نایزانن.';
$t[20] = 'ئه&zwnj;گه&zwnj;ر فه&zwnj;زڵ و میهره&zwnj;بانی خوایی نه&zwnj;بوایه له&zwnj;سه&zwnj;رتان (به&zwnj;و تۆمه&zwnj;ت و بوختانه&zwnj;ی که هه&zwnj;ڵبه&zwnj;سترا بۆ دایکی ئیمانداران)، تووشی به&zwnj;ڵایه&zwnj;کی سه&zwnj;خت ده&zwnj;بوون، به&zwnj;ڵام دڵنیابن که به ڕاستی خوا به سۆزو میهره&zwnj;بانه (زوو تۆڵه ناسێنێت).';
$t[21] = 'ئه&zwnj;ی ئه&zwnj;وانه&zwnj;ی ئیمان و باوه&zwnj;ڕتان هێناوه شوێنی هه&zwnj;نگاوو نه&zwnj;خشه و پیلانه&zwnj;کانی شه&zwnj;یتان مه&zwnj;که&zwnj;ون، هه&zwnj;رکه&zwnj;س شوێنی هه&zwnj;نگاوو نه&zwnj;خشه&zwnj;و پیلانی شه&zwnj;یتان بکه&zwnj;وێت زه&zwnj;ره&zwnj;ر ده&zwnj;کات، چونکه به&zwnj;ڕاستی شه&zwnj;یتان هه&zwnj;میشه فه&zwnj;رمان به کارو کرده&zwnj;وه&zwnj;ی خراپ و گوناهو تاوان و ناشیرینی ده&zwnj;کات، ئه&zwnj;گه&zwnj;ر فه&zwnj;زڵ و میهره&zwnj;بانی خوا نه&zwnj;بوایه له&zwnj;سه&zwnj;رتان ئه&zwnj;وه هه&zwnj;رگیز که&zwnj;ستان پاك و پوخته&zwnj;و ڕزگار نه&zwnj;ده&zwnj;بوون (له&zwnj;ده&zwnj;ست بوختانچیه&zwnj;کان، یاخود به ته&zwnj;وبه پاك نه&zwnj;ده&zwnj;بوونه&zwnj;وه&zwnj;) به&zwnj;ڵام خوا هه&zwnj;ر که&zwnj;سێکی بوێت و شایسته بێت، پاکو پوخته&zwnj;ی ده&zwnj;کات و له گوناهه&zwnj;کانی ده&zwnj;بورێت، خوایش هه&zwnj;میشه بیسه&zwnj;رو زانایه (به ته&zwnj;وبه&zwnj;و په&zwnj;شیمانیتان و به نیه&zwnj;تی دڵ و ده&zwnj;روونتان).';
$t[22] = 'ئه&zwnj;وانه&zwnj;ی که خاوه&zwnj;نی سامان و به&zwnj;خشنده&zwnj;یین له ئێوه با سوێند نه&zwnj;خۆن که یارمه&zwnj;تی خزمان و هه&zwnj;ژاران و کۆچبه&zwnj;ران له پێناوی خوا نه&zwnj;ده&zwnj;ن (له&zwnj;به&zwnj;ر ئه&zwnj;وه&zwnj;ی به&zwnj;شداربوون له وتنی بوختانه&zwnj;که&zwnj;دا)، ده&zwnj;با لێبوورده&zwnj;بن، ده&zwnj;با چاوپۆشی بکه&zwnj;ن، مه&zwnj;گه&zwnj;ر حه&zwnj;ز ناکه&zwnj;ن خوا لێتان خۆش ببێت، خوایش هه&zwnj;میشه لێخۆشبوو میهره&zwnj;بانه&zwnj;.';
$t[23] = 'به ڕاستی ئه&zwnj;وانه&zwnj;ی ئافره&zwnj;تانی ئیمانداری پاك داوێن و خاوه&zwnj;ن مێردو بێ ئاگا له گوناه، تۆمه&zwnj;تبار ده&zwnj;که&zwnj;ن، ئه&zwnj;وانه نه&zwnj;فره&zwnj;تیان لێکراوه له دنیاو له قیامه&zwnj;تداو سزایه&zwnj;کی گه&zwnj;وره&zwnj;و سه&zwnj;خت ئاماده&zwnj;یه بۆیان (له تۆڵه&zwnj;ی له&zwnj;که&zwnj;دار کردنی پاکاندا)...';
$t[24] = 'له ڕۆژێکدا که زمان و ده&zwnj;ست و قاچیان لێیان ده&zwnj;بێته شایه&zwnj;تێکی گۆیا، له به&zwnj;رامبه&zwnj;ر ئه&zwnj;و کرده&zwnj;وانه که ئه&zwnj;نجامیان ده&zwnj;داو (ئه&zwnj;و بوختانانه&zwnj;ی که دایانکرد).';
$t[25] = 'ئه&zwnj;و ڕۆژه خوا به گوێره&zwnj;ی کارو کرده&zwnj;وه&zwnj;و ئیمان و باوه&zwnj;ڕی حه&zwnj;قیقیان پاداشتیان ده&zwnj;داته&zwnj;وه&zwnj;، ئه&zwnj;وسا چاك بۆیان ده&zwnj;رده&zwnj;که&zwnj;وێت که به ڕاستی خوا خۆی زاتێکی ڕاست و دروستی ئاشکرایه (هیچی لێ ون نابێت).';
$t[26] = 'ژنانی ناپاك بۆ پیاوانی ناپاك و پیاوانی ناپوخت بۆ ژنانی ناپوخت، ئافره&zwnj;تانی پاکیش بۆ پیاوانی پاك، پیاوانی پاکیش بۆ ئافره&zwnj;تانی پاك، ئه&zwnj;و پاکانه به&zwnj;رین له&zwnj;و تۆمه&zwnj;تانه&zwnj;ی که ناپاکان هه&zwnj;ڵی ده&zwnj;به&zwnj;ستن (چونکه محمدی پاك هاوسه&zwnj;ری عائیشه&zwnj;ی پاکه صلی الله علیه وسلم)، بۆ ئه&zwnj;و چاکانه لێخۆشبوون و ڕزق و ڕۆزی به&zwnj;نرخ ئاماده&zwnj;یه&zwnj;.';
$t[27] = 'ئه&zwnj;ی ئه&zwnj;وانه&zwnj;ی باوه&zwnj;ڕتان هێناوه مه&zwnj;چنه هیچ ماڵێکی تر جگه له ماڵه&zwnj;کانی خۆتان هه&zwnj;تا کارێك نه&zwnj;که&zwnj;ن که ئه&zwnj;وان ئاگاداربن (وه&zwnj;کو زه&zwnj;نگ لێدان یان له ده&zwnj;رگادان) پاشان که مۆڵه&zwnj;تدران سه&zwnj;لام بکه&zwnj;ن له خه&zwnj;ڵکی ماڵه&zwnj;که&zwnj;، ئه&zwnj;وه چاکتره بۆتان و به&zwnj;و شێوه&zwnj;یه ڕه&zwnj;فتار بکه&zwnj;ن، هه&zwnj;وڵ بده&zwnj;ن له یادتان بێت و ئه&zwnj;و شێوازه فه&zwnj;رامۆش نه&zwnj;که&zwnj;ن (کاتی له بارو گونجاو هه&zwnj;ڵبژێرن بۆ سه&zwnj;ردانه&zwnj;کانتان، بۆ میوان و خاوه&zwnj;ن ماڵ دڵیان به&zwnj;یه&zwnj;ك بکرێته&zwnj;وه&zwnj;).';
$t[28] = 'جا ئه&zwnj;گه&zwnj;ر که&zwnj;ستان نه&zwnj;بینی تیایدا، یاخود هه&zwnj;ستتان کرد که&zwnj;س له&zwnj;ماڵ نیه&zwnj;، مه&zwnj;چنه ژووره&zwnj;وه هه&zwnj;تا مۆڵه&zwnj;ت نه&zwnj;درێن، ئه&zwnj;گه&zwnj;ر پێتان وترا بگه&zwnj;ڕێنه&zwnj;وه (کاتمان نیه&zwnj;، یان کارمان هه&zwnj;یه&zwnj;...) بگه&zwnj;ڕێنه&zwnj;وه (به&zwnj;بێ دڵگرانی) ئه&zwnj;وه چاکتره بۆتان، چاکیش بزانن خوا زانایه به هه&zwnj;موو ئه&zwnj;و کارو کرده&zwnj;وانه&zwnj;ی که ئه&zwnj;نجامی ده&zwnj;ده&zwnj;ن.';
$t[29] = 'گوناهبار نابن ئه&zwnj;گه&zwnj;ر بچنه ماڵێکه&zwnj;وه که&zwnj;سی تێدا نیشته&zwnj;جێ نه&zwnj;بێت و که&zwnj;لو په&zwnj;لی خۆتانی تێدا بێت، خواش ده&zwnj;زانێت و ئاگاداره به&zwnj;وه&zwnj;ی ده&zwnj;ری ده&zwnj;خه&zwnj;ن و به&zwnj;وه&zwnj;ش که ده&zwnj;یشارنه&zwnj;وه&zwnj;.';
$t[30] = '(ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر صلی الله علیه وسلم) به پیاوانی ئیماندار بڵێ: چاویان بپارێزن (له&zwnj;سه&zwnj;رنجدانی ئافره&zwnj;تانی نامه&zwnj;حره&zwnj;م) هه&zwnj;روه&zwnj;ها نامووس و عه&zwnj;وره&zwnj;تی خۆیان له حه&zwnj;رام بپارێزن، بێگومان ئه&zwnj;وه پاکترو پوختتره بۆیان (بۆ دڵ و ده&zwnj;روون و ڕواڵه&zwnj;ت و ڕۆح و لاشه&zwnj;یان) به&zwnj;ڕاستی خوا ئاگاداره به&zwnj;و هه&zwnj;ڵس و که&zwnj;وت و ڕه&zwnj;فتارانه&zwnj;ی که ده&zwnj;یکه&zwnj;ن.';
$t[31] = 'به ئافره&zwnj;تانی ئیمانداریش بڵێ: چاویان بپارێزن (له ته&zwnj;ماشای نامه&zwnj;حره&zwnj;م) داوێنی خۆیان بپارێزن له گوناهو جوانی خۆیان ده&zwnj;رنه&zwnj;خه&zwnj;ن جگه له&zwnj;وه&zwnj;ی که دیاره (وه&zwnj;ك ده&zwnj;م و چاوو ده&zwnj;ست) با سه&zwnj;رپۆشه&zwnj;کانیان بده&zwnj;ن به&zwnj;سه&zwnj;ر سینه&zwnj;و ملیانداو جوانی و زینه&zwnj;تی خۆیان ده&zwnj;رنه&zwnj;خه&zwnj;ن به ته&zwnj;واوی بۆ هاوسه&zwnj;ره&zwnj;کانیان نه&zwnj;بێت، یان باوکی خۆیان، یان باوکی مێرده&zwnj;کانیان، یان کوڕی خۆیان، یان کوڕی مێرده&zwnj;کانیان، یان بۆ براکانی خۆیان، یان برازاکانیان، یان خوشکه&zwnj;زاکانیان، یان ئافره&zwnj;تێك که په&zwnj;یوه&zwnj;ندیان هه&zwnj;یه پێکه&zwnj;وه&zwnj;، یان ئه&zwnj;وانه&zwnj;ش که که&zwnj;نیزه&zwnj;کانیان (که له بارودۆخی تایبه&zwnj;تدا هه&zwnj;بوون) یان ئه&zwnj;وانه&zwnj;ی که له ماڵه&zwnj;کانتاندا ده&zwnj;ژین و پێویستیان به ئافره&zwnj;ت نی یه (له به&zwnj;ر پیری، یان نه&zwnj;خۆشی... هتد) یاخود ئه&zwnj;و منداڵانه&zwnj;ی که هێشتا شاره&zwnj;زاییان له ئافره&zwnj;تان نیه&zwnj;و (ئاره&zwnj;زووی جنسیان بۆ دروست نه&zwnj;بووه&zwnj;و نازانن باسی عه&zwnj;ورات بکه&zwnj;ن) نابێت ئافره&zwnj;تانی ئیماندار قاچیان ده&zwnj;ربخه&zwnj;ن و پێیان بده&zwnj;ن به زه&zwnj;ویدا (تا ده&zwnj;نگی خشڵ و پاوانه&zwnj;کانیان بێت) و بزانرێت که زینه&zwnj;تدارو خشڵدارن، که&zwnj;واته هه&zwnj;مووتان ئه&zwnj;ی ئیمانداران به&zwnj;ره&zwnj;و لای خواو به&zwnj;ده&zwnj;ستهێنانی ڕه&zwnj;زامه&zwnj;ندی ئه&zwnj;و بگه&zwnj;ڕێنه&zwnj;وه بۆ ئه&zwnj;وه&zwnj;ی ڕزگارو سه&zwnj;رفرازبن.';
$t[32] = 'ژن بده&zwnj;ن به پیاوانی بێ ژن و ئافره&zwnj;تانی بێوه&zwnj;ژن بده&zwnj;ن به&zwnj;شوو، هه&zwnj;روه&zwnj;ها ژن بده&zwnj;ن به به&zwnj;نده صاڵحه&zwnj;کانتان و که&zwnj;نیزه&zwnj;که کانیشتان بده&zwnj;ن به شوو ئه&zwnj;گه&zwnj;ر هه&zwnj;ژاریش بن، ئه&zwnj;وه خوا له فه&zwnj;زڵ و به&zwnj;خششی خۆی به&zwnj;هره&zwnj;وه&zwnj;ریان ده&zwnj;کات، خوای گه&zwnj;وره&zwnj;ش هه&zwnj;میشه فراوانگیرو زانایه&zwnj;.';
$t[33] = 'ده&zwnj;با ئه&zwnj;وانه&zwnj;ی هاوسه&zwnj;ریان ده&zwnj;ست ناکه&zwnj;وێت هه&zwnj;وڵ بده&zwnj;ن داوێنی خۆیان پاك ڕابگرن، هه&zwnj;تا خوا له فه&zwnj;زڵی خۆی ده&zwnj;وڵه&zwnj;مه&zwnj;ندو بێ نیازیان ده&zwnj;کات، ئه&zwnj;و که&zwnj;سانه&zwnj;ش که ده&zwnj;یانه&zwnj;وێت خۆیان ڕزگار بکه&zwnj;ن له به&zwnj;نده&zwnj;کانتان و خۆیان بکڕنه&zwnj;وه&zwnj;، ئه&zwnj;وه ئێوه له&zwnj;گه&zwnj;ڵیاندا ڕێك بکه&zwnj;ون و ڕێگه&zwnj;ی ئازادییان بۆ ئاسان بکه&zwnj;ن، ئه&zwnj;گه&zwnj;ر خێرتان تێدا به&zwnj;دی ده&zwnj;کردن و له&zwnj;و ماڵ و سامانه&zwnj;ی که خوا پێی به&zwnj;خشیوون به&zwnj;شیان بده&zwnj;ن و به&zwnj;زۆر که&zwnj;نیزه&zwnj;که&zwnj;کانتان هانمه&zwnj;ده&zwnj;ن بۆ داوێن پیسی، ئه&zwnj;گه&zwnj;ر ئه&zwnj;وان پاك داوێنیان ده&zwnj;ویست (ده&zwnj;بێت پیاوی ئازاد زیاتر پاکداوێنی مه&zwnj;به&zwnj;ست بێت)، بۆ ئه&zwnj;وه&zwnj;ی له&zwnj;و ڕێگه چه&zwnj;وته&zwnj;وه ماڵ و سامانی ژیانی دنیا به&zwnj;ده&zwnj;ست بهێنن، جا ئه&zwnj;وه&zwnj;ی به&zwnj;زۆر ئه&zwnj;و گوناهه&zwnj;یان پێ بکات، ئه&zwnj;وه به&zwnj;ڕاستی خوا دوای ئه&zwnj;و زۆر بۆ هێنانه&zwnj;یان لێخۆش بوو میهره&zwnj;بانه&zwnj;و چاوپۆشیان لێ ده&zwnj;کات.';
$t[34] = 'سوێند به خوا بێگومان ئێمه چه&zwnj;نده&zwnj;ها ئایه&zwnj;ت و فه&zwnj;رمانی ئاشکراو ڕوونمان بۆ دابه&zwnj;زاندوون و هه&zwnj;روه&zwnj;ها چه&zwnj;نده&zwnj;ها نموونه و سه&zwnj;رگوزه&zwnj;شته&zwnj;ی ئه&zwnj;وانه&zwnj;ی پێش خۆتان که تێپه&zwnj;ڕیان کردووه&zwnj;، هاوڕێ له&zwnj;گه&zwnj;ڵ په&zwnj;ندو ئامۆژگاری بۆ خواناس و پارێزکاران.';
$t[35] = 'خوا نوورو ڕووناکی ئاسمانه&zwnj;کان و زه&zwnj;ویه (نوورو ڕووناکی به&zwnj;خشه پێیان)، نموونه&zwnj;ی نوورو ڕووناکیه&zwnj;که&zwnj;ی وه&zwnj;ك تاقێك وایه چرایه&zwnj;کی تێدا بێت، چراکه&zwnj;ش له&zwnj;ناو شووشه&zwnj;یه&zwnj;کدا بێت شووشه&zwnj;که&zwnj;ش وه&zwnj;ك هه&zwnj;ساره&zwnj;یه&zwnj;کی گه&zwnj;ش و پرشنگدار وابێت و داگیرسابێت به ڕۆنی دره&zwnj;ختی موباره&zwnj;ك و به&zwnj;فه&zwnj;ڕی زه&zwnj;یتوون، ڕووی نه له ڕۆژ هه&zwnj;ڵات و نه له ڕۆژ ئاوا بێت (له شوێنێکدا بێت به&zwnj;یانی و ئێواره خۆر لێ بدات)، ڕۆنه&zwnj;که&zwnj;شی (ئه&zwnj;وه&zwnj;نده پاك و بێگه&zwnj;رده&zwnj;) خه&zwnj;ریکه خۆی دابگیرسێت و ڕووناکی بداته&zwnj;وه&zwnj;، ئه&zwnj;گه&zwnj;ر ئاگریش لێی نه&zwnj;دات، ئه&zwnj;و هیدایه&zwnj;ت و ڕێنموویی یه که قورئانه ڕووناکی یه له&zwnj;سه&zwnj;ر ڕووناکی، خوایش هیدایه&zwnj;تی ئه&zwnj;و که&zwnj;سانه ده&zwnj;دات که ده&zwnj;یه&zwnj;وێت و شایسته&zwnj;ن بۆ لای نووری خۆی که قورئانه&zwnj;، خوا چه&zwnj;نده&zwnj;ها نموونه ده&zwnj;هێنێته&zwnj;وه بۆ خه&zwnj;ڵکی (تا له نموونه مه&zwnj;عنه&zwnj;ویی یه&zwnj;کان تێبگه&zwnj;ن) خوایش به&zwnj;هه&zwnj;موو شتێك زانایه&zwnj;...';
$t[36] = 'ئه&zwnj;و نووره&zwnj;ش ده&zwnj;ستده&zwnj;که&zwnj;وێت له چه&zwnj;ند ماڵێکدا (که مزگه&zwnj;وته&zwnj;کانن) خوا فه&zwnj;رمانیداوه که دروست بکرێن و به&zwnj;رز بکرێنه&zwnj;وه و ناوی پیرۆزی ئه&zwnj;وی تیادا ببرێت (نوێژو قورئان و وانه&zwnj;ی ئاینی تیادا ئه&zwnj;نجام بدرێت)و ستایش و ته&zwnj;سبیحاتی تیادا بکرێت له به&zwnj;یانیان و ده&zwnj;مه و ئێواراندا.';
$t[37] = 'پیاوانێك ستایشی خوا ده&zwnj;که&zwnj;ن و له&zwnj;و مزگه&zwnj;وتانه&zwnj;دا که بازرگانی و کڕین و فرۆشتن سه&zwnj;ر گه&zwnj;رمیان ناکات و غافڵ نابن له یادی خواو ئه&zwnj;نجامدانی نوێژه&zwnj;کان و به&zwnj;خشینی زه&zwnj;کات و له ڕۆژێك ده&zwnj;ترسن که دڵان و چاوان تیایدا ئاوه&zwnj;ژوو ده&zwnj;بن (له ترس و هه&zwnj;یبه&zwnj;تی ئه&zwnj;و ڕۆژه&zwnj;).';
$t[38] = 'به ئومێدی ئه&zwnj;وه&zwnj;ن خوا به چاکترین شێوه پاداشتیان بداته&zwnj;وه&zwnj;و له فه&zwnj;زڵ و به&zwnj;خششی خۆی به&zwnj;هره&zwnj;وه&zwnj;ریان بکات، به ڕاستی خوا ڕزق و ڕۆزی بێ سنوور ده&zwnj;به&zwnj;خشێت به&zwnj;هه&zwnj;ر که&zwnj;س که بیه&zwnj;وێت و شایسته بێت.';
$t[39] = 'ئه&zwnj;وانه&zwnj;ی که ڕێبازی کوفریان گرتۆته به&zwnj;ر کرده&zwnj;وه&zwnj;کانیان وه&zwnj;ك تراویلکه&zwnj;و سه&zwnj;رابێك وایه له بیابانێکدا بریسکه بداته&zwnj;وه که&zwnj;سێکی تینوو وا بزانێت ئاوه (به&zwnj;تاڵوکه بۆی بچێت)، هه&zwnj;تا کاتێك گه&zwnj;یشته لای ده&zwnj;بینێت هیچ نیه&zwnj;، ئه&zwnj;وسا (کاتێك به&zwnj;خۆی ده&zwnj;زانێت ژیانی کۆتایه&zwnj;و قیامه&zwnj;ت به&zwnj;رپا بووه&zwnj;) خوایش ئاماده&zwnj;یه له&zwnj;لایدا به گوێره&zwnj;ی کرده&zwnj;وه&zwnj;ی موحاسه&zwnj;به&zwnj;ی ده&zwnj;کات و لێی ده&zwnj;پرسێته&zwnj;وه&zwnj;، خوایش به خێرایی لێ پرسینه&zwnj;وه ئه&zwnj;نجام ده&zwnj;دات.';
$t[40] = 'یاخود (کرده&zwnj;وه&zwnj;یان) وه&zwnj;ك چه&zwnj;نده&zwnj;ها تاریکایی وایه که له&zwnj;ناو ده&zwnj;ریایه&zwnj;کی قوڵدا شه&zwnj;پۆل له&zwnj;سه&zwnj;ر شه&zwnj;پۆل بدات به&zwnj;سه&zwnj;ریدا، له سه&zwnj;روشیه&zwnj;وه هه&zwnj;ورێکی تاریك دایگرتبێت، تاریکاییه&zwnj;کان چین چین له&zwnj;سه&zwnj;ریه&zwnj;ك نیشتبن، کاتێك که ده&zwnj;ستی ده&zwnj;ربهێنێت خه&zwnj;ریکه ده&zwnj;ستی خۆی نه&zwnj;بینێت (له&zwnj;به&zwnj;ر تاریکی) بێگومان ئه&zwnj;وه&zwnj;ی خوا نووری پێ نه&zwnj;به&zwnj;خشێت، هه&zwnj;رگیز نووری ده&zwnj;ست ناکه&zwnj;وێت.';
$t[41] = 'ئایا نه&zwnj;تبینیووه که به ڕاستی هه&zwnj;رچی و هه&zwnj;رکه&zwnj;س له ئاسمانه&zwnj;کان و زه&zwnj;ویدا هه&zwnj;یه&zwnj;، ته&zwnj;سبیحات و ستایشی خوا ده&zwnj;که&zwnj;ن، هه&zwnj;روه&zwnj;ها باڵنده&zwnj;کانیش له کاتێکدا پۆل پۆل (به ئاسماندا ده&zwnj;فڕن) هه&zwnj;ر هه&zwnj;موویان ستایش و په&zwnj;رستش و ته&zwnj;سبیحاتی خۆیان زانیووه&zwnj;، خوایش هه&zwnj;میشه زانایه به&zwnj;وه&zwnj;ی که ده&zwnj;یکه&zwnj;ن.';
$t[42] = 'هه&zwnj;موو ئاسمانه&zwnj;کان و زه&zwnj;وی هه&zwnj;ر خوا خاوه&zwnj;نیانه&zwnj;، سه&zwnj;ره&zwnj;نجام گه&zwnj;ڕانه&zwnj;وه&zwnj;ی هه&zwnj;مووان هه&zwnj;ر بۆ لای خوایه&zwnj;';
$t[43] = 'ئایا نابینیت که بێگومان خوا به ئارامی و شێنه&zwnj;یی هه&zwnj;وره&zwnj;کان به ئاسماندا ده&zwnj;بات دوایی په&zwnj;یوه&zwnj;ستیان ده&zwnj;کات به&zwnj;یه&zwnj;که&zwnj;وه&zwnj;، ئه&zwnj;وسا که&zwnj;ڵه&zwnj;که&zwnj;یان ده&zwnj;کات له&zwnj;سه&zwnj;ر یه&zwnj;ك، ئینجا ده&zwnj;بینیت له توێی ئه&zwnj;و هه&zwnj;ورانه&zwnj;وه که وه&zwnj;کو کێو وان باران و ته&zwnj;رزه ده&zwnj;بارێنێت (کاتێك که به&zwnj;فرۆکه سه&zwnj;روو هه&zwnj;وره&zwnj;کان ده&zwnj;که&zwnj;وین به چاکی بۆمان ده&zwnj;رده&zwnj;که&zwnj;وێت، که هه&zwnj;وره&zwnj;کان وه&zwnj;ك چیا چوون به ئاسمانداو دۆڵ و شیو له نێوانیاندا هه&zwnj;یه&zwnj;)، جا له سوودی ئه&zwnj;و بارانه به هه&zwnj;ر که&zwnj;س خوا بیه&zwnj;وێت، خێرو به&zwnj;ره&zwnj;که&zwnj;ت ده&zwnj;به&zwnj;خشێت، هه&zwnj;ر که&zwnj;سیش که بیه&zwnj;وێت لێی دوور ده&zwnj;خاته&zwnj;وه&zwnj;، (هه&zwnj;وره بروسکه&zwnj;کان ئه&zwnj;وه&zwnj;نده به&zwnj;هێزن) خه&zwnj;ریکه بریسکه&zwnj;که&zwnj;یان بینایی چاوان به&zwnj;رێت.';
$t[44] = 'خوای گه&zwnj;وره ئاڵو گۆڕ به&zwnj;شه&zwnj;وو ڕۆژ ده&zwnj;کات، به ڕاستی ئا له&zwnj;و شتانه&zwnj;دا په&zwnj;ندو ئامۆژگاری هه&zwnj;یه بۆ خاوه&zwnj;ن دیدو ڕۆشنبیره&zwnj;کان.';
$t[45] = 'هه&zwnj;روه&zwnj;ها خوا هه&zwnj;موو زینده&zwnj;وه&zwnj;رێکی له ئاوێکی تایبه&zwnj;ت دروست کردووه&zwnj;، جا هه&zwnj;یانه له&zwnj;سه&zwnj;ر سکی ده&zwnj;ڕوات، هه&zwnj;یشه له&zwnj;سه&zwnj;ردوو قاچ ده&zwnj;ڕوات، هه&zwnj;یشه له&zwnj;سه&zwnj;ر چوار پێ ده&zwnj;ڕوات، خوا هه&zwnj;رچی بوێت دروستی ده&zwnj;کات، به ڕاستی خوا ده&zwnj;سه&zwnj;ڵاتی به&zwnj;سه&zwnj;ر هه&zwnj;موو شتێکدا هه&zwnj;یه&zwnj;.';
$t[46] = 'سوێند به خوا بێگومان ئێمه چه&zwnj;نده&zwnj;ها به&zwnj;ڵگه&zwnj;ی ڕوون و ئاشکرامان دابه&zwnj;زاندووه&zwnj;و خوا هیدایه&zwnj;تی هه&zwnj;ر که&zwnj;سێك که ویستی له&zwnj;سه&zwnj;ر بێت و خۆیشی بیه&zwnj;وێت ده&zwnj;دات بۆ ڕێگه&zwnj;و ڕێبازی ڕاست و دروست.';
$t[47] = '(دووڕووه&zwnj;کان) ته&zwnj;نها به زمان ده&zwnj;ڵێن: ئێمه&zwnj;ش باوه&zwnj;ڕمان هێناوه به خواو به پێغه&zwnj;مبه&zwnj;رو فه&zwnj;رمانبه&zwnj;رداریشین، که&zwnj;چی له&zwnj;وه&zwnj;ودوا ده&zwnj;سته&zwnj;یه&zwnj;کیان ڕوو وه&zwnj;رده&zwnj;گێڕن و درۆ ده&zwnj;که&zwnj;ن، ئا ئه&zwnj;وانه ئیماندارنین.';
$t[48] = 'ئه&zwnj;وانه کاتێك بانگ ده&zwnj;کرێن بۆ لای به&zwnj;رنامه&zwnj;ی خواو پێغه&zwnj;مبه&zwnj;ره&zwnj;که&zwnj;ی تا فه&zwnj;رمانڕه&zwnj;وا بێت له نێوانیاندا، خێرا ده&zwnj;سته&zwnj;یه&zwnj;کیان پشت هه&zwnj;ڵده&zwnj;که&zwnj;ن و ناڕه&zwnj;زایی ده&zwnj;رده&zwnj;بڕن.';
$t[49] = 'خۆ ئه&zwnj;گه&zwnj;ر شه&zwnj;رع حه&zwnj;قه&zwnj;که به&zwnj;وان بدات ئه&zwnj;وه به ملکه&zwnj;چی و فه&zwnj;رمانبه&zwnj;ردارییه&zwnj;وه دێن بۆ لای.';
$t[50] = 'ئایا ئه&zwnj;وانه له دڵه&zwnj;کانیاندا نه&zwnj;خۆشی کوفرو خوا نه&zwnj;ناسی هه&zwnj;یه&zwnj;؟ یاخود گومانیان هه&zwnj;یه؟ (له دادپه&zwnj;روه&zwnj;ریی پێغه&zwnj;مبه&zwnj;رو قورئان)، یاخود ده&zwnj;ترسن له&zwnj;وه&zwnj;ی که خوا و پێغه&zwnj;مبه&zwnj;ری سته&zwnj;میان لێ بکات؟! نه&zwnj;خێر وانی یه&zwnj;، ئا ئه&zwnj;وانه هه&zwnj;ر خۆیان سته&zwnj;مکارن (چاویان به حه&zwnj;قدا هه&zwnj;ڵنایه&zwnj;ت).';
$t[51] = 'به&zwnj;ڵام گوفتاری ئیمانداران کاتێك بانگ ده&zwnj;کرێن بۆ لای خواو پێغه&zwnj;مبه&zwnj;ره&zwnj;که&zwnj;ی تا فه&zwnj;رمانڕه&zwnj;وایی بکات له نێوانیاندا ته&zwnj;نها ئه&zwnj;وه&zwnj;یه که ده&zwnj;ڵێن: بیستمان و گوێڕایه&zwnj;ڵین بۆ هه&zwnj;موو فه&zwnj;رمانێکی شه&zwnj;رع، هه&zwnj;ر ئا ئه&zwnj;وانه&zwnj;شن سه&zwnj;ر فرازو سه&zwnj;رکه&zwnj;وتوو.';
$t[52] = 'جا ئه&zwnj;وه&zwnj;ی فه&zwnj;رمانبه&zwnj;رداری خواو پێغه&zwnj;مبه&zwnj;ره&zwnj;که&zwnj;ی بکات و له خوا بترسێت و له&zwnj;سه&zwnj;ر پێچی و نافه&zwnj;رمانی خۆی بپارێزێت، جا ئا ئه&zwnj;وانه سه&zwnj;رفرازو به ئاوات گه&zwnj;یشتوون.';
$t[53] = 'دوو ڕوه&zwnj;کان سوێندی گه&zwnj;وره به خوا ده&zwnj;خۆن، ئه&zwnj;گه&zwnj;ر فه&zwnj;رمانیان پێ بده&zwnj;یت که ده&zwnj;رچن بۆ خه&zwnj;بات و جیهاد، ده&zwnj;رده&zwnj;چن و ئاماده&zwnj;ن، پێیان بڵێ: سوێند مه&zwnj;خۆن ملکه&zwnj;چی ئێوه زانراوو دیاره&zwnj;؟! (هه&zwnj;موو کات کردارو گوفتاریان جیایه&zwnj;)، به ڕاستی خوا ئاگایه به&zwnj;و کارو کرده&zwnj;وانه&zwnj;ی که ئه&zwnj;نجامی ده&zwnj;ده&zwnj;ن.';
$t[54] = 'پێیان بڵێ: فه&zwnj;رمانبه&zwnj;رداری خواو فه&zwnj;رمانبه&zwnj;رداری پێغه&zwnj;مبه&zwnj;ر بن، خۆ ئه&zwnj;گه&zwnj;ر پشتیان هه&zwnj;ڵکرد، ئه&zwnj;وه ئه&zwnj;و ته&zwnj;نها گه&zwnj;یاندنی په&zwnj;یامی خوای له&zwnj;سه&zwnj;ر شانه&zwnj;، ئێوه&zwnj;ش سه&zwnj;رپێچی و یاخی بوونه&zwnj;که&zwnj;تان له&zwnj;سه&zwnj;رتان ده&zwnj;که&zwnj;وێت، خۆ ئه&zwnj;گه&zwnj;ر فه&zwnj;رمانبه&zwnj;رداری بن و گوێڕایه&zwnj;ڵ و ملکه&zwnj;چ بن به ڕاستی ئه&zwnj;وه هیدایه&zwnj;ت وه&zwnj;رده&zwnj;گرن، دڵنیاش بن که ئه&zwnj;رکی سه&zwnj;رشانی پێغه&zwnj;مبه&zwnj;ر صلی الله علیه وسلم ته&zwnj;نها گه&zwnj;یاندنێکی ڕوون و ئاشکرایه و هیچی تر نی یه&zwnj;.';
$t[55] = 'خوا به&zwnj;ڵێنی دڵنیابه&zwnj;خش و ته&zwnj;واوی داوه به&zwnj;وانه&zwnj;ی باوه&zwnj;ڕیان هێناوه له ئێوه&zwnj;و کارو کرده&zwnj;وه چاکه&zwnj;کانیان ئه&zwnj;نجامداوه به ڕاستی له ئاینده&zwnj;یه&zwnj;کی نزیکدا جێنشین و پایه&zwnj;داریان ده&zwnj;کات له وڵاتدا هه&zwnj;روه&zwnj;ك چۆن ئیماندارانی پێش ئه&zwnj;مانی جێنشین کردووه&zwnj;و ئه&zwnj;و دین و ئاینه&zwnj;یان بۆ ده&zwnj;چه&zwnj;سپێنێت خوا خۆی پێی ڕازیه&zwnj;، هه&zwnj;روه&zwnj;ها ترس و بیمیان بۆ ده&zwnj;گۆڕێت به ئارامی و هێمنی، ئه&zwnj;و کاته ئیتر به ته&zwnj;واوی هه&zwnj;ر من ده&zwnj;په&zwnj;رستن و هه&zwnj;رگیز هیچ جۆره هاوه&zwnj;ڵ و شه&zwnj;ریکێکم بۆ بڕیار ناده&zwnj;ن، ئه&zwnj;وسا ئه&zwnj;وه&zwnj;ی دوای ئه&zwnj;و پایه&zwnj;داریه بێباوه&zwnj;ڕ بێت، ئا ئه&zwnj;و جۆره که&zwnj;سانه تاوانبارو له سنوور ده&zwnj;رچوون.';
$t[56] = 'ئه&zwnj;ی ئیمانداران: نوێژه&zwnj;کانتان له کاتی خۆیدا به چاکی ئه&zwnj;نجام بده&zwnj;ن و زه&zwnj;کاتی ماڵه&zwnj;کانتان بده&zwnj;ن و فه&zwnj;رمانبه&zwnj;رداری پێغه&zwnj;مبه&zwnj;ریش بکه&zwnj;ن به&zwnj;ڵکو ڕه&zwnj;حمتان پێ بکرێت.';
$t[57] = 'وا مه&zwnj;زانه ئه&zwnj;وانه&zwnj;ی که بێ باوه&zwnj;ڕو خوا نه&zwnj;ناس بوون له ده&zwnj;ست سزاو تۆڵه&zwnj;ی خوا له&zwnj;سه&zwnj;ر زه&zwnj;وی ڕزگاریان ده&zwnj;بێت، ئه&zwnj;وانه له قیامه&zwnj;تدا شوێنیان ئاگری دۆزه&zwnj;خه&zwnj;، ئای چ سه&zwnj;ره&zwnj;نجامێکی خراپه&zwnj;.';
$t[58] = 'ئه&zwnj;ی ئه&zwnj;وانه&zwnj;ی باوه&zwnj;ڕتان هێناوه با به&zwnj;نده&zwnj;و ئه&zwnj;و منداڵانه&zwnj;تان (که هێشتا نه&zwnj;گه&zwnj;یشتونه&zwnj;ته ته&zwnj;مه&zwnj;نی هه&zwnj;رزه&zwnj;کاری) له سێ کاتدا (فێری ئه&zwnj;وه بکرێن) مۆڵه&zwnj;ت بخوازن (له&zwnj;سه&zwnj;ردانی ژووری نووستنی ژن و مێرد) پێش نوێژی به&zwnj;یانی، کاتی نیوه&zwnj;ڕۆ که پۆشاکتان دا ده&zwnj;نێن (بۆ حه&zwnj;وانه&zwnj;وه&zwnj;)، له دوای نوێژی خه&zwnj;وتنان، سێ کاته که له&zwnj;وانه&zwnj;یه عه&zwnj;وره&zwnj;تی ئێوه&zwnj;ی تێدا ده&zwnj;ربکه&zwnj;وێت (با که&zwnj;سی تر؛ نه&zwnj;تانبینێت)، له دوای ئه&zwnj;و سێ کاته قه&zwnj;یناکات بۆ ئێوه&zwnj;ش و ئه&zwnj;وانیش، که بگه&zwnj;ڕێن به ناویه&zwnj;کدا و بچن بۆ لای یه&zwnj;کتر، ئا به&zwnj;و شێوه&zwnj;یه خوا ئایه&zwnj;ته&zwnj;کانی خۆیتان بۆ ڕوون ده&zwnj;کاته&zwnj;وه&zwnj;، (تا تووشی کێشه&zwnj;ی ده&zwnj;روونی نه&zwnj;بن) خواش هه&zwnj;میشه زاناو دانایه (به&zwnj;وه&zwnj;ی که ده&zwnj;بێته مایه&zwnj;ی خێرو خۆشیتان).';
$t[59] = 'هه&zwnj;ر کاتێك منداڵه&zwnj;کانتان باڵغ بوون، با ئه&zwnj;وانیش مۆڵه&zwnj;ت بخوازن، وه&zwnj;کو چۆن که&zwnj;سانی پێش ئه&zwnj;مان مۆڵه&zwnj;تیان خواستووه (له کاتی هاتووچۆ کردنی ژووری دایك و باوكدا)، ئا به&zwnj;و شێوه&zwnj;یه خوا ئایه&zwnj;ته&zwnj;کانی خۆیتان بۆ ڕوون ده&zwnj;کاته&zwnj;وه و خوا زاناو دانایه (به به&zwnj;رژه&zwnj;وه&zwnj;ندیه&zwnj;کانتان).';
$t[60] = 'ئه&zwnj;و ئافره&zwnj;ته پیرو په&zwnj;ککه&zwnj;وتانه&zwnj;ی که به ته&zwnj;مای شوو کردن نین، قه&zwnj;یناکات ئه&zwnj;گه&zwnj;ر هه&zwnj;ندێك له پۆشاکیان دابنێن، له کاتێکدا جوانی خۆیان ده&zwnj;رنه&zwnj;خه&zwnj;ن، خۆ ئه&zwnj;گه&zwnj;ر پۆشته بن ئه&zwnj;وه چاکتره بۆیان، خوایش بیسه&zwnj;رو زانایه (به نیه&zwnj;ت و خه&zwnj;ته&zwnj;ره&zwnj;و خه&zwnj;یاڵی دڵه&zwnj;کان).';
$t[61] = 'کوێرو نابینا گوناهو گله&zwnj;ییان له&zwnj;سه&zwnj;ر نییه&zwnj;، هه&zwnj;روه&zwnj;ها شه&zwnj;ل، نه&zwnj;خۆش، هه&zwnj;روه&zwnj;ها خۆیشتان، که خواردن بخۆن له ماڵی خۆتان یان ماڵی باوکتان یان ماڵی دایکتان، یان ماڵی براکانتان یان ماڵی خوشکه&zwnj;کانتان، یان ماڵی مامه&zwnj;کانتان یان ماڵی پووره&zwnj;کانتان (خوشکانی باوك)، یان له ماڵی خاڵه&zwnj;کانتان یان له ماڵی پووره&zwnj;کانتان (خوشکانی دایك)، یان ئه&zwnj;و ماڵه&zwnj;ی که کلیلی لای ئێوه&zwnj;یه&zwnj;، یان ماڵی هاوه&zwnj;ڵتان، هیچ گوناهتان ناگات که به کۆمه&zwnj;ڵ (له&zwnj;سه&zwnj;ر یه&zwnj;ك سفره&zwnj;) نان بخۆن یاخود جیا به جیا، هه&zwnj;ر کاتێك چوونه هه&zwnj;ر ماڵێك، سه&zwnj;لام له خۆتان بکه&zwnj;ن و سه&zwnj;لام له یه&zwnj;کتر بکه&zwnj;ن، سه&zwnj;لامێك له لایه&zwnj;ن خوای گه&zwnj;وره&zwnj;وه&zwnj;، که پڕه له به&zwnj;ره&zwnj;که&zwnj;ت و فه&zwnj;ڕو جوانی و پاکی، ئا به&zwnj;و شێوه&zwnj;یه خوا ئایه&zwnj;ته&zwnj;کانی خۆیتان بۆ ڕوون ده&zwnj;کاته&zwnj;وه بۆ ئه&zwnj;وه&zwnj;ی بیرو هۆش بکه&zwnj;نه&zwnj;وه&zwnj;و لێکی بده&zwnj;نه&zwnj;وه (که چ په&zwnj;روه&zwnj;ردگارێکی دڵسۆزو غه&zwnj;مخوارمان هه&zwnj;یه&zwnj;، چۆن ده&zwnj;یه&zwnj;وێت ژیانمان به شێوه&zwnj;یه&zwnj;ك ڕێك بخات که به خۆشی و شادی پێکه&zwnj;وه بژین، دوور له گرفت و کێشه&zwnj;ی ده&zwnj;روونی و ڕه&zwnj;وشتی و کۆمه&zwnj;ڵه&zwnj;یه&zwnj;تی).';
$t[62] = 'به ڕاستی ئیمانداران ئه&zwnj;وانه&zwnj;ن که باوه&zwnj;ڕیان هێناوه به خواو به پێغه&zwnj;مبه&zwnj;ره&zwnj;که&zwnj;ی و هه&zwnj;ر کاتێك کۆ ده&zwnj;بنه&zwnj;وه له خزمه&zwnj;تیدا بۆ کارێکی گرنگ و پێویست که هاوکاریی ده&zwnj;وێت، ناڕۆن و (خۆیان نادزنه&zwnj;وه&zwnj;) هه&zwnj;تا مۆڵه&zwnj;ت نه&zwnj;خوازن، به ڕاستی ئه&zwnj;وانه&zwnj;ی که مۆڵه&zwnj;تت لێ ده&zwnj;خوازن هه&zwnj;ر ئه&zwnj;و که&zwnj;سانه&zwnj;ن که باوه&zwnj;ڕیان به خواو پێغه&zwnj;مبه&zwnj;ره&zwnj;که&zwnj;ی هه&zwnj;یه&zwnj;، هه&zwnj;ر کاتێك بۆ کارێکی تایبه&zwnj;تی خۆیان داوای مۆڵه&zwnj;تیان لێکردیت، ئه&zwnj;وه مۆڵه&zwnj;تی ئه&zwnj;وانه بده که ده&zwnj;ته&zwnj;وێت و داوای لێ خۆشبوونیشیان له خوا بۆ بکه&zwnj;، به ڕاستی خوا لێخۆشبوو میهره&zwnj;بانه&zwnj;.';
$t[63] = '(ئه&zwnj;ی یاوه&zwnj;ران) نه&zwnj;که&zwnj;ن بانگی پێغه&zwnj;مبه&zwnj;ر بکه&zwnj;ن، وه&zwnj;ك ئه&zwnj;وه&zwnj;ی خۆتان له نێوان یه&zwnj;کتردا بانگی یه&zwnj;ك ده&zwnj;که&zwnj;ن (چونکه ده&zwnj;بێت قه&zwnj;دری پێغه&zwnj;مبه&zwnj;ری خوا صلی الله علیه وسلم تایبه&zwnj;تی بێت) بێگومان خوا ده&zwnj;زانێت به&zwnj;وانه&zwnj;ی که خۆیان په&zwnj;نهان ده&zwnj;که&zwnj;ن و خۆ ده&zwnj;دزنه&zwnj;وه له کارو فرمان، ده&zwnj;با ئه&zwnj;وانه&zwnj;ی که سه&zwnj;رپێچی له فه&zwnj;رمانی ده&zwnj;که&zwnj;ن بترسن له&zwnj;وه&zwnj;ی که تووشی تاقی کردنه&zwnj;وه&zwnj;یه&zwnj;کی سه&zwnj;خت، یان تووشی سزایه&zwnj;کی به ئێش ببن.';
$t[64] = 'ئاگاداربن که هه&zwnj;رچی له ئاسمانه&zwnj;کان و زه&zwnj;ویدا هه&zwnj;یه خوا خاوه&zwnj;نیانه&zwnj;، زۆر به چاکی ده&zwnj;زانێت ئێوه چی ده&zwnj;که&zwnj;ن و خه&zwnj;ریکی چین، ئه&zwnj;و ڕۆژه&zwnj;ش که ده&zwnj;برێنه&zwnj;وه بۆ لای هه&zwnj;واڵی هه&zwnj;موو ئه&zwnj;و کارانه&zwnj;یان پێ ده&zwnj;ڵێت که ئه&zwnj;نجامیان داوه&zwnj;و خوا هه&zwnj;میشه به هه&zwnj;موو شتێك ئاگاو زانایه&zwnj;.';
?>