$t[67] = 'ته&zwnj;ماشای ئایه&zwnj;تی 8، 9 بکه&zwnj;.';
$t[68] = 'ته&zwnj;ماشای ئایه&zwnj;تی 8، 9 بکه&zwnj;.';
$t[69] = 'هه&zwnj;روه&zwnj;ها به&zwnj;سه&zwnj;رهات وهه&zwnj;واڵی ئیبراهیم بۆ ئه&zwnj;و خه&zwnj;ڵکه باس بکه.';
$t[70] = 'کاتێک به باوکی و به قه&zwnj;ومه&zwnj;که&zwnj;ی وت: باشه&zwnj;، ئه&zwnj;وه ئێوه چی ده&zwnj;په&zwnj;رستن؟!';
