$t[72] = 'ئیبراهیم وتی: ئایا کاتێک که هاناو هاواریان بۆ ده&zwnj;به&zwnj;ن و دوعا و نزا ده&zwnj;که&zwnj;ن، گوێیان له ئا&zwnj;هو ناڵه و داخوازیه&zwnj;کانتان هه&zwnj;یه&zwnj;؟!...';
$t[73] = 'یاخود ده&zwnj;توانن ئه&zwnj;وانه قازانجێک یان زیانێکتان پێبگه&zwnj;یه&zwnj;نن؟...';
$t[74] = 'بت په&zwnj;رستان وتیان: (ئێمه ئه&zwnj;وه نازانین) باو باپیرانمان بینیوه ئاوایان کردووه (ئێمه&zwnj;ش وا ده&zwnj;که&zwnj;ین)...';
$t[75] = 'ئیبراهیم وتی: باشه ئاخر ئێوه نابینن چی ده&zwnj;په&zwnj;رستن (که&zwnj;ی ئه&zwnj;و شتانه شایسته&zwnj;ی په&zwnj;رستنن)...';
$t[76] = 'به ڕاستی ئێوه&zwnj;ش و باو باپیرانی دێرینیشتان (به هه&zwnj;ڵه&zwnj;دا چوون)...';
$t[77] = 'بێگومان هه&zwnj;موو ئه&zwnj;وانه دوژمنی منن، به دوژمنی خۆمیان ده&zwnj;زانم جگه له په&zwnj;روه&zwnj;ردگاری جیهانه&zwnj;کان...';
$t[78] = 'ئه&zwnj;وه&zwnj;ی دروستی کردووم هه&zwnj;ر ئه&zwnj;و ڕێنموییم ده&zwnj;کات (بۆ به&zwnj;خته&zwnj;وه&zwnj;ری هه&zwnj;ردوو جیهان)...';
$t[79] = 'ئه&zwnj;و زاته&zwnj;ش خۆی خۆراکم پێ ده&zwnj;به&zwnj;خشێت و تینویه&zwnj;تیم ده&zwnj;شکێنێت...';
$t[80] = 'کاتێکیش که نه&zwnj;خۆش ده&zwnj;که&zwnj;وم، ئه&zwnj;وه هه&zwnj;ر ئه&zwnj;و چاره&zwnj;سه&zwnj;رم ده&zwnj;کات و شیفام بۆ ده&zwnj;نێرێت...';
$t[81] = 'هه&zwnj;ر ئه&zwnj;ویش ده&zwnj;ممرێنێت و له&zwnj;وه&zwnj;ودواش زیندووم ده&zwnj;کاته&zwnj;وه&zwnj;...';
$t[82] = 'هه&zwnj;ر ئه&zwnj;وه ئومێدم پێیه&zwnj;تی که له گوناهم خۆش ببێت له ڕۆژی قیامه&zwnj;تدا...';
$t[83] = '(ئینجا ئیبراهیم نزای کردو وتی) په&zwnj;روه&zwnj;ردگارا: زانیاری ودانایی و ده&zwnj;سه&zwnj;ڵاتی ته&zwnj;واوم پێ ببه&zwnj;خشه و په&zwnj;یوه&zwnj;ستم بکه به چاکه&zwnj;کارانه&zwnj;وه&zwnj;.';
