$t[207] = 'ئه&zwnj;وسا ئیتر ئه&zwnj;و ڕابواردنه&zwnj;ی دنیایان هیچ فریایان ناکه&zwnj;وێت و بێ سوود ده&zwnj;بێت.';
$t[208] = 'ئێمه هه&zwnj;ر شارو شارۆچکه&zwnj;یه&zwnj;کمان کاول کردبێت به&zwnj;بێ فرستاده و بێدارخه&zwnj;ره&zwnj;وه نه&zwnj;بووه&zwnj;.';
$t[209] = 'تا دابچڵه&zwnj;کێن و بیر بکه&zwnj;نه&zwnj;وه&zwnj;، بێگومان هه&zwnj;رگیز ئێمه سته&zwnj;مکار نه&zwnj;بووین و نابین.';
$t[210] = 'دڵنیا بن که ئایه&zwnj;ته&zwnj;کانی ئه&zwnj;م قورئانه وه&zwnj;نه&zwnj;بێت له ڕێگه&zwnj;ی شه&zwnj;یتانه&zwnj;کانه&zwnj;وه ڕه&zwnj;وانه کرابێت';
$t[211] = 'شتی وا به&zwnj;وان ناکرێت و هه&zwnj;رگیز ناتوانن به کاری وا هه&zwnj;ستن.';
$t[212] = 'بێگومان ئه&zwnj;و شه&zwnj;یتانانه له بیستنی وه&zwnj;حی و فه&zwnj;رمانه&zwnj;کانی خوا که ڕاده&zwnj;گه&zwnj;یه&zwnj;نرێت به فریشته&zwnj;کان دوور و به&zwnj;رکه&zwnj;نارن.';
$t[213] = 'که&zwnj;واته ئه&zwnj;ی ئیمانداران، له&zwnj;گه&zwnj;ڵ خوادا هانا و هاوار بۆ به&zwnj;ناو خوایه&zwnj;کی تر مه&zwnj;به&zwnj;، چونکه به&zwnj;هۆی ئه&zwnj;وه&zwnj;وه تۆش له سزا دراوان ده&zwnj;بیت.';
$t[214] = 'ئه&zwnj;ی محمد (صلی الله علیه وسلم) سه&zwnj;ره&zwnj;تا عه&zwnj;شره&zwnj;ت و تایه&zwnj;فه و که&zwnj;س و کاری نزیکت بێدار بکه&zwnj;ره&zwnj;وه و بترسێنه&zwnj;.';
$t[215] = 'باڵی به&zwnj;زه&zwnj;یی و میهره&zwnj;بانی خۆت نه&zwnj;وی بکه بۆ ئه&zwnj;و ئیماندارانه&zwnj;ی که شوێنت که&zwnj;وتوون.';
$t[216] = 'ئه&zwnj;گه&zwnj;ر خوانه&zwnj;ناسان یاخی بوون و سه&zwnj;رپێچیان کرد، ئه&zwnj;وه پێیان بڵێ: که به&zwnj;ڕاستی من به&zwnj;ریم له هه&zwnj;موو ئه&zwnj;و کارو کرده&zwnj;وانه&zwnj;ی که ده&zwnj;یکه&zwnj;ن.';
$t[217] = 'محمد (صلی الله علیه وسلم) پشت به خوای باڵا ده&zwnj;ست ومیهره&zwnj;بانت ببه&zwnj;سته&zwnj;.';
$t[218] = 'ئه&zwnj;و زاته&zwnj;ی که ده&zwnj;تبینێت کاتێک هه&zwnj;ڵده&zwnj;ستیت له شه&zwnj;وگاردا ده&zwnj;یپه&zwnj;رستیت.';
$t[219] = 'ئه&zwnj;و زاته تۆ ده&zwnj;بینێت که چۆن له ڕیزی سوژده&zwnj;به&zwnj;راندا هه&zwnj;ڵس و که&zwnj;وت ده&zwnj;که&zwnj;یت.';
$t[220] = 'به&zwnj;ڕاستی ئه&zwnj;و خوایه زاتێکی بیسه&zwnj;رو زانایه (به&zwnj;گوفتارو نیه&zwnj;تتان).';
$t[221] = 'ئایا پێتان بڵێم که شه&zwnj;یتانه&zwnj;کان داده&zwnj;به&zwnj;زنه سه&zwnj;ر کێ؟';
$t[222] = 'داده&zwnj;به&zwnj;زنه سه&zwnj;ر هه&zwnj;موو که&zwnj;سێکی بوختانچی و تاوانبار و گوناهکار.';
$t[223] = 'شه&zwnj;یتانه&zwnj;کان گوێ هه&zwnj;ڵده&zwnj;خه&zwnj;ن بۆ هه&zwnj;واڵی ئاسمان و زۆربه&zwnj;شیان درۆزنن.';
$t[224] = 'سه&zwnj;رگه&zwnj;ردان و سه&zwnj;رلێشێواوه&zwnj;کانیش شوێنی شاعیران ده&zwnj;که&zwnj;ون.';
$t[225] = 'ئایا نابینیت که ئه&zwnj;وانه له هه&zwnj;موو دۆڵێکدا سه&zwnj;رهه&zwnj;ڵده&zwnj;ده&zwnj;ن و له هه&zwnj;موو شتێک ده&zwnj;دوێن.';
$t[226] = 'به&zwnj;ڕاستی ئه&zwnj;وانه شتێک ده&zwnj;ڵێن و به&zwnj;کرده&zwnj;وه نایکه&zwnj;ن (کردارو گوفتاریان دووره له یه&zwnj;که&zwnj;وه&zwnj;).';
$t[227] = 'جگه له&zwnj;وانه&zwnj;یان که باوه&zwnj;ڕیان هێناوه و کرده&zwnj;وه چاکه&zwnj;کانیان ئه&zwnj;نجامداوه و یادی خوایان زۆر کردووه&zwnj;، (به&zwnj;و تواناییه&zwnj;ی که هه&zwnj;یانه&zwnj;) پشتیوانیان کردووه له&zwnj;و ئیماندارانه&zwnj;ی که سته&zwnj;میان لێکراوه&zwnj;، سه&zwnj;رئه&zwnj;نجام ئه&zwnj;وانه&zwnj;ی سته&zwnj;میان کردووه ده&zwnj;زانن که به&zwnj;چۆن تێکشکاویه&zwnj;ک ده&zwnj;گه&zwnj;ڕێنه&zwnj;وه بۆ لای خواو چۆن ژێره&zwnj;و ژوور ده&zwnj;بن و تیاده&zwnj;چن.';
?>