$t = array();
$t[1] = 'سه&zwnj;رنجی سه&zwnj;ره&zwnj;تای سووره&zwnj;تی (البقره&zwnj;) بده&zwnj;.';
$t[2] = 'ئایا خه&zwnj;ڵكی وایانزانیوه&zwnj;، وازیان لێده&zwnj;هێنرێت و پشتگوێ ده&zwnj;خرێن كه بڵێن ئیمان و باوه&zwnj;ڕمان هێناوه&zwnj;، له&zwnj;كاتێكدا ئه&zwnj;وان تاقی نه&zwnj;كرێنه&zwnj;وه و تووشی سزا و ئازار نه&zwnj;كرێن؟';
$t[3] = 'سوێند به&zwnj;خوا بێگومان ئێمه ئه&zwnj;وانه&zwnj;ی پێش ئه&zwnj;مانمان تاقیكردۆته&zwnj;وه به جۆره&zwnj;ها شێوه&zwnj;، تا خوای گه&zwnj;وره (له جیهانی واقیعدا) ئه&zwnj;وانه&zwnj;ی ڕاستیان كردووه بیانناسێت و ده&zwnj;ركه&zwnj;ون، هه&zwnj;روه&zwnj;ها درۆزنه&zwnj;كانیش ده&zwnj;بێت بناسێت و ده&zwnj;ركه&zwnj;ون.';
$t[4] = 'ئایا ئه&zwnj;وانه&zwnj;ی خراپه و نادروستی ده&zwnj;كه&zwnj;ن واده&zwnj;زانن كه له ده&zwnj;ستمان ده&zwnj;رده&zwnj;چن و ده&zwnj;رباز ده&zwnj;بن؟! ئای چه&zwnj;نده خراپه ئه&zwnj;و بڕیاره&zwnj;ی كه ده&zwnj;یده&zwnj;ن.';
$t[5] = 'ئه&zwnj;وه&zwnj;ی كه ئاواته&zwnj;خوازه به&zwnj;دیداری خوا شاد ببێت، با دڵنیابێت كه&zwnj;كاتی دیاریكراوی خواو ساتی سه&zwnj;ره&zwnj;مه&zwnj;رگ هه&zwnj;ر دێت (تا به&zwnj;دیداری شاد ببێت)، ئه&zwnj;و خوایه زۆر بیسه&zwnj;ره&zwnj;( به&zwnj;گوفتاری به&zwnj;نده&zwnj;كانی) و زانایه به&zwnj;حاڵیان.';
$t[6] = 'ئه&zwnj;وه&zwnj;ش كه هه&zwnj;وڵ ده&zwnj;دات و كۆشش ده&zwnj;كات له پێناوی گه&zwnj;یاندی ئاینی خوادا; بێگومان قازانج و سوودی هه&zwnj;وڵ و كۆششه&zwnj;كه&zwnj;ی هه&zwnj;ر بۆ خۆیه&zwnj;تی، چونكه به&zwnj;ڕاستی خوا بێ نیازه له هه&zwnj;موو خڵكی. ( به&zwnj;ڵام له قیامه&zwnj;تدا ئه&zwnj;و زاته زۆر سوپاسگوزاری ئه&zwnj;و كه&zwnj;سانه&zwnj;یه كه هه&zwnj;وڵ و كۆششیان داوه له پێناوی ئاینه&zwnj;كه&zwnj;یدا ).';
$t[7] = 'ئه&zwnj;وانه&zwnj;ی كه ئیمان و باوه&zwnj;ریان هێناوه و كار و كرده&zwnj;وه چاكه&zwnj;كانیان ئه&zwnj;نجام داوه&zwnj;، به&zwnj;ڕاستی هه&zwnj;ڵه وگوناهه&zwnj;كانیان ده&zwnj;سڕینه&zwnj;وه و به&zwnj;چاكتر له&zwnj;وه&zwnj;ی كه ده&zwnj;یانكرد پاداشتیان ده&zwnj;ده&zwnj;ینه&zwnj;وه.';
$t[8] = 'ئێمه فه&zwnj;رمانمانداوه به ئاده&zwnj;میزاد كه چاكره&zwnj;فتار بێت له&zwnj;گه&zwnj;ڵ دایك و باوكیدا، خۆ ئه&zwnj;گه&zwnj;ر زۆریان لێكردیت تا هاوه&zwnj;ڵ و هاوبه&zwnj;شم بۆ بریار بده&zwnj;یت، بێ ئه&zwnj;وه&zwnj;ی زانست و زانیاریت هه&zwnj;بێت ده&zwnj;رباره&zwnj;ی، ئه&zwnj;وه ملكه&zwnj;چیان مه&zwnj;به و به&zwnj;گوێیان مه&zwnj;كه&zwnj;، دڵنیاش بن كه گه&zwnj;ڕانه&zwnj;وه&zwnj;تان بۆ لای منه&zwnj;، ئه&zwnj;و كاته ئاگادارتان ده&zwnj;كه&zwnj;مه&zwnj;وه له&zwnj;و كرده&zwnj;وانه&zwnj;ی كه ئه&zwnj;نجامتان ده&zwnj;دا.';
$t[9] = 'جا ئه&zwnj;وانه&zwnj;ی كه ئیمان و باوه&zwnj;ریان هێناوه و كار و كرده&zwnj;وه چاكه&zwnj;كانیان ئه&zwnj;نجام داوه&zwnj;، ئه&zwnj;وه بێگومان ده&zwnj;یانخه&zwnj;ینه ڕیزی چاكه&zwnj;كارانه&zwnj;وه&zwnj;.';
$t[10] = 'هه&zwnj;ندێك له&zwnj;خاڵكی هه&zwnj;ن كه ده&zwnj;ڵێن: باوه&zwnj;ڕمان به&zwnj;خوا هێناوه&zwnj;، جا كاتێك یه&zwnj;كێك له&zwnj;وانه له به&zwnj;ر خواو له پێناوی خوادا تووشی ئازار و ناخۆشی كرا، واده&zwnj;زانێت سزاو ئه&zwnj;شكه&zwnj;نجه&zwnj;ی خه&zwnj;ڵكی وه&zwnj;ك سزای خوا وایه ( وه&zwnj;ك چۆن له دۆزه&zwnj;خدا په&zwnj;شیمانه لێره&zwnj;ش به&zwnj;هۆی ئه&zwnj;شكه&zwnj;نجه&zwnj;ی خه&zwnj;ڵكیه&zwnj;وه له دینپه&zwnj;شیمان ده&zwnj;بێته&zwnj;وه )، به&zwnj;ڕاستی ئه&zwnj;گه&zwnj;ر په&zwnj;روه&zwnj;ردگارت سه&zwnj;ركه&zwnj;وتنێكی به&zwnj;خشی، دوورووه&zwnj;كان به&zwnj;گه&zwnj;رمی ده&zwnj;ڵێن خۆ ئێمه&zwnj;ش بێگومان هاوكارتان بووین، مه&zwnj;گه&zwnj;ر خوا خۆی نازانێت چی حه&zwnj;شاردراوه له ده&zwnj;روونی هه&zwnj;موو خه&zwnj;ڵكیدا ( به&zwnj;تایبه&zwnj;ت له دڵی ئه&zwnj;و جۆره كه&zwnj;سانه&zwnj;دا )؟';
$t[11] = 'سوێند بێت، خوا ئاگاداره به&zwnj;وانه&zwnj;ی باوه&zwnj;ڕیان هێناوه&zwnj;، هه&zwnj;روه&zwnj;ها به&zwnj;وانه&zwnj;ش كه دووڕوون ( جیایان ده&zwnj;كاته&zwnj;وه ).';
$t[12] = 'ئه&zwnj;وانه&zwnj;ی كه بێ باوه&zwnj;ربوون وتیان به&zwnj;وانه&zwnj;ی كه باوه&zwnj;ڕیان هێناوه: وه&zwnj;رن شوێنی ڕێبازی ئێمه بكه&zwnj;ون ( ئه&zwnj;گه&zwnj;ر شوێنكه&zwnj;وتنی ڕێبازی ئێمه هه&zwnj;ڵه بێت ) ئه&zwnj;وه گوناهتان به ئه&zwnj;ستۆی ئێمه&zwnj;، ئه&zwnj;ڵبه&zwnj;ته ئه&zwnj;وانه له قیامه&zwnj;تدا ناتوانن هیچ گوناهو تاوانێكی ئه&zwnj;وان بگرنه ئه&zwnj;ستۆ، به&zwnj;ڕاستی ئه&zwnj;وانه هه&zwnj;ر درۆده&zwnj;كه&zwnj;ن و درۆزنن.';
$t[13] = 'سوێند به&zwnj;خوا سه&zwnj;رئه&zwnj;نجام گوناهی سه&zwnj;نگینی خۆیان و ئه&zwnj;و كه&zwnj;سانه&zwnj;ش كه گومڕایان كردوون ده&zwnj;بێته سه&zwnj;رباریان، له ڕۆژی قیامه&zwnj;تیشدا پرسیاریان لێده&zwnj;كرێت له&zwnj;و درۆو ده&zwnj;له&zwnj;سه&zwnj;و ته&zwnj;ڵه&zwnj;كه&zwnj;بازییه&zwnj;ی كه هه&zwnj;ڵیانده&zwnj;به&zwnj;ست.';
$t[14] = 'سوێند به خوا ئێمه - نوح - مان ڕه&zwnj;وانه كرد بۆ سه&zwnj;ر قه&zwnj;وم و هۆزه&zwnj;كه&zwnj;ی، هه&zwnj;زار ساڵ په&zwnj;نجا كه&zwnj;م له&zwnj;ناویاندا مایه&zwnj;وه&zwnj;، سه&zwnj;رئه&zwnj;نجام لافاو و زریان هه&zwnj;موویانی پێچایه&zwnj;وه له كاتێكدا ئه&zwnj;وان سته&zwnj;مكار بوون.';
$t[15] = 'ئه&zwnj;و كاته ئیتر ئه&zwnj;ومان ڕزگاركرد هاوڕێ له&zwnj;گه&zwnj;ڵ هه&zwnj;موو سه&zwnj;رنشینه&zwnj;كانی كه&zwnj;شتیه&zwnj;كه&zwnj;دا و ئه&zwnj;و ڕووداو و كه&zwnj;شتیه&zwnj;مان كرده به&zwnj;ڵگه و نیشانه بۆ هه&zwnj;موو لایه&zwnj;ك و هه&zwnj;موو نه&zwnj;وه&zwnj;كان.';
$t[16] = 'باسی ئیبراهیمیش بكه كه كاتی خۆی به قه&zwnj;وم و هۆزی خۆی وت: ته&zwnj;نها خوا بپه&zwnj;رستن و هه&zwnj;ر له&zwnj;و بترسن، ئه&zwnj;و بیروباوه&zwnj;ڕه چاك و په&zwnj;سه&zwnj;ند و خێردارتره بۆتان، ئه&zwnj;گه&zwnj;ر بزانن و تێبگه&zwnj;ن.';
$t[17] = 'به&zwnj;ڕاستی ئه&zwnj;و شتانه&zwnj;ی كه له&zwnj;جیاتی خوا ده&zwnj;یانپه&zwnj;ستن ته&zwnj;نها بت و په&zwnj;یكه&zwnj;رن، ئێوه بوختان و شتی نابه&zwnj;جێ دروست ده&zwnj;كه&zwnj;ن، بێگومان ئه&zwnj;و شتانه&zwnj;ی كه له&zwnj;جیاتی خوا ده&zwnj;یانپه&zwnj;رستن خاوه&zwnj;نی هیچ ڕزق و ڕۆزیه&zwnj;ك نین بۆ ئێوه&zwnj;، كه&zwnj;واته هه&zwnj;ر داوای ڕزق و ڕۆزی له خوا بكه&zwnj;ن، هه&zwnj;ر ئه&zwnj;ویش بپه&zwnj;رستن، هه&zwnj;ر سوپاسگوزاری ئه&zwnj;و بن، بۆ لای ئه&zwnj;ویش ده&zwnj;برێنه&zwnj;وه&zwnj;.';
$t[18] = 'خۆ ئه&zwnj;گه&zwnj;ر ئێوه ئه&zwnj;م په&zwnj;یامه به&zwnj;درۆ ده&zwnj;زانن (وه&zwnj;نه&zwnj;بێت یه&zwnj;كه&zwnj;م هۆزو گه&zwnj;ل بن كه له&zwnj;به&zwnj;رامبه&zwnj;ری په&zwnj;یامی خواوه هه&zwnj;ڵوێستتان ئاوا ناڕه&zwnj;وا بێت) ئه&zwnj;وه به&zwnj;ڕاستی گه&zwnj;لانێكی پێش ئێوه&zwnj;ش په&zwnj;یامی خوایان به&zwnj;درۆ ده&zwnj;زانی، پێعه&zwnj;مبه&zwnj;ریش ته&zwnj;نها گه&zwnj;یاندن و ڕوونكردنه&zwnj;وه&zwnj;ی ئاشكرا ئه&zwnj;ركی سه&zwnj;رشانێتی و هیچی تر.';
$t[19] = 'ئایا ئه&zwnj;وه سه&zwnj;رنجیان نه&zwnj;داوه چۆن خوا له سه&zwnj;ره&zwnj;تاوه دروستكراوان به&zwnj;دی ده&zwnj;هێنێت و پاشان دووباره&zwnj;ی ده&zwnj;كاته&zwnj;وه&zwnj;، بێگومان ئه&zwnj;و شتانه بۆ خوا زۆر ساده و ئاسانه&zwnj;.';
$t[20] = '(ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر(ص) پێیان بڵێ: با بگه&zwnj;رێن به زه&zwnj;ویدا، ته&zwnj;ماشا بكه&zwnj;ن و سه&zwnj;رنج بده&zwnj;ن چۆن ئه&zwnj;و زاته دروستكراوانی به&zwnj;دیهێناوه&zwnj;، له&zwnj;وه&zwnj;ودواش هه&zwnj;ر خوا سه&zwnj;ر له&zwnj;نوێ به&zwnj;رپایان ده&zwnj;كاته&zwnj;وه ( به شێوه&zwnj;و شێوازێكی تر ئاسمانه&zwnj;كان و زه&zwnj;وی و ئێمه&zwnj;ش ده&zwnj;هێنێته&zwnj;وه كایه&zwnj;) به&zwnj;ڕاستی خوا ده&zwnj;سه&zwnj;ڵاتی به&zwnj;سه&zwnj;ر هه&zwnj;موو شتێكدا هه&zwnj;یه&zwnj;.';
$t[21] = 'هه&zwnj;ندێك جار، سزای ئه&zwnj;و كه&zwnj;سانه ده&zwnj;دات كه خوا بیه&zwnj;وێت و شایسته&zwnj;ی سزان و سۆزو میهره&zwnj;بانی خۆیشی ده&zwnj;نوێنێت بۆ ئه&zwnj;وانه&zwnj;ی كه ده&zwnj;یوێت و باوه&zwnj;ڕدارن، سه&zwnj;رئه&zwnj;نجامیش هه&zwnj;ر بۆ لای ئه&zwnj;و زاته ده&zwnj;برێنه&zwnj;وه&zwnj;.';
$t[22] = '(ئه&zwnj;ی گرۆی ئاده&zwnj;میزاد) ئێوه ناتوانن خوا ده&zwnj;سه&zwnj;وسان بكه&zwnj;ن، نه له زه&zwnj;ویدا نه له ئاسماندا، كه&zwnj;سیش نیه&zwnj;جگه له خوا پشتیوانتان بێت و فریاتان بكه&zwnj;وێت (ئه&zwnj;گه&zwnj;ر ڕێگه&zwnj;ی چه&zwnj;وت و نادروست بگرن).';
$t[23] = 'ئه&zwnj;وانه&zwnj;ی كه بڕوایان نیه به&zwnj;ئایه&zwnj;ت و فه&zwnj;رمانه&zwnj;كانی خواو به&zwnj;دیدار ئاماده&zwnj;بوونی به&zwnj;رده&zwnj;م باره&zwnj;گاكه&zwnj;ی (بۆ لێپر سینه&zwnj;وه&zwnj;) ئا ئه&zwnj;وانه (خوا ده&zwnj;فه&zwnj;رموێت) له میهره&zwnj;بانی من نائومێدن و ئه&zwnj;وانه سزای زۆر به&zwnj;ئێش بۆیان ئاماده&zwnj;یه&zwnj;.';
$t[24] = 'وه&zwnj;ڵامی قه&zwnj;وم و هۆزه&zwnj;كه&zwnj;ی ئیبراهیم ته&zwnj;نها ئه&zwnj;وه بوو وتیان: بیكوژن یان بیسوتێنن به&zwnj;ڵام خوا له ئاگره&zwnj;كه ڕزگاری كرد، بێگومان ئا له&zwnj;و پێشهاته&zwnj;دا نیشانه و به&zwnj;ڵگه&zwnj;ی زۆر هه&zwnj;یه بۆ ئه&zwnj;و كه&zwnj;سانه&zwnj;ی كه ئیمان و باوه&zwnj;ڕ ده&zwnj;هێنن.';
$t[25] = 'له&zwnj;وه&zwnj;ودوا پێی وتن: به&zwnj;ڕاستی ئه&zwnj;وه&zwnj;ی ئێوه له&zwnj;جیاتی خوا ده&zwnj;تان په&zwnj;رست ته&zwnj;نها بت و په&zwnj;یكه&zwnj;رن، له&zwnj;به&zwnj;رخاتری یه&zwnj;كتر و ڕێزدانانتان بۆ یه&zwnj;كتره له ژیانی دنیادا، دواجار له ڕۆژی قیامه&zwnj;تدا یه&zwnj;كتر كافرده&zwnj;كه&zwnj;ن و یه&zwnj;خه&zwnj;ی یه&zwnj;كتر ده&zwnj;گرن، هه&zwnj;ندێكیشتان نه&zwnj;فرین له هه&zwnj;ندێكی ترتان ده&zwnj;كه&zwnj;ن، سه&zwnj;ره&zwnj;نجامیش شوێن و جێگه&zwnj;ی دیاریكراوتان ناو ئاگره&zwnj;، ڕزگار كه&zwnj;ریشتان نی یه.';
$t[26] = 'ئینجا - لوط - باوه&zwnj;ڕی پێ هێنا، ئیبراهیم دوای ئه&zwnj;وه&zwnj;ی كه نائومێد بوو له قه&zwnj;ومه&zwnj;كه&zwnj;ی وتی: به&zwnj;ڕاستی من ئیتر كۆچ ده&zwnj;كه&zwnj;م بۆلای په&zwnj;روه&zwnj;ردگارم ( بۆ ئه&zwnj;و شوێنه&zwnj;ی كه ئه&zwnj;و بۆم دیاری ده&zwnj;كات ) چونكه به&zwnj;ڕاستی ئه&zwnj;و زاتێكی زۆر به ده&zwnj;سه&zwnj;ڵات و دانایه.';
$t[27] = 'پاشان ئیسحاقمان پێبه&zwnj;خشی و یه&zwnj;عقوبیشمان به ئیسحاق به&zwnj;خشی و پێغه&zwnj;مبه&zwnj;رایه&zwnj;تیمان كرده كاڵای باڵای نه&zwnj;وه&zwnj;كانی، كتێبه ئاسمانه&zwnj;كانیشمان بۆ ناردن، هه&zwnj;ر له دنیادا پاداشتمان دایه&zwnj;وه&zwnj;، ئه&zwnj;و به&zwnj;ڕاستی له&zwnj;قیامه&zwnj;تیشدا له ڕیزی پیاوچاكاندایه.';
$t[28] = 'یادی ( لوط ) یش بكه كاتێك به&zwnj;قه&zwnj;ومه&zwnj;كه&zwnj;ی وت: ئێوه به&zwnj;ڕاستی تاوانێكی گه&zwnj;وره ده&zwnj;كه&zwnj;ن، ئێوه گوناهێكی ناشیرین ئه&zwnj;نجام ده&zwnj;ده&zwnj;ن، هیچ كه&zwnj;س به&zwnj;ر له ئێوه به&zwnj;كاری وا هه&zwnj;ڵنه&zwnj;ساوه!!!';
$t[29] = 'ئایه ئێوه به&zwnj;ڕاستتانه نێربازی ده&zwnj;كه&zwnj;ن و ڕێگا به&zwnj;خه&zwnj;ڵكی ده&zwnj;گرن و له یانه&zwnj;كه&zwnj;تانداكاری نا به&zwnj;جێ و ناشیرین و نادروست ئه&zwnj;نجام ده&zwnj;ده&zwnj;ن؟! كه&zwnj;چی وه&zwnj;ڵامی قه&zwnj;ومی ( به&zwnj;دخوو ) ته&zwnj;نها ئه&zwnj;وه&zwnj;بوو كه وتیان: ئاده&zwnj;ی كوا سزاو تۆڵه&zwnj;ی خوا، ئه&zwnj;گه&zwnj;ر تۆ ڕاست ده&zwnj;كه&zwnj;یت؟! بۆمان بیهێنه&zwnj;وه؟';
$t[30] = '( لوط دوعای لێكردن ) وتی: په&zwnj;روه&zwnj;ردگاره&zwnj;، سه&zwnj;ركه&zwnj;وتنم پێ ببه&zwnj;خشه به&zwnj;سه&zwnj;ر ئه&zwnj;م قه&zwnj;ومه&zwnj;ی كه تۆوی خراپه ده&zwnj;چینن.';
$t[31] = 'جا كاتێك كه فریشته&zwnj;كانمان گه&zwnj;یشتنه لای ئیبراهیم و مژده&zwnj;ی هاتنه دنیای ئیسحاقیان پێبه&zwnj;خشی، وتیان: به&zwnj;ڕاستی ئێمه له&zwnj;ناو به&zwnj;ری خه&zwnj;ڵكی ئه&zwnj;م شاره&zwnj;ین، چونكه بێگومان خه&zwnj;ڵكه&zwnj;كه&zwnj;ی زۆر سته&zwnj;مكار بوون.';
$t[32] = 'ئیبراهیم وتی: ئاخر ( لوط ) له&zwnj;وێیه&zwnj;، فریشته&zwnj;كان وتیان: ئێمه چاكتر ده&zwnj;زانین كێی لێیه&zwnj;، دڵنیابه كه ئه&zwnj;وو خاوو خێزانی ڕزگار ده&zwnj;كه&zwnj;ین جگه له هاوسه&zwnj;ره&zwnj;كه&zwnj;ی كه له تیاچووان ده&zwnj;بێت.';
$t[33] = 'جا كاتێك فریشته&zwnj;كان گه&zwnj;یشتنه لای ( لوط )، به هاتنیان زۆر دڵته&zwnj;نگ و غه&zwnj;مبار بوو، ( نه&zwnj;یده&zwnj;زانی كه فریشته&zwnj;ن ) سه&zwnj;ری دنیای لێ هاته&zwnj;وه یه&zwnj;ك، وتیان: مه&zwnj;ترسه&zwnj;و غه&zwnj;مت نه&zwnj;بێت ( ئه&zwnj;وانه ناتوانن ده&zwnj;ستدرێژی بكه&zwnj;نه سه&zwnj;ر ئێمه&zwnj;، ئێمه فریشته&zwnj;ین ) بێگومان ئێمه ڕزگاركه&zwnj;ری تۆو كه&zwnj;س و كاره&zwnj;كه&zwnj;تین جگه له هاوسه&zwnj;ره&zwnj;كه&zwnj;ت، كه له تیاچووانه.';
$t[34] = 'به&zwnj;ڕاستی ئێمه دابه&zwnj;زێنه&zwnj;ری سزاو به&zwnj;ڵای ئاسمانین به&zwnj;سه&zwnj;ر خه&zwnj;ڵكی ئه&zwnj;م شاره&zwnj;دا به&zwnj;هۆی ئه&zwnj;وه&zwnj;ی كه ده&zwnj;رده&zwnj;چوون له فه&zwnj;رمانی خوا.';
$t[35] = 'سوێند به&zwnj;خوا به&zwnj;ڕاستی سه&zwnj;رئه&zwnj;نجام ئاسه&zwnj;وارێكی ئاشكرامان ( له&zwnj;شاری كاول و وێران ) به&zwnj;جێهه&zwnj;شت بۆ كه&zwnj;سانێك كه ژیر و هۆشمه&zwnj;ند بن ( كه تێ بگه&zwnj;ن و سه&zwnj;رنج بده&zwnj;ن ).';
$t[36] = 'هه&zwnj;روه&zwnj;ها شوعه&zwnj;یبمان ڕه&zwnj;وانه كرد بۆ ناو براكانی له&zwnj;شاری مه&zwnj;دیه&zwnj;ن، پێی وتن: ئه&zwnj;ی قه&zwnj;وم و هۆزم ته&zwnj;نها خوا بپه&zwnj;رستن، به هیوای قیامه&zwnj;ت و ئایینده&zwnj;وه بژین و ترسی ته&zwnj;نگانه&zwnj;ی ئه&zwnj;و ڕۆژه&zwnj;تان هه&zwnj;بێت، گوناهو تاوان و خراپه له سه&zwnj;ر زه&zwnj;ویدا ئه&zwnj;نجام مه&zwnj;ده&zwnj;ن و مه&zwnj;چێنن ( چونكه زه&zwnj;وی بێ خاوه&zwnj;ن نییه و ژیانتان تا سه&zwnj;ر نیه ).';
$t[37] = 'له ئاكامدا خه&zwnj;ڵكه&zwnj;كه بڕوایان پێنه&zwnj;كرد، ئیتر بومه&zwnj;له&zwnj;رزه&zwnj;یه&zwnj;ك دووچاریان بوو، هه&zwnj;ر هه&zwnj;مویان له&zwnj;ماڵه&zwnj;كانیاندا به&zwnj;چۆكدا هاتن و مردن (بوون به ژێر خاك و خۆڵه&zwnj;وه ).';
$t[38] = 'هه&zwnj;روه&zwnj;ها ( عاد ) ی قه&zwnj;ومی ( هود ) و ( ثمود ) ی قه&zwnj;ومی ( صالح ) یشمان له&zwnj;ناو برد كه ئاشكرا بوو بۆتیاچوونیان له شوێنه&zwnj;واری كاولیانه&zwnj;وه&zwnj;، شه&zwnj;یتانیش كرده&zwnj;وه&zwnj;ی نادروست و ناپه&zwnj;سندیانی بۆ ڕازاندبونه&zwnj;وه&zwnj;، له ڕێگا و ڕێبازی ڕاست و دروست وێڵی كردبوون، له&zwnj;كاتێكدا ئه&zwnj;وان خاوه&zwnj;نی عه&zwnj;قڵ و بیر و هۆشیش بوون.';
$t[39] = 'قاروون و فیرعه&zwnj;ون و هامانیشمان له ناوبرد، سوێند به خوا بێگومان موسا به&zwnj;ڵگه&zwnj;ی زۆری بۆ هێنان، كه&zwnj;چی خۆیان زۆر به&zwnj;گه&zwnj;وره دانا له زه&zwnj;ویدا، سه&zwnj;رئه&zwnj;نجام نه&zwnj;یان توانی له سزاو تۆڵه&zwnj;ی خوا خۆیان ڕزگار بكه&zwnj;ن.';
$t[40] = 'ئێمه هه&zwnj;ر یه&zwnj;كێك یان هه&zwnj;ر ده&zwnj;سته&zwnj;یه&zwnj;ك له وانه&zwnj;مان به سزای تاوانی خۆی گه&zwnj;یاند، جا هه&zwnj;یان بوو به&zwnj;رد بارانمان كرد، هه&zwnj;یان بوو ده&zwnj;نگ و سه&zwnj;دای به هێز له&zwnj;ناوی برد، هه&zwnj;یان بوو به ناخی زه&zwnj;ویدا بردمانه خواره&zwnj;وه&zwnj;، هه&zwnj;شیان بوو نوقمی ده&zwnj;ریامان كرد، جا وه نه&zwnj;بێت خوا سته&zwnj;می لێ كردبن، به&zwnj;ڵك و خۆیان سته&zwnj;میان له خۆیان ده كرد.';
$t[41] = 'نمونه&zwnj;ی ئه&zwnj;وانه&zwnj;ی جگه له خوای گه&zwnj;وره&zwnj;، كه&zwnj;سانێكی تر ده&zwnj;كه&zwnj;نه پشتیوانی خۆیان، وه&zwnj;ك نمونه&zwnj;ی جاڵجاڵۆكه&zwnj;یه&zwnj;ك وایه كه ماڵێكی بۆخۆی سازكردبێت به&zwnj;ڕاستی لاوازترینی هه&zwnj;موو ماڵه&zwnj;كان، ماڵی جاڵجاڵۆكه&zwnj;یه ئه&zwnj;گه&zwnj;ر سه&zwnj;رنجیان بدایه و تێبگه&zwnj;یشتنایه ( چونكه ماڵی جاڵجاڵۆكه نه بناغه و نه دیواری هه&zwnj;یه&zwnj;، نه سه&zwnj;رو ده&zwnj;رگا و په&zwnj;نجه&zwnj;ره&zwnj;، نه ده&zwnj;توانێت به&zwnj;رگه&zwnj;ی با و باران و ڕوداوه&zwnj;كان بگرێت مێیه&zwnj;كه&zwnj;ش نێره&zwnj;كه له ناو ده&zwnj;بات دوای جووت بوون، پاشان بێچوه&zwnj;كانیش دایكیان له ناو ده&zwnj;به&zwnj;ن )!!!';
$t[42] = 'بێگومان خوا خۆی چاكده&zwnj;زانێت ئه&zwnj;وه&zwnj;ی ئه&zwnj;و خه&zwnj;ڵكه هاناو هاواری بۆ ده&zwnj;به&zwnj;ن له جیاتی ئه&zwnj;و، چه&zwnj;نده بێ ده&zwnj;سه&zwnj;ڵاتن، چونكه هه&zwnj;ر خۆی خواوه&zwnj;ن ده&zwnj;سه&zwnj;ڵاته و زۆریش دانایه ( له&zwnj;و بواره&zwnj;ی كه بۆ تاقیكردنه&zwnj;وه&zwnj;ی خه&zwnj;ڵكی ڕه&zwnj;خساندویه&zwnj;تی ).';
$t[43] = 'ئا ئه&zwnj;م نمونانه ئێمه ده&zwnj;یهێنینه&zwnj;وه بۆ خه&zwnj;ڵكی، ته&zwnj;نها زانا و شاره&zwnj;زاكان تێی ده&zwnj;گه&zwnj;ن و تێ ده&zwnj;فكرن.';
$t[44] = 'ئاشكرایه خوا ئاسمانه&zwnj;كان و زه&zwnj;وی به ڕێك و پێكترین شێوه دروست كردووه&zwnj;، به&zwnj;ڕاستی ئا له&zwnj;و كاره&zwnj;دا به&zwnj;ڵگه و نیشانه&zwnj;ی ته&zwnj;واو هه&zwnj;یه بۆ ئیمانداران.';
$t[45] = 'ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر ( ص ) ئه&zwnj;وه&zwnj;ی بۆت ڕه&zwnj;وانه ده&zwnj;كرێت له قورئان بیخوێنه&zwnj;ره&zwnj;وه و نوێژیش به&zwnj;چاكی ئه&zwnj;نجام بده&zwnj;، چونكه به&zwnj;ڕاستی نوێژ به&zwnj;رهه&zwnj;ڵستی له گوناهو تاوان و نادروستی ده&zwnj;كات، بێگومان یادی خوا له هه&zwnj;موو شت گه&zwnj;وره&zwnj;تر و به&zwnj;نرختره ( كه ده&zwnj;بێت له كاتی نوێژ و كاتی تریشدا بیر و هۆشی ئیماندار، دابگرێت ) و خوا خۆی زانایه به هه&zwnj;موو ئه&zwnj;و كار و كرده&zwnj;وانه&zwnj;ی كه ئه&zwnj;نجامی ده&zwnj;ده&zwnj;ن.';
$t[46] = 'گفتوگۆ و (مجادلـة) مه&zwnj;که&zwnj;ن له&zwnj;گه&zwnj;ڵ خاوه&zwnj;نی کتێبه ئاسمانیه&zwnj;کاندا مه&zwnj;گه&zwnj;ر به&zwnj;جوانترین شێوه نه&zwnj;بێت، جگه له&zwnj;وانه&zwnj;یان که سته&zwnj;میان کردووه (ئه&zwnj;وه ڕه&zwnj;فتارتان له به&zwnj;رامبه&zwnj;ریانه&zwnj;وه ده&zwnj;بێت جیاواز بێت) پێشیان بڵێن: ئێمه باوه&zwnj;ڕمان هێناوه به&zwnj;وه&zwnj;ی بۆ ئێمه و ئێوه دابه&zwnj;زێنراوه&zwnj;، خوای ئێمه و خوای ئێوه ته&zwnj;نها یه&zwnj;کێکه&zwnj;، ئێمه فه&zwnj;رمانبه&zwnj;رداری ئه&zwnj;و زاته&zwnj;ین.';
$t[47] = 'ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر (صلی الله علیه وسلم) هه&zwnj;ر به شێوه&zwnj;یه&zwnj;ی کتێبه&zwnj;کانی پێشوو ئێمه قورئانمان بۆ دابه&zwnj;زاندوویت، جا ئه&zwnj;وانه&zwnj;ی کتێبمان پێداون و به ڕاستی خوێندوویانه&zwnj;ته&zwnj;وه&zwnj;، ئه&zwnj;وه باوه&zwnj;ڕ ده&zwnj;هێنن به قورئان و هه&zwnj;ندێک له و خه&zwnj;ڵکه&zwnj;ی تریش هه&zwnj;ن باوه&zwnj;ڕ ده&zwnj;هێنن پێی و موسڵمان ده&zwnj;بن، جا که&zwnj;س نیه ئینکاری ئایه&zwnj;ت و به&zwnj;ڵگه و نیشانه&zwnj;ی ئێمه بکات (دوای لابردنی گومان) جگه له خوانه&zwnj;ناسان.';
$t[48] = 'تۆ ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر (صلی الله علیه وسلم) پێش ناردنی ئه&zwnj;م قورئانه هیچ کتێبێکت نه&zwnj;ده&zwnj;خوێنده&zwnj;وه&zwnj;، به ده&zwnj;ستی خۆیشت هیچ شتێکت نه&zwnj;نووسیووه&zwnj;، (چونکه نه&zwnj;خوێنده&zwnj;وار بوویت) خۆ ئه&zwnj;گه&zwnj;ر شتیکی وات ئه&zwnj;نجام بدایه&zwnj;، له&zwnj;و کاته&zwnj;دا نه&zwnj;فام و ده&zwnj;م به تاڵه&zwnj;کان دنیایان پڕ ده&zwnj;کرد له شک و گومان (که گوایه قورئان له دانان و داڕشتنی تۆیه&zwnj;).';
$t[49] = 'نه&zwnj;خێر هی تۆ نییه&zwnj;، به&zwnj;ڵکو قورئان چه&zwnj;ند ئایه&zwnj;تێکی ڕوون و ئاشکران له سێنه&zwnj;و دڵ و ده&zwnj;روونی ئه&zwnj;و که&zwnj;سانه&zwnj;دا عیلم و زانستیان پێبه&zwnj;خشراوه و له&zwnj;به&zwnj;ریانه&zwnj;، به&zwnj;ڕاستی جگه له سته&zwnj;مکاران، که&zwnj;س به&zwnj;رهه&zwnj;ڵستی و نکوڵی له ئایه&zwnj;ته&zwnj;کانی ئێمه نیه&zwnj;.';
$t[50] = 'کافران (وادیاره قورئان به به&zwnj;ڵگه دانانێن بۆیه&zwnj;) ده&zwnj;ڵێن: باشه چی ده&zwnj;بوو: ئه&zwnj;گه&zwnj;ر نیشانه و به&zwnj;ڵگه&zwnj;ی له&zwnj;لایه&zwnj;ن په&zwnj;روه&zwnj;ردگاریه&zwnj;وه بۆ ڕه&zwnj;وانه بکرایه&zwnj;، تۆش ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ری له وه&zwnj;ڵامیاندا بڵێ: موعجیزه و نیشانه و به&zwnj;ڵگه ماددیه&zwnj;کان به ده&zwnj;ست خوا خۆیه&zwnj;تی و من ته&zwnj;نها ئاگادارکه&zwnj;ره&zwnj;وه و بێدارکه&zwnj;ره&zwnj;وه&zwnj;یه&zwnj;کی ئاشکرام بۆتان.';
$t[51] = 'ئایا به&zwnj;س نیه بۆیان که به&zwnj;ڕاستی ئێمه قورئانمان دابه&zwnj;زاندووه بۆت و به&zwnj;سه&zwnj;ریاندا ده&zwnj;خوێنرێته&zwnj;وه&zwnj;، بێگومان ئا له&zwnj;و قورئانه دا ڕه&zwnj;حمه&zwnj;ت و یاداوه&zwnj;ری ئاشکرا هه&zwnj;یه بۆ ئه&zwnj;و که&zwnj;سانه&zwnj;ی که ئیمان ده&zwnj;هێنن.';
$t[52] = '(له&zwnj;وه&zwnj;ودوا ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر (صلی الله علیه وسلم) پێیان بڵێ: ته&zwnj;نها خوا به&zwnj;سه شایه&zwnj;ت بێت له نێوانماندا (که من په&zwnj;یامه&zwnj;که&zwnj;یم پێ ڕاگه&zwnj;یاندوون)، جا هه&zwnj;ر ئه&zwnj;و زاته زانا و ئاگایه به&zwnj;هه&zwnj;رچی له ئاسمانه&zwnj;کان و زه&zwnj;ویدا هه&zwnj;یه&zwnj;، ئه&zwnj;وانه&zwnj;ش باوه&zwnj;ڕ به ناحه&zwnj;ق و به تاڵ هێناوه و باوه&zwnj;ڕیان به&zwnj;خوای (تاک و ته&zwnj;نها) نه&zwnj;هێناوه&zwnj;، ئا ئه&zwnj;وانه هه&zwnj;ر خۆیان خه&zwnj;ساره&zwnj;تمه&zwnj;ند و زه&zwnj;ره&zwnj;رمه&zwnj;ندن.';
$t[53] = 'بێ باوه&zwnj;ڕان په&zwnj;له&zwnj;ت لێ ده&zwnj;که&zwnj;ن و داوای هاتنی سزات لێ ده&zwnj;که&zwnj;ن،، خۆ ئه&zwnj;گه&zwnj;ر کاتی تایبه&zwnj;تیمان دیاری نه&zwnj;کردایه بۆ پێشهاتنی، یه&zwnj;کسه&zwnj;ر سزایان بۆ ده&zwnj;هات، بێگومان ئه&zwnj;و سزاو تۆڵه&zwnj;یه&zwnj;، له ناکاو یه&zwnj;خه&zwnj;یان پێده&zwnj;گرێت و له&zwnj;کاتێکدا ئه&zwnj;وان هه&zwnj;ست به نزیک بوونه&zwnj;وه&zwnj;شی ناکه&zwnj;ن.';
$t[54] = 'بێ باوه&zwnj;ڕان په&zwnj;له&zwnj;ت لێ ده&zwnj;که&zwnj;ن و داوای هاتنی سزات لێده&zwnj;که&zwnj;ن، بێگومانیش دۆزه&zwnj;خ ده&zwnj;وری کافرانی داوه&zwnj;و هه&zwnj;رگیز له سزای ده&zwnj;رباز نابن...';
$t[55] = 'له&zwnj;ڕۆژێکدا که سزا دا&zwnj;یانده&zwnj;گرێت له سه&zwnj;رویانه&zwnj;وه&zwnj;، له ژێر پێیانه&zwnj;وه خوای گه&zwnj;وره پێیان ده&zwnj;فه&zwnj;رموێت: بچێژن سه&zwnj;رئه&zwnj;نجامی ئه&zwnj;و کارو کرده&zwnj;وانه&zwnj;ی که ئه&zwnj;نجامتان ده&zwnj;دا.';
$t[56] = '(په&zwnj;روه&zwnj;ردگار به&zwnj;ئیماندارانی به&zwnj;گیرهاتوو له&zwnj;ناو کافراندا ڕاده&zwnj;گه&zwnj;یه&zwnj;نێت): ئه&zwnj;ی به&zwnj;نده&zwnj;کانم زه&zwnj;وی من فراوانه&zwnj;، موڵکی من به&zwnj;ربڵاوه (کۆچ بکه&zwnj;ن، خۆتان ڕزگار بکه&zwnj;ن)، ته&zwnj;نها هه&zwnj;ر من بپه&zwnj;رستن.';
$t[57] = '(له&zwnj;وانه&zwnj;ئه ترسی مه&zwnj;رگ له ئارادا هه&zwnj;بێت بۆیه ترسیان ده&zwnj;ڕه&zwnj;وێنێه&zwnj;وه و ده&zwnj;فه&zwnj;رموێت): هه&zwnj;موو که&zwnj;س ده&zwnj;بێت مردن بچێژێت (که&zwnj;ی، له کوێ، به&zwnj;چی، خوا نه&zwnj;بێت که&zwnj;س نازانێت) له&zwnj;وه&zwnj;ودوا ته&zwnj;نها بۆ لای ده&zwnj;برێنه&zwnj;وه&zwnj;.';
$t[58] = 'جا ئه&zwnj;وانه&zwnj;ی که ئیمان و باوه&zwnj;ڕیان هێناوه و کارو کرده&zwnj;وه چاکه&zwnj;کانیان ئه&zwnj;نجامداوه&zwnj;، سوێند به خوا ئه&zwnj;وانه له به&zwnj;هه&zwnj;شتدا له چه&zwnj;نده&zwnj;ها کۆشک و ته&zwnj;لارو ژووری به&zwnj;رزدا داده&zwnj;به&zwnj;زێنین، که چه&zwnj;نده&zwnj;ها ڕووبارو تاڤگه به&zwnj;ژێر دره&zwnj;خته&zwnj;کانیدا و به به&zwnj;رده&zwnj;م کۆشک و ته&zwnj;لاره&zwnj;کانیدا ده&zwnj;ڕوات، (ئه&zwnj;و به&zwnj;خته&zwnj;وه&zwnj;رانه&zwnj;) ژیانی نه&zwnj;بڕاوه له&zwnj;و (شوێنه خۆشانه&zwnj;دا) ده&zwnj;به&zwnj;نه سه&zwnj;ر، ئای چه&zwnj;ند جوانه پاداشتی ئیشکه&zwnj;ران بۆ خوا...';
$t[59] = 'ئه&zwnj;وانه&zwnj;ن خۆگربوون له&zwnj;به&zwnj;رده&zwnj;م ئه&zwnj;شکه&zwnj;نجه و ئازاری بێ باوه&zwnj;ڕاندا و هه&zwnj;ر پشت به په&zwnj;روه&zwnj;ردگاریان ده&zwnj;به&zwnj;ستن...';
$t[60] = '(هه&zwnj;روه&zwnj;ها خوای گه&zwnj;وره ده&zwnj;یه&zwnj;وێت له گیروگرفتی ڕزق و ڕۆزیش دڵنیایان بکات؛ بۆیه ده&zwnj;فه&zwnj;رموێت): زۆر زینده&zwnj;وه&zwnj;ر هه&zwnj;یه ڕزق و ڕۆزی خۆیان نه&zwnj;داوه به&zwnj;کۆڵی خۆیاندا (که&zwnj;چی خوا په&zwnj;کیان ناخات و له برساندا نامرن)، چونکه خوا ڕزقی ئه&zwnj;وانیش و ئێوه&zwnj;ش ده&zwnj;دات، بێگومان ئه&zwnj;وزاته بیسه&zwnj;ر (به قسه و گوفتارتان) و زانایه (به&zwnj;خه&zwnj;یاڵی دڵ و ده&zwnj;روونتان).';
$t[61] = 'سوێند به خوا ئه&zwnj;گه&zwnj;ر پرسیار له بێ باوه&zwnj;ڕان بکه&zwnj;یت: کێ ئاسمانه&zwnj;کان و زه&zwnj;وی دروست کردووه&zwnj;، ڕۆژو مانگی ڕام کردووه&zwnj;، به&zwnj;دڵنیایی و بێ دوودڵی ده&zwnj;ڵێن: (الله&zwnj;) دروستی کردوون، جا چۆن لاده&zwnj;درێن له یه&zwnj;کتاپه&zwnj;رستی و فریو ده&zwnj;خۆن؟!';
$t[62] = 'هه&zwnj;ر خوایه ڕزق و ڕۆزی ده&zwnj;به&zwnj;خشێت به هه&zwnj;ر کام بیه&zwnj;وێت له به&zwnj;نده&zwnj;کانی، یان ده&zwnj;یگرێته&zwnj;وه و که&zwnj;می ده&zwnj;کاته&zwnj;وه (بۆ تاقی کردنه&zwnj;وه&zwnj;)، به&zwnj;ڕاستی خوا خۆی به&zwnj;هه&zwnj;موو شتێک زاناو ئاگایه (ده&zwnj;زانێت چ حاڵه&zwnj;تێک چاک و له&zwnj;باره بۆ هه&zwnj;ر به&zwnj;نده&zwnj;یه&zwnj;ک).';
$t[63] = 'خۆ ئه&zwnj;گه&zwnj;ر لێیان بپرسیت: کێ ئاو له ئاسمانه&zwnj;وه ده&zwnj;بارێنێت، جا زه&zwnj;وی پێ ده&zwnj;بوژێنێته&zwnj;وه&zwnj;، دوای ئه&zwnj;وه&zwnj;ی خامۆش و مردوو بوو، ئه&zwnj;وه به&zwnj;دڵنیاییه&zwnj;وه ده&zwnj;ڵێن.. (الله&zwnj;) (تۆ ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر (صلی الله علیه وسلام)، ئه&zwnj;ی ئیماندار) بڵێ: سوپاس بۆ خوا، که ئاوا، دان به ده&zwnj;سه&zwnj;ڵاتیدا ده&zwnj;نێن، نه&zwnj;خێر به&zwnj;ڕاستیا نیه&zwnj;، به&zwnj;ڵکو هێشتا زۆربه&zwnj;یان بێ بیرو هۆشن و ژیریان نا&zwnj;خه&zwnj;نه&zwnj;کار.';
$t[64] = 'ژیانی ئه&zwnj;م دنیایه جگه له گه&zwnj;مه و گاڵته و یاری شتێکی تر نیه&zwnj;... (واته بێ نرخه و ئه&zwnj;وه ناژی خه&zwnj;ڵکی ئاوا په&zwnj;لاماری ده&zwnj;ده&zwnj;ن و سته&zwnj;م له پێناویدا ده&zwnj;که&zwnj;ن) به&zwnj;ڕاستی ماڵی قیامه&zwnj;تیش و ژیانی ناو به&zwnj;هه&zwnj;شت، هه&zwnj;ر ئه&zwnj;وه ژیانه (شایسته&zwnj;ی ئه&zwnj;وه&zwnj;یه که ئاده&zwnj;میزاد هه&zwnj;وڵ و کۆششی بۆ بدات)، ئه&zwnj;گه&zwnj;ر بیانزانیایه و تێبگه&zwnj;یشتنایه&zwnj;.';
$t[65] = 'هه&zwnj;ر کاتێک خه&zwnj;ڵکی (باوه&zwnj;ڕ لاواز) سواری که&zwnj;شتی ده&zwnj;بن، مه&zwnj;ترسی نوقم بوون و تیاچوون ڕوویان تێده&zwnj;کات، زۆر به&zwnj;کوڵ، هاناو هاوار بۆ خوا ده&zwnj;به&zwnj;ن و به دڵسۆزی و ملکه&zwnj;چیه&zwnj;وه باوه&zwnj;ڕ ده&zwnj;هێنن، به&zwnj;ڵام کاتێک خوا ڕزگاریان ده&zwnj;کات و ده&zwnj;گه&zwnj;نه&zwnj;وه وشکانی ده&zwnj;ستبه&zwnj;جێ له ڕێبازی یه&zwnj;کتاپه&zwnj;رستی ده&zwnj;ترازێن و هاوه&zwnj;ڵ و هاوبه&zwnj;ش بۆ خوا بڕیار ده&zwnj;ده&zwnj;ن.';
$t[66] = 'جا با ئه&zwnj;وانه هه&zwnj;ر ناشوکرو به&zwnj;دنمه&zwnj;ک بن له به&zwnj;رامبه&zwnj;ر ئه&zwnj;و هه&zwnj;موو به&zwnj;خششانه&zwnj;ی ئێمه&zwnj;وه&zwnj;، با نازو نیعمه&zwnj;ته&zwnj;کانی خوا به&zwnj;کار بهێنن به&zwnj;و شێوازه&zwnj;ی که پێی ڕازی نیه&zwnj;، سه&zwnj;ره&zwnj;نجام له داهاتوودا خۆیان ده&zwnj;بیننه&zwnj;وه و به&zwnj;ئاگا دێنه&zwnj;وه&zwnj;..!';
$t[67] = '(باشه&zwnj;، خوانه&zwnj;ناسانی مه&zwnj;ککه&zwnj;) هه&zwnj;ست ناکه&zwnj;ن، نابینن که چۆن شوێنێکی ئه&zwnj;مین و پڕ له ئاسایشمان بۆ فه&zwnj;راهه&zwnj;م هێناون له&zwnj;کاتێکدا، له ده&zwnj;وروبه&zwnj;ریانه&zwnj;وه خه&zwnj;ڵکی ده&zwnj;فڕێنرێت، ئایا هه&zwnj;ر باوه&zwnj;ڕ به به&zwnj;تاڵ و ناحه&zwnj;قی ده&zwnj;هێنن و هه&zwnj;ست به نازو نیعمه&zwnj;ته&zwnj;کانی خوا ناکه&zwnj;ن و سپڵه&zwnj;یی ده&zwnj;که&zwnj;ن؟!';
$t[68] = 'جا ئیتر کێ له&zwnj;وه سته&zwnj;مکارتره که درۆ هه&zwnj;ڵده&zwnj;به&zwnj;سێت و هاوه&zwnj;ڵی بۆ بڕیار ده&zwnj;دات، یان ئه&zwnj;و حه&zwnj;ق و ڕاستیه&zwnj;ی که بۆی هاتووه به درۆی ده&zwnj;زانێت، مه&zwnj;گه&zwnj;ر هه&zwnj;ر دۆزه&zwnj;خ شایسته بێت، ئایا له دۆزه&zwnj;خدا جێگه و ڕێگه&zwnj;یه&zwnj;کی هه&zwnj;میشه&zwnj;یی بۆ بێ باوه&zwnj;ڕان دابین نه&zwnj;کراوه&zwnj;؟ (بێگومان ئاماده کراوه&zwnj;).';
$t[69] = 'ئه&zwnj;وانه&zwnj;ش له پێناوی ئێمه&zwnj;داو بۆ به&zwnj;ده&zwnj;ستهێنانی ڕه&zwnj;زامه&zwnj;ندی ئێمه&zwnj;؛ هه&zwnj;وڵ و کۆششیان کردووه و خۆیان ماندوو کردووه&zwnj;، سوێند به&zwnj;خوا به&zwnj;ڕاستی ئه&zwnj;وانه ڕێنموویی ده&zwnj;که&zwnj;ین بۆ هه&zwnj;موو ڕێگه&zwnj;یه&zwnj;کی چاک و دروستی خۆمان (که به&zwnj;ده&zwnj;ستهێنانی ڕه&zwnj;زامه&zwnj;ندی ئێمه&zwnj;ی له&zwnj;دوایه&zwnj;)، بێگومان خوای په&zwnj;روه&zwnj;ردگار هه&zwnj;میشه له&zwnj;گه&zwnj;ڵ جاکه&zwnj;کارانه&zwnj;.';
?>