<?php
$t = array();
$t[1] = '&zwnj;چه&zwnj;ند پیتێك له سه&zwnj;ره&zwnj;تای هه&zwnj;ندێ له سووره&zwnj;ته&zwnj;كانی قورئاندا هاتوون ژماره&zwnj;یان (١٤) پیته&zwnj;، نیوه&zwnj;ی كۆی پیته&zwnj;كانی زمانی عه&zwnj;ره&zwnj;بین، بۆ سه&zwnj;لماندنی (اعجاز) و مه&zwnj;زنی و گرنگی قورئان كه له توانای هیچ كه&zwnj;سدا نیه به&zwnj;و پیتانه كتێبێكی ئاوا بێ وێنه دابنێت، به&zwnj;راده&zwnj;یه&zwnj;ك هه&zwnj;تا ئه&zwnj;گه&zwnj;ر هه&zwnj;موو گرۆی ئاده&zwnj;میزاد و په&zwnj;ری كۆببنه&zwnj;وه بۆ ئه&zwnj;و مه&zwnj;به&zwnj;سته بێگومان ناتوانن، هه&zwnj;روه&zwnj;ها چه&zwnj;نده&zwnj;ها نهێنی تریشی تێدایه&zwnj;، زاناكان له هه&zwnj;ندێكی دواون، هه&zwnj;ندێكیشیان وتوویانه&zwnj;: هه&zwnj;ر خوا خۆی زانایه به نهێنی ئه&zwnj;و پیتانه&zwnj;.';
$t[2] = 'خوا زاتێکه جگه ئه&zwnj;و هیچ خوایه&zwnj;کی تر نی یه شایه&zwnj;نی په&zwnj;رستن بێت و ئه&zwnj;و هه&zwnj;میشه زیندووه و ڕاگرو سه&zwnj;رپه&zwnj;رشتیاری هه&zwnj;موو دروستکراوه&zwnj;کانیه&zwnj;تی.';
$t[3] = 'ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر (صلی الله علیه وسلم) خوا قورئانی بۆ تۆ به&zwnj;ڕاست و دروست (به چه&zwnj;ند قۆناغێک) دابه&zwnj;زاندووه&zwnj;، به&zwnj;ڕاست دانه&zwnj;ری سه&zwnj;رجه&zwnj;م کتێبه پێشووه&zwnj;کانی پیش خۆیه&zwnj;تی (پێش ده&zwnj;ستکاری کردنیان)، هه&zwnj;روه&zwnj;ها ته&zwnj;وراتی بۆ (موسا) و ئینجیلی بۆ (عیسا) ڕه&zwnj;وانه کردووه له پێش قورئاندا...';
$t[4] = 'له&zwnj;کاتێکدا هیدایه&zwnj;ت و ڕێنموویی به&zwnj;خشی خه&zwnj;ڵکی بوون، هه&zwnj;روه&zwnj;ها جیاکه&zwnj;ره&zwnj;وه&zwnj;ی حه&zwnj;ق و به&zwnj;تاڵی نارده خواره&zwnj;وه (که قورئانه&zwnj;)، بێگومان ئه&zwnj;وانه&zwnj;ی بڕوایان نه&zwnj;هێنا به ئایاته&zwnj;کانی خوا، بۆ ئه&zwnj;وان سزایه&zwnj;کی سه&zwnj;خت (له دواڕۆژدا) هه&zwnj;یه&zwnj;، بێگومان خوای گه&zwnj;وره باڵاده&zwnj;سته&zwnj;و خاوه&zwnj;نی تۆڵه&zwnj;و سزایه (له بێ باوه&zwnj;ڕان).';
$t[5] = 'بێگومان هیچ شتێک له خوا ون نابێت نه له زه&zwnj;وی و نه له ئاسماندا.';
$t[6] = 'ئه&zwnj;و خوایه زاتێکه نه&zwnj;خشه&zwnj;ی ڕووخسارتان له منداڵدانه&zwnj;کاندا ده&zwnj;کێشێت چۆن بیه&zwnj;وێت، هیچ په&zwnj;رستراوێکی به&zwnj;ڕاست نی یه جگه له&zwnj;و خوایه و ئه&zwnj;و زاته باڵاده&zwnj;ست و دانا و کاربه&zwnj;جێ یه&zwnj;.';
$t[7] = 'ئه&zwnj;و خوایه زاتێکه که قورئانی بۆ سه&zwnj;ر تۆ (ئه&zwnj;ی محمد (صلی الله علیه وسلم) نارده خواره&zwnj;وه&zwnj;، به&zwnj;شێک له ئایه&zwnj;ته&zwnj;کانی دامه&zwnj;زراو و پایه&zwnj;دارو ڕوون و ئاشکران، ئه&zwnj;وانه بنچینه&zwnj;و بناغه&zwnj;ی قورئانه&zwnj;که&zwnj;ن و هه&zwnj;ندێکی تریشی واتای جۆراوجۆر هه&zwnj;ڵده&zwnj;گرن و به ئاسانی نازانرێن، جا ئه&zwnj;وانه&zwnj;ی که له دڵیاندا لاری و لادان هه&zwnj;یه&zwnj;، حه&zwnj;ز له حه&zwnj;ق و ڕاستی ناکه&zwnj;ن، ئه&zwnj;وانه شوێنی ئه&zwnj;و ئایه&zwnj;تانه ده&zwnj;که&zwnj;ون که مانای جۆراو جۆر و فراوانیان هه&zwnj;یه&zwnj;، بۆ ئه&zwnj;وه&zwnj;ی فیتنه و ئاشوب و ناکۆکی و دووبه&zwnj;ره&zwnj;کی دروست بکه&zwnj;ن، یان بۆ ئه&zwnj;وه&zwnj;ی لێکدانه&zwnj;وه&zwnj;ی هه&zwnj;ڵه&zwnj;ی بۆ هه&zwnj;ڵبه&zwnj;ستن، له حاڵێکدا که لێکدانه&zwnj;وه&zwnj;ی ئه&zwnj;و جۆره ئایه&zwnj;تانه هه&zwnj;ر خوا خۆی به ته&zwnj;واوی ده&zwnj;یزانێت، جا ئه&zwnj;و که&zwnj;سانه&zwnj;ی که&zwnj;له زانستدا ڕۆچوون ده&zwnj;ڵێن: ئێمه باوه&zwnj;ڕی دامه&zwnj;زراومان به&zwnj;هه&zwnj;ر هه&zwnj;موو (ئه&zwnj;و ئایه&zwnj;تانه&zwnj;)هه&zwnj;یه که له&zwnj;لایه&zwnj;ن په&zwnj;روه&zwnj;ردگارمانه&zwnj;وه&zwnj;یه و فه&zwnj;رمایشی ئه&zwnj;وه&zwnj;، (له&zwnj;ڕاستیدا) بێجگه له خاوه&zwnj;ن بیر و ژیره&zwnj;کان یاداوه&zwnj;ری وه&zwnj;رناگرن و بیرناکه&zwnj;نه&zwnj;وه (له&zwnj;م قورئانه&zwnj;).';
$t[8] = '(ئه&zwnj;وانه ده&zwnj;ڵێن) په&zwnj;روه&zwnj;ردگارا.. دوای ئه&zwnj;وه&zwnj;ی که هیدایه&zwnj;ت و ڕێنمووییت کردین، دڵه&zwnj;کانمان لامه&zwnj;ده له ڕێگه&zwnj;ی ڕاست و له ڕه&zwnj;حمه&zwnj;ت و به&zwnj;خششی تایبه&zwnj;تی خۆت به&zwnj;هره&zwnj;وه&zwnj;رمان بکه&zwnj;، چونکه به&zwnj;ڕاستی تۆ به&zwnj;خشنده و به&zwnj;خشێنه&zwnj;ری.';
$t[9] = 'په&zwnj;روه&zwnj;ردگارا به&zwnj;ڕاستی تۆ کۆکه&zwnj;ره&zwnj;وه&zwnj;ی هه&zwnj;موو خه&zwnj;ڵکیت له ڕۆژێکدا که هیچ گومانی تێدانیه&zwnj;، بێگومان خوا (زاتێک نیه&zwnj;) که به&zwnj;ڵێنی خۆی نه&zwnj;باته سه&zwnj;ر....';
$t[10] = '(له&zwnj;و ڕۆژه&zwnj;دا) به&zwnj;ڕاستی ئه&zwnj;وانه&zwnj;ی که بێ باوه&zwnj;ڕن هه&zwnj;رگیز ماڵ و منداڵیان هیچ جۆره به&zwnj;رگریه&zwnj;کیان لێ ناکه&zwnj;ن له سزای خوا، ئا ئه&zwnj;وانه سووته&zwnj;مه&zwnj;نی ئاگری دۆزه&zwnj;خن.';
$t[11] = 'ئه&zwnj;وانه داب و نه&zwnj;ریتیان وه&zwnj;کو داب و نه&zwnj;ریتی داروده&zwnj;سته&zwnj;ی فیرعه&zwnj;ون و ئه&zwnj;وانه&zwnj;ی که پێشیان هاتوون وایه&zwnj;، ئایه&zwnj;ت و موعجیزه&zwnj;کانی ئێمه&zwnj;یان به&zwnj;درۆ دانا، خوایش ئه&zwnj;وانی به&zwnj;هۆی گوناهو تاوانیانه&zwnj;وه به&zwnj;توندی سزادا و گرفتاری کردن و خوا تۆڵه&zwnj;سێنه&zwnj;رێکی به&zwnj;هێز و به&zwnj;زه&zwnj;بره&zwnj;.';
$t[12] = '(ئه&zwnj;ی محمد (صلی الله علیه وسلم) بڵێ به&zwnj;وانه&zwnj;ی که بڕوایان نه&zwnj;هێناوه&zwnj;: سه&zwnj;رئه&zwnj;نجام تێکده&zwnj;شکێن و ژێرده&zwnj;ست ده&zwnj;خرێن (له دنیادا، له ڕۆژی دوایشدا) کۆ ده&zwnj;کرێنه&zwnj;وه و ڕاپێچ ده&zwnj;کرێن بۆ ناو دۆزه&zwnj;خ که ناخۆشترین جێگه&zwnj;یه&zwnj;.';
$t[13] = '(ئه&zwnj;ی محمد (صلی الله علیه وسلم) به&zwnj;و خه&zwnj;ڵکه بڵێ) بێگومان له&zwnj;و دوو ده&zwnj;سته&zwnj;یه&zwnj;ی که له (جه&zwnj;نگی به&zwnj;دردا) ڕوو به ڕووی یه&zwnj;کتر وه&zwnj;ستان نیشانه&zwnj;و په&zwnj;ندێکی گه&zwnj;وره بوو بۆتان، ده&zwnj;سته&zwnj;یه&zwnj;کیان له پێناوی خوادا ده&zwnj;جه&zwnj;نگان، ده&zwnj;سته&zwnj;که&zwnj;ی تریان بێ باوه&zwnj;ڕ بوون، ئیمانداران له&zwnj;به&zwnj;ر چاوی ئه&zwnj;واندا دوو ئه&zwnj;وه&zwnj;نده&zwnj;یان ده&zwnj;نواند (له مه&zwnj;یدانی جه&zwnj;نگدا به&zwnj;چاوی بینایی و بینین)، (خوا فریشته&zwnj;یشی دابه&zwnj;زاند بۆ یارمه&zwnj;تی ئیمانداران)، خوا پشتگیری (هه&zwnj;ر ده&zwnj;سته&zwnj;یه&zwnj;ک)و هه&zwnj;رکه&zwnj;سێک بیه&zwnj;وێت ده&zwnj;یکات، به&zwnj;ڕاستی ئا له&zwnj;و یارمه&zwnj;تیه&zwnj;دا په&zwnj;ند و ئامۆژگاری هه&zwnj;یه بۆ ئه&zwnj;وانه&zwnj;ی که بینایی خۆیان به&zwnj;کارده&zwnj;هێنن و به&zwnj;وردیی بیرده&zwnj;که&zwnj;نه&zwnj;وه (که چۆن کۆمه&zwnj;ڵێکی که&zwnj;م زاڵ بوون به&zwnj;سه&zwnj;ر کۆمه&zwnj;ڵێکی زۆردا).';
$t[14] = 'ئاره&zwnj;زووی ئافره&zwnj;ت و بوونی نه&zwnj;وه&zwnj;ی نێرینه و خاوه&zwnj;نێتی ئاڵتون و زێڕو زیووی زۆرو زه&zwnj;به&zwnj;نده و ئه&zwnj;سپی چاک و ڕه&zwnj;سه&zwnj;ن و (هۆکارهی هاتووچۆ) هه&zwnj;روه&zwnj;ها ماڵات و زه&zwnj;وی و زاری کشتوکاڵ بۆ خه&zwnj;ڵکی ڕازێنراوه&zwnj;ته&zwnj;وه&zwnj;، ئه&zwnj;وانه هه&zwnj;مووی نازو نیعمه&zwnj;تی ژیانی دنیان، خوایش (بۆ چاکان) شوێنی گه&zwnj;ڕانه&zwnj;وه&zwnj;ی ڕازاوه&zwnj;و خۆشی لای خۆی ئاماده کردووه (که به&zwnj;هه&zwnj;شته&zwnj;).';
$t[15] = '(ئه&zwnj;ی محمد (صلی الله علیه وسلم) به&zwnj;و خه&zwnj;ڵکه&zwnj;) بڵێ: ئایا هه&zwnj;واڵی چاکتر له&zwnj;و نازو نیعمه&zwnj;تانه&zwnj;تان پێ ڕابگه&zwnj;یه&zwnj;نم؟! (ئه&zwnj;ویش ئه&zwnj;مه&zwnj;یه که&zwnj;): بۆ ئه&zwnj;وانه&zwnj;ی پارێزکارو خواناسن، لای په&zwnj;روه&zwnj;ردگاریان باخه&zwnj;کانی به&zwnj;هه&zwnj;شت ئاماده&zwnj;یه بۆیان که&zwnj;: چه&zwnj;نده&zwnj;ها ڕووبار به&zwnj;ژێر دره&zwnj;خته&zwnj;کانیداو به&zwnj;به&zwnj;رده&zwnj;م کۆشکه&zwnj;کانیدا ده&zwnj;ڕوات، هاوڕێ له&zwnj;گه&zwnj;ڵ ژیانی نه&zwnj;بڕاوه له&zwnj;گه&zwnj;ڵ هاوسه&zwnj;رانی پاکیزه&zwnj;دا و به&zwnj;ده&zwnj;م هه&zwnj;ست کردنی به&zwnj;رده&zwnj;وام به&zwnj;ڕه&zwnj;زامه&zwnj;ندیی په&zwnj;روه&zwnj;ردگاری ئازیز، بێگومان ئه&zwnj;و خوایه بینایه به&zwnj;به&zwnj;نده&zwnj;کانی.';
$t[16] = 'ئه&zwnj;وانه&zwnj;ی که ده&zwnj;ڵێن: په&zwnj;روه&zwnj;ردگارا، به&zwnj;ڕاستی ئێمه ئیمان و باوه&zwnj;ڕمان هێناوه&zwnj;، ده له گوناهه&zwnj;کانمان خۆش ببه و له ئاگری دۆزه&zwnj;خ بمانپارێزه&zwnj;.';
$t[17] = '(جا ئه&zwnj;و ئیماندارانه&zwnj;، ئه&zwnj;مه سیفه&zwnj;تیانه&zwnj;) خۆگرو ئارامگرن (له به&zwnj;رامبه&zwnj;ر ناسۆر و ناخۆشیه&zwnj;کانی ژیانه&zwnj;وه&zwnj;)، ڕاستگۆو خواپه&zwnj;رستن، ماڵ و سامان ده&zwnj;به&zwnj;خشن و، له به&zwnj;ره&zwnj;به&zwnj;یانه&zwnj;کاندا داوای لێخۆشبوون (له په&zwnj;روه&zwnj;ردگاریان ده&zwnj;که&zwnj;ن چونکه واهه&zwnj;ست ده&zwnj;که&zwnj;ن که وه&zwnj;ک پێویست خواپه&zwnj;رستیان نه&zwnj;کردووه&zwnj;).';
$t[18] = 'خوای گه&zwnj;وره (له ڕێگه&zwnj;ی دروست کراوانیه&zwnj;وه&zwnj;، که هه&zwnj;مووی نیشانه&zwnj;ی ده&zwnj;سه&zwnj;ڵاتی ده&zwnj;نوێنێت) خۆی شایه&zwnj;تی داوه و ده&zwnj;ریخستووه و سه&zwnj;لماندویه&zwnj;تی که به&zwnj;ڕاستی جگه له&zwnj;و هیچ خوایه&zwnj;کی تر نی یه که شایانی په&zwnj;رستن بێت، هه&zwnj;روه&zwnj;ها فریشته&zwnj;کان و خاوه&zwnj;ن زانسته&zwnj;کانیش (له پێغه&zwnj;مبه&zwnj;ران و زانایان) که به&zwnj;ڕێگه&zwnj;یه&zwnj;کی ڕاست و دروست ده&zwnj;یسه&zwnj;لمێنن خوا هه&zwnj;میشه داد په&zwnj;روه&zwnj;ری ڕه&zwnj;هایه و جگه له&zwnj;وزاته خوایه&zwnj;کی تر نیه و ئه&zwnj;و خوایه&zwnj;کی باڵاده&zwnj;ست و دانایه&zwnj;.';
$t[19] = 'به&zwnj;ڕاستی دین و ئاینی په&zwnj;سه&zwnj;ند لای خوا ته&zwnj;نها ئاینی ئیسلامه&zwnj;، ئه&zwnj;وانه&zwnj;ش که کتێبه (ئاسمانیه&zwnj;کانیان) دراوه&zwnj;تێ، جیاوازیان له نێواندا په&zwnj;یدا نه&zwnj;بووه مه&zwnj;گه&zwnj;ر له دوای ئه&zwnj;وه&zwnj;ی زانستی و زانیاری ته&zwnj;واویان بۆ هاتووه (ده&zwnj;رباره&zwnj;ی ڕاستی محمد (صلی الله علیه وسلم) و ئیسلام)، ئه&zwnj;مه&zwnj;ش به&zwnj;هۆی دنیاپه&zwnj;رستی و حه&zwnj;سوودی و سته&zwnj;م کردنیان له نێو خۆیان (چونکه ڕازی نه&zwnj;بوون به پێغه&zwnj;مبه&zwnj;رێک له غه&zwnj;یری خۆیان)، جا ئه&zwnj;وه&zwnj;ی باوه&zwnj;ڕی به فه&zwnj;رمان و ئایه&zwnj;ته&zwnj;کانی خوا نه&zwnj;بێت، ئه&zwnj;وه بێگومان خوا به&zwnj;خێرایی حسابی خۆی ده&zwnj;کات له&zwnj;گه&zwnj;ڵیاندا (هه&zwnj;ر زوو به&zwnj;دۆزه&zwnj;خیان ده&zwnj;گه&zwnj;یه&zwnj;نێت).';
$t[20] = 'خۆ ئه&zwnj;گه&zwnj;ر موجاده&zwnj;له و گفتوگۆی سه&zwnj;ختیان له&zwnj;گه&zwnj;ڵدا کردیت، ئه&zwnj;ی محمد (صلی الله علیه وسلم) تۆس بڵێ: من و ئه&zwnj;وانه&zwnj;ی که شوێنم که&zwnj;وتوون ڕوومان کردۆته خواو ته&zwnj;نها ملکه&zwnj;چی ئه&zwnj;وین، به&zwnj;وانه&zwnj;ی که کتێبیان پێدراوه و ئه&zwnj;وانه&zwnj;ش نه&zwnj;خوێنده&zwnj;وارن له (موشریکانی عه&zwnj;ره&zwnj;ب) بڵێ: ئایا ئێوه ته&zwnj;سلیم بوون به&zwnj;خواو موسوڵمان بوون؟ خۆ ئه&zwnj;گه&zwnj;ر ته&zwnj;سلیم بوون، ئه&zwnj;وه مانای وایه به&zwnj;ڕاستی ئه&zwnj;وه هیدایه&zwnj;تیان وه&zwnj;رگرتووه&zwnj;، خۆ ئه&zwnj;گه&zwnj;ر پشت هه&zwnj;ڵکه&zwnj;ن و باوه&zwnj;ڕنه&zwnj;هێنن، ئه&zwnj;وه ته&zwnj;نها گه&zwnj;یاندنی په&zwnj;یامه&zwnj;که له&zwnj;سه&zwnj;رتۆیه&zwnj;، خوای گه&zwnj;وره&zwnj;ش بینایه به&zwnj;به&zwnj;نده&zwnj;کانی.';
$t[21] = 'به&zwnj;ڕاستی ئه&zwnj;وانه&zwnj;ی باوه&zwnj;ڕ ناهێنن به&zwnj;ئایه&zwnj;ت و فه&zwnj;رمانه&zwnj;کانی خوا و پێغه&zwnj;مبه&zwnj;رانیش به&zwnj;بێ هیچ حه&zwnj;قێک ده&zwnj;کوژن و ئه&zwnj;و خه&zwnj;ڵکانه&zwnj;ش ده&zwnj;کوژن که فه&zwnj;رمان به دادپه&zwnj;روه&zwnj;ری ده&zwnj;ده&zwnj;ن، که&zwnj;واته مژده&zwnj;ی سزایه&zwnj;کی پڕ ئێشیان پێ بده&zwnj;!!';
$t[22] = 'ئا ئه&zwnj;وانه که&zwnj;سانێکن کرده&zwnj;وه&zwnj;کانیان پووچ بۆته&zwnj;وه له دنیاو قیامه&zwnj;تدا، که&zwnj;سیش نیه پشتیوانی له&zwnj;وان بکات.';
$t[23] = 'ئایا نابینی ئه&zwnj;و که&zwnj;سانه&zwnj;ی به&zwnj;شێک له کتێبی ئاسمانیان پێدراوه کاتێک بانگ ده&zwnj;کرێن بۆ لای کتێبی خوا (ته&zwnj;ورات) تا فه&zwnj;رمانڕه&zwnj;وایی بکات له نێوانیاندا، پاشان ده&zwnj;سته&zwnj;یه&zwnj;کیان پشت هه&zwnj;ڵده&zwnj;که&zwnj;ن و له کاتێکدا ئه&zwnj;وان سه&zwnj;رپێچیکه&zwnj;رن.';
$t[24] = 'بۆیه&zwnj;ش (واده&zwnj;که&zwnj;ن) چونکه به&zwnj;ڕاستی ئه&zwnj;وان ده&zwnj;یانووت: هه&zwnj;رگیز ئاگری دۆزه&zwnj;خ چه&zwnj;ند ڕۆژێکی که&zwnj;م نه&zwnj;بێت به&zwnj;ر له&zwnj;شمان ناکه&zwnj;وێت و ئه&zwnj;وه&zwnj;ی له ئاینه&zwnj;کانیاندا بۆ خۆیان هه&zwnj;ڵبه&zwnj;ستبوو، سه&zwnj;ری لێ شێواندن و ته&zwnj;فره&zwnj;ی دان.';
$t[25] = 'ئاخۆ حاڵیان چۆن بێت کاتێ له&zwnj;و ڕۆژه&zwnj;دا کۆیان ده&zwnj;که&zwnj;ینه&zwnj;وه که هیچ گومانێک له هاتنیدا نیه و هه&zwnj;موو که&zwnj;س پاداشتی کارو کرده&zwnj;وه&zwnj;ی خۆی پێده&zwnj;درێت، له&zwnj;کاتێکدا ئه&zwnj;وان سته&zwnj;میان لێناکرێت.';
$t[26] = '(ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر (صلی الله علیه وسلم)، ئه&zwnj;ی ئیماندار) بڵێ: خوایه تۆ خاوه&zwnj;نی هه&zwnj;موو شتێکیت، ده&zwnj;سه&zwnj;ڵات ده&zwnj;به&zwnj;خشیت به&zwnj;هه&zwnj;ر که&zwnj;سێک بته&zwnj;وێت و ده&zwnj;سه&zwnj;ڵات وه&zwnj;رده&zwnj;گرێته&zwnj;وه له هه&zwnj;ر که&zwnj;سێک بته&zwnj;وێت هه&zwnj;ر که&zwnj;س بته&zwnj;وێت باڵاده&zwnj;ستی ده&zwnj;که&zwnj;یت، هه&zwnj;رکه&zwnj;سێکیش بته&zwnj;وێت زه&zwnj;لیل و بێ ده&zwnj;سه&zwnj;ڵات و ژێربه&zwnj;ده&zwnj;ستی ده&zwnj;که&zwnj;یت، هه&zwnj;رچی خێر هه&zwnj;یه هه&zwnj;ر به ده&zwnj;ست تۆیه&zwnj;، بێگومان تۆ ده&zwnj;سه&zwnj;ڵاتداری به&zwnj;سه&zwnj;ر هه&zwnj;موو شتێکدا.';
$t[27] = 'شه&zwnj;و تێکهه&zwnj;ڵکێشی ڕۆژ ده&zwnj;که&zwnj;یت، ڕۆژ تێکهه&zwnj;ڵکێشی شه&zwnj;و ده&zwnj;که&zwnj;یت، زیندوو له مردوو ده&zwnj;ردێنیت، مردوو له زیندوو ده&zwnj;ردێنیت، ڕزق و ڕۆزی بێ حساب و بێ شومار ده&zwnj;به&zwnj;خشیت به هه&zwnj;ر که&zwnj;س بته&zwnj;وێت (کاره&zwnj;کانت هه&zwnj;ر هه&zwnj;مووی پڕه له حیکمه&zwnj;ت و دانایی، به&zwnj;نده&zwnj;کانت سه&zwnj;رسام و سه&zwnj;ر سووڕماوون له که&zwnj;ماڵی تۆ).';
$t[28] = 'نابێت ئیمانداران، کافران بکه&zwnj;نه پشت و په&zwnj;ناو یارو یاوه&zwnj;ری خۆیان له&zwnj;جیاتی ئیمانداران، ئه&zwnj;وه&zwnj;ی کاری وا بکات، ئه&zwnj;وکاره&zwnj;ی ده&zwnj;بێته هۆی ئه&zwnj;وه&zwnj;ی هیچ په&zwnj;یوه&zwnj;ندییه&zwnj;کی ئیمانیی به&zwnj;خواوه نه&zwnj;مێنێت، مه&zwnj;گه&zwnj;ر له&zwnj;به&zwnj;ر ئه&zwnj;وه&zwnj;ی خۆتان به بێگومان له شه&zwnj;ڕیان بپارێزن و قوتاربن (ئه&zwnj;ویش به&zwnj;وه&zwnj;ی به&zwnj;ده&zwnj;م ڕازییان بکه&zwnj;ن، نه&zwnj;ک به&zwnj;دڵ و کرده&zwnj;وه&zwnj;)، خوای گه&zwnj;وره&zwnj;ش بێدارتان ده&zwnj;کاته&zwnj;وه به&zwnj;سیفه&zwnj;تی تۆڵه&zwnj;سه&zwnj;ندنه&zwnj;وه&zwnj;ی خۆی ده&zwnj;تانترسێنێت (تا کاری ناڕه&zwnj;وای وانه&zwnj;که&zwnj;ن)، سه&zwnj;رئه&zwnj;نجام و گه&zwnj;ڕانه&zwnj;وه&zwnj;تان هه&zwnj;ر بۆ لای خوایه&zwnj;.';
$t[29] = 'پێیان بڵێ: ئه&zwnj;گه&zwnj;ر ئه&zwnj;وه&zwnj;ی له سینه&zwnj;تاندا جێگیره بیشارنه&zwnj;وه یاخود ئاشکرای بکه&zwnj;ن، ئه&zwnj;وه خوا پێی ده&zwnj;زانێت، هه&zwnj;رچیش له ئاسمانه&zwnj;کان و زه&zwnj;ویدا هه&zwnj;یه هه&zwnj;ر پێی ده&zwnj;زانێت و خوا ده&zwnj;سه&zwnj;ڵاتی به&zwnj;سه&zwnj;ر هه&zwnj;موو شتێکدا هه&zwnj;یه&zwnj;.';
$t[30] = 'ڕۆژێک دێت هه&zwnj;مووکه&zwnj;س، کاروکرده&zwnj;وه&zwnj;کانی خۆی ده&zwnj;بینێت ئاماده کراون به&zwnj;&zwnj;خێرو شه&zwnj;ڕیه&zwnj;وه&zwnj;، تاوانبار ئاوات ده&zwnj;خوازێت که له نێوان خۆی و کرده&zwnj;وه خراپه&zwnj;کانیدا مه&zwnj;ودایه&zwnj;کی دوورو درێژ هه&zwnj;بوایه&zwnj;، خوای گه&zwnj;وره&zwnj;ش بێدارتان ده&zwnj;کاته&zwnj;وه به سیفه&zwnj;تی تۆڵه&zwnj;سه&zwnj;ندنه&zwnj;وه&zwnj;ی خۆی ده&zwnj;تانترسێنێت (له&zwnj;وه&zwnj;ی که کاری ناڕه&zwnj;وا بکه&zwnj;ن) و خوا به&zwnj;سۆز و به به&zwnj;زه&zwnj;ییه بۆ ئه&zwnj;و به&zwnj;ندانه&zwnj;ی (که به&zwnj;م ئاگاداریانه دڵیان ڕاده&zwnj;چڵه&zwnj;کێت پێش هاتنی ئه&zwnj;و ڕۆژه&zwnj;).';
$t[31] = '(ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر (صلی الله علیه وسلم) به ئیمانداران) بڵێ: ئه&zwnj;گه&zwnj;ر ئێوه خواتان خۆش ده&zwnj;وێت ده شوێنی من بکه&zwnj;ون و له فه&zwnj;رمانی من ده&zwnj;رمه&zwnj;چن، ئه&zwnj;وکاته خوایش ئێوه&zwnj;ی خۆش ده&zwnj;وێت و له گوناهو هه&zwnj;ڵه&zwnj;کانیشتان خۆش ده&zwnj;بێت، ئه&zwnj;و خوایه لێخۆشبوو و میهره&zwnj;بانه&zwnj;.';
$t[32] = '(دووباره&zwnj;) پێیان بڵێ: فه&zwnj;رمانبه&zwnj;رداری خواو پێغه&zwnj;مبه&zwnj;ر بن، خۆ ئه&zwnj;گه&zwnj;ر سه&zwnj;پێچی بکه&zwnj;ن و پشتی تێبکه&zwnj;ن ئه&zwnj;وه دڵنیابن خوا بێ باوه&zwnj;ڕانی خۆش ناوێت.';
$t[33] = 'به&zwnj;ڕاستی خوا ئاده&zwnj;م و نوح و نه&zwnj;وه&zwnj;کانی ئیبراهیم و نه&zwnj;وه&zwnj;کانی عیمرانی هه&zwnj;ڵبژاردووه&zwnj;و ڕێزی داوون به&zwnj;سه&zwnj;ر خه&zwnj;ڵکی هه&zwnj;موو جیهاندا.';
$t[34] = 'ئه&zwnj;وانه نه&zwnj;وه&zwnj;یه&zwnj;کن یه&zwnj;ک ڕه&zwnj;چه&zwnj;ڵن، هه&zwnj;ندێکیان له هه&zwnj;ندێکی تریان بوون، خوا خۆی بیسه&zwnj;رو زانایه (چاک ده&zwnj;یزانی که شایسته&zwnj;ن بۆ ئه&zwnj;و کاره پیرۆزه&zwnj;).';
$t[35] = '(به&zwnj;یاد بێنه&zwnj;) کاتێک هاوسه&zwnj;ری عیمران وتی: په&zwnj;روه&zwnj;ردگارا؛ به&zwnj;ڕاستی من بڕیارم داوه&zwnj;و نه&zwnj;زرم کردووه ئه&zwnj;و منداڵه&zwnj;ی له سکمدایه ئازاد کراوبێت له هه&zwnj;موو ئه&zwnj;رک و ئیش و کارێک (تا هه&zwnj;ر له خزماتی بیت المقدس دا بێت) ده تۆش لێم وه&zwnj;ربگره&zwnj;، چونکه به&zwnj;ڕاستی تۆ خوایه&zwnj;کی بیسه&zwnj;رو زانایت.';
$t[36] = 'به&zwnj;ڵام کاتێک منداڵه&zwnj;که&zwnj;ی بوو (به دڵشکاویه&zwnj;وه&zwnj;) وتی: په&zwnj;روه&zwnj;ردگارا خۆ من کچم بوو، بێگومان خوا خۆی چاک ده&zwnj;زانێت که چی بووه&zwnj;، (خۆ ئاشکرایه&zwnj;) کوڕیش وه&zwnj;کو نی یه (له&zwnj;گه&zwnj;لێ ڕووه&zwnj;وه و هه&zwnj;ریه&zwnj;که ئه&zwnj;رک و فرمانی خۆی هه&zwnj;یه&zwnj;، ئه&zwnj;وه&zwnj;ی به ئافره&zwnj;ت ده&zwnj;کرێت به&zwnj;پیاو ناکرێت، به&zwnj;پێچه&zwnj;وانه&zwnj;شه&zwnj;وه&zwnj;، ژن و پیاو ته&zwnj;واوکاری یه&zwnj;کترن و هیچیان لای خوا که&zwnj;م ڕێزنین ئه&zwnj;رکی خزمه&zwnj;تکاری بیت المقدسیش به&zwnj;و ناکرێت)، منیش به&zwnj;ڕاستی ناوم نا مه&zwnj;ریه&zwnj;م و من ئه&zwnj;یپارێزم به&zwnj;تۆ خۆی و وه&zwnj;چه&zwnj;که&zwnj;ی له شه&zwnj;یتانی ڕه&zwnj;جم کراو.';
$t[37] = 'ئینجا په&zwnj;روه&zwnj;ردگاری به&zwnj;شێوه&zwnj;یه&zwnj;کی جوان و په&zwnj;سه&zwnj;ند مه&zwnj;ریه&zwnj;می وه&zwnj;رگرت، زۆر به&zwnj;جوانی پێی گه&zwnj;یاند، زه&zwnj;که&zwnj;ریای کرده مامۆستاو سه&zwnj;رپه&zwnj;رشتی (که هه&zwnj;م خزم و هه&zwnj;م پێغه&zwnj;مبه&zwnj;ر بوو، تا به&zwnj;چاکی دینداری فێر بکات، پاشان که مه&zwnj;ریه&zwnj;م گه&zwnj;وره بوو) هه&zwnj;ر کاتێک زه&zwnj;که&zwnj;ریا ده&zwnj;چوو بۆ سه&zwnj;ردانی له شوێنی خواپه&zwnj;رستیه&zwnj;که&zwnj;یدا ده&zwnj;یبینی ڕزق و ڕۆزیی و نازو نیعمه&zwnj;تی جۆراوجۆری لایه&zwnj;، زه&zwnj;که&zwnj;ریا: وتی ئه&zwnj;ی مه&zwnj;ریه&zwnj;م ئه&zwnj;م هه&zwnj;موو ڕزق و ڕۆزییه&zwnj;ت له&zwnj;کوێ بوو؟!، مه&zwnj;ریه&zwnj;م له وه&zwnj;ڵامیدا وتی: ئه&zwnj;وه له لایه&zwnj;ن خواوه&zwnj;یه&zwnj;، ئه&zwnj;و بۆم ده&zwnj;نێرێت، به&zwnj;ڕاستی خوا به هه&zwnj;ر که&zwnj;س بیه&zwnj;وێت ڕزق و ڕۆزیی بێ سنوورو بێ هۆکار ده&zwnj;به&zwnj;خشێت.';
$t[38] = 'ئا له&zwnj;و کات و شوێنه&zwnj;دا، زه&zwnj;که&zwnj;ریا هانا و نزای بۆ په&zwnj;روه&zwnj;ردگاری لێ به&zwnj;رز بۆوه و وتی: په&zwnj;روه&zwnj;ردگارا نه&zwnj;وه&zwnj;ی چاک و پاکم پێ ببه&zwnj;خشه&zwnj;، به&zwnj;ڕاستی تۆ بیسه&zwnj;ری دوعا و نزایت.';
$t[39] = 'جا یه&zwnj;کسه&zwnj;ر فریشته جبرئیل بانگی کرد له&zwnj;کاتێکدا زه&zwnj;که&zwnj;ریا له میحرابه&zwnj;که&zwnj;دا وه&zwnj;ستا بوو، نوێژی ده&zwnj;کرد، (وتی) به&zwnj;ڕاستی خوای گه&zwnj;وره مژده&zwnj;ت ده&zwnj;داتێ به به&zwnj;خشینی کوڕێک ناوی یه&zwnj;حیایه&zwnj;، به&zwnj;ڕاستدانه&zwnj;ری فه&zwnj;رمانی تایبه&zwnj;تی خوایه و گه&zwnj;وره و ڕێزداره و خۆپارێزه له گوناهان و پێغه&zwnj;مبه&zwnj;رێکه له چاکان.';
$t[40] = 'زاکه&zwnj;ریا وتی: په&zwnj;روه&zwnj;ردگارا؛ ئاخۆ چۆن منداڵم پێ ده&zwnj;به&zwnj;خشیت خۆ من پیری ته&zwnj;نگی پێ هه&zwnj;ڵچنیووم، هاوسه&zwnj;ره&zwnj;که&zwnj;شم سک ناکات، خوای گه&zwnj;وره&zwnj;ش فه&zwnj;رمووی: کاری خوا هه&zwnj;ر ئاوایه و هه&zwnj;ر چیه&zwnj;کی بوێت، ده&zwnj;یکات.';
$t[41] = 'ئینجا زه&zwnj;که&zwnj;ریا وتی: په&zwnj;روه&zwnj;ردگارا نیشانه&zwnj;یه&zwnj;کم بۆ دیاری بکه تا دڵنیابم که خێزانم سکی بووه&zwnj;، خوای گه&zwnj;وره فه&zwnj;رمووی: نیشانه&zwnj;که&zwnj;ت ئه&zwnj;وه&zwnj;یه&zwnj;که توانای قسه کردنت تا سێ ڕۆژ له&zwnj;گه&zwnj;ڵ خه&zwnj;ڵکیدا نابێت (زمانت ده&zwnj;به&zwnj;سترێت) مه&zwnj;گه&zwnj;ر به ئیشاره&zwnj;ت و ئاماژه نه&zwnj;بێت، تۆش زۆر یادی په&zwnj;روه&zwnj;ردگارت و ته&zwnj;سبیحات و ستایشی بکه به&zwnj;تایبه&zwnj;تی له ده&zwnj;مه و ئێواره و به&zwnj;ره&zwnj;به&zwnj;یاندا.';
$t[42] = '(هه&zwnj;روه&zwnj;ها یادیان بهێنه کاتێک) جبرئیلی فریشته وتی: ئه&zwnj;ی مه&zwnj;ریه&zwnj;م به&zwnj;ڕاستی خوا تۆی هه&zwnj;ڵبژاردوه و خاوێن و پاکیزه&zwnj;ی کردویت، هه&zwnj;ڵی بژاردویت و ڕێزی داویت به&zwnj;سه&zwnj;ر هه&zwnj;موو ئافره&zwnj;تانی جیهاندا.';
$t[43] = 'ئه&zwnj;ی مه&zwnj;ریه&zwnj;م ملکه&zwnj;چ و فه&zwnj;رمانبه&zwnj;رداری په&zwnj;روه&zwnj;ردگارت به&zwnj;، سوژده و کڕنووشی بۆ ببه له&zwnj;گه&zwnj;ڵ کڕنووشبه&zwnj;راندا.';
$t[44] = 'ئه&zwnj;وشتانه&zwnj;ی باسکران چه&zwnj;ند هه&zwnj;واڵێک بوون له غه&zwnj;یبه&zwnj;وه&zwnj;، ئێمه له&zwnj;ڕێگه&zwnj;ی (وه&zwnj;حی) و سروشه&zwnj;وه به&zwnj;تۆی ده&zwnj;گه&zwnj;یه&zwnj;نین، خۆ تۆ (ئه&zwnj;ی محمد (صلی الله علیه وسلم) له&zwnj;وێ نه&zwnj;بوویت کاتێک پێنوسه&zwnj;کانیان هه&zwnj;ڵئه&zwnj;دا&zwnj;یه (ئاوه&zwnj;وه&zwnj;، پێنوسی کێ سه&zwnj;رکه&zwnj;وت و ئاو نه&zwnj;یبرد) ئه&zwnj;وه&zwnj;یان مه&zwnj;ریه&zwnj;م په&zwnj;روه&zwnj;رده بکات، تۆ له&zwnj;وێش نه&zwnj;بوویت کاتێک کێشه&zwnj;یان هه&zwnj;بوو (له&zwnj;سه&zwnj;ر ئه&zwnj;وه&zwnj;ی کێ به&zwnj;خێوی بکات).';
$t[45] = '(هه&zwnj;روه&zwnj;ها یادیان بهێنه&zwnj;) کاتێک فریشه&zwnj;که به مه&zwnj;ریه&zwnj;می وت: ئه&zwnj;ی مه&zwnj;ریه&zwnj;م به&zwnj;ڕاستی خوا مژده&zwnj;ت ده&zwnj;داتێ: به وشه&zwnj;یه&zwnj;کی خۆی (که فرمانی ـ کن فیکون ـ ه&zwnj;، ڕۆڵه&zwnj;یه&zwnj;کت پێ ببه&zwnj;خشێت) که ناوی (المسیح)ه، عیسای کوڕی مه&zwnj;ریه&zwnj;مه&zwnj;، که پیاو ماقووڵ و پایه به&zwnj;رزه له&zwnj;م دنیادا، له قیامه&zwnj;تیشدا له ده&zwnj;سته&zwnj;ی نزیکانه (له ئێمه&zwnj;وه&zwnj;).';
$t[46] = 'جا عیسا له بێشکه&zwnj;داو به&zwnj;گه&zwnj;وره&zwnj;ییش قسه ده&zwnj;کات بۆ خه&zwnj;ڵکی، له پیاو چاکانیشه&zwnj;.';
$t[47] = 'ئه&zwnj;وسا مه&zwnj;ریه&zwnj;م وتی: په&zwnj;روه&zwnj;ردگارا، جا چۆن من منداڵم ده&zwnj;بێت: خۆ که&zwnj;س توخنم نه&zwnj;که&zwnj;وتووه&zwnj;، (جوبره&zwnj;ئیل فه&zwnj;رمووی): ویست و فه&zwnj;رمانی خوا هه&zwnj;روایه و چی بوێت، دروستی ده&zwnj;کات، هه&zwnj;ر کاتێک شتێکی بوێت ته&zwnj;نها پێی بڵێ ببه&zwnj;، ده&zwnj;ستبه&zwnj;جێ ده&zwnj;بێت.';
$t[48] = 'هه&zwnj;روه&zwnj;ها خوای گه&zwnj;وره فێری کتێب و نووسین و دانایی و ته&zwnj;ورات و ئینجیلی ده&zwnj;کات.';
$t[49] = '(له ئاینده&zwnj;دا ڕایده&zwnj;گه&zwnj;یه&zwnj;نێت) به&zwnj;نه&zwnj;وه&zwnj;ی ئیسرائیل که من پێغه&zwnj;مبه&zwnj;رێکم، بۆ لای ئێوه ڕه&zwnj;وانه&zwnj;کراوم، به&zwnj;ڵگه و نیشانه&zwnj;یه&zwnj;کیشم پێ یه له لایه&zwnj;ن په&zwnj;روه&zwnj;ردگارتانه&zwnj;وه&zwnj;، (ئه&zwnj;ویش ئه&zwnj;مه&zwnj;یه که&zwnj;): من له قوڕ شێوه&zwnj;ی باڵنده&zwnj;تان بۆ دروست ده&zwnj;که&zwnj;م، ئینجا فووی پێدا ده&zwnj;که&zwnj;م، به&zwnj;فرمانی خوا یه&zwnj;کسه&zwnj;ر، ده&zwnj;بێته باڵنده&zwnj;یه&zwnj;کی زیندوو و کوێری زکماک و نه&zwnj;خۆشی به&zwnj;ڵه&zwnj;کیی چاک ده&zwnj;که&zwnj;مه&zwnj;وه به&zwnj;ویستی خوا و مردووانیش زیندوو ده&zwnj;که&zwnj;مه&zwnj;وه به&zwnj;فرمانی خوا و هه&zwnj;واڵی ئه&zwnj;و شتانه&zwnj;ی که ده&zwnj;یخۆن و هه&zwnj;ڵی ده&zwnj;گرن له ماڵه&zwnj;کانتاندا پێتان ڕاده&zwnj;گه&zwnj;یه&zwnj;نم، به&zwnj;ڕاستی له&zwnj;و شتانه&zwnj;دا به&zwnj;ڵگه&zwnj;و نیشانه هه&zwnj;یه بۆتان ئه&zwnj;گه&zwnj;ر ئیماندارن.';
$t[50] = 'هه&zwnj;روه&zwnj;ها به&zwnj;ڕاستدانه&zwnj;ری ئه&zwnj;و ته&zwnj;وراته&zwnj;م که له&zwnj;به&zwnj;ر ده&zwnj;ستمدایه&zwnj;، هاتووم بۆ ئه&zwnj;وه&zwnj;ی هه&zwnj;ندێک له&zwnj;و شتانه&zwnj;ی له&zwnj;سه&zwnj;رتان حه&zwnj;رامکراوه (به&zwnj;فه&zwnj;رمانی خوا) حه&zwnj;ڵاڵی بکه&zwnj;م و به&zwnj;ڵگه&zwnj;شم بۆ هێناون له&zwnj;لایه&zwnj;ن په&zwnj;روه&zwnj;ردگارتانه&zwnj;وه (له&zwnj;سه&zwnj;ر ڕاستی پێغه&zwnj;مبه&zwnj;رایه&zwnj;تیم) که&zwnj;واته له خوا بترسن و فه&zwnj;رمانبه&zwnj;رداری من بن و به قسه&zwnj;م بکه&zwnj;ن.';
$t[51] = 'به&zwnj;ڕاستی هه&zwnj;ر خوا په&zwnj;روه&zwnj;ردگاری منیش و ئێوه&zwnj;شه&zwnj;، که&zwnj;واته ته&zwnj;نها ئه&zwnj;و بپه&zwnj;رستن و ملکه&zwnj;چی ئه&zwnj;و بن، ئه&zwnj;مه&zwnj;یه ڕێبازی ڕاست و دروست.';
$t[52] = 'جا کاتێک عیسا هه&zwnj;ستی به کوفرو بێ دینی ئه&zwnj;وانه کرد وتی: کێ پشتیوانمه بۆ لای خوا، (کێ دێت تا پێکه&zwnj;وه به&zwnj;رنامه&zwnj;ی خوا بگه&zwnj;یه&zwnj;نین)، حه&zwnj;وارییه&zwnj;کان وتیان: ئێمه&zwnj;ین پشتیوانانی دین و ئاینی خوا، باوه&zwnj;ڕی ته&zwnj;واومان به&zwnj;و خوایه هێناوه&zwnj;، تۆش شایه&zwnj;تی بده که به&zwnj;ڕاستی ئێمه موسڵمان و ملکه&zwnj;چ و فه&zwnj;رمانبه&zwnj;ردارین.';
$t[53] = '(ئه&zwnj;وسا وتیان): په&zwnj;روه&zwnj;ردگارا ئێمه باوه&zwnj;ڕمان هێناوه&zwnj;، به&zwnj;وه&zwnj;ی داتبه&zwnj;زاندووه و ڕه&zwnj;وانه&zwnj;ت کردووه&zwnj;، شوێنکه&zwnj;وته&zwnj;ی پێغه&zwnj;مبه&zwnj;ره&zwnj;که&zwnj;شتین، ده تۆیش له ڕیزی شایه&zwnj;ته&zwnj;کانی (گه&zwnj;یاندنی به&zwnj;رنامه&zwnj;که&zwnj;تدا) تۆمارمان بکه&zwnj;.';
$t[54] = '(به&zwnj;ڵام دوژمنانی خوا) پیلانیان گێڕا، خوایش پیلانه&zwnj;کانی پووچ کردنه&zwnj;وه&zwnj;، چونکه خوا چاکترین و به&zwnj;ده&zwnj;سه&zwnj;ڵاترین زاتێکه&zwnj;له هه&zwnj;ڵوه&zwnj;شاندنی نه&zwnj;خشه و پیلانی دوژمنانیدا.';
$t[55] = '(به&zwnj;یادیان بهێنه&zwnj;) کاتێک خوا فه&zwnj;رمووی: ئه&zwnj;ی عیسا من (به&zwnj;شێوه&zwnj;یه&zwnj;کی ئاسایی) تۆ ده&zwnj;مرێنم پاشان تۆ پایه&zwnj;دارو بڵند ده&zwnj;که&zwnj;مه&zwnj;وه لای خۆم، له ده&zwnj;ستی ناپاکی ئه&zwnj;وانه&zwnj;ی که&zwnj;بێ باوه&zwnj;رن ڕزگارت ده&zwnj;که&zwnj;م و پاک ڕاتده&zwnj;گرم و به&zwnj;رزیی و بڵندیی ده&zwnj;به&zwnj;خشم به&zwnj;وانه&zwnj;ی که شوێنکه&zwnj;وته&zwnj;ی تۆن به&zwnj;سه&zwnj;ر کافراندا هه&zwnj;تا ڕۆژی قیامه&zwnj;ت، له&zwnj;وه&zwnj;ودواش گه&zwnj;ڕانه&zwnj;وه&zwnj;تان هه&zwnj;ر بۆلای منه&zwnj;، ئه&zwnj;وسا دادوه&zwnj;ریی و فه&zwnj;رمانڕه&zwnj;وایی دادپه&zwnj;روه&zwnj;رانه ده&zwnj;که&zwnj;م له نێوانتاندا له&zwnj;و شتانه&zwnj;دا که کێشه&zwnj;تان له&zwnj;سه&zwnj;ری هه&zwnj;بووه و ڕاتان ده&zwnj;رباره&zwnj;ی جیاواز بووه&zwnj;.';
$t[56] = 'جا ئه&zwnj;وانه&zwnj;ی بێ باوه&zwnj;ڕ بوون سزای توند و تیژیان ده&zwnj;ده&zwnj;م له دنیادا (به شکستی و سه&zwnj;رشۆڕیان)، له قیامه&zwnj;تیشدا (به ژیانی سه&zwnj;ختی ناو دۆزه&zwnj;خ)، جا له&zwnj;وێ که&zwnj;سیان ده&zwnj;ست ناکه&zwnj;وێت که یاریده&zwnj;ده&zwnj;رو پشتیوانیان بێت (له سزای من بیانپارێزێت).';
$t[57] = 'ئه&zwnj;وانه&zwnj;ش که باوه&zwnj;ڕیان هێناوه (به ئاینی ئیسلام) و کارو کرده&zwnj;وه چاکه&zwnj;کانیان ئه&zwnj;نجام داوه&zwnj;، ئه&zwnj;وه خوا به ته&zwnj;واوی پاداشتیان ده&zwnj;داته&zwnj;وه&zwnj;، به&zwnj;ڕاستی خوا سته&zwnj;مکارانی خۆشناوێت.';
$t[58] = 'ئه&zwnj;و هه&zwnj;واڵ و به&zwnj;سه&zwnj;رهاتانه&zwnj;ی که به&zwnj;سه&zwnj;رتدا ده&zwnj;یخوێنینه&zwnj;وه (ئه&zwnj;ی محمد (صلی الله علیه وسلم) له ئایه&zwnj;ته&zwnj;&zwnj;کانی خوایه و له یادو باسه پڕداناییه&zwnj;کانه&zwnj;.';
$t[59] = 'به&zwnj;ڕاستی نموونه&zwnj;ی دروست بوونی عیسا له&zwnj;لایه&zwnj;ن خواوه&zwnj;؛ وه&zwnj;ک نموونه&zwnj;ی ئاده&zwnj;م وایه که له خاک دروستی کرد و له&zwnj;وه&zwnj;ودوا فه&zwnj;رمانی داو پێی وت: ببه (به ئینسان) ئه&zwnj;ویش بوو، (عیسا دایکی هه&zwnj;بوو، ئاده&zwnj;م دایک و باوکیشی نه&zwnj;بوو).';
$t[60] = 'حه&zwnj;قیقه&zwnj;ت و ڕاستی هه&zwnj;ر ئه&zwnj;وه&zwnj;یه له&zwnj;لایه&zwnj;ن په&zwnj;روه&zwnj;ردگارته&zwnj;وه&zwnj;یه&zwnj;، کاواته نه&zwnj;که&zwnj;یت بچیته ڕیزی ئه&zwnj;وانه&zwnj;ی که گومان کارکردی هه&zwnj;یه له&zwnj;سه&zwnj;ریان.';
$t[61] = '(دوای ئه&zwnj;وه&zwnj;ی که هه&zwnj;موو ئه&zwnj;و ڕاستیانه&zwnj;ت پێگه&zwnj;یشت ده&zwnj;رباره&zwnj;ی عیسا)، جا ئه&zwnj;گه&zwnj;ر که&zwnj;سانێک هه&zwnj;ر موجاده&zwnj;له&zwnj;یان کردوو له&zwnj;سه&zwnj;ری ڕۆیشتن و شتی ناڕه&zwnj;وایان ده&zwnj;رباره&zwnj;ی عیسا وت، تۆیش پێیان بڵێ: وه&zwnj;رن با کوڕانی ئێمه و کوڕانی ئێوه&zwnj;، ئافره&zwnj;تانی ئێمه و ئافره&zwnj;تانی ئێوه&zwnj;، خۆمان و خۆشتان بانگ بکه&zwnj;ین و ئاماده&zwnj;بین (له شوێنێکی تایبه&zwnj;تدا)، پاشان له خوا بپاڕێنه&zwnj;وه و ئینجا داوا بکه&zwnj;ین نه&zwnj;فرین و له&zwnj;عنه&zwnj;تی خوا داببارێته سه&zwnj;ر درۆزنه&zwnj;کان.';
$t[62] = 'به&zwnj;ڕاستی هه&zwnj;ر ئه&zwnj;مه&zwnj;یه به&zwnj;سه&zwnj;رهاتی ڕاست و دروست (ده&zwnj;رباره&zwnj;ی عیسا و دایکی)، هیچ خوایه&zwnj;ک نیه جگه له (الله&zwnj;)، بێگومان ئه&zwnj;و (الله&zwnj;) یه هه&zwnj;ر خۆی باڵاده&zwnj;ست و دانایه&zwnj;.';
$t[63] = '(له&zwnj;گه&zwnj;ڵ ئه&zwnj;م هه&zwnj;موو به&zwnj;ڵگه و ڕاستیانه&zwnj;دا) ئه&zwnj;گه&zwnj;ر هه&zwnj;ر ڕوویان وه&zwnj;رگێڕاو پشتیان تێکرد (ئه&zwnj;وه بزانه ئه&zwnj;وانه ڕاستیخوازنین)، خوایش زاناو ئاگایه به&zwnj;وانه&zwnj;ی که تۆوی تاوان و خراپه ده&zwnj;چێنن.';
$t[64] = '(ئه&zwnj;ی محمد (صلی الله علیه وسلم) بڵێ: ئه&zwnj;ی ئه&zwnj;وانه&zwnj;ی کتێبتان بۆ ڕه&zwnj;وانه&zwnj;کراوه وه&zwnj;رن به&zwnj;ده&zwnj;م بانگه&zwnj;واز و به&zwnj;رنامه&zwnj;یه&zwnj;که&zwnj;وه که هاوبه&zwnj;ش و هاوسه&zwnj;نگه له نێوان ئێمه&zwnj;ش و ئێوه&zwnj;شدا (ئه&zwnj;ویش ئه&zwnj;مه&zwnj;یه&zwnj;): جگه له (الله&zwnj;) که&zwnj;سی تر نه&zwnj;په&zwnj;رستین، هیچ جۆره هاوه&zwnj;ڵێکی بۆ بڕیار نه&zwnj;ده&zwnj;ین، هیچ لایه&zwnj;کمان لاکه&zwnj;ی ترمان (به&zwnj;پیرۆز و فه&zwnj;رمانڕه&zwnj;وا دانه&zwnj;نێین) له&zwnj;جیاتی ئه&zwnj;و زاته و نه&zwnj;یپه&zwnj;رستین (خۆ ئه&zwnj;گه&zwnj;ر له&zwnj;م بانگه&zwnj;وازدا) سه&zwnj;رپێچیان کردو گوێیان پێنه&zwnj;دا، ئه&zwnj;وه پێیان بڵێ: که ئێوه به&zwnj;شایه&zwnj;ت بن که ئێمه موسڵمانین و ملکه&zwnj;چی فه&zwnj;رمانی په&zwnj;روه&zwnj;ردگارین.';
$t[65] = 'ئه&zwnj;ی خاوه&zwnj;ن کتێبه&zwnj;کان، ئێوه بۆ له ئیبراهیم ده&zwnj;دوێن و (هه&zwnj;ریه&zwnj;که&zwnj;تان واداده&zwnj;نێت که له&zwnj;سه&zwnj;ر ئاینی ئه&zwnj;وه&zwnj;) خۆ ته&zwnj;ورات و ئینجیل دوای ئه&zwnj;و ڕه&zwnj;وانه کراون، ئه&zwnj;وه بۆ عه&zwnj;قڵ و ژیرتان ناخه&zwnj;نه&zwnj;کارو تاکه&zwnj;ی ژیرنابن؟!';
$t[66] = 'ئه&zwnj;وه ئێوه&zwnj;ن موجاده&zwnj;له&zwnj;و باس و خواستان له&zwnj;سه&zwnj;ر ئه&zwnj;و شتانه ده&zwnj;کرد که زانیاریتان ده&zwnj;رباره&zwnj;ی هه&zwnj;بوو.. که&zwnj;وابوو بۆچی ده&zwnj;م له&zwnj;و شتانه&zwnj;شه&zwnj;وه ده&zwnj;ده&zwnj;ن که هیچ زانیاریتان ده&zwnj;رباره&zwnj;ی نی یه&zwnj;؟ دیاره هه&zwnj;ر خوا ئه&zwnj;و شتانه ده&zwnj;زانێت، ئێوه نایزانن.';
$t[67] = '(ئاشکرایه&zwnj;) که ئیبراهیم نه جووله&zwnj;که بووه و نه&zwnj;گاور، به&zwnj;ڵکو ملکه&zwnj;چ و فه&zwnj;رمانبه&zwnj;ردارو موسڵمان بووه و له ده&zwnj;سته&zwnj;ی موشریک و هاوه&zwnj;ڵگه&zwnj;ران نه&zwnj;بووه&zwnj;.';
$t[68] = 'نزیکترین که&zwnj;س له ئیبراهیمه&zwnj;وه&zwnj;: ئه&zwnj;و که&zwnj;سانه&zwnj;ن که له سه&zwnj;رده&zwnj;می خۆیدا شوێنی که&zwnj;وتبوون، هه&zwnj;روه&zwnj;ها ئه&zwnj;م پێغه&zwnj;مبه&zwnj;ره&zwnj;ش (صلی الله علیه وسلم) و ئه&zwnj;وانه&zwnj;ی که باوه&zwnj;ڕیان هێناوه (به پێغه&zwnj;مبه&zwnj;رایه&zwnj;تی ئیبراهیم و موسا و عیسا و محمد و هه&zwnj;موو پێغه&zwnj;مبه&zwnj;ران به&zwnj;گشتی علیهم السلام)، بێگومان خوا یارو یاوه&zwnj;رو پشتگیری ئیماندارانه&zwnj;.';
$t[69] = 'ده&zwnj;سته&zwnj;یه&zwnj;ک له&zwnj;خاوه&zwnj;ن کتێبه&zwnj;کان ئاواته&zwnj;خوازن و (له هه&zwnj;وڵدان) که گومڕاتان بکه&zwnj;ن، له&zwnj;کاتێکدا ئه&zwnj;وانه هه&zwnj;ر خۆیان گومڕا ده&zwnj;که&zwnj;ن و هه&zwnj;ستیشی پێناکه&zwnj;ن و (خۆیان ده&zwnj;خه&zwnj;ڵه&zwnj;تێنن).';
$t[70] = 'ئه&zwnj;ی خاوه&zwnj;ن کتێبه&zwnj;کان بۆ بڕواتان نی یه به&zwnj;ئایه&zwnj;ته&zwnj;کانی خوا (که هه&zwnj;مووی به&zwnj;ڵگه&zwnj;ن له&zwnj;سه&zwnj;ر ڕاستی پێغه&zwnj;مبه&zwnj;رایه&zwnj;تی محمد (صلی الله علیه وسلم) له&zwnj;کاتێکدا ئێوه به&zwnj;ئاشکرا (نیشانه&zwnj;ی پێغه&zwnj;مبه&zwnj;رایه&zwnj;تی ئه&zwnj;و) ده&zwnj;بینن (له کتێبه&zwnj;کانتاندا).';
$t[71] = 'ئه&zwnj;ی خاوه&zwnj;ن کتێبه&zwnj;کان بۆچی به&zwnj;رگی به&zwnj;تاڵ و ناحه&zwnj;قی ده&zwnj;که&zwnj;ن به&zwnj;به&zwnj;ری حه&zwnj;ق و ڕاستیداو حه&zwnj;ق و ڕاستی ده&zwnj;شارنه&zwnj;وه&zwnj;، له&zwnj;کاتێکدا خۆتان چاک ده&zwnj;زانن (که&zwnj;حه&zwnj;ق و ڕاستی کامه&zwnj;یه&zwnj;).';
$t[72] = 'ده&zwnj;سته&zwnj;یه&zwnj;ک له خاوه&zwnj;ن کتێبه&zwnj;کان (جووه&zwnj;کان) وتیان به&zwnj;تاقمێکیان: ئێوه له سه&zwnj;ره&zwnj;تای ڕۆژدا (به&zwnj;درۆوه&zwnj;) ئیمان و باوه&zwnj;ڕ بهێنن به&zwnj;و (قورئانه&zwnj;ی) بۆ ئیمانداران ڕه&zwnj;وانه کراوه&zwnj;، به&zwnj;ڵام له&zwnj;کۆتایی ڕۆژداو ده&zwnj;مه&zwnj;و ئێواره په&zwnj;شیمان ببنه&zwnj;وه (تا دوودڵی و گومانیان تێدا دروست ببێت) بۆ ئه&zwnj;وه&zwnj;ی پاشگه&zwnj;ز ببنه&zwnj;وه&zwnj;.';
$t[73] = '(هه&zwnj;روه&zwnj;ها ده&zwnj;ڵێن): بڕوا مه&zwnj;که&zwnj;ن و متمانه مه&zwnj;که&zwnj;ن به&zwnj;که&zwnj;س جگه به&zwnj;وکه&zwnj;سانه&zwnj;ی که شوێنی ئاینی ئێوه که&zwnj;وتوون! (ئه&zwnj;ی محمد (صلی الله علیه وسلم) پێیان) بڵێ: که به&zwnj;ڕاستی هه&zwnj;ر هیدایه&zwnj;ت و ڕێنموویی خوایی هیدایه&zwnj;ت و ڕێنمویی یه&zwnj;، (که&zwnj;چی جووه&zwnj;کان به&zwnj;یه&zwnj;کتر ده&zwnj;ڵێن): دان به&zwnj;وه&zwnj;دا مه&zwnj;نێن که&zwnj;که&zwnj;س وه&zwnj;کو ئێوه&zwnj;ی پێبه&zwnj;خشراوه&zwnj;، باسی (ته&zwnj;ورات و دواهه&zwnj;مین پێغه&zwnj;مبه&zwnj;ریش مه&zwnj;که&zwnj;ن)!! تا لای په&zwnj;روه&zwnj;ردگارتان (سبه&zwnj;ی له قیامه&zwnj;تدا) قسه&zwnj;یان زاڵ نه&zwnj;بێت به&zwnj;سه&zwnj;رتاندا!! (ئه&zwnj;ی محمد (صلی الله علیه وسلم) تۆش) بڵێ: به&zwnj;ڕاستی هه&zwnj;رچی ڕێزو به&zwnj;خشش و فه&zwnj;زڵ هه&zwnj;یه به&zwnj;ده&zwnj;ست خوایه&zwnj;و ده&zwnj;یبه&zwnj;خشێت به&zwnj;و که&zwnj;سانه&zwnj;ی که ده&zwnj;یه&zwnj;وێت و (شایسته&zwnj;ن) و خوا فراوانگیرو زانایه&zwnj;.';
$t[74] = 'ئه&zwnj;و خوایه ڕه&zwnj;حمه&zwnj;ت و میهره&zwnj;بانی خۆی تایبه&zwnj;ت ده&zwnj;کات بۆ هه&zwnj;ر که&zwnj;سێک بیه&zwnj;وێت و (شایسته بێت) و خوا خاوه&zwnj;نی ڕێزو به&zwnj;خشش و فه&zwnj;زڵی زۆر گه&zwnj;وره و بێ ئه&zwnj;ندازه&zwnj;یه&zwnj;.';
$t[75] = 'له شوێنکه&zwnj;وتووانی کتێبه ئاسمانیه&zwnj;کان که&zwnj;سانێک هه&zwnj;ن ئه&zwnj;گه&zwnj;ر ماڵ و داراییه&zwnj;کی زۆری به&zwnj;ئه&zwnj;مانه&zwnj;ت له&zwnj;لا دابنێیت (هه&zwnj;ر کاتێک داوای بکه&zwnj;یت) بۆت ده&zwnj;گێڕێته&zwnj;وه به&zwnj;ته&zwnj;واویی، هه&zwnj;شیانه ئه&zwnj;گه&zwnj;ر دینارێکی به&zwnj;ئه&zwnj;مانه&zwnj;ت لادابنێیت بۆت نایگێڕێته&zwnj;وه مه&zwnj;گه&zwnj;ر به سه&zwnj;ریه&zwnj;وه ڕاوه&zwnj;ستابیت و کۆڵی لێنه&zwnj;ده&zwnj;یت، ئه&zwnj;وانه بۆیه واده&zwnj;که&zwnj;ن چونکه ده&zwnj;ڵێن: له به&zwnj;رامبه&zwnj;ر ده&zwnj;سته&zwnj;ی نه&zwnj;خوێنده&zwnj;واراندا (مه&zwnj;به&zwnj;ستیان موسڵمانانه&zwnj;) لێپرسراو نابین (ئه&zwnj;گه&zwnj;ر فێڵیان لێبکه&zwnj;ین و ماڵیان بخۆین)، ئه&zwnj;وانه به&zwnj;ده&zwnj;م خواوه درۆ هه&zwnj;ڵده&zwnj;به&zwnj;ستن و ده&zwnj;شزانن که درۆ ده&zwnj;که&zwnj;ن...';
$t[76] = 'نه&zwnj;خێر وانیه&zwnj;؛ هه&zwnj;ر که&zwnj;س ئه&zwnj;و په&zwnj;یمانه&zwnj;ی که داویه&zwnj;تی به&zwnj;خوا جێبه&zwnj;یێی کردبێت و خۆی پاڕاستبێت له حه&zwnj;رام، ئه&zwnj;وه به&zwnj;ڕاستی خوا پارێزکاران و دیندارانی خۆشده&zwnj;وێت.';
$t[77] = 'به&zwnj;ڕاستی ئه&zwnj;وانه&zwnj;ی که په&zwnj;یمانی خواو سوێنده&zwnj;کانیان به&zwnj;نرخێکی که&zwnj;م ده&zwnj;فرۆشن (له پله&zwnj;و پایه&zwnj;ی دنیایی، پاره&zwnj;وپول و سامان، ئه&zwnj;ڵبه&zwnj;ته هه&zwnj;رچه&zwnj;نده بێت هه&zwnj;رکه&zwnj;مه&zwnj;) ئه&zwnj;وانه له دواڕۆژدا هیچ به&zwnj;شێکیان نابێت (له سۆزو میهره&zwnj;بانی خوا)، هه&zwnj;روه&zwnj;ها له ڕۆژی قیامه&zwnj;تدا خوا نایاندوێنێت و ته&zwnj;ماشایان ناکات (له چڵک و پیسی گوناهـ) خاوێنیان ناکات و سزای پڕ ئێش و ئازاریش بۆیان ئاماده&zwnj;یه&zwnj;.';
$t[78] = 'به&zwnj;ڕاستی ده&zwnj;سته&zwnj;یه&zwnj;کیش له&zwnj;وانه&zwnj;ی (که له چه&zwnj;ند ئایه&zwnj;تی پێشوودا باسکران واته جووه&zwnj;کان) زمانیان باده&zwnj;ده&zwnj;ن و به&zwnj;جۆرێک شت ده&zwnj;ڵێن، گوایه (ته&zwnj;ورات) ده&zwnj;خوێننه&zwnj;وه&zwnj;، بۆ ئه&zwnj;وه&zwnj;ی (ئێوه چه&zwnj;واشه بکه&zwnj;ن و) وا بنوێنن که ئه&zwnj;و کتێبه ده&zwnj;ور ده&zwnj;که&zwnj;نه&zwnj;وه&zwnj;، له&zwnj;حاڵێکدا ئه&zwnj;وه&zwnj;ی ده&zwnj;یخوێننه&zwnj;وه له&zwnj;و کتێبه نیه (زۆر جار) ده&zwnj;ڵێن: ئه&zwnj;وه&zwnj;ی که ده&zwnj;یخوێنین هه&zwnj;ر ئه&zwnj;وه&zwnj;یه خوا ڕه&zwnj;وانه&zwnj;ی کردووه&zwnj;، له ڕاستیدا هه&zwnj;رگیز ئه&zwnj;وه له&zwnj;لایه&zwnj;ن خواوه نیه و به&zwnj;ده&zwnj;م خواوه درۆ هه&zwnj;ڵده&zwnj;به&zwnj;ستن و ده&zwnj;شزانن که درۆ و بوختان ده&zwnj;که&zwnj;ن.';
$t[79] = 'بۆ هیچ ئاده&zwnj;میزاد و که&zwnj;سێک نه&zwnj;گونجاوه که خوا کتێبی ئاسمانی و فه&zwnj;رمانڕه&zwnj;وایی و پێغه&zwnj;مبه&zwnj;رایه&zwnj;تی پێببه&zwnj;خشێت، له&zwnj;وه&zwnj;ودوا به&zwnj;خه&zwnj;ڵکی بڵێت: وه&zwnj;رن ببنه عه&zwnj;بد و به&zwnj;نده&zwnj;ی من له جیاتی ئه&zwnj;وه&zwnj;ی به&zwnj;نده و عه&zwnj;بدی خوا بن، به&zwnj;ڵکو (به&zwnj;خه&zwnj;ڵکیان وتووه&zwnj;) هه&zwnj;وڵ بده&zwnj;ن بۆ خوا بژین و لێبڕاو بن، بۆ ڕه&zwnj;زامه&zwnj;ندی په&zwnj;روه&zwnj;ردگار هه&zwnj;وڵ بده&zwnj;ن، به&zwnj;وه&zwnj;ش ده&zwnj;بنه خواناس و ڕه&zwnj;بانی که به&zwnj;رنامه&zwnj;ی خوا فێری خه&zwnj;ڵکی بکه&zwnj;ن و خۆشتان به&zwnj;رده&zwnj;وام هه&zwnj;وڵ بده&zwnj;ن فێری ببن.';
$t[80] = '(هیچ پێغه&zwnj;مبه&zwnj;رێک) فرمانی ئه&zwnj;وه&zwnj;ی نه&zwnj;داوه که فریشته&zwnj;کان و پێغه&zwnj;مبه&zwnj;ران بکه&zwnj;ن به په&zwnj;روه&zwnj;ردگار (هه&zwnj;رگیز کاری له&zwnj;و جۆره له&zwnj;وان ناوه&zwnj;شێته&zwnj;وه&zwnj;) ئایا ڕاسته فرمانی بێ باوه&zwnj;ڕیی و کوفرتان پێ بدات دوای ئه&zwnj;وه&zwnj;ی که ئێوه موسڵمانن و ملکه&zwnj;چی په&zwnj;روه&zwnj;ردگارن؟!';
$t[81] = '(یادیان بێنه&zwnj;) کاتێک خوا په&zwnj;یمانی له یه&zwnj;که یه&zwnj;که&zwnj;ی پێغه&zwnj;مبه&zwnj;ران وه&zwnj;رگرت کاتێک کتێب و دانایی پێبه&zwnj;خشین به&zwnj;وه&zwnj;ی که ئه&zwnj;گه&zwnj;ر: له ئاینده&zwnj;دا پێغه&zwnj;مبه&zwnj;رێکم بۆ ڕه&zwnj;وانه کردن و به&zwnj;ڕاستدانه&zwnj;ری ئه&zwnj;وه&zwnj;ی ئێوه&zwnj;ش بوو، ئه&zwnj;وه ده&zwnj;بێت باوه&zwnj;ڕی پێ بهێنن و پشتگیری بکه&zwnj;ن، ئینجا خوا فه&zwnj;رمووی: ئایا بڕیارتانداو په&zwnj;یمانی منتان به&zwnj;و جۆره وه&zwnj;رگرت که پێم ڕاگه&zwnj;یاندن؟ وتیان: بڕیارمانداو (هه&zwnj;روا ده&zwnj;که&zwnj;ین) ئه&zwnj;وسا خوا پێی فه&zwnj;رموون: ده به&zwnj;شایه&zwnj;ت بن و منیش له&zwnj;گه&zwnj;ڵتان یه&zwnj;کێکم له شایه&zwnj;ته&zwnj;کان.';
$t[82] = 'جا ئه&zwnj;وه&zwnj;ی پشتی هه&zwnj;ڵکردو سه&zwnj;رپێچی کرد دوای ئه&zwnj;و په&zwnj;یمانه&zwnj;، ئه&zwnj;وه ئیتر هه&zwnj;ر ئه&zwnj;وانه&zwnj;ن فاسق و گوناهکار.';
$t[83] = 'ئایا ئه&zwnj;وانه بێجگه له ئاینی خوا ئاینێکی تریان ده&zwnj;وێت؟! له&zwnj;کاتێکدا که هه&zwnj;رچی له&zwnj;ئاسمانه&zwnj;کان و زه&zwnj;ویدایه به ڕه&zwnj;زامه&zwnj;ندی بێت یان به&zwnj;ناچاریی ته&zwnj;سلیم و ملکه&zwnj;چی په&zwnj;روه&zwnj;ردگارن، هه&zwnj;مووانیش هه&zwnj;ر بۆ لای ئه&zwnj;و ده&zwnj;گه&zwnj;ڕێنه&zwnj;وه&zwnj;.';
$t[84] = '(ئه&zwnj;ی محمد (صلی الله علیه وسلم) ئه&zwnj;ی ئیماندار) بڵێ: باوه&zwnj;ڕمان هێناوه&zwnj;به&zwnj;خواو به&zwnj;وه&zwnj;ی بۆمان نێرراوه&zwnj;ته خواره&zwnj;وه و به&zwnj;وه&zwnj;ش که نێرراوه&zwnj;ته خواره&zwnj;وه بۆ سه&zwnj;ر ئیبراهیم و ئیسماعیل و ئیسحاق و یه&zwnj;عقوب و نه&zwnj;وه&zwnj;کانی و به&zwnj;وه&zwnj;ش که دراوه به موساو عیساو پێغه&zwnj;مبه&zwnj;ران له&zwnj;لایه&zwnj;ن په&zwnj;روه&zwnj;ردگاریانه&zwnj;وه و ئێمه جیاوازیی ناکه&zwnj;ین له&zwnj;نێوان هیچ کامیانداو هه&zwnj;موومان ته&zwnj;سلیم و ملکه&zwnj;چی ئه&zwnj;و په&zwnj;روه&zwnj;ردگاره&zwnj;ین.';
$t[85] = 'جائه&zwnj;وه&zwnj;ی بێجگه له ئاینی ئیسلام په&zwnj;یڕه&zwnj;وی هه&zwnj;ر ئاینێکی تر بکات لێی وه&zwnj;رناگیرێت، له&zwnj;قیامه&zwnj;تیشدا له خه&zwnj;ساره&zwnj;تمه&zwnj;ندو زه&zwnj;ره&zwnj;رمه&zwnj;ندانه&zwnj;.';
$t[86] = 'چۆن خوا گه&zwnj;لێک هیدایه&zwnj;ت ده&zwnj;دات که دوای ئه&zwnj;وه&zwnj;ی باوه&zwnj;ڕیان هێنا، هه&zwnj;ڵگه&zwnj;ڕانه&zwnj;وه و کافر بوون؟ له حاڵێکدا شایه&zwnj;تییان دابوو که&zwnj;ئه&zwnj;م پێغه&zwnj;مبه&zwnj;ره حه&zwnj;قه&zwnj;و چه&zwnj;نده&zwnj;ها به&zwnj;ڵگه&zwnj;و نیشانه&zwnj;شیان بۆ هاتبوو (له&zwnj;سه&zwnj;ر ڕاستی محمد (صلی الله علیه وسلم)، خوای گه&zwnj;وره&zwnj;ش هیدایه&zwnj;ت و ڕێنموویی ئه&zwnj;و ده&zwnj;سته&zwnj;و تاقمه سته&zwnj;مکاره نادات.';
$t[87] = 'ئا ئه&zwnj;و سته&zwnj;مکارانه پاداشت و تۆڵه&zwnj;یان ئه&zwnj;وه&zwnj;یه&zwnj;: نه&zwnj;فرینی خواو فریشته و هه&zwnj;موو خه&zwnj;ڵکییان له&zwnj;سه&zwnj;ره به&zwnj;گشتی..';
$t[88] = 'له&zwnj;ناو ئه&zwnj;و خه&zwnj;شم و نه&zwnj;فرینه&zwnj;دا ده&zwnj;مێننه&zwnj;وه و سزا له&zwnj;سه&zwnj;ریان سووک ناکرێت و موڵه&zwnj;تیش نادرێن.';
$t[89] = 'مه&zwnj;گه&zwnj;ر که&zwnj;سانێک که فریای خۆیان که&zwnj;وتبن و ته&zwnj;وبه&zwnj;یان کردبێت له دوای ئه&zwnj;و لادان و یاخیی بوونه&zwnj;یان و چاکسازیشیان کردبێت، ئه&zwnj;وه به&zwnj;ڕاستی خوا بۆ ئه&zwnj;وانه لێخۆشبوو و میهره&zwnj;بانه&zwnj;.';
$t[90] = 'به&zwnj;ڕاستی ئه&zwnj;وانه&zwnj;ی دوای باوه&zwnj;ڕ هێنانیان کافر بوون و له&zwnj;وه&zwnj;ودوا زیاتر ڕۆچوون له کوفرو بێ دینیدا، ئه&zwnj;وانه په&zwnj;شیمانی و ته&zwnj;وبه&zwnj;یان هه&zwnj;رگیز لێ وه&zwnj;رناگیرێت، ئا ئه&zwnj;وانه ده&zwnj;سته&zwnj;ی گومڕاو سه&zwnj;ر لێشێواوه&zwnj;کانن.';
$t[91] = 'به&zwnj;ڕاستی ئه&zwnj;وانه&zwnj;ی بێ باوه&zwnj;ڕ بوون و به&zwnj;بێ باوه&zwnj;ڕیی مردوون، (ئه&zwnj;گه&zwnj;ر) هه&zwnj;ر&zwnj;یه&zwnj;که&zwnj;یان بتوانێت پڕ به&zwnj;پڕی زه&zwnj;وی ئاڵتون بهێنێت و بیکات به&zwnj;فیدیه&zwnj;ی خۆی هه&zwnj;رگیز لێی وه&zwnj;رناگیرێت، ئه&zwnj;وانه سزای به ئێش و ئازار بۆیان ئاماده&zwnj;یه و که&zwnj;سیش نی یه پشتیوانییان لێ بکات و فریایان بکه&zwnj;وێت.';
$t[92] = 'ئێوه هه&zwnj;رگیز ناگه&zwnj;نه پله&zwnj;ی چاکیی و چاکه&zwnj;کاری هه&zwnj;تا له&zwnj;و شتانه&zwnj;ی که خۆشتان ده&zwnj;وێت نه&zwnj;به&zwnj;خشن، هه&zwnj;ر شتێکیش ببه&zwnj;خشن ئه&zwnj;وه خوا ئاگاو زانایه پێی (لێی ون نابێت و بێ پاداشت نابێت).';
$t[93] = 'هه&zwnj;رچی خۆراک هه&zwnj;یه بۆ نه&zwnj;وه&zwnj;ی ئیسرائیل حه&zwnj;ڵاڵ بوو مه&zwnj;گه&zwnj;ر ئه&zwnj;وه&zwnj;ی که یه&zwnj;عقوب خۆی له&zwnj;سه&zwnj;ر خۆی حه&zwnj;رام کردبوو پێش ناردنی ته&zwnj;ورات، پێیان بڵێ: ده ئێوه ته&zwnj;ورات بهێنن و بیخوێننه&zwnj;وه ئه&zwnj;گه&zwnj;ر ڕاستگۆن (دیاره&zwnj;که جوله&zwnj;که هه&zwnj;ر له خۆیانه&zwnj;وه ده&zwnj;مبازی ده&zwnj;که&zwnj;ن و قسه ڕێکده&zwnj;خه&zwnj;ن و شت حه&zwnj;رامده&zwnj;که&zwnj;ن).';
$t[94] = 'جائیتر دوای ئه&zwnj;و هه&zwnj;موو ڕوونکردنه&zwnj;وانه ئه&zwnj;وه&zwnj;ی به&zwnj;ده&zwnj;م خواوه درۆ هه&zwnj;ڵده&zwnj;به&zwnj;ستێت، ئائه&zwnj;وانه هه&zwnj;ر سته&zwnj;مکارن.';
$t[95] = '(ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر (صلی الله علیه وسلم) پێیان) بڵێ: خوای گه&zwnj;وره له هه&zwnj;موو فه&zwnj;رمایشته&zwnj;کانیدا ڕاستی فه&zwnj;رمووه&zwnj;، که&zwnj;واته شوێنی ئاین و به&zwnj;رنامه&zwnj;ی ئیبراهیم بکه&zwnj;ون که پاکو دروست و فه&zwnj;رمانبه&zwnj;رداره و ئه&zwnj;و هه&zwnj;رگیز له موشریک و هاوه&zwnj;ڵگه&zwnj;ران نه&zwnj;بووه&zwnj;.';
$t[96] = 'به&zwnj;ڕاستی یه&zwnj;که&zwnj;م خانه و ماڵ و مه&zwnj;نزڵگه&zwnj;یه&zwnj;ک که بۆ خه&zwnj;ڵکی دانرا له&zwnj;سه&zwnj;ر زه&zwnj;وی (بۆ خواپه&zwnj;رستی) ئه&zwnj;وه&zwnj;یه له مه&zwnj;ککه&zwnj;دایه&zwnj;، که پیرۆزه و هیدایه&zwnj;ت و ڕێنموویی به&zwnj;خشه بۆ هه&zwnj;موو خه&zwnj;ڵکی جیهان.';
$t[97] = 'له&zwnj;و ماڵه&zwnj;دا (که&zwnj;عبه&zwnj;) نیشانه&zwnj;و به&zwnj;ڵگه&zwnj;ی زۆر و ئاشکرا هه&zwnj;یه&zwnj;، که یه&zwnj;کێکیان شوێنی تایبه&zwnj;تی خواپه&zwnj;رستیی ئیبراهیمه&zwnj;، هه&zwnj;روه&zwnj;ها ئه&zwnj;وه&zwnj;ی بچێته ناوی پێویسته ئه&zwnj;مین و ئاسوده&zwnj;بێت، له هه&zwnj;موو ده&zwnj;ستدرێژیه&zwnj;ک، جا خوای گه&zwnj;وره&zwnj;ش بڕیاریداوه له&zwnj;سه&zwnj;ر خه&zwnj;ڵکی حه&zwnj;ج و سه&zwnj;ردانی (بیت الحرام) بکه&zwnj;ن به ته&zwnj;نها ئه&zwnj;وانه&zwnj;ی که توانای (دارایی و ته&zwnj;ندروستیان) هه&zwnj;یه&zwnj;، جا ئه&zwnj;وه&zwnj;ی که باوه&zwnj;ڕی نه&zwnj;هێناوه&zwnj;، ئه&zwnj;وه با بزانێت به&zwnj;ڕاستی خوا بێ نیازه له هه&zwnj;موو خه&zwnj;ڵکان (پێویستی به چاکه و په&zwnj;رستنی که&zwnj;س نیه&zwnj;).';
$t[98] = 'ئه&zwnj;ی پیغه&zwnj;مبه&zwnj;ر (صلی الله علیه وسلم) به خاوه&zwnj;نانی کتێب بڵێ: بۆچی ئێوه باوه&zwnj;ڕ ناکه&zwnj;ن به ئایه&zwnj;ته&zwnj;کانی خوا، خۆ خوا شایه&zwnj;ته له&zwnj;سه&zwnj;ر هه&zwnj;موو ئه&zwnj;و کارو کرده&zwnj;وانه&zwnj;ی که ئه&zwnj;نجامی ده&zwnj;ده&zwnj;ن.';
$t[99] = '(هه&zwnj;روه&zwnj;ها) بڵێ: ئه&zwnj;ی خاوه&zwnj;نانی کتێب بۆ ڕێده&zwnj;گرن له بڵاو بوونه&zwnj;وه&zwnj;ی ئایینی خواو ده&zwnj;تانه&zwnj;وێت ئه&zwnj;وه&zwnj;ی باوه&zwnj;ڕی هێناوه ڕێبازی دینداری به چه&zwnj;وتی بگرێت؟ له&zwnj;کاتێکدا خۆتان شایه&zwnj;تن و چاک ده&zwnj;زانن (ئه&zwnj;وه ڕاست نی یه و چه&zwnj;وته&zwnj;) دڵنیاش بن که خوا غافڵ و بێ ئاگا نیه له&zwnj;و شتانه&zwnj;ی که ده&zwnj;یکه&zwnj;ن.';
$t[100] = 'ئه&zwnj;ی ئه&zwnj;وانه&zwnj;ی باوه&zwnj;ڕتان هێناوه ئه&zwnj;گه&zwnj;ر گوێڕایه&zwnj;ڵی هه&zwnj;ندێک له&zwnj;وانه ببن که کتێبی (ئاسمانییان) پێدراوه&zwnj;، دوای ئه&zwnj;وه&zwnj;ی که ئێوه باوه&zwnj;ڕتان هێناوه&zwnj;، ده&zwnj;تانگێڕنه&zwnj;وه سه&zwnj;ر ڕێبازی کوفرو بێ باوه&zwnj;ڕی.';
$t[101] = 'جا ئێوه چۆن کافر ده&zwnj;بن له کاتێکدا که ئایه&zwnj;ته&zwnj;کانی خواتان به&zwnj;سه&zwnj;ردا ده&zwnj;خوێنرێته&zwnj;وه و پێغه&zwnj;مبه&zwnj;ره&zwnj;که&zwnj;ی له&zwnj;ناوتاندایه (دوای وه&zwnj;فاتیشی (صلی الله علیه وسلم) به&zwnj;سیره&zwnj;تی پاک و بێگه&zwnj;ردی هه&zwnj;ر له&zwnj;ناوماندایه&zwnj;) جا ئه&zwnj;وه&zwnj;ی پشت به&zwnj;خوا ببه&zwnj;ستێت و پیڕه&zwnj;وی له به&zwnj;رنامه&zwnj;که&zwnj;ی بکات، ئه&zwnj;وه به&zwnj;ڕاستی بۆ ڕێگه&zwnj;و ڕێبازێکی ڕاست و دروست هیدایه&zwnj;ت دراوه&zwnj;.';
$t[102] = 'ئه&zwnj;ی ئه&zwnj;وانه&zwnj;ی باوه&zwnj;ڕتان هێناوه&zwnj;، له خوا بترسن و پارێزکاربن به&zwnj;شێوه&zwnj;یه&zwnj;کی وا که شایسته&zwnj;ی ئه&zwnj;و زاته&zwnj;یه و هه&zwnj;میشه پابه&zwnj;ند و دامه&zwnj;زراو بن له&zwnj;سه&zwnj;ر ئیسلامه&zwnj;تی، بۆ ئه&zwnj;وه&zwnj;ی هه&zwnj;ر کاتێک مردن به ئیمان و ئیسلامه&zwnj;تی نه&zwnj;بێت نه&zwnj;مرن.';
$t[103] = '(هه&zwnj;وڵ بده&zwnj;ن) هه&zwnj;ر هه&zwnj;موتان به توندی ده&zwnj;ست بگرن به ئاینی خواوه&zwnj;و په&zwnj;رت و بڵاو مه&zwnj;ب، یادی نازو نیعمه&zwnj;تی خوا بکه&zwnj;نه&zwnj;وه له&zwnj;سه&zwnj;رتان، (چونکه&zwnj;) کاتی خۆی دوژمنی یه&zwnj;کتر بوون، ئه&zwnj;وه بوو دڵه&zwnj;کانتانی به&zwnj;هۆی نیعمه&zwnj;تی (ئیسلامه&zwnj;وه&zwnj;) په&zwnj;یوه&zwnj;ست کرد به&zwnj;یه&zwnj;که&zwnj;وه و هه&zwnj;موو بوونه برای یه&zwnj;کتر (هه&zwnj;روه&zwnj;ها ئه&zwnj;و کاته&zwnj;) ئێوه له&zwnj;سه&zwnj;ر لێواری چاڵێکی ئاگر وه&zwnj;ستا بوون و (خه&zwnj;ریک بوو بکه&zwnj;ونه ناوی)، باڵام خوا (به&zwnj;ڕه&zwnj;حم و میهره&zwnj;بانی خۆی) له&zwnj;و ئاگره ڕزگاری کردن، ئابه&zwnj;و شێوه&zwnj;یه خوا ئایه&zwnj;ته&zwnj;کانی خۆیتان بۆ ڕوون ده&zwnj;کاته&zwnj;وه&zwnj;، بۆ ئه&zwnj;وه&zwnj;ی هیدایه&zwnj;ت وه&zwnj;ربگرن.';
$t[104] = 'پێویسته له ئێوه ئومه&zwnj;تێک پێک بێت، یاخود با هه&zwnj;مووتان ئومه&zwnj;تێک بن که بانگه&zwnj;واز بکه&zwnj;ن بۆ خێرو چاکه&zwnj;، فه&zwnj;رمان بده&zwnj;ن به&zwnj;هه&zwnj;موو کارێکی باوو دروست، قه&zwnj;ده&zwnj;غه&zwnj;ی هه&zwnj;موو نادروستیه&zwnj;ک بکه&zwnj;ن، ئا ئه&zwnj;وانه هه&zwnj;ر سه&zwnj;رفرازن.';
$t[105] = 'نه&zwnj;که&zwnj;ن وه&zwnj;کو ئه&zwnj;وکه&zwnj;سانه بن که ناکۆکی و دووبه&zwnj;ره&zwnj;کیان له&zwnj;ناودا به&zwnj;رپابوو، له&zwnj;یه&zwnj;کتر دابڕان، دوای ئه&zwnj;وه&zwnj;ی ئه&zwnj;و هه&zwnj;موو به&zwnj;ڵگه&zwnj;و نیشانه ڕوونه&zwnj;یان بۆ هاتبوو (که سه&zwnj;رکه&zwnj;وتن له یه&zwnj;ک ڕیزیدایه&zwnj;)، ئا ئه&zwnj;وانه سزایه&zwnj;کی گه&zwnj;وره و سه&zwnj;خت چاوه&zwnj;ڕێیانه&zwnj;.';
$t[106] = 'ڕۆژێک دێت ڕووخسارانێک سپی ده&zwnj;بن و ڕووخسارانێک ڕه&zwnj;ش هه&zwnj;ڵده&zwnj;گه&zwnj;ڕێن، جا ئه&zwnj;وانه&zwnj;ی که ڕووخساریان ڕه&zwnj;ش هه&zwnj;ڵگه&zwnj;ڕاوه (پێیان ده&zwnj;وترێت): باشه&zwnj;، ئه&zwnj;وه ئێوه پاش ئیمانداریی، په&zwnj;شیمان بوونه&zwnj;وه و ڕێبازی کوفرتان گرته&zwnj;به&zwnj;ر؟! ده بچێژن سزاو ئازار به&zwnj;هۆی کافر بوونتانه&zwnj;وه&zwnj;.';
$t[107] = 'به&zwnj;ڵام ئه&zwnj;وانه&zwnj;ی ڕووخساریان سپی و گه&zwnj;ش و جوانه له&zwnj;ناو ڕه&zwnj;حمه&zwnj;ت و میهره&zwnj;بانی خوادا ژیان ده&zwnj;به&zwnj;نه سه&zwnj;ر بۆ هه&zwnj;میشه و بۆ هه&zwnj;تا هه&zwnj;تایی.';
$t[108] = 'ئه&zwnj;وانه ئایه&zwnj;ته&zwnj;کانی خوای گه&zwnj;وره&zwnj;ن که به&zwnj;ڕاست و دروستی به&zwnj;سه&zwnj;رتا ده&zwnj;یخوێنینه&zwnj;وه ئه&zwnj;ی محمد (صلی الله علیه وسلم)، خوایش هیچ سته&zwnj;می ناوێت و لای په&zwnj;سه&zwnj;ند نیه (له دروست کراوانی و) له هیچ خه&zwnj;ڵکێک بکرێت.';
$t[109] = 'هه&zwnj;رچی له ئاسمانه&zwnj;کان و هه&zwnj;رچی له زه&zwnj;ویدا هه&zwnj;یه&zwnj;، هه&zwnj;ر خوا خاوه&zwnj;نیانه و گه&zwnj;ڕانه&zwnj;وه&zwnj;ی هه&zwnj;موو شتێک هه&zwnj;ر بۆ لای خوایه&zwnj;.';
$t[110] = '(به&zwnj;ڕاستی) ئێوه چاکترین ئوممه&zwnj;تێکن که (بۆ سوودی) خه&zwnj;ڵکی هێنراونه&zwnj;ته مه&zwnj;یدانه&zwnj;وه&zwnj;، چونکه فه&zwnj;رمان به&zwnj;چاکه ده&zwnj;ده&zwnj;ن و قه&zwnj;ده&zwnj;غه&zwnj;ی خراپه ده&zwnj;که&zwnj;ن و باوه&zwnj;ڕی دامه&zwnj;زراوتان بخوا ده&zwnj;هێنن، خۆ ئه&zwnj;گه&zwnj;ر خاوه&zwnj;نانی کتێب باوه&zwnj;ڕیان بهێنایه (به ئاینی ئیسلام) ئه&zwnj;وه چاکتر بوو بۆیان هه&zwnj;یانه ئیماندارن، به&zwnj;ڵام زۆربه&zwnj;یان گوناهبارو تاوانکارن.';
$t[111] = 'هه&zwnj;رگیز ناتوانن زیان (له بیرو باوه&zwnj;ڕتان) بده&zwnj;ن مه&zwnj;گه&zwnj;ر ئازارێکی که&zwnj;م و ئه&zwnj;گه&zwnj;ر بجه&zwnj;نگن دژتان پشتتان لێهه&zwnj;ڵده&zwnj;که&zwnj;ن و هه&zwnj;ڵدێن، له&zwnj;وه&zwnj;ودواش سه&zwnj;رکه&zwnj;وتوو نابن.';
$t[112] = '(جووله&zwnj;که&zwnj;) زه&zwnj;لیلیی و ڕیسواییان بۆ بڕیاردراوه له هه&zwnj;ر کوێ جێگیر بن مه&zwnj;گه&zwnj;ر له سایه&zwnj;ی ئاینی خوادا بحه&zwnj;وێنه&zwnj;وه و (زوڵمیان لێنه&zwnj;کرێت کاتێک ئیسلام فه&zwnj;رمانڕه&zwnj;وا بێت) یان به&zwnj;خۆ هه&zwnj;ڵواسین به&zwnj;خه&zwnj;ڵکیدا بۆ یارمه&zwnj;تیدانیان، یان پشتگرتنیان (له&zwnj;و سه&zwnj;رده&zwnj;مانه&zwnj;دا که ئیسلام فه&zwnj;رمانڕه&zwnj;وا بووه به&zwnj;ئاسووده&zwnj;یی ژیاون، هه&zwnj;رچه&zwnj;ند له ژێره&zwnj;وه هه&zwnj;ر خه&zwnj;ریکی پیلانگێڕان بوون، له سه&zwnj;ده&zwnj;ی بیسته&zwnj;میشدا توانییان ئه&zwnj;مریکاو وڵاتانی ڕۆژئاواو هه&zwnj;ندێ وڵاتی تریش، بکه&zwnj;نه پشتیوان و دارده&zwnj;ستی خۆیان). ئه&zwnj;وانه شایسته&zwnj;ی خه&zwnj;شم و قینه&zwnj;ی خوا بوون و زه&zwnj;لیلی و خه&zwnj;جاڵه&zwnj;تی به&zwnj;شیانه&zwnj;، چونکه ئه&zwnj;وانه بڕوایان نیه به&zwnj;ئایه&zwnj;ته&zwnj;کانی خواو پێغه&zwnj;مبه&zwnj;رانیش به ناحه&zwnj;ق ده&zwnj;کوژن، ئه&zwnj;م یاخی بوون و سه&zwnj;رشۆڕیه&zwnj;یان، به&zwnj;هۆی سه&zwnj;رپێچی و گوناهباریانه&zwnj;وه&zwnj;یه&zwnj;، ئه&zwnj;وانه هه&zwnj;میشه له سنوور ده&zwnj;رده&zwnj;چوون و ده&zwnj;رده&zwnj;چن.';
$t[113] = '(گاور و جوو) هه&zwnj;موویان یه&zwnj;کسان و چوونیه&zwnj;ک نین، به&zwnj;ڵکو له خاوه&zwnj;نانی کتێب ده&zwnj;سته&zwnj;یه&zwnj;ک له ئیماندارانیان هه&zwnj;ن، ئایه&zwnj;ته&zwnj;کانی خوا له&zwnj;کاته&zwnj;کانی شه&zwnj;ودا ده&zwnj;خوێننه&zwnj;وه&zwnj;و نوێژده&zwnj;که&zwnj;ن و سوژده ده&zwnj;به&zwnj;ن (موسڵمانن به&zwnj;هه&zwnj;موو واتایه&zwnj;ک).';
$t[114] = 'ئه&zwnj;وانه ئیمان و باوه&zwnj;ڕیان به&zwnj;خواو به ڕۆژی دوایی هه&zwnj;یه و فه&zwnj;رمان ده&zwnj;ده&zwnj;ن به چاکه و قه&zwnj;ده&zwnj;غه&zwnj;ی خراپه ده&zwnj;که&zwnj;ن و به په&zwnj;له و تاڵوکه&zwnj;ن بۆ ئه&zwnj;نجامدانی هه&zwnj;موو خێرو چاکه&zwnj;یه&zwnj;ک، بێگومان ئه&zwnj;وانه له ڕیزی چاکه&zwnj;کار و چاکه&zwnj;خوازاندان.';
$t[115] = '(ئه&zwnj;و ئیماندارانه&zwnj;) هه&zwnj;ر چاکه&zwnj;یه&zwnj;ک ئه&zwnj;نجام بده&zwnj;ن به&zwnj;فیڕۆ ناچێت، خوایش زانایه به پارێزکارو دینداران.';
$t[116] = 'به&zwnj;ڕاستی ئه&zwnj;وانه&zwnj;ی کافرن هه&zwnj;رگیز ماڵ و منداڵیان فریایان ناکه&zwnj;وێت و له ده&zwnj;ست خه&zwnj;شم و قینی خوا ڕزگاریان نابێت، ئه&zwnj;وان نیشته&zwnj;جێی ناو دۆزه&zwnj;خن و تیایدا ژیانی هه&zwnj;میشه&zwnj;یی ده&zwnj;به&zwnj;نه&zwnj;سه&zwnj;ر.';
$t[117] = 'نمونه&zwnj;ی (ئه&zwnj;و ماڵ و داراییه&zwnj;ی که&zwnj;) ده&zwnj;یبه&zwnj;خشن له&zwnj;م ژیانی دنیایه&zwnj;دا وه&zwnj;ک ڕه&zwnj;شه&zwnj;بایه&zwnj;کی زۆر سارد وایه بدات به&zwnj;سه&zwnj;ر کێڵگه&zwnj;ی که&zwnj;سانێکدا که سته&zwnj;میان له خۆیان کردبێت (به&zwnj;هۆی گوناهو تاوانه&zwnj;وه&zwnj;)، هه&zwnj;مووی تیا به&zwnj;رێت، جا وه&zwnj;نه&zwnj;بێت خوا سته&zwnj;می لێکردبن به&zwnj;ڵکو هه&zwnj;ر خۆیان سته&zwnj;میان له خۆیان کردووه&zwnj;.';
$t[118] = 'ئه&zwnj;ی ئه&zwnj;وانه&zwnj;ی باوه&zwnj;ڕتان هێناوه&zwnj;، به که&zwnj;سانێکی تر جگه له خۆتان متمانه مه&zwnj;که&zwnj;ن و نهێنی خۆتان لایان مه&zwnj;درکێنن، چونکه ئه&zwnj;وان کۆڵناده&zwnj;ن له دروستکردنی ته&zwnj;نگانه&zwnj;و گیروگرفت و ناخۆشی بۆتان، ئاواتیان ئه&zwnj;وه&zwnj;یه دووچاری ئازارو کێشه ببن، به&zwnj;ئاشکرا ڕق و کینه له ده&zwnj;م و دویاندا دیاره&zwnj;، ئه&zwnj;وه&zwnj;ی له سینه&zwnj;یاندا شاردویانه&zwnj;ته&zwnj;وه زۆر گه&zwnj;وره&zwnj;تره له&zwnj;وه&zwnj;ی که ده&zwnj;ریده&zwnj;خه&zwnj;ن، بێگومان ئێمه نیشانه&zwnj;و به&zwnj;ڵگه&zwnj;مان بۆ دیاری کردوون (تا دۆست له دوژمن جیابکه&zwnj;نه&zwnj;وه&zwnj;)، ئه&zwnj;گه&zwnj;ر عه&zwnj;قڵ و ژیریتان بخه&zwnj;نه&zwnj;کار.';
$t[119] = 'ئاگاداربن! ئێوه ئه&zwnj;وانتان خۆش ده&zwnj;وێت، به&zwnj;ڵام ئه&zwnj;وان ئێوه&zwnj;یان خۆش ناوێت و باوه&zwnj;ڕتان به&zwnj;هه&zwnj;موو کتێبه ئاسمانیه&zwnj;کانیش هه&zwnj;یه (به&zwnj;ڵام ئه&zwnj;وان باوه&zwnj;ڕیان به محمد (صلی الله علیه وسلم) و به قورئان نیه&zwnj;)، کاتێکیش پێتان ده&zwnj;گه&zwnj;ن ده&zwnj;ڵێن: ئێمه ئیماندارین به&zwnj;ڵام کاتێک به ته&zwnj;نها ده&zwnj;بن، په&zwnj;نجه&zwnj;ی خۆیان ده&zwnj;گه&zwnj;زن ئه&zwnj;وه&zwnj;نده ڕق ئه&zwnj;ستوور و داخ له دڵن، پێیان بڵێ: ده&zwnj;ک بمرن له داخا، به&zwnj;ڕاستی خوا خۆی زانایه به&zwnj;وه&zwnj;ی که له دڵ و ده&zwnj;روون و سینه&zwnj;کاندا حه&zwnj;شاردراوه و (چاکتان ده&zwnj;ناسێت).';
$t[120] = 'ئه&zwnj;گه&zwnj;ر چاکه&zwnj;یه&zwnj;کتان تووش بوو، یان خێرێک ڕووی تێکردن، ئه&zwnj;وان پێی غه&zwnj;مبار ده&zwnj;بن و پێیان ناخۆشه&zwnj;، خۆ ئه&zwnj;گه&zwnj;ر به&zwnj;ڵاو ناخۆشییه&zwnj;کتان بۆ پێش بێت، ئه&zwnj;وان پێی دڵخۆش ده&zwnj;بن، جا ئه&zwnj;گه&zwnj;ر خۆگر بن و پارێزکار بن، پیلان و ته&zwnj;ڵه&zwnj;که&zwnj;ی ئه&zwnj;وانه هیچ زیانێکتان پێناگه&zwnj;یه&zwnj;نێت، چونکه به&zwnj;ڕاستی خوا ده&zwnj;وری پیلان و پلانیانی داوه&zwnj;و ئاگایه به هه&zwnj;موو کارو کرده&zwnj;وه&zwnj;و نه&zwnj;خشه&zwnj;یه&zwnj;کیان.';
$t[121] = '(لێره&zwnj;وه قورئان له غه&zwnj;زای ئوحود ده&zwnj;دوێت: یادی ئیمانداران بخه&zwnj;ره&zwnj;وه&zwnj;) کاتێک به&zwnj;یانی زوو له ماڵ ده&zwnj;رچوویت، شوێنی جه&zwnj;نگت بۆ ئیمانداران دیاری کرد (تیر ئه&zwnj;ندازانی له&zwnj;سه&zwnj;ر گرده&zwnj;که دامه&zwnj;زراندو فه&zwnj;رمووی پێیان: ئێمه سه&zwnj;رکه&zwnj;وتوو بووین، یان شکستمان هێنا، ئێوه شوێنی خۆتان به&zwnj;جێ مه&zwnj;هێڵن) و شوێنی تریشی بۆ ئه&zwnj;وانی تر دیاری کرد، خوای گه&zwnj;وره&zwnj;ش بیسه&zwnj;ره&zwnj;، (به&zwnj;گوفتارتان) زانایه (به بیرو بۆچوونتان)...';
$t[122] = 'ئه&zwnj;وسا خه&zwnj;ریکبوو دوو ده&zwnj;سته له ئێوه په&zwnj;شیمان بنه&zwnj;وه&zwnj;و (له نیوه&zwnj;ی ڕێ بگه&zwnj;ڕێنه&zwnj;وه&zwnj;)، به&zwnj;ڵام خوا یارو یاوه&zwnj;ریان بوو (دڵیانی ده&zwnj;مه&zwnj;زراند له&zwnj;سه&zwnj;ر فه&zwnj;رمانبه&zwnj;رداری) ده&zwnj;با ئیمانداران هه&zwnj;ر پشت به خوا ببه&zwnj;ستن.';
$t[123] = 'بێگومان خوا له غه&zwnj;زای به&zwnj;دردا سه&zwnj;رکه&zwnj;وتوی کردن، هه&zwnj;رچه&zwnj;نده ئێوه (له ژماره و چه&zwnj;کدا) لاوازو که&zwnj;م بوون، ده&zwnj;ی که&zwnj;واته له خوا بترسن، بۆ ئه&zwnj;وه&zwnj;ی سوپاسگوزار بن.';
$t[124] = '(یادیان بهێنه&zwnj;) کاتێک به ئیماندارانت ده&zwnj;وت: ئایا ئه&zwnj;وه&zwnj;نده به&zwnj;س نیه که په&zwnj;روه&zwnj;ردگارتان به سێ هه&zwnj;زار فریشته کۆمه&zwnj;کتان بکات و دایانبه&zwnj;زێنێت؟!';
$t[125] = 'به&zwnj;ڵێ ئه&zwnj;گه&zwnj;ر خۆڕاگر بن و پارێزکار بن هه&zwnj;ر ئێستا دێن بۆ لاتان و په&zwnj;روه&zwnj;ردگارتان پێنج هه&zwnj;زار فریشته&zwnj;ی نیشانه&zwnj;دارتان بۆ ڕه&zwnj;وانه ده&zwnj;کات، بۆ ئه&zwnj;وه&zwnj;ی کۆمه&zwnj;کیتان بکه&zwnj;ن.';
$t[126] = 'جا ئه&zwnj;و (هاوکاری) فریشتانه له&zwnj;لایه&zwnj;ن خواوه ته&zwnj;نها مژده&zwnj;یه&zwnj;ک بوو بۆ ئێوه&zwnj;، تادڵه&zwnj;کانتان پێی دامه&zwnj;زراوتر بێت، ئه&zwnj;گینا سه&zwnj;رکه&zwnj;وتن ته&zwnj;نها له&zwnj;لایه&zwnj;ن خوای باڵاده&zwnj;ست و داناوه&zwnj;یه&zwnj;و هه&zwnj;ر ئه&zwnj;و ده&zwnj;یبه&zwnj;خشێت.';
$t[127] = '(ئه&zwnj;و یاریده&zwnj;ی فریشتانه&zwnj;) له&zwnj;به&zwnj;رئه&zwnj;وه بوو که&zwnj;: لایه&zwnj;ک له هێزی کافره&zwnj;کان دابڕێت، تا سه&zwnj;رکوت و نابووتیان بکات و ئه&zwnj;وسا به نائومێدیی و خه&zwnj;جاڵه&zwnj;تییه&zwnj;وه بگه&zwnj;ڕێنه&zwnj;وه&zwnj;.';
$t[128] = 'بێگومان تۆ هیچ شتێکت له کاروباری به&zwnj;نده&zwnj;کان به&zwnj;ده&zwnj;ست نیه (به&zwnj;ڵکو هه&zwnj;مووشت به&zwnj;ده&zwnj;ست خوا خۆیه&zwnj;تی) ئه&zwnj;گه&zwnj;ر ته&zwnj;وبه&zwnj;و په&zwnj;شیمانیان لێ وه&zwnj;ربگرێت، یان سزاو ئازاریان بدات، (سه&zwnj;ربه&zwnj;سته&zwnj;)، چونکه ئه&zwnj;وانه سته&zwnj;مکارن.';
$t[129] = 'هه&zwnj;رچی له ئاسمانه&zwnj;کان و زه&zwnj;ویدا هه&zwnj;یه خوای گه&zwnj;وره خاوه&zwnj;نیانه&zwnj;، له هه&zwnj;ر که&zwnj;س بیه&zwnj;وێت خۆش ده&zwnj;بێت و (چاو پۆشی زۆره&zwnj;)، هه&zwnj;رکه&zwnj;سیش (شایسته&zwnj;ی سزابێت) سزای ده&zwnj;دات، خوای گه&zwnj;وره&zwnj;ش لێخۆشبوو میهره&zwnj;بانه&zwnj;.';
$t[130] = '(ئه&zwnj;م ئایه&zwnj;ته تێکهه&zwnj;ڵکێشی باسی غه&zwnj;زای ئوحود کراوه بۆ ئه&zwnj;وه&zwnj;ی بسه&zwnj;لمێت که ئه&zwnj;م ئاینه هه&zwnj;موو چه&zwnj;مکه&zwnj;کانی ژیان ده&zwnj;گرێته&zwnj;وه&zwnj;، بۆیه ڕوو به&zwnj;ئیمانداران ده&zwnj;فه&zwnj;رموێت): ئه&zwnj;ی ئه&zwnj;وانه&zwnj;ی باوه&zwnj;ڕتان هێناوه سوو به&zwnj;چه&zwnj;ند قات وچه&zwnj;ند به&zwnj;رامبه&zwnj;ر مه&zwnj;خۆن (ئه&zwnj;مه&zwnj;ش قۆناغێک بوو له حه&zwnj;رامکردنی یه&zwnj;کجاریی)، له خوا بترسن، بۆ ئه&zwnj;وه&zwnj;ی سه&zwnj;رفراز بن.';
$t[131] = '(هه&zwnj;وڵبده&zwnj;ن) خۆتان بپارێزن له&zwnj;و ئاگره&zwnj;ی که ئاماده کراوه بۆ کافرو بێ باوه&zwnj;ڕان.';
$t[132] = 'گوێڕایه&zwnj;ڵ و فه&zwnj;رمانبه&zwnj;رداری خواو پێغه&zwnj;مبه&zwnj;ره&zwnj;که&zwnj;ی بن بۆ ئه&zwnj;وه&zwnj;ی ڕه&zwnj;حمتان پێ بکرێت.';
$t[133] = 'به&zwnj;په&zwnj;له بن و پێشبڕکێ بکه&zwnj;ن تا ده&zwnj;گه&zwnj;نه لێخۆشبوونی په&zwnj;روه&zwnj;ردگارتان و به&zwnj;هه&zwnj;شتێک که پاناییه&zwnj;که&zwnj;ی ئاسمان و زه&zwnj;وی گرتۆته&zwnj;وه&zwnj;و ئاماده کراوه بۆ پارێزکاران، (پێغه&zwnj;مبه&zwnj;ریش (صلی الله علیه وسلم) ده&zwnj;فه&zwnj;رموێت: مه&zwnj;نزڵگای هه&zwnj;ندێ ده&zwnj;بینن وه&zwnj;کو چۆن ئه&zwnj;ستێره&zwnj;ی گه&zwnj;ش ده&zwnj;بینن له دووره&zwnj;وه&zwnj;، فه&zwnj;رموویانه ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ری خوا: ڕه&zwnj;نگه ئه&zwnj;وانه مه&zwnj;نزڵگای پێغه&zwnj;مبه&zwnj;ران بن، فه&zwnj;رمویه&zwnj;تی: نه&zwnj;خێر، به&zwnj;ڵکو مه&zwnj;نزڵگای که&zwnj;سانێکن که باوه&zwnj;ڕیان به خواو پێغه&zwnj;مبه&zwnj;ره&zwnj;که&zwnj;ی هێناوه&zwnj;، جا له&zwnj;م سه&zwnj;رده&zwnj;مانه&zwnj;ی دوایدا زانراوه ئه&zwnj;ستێره&zwnj;ی چه&zwnj;نده گه&zwnj;وره گه&zwnj;وره هه&zwnj;یه که خۆر له&zwnj;ئاستیاندا زۆر بچوکه چ جای زه&zwnj;وی!!)';
$t[134] = 'ئه&zwnj;وانه&zwnj;ی له خۆشی و ناخۆشیدا، له هه&zwnj;رزانی و گرانیدا ماڵ و سامانیان ده&zwnj;به&zwnj;خشن، ڕق و کینه&zwnj;ی خۆیان ده&zwnj;خۆنه&zwnj;وه&zwnj;و خۆگرن، له خه&zwnj;ڵکی خۆش ده&zwnj;بن و لێبووهرده&zwnj;ن، خوای میهره&zwnj;بانیش چاکه&zwnj;کارو چاکه&zwnj;خوازانی خۆش ده&zwnj;وێت.';
$t[135] = 'ئه&zwnj;وانه&zwnj;ش که تاوان یان گوناهێک ئه&zwnj;نجام ده&zwnj;ده&zwnj;ن، یاخود سته&zwnj;م له خۆیان ده&zwnj;که&zwnj;ن (دوایی به خۆیاندا دێنه&zwnj;وه&zwnj;و سزای) خوا دێته&zwnj;وه یادیان، ئه&zwnj;وسا داوای لێخۆشبوون ده&zwnj;که&zwnj;ن، جا کێ هه&zwnj;یه که له گوناهان خۆش ببێت جگه له خوا؟! هه&zwnj;روه&zwnj;ها له&zwnj;سه&zwnj;ر گوناهو تاوانیان به&zwnj;رده&zwnj;وام نابن، له کاتێکدا ده&zwnj;زانن (ئه&zwnj;وه تاوانه&zwnj;).';
$t[136] = 'ئا ئه&zwnj;وانه پاداشتیان لێخۆشبوون و لێبووردنه له لایه&zwnj;ن په&zwnj;روه&zwnj;ردگاریانه&zwnj;وه له&zwnj;گه&zwnj;ڵ باخه&zwnj;کانی به&zwnj;هه&zwnj;شتدا که چه&zwnj;ندان ڕووبار به&zwnj;ژێر دره&zwnj;خت و به به&zwnj;رده&zwnj;م کۆشکه&zwnj;کانیاندا ده&zwnj;ڕوات و ژیانی هه&zwnj;میشه&zwnj;یی تیایدا ده&zwnj;به&zwnj;نه&zwnj;سه&zwnj;ر، ئه&zwnj;م به&zwnj;هره&zwnj;یه پاداشتێکی زۆر چاکی کۆششکارانه (چونکه دوای ته&zwnj;وبه&zwnj;که&zwnj;یان له هه&zwnj;وڵ و کۆششی به&zwnj;ده&zwnj;ست هێنانی ڕه&zwnj;زامه&zwnj;ندیی خوادا ده&zwnj;بن).';
$t[137] = 'به&zwnj;ڕاستی پێش ئێوه چه&zwnj;ندان یاساو به&zwnj;رنامه تێپه&zwnj;ڕیوه (له&zwnj;لایه&zwnj;ن خواوه که سه&zwnj;رئه&zwnj;نجام ئیماندارانی سه&zwnj;رخستووه و کافرانی له&zwnj;ناو بردووه&zwnj;) ده ئێوه&zwnj;ش به زه&zwnj;ویدا بگه&zwnj;ڕێن و ته&zwnj;ماشا بکه&zwnj;ن و ئینجا سه&zwnj;رنج بده&zwnj;ن و وردببنه&zwnj;وه&zwnj;: بزانن سه&zwnj;رئه&zwnj;نجامی ئه&zwnj;وانه چۆن بووه که به&zwnj;رنامه&zwnj;ی پێغه&zwnj;مبه&zwnj;رانی خوایان به درۆ زانیوه&zwnj;.';
$t[138] = 'ئا ئه&zwnj;مه&zwnj;ی که باسکرا به&zwnj;یاننامه&zwnj;و ڕوونکردنه&zwnj;وه&zwnj;یه بۆ خه&zwnj;ڵکی به گشتی و ڕێنمووبه&zwnj;خش و ئامۆژگاریشه بۆ پارێزکاران به تایبه&zwnj;تی.';
$t[139] = '(ئه&zwnj;ی ئیمانداران) کۆڵ مه&zwnj;ده&zwnj;ن و ساردمه&zwnj;بنه&zwnj;وه و خه&zwnj;فه&zwnj;ت مه&zwnj;خۆن، چونکه هه&zwnj;ر ئێوه سه&zwnj;ربه&zwnj;رزو سه&zwnj;رفرازو سه&zwnj;رکه&zwnj;وتووترن ئه&zwnj;گه&zwnj;ر ئیمانداری چاک و ڕاست و دروست بن.';
$t[140] = 'ئه&zwnj;گه&zwnj;ر زام و سه&zwnj;ختی و ناخۆشیه&zwnj;ک (له جه&zwnj;نگی ئوحودا)دا تووشی ئێوه هاتووه&zwnj;، ئه&zwnj;و خه&zwnj;ڵکه&zwnj;ش (له&zwnj;جه&zwnj;نگی به&zwnj;دردا) تووشی هه&zwnj;مان زام و سه&zwnj;ختی و ناخۆشی هاتن، ئێمه ئه&zwnj;و ڕۆژگارانه ده&zwnj;ستاو ده&zwnj;ست پێ ده&zwnj;که&zwnj;ین له&zwnj;ناو خه&zwnj;ڵکیدا (تا له جیهانی واقعیشدا) ئه&zwnj;وانه&zwnj;ی باوه&zwnj;ڕیان هێناوه خوا ده&zwnj;ریان بخات و هه&zwnj;ندێکتان بکات به شایه&zwnj;ت و به پله&zwnj;ی شه&zwnj;هیدی بگه&zwnj;ن، بێگومان خوا سته&zwnj;مکارانی خۆش ناوێت.';
$t[141] = 'بێگومان خوای په&zwnj;روه&zwnj;ردگار (تاقی کردنه&zwnj;وه پێش ده&zwnj;هێنێت) تا ئه&zwnj;وانه&zwnj;ی ئیماندارن پاک و بێگه&zwnj;ردیان بکات، کافرانیش نابووت و ڕیسوا بکات.';
$t[142] = 'یان واگومانتان ده&zwnj;برد (هه&zwnj;روا به&zwnj;ئاسانی) ده&zwnj;چنه به&zwnj;هه&zwnj;شته&zwnj;وه&zwnj;؟! که هێشتا خوا (له جیهانی واقعدا) ده&zwnj;ریشی نه&zwnj;خستووه کامتان هه&zwnj;وڵ و کۆشش و جیهادی کردووه&zwnj;و کامتان خۆڕاگرو ئارامگر بووه&zwnj;...';
$t[143] = 'له&zwnj;ڕاستیدا ئێوه ئاواتی مردنتان ده&zwnj;خواست پێش ئه&zwnj;وه&zwnj;ی پێی بگه&zwnj;ن (ده&zwnj;تانوت: که&zwnj;ی جه&zwnj;نگ ده&zwnj;بێت تا شه&zwnj;هید ببین) خۆ له ڕاستیدا مه&zwnj;رگتان به&zwnj;چاوی خۆتان بینی له کاتێکدا ئێوه چاوه&zwnj;ڕوانی مه&zwnj;رگ بوون.';
$t[144] = '(محمد (صلی الله علیه وسلم) ته&zwnj;نها پێغه&zwnj;مبه&zwnj;رێک نه&zwnj;بووه وه&zwnj;فات بکات، بێگومان پێغه&zwnj;مبه&zwnj;رانی پێش ئه&zwnj;میش (وه&zwnj;فاتیان کردووه&zwnj;)، ئایا ئه&zwnj;گه&zwnj;ر وه&zwnj;فاتی کرد یان کوژراو شه&zwnj;هید بوو، ئێوه پاشگه&zwnj;ز ده&zwnj;بنه&zwnj;وه و هه&zwnj;ڵده&zwnj;گه&zwnj;ڕێنه&zwnj;وه&zwnj;؟! (له&zwnj;و دین و ئاینه&zwnj;ی که پێی ڕاگه&zwnj;یاندوون) خۆ ئه&zwnj;وه&zwnj;ی هه&zwnj;ڵگه&zwnj;ڕێته&zwnj;وه و پاشگه&zwnj;زبێته&zwnj;وه&zwnj;، ئه&zwnj;وه هه&zwnj;رگیز زیان به&zwnj;خوا ناگه&zwnj;یه&zwnj;نێت، له داهاتویه&zwnj;کی نزیکدا خوای گه&zwnj;وره پاداشتی سوپاسگوزاران ده&zwnj;داته&zwnj;وه&zwnj;.';
$t[145] = 'هیچ که&zwnj;سێکیش نامرێت مه&zwnj;گه&zwnj;ر به&zwnj;ویستی خوا نه&zwnj;بێت، له کات و وه&zwnj;ختی دیاری کراودا (ئه&zwnj;مه&zwnj;ش خۆی هانده&zwnj;رێکه بۆ ئیماندار که له مه&zwnj;رگ نه&zwnj;ترسێت) جا ئه&zwnj;وه&zwnj;ی پاداشتی دنیای ده&zwnj;وێت هه&zwnj;ندێکی پێده&zwnj;به&zwnj;خشین، ئه&zwnj;وه&zwnj;ش قیامه&zwnj;تی ده&zwnj;وێت هه&zwnj;ندێکی پێده&zwnj;به&zwnj;خشین، له داهاتویه&zwnj;کی نزیکیشدا پاداشتی سوپاسگوزاران ده&zwnj;ده&zwnj;ینه&zwnj;وه&zwnj;.';
$t[146] = 'چه&zwnj;نده&zwnj;ها پێغه&zwnj;مبه&zwnj;ر هه&zwnj;بوون که خواناس و پیاوچاکانی زۆر له&zwnj;گه&zwnj;ڵیدا بوون و دژ به دوژمنان جه&zwnj;نگاون، جا ئه&zwnj;وانه هیچ کات سستی و بێزاری و بێتاقه&zwnj;تی ڕووی تێنه&zwnj;کردوون به&zwnj;هۆی ئه&zwnj;وه&zwnj;ی که تووشیان هاتووه له پێناو ڕێبازی خوادا، لاوازو بێ هێزو سه&zwnj;رشۆریش نه&zwnj;بوون بۆ دوژمنان، خوای گه&zwnj;وره&zwnj;ش ئه&zwnj;و ئارامگرو خۆڕاگرانه&zwnj;ی خۆشده&zwnj;وێت.';
$t[147] = 'ئه&zwnj;وکاته گوفتاریان ته&zwnj;نها ئه&zwnj;مه بووه که وتویانه&zwnj;: په&zwnj;روه&zwnj;ردگارا له گوناهو هه&zwnj;ڵه&zwnj;کانمان ببوره&zwnj;و له زیاده&zwnj;ڕه&zwnj;وی کاروبارمان خۆش ببه&zwnj;، دامه&zwnj;زراومان بکه&zwnj;، سه&zwnj;رمان بخه به&zwnj;سه&zwnj;ر ئه&zwnj;م گه&zwnj;له بێباوه&zwnj;ڕه&zwnj;دا.';
$t[148] = 'ئه&zwnj;وسا ئیتر خوا پاداشتی دنیای پێبه&zwnj;خشین (به&zwnj;سه&zwnj;رکه&zwnj;وتن و پایه&zwnj;داریی) پاداشتی چاکی قیامه&zwnj;تیشی بۆ مسۆگه&zwnj;ر کردن و خوا ئه&zwnj;و چاکه&zwnj;کارانه&zwnj;ی خۆش ده&zwnj;وێت.';
$t[149] = 'ئه&zwnj;ی ئه&zwnj;وانه&zwnj;ی باوه&zwnj;ڕتان هێناوه ئه&zwnj;گه&zwnj;ر ملکه&zwnj;چ و گوێڕایه&zwnj;ڵی ئه&zwnj;وانه بن که کافر و بێ دینن، ئه&zwnj;وه به&zwnj;ره&zwnj;ودوا ده&zwnj;تانگێڕنه&zwnj;وه و پاشگه&zwnj;زتان ده&zwnj;که&zwnj;نه&zwnj;وه و له دین وه&zwnj;رتانده&zwnj;گێڕن، ئه&zwnj;وسا ئیتر به دۆڕاوی ده&zwnj;گه&zwnj;ڕێنه&zwnj;وه&zwnj;.';
$t[150] = '(دڵنیابن که خوانه&zwnj;ناسان یارو یاوه&zwnj;ری ئێوه نین) به&zwnj;ڵکو خوا یارو یاوه&zwnj;رتانه&zwnj;، بێگومان ئه&zwnj;و زاته چاکترین پشتیوان و یاریده&zwnj;ه&zwnj;رتانه&zwnj;.';
$t[151] = '(ئه&zwnj;ی گرۆی ئیمانداران دڵنیابن) که له&zwnj;مه&zwnj;ودوا ئێمه&zwnj;: ترس و بیم فڕێ ده&zwnj;ده&zwnj;ینه دڵی ئه&zwnj;وانه&zwnj;ی که بێ باوه&zwnj;ڕن، به&zwnj;هۆی ئه&zwnj;وه&zwnj;ی شه&zwnj;ریک و هاوه&zwnj;ڵیان بۆ خوا بڕیارداوه بێ ئه&zwnj;وه&zwnj;ی خوا هیچ ده&zwnj;سه&zwnj;ڵاتێکی پێدابن (بۆ دانانی هاوه&zwnj;ڵگه&zwnj;ری)، ئه&zwnj;وانه سه&zwnj;ره&zwnj;نجامی گه&zwnj;ڕانه&zwnj;وه&zwnj;یان ئاگری دۆزه&zwnj;خه که چه&zwnj;ند ناخۆش و خراپه جێگه&zwnj;ی سته&zwnj;مکاران.';
$t[152] = 'سوێند بێت، خوا به&zwnj;ڵێنی خۆی بۆ ئێوه به&zwnj;ڕاستی برده&zwnj;سه&zwnj;ر (له&zwnj;سه&zwnj;ره&zwnj;تای جه&zwnj;نگی ئوحود)دا، هه&zwnj;ر به&zwnj;مۆڵه&zwnj;ت و یارمه&zwnj;تی ئه&zwnj;و، (دوژمنانتان) به&zwnj;گیر هێناو تێکتان شکاندن، هه&zwnj;تا ئه&zwnj;وکاته&zwnj;ی که ساردیی ڕووی تێکردن و بوو به کێشه&zwnj;تان له&zwnj;سه&zwnj;ر هه&zwnj;ندێک شت و یاخی بوون (له فه&zwnj;رمانی پێغه&zwnj;مبه&zwnj;ر (صلی الله علیه وسلم) سه&zwnj;نگه&zwnj;رتان چۆڵ کرد) دوای ئه&zwnj;وه&zwnj;ی هه&zwnj;ندێک شتی نیشاندان که حه&zwnj;زتان لێده&zwnj;کرد، هه&zwnj;تانه دنیای ده&zwnj;وێت و هه&zwnj;تانه قیامه&zwnj;تی مه&zwnj;به&zwnj;سته&zwnj;، له&zwnj;وه&zwnj;ودوا ده&zwnj;ستی ئێوه&zwnj;ی کۆتاکرد (له دوژمنان) تا تاقیتان بکاته&zwnj;وه&zwnj;، سوێند بێت، به&zwnj;ڕاستی خوا چاوپۆشی لێکردن و لێتان خۆشبوو و خوا فه&zwnj;زڵ و ڕێزی هه&zwnj;یه بۆ ئیمانداران.';
$t[153] = 'ئیتر ئه&zwnj;وه&zwnj;بوو که (به&zwnj;گیرهاتن و کشانه&zwnj;وه و شپرزه و په&zwnj;رت و بڵاوبوون) خۆتان هه&zwnj;ڵده&zwnj;گێڕا به&zwnj;کێوی ئوحود و ده&zwnj;ورو به&zwnj;ریداو ئاوڕتان له که&zwnj;س نه&zwnj;ده&zwnj;دایه&zwnj;وه&zwnj;، له&zwnj;کاتێکدا پێغه&zwnj;مبه&zwnj;ریش به&zwnj;دواتاندا بانگی ده&zwnj;کردن له لاکه&zwnj;ی تره&zwnj;وه&zwnj;: (ئه&zwnj;ی به&zwnj;نده&zwnj;کانی خوا بگه&zwnj;ڕێنه&zwnj;وه و ڕامه&zwnj;که&zwnj;ن)، جا ئه&zwnj;وه&zwnj;بوو ئێوه خه&zwnj;م و خه&zwnj;فه&zwnj;تتان کرده دڵی پێغه&zwnj;مبه&zwnj;ره&zwnj;وه (به نافه&zwnj;رمانیتان خوای گه&zwnj;وره&zwnj;ش) خه&zwnj;م و په&zwnj;ژاره&zwnj;ی کرده دڵی ئێوه&zwnj;وه (به&zwnj;ده&zwnj;نگو باسی بریندار بوونی پێغه&zwnj;مبه&zwnj;رو شه&zwnj;هید بوونی) تاوه&zwnj;کو خه&zwnj;فه&zwnj;ت نه&zwnj;خۆن بۆ ئه&zwnj;وه&zwnj;ی له ده&zwnj;ستتان چووه و ئه&zwnj;وه&zwnj;ش که خوا تووشی کردوون و به&zwnj;سه&zwnj;ری هێنان (دیاره هاوه&zwnj;ڵان پێغه&zwnj;مبه&zwnj;ریان له&zwnj;خۆیان و له هه&zwnj;موو شت لاخۆشه&zwnj;ویستتر بووه&zwnj;) خواش ئاگاداره له&zwnj;و کاروکرده&zwnj;وانه&zwnj;ی که ئه&zwnj;نجامی ده&zwnj;ده&zwnj;ن.';
$t[154] = 'پاشان، دوای ئه&zwnj;و غه&zwnj;م و په&zwnj;ژارایه ئارامیی و هێمنیی به&zwnj;سه&zwnj;ردا دابه&zwnj;زاندن که وه&zwnj;نه&zwnj;وزێک بوو ده&zwnj;سته&zwnj;یه&zwnj;کتانی داگرتبوو، ده&zwnj;سته&zwnj;یه&zwnj;کی تریش به&zwnj;ڕاستی خه&zwnj;می خۆیان بوو، گومانی ناڕه&zwnj;وایان ده&zwnj;برد به&zwnj;خوای گه&zwnj;وره به گومانی نه&zwnj;فامانه&zwnj;، ده&zwnj;یانوت: ئایا ئێمه له&zwnj;م کاره&zwnj;دا (له به&zwnj;ده&zwnj;ستهێنانی سه&zwnj;رکه&zwnj;وتن) هیچ شتێکمان هه&zwnj;یه&zwnj;؟! (ئه&zwnj;وپشتیوانه&zwnj;ی که&zwnj;خوا به&zwnj;ڵێنی پێدابووین بۆ نه&zwnj;هات؟!) ئه&zwnj;ی محمد (صلی الله علیه وسلم) پێیان بڵێ: بێگومان هه&zwnj;موو شتێک به&zwnj;ده&zwnj;ست خوایه&zwnj;، ئه&zwnj;وانه هه&zwnj;ندێک شت ده&zwnj;شارنه&zwnj;وه له ده&zwnj;روونی خۆیاندا و بۆ تۆی ده&zwnj;رناخه&zwnj;ن، هه&zwnj;روه&zwnj;ها ده&zwnj;ڵێن: ئه&zwnj;گه&zwnj;ر (به&zwnj;ڵێنی سه&zwnj;رکه&zwnj;وتن ڕاست بوایه&zwnj;) ئێمه شتێکمان به&zwnj;ده&zwnj;ست بوایه ئالێره&zwnj;دا نه&zwnj;ده&zwnj;کوژراین!! پێیان بڵێ: ئه&zwnj;گه&zwnj;ر له&zwnj;ماڵی خۆشتاندا بوونایه&zwnj;، ئه&zwnj;وانه&zwnj;ی که بڕیاره بکوژرانایه&zwnj;، ده&zwnj;بوا هه&zwnj;روایان به&zwnj;سه&zwnj;ر بهاتایه&zwnj;و له شوێنی کوژرانیاندا ڕاکشانایه بێگومان خوا ئه&zwnj;م شتانه&zwnj;تان به&zwnj;سه&zwnj;ر ده&zwnj;هێنێت تا ئه&zwnj;وه&zwnj;ی له ده&zwnj;روونتاندا هه&zwnj;یه تاقی بکاته&zwnj;وه و چی له&zwnj;دڵه&zwnj;کانتاندا هه&zwnj;یه خاوێنی بکاته&zwnj;وه و حه&zwnj;قیقه&zwnj;تی ده&zwnj;ربخات، ئه&zwnj;و خوایه&zwnj;ش زانایه به&zwnj;وه&zwnj;ی که له ناخی سینه&zwnj;کاندا حه&zwnj;شار دراوه&zwnj;.';
$t[155] = 'بێگومان ئه&zwnj;وانه&zwnj;ی پشتیان هه&zwnj;ڵکرد (له جه&zwnj;نگی ئوحود) له ڕۆژی به&zwnj;یه&zwnj;کگه&zwnj;یشتنی هه&zwnj;ردوو ئۆردوو (ئۆردووی ئیمانداران و ئۆردووی بێ باوه&zwnj;ڕان)، ئه&zwnj;وانه ته&zwnj;نها شه&zwnj;یتان تووشی هه&zwnj;ڵه&zwnj;ی کردن له خشته&zwnj;ی بردن به&zwnj;هۆی هه&zwnj;ندێک گوناهه&zwnj;وه که ئه&zwnj;نجامیان دابوو، به&zwnj;ڕاستی خوا لێیان خۆشبوو چونکه بێگومان خوا لێخۆشبووه (له گوناهه&zwnj;که&zwnj;تان) و ئارامگرو خۆڕاگره (زوو تۆڵه له یاخی بوان ناسێنێت).';
$t[156] = 'ئه&zwnj;ی ئه&zwnj;وانه&zwnj;ی باوه&zwnj;ڕتان هێناوه وه&zwnj;کو ئه&zwnj;وانه مه&zwnj;بن که بێ باوه&zwnj;ڕ بوون، له&zwnj;کاتی سه&zwnj;فه&zwnj;ر به&zwnj;سه&zwnj;ر زه&zwnj;ویدا، یاخود کاتی جه&zwnj;نگ، به&zwnj;برایانی (هاوبیریان) ده&zwnj;وت: ئه&zwnj;گه&zwnj;ر لای ئێمه بوونایه نه ده&zwnj;مردن، نه ده&zwnj;کوژران!! بۆ ئه&zwnj;وه&zwnj;ی خوا ئه&zwnj;وه بکاته خه&zwnj;فه&zwnj;ت و ناخۆشی له دڵیاندا، خۆ هه&zwnj;ر خوا ژیان به&zwnj;خشه و مرێنه&zwnj;ره&zwnj;، خوا بینایه به&zwnj;و کارو کرده&zwnj;وانه&zwnj;ی که ئه&zwnj;نجامی ده&zwnj;ده&zwnj;ن.';
$t[157] = 'خۆ ئه&zwnj;گه&zwnj;ر بکوژرێن له پێناوی خوادا یاخود بمرن، لێخۆش بوون له&zwnj;لایه&zwnj;ن خواوه هاوڕێ له&zwnj;گه&zwnj;ڵ ڕه&zwnj;حمه&zwnj;ت، چاکتره له&zwnj;وه&zwnj;ی که خه&zwnj;ڵکی کۆی ده&zwnj;که&zwnj;نه&zwnj;وه&zwnj;.';
$t[158] = 'سوێند بێت ئه&zwnj;گه&zwnj;ر بمرن یان بکوژرێن هه&zwnj;ر بۆ لای خوا کۆ ده&zwnj;کرێنه&zwnj;وه&zwnj;.';
$t[159] = 'که&zwnj;وابوو (ئه&zwnj;ی محمد (صلی الله علیه وسلم) به&zwnj;&zwnj;هۆی ڕه&zwnj;حمه&zwnj;تی خواوه&zwnj;یه تۆ نه&zwnj;رم و هێمن بویت له&zwnj;گه&zwnj;ڵیاندا، خۆ ئه&zwnj;گه&zwnj;ر توڕه&zwnj;و قسه ڕه&zwnj;ق و بێ به&zwnj;زه&zwnj;یی بوویتایه&zwnj;، ئه&zwnj;وه هه&zwnj;موو له ده&zwnj;ورت بڵاوه&zwnj;یان ده&zwnj;کرد، که&zwnj;واته چاوپۆشیان لێبکه و لێیان ببوره&zwnj;و داوای لێخۆش بوونیان بۆ بکه&zwnj;، له کارو پێشهاتدا پرس و ڕایان پێ بکه&zwnj;، (دوای مه&zwnj;شوره&zwnj;ت و پرس و ڕا) ئه&zwnj;گه&zwnj;ر بڕیارتدا (کارێک ئه&zwnj;نجام بده&zwnj;یت، ئه&zwnj;نجامی بده&zwnj;و) پشت به&zwnj;خوا ببه&zwnj;سته&zwnj;، چونکه به&zwnj;ڕاستی خوا ئه&zwnj;وانه&zwnj;ی خۆشده&zwnj;وێت که پشتی پێده&zwnj;به&zwnj;ستن.';
$t[160] = 'ئه&zwnj;گه&zwnj;ر خوا سه&zwnj;رکه&zwnj;وتنتان پێ ببه&zwnj;خشێت ئه&zwnj;وه هیچ که&zwnj;س ناتوانێت سه&zwnj;رکه&zwnj;وێت به&zwnj;سه&zwnj;رتاندا، خۆ ئه&zwnj;گه&zwnj;ر پشتتان به&zwnj;ربدات، ئه&zwnj;وه کێ هه&zwnj;یه دوای ئه&zwnj;و کۆمه&zwnj;کتان بکات و سه&zwnj;رتان بخات، ده با ئه&zwnj;وانه&zwnj;ی ئیماندارن هه&zwnj;ر پشت به&zwnj;خوا ببه&zwnj;ستن.';
$t[161] = '(هه&zwnj;ندێک گومانیان برد که پێغه&zwnj;مبه&zwnj;ر (صلی الله علیه وسلم) له دا&zwnj;به&zwnj;شکردنی ده&zwnj;سکه&zwnj;وتدا نادادیی ده&zwnj;کات) بێگومان بۆ هیچ پێغه&zwnj;مبه&zwnj;رێک نه&zwnj;بووه که نادادی و خیانه&zwnj;ت بکات، هه&zwnj;ر که&zwnj;س ناحه&zwnj;قی و نادادی بکات له ڕۆژی قیامه&zwnj;تدا نادادیه&zwnj;که&zwnj;ی (یه&zwnj;خه&zwnj;ی پێ ده&zwnj;گرێت و) به&zwnj;کۆڵیه&zwnj;وه&zwnj;یه&zwnj;تی، له&zwnj;وه&zwnj;ودوا پاداشتی هه&zwnj;ر که&zwnj;سێک به&zwnj;گوێره&zwnj;ی کرده&zwnj;وه&zwnj;کانی ده&zwnj;درێته&zwnj;وه و ئه&zwnj;وانه سته&zwnj;میان لێناکرێت.';
$t[162] = 'ئایا ئه&zwnj;و که&zwnj;سه&zwnj;ی (به په&zwnj;رستن و چاکه&zwnj;کاری) شوێنی ڕه&zwnj;زامه&zwnj;ندیی خوا که&zwnj;وتبێت، وه&zwnj;کو ئه&zwnj;و که&zwnj;سه وایه (که به&zwnj;هۆی گوناهو تاوانه&zwnj;وه&zwnj;) شایسته&zwnj;ی خه&zwnj;شم و قینی خوا بێت؟ بێگومان شوێنی ئه&zwnj;و جۆره که&zwnj;سانه دۆزه&zwnj;خه که سه&zwnj;رنجامێکی ناخۆشه&zwnj;.';
$t[163] = 'هه&zwnj;ریه&zwnj;ک له&zwnj;و (دوو ده&zwnj;سته&zwnj;یه&zwnj;) پله&zwnj;و پایه&zwnj;ی خۆی هه&zwnj;یه لای خوا (له به&zwnj;هه&zwnj;شتی به&zwnj;ریندا یاخود له ناخی دۆزه&zwnj;خدا)، به&zwnj;ڕاستی خوا بینایه به&zwnj;و کارو کرده&zwnj;وانه&zwnj;ی که ئه&zwnj;نجامی ده&zwnj;ده&zwnj;ن.';
$t[164] = 'سوێند بێت بێگومان خوا منه&zwnj;تی ناوه&zwnj;ته سه&zwnj;ر ئیمانداران کاتێک پێغه&zwnj;مبه&zwnj;رێکی بۆ ڕه&zwnj;وانه کردوون له خۆیان ئایه&zwnj;ته&zwnj;کانی ئه&zwnj;ویان به&zwnj;سه&zwnj;ردا ده&zwnj;خوێنێته&zwnj;وه&zwnj;، دڵ و ده&zwnj;روونیان پاک و پوخت ده&zwnj;کاته&zwnj;وه&zwnj;، هه&zwnj;روه&zwnj;ها فێری قورئان و داناییان ده&zwnj;کات، به&zwnj;ڕاستی پێشتر له گومڕاییه&zwnj;کی ئاشکرادا ڕۆچوو بوون.';
$t[165] = 'ئایا کاتێک به&zwnj;ڵاو ناخۆشیه&zwnj;کتان تووشهات (له جه&zwnj;نگی ئوحود)دا بۆ ده&zwnj;ڵێن: ئه&zwnj;مه چی بوو؟ بۆ وامان به&zwnj;سه&zwnj;ر هات؟ له&zwnj;کوێوه ئه&zwnj;مه&zwnj;مان بۆهات؟ (ئه&zwnj;ی بۆ له&zwnj;یادیان چوو، خۆ له&zwnj;جه&zwnj;نگی به&zwnj;دردا) به&zwnj;ڕاستی ئێوه دووئه&zwnj;وه&zwnj;نده&zwnj;تان به&zwnj;وان کرد؟! ئینجا پێیان بڵێ: ئه&zwnj;و به&zwnj;ڵایه له&zwnj;لایه&zwnj;ن خۆتانه&zwnj;وه توشتان هات (کاتێک نافه&zwnj;رمانی پێغه&zwnj;مبه&zwnj;رتان کردو ته&zwnj;ماعی دنیا له ده&zwnj;روونتاندا جووڵا) به&zwnj;ڕاستی خوا ده&zwnj;سه&zwnj;ڵاتی به&zwnj;سه&zwnj;ر هه&zwnj;موو شتێکدا هه&zwnj;یه&zwnj;، (به&zwnj;نده چاکه&zwnj;کانیشی کاتێک هه&zwnj;ڵه بکه&zwnj;ن تووشی به&zwnj;ڵایان ده&zwnj;کات)...';
$t[166] = 'ئه&zwnj;وه&zwnj;ش که تووشتان بوو له ڕۆژی به&zwnj;یه&zwnj;ک گه&zwnj;یشتنی هه&zwnj;ردوو ئۆردووه&zwnj;که (له جه&zwnj;نگی ئوحود)دا ئه&zwnj;وه به فرمان و ویستی خوا بوو، تا ئیمانی باوه&zwnj;ڕداران ده&zwnj;ربخات...';
$t[167] = 'دووڕوویی و ناله&zwnj;باریی دووڕووه&zwnj;کانیش ده&zwnj;ربخات، کاتێک پێیان وترا: وه&zwnj;رن بجه&zwnj;نگن له پێناوی خوادا، یان (هیچ نه&zwnj;بێت) به&zwnj;رگری له خۆتان و ماڵ و منداڵتان بکه&zwnj;ن، وتیان: ئه&zwnj;گه&zwnj;ر بمانزانیایه شه&zwnj;ڕو جه&zwnj;نگ به&zwnj;رپا ده&zwnj;بێت ئه&zwnj;وه شوێنتان ده&zwnj;که&zwnj;وتین و ده&zwnj;جه&zwnj;نگاین، ئه&zwnj;وانه ئه&zwnj;و ڕۆژه له کوفره&zwnj;وه نزیکترن تا له ئیمان، به&zwnj;ده&zwnj;میان شتێک ده&zwnj;ڵێن که له دڵ و ده&zwnj;روونیاندا نییه&zwnj;، خوایش زانایه به&zwnj;وه&zwnj;ی که ده&zwnj;یشار&zwnj;نه&zwnj;وه&zwnj;...';
$t[168] = 'ئه&zwnj;وانه&zwnj;ی بۆی دانیشتن و (نه&zwnj;چوون بۆ جه&zwnj;نگ) به&zwnj;براکانی خۆیانیان وت: ئه&zwnj;گه&zwnj;ر ئه&zwnj;وانه (واته شه&zwnj;هیده&zwnj;کان) به&zwnj;قسه&zwnj;ی ئێمه&zwnj;یان بکردایه نه ده&zwnj;کوژران، ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر (صلی الله علیه وسلم) پێیان بڵێ: ده&zwnj;ی که&zwnj;واته مردن له خۆتان دووربخه&zwnj;نه&zwnj;وه ئه&zwnj;گه&zwnj;ر ڕاست ده&zwnj;که&zwnj;ن؟! هه&zwnj;رگیز وامه&zwnj;زانن و وادامه&zwnj;نێن ئه&zwnj;وانه&zwnj;ی له&zwnj;پێناوی خوادا شه&zwnj;هید بوون و کوژران، مردوون، نه&zwnj;خێر وانیه&zwnj;، به&zwnj;ڵکو ئه&zwnj;وانه زیندوون، لای په&zwnj;روه&zwnj;ردگاریان له ڕزق و ڕۆزیی (هه&zwnj;مه جۆرو تایبه&zwnj;ت) به&zwnj;هره&zwnj;وه&zwnj;رن.';
$t[169] = 'هه&zwnj;رگیز وامه&zwnj;زانن و وادامه&zwnj;نێن ئه&zwnj;وانه&zwnj;ی له&zwnj;پێناوی خوادا شه&zwnj;هید بوون و کوژران، مردوون، نه&zwnj;خێر وانیه&zwnj;، به&zwnj;ڵکو ئه&zwnj;وانه زیندوون، لای په&zwnj;روه&zwnj;ردگاریان له ڕزق و ڕۆزیی (هه&zwnj;مه جۆرو تایبه&zwnj;ت) به&zwnj;هره&zwnj;وه&zwnj;رن.';
$t[170] = 'زۆر دڵخۆش و شادمانن به&zwnj;و به&zwnj;خشش و زیاده فه&zwnj;زڵه&zwnj;ی که خوا پێی به&zwnj;خشیوون، مژده&zwnj;ش ده&zwnj;ده&zwnj;ن به&zwnj;وانه&zwnj;ی هێشتا به&zwnj;وان نه&zwnj;گه&zwnj;یشتوون و (شه&zwnj;هید نه&zwnj;بوون) که بێگومان هیچ ترس و بیمێکیان له&zwnj;سه&zwnj;ر نییه&zwnj;، هیچ غه&zwnj;م و په&zwnj;ژاره&zwnj;یه&zwnj;کتان بۆ پێش نایه&zwnj;ت (له به&zwnj;جێ هێشتنی دنیا).';
$t[171] = 'مژده ده&zwnj;ده&zwnj;ن به&zwnj;نازو نیعمه&zwnj;تانی تایبه&zwnj;تی و فه&zwnj;زڵ و ڕێزی بێ سنوور له&zwnj;لایه&zwnj;ن خوای میهره&zwnj;بانه&zwnj;وه&zwnj;، به&zwnj;ڕاستی خوایش پاداشتی ئیمانداران زایه ناکات و (ره&zwnj;نج به&zwnj;خه&zwnj;سار نابن)...';
$t[172] = 'ئه&zwnj;وانه&zwnj;ی که به&zwnj;ده&zwnj;م بانگه&zwnj;وازی خواو پێغه&zwnj;مبه&zwnj;ره&zwnj;وه (صلی الله علیه وسلم) چوون، دوای ئه&zwnj;وه&zwnj;ی که تووشی ناخۆشی و زام و ئێش بوون، بۆ ئه&zwnj;وانه&zwnj;یان که چاکه&zwnj;یان کردو پارێزکار بوون، پاداشتی زۆر گه&zwnj;وره و بێ سنوور ئاماده&zwnj;یه&zwnj;...';
$t[173] = 'ئه&zwnj;وانه&zwnj;ی که خه&zwnj;ڵکی (باوه&zwnj;ڕ لاواز) پێیان ده&zwnj;ڵێن: به&zwnj;ڕاستی خه&zwnj;ڵکی بێ باوه&zwnj;ڕو خوانه&zwnj;ناس خۆیان بۆ ئێوه کۆ کردۆته&zwnj;وه و خۆیان بۆ ئێوه مه&zwnj;ڵاس داوه&zwnj;، لێیان بترسن (خۆتان تووشی به&zwnj;ڵا مه&zwnj;که&zwnj;ن) به&zwnj;ڵام ئه&zwnj;وان (واته ئیمانداران به&zwnj;و پڕوپاگه&zwnj;نده&zwnj;یه&zwnj;) باوه&zwnj;ڕیان زیاتر دامه&zwnj;زراو، وتیان: خوامان به&zwnj;سه که یاریده&zwnj;ده&zwnj;رو یارمه&zwnj;تیده&zwnj;رێکی چاکه&zwnj;.';
$t[174] = 'ئه&zwnj;وسا ئیتر موسوڵمانان گه&zwnj;ڕانه&zwnj;وه هاوڕێ له&zwnj;گه&zwnj;ڵ فه&zwnj;زڵ و نازو نیعمه&zwnj;تی خوایی دا، هیچ ناخۆشیه&zwnj;کیشیان بۆ پێش نه&zwnj;هات، چونکه شوێنی ڕه&zwnj;زامه&zwnj;ندی خوا که&zwnj;وتبوون و (هه&zwnj;ر ئه&zwnj;وه&zwnj;شیان مه&zwnj;به&zwnj;ست بوو)، خوای گه&zwnj;وره&zwnj;ش هه&zwnj;ر خۆی خاوه&zwnj;نی فه&zwnj;زڵ و ڕێزێکی زۆر گه&zwnj;وره&zwnj;یه&zwnj;.';
$t[175] = 'ئه&zwnj;وه به&zwnj;ڕاستی ته&zwnj;نها شه&zwnj;یتانه یاوه&zwnj;ره&zwnj;کانی خۆی ده&zwnj;ترسێنێت، ئێوه&zwnj;ی (ئیماندار) لێیان مه&zwnj;ترسن، به&zwnj;ڵکو هه&zwnj;ر له من بترسن ئه&zwnj;گه&zwnj;ر ئیماندارن (بێگومان ترسان له خوا ئاسووده&zwnj;یی و ئارامیی ده&zwnj;به&zwnj;خشێت به ئیماندار).';
$t[176] = '(ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر (صلی الله علیه وسلم) ئه&zwnj;وانه&zwnj;ی که پێشبڕکێ ده&zwnj;که&zwnj;ن له بێ باوه&zwnj;ڕیدا خه&zwnj;مبارت نه&zwnj;که&zwnj;ن، چونکه به&zwnj;ڕاستی ئه&zwnj;وانه هه&zwnj;رگیز هیچ زیانێک به&zwnj;خوا ناگه&zwnj;یه&zwnj;نن، خوا ده&zwnj;یه&zwnj;وێت هیچ به&zwnj;شێکیان له قیامه&zwnj;تدا پێ نه&zwnj;به&zwnj;خشێت، سزای سه&zwnj;خت و زۆر ناسۆریش بۆیان ئاماده&zwnj;یه&zwnj;.';
$t[177] = 'به&zwnj;ڕاستی ئه&zwnj;وانه&zwnj;ی بێ باوه&zwnj;ڕییان هه&zwnj;ڵبژارد له جیاتی ئیمان، بێ باوه&zwnj;ڕییان کڕی له&zwnj;جیاتی باوه&zwnj;ڕ، به&zwnj;هیچ جۆرێک زیان به&zwnj;خوا ناگه&zwnj;یه&zwnj;نن به&zwnj;ڵکو سزای به&zwnj;ئێش چاوه&zwnj;ڕێیانه&zwnj;.';
$t[178] = 'ئه&zwnj;وانه ڕێبازی کوفریان گرتۆته&zwnj;به&zwnj;ر، وا گومان نه&zwnj;به&zwnj;ن که مۆڵه&zwnj;تمان داون و (نازو نیعمه&zwnj;تی دنیامان پێبه&zwnj;خشیوون) ئه&zwnj;وه خێره بۆیان، ئێمه ته&zwnj;نها بۆیه مۆڵه&zwnj;تیان ده&zwnj;ده&zwnj;ین تا زیاتر گوناهبار ببن و سزایه&zwnj;کی ڕیسواکه&zwnj;ر چاوه&zwnj;ڕێیانه&zwnj;.';
$t[179] = 'خوا ئیمانداران واز لێناهێنێت هه&zwnj;ر وه&zwnj;کو ئه&zwnj;وه&zwnj;ی که ئێستا ئێوه له&zwnj;سه&zwnj;ری هه&zwnj;ن (دڵسۆزو نادڵسۆز، خه&zwnj;مخۆرو بێ باک تێکه&zwnj;ڵن)، هه&zwnj;تا پیس و پاک له&zwnj;یه&zwnj;ک جیانه&zwnj;کاته&zwnj;وه (به&zwnj;هۆی جۆره&zwnj;ها تاقی کردنه&zwnj;وه و پێشهاته&zwnj;وه&zwnj;)، بێگومان خوا جیهانی شاراوه&zwnj;تان نیشان نادات (تا ئاینده بزانن)، به&zwnj;ڵکو خوا له پێغه&zwnj;مبه&zwnj;ره&zwnj;کانی که&zwnj;سانێک هه&zwnj;ڵده&zwnj;بژێرێت که بیه&zwnj;وێت (تا له هه&zwnj;ندێک نهێنی ئاگاداربن به&zwnj;و ڕاده&zwnj;یه&zwnj;ی که پێویسته&zwnj;)، جا باوه&zwnj;ڕ به&zwnj;خواو پێغه&zwnj;مبه&zwnj;رانی بهێنن، خۆ ئه&zwnj;گه&zwnj;ر باوه&zwnj;ڕی دامه&zwnj;زراو بهێنن و پارێزکاری بکه&zwnj;ن و له خوا ترس بن، ئه&zwnj;وه پاداشتی بێ سنوور و نه&zwnj;بڕاوه چاوه&zwnj;ڕێتانه&zwnj;.';
$t[180] = 'واگومان نه&zwnj;به&zwnj;ن ئه&zwnj;وانه&zwnj;ی که به&zwnj;خیلی و ڕه&zwnj;زیلی ده&zwnj;که&zwnj;ن، له&zwnj;و نازو نیعمه&zwnj;تانه&zwnj;ی خوا پێی به&zwnj;خشیوون، نابه&zwnj;خشن، کارێکی چاکه بۆیان، نه&zwnj;خێر ئه&zwnj;وه کارێکی خراپ و نادروسته بۆیان، چونکه ئه&zwnj;و شتانه&zwnj;ی که نه&zwnj;یانده&zwnj;به&zwnj;خشی له ڕۆژی قیامه&zwnj;تدا ده&zwnj;ست ده&zwnj;نێته بینیان و ده&zwnj;بنه کۆت له گه&zwnj;ردنیاندا، (بێگومان) هه&zwnj;رچی له ئاسمانه&zwnj;کان و زه&zwnj;ویدا هه&zwnj;یه هه&zwnj;ر خوا خاوه&zwnj;نیانه&zwnj;و خوا ئاگایه به هه&zwnj;موو ئه&zwnj;و کارو کرده&zwnj;وانه&zwnj;ی که ئێوه ئه&zwnj;نجامی ده&zwnj;ده&zwnj;ن.';
$t[181] = 'سوێند بێت بێگومان خوای په&zwnj;روه&zwnj;ردگار گوفتاری (نابه&zwnj;جێی) ئه&zwnj;و (جوله&zwnj;کانه&zwnj;ی) بیست که وتیان: خوا هه&zwnj;ژاره و ئێمه ده&zwnj;وڵه&zwnj;مه&zwnj;ندین!! ئێمه ئه&zwnj;و گوفتارانه یاداشت و تۆمار ده&zwnj;که&zwnj;ین له&zwnj;سه&zwnj;ریان، هه&zwnj;روه&zwnj;ها کوشتنی پێغه&zwnj;مبه&zwnj;ران به ده&zwnj;ستی ئه&zwnj;وان به ناحه&zwnj;ق، (له دۆزه&zwnj;خیشدا) پێیان ده&zwnj;ڵێین: بچێژن سزای ئاگری سوتێنه&zwnj;ر...';
$t[182] = 'ئه&zwnj;و سزایه له ده&zwnj;ره&zwnj;نجامی ده&zwnj;ست پێشخه&zwnj;ری خۆتاندایه&zwnj;، چونکه به&zwnj;ڕاستی خوا هیچ جۆره سته&zwnj;مێک له به&zwnj;نده&zwnj;کان ناکات.';
$t[183] = '(ئه&zwnj;و جووله&zwnj;کانه&zwnj;ی) ده&zwnj;ڵێن: خوا په&zwnj;یمانی داوه به ئێمه که به هیچ پێغه&zwnj;مبه&zwnj;رێک باوه&zwnj;ڕ نه&zwnj;که&zwnj;ین تاقوربانیه&zwnj;کمان بۆ نه&zwnj;هێنێت و (نه&zwnj;یبینین) ئاگر ده&zwnj;یخوات و ده&zwnj;یسوتێنێت و له ناوی ده&zwnj;بات، (ئه&zwnj;ی محمد (صلی الله علیه وسلم) پێیان بڵێ: خۆ ژماره&zwnj;یه&zwnj;ک پێغه&zwnj;مبه&zwnj;ر له پێش منه&zwnj;وه ڕه&zwnj;وانه&zwnj;کران بۆتان به&zwnj;چه&zwnj;نده&zwnj;ها به&zwnj;ڵگه&zwnj;و نیشانه&zwnj;ی ئاشکراوه و به&zwnj;و داخوازیه&zwnj;شه&zwnj;وه که&zwnj;وتتان، جا بۆ کوشتانن و (شه&zwnj;هیدتان کردن) ئه&zwnj;گه&zwnj;ر ئێوه ڕاستگۆن؟!';
$t[184] = 'خۆ ئه&zwnj;گه&zwnj;ر بڕوایان پێنه&zwnj;کردیت، ئه&zwnj;وه به&zwnj;ڕاستی زۆرێک له پێغه&zwnj;مبه&zwnj;رانی پێش تۆ بروایان پێ نه&zwnj;کراوه هه&zwnj;رچه&zwnj;نده جۆره&zwnj;ها موعجیزه و نامه&zwnj;ی پڕ له ستایش و ئامۆژگاری و کتێبی ڕووناککه&zwnj;ره&zwnj;وه&zwnj;یان هێنابوو.';
$t[185] = 'هه&zwnj;موو که&zwnj;سێک ده&zwnj;بێت بمرێت و مردن بچێژێت، ته&zwnj;نها له ڕۆژی قیامه&zwnj;تدا کرێ و پاداشتان به&zwnj;ته&zwnj;واوی پێ ده&zwnj;درێت (ده&zwnj;رئه&zwnj;نجامی کرده&zwnj;وه&zwnj;کانتان دێته&zwnj;وه ڕێتان) ئه&zwnj;وسا ئه&zwnj;وه&zwnj;ی دوور خرایه&zwnj;وه له ئاگری دۆزه&zwnj;خ و خرایه به&zwnj;هه&zwnj;شته&zwnj;وه&zwnj;، ئه&zwnj;وه به&zwnj;ڕاستی سه&zwnj;رفراز بووه&zwnj;، ژیانی دنیاش بێجگه له&zwnj;وه&zwnj;ی که ڕابواردنێکی خه&zwnj;ڵه&zwnj;تێنه&zwnj;ره شتێکی تر نیه&zwnj;.';
$t[186] = 'سوێند بێت بێگومان تاقی ده&zwnj;کرێنه&zwnj;وه له ماڵ و دارایی و خۆیشتاندا (زیان له ساماندا ده&zwnj;که&zwnj;ن، خۆتان توشی ناخۆشی و ئازار ده&zwnj;بن) هه&zwnj;روه&zwnj;ها سوێند بێت له خاوه&zwnj;نانی کتێبی پێش خۆتان و له موشریک و هاوه&zwnj;ڵگه&zwnj;رانه&zwnj;وه ئازارێکی زۆر ده&zwnj;بیسن (ئه&zwnj;وه&zwnj;نده بوختان و قسه&zwnj;ی نابه&zwnj;جێتان بۆ هه&zwnj;ڵده&zwnj;به&zwnj;ستن، وه&zwnj;ک ئازار به&zwnj;رگوێتان بکه&zwnj;وێت وایه&zwnj;)، به&zwnj;ڵام ئه&zwnj;گه&zwnj;ر خۆڕاگر بن و پارێزکار بن و له خوا بترسن (له سنوور ده&zwnj;رنه&zwnj;چن)، به&zwnj;ڕاستی ئه&zwnj;و هه&zwnj;ڵوێسته له&zwnj;کاره په&zwnj;سه&zwnj;ندو کرده&zwnj;وه پایه&zwnj;داره&zwnj;کانه&zwnj;.';
$t[187] = '(یاد بێنه&zwnj;ره&zwnj;وه&zwnj;) کاتێک خوا په&zwnj;یمانی وه&zwnj;رگرت له&zwnj;وانه&zwnj;ی کتێبیان پێ به&zwnj;خشراوه که ڕوونی بکه&zwnj;نه&zwnj;وه بۆ خه&zwnj;ڵکی و مه&zwnj;رامه&zwnj;کانی باس بکه&zwnj;ن و نه&zwnj;یشارنه&zwnj;وه&zwnj;، که&zwnj;چی خستیانه پشت خۆیان و به نرخێکی هه&zwnj;رزان فرۆشتیان (ئه&zwnj;گه&zwnj;ر هه&zwnj;موو دنیاش بێت، دینی بۆ بفرۆشی هه&zwnj;رزان فرۆشراوه و زه&zwnj;ره&zwnj;رێکی بێ سنووره&zwnj;) ئای که شتێکی بێ نرخیان کڕیوه و ده&zwnj;ستیان که&zwnj;وتووه&zwnj;.';
$t[188] = 'وا گومان مه&zwnj;به به&zwnj;وانه&zwnj;ی دڵخۆشن به&zwnj;وه&zwnj;ی که هه&zwnj;یانه&zwnj;و حه&zwnj;زده&zwnj;که&zwnj;ن باس بکرێن و خه&zwnj;ڵکی سوپاسیان بکات له&zwnj;سه&zwnj;ر کارو چاکه&zwnj;یه&zwnj;ک که نه&zwnj;یانکردووه&zwnj;، ئه&zwnj;وه هه&zwnj;رگیز وامه&zwnj;زانه ئه&zwnj;وانه له ده&zwnj;ست سزا ڕزگاریان ده&zwnj;بێت، به&zwnj;ڵکو سزایه&zwnj;کی به ئێش چاوه&zwnj;ڕێیانه&zwnj;.';
$t[189] = 'هه&zwnj;رچی له ئاسمانه&zwnj;کان و زه&zwnj;ویدا هه&zwnj;یه هه&zwnj;ر خوا خاوه&zwnj;نیانه و ئه&zwnj;و خوایه ده&zwnj;سه&zwnj;ڵاتی به&zwnj;سه&zwnj;ر هه&zwnj;موو شتێکدا هه&zwnj;یه&zwnj;.';
$t[190] = 'به&zwnj;ڕاستی له دروستکردنی ئاسمانه&zwnj;کان و زه&zwnj;ویدا و له ئاڵو گۆڕی شه&zwnj;و و ڕۆژدا به&zwnj;ڵگه و نیشانه&zwnj;ی زۆر هه&zwnj;ن بۆ که&zwnj;سانی ژیر و هۆشمه&zwnj;ند...';
$t[191] = 'ئه&zwnj;وانه&zwnj;ی یادی خوا ده&zwnj;که&zwnj;ن له&zwnj;کاتێکدا که به&zwnj;پێوه&zwnj;ن یان دانیشتوون یان ڕاکشاوون (له هیچ حاڵه&zwnj;تێکدا یادی ئه&zwnj;وزاته فه&zwnj;رامۆش ناکه&zwnj;ن)، هه&zwnj;میشه بیرده&zwnj;که&zwnj;نه&zwnj;وه له دروست بوونی ئاسمانه&zwnj;کان و زه&zwnj;وی (بیرده&zwnj;که&zwnj;نه&zwnj;وه له هه&zwnj;موو ورده&zwnj;کاریه&zwnj;ک، سه&zwnj;رنجی هه&zwnj;موو دیارده&zwnj;یه&zwnj;ک ده&zwnj;ده&zwnj;ن، سه&zwnj;رئه&zwnj;نجام ده&zwnj;ڵێن): په&zwnj;روه&zwnj;ردگارا تۆ ئه&zwnj;م هه&zwnj;موو دروست کراوانه&zwnj;ت بێ هوده و بێ ئامانج دروست نه&zwnj;کردووه&zwnj;، پاکیی و بێگه&zwnj;ردیی شایسته&zwnj;ی تۆیه&zwnj;، ده تۆش بمانپارێزه له سزای ئاگری دۆزه&zwnj;خ.';
$t[192] = 'په&zwnj;روه&zwnj;ردگارا به&zwnj;ڕاستی تۆ هه&zwnj;رکه&zwnj;س بخه&zwnj;یته ناو ئاگری دۆزه&zwnj;خه&zwnj;وه&zwnj;، ئه&zwnj;وه ئیتر شه&zwnj;رمه&zwnj;زارو ڕیسواو خه&zwnj;جاڵه&zwnj;تت کردووه&zwnj;، (جا بێگومان له&zwnj;وێ) سته&zwnj;مکاران هیچ جۆره پشتیوانێکیان نیه&zwnj;.';
$t[193] = 'په&zwnj;روه&zwnj;ردگارا؛ به&zwnj;ڕاستی ئێمه گوێمان له بانگخوازێک بوو، بانگی کردین بۆ ئیمان و (ده&zwnj;یفه&zwnj;رموو): ئیمان بهێنن به په&zwnj;روه&zwnj;ردگارتان جا ئێمه&zwnj;ش (بێ دوودڵی)، خێرا باوه&zwnj;ڕمان هێناو (وتمان): په&zwnj;روه&zwnj;دگارا؛ ده&zwnj;تۆش له&zwnj;گوناهو هه&zwnj;ڵه&zwnj;کانمان خۆش ببه&zwnj;، چاوپۆشی بکه له&zwnj;کرداره ناپه&zwnj;سه&zwnj;نده&zwnj;کانمان و (له&zwnj;سه&zwnj;ر ڕێبازی) چاکان و (به&zwnj;خته&zwnj;وه&zwnj;ران) بمانمرێنه و له&zwnj;گه&zwnj;ڵ ئه&zwnj;واندا کۆمان بکه&zwnj;ره&zwnj;وه&zwnj;.';
$t[194] = 'په&zwnj;روه&zwnj;ردگارا؛ ئه&zwnj;و به&zwnj;ڵێنانه&zwnj;شمان بۆ ئه&zwnj;نجام بده که له&zwnj;سه&zwnj;ر زاری پێغه&zwnj;مبه&zwnj;رانتدا به&zwnj;ئێمه&zwnj;ت داوه (که سه&zwnj;رکه&zwnj;وتنه له دنیاداو به&zwnj;هه&zwnj;شتی به&zwnj;رینه له داهاتوودا) و له ڕۆژی قیامه&zwnj;تیشدا خه&zwnj;جاڵه&zwnj;ت و ڕوو زه&zwnj;ردمان مه&zwnj;که&zwnj;، بێگومان تۆ به&zwnj;ڵێن شکێن نیت و به&zwnj;ڵێنی خۆت ده&zwnj;به&zwnj;یته سه&zwnj;ر.';
$t[195] = '(کاتێک ئیمانداران به&zwnj;م شێوه&zwnj;یه نزایان کرد، که په&zwnj;روه&zwnj;ردگاری ئازیزیان فێریان ده&zwnj;کات) بێگومان ئه&zwnj;و زاته دوعاکانیان لێوه&zwnj;رده&zwnj;گرێت (به هاناو هاواریانه&zwnj;وه ده&zwnj;چێت بۆیه ده&zwnj;فه&zwnj;رموێت): به&zwnj;ڕاستی من هه&zwnj;وڵ و کۆشش و کارو کرده&zwnj;وه&zwnj;ی هیچ کام له ئێوه به&zwnj;زایه ناده&zwnj;م، چ نێرینه و پیاوتان، چ مێینه و ئافره&zwnj;تتان (چونکه&zwnj;)هاوکاری یه&zwnj;کتر بوون (له چاکه&zwnj;و چاکه&zwnj;کاریدا)، جا ئه&zwnj;وانه&zwnj;ی کۆچیان کردو له شوێنه&zwnj;واری خۆیان ده&zwnj;ربه&zwnj;ده&zwnj;رکران و ئازارو ئه&zwnj;شکه&zwnj;نجه&zwnj;دران له پێناوی مندا و جه&zwnj;نگان و شه&zwnj;هیدکران، به&zwnj;ڕاستی سوێند بێت چاوپۆشی ده&zwnj;که&zwnj;م له هه&zwnj;ڵه&zwnj;و گوناهه&zwnj;کانیان و ده&zwnj;یانخه&zwnj;مه باخه&zwnj;کانی به&zwnj;هه&zwnj;شته&zwnj;وه که چه&zwnj;نده&zwnj;ها ڕووبار به&zwnj;ژێر دره&zwnj;خته&zwnj;کانیداو به&zwnj;رده&zwnj;م کۆشکه&zwnj;کانیدا ده&zwnj;ڕوات، ئه&zwnj;مه&zwnj;ش پاداشتێکه له لایه&zwnj;ن خواوه (پێشکه&zwnj;شیان ده&zwnj;کرێت) خوا پاداشتی چاک و جوانی لایه (بۆ تێکۆشه&zwnj;ران).';
$t[196] = '(ئه&zwnj;ی ئیماندار) هه&zwnj;رگیز هێزو ده&zwnj;سه&zwnj;ڵاتی ئه&zwnj;وانه&zwnj;ی بێ باوه&zwnj;ڕن له وڵاتاندا تۆ له&zwnj;خشته نه&zwnj;بات.';
$t[197] = '(ئه&zwnj;وه هه&zwnj;رهه&zwnj;مووی) ڕابواردنێکی که&zwnj;مه (چونکه&zwnj;) له&zwnj;وه&zwnj;ودوا شوێن و جێگه&zwnj;یان دۆزه&zwnj;خه&zwnj;، که ناخۆشترین جێگه و ڕێگه&zwnj;یه&zwnj;.';
$t[198] = 'به&zwnj;ڵام ئه&zwnj;وانه که پارێزکارن و له سزای په&zwnj;روه&zwnj;ردگاریان ده&zwnj;ترسن (جێگه و ڕێگه&zwnj;یان) باخه&zwnj;کانی به&zwnj;هه&zwnj;شته که چه&zwnj;نده&zwnj;ها ڕووبار به&zwnj;ژێر دره&zwnj;خته&zwnj;کانیداو به&zwnj;به&zwnj;رده&zwnj;م کۆشکه&zwnj;کانیدا ده&zwnj;ڕوات، هاوڕێ له&zwnj;گه&zwnj;ل ژیانی هه&zwnj;مشه&zwnj;یی له&zwnj;وێدا، ئه&zwnj;و شوێنانه&zwnj;ش له&zwnj;لایه&zwnj;ن خواوه ئاماده&zwnj;کراوه&zwnj;، بۆ میوانداری هه&zwnj;میشه&zwnj;یی به&zwnj;خته&zwnj;وه&zwnj;ران، ئه&zwnj;ڵبه&zwnj;ته ئه&zwnj;وبه&zwnj;شه&zwnj;ی که لای خوایه چاکترو به&zwnj;نرختره بۆ چاکه&zwnj;کاران و خواناسان.';
$t[199] = 'به&zwnj;ڕاستی هه&zwnj;ندێ که&zwnj;س هه&zwnj;ن له خاوه&zwnj;نانی کتێب که باوه&zwnj;ڕی به&zwnj;خوا پته&zwnj;و و دامه&zwnj;زراوه&zwnj;، هه&zwnj;روه&zwnj;ها به&zwnj;و (قورئانه&zwnj;ش) که بۆ ئێوه ڕه&zwnj;وانه&zwnj;کراوه و به&zwnj;و کتێبانه&zwnj;ش بۆ خۆیان پێشتر ڕه&zwnj;وانه کراوه&zwnj;، له&zwnj;کاتێکدا ئه&zwnj;وانه ترسی خوایان له دڵدایه و مه&zwnj;به&zwnj;ستیانه خوا لێیان ڕازی بێت، ئایه&zwnj;ته&zwnj;کانی خوا به&zwnj;نرخێکی که&zwnj;م (یاخود به&zwnj;هیچ نرخێک) نافرۆشن، ئه&zwnj;وانه پاداشتی (ئیمان و باوه&zwnj;ڕی) خۆیان لای په&zwnj;روه&zwnj;ردگاریان وه&zwnj;رده&zwnj;گرن، به&zwnj;ڕاستی خوا به&zwnj;خێرایی لێپرسینه&zwnj;وه ئه&zwnj;نجام ده&zwnj;دات (تا به&zwnj;زوویی به&zwnj;خته&zwnj;وه&zwnj;ران بخاته به&zwnj;هه&zwnj;شته&zwnj;وه و ناپوخته&zwnj;کانیش له دۆزه&zwnj;خ توند بکات).';
$t[200] = 'ئه&zwnj;ی ئه&zwnj;وانه&zwnj;ی که باوه&zwnj;ڕتان هێناوه خۆگربن و خۆڕاگریی و ئارامگریی و به&zwnj;سه&zwnj;ر خۆتاندا بهێنن، نه&zwnj;به&zwnj;زن له خۆگری مانه&zwnj;وه&zwnj;تان له سه&zwnj;نگه&zwnj;ردا، هه&zwnj;وڵ بده&zwnj;ن هه&zwnj;میشه له سه&zwnj;نگه&zwnj;ری خه&zwnj;بات و کۆششدا بن، له خوا بترسن و پارێزکاربن بۆ ئه&zwnj;وه&zwnj;ی سه&zwnj;رفرازیی (هه&zwnj;میشه&zwnj;یی) به&zwnj;ده&zwnj;ست بهێنن.';
?>