$t = array();
$t[1] = 'سه&zwnj;رنجی سه&zwnj;ره&zwnj;تای سووره&zwnj;تی (البقرة) بده&zwnj;.';
$t[2] = 'دابه&zwnj;زاندنی ئه&zwnj;م قورئانه له&zwnj;لایه&zwnj;ن په&zwnj;روه&zwnj;ردگاری جیهانیانه&zwnj;وه هیچ گومانی تێدانیه&zwnj;.';
$t[3] = 'ئایا خوانه&zwnj;ناسان ده&zwnj;ڵێن: (محمد صلی الله علیه وسلم) ئه&zwnj;م قورئانه&zwnj;ی هه&zwnj;ڵبه&zwnj;ستووه&zwnj;؟ نه&zwnj;خێر وانی یه&zwnj;، به&zwnj;ڵکو قورئان حه&zwnj;ق و ڕاسته و له&zwnj;لایه&zwnj;ن پروه&zwnj;ردگارته&zwnj;وه نێردراوه&zwnj;، بۆ ئه&zwnj;وه&zwnj;ی قه&zwnj;وم و هۆزێک بێدار بکه&zwnj;یته&zwnj;وه که پێش تۆ بێدارخه&zwnj;ره&zwnj;وه&zwnj;یه&zwnj;کیان بۆ نه&zwnj;هاتووه&zwnj;، تاڕێگه&zwnj;ی هیدایه&zwnj;ت و ئیمان بگرنه&zwnj;به&zwnj;ر.';
$t[4] = 'خوا ئه&zwnj;و زاته&zwnj;یه که ئاسمانه&zwnj;کان و زه&zwnj;وی و هه&zwnj;رچی له نێوانیاندا هه&zwnj;یه له شه&zwnj;ش ڕۆژدا دروستی کردووه (مه&zwnj;رج نیه دڕێژی ئه&zwnj;و ڕۆژانه به&zwnj;قه&zwnj;ده&zwnj;ر دڕێژی شه&zwnj;و و ڕۆژی زه&zwnj;وی بێت که به بیست و چوار سه&zwnj;عات جارێک به&zwnj;ده&zwnj;وری خۆیدا ده&zwnj;سوڕێته&zwnj;وه&zwnj;، چونکه هه&zwnj;ندێک ڕۆژ هه&zwnj;روه&zwnj;ک قورئان خۆی ئاماژه&zwnj;ی بۆ ده&zwnj;کات هه&zwnj;زار ساڵه&zwnj;، هه&zwnj;ندێکی تری په&zwnj;نجا هه&zwnj;زار ساڵه&zwnj;)، پاشان خوا له&zwnj;سه&zwnj;ر ته&zwnj;ختی فه&zwnj;رمانڕه&zwnj;واییه&zwnj;وه سه&zwnj;رپه&zwnj;رشتی کارو فرمانی دروستکراوانی ده&zwnj;کات و ئه&zwnj;وه&zwnj;ی ئه&zwnj;و عیلمی پێی هه&zwnj;یه و ده&zwnj;یزانێت و ده&zwnj;یه&zwnj;وێت، هه&zwnj;ر ئه&zwnj;وه پێش دێت، (سه&zwnj;رنجی ئایه&zwnj;تی _2_الرعد بده&zwnj;)، خه&zwnj;ڵکینه جگه له&zwnj;و خوایه&zwnj;که&zwnj;س نیه پشتیوانیتان لێ بکات یان تکا کارتان بێت، ئایه یاداوه&zwnj;ری وه&zwnj;رده&zwnj;گرن و تێده&zwnj;گه&zwnj;ن.';
$t[5] = 'ئه&zwnj;و خوایه له ژوور ئاسمانه&zwnj;وه کاروباری زه&zwnj;وی ڕێک ده&zwnj;خات و سه&zwnj;رپه&zwnj;رشتی ده&zwnj;کات، له&zwnj;وه&zwnj;ودوا ڕووداوه&zwnj;کان و به&zwnj;سه&zwnj;رهاته&zwnj;کان و کارو کرده&zwnj;وه&zwnj;ی به&zwnj;نده&zwnj;کان ده&zwnj;گه&zwnj;رێته&zwnj;وه بۆ لای، له ڕۆژێکدا که به&zwnj;رامبه&zwnj;ر هه&zwnj;زار ساڵه به&zwnj;و حسابه&zwnj;ی ئێوه که ده&zwnj;یکه&zwnj;ن.';
$t[6] = 'ئا ئه&zwnj;و خوایه زانایه به نهێنی و ئاشکراکان، له هه&zwnj;مان کاتدا خاوه&zwnj;ن ده&zwnj;سه&zwnj;ڵات و میهره&zwnj;بانیشه&zwnj;.';
$t[7] = 'ئه&zwnj;و خوایه زاتێکه هه&zwnj;موو شتێکی به&zwnj;چاکی و ڕێکوپێکی به&zwnj;دیهێناوه&zwnj;، له&zwnj;سه&zwnj;ره&zwnj;تاوه ئاده&zwnj;میزادی له قوڕ دروست کردووه&zwnj;.';
$t[8] = 'پاشان زنجیره&zwnj;ی نه&zwnj;وه&zwnj;کانی له ئاوێکی ساده و بێنرخ به&zwnj;دیهێناوه&zwnj;.';
$t[9] = 'پاشان ئاده&zwnj;میزاده به&zwnj;ڕێک و پێکی ڕێکخست، له ڕوح و گیانێک که خۆی خاوه&zwnj;نیه&zwnj;تی فووی پێداکرد، ئینجا ده&zwnj;زگای بیستن و بینین و لێکدانه&zwnj;وه&zwnj;ی پێبه&zwnj;خشیون، که&zwnj;متان سوپاسگوزارن.';
$t[10] = '(له&zwnj;گه&zwnj;ڵ ئه&zwnj;و هه&zwnj;موو به&zwnj;ڵگه به&zwnj;هێزانه&zwnj;دا) خوانه&zwnj;ناسان ده&zwnj;یانوت: ئایه ئه&zwnj;گه&zwnj;ر مردین و بزر بووین له توێی خاکی زه&zwnj;ویدا، ئایا جارێکی تر سه&zwnj;رله&zwnj;نوێ دروست ده&zwnj;کرێینه&zwnj;وه&zwnj;؟! نه&zwnj;خێر ون نابن! به&zwnj;ڵکو ئه&zwnj;وانه (هه&zwnj;ر قسه&zwnj;ی به&zwnj;تاڵ و پڕوپوچ ده&zwnj;که&zwnj;ن ئه&zwnj;گینا له بنه&zwnj;ڕه&zwnj;تدا به ئاماده بوونی به&zwnj;رده&zwnj;م باره&zwnj;گای په&zwnj;روه&zwnj;ردگاریان) بێ باوه&zwnj;ڕن...';
$t[11] = 'جا پێیان بڵێ: فریشته&zwnj;ی گیان کێشان که کاری گیان کێشانتانی پێ سپێردراوه ده&zwnj;تانمرێنێت، له&zwnj;وه&zwnj;ودوا بۆلای په&zwnj;روه&zwnj;ردگارتان ده&zwnj;برێنه&zwnj;وه&zwnj;.';
$t[12] = 'ئه&zwnj;گه&zwnj;ر ده&zwnj;تبینی کاتێك ئه&zwnj;و تاوانبارانه (که باوه&zwnj;ڕیان به&zwnj;زیندوو بوونه&zwnj;وه نه&zwnj;بوو) خه&zwnj;جاڵه&zwnj;ت و سه&zwnj;رشۆڕن له&zwnj;به&zwnj;رده&zwnj;م دادگای خواییدا، ده&zwnj;ڵێن: په&zwnj;روه&zwnj;ردگارا ئه&zwnj;وه ئیتر به&zwnj;چاوی خۆمان بینیمان، به گوێی خۆمان بیستمان ئه&zwnj;وه&zwnj;ی بڕوامان پێی نه&zwnj;بوو، ئێسته&zwnj;ش ئێمه بگێڕه&zwnj;وه بۆ دنیا مه&zwnj;رج بێت هه&zwnj;ر کارو کرده&zwnj;وه&zwnj;ی چاکه ئه&zwnj;نجام بده&zwnj;ین، ئێمه ئێسته ته&zwnj;واو دڵنیاین..!!';
$t[13] = 'بێگومان ئه&zwnj;گه&zwnj;ر بمانوویستایه هه&zwnj;موو که&zwnj;سێکمان هیدایه&zwnj;ت و ڕێنموویی ده&zwnj;کرد (به&zwnj;ناچاری و به&zwnj;بێ ویستی خۆیان)، به&zwnj;ڵام بڕیاری من ئه&zwnj;وه&zwnj;یه (که ئاده&zwnj;میزاد و په&zwnj;ریه&zwnj;کان سه&zwnj;ربه&zwnj;ست و سه&zwnj;رپشك و ئازاد بکه&zwnj;م، سه&zwnj;رئه&zwnj;نجامیش) هه&zwnj;ر دۆزه&zwnj;خ پڕ ده&zwnj;که&zwnj;م له په&zwnj;ریه&zwnj;کان و له خه&zwnj;ڵکی تێکرا، (له&zwnj;وانه&zwnj;ی بڕوایان به خواو پێغه&zwnj;مبه&zwnj;ره&zwnj;کانی نه&zwnj;بوو).';
$t[14] = 'ده&zwnj;ی که&zwnj;واته بچێژن ماده&zwnj;م ئێوه ئه&zwnj;م ڕۆژه&zwnj;تان فه&zwnj;رامۆش کرد، ئێمه&zwnj;ش به&zwnj;ڕاستی ئێوه پشتگوێ ده&zwnj;خه&zwnj;ین، بچێژن ئێش و سزای نه&zwnj;بڕاوه&zwnj;، له سه&zwnj;رئه&zwnj;نجامی کارو کرده&zwnj;وه&zwnj;یه&zwnj;ك که ده&zwnj;تانکرد.';
$t[15] = 'به&zwnj;ڕاستی ته&zwnj;نها ئه&zwnj;وانه بڕوا به ئایه&zwnj;ته&zwnj;کانی ئێمه ده&zwnj;هێنن، کاتێك یاداوه&zwnj;ری ده&zwnj;کرێن پێی، خێرا سوژده ده&zwnj;به&zwnj;ن ته&zwnj;سبیحات و سوپاسگوزاری په&zwnj;روه&zwnj;ردگاریان ده&zwnj;که&zwnj;ن، له کاتێکدا ئه&zwnj;وانه به هیچ شێوه&zwnj;یه&zwnj;ك فیزیان نیه و خۆیان به&zwnj;گه&zwnj;وره نازانن.';
$t[16] = '(ئه&zwnj;و ئیمانداره به&zwnj;خته&zwnj;وه&zwnj;رانه&zwnj;) نێوانیان خۆش نیه له&zwnj;گه&zwnj;ڵ ڕاکشان له جێگه&zwnj;و ڕێگه&zwnj;و ڕێگه&zwnj;ی گه&zwnj;رم و نه&zwnj;رما (واته به&zwnj;نوێژو خواپه&zwnj;رستی به&zwnj;شێکی شه&zwnj;و ده&zwnj;به&zwnj;نه سه&zwnj;ر)، هه&zwnj;میشه نزاو پاڕانه&zwnj;وه&zwnj;یان ڕوو به په&zwnj;روه&zwnj;ردگاریانه له خه&zwnj;شم و قین و دۆزه&zwnj;خی ده&zwnj;ترسن، ئاواتی ڕه&zwnj;زامه&zwnj;ندی و به&zwnj;هه&zwnj;شتیان هه&zwnj;یه و له&zwnj;و ڕزق و ڕۆزی و توانایی و زانیاریه&zwnj;ی پێمه&zwnj;ن به&zwnj;خشیوون ده&zwnj;به&zwnj;خشن.';
$t[17] = 'جا هیچ که&zwnj;س له&zwnj;و به&zwnj;خته&zwnj;وه&zwnj;رانه نازانێت چی بۆهه&zwnj;ڵگیراوه وشاراوه&zwnj;ته&zwnj;وه له نازو نیعمه&zwnj;تی جۆراوجۆر لای په&zwnj;روه&zwnj;ردگاری، که چاوان پێی گه&zwnj;ش ده&zwnj;بنه&zwnj;وه و به&zwnj;دیتنی شاد ده&zwnj;بن، له پاداشتی کارو کرده&zwnj;وه&zwnj;ی چاك و پاکیاندا که ئه&zwnj;نجامیان ده&zwnj;دا.';
$t[18] = 'ئایا ئه&zwnj;و که&zwnj;سه&zwnj;ی ئیماندار بوو بێت وه&zwnj;ك ئه&zwnj;و که&zwnj;سه&zwnj;یه که تاوانبار بوو بێت؟ وه&zwnj;ك یه&zwnj;ك نین...';
$t[19] = 'به&zwnj;ڵکو ئه&zwnj;وانه&zwnj;ی که باوه&zwnj;ڕیان هێناوه و کرده&zwnj;وه چاکه&zwnj;کانیان ئه&zwnj;نجامداوه&zwnj;، شوێنیان باخچه&zwnj;کانی به&zwnj;هه&zwnj;شته (که بۆ هه&zwnj;میشه جێگه&zwnj;ی حه&zwnj;وانه&zwnj;وه و حه&zwnj;سانه&zwnj;وه&zwnj;یانه&zwnj;)، هه&zwnj;ر له&zwnj;وێ داده&zwnj;به&zwnj;زن، له پاداشتی کارو کرده&zwnj;وه&zwnj;یه&zwnj;ك که ده&zwnj;یانکرد.';
$t[20] = 'ئه&zwnj;وانه&zwnj;ش تاوانبار یاخی بوون، شوێنیان ناو ئاگری دۆزه&zwnj;خه&zwnj;، هه&zwnj;ر کاتێك ویستیان لێی ده&zwnj;رچن، ده&zwnj;گێڕدرێنه&zwnj;وه بۆ ناوی، پێیان ده&zwnj;وترێت: بچێژن سزای ئه&zwnj;و ئاگره&zwnj;ی که بڕواتان پێی نه&zwnj;بوو.';
$t[21] = 'سوێند به&zwnj;خوا ئێمه تاڵاو له دنیادا ده&zwnj;که&zwnj;ین به&zwnj;گه&zwnj;رویاندا، بێجگه له سزای ڕیشه&zwnj;که&zwnj;نکردنیان، بۆئه&zwnj;وه&zwnj;ی هۆش و بیربکه&zwnj;نه&zwnj;وه&zwnj;و بیرێك بکه&zwnj;نه&zwnj;وه و بگه&zwnj;ڕێنه&zwnj;وه بۆ لای ئیمان.';
$t[22] = 'جا کێ له&zwnj;و که&zwnj;سه سته&zwnj;مکارتره که به فه&zwnj;رمووده&zwnj;کانی خوا یاداوه&zwnj;ری و ئامۆژگاری کرابێت، پاشان پشتی لێ هه&zwnj;ڵکربێت و گوێی پێنه&zwnj;دات، به&zwnj;ڕاستی ئێمه تۆڵه له تاوانبارو تاوانکاران ده&zwnj;ستێنین.';
$t[23] = 'سوێند به خوا به&zwnj;ڕاستی ئێمه ته&zwnj;وراتمان به&zwnj;خشی به موسا، تۆ (ئه&zwnj;ی محمد صلی الله علیه وسلم) هیچ گومانت نه&zwnj;بێت له دیداری موساو له&zwnj;وه&zwnj;ش که ته&zwnj;ورات له&zwnj;لایه&zwnj;ن ئێمه&zwnj;وه به&zwnj;خشراوه به&zwnj;و، ئه&zwnj;و کاته ئێمه ته&zwnj;وراتمان کرد به ڕێنموویی بۆ نه&zwnj;وه&zwnj;ی ئیسرائیل.';
$t[24] = 'پاشان که نه&zwnj;وه&zwnj;ی ئیسرائیل خۆگری و ئارامیان کرده پیشه&zwnj;یان کردمانن به پێشه&zwnj;واو ده&zwnj;سه&zwnj;ڵاتمان پێبه&zwnj;خشین، ئه&zwnj;و کاته دڵنیایی و بڕوایه&zwnj;کی ته&zwnj;واویان هه&zwnj;بوو به ئایه&zwnj;ته&zwnj;کانی ئێمه (شایسته&zwnj;ی ئه&zwnj;و ڕێزه بوون).';
$t[25] = 'به&zwnj;ڕاستی په&zwnj;روه&zwnj;ردگارت خۆی (دوای ئه&zwnj;وه&zwnj;ی که بوونه چه&zwnj;نده&zwnj;ها ده&zwnj;سته و گرۆ) له ڕۆژی قیامه&zwnj;تدا له&zwnj;یه&zwnj;ك جیایان ده&zwnj;کاته&zwnj;وه&zwnj;، چاك و خراپیان دیاری ده&zwnj;کات له&zwnj;و شتانه&zwnj;دا که&zwnj;وا ئه&zwnj;وان کێشه&zwnj;یان تێدا ده&zwnj;کرد و ڕووبه&zwnj;ڕوی یه&zwnj;ك ده&zwnj;بوونه&zwnj;وه و به&zwnj;رامبه&zwnj;ر یه&zwnj;ك ده&zwnj;وه&zwnj;ستانه&zwnj;وه&zwnj;.';
$t[26] = 'ئایا ئه&zwnj;وه په&zwnj;ندو ئامۆژگاری وه&zwnj;رناگرن له&zwnj;وه&zwnj;ی: ئێمه پێش ئه&zwnj;مان چه&zwnj;نده&zwnj;ها جیل و نه&zwnj;وه&zwnj;مان له&zwnj;ناوبردوه&zwnj;، به&zwnj;ناو ماڵه&zwnj;کانیاندا دێن و ده&zwnj;چن، به&zwnj;ڕاستی ئا له&zwnj;و دیمه&zwnj;نانه&zwnj;دا به&zwnj;ڵگه&zwnj;ی زۆر به&zwnj;هێز هه&zwnj;یه له&zwnj;سه&zwnj;ر ده&zwnj;سه&zwnj;ڵاتی ئێمه&zwnj;، ئایا ئه&zwnj;وه نابیستن؟!';
$t[27] = 'ئایا سه&zwnj;رنجیان نه&zwnj;داوه که به&zwnj;ڕاستی چۆن ئێمه ئاوکێش ده&zwnj;که&zwnj;ین و ده&zwnj;یبه&zwnj;ین بۆ زه&zwnj;وی وزاری وشك و بێ ڕوه&zwnj;ك جا به&zwnj;و ئاوه ڕوه&zwnj;ك و گژوگیا ده&zwnj;رده&zwnj;هێنین که ماڵاتیان و خۆیشیان لێی ده&zwnj;خۆن، ئایا ئه&zwnj;وه نابینن و سه&zwnj;رنج ناده&zwnj;ن؟!';
$t[28] = 'بێ باوه&zwnj;ڕان ده&zwnj;ڵێن: باشه&zwnj;، ئه&zwnj;وه که&zwnj;ی ئه&zwnj;م جیاکردنه&zwnj;وه و سه&zwnj;رکه&zwnj;وتنی ئیمانداره پێش دێت، ئه&zwnj;گه&zwnj;ر ڕاست ده&zwnj;که&zwnj;ن؟!';
$t[29] = 'ئه&zwnj;ی محمد صلی الله علیه وسلم پێیان بڵێ: ڕۆژی جیاکردنه&zwnj;وه و سه&zwnj;رکه&zwnj;وتنی ئیمان، باوه&zwnj;ڕهێنانی کافران هیچ سوودی نابێت بۆیان، هیچ مۆڵه&zwnj;تیشیان نادرێت (په&zwnj;شیمانییش سوودی نابێت).';
$t[30] = 'جا تۆ ڕوویان لێ وه&zwnj;رگێڕه و گوێیان مه&zwnj;ده&zwnj;رێ، به&zwnj;ڵکو چاوه&zwnj;ڕوان به&zwnj;، به&zwnj;ڕاستی ئه&zwnj;وانه چاوه&zwnj;ڕوانن!!';
?>