$t = array();
$t[1] = 'ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر صلی الله علیه وسلم له&zwnj;خوا بترسه&zwnj;و به&zwnj;رده&zwnj;وام به&zwnj;له&zwnj;سه&zwnj;ر ته&zwnj;قواو پارێزکاری، هه&zwnj;رگیز ملکه&zwnj;چی بێ باوه&zwnj;ڕان و دوورووه&zwnj;کان مه&zwnj;به (که&zwnj;ده&zwnj;یانه&zwnj;وێت له&zwnj;ڕێگه&zwnj;ی ڕاست وێڵت بکه&zwnj;ن، چونکه&zwnj;&zwnj;) به&zwnj;ڕاستی خوا هه&zwnj;میشه و به&zwnj;رده&zwnj;وام زاناو دانایه&zwnj;.';
$t[2] = 'به&zwnj;ڵکو ته&zwnj;نها شوێنی ئه&zwnj;و (&zwnj;ڕێنموونییانه&zwnj;) بکه&zwnj;وه که له&zwnj;لایه&zwnj;ن په&zwnj;روه&zwnj;ردگارته&zwnj;وه به&zwnj;نیگا بۆت ڕه&zwnj;وانه&zwnj;ده&zwnj;کرێت، به&zwnj;ڕاستی خوا هه&zwnj;میشه ئاگاداره به&zwnj;و کرده&zwnj;وانه&zwnj;ی که ئه&zwnj;نجامی ده&zwnj;ده&zwnj;ن.';
$t[3] = 'هه&zwnj;میشه&zwnj;و به&zwnj;رده&zwnj;وام پشت به&zwnj;خوا ببه&zwnj;سته&zwnj;، ته&zwnj;نها خوا به&zwnj;سه که پشتیوان و یاریده&zwnj;ده&zwnj;ر بێت.';
$t[4] = 'خوا دوو دڵی دروست نه&zwnj;کردووه له ده&zwnj;روونی هیچ که&zwnj;سێکدا (دوو بیروباوه&zwnj;ڕ جێی نابێته&zwnj;وه&zwnj;)، که&zwnj;واته هاوسه&zwnj;رانیشتان که به&zwnj;ده&zwnj;م پێیان ده&zwnj;ڵێن: (ئێوه له&zwnj;جێی دایکمانن) به&zwnj;و قسه&zwnj;یه نابنه دایکتان، ئه&zwnj;وانه&zwnj;ش به کوڕی خۆتانی ده&zwnj;زانن (له کاتێکدا ڕاستیه&zwnj;که&zwnj;ی وا نیه&zwnj;)، خوا حه&zwnj;ق وێژه&zwnj;و هه&zwnj;ر ئه&zwnj;و ڕێبازی ڕاست و دروست، نیشانی (به&zwnj;نده&zwnj;کانی) ده&zwnj;دات.';
$t[5] = 'ئه&zwnj;و کوڕانه به&zwnj;ناوی باوکی خۆیانه&zwnj;وه ناویان به&zwnj;رن و بانگیان بکه&zwnj;ن، ئه&zwnj;وه په&zwnj;سه&zwnj;نده لای خوا، ئه&zwnj;گه&zwnj;ر باوکیانتان نه&zwnj;ده&zwnj;ناسی، خۆ برای دینیتانن، سه&zwnj;ربه ئێوه&zwnj;ن و له ئێوه&zwnj;ن (ئه&zwnj;گه&zwnj;ر دوور له&zwnj;سووکایه&zwnj;تی) هه&zwnj;ڵه&zwnj;یه&zwnj;کتان کرد ئه&zwnj;وه قه&zwnj;یناکات، به&zwnj;ڵام ئه&zwnj;گه&zwnj;ر له&zwnj;دڵتاندا، نیه&zwnj;تێکی ناپاک هه&zwnj;بێت بۆ سووکایه&zwnj;تی پێکردنیان ئه&zwnj;وه لێپرسینه&zwnj;وه&zwnj;ی ده&zwnj;بێت، هه&zwnj;میشه&zwnj;و به&zwnj;رده&zwnj;وامیش خوا لێ خۆشبوو میهره&zwnj;بانه (له&zwnj;هه&zwnj;ڵه&zwnj;کانتان ده&zwnj;بوورێت، ئه&zwnj;گه&zwnj;ر لێی په&zwnj;شیمان بنه&zwnj;وه&zwnj;).';
$t[6] = 'پێغه&zwnj;مبه&zwnj;ر (صلی الله علیه وسلم) نزیکتره به&zwnj;ئیمانداران له&zwnj;خودی خۆیان (واته&zwnj;ده&zwnj;بێت له&zwnj;خۆیان زیاتر خۆشیان بوێت و فه&zwnj;رمانه&zwnj;کانی له&zwnj;سه&zwnj;رو فه&zwnj;رمان و داخوازی خۆیانه&zwnj;وه&zwnj;بێت) خێزانه&zwnj;کانیشی دایکیانن (دایکی ئیماندارانن، دایکی مه&zwnj;عنه&zwnj;ویانن) خزمانیش هه&zwnj;ندێکیان له&zwnj;هه&zwnj;ندێکی تریان له&zwnj;پێشترن، له&zwnj;ققورئانی خوادا، له&zwnj;ئیمانداران و کۆچبه&zwnj;ران، مه&zwnj;گه&zwnj;ر له&zwnj;گه&zwnj;ڵ دۆستان و خزامان و ئاشنایانی خۆتان کارێکی چاك ئه&zwnj;نجام بده&zwnj;ن، ئه&zwnj;و چاکه&zwnj;یه&zwnj;تان له&zwnj;نامه&zwnj;ی کرده&zwnj;وه&zwnj;کانتاندا بۆتان تۆمار کراوه&zwnj;.';
$t[7] = '(بیریان بخه&zwnj;ره&zwnj;وه که ئێمه&zwnj;) په&zwnj;یمانمان له&zwnj;پێغه&zwnj;مبه&zwnj;ران وه&zwnj;رگرتووه په&zwnj;یمانی یه&zwnj;کتاپه&zwnj;ره&zwnj;ستی، په&zwnj;یمانی گه&zwnj;یاندنی بانگه&zwnj;وازی خوا، به&zwnj;تایبه&zwnj;ت تۆ ئه&zwnj;ی محمد (صلی الله علیه&zwnj;وسلم) و له&zwnj;نوح و ئیبراهیم و موسا و عیسای کوڕی مه&zwnj;ریه&zwnj;م، بێگومان په&zwnj;یمانێکی به&zwnj;هێز و بته&zwnj;ومان لێ وه&zwnj;رگرتن...';
$t[8] = 'تا له&zwnj;ڕۆژی قیامه&zwnj;تدا، خوا پرسیار بکات له&zwnj;ڕاستگۆیان ده&zwnj;رباره&zwnj;ی ڕاستیه&zwnj;که&zwnj;یان (واته له پێغه&zwnj;به&zwnj;ران ده&zwnj;رباره&zwnj;ی گه&zwnj;یاندنی په&zwnj;یامه&zwnj;که&zwnj;یان)، بۆ کافرانیش سزاو تۆڵه&zwnj;یه&zwnj;کی به&zwnj;ئێشی ئاماده&zwnj;کردووه&zwnj;.';
$t[9] = 'ئه&zwnj;ی ئه&zwnj;وانه&zwnj;ی ئیمان و باوه&zwnj;ڕتان هێناوه یادی نیعمه&zwnj;تی خوا بکه&zwnj;نه&zwnj;وه له&zwnj;سه&zwnj;رتان (مه&zwnj;به&zwnj;ست جه&zwnj;نگی خه&zwnj;نده&zwnj;قه که خوای گه&zwnj;وره فریای ئیمانداران که&zwnj;وت)، کاتێك سه&zwnj;ربازانی کوفر هاتنه سه&zwnj;رتان ئێمه&zwnj;ش ڕه&zwnj;شه&zwnj;بایه&zwnj;کی به&zwnj;هێز و سه&zwnj;ربازانێکمان نارده&zwnj;سه&zwnj;ریان که نه&zwnj;تانده&zwnj;بینین، خوای گه&zwnj;وره&zwnj;بینا بوو به&zwnj;هه&zwnj;وڵ و کۆششتان (چۆن پاسه&zwnj;وانیتان ده&zwnj;کرد، چۆن ئه&zwnj;و چاڵه&zwnj;قووڵ و درێژه&zwnj;تان هه&zwnj;ڵکه&zwnj;ند هتد).';
$t[10] = 'کاتێکیش دوژمنان له&zwnj;سه&zwnj;رو خوارتانه&zwnj;وه هاتنه سه&zwnj;رتان، کاتێکیش چاوه&zwnj;کان ئه&zwnj;بله&zwnj;ق بوون، دڵه&zwnj;کان گه&zwnj;شتبوونه گه&zwnj;روو، گومانی جۆراو جۆرتان ده&zwnj;برد به&zwnj;به&zwnj;ڵێنه&zwnj;کانی خوای گه&zwnj;وره (به&zwnj;سه&zwnj;رکه&zwnj;وتنی ئیمانداران).';
$t[11] = 'له&zwnj;وێدا ئیمانداران تاقی کرانه&zwnj;وه و تووشی ته&zwnj;نگانه و سه&zwnj;ختیه&zwnj;کی زۆر توندو به&zwnj;هێز بوون، تووشی دڵه&zwnj;له&zwnj;رزه&zwnj;یه&zwnj;کی زۆر سه&zwnj;خت کران.';
$t[12] = 'کاتێکیش دووڕووه&zwnj;کان و ئه&zwnj;وانه&zwnj;ی دڵیان نه&zwnj;خۆشی گومانی تێدابوو ده&zwnj;یانوت به&zwnj;ڵێنی خوا و پێغه&zwnj;مبه&zwnj;ره&zwnj;که&zwnj;ی ته&zwnj;نها بۆ خه&zwnj;ڵه&zwnj;تاندن بوو، هیچی تر نه&zwnj;بوو؟!';
$t[13] = 'کاتێكیش هه&zwnj;ندێك له دووڕووه&zwnj;کان وتیان: ئه&zwnj;ی خه&zwnj;ڵکی یثریب، ئه&zwnj;مه هیچ شوێنی ئێوه نییه&zwnj;، بگه&zwnj;ڕێنه&zwnj;وه ناوشار، ده&zwnj;سته&zwnj;یه&zwnj;کێش له&zwnj;وان مۆڵه&zwnj;تیان له&zwnj;پێغه&zwnj;مبه&zwnj;ر ده&zwnj;خواست و ده&zwnj;یانوت: ماڵه&zwnj;کانمان به&zwnj;ده&zwnj;ره&zwnj;وه&zwnj;یه (گوایه دوژمن به&zwnj;ئاسانی ده&zwnj;توانێت هه&zwnj;ڵکوتێته سه&zwnj;ری)، له&zwnj;ڕاستیدا وانییه&zwnj;، ماڵه&zwnj;کانیان به&zwnj;ده&zwnj;ره&zwnj;وه&zwnj;نین (دوژمن ناتوانێت زه&zwnj;فه&zwnj;ریان پێبه&zwnj;رێت)، ته&zwnj;نها ڕاکردنیان مه&zwnj;به&zwnj;سته&zwnj;و هیچی تر.';
$t[14] = 'خۆ ئه&zwnj;گه&zwnj;ر له&zwnj;شکری دوژمنان له&zwnj;چوارلای یثریبه&zwnj;و ه&zwnj;بچونایه&zwnj;ته سه&zwnj;ریان، له&zwnj;وه&zwnj;و دوا داوای جه&zwnj;نگیان لێبکرایه دژی موسوڵمانان، ئه&zwnj;وه&zwnj;ده&zwnj;چوون به&zwnj;ده&zwnj;مییه&zwnj;وه&zwnj;، ته&zwnj;نها که&zwnj;مێك نه&zwnj;بێت وه&zwnj;ڵامه&zwnj;که&zwnj;یان دوانه&zwnj;ده&zwnj;خست.';
$t[15] = 'سوێند به&zwnj;خوا به&zwnj;ڕاستی زووتر ئه&zwnj;وانه په&zwnj;یمانیان به&zwnj;خوای گه&zwnj;وره دابوو پشت هه&zwnj;ڵنه&zwnj;که&zwnj;نن له&zwnj;کاتی به&zwnj;ره&zwnj;نگاری دوژمندا، بێگومان به&zwnj;ڵێنێك به&zwnj;خوا درابێت لێی ده&zwnj;پرسرێته&zwnj;وه&zwnj;.';
$t[16] = 'پێیان بڵێ: (ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر صلی الله علیه وسلم) هه&zwnj;رگیز ڕاکردن، سوودبه&zwnj;خش نابێت بۆتان ئه&zwnj;گه&zwnj;ر هه&zwnj;ڵبێن له مردن، یان له&zwnj;کوشتن، ئه&zwnj;وسا ته&zwnj;نها که&zwnj;مێك نه&zwnj;بێت دواناخرێن و ڕابواردن و ژیانتان زۆر که&zwnj;مووکۆتایه&zwnj;.';
$t[17] = 'پێیان بڵێ: کێ هه&zwnj;یه په&zwnj;ناتان بدات له&zwnj;خواو پتانپارێزێت له تۆڵه سه&zwnj;ندنه&zwnj;وه&zwnj;ی، ئه&zwnj;گه&zwnj;ر بییه&zwnj;وێت ناخۆشییه&zwnj;کتان بۆ پێش بهێنێت، یا خود کێ هه&zwnj;یه ده&zwnj;توانێت به&zwnj;ری میهره&zwnj;بانی و سۆزی بگرێت، ئه&zwnj;گه&zwnj;ر بیه&zwnj;وێت به&zwnj;زه&zwnj;یی و ڕه&zwnj;حمه&zwnj;تی خۆیتان پێ ببه&zwnj;خشێت، ئه&zwnj;وکاته ئیتر ئه&zwnj;وانه جگه له خوا که&zwnj;سیان ده&zwnj;ست ناکه&zwnj;وێت پشتیوانیان لێ بکات و سه&zwnj;ریان بخات.';
$t[18] = 'به&zwnj;ڕاستی خوا ئه&zwnj;و که&zwnj;سانه ده&zwnj;ناسێت که کۆسپ و ته&zwnj;گه&zwnj;ره درووست ده&zwnj;که&zwnj;ن له&zwnj;ئێوه&zwnj;، ئه&zwnj;وانه&zwnj;ی به براکانیان ده&zwnj;ڵێن وه&zwnj;رن بۆ لای ئێمه&zwnj;، (خۆتان بدزنه&zwnj;وه له سوپای ئیمانداران)، ئه&zwnj;وانه ته&zwnj;نها که&zwnj;م نه&zwnj;بێت خۆیان ناخه&zwnj;نه ناو تاڵوکه&zwnj;ی جه&zwnj;نگه&zwnj;وه&zwnj;.';
$t[19] = 'به&zwnj;رامبه&zwnj;ر ئێوه ده&zwnj;ست نوقاوو بێ هیمه&zwnj;تن، جا کاتێك ترس وبیمی جه&zwnj;نگیان بۆ دێت ئه&zwnj;وه ده&zwnj;یانبینێت، ته&zwnj;ماشات ده&zwnj;که&zwnj;ن، چاوه&zwnj;کانیان ده&zwnj;سوڕێته&zwnj;وه و ئه&zwnj;بڵه&zwnj;ق ده&zwnj;بێت وه&zwnj;ك که&zwnj;سێك له&zwnj;ترسی مه&zwnj;رگ بێ هۆشی به&zwnj;سه&zwnj;ردا هاتبێت، جا کاتێك ئه&zwnj;و ترس و بیمه نه&zwnj;ما، زمان درێژه&zwnj;کانیان ئازارتان ده&zwnj;دن به&zwnj;قسه&zwnj;ی ڕه&zwnj;ق و ناخۆش، له&zwnj;کاتێكدا ئه&zwnj;وان چاوبرسی و به&zwnj;هه&zwnj;ڵپه&zwnj;ن بۆ پاره&zwnj;، ئائه&zwnj;وانه باوه&zwnj;ڕیان نه&zwnj;هێناوه&zwnj;، به&zwnj;و هۆیه&zwnj;وه خوا کرده&zwnj;وه&zwnj;کانیان پووچ کردنه&zwnj;وه&zwnj;، ئه&zwnj;وکاره&zwnj;ش هه&zwnj;میشه بۆ خوا ئاسانه&zwnj;.';
$t[20] = 'ئه&zwnj;وانه هێشتا واده&zwnj;زانن که له&zwnj;شکر و گرۆ و ده&zwnj;سته&zwnj;کان نه&zwnj;ڕۆیشتوون، ئه&zwnj;گه&zwnj;ر له&zwnj;شکری گرۆکان هات، ئاوات ده&zwnj;خوازن بریا له&zwnj;ناو عه&zwnj;ره&zwnj;به بیابان نشینه&zwnj;کاندا بوونایه&zwnj;و له&zwnj;وێوه&zwnj;هه&zwnj;واڵی ئێوه&zwnj;یان بپرسیایه&zwnj;!! خۆ ئه&zwnj;گه&zwnj;ر له&zwnj;ناو ئێوه&zwnj;دا بوونایه&zwnj;ته&zwnj;نها که&zwnj;م نه&zwnj;بێت جه&zwnj;نگیان نه&zwnj;ده&zwnj;کرد.';
$t[21] = 'سوێند به&zwnj;خوا به&zwnj;ڕاستی له&zwnj;پێغه&zwnj;مبه&zwnj;ری خوادا چاکترین نموونه&zwnj;ی ته&zwnj;واو ڕێك پێك هه&zwnj;یه&zwnj;تا چوی لێ بکه&zwnj;ن و شوێنی بکه&zwnj;ون، به&zwnj;تایبه&zwnj;ت بۆ ئه&zwnj;و که&zwnj;سانه&zwnj;ی ڕه&zwnj;زامه&zwnj;ندی خوایان مه&zwnj;به&zwnj;سته&zwnj;، سه&zwnj;رفرازی قیامه&zwnj;ت ئاواتیانه&zwnj;، هه&zwnj;میشه و به&zwnj;رده&zwnj;وام یادی خوا به زۆری ئه&zwnj;نجام ده&zwnj;ده&zwnj;ن و زمانیان پاراوه به&zwnj;یادی ئه&zwnj;و.';
$t[22] = 'کاتێك ئیمانداران ئه&zwnj;و هه&zwnj;موو ده&zwnj;سته و گرۆهانه&zwnj;یان بینی وتیان: ئه&zwnj;مه ئه&zwnj;وه&zwnj;یه که خوا و پێغه&zwnj;مبه&zwnj;ره&zwnj;که&zwnj;ی به&zwnj;ڵێنی پێداوین (که&zwnj;ئاوا دژایه&zwnj;تی ئایینه&zwnj;که&zwnj;ی ده&zwnj;کرێت و کافران ودووڕووه&zwnj;کان نه&zwnj;خشه&zwnj;و پیلان ده&zwnj;کێشن بۆ ڕیشه&zwnj;که&zwnj;ن کردنی)، دیاره که خوا و پێغه&zwnj;مبه&zwnj;ره&zwnj;که&zwnj;ی ڕاستیان فه&zwnj;رموو له&zwnj;پێش هاتنیدا، بێگومان ئه&zwnj;و دژایه&zwnj;تییه باوه&zwnj;ڕو ملکه&zwnj;چی نه&zwnj;بێت هیچی تری بۆ زیاد نه&zwnj;کردن (واته&zwnj;سوورتر بوون له&zwnj;سه&zwnj;ر ئیمان).';
$t[23] = 'مه&zwnj;ردانێکی زۆر هه&zwnj;ن له&zwnj;ئیمانداران ئه&zwnj;و په&zwnj;یمانه&zwnj;ی داویانه به خوا، ڕاستیان کرد له&zwnj;سه&zwnj;ری ژیان و پابه&zwnj;ند بوون پێوه&zwnj;ی و لایان نه&zwnj;دا لێی، جا هه&zwnj;یانه په&zwnj;یمانه&zwnj;که&zwnj;ی جێبه&zwnj;جێ کرد و مرد له&zwnj;سه&zwnj;ری، هه&zwnj;شیانه چاوه&zwnj;ڕوانه&zwnj;، به&zwnj;هیچ شێوه&zwnj;یه&zwnj;ك گۆڕان و لاری ڕووی تێ نه&zwnj;کردوون.';
$t[24] = 'تا خوای گه&zwnj;وره پاداشتی ڕاسگۆیان به&zwnj;هۆی ڕاستیه&zwnj;که&zwnj;یان بداته&zwnj;وه&zwnj;، هه&zwnj;روه&zwnj;ها سزاو تۆڵه&zwnj;ی دووڕووه&zwnj;کانیش بدات ئه&zwnj;گه&zwnj;ر بیه&zwnj;وێت، یان تۆبه&zwnj;و په&zwnj;شیمانیان لێ وه&zwnj;رده&zwnj;گرێت، چونکه به&zwnj;ڕاستی خوا هه&zwnj;میشه و به&zwnj;رده&zwnj;وام لێ خۆشبوو میهره&zwnj;بانه&zwnj;.';
$t[25] = 'خوا کافرانی تێكشکاند و گێڕانیه&zwnj;وه دواوه به&zwnj;خۆیان و کینه و بوغزیانه&zwnj;وه&zwnj;، هیچ جۆره خه&zwnj;یرێکیان ده&zwnj;ست نه&zwnj;که&zwnj;وت، هه&zwnj;ر خوا خۆی ئیماندارانی له&zwnj;جه&zwnj;نگ پاڕاست (هه&zwnj;ر خۆێشی به باو باران و له&zwnj;شکری تایبه&zwnj;تی کافرانی تێكشکان)، جا هه&zwnj;میشه&zwnj;و به&zwnj;رده&zwnj;وام خوا به&zwnj;هێزه&zwnj;و به&zwnj;ده&zwnj;سه&zwnj;ڵاته&zwnj;.';
$t[26] = 'ئه&zwnj;وانه&zwnj;ی پشتگیری قوڕه&zwnj;یشیه&zwnj;کانیان کرد له&zwnj;خاوه&zwnj;نی کتێبه ئاسمانیه&zwnj;کان، له&zwnj;جووه&zwnj;کانی (بنی قریزة) خوای گه&zwnj;وره له قڵاکانیان دایگرتن، ترس و بیمی فڕێدایه دڵیان، تا وایان لێهات، ده&zwnj;سته&zwnj;یه&zwnj;کتان کوشتن و ده&zwnj;سته&zwnj;یه&zwnj;کیشتان به&zwnj;دیل گرتن.';
$t[27] = 'سه&zwnj;ره&zwnj;نجام هه&zwnj;رچی زه&zwnj;وی و زارو ماڵ و خانوو سامانێکیان هه&zwnj;بوو خوا به&zwnj;خشی به&zwnj;ئێوه&zwnj;، هه&zwnj;روه&zwnj;ها شوێنی تریش که&zwnj;هێشتا تا ئێستا پێیدا نه&zwnj;ڕۆشتوون، هه&zwnj;میشه&zwnj;و به&zwnj;رده&zwnj;وامیش خوا ده&zwnj;ستی به&zwnj;سه&zwnj;ر هه&zwnj;موو شتێکدا ده&zwnj;ڕوات. (به&zwnj;پشتیوانی خوای گه&zwnj;وره ڕۆژێك دێت، پیلان گێڕان سه&zwnj;رشۆڕ و ڕیسوا ده&zwnj;کات وه هه&zwnj;رچی سامان و ماڵێك که به&zwnj;خیانه&zwnj;ت و فێڵ به&zwnj;ده&zwnj;ستیان هێناوه&zwnj;، ده&zwnj;درێته&zwnj;وه به&zwnj;خاوه&zwnj;نه&zwnj;کانی... جا ئه&zwnj;و ڕۆژه ئه&zwnj;م ئایه&zwnj;تانه&zwnj;ی سوره&zwnj;تی (أحزاب)21-27 ده&zwnj;ڵێی ئه&zwnj;و ساته&zwnj;هاتووه&zwnj;ته&zwnj;خواره&zwnj;وه&zwnj;).';
$t[28] = 'ئه&zwnj;پێغه&zwnj;مبه&zwnj;ر به&zwnj;هاوسه&zwnj;رانت بڵێ: ئه&zwnj;گه&zwnj;ر ئێوه ته&zwnj;نها ژیانی دونیا و ڕازاوه&zwnj;یه&zwnj;که&zwnj;یتان ده&zwnj;وێت، وه&zwnj;رن با هه&zwnj;ندێك شتومه&zwnj;ك و پاره و سامانتان پێ ببه&zwnj;خشم، له&zwnj;وه&zwnj;ودواش به جوانترین شێوه ده&zwnj;ست به&zwnj;ردارتان ببم.';
$t[29] = 'خۆ ئه&zwnj;گه&zwnj;ر ئێوه خوا و پێغه&zwnj;مه&zwnj;ره&zwnj;که&zwnj;ی و پاداشتی ڕۆژی قیامه&zwnj;تان ده&zwnj;وێت، ئه&zwnj;وه دڵنیا بن که خوا بۆ چاکه&zwnj;کارانتان پاداشتی زۆر گه&zwnj;وره و بێ ئه&zwnj;ندازه&zwnj;ی ئاماده&zwnj;کردووه (پێغه&zwnj;مبه&zwnj;ر هه&zwnj;موو هاوسه&zwnj;رانی یه&zwnj;ك یه&zwnj;ك تاقی کرده&zwnj;وه هه&zwnj;موانیان خواو پێغه&zwnj;مبه&zwnj;رو پاداشتی قیامه&zwnj;تیان هه&zwnj;ڵبژارد).';
$t[30] = 'ئه&zwnj;ی هاوسه&zwnj;رانی پێغه&zwnj;مبه&zwnj;ر: هه&zwnj;رکه&zwnj;س له&zwnj;ئێوه به گوناهێکی گه&zwnj;وره&zwnj;ی ئاشکرا هه&zwnj;ستێت، ئه&zwnj;وه بابزانێت که دووبه&zwnj;رابه&zwnj;ر سزا ده&zwnj;درێت، ئه&zwnj;وه&zwnj;ش له&zwnj;لای خوا زۆر ئاسانه چونکه ئێوه قه&zwnj;درو ڕێزی تایبه&zwnj;تیتان هه&zwnj;یه&zwnj;.';
$t[31] = 'ئه&zwnj;وه&zwnj;شتان فه&zwnj;رمانبه&zwnj;ردارو ملکه&zwnj;چی خواو پێغه&zwnj;مبه&zwnj;رکه&zwnj;ی بێت، کرده&zwnj;وه&zwnj;ی چاکیش ئه&zwnj;نجام بدات ئه&zwnj;وه&zwnj;دوو جار پاداشتی ده&zwnj;ده&zwnj;ینه&zwnj;وه&zwnj;، هه&zwnj;روه&zwnj;ها ڕزق و ڕۆزیه&zwnj;کی به&zwnj;نرخ و پیرۆزمان بۆ ئاماده&zwnj;کردووه&zwnj;.';
$t[32] = 'ئه&zwnj;ی ژنانی پێغه&zwnj;مبه&zwnj;ر (صلی الله علیه و سلم)، ئێوه وه&zwnj;ك هیچ ئافره&zwnj;تێکی تر نین، ئه&zwnj;گه&zwnj;ر به&zwnj;ته&zwnj;واوی ترسی خوا له&zwnj;دڵ و ده&zwnj;رونتاندا قسه به&zwnj;ناسكی و شێوه&zwnj;یه&zwnj;ك مه&zwnj;که&zwnj;ن، نه&zwnj;وه&zwnj;کو ئه&zwnj;وه&zwnj;ی دڵی نه&zwnj;خۆشه بووروژێت و نیازی خراپ بکات، هه&zwnj;میشه&zwnj;و به&zwnj;رده&zwnj;وامیش قسه&zwnj;ی چاك و به&zwnj;جێ بکه&zwnj;ن (که هیچ که&zwnj;س نه&zwnj;توانێت ڕخنه&zwnj;ی لێبگرێت).';
$t[33] = 'ئه&zwnj;ی هاوسه&zwnj;رانی پێغه&zwnj;مبه&zwnj;ر (صلی الله علیه&zwnj;وسلم) له&zwnj;ماڵه&zwnj;کانی خۆتاندا به&zwnj;سه&zwnj;نگینی و سه&zwnj;لاری بسره&zwnj;ون، زینه&zwnj;ت و جوانی خۆتان ده&zwnj;رمه&zwnj;خن وه&zwnj;کوو سه&zwnj;رده&zwnj;می نه&zwnj;فامی یه&zwnj;که&zwnj;م، نوێژه&zwnj;کانیشتان به&zwnj;چاکی ئه&zwnj;نجام بده&zwnj;ن و زه&zwnj;کات بده&zwnj;ن (له&zwnj;ماڵ و سامان)، فه&zwnj;رمانبه&zwnj;رداری خواو پێغه&zwnj;مبه&zwnj;ره&zwnj;که&zwnj;شی بکه&zwnj;ن، چونکه به&zwnj;ڕاستی خوا ته&zwnj;نها ده&zwnj;یه&zwnj;وێت هه&zwnj;موو ناپاکی و ناسازی و نادروستیه&zwnj;ك له ئێوه دوور بخاته&zwnj;وه ئه&zwnj;ی خاوخێزانی ماڵی پێغه&zwnj;مبه&zwnj;رو به&zwnj;ته&zwnj;واوی پاکتان بکاته&zwnj;وه (دوور له&zwnj;هه&zwnj;موو ناته&zwnj;واوی و قسه&zwnj;و قسه&zwnj;ڵۆکێك).';
$t[34] = 'هه&zwnj;رچی له&zwnj;ماڵه&zwnj;کانتاندا ده&zwnj;خوێنرێته&zwnj;وه له&zwnj;قورئان و فه&zwnj;رمووده یادی بگرن و فه&zwnj;رامۆشی مه&zwnj;که&zwnj;ن و باسی بکه&zwnj;ن بۆ خه&zwnj;ڵکی، به&zwnj;ڕاستی خوا هه&zwnj;میشه&zwnj;و به&zwnj;رده&zwnj;وام به&zwnj;لوطف و وردبین و به&zwnj;ئاگایه (له هه&zwnj;ڵسوکه&zwnj;وتی به&zwnj;نده&zwnj;کانی).';
$t[35] = 'به&zwnj;ڕاستی پیاوان و ئافره&zwnj;تانی موسوڵمان، پیاوان و ئافره&zwnj;تانی ئیمان دار، پیاوان و ئافره&zwnj;تانی ملکه&zwnj;چ و فه&zwnj;رمانبه&zwnj;ردار، پیاوان و ئافره&zwnj;تانی ڕاستگۆ، پیاوان و ئافره&zwnj;تانی خۆگر، پیاوان و ئافره&zwnj;تانی له&zwnj;خوا ترس، پیاوان و ئافره&zwnj;تانی به&zwnj;خشه&zwnj;نده&zwnj;، پیاوان و ئافره&zwnj;تانی ڕۆژووان، پیاوان و ئافره&zwnj;تانی داوێن پاك و ئه&zwnj;و پیاو و ئافره&zwnj;تانه&zwnj;ی که زۆر یادی خوا ده&zwnj;که&zwnj;ن، به&zwnj;خشین و لێ بورده&zwnj;یی و پاداشتی زۆر مه&zwnj;زن و بێ سنووریان له&zwnj;لایه&zwnj;ن خوای گه&zwnj;وره&zwnj;وه بۆ ئاماده&zwnj;کراوه (هه&zwnj;ر که دنیایان به&zwnj;جێهێشت پێی شاد ده&zwnj;بن).';
$t[36] = 'بۆ هیچ پیاوێکی ئیماندار یان ژنێکی ئیماندار، نیه کاتێک که خواو پێغه&zwnj;مبه&zwnj;ره&zwnj;که&zwnj;ی بڕیاری شتێک بده&zwnj;ن ئه&zwnj;وان سه&zwnj;رپشک بن (له ئه&zwnj;نجامدانی ئه&zwnj;و کاره، چونکه) ئه&zwnj;&zwnj;وه&zwnj;ی سه&zwnj;رپێچی بکات له فه&zwnj;رمانی خواو پێغه&zwnj;مبه&zwnj;ره&zwnj;که&zwnj;ی ئه&zwnj;وه ئیتر به ئاشکرا گومڕاو سه&zwnj;رلێشێواوه.';
$t[37] = 'تۆ ئه&zwnj;ی محمد (صلى الله عليه وسلم) کاتێ به&zwnj;و که&zwnj;سه&zwnj;ی که (زید) ه&zwnj;و خوا نیعمه&zwnj;تی ئیمانی پێبه&zwnj;خشیوه&zwnj;، تۆش نیعمه&zwnj;تی ئازادیی و په&zwnj;روه&zwnj;رده&zwnj;ت له نزیکی خۆته&zwnj;وه پێداوه ده&zwnj;ڵێت: هاوسه&zwnj;ری خۆت بهێڵه&zwnj;ره&zwnj;وه لای خۆت که (زه&zwnj;ینه&zwnj;ب)ه&zwnj;، له خوا بترسه&zwnj;و (ته&zwnj;ڵاقی مه&zwnj;ده&zwnj;)، وه شتێک له دڵ و ده&zwnj;روونی خۆتدا ده&zwnj;شاریته&zwnj;وه که خوا ده&zwnj;یه&zwnj;وێت ئاشکرای بکات، له خه&zwnj;ڵکی ده&zwnj;ترسیت، (له&zwnj;به&zwnj;ر ئه&zwnj;وه) کاتێ که زه&zwnj;ید خواستی و بوو به هاوسه&zwnj;ری (پاشان ڕێک نه&zwnj;که&zwnj;وتن و جیابوونه&zwnj;وه&zwnj;و ته&zwnj;ڵاق که&zwnj;وه&zwnj;ته نێوانیان)، ئێمه (زه&zwnj;ینه&zwnj;ب)مان له تۆ ماره&zwnj;کرد، بۆ ئه&zwnj;وه&zwnj;ی هیچ جێپێلێژبوون و دڵته&zwnj;نگییه&zwnj;ک بۆ ئیمانداران دروست نه&zwnj;بێت، له هێنانی هاوسه&zwnj;رانی به&zwnj;ناو کوڕیان کاتێک که پێشتر هاوسه&zwnj;رو حه&zwnj;ڵالیان بوون، فه&zwnj;رمانی خواش ده&zwnj;بێت جێبه&zwnj;جێ بکرێت.';
$t[38] = 'هیچ گوناهو هه&zwnj;ڵه&zwnj;یه&zwnj;ک له سه&zwnj;ر پێغه&zwnj;مبه&zwnj;ر نیه له به&zwnj;رانبه&zwnj;ر کارێکه&zwnj;وه که خوا خستوویه&zwnj;ته سه&zwnj;رشانی، (وه ئه&zwnj;م جۆره گۆڕانکاریانه&zwnj;) به&zwnj;رنامه&zwnj;ی خوایه&zwnj;و له پێغه&zwnj;مبه&zwnj;ران و قه&zwnj;ومه پێشووه&zwnj;کانیشدا ڕوه&zwnj;یداوه (که عه&zwnj;قیده&zwnj;و بیروباوه&zwnj;ڕیان ڕاستکردووه&zwnj;ته&zwnj;وه هه&zwnj;روه&zwnj;ها په&zwnj;یوه&zwnj;ندیه کۆمه&zwnj;ڵایه&zwnj;تی و ئابووری و ڕامیاریه&zwnj;کانیشیان به&zwnj;گوێره&zwnj;ی به&zwnj;رنامه&zwnj;ی خوا سازدراوه&zwnj;)، به&zwnj;رده&zwnj;وامیش فه&zwnj;رمانه&zwnj;کانی خوا به&zwnj;وردی و ڕێکوپێکی ده&zwnj;بێت جێبه&zwnj;جێ بکرێت.';
$t[39] = 'ئه&zwnj;وانه&zwnj;ی که په&zwnj;یامه&zwnj;کانی خوا ده&zwnj;گه&zwnj;یه&zwnj;نن، وه له&zwnj;و ده&zwnj;ترسن و جگه له&zwnj;و له هێچ که&zwnj;سی تر ناترسن، خوا خۆی به&zwnj;سه که پاداشتی (چاکه&zwnj;کاران بداته&zwnj;وه&zwnj;و له بێ باوه&zwnj;ڕانیش بپرسێته&zwnj;وه).';
$t[40] = 'محمد وه&zwnj;نه&zwnj;بی باوکی هیچ که&zwnj;س له پیاوانتان بێت به&zwnj;ڵکو پێغه&zwnj;مبه&zwnj;ری خوایه&zwnj;و دواهه&zwnj;مینی پێغه&zwnj;مبه&zwnj;رانه&zwnj;، هه&zwnj;میشه&zwnj;و به&zwnj;رده&zwnj;وامیش خوا به هه&zwnj;موو شتێک زانایه.';
$t[41] = 'ئه&zwnj;ی ئه&zwnj;و که&zwnj;سانه&zwnj;ی ئیمان و باوه&zwnj;ڕتان هێناوه زۆر زۆر یادی خوا بکه&zwnj;ن (هه&zwnj;میشه ناوی پیرۆزی ئه&zwnj;وتان له&zwnj;یاد بێ له دڵ و ده&zwnj;روونتانا جۆش بخواو بێته سه&zwnj;ر زارتان).';
$t[42] = 'وه (ته&zwnj;سبیحات) و ستایشی بکه&zwnj;ن له به&zwnj;یانیان و ده&zwnj;مه&zwnj;و ئێواراندا.';
$t[43] = 'هه&zwnj;ر ئه&zwnj;و زاته خۆی (صلوات) و ڕه&zwnj;حمه&zwnj;تی خۆیتان هه&zwnj;میشه&zwnj;و به&zwnj;رده&zwnj;وام به&zwnj;سه&zwnj;ردا ده&zwnj;بارێنێت، هه&zwnj;روه&zwnj;ها فریشته&zwnj;کانیشی (داوای لێخۆشبونتان بۆ ده&zwnj;که&zwnj;ن) بۆ ئه&zwnj;وه&zwnj;ی له تاریکستانه&zwnj;کان ده&zwnj;رتان بکات و ڕزگارتان بکات وه بتانخاته ناو نوورو ڕووناکی (دینداریه&zwnj;وه له دنیادا، وه به&zwnj;هه&zwnj;شتی به&zwnj;رینی له قیامه&zwnj;تدا، جا ئه&zwnj;و په&zwnj;روه&zwnj;ردگاره&zwnj;) هه&zwnj;میشه&zwnj;و به&zwnj;رده&zwnj;وام بۆ ئیمانداران به&zwnj;ڕه&zwnj;حم و میهره&zwnj;بان و دلۆڤانه&zwnj;.';
$t[44] = 'ئه&zwnj;و ڕۆژه&zwnj;ش که به&zwnj;دیداری شاد ده&zwnj;بن، به سه&zwnj;لام کردن پێشوازیتان لێ ده&zwnj;کات وه پاداشتی زۆر به&zwnj;نرخ و پیرۆزی بۆ ئاماده کردوون.';
$t[45] = 'ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر (صلی الله علیه و سلم) ئێمه تۆمان ڕه&zwnj;وانه کردووه که شایه&zwnj;ت بیت (به&zwnj;سه&zwnj;ر ئومه&zwnj;تی ئیسلامه&zwnj;وه له په&zwnj;یامی خوات پێ ڕاگه&zwnj;یاندوون) وه مژده به&zwnj;خشیت (به ئیمانداران به&zwnj;وه&zwnj;ی که ڕه&zwnj;زامه&zwnj;ندی خواو به&zwnj;هه&zwnj;شتی به&zwnj;رین چاوه&zwnj;ڕێیانه&zwnj;) ه&zwnj;و ترسێنه&zwnj;ریشیت (بێ باوه&zwnj;ڕان ده&zwnj;ترسێنیت له خه&zwnj;شم و قینی خواو دۆزه&zwnj;خ).';
$t[46] = 'هه&zwnj;روه&zwnj;ها بانگه&zwnj;واز ده&zwnj;که&zwnj;ی بۆلای خوا له&zwnj;سه&zwnj;ر مۆڵه&zwnj;&zwnj;تی ئه&zwnj;و، چرایه&zwnj;کی ڕووناکیشی (بۆ سه&zwnj;رجه&zwnj;م سه&zwnj;رگه&zwnj;ردانان).';
$t[47] = '(به&zwnj;رده&zwnj;وام) مژده بده به ئیمانداران، که له&zwnj;لایه&zwnj;ن خواوه ڕێز و قه&zwnj;درێکی زۆریان هه&zwnj;یه&zwnj;.';
$t[48] = 'ملکه&zwnj;چ و فه&zwnj;رمانداری کافرو دووڕووه&zwnj;کان مه&zwnj;به گوێ مه&zwnj;ده به&zwnj;و یازارو ناخۆشیانه&zwnj;ی (که پێشی ده&zwnj;هێنن بۆ خۆت و بانگه&zwnj;وازه&zwnj;که&zwnj;ت)، هه&zwnj;میشه پشت به&zwnj;خوا ببه&zwnj;سته، ته&zwnj;نها خواش به&zwnj;سه که پشتیوان و یاریده&zwnj;ده&zwnj;ربێت.';
$t[49] = 'ئه&zwnj;ی ئه&zwnj;و که&zwnj;سانه&zwnj;ی باوه&zwnj;ڕتان هێناوه ئه&zwnj;گه&zwnj;رئافره&zwnj;تانی ئیماندارانتان ماره&zwnj;کرد، له&zwnj;وه&zwnj;ودوا جیابوونه&zwnj;وه&zwnj;و ته&zwnj;ڵاقدانتان پێش ئه&zwnj;وه&zwnj;ی بچنه لایان، ئه&zwnj;وه هیچ (عـــدة)و ماوه&zwnj;یه&zwnj;کیان ناوێ تا حیسابی بۆ بکه&zwnj;ن و (ماوه&zwnj;ی چاوه&zwnj;ڕوانی ناوێ بۆ ئه&zwnj;وه&zwnj;ی شوو بکات به یه&zwnj;کێکی تر)، (له&zwnj;گه&zwnj;ڵ ئه&zwnj;وه&zwnj;شدا) هه&zwnj;ندێك دیاریان پێشکه&zwnj;ش بکه&zwnj;ن و به جوانی ده&zwnj;ستبه&zwnj;رداریان ببن.';
$t[50] = 'ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر (صلی الله علیه و سلم) ئێمه ئه&zwnj;و هاوسه&zwnj;رانه&zwnj;تمان بۆ حه&zwnj;ڵاڵ کردویت که ماره&zwnj;ییت پێ به&zwnj;خشیون، ئه&zwnj;و ئافره&zwnj;تانه&zwnj;ش که که&zwnj;نیزه&zwnj;کی تۆن و له جه&zwnj;نگدا به&zwnj;دیل گیراون (له&zwnj;و سه&zwnj;رده&zwnj;مه&zwnj;دا که&zwnj;نیزه&zwnj;ك دیارده&zwnj;یه&zwnj;کی کۆمه&zwnj;ڵایه&zwnj;تی سروشتی بووه&zwnj;، ئاینی پیرۆزی ئیسلام به&zwnj;جۆره&zwnj;ها شێوه هه&zwnj;و&lrm;ڵی نه&zwnj;&zwnj;هێشتنی ئه دیارده&zwnj;یه داوه&zwnj;)، هه&zwnj;روه&zwnj;ها کچه مامه&zwnj;کانت، کچه پووره&zwnj;کانت، کچه خاڵه&zwnj;کانت، کچی ئه&zwnj;و پوورانه&zwnj;ت که کۆچیان کرد له ته&zwnj;ک تۆدا، وه هه&zwnj;ر ئافره&zwnj;تێکی بڕوادار که ئه&zwnj;گه&zwnj;ر ویستی خۆی پێشکه&zwnj;شی پێغه&zwnj;مبه&zwnj;ر بکات، وه پێغه&zwnj;مبه&zwnj;ریش ویستی ماره&zwnj;ی بکات، که ئه&zwnj;مه&zwnj;یان تایبه&zwnj;تی تۆیه (ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر صلی الله علیه و سلم) نه&zwnj;ك ئیمانداران، ئێمه ڕێ و شوێنمان بۆ ئه&zwnj;وان (واته ئیمانداران) ڕوونکردۆته&zwnj;وه به&zwnj;رانبه&zwnj;ر هاوسه&zwnj;ران و که&zwnj;نیزه&zwnj;کانیان (ئه&zwnj;م ڕوونکردنه&zwnj;وه&zwnj;یه بۆ ئه&zwnj;وه&zwnj;یه که&zwnj;:) هیچ دڵته&zwnj;نگی و غه&zwnj;مبارییه&zwnj;ك ڕووت تێ نه&zwnj;کات (وابزانی که تۆ له سنوور ده&zwnj;رچوویت به&zwnj;وه&zwnj;ی که له چوار هاوسه&zwnj;ر زیاترت هه&zwnj;یه&zwnj;) هه&zwnj;میشه&zwnj;و به&zwnj;رده&zwnj;وام خوا لێخۆشبوو و میهره&zwnj;بانه (بۆ ئیمانداران).';
$t[51] = 'هه&zwnj;رکامێکیان لابخه&zwnj;یت (له هاوسه&zwnj;رانت) یا هه&zwnj;رکامێکیان نزیک بکه&zwnj;یته&zwnj;وه، یاخود ئه&zwnj;وه&zwnj;یان که بته&zwnj;وێت و ئه&zwnj;وه&zwnj;یان که وازت لێ هێنابێ، پێی گونهبار نابیت (چونکه تۆ سته&zwnj;مکار نیت) ئا به&zwnj;م شێوه&zwnj;یه (خوای میهره&zwnj;بان ئه&zwnj;م بارودۆخه خێزانییه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر باس ده&zwnj;فه&zwnj;رموێت) تا (خێزانه&zwnj;کانت) دڵیان پێی ئارام بگرێت (چونکه ئه&zwnj;و هه&zwnj;موو ئه&zwnj;رکه&zwnj;ی که له&zwnj;سه&zwnj;ر شانی پێغه&zwnj;مبه&zwnj;ره&zwnj;، ئه&zwnj;وه ده&zwnj;خوازێت که جۆره سه&zwnj;ربه&zwnj;ستییه&zwnj;کی هه&zwnj;بێت)، هاوسه&zwnj;رانیش غه&zwnj;م و خه&zwnj;فه&zwnj;ت نه&zwnj;خۆن و (وابزانن که پێغه&zwnj;مبه&zwnj;ره لێیان زویره&zwnj;) وه هه&zwnj;مووان ڕازی بن به&zwnj;و بارودۆخه خێزانییه&zwnj;ی که بۆ پێغه&zwnj;مبه&zwnj;ر له&zwnj;بار و گونجاوه&zwnj;، خا خۆی ده&zwnj;زانێ که چی له دڵه&zwnj;کاندا حه&zwnj;شار داوه&zwnj;، بێگومان هه&zwnj;میشه و به&zwnj;رده&zwnj;وام خوا زانا و دانایه&zwnj;.';
$t[52] = 'له&zwnj;مه&zwnj;ودوا هێنانی ئافره&zwnj;تانی تر بۆ تۆ حه&zwnj;ڵاڵ نیه&zwnj;، یان واز هێنان له هه&zwnj;ندێکیان، تا هاوسه&zwnj;رانی تر بخه&zwnj;یته جێیان، هه&zwnj;رچه&zwnj;نده ئه&zwnj;و ئافره&zwnj;تانه&zwnj;ی ده&zwnj;ته&zwnj;وێ بیانخیته جێگه&zwnj;ی هاوسه&zwnj;رانت سه&zwnj;رسام بیت له ڕێك و پێکییان جگه که&zwnj;نیزه&zwnj;ك، هه&zwnj;میشه و به&zwnj;رده&zwnj;وام خوا چاودێری هه&zwnj;موو شتێك ده&zwnj;کات.';
$t[53] = 'ئه&zwnj;ی ئه&zwnj;و که&zwnj;سانه&zwnj;ی باوه&zwnj;ڕتان هێناوه مه&zwnj;چن بۆ سه&zwnj;ردانی ماڵه&zwnj;کان پێغه&zwnj;مبه&zwnj;ر مه&zwnj;گه&zwnj;ر مۆڵه&zwnj;تتان بدرێت و مۆڵه&zwnj;ت بخوازن، (ئه&zwnj;گه&zwnj;ر مۆڵه&zwnj;ت دران بۆ نانخواردنێك) چاوه&zwnj;ڕێی پێگه&zwnj;یشتنی مه&zwnj;که&zwnj;ن ئه&zwnj;گه&zwnj;ر پێنه&zwnj;گه&zwnj;یشتبوو (مه&zwnj;به&zwnj;ست ئه&zwnj;وه&zwnj;یه که زوو مه&zwnj;چن که هێشتا خواردنه&zwnj;که پێنه&zwnj;گه&zwnj;یشتووه&zwnj;)، به&zwnj;ڵام بانگ کران ئه&zwnj;وه بچنه ژووره&zwnj;وه&zwnj;، خۆتان به گفتوگۆوه سه&zwnj;رگه&zwnj;رم مه&zwnj;که&zwnj;ن، چونکه ئه&zwnj;و کاره&zwnj;تان پێغه&zwnj;مبه&zwnj;ر ئازار ده&zwnj;دات (هه&zwnj;رچه&zwnj;نده&zwnj;) شه&zwnj;رمتان لێده&zwnj;کات و (داوای چوونه ده&zwnj;ره&zwnj;وه&zwnj;تان لێناکات، بێگومان) خوا شه&zwnj;رم له (ڕاگه&zwnj;یاندنی) حه&zwnj;ق و دروستی ناکات خۆ ئه&zwnj;گه&zwnj;ر داوای شتێکی پێویستان (له خێزانه&zwnj;کانی پێغه&zwnj;مبه&zwnj;ر کرد) له پشتی په&zwnj;رده&zwnj;وه داوای لێبکه&zwnj;ن، ئا ئه&zwnj;و جۆره ڕه&zwnj;فتاره چاکتر و پاکتره بۆ دلی ئیوه و ئه&zwnj;وانێش، بێگومان هیچ کات بۆتان نیه ئازارو دڵته&zwnj;نگی بۆ پێغه&zwnj;مبه&zwnj;ری خوا پێش بێنن، هه&zwnj;رگز بۆتان نیه هاوسه&zwnj;رانی دوای خۆی به هیچ جۆرێك ماره بکه&zwnj;ن چونکه ئه&zwnj;و کاره&zwnj;تان لای خوا زۆر گه&zwnj;وره&zwnj;یه&zwnj;و (زۆر نابه&zwnj;جێیه&zwnj;، چونکه ئه&zwnj;وان دایکتانن، دایکیش له ڕۆڵه&zwnj;ی حه&zwnj;رامه&zwnj;).';
$t[54] = 'ئه&zwnj;گه&zwnj;ر شتێك ده&zwnj;رببڕن و ئاشکرای بکه&zwnj;ن یان بیشارنه&zwnj;وه&zwnj;و ده&zwnj;ری نه&zwnj;بڕن، ئه&zwnj;وا بێگومان خوا به هه&zwnj;موو شتێك ئاگاو زانایه (نهێنی و ئاشکرا لای ئه&zwnj;و زاته چوون یه&zwnj;که&zwnj;).';
$t[55] = '(ئافره&zwnj;تان) چاوپۆسییان لێده&zwnj;کرێت و قه&zwnj;یناکا باوانیان بیانبینن، یان کوڕه&zwnj;کانی خۆیان، یان براکانیان، یان برازاکانیان، یان خوشکه&zwnj;زاکانیان، یان ئه&zwnj;و ئافره&zwnj;تانه&zwnj;یان (که ئیشیان بۆ ده&zwnj;که&zwnj;ن له ماڵدا)، یان ئه&zwnj;وکه&zwnj;سانه&zwnj;ی که له ماڵه&zwnj;کانیاندا ده&zwnj;ژین، له خوا بترسن (ئه&zwnj;و سنوورانه&zwnj;ی که بۆی داناون مه&zwnj;یشکێنن)، بێگومان خوا شایه&zwnj;ته به&zwnj;سه&zwnj;ر هه&zwnj;موو شتێکه&zwnj;وه&zwnj;.';
$t[56] = 'به&zwnj;ڕاستی خواو فرێشته&zwnj;کانی (صلوات ده&zwnj;ده&zwnj;ن له&zwnj;سه&zwnj;ر پێغه&zwnj;مبه&zwnj;ر صلی الله علیه وسلم، ئه&zwnj;ی ئه&zwnj;وانه&zwnj;ی باوه&zwnj;ڕتان هێناوه ئێوه&zwnj;ش (صلوات)ی له&zwnj;سه&zwnj;ر بده&zwnj;ن و (به&zwnj;رده&zwnj;وام یادی خێری بکه&zwnj;ن).';
$t[57] = 'بێگومان ئه&zwnj;وانه&zwnj;ی ئازاری خواو پێغه&zwnj;مبه&zwnj;ه&zwnj;که&zwnj;ی ده&zwnj;ده&zwnj;ن (مه&zwnj;به&zwnj;&zwnj;ست ئازاردانی پێغه&zwnj;مبه&zwnj;ره صلی الله علیه وسلم) یان ئه&zwnj;وانه&zwnj;ی که هاوه&zwnj;ڵ بۆ خوا بڕیار ده&zwnj;ده&zwnj;ن و کوفر ده&zwnj;که&zwnj;ن و جوێنی پێده&zwnj;ده&zwnj;ن) خوا نه&zwnj;فرینی لێکردوون له دونیاو قیامه&zwnj;تدا سزایه&zwnj;کی زۆر خه&zwnj;جاڵه&zwnj;تئاوه&zwnj;رو سه&zwnj;رشۆڕکه&zwnj;ریشی بۆ ئاماده کردوون.';
$t[58] = 'ئه&zwnj;وانه&zwnj;ش که ئازاری پیاوانی ئیماندارو ئافره&zwnj;تانی ئیماندار ده&zwnj;دن بێئه&zwnj;وه&zwnj;ی تاوانێکیان ئه&zwnj;نجامدابێت، ئه&zwnj;وانه بوختان و گوناهو تاوانێکی ئاشکرا ده&zwnj;گرنه ئه&zwnj;ستۆ.';
$t[59] = 'ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر (صلی الله علیه و سلم) به هاوسه&zwnj;رانت و کچه&zwnj;کانت و ئافره&zwnj;تانی ئیماندار بڵێ: که به پۆشاکی باڵا پۆش خۆیان داپۆشن ئه&zwnj;وه له&zwnj;بارترین شته که پێی بناسرێن، تا ئازار نه&zwnj;درێن و (توشی گیروگرفت نه&zwnj;بن)، هه&zwnj;میشه&zwnj;و به&zwnj;رده&zwnj;وام خوا لێخۆشبوو میهره&zwnj;بانه (له که&zwnj;م و کووڕی ڕابوردووتان خۆش ده&zwnj;بێت).';
$t[60] = 'ئه&zwnj;گه&zwnj;ر دووڕووه&zwnj;کان و دڵ بیماره&zwnj;کان و پڕوپاگه&zwnj;نده&zwnj;چیه&zwnj;کان له مه&zwnj;دینه&zwnj;دا کۆڵ نه&zwnj;ده&zwnj;ن، مۆڵه&zwnj;ت و هانی تۆ ده&zwnj;ده&zwnj;ین که تۆڵه&zwnj;یان لێ بسێنیت، (له&zwnj;وه&zwnj;ودوا زۆربه&zwnj;یان ده&zwnj;ربه&zwnj;ده&zwnj;ر بکه&zwnj;یت)و که&zwnj;مێکیان نه&zwnj;بێت له شاردا نه&zwnj;میننه&zwnj;وه&zwnj;.';
$t[61] = '(ئه&zwnj;وانه) نه&zwnj;فرین لێکراون له هه&zwnj;رکوێ بژین، ده&zwnj;بیت ده&zwnj;ستگیر بکرێن و ده&zwnj;بێت بکوژرێن، (چونکه له خراپه&zwnj;و پیلان و تاوان و کوشتن و دزی و درۆزنی زیاتر هیچیان لێ نه&zwnj;وه&zwnj;شاوه&zwnj;ته&zwnj;وه&zwnj;).';
$t[62] = '(دژایه&zwnj;تی ئه&zwnj;و دووڕووانه&zwnj;) له&zwnj;به&zwnj;رنامه&zwnj;ی خوادایه&zwnj;و له ڕابوردووشدا ئه&zwnj;نجامدراوه&zwnj;، هه&zwnj;رگیز گۆڕانکاری نابینی له یاساو به&zwnj;رنامه&zwnj;کانی خوادا.';
$t[63] = 'خه&zwnj;لکی پرسیارت لێده&zwnj;که&zwnj;ن ده&zwnj;رباره&zwnj;ی ڕۆژی قیامه&zwnj;ت (که&zwnj;ی به&zwnj;رپا ده&zwnj;بێت) پێیان بڵێ: ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر&lrm;: (کاتی به&zwnj;رپابوونی) هه&zwnj;ر خوا خۆی ده&zwnj;زانێ، جا تۆ چووزانی له&zwnj;وانه&zwnj;یه ڕۆژی قیامه&zwnj;ت زۆر نزیکیش بێت.';
$t[64] = 'بێگومان ئه&zwnj;و خوایه نه&zwnj;فرینی کردوه له کافره&zwnj;کان (له ڕۆژه قیامه&zwnj;تیشدا) دۆزه&zwnj;خێکی زۆر گه&zwnj;رمی بۆ ئاماده کردوون.';
$t[65] = 'بۆ هه&zwnj;میشه تیایدا ده&zwnj;بێ بژین، له&zwnj;وێش نه پشتیوانێك شکده&zwnj;به&zwnj;ن فریایان بکه&zwnj;و&lrm;ێت نه که&zwnj;سێك ڕزگاریان بکات.';
$t[66] = 'ڕۆژێك دێت ڕوخساریان ئه&zwnj;م دیودیوی پێده&zwnj;کرێت له&zwnj;ناو ئاگردا (تا چاك ببرژێن و بسوتێن) (به&zwnj;ده&zwnj;م ئاهو ناڵه&zwnj;وه ده&zwnj;ڵێن): ئای خۆزگه فه&zwnj;رمانبه&zwnj;رداری خوا بووینایه&zwnj;، گوێڕایه&zwnj;ڵی پێغه&zwnj;مبه&zwnj;ر بووینایه&zwnj;.';
$t[67] = '(هه&zwnj;روه&zwnj;ها) ده&zwnj;ڵێن: ئێمه ملکه&zwnj;چ و فه&zwnj;رمانبه&zwnj;رداری ئاغاو گه&zwnj;وره&zwnj;کانمان بووین، هه&zwnj;ر ئه&zwnj;وان ڕێگه&zwnj;و ڕێبازی (ڕاستیان) له ئێمه شێوان و ون کرد (خوا ڕسوایان ده&zwnj;کات).';
$t[68] = 'په&zwnj;روه&zwnj;ردگارا&zwnj;: دووبه&zwnj;رابه&zwnj;رو دوو جه&zwnj;ندابه سزای ئه&zwnj;وان بده&zwnj;و نه&zwnj;فرینێکی زۆر بێئه&zwnj;ندازه&zwnj;یان لێ بکه&zwnj;.';
$t[69] = 'ئه&zwnj;ی ئه&zwnj;وکه&zwnj;سانه&zwnj;ی باوه&zwnj;ڕتان هێناوه&zwnj;: وه&zwnj;کو ئه&zwnj;وانه مه&zwnj;بن که ئازاری موسایان دا (تۆمه&zwnj;تیان بۆ دروستکرد، بوختانیان بۆ هه&zwnj;ڵبه&zwnj;ست) خوای گه&zwnj;وره&zwnj;ش (له&zwnj;و تۆمه&zwnj;ت و بوختانه&zwnj;یان) پاڕاستی و پاکیی ئاشکرا کرد وه ئه&zwnj;و لای خوا زۆر به&zwnj;ڕێزو ئازیزه&zwnj;.';
$t[70] = 'ئه&zwnj;ی ئه&zwnj;وکه&zwnj;سانه&zwnj;ی باوه&zwnj;ڕتان هێناوه له خوا بترسن و پارێزکاربن، هه&zwnj;میشه قسه و گوفتاری به&zwnj;جێ و به&zwnj;سوود بکه&zwnj;ن.';
$t[71] = '(ئه&zwnj;وسا ئیتر) کرده&zwnj;وه&zwnj;کانتان چاك و به&zwnj;جێ و په&zwnj;سه&zwnj;ند ده&zwnj;کات، له گوناهه&zwnj;کانیشتان خۆش ده&zwnj;بێت، جا ئه&zwnj;وه&zwnj;ی فه&zwnj;رمانبه&zwnj;رداری خواو پێغه&zwnj;مبه&zwnj;ره&zwnj;که&zwnj;ی بێت، ئه&zwnj;وه ئیتر سه&zwnj;رکه&zwnj;وتن و سه&zwnj;رفه&zwnj;راز&zwnj;یه&zwnj;کی بێ ءه&zwnj;ندازه&zwnj;و بێ سنووری به&zwnj;ده&zwnj;ست هێناوه&zwnj;.';
$t[72] = 'ئێمه ئه&zwnj;مانه&zwnj;تی (عه&zwnj;قیده و بیروباوڕ، ئه&zwnj;مانتی سه&zwnj;رپشکیمان) نیشانی ئاسمانه&zwnj;کان و زه&zwnj;وی و کێوه&zwnj;کان دا، (یاخود پێشنیارمان کرد که ئێوه هه&zwnj;ڵگری ئه&zwnj;م ئه&zwnj;مانه&zwnj;ته بن)، به&zwnj;ڵام هه&zwnj;مووان ده&zwnj;ستیان پێوه&zwnj;ناو ترسان لێی (هه&zwnj;ر پێیان چاك بوو ئاوا (مسیر بن، سه&zwnj;رپشك نه&zwnj;بن) که&zwnj;چی ئاده&zwnj;میزاد هه&zwnj;&lrm;ڵیگرت و گرتیه ئه&zwnj;ستۆی، به&zwnj;ڕاستی زۆر سته&zwnj;مکار و نه&zwnj;فام بوو (ئه&zwnj;گه&zwnj;ر زۆرینه ڕه&zwnj;چاو بکه&zwnj;ین).';
$t[73] = 'تا خوا سزای پیاوان و، وه ئافره&zwnj;تانی دووڕوو، وه هاوه&zwnj;ڵپه&zwnj;رستان له پیاوان و له ئافره&zwnj;تان بدات، هه&zwnj;روه&zwnj;ها ته&zwnj;وبه&zwnj;و په&zwnj;شیمانی له پیاوان و ئافره&zwnj;تانی ئیماندار وه&zwnj;رده&zwnj;گرێت، بێگومان خوای گه&zwnj;وره (بۆ ئه&zwnj;وان) زۆر لێخۆشبوو و به&zwnj;ڕه&zwnj;حم و میهره&zwnj;بانه&zwnj;.';
?>