<?php
$t = array();
$t[1] = 'سه&zwnj;رنجی سه&zwnj;ره&zwnj;تای سووره&zwnj;تی (البقرة) بده&zwnj;.';
$t[2] = 'سوێند به&zwnj;و قورئانه&zwnj;ی که هه&zwnj;موو فه&zwnj;رمانێکی له شوێنی خۆیایه&zwnj;تی...';
$t[3] = 'به&zwnj;ڕاستی تۆ ئه&zwnj;ی محمد (صلی الله علیه وسلم) له فرستاده و پێغه&zwnj;مبه&zwnj;رانیت...';
$t[4] = 'له&zwnj;سه&zwnj;ر ڕێگه&zwnj;و ڕێبازێکی ڕاست و دروستیت.';
$t[5] = 'ئه&zwnj;م قورئانه له&zwnj;لایه&zwnj;ن زاتێکی خاوه&zwnj;ن ده&zwnj;سه&zwnj;ڵات و میهره&zwnj;بانه&zwnj;وه دابه&zwnj;زیووه و ڕه&zwnj;وانه کراوه&zwnj;...';
$t[6] = 'بۆ ئه&zwnj;وه&zwnj;ی گه&zwnj;ل و هۆزێکی پێ بێدار بکه&zwnj;یته&zwnj;وه که باوو باپیرانیان بێدار نه&zwnj;کراونه&zwnj;ته&zwnj;وه&zwnj;و بێ ئاگایی و نه&zwnj;شاره&zwnj;زایی ماونه&zwnj;ته&zwnj;وه&zwnj;.';
$t[7] = 'سوێند به خوا به ڕاستی بڕیاری خوا هاته دی له&zwnj;سه&zwnj;ر زۆربه&zwnj;یان که دۆزه&zwnj;خ پڕ ده&zwnj;که&zwnj;ین لێیان، له&zwnj;به&zwnj;ر ئه&zwnj;وه ئیمان و باوه&zwnj;ڕ ناهێنن (به&zwnj;هۆی سه&zwnj;رسه&zwnj;ختی و گوێ نه&zwnj;گرتنیانه&zwnj;وه&zwnj;).';
$t[8] = 'به&zwnj;ڕاستی هه&zwnj;ر ده&zwnj;ڵێیت ئێمه کۆت و زنجیرمان کردۆته گه&zwnj;ردنیان و له ژێر چه&zwnj;ناگه&zwnj;یان توندکراوه و به ناچاری سه&zwnj;ریان به&zwnj;رز ڕاگیراوه و ئه&zwnj;م لاولایان پێناکرێت و به&zwnj;رپێی خۆیان نابینن.';
$t[9] = 'هه&zwnj;روه&zwnj;ها به&zwnj;ربه&zwnj;ستمان له به&zwnj;رده&zwnj;میانه&zwnj;وه و له پشتیانه&zwnj;وه داناوه&zwnj;، په&zwnj;رده&zwnj;شمان هێناوه به&zwnj;سه&zwnj;ر چاویاندا (مه&zwnj;به&zwnj;ست ئه&zwnj;و به&zwnj;ربه&zwnj;ستانه&zwnj;شه که له ده&zwnj;روونیاندا هه&zwnj;یه و نایه&zwnj;ڵێت ڕێگه&zwnj;ی ڕاست بدۆزنه&zwnj;وه&zwnj;) ئیتر ئه&zwnj;وانه حه&zwnj;ق و ڕاستی نابینن.';
$t[10] = 'ئه&zwnj;وانه چونیه که بۆیان، بێداریان بکه&zwnj;یته&zwnj;وه یان بێداریان نه&zwnj;که&zwnj;یته&zwnj;وه&zwnj;، هه&zwnj;ر باوه&zwnj;ڕ ناهێنن.';
$t[11] = 'بێگومان تۆ ته&zwnj;نها ده&zwnj;توانیت ئه&zwnj;وانه بێدار بکه&zwnj;یته&zwnj;وه که شوێنی قورئان و به&zwnj;رنامه&zwnj;ی خوا که&zwnj;وتوون، له په&zwnj;نهانیشدا له خوا ده&zwnj;ترسن، به&zwnj;و هۆیه&zwnj;وه مژده&zwnj;ی لێخۆشبوون و پاداشتی بایه&zwnj;خدار و به&zwnj;نرخ بده به&zwnj;و جۆره که&zwnj;سانه&zwnj;.';
$t[12] = 'بێگومان هه&zwnj;ر ئێمه مردووه&zwnj;کان زیندوو ده&zwnj;که&zwnj;ینه&zwnj;وه&zwnj;، هه&zwnj;رچی کردوویانه و ده&zwnj;یکه&zwnj;ن تۆماری ده&zwnj;&zwnj;که&zwnj;ین له&zwnj;گه&zwnj;ڵ ئاسه&zwnj;واره&zwnj;کانیشیاندا، هه&zwnj;موو شتێکمان له کتێبێکی ئاشكرادا (تۆمارگه&zwnj;ی تایبه&zwnj;تدا) به&zwnj;وردی تۆمار و ئامارکردووه&zwnj;.';
$t[13] = 'نموونه&zwnj;یان بۆ بهێنه&zwnj;ره&zwnj;وه به خه&zwnj;ڵكی شاری (انطاکیة) کاتێک ده&zwnj;سته&zwnj;یه&zwnj;ک پێغه&zwnj;مبه&zwnj;ریان بۆ هات.';
$t[14] = 'سه&zwnj;ره&zwnj;تا دووانمان بۆ ڕه&zwnj;وانه کردن، که&zwnj;چی بروایان پێنه&zwnj;کردن، ئێمه&zwnj;ش سێهه&zwnj;مێکمان بۆ پشتگیریان ڕه&zwnj;وانه کرد، هه&zwnj;موویان وتیان: ئێمه بێگومان بۆ لای ئێوه له&zwnj;لایه&zwnj;ن په&zwnj;روه&zwnj;ردگاره&zwnj;وه ڕه&zwnj;وانه کراوین.';
$t[15] = 'خه&zwnj;&zwnj;ڵکه&zwnj;که وتیان: ئێوه ته&zwnj;نها ئاده&zwnj;میزادێکن وه&zwnj;کو ئێمه&zwnj;، خوای میهره&zwnj;بانیش هیچ شتێکی ڕه&zwnj;وانه نه&zwnj;کردووه&zwnj;، ئێوه هه&zwnj;ر درۆ ده&zwnj;که&zwnj;ن.';
$t[16] = 'پێغه&zwnj;مبه&zwnj;ره&zwnj;کان وتیان: په&zwnj;روه&zwnj;ردگارمان خۆی چاک ده&zwnj;زانێت ئێمه به&zwnj;ڕاستی بۆ لای ئێوه ڕه&zwnj;وانه کراوین به پێغه&zwnj;مبه&zwnj;رایه&zwnj;تی.';
$t[17] = 'ته&zwnj;نها ئه&zwnj;وه&zwnj;شمان له&zwnj;سه&zwnj;ره که به ڕوون و ئاشکرا په&zwnj;یمانی خواتان پێ ڕابگه&zwnj;یه&zwnj;نین.';
$t[18] = 'خه&zwnj;ڵکه&zwnj;که وتیان: ئێمه به ڕاستی به&zwnj;هاتنتان تووشی نه&zwnj;گبه&zwnj;تی بووین، سوێند به خوا ئه&zwnj;گه&zwnj;ر واز نه&zwnj;هێنن و کۆڵ نه&zwnj;ده&zwnj;ن، به&zwnj;رد بارانتان ده&zwnj;که&zwnj;ین و له لایه&zwnj;ن ئێمه&zwnj;وه توشی ئازارێکی به ئێش ده&zwnj;بن.';
$t[19] = 'پێغه&zwnj;مبه&zwnj;ره&zwnj;کان وتیان: ئێوه نه&zwnj;گبه&zwnj;تی و شوومیتان له&zwnj;گه&zwnj;ڵ خۆتاندایه (به&zwnj;هۆی بێ باوه&zwnj;ڕیتانه&zwnj;وه&zwnj;)، ئایا ئه&zwnj;گه&zwnj;ر ئامۆژگاری و یاداوه&zwnj;ری بکرێن ئه&zwnj;وه ده&zwnj;بێته نه&zwnj;گبه&zwnj;تی و شووم بۆتان؟! نه&zwnj;خێر، وانییه&zwnj;، ئێوه هه&zwnj;ر خۆتان قه&zwnj;وم و هۆزێکی سه&zwnj;ره&zwnj;ڕۆن و له گوناهدا ڕۆچوون بۆیه شووم و نه&zwnj;گبه&zwnj;تی یه&zwnj;خه&zwnj;ی گرتوون.';
$t[20] = 'ئه&zwnj;وسا له&zwnj;وپه&zwnj;ڕی شاره&zwnj;وه پیاوێکی ئیماندار به&zwnj;په&zwnj;له&zwnj;، به گورج و گۆڵی هات و وتی: ئه&zwnj;ی قه&zwnj;وم و هۆزم ئه&zwnj;م پێغه&zwnj;مبه&zwnj;رانه ڕاست ده&zwnj;که&zwnj;ن، ئه&zwnj;م فرستادانه ڕاست ده&zwnj;ڵێن شوێنیان بکه&zwnj;ون، بڕوایان پێ بکه&zwnj;ن، ئیمانیان پێ بهێنن...';
$t[21] = 'شوێنی ئه&zwnj;و که&zwnj;سانه بکه&zwnj;ون که پاداشت و کرێتان لێ داوا ناکه&zwnj;ن، ئه&zwnj;وانه هیدایه&zwnj;ت دراون و له&zwnj;سه&zwnj;ر به&zwnj;رنامه&zwnj;یه&zwnj;کی ڕاست و دروستن.';
$t[22] = 'دیاره خه&zwnj;ڵکه&zwnj;که کردویانه به گاڵه&zwnj;، بۆیه پیاو چاکه&zwnj;که وتوویه&zwnj;تی: جا ئه&zwnj;وه بۆچی من ئه&zwnj;و زاته نه&zwnj;په&zwnj;رستم که به&zwnj;دی هێناوم، ئێوه&zwnj;ش خه&zwnj;ڵکینه هه&zwnj;ر بۆ لای ئه&zwnj;و ده&zwnj;برێنه&zwnj;وه&zwnj;.';
$t[23] = 'ئایا ڕه&zwnj;وایه له جیاتی خوا چه&zwnj;ند په&zwnj;رستراوێکی تر بۆ خۆم بڕیار بده&zwnj;م که هیچیان به&zwnj;ده&zwnj;ست نیه خۆ ئه&zwnj;گه&zwnj;ر خوای میهره&zwnj;بان بیه&zwnj;وێت تووشی ناخۆشیه&zwnj;که&zwnj;م بکات، تکاو پاڕانه&zwnj;وه&zwnj;ی ئه&zwnj;و په&zwnj;رستراوانه هیچ سوودێکم پێ ناگه&zwnj;یه&zwnj;نن و فریام ناکه&zwnj;ون و ڕزگارم ناکه&zwnj;ن.';
$t[24] = 'بێگومان من ئه&zwnj;گه&zwnj;ر شتی وا بکه&zwnj;م، ئه&zwnj;و کاته من له گومڕاییه&zwnj;کی ئاشكرا ده&zwnj;بم.';
$t[25] = 'به ڕاستی من بڕیارم داوه که ئیمان به په&zwnj;روه&zwnj;ردگارتان بهێنم، با چاک گوێتان لی بێت.';
$t[26] = '(دیاره&zwnj;، خوانه&zwnj;ناسان په&zwnj;لاماریان داوه و شه&zwnj;هیدیان کردووه&zwnj;) بۆیه پێی وتراوه&zwnj;: فه&zwnj;رموو بۆ ناو به&zwnj;هه&zwnj;شت، له&zwnj;وێشه&zwnj;وه هه&zwnj;ر دڵسۆزانه وتویه&zwnj;تی: خۆزگه قه&zwnj;ومه&zwnj;که&zwnj;م بیازنانیایه سه&zwnj;رئه&zwnj;نجامی ئیمان چۆنه و که&zwnj;وتومه&zwnj;ته ناو چ ناز و نیعمه&zwnj;تێکه&zwnj;وه&zwnj;.';
$t[27] = 'به&zwnj;هۆی ئه&zwnj;وه&zwnj;وه که په&zwnj;روه&zwnj;ردگارم لێم خۆشبووه و خستمیه ڕیزی ڕێزلێگیراوانه&zwnj;وه&zwnj;.';
$t[28] = 'خوا ده&zwnj;فه&zwnj;رمێت: وه&zwnj;نه&zwnj;بێت هیچ له&zwnj;شکرێکمان له ئاسمانه&zwnj;وه ناردبێته خواره&zwnj;وه بۆ سه&zwnj;ر هۆزه&zwnj;که&zwnj;ی دوای شه&zwnj;هیدکردنی، وه&zwnj;نه&zwnj;بێت هیچ له&zwnj;شکرێکمان ڕه&zwnj;وانه کردبێت بۆ له&zwnj;ناو&zwnj;بردنیان.';
$t[29] = 'به&zwnj;ڵکو ته&zwnj;نها یه&zwnj;ک ده&zwnj;نگی گه&zwnj;وره&zwnj;و شریخه&zwnj;یه&zwnj;کی به&zwnj;هێز نه&zwnj;بێت هیچی تر نه&zwnj;بوو، جا ئه&zwnj;وان ده&zwnj;ستبه جێ مردن خامۆش بوون.';
$t[30] = 'په&zwnj;شیمانی و داخ و ناڵه دابارێته سه&zwnj;ر ئه&zwnj;و به&zwnj;ندانه&zwnj;، هیچ پێغه&zwnj;مبه&zwnj;رێکیان بۆ نایه&zwnj;ت که ئه&zwnj;وان گاڵته&zwnj;ی پێنه&zwnj;که&zwnj;ن.';
$t[31] = 'ئائه ئه&zwnj;وه سه&zwnj;رنجیان نه&zwnj;داوه چه&zwnj;نده&zwnj;ها هۆزی پێش ئه&zwnj;مانمان له&zwnj;ناو بردووه&zwnj;، خۆ هه&zwnj;رگیز ناگه&zwnj;رێنه&zwnj;وه نه&zwnj;ویان و ناگه&zwnj;رێنه&zwnj;وه بۆ دنیا.';
$t[32] = 'که&zwnj;سیان نی یه له&zwnj;و له&zwnj;ناو براوانه که لای ئێمه به کۆمه&zwnj;ڵ ئاماده&zwnj;ی لێپرسینه&zwnj;وه نه&zwnj;کرێن.';
$t[33] = 'له به&zwnj;ڵگه&zwnj;و نیشانه&zwnj;ی ئاشکرای خوا بۆ ئه&zwnj;و خه&zwnj;ڵکه&zwnj;: زه&zwnj;وی مردووه که به&zwnj;هۆی ئاوی بارانه&zwnj;وه زیندووی ده&zwnj;که&zwnj;ینه&zwnj;وه&zwnj;، دانه&zwnj;وێڵه لێ په&zwnj;یدا ده&zwnj;کرین که لێی ده&zwnj;خۆن.';
$t[34] = 'هه&zwnj;روه&zwnj;ها جۆره&zwnj;ها باخاتی لێ به&zwnj;رهه&zwnj;م ده&zwnj;هێنین، له دارخورما و چه&zwnj;ند جۆری ترێ، هه&zwnj;روه&zwnj;ها چه&zwnj;نده&zwnj;ها کانیاویشمان تیایدا هه&zwnj;ڵقوڵاندووه&zwnj;....';
$t[35] = 'بۆ ئه&zwnj;وه&zwnj;ی له به&zwnj;رووبومه&zwnj;که&zwnj;ی بخۆن، له&zwnj;وه&zwnj;ش که به&zwnj;ده&zwnj;ستی خۆیان له&zwnj;و به&zwnj;رو بوومانه دروستی ده&zwnj;که&zwnj;ن (وه&zwnj;کو دۆشاو و سرکه و شه&zwnj;ربه&zwnj;ت و... هه&zwnj;روه&zwnj;ها جێگه&zwnj;ی تێڕامانه که&zwnj;: ئاده&zwnj;میزاد ورد بێته&zwnj;وه له دانه دانه&zwnj;ی ئه&zwnj;و به&zwnj;روبوومانه&zwnj;، چۆن پێچراونه&zwnj;ته&zwnj;وه&zwnj;، قه&zwnj;باره&zwnj;یان، ڕه&zwnj;نگیان، بۆنیان، تامیان.... هتد) ئایا له به&zwnj;رامبه&zwnj;ر ئه&zwnj;و هه&zwnj;موو نازو نیعمه&zwnj;تانه&zwnj;وه سوپاسگوزاری ناکه&zwnj;ن؟!';
$t[36] = 'پاکی و بێگه&zwnj;ردی بۆ ئه&zwnj;و زاته&zwnj;ی هه&zwnj;ر هه&zwnj;موو به&zwnj;دیهێنراوه&zwnj;کانی به&zwnj;جووت دروست کردووه له&zwnj;وه&zwnj;ی له زه&zwnj;ویدا ده&zwnj;ڕۆیت، له خۆیشیان، له&zwnj;و شتانه&zwnj;ش که نایزانن (له سه&zwnj;رده&zwnj;می عیلم و زانستیدا ڕوون بۆته&zwnj;وه&zwnj;: که هه&zwnj;موو به&zwnj;دیهێنراوان_مادی و مه&zwnj;عنه&zwnj;وی_جووتن).';
$t[37] = 'هه&zwnj;روه&zwnj;ها به&zwnj;ڵگه&zwnj;و نیشانه&zwnj;یه&zwnj;کی ئاشکرای تر بۆیان، شه&zwnj;وه&zwnj;، که پۆشاکی ڕووناکی ڕۆژی له&zwnj;به&zwnj;ر داده&zwnj;ماڵین، به&zwnj;و هۆیه&zwnj;وه دنیایان لێ تاریك ده&zwnj;بێت.';
$t[38] = 'خۆریش له گه&zwnj;ردش و سوڕانه&zwnj;وه&zwnj;ی خۆیدا به&zwnj;رده&zwnj;وامه و به&zwnj;ره&zwnj;و سره&zwnj;وت ده&zwnj;چێت، یاخود به ده&zwnj;وری چه&zwnj;قی خۆیدا ده&zwnj;سوڕێته&zwnj;وه&zwnj;، بێگومان ئه&zwnj;وه توانای زاتێکی باڵاده&zwnj;ست و زانا ده&zwnj;رده&zwnj;خات (که هه&zwnj;موو شتێکی به ڕێکوپێکی دروست کردووه&zwnj;).';
$t[39] = 'هه&zwnj;روه&zwnj;ها کات و شوێنی هه&zwnj;ڵهاتنی مانگیشمان دیاری کردووه (هه&zwnj;ر شه&zwnj;وێك له&zwnj;کاتێکی دیاریکراو و شوێنێکی دیاریکراودا هه&zwnj;ڵدێت، ڕاده&zwnj;ی ئه&zwnj;و ڕوناکیه&zwnj;ش که ده&zwnj;که&zwnj;وێته سه&zwnj;ری جیاوازه&zwnj;، هه&zwnj;تا (سه&zwnj;رئه&zwnj;نجام هه&zwnj;موو مانگێك له ڕواڵه&zwnj;تدا) وه&zwnj;ك چیلکه&zwnj;یه&zwnj;کی وشکی باریك ده&zwnj;نوێنێت.';
$t[40] = 'نه خۆر بۆی هه&zwnj;یه به مانگ بگات (له هێڵ و ڕێڕه&zwnj;وی خۆی ده&zwnj;ربچێت)، نه شه&zwnj;ویش پێش ڕۆژ ده&zwnj;که&zwnj;وێت، به&zwnj;ڵکو هه&zwnj;ریه&zwnj;که&zwnj;یان له خولگه&zwnj;ی تایبه&zwnj;تی خۆیاندا مه&zwnj;له ده&zwnj;که&zwnj;ن و به ئاسانی ده&zwnj;سوڕێنه&zwnj;وه&zwnj;.';
$t[41] = 'به&zwnj;ڵگه و نیشانه&zwnj;یه&zwnj;کی تر له هێزو ده&zwnj;سه&zwnj;ڵاتمان بۆیان، ئه&zwnj;وه&zwnj;یه که به&zwnj;ڕاستی ئێمه نه&zwnj;وه&zwnj;کانیانمان له که&zwnj;شتیه&zwnj;که&zwnj;دا هه&zwnj;ڵگرت (مه&zwnj;به&zwnj;ست که&zwnj;شتی-نوح- ه که به باپیره گه&zwnj;وره&zwnj;ی دووهه&zwnj;می ئاده&zwnj;میزاد ده&zwnj;درێته قه&zwnj;ڵه&zwnj;م).';
$t[42] = 'هه&zwnj;روه&zwnj;ها که&zwnj;شتی و هۆکاری تری هاتوچۆی له وێنه&zwnj;ی ئه&zwnj;و بۆ سازاندوون، (که هۆکاره&zwnj;کانی گواستنه&zwnj;ی ئاسمانی و زه&zwnj;مینی و ده&zwnj;ریایی ده&zwnj;گرێته&zwnj;وه&zwnj;، چونکه هه&zwnj;ر خوا به&zwnj;دیهێنه&zwnj;ری ئاده&zwnj;میزاد و ماده خاو و سوته&zwnj;مه&zwnj;نی و... هتد';
$t[43] = 'خۆ ئه&zwnj;گه&zwnj;ر بمانه&zwnj;وێت نوقمیان ده&zwnj;که&zwnj;ین، ئه&zwnj;و کاته که&zwnj;س نی یه بێت به هاواریانه&zwnj;وه و ڕزگاریش ناکرێن له مردن.....';
$t[44] = 'مه&zwnj;گه&zwnj;ر ڕه&zwnj;حمه&zwnj;ت و میهره&zwnj;بانی ئێمه مۆڵه&zwnj;تیان بدات تا ماوه&zwnj;یه&zwnj;کی دیاریکراو به&zwnj;هره له ژیان وه&zwnj;ربگرن.';
$t[45] = 'کاتێکیش به&zwnj;بێ باوه&zwnj;ڕان بوترێت: خۆتان له&zwnj;و سزایه بپارێزن که به&zwnj;سه&zwnj;ر گه&zwnj;لانی پێشوودا هات، هه&zwnj;روه&zwnj;ها له سزای داهاتووتان، بۆ ئه&zwnj;وه&zwnj;ی ڕه&zwnj;حمتان پێ بکرێت، (که&zwnj;چی هه&zwnj;ر باکیان نیه&zwnj;).';
$t[46] = 'هیچ ئایه&zwnj;ت و به&zwnj;ڵگه و نیشانه&zwnj;یه&zwnj;کیان له ئایه&zwnj;ت و به&zwnj;ڵگه&zwnj;کانی په&zwnj;روه&zwnj;ردگاریان بۆ نایه&zwnj;ت که پشتی تێ نه&zwnj;که&zwnj;ن و ڕووی لێ وه&zwnj;رنه&zwnj;گێڕن.';
$t[47] = 'کاتێکیش پێیان بوترێت ببه&zwnj;خشن له&zwnj;وه&zwnj;ی که خوا پێی به&zwnj;خشیون، ئه&zwnj;وانه&zwnj;ی بێ باوه&zwnj;ڕ بوون به&zwnj;وانه ده&zwnj;ڵێن که باوه&zwnj;ڕیان هێناوه&zwnj;: ئایا ئێمه خواردن بده&zwnj;ین به که&zwnj;سێك ئه&zwnj;گه&zwnj;ر خوا بیوستایه خۆراکی پێ ده&zwnj;به&zwnj;خشی؟ ئێوه هه&zwnj;ر له گومڕایی و سه&zwnj;رلێشێواوییه&zwnj;کی ئاشکرادا گیرتان خواردووه&zwnj;!!';
$t[48] = 'بێ باوه&zwnj;ڕان: به گومانه&zwnj;وه ڕوو به&zwnj;ئیمانداران ده&zwnj;ڵێن: باشه&zwnj;، ئه&zwnj;گه&zwnj;ر قیامه&zwnj;ت و لێ پرسینه&zwnj;وه ڕاسته&zwnj;، که&zwnj;ی ئه&zwnj;و کاته دێت ئه&zwnj;گه&zwnj;ر ڕاست ده&zwnj;که&zwnj;ن؟!';
$t[49] = 'ئه&zwnj;وانه ته&zwnj;نها چاوه&zwnj;ڕێی یه&zwnj;ك شریخه و ده&zwnj;نگێکی زۆر گه&zwnj;وره&zwnj;ی سامناك نه&zwnj;بێت چاوه&zwnj;ڕێی هیچی تر ناکه&zwnj;ن، که هه&zwnj;موویان ده&zwnj;پێچێته&zwnj;وه (کۆتایی به ژیانیان ده&zwnj;هێنێت) له&zwnj;کاتێکدا که سه&zwnj;رگه&zwnj;رمی کاروباری دنیای خۆیانن، سه&zwnj;رگه&zwnj;رمی کێشه و به&zwnj;ربه&zwnj;ره&zwnj;کانێی ناحه&zwnj;قن.';
$t[50] = 'ئه&zwnj;وسا ئیتر نه ده&zwnj;توانن ڕاسپارده&zwnj;یه&zwnj;ك بگه&zwnj;یه&zwnj;نن، نه ده&zwnj;توانن بگه&zwnj;ڕێنه&zwnj;وه بۆ ناو ماڵ و منداڵیان (چونکه هه&zwnj;موویان له&zwnj;ناوچوون، هه&zwnj;موویان خامۆش بوون).';
$t[51] = 'پاشان هه&zwnj;ر که فوو ده&zwnj;کرێت به&zwnj;- صور- دا، ده&zwnj;ستبه&zwnj;جێ خێرا هه&zwnj;موویان له گۆڕه&zwnj;کانیان له توێی خاکه&zwnj;وه به&zwnj;په&zwnj;له دێنه ده&zwnj;ره&zwnj;وه بۆ لای په&zwnj;روه&zwnj;ردگاریان (که&zwnj;س ناتوانێت سه&zwnj;رپێچی بکات، یان خۆی بدزێته&zwnj;وه&zwnj;).';
$t[52] = 'به سه&zwnj;رسامیه&zwnj;کی زۆره&zwnj;وه ده&zwnj;ڵێن: ئه&zwnj;ی هاوار ئه&zwnj;وه کێ ئێمه&zwnj;ی زیندوو کرده&zwnj;وه&zwnj;؟ کێ ئێمه&zwnj;ی له شوێنی ڕاکشانمان له گۆڕه&zwnj;کانمان ده&zwnj;رهێنا؟ (ئه&zwnj;وسا ئیمانداران، یان فریشته&zwnj;کان یان خوای گه&zwnj;وره خۆی ده&zwnj;فه&zwnj;رموێت): ئائه&zwnj;مه به&zwnj;ڵێنی خوای میهره&zwnj;بانه و پێغه&zwnj;مبه&zwnj;رانیش له هه&zwnj;وڵدانی ئه&zwnj;و ڕۆژه ڕاستیان کردووه&zwnj;.';
$t[53] = 'له&zwnj;وماوه&zwnj;یه&zwnj;دا هیچ ڕوونادات جگه&zwnj;له ده&zwnj;نگێکی گه&zwnj;وره&zwnj;ی کوتوپڕ نه&zwnj;بێت، جاده&zwnj;ستبه&zwnj;جێ هه&zwnj;موویان لای ئێمه ئاماده&zwnj;کراون.';
$t[54] = 'ئینجا ئه&zwnj;م جاره ده&zwnj;درێت: ئه&zwnj;مڕۆ هیچ که&zwnj;س سته&zwnj;می لێ ناکرێت به&zwnj;هیچ شێوه&zwnj;یه&zwnj;ك، خه&zwnj;ڵکینه دڵنیابن که&zwnj;: ته&zwnj;نها پاداشتی کارێك ده&zwnj;درێنه&zwnj;وه که ده&zwnj;تانکرد.';
$t[55] = 'به&zwnj;ڕاستی ئه&zwnj;مڕۆ به&zwnj;هه&zwnj;شتیه&zwnj;کان له&zwnj;ناو خۆشی و شادی و کامه&zwnj;رانیه&zwnj;کی بێ وێنه&zwnj;دا سه&zwnj;رگه&zwnj;رمن.';
$t[56] = 'خۆیان و هاوسه&zwnj;رانیان له ژێر سێبه&zwnj;ری دره&zwnj;ختی جوان و چڕو پڕدا له&zwnj;سه&zwnj;ر کورسی و قه&zwnj;نه&zwnj;فه ڕازاوه&zwnj;کان شانیان داداوه&zwnj;.';
$t[57] = 'میوه&zwnj;هاتی زۆرو جۆراو جۆریان له&zwnj;وێدا پێشکه&zwnj;ش ده&zwnj;کرێت، هه&zwnj;روه&zwnj;ها هه&zwnj;رچی داوای بکه&zwnj;ن و حه&zwnj;زی لێ بکه&zwnj;ن ده&zwnj;ستبه&zwnj;جێ ئاماده&zwnj;یه (له خواردن و خواردنه&zwnj;وه&zwnj;، له جل و به&zwnj;رگی جوان، له جێگه&zwnj;و ڕێگه&zwnj;ی خۆش، له هۆکاری هاتوچۆ، له هه&zwnj;زاران نازو نیعمه&zwnj;تی بێشومار).';
$t[58] = 'هاوڕێ له&zwnj;گه&zwnj;ڵ سڵاو ڕێزدا له&zwnj;لایه&zwnj;ن په&zwnj;روه&zwnj;ردگاری میهره&zwnj;بان و دلۆڤانه&zwnj;وه&zwnj;.';
$t[59] = 'له&zwnj;ولاشه&zwnj;وه یاخیه&zwnj;کان پێیان ده&zwnj;وترێت: ئاده&zwnj;ی تاوانبارو گوناهکاران، جیاببنه&zwnj;وه&zwnj;.';
$t[60] = 'ئه&zwnj;وسا خوای گه&zwnj;وره گله&zwnj;یی له یاخیه&zwnj;کانی نه&zwnj;وه&zwnj;ی ئاده&zwnj;م ده&zwnj;کات و ده&zwnj;فه&zwnj;رموێت: ئه&zwnj;ی نه&zwnj;وه&zwnj;ی ئاده&zwnj;م، مه&zwnj;گه&zwnj;ر من پێم ڕانه&zwnj;گه&zwnj;یاندن که شه&zwnj;یتان نه&zwnj;په&zwnj;رستن، چونکه ئه&zwnj;و به&zwnj;ڕاستی دوژمنی ئاشکراتانه&zwnj;.';
$t[61] = 'به&zwnj;ڵکو ته&zwnj;نها من بپه&zwnj;رستن که هه&zwnj;ر ئه&zwnj;مه&zwnj;یه ڕێگه&zwnj;و ڕێبازی ڕاست و دروست.';
$t[62] = 'بێگومان شه&zwnj;یتان خه&zwnj;ڵکێکی زۆری له ئێوه گومڕا کرد، جا ئایا بۆچی ژیر نه&zwnj;بوون.';
$t[63] = 'ئه&zwnj;مه ئه&zwnj;و دۆزه&zwnj;خه&zwnj;یه که جاران پێی ده&zwnj;ترسێنران.';
$t[64] = 'ده&zwnj;بێت ئه&zwnj;مڕۆ بگه&zwnj;یه&zwnj;نرێنه ناوی له سه&zwnj;رئه&zwnj;نجامی بێ باوه&zwnj;ڕیتاندا و ببرژێن تیایدا به&zwnj;هۆی ئه&zwnj;وه&zwnj;ی که باوه&zwnj;ڕتان پێی نه&zwnj;بوو.';
$t[65] = 'ئه&zwnj;مڕۆ مۆر ده&zwnj;نێن به ده&zwnj;میاندا و قسه&zwnj;یان بۆ ناکرێت، به&zwnj;ڵکو ده&zwnj;ستیان قسه&zwnj;مان بۆ ده&zwnj;کات، قاچیشیان شایه&zwnj;تی ده&zwnj;دات به&zwnj;و کارو کرده&zwnj;وانه که ئه&zwnj;نجامیان ده&zwnj;دا (ده&zwnj;رباره&zwnj;ی ئه&zwnj;و تاوانانه&zwnj;ی که ئه&zwnj;نجامیان داوه&zwnj;، کاتێك شریتی کاسێت و ڤیدیۆ و سی دی و دیسك قسه&zwnj;مان بۆ ده&zwnj;که&zwnj;ن باوه&zwnj;ڕ بوون به&zwnj;قسه&zwnj;کردنی ده&zwnj;ست و قاچ و... هتد، ئاسانه&zwnj;).';
$t[66] = 'خوا نه&zwnj;ناسان بۆ بیرێك ناکه&zwnj;نه&zwnj;وه خۆ ئه&zwnj;گه&zwnj;ر بمانه&zwnj;وێت چاویان کوێر ده&zwnj;که&zwnj;ین و شوێنه&zwnj;واری نایه&zwnj;ڵین، ئه&zwnj;و کاته ئه&zwnj;گه&zwnj;ر بیانه&zwnj;وێت به ڕێگا&zwnj;که&zwnj;دا بڕۆن، چۆن ده&zwnj;توانن ڕێگه&zwnj;که ببینن و پێشبڕکێ بکه&zwnj;ن؟!';
$t[67] = 'یاخود ئه&zwnj;گه&zwnj;ر بمانه&zwnj;وێت هه&zwnj;ر له جێگه&zwnj;ی خۆیان (له&zwnj;و جێگه&zwnj;دا که تاوانی تێدا ئه&zwnj;نجام ده&zwnj;ده&zwnj;ن) گۆج و ئیفلیجیان ده&zwnj;که&zwnj;ین، یان ڕووخساریان ده&zwnj;خه&zwnj;ینه شێوه&zwnj;ی ئاژه&zwnj;ڵ و جانه&zwnj;وه&zwnj;ره&zwnj;وه&zwnj;، ئه&zwnj;وسا ئیتر ناتوانن نه به&zwnj;ره&zwnj;و پێش بچن، نه بگه&zwnj;ڕێنه&zwnj;وه دواوه&zwnj;.';
$t[68] = 'خۆ ئاشکرایه ئه&zwnj;وه&zwnj;ی ته&zwnj;مه&zwnj;نی درێژی پێ ببه&zwnj;خشین. جوانی و ڕواڵه&zwnj;تی بۆ ناهێڵین، جا ئایا بۆ ژیریان ناخه&zwnj;نه کار؟!';
$t[69] = '(دیاره خوا نه&zwnj;ناسان ئایاته&zwnj;کانی قورئانیان به شیعر داوه&zwnj;ته قه&zwnj;ڵه&zwnj;م و پێغه&zwnj;مبه&zwnj;ریش صلی الله علیه وسلم به شاعیر، بۆیه ده&zwnj;فه&zwnj;رموێت): وه&zwnj;نه&zwnj;بێت شیعرمان فێر کردبێت، شیعر وتنیش له&zwnj;و قه&zwnj;ده&zwnj;غه&zwnj;یه&zwnj;، ئه&zwnj;وه&zwnj;ی که فێرمان کردووه ته&zwnj;نها یادخه&zwnj;ره&zwnj;وه&zwnj;و قورئانێکی ئاشکراو ڕوونه&zwnj;.';
$t[70] = 'ئه&zwnj;م قورئانه بۆ ئه&zwnj;وه&zwnj;یه ئه&zwnj;و که&zwnj;سانه بێداربکاته&zwnj;وه که زیندوون (دڵ و عه&zwnj;قڵیان زیندوو هۆشیاره&zwnj;) تا بڕیاری خوا له&zwnj;سه&zwnj;ر کافران جێبه&zwnj;جێ بکرێت (بڕیاری سه&zwnj;رگه&zwnj;ردانی دنیا و سزای قیامه&zwnj;ت، ئه&zwnj;گه&zwnj;ر باوه&zwnj;ڕی پێ نه&zwnj;که&zwnj;ن).';
$t[71] = 'باشه ئه&zwnj;و خه&zwnj;ڵکه سه&zwnj;رنجیان نه&zwnj;داوه که به&zwnj;ڕاستی به ده&zwnj;سه&zwnj;ڵاتی خۆمان ماڵاتمان بۆ درووست کردوون و ئه&zwnj;وان بوون به&zwnj;خاوه&zwnj;نی؟';
$t[72] = 'ئینجا ئه&zwnj;و ماڵاته&zwnj;مان ڕام هێناوه بۆیان، هه&zwnj;ندێکی بۆ سواری به&zwnj;کار ده&zwnj;هێنن، له گۆشتی هه&zwnj;ندێکیشیان ده&zwnj;خۆن.';
$t[73] = 'هه&zwnj;روه&zwnj;ها سوود و قازانجی زۆریان لێ ده&zwnj;بینن (جگه له&zwnj;وانه&zwnj;ی باسکرا، بۆ زه&zwnj;وی کێڵان و ئاو هێنان،...) شیره&zwnj;که&zwnj;ی ده&zwnj;خۆن (جۆره&zwnj;ها خۆراکی تری لێ دروست ده&zwnj;که&zwnj;ن) جا ئایا ئه&zwnj;وه بۆ سوپاسی خوای به&zwnj;خشنده ناکه&zwnj;ن له&zwnj;سه&zwnj;ری؟!';
$t[74] = 'سه&zwnj;ره&zwnj;ڕای ئه&zwnj;م هه&zwnj;موو نازو نیعمه&zwnj;ته ئاشکرایه&zwnj;، نه&zwnj;فامان: له جیاتی خواپه&zwnj;رستیی، بت و شتی تر ده&zwnj;په&zwnj;رستن و بۆ ئه&zwnj;وه&zwnj;ی له ته&zwnj;نگانه&zwnj;دا کۆمه&zwnj;کی بکرێنو سه&zwnj;ربخرێن.';
$t[75] = 'به&zwnj;ڵام ناتوانن سه&zwnj;ریان بخه&zwnj;ن به&zwnj;ڵکو هه&zwnj;ر ئه&zwnj;مان بوونه&zwnj;ته سه&zwnj;رباز و پاسه&zwnj;وان بۆ بته&zwnj;کان و هه&zwnj;میشه له خزمه&zwnj;تیاندا ئاماده کراون.';
$t[76] = 'ئه&zwnj;ی محمد صلی الله علیه وسلم قسه و گوفتاری ئه&zwnj;وانه غه&zwnj;مبارت نه&zwnj;کات، چونکه به&zwnj;ڕاستی ئێمه ئاگاداری ئه&zwnj;وه&zwnj;ین که ده&zwnj;یشارنه&zwnj;وه و ئه&zwnj;وه&zwnj;ش ئاشکرای ده&zwnj;که&zwnj;ن.';
$t[77] = 'ئایا ئاده&zwnj;میزاد سه&zwnj;رنجی نه&zwnj;داوه که&zwnj;: ئێمه بێگومان دروستمان کردووه له نوتفه&zwnj;یه&zwnj;ك، که&zwnj;چی خێرا بووه&zwnj;ته دوژمنێکی ئاشکراو دژایه&zwnj;تیمان ده&zwnj;کات؟!';
$t[78] = 'نموونه&zwnj;ی لاوازیی وبێ ده&zwnj;سه&zwnj;ڵاتی بۆ ئێمه هێناوه&zwnj;ته&zwnj;وه و به&zwnj;دیهاتنی خۆی له&zwnj;بیر کردووه&zwnj;، ده&zwnj;یوت: کێ ئه&zwnj;م ئێسکانه زیندوو ده&zwnj;کاته&zwnj;وه له کاتێکدا که ڕزیوو و پروکاون؟!';
$t[79] = 'پێیان بڵێ: ئه&zwnj;و زاته&zwnj;ی که یه&zwnj;که&zwnj;مجار دروستی کرد، هه&zwnj;ر ئه&zwnj;ویش زیندووی ده&zwnj;کاته&zwnj;وه&zwnj;، ئه&zwnj;و به&zwnj;هه&zwnj;موو دروستکراوێک زاناو ئاگایه (دروستکردن و دروستکردنه&zwnj;وه&zwnj;ی هه&zwnj;موو شتێکی لا ئاسانه&zwnj;)....';
$t[80] = 'ئه&zwnj;و زاته&zwnj;ی که له دره&zwnj;ختی سه&zwnj;وز ئاگرتان بۆ به&zwnj;دی ده&zwnj;هێنێت، ئیتر ئێوه دایده&zwnj;گرسێنن.';
$t[81] = 'ئایا ئه&zwnj;و زاته&zwnj;ی که ئاسمانه&zwnj;کان و زه&zwnj;وی دروست کردووه ناتوانێت دووباره وه&zwnj;ک خۆیان دروستیان بکاته&zwnj;وه&zwnj;؟! به&zwnj;ڵێ (زۆر چاک ده&zwnj;توانێت چونکه ئه&zwnj;و زاته له دروستکردن و داهێناندا به توانا و زانایه&zwnj;.';
$t[82] = 'بێگومان هه&zwnj;ر کاتێک فه&zwnj;رمانی خوا ده&zwnj;رچوو، ویستی شتێک جێبه&zwnj;جێ بکات و بیهێنێته دی، پێی ده&zwnj;ڵێت ببه&zwnj;، ئه&zwnj;ویش ده&zwnj;ستبه&zwnj;جێ و بێ دواکه&zwnj;وتن پێش دێت و ئه&zwnj;نجام ده&zwnj;درێت و دروست ده&zwnj;بێت.';
$t[83] = 'پاکی و بێگه&zwnj;ردی بۆ ئه&zwnj;و زاته&zwnj;ی جڵه&zwnj;وی هه&zwnj;موو شتێکی به&zwnj;ده&zwnj;سته&zwnj;، هه&zwnj;ر بۆ لای ئه&zwnj;ویش ده&zwnj;برێنه&zwnj;وه&zwnj;.';
?>