$t[60] = 'به&zwnj;ڕاستی ئا ئه&zwnj;مه&zwnj;یه سه&zwnj;رکه&zwnj;وتن و سه&zwnj;رفرازی و به&zwnj;خته&zwnj;وه&zwnj;ری گه&zwnj;وره و بێ سنوور.';
$t[61] = 'ده&zwnj;با ئا بۆ ئه&zwnj;م به&zwnj;هشته&zwnj;، بۆ ئه&zwnj;م کامه&zwnj;رانیه&zwnj;، بۆ ئه&zwnj;م نازو نیعمه&zwnj;ته بێ سنووره&zwnj;، ئه&zwnj;وانه&zwnj;ی که کارو کۆشش ده&zwnj;که&zwnj;ن، با بۆ ئه&zwnj;م ده&zwnj;سکه&zwnj;وتانه بیکه&zwnj;ن.';
$t[62] = 'باشه&zwnj;، ئایا ئه&zwnj;و به&zwnj;هه&zwnj;شت و نازو نیعمه&zwnj;ته چاکه&zwnj;؟ یان دره&zwnj;ختی ژه&zwnj;قنه بووت.';
$t[63] = 'ئێمه کردوومانه به&zwnj;هۆی ناخۆشی و ئازار بۆ سته&zwnj;مکاران (ئه&zwnj;وانه&zwnj;ی له دنیادا گاڵته&zwnj;یان ده&zwnj;هات و ده&zwnj;یانوت چۆن له&zwnj;ناو ئاگردا دره&zwnj;خت ده&zwnj;ڕوێت؟!)';
$t[64] = 'بێگومان ئه&zwnj;وه دره&zwnj;ختێکه له ناخی دۆزه&zwnj;خدایه و له&zwnj;وێوه (لق و پۆپه&zwnj;کانی) به&zwnj;هه&zwnj;موو به&zwnj;شه&zwnj;کانی دۆزه&zwnj;خدا بڵاوکردۆته&zwnj;وه&zwnj;.';
$t[65] = 'به&zwnj;روبوومه&zwnj;که&zwnj;ی ده&zwnj;ڵێی سه&zwnj;لکه&zwnj;سه&zwnj;ری شه&zwnj;یتانه&zwnj;کانه (له ناشرینی و بێ له&zwnj;زه&zwnj;تیدا).';
$t[66] = 'دۆزه&zwnj;خیه&zwnj;کان به ناچاری لێی ده&zwnj;خۆن، سکیانی لێ پڕ ده&zwnj;که&zwnj;ن.';
$t[67] = 'له&zwnj;وه&zwnj;ودوا شله&zwnj;مه&zwnj;نیه&zwnj;کی زۆر گه&zwnj;رم ده&zwnj;که&zwnj;ن به&zwnj;سه&zwnj;ریدا (به&zwnj;زۆر ده&zwnj;رخواردیان ده&zwnj;درێت).';
$t[68] = 'پاسان (دوای ئه&zwnj;وه&zwnj;ی که تێریان خواردو تێریان خوارده&zwnj;وه&zwnj;!! به ده&zwnj;م شه&zwnj;ق و تێهه&zwnj;ڵدان و سه&zwnj;رزه&zwnj;نیشتی فریشته&zwnj;کانی دۆزه&zwnj;خه&zwnj;وه&zwnj;) به&zwnj;زۆر ده&zwnj;گێڕدێنره&zwnj;وه بۆ ناو دۆزه&zwnj;خ، (دیاره براون بۆ شوێنی تایبه&zwnj;تی خواردن و خواردنه&zwnj;وه&zwnj;!!)';
$t[69] = 'ئه&zwnj;وانه کاتی خۆی باوو باپیرانی خۆیانیان بینی به&zwnj;گومڕایی (بیریان نه&zwnj;کرده&zwnj;وه و کوێرانه شوێنیان که&zwnj;وتن).';
$t[70] = '(ئیتر به&zwnj;بێ لێکدانه&zwnj;وه&zwnj;) ئه&zwnj;وانه به&zwnj;شوێنیاندا به&zwnj;تاڵۆکه و هه&zwnj;ڵه&zwnj;داوان چوون (باوه&zwnj;ڕیان به پێغه&zwnj;مبه&zwnj;ران و ئیمانداران نه&zwnj;کرد).';
$t[71] = 'سوێند به&zwnj;خوا بێگومان زۆربه&zwnj;ی پێشینانیان هه&zwnj;ر گومڕا بوون (چونکه دڵڕه&zwnj;ق بوون و ئاماده&zwnj;ی بیستنی ڕاستی نه&zwnj;بوون).';
$t[72] = 'هه&zwnj;رچه&zwnj;نده ئێمه به&zwnj;ڕاستی، پێغه&zwnj;مبه&zwnj;رانی بێدارکه&zwnj;ره&zwnj;وه&zwnj;مان بۆ ڕه&zwnj;وانه کردبوون (به&zwnj;ڵام زۆربه&zwnj;یان گاڵته&zwnj;یان پێ ده&zwnj;هات).';
$t[73] = 'جا ئیتر ته&zwnj;ماشابکه سه&zwnj;رنج بده&zwnj;، سه&zwnj;رئه&zwnj;نجامی بێدارکراوه&zwnj;کان چی بوو (چۆن به دۆزه&zwnj;خ پاداشت درانه&zwnj;وه&zwnj;!)';
$t[74] = 'جگه له به&zwnj;نده دڵسۆزو هه&zwnj;ڵبژارده&zwnj;کانی خوا (که ئه&zwnj;وان له دۆزه&zwnj;خ دوورن و به به&zwnj;هه&zwnj;شت شاد بوون).';
$t[75] = 'بێگومان نوح هانا و هاواری بۆ هێناین، ئێمه&zwnj;یش چاکترین فریاد ڕه&zwnj;سێک بووین که به&zwnj;هانا و هاواریه&zwnj;وه چووین.';
$t[76] = 'خۆی و که&zwnj;سوکاری باوه&zwnj;ڕداریمان له ته&zwnj;نگانه گه&zwnj;وره&zwnj;که ڕزگار کرد...';
$t[77] = 'جا ته&zwnj;نها هه&zwnj;ر ئه&zwnj;وه&zwnj;ی ئه&zwnj;ویشمان هێشته&zwnj;وه (که ببنه هۆی ئاوه&zwnj;دانکردنه&zwnj;وه&zwnj;ی زه&zwnj;وی سه&zwnj;ر له&zwnj;نوێ).';
$t[78] = 'هه&zwnj;روه&zwnj;ها به&zwnj;سه&zwnj;رهاتی ئه&zwnj;ومان هێشته&zwnj;وه بۆ نه&zwnj;وه&zwnj;کانی داهاتوو (تا په&zwnj;ندی لێوه&zwnj;ربگرن).';
$t[79] = 'سڵاو له&zwnj;سه&zwnj;ر نوح له لایه&zwnj;ن سه&zwnj;رجه&zwnj;م ئیماندارانه&zwnj;وه له هه&zwnj;موو کات و شوێنێکدا.';
$t[80] = 'به&zwnj;ڕاستی ئێمه ئا به&zwnj;و شێوه&zwnj;یه پاداشتی چاکه&zwnj;کاران ده&zwnj;ده&zwnj;ینه&zwnj;وه (یادیان ده&zwnj;هێڵینه&zwnj;وه&zwnj;).';
$t[81] = 'به&zwnj;ڕاستی ئه&zwnj;و له&zwnj;به&zwnj;نده ئیمانداره&zwnj;کانی ئێمه&zwnj;یه&zwnj;.';
$t[82] = 'له&zwnj;وه&zwnj;ودوا ئه&zwnj;وانی ترمان نوقمی زریان کرد.';
$t[83] = 'بێگومان له شوێنکه&zwnj;وته و په&zwnj;یڕه&zwnj;وانی نوح - ئیبراهیم - ه (سه&zwnj;لامی خوایان لێ بێت).';
$t[84] = 'کاتێک ڕووی کرده په&zwnj;روه&zwnj;ردگاری، به پیر بانگه&zwnj;وازیه&zwnj;وه چوو، به&zwnj;دڵێکی پڕ له ئیمان و یه&zwnj;ک خواناسیه&zwnj;وه&zwnj;.';
$t[85] = 'ڕۆژێک به باوکی و قه&zwnj;ومی و هۆزه&zwnj;که&zwnj;ی وت: باشه&zwnj;، ئه&zwnj;وه ئێوه چی ده&zwnj;په&zwnj;رستن؟!';
$t[86] = 'ئایا شتی ده&zwnj;ست هه&zwnj;ڵبه&zwnj;سته ده&zwnj;که&zwnj;نه خواو له جیاتی په&zwnj;روه&zwnj;ردگار ده&zwnj;تانه&zwnj;وێت بیپه&zwnj;رستن؟!';
$t[87] = 'باشه&zwnj;، ئاخر ئێوه&zwnj;، چ گومانێکتان هه&zwnj;یه له په&zwnj;روه&zwnj;ردگاری جیهانیان؟!';
$t[88] = '(ئه&zwnj;وسا چاوی هه&zwnj;ڵبڕی بۆ ئاسمان و) ته&zwnj;ماشایه&zwnj;کی ئه&zwnj;ستێره&zwnj;کانی کرد، (له ساخی بیروبۆچوونیان).';
$t[89] = '(دیاره که سبه&zwnj;ی جه&zwnj;ژنیان بووه و ده&zwnj;عوه&zwnj;تی حه&zwnj;زره&zwnj;تی ئیبراهیمیشیان کردووه&zwnj;، ئه&zwnj;میش حه&zwnj;زی نه&zwnj;کردووه و بێزاربووه بۆیه&zwnj;) وتی: من نه&zwnj;خۆشم.';
$t[90] = 'ئه&zwnj;وانیش به&zwnj;جێیان هێشت و ڕۆیشتن.';
$t[91] = 'ئه&zwnj;وسا به په&zwnj;نهانی چوو بۆ لای بته&zwnj;کانیان و وتی: ئه&zwnj;وه ناخۆن؟ (دیاره خۆراکیان بۆ بته&zwnj;کان داناوه&zwnj;!)';
$t[92] = 'ئه&zwnj;وسا هاواری له بته&zwnj;کان کردوو، به توڕه&zwnj;ییه&zwnj;وه پێی وتن: ئه&zwnj;وه بۆ ڵاڵن؟! بۆ قسه&zwnj;یه&zwnj;کی ژیرانه ناکه&zwnj;ن؟!';
$t[93] = 'ئینجا تێیان که&zwnj;وت، به&zwnj;ده&zwnj;ستی ڕاستی پیاده&zwnj;ماڵین و ده&zwnj;یشکاندن.';
$t[94] = 'دوایی بت په&zwnj;رستان گه&zwnj;ڕانه&zwnj;وه و بینیان خواکانیان تێکوپێک شکاون، به&zwnj;په&zwnj;له و شڵه&zwnj;ژاوییه&zwnj;وه هاتن بۆ ئیبراهیم...!!';
$t[95] = 'ئه&zwnj;ویش پێی وتن: باشه&zwnj;، ئایا ئه&zwnj;و په&zwnj;یکه&zwnj;رانه&zwnj;ی خۆتان دایده&zwnj;تاشن، ده&zwnj;یپه&zwnj;رستن؟!';
$t[96] = 'له&zwnj;کاتێکدا خوا به&zwnj;دیهێنه&zwnj;ری ئێوه&zwnj;یش و ئه&zwnj;م بتانه&zwnj;شه که دروستتان کردوون (نه&zwnj;شیانتوانی به&zwnj;رگری له خۆیان بکه&zwnj;ن!!).';
$t[97] = 'که&zwnj;چی بیریان نه&zwnj;کرده&zwnj;وه له&zwnj;و به&zwnj;ڵگه به&zwnj;هێزانه&zwnj;وه&zwnj;، وتیان: شوێنێک دروست بکه&zwnj;ن و ئاگری تێدا بکه&zwnj;نه&zwnj;وه و ئیبراهیمی تێ فڕێ بده&zwnj;ن (با بسوتێت و له&zwnj;ناو بچێت).';
$t[98] = 'ئه&zwnj;وان ویستیان پیلانێکی بۆ دروست بکه&zwnj;ن، ئێمه ئه&zwnj;وانمان تێکشکاند و ڕیسوامان کردن.';
$t[99] = 'ئینجا ئیبراهیم وتی: من به&zwnj;ڕاستی ئێوه به جێ ده&zwnj;هێڵم و ده&zwnj;ڕۆم به&zwnj;ره&zwnj;و ڕه&zwnj;زامه&zwnj;ندی په&zwnj;روه&zwnj;ردگارم، له ئایینده&zwnj;شدا به&zwnj;رده&zwnj;وام ڕێنموویم ده&zwnj;کات.';
$t[100] = '(ئه&zwnj;وسا کۆچی کرد)، له&zwnj;و ڕێگه&zwnj;و بانه&zwnj;دا داوای له&zwnj;خوا کردو وتی: په&zwnj;روه&zwnj;ردگارا نه&zwnj;وه&zwnj;یه&zwnj;کی چاک و پاکم پێ ببه&zwnj;خشه&zwnj;.';
$t[101] = 'ئێمه&zwnj;ش مژده&zwnj;ی کوڕێکی به ئارام و خۆگرو ژیرمان پێبه&zwnj;خشی که -ئیسماعیل- ه&zwnj;.';
$t[102] = 'کاتێک ئه&zwnj;و کوڕه که&zwnj;وته ده&zwnj;روماڵ له&zwnj;گه&zwnj;ڵیدا به&zwnj;یانیه&zwnj;ک پێی وت: کوڕم شیرینم بێگومان من ده&zwnj;مبینی له خه&zwnj;ونمدا که تۆ سه&zwnj;رده&zwnj;بڕم، تۆ ده&zwnj;ڵێی چی؟ ڕات چۆنه&zwnj;؟! (کوڕی چاک و خواناس و خۆگر به&zwnj;زمانی شیرین) وتی: بابه&zwnj;گیان هه&zwnj;ر فه&zwnj;رمانێکت پێ دراوه جێبه&zwnj;جێی بکه&zwnj;، ده&zwnj;مبینیت ئه&zwnj;گه&zwnj;ر خوا حه&zwnj;ز بکات، خوابیه&zwnj;وێت، له ئارامکاران ده&zwnj;بم.';
$t[103] = 'کاتێک هه&zwnj;ردووکیان ته&zwnj;سلیمی فه&zwnj;رمانی په&zwnj;روه&zwnj;ردگار بوون، ئیبراهیم، ئیسماعیلی به ڕوودا خسته سه&zwnj;ر خاک بۆ ئه&zwnj;وه&zwnj;ی سه&zwnj;ری ببڕێت.';
$t[104] = 'ئێمه&zwnj;ش بانگمان لێکرد: ئه&zwnj;ی ئیبراهیم!!';
$t[105] = 'به&zwnj;ڕاستی خه&zwnj;وه&zwnj;که&zwnj;ت هێنایه دی، ئێمه هه&zwnj;ر ئاوا پاداشتی چاکه&zwnj;کاران ده&zwnj;ده&zwnj;ینه&zwnj;وه (له تاقی کردنه&zwnj;وه&zwnj;کاندا سه&zwnj;ریان ده&zwnj;خه&zwnj;ین، چونکه لێبڕاون).';
$t[106] = 'به&zwnj;ڕاستی ئه&zwnj;مه خۆی تاقیکردنه&zwnj;وه&zwnj;یه&zwnj;کی ئاشکراو ڕوونه&zwnj;.';
$t[107] = 'ئێمه&zwnj;ش قۆچێکی گه&zwnj;وره&zwnj;مان کرده قوربانی ئیسماعیل تا له جیاتی ئه&zwnj;ودا سه&zwnj;ری ببڕێت.';
$t[108] = 'پاشان ئێمه له&zwnj;ناو گه&zwnj;لان و نه&zwnj;وه&zwnj;کانی داهاتوودا ناوی ئیبراهیممان هێشته&zwnj;وه&zwnj;.';
$t[109] = 'سڵاو و دروود و ڕێز له&zwnj;سه&zwnj;ر ئیبراهیم.';
$t[110] = 'ئێمه ئا به&zwnj;و شێوه&zwnj;یه پاداشتی چاکه&zwnj;کاران ده&zwnj;ده&zwnj;ینه&zwnj;وه&zwnj;...';
$t[111] = 'به&zwnj;ڕاستی ئه&zwnj;میش له&zwnj;به&zwnj;نده ئیمانداره&zwnj;کانی ئێمه&zwnj;یه&zwnj;...';
$t[112] = 'مژده&zwnj;ی هاتنه دنیای - ئیسحاق - یشمان پێبه&zwnj;خشی، که ئه&zwnj;ویش پێغه&zwnj;مبه&zwnj;رێکه له چاکان.';
$t[113] = 'خێرو به&zwnj;ره&zwnj;که&zwnj;تمان ڕژاند به&zwnj;سه&zwnj;ر ئیبراهیم و به&zwnj;سه&zwnj;رئیسحاقدا و له&zwnj;نه&zwnj;وه&zwnj;ی هه&zwnj;ردووکیان، واته&zwnj;: ئیسماعلیل و ئیسحاق چاکان و پاکان، هه&zwnj;روه&zwnj;ها ئه&zwnj;و که&zwnj;سانه&zwnj;ش په&zwnj;یداده&zwnj;بن به&zwnj;روونی سته&zwnj;م له خۆیان ده&zwnj;که&zwnj;ن.';
$t[114] = 'به&zwnj;ڕاستی ئێمه نازو نیعمه&zwnj;تی خۆمانمان ڕژاند به&zwnj;سه&zwnj;ر موسا و هاروون (هه&zwnj;ردووکیانمان هه&zwnj;ڵبژارد بۆ پێغه&zwnj;مبه&zwnj;رایه&zwnj;تی).';
$t[115] = 'هه&zwnj;ردووکیان و قه&zwnj;ومه&zwnj;که&zwnj;شیانمان له ته&zwnj;نگانه گه&zwnj;وره&zwnj;که ڕزگارکرد (له غه&zwnj;رق بوون و خنکان).';
$t[116] = 'سه&zwnj;رکه&zwnj;وتنمان پێبه&zwnj;خشین به&zwnj;سه&zwnj;ر دوژمنه&zwnj;کانیاندا، هه&zwnj;ر ئه&zwnj;وانیش زاڵ بوون.';
$t[117] = 'پاشان ته&zwnj;وراتمان پێبه&zwnj;خشین که ڕوونکه&zwnj;ره&zwnj;وه&zwnj;ی هه&zwnj;موو شتێکه&zwnj;، (تا په&zwnj;یڕه&zwnj;ویی بکه&zwnj;ن).';
$t[118] = 'هه&zwnj;روه&zwnj;ها ڕێنموویی هه&zwnj;ردووکیانمان کرد بۆ ڕێگه و ڕێبازی ڕاست و دروست (تا قه&zwnj;ومه&zwnj;که&zwnj;یان سه&zwnj;رفراز بکه&zwnj;ن).';
$t[119] = 'ناوبانگی خێریانمان له نه&zwnj;وه&zwnj;کانی داهاتوودا هێشته&zwnj;وه (که هه&zwnj;تا دنیایه به&zwnj;ڕێزه&zwnj;وه ناویان ببرێت).';
$t[120] = 'سڵاو و دروود و ڕێز له&zwnj;سه&zwnj;ر موسا و هاروون (له لایه&zwnj;ن په&zwnj;روه&zwnj;ردگاری مه&zwnj;زن و به&zwnj;ڕێزه&zwnj;وه&zwnj;).';
$t[121] = 'بێگومان ئێمه&zwnj;، ئا به&zwnj;و شێوه&zwnj;یه پاداشتی چاکه&zwnj;کاران ده&zwnj;ده&zwnj;ینه&zwnj;وه (له دنیاو قیامه&zwnj;تدا ڕێزدارن).';
$t[122] = 'به&zwnj;ڕاستی هه&zwnj;ردووکیان له به&zwnj;نده ئیمانداره&zwnj;کانی ئێمه بوون (ئه&zwnj;رکی سه&zwnj;رشانی خۆیان به چاکی ئه&zwnj;نجامدا).';
$t[123] = 'بێگومان - ئیلیاس - یش له ده&zwnj;سته&zwnj;ی پێغه&zwnj;مبه&zwnj;ران و ڕه&zwnj;وانه کراوانه (له لایه&zwnj;ن ئێمه&zwnj;وه&zwnj;).';
$t[124] = 'ئه&zwnj;وکاته ئه&zwnj;ویش به قه&zwnj;وم و گه&zwnj;له&zwnj;که&zwnj;ی خۆی وت: ئه&zwnj;وه ناکرێت خواناس بن؟ ئه&zwnj;وه بۆ له&zwnj;خوا ناترسن؟!';
$t[125] = 'ئایا ئێوه هاناو هاوارو نزا بۆ - به&zwnj;عل - ده&zwnj;به&zwnj;ن (بتێکی گه&zwnj;وره بوو ده&zwnj;یانپه&zwnj;رست) وه واز له خواپه&zwnj;رستی ده&zwnj;هێنن له&zwnj;کاتێکدا ئه&zwnj;و زاته چاکترینی دروستکاره&zwnj;کانه&zwnj;.';
$t[126] = 'که - الله - یه&zwnj;، په&zwnj;روه&zwnj;ردگارتانه&zwnj;، په&zwnj;روه&zwnj;ردگاری باو و باپیرانی دێرینیشتانه (ئه&zwnj;وه بۆ بیرێک ناکه&zwnj;نه&zwnj;وه&zwnj;)؟!';
$t[127] = 'که&zwnj;چی ئه&zwnj;وان بڕوایان پێ نه&zwnj;کرد، که&zwnj;وابوو بێگومان ئه&zwnj;وان ئاماده&zwnj;کراون بۆ دۆزه&zwnj;خ.';
$t[128] = 'جگه له به&zwnj;نده دڵسۆزو هه&zwnj;ڵبژارده&zwnj;کانی خوا.';
$t[129] = 'ئینجا ناوبانگی خێری ئیلیاسمان له نه&zwnj;وه&zwnj;کانی داهاتوودا هێشته&zwnj;وه&zwnj;.';
$t[130] = 'سڵاو و دروود و ڕێز له&zwnj;سه&zwnj;ر ئیلیاسین بێت.';
$t[131] = 'ئێمه به&zwnj;ڕاستی به&zwnj;و شێوه&zwnj;یه پاداشتی چاکه&zwnj;کاران ده&zwnj;ده&zwnj;ینه&zwnj;وه&zwnj;.';
$t[132] = 'بێگومان ئه&zwnj;ویش له به&zwnj;نده ئیمانداره&zwnj;کانی ئێمه&zwnj;یه&zwnj;.';
$t[133] = 'به&zwnj;ڕاستی -ڵوط - یش له پێغه&zwnj;مبه&zwnj;رانه&zwnj;.';
$t[134] = 'کاتی خۆی ئه&zwnj;و و هه&zwnj;موو که&zwnj;س و کاریمان ڕزگار کرد.';
$t[135] = 'بێجگه له پیره&zwnj;ژنێک (بێ باوه&zwnj;ڕ بوو به&zwnj;رده&zwnj;وام گرفتی بۆ پێش ده&zwnj;هێنا) له&zwnj;ناومان برد.';
$t[136] = 'پاشان هه&zwnj;مووانمان ته&zwnj;فرو تونا کرد.';
$t[137] = 'بێگومان ئێوه هه&zwnj;موو به&zwnj;یانیه&zwnj;ک ده&zwnj;ڕۆن به&zwnj;سه&zwnj;ر شوێنه&zwnj;واره&zwnj;کانیاندا.';
$t[138] = 'هه&zwnj;روه&zwnj;ها له شه&zwnj;ویشدا (هه&zwnj;ندێک جار له&zwnj;وێوه تێده&zwnj;په&zwnj;ڕن) ئایا ئه&zwnj;وه ژیریتان ناخه&zwnj;نه کارو بیرێک ناکه&zwnj;نه&zwnj;وه (چیمان به&zwnj;سه&zwnj;ر هێنان؟!!)';
$t[139] = 'به&zwnj;ڕاستی - یونس - یش له پێغه&zwnj;مبه&zwnj;رانه&zwnj;.';
$t[140] = 'که&zwnj;چی ڕای کرد به&zwnj;ره&zwnj;و که&zwnj;شتیه پڕ له که&zwnj;لوپه&zwnj;ل و خه&zwnj;ڵکه&zwnj;که&zwnj;...';
$t[141] = 'ئه&zwnj;وسا به&zwnj;ناچاری به&zwnj;شداری قورعه&zwnj;ی کرد، له ڕیزی ئه&zwnj;وانه&zwnj;دا بوو که&zwnj;له قورعه&zwnj;که&zwnj;دا سه&zwnj;رنه&zwnj;که&zwnj;وتن...';
$t[142] = 'ئینجانه&zwnj;هه&zwnj;نگێک قووتی دا، له&zwnj;کاتێکدا که شایانی سه&zwnj;رزه&zwnj;نشت و گله&zwnj;یی بوو (چونکه پێش ئه&zwnj;وه&zwnj;ی خوا مۆڵه&zwnj;تی بدات قه&zwnj;ومه&zwnj;که&zwnj;ی به&zwnj;جێهێشت).';
$t[143] = 'خۆ ئه&zwnj;گه&zwnj;ر له&zwnj;و که&zwnj;سانه نه&zwnj;بوایه که ته&zwnj;سبیحات و یادی خوا ده&zwnj;که&zwnj;ن و نه&zwnj;یوتایه (لا اله الا انت سبحانك انی کنت من الظالمین): په&zwnj;روه&zwnj;ردگارا هیچ خوایه&zwnj;ک نیه جگه له تۆ، پاکی و بێگه&zwnj;ردی و ستایش شایسته&zwnj;ی تۆیه و من له سته&zwnj;مکارانم.';
$t[144] = 'ئه&zwnj;وه ده&zwnj;مایه&zwnj;وه له سکی نه&zwnj;هه&zwnj;نگه&zwnj;که&zwnj;دا تا ڕۆژێک که هه&zwnj;مووان زیندوو ده&zwnj;کرێنه&zwnj;وه&zwnj;.';
$t[145] = 'له&zwnj;وه&zwnj;ودوا فڕێماندایه چۆڵیه&zwnj;کی بێ گژوگیاو دره&zwnj;خته&zwnj;وه بێ هێز و ماندوو نه&zwnj;خۆش.';
$t[146] = 'ئینجا ڕووه&zwnj;کی کوله&zwnj;که&zwnj;ی گه&zwnj;وره&zwnj;مان بۆ پێگه&zwnj;یاندن (که گه&zwnj;ڵاکانی پان و گه&zwnj;وره&zwnj;یه و مێش و مه&zwnj;گه&zwnj;نزێکی ناکه&zwnj;ون).';
$t[147] = 'ئینجا گه&zwnj;ڕانما&zwnj;نه&zwnj;وه و ڕه&zwnj;وانه&zwnj;مه&zwnj;ن کرد به پێغه&zwnj;مبه&zwnj;رایه&zwnj;تی بۆ لای سه&zwnj;د هه&zwnj;زار که&zwnj;س و زیاتریش ده&zwnj;بن (که قه&zwnj;ومه&zwnj;که&zwnj;یه&zwnj;تی).';
$t[148] = 'ئه&zwnj;وسا هه&zwnj;موویان ئیمان و باوه&zwnj;ڕیان هێنا، ئێمه&zwnj;ش ژیانێکی خۆشمان بۆ فه&zwnj;راهه&zwnj;م هێنان تا کاتێکی دیاریکراو.';
$t[149] = 'ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر ڕاپرسیان لێ بکه&zwnj;: ئایا ڕاسته! که کچان شایسته&zwnj;ی په&zwnj;روه&zwnj;ردگارت بن و کوڕان شایسته&zwnj;ی خۆیان بێت؟! (هه&zwnj;ڵبه&zwnj;ته کوڕو کچ هه&zwnj;ردوو به&zwnj;دیهێنراوی خوان، پێوه&zwnj;ر ته&zwnj;نها ته&zwnj;قوایه&zwnj;، به&zwnj;ڵام قورئان ده&zwnj;یه&zwnj;وێت به&zwnj;بیرو بۆچوونی خۆیان، پووچه&zwnj;ڵی به&zwnj;ڵگه&zwnj;که&zwnj;یان ده&zwnj;ر بخات، کاتێ که وتویانه&zwnj;: فریشته&zwnj;کان کچی جوانن له حاڵێکدا ئه&zwnj;وان کچ به&zwnj;چاوی سووک ته&zwnj;ماشا ده&zwnj;که&zwnj;ن و به&zwnj;که&zwnj;می ده&zwnj;زانن، ئه&zwnj;ی بۆچی بۆ خوای په&zwnj;روه&zwnj;ردگار که له هه&zwnj;موو ئه&zwnj;و بیروباوه&zwnj;ڕه چه&zwnj;وتانه دووره ڕه&zwnj;وای ده&zwnj;بینن؟)';
$t[150] = 'ئایا مه&zwnj;گه&zwnj;ر فریشته&zwnj;کانمان به مێینه درووست کردووه و له&zwnj;و کاته&zwnj;دا ئه&zwnj;وان له&zwnj;وێ بوون؟!';
$t[151] = 'ئاگادار بن!! به&zwnj;ڕاستی ئه&zwnj;وانه هه&zwnj;ندێک له درۆ و بوختانیان ئه&zwnj;وه&zwnj;یه که ده&zwnj;ڵێن:';
$t[152] = 'خوا منداڵی بووه&zwnj;!! به&zwnj;ڕاستی و بێگومان ئه&zwnj;وانه زۆر درۆزنن.';
$t[153] = 'ئایا مه&zwnj;گه&zwnj;ر کچانی هه&zwnj;ڵبژاردووه که بیکات به نه&zwnj;وه&zwnj;ی خۆی وه ڕێزی داون به&zwnj;سه&zwnj;ر کوڕاندا؟!!';
$t[154] = 'ئایا چیتانه و چۆن بڕیاری بێ به&zwnj;ڵگه&zwnj;ی وا ده&zwnj;ده&zwnj;ن.';
$t[155] = 'ئایا بیرێک ناکه&zwnj;نه&zwnj;وه&zwnj;؟ (له&zwnj;م قسه بێ سه&zwnj;روبه&zwnj;نانه؟! له&zwnj;م تۆمه&zwnj;ته ناماقوڵانه&zwnj;؟!).';
$t[156] = 'ئایا مه&zwnj;گه&zwnj;ر به&zwnj;ڵگه و شتێکی به&zwnj;هێز و ئاشکراتان به&zwnj;ده&zwnj;سته&zwnj;وه&zwnj;یه (بۆ ئه&zwnj;م قسه حه&zwnj;له&zwnj;ق و مه&zwnj;له&zwnj;قانه&zwnj;).';
$t[157] = 'ئاده&zwnj;ی: کوا کتێبتان، کوا به&zwnj;ڵگه&zwnj;تان ئه&zwnj;گه&zwnj;ر ئێوه ڕاست ده&zwnj;که&zwnj;ن بیهێننه مه&zwnj;یدان.';
$t[158] = '(له&zwnj;لایه&zwnj;کی تره&zwnj;وه کافره&zwnj;کان) خزمایه&zwnj;تیان بڕیار داوه له&zwnj;نێوان زاتی په&zwnj;روه&zwnj;ردگار و په&zwnj;ریه&zwnj;کاندا، سوێن به&zwnj;خوا په&zwnj;ریه&zwnj;کانیش چاک زانیویانه که ئاماده ده&zwnj;کرێن (بۆ لێپرسینه&zwnj;وه و محاسه&zwnj;به ئه&zwnj;گه&zwnj;ر ئاوا خزمایه&zwnj;تیه&zwnj;ک هه&zwnj;بێت چۆن دادگای ده&zwnj;کرێن؟!).';
$t[159] = 'پاکی و بێگه&zwnj;ردی بۆ خوای گه&zwnj;وه&zwnj;ره&zwnj;، ده&zwnj;رباره&zwnj;ی ئه&zwnj;و شتانه&zwnj;ی که ئه&zwnj;وان باسی ئه&zwnj;که&zwnj;ن و ئه&zwnj;و قسه بێ جێیانه&zwnj;ی ده&zwnj;یکه&zwnj;ن به&zwnj;رامبه&zwnj;ر به خوا.';
$t[160] = 'به&zwnj;ڵام به&zwnj;نده دڵسۆزو هه&zwnj;ڵبژارده&zwnj;کانی خوا (له&zwnj;و گوفتارو بۆچونه خوارو خێچانه دوورن).';
$t[161] = 'ئه&zwnj;ی بێ باوه&zwnj;ران به&zwnj;ڕاستی ئێوه و ئه&zwnj;وه&zwnj;ش ده&zwnj;یپه&zwnj;رستن.';
$t[162] = 'ناتوانن که&zwnj;سی پێ گومڕاو و سه&zwnj;رلێشێواو بکه&zwnj;ن و له خوای دوور بخه&zwnj;نه&zwnj;وه&zwnj;.';
$t[163] = 'مه&zwnj;گه&zwnj;ر ئه&zwnj;و که&zwnj;سه&zwnj;ی نیشته&zwnj;جێی ناو دۆزه&zwnj;خه&zwnj;.';
$t[164] = 'فریشته&zwnj;کان ده&zwnj;ڵێن: که&zwnj;سمان نی یه شوێنی دیاریکراوی خۆی نه&zwnj;بێت.';
$t[165] = 'به&zwnj;ڕاستی ئێمه هه&zwnj;ر هه&zwnj;مومان به ڕیز وه&zwnj;ستاوین (ئاماده&zwnj;ی فه&zwnj;رمانین).';
$t[166] = 'دووباره ده&zwnj;ڵێن: ئیمه هه&zwnj;موو ده&zwnj;م سه&zwnj;رگه&zwnj;رمی ته&zwnj;سبیحات و ستایشی خوای میهره&zwnj;بانین.';
$t[167] = 'هه&zwnj;رچه&zwnj;نده هاوه&zwnj;ڵگه&zwnj;ڕان پێش ڕه&zwnj;وانه کردنی پێغه&zwnj;مبه&zwnj;ر ده&zwnj;یان وت:';
$t[168] = 'ئه&zwnj;گه&zwnj;ر ئێمه&zwnj;ش کتێبێکی ئاسمانیمان وه&zwnj;ک پێشووه&zwnj;کان بۆ بهاتایه&zwnj;...';
$t[169] = 'ئێمه&zwnj;یش ده&zwnj;بووینه به&zwnj;نده دڵسۆزو چاکه&zwnj;کانی خوای گه&zwnj;وره&zwnj;.';
$t[170] = 'که&zwnj;چی که قورئانمان ڕه&zwnj;وانه کرد بڕوایان پێ نه&zwnj;کرد، سه&zwnj;رئه&zwnj;نجام خۆیان ده&zwnj;بیننه&zwnj;وه (چیان به&zwnj;سه&zwnj;ر ده&zwnj;هێنین).';
$t[171] = 'سوێن به&zwnj;خوا بێگومان بڕیاری ئێمه بۆ به&zwnj;نده فرستاده و پێغه&zwnj;مبه&zwnj;ره&zwnj;کانمان (هه&zwnj;ر زوو ده&zwnj;رچووه)...';
$t[172] = 'که به&zwnj;دڵنیایی، هه&zwnj;ر ئه&zwnj;وان سه&zwnj;رکه&zwnj;وتوو سه&zwnj;رفراز ده&zwnj;بن.';
$t[173] = 'سه&zwnj;ربازه ئیمانداره&zwnj;کانیشمان هه&zwnj;ر ده&zwnj;سه&zwnj;ڵات ده&zwnj;گرنه ده&zwnj;ست و زاڵ ده&zwnj;بن.';
$t[174] = 'جارێ تا ماوه&zwnj;یه&zwnj;ک خۆت بگره و گویان پێ مه&zwnj;ده&zwnj;.';
$t[175] = 'تۆ سه&zwnj;رئه&zwnj;نجامی لاری و خوانه&zwnj;ناسینیان بخه&zwnj;ره به&zwnj;رچاو، له ئاینده&zwnj;دا خۆیان ده&zwnj;بیننه&zwnj;وه&zwnj;.';
$t[176] = 'ئایا ئه&zwnj;وانه په&zwnj;له&zwnj;یانه که زوو سزاو تۆڵه&zwnj;ی ئێمه ڕووبه&zwnj;ڕوویان ببێت و یه&zwnj;خه&zwnj;یان پێ بگرێت؟!';
$t[177] = '(قووڕ به&zwnj;سه&zwnj;ریان خاک به&zwnj;دامانیان) کاتێک که سزاو تۆڵه&zwnj;ی ئێمه داده&zwnj;به&zwnj;زێته مه&zwnj;یدانیان، ئای که به&zwnj;ربه&zwnj;یانیه&zwnj;کی سامناکه بۆ بێدار کراوه&zwnj;کان.';
$t[178] = 'تۆ تا ماوه&zwnj;یه&zwnj;ک مۆڵه&zwnj;تیان بده و گوێیان پێ مه&zwnj;ده.';
$t[179] = 'ڕاستیه&zwnj;کان بخه&zwnj;ره به&zwnj;رچاو، سه&zwnj;رئه&zwnj;نجام خۆیان ئه&zwnj;بیننه&zwnj;وه&zwnj;.';
$t[180] = 'پاکی و بێگه&zwnj;ردی بۆ په&zwnj;روه&zwnj;ردگارت، په&zwnj;روه&zwnj;ردگاری خاوه&zwnj;ن ده&zwnj;سه&zwnj;ڵات، له&zwnj;وه&zwnj;ی که ئه&zwnj;وانه ده&zwnj;یده&zwnj;نه پاڵ ئه&zwnj;و زاته بێ هاوه&zwnj;ڵ و هاوتایه&zwnj;.';
$t[181] = 'سڵاو دروودیش له&zwnj;سه&zwnj;ر هه&zwnj;موو پێغه&zwnj;مبه&zwnj;ران و سه&zwnj;رجه&zwnj;م فرستاده&zwnj;کانی خوا.';
$t[182] = 'سوپاس و ستایشیش بۆ خوا، په&zwnj;روه&zwnj;ردگاری هه&zwnj;موو جیهانان هه&zwnj;میشه و به&zwnj;رده&zwnj;وام.';
?>