<?php
$t = array();
$t[1] = 'ئه&zwnj;ی خه&zwnj;ڵکینه له خه&zwnj;شم و ناڕه&zwnj;زایی ئه&zwnj;و په&zwnj;روه&zwnj;ردگاره&zwnj;تان خۆ بپارێزن که ئێوه&zwnj;ی له تاکه نه&zwnj;فسێک دروست کردووه&zwnj;، هه&zwnj;ر له&zwnj;و نه&zwnj;فسه&zwnj;ش هاوسه&zwnj;ره&zwnj;که&zwnj;ی به&zwnj;دی هێناو له&zwnj;و دووانه پیاوان و ژنانی زۆری خستۆته&zwnj;وه و بڵاوکرده&zwnj;وه&zwnj;، هه&zwnj;روه&zwnj;ها له&zwnj;و خوایه بترسن که له یه&zwnj;کتر داوا ده&zwnj;که&zwnj;ن به&zwnj;ناوهێنانی ئه&zwnj;وه&zwnj;وه&zwnj;، هه&zwnj;وڵ بده&zwnj;ن په&zwnj;یوه&zwnj;ندی خزمایه&zwnj;تیش بپارێزن و (پته&zwnj;وی بکه&zwnj;ن)چونکه به&zwnj;ڕاستی خوا (هه&zwnj;میشه و به&zwnj;رده&zwnj;وام) چاودێره به&zwnj;سه&zwnj;رتانه&zwnj;وه&zwnj;.';
$t[2] = '(خه&zwnj;ڵکینه&zwnj;) ماڵ و سامانی هه&zwnj;تیوان بده&zwnj;نه&zwnj;وه ده&zwnj;ست خۆیان (کاتێک گه&zwnj;وره بوون و فامیان کرده&zwnj;وه&zwnj;)، ماڵی خراپ و پیسی خۆتان مه&zwnj;گۆڕنه&zwnj;وه به&zwnj;ماڵی چاک و پاکی ئه&zwnj;وان، ماڵ و سامانیان مه&zwnj;خۆن له&zwnj;گه&zwnj;ڵ ماڵ و سامانی خۆتاندا (مه&zwnj;یفه&zwnj;وتێنن) به&zwnj;ڕاستی ئه&zwnj;و کاره سته&zwnj;م و گوناهێکی گه&zwnj;وره&zwnj;یه&zwnj;.';
$t[3] = 'ئه&zwnj;گه&zwnj;ر ترسان له ئه&zwnj;نجام نه&zwnj;دانی دادپه&zwnj;روه&zwnj;ری له&zwnj;گه&zwnj;ڵ کچه بێ باوکه&zwnj;کاندا (ئه&zwnj;گه&zwnj;ر بیانکه&zwnj;نه هاوسه&zwnj;رتان، ئه&zwnj;وه واز له&zwnj;وان بهێنن)، ئه&zwnj;وه له ئافره&zwnj;تانی تر که حه&zwnj;ڵاڵن بۆتان ژن ماره بکه&zwnj;ن دوو دوو، یان سیان سیان، یان چوار چوار، (ئه&zwnj;ڵبه&zwnj;ته له یه&zwnj;ک هاوسه&zwnj;ر زیاتر مه&zwnj;رج و بارودۆخی تایبه&zwnj;تی خۆی هه&zwnj;یه&zwnj;) خۆ ئه&zwnj;گه&zwnj;ر ترسان له&zwnj;وه&zwnj;ی که نه&zwnj;توانن دادپه&zwnj;روه&zwnj;ر بن (له&zwnj;نێوان هاوسه&zwnj;ره&zwnj;کانتاندا) ئه&zwnj;وه با یه&zwnj;ک ژن ماره بکه&zwnj;ن، یان ئه&zwnj;و که&zwnj;نیزه&zwnj;کانه&zwnj;ی که هه&zwnj;تانن (که دیارده&zwnj;یه&zwnj;کی کاتی بوو، ئیسلام بنه&zwnj;بڕی کرد)، ئه&zwnj;وه&zwnj;ی که باسکرا نزیکتره له&zwnj;وه&zwnj;ی سته&zwnj;م نه&zwnj;که&zwnj;ن (ئه&zwnj;گه&zwnj;ر په&zwnj;یڕه&zwnj;وی بکه&zwnj;ن).';
$t[4] = 'ماره&zwnj;یی ئافره&zwnj;تان بده&zwnj;ن به&zwnj;وپه&zwnj;ڕی دڵفراوانی و ئاسووده&zwnj;ییه&zwnj;وه&zwnj;، خۆ ئه&zwnj;گه&zwnj;ر به&zwnj;ئاره&zwnj;زوو ویستی خۆیان ده&zwnj;ستیان هه&zwnj;ڵگرت له هه&zwnj;ندێک ماره&zwnj;یی و به&zwnj;خشییان به&zwnj;ئێوه&zwnj;، ئێوه بۆتان هه&zwnj;یه بیخۆن و به&zwnj;کاری بهێنن و به&zwnj;بێ دوودڵی نۆشی بکه&zwnj;ن (سوودی لێ وه&zwnj;ربگرن).';
$t[5] = 'ماڵ و سامانتان مه&zwnj;ده&zwnj;ن به&zwnj;که&zwnj;م عه&zwnj;قڵ و پیاو خراپان (که ئێوه به&zwnj;رپرسن له ماڵیان)، له&zwnj;کاتێکدا خوا کردوویه&zwnj;تی به&zwnj;هۆی ژیان و پایه&zwnj;داری و هه&zwnj;ستانه&zwnj;وه&zwnj;تان، به&zwnj;ڵکو ته&zwnj;نها به&zwnj;شیان بده&zwnj;ن بۆ خۆراک و پۆشاک، به&zwnj;جوانی قسه&zwnj;ی ڕاست و دروستیان بۆ بکه&zwnj;ن (ته&zwnj;ره&zwnj;یان مه&zwnj;که&zwnj;ن).';
$t[6] = 'هه&zwnj;تیوان تاقی بکه&zwnj;نه&zwnj;وه (به&zwnj;وه&zwnj;ی که هه&zwnj;ندێک پاره&zwnj;یان بده&zwnj;نێ و خۆتان سه&zwnj;رپه&zwnj;رشتییان بکه&zwnj;ن) هه&zwnj;تا ئه&zwnj;وکاته&zwnj;ی که وه&zwnj;ختی ئه&zwnj;وه&zwnj;یان دێت هاوسه&zwnj;ر بگرن، ئه&zwnj;وسا ئه&zwnj;گه&zwnj;ر هه&zwnj;ستتان کرد ژیرو سه&zwnj;لارن، پاره و سامانه&zwnj;که&zwnj;یان پێ بده&zwnj;نه&zwnj;وه&zwnj;، نه&zwnj;که&zwnj;ن ماڵ و سامانیان بخۆن به&zwnj;زیاده&zwnj;ڕه&zwnj;وی له&zwnj;خه&zwnj;رج کردنیدا له ترسی ئه&zwnj;وه&zwnj;ی نه&zwnj;وه&zwnj;کو گه&zwnj;وره بن و زوو له ده&zwnj;ستتان بسێننه&zwnj;وه&zwnj;، جا ئه&zwnj;وه&zwnj;ی ده&zwnj;وڵه&zwnj;مه&zwnj;نده باده&zwnj;ستی بپارێزێت و ئه&zwnj;وه&zwnj;ش که هه&zwnj;ژاره بالێی بخوات و له سنوور ده&zwnj;رنه&zwnj;چێت و زیاده&zwnj;ڕه&zwnj;وی نه&zwnj;کات، جاکاتێک ماڵ و سامانه&zwnj;کانیان ته&zwnj;سلیم ده&zwnj;که&zwnj;نه&zwnj;وه ئه&zwnj;وه شایه&zwnj;تیان له&zwnj;سه&zwnj;ر بگرن (هه&zwnj;رچه&zwnj;نده&zwnj;) ته&zwnj;نها خوا به&zwnj;سه بۆ لێپرسینه&zwnj;وه و چاودێری.';
$t[7] = 'پیاوان به&zwnj;شیان هه&zwnj;یه له&zwnj;و شتانه&zwnj;دا که&zwnj;باوک و دایک و خزمان به میراتی له&zwnj;دوایان به&zwnj;جێ ده&zwnj;مێنێت، هه&zwnj;روه&zwnj;ها ژنانیش به&zwnj;شیان هه&zwnj;یه له&zwnj;و شتانه&zwnj;دا که به میراتیی له باوک و دایک و خزمان به&zwnj;جێ ده&zwnj;مێنێت، له که&zwnj;م و زۆری به&zwnj;شی هه&zwnj;رکه&zwnj;سێک دیاری کراوه و، ده&zwnj;بێت بیدرێتێ.';
$t[8] = 'خۆ ئه&zwnj;گه&zwnj;ر کاتێک له دابه&zwnj;ش کردنی میراتیدا خزمان و هه&zwnj;تیوان و هه&zwnj;ژاران ئاماده بوون، ئه&zwnj;وه له&zwnj;و میراتیه به&zwnj;شیان بده&zwnj;ن و به&zwnj;جوانی قسه&zwnj;یان بۆ بکه&zwnj;ن (پیایاندا هه&zwnj;ڵمه&zwnj;شاخێن).';
$t[9] = 'ده&zwnj;با ئه&zwnj;وانه&zwnj;ش که (دوورنیه به&zwnj;م نزیکانه بمرن) و نه&zwnj;وه&zwnj;ی لاواز و ساوایان له&zwnj;دوا به&zwnj;جێ بمێنێت، خواناسی و ئاینداری بکه&zwnj;نه پیشه&zwnj;یان و گوفتاری چاک و به&zwnj;جێ بڵێن (وه&zwnj;سیه&zwnj;ت بکه&zwnj;ن، بێگومان خوا ده&zwnj;مێنێت بۆ منداڵی جێماویان ئه&zwnj;گه&zwnj;ر خواناس و گوفتار جوان و به&zwnj;خشنده بن).';
$t[10] = 'به&zwnj;ڕاستی ئه&zwnj;وانه&zwnj;ی که ماڵی هه&zwnj;تیووان به ناحه&zwnj;ق و سته&zwnj;م ده&zwnj;خۆن، بێگومان ئه&zwnj;وانه ته&zwnj;نها ئاگر ده&zwnj;خۆن و ئه&zwnj;و ماڵه ده&zwnj;بێته ئاگر له سکیاندا، له قیامه&zwnj;تیشدا ده&zwnj;چنه ناو ئاگری دۆزه&zwnj;خه&zwnj;وه&zwnj;.';
$t[11] = 'خوا ڕێنموویتان ده&zwnj;کات و فه&zwnj;رمانتان پێ ده&zwnj;دا ده&zwnj;رباره&zwnj;ی نه&zwnj;وه&zwnj;کانتان له بواری میراتی گرتندا (ئه&zwnj;ویش ئه&zwnj;وه&zwnj;یه&zwnj;) که به&zwnj;شی نێرینه&zwnj;یه&zwnj;کیان ئه&zwnj;ندازه&zwnj;ی به&zwnj;شی دوو مێینه&zwnj;یانه&zwnj;، خۆ ئه&zwnj;گه&zwnj;ر مناڵه&zwnj;کان ته&zwnj;نها ئافره&zwnj;ت بوون (دووان) و له دووان زیاتر بوون ئه&zwnj;وه دووبه&zwnj;ش له سێ به&zwnj;شی میراتیه&zwnj;که له&zwnj;وان به&zwnj;ش ده&zwnj;کرێت، خۆ ئه&zwnj;گه&zwnj;ر مردووه&zwnj;که ته&zwnj;نها کچێکی هه&zwnj;بوو ئه&zwnj;وه نیوه&zwnj;ی داراییه&zwnj;که&zwnj;ی ده&zwnj;درێتێ، شه&zwnj;شیه&zwnj;کیش ده&zwnj;درێت به&zwnj;هه&zwnj;ریه&zwnj;ک له دایک و باوکی ئه&zwnj;گه&zwnj;ر مردووه&zwnj;که منداڵی هه&zwnj;بوو، خۆ ئه&zwnj;گه&zwnj;ر مردووه&zwnj;که منداڵی نه&zwnj;بوو، ته&zwnj;نها دایک و باوکی هه&zwnj;بوو ئه&zwnj;وه دایکی سێیه&zwnj;کی ده&zwnj;بات و ئه&zwnj;وی تریشی بۆ باوکی ده&zwnj;بێت، خۆ ئه&zwnj;گه&zwnj;ر باوکی نه&zwnj;بوو دایک و براو خوشکی هه&zwnj;بوو شه&zwnj;شیه&zwnj;کی میرات بۆ دایکی ده&zwnj;بێت، ئه&zwnj;مانه هه&zwnj;مووی دوای وه&zwnj;سیه&zwnj;تێک که مردووه&zwnj;که کردوویه&zwnj;تی و دانه&zwnj;وه&zwnj;ی قه&zwnj;رزو قه&zwnj;رزاری ده&zwnj;بێت، ئێوه نازانن که باوانتان و نه&zwnj;وه&zwnj;کانتان نازانن کام لایان سوودی زیاتره بۆتان (له&zwnj;به&zwnj;رئه&zwnj;وه به&zwnj;گوێره&zwnj;ی فه&zwnj;رمانی خوا میراتی دابه&zwnj;ش بکه&zwnj;ن، ئابه&zwnj;م شێوه&zwnj;یه میراتی دابه&zwnj;ش کردن) فه&zwnj;رزه له&zwnj;سه&zwnj;رتان که په&zwnj;یڕه&zwnj;وی بکه&zwnj;ن چونکه له&zwnj;لایه&zwnj;ن خواوه دیاریکراوه&zwnj;، به&zwnj;ڕاستی خوایش هه&zwnj;میشه و به&zwnj;رده&zwnj;وام زاناو دانایه&zwnj;.';
$t[12] = 'هه&zwnj;روه&zwnj;ها نیوه&zwnj;ی میراتی ژنه&zwnj;کانتان ده&zwnj;درێت به ئێوه ئه&zwnj;گه&zwnj;ر منداڵیان نه&zwnj;بوو، خۆ ئه&zwnj;گه&zwnj;ر منداڵیان هه&zwnj;بوو ئه&zwnj;وه چواریه&zwnj;کی میراتی ژنه&zwnj;کانتان بۆ هه&zwnj;یه&zwnj;، ئه&zwnj;مانه هه&zwnj;مووی دوای به&zwnj;جێهێنانی وه&zwnj;سیه&zwnj;تی ژنان و دانه&zwnj;وه&zwnj;ی قه&zwnj;رزیان بۆ ژنان چواریه&zwnj;کی میراتی ئێوه بڕیاردراوه ئه&zwnj;گه&zwnj;ر ئێوه منداڵتان له&zwnj;دوا به&zwnj;جێ نه&zwnj;مابێت، خۆ ئه&zwnj;گه&zwnj;ر منداڵتان هه&zwnj;بوو ئه&zwnj;وه هه&zwnj;شتیه&zwnj;ک بۆ ژنه&zwnj;که ده&zwnj;بێت له&zwnj;وه&zwnj;ی که به میراتی لێتان به&zwnj;جێ ماوه&zwnj;، ئه&zwnj;مانه هه&zwnj;مووی دوای به&zwnj;جێهێنانی وه&zwnj;سیه&zwnj;تتان و دانه&zwnj;وه&zwnj;ی قه&zwnj;رزه&zwnj;کانتان، خۆ ئه&zwnj;گه&zwnj;ر پیاوێک یان ئافره&zwnj;تێک مرد منداڵ و باوک و باپیرو براو خوشکی دایک و باوکی نه&zwnj;بوو، به&zwnj;ڵکو ته&zwnj;نها خوشکێک یان برایه&zwnj;کی دایکی هه&zwnj;بوو، ئه&zwnj;وه شه&zwnj;شیه&zwnj;ک به&zwnj;شی هه&zwnj;ر یه&zwnj;کێک له&zwnj;وان ده&zwnj;بێت، خۆ ئه&zwnj;گه&zwnj;ر براو خوشکی دایکی له یه&zwnj;ک زیاتر بوون ئه&zwnj;وه هه&zwnj;موویان هاوبه&zwnj;شن له سێیه&zwnj;کی میراتیه&zwnj;که&zwnj;دا (به&zwnj;یه&zwnj;کسانی ژن و پیاو) ئه&zwnj;مانه هه&zwnj;مووی دوای وه&zwnj;سیه&zwnj;تێک که ده&zwnj;کرێت، دانه&zwnj;وه&zwnj;ی قه&zwnj;رز، له&zwnj;کاتێکدا ئه&zwnj;و وه&zwnj;سیه&zwnj;ته زیانبه&zwnj;خش نه&zwnj;بێت به&zwnj;میراتگران، ئه&zwnj;مانه هه&zwnj;ر هه&zwnj;مووی بڕیار و فرمانن له&zwnj;لایه&zwnj;ن خوای گه&zwnj;وره&zwnj;وه و ئه&zwnj;و خوایه زانا و به حیلم و حه&zwnj;وسه&zwnj;ڵه&zwnj;یه&zwnj;.';
$t[13] = 'ئه&zwnj;وانه&zwnj;ی باسکران (ده&zwnj;رباره&zwnj;ی هه&zwnj;تیووان و چۆنیه&zwnj;تی دابه&zwnj;شکردنی میراتی) ئه&zwnj;و سنوورانه&zwnj;ن خوا دیاری کردوون، جا ئه&zwnj;و که&zwnj;سه&zwnj;ی فه&zwnj;رمانبه&zwnj;رداری خواو پێغه&zwnj;مبه&zwnj;ره&zwnj;که&zwnj;ی بکات (له&zwnj;م بواره&zwnj;داو له هه&zwnj;موو بواره&zwnj;کانی تردا)، ئه&zwnj;وه خوا ده&zwnj;یخاته ناو باخه&zwnj;کانی به&zwnj;هه&zwnj;شته&zwnj;وه&zwnj;: که چه&zwnj;نده&zwnj;ها ڕووبار به&zwnj;ژێر دره&zwnj;خته&zwnj;کانیدا ده&zwnj;ڕوات، هاوڕێ له&zwnj;گه&zwnj;ڵ ژیانی نه&zwnj;بڕاوه تیایدا، بێگومان هه&zwnj;ر ئه&zwnj;وه&zwnj;شه سه&zwnj;رفرازیی گه&zwnj;وره و ده&zwnj;ستکه&zwnj;وتی بێ سنوور.';
$t[14] = '(به&zwnj;ڵام) ئه&zwnj;وه&zwnj;ی یاخی بێت له خواو پێغه&zwnj;مبه&zwnj;ره&zwnj;که&zwnj;ی و ده&zwnj;رچێت له سنووره&zwnj;کانی، ئه&zwnj;وه خوا ده&zwnj;یخاته ناو ئاگرێکه&zwnj;وه که بۆ هه&zwnj;میشه تیایدا ده&zwnj;مێنێته&zwnj;وه و سزایه&zwnj;کی سه&zwnj;ر شۆڕکه&zwnj;ر و ڕیسواکه&zwnj;ری بۆ هه&zwnj;یه&zwnj;.';
$t[15] = 'ئه&zwnj;و ئافره&zwnj;تانه&zwnj;ی که زینا ده&zwnj;که&zwnj;ن له ژنانتان ئه&zwnj;وه چوار شایه&zwnj;تیان له خۆتان له&zwnj;سه&zwnj;ر بگرن، جا ئه&zwnj;گه&zwnj;ر شایه&zwnj;تیاندا، ئێوه له ماڵدا به&zwnj;ندیان بکه&zwnj;ن، تا مردن یه&zwnj;خه&zwnj;یان پێده&zwnj;گرێت یان خوا ده&zwnj;روویه&zwnj;کیان لێ ده&zwnj;کاته&zwnj;وه&zwnj;.';
$t[16] = 'ئه&zwnj;و ژن و پیاوه&zwnj;تان، یان ئه&zwnj;و کوڕ و کچه&zwnj;تان که زینایان له ده&zwnj;ست ده&zwnj;قه&zwnj;ومێت، ئه&zwnj;وه ئێوه لێیان بده&zwnj;ن و ئازاریان بده&zwnj;ن، خۆ ئه&zwnj;گه&zwnj;ر ته&zwnj;وبه&zwnj;یان کردو خۆیان په&zwnj;روه&zwnj;رده و چاککرد، ئه&zwnj;وه ئێوه وازیان لێ بهێنن (ته&zwnj;عنه&zwnj;یان لێ مه&zwnj;ده&zwnj;ن، به&zwnj;چاوی سوک ته&zwnj;ماشایان مه&zwnj;که&zwnj;ن) چونکه به&zwnj;ڕاستی خوا هه&zwnj;میشه ته&zwnj;وبه وه&zwnj;رگره و به&zwnj;ڕه&zwnj;حم و میهره&zwnj;بانیشه&zwnj;.';
$t[17] = 'خوای گه&zwnj;وره ته&zwnj;نها ئه&zwnj;و ته&zwnj;وبه&zwnj;و په&zwnj;شیمانیه قبوڵ ده&zwnj;کات و ئه&zwnj;و که&zwnj;سانه ده&zwnj;گرێته&zwnj;وه که گوناهـ و نادروستی به&zwnj;نه&zwnj;فامی و نه&zwnj;زانی ئه&zwnj;نجام ده&zwnj;ده&zwnj;ن و پاشان به&zwnj;زوویی ته&zwnj;وبه ده&zwnj;که&zwnj;ن و په&zwnj;شیمانی ده&zwnj;رده&zwnj;بڕن، ئا ئه&zwnj;و جۆره که&zwnj;سانه خوا ته&zwnj;وبه&zwnj;یان لێ وه&zwnj;رده&zwnj;گرێت و هه&zwnj;میشه و به&zwnj;رده&zwnj;وام خوا زانایه (به&zwnj;وه&zwnj;ی به&zwnj;نه&zwnj;زانیی و نه&zwnj;فامی هه&zwnj;ڵه و گوناهـ ئه&zwnj;نجام ده&zwnj;دات) و دانایه و (ده&zwnj;زانێت که ئاده&zwnj;میزاد هه&zwnj;ندێک جار لاوازیی ڕووی تێده&zwnj;کات و به&zwnj;هه&zwnj;ڵه&zwnj;دا ده&zwnj;چێت، له&zwnj;به&zwnj;رئه&zwnj;وه نائومێدی ناکات).';
$t[18] = 'ته&zwnj;وبه&zwnj;و په&zwnj;شیمانی بۆ ئه&zwnj;و که&zwnj;سانه نیه که به&zwnj;رده&zwnj;وامن له&zwnj;سه&zwnj;ر گوناهـو تاوان، ئه&zwnj;وکاته&zwnj;ی مردن یه&zwnj;خه&zwnj;ی به&zwnj;یه&zwnj;کێکیان گرت ئه&zwnj;وسا بڵێت: ئه&zwnj;وه ئیتر من به&zwnj;ڕاستی له ئێسته&zwnj;وه ته&zwnj;وبه&zwnj;م کرد، ته&zwnj;وبه له&zwnj;وانه&zwnj;ش وه&zwnj;رناگیرێت که به&zwnj;کافری ده&zwnj;مرن، بۆ ئه&zwnj;و جۆره تاوانبارانه هه&zwnj;موویان سزایه&zwnj;کی به&zwnj;ئێشمان ئاماده کردووه&zwnj;.';
$t[19] = 'ئه&zwnj;ی ئه&zwnj;وانه&zwnj;ی باوه&zwnj;ڕتان هێناوه دروست نیه بۆتان ژنان به زۆر بکه&zwnj;نه میراتی خۆتان و ده&zwnj;ستیان به&zwnj;سه&zwnj;ردا بگرن، (وه&zwnj;کو که&zwnj;لوپه&zwnj;لی تری ناوماڵ)، ناچاریان مه&zwnj;که&zwnj;ن تا هه&zwnj;ندێک ماره&zwnj;ییتان بۆ بگه&zwnj;ڕێننه&zwnj;وه&zwnj;، مه&zwnj;گه&zwnj;ر ئه&zwnj;وان گوناهێکی ئاشکراو دیار ئه&zwnj;نجام بده&zwnj;ن، هاوژینییان به&zwnj;جوانی له&zwnj;گه&zwnj;ڵدا به&zwnj;رنه&zwnj;سه&zwnj;ر، خۆ ئه&zwnj;گه&zwnj;ر خۆشتان نه&zwnj;ویستن و لێیان بێزار بوون، ئه&zwnj;وه دوور نیه و ده&zwnj;شێت حه&zwnj;زتان له شتێک نه&zwnj;بێت و خوا بیکات به&zwnj;مایه&zwnj;ی خێرو بێری زۆرو بێ سنوور.';
$t[20] = 'خۆ ئه&zwnj;گه&zwnj;ر ویستتان هاوسه&zwnj;رێک بخه&zwnj;نه جێگه&zwnj;ی هاوسه&zwnj;رێکی ترو ماره&zwnj;ییه&zwnj;که&zwnj;ی (ئاڵتون و سامانێکی) زۆرتان پێدا بوو، ئه&zwnj;وه نابێت هیچ شتێکی لێ بگێڕنه&zwnj;وه و لێی که&zwnj;م بکه&zwnj;نه&zwnj;وه&zwnj;، ئایا ئه&zwnj;و ماره&zwnj;ییه&zwnj;ی که پێتانداوه وه&zwnj;ریده&zwnj;گرنه&zwnj;وه لێیان به&zwnj;دروست کردنی بوختانێکی ناڕه&zwnj;واو گوناهو سته&zwnj;مێکی ئاشکرا؟!';
$t[21] = 'جا چۆن دروسته ماره&zwnj;ییان لێوه&zwnj;ربگرنه&zwnj;وه&zwnj;، له&zwnj;کاتێکدا ئێوه چوونه&zwnj;ته لای یه&zwnj;کترو ژنانیش په&zwnj;یمان و به&zwnj;ڵێنێکی گه&zwnj;وره&zwnj;و گرنگیان لێ وه&zwnj;رگرتوون (له&zwnj;کاتی ماره&zwnj;کردنیاندا).';
$t[22] = 'نه&zwnj;که&zwnj;ن ئه&zwnj;و ئافره&zwnj;تانه ماره بکه&zwnj;ن که پێشتر باوکتان ماره&zwnj;ی کردبوو، مه&zwnj;گه&zwnj;ر ئه&zwnj;وه&zwnj;ی که ڕابوورد (له&zwnj;سه&zwnj;رده&zwnj;می نه&zwnj;فامیدا پێش حه&zwnj;رام کردنی) به&zwnj;ڕاستی ئه&zwnj;و کاره گوناهێکی گه&zwnj;وره و ناشرین و ناپه&zwnj;سه&zwnj;نده و ڕێبازێکی خراپه&zwnj;.';
$t[23] = '(ئیمانداران، ئه&zwnj;م ئافره&zwnj;تانه&zwnj;) ماره&zwnj;کردنیان حه&zwnj;رامکراوه له&zwnj;سه&zwnj;رتان: دایکانتان، کچه&zwnj;کانتان، خوشکه&zwnj;کانتان، پوره&zwnj;کانتان (خوشکی باوک)، پوره&zwnj;کانتان (خوشکی دایک)، برازاتان، خوشکه&zwnj;زاتان، ئه&zwnj;و دایکانه&zwnj;ی که شیریان داونه&zwnj;تێ، خوشکه شیریه&zwnj;کانتان، دایکی هاوسه&zwnj;رتان (خه&zwnj;سوو)، ئه&zwnj;و کچانه&zwnj;ی له پیاوێکی ترن و لای ئێوه ده&zwnj;ژین و په&zwnj;روه&zwnj;رده ده&zwnj;بن و له&zwnj;و ژنانه&zwnj;تانن که چوونه&zwnj;ته لایان (واته زڕ کچه&zwnj;کانتان)، خۆ ئه&zwnj;گه&zwnj;ر نه&zwnj;چووبنه لای دایکی ئه&zwnj;و جۆره کچانه ئه&zwnj;وه هیچ گوناهتان له&zwnj;سه&zwnj;ر نی یه (ماره&zwnj;یان بکه&zwnj;ن)، (هه&zwnj;روه&zwnj;ها حه&zwnj;رامه&zwnj;) هاوسه&zwnj;ری ئه&zwnj;و کوڕانه&zwnj;تان ماره بکه&zwnj;ن که له&zwnj;پشتی خۆتانن (واته بوکه&zwnj;کانتان)، (هه&zwnj;روه&zwnj;ها حه&zwnj;رامه&zwnj;) دوو خوشکیش پێکه&zwnj;وه بکه&zwnj;نه هاوسه&zwnj;ری خۆتان له یه&zwnj;ک کاتدا، جگه له&zwnj;وانه&zwnj;ی که ڕابوورد، به&zwnj;ڕاستی خوا هه&zwnj;میشه&zwnj;و به&zwnj;رده&zwnj;وام لێخۆشبوو میهره&zwnj;بانه&zwnj;.';
$t[24] = '(هه&zwnj;روه&zwnj;ها حه&zwnj;رامه&zwnj;) لێتان ئه&zwnj;و ئافره&zwnj;تانه&zwnj;ی که مێردیان هه&zwnj;یه جگه له که&zwnj;نیزه&zwnj;کتان (له&zwnj;و زه&zwnj;مانه&zwnj;دا باو بووه&zwnj;، جا ئه&zwnj;م سنوورانه&zwnj;) خوا بڕیاریداوه له&zwnj;سه&zwnj;رتان، جگه له&zwnj;وانه&zwnj;ی که باسکران، حه&zwnj;ڵاڵه بۆتان به&zwnj;هۆی ماڵ و دارایتانه&zwnj;وه هاوسه&zwnj;ر بگرن (تا چوار ئافره&zwnj;ت) تا پاک داوێن بن و له سنووری ڕه&zwnj;وشتی به&zwnj;رز ده&zwnj;رنه&zwnj;چن، جا له&zwnj;گه&zwnj;ڵ هه&zwnj;ر ئافره&zwnj;تێکدا هاوسه&zwnj;ریتان گرت ئه&zwnj;وه فه&zwnj;رزه که ماره&zwnj;ییه&zwnj;که&zwnj;یان بده&zwnj;نێ و هیچ گوناهیش نیه له سه&zwnj;رتان له که&zwnj;م کردنه&zwnj;وه&zwnj;ی ئه&zwnj;و ماره&zwnj;ییه&zwnj;ی له&zwnj;سه&zwnj;ری ڕێککه&zwnj;وتبوون له دوای دیاریکردنی، به&zwnj;ڕاستی خوا هه&zwnj;میشه&zwnj;و به&zwnj;رده&zwnj;وام زاناو دانایه&zwnj;.';
$t[25] = 'ئه&zwnj;وه&zwnj;ش که نه&zwnj;یتوانی له ئێوه له&zwnj;به&zwnj;ر ده&zwnj;ست کورتی، ئافره&zwnj;تانی سه&zwnj;ربه&zwnj;ستی باوه&zwnj;ڕدا ماره بکات، ئه&zwnj;وه با له کچه&zwnj;که&zwnj;نیزه&zwnj;که ئیمانداره&zwnj;کانتان بخوازێت، خوایش زانایه که ڕاده&zwnj;ی ئیمانتان چه&zwnj;نده و چۆنه&zwnj;، ئێوه هه&zwnj;ندێکتان له هه&zwnj;ندێکتانن (هه&zwnj;مووتان له ئاده&zwnj;م و حه&zwnj;وان و ئه&zwnj;م جیاوازیه چینایه&zwnj;تیه شتێکی کاتی یه&zwnj;)، له&zwnj;به&zwnj;رئه&zwnj;وه ئه&zwnj;و که&zwnj;نیزه&zwnj;کانه ماره بکه&zwnj;ن له&zwnj;سه&zwnj;ر ڕه&zwnj;زامه&zwnj;ندی که&zwnj;س و کارو خاوه&zwnj;نه&zwnj;کانیان و ماره&zwnj;ییان به&zwnj;چاکی پێ بده&zwnj;ن، له&zwnj;کاتێکدا ئه&zwnj;و که&zwnj;نیزه&zwnj;کانه&zwnj;ش پێویسته پاک داوێن بن و دووربن له داوێن پیسی و دۆست گرتنه&zwnj;وه&zwnj;، خۆ ئه&zwnj;گه&zwnj;ر شوویان کردو له&zwnj;وه&zwnj;ودوا داوێن پیسیه&zwnj;کانیان ئه&zwnj;نجامدا ئه&zwnj;وه نیوه&zwnj;ی سزای ئافره&zwnj;تانی سه&zwnj;ربه&zwnj;ست ده&zwnj;درێن (چونکه ئازادیی ته&zwnj;واویان نه&zwnj;بووه له&zwnj;و سه&zwnj;رده&zwnj;مه&zwnj;دا)، له&zwnj;به&zwnj;ر ئه&zwnj;وه ماره&zwnj;کردنیان بۆ ئه&zwnj;وانه&zwnj;تانه که ده&zwnj;ترسێت تووشی هه&zwnj;ڵه&zwnj;و گوناهـ ببێت، خۆڕاگریتان (تا خوا ده&zwnj;رووتان لێده&zwnj;کاته&zwnj;وه&zwnj;) چاکتره بۆتان، خوایش لێخۆشبوو و میهره&zwnj;بانه&zwnj;.';
$t[26] = 'بێگومان خوا ده&zwnj;یه&zwnj;وێت (به&zwnj;م شێوه&zwnj;یه&zwnj;) هه&zwnj;موو شتێکتان بۆ ڕوون بکاته&zwnj;وه و ڕێنموویتان بکات بۆ ئه&zwnj;و یاساو به&zwnj;رنامانه&zwnj;ی پێش خۆتان (که پێغه&zwnj;مبه&zwnj;ران هێناویانه له جیهانی کۆمه&zwnj;ڵایه&zwnj;تی و په&zwnj;یوه&zwnj;ندیه&zwnj;کان و... هتد) و ته&zwnj;وبه و په&zwnj;شیمانیشتان لێ وه&zwnj;ربگرێت، خوا زاناو دانایه&zwnj;.';
$t[27] = 'بێگومان خوا ده&zwnj;یه&zwnj;وێت ته&zwnj;وبه و په&zwnj;شیمانیتان لێ وه&zwnj;ربگرێت و له هه&zwnj;ڵه&zwnj;کانتان خۆش بێت، (به&zwnj;ڵام) ئه&zwnj;وانه&zwnj;ی شوێن ئاره&zwnj;زه&zwnj;ووات و هه&zwnj;وه&zwnj;سبازی که&zwnj;وتوون ده&zwnj;یانه&zwnj;وێت ئێوه لابده&zwnj;ن به لادانێکی گه&zwnj;وره&zwnj;.';
$t[28] = 'خوا ده&zwnj;یه&zwnj;وێت (به&zwnj;م به&zwnj;رنامه و ڕێبازانه&zwnj;) بارتان سووک بکات و کار ئاسانیتان بۆ بکات (چونکه&zwnj;) ئاده&zwnj;میزاد به لاوازی دروست کراوه (له به&zwnj;رامبه&zwnj;ر ئاره&zwnj;زووه&zwnj;کانیه&zwnj;وه خۆڕاگرنیه&zwnj;).';
$t[29] = 'ئه&zwnj;ی ئه&zwnj;وانه&zwnj;ی باوه&zwnj;ڕتان هێناوه ماڵ و سامانی یه&zwnj;کتر به&zwnj;ناحه&zwnj;ق و به&zwnj;بێ به&zwnj;رامبه&zwnj;ر مه&zwnj;خۆن له&zwnj;نێوان خۆتاندا، مه&zwnj;گه&zwnj;ر بازرگانیه&zwnj;ک بێت و له&zwnj;سه&zwnj;ر ڕه&zwnj;زامه&zwnj;ندی هه&zwnj;موولایه&zwnj;کتان بێت، خۆتان مه&zwnj;کوژن و (یه&zwnj;کتر به&zwnj;ناحه&zwnj;ق مه&zwnj;کوژن، کاتێک بێزاریی و بێتاقه&zwnj;تی ڕووتان تێده&zwnj;کات یان تووشی زیانێک ده&zwnj;بن)، به&zwnj;ڕاستی خوا میهره&zwnj;بانه پێتان.';
$t[30] = 'هه&zwnj;ر که&zwnj;سێکیش کاری وابکات (سامانی خه&zwnj;ڵکی به&zwnj;ناحه&zwnj;ق بخوات یان خۆی یان خه&zwnj;ڵک بکوژێت) به&zwnj;دوژمنایه&zwnj;تی و سته&zwnj;مکردن، ئه&zwnj;وه له ئاینده&zwnj;دا ده&zwnj;یخه&zwnj;ینه دۆزه&zwnj;خه&zwnj;وه و به&zwnj;ئاگر ده&zwnj;یسووتێنین، ئه&zwnj;و کاره&zwnj;ش بۆ خوا هه&zwnj;میشه و به&zwnj;رده&zwnj;وام ئاسانه&zwnj;.';
$t[31] = 'ئه&zwnj;گه&zwnj;ر خۆتان بپارێزن له&zwnj;و تاوانه گه&zwnj;ورانه&zwnj;ی قه&zwnj;ده&zwnj;غه کراوه لێتان، ئه&zwnj;وه له گوناهه&zwnj;کانی ترتان چاوپۆشی ده&zwnj;که&zwnj;ین و ده&zwnj;تانخه&zwnj;ینه جێیه&zwnj;کی خۆش و سازگارو ڕێزداره&zwnj;وه (که به&zwnj;هه&zwnj;شتی به&zwnj;رینه&zwnj;&zwnj;).';
$t[32] = 'ئاواته&zwnj;خوازی شتێک مه&zwnj;که&zwnj;ن که خوا به&zwnj;خشیوویه&zwnj;تی به&zwnj;هه&zwnj;ندێکتان و (نه&zwnj;یبه&zwnj;خشیووه به&zwnj;هه&zwnj;ندێکتان، چونکه خوا دادپه&zwnj;روه&zwnj;ره و یاساکانی له ڕووی زانستی و حیکمه&zwnj;ته&zwnj;وه&zwnj;یه&zwnj;، کاتێک مافی داوه به&zwnj;پیاوان، له هه&zwnj;ندێک ڕووی تریشه&zwnj;وه مافی داوه به&zwnj;ئافره&zwnj;تان... هتد) پیاوان، به&zwnj;شێکیان هه&zwnj;یه له&zwnj;وه&zwnj;ی که به&zwnj;ده&zwnj;ستیان هێناوه&zwnj;، هه&zwnj;روه&zwnj;ها ئافره&zwnj;تانیش به&zwnj;شێکیان هه&zwnj;یه له&zwnj;وه&zwnj;ی که به&zwnj;ده&zwnj;ستیان هێناوه&zwnj;، هه&zwnj;میشه و به&zwnj;رده&zwnj;وامیش داوای زیاده به&zwnj;خشش و به&zwnj;هره&zwnj;ی خوا بکه&zwnj;ن، چونکه به&zwnj;ڕاستی خوا به&zwnj;هه&zwnj;موو شتێک زانایه&zwnj;.';
$t[33] = 'بۆ هه&zwnj;موو که&zwnj;سێک و هه&zwnj;موو سامانێک چه&zwnj;ند میراتبه&zwnj;رێکمان داناوه له&zwnj;وه&zwnj;ی که له دایک و باوک و خزمان به&zwnj;جێ ماوه (تا به&zwnj;دادپه&zwnj;روه&zwnj;ریی وه&zwnj;کو دیاریکراوه دابه&zwnj;شی بکه&zwnj;ن) و بۆ ئه&zwnj;وانه&zwnj;ش که په&zwnj;یمانتان له&zwnj;گه&zwnj;ڵ به&zwnj;ستوون که&zwnj;وا میرات له&zwnj;یه&zwnj;کتر بگرن، مافی ئه&zwnj;وانیش بده&zwnj;ن، به&zwnj;ڕاستی خوا هه&zwnj;میشه&zwnj;و به&zwnj;رده&zwnj;وام شایه&zwnj;تێکی ئاگاداره به&zwnj;سه&zwnj;ر هه&zwnj;موو شتێکدا.';
$t[34] = 'پیاوان سه&zwnj;رپه&zwnj;رشتیارن به&zwnj;سه&zwnj;ر هاوسه&zwnj;ره&zwnj;کانیانه&zwnj;وه&zwnj;، ئه&zwnj;مه&zwnj;ش به&zwnj;هۆی ڕێزو زیاده ئه&zwnj;رکێکه&zwnj;وه که خوا داویه&zwnj;تی به&zwnj;هه&zwnj;ندێک به&zwnj;سه&zwnj;ر هه&zwnj;ندێکی تردا (سه&zwnj;ره&zwnj;ڕای ئه&zwnj;وه&zwnj;ی که&zwnj;)پیاوان له&zwnj;ماڵ و سامانیان خه&zwnj;رج ده&zwnj;که&zwnj;ن (له پێناوی ئاسووده&zwnj;یی و خۆشگوزه&zwnj;رانی ئافره&zwnj;تاندا)، له&zwnj;ولاشه&zwnj;وه ئافره&zwnj;تانی ژیرو دیندارو گونجاو هه&zwnj;میشه گوێڕایه&zwnj;ڵی مێرده&zwnj;کانیانن، نهێنی نێوان خۆیان و مێرده&zwnj;کانیان ده&zwnj;پارێزن (به&zwnj;تایبه&zwnj;ت ئه&zwnj;و نهێنیانه&zwnj;ی خوا ده&zwnj;یه&zwnj;وێت پارێزراو بن) به&zwnj;هۆی ئه&zwnj;وه&zwnj;ی که خوا مافه&zwnj;کانی ئه&zwnj;وانی پاڕاستووه&zwnj;، ئه&zwnj;و ئافره&zwnj;تانه&zwnj;ش ئێوه ده&zwnj;ترسن سه&zwnj;رکه&zwnj;شی وسه&zwnj;رپێچی بکه&zwnj;ن سه&zwnj;ره&zwnj;تا ئامۆژگاریان بکه&zwnj;ن، (ئه&zwnj;گه&zwnj;ر سوودی نه&zwnj;بوو) له&zwnj;ناو جێدا پشتیان تێبکه&zwnj;ن، (ئه&zwnj;گه&zwnj;ر ئه&zwnj;وه&zwnj;ش سوودی نه&zwnj;بوو) لێیان بده&zwnj;ن (به&zwnj;مه&zwnj;رجێک لێدانه&zwnj;که له ده&zwnj;م و چاو نه&zwnj;بێت و ئاسه&zwnj;وار به&zwnj;جێنه&zwnj;هێڵێت)، ئه&zwnj;گه&zwnj;ر به&zwnj;م هۆیانه&zwnj;وه له لادان و یاخی بوون وازیان هێنا، ئه&zwnj;وه ئیتر سته&zwnj;میان لێمه&zwnj;که&zwnj;ن و ڕێگه&zwnj;ی تر مه&zwnj;گرنه&zwnj;به&zwnj;ر (بۆ ئازاردانیان) بێگومان خوا به&zwnj;رزو بڵندو گه&zwnj;وره&zwnj;یه&zwnj;، (ئه&zwnj;گه&zwnj;ر سته&zwnj;میان لێبکه&zwnj;ن تۆڵه&zwnj;تان لێده&zwnj;سێنێت).';
$t[35] = 'خۆ ئه&zwnj;گه&zwnj;ر ترسان له&zwnj;وه&zwnj;ی نێوان ژن و مێرد بشێوێت (کێشه&zwnj;یان چاره&zwnj;سه&zwnj;ر نه&zwnj;کرێت)، دادوه&zwnj;رێک له خزمی پیاوه&zwnj;که و دادوه&zwnj;رێک له خزمی ژنه&zwnj;که بنێرن (بۆ ڕێکخستنیان) ئه&zwnj;گه&zwnj;ر چاکسازی و ڕێکخستنیان بوێت، ئه&zwnj;وه خوا پێکهاتن فه&zwnj;راهه&zwnj;م ده&zwnj;هێنێت له نێوان ژن و مێرده&zwnj;که&zwnj;دا، بێگومان خوا هه&zwnj;میشه&zwnj;و به&zwnj;رده&zwnj;وام زاناو ئاگایه&zwnj;.';
$t[36] = '(ئه&zwnj;ی خه&zwnj;ڵکینه&zwnj;) ته&zwnj;نها خوا بپه&zwnj;رستن و هیچ جۆره شه&zwnj;ریک و هاوه&zwnj;ڵێکی بۆ بڕیار مه&zwnj;ده&zwnj;ن و چاکه&zwnj;کار بن له&zwnj;گه&zwnj;ڵ دایک و باوکتان و خزمان و هه&zwnj;تیووان و هه&zwnj;ژاران و دراوسێی خزم و دراوسێی نزیک و دراوسێی بێگانه&zwnj;داو هاوه&zwnj;ڵی کارو سه&zwnj;فه&zwnj;رو ڕێبوارو ژێر ده&zwnj;سته&zwnj;کانتاندا، به&zwnj;ڕاستی خوا ئه&zwnj;وانه&zwnj;ی خۆش ناوێت که خۆده&zwnj;نوێنن و فیزده&zwnj;که&zwnj;ن و فه&zwnj;خر ده&zwnj;فرۆشن به&zwnj;سه&zwnj;ر خه&zwnj;ڵکیدا.';
$t[37] = 'ئه&zwnj;وانه&zwnj;ی ڕه&zwnj;زیلی ده&zwnj;که&zwnj;ن و فه&zwnj;رمان ده&zwnj;ده&zwnj;ن به&zwnj;خه&zwnj;ڵکی که ڕه&zwnj;زیل بن و ئه&zwnj;و شتانه&zwnj;ی خوا له زیاده فه&zwnj;زڵی خۆی پێی به&zwnj;خشیوون (له ماڵ و سامان و زانستی) ده&zwnj;یشارنه&zwnj;وه و (نایه&zwnj;ڵن که&zwnj;س سوودی لێوه&zwnj;ربگرێت، با ئه&zwnj;وه بزانن) که ئێمه بۆ خوانه&zwnj;ناسان سزایه&zwnj;کی سه&zwnj;رشۆڕکه&zwnj;رمان ئاماده کردووه&zwnj;.';
$t[38] = 'ئه&zwnj;وانه&zwnj;ش که ماڵ و سامانیان بۆ ڕووپامایی خه&zwnj;ڵکی ده&zwnj;به&zwnj;خشن، باوه&zwnj;ڕناهێنن نه به خوا، نه به&zwnj;رۆژی دوایی (دیاره که شه&zwnj;یتان ئه&zwnj;وانی له خشته بردووه و کردوونی به&zwnj;هاوه&zwnj;ڵی خۆی) جا ئه&zwnj;وه&zwnj;ی شه&zwnj;یتان بکاته هاوه&zwnj;ڵ و هاوده&zwnj;می خۆی (مه&zwnj;گه&zwnj;ر خوا بزانێت) چ ناپوخت و ناله&zwnj;بارێکی کردۆته هاوه&zwnj;ڵی خۆی.';
$t[39] = 'چ زیانێکیان لێده&zwnj;که&zwnj;وت؟ چیان به&zwnj;سه&zwnj;ر ده&zwnj;هات؟ (ئه&zwnj;و خه&zwnj;ڵکه&zwnj;) ئه&zwnj;گه&zwnj;ر باوه&zwnj;ڕیان به خواو ڕۆژی دوایی بهێنایه و له&zwnj;و ڕزق و ڕۆزیه&zwnj;ی که خوا پێی به&zwnj;خشیوون بیانبه&zwnj;خشیایه (جگه له سوود و قازانجی بێ سنوور له هه&zwnj;ردوو دنیادا) خۆ خوا هه&zwnj;میشه&zwnj;و به&zwnj;رده&zwnj;وام ئاگای لێیانه&zwnj;و زانیاری هیه ده&zwnj;رباره&zwnj;یان.';
$t[40] = 'به&zwnj;ڕاستی خوا به&zwnj;قه&zwnj;ده&zwnj;ر تۆزقاڵێک سته&zwnj;م (له ئاده&zwnj;میزاد) ناکات، خۆ ئه&zwnj;گه&zwnj;ر چاکه&zwnj;یه&zwnj;ک هه&zwnj;بێت (به&zwnj;ره&zwnj;که&zwnj;تی به&zwnj;سه&zwnj;ردا ده&zwnj;ڕژێت و) چه&zwnj;ند به&zwnj;رابه&zwnj;ری ده&zwnj;کات و له&zwnj;لایه&zwnj;ن خۆیه&zwnj;وه پاداشتی گه&zwnj;وره&zwnj;و بێ سنوور ده&zwnj;به&zwnj;خشێت (به چاکه خوازان).';
$t[41] = 'ئینجا ده&zwnj;بێت حاڵی ئه&zwnj;و خه&zwnj;ڵکه چۆن بێت کاتێک له هه&zwnj;ر میلله&zwnj;تێک شایه&zwnj;تێکمان هێنا و تۆشمان هێنا (ئه&zwnj;ی محمد (صلی الله علیه وسلم) به&zwnj;شایه&zwnj;ت به&zwnj;سه&zwnj;ر ئه&zwnj;و خه&zwnj;ڵکه&zwnj;وه (که په&zwnj;یامی خوات پێڕاگه&zwnj;یاندوون).';
$t[42] = 'ئه&zwnj;و ڕۆژه ئه&zwnj;وانه&zwnj;ی بێ باوه&zwnj;ڕ بوون و له پێغه&zwnj;مبه&zwnj;ر یاخی بوون، ئاواته&zwnj;خوازن که زه&zwnj;وییان پێ ته&zwnj;خت بکرایه&zwnj;و (هه&zwnj;ر له&zwnj;توێی خاکدا ون بوونایه&zwnj;و زیندوو نه&zwnj;کرانایه&zwnj;ته&zwnj;وه&zwnj;) و ناتوانن هیچ گوفتارێکی په&zwnj;نهانیان له خوا بشارنه&zwnj;وه&zwnj;.';
$t[43] = 'ئه&zwnj;ی ئه&zwnj;وانه&zwnj;ی باوه&zwnj;ڕتان هێناوه نزیکی نوێژ مه&zwnj;که&zwnj;ون له&zwnj;کاتێکدا ئێوه سه&zwnj;رخۆشن ـ ئه&zwnj;مه کاتێک بوو هێشتا عه&zwnj;ره&zwnj;ق و شه&zwnj;راب حه&zwnj;رام نه&zwnj;کرابوو ـ هه&zwnj;تا ئاسه&zwnj;واری سه&zwnj;رخۆشیی نامێنێت و ده&zwnj;زانن چی ده&zwnj;ڵێن (له نوێژه&zwnj;که&zwnj;دا) و به له&zwnj;شی بێ نوێژیشه&zwnj;وه نوێژ مه&zwnj;که&zwnj;ن مه&zwnj;گه&zwnj;ر ڕێبوار بن و له سه&zwnj;فه&zwnj;ردا بن، هه&zwnj;تا خۆتان ده&zwnj;شۆن، خۆ ئه&zwnj;گه&zwnj;ر نه&zwnj;خۆش بوون یان له سه&zwnj;فه&zwnj;ردا بوون یان له&zwnj;سه&zwnj;رئاو گه&zwnj;ڕانه&zwnj;وه&zwnj;، یان له&zwnj;گه&zwnj;ڵ ئافره&zwnj;تانی نامه&zwnj;حره&zwnj;م له یه&zwnj;ککه&zwnj;ون و ئاوتان ده&zwnj;ست نه&zwnj;ده&zwnj;که&zwnj;وت (بۆ خۆشتن و ده&zwnj;ست نوێژ گرتن) ئه&zwnj;وا ته&zwnj;یه&zwnj;موم بکه&zwnj;ن به&zwnj;خاکێکی پاک و (به&zwnj;و خاکه&zwnj;) ده&zwnj;ست بهێنن به&zwnj;سه&zwnj;ر ده&zwnj;م و چاوو ده&zwnj;ستاندا، بێگومان خوا هه&zwnj;میشه و به&zwnj;رده&zwnj;وام لێ خۆشبووه&zwnj;و گوزه&zwnj;شت و چاوپۆشی هه&zwnj;یه&zwnj;.';
$t[44] = 'ئایا نه&zwnj;تڕوانیوه&zwnj;ته ئه&zwnj;وانه&zwnj;ی که شاره&zwnj;زایی هه&zwnj;ندێک له کتێبه ئاسمانیه&zwnj;کانیان دراوه&zwnj;تێ، ئه&zwnj;وانه گومڕایی ده&zwnj;کڕن و ده&zwnj;یانه&zwnj;وێت ئێوه&zwnj;ش گومڕاو سه&zwnj;رلێشێواو بن.';
$t[45] = 'خوای گه&zwnj;وره&zwnj;ش چاک دوژمنه&zwnj;کانتان ده&zwnj;ناسێت، جا ته&zwnj;نها خوا به&zwnj;سه که یارو یاوه&zwnj;ر بێت و ته&zwnj;نها خوا به&zwnj;سه پشتیوان بێت.';
$t[46] = 'هه&zwnj;ندێک له&zwnj;وانه&zwnj;ی که بوونه&zwnj;ته جوو قسه له&zwnj;جێی خۆیدا ده&zwnj;سکاریی ده&zwnj;که&zwnj;ن و جێ گۆرکێی پێده&zwnj;که&zwnj;ن و به&zwnj;ئاره&zwnj;زووی خۆیان لێکی ده&zwnj;ده&zwnj;نه&zwnj;وه (به کینه&zwnj;وگاڵته&zwnj;وه) ده&zwnj;ڵێن: بیستمان و به&zwnj;قسه&zwnj;شمان نه&zwnj;کردیت!! گوێ بگره ده&zwnj;ک گوێت لێ نه&zwnj;بێت!! (هه&zwnj;روه&zwnj;ها ده&zwnj;یان وت:) (راعنا) و ده&zwnj;میان بۆ خوار ده&zwnj;کرده&zwnj;وه تا مانایه&zwnj;کی هه&zwnj;ڵه ببه&zwnj;خشێت و ببێته ته&zwnj;عنه و ڕه&zwnj;خنه له دین، خۆ ئه&zwnj;گه&zwnj;ر به&zwnj;ڕاستی ئه&zwnj;وان بیانوتایه&zwnj;: بیستمان و گوێڕایه&zwnj;ڵین و تۆیش گوێمان بۆ بگره&zwnj;و مۆڵه&zwnj;تمان بده (تا تێبگه&zwnj;ین) چاکترو ڕاستترو به&zwnj;نرختر بوو بۆیان، به&zwnj;ڵام (چونکه پاک نین و ده&zwnj;روون نه&zwnj;خۆشن) خوا نه&zwnj;فره&zwnj;تی لێ کردوون به&zwnj;هۆی کوفریانه&zwnj;وه&zwnj;، بۆیه باوه&zwnj;ڕناهێنن که&zwnj;مێک نه&zwnj;بێت.';
$t[47] = 'ئه&zwnj;ی ئه&zwnj;وانه&zwnj;ی کتێبتان پێ به&zwnj;خشراوه باوه&zwnj;ڕ بهێنن به&zwnj;و (قورئانه&zwnj;ی) که ناردوومانه&zwnj;و به&zwnj;ڕاست دانه&zwnj;ری ئه&zwnj;وه&zwnj;ی ئێوه&zwnj;یه&zwnj;، پێش ئه&zwnj;وه&zwnj;ی (ڕۆژێک بێت) که ڕووخسارانێکی (بێ دین) پان و پلیش بکه&zwnj;ینه&zwnj;وه و ئاسه&zwnj;واری ده&zwnj;م و چاو نه&zwnj;یه&zwnj;ڵین و به&zwnj;ره&zwnj;و پشتی بکه&zwnj;ین و وه&zwnj;کو پشتی سه&zwnj;ری لێبکه&zwnj;ین، یاخود نه&zwnj;فرینیان لێبکه&zwnj;ین وه&zwnj;کو چۆن نه&zwnj;فرینمان له خه&zwnj;ڵکانی شه&zwnj;مووان کرد (ئه&zwnj;وانه&zwnj;ی که به&zwnj;فێڵ ڕاوه ماسیان کردو فه&zwnj;رمانی خوایان شکاند)، بێگومان هه&zwnj;ر فه&zwnj;رمانی خوایه که هه&zwnj;میشه&zwnj;و به&zwnj;رده&zwnj;وام ئه&zwnj;نجام دراوه&zwnj;.';
$t[48] = 'به&zwnj;ڕاستی خوا خۆش نابێت ئه&zwnj;گه&zwnj;ر هاوه&zwnj;ڵی بۆ بڕیار بدرێت، بێجگه له&zwnj;وه له هه&zwnj;ر گوناهێکی تر بیه&zwnj;وێت خۆش ده&zwnj;بێت، بۆ ئه&zwnj;و که&zwnj;سه&zwnj;ی که ده&zwnj;یه&zwnj;وێت، جا ئه&zwnj;وه&zwnj;ی هاوه&zwnj;ڵ و شه&zwnj;ریک بۆ خوا بڕیار بدات، ئه&zwnj;وه ئیتر به&zwnj;ڕاستی تاوانێکی گه&zwnj;وره&zwnj;ی هه&zwnj;ڵبه&zwnj;ستووه&zwnj;.';
$t[49] = 'ئایا نه&zwnj;تڕوانیووه&zwnj;ته ئه&zwnj;و که&zwnj;سانه&zwnj;ی خۆیان خۆیان به چاک ده&zwnj;ده&zwnj;نه قه&zwnj;ڵه&zwnj;م (ده&zwnj;ڵێن ئێمه هه&zwnj;ڵبژارده&zwnj;ی خواین) ئه&zwnj;وه وا نیه&zwnj;، به&zwnj;ڵکو خوا (به&zwnj;نده&zwnj;کانی ده&zwnj;ناسێت)و هه&zwnj;رکه&zwnj;س (شایسته بێت، په&zwnj;سه&zwnj;ندی ده&zwnj;کات)و دڵ و ده&zwnj;روونی خاوێن ده&zwnj;کات و ئه&zwnj;وانه به قه&zwnj;ده&zwnj;ر تاڵی ناوکی خورما سته&zwnj;میان لێ ناکرێت.';
$t[50] = 'سه&zwnj;رنج بده&zwnj;و ته&zwnj;ماشا بکه چۆن (خاوه&zwnj;نانی کتێبی ئاسمانی) درۆ به ده&zwnj;م خواوه ده&zwnj;که&zwnj;ن (بێباکانه ده&zwnj;ڵێن: عوزه&zwnj;یر یان مه&zwnj;سیح کوڕی خوان) ئه&zwnj;و تاوان و گوناهه&zwnj;یان به&zwnj;سه (بۆ سزادانیان چونکه تاوانێکی زۆر گه&zwnj;وره&zwnj;)ی ئاشکرایه&zwnj;.';
$t[51] = 'ئایا نه&zwnj;تڕوانیوه&zwnj;ته ئه&zwnj;وانه&zwnj;ی که به&zwnj;شێک له کتێبی ئاسمانی پێدراوه (که&zwnj;چی له&zwnj;گه&zwnj;ڵ ئه&zwnj;وه&zwnj;شدا) باوه&zwnj;ڕیان به&zwnj;فاڵچی و جادووگه&zwnj;رو بت و په&zwnj;یکه&zwnj;رو شه&zwnj;یتان و... هه&zwnj;یه و ده&zwnj;رباره&zwnj;ی ئه&zwnj;وانه&zwnj;ی که بێ باوه&zwnj;ڕن ده&zwnj;ڵێن: ئه&zwnj;وانه چاکترن و ڕێبازی ڕاستتریان گرتووه&zwnj;، تا ئه&zwnj;وانه&zwnj;ی که ئیمان و باوه&zwnj;ڕیان (به&zwnj;ئاینی ئیسلام هێناوه&zwnj;).';
$t[52] = 'ئه&zwnj;و (جووله&zwnj;کانه&zwnj;) که&zwnj;سانێکن خوا نه&zwnj;فرینی لێکردوون، که&zwnj;سێکیش خوا نه&zwnj;فرینی لێ بکات، ئه&zwnj;وه هه&zwnj;رگیز هیچ که&zwnj;ست ده&zwnj;ست ناکه&zwnj;وێت بیکه&zwnj;یته پشتیوانی.';
$t[53] = 'مه&zwnj;گه&zwnj;ر ئه&zwnj;وانه هه&zwnj;ندێک ده&zwnj;سه&zwnj;ڵاتیان به&zwnj;ده&zwnj;سته&zwnj;، تا ئه&zwnj;وکاته به&zwnj;قه&zwnj;ده&zwnj;ر تۆزقاڵێک له ناوکی خورما به&zwnj;خه&zwnj;ڵکی نه&zwnj;ده&zwnj;ن؟!';
$t[54] = 'یان هه&zwnj;ر ئه&zwnj;وه&zwnj;یه که حه&zwnj;ساده&zwnj;ت و به&zwnj;خیلی به&zwnj;خه&zwnj;ڵکی (ئیماندار) ده&zwnj;به&zwnj;ن له&zwnj;سه&zwnj;ر ئه&zwnj;وه&zwnj;ی خوا له فه&zwnj;زڵ و به&zwnj;خششی خۆی به&zwnj;شی داون (پێغه&zwnj;مبه&zwnj;ری له&zwnj;ناویاندا هه&zwnj;ڵبژاردووه&zwnj;) خۆ بێگومان ئێمه به&zwnj;نه&zwnj;وه&zwnj;ی ئیبراهیم کتێبمان به&zwnj;خشیووه (به موسا ته&zwnj;ورات و به عیسا ئینجیل و به محمدیش (صلی الله علیه وسلم) قورئان) هه&zwnj;روه&zwnj;ها له&zwnj;جۆره&zwnj;ها دانایی به&zwnj;هره&zwnj;وه&zwnj;رمان کردوون و (به هه&zwnj;ندێکیان) وه&zwnj;ک (یوسف و داود و سوله&zwnj;یمان) ده&zwnj;سه&zwnj;ڵات و پاشایه&zwnj;تیه&zwnj;کی گه&zwnj;وره&zwnj;مان به&zwnj;خشیووه&zwnj;.';
$t[55] = 'جا له&zwnj;و خه&zwnj;ڵکه هه&zwnj;یه باوه&zwnj;ڕیان به (محمد (صلی الله علیه وسلم) و به قورئان) هێناوه&zwnj;و شوێنی که&zwnj;وتوون، هه&zwnj;شیانه ڕێی لێگرتووه و پشتی تێکردووه&zwnj;، دۆزه&zwnj;خیش به&zwnj;سه (بۆ ئه&zwnj;و یاخییانه&zwnj;) که ئاماده&zwnj;یه و کڵپه&zwnj;ی سه&zwnj;ندووه&zwnj;.';
$t[56] = 'به&zwnj;ڕاستی ئه&zwnj;وانه&zwnj;ی باوه&zwnj;ڕیان نه&zwnj;هێناوه به ئایه&zwnj;ت و فه&zwnj;رمانه&zwnj;کانی ئێمه&zwnj;، له ئاینده&zwnj;دا ده&zwnj;یانسوتێنین به&zwnj;ئاگری دۆزه&zwnj;خ، هه&zwnj;موو جارێک پێستیان داوه&zwnj;شاو سووتاو هه&zwnj;ڵقرچا، پێستی تر ده&zwnj;که&zwnj;ین به&zwnj;به&zwnj;ریاندا، بۆ ئه&zwnj;وه&zwnj;ی به چاکی سزاو ئازار بچه&zwnj;ژن، به&zwnj;ڕاستی خوا هه&zwnj;میشه&zwnj;و به&zwnj;رده&zwnj;وام باڵاده&zwnj;سته و (ده&zwnj;توانێت تۆڵه له خوانه&zwnj;ناسان بسه&zwnj;نێت)، دانایه (که مۆڵه&zwnj;تیان ده&zwnj;دات به&zwnj;ڵکو بگه&zwnj;رێنه&zwnj;وه بۆ ڕێبازی دینداری).';
$t[57] = 'ئه&zwnj;وانه&zwnj;ش که باوه&zwnj;ڕیان هێناوه&zwnj;و کارو کرده&zwnj;وه&zwnj;ی چاکه ئه&zwnj;نجام ده&zwnj;ده&zwnj;ن له&zwnj;ئاینده&zwnj;یه&zwnj;کی نزیکدا ده&zwnj;یانخه&zwnj;ینه ناو باخه&zwnj;کانی به&zwnj;هه&zwnj;شته&zwnj;وه که چه&zwnj;نده&zwnj;ها ڕووبار (به&zwnj;ژێر دره&zwnj;خته&zwnj;کانیداو به به&zwnj;رده&zwnj;م کۆشکه&zwnj;کانیدا) ده&zwnj;ڕوات و له&zwnj;وێدا ژیانی هه&zwnj;میشه&zwnj;یی و به&zwnj;رده&zwnj;وام ده&zwnj;به&zwnj;نه سه&zwnj;ر، هاوڕێ له&zwnj;گه&zwnj;ڵ چه&zwnj;نده&zwnj;ها هاوسه&zwnj;ری پاک و بێگه&zwnj;ردو خاوێن و پاکیزه&zwnj;دا، هه&zwnj;روه&zwnj;ها ده&zwnj;یانخه&zwnj;ینه ژێر سایه&zwnj;ی سێبه&zwnj;ری جوان و ڕازاوه&zwnj;ی هه&zwnj;میشه&zwnj;یی یه&zwnj;وه (نه گه&zwnj;رمایان ده&zwnj;بێت، نه سه&zwnj;رما).';
$t[58] = 'بێگومان خوا فه&zwnj;رمانتان پێده&zwnj;دات که هه&zwnj;موو ئه&zwnj;مانه&zwnj;ته&zwnj;کان (سپارده&zwnj;کان) بگه&zwnj;ڕێننه&zwnj;وه ده&zwnj;ست خاوه&zwnj;نه&zwnj;کانیان و هه&zwnj;ر کاتێکیش داوه&zwnj;ریتان کرد له&zwnj;نێوان خه&zwnj;ڵکیدا، دادپه&zwnj;روه&zwnj;رانه فه&zwnj;رمانڕه&zwnj;وایی بکه&zwnj;ن، چونکه به&zwnj;ڕاستی خوا به&zwnj;جوانی و چاکی و ته&zwnj;واوی ئامۆژگاری و فه&zwnj;رمانتان پێده&zwnj;دات، به&zwnj;ڕاستی خوا هه&zwnj;میشه&zwnj;و به&zwnj;رده&zwnj;وام بیسه&zwnj;ر و بینایه&zwnj;.';
$t[59] = 'ئه&zwnj;ی ئه&zwnj;وانه&zwnj;ی باوه&zwnj;ڕتان هێناوه&zwnj;، فه&zwnj;رمانبه&zwnj;ردارو گوێڕایه&zwnj;ڵی خواو پێغه&zwnj;مبه&zwnj;رو فه&zwnj;رمانڕه&zwnj;واکان بکه&zwnj;ن که له خودی خۆتانن (له بیروباوه&zwnj;ڕو پابه&zwnj;ندیدا)، خۆ ئه&zwnj;گه&zwnj;ر له شتێکدا بوو به&zwnj;کێشه&zwnj;تان ده&zwnj;رباره&zwnj;ی حوکمی خوایی، ئه&zwnj;وه ئه&zwnj;و کێشه&zwnj;یه بگێڕنه&zwnj;وه بۆ لای خواو پێغه&zwnj;مبه&zwnj;ره&zwnj;که&zwnj;ی (صلی الله علیه وسلم) (که قورئان و سوننه&zwnj;ت ده&zwnj;گه&zwnj;یه&zwnj;نێت)، ئه&zwnj;گه&zwnj;ر ئێوه باوه&zwnj;ڕتان به&zwnj;خواو به ڕۆژی قیامه&zwnj;ت هه&zwnj;یه&zwnj;، (ڕه&zwnj;فتارکردنتان) به&zwnj;و شێوه&zwnj;یه چاکترین و جوانترین سه&zwnj;رچاوه&zwnj;یه بۆ چاره&zwnj;سه&zwnj;ری کێشه&zwnj;کانتان.';
$t[60] = 'ئایا نه&zwnj;تڕوانیووه&zwnj;ته ئه&zwnj;و (دوورووانه&zwnj;ی) لافی ئه&zwnj;وه لێ ده&zwnj;ده&zwnj;ن گوایه به&zwnj;ڕاستی باوه&zwnj;ڕیان هێناوه به&zwnj;وه&zwnj;ی که بۆ تۆ ڕه&zwnj;وانه&zwnj;کراوه و به&zwnj;وه&zwnj;ش که پێش تۆ ڕه&zwnj;وانه&zwnj;کراوه (بۆ پێغه&zwnj;مبه&zwnj;ران)! که&zwnj;چی ده&zwnj;یانه&zwnj;وێت ڕووبکه&zwnj;نه زۆردارو جادووگه&zwnj;رو خوانه&zwnj;ناسان تا دادوه&zwnj;ریان له نێواندا بکه&zwnj;ن و چاره&zwnj;سه&zwnj;ری کێشه&zwnj;کانیان بکه&zwnj;ن، له&zwnj;کاتێکدا به ڕاستی فه&zwnj;رمانیان پێدراوه که باوه&zwnj;ڕیان به (تاغوت) نه&zwnj;بێت، شه&zwnj;یتانیش ده&zwnj;یه&zwnj;وێت گومڕایان بکات به&zwnj;گومڕاییه&zwnj;کی دوورو بێ سنوور.';
$t[61] = 'کاتێکیش پێیان بوترێ: وه&zwnj;رن بۆ لای ئه&zwnj;و (قورئانه&zwnj;ی) که خوا ناردوویه&zwnj;تی یه خواره&zwnj;وه و بۆ لای پێغه&zwnj;مبه&zwnj;ره&zwnj;که&zwnj;ی (بۆ چاره&zwnj;سه&zwnj;ری کێشه&zwnj;کانتان) که&zwnj;چی ده&zwnj;بینی دووڕووه&zwnj;کان پشتت تێده&zwnj;که&zwnj;ن و ڕووت لێ وه&zwnj;رده&zwnj;گێڕن به&zwnj;ته&zwnj;واوی.';
$t[62] = 'ئاخۆ ئه&zwnj;وانه حاڵیان چۆن بێت کاتێک به&zwnj;هۆی ڕه&zwnj;فتاری نادروستیانه&zwnj;وه به&zwnj;ڵایه&zwnj;ک دووچاریان ده&zwnj;بێت، که&zwnj;چی له&zwnj;وه&zwnj;ودوا (که په&zwnj;نایان نامێنێت) هاوار بۆ تۆ ده&zwnj;هێنێن و سوێند به&zwnj;خوا ده&zwnj;خۆن: له&zwnj;و کاره&zwnj;ماندا مه&zwnj;به&zwnj;ستمان ته&zwnj;نها چاکه&zwnj;کاریی و ڕێکخستن و ئاشتی بوو.';
$t[63] = 'ئا ئه&zwnj;وانه که&zwnj;سانێکن خوا ده&zwnj;زانێت چی له دڵ و ده&zwnj;روونیاندا حه&zwnj;شاردراوه&zwnj;، که&zwnj;واته ڕه&zwnj;فتاری ئه&zwnj;وانه پشتگوێ بخه&zwnj;، ئامۆژگاریان بکه و قسه&zwnj;ی به&zwnj;پێزو به&zwnj;توێکڵی وایان پێ بڵێ که به&zwnj;قوڵایی ده&zwnj;روونیاندا بچێته خواره&zwnj;وه و بیانهه&zwnj;ژێنێت.';
$t[64] = 'هیچ فرستاده&zwnj;یه&zwnj;کمان نه&zwnj;ناردوه مه&zwnj;گه&zwnj;ر ته&zwnj;نها بۆ ئه&zwnj;وه&zwnj;ی گوێڕایه&zwnj;ڵی بکرێت به&zwnj;فه&zwnj;رمانی خوا، خۆ ئه&zwnj;گه&zwnj;ر ئه&zwnj;و خه&zwnj;ڵکه به&zwnj;ڕاستی کاتێک سته&zwnj;میان له خۆیان کرد (به لادان له شه&zwnj;ریعه&zwnj;ت) بهاتنایه بۆ لای تۆ و داوای لێخۆشبوونیان له خوای گه&zwnj;وره بکردایه&zwnj;، پێغه&zwnj;مبه&zwnj;ریش داوای لێخۆشبوونی بۆ بکردنایه&zwnj;، ئه&zwnj;وه بێگومان ئه&zwnj;و کاته ده&zwnj;یانزانی که خوا ته&zwnj;وبه و په&zwnj;شیمانی وه&zwnj;رگره&zwnj;و میهره&zwnj;بان و دلۆڤانه (ئه&zwnj;گه&zwnj;ر به&zwnj;ڕاستیان بوایه&zwnj;).';
$t[65] = 'نه&zwnj;خێر، سوێند به په&zwnj;روه&zwnj;ردگارت ئه&zwnj;وانه&zwnj;ی (لافی بڕوا لێده&zwnj;ده&zwnj;ن) بڕوا ناهێنن هه&zwnj;تا له هه&zwnj;موو کێشه&zwnj;یه&zwnj;کدا که ڕووده&zwnj;دا له نێوانیاندا تۆ نه&zwnj;که&zwnj;ن به دادوه&zwnj;رو گوێڕایه&zwnj;ڵی تۆ نه&zwnj;که&zwnj;ن، دوای ئه&zwnj;وه&zwnj;ش نابێت له دڵ و ده&zwnj;روونیاندا هیچ ناڕه&zwnj;زاییه&zwnj;ک دروست ببێت به&zwnj;رامبه&zwnj;ر ئه&zwnj;وه&zwnj;ی که داوه&zwnj;ریت له&zwnj;سه&zwnj;ر کردووه و ده&zwnj;بێت به&zwnj;ته&zwnj;واویی ته&zwnj;سلیم بن و ڕازیی بن.';
$t[66] = 'خۆ ئه&zwnj;گه&zwnj;ر به&zwnj;ڕاستی ئێمه پێویستمان بکردایه له&zwnj;سه&zwnj;ریان که خۆتان بکوژن، یان له وڵات و نیشتمانتان ده&zwnj;رچن ئه&zwnj;وه&zwnj;یان نه&zwnj;ده&zwnj;کرد، مه&zwnj;گه&zwnj;ر که&zwnj;مێکیان نه&zwnj;بێت (چونکه به&zwnj;جێ هێنانی فرمانی خوا به&zwnj;بێ چه&zwnj;ن و چوون پیشه&zwnj;ی به&zwnj;نده ڕاسته&zwnj;قینه&zwnj;کانه&zwnj;، ئێمه&zwnj;ش ئه&zwnj;و داخوازیه&zwnj;مان لێ نه&zwnj;کردوون) جا ئه&zwnj;گه&zwnj;ر به&zwnj;ڕاستی ئه&zwnj;وانه به&zwnj;گوێره&zwnj;ی ئامۆژگاری و فرمانی ئێمه ڕه&zwnj;فتاریان بکردایه&zwnj;، ئه&zwnj;وه چاکتر بوو بۆیان و دامه&zwnj;زراوترو چه&zwnj;سپاوتر ده&zwnj;بوون له&zwnj;سه&zwnj;ر حه&zwnj;ق و ڕاستی.';
$t[67] = 'جا ئه&zwnj;وکاته بێگومان له&zwnj;لایه&zwnj;ن ئێمه&zwnj;وه پاداشتی گه&zwnj;وره&zwnj;و بێ سنوورمان پێده&zwnj;به&zwnj;خشین...';
$t[68] = 'به&zwnj;ته&zwnj;واویش هیدایه&zwnj;ت و ڕێنموویمان ده&zwnj;کردن بۆ ڕێگه&zwnj;و ڕێبازی ڕاست و دروست.';
$t[69] = 'ئه&zwnj;وه&zwnj;ی فه&zwnj;رمانبه&zwnj;رداری خواو پێغه&zwnj;مبه&zwnj;ر بکات، جا ئه&zwnj;وانه له&zwnj;گه&zwnj;ڵ ئه&zwnj;و که&zwnj;سانه&zwnj;دا ده&zwnj;بن (له به&zwnj;هه&zwnj;شتی به&zwnj;ریندا) که&zwnj;خوا نازونیعمه&zwnj;تی ڕژاندووه به&zwnj;سه&zwnj;ریاندا له&zwnj;پێغه&zwnj;مبه&zwnj;ران و ڕاستگۆیان و شه&zwnj;هیدان و پیاوچاکان، ئای که ئه&zwnj;وانه هاوه&zwnj;ڵ و هاوڕێ و هاوده&zwnj;مێکی چاک و بێ وێنه&zwnj;ن!';
$t[70] = 'ئه&zwnj;و ڕێزو به&zwnj;هره گه&zwnj;وره&zwnj;یه هه&zwnj;ر له&zwnj;لایه&zwnj;ن خواوه&zwnj;یه (بۆ به&zwnj;خته&zwnj;وه&zwnj;رانه&zwnj;)، خوا خۆی به&zwnj;سه که زانایه به&zwnj;هه&zwnj;موو شتێک.';
$t[71] = 'ئه&zwnj;ی ئه&zwnj;وانه&zwnj;ی که باوه&zwnj;ڕتان هێناوه ئه&zwnj;وه&zwnj;ی خۆتانی پێده&zwnj;پارێزن بیگرنه ده&zwnj;ست و ئاماده باش بن، ئینجا ده&zwnj;سته ده&zwnj;سته یان هه&zwnj;مووتان یه&zwnj;ک پارچه (به&zwnj;ره&zwnj;و به&zwnj;ره&zwnj;کانی جه&zwnj;نگ) هه&zwnj;ڵمه&zwnj;ت به&zwnj;رن و هێرش ببه&zwnj;ن. (هه&zwnj;رکاتێک له&zwnj;لایه&zwnj;ن سه&zwnj;رکردایه&zwnj;تیه&zwnj;کی متمانه پێکراوه&zwnj;وه داواتان لێکرا).';
$t[72] = 'سوێند بێت (ده&zwnj;سته&zwnj;یه&zwnj;ک دووڕوو هه&zwnj;ن) له&zwnj;ناوتاندا به&zwnj;ڕاستی خۆیان ده&zwnj;خڵافێنن و سستی ده&zwnj;که&zwnj;ن، جا ئه&zwnj;گه&zwnj;ر به&zwnj;ڵایه&zwnj;کتان تووش بێت (هه&zwnj;ریه&zwnj;ک له&zwnj;وانه&zwnj;) ده&zwnj;ڵێت: به&zwnj;ڕاستی خوا چاکی بۆ کردم که له&zwnj;گه&zwnj;ڵ ئه&zwnj;واندا ئاماده&zwnj;ی (ئه&zwnj;و جه&zwnj;نگه&zwnj;) نه&zwnj;بووم.';
$t[73] = 'سوێند بێت ئه&zwnj;گه&zwnj;ر فه&zwnj;زڵ و ده&zwnj;ستکه&zwnj;وتێکتان له&zwnj;خواوه ده&zwnj;ستکه&zwnj;وت به&zwnj;ڕاشکاوی ده&zwnj;ڵێت ـ هه&zwnj;ر وه&zwnj;ک له&zwnj;نێوان ئێوه و ئه&zwnj;و جۆره که&zwnj;سانه&zwnj;دا دۆستایه&zwnj;تی و ئاشنایه&zwnj;تی نه&zwnj;بوو بێت ـ خۆزگه له&zwnj;گه&zwnj;ڵ ئه&zwnj;واندا بوومایه و بۆ ئه&zwnj;وه&zwnj;ی ده&zwnj;ستکه&zwnj;وتێکی چاک و زۆر و زه&zwnj;به&zwnj;نده&zwnj;م به&zwnj;ده&zwnj;ست بهێنایه&zwnj;.';
$t[74] = 'ده&zwnj;با بجگێت له پێناوی خوادا، ئه&zwnj;وانه&zwnj;ی ژیانی دنیا ده&zwnj;فرۆشن به&zwnj;رۆژی دوایی، جا هه&zwnj;ر که&zwnj;س بجه&zwnj;نگێت له&zwnj;پێناوی خوادا ئه&zwnj;وه ئه&zwnj;گه&zwnj;ر بکوژرێت و شه&zwnj;هید ببێت، یان سه&zwnj;رکه&zwnj;وتن به&zwnj;ده&zwnj;ست بهێنێت، ئه&zwnj;وه له&zwnj;ئاینده&zwnj;دا پاداشتی گه&zwnj;وره و بێ سنووری پێده&zwnj;به&zwnj;خشین.';
$t[75] = '(ئه&zwnj;ی ئیماندارن) ئه&zwnj;وه چیتانه&zwnj;و بۆچی ناجه&zwnj;نگن له&zwnj;پێناوی ڕه&zwnj;زا&zwnj;مه&zwnj;ندی خوادا و ڕزگار کردنی لاوازکراوو چه&zwnj;وساوه&zwnj;کان، له&zwnj;و پیاوان و ئافره&zwnj;تان و منداڵانه&zwnj;ی که ده&zwnj;ڵێن: په&zwnj;روه&zwnj;ردگارا تۆ ڕزگارمان بکه له&zwnj;م شاره&zwnj;ی که خه&zwnj;ڵکه&zwnj;که&zwnj;ی سته&zwnj;مکارن و له&zwnj;لایه&zwnj;ن خۆته&zwnj;وه پشتیوان و یارمه&zwnj;تیده&zwnj;رێکمان بۆ ساز بکه&zwnj;.';
$t[76] = 'ئه&zwnj;وانه&zwnj;ی ئیماندارن (ته&zwnj;نها) له پێناوی خوادا ده&zwnj;جه&zwnj;نگن (به&zwnj;ڵام) ئه&zwnj;وانه&zwnj;ی بێ باوه&zwnj;ڕن له پێناوی سته&zwnj;مکارو زۆردار و (تاغوت)دا ده&zwnj;جه&zwnj;نگن، ده بجه&zwnj;نگن دژی یاوه&zwnj;رانی شه&zwnj;یتان (چونکه&zwnj;) به&zwnj;ڕاستی پیلان و ته&zwnj;ڵه&zwnj;که&zwnj;ی شه&zwnj;یتان پووچ و لاوازه&zwnj;.';
$t[77] = 'ئایا نه&zwnj;تڕوانیووه&zwnj;ته ئه&zwnj;وانه&zwnj;ی که پێیان وترا: ده&zwnj;ست بپارێزن کاتی جه&zwnj;نگ نیه&zwnj;! به&zwnj;ڵکو هه&zwnj;ر نوێژ بکه&zwnj;ن و زه&zwnj;کات بده&zwnj;ن، که&zwnj;چی کاتێک جه&zwnj;نگیان بۆ بڕیاردرا، ده&zwnj;سته&zwnj;یه&zwnj;کیان له خه&zwnj;ڵکی ده&zwnj;ترسان به&zwnj;قه&zwnj;ده&zwnj;ر ئه&zwnj;وه&zwnj;ی که له خوا ده&zwnj;ترسن، یاخود زیاتریش و ده&zwnj;یانوت: خوایه تۆ بۆچی جه&zwnj;نگت له&zwnj;سه&zwnj;ر فه&zwnj;رزکردین، خۆزگه بۆ ماوه&zwnj;یه&zwnj;ک دوات ده&zwnj;خستین، به&zwnj;وانه بڵێ: ڕابواردنی ژیانی دنیا زۆر که&zwnj;م و کورته&zwnj;، ژیانی قیامه&zwnj;ت چاکترو خۆشترو نه&zwnj;بڕاوه&zwnj;یه بۆ ئه&zwnj;و که&zwnj;سه&zwnj;ی له خوا ده&zwnj;ترسێت و پارێزکاره&zwnj;، له&zwnj;وێ به&zwnj;قه&zwnj;ده&zwnj;ر تاڵی ناوکی خورمایه&zwnj;ک سته&zwnj;متان لێناکرێت.';
$t[78] = '(دڵنیا بن) له هه&zwnj;ر کوێ بن مردن یه&zwnj;خه&zwnj;تان پێ ده&zwnj;گرێت، با له&zwnj;ناو چه&zwnj;نده&zwnj;ها قه&zwnj;ڵاو شوێنی سه&zwnj;خت و قایمیشدا بن، (جا دووڕووه&zwnj;کان) ئه&zwnj;گه&zwnj;ر خێریان بێته ڕێ ده&zwnj;ڵێن: ئه&zwnj;مه له لایه&zwnj;ن خواوه&zwnj;یه و ئه&zwnj;گه&zwnj;ر توشی ناخۆشی و زیانێک بێن (به پێغه&zwnj;مبه&zwnj;ر (صلی الله علیه وسلم) ده&zwnj;ڵێن: ئه&zwnj;مه&zwnj;یان به&zwnj;هۆی تۆوه&zwnj;یه&zwnj;، پێیان بڵێ: هه&zwnj;ر هه&zwnj;مووی (هه&zwnj;رچی پێش دێت) له&zwnj;لایه&zwnj;ن خواوه&zwnj;یه و (بۆ تاقی کردنه&zwnj;وه&zwnj;یه&zwnj;) ئه&zwnj;وه بۆچی ئه&zwnj;و خه&zwnj;ڵکه (باوه&zwnj;ڕ لاوازه&zwnj;) هیچ شتێک تێناگه&zwnj;ن و له ڕاستیه&zwnj;کان حاڵیی نابن.';
$t[79] = '(ڕاسته&zwnj;) که هه&zwnj;رچی بۆ تۆ (بۆ هه&zwnj;رکه&zwnj;سێک) پێش دێت له خێرو چاکه له خواوه&zwnj;یه و هه&zwnj;رچی ناخۆشیه&zwnj;کیشت بۆ پێش دێت ده&zwnj;ستی خۆتی تیایه (هه&zwnj;رچه&zwnj;نده به&zwnj;بێ ویستی خوا ڕوو نادات، به&zwnj;ڵام پاداشتت هه&zwnj;یه ئه&zwnj;گه&zwnj;ر خۆگر بێت) و ئێمه تۆمان ناردووه (ئه&zwnj;ی محمد (صلی الله علیه وسلم) بۆسه&zwnj;ر خه&zwnj;ڵی به&zwnj;پێغه&zwnj;مبه&zwnj;رایه&zwnj;تی (تا بیرو بۆچوونیان ڕاست بکه&zwnj;یته&zwnj;وه&zwnj;)، خوایش به&zwnj;سه که شایه&zwnj;ته بۆ ئه&zwnj;و مه&zwnj;به&zwnj;سته&zwnj;.';
$t[80] = 'ئه&zwnj;وه&zwnj;ی فه&zwnj;رمانبه&zwnj;رداری پێغه&zwnj;مبه&zwnj;ر (صلی الله علیه وسلم) بکات، ئه&zwnj;وه به&zwnj;ڕاستی فه&zwnj;رمانبه&zwnj;رداری خوای کردووه&zwnj;، ئه&zwnj;وه&zwnj;ش پشتی هه&zwnj;ڵکردووه (ئه&zwnj;وه خۆی زه&zwnj;ره&zwnj;رمه&zwnj;ند ده&zwnj;بێت) خۆ ئێمه تۆمان نه&zwnj;ناردوه تا ببیته چاودێر به&zwnj;سه&zwnj;ر ئه&zwnj;وانه&zwnj;وه&zwnj;.';
$t[81] = '(دووڕووه&zwnj;کان کاتێک لای تۆن) ده&zwnj;ڵێن: ملکه&zwnj;چ و فه&zwnj;رمانبه&zwnj;ردارین، به&zwnj;ڵام کاتێک لای تۆ هه&zwnj;ستان و دوورکه&zwnj;وتنه&zwnj;وه&zwnj;، ده&zwnj;سته&zwnj;یه&zwnj;کیان قسه&zwnj;کانی تۆ له شه&zwnj;ودا ده&zwnj;گۆڕن و ئه&zwnj;وه ناڵێن که تۆ وتوته&zwnj;، جا ئه&zwnj;وه&zwnj;ی شه&zwnj;و هه&zwnj;ڵی ده&zwnj;به&zwnj;ستن و (پیلانی بۆ ده&zwnj;کێشن) خوا تۆماری ده&zwnj;کات، که&zwnj;واته گوێ به&zwnj;وانه مه&zwnj;ده و پشت به خوا ببه&zwnj;سته ته&zwnj;نها خوات به&zwnj;سه که یارو یاوه&zwnj;رت بێت.';
$t[82] = 'ئه&zwnj;وه بۆ ئه&zwnj;وانه سه&zwnj;رنجی قورئان ناده&zwnj;ن و لێکی ناده&zwnj;نه&zwnj;وه&zwnj;و لێی وردنابنه&zwnj;وه&zwnj;؟ خۆ ئه&zwnj;گه&zwnj;ر له لایه&zwnj;ن که&zwnj;سێکی تره&zwnj;وه بوایه جگه&zwnj;له خوا بێگومان جیاوازی و دژایه&zwnj;تیه&zwnj;کی زۆریان تێدا ده&zwnj;دۆزیه&zwnj;وه&zwnj;.';
$t[83] = '(لێره&zwnj;دا خوا ڕه&zwnj;خنه ده&zwnj;گرێت له هه&zwnj;ندێک ئیمانداران): هه&zwnj;رکاتێک هه&zwnj;واڵێکیان بۆ بێت ده&zwnj;رباره&zwnj;ی ئاسایش و هێمنایه&zwnj;تی، یاخود ترس و بیم، بڵاوی ده&zwnj;که&zwnj;نه&zwnj;وه (بیر له سه&zwnj;رئه&zwnj;نجام ناکه&zwnj;نه&zwnj;وه&zwnj;)، خۆ ئه&zwnj;گه&zwnj;ر ئه&zwnj;و هه&zwnj;واڵه&zwnj;یان بڵاو نه&zwnj;کردایه&zwnj;ته&zwnj;وه و بیانگێڕایه&zwnj;ته&zwnj;وه بۆلای پێغه&zwnj;مبه&zwnj;رو (صلی الله علیه وسلم)، یان بۆ لای کاربه&zwnj;ده&zwnj;ستانێک که له خۆیانن، ئه&zwnj;وه ئه&zwnj;وانه&zwnj;ی که&zwnj;له نهێنی هه&zwnj;واڵه&zwnj;کان تێده&zwnj;گه&zwnj;ن و ده&zwnj;کۆڵنه&zwnj;وه&zwnj;، نهێنی و ڕازه&zwnj;کانیان لێ ده&zwnj;رده&zwnj;هێنا، جا ئه&zwnj;گه&zwnj;ر فه&zwnj;زڵ و چاودێری خواو ڕه&zwnj;حمه&zwnj;ت و میهره&zwnj;بانیه&zwnj;که&zwnj;ی نه&zwnj;بوایه شوێنی شه&zwnj;یتان ده&zwnj;که&zwnj;وتن، جگه له که&zwnj;مێکتان.';
$t[84] = '(ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر (صلی الله علیه وسلم) بجه&zwnj;نگه له پێناوی خوادا، چونکه ئه&zwnj;و ئه&zwnj;رکه هه&zwnj;ر له&zwnj;سه&zwnj;رشانی خۆته&zwnj;، (واته&zwnj;: هه&zwnj;تا ئه&zwnj;گه&zwnj;ر ته&zwnj;نها خۆیشت بیت)، هه&zwnj;روه&zwnj;ها هانی ئیماندارانیش بده (بۆ به&zwnj;شداری له شه&zwnj;ڕ&zwnj;ه&zwnj;کاندا) ئومێده خوای گه&zwnj;وره هێز له بێ باوه&zwnj;ڕان بسه&zwnj;نێته&zwnj;وه و به&zwnj;ریان بگرێت و زاڵتان بکات به&zwnj;سه&zwnj;ریاندا، چونکه خوا هێزو ده&zwnj;سه&zwnj;ڵاتی زۆرتره (به&zwnj;ڵکو به&zwnj;راورد ناکرێت) و به&zwnj;توندی و سه&zwnj;ختی تۆڵه&zwnj;یان لێده&zwnj;سێنێت.';
$t[85] = 'ئه&zwnj;وه&zwnj;ی تکایه&zwnj;کی باش بکات، خه&zwnj;ڵک هان بدات بۆ چاکه&zwnj;خوازی، (له نێوان خه&zwnj;ڵکدا هه&zwnj;وڵی ته&zwnj;بایی بدات)، ئه&zwnj;وه به&zwnj;شێک له&zwnj;و پاداشتی چاکه&zwnj;یه&zwnj;ی بۆ هه&zwnj;یه&zwnj;و له&zwnj;و خێره&zwnj;دا به&zwnj;شداره (که ڕۆڵی تیادا هه&zwnj;یه&zwnj;) هه&zwnj;روه&zwnj;ها ئه&zwnj;وه&zwnj;ش که تکایه&zwnj;کی خراپ ده&zwnj;کات و ڕۆڵێکی خراپ ده&zwnj;بینێت و هه&zwnj;وڵی فیتنه به&zwnj;رپا کردن ده&zwnj;دات و هانده&zwnj;رێتی، ئه&zwnj;وه ئه&zwnj;ویش به&zwnj;شێکی مسۆگه&zwnj;ری هه&zwnj;یه له سزاو لێپرسینه&zwnj;وه&zwnj;، خوایش هه&zwnj;میشه&zwnj;و به&zwnj;رده&zwnj;وام ئاگاو چاودێره به&zwnj;سه&zwnj;ر هه&zwnj;موو شتێکدا.';
$t[86] = 'هه&zwnj;رکاتێک سڵاو و چاک و چۆنیه&zwnj;کتان له&zwnj;گه&zwnj;ڵدا کرا ئه&zwnj;وه به&zwnj;جوانتر له سڵاوه&zwnj;که&zwnj;ی ئه&zwnj;و یاخود (هیچ نه&zwnj;بێت) وه&zwnj;کو خۆی وه&zwnj;ڵام بده&zwnj;نه&zwnj;وه&zwnj;، بێگومان خوا له هه&zwnj;موو شتێک ده&zwnj;پرسێته&zwnj;وه&zwnj;.';
$t[87] = '(الله&zwnj;) خوایه&zwnj;که بێجگه له&zwnj;و هیج خوایه&zwnj;کی تر نیه (شایسته&zwnj;ی په&zwnj;رستن بێت)سوێند به خوا له ڕۆژی قیامه&zwnj;تدا کۆتانده&zwnj;کاته&zwnj;وه و هیچ شک و گومانێکی تێدا نی یه&zwnj;، جا کێ هه&zwnj;یه قسه و گوفتاری له فه&zwnj;رمایش و فه&zwnj;رمووده&zwnj;ی خوا ڕاستترو دروستتر بێت؟';
$t[88] = 'جا ئه&zwnj;وه چیتانه (ئه&zwnj;ی ئیمانداران) ده&zwnj;رباره&zwnj;ی دووڕووه&zwnj;کان بوون به دوو ده&zwnj;سته&zwnj;وه&zwnj;؟! له کاتێکدا خوا (سه&zwnj;ر شۆڕی کردوون) و وه&zwnj;ری گێڕاونه&zwnj;ته&zwnj;وه بۆ بێ باوه&zwnj;ڕی به&zwnj;هۆی ئه&zwnj;و کرده&zwnj;وانه&zwnj;ی که کردوویانه&zwnj;، ئایا ده&zwnj;تانه&zwnj;وێت (دڵی) که&zwnj;سێک هیدایه&zwnj;ت و ڕێنموویی بکه&zwnj;ن که خوا گومڕای کردووه&zwnj;؟! (به&zwnj;هۆی باوه&zwnj;ڕی دانه&zwnj;مه&zwnj;زراو و دڵ و ده&zwnj;روونی نه&zwnj;خۆشیه&zwnj;وه&zwnj;)، بێگومان ئه&zwnj;وه&zwnj;ی خوا گومڕای بکات (به&zwnj;هۆی کارو کرده&zwnj;وه&zwnj;ی ناپه&zwnj;سه&zwnj;ندیه&zwnj;وه&zwnj;) ئه&zwnj;وه هه&zwnj;رگیز ڕێگه&zwnj;یه&zwnj;کت ده&zwnj;ست ناکه&zwnj;وێت بۆ ڕێنموویی کردنی.';
$t[89] = '(ئه&zwnj;و دووڕووانه&zwnj;) ئاواته&zwnj;خوازن که ئێوه&zwnj;ش بێ باوه&zwnj;ڕ بن وه&zwnj;ک خۆیان بێ باوه&zwnj;ڕ بوون، ئه&zwnj;و کاته (هه&zwnj;ردوولاتان) یه&zwnj;کسان ده&zwnj;بن له بێ باوه&zwnj;ڕیدا، که&zwnj;واته له&zwnj;وانه پشتیوان و یاوه&zwnj;ر مه&zwnj;گرن هه&zwnj;تا کۆچ ده&zwnj;که&zwnj;ن له&zwnj;پێناوی خوادا، خۆ ئه&zwnj;گه&zwnj;ر یاخی بوون و پشتیان تێکردن، ئێوه بیانگرن و بیانکوژن له هه&zwnj;ر کوێدا ئه&zwnj;وانه&zwnj;تان ده&zwnj;ستکه&zwnj;وت، هه&zwnj;رگیز نه&zwnj;که&zwnj;ن ئه&zwnj;وانه بکه&zwnj;نه یاوه&zwnj;رو هاوکار...';
$t[90] = 'جگه له&zwnj;وانه&zwnj;ی که په&zwnj;نا ده&zwnj;به&zwnj;نه به&zwnj;ر تیره و هۆزێک په&zwnj;یمان له&zwnj;نێوان ئێوه و ئه&zwnj;واندا به&zwnj;ستراوه&zwnj;، یاخود هاتبن بۆ لای ئێوه له&zwnj;کاتێکدا دڵیان ته&zwnj;نگ و بێزار بێت له&zwnj;وه&zwnj;ی جه&zwnj;نگتان له&zwnj;گه&zwnj;ڵ بکه&zwnj;ن یان دژی تیره و هۆزه&zwnj;که&zwnj;ی خۆیان بجه&zwnj;نگن، خۆ ئه&zwnj;گه&zwnj;ر خوا بیویستایه ئه&zwnj;وانی زاڵ ده&zwnj;کرد به&zwnj;سه&zwnj;رتانداو دژتان ده&zwnj;جه&zwnj;نگان، جا ئه&zwnj;گه&zwnj;ر ئه&zwnj;وانه دووره په&zwnj;رێزو که&zwnj;نارگیر بوون له ئێوه و جه&zwnj;نگیان له&zwnj;گه&zwnj;ڵ نه&zwnj;کردن و ڕێبازی ئاشتیان گرته&zwnj;به&zwnj;رو ملکه&zwnj;چ بوون، ئه&zwnj;وه خوا هیچ ڕێگه&zwnj;یه&zwnj;کی دانه&zwnj;ناوه بۆ ئێوه به&zwnj;سه&zwnj;ر ئه&zwnj;وانه&zwnj;وه (که شه&zwnj;ڕیان پێ بفرۆشن بیانکوژن یان به&zwnj;دیل بیانگرن).';
$t[91] = '(هه&zwnj;روه&zwnj;ها) چه&zwnj;ند که&zwnj;سانێکی ترتان ده&zwnj;ستده&zwnj;که&zwnj;وێت که ده&zwnj;یانه&zwnj;وێت له ئێوه&zwnj;ش و له هۆز و قه&zwnj;ومی خۆشیان ئه&zwnj;مین بن، هه&zwnj;ر کاتێک بانگ کرابنه&zwnj;وه بۆ ئاشووبنانه&zwnj;وه درێغیان نه&zwnj;کردووه و ڕۆچوون تیایدا و چاکتر تێهه&zwnj;ڵچوونه&zwnj;ته&zwnj;وه&zwnj;، جا ئه&zwnj;گه&zwnj;ر ئه&zwnj;وانه دووره په&zwnj;رێزیان نه&zwnj;گرت لێتان و که&zwnj;نارگیر نه&zwnj;بوون و ڕێبازی ئاشتیان نه&zwnj;گرته&zwnj;به&zwnj;رو ده&zwnj;ستیان نه&zwnj;کێشایه&zwnj;وه له جه&zwnj;نگ کردن له&zwnj;گه&zwnj;ڵتان، ئه&zwnj;و کاته بیانگرن و بیانکوژن له هه&zwnj;ر کوێ ده&zwnj;ستان ڕۆیی به&zwnj;سه&zwnj;ریاندا، ئا ئه&zwnj;وانه گێڕاومانه بۆتان به&zwnj;سه&zwnj;ریانه&zwnj;وه به&zwnj;ڵگه&zwnj;یه&zwnj;کی ڕوون و ئاشکرا له&zwnj;سه&zwnj;ر (ڕاست و ڕه&zwnj;وایی کوشتن و به&zwnj;دیل کردنیان).';
$t[92] = 'بۆ هیچ ئیماندارێک نیه ئیماندارێکی تر بکوژێت مه&zwnj;گه&zwnj;ر به هه&zwnj;ڵه&zwnj;، خۆ ئه&zwnj;گه&zwnj;ر که&zwnj;سێکی ئیماندار به هه&zwnj;ڵه ئیماندارێک بکوژێت، ئه&zwnj;وه ده&zwnj;بێت به&zwnj;نده&zwnj;یه&zwnj;کی ئیماندار ئازاد بکات و خوێن باییه&zwnj;که&zwnj;ی بدات به که&zwnj;سوکاری کوژراوه&zwnj;که (وه&zwnj;ک میراتی دابه&zwnj;شی بکه&zwnj;ن) مه&zwnj;گه&zwnj;ر لێی ببوورن و له خوێنه&zwnj;که&zwnj;ی خۆش ببن، خۆ ئه&zwnj;گه&zwnj;ر کوژراوه&zwnj;که له ناو قه&zwnj;وم و ده&zwnj;سته&zwnj;یه&zwnj;کدا بوو دوژمنی ئێوه بوون، به&zwnj;ڵام ئه&zwnj;و (کوژراوه&zwnj;) ئیماندار بوو، با (بکوژه&zwnj;که&zwnj;) به&zwnj;نده&zwnj;یه&zwnj;کی ئیماندار ئازادبکات و ئه&zwnj;گه&zwnj;ر ئیمانداره کوژراوه&zwnj;که له&zwnj;ناو قه&zwnj;وم و ده&zwnj;سته&zwnj;یه&zwnj;ک بوو که په&zwnj;یمان له&zwnj;نێوانتاندا هه&zwnj;بوو، ده&zwnj;بێت خوێنبایی بدرێت به&zwnj;که&zwnj;سوکاری و به&zwnj;نده&zwnj;یه&zwnj;کی ئیمانداریش ئازاد بکرێت، (خۆ ئه&zwnj;گه&zwnj;ر بکوژه&zwnj;که&zwnj;) ئه&zwnj;وه&zwnj;ی بۆ نه&zwnj;لواو ده&zwnj;ستی نه&zwnj;که&zwnj;وت (به&zwnj;نده ئازاد بکات و به&zwnj;ندایه&zwnj;تی باوی نه&zwnj;مابوو ماڵ و سامانیشی نه&zwnj;بوو) ئه&zwnj;وه ده&zwnj;بێت دوومانگ له&zwnj;سه&zwnj;ریه&zwnj;ک به&zwnj;ڕۆژوو بێت، ئه&zwnj;مه بڕیارێکه بۆ ڕاستی و دروستی ته&zwnj;وبه و گه&zwnj;ڕانه&zwnj;وه&zwnj;که&zwnj;ی، ئه&zwnj;مه&zwnj;ش له&zwnj;لایه&zwnj;ن خواوه&zwnj;یه&zwnj;، بێگومان خوای گه&zwnj;وره هه&zwnj;میشه و به&zwnj;رده&zwnj;وام زاناو دانایه&zwnj;.';
$t[93] = 'هه&zwnj;ر که&zwnj;سێک به&zwnj;ده&zwnj;ستی ئه&zwnj;نقه&zwnj;ست ئیماندارێک بکوژێت (به&zwnj;حه&zwnj;لالی بزانێت)، ئه&zwnj;وه تۆڵه&zwnj;ی ئه&zwnj;و که&zwnj;سه دۆزه&zwnj;خه&zwnj;، نه&zwnj;مره تیایدا، هه&zwnj;روه&zwnj;ها خوا لێی توڕه بووه و نه&zwnj;فرینی لێکردووه و سزایه&zwnj;کی گه&zwnj;وره و بێ سنووری بۆ ئاماده کردووه&zwnj;.';
$t[94] = 'ئه&zwnj;ی ئه&zwnj;وانه&zwnj;ی باوه&zwnj;ڕتان هێناوه&zwnj;، کاتێک ده&zwnj;رئه&zwnj;چن بۆ گه&zwnj;یاندنی ئیسلام و جیهاد له&zwnj;پێناوی خوادا، وردبین بن و هه&zwnj;موو شتێک ڕوون بکه&zwnj;نه&zwnj;وه (دووره په&zwnj;رێزبن له په&zwnj;له&zwnj;و هه&zwnj;ڵه&zwnj;شه&zwnj;یی) و به&zwnj;وکه&zwnj;سانه مه&zwnj;ڵێن که سه&zwnj;لامتان لێده&zwnj;که&zwnj;ن: (ڕاست ناکه&zwnj;یت) تۆ ئیماندار نیت!! (به&zwnj;و هه&zwnj;ڵوێسته&zwnj;) مه&zwnj;به&zwnj;ستتان ده&zwnj;سکه&zwnj;وتی ژیانی دنیابێت، لای خوا ده&zwnj;ستکه&zwnj;وتی زۆر هه&zwnj;یه&zwnj;، خۆ کاتی خۆی ئێوه&zwnj;ش وابوون (باوه&zwnj;ڕتان شاراوه بوو) تا خوا منه&zwnj;تی نایه سه&zwnj;رتان (سه&zwnj;رکه&zwnj;وتنی پێبه&zwnj;خشین و ده&zwnj;رووی لێکردنه&zwnj;وه&zwnj;) که&zwnj;واته&zwnj;، وردو دووربین بن، چونکه به&zwnj;ڕاستی خوا ئاگاداره به&zwnj;هه&zwnj;موو ئه&zwnj;وکاروکرده&zwnj;وانه&zwnj;ی که ده&zwnj;یکه&zwnj;ن. (پێغه&zwnj;مبه&zwnj;ر (صلی الله علیه وسلم) ده&zwnj;سته&zwnj;یه&zwnj;ک له هاوه&zwnj;ڵانی نارد بۆ گه&zwnj;یاندنی ئیسلام، ئوسامه&zwnj;ی کوڕی زه&zwnj;یدی کرده لێپرسراویان، کابرایه&zwnj;ک که ناوی (مه&zwnj;رداس) بوو مه&zwnj;ڕو وشتری ده&zwnj;له&zwnj;وه&zwnj;ڕان، ئوسامه&zwnj;و هاوه&zwnj;ڵانی بینی، ترساو به&zwnj;په&zwnj;له دوور که&zwnj;وته&zwnj;وه&zwnj;و خۆی کرد به ئه&zwnj;شکه&zwnj;وتێکدا، ئه&zwnj;مانیش شوێنی که&zwnj;وتن، کاتێک چوونه به&zwnj;رده&zwnj;م ئه&zwnj;شکه&zwnj;وته&zwnj;که کابرا هات به&zwnj;پیریانه&zwnj;وه&zwnj;و سه&zwnj;لامی لێکردن و شایه&zwnj;تمانی هێنا، به&zwnj;ڵام ئوسامه هێرشی کرده سه&zwnj;ری و کوشتی.. دوایی که پێغه&zwnj;مبه&zwnj;ر (صلی الله علیه وسلم) بیستی تووڕه بوو له ئوسامه و فه&zwnj;رمووی: بۆچی کوشتت؟ ئایا دڵ و سینه&zwnj;تان هه&zwnj;ڵدڕی بوو بزانن به&zwnj;ڕاستێتی یان نا!!)...';
$t[95] = 'ئه&zwnj;وانه&zwnj;ی دانیشتوون له ئیمانداران و (جه&zwnj;نگ ناکه&zwnj;ن) ـ مه&zwnj;گه&zwnj;ر ئه&zwnj;وانه&zwnj;ی که ناته&zwnj;واون ـ (کوێر، شه&zwnj;ل، نه&zwnj;خۆش،... هتد) له&zwnj;گه&zwnj;ڵ ئه&zwnj;وانه&zwnj;دا که جیهادو کۆشش ده&zwnj;که&zwnj;ن له پێناوی خوادا به ماڵ و سامان و خۆیان چوونیه&zwnj;ک نین، بێگومان خوا فه&zwnj;زڵ و ڕێزو پله&zwnj;یه&zwnj;کی تایبه&zwnj;تی بڕیارداوه بۆ تێکۆشه&zwnj;ران به ماڵیان و خۆیان، به&zwnj;سه&zwnj;ر ئه&zwnj;وانه&zwnj;دا که دانیشتوون و (فیداکاری ناکه&zwnj;ن و گیان به&zwnj;خت ناکه&zwnj;ن)، خوای گه&zwnj;وره به&zwnj;هه&zwnj;ر ده&zwnj;سته&zwnj;یه&zwnj;کیان به&zwnj;ڵێنی چاک و به&zwnj;نرخی داوه&zwnj;، (بێگومان) خوا فه&zwnj;زڵ و ڕێزو پاداشتی گه&zwnj;وره&zwnj;و بێ سنووری ئه&zwnj;و تێکۆشه&zwnj;ر و موجاهیدانه&zwnj;ی داوه به&zwnj;سه&zwnj;ر ئه&zwnj;و دانیشتوانه&zwnj;دا...';
$t[96] = 'ئه&zwnj;ویش چه&zwnj;نده&zwnj;ها پله&zwnj;و پایه&zwnj;ن (به&zwnj;گوێره&zwnj;ی هه&zwnj;وڵ و کۆشش) له&zwnj;لایه&zwnj;ن خواوه پێشکه&zwnj;ش ده&zwnj;کرێن (به&zwnj;گوێره&zwnj;ی ڕاده&zwnj;ی نیه&zwnj;ت پاکیی و تێکۆشان)، هاوڕێ له&zwnj;گه&zwnj;ڵ لێخۆشبوون و ڕه&zwnj;حمه&zwnj;تدا، چونکه خوا هه&zwnj;میشه&zwnj;و به&zwnj;رده&zwnj;وام لێخۆشبووه&zwnj;و به ڕه&zwnj;حم و دلۆڤانه&zwnj;.';
$t[97] = 'به&zwnj;ڕاستی ئه&zwnj;وانه&zwnj;ی له سه&zwnj;ره مه&zwnj;رگدا فریشته گیانیان ده&zwnj;کێشن له&zwnj;کاتێکدا ئه&zwnj;وان هه&zwnj;میشه سته&zwnj;میان له خۆیان ده&zwnj;کرد (ئیمان و باوه&zwnj;ڕیان له مه&zwnj;ترسیدا بوو که&zwnj;چی کۆچیان نه&zwnj;ده&zwnj;کرد بۆ نیشتمانی ئیمان، به&zwnj;توندیی پیایاندا هه&zwnj;ڵده&zwnj;شاخێن)و ده&zwnj;ڵێن: باشه ئێوه له چیدا بوون و له&zwnj;کوێ ژیانتان ده&zwnj;برده سه&zwnj;ر؟! له وه&zwnj;ڵامدا ده&zwnj;ڵێن: ئێمه بێ ده&zwnj;سه&zwnj;ڵات و لاواز کراو بووین له زه&zwnj;ویدا، فریشته&zwnj;کان ده&zwnj;ڵێن: باشه ئایا زه&zwnj;وی خوا فراوان و پان و پۆر نه&zwnj;بوو تا کۆچی تێدا بکه&zwnj;ن و (کۆچ بکه&zwnj;ن بۆ نیشتمانێک که ئیمانداران تیایدا فه&zwnj;رمانڕه&zwnj;وان یاخود سه&zwnj;ربه&zwnj;ستن)؟ ئا ئه&zwnj;وانه شوێنه&zwnj;واریان دۆزه&zwnj;خه&zwnj;، که جێگه و ڕێگه و سه&zwnj;رئه&zwnj;نجامێکی ناخۆش و ناسازو خراپه&zwnj;.';
$t[98] = 'مه&zwnj;گه&zwnj;ر ئه&zwnj;وانه&zwnj;یان که به&zwnj;ڕاستی بێ ده&zwnj;سه&zwnj;ڵات و ناته&zwnj;وان و لاوازکراون له&zwnj;و پیاوانه&zwnj;ی ژن و منداڵیان له&zwnj;گه&zwnj;ڵدایه&zwnj;و ناتوانن ته&zwnj;گبیرو گۆڕانکاریه&zwnj;ک بۆ خۆیان بکه&zwnj;ن و هیچ ڕێگه&zwnj;یه&zwnj;کیش شاره&zwnj;زانین بۆ چاره&zwnj;سه&zwnj;رو ده&zwnj;ربازکردنی خۆیان.';
$t[99] = 'ئا ئه&zwnj;وانه خوا لێبووردن و چاوپۆشی بۆ ئاماده کردوون و خوا هه&zwnj;میشه&zwnj;و به&zwnj;رده&zwnj;وام زۆر لێخۆشبووه&zwnj;و زۆریش چاوپۆشی ده&zwnj;کات (مه&zwnj;به&zwnj;ست له وشه&zwnj;ی ـ عسی ـ بۆ هاتنه دی به&zwnj;ڵێنه خواییه&zwnj;که&zwnj;یه&zwnj;).';
$t[100] = 'جا ئه&zwnj;وه&zwnj;ی کۆچ بکات له پێناوی خوادا له&zwnj;و زه&zwnj;وییه&zwnj;ی که کۆچی بۆ ده&zwnj;کات، سه&zwnj;ربه&zwnj;رزییه&zwnj;ک بۆ خۆی و ڕیسواییه&zwnj;کی زۆریش بۆ دوژمنانی دروست ده&zwnj;کات، به&zwnj;ره&zwnj;که&zwnj;ت و فراوانی ڕزق و ڕۆزی ڕووی تێده&zwnj;کات، هه&zwnj;ر که&zwnj;سێکیش ماڵ و حاڵی خۆی به&zwnj;جێ بهێڵێت وله پێناو خواو پێغه&zwnj;مبه&zwnj;ره&zwnj;که&zwnj;یدا ده&zwnj;رچێت (واته به&zwnj;ره&zwnj;و وڵاتی ئیسلام، یاخود شوێنێکی ئازاد) کۆچ بکات، پاشان مردن یه&zwnj;خه&zwnj;ی پێبگرێت، ئه&zwnj;وه ئیتر ئه&zwnj;و که&zwnj;سه پاداشته&zwnj;که&zwnj;ی که&zwnj;وتۆته سه&zwnj;ر خوا (هه&zwnj;ر خۆی ده&zwnj;زانێت چۆن پاداشتی ده&zwnj;داته&zwnj;وه&zwnj;)، خوا هه&zwnj;میشه&zwnj;و به&zwnj;رده&zwnj;وام لێخۆشبوو میهره&zwnj;بانه (به&zwnj;تایبه&zwnj;ت بۆ ئه&zwnj;و جۆره که&zwnj;سانه&zwnj;).';
$t[101] = 'هه&zwnj;ر کاتێک به&zwnj;سه&zwnj;فه&zwnj;ر به&zwnj;لایه&zwnj;کی زه&zwnj;ویدا ڕۆیشتن، ئه&zwnj;وه گوناهبار نابن به&zwnj;کورت کردنه&zwnj;وه&zwnj;ی نوێژ، (به&zwnj;تایبه&zwnj;ت) ئه&zwnj;گه&zwnj;ر ترسان ئه&zwnj;وانه&zwnj;ی خوانه&zwnj;ناسن پیلان و به&zwnj;ڵایه&zwnj;کتان به&zwnj;سه&zwnj;ربهێنن وئازارتان بده&zwnj;ن، چونکه به&zwnj;ڕاستی کافران دوژمنانی ئاشکرای ئێوه&zwnj;ن.';
$t[102] = 'کاتێکیش تۆ (ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر (صلی الله علیه وسلم) له&zwnj;ناو هاوه&zwnj;ڵاندا بویت و پێش نوێژیت بۆ ده&zwnj;کردن (له کاتی جه&zwnj;نگدا)، ئه&zwnj;وه با ده&zwnj;سته&zwnj;یه&zwnj;کیان له&zwnj;گه&zwnj;ڵ تۆدا بوه&zwnj;ستن بۆ نوێژ و چه&zwnj;که&zwnj;کانیان پێ بێت، جا کاتێک ئه&zwnj;م ده&zwnj;سته&zwnj;یه&zwnj;ی که نوێژ ده&zwnj;کات چوونه سووژده بائه&zwnj;وانی تر له پشتانه&zwnj;وه بن (ئینجا که لێبوونه&zwnj;وه له&zwnj;و ڕکاته&zwnj;) ئه&zwnj;و ده&zwnj;سته&zwnj;یه&zwnj;ی تر که پاسه&zwnj;وانییان ده&zwnj;کردو نوێژیان نه&zwnj;کردووه بابێن له&zwnj;گه&zwnj;ڵ تۆدا نوێژه&zwnj;که&zwnj;یان بکه&zwnj;ن، با هوشیارو چه&zwnj;کداریش بن، چونکه بێ باوه&zwnj;ڕان ئاواته&zwnj;خوازن که ئێوه غافڵ بن له چه&zwnj;که&zwnj;کانتان و له که&zwnj;ل و په&zwnj;لتان، تایه&zwnj;ک هێرش بکه&zwnj;نه سه&zwnj;رتان وته&zwnj;فرو توناتان بکه&zwnj;ن، به&zwnj;ڵام بۆتان هه&zwnj;یه و قه&zwnj;یناکات چه&zwnj;که&zwnj;کانتان دانێن ئه&zwnj;گه&zwnj;ر ناڕه&zwnj;حه&zwnj;ت بوون به&zwnj;هۆی بارانه&zwnj;وه&zwnj;، یان هه&zwnj;ندێکتان نه&zwnj;خۆشکه&zwnj;وتن، هه&zwnj;میشه وریابن، به&zwnj;ڕاستی خوا سزای ڕیسواکه&zwnj;رو شه&zwnj;رمه&zwnj;زاری بۆ خوانه&zwnj;ناسان ئاماده کردووه&zwnj;.';
$t[103] = 'جا کاتێک له نوێژ بوونه&zwnj;وه ئه&zwnj;وه یادی خوا بکه&zwnj;ن به&zwnj;پێوه&zwnj;بن یان به&zwnj;دانیشتنه&zwnj;وه یان به ڕاکشانه&zwnj;وه (به&zwnj;هه&zwnj;رجۆرێک بۆتان گونجا) کاتێکیش ئارامی باڵی کێشا به&zwnj;سه&zwnj;رتانداو نیشته&zwnj;جێ بوون، ئه&zwnj;وه نوێژ به&zwnj;چاکی و ته&zwnj;واوی ئه&zwnj;نجام بده&zwnj;ن، چونکه به&zwnj;ڕاستی نوێژ له&zwnj;سه&zwnj;ر ئیمانداران فه&zwnj;رزه له&zwnj;کاتی دیاریکراودا ده&zwnj;بێ ئه&zwnj;نجام بدرێت.';
$t[104] = 'له به&zwnj;رامبه&zwnj;ر (له&zwnj;شکری بێ باوه&zwnj;ڕانه&zwnj;وه که شه&zwnj;ڕتان پێ ده&zwnj;فرۆشن یان به&zwnj;ربه&zwnj;ستن له ڕێگه&zwnj;ی پێگه&zwnj;یاندنی بانگه&zwnj;وازی خوادا) سستی و که&zwnj;مته&zwnj;رخه&zwnj;می مه&zwnj;که&zwnj;ن و بکه&zwnj;ونه دوایان، چونکه ئه&zwnj;گه&zwnj;ر ئێوه ئێش و ئازارو ماندوێتی و برینداریتان بۆ پێش بێت، بۆ ئه&zwnj;وانیش هه&zwnj;رپێش دێت، به&zwnj;ڵام ئه&zwnj;وه&zwnj;ی ئێوه به&zwnj;ئومێدو به&zwnj;ته&zwnj;مان له&zwnj;لایه&zwnj;ن خواوه ده&zwnj;ستان بکه&zwnj;وێت (له به&zwnj;هه&zwnj;شتدا) ئه&zwnj;وان به ته&zwnj;مای نین، خوا هه&zwnj;میشه و به&zwnj;رده&zwnj;وام زاناو دانایه&zwnj;.';
$t[105] = 'ئێمه به&zwnj;ڕاستی قورئانمان بۆ لای تۆ دابه&zwnj;زاندوه&zwnj;ته خواره&zwnj;وه&zwnj;، سه&zwnj;رتاپای حه&zwnj;ق و ڕاستی به&zwnj;رده&zwnj;وامه&zwnj;، بۆ ئه&zwnj;وه&zwnj;ی دادوه&zwnj;ری بکه&zwnj;یت له نێوان خه&zwnj;ڵکیدا به&zwnj;و شێوه&zwnj;یه&zwnj;ی که خوا نیشانی داویت و ڕێنموویی کردویت و به&zwnj;گونجاوی ده&zwnj;زانیت (بۆ چاره&zwnj;سه&zwnj;ری کێشه&zwnj;کان و به&zwnj;ڕێوه&zwnj;بردنی کۆمه&zwnj;ڵگا و قازانجی خه&zwnj;ڵکی)، نه&zwnj;که&zwnj;یت به&zwnj;رگری له ناپاکان بکه&zwnj;یت... (ده&zwnj;گێڕنه&zwnj;وه جوله&zwnj;که&zwnj;یه&zwnj;ک لای (طعمة) ناوێک قه&zwnj;ڵغانێک به&zwnj;ئه&zwnj;مانه&zwnj;ت داده&zwnj;نێت، به&zwnj;ڵام (طعمة) حاشای لێده&zwnj;کات، که دێن ماڵه&zwnj;که&zwnj;ی بگه&zwnj;ڕێن فڕێی ده&zwnj;داته ماڵه دراوسێکه&zwnj;یه&zwnj;وه&zwnj;، جاکه کێشه&zwnj;که ده&zwnj;گاته لای پێغه&zwnj;مبه&zwnj;ر (صلی الله علیه وسلم) خه&zwnj;ریکه لایه&zwnj;نی (طعمة) بگرێت، چونکه زۆر به&zwnj;ده&zwnj;م و پل بووه&zwnj;، به&zwnj;ڵام خوای گه&zwnj;وره ئه&zwnj;م چه&zwnj;ند ئایه&zwnj;ته داده&zwnj;به&zwnj;زێنێت له ـ (١٠٥ تا ١١٣).';
$t[106] = '(ئه&zwnj;ی محمد (صلی الله علیه وسلم) به&zwnj;رده&zwnj;وام) داوای لێخۆش بوون له خوا بکه&zwnj;، چونکه به&zwnj;ڕاستی ئه&zwnj;و خوایه هه&zwnj;میشه&zwnj;و به&zwnj;رده&zwnj;وام لێخۆشبوو میهره&zwnj;بانه&zwnj;.';
$t[107] = 'به&zwnj;رگری له&zwnj;و که&zwnj;سانه&zwnj;ش مه&zwnj;که که ناپاکن و خیانه&zwnj;ت له خۆیان ده&zwnj;که&zwnj;ن، چونکه به&zwnj;ڕاستی خوا ئه&zwnj;و که&zwnj;سانه&zwnj;ی خۆش ناوێت هه&zwnj;میشه خیانه&zwnj;تکارو تاوانبارن.';
$t[108] = 'ئه&zwnj;وانه ده&zwnj;یانه&zwnj;وێت خیانه&zwnj;ت و تاوانه&zwnj;کانیان له&zwnj;خه&zwnj;ڵکی بشارنه&zwnj;وه&zwnj;، له&zwnj;کاتێکدا ناتوانن له خوای بشارنه&zwnj;وه که له&zwnj;گه&zwnj;ڵیاندایه&zwnj;و چاودێریانه&zwnj;، به&zwnj;شه&zwnj;ودا قسه&zwnj;ی واهه&zwnj;ڵده&zwnj;به&zwnj;ستن خوا پێی ڕازی نیه و خوا به&zwnj;رده&zwnj;وام گه&zwnj;مارۆده&zwnj;ری ئه&zwnj;و کارو کرده&zwnj;وانه&zwnj;یه که ئه&zwnj;نجامی ده&zwnj;ده&zwnj;ن.';
$t[109] = 'باشه ئه&zwnj;وه&zwnj;ته ئێوه به&zwnj;رگریان لێده&zwnj;که&zwnj;ن له ژیانی ئه&zwnj;م دنیایه&zwnj;دا، ئه&zwnj;ی ڕۆژی قیامه&zwnj;ت، لای خوا کێ به&zwnj;رگریان لێده&zwnj;کات؟ یاخود کێ پشتیوان و یاریده&zwnj;ده&zwnj;ریان ده&zwnj;بێت (تا له سزای دۆزه&zwnj;خ ڕزگاریان بکات)؟';
$t[110] = 'ئه&zwnj;وه&zwnj;ی خراپه&zwnj;یه&zwnj;ک بکات (دژ به&zwnj;خه&zwnj;ڵکی) یان سته&zwnj;م له خۆی بکات له&zwnj;وه&zwnj;ودوا (به&zwnj;خۆیدا بچێته&zwnj;وه&zwnj;) و داوای لێخۆشبوون له خوا بکات، ئه&zwnj;وه خوایه&zwnj;کی لێخۆشبوو و میهره&zwnj;بانی ده&zwnj;ستده&zwnj;که&zwnj;وێت.';
$t[111] = 'ئه&zwnj;وه&zwnj;ش که گوناهو تاوانێک ئه&zwnj;نجام ده&zwnj;دات، ئه&zwnj;وه به&zwnj;زیانی خۆی ته&zwnj;واو ده&zwnj;بێت، زیان به&zwnj;خۆی ده&zwnj;گات (له قیامه&zwnj;تدا)، بێگومان خوا هه&zwnj;میشه&zwnj;و به&zwnj;رده&zwnj;وام زاناو دانایه&zwnj;.';
$t[112] = 'جا ئه&zwnj;وه&zwnj;ی هه&zwnj;ڵه&zwnj;یه&zwnj;ک یان تاوانێک ئه&zwnj;نجام بدات و له&zwnj;وه&zwnj;ودوا بیسه&zwnj;پێنێت به&zwnj;سه&zwnj;ر بێ تاوانێکداو بێ تاوانێکی پێ له&zwnj;که&zwnj;دار بکات، بێگومان ئه&zwnj;و که&zwnj;سه بوختان و تاوانێکی ئاشکرای ئه&zwnj;نجامداوه و تاوانێکی گه&zwnj;وره&zwnj;ی کردووه (زیاد له گوناهه&zwnj;که&zwnj;ی خۆی).';
$t[113] = 'خۆ ئه&zwnj;گه&zwnj;ر فه&zwnj;زڵ و ڕه&zwnj;حمه&zwnj;تی خوا نه&zwnj;بوایه به&zwnj;سه&zwnj;رته&zwnj;وه خه&zwnj;ریک بوو هه&zwnj;ندێکیان سه&zwnj;رت لێ بشێوێنن، بێگومان ئه&zwnj;وان به&zwnj;و کاره&zwnj;یان هه&zwnj;ر سه&zwnj;ر له خۆیان ده&zwnj;شێوێنن، دڵنیابه که ناتوانن له هیچ شتێکدا زیانت پێبگه&zwnj;یه&zwnj;نن، خوای گه&zwnj;وره&zwnj;ش قورئان و دانایی بۆ دابه&zwnj;زاندوویت و فێری ئه&zwnj;و شتانه&zwnj;ی کردوویت که جاران نه&zwnj;تده&zwnj;زانی، به&zwnj;رده&zwnj;وامیش فه&zwnj;زڵ و ڕێزی خوا له&zwnj;سه&zwnj;ر تۆ (ئه&zwnj;ی محمد (صلی الله علیه وسلم) زۆرو فراوان و ئاشکراو دیاره و (نایه&zwnj;ڵێت تووشی هه&zwnj;ڵه ببیت).';
$t[114] = 'له زۆربه&zwnj;ی چپه و سرته و قسه نهێنیه&zwnj;کانیاندا هیچ خێرێک به&zwnj;دی ناکرێت و تیایدا نیه&zwnj;، مه&zwnj;گه&zwnj;ر چپه&zwnj;ی ئه&zwnj;و که&zwnj;سه&zwnj;ی که فه&zwnj;رمان بدات به&zwnj;به&zwnj;خششێک، یان چاکه&zwnj;یه&zwnj;ک، یان ڕێکخستن و چاکسازی و ئاشتبوونه&zwnj;وه&zwnj;یه&zwnj;کی نێوان خه&zwnj;ڵکی، جا ئه&zwnj;وه&zwnj;ی (ئه&zwnj;و شێوازه چاکه&zwnj;یه&zwnj;) ئه&zwnj;نجام بدات و مه&zwnj;به&zwnj;ستی ته&zwnj;نها ده&zwnj;ستکه&zwnj;وتنی ڕه&zwnj;زامه&zwnj;ندی خوا بێت، ئه&zwnj;وه له ئاینده&zwnj;دا پاداشتی زۆرو بێ سنوور و گه&zwnj;وره&zwnj;ی پێده&zwnj;به&zwnj;خشین.';
$t[115] = 'ئه&zwnj;وه&zwnj;ش دوژمنایه&zwnj;تی پێغه&zwnj;مبه&zwnj;ر (صلی الله علیه وسلم) بکات و لێی جیابێته&zwnj;وه له دوای ئه&zwnj;وه&zwnj;ی ڕێگه&zwnj;ی ڕاست و دروستی بۆ ڕوون بۆته&zwnj;وه و ڕێبازێک بگرێته&zwnj;به&zwnj;ر که جیابێت له ڕێگه&zwnj;و ڕێبازی ئیمانداران، ئه&zwnj;وه ڕووی وه&zwnj;رده&zwnj;چه&zwnj;رخێنین بۆ ئه&zwnj;و ڕووگه&zwnj;و به&zwnj;رنامه&zwnj;یه&zwnj;ی هه&zwnj;ڵی بژاردووه&zwnj;و ڕووی تێکردووه&zwnj;، (له قیامه&zwnj;تیشدا) ده&zwnj;یخاته ناو دۆزه&zwnj;خه&zwnj;وه و ده&zwnj;یسوتێنین که ئه&zwnj;ویش سه&zwnj;رئه&zwnj;نجامێکی زۆر ناخۆش و سامناکه&zwnj;.';
$t[116] = 'به&zwnj;ڕاستی خوا خۆش نابێت له&zwnj;وه هاوه&zwnj;ڵ و شه&zwnj;ریکی بۆ بڕیار بدرێت و جگه له&zwnj;و (هاوبه&zwnj;ش دانانه&zwnj;) له گوناهانی تر بۆ هه&zwnj;ر که&zwnj;س بیه&zwnj;وێت و شایسته بێت و (په&zwnj;شیمان بێت) خۆش ده&zwnj;بێت، جا ئه&zwnj;وه&zwnj;ی شه&zwnj;ریک و هاوه&zwnj;ڵ بۆ خوا بڕیار بدات، ئه&zwnj;وه به&zwnj;ڕاستی گومڕا بووه به&zwnj;گومڕاییه&zwnj;کی زۆر دوور.';
$t[117] = '(ئه&zwnj;و هاوه&zwnj;ڵگه&zwnj;رانه&zwnj;) ئه&zwnj;وه&zwnj;ی هاناو هاواری بۆ ده&zwnj;به&zwnj;ن و ده&zwnj;یپه&zwnj;رستن ـ جگه له&zwnj;خوا ـ چه&zwnj;ند په&zwnj;رستراوێکی کار تێکراون (په&zwnj;رستراو ده&zwnj;بێت کارتێکه&zwnj;ر بێت نه&zwnj;ک کارتێکراو، بۆیه بته&zwnj;کانیان به&zwnj;مێینه ناوناوه وه&zwnj;ک: لات و عوززا)، ته&zwnj;نها هاناو هاواریشیان هه&zwnj;ر بۆ شه&zwnj;یتانی سه&zwnj;رکه&zwnj;ش و یاخی یه که له هه&zwnj;موو خێرێک داماڵراوه&zwnj;.';
$t[118] = 'ئه&zwnj;و شه&zwnj;یتانه&zwnj;ی که خوا نه&zwnj;فرینی لێکردووه وتوویه&zwnj;تی: سوێند بێت به تۆ ئه&zwnj;ی خوا به&zwnj;شێکی دیاریکراو له به&zwnj;نده&zwnj;کانت دائه&zwnj;بڕم له ئیمان و خواناسی.';
$t[119] = 'سوێند بێت به تۆ گومڕایان ده&zwnj;که&zwnj;م و به ئاوات و خه&zwnj;یاڵی پووچه&zwnj;وه سه&zwnj;رگه&zwnj;رمیان ده&zwnj;که&zwnj;م و خۆم فرمانیان پێده&zwnj;ده&zwnj;م و هانیان ده&zwnj;ده&zwnj;م و ئه&zwnj;وجا ئه&zwnj;وانیش بێگومان گوێی ئاژه&zwnj;ڵه&zwnj;کانیان ده&zwnj;بڕن، هه&zwnj;روه&zwnj;ها فرمانیان پێ ده&zwnj;ده&zwnj;م ئه&zwnj;وانیش دروست کراوانی خوا ده&zwnj;ستکاری بکه&zwnj;ن و بیگۆڕن (له&zwnj;م سه&zwnj;رده&zwnj;مه&zwnj;دا ئه&zwnj;ڵقه له&zwnj;گوێکانی به&zwnj;ناو زانا ده&zwnj;ستکاریی جینات و سیفاتی ڕه&zwnj;چه&zwnj;ڵه&zwnj;کیی ده&zwnj;که&zwnj;ن و به&zwnj;گوێی شه&zwnj;یتان ده&zwnj;که&zwnj;ن، ئاده&zwnj;میزادو زینده&zwnj;وه&zwnj;ران تێکده&zwnj;ده&zwnj;ن، جگه له&zwnj;هه&zwnj;ندێکیان نه&zwnj;بێت که مه&zwnj;به&zwnj;ستیان خزمه&zwnj;تی ئاده&zwnj;میزاده&zwnj;)، جا ئه&zwnj;وه&zwnj;ی له جیاتی خوا شه&zwnj;یتان بکاته پشتیوانی خۆی، ئه&zwnj;وه به&zwnj;ڕاستی دووچاری زیان و خه&zwnj;ساره&zwnj;تمه&zwnj;ندیه&zwnj;کی ئاشکرا بووه&zwnj;.';
$t[120] = '(شه&zwnj;یتان) به&zwnj;ڵێنیان ده&zwnj;داتێ و ئاوات و ئاره&zwnj;زوو ده&zwnj;ڕازێنێته&zwnj;وه بۆیان، دیاره که شه&zwnj;یتانیش هیچ به&zwnj;ڵێنێکیان پێنادات جگه له فریودان و خه&zwnj;ڵه&zwnj;تاندن نه&zwnj;بێت.';
$t[121] = 'ئائه&zwnj;وانه جێگه&zwnj;و سه&zwnj;ره&zwnj;نجامیان دۆزه&zwnj;خه و که&zwnj;لێنی لێ ده&zwnj;رچوونیان ده&zwnj;ست ناکه&zwnj;وێت.';
$t[122] = 'ئه&zwnj;وانه&zwnj;ش باوه&zwnj;ڕیان هێناوه و کارو کرده&zwnj;وه چاکه&zwnj;کانیان ئه&zwnj;نجامداوه&zwnj;، ئه&zwnj;وه له&zwnj;مه&zwnj;ودوا ده&zwnj;یانخه&zwnj;ینه ناو باخه&zwnj;کانی به&zwnj;هه&zwnj;شته&zwnj;وه که چه&zwnj;نده&zwnj;ها ڕووبار به&zwnj;ژێر دره&zwnj;خت و به&zwnj;به&zwnj;رده&zwnj;م کۆشکه&zwnj;کانیدا ده&zwnj;ڕوات و ژیانی هه&zwnj;میشه&zwnj;یی و نه&zwnj;بڕاوه و به&zwnj;رده&zwnj;وام تیایدا ده&zwnj;به&zwnj;نه&zwnj;سه&zwnj;ر، ئه&zwnj;وه به&zwnj;ڵێنێکی نه&zwnj;گۆڕاوی خوایی یه&zwnj;، جاکێ هه&zwnj;یه ڕاستترو دروستتر له خوا به&zwnj;ڵێنی خۆی بباته&zwnj;سه&zwnj;ر؟';
$t[123] = '(پاداشت و ڕێزو نرخ) نه به ئاره&zwnj;زووی ئێوه&zwnj;یه&zwnj;، نه به ئاره&zwnj;زووی خاوه&zwnj;نانی کتێبه&zwnj;، ئه&zwnj;وه&zwnj;ی خراپه&zwnj;و گوناهێک بکات، ئه&zwnj;وه به&zwnj;هۆیه&zwnj;وه سزا ده&zwnj;درێت و هیچ که&zwnj;س و هیچ هێزێکی تری ده&zwnj;ست ناکه&zwnj;وێت جگه له خوا پشتگیریی بکات و یارمه&zwnj;تی بدات.';
$t[124] = 'ئه&zwnj;وه&zwnj;ش کرده&zwnj;وه چاکه&zwnj;کان ئه&zwnj;نجام بدات نێر بێت یان مێ له&zwnj;کاتێکدا ئیماندار بێت، جا ئه&zwnj;وانه ده&zwnj;چنه به&zwnj;هه&zwnj;شته&zwnj;وه&zwnj;و به&zwnj;قه&zwnj;ده&zwnj;ر په&zwnj;رده&zwnj;ی ناوکی خورما سته&zwnj;میان لێناکرێت.';
$t[125] = 'کێ هه&zwnj;یه ئاینی چاکتره په&zwnj;سه&zwnj;ندترو جوانتر بێت له&zwnj;و که&zwnj;سه&zwnj;ی ڕووی کردۆته خوا و خۆی ته&zwnj;سلیمی ئه&zwnj;وزاته کردووه&zwnj;و له&zwnj;کاتێکدا کارجوان و کار په&zwnj;سه&zwnj;نده&zwnj;و شوێنی ئاینی ئیبراهیمی ملکه&zwnj;چ و فه&zwnj;رمانبه&zwnj;ردار که&zwnj;وتبێت، که خوا ئیبراهیمی کردۆته خۆشه&zwnj;ویستی خۆی ڕێزداری کردووه&zwnj;.';
$t[126] = 'هه&zwnj;رچی له ئاسمانه&zwnj;کان و هه&zwnj;رچی له زه&zwnj;ویدا هه&zwnj;یه هه&zwnj;ر خوا خاوه&zwnj;نیانه&zwnj;و ده&zwnj;سه&zwnj;ڵاتی ئه&zwnj;و خوایه ده&zwnj;وری هه&zwnj;موو شتێکی داوه (به&zwnj;وردیی و به&zwnj;ته&zwnj;واویی له هه&zwnj;موو شتێک ئاگاداره&zwnj;).';
$t[127] = 'ئه&zwnj;ی محمد (صلی الله علیه وسلم) پرسیارت لێده&zwnj;که&zwnj;ن ده&zwnj;رباره&zwnj;ی ئه&zwnj;وه&zwnj;ی که پێویسته پیاوان ئه&zwnj;نجامی بده&zwnj;ن به&zwnj;رامبه&zwnj;ر به ژنان، پێیان بڵێ: خوای گه&zwnj;وره ده&zwnj;رباره&zwnj;یان وه&zwnj;ڵامتان ده&zwnj;داته&zwnj;وه&zwnj;؛ جائه&zwnj;وه&zwnj;ی به&zwnj;سه&zwnj;رتاندا ده&zwnj;خوێندرێته&zwnj;وه له قورئاندا (روونی کردۆته&zwnj;وه له&zwnj;مه&zwnj;وپێش له سه&zwnj;ره&zwnj;تای ئه&zwnj;م سووره&zwnj;ته&zwnj;وه&zwnj;) ده&zwnj;رباره&zwnj;ی ئه&zwnj;و کچه بێ باوکانه&zwnj;ی ئه&zwnj;وه&zwnj;ی خوا بۆی داناون نایانده&zwnj;نێ و ناشتانه&zwnj;وێت ماره&zwnj;یان بکه&zwnj;ن و (رێگه&zwnj;ش له شووکردنیان ده&zwnj;گرن) و ده&zwnj;رباره&zwnj;ی میراتی ئه&zwnj;و منداڵه بێ ده&zwnj;سه&zwnj;ڵاتانه&zwnj;ی ناتانه&zwnj;وێت به&zwnj;دادپه&zwnj;روه&zwnj;ری هه&zwnj;ڵسوکه&zwnj;وت و مامه&zwnj;ڵه&zwnj;یان له&zwnj;گه&zwnj;ڵدا بکه&zwnj;ن و به&zwnj;رپاکردنی دادگه&zwnj;ریی له&zwnj;نێوان بێ باوکاندا (ئه&zwnj;رکی سه&zwnj;رشانی ئیماندارانه&zwnj;)، ئه&zwnj;وه&zwnj;ی که ئه&zwnj;نجامی ده&zwnj;ده&zwnj;ن له چاکه ئه&zwnj;وه بێگومان خوای گه&zwnj;وره پێی ئاگاداره به&zwnj;چاکه&zwnj;کاریی له&zwnj;گه&zwnj;ڵ منداڵانی بێ باوکدا (که مافیان بده&zwnj;ن و سته&zwnj;میان لێنه&zwnj;که&zwnj;ن)، جا هه&zwnj;ر خێرو چاکه&zwnj;یه&zwnj;ک ئه&zwnj;نجام بده&zwnj;ن خوا زانایه پێی.';
$t[128] = 'ئه&zwnj;گه&zwnj;ر ئافره&zwnj;تێک له لووت به&zwnj;رزی یان پشت هه&zwnj;ڵکردنی مێرده&zwnj;که&zwnj;ی ترسا، ئه&zwnj;وه هیچ گوناهیان له&zwnj;سه&zwnj;ر نیه و کارێکی چاکه&zwnj;: ئه&zwnj;گه&zwnj;ر نێوان خۆیان چاک بکه&zwnj;ن و له&zwnj;سه&zwnj;ر شێوازێک ڕێک بکه&zwnj;ون (وه&zwnj;ک ئه&zwnj;وه&zwnj;ی ئافره&zwnj;ته&zwnj;که ده&zwnj;ست هه&zwnj;ڵگرێت له&zwnj;هه&zwnj;ندێ مافی خۆی تابه&zwnj;ڕێزو سه&zwnj;ربه&zwnj;رزیه&zwnj;وه بمێنێته&zwnj;وه له&zwnj;گه&zwnj;ڵ مێرده&zwnj;که&zwnj;یدا) هه&zwnj;میشه ڕێکه&zwnj;وتن و ته&zwnj;بایی شتێکی چاک و په&zwnj;سه&zwnj;نده&zwnj;، هه&zwnj;رچه&zwnj;نده خه&zwnj;ڵکی به سروشت حه&zwnj;ز به&zwnj;ره&zwnj;زیلی ده&zwnj;که&zwnj;ن و ته&zwnj;نها به&zwnj;رژه&zwnj;وه&zwnj;ندی خۆیان ده&zwnj;وێت، (ئه&zwnj;گه&zwnj;ر قیامه&zwnj;ت و به&zwnj;هه&zwnj;شت و دۆزه&zwnj;خ له&zwnj;به&zwnj;رچاو نه&zwnj;گیرێت) جا ئه&zwnj;گه&zwnj;ر چاکه&zwnj;کارو ڕه&zwnj;فتارجوان بن (له&zwnj;گه&zwnj;ڵ هاوسه&zwnj;رانتاندا)و خاوه&zwnj;ن ته&zwnj;قواو پارێزکار بن و سته&zwnj;میان لێ نه&zwnj;که&zwnj;ن، ئه&zwnj;وه دڵنیابن به&zwnj;ڕاستی خوا هه&zwnj;میشه&zwnj;و به&zwnj;رده&zwnj;وام ئاگایه به کارو کرده&zwnj;وه&zwnj;تان.';
$t[129] = 'هه&zwnj;رگیز ئێوه ناتوانن له نێوان هاوسه&zwnj;ره&zwnj;کانتاندا دادپه&zwnj;روه&zwnj;رانه ڕه&zwnj;فتار بکه&zwnj;ن، له کاتێکدا ئه&zwnj;گه&zwnj;ر زۆریش سوور بن له&zwnj;سه&zwnj;ری، جا که&zwnj;وابوو نه&zwnj;که&zwnj;ن هه&zwnj;ر ڕوو له یه&zwnj;کێکیان بکه&zwnj;ن به ته&zwnj;واوی ئه&zwnj;وی تر واز لێبهێنن وه&zwnj;ک هه&zwnj;ڵپه&zwnj;سێرراو، نه ئه&zwnj;وه&zwnj;یه مێردار بێت، نه ئه&zwnj;وه&zwnj;شه بێ مێرد بێت، خۆ ئه&zwnj;گه&zwnj;ر چاکسازی بکه&zwnj;ن و ڕێک بکه&zwnj;ون و خۆتان بپارێزن له سته&zwnj;م، ئه&zwnj;وه به&zwnj;ڕاستی خوا هه&zwnj;میشه&zwnj;و به&zwnj;رده&zwnj;وام لێخۆشبوو میهره&zwnj;بانه&zwnj;.';
$t[130] = 'خۆ ئه&zwnj;گه&zwnj;ر ژن و مێرد لێک جیاببنه&zwnj;وه (به&zwnj;گوێره&zwnj;ی شه&zwnj;رع و به&zwnj;رنامه&zwnj;ی خوا) ئه&zwnj;وه خوا هه&zwnj;ریه&zwnj;که&zwnj;یان شادو بێ نیازو به&zwnj;خته&zwnj;وه&zwnj;ر ده&zwnj;کات، له به&zwnj;خشینی فراوانی خۆی (به&zwnj;هاوسه&zwnj;رێکی گونجاو) چونکه خوا هه&zwnj;میشه&zwnj;و به&zwnj;رده&zwnj;وام فراوانگیرو دانایه&zwnj;.';
$t[131] = 'هه&zwnj;ر بۆ خوایه ئه&zwnj;وه&zwnj;ی له ئاسمانه&zwnj;کان و ئه&zwnj;وه&zwnj;ی له زه&zwnj;ویدایه&zwnj;، سوێند به&zwnj;خوا به&zwnj;ڕاستی ئێمه ئامۆژگاری و فه&zwnj;رمانمانداوه به&zwnj;سه&zwnj;ر ئه&zwnj;وانه&zwnj;ی به&zwnj;رنامه&zwnj;ی خوایان بۆ هاتبوو له پێش ئێوه&zwnj;، هه&zwnj;روه&zwnj;ها ئامۆژگاری ئێوه&zwnj;شمان کردووه له خوا بترسن و به ته&zwnj;قواو پارێزکاربن، خۆ ئه&zwnj;گه&zwnj;ر بێ باوه&zwnj;ڕو یاخی بن (ئه&zwnj;وه خۆتان زیان ده&zwnj;که&zwnj;ن)، چونکه به&zwnj;ڕاستی هه&zwnj;ر بۆ خوایه ئه&zwnj;وه&zwnj;ی له ئاسمانه&zwnj;کان و ئه&zwnj;وه&zwnj;ی له زه&zwnj;ویدایه&zwnj;، خوایش هه&zwnj;میشه&zwnj;و به&zwnj;رده&zwnj;وام بێ نیازه&zwnj;و شایسته&zwnj;ی سوپاسه&zwnj;.';
$t[132] = 'هه&zwnj;ر بۆ خوایه ئه&zwnj;وه&zwnj;ی له ئاسمانه&zwnj;کان و ئه&zwnj;وه&zwnj;ی له زه&zwnj;ویدایه و هه&zwnj;ر ئه&zwnj;و خوایه به&zwnj;سه بۆ ئه&zwnj;وه&zwnj;ی چاودێر بێت و کاروباری پێ بسپێریت (ئاسمانه&zwnj;کان هه&zwnj;ر خوا خۆی فراوانییان ده&zwnj;زانێت و نهێنیه&zwnj;کانی زه&zwnj;ویش که&zwnj;مێکی زانراوه&zwnj;).';
$t[133] = 'خه&zwnj;ڵکینه&zwnj;، ئه&zwnj;گه&zwnj;ر خوا بیه&zwnj;وێت، ئێوه له&zwnj;ناو ده&zwnj;بات و که&zwnj;سانی تر ده&zwnj;هێنێته مه&zwnj;یدانه&zwnj;وه و خوا هه&zwnj;میشه و به&zwnj;رده&zwnj;وام ده&zwnj;سه&zwnj;ڵاتی به&zwnj;سه&zwnj;ر ئه&zwnj;وکاره&zwnj;دا هه&zwnj;یه&zwnj;.';
$t[134] = 'ئه&zwnj;و که&zwnj;سه&zwnj;ی ته&zwnj;نها پاداشتی دنیای ده&zwnj;وێت (ئه&zwnj;وه بابزانێت) که لای خوا پاداشتی دنیاو پاداشتی قیامه&zwnj;تیش هه&zwnj;یه&zwnj;، خوا هه&zwnj;میشه&zwnj;و به&zwnj;رده&zwnj;وام بیسه&zwnj;رو بینایه&zwnj;.';
$t[135] = 'ئه&zwnj;ی ئه&zwnj;وانه&zwnj;ی باوه&zwnj;ڕتان هێناوه هه&zwnj;میشه ڕاگری دادوه&zwnj;ری بن و شایه&zwnj;تیی بۆ خوا بده&zwnj;ن، با شایه&zwnj;تیه&zwnj;که&zwnj;شتان زیانی بۆ خۆتان و دایک و باوکتان یان خزمانیش هه&zwnj;بێت، ئه&zwnj;گه&zwnj;ر که&zwnj;سانێک له&zwnj;وانه ده&zwnj;وڵه&zwnj;مه&zwnj;ندبێت یان هه&zwnj;ژار خوا به&zwnj;رژه&zwnj;وه&zwnj;ندی ئه&zwnj;وان چاکتر ده&zwnj;زانێت له ئێوه (واته به&zwnj;ته&zwnj;ماعی ده&zwnj;وڵه&zwnj;مه&zwnj;ندی له دادپه&zwnj;روه&zwnj;ری لامه&zwnj;ده&zwnj;ن و به&zwnj;هۆی نه&zwnj;داریی و به&zwnj;زه&zwnj;یی بێ جێ له حه&zwnj;ق لامه&zwnj;ده&zwnj;ن) نه&zwnj;که&zwnj;ن شوێن ئاره&zwnj;زووی نه&zwnj;فس بکه&zwnj;ون له دادپه&zwnj;روه&zwnj;ری لابده&zwnj;ن، جا ئه&zwnj;گه&zwnj;ر ئێوه پێچ لێ بده&zwnj;ن (زمان بگۆرن) و شایه&zwnj;تی ناحه&zwnj;ق بده&zwnj;ن، یان پشت هه&zwnj;ڵکه&zwnj;ن، ئه&zwnj;وه چاک بزانن خوا ئاگاداره به کارو کرده&zwnj;وه&zwnj;تان.';
$t[136] = 'ئه&zwnj;ی ئه&zwnj;وانه&zwnj;ی باوه&zwnj;ڕتان هێناوه&zwnj;، به&zwnj;رده&zwnj;وام و دامه&zwnj;زراو بن له&zwnj;سه&zwnj;ر ئیمان به خوا و پێغه&zwnj;مبه&zwnj;ره&zwnj;که&zwnj;ی و به&zwnj;و قورئانه&zwnj;ش که له ماوه&zwnj;ی چه&zwnj;ند ساڵێکدا دایبه&zwnj;زاندووه بۆ پێغه&zwnj;مبه&zwnj;ره&zwnj;که&zwnj;ی و به&zwnj;و کتێبانه&zwnj;ش که پێشتر ڕه&zwnj;وانه&zwnj;ی کردوون، جا ئه&zwnj;وه&zwnj;ی باوه&zwnj;ڕی نه&zwnj;بێت به&zwnj;خواو فریشته&zwnj;کانی و به کتێبه&zwnj;کانی و به&zwnj;پێغه&zwnj;مبه&zwnj;رانی و به&zwnj;رۆژی دوایی، ئه&zwnj;وه به&zwnj;ڕاستی گومڕابووه به&zwnj;ئه&zwnj;وپه&zwnj;ڕی گومڕایی.';
$t[137] = 'به&zwnj;ڕاستی ئه&zwnj;وانه&zwnj;ی باوه&zwnj;ڕیان هێناوه و دوایی پاشگه&zwnj;ز بوونه&zwnj;ته&zwnj;وه&zwnj;، دووباره ئیمانیان هێنایه&zwnj;وه و دوایی پاشگه&zwnj;ز بوونه&zwnj;وه&zwnj;، له&zwnj;وه&zwnj;ودوا زیاتر له بێ دینی و کافریدا ڕۆچوون، هیچ کات خوا له&zwnj;وانه خۆشنابێت و ڕێنمووییان ناکات بۆ ڕێبازێکی سوود به&zwnj;خش.';
$t[138] = '(ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر (صلی الله علیه وسلم) مژده به دووڕووه&zwnj;کان بده که به&zwnj;ڕاستی سزایه&zwnj;کی به&zwnj;ئێشیان بۆ ئاماده کراوه&zwnj;!!';
$t[139] = 'ئه&zwnj;و دووڕووانه&zwnj;ی که بێ باوه&zwnj;ڕه&zwnj;کان ده&zwnj;که&zwnj;نه پشتیوان و یارویاوه&zwnj;ری خۆیان له&zwnj;جیاتی ئیمانداران، ئایا ده&zwnj;یانه&zwnj;وێت عیززه&zwnj;ت و ده&zwnj;سه&zwnj;ڵات و سه&zwnj;ربه&zwnj;رزیان لای ئه&zwnj;وان ده&zwnj;ست بکه&zwnj;وێت؟ (ئه&zwnj;وانه به&zwnj;هه&zwnj;ڵه&zwnj;دا چوون) چونکه به&zwnj;ڕاستی عیزه&zwnj;ت و باڵاداستی و سه&zwnj;ربه&zwnj;رزیی هه&zwnj;ر هه&zwnj;مووی بۆ خوایه&zwnj;.';
$t[140] = 'به&zwnj;ڕاستی (خوای گه&zwnj;وره&zwnj;) له قورئاندا ئه&zwnj;م بڕیاره&zwnj;ی ناردووه بۆ سه&zwnj;رتان که&zwnj;: هه&zwnj;ر کاتێک له جێیه&zwnj;کدا گوێتان لێ بوو کوفرو گاڵته به&zwnj;ئایه&zwnj;ته&zwnj;کانی خوا ده&zwnj;کرا، ئه&zwnj;وه دامه&zwnj;نیشن له&zwnj;گه&zwnj;ڵ ئه&zwnj;وانه&zwnj;دا هه&zwnj;تا قسه&zwnj;و باسه&zwnj;که&zwnj;یان به&zwnj;گۆڕن (ئه&zwnj;گه&zwnj;ر دابنیشن و گوێ نه&zwnj;ده&zwnj;نێ) ئه&zwnj;وه به&zwnj;ڕاستی ئێوه&zwnj;ش له&zwnj;و کاته&zwnj;دا وه&zwnj;ک ئه&zwnj;وانن و (حسابی ئه&zwnj;وانتان بۆ ده&zwnj;کرێت) به&zwnj;ڕاستی خوا دووڕووه&zwnj;کان و بێ باوه&zwnj;ڕه&zwnj;کان هه&zwnj;موویان له دۆزه&zwnj;خدا کۆده&zwnj;کاته&zwnj;وه&zwnj;.';
$t[141] = 'ئه&zwnj;وانه&zwnj;ی که خۆیان بۆ ئێوه مه&zwnj;ڵاسداوه چاوه&zwnj;ڕوانی به&zwnj;سه&zwnj;رهاتتان ده&zwnj;که&zwnj;ن، ئه&zwnj;گه&zwnj;ر سه&zwnj;رکه&zwnj;وتنێکتان له&zwnj;لایه&zwnj;ن خواوه ده&zwnj;ست بکه&zwnj;وێت ده&zwnj;ڵێن: ئه&zwnj;ی ئێمه له&zwnj;گه&zwnj;ڵتاندا نه&zwnj;بووین؟! خۆ ئه&zwnj;گه&zwnj;ر کافره&zwnj;کان به&zwnj;شێکیان ببێت له سه&zwnj;رکه&zwnj;وتن، دووڕوه&zwnj;کان ده&zwnj;ڵێن: ئایا ئێمه له&zwnj;سه&zwnj;ر ئێوه&zwnj;مان نه&zwnj;ده&zwnj;کرده&zwnj;وه و، نه&zwnj;مانهێشت ئیمانداران زیانتان پێبگه&zwnj;یه&zwnj;نن، ئینجا خوا له ڕۆژی قیامه&zwnj;تدا دادوه&zwnj;ریی ده&zwnj;کات له نێوانتاندا (ئه&zwnj;ی بێ باوه&zwnj;ڕ و دووڕووه&zwnj;کان) دڵنیابن هه&zwnj;رگیز خوا نایه&zwnj;ڵێت بێ باوه&zwnj;ڕان به&zwnj;سه&zwnj;ر ئیمانداراندا ده&zwnj;ست ڕۆیشتوو بن و ڕیشه&zwnj;که&zwnj;نیان بکه&zwnj;ن.';
$t[142] = 'به&zwnj;ڕاستی دووڕووه&zwnj;کان (وا ده&zwnj;زانن) فێڵ له خوا ده&zwnj;که&zwnj;ن (له&zwnj;کاتێکدا) ئه&zwnj;و زاته فێڵه&zwnj;کانیان پووچ ده&zwnj;کاته&zwnj;وه (نازانن که دیار و نادیار، لای خوا ڕوون و ئاشکرایه&zwnj;)، هه&zwnj;ر کاتێکیش (بۆ ڕیاو ڕووپامایی) هه&zwnj;ڵده&zwnj;ستن بۆ نوێژ، زۆر به&zwnj;له&zwnj;ش گرانیی و ته&zwnj;مه&zwnj;ڵیی هه&zwnj;ڵده&zwnj;ستن، ئه&zwnj;وانه ڕیایی و ڕوپامایی بۆ خه&zwnj;ڵکی ده&zwnj;که&zwnj;ن، زۆر که&zwnj;م یادی خوا ده&zwnj;که&zwnj;ن.';
$t[143] = 'ئه&zwnj;وانه گیریان خواردووه له&zwnj;و نێوانه&zwnj;دا، نه&zwnj;ئه&zwnj;وه&zwnj;یه پاڵ بده&zwnj;ن به&zwnj;ئیماندارانه&zwnj;وه&zwnj;، نه&zwnj;ئه&zwnj;وه&zwnj;شه پاڵ بده&zwnj;ن به&zwnj;کافرانه&zwnj;وه&zwnj;، هه&zwnj;ر که&zwnj;س خوا (به&zwnj;هۆی ناله&zwnj;باریی خۆیه&zwnj;وه&zwnj;) گومڕای بکات، ئه&zwnj;وه هه&zwnj;رگیز ناتوانیت ڕێبازێکی ڕاست و ڕزگاری بۆ بدۆزێته&zwnj;وه&zwnj;.';
$t[144] = 'ئه&zwnj;ی ئه&zwnj;وانه&zwnj;ی باوه&zwnj;ڕتان هێناوه بێ باوه&zwnj;ڕان مه&zwnj;که&zwnj;نه یارو یاوه&zwnj;ری خۆتان له&zwnj;جیاتی ئیمانداران، ئایا ده&zwnj;تانه&zwnj;وێت به ده&zwnj;ستی خۆتان به&zwnj;ڵگه&zwnj;یه&zwnj;کی ئاشکرا بده&zwnj;نه ده&zwnj;ست خوا له&zwnj;سه&zwnj;ر خۆتان.';
$t[145] = 'به&zwnj;ڕاستی دووڕووه&zwnj;کان له چینی هه&zwnj;ره خواری ناو دۆزه&zwnj;خدان و هه&zwnj;رگیز بۆ ئه&zwnj;وان پشتیوانێکت ده&zwnj;ست ناکه&zwnj;وێت.';
$t[146] = 'جگه له&zwnj;وانه&zwnj;یان ته&zwnj;وبه&zwnj;ی ڕاسته&zwnj;قینه&zwnj;یان کردووه و کارونیه&zwnj;تیان چاک کردووه و ده&zwnj;ستیان به&zwnj;ئاینی خواوه گرتووه و په&zwnj;یڕه&zwnj;وی به&zwnj;رنامه&zwnj;که&zwnj;ی ده&zwnj;که&zwnj;ن و به&zwnj;ته&zwnj;واویی دین و ئاینیان ساغ کردۆته&zwnj;وه بۆ خوا، جا ئه&zwnj;وانه له&zwnj;گه&zwnj;ڵ ئیمانداراندا ده&zwnj;بن و (له&zwnj;گه&zwnj;ڵ ئه&zwnj;واندا حه&zwnj;شر ده&zwnj;کرێن)، بێگومان له&zwnj;ئاینده&zwnj;دا خوا پاداشتی زۆرو بێ سنوور ده&zwnj;به&zwnj;خشێت به&zwnj;ئیمانداران.';
$t[147] = 'خوا چ پێویستیه&zwnj;کی به&zwnj;سزادانی ئێوه هه&zwnj;یه&zwnj;؟ ئه&zwnj;گه&zwnj;ر شوکرانه بژێرو سوپاسگوزاربن و ئیمان و باوه&zwnj;ڕی دامه&zwnj;زراوتان هه&zwnj;بێت، خوایش هه&zwnj;میشه&zwnj;و به&zwnj;رده&zwnj;وام سوپاسگوزاری به&zwnj;نده چاکه&zwnj;کانیه&zwnj;تی و زاناو ئاگایه پێیان.';
$t[148] = 'بێگومان خوا حه&zwnj;ز ناکات له قسه&zwnj;ی ناشیرین و نابه&zwnj;جێ و ناو ناتۆره وتن و جنێودان، مه&zwnj;گه&zwnj;ر که&zwnj;سێک سته&zwnj;م لێکراو بێت، خوا هه&zwnj;میشه&zwnj;و به&zwnj;رده&zwnj;وام بیسه&zwnj;رو زانایه&zwnj;.';
$t[149] = 'ئه&zwnj;گه&zwnj;ر کردارو گوفتارو به&zwnj;خششی خۆتان ئاشکرا بکه&zwnj;ن یان بیشارنه&zwnj;وه&zwnj;، یاخود چاوپۆشی له هه&zwnj;ڵه&zwnj;و گوناهێک بکه&zwnj;ن، ئه&zwnj;وه بێگومان خوا هه&zwnj;میشه&zwnj;و به&zwnj;رده&zwnj;وام لێخۆشبووه به&zwnj;ده&zwnj;سه&zwnj;ڵاتیشه&zwnj;.';
$t[150] = 'بێگومان ئه&zwnj;وانه&zwnj;ی باوه&zwnj;ڕ به خواو پێغه&zwnj;مبه&zwnj;ران ناهێنن و ده&zwnj;یانه&zwnj;وێت جیاوازی بکه&zwnj;ن له نێوان خواو پێغه&zwnj;مبه&zwnj;ره&zwnj;کانیدا (گوایه باوه&zwnj;ڕیان به&zwnj;خوا هه&zwnj;یه به&zwnj;ڵام باوه&zwnj;ڕیان به&zwnj;پێغه&zwnj;مبه&zwnj;ران نیه&zwnj;)، (یاخود) ده&zwnj;ڵێن: باوه&zwnj;ڕ ده&zwnj;هێنین به&zwnj;هه&zwnj;ندێک له پێغه&zwnj;مبه&zwnj;ران و باوه&zwnj;ڕ به&zwnj;هه&zwnj;ندێکیان ناهێنین و ده&zwnj;یانه&zwnj;وێت له&zwnj;و نێوانه&zwnj;دا ڕێگه و به&zwnj;رنامه&zwnj;یه&zwnj;ک بۆ خۆیان به&zwnj;ده&zwnj;ست بهێنن...';
$t[151] = 'ئا ئه&zwnj;و که&zwnj;سانه به&zwnj;ڕاستی هه&zwnj;ر بێ باوه&zwnj;ڕن و بۆ بێ باوه&zwnj;ڕان سزایه&zwnj;کی سه&zwnj;ر شۆڕکه&zwnj;رمان ئاماده&zwnj;کردوه&zwnj;.';
$t[152] = 'ئه&zwnj;وانه&zwnj;ی باوه&zwnj;ڕیان به خوا و پێغه&zwnj;مبه&zwnj;ره&zwnj;کانی هێناوه و هیچ جۆره جیاوازیه&zwnj;کیان نه&zwnj;کردووه له نێوان هیچ کامیانداو باوه&zwnj;ڕیان به هه&zwnj;موویان هه&zwnj;یه&zwnj;، ئا ئه&zwnj;وانه له&zwnj;ئاینده&zwnj;دا خوای گه&zwnj;وره پاداشتی خۆیان ده&zwnj;داتێ، خوای گه&zwnj;وره&zwnj;ش هه&zwnj;میشه و به&zwnj;رده&zwnj;وام لێخۆشبوو میهره&zwnj;بانه (بۆ باوه&zwnj;ڕداران).';
$t[153] = 'ئه&zwnj;ی محمد (صلی الله علیه وسلم) خاوه&zwnj;نانی کتێب داوای ئه&zwnj;وه&zwnj;ت لێده&zwnj;که&zwnj;ن که له ئاسمانه&zwnj;وه کتێبێکیان بۆ دابه&zwnj;زێنیت، جا ئه&zwnj;وانه به&zwnj;ڕاستی داوای له&zwnj;وه گه&zwnj;وره&zwnj;تریان له موسا کرد، وتیان: ده&zwnj;مانه&zwnj;وێت خوامان به&zwnj;ئاشکرا نیشان بده&zwnj;یت، ئه&zwnj;سا هه&zwnj;وره بروسکه&zwnj;یه&zwnj;ک لێیدان له سه&zwnj;رئه&zwnj;نجامی ئه&zwnj;و سته&zwnj;مه ناڕه&zwnj;وایه&zwnj;و (ئه&zwnj;و داخوازیه نادروسه&zwnj;یاندا) که&zwnj;چی له&zwnj;وه&zwnj;ودوا گوێره&zwnj;که په&zwnj;رستیانکرد له دوای ئه&zwnj;و هه&zwnj;موو به&zwnj;ڵگه و موعجیزه ئاشکرایانه که بۆیان هات، جا له&zwnj;وه&zwnj;شیان خۆش بووین و به&zwnj;ڵگه&zwnj;و موعجیزه و به&zwnj;رنامه&zwnj;ی ئاشکرامان به&zwnj;خشی به موسا (تا ڕێنمووییان بکات).';
$t[154] = 'بۆ چه&zwnj;سپاندن و وه&zwnj;رگرتنی په&zwnj;یمانه&zwnj;که کێوی (طور) مان به&zwnj;رز کرده&zwnj;وه به&zwnj;سه&zwnj;ریانداو پێمان وتن: که له ده&zwnj;روازه&zwnj;ی شاری (قدس)ه&zwnj;وه به&zwnj;سوژده&zwnj;وه بچنه ژوره&zwnj;وه (هه&zwnj;روه&zwnj;ها فرمانمانداو) وتمان: له ڕۆژانی شه&zwnj;موواندا ده&zwnj;ستدرێژی مه&zwnj;که&zwnj;ن و (ڕاوه ماسی مه&zwnj;که&zwnj;ن، بۆ ئه&zwnj;و مه&zwnj;به&zwnj;سته&zwnj;) په&zwnj;یمانێکی توندو به&zwnj;هێزمان لێوه&zwnj;رگرتن.';
$t[155] = 'ئینجا به&zwnj;هۆی په&zwnj;یمان شکاندنیان و بێ باوه&zwnj;ڕیان به&zwnj;ئایه&zwnj;ته&zwnj;کانی خواو کوشتنی به&zwnj;ناحه&zwnj;قی پێغه&zwnj;مبه&zwnj;ران له&zwnj;لایه&zwnj;ن ئه&zwnj;وانه&zwnj;وه و ئه&zwnj;و قسه&zwnj;یه&zwnj;شیان که ده&zwnj;ڵێن دڵی ئێمه داخراوه و به&zwnj;رگی پێوه&zwnj;یه (مه&zwnj;به&zwnj;ستیان ئه&zwnj;وه&zwnj;یه که ئاماده&zwnj;نین گوێ بۆ هیچ ڕاستیه&zwnj;ک بگرن، خوا خه&zwnj;شمی لێگرتن و نه&zwnj;فرینی لێکردن) نه&zwnj;خێر به&zwnj;و جۆره نیه که ئه&zwnj;وانه ده&zwnj;یڵێن، به&zwnj;ڵکو خوا مۆری ناوه به&zwnj;سه&zwnj;ر دڵ و ده&zwnj;روونیاندا به&zwnj;هۆی بێ باوه&zwnj;ڕ بوونیانه&zwnj;وه&zwnj;، جا هه&zwnj;ر بۆیه که&zwnj;مێکیان نه&zwnj;بێت باوه&zwnj;ڕناهێنن.';
$t[156] = '(هه&zwnj;روه&zwnj;ها خوا ڕقی لێیانه&zwnj;) به&zwnj;هۆی بێ باوه&zwnj;ڕ بوون و ئه&zwnj;و بوختانه گه&zwnj;وره&zwnj;یه&zwnj;ی که بۆ مه&zwnj;ریه&zwnj;م هه&zwnj;ڵیانبه&zwnj;ست...';
$t[157] = 'به&zwnj;هۆی ئه&zwnj;و قسه&zwnj;یه&zwnj;شیان که ده&zwnj;یانوت به ڕاستی ئێمه مه&zwnj;سیح پێغه&zwnj;مبه&zwnj;ری خوا عیسای کوڕی مه&zwnj;ریه&zwnj;ممان کوشتووه&zwnj;، که&zwnj;چی له&zwnj;ڕاستیدا نه&zwnj;کوشتوویانه&zwnj;و نه له قه&zwnj;ناره&zwnj;و خاچیانداو، به&zwnj;ڵکو لێیان تێکچووه و (که&zwnj;سێکی تریان کوشتووه له بری ئه&zwnj;و)، به&zwnj;ڕاستی ئه&zwnj;وانه&zwnj;ی که بیرو بۆچوونی جیاوازیان له&zwnj;و کێشه&zwnj;یه&zwnj;دا هه&zwnj;یه&zwnj;، خۆشیان له گوماندان تیایدا و ئه&zwnj;وانه هیچ زانستیه&zwnj;کی ڕاستیان نیه له&zwnj;و باره&zwnj;یه&zwnj;وه&zwnj;، جگه له شوێنکه&zwnj;وتنی گومان نه&zwnj;بێت و به&zwnj;دڵنیاییه&zwnj;وه (ده&zwnj;ڵێین) که نه&zwnj;یانکوشتووه&zwnj;و بۆیان نه&zwnj;کوژراوه&zwnj;.';
$t[158] = 'نه&zwnj;خێر، به&zwnj;ڵکو خوا به&zwnj;رزی کرده&zwnj;وه بۆ لای خۆی، خوا هه&zwnj;میشه&zwnj;و به&zwnj;رده&zwnj;وام باڵاده&zwnj;ست و دانایه&zwnj;.';
$t[159] = 'هیچ که&zwnj;سێک نیه له خاوه&zwnj;نانی کتێب، که باوه&zwnj;ڕ نه&zwnj;هێنێت به عیسا پێش ئه&zwnj;وه&zwnj;ی گیانی ده&zwnj;ربچێت، به&zwnj;ڵکو سوێند به&zwnj;خوا هه&zwnj;ر باوه&zwnj;ڕ ده&zwnj;هێنێت پێی له پێش مردنیدا (له سه&zwnj;ره&zwnj;مه&zwnj;رگدا په&zwnj;رده له&zwnj;سه&zwnj;ر چاویان لاده&zwnj;درێت و گه&zwnj;لێک ڕاستی ده&zwnj;رده&zwnj;که&zwnj;وێت بۆیان، یاخود کاتێک عیسا داده&zwnj;به&zwnj;زێت گاورو جووه&zwnj;کانی ئه&zwnj;و کاته باوه&zwnj;ڕی دروستی پێده&zwnj;هێنن) و له ڕۆژی قیامه&zwnj;تیشدا شایه&zwnj;ته به&zwnj;سه&zwnj;ریانه&zwnj;وه (که ئه&zwnj;وانه&zwnj;ی گومڕا بوون، خۆیان گومڕا بوون و ئه&zwnj;م کاتی خۆی هه&zwnj;ر ڕێگه&zwnj;ی ڕاستی نیشانداون).';
$t[160] = 'به&zwnj;هۆی زوڵم و سته&zwnj;مێک له&zwnj;لایه&zwnj;ن ئه&zwnj;وانه&zwnj;ی که بوونه&zwnj;ته جوو و به&zwnj;هۆی ئه&zwnj;وه&zwnj;ی که زۆر کۆسپیان ده&zwnj;خسته به&zwnj;ر ڕێبازی خوا، ئێمه هه&zwnj;ندێک نازو نیعمه&zwnj;تمان لێ حه&zwnj;رام کردن که پێشتر بۆیان حه&zwnj;ڵاڵ بوو...';
$t[161] = 'هه&zwnj;روه&zwnj;ها به&zwnj;هۆی سوو خۆریانه&zwnj;وه که به&zwnj;ڕاستی لێیان حه&zwnj;رام کرابوو و به&zwnj;هۆی ئه&zwnj;وه&zwnj;شه&zwnj;وه که ماڵی خه&zwnj;ڵکی به&zwnj;ناڕه&zwnj;وا ده&zwnj;خۆن (هه&zwnj;ندێک شتی تری حه&zwnj;ڵاڵمان لێ حه&zwnj;رام کردن)، بێگومان بۆ ئه&zwnj;وانه&zwnj;یان که بێ باوه&zwnj;ڕن سزایه&zwnj;کی به&zwnj;ئێشمان ئاماده کردووه&zwnj;.';
$t[162] = 'به&zwnj;ڵام ئه&zwnj;وانه&zwnj;یان که له زانستی و زانیاریدا ڕۆچوون، هه&zwnj;روه&zwnj;ها ئیمانداران (له&zwnj;وانیش و له&zwnj;ئێمه&zwnj;یش)، باوه&zwnj;ڕ ده&zwnj;هێنن به&zwnj;وه&zwnj;ی بۆ تۆ ڕه&zwnj;وانه&zwnj;کراوه و به&zwnj;وه&zwnj;ش پێش تۆ ڕه&zwnj;وانه کراوه&zwnj;، به&zwnj;تایبه&zwnj;ت ئه&zwnj;وانه&zwnj;ی که به&zwnj;چاکی نوێژه&zwnj;کانیان به&zwnj;دروشمه&zwnj;کانه&zwnj;وه ئه&zwnj;نجام ده&zwnj;ده&zwnj;ن و زه&zwnj;کاتیش ده&zwnj;ده&zwnj;ن، باوه&zwnj;ڕیشیان به&zwnj;خواو به&zwnj;رۆژی دوایی هه&zwnj;یه&zwnj;، ئا ئه&zwnj;وانه له ئاینده&zwnj;یه&zwnj;کی نزیکدا پاداشتی گه&zwnj;وره&zwnj;و بێ سنووریان ده&zwnj;ده&zwnj;ینه&zwnj;وه&zwnj;.';
$t[163] = 'به&zwnj;ڕاستی ئێمه (وحی)و نیگامان بۆ تۆ ناردووه هه&zwnj;ر وه&zwnj;کو چۆن (وحی) و نیگامان بۆ نوح و پێغه&zwnj;مبه&zwnj;رانی دوای ئه&zwnj;و ناردووه&zwnj;، هه&zwnj;روه&zwnj;ها (وحی) و نیگامان ناردووه بۆ ئیبراهیم و ئیسماعیل و ئیسحاق و یه&zwnj;عقوب و (هه&zwnj;ندێک) له کوڕانی یه&zwnj;عقوب و عیسا و ئه&zwnj;یوب و یونس و هاروون و سوله&zwnj;یمان، زه&zwnj;بوریشمان به داود به&zwnj;خشیووه (کتێبێک بووه زۆربه&zwnj;ی ته&zwnj;سبیحات و ستایشی په&zwnj;روه&zwnj;ردگار بووه&zwnj;).';
$t[164] = 'بێگومان به&zwnj;سه&zwnj;رهاتی هه&zwnj;ندێک له پێغه&zwnj;مبه&zwnj;رانمان بۆ گێڕاویته&zwnj;وه&zwnj;، به&zwnj;سه&zwnj;رهاتی هه&zwnj;ندێکی تریشمان بۆ نه&zwnj;گێڕاویته&zwnj;وه&zwnj;، به&zwnj;ڕاستی خوای گه&zwnj;وره خۆی له&zwnj;گه&zwnj;ڵ موسادا گفتوگۆی یه&zwnj;کسه&zwnj;ری و ڕاسته&zwnj;وخۆی ئه&zwnj;نجامداوه&zwnj;.';
$t[165] = 'پێغه&zwnj;مبه&zwnj;رانمان (ڕه&zwnj;وانه کردووه&zwnj;) که مژده&zwnj;ده&zwnj;رن (به ئیمانداران) و ترسێنه&zwnj;رن بۆ (یاخیه&zwnj;کان)، تا خه&zwnj;ڵکی هیچ به&zwnj;ڵگه&zwnj;یه&zwnj;کیان به&zwnj;ده&zwnj;سته&zwnj;وه نه&zwnj;مێنێت (له&zwnj;سه&zwnj;ر بێ دینی و یاخی بوونیان) دوای ئه&zwnj;وه&zwnj;ی خوا پێغه&zwnj;مبه&zwnj;رانی بۆ ڕه&zwnj;وانه کردوون، بێگومان هه&zwnj;میشه&zwnj;و به&zwnj;رده&zwnj;وام خوا باڵاده&zwnj;ست و دانایه&zwnj;.';
$t[166] = '(ئه&zwnj;ی محمد (صلی الله علیه وسلم)، هه&zwnj;رچه&zwnj;نده ئه&zwnj;و خه&zwnj;ڵکه باوه&zwnj;ڕت پێناکه&zwnj;ن) به&zwnj;ڵام خوا خۆی شایه&zwnj;ته له&zwnj;سه&zwnj;ر ئه&zwnj;و قورئانه&zwnj;ی که ڕه&zwnj;وانه&zwnj;ی کردووه بۆت، به&zwnj;ئاگاداری و زانیاری خۆیشی ڕه&zwnj;وانه&zwnj;ی کردووه&zwnj;، فریشته&zwnj;کانیش شایه&zwnj;تی ده&zwnj;ده&zwnj;ن (له&zwnj;سه&zwnj;ر ئه&zwnj;وه&zwnj;، هه&zwnj;رچه&zwnj;نده&zwnj;) خوا خۆی به&zwnj;سه بۆ ئه&zwnj;و شایه&zwnj;تییه&zwnj;.';
$t[167] = 'به&zwnj;ڕاستی ئه&zwnj;وانه&zwnj;ی که بێ باوه&zwnj;ڕ بوون و ڕێبازی بێ باوه&zwnj;ڕیان گرتۆته&zwnj;به&zwnj;ر و به&zwnj;ربه&zwnj;ستیشیان دانا له&zwnj;به&zwnj;رده&zwnj;م ڕێبازی خوادا (به&zwnj;شێنه&zwnj;یی و نه&zwnj;رم و نیانی)، بێگومان ئه&zwnj;وانه گومڕا بوون و له ڕاستی زۆر دوور که&zwnj;وتوونه&zwnj;ته&zwnj;وه&zwnj;.';
$t[168] = 'به&zwnj;ڕاستی ئه&zwnj;وانه&zwnj;ی که بێ باوه&zwnj;ڕ بوون و سته&zwnj;میان کردووه خوا نه&zwnj;یویستووه لێیان خۆش بێت، هیدایه&zwnj;ت و ڕێنمویشیان بکات بۆ ڕێگه و ڕێبازێک (که سوودی لێوه&zwnj;ربگرن)...';
$t[169] = 'جگه له ڕێگه&zwnj;ی دۆزه&zwnj;خ که ده&zwnj;یانگه&zwnj;یه&zwnj;نێته ناوی و ژیانی نه&zwnj;مریی به&zwnj;ناچاریی ده&zwnj;بێت تیایدا به&zwnj;رنه&zwnj;سه&zwnj;ر، ئه&zwnj;و سزادانه&zwnj;شیان لای خوا زۆر ئاسانه (چونکه ئه&zwnj;وانه شایسته&zwnj;ن).';
$t[170] = 'ئه&zwnj;ی خه&zwnj;ڵکینه به&zwnj;ڕاستی له&zwnj;لایه&zwnj;ن په&zwnj;روه&zwnj;ردگارتانه&zwnj;وه پێغه&zwnj;مبه&zwnj;رێکتان بۆ هاتووه به&zwnj;حه&zwnj;ق و ڕاستی، ده ئێوه&zwnj;ش ئیمان و باوه&zwnj;ڕی پێ بهێنن، ئه&zwnj;وه چاکتره بۆتان، خۆ ئه&zwnj;گه&zwnj;ر باوه&zwnj;ڕ نه&zwnj;هێنن، ئه&zwnj;وه دڵنیابن که هه&zwnj;رچی له ئاسمانه&zwnj;کان و زه&zwnj;ویدا هه&zwnj;یه هه&zwnj;ر خوا خاوه&zwnj;نیانه (ئاتاجی ئیمانی ئێوه نیه&zwnj;)، چونکه خوا هه&zwnj;میشه&zwnj;و به&zwnj;رده&zwnj;وام زاناو دانایه&zwnj;.';
$t[171] = 'ئه&zwnj;ی خاوه&zwnj;نانی کتێب زیاده&zwnj;ڕه&zwnj;وی مه&zwnj;که&zwnj;ن له ئاینه&zwnj;که&zwnj;تاندا، جگه له حه&zwnj;ق و ڕاستی هیچی تر به ده&zwnj;م خواوه مه&zwnj;ڵێن، به&zwnj;ڕاستی مه&zwnj;سیح، عیسای کوڕی مه&zwnj;ریه&zwnj;م جگه له&zwnj;وه&zwnj;ی که فرستاده&zwnj;ی خوایه (شتێکی تر نه&zwnj;بووه&zwnj;)، له دایک بوونه&zwnj;که&zwnj;ی به&zwnj;فه&zwnj;رمانی خوا بووه که&zwnj;له مه&zwnj;ریه&zwnj;مدا (به&zwnj;رجه&zwnj;سته بوو)، عیسا ڕۆح و گیانێکی (تایبه&zwnj;تیه&zwnj;) له&zwnj;لایه&zwnj;ن خواوه به&zwnj;خشراوه&zwnj;، ده ئیتر باوه&zwnj;ڕی دامه&zwnj;زراو به خوایه&zwnj;تی خواو به پێغه&zwnj;مبه&zwnj;رانی بهێنن، هه&zwnj;رگیز مه&zwnj;ڵێن خوا سیانه (له&zwnj;و گوفتاره نابه&zwnj;جێ یه&zwnj;) واز بهێنن چاکتره و قازانجتانه&zwnj;، چونکه به&zwnj;ڕاستی خوا ته&zwnj;نها یه&zwnj;ک تاکه خوایه&zwnj;، پاک و بێگه&zwnj;ردو دووره له&zwnj;وه&zwnj;ی که منداڵی ببێت (بوختانی ناقۆڵای وا، چۆن ده&zwnj;که&zwnj;ن)؟! هه&zwnj;رچی له ئاسمانه&zwnj;کان و هه&zwnj;رچی له زه&zwnj;ویدا هه&zwnj;یه هه&zwnj;ر هه&zwnj;مووی ئه&zwnj;و خاوه&zwnj;نیانه&zwnj;، بێگومان خوا به&zwnj;سه که به&zwnj;دیهێنه&zwnj;رو سه&zwnj;رپه&zwnj;رشتیاری (دروستکراوانی بێت).';
$t[172] = 'هه&zwnj;رگیز مه&zwnj;سیح لاری له&zwnj;وه نیه که به&zwnj;نده&zwnj;ی خوا بێت، هه&zwnj;روه&zwnj;ها فریشته نزیکه&zwnj;کانیش، جا ئه&zwnj;وه&zwnj;ی لاری و سه&zwnj;رپێچی بکات له په&zwnj;رستنی خواو خۆی به&zwnj;زل بزانێت، ئه&zwnj;وه (با دڵنیابێت) هه&zwnj;مووان له ئاینده&zwnj;دا لای ئه&zwnj;و زاته ده&zwnj;بێت کۆببنه&zwnj;وه (بۆ لێپرسینه&zwnj;وه&zwnj;).';
$t[173] = '(جا ئه&zwnj;و کاته&zwnj;) ئه&zwnj;وانه&zwnj;ی که باوه&zwnj;ڕیان هێناوه و کاروکرده&zwnj;وه چاکه&zwnj;کانیان ئه&zwnj;نجامداوه&zwnj;، ئه&zwnj;وه خوا به ته&zwnj;واوی و به&zwnj;چاکی پاداشته&zwnj;کانیان ده&zwnj;داته&zwnj;وه و له فه&zwnj;زڵ و به&zwnj;خششی تایبه&zwnj;تی خۆی بۆیان زیادیش ده&zwnj;کات، به&zwnj;ڵام ئه&zwnj;وانه&zwnj;ی سه&zwnj;رپێچی و لاریان کردووه و خۆیان به&zwnj;زل زانیووه&zwnj;، ئه&zwnj;وه خوا سزای زۆر به&zwnj;ئێشیان ده&zwnj;دات، ئه&zwnj;وسا بێجگه له خوا که&zwnj;سێکیان ده&zwnj;ست ناکه&zwnj;وێت که پشتیوانیان لێ بکات و سه&zwnj;ریان بخات (ئه&zwnj;ڵبه&zwnj;ته ئه&zwnj;و زاته&zwnj;ش به&zwnj;زه&zwnj;یی پیایاندا نایه&zwnj;ته&zwnj;وه&zwnj;).';
$t[174] = 'ئه&zwnj;ی خه&zwnj;ڵکینه به&zwnj;ڕاستی له لایه&zwnj;ن په&zwnj;روه&zwnj;ردگارتانه&zwnj;وه به&zwnj;ڵگه&zwnj;و نیشانه&zwnj;ی ئاشکراتان بۆ هاتووه (که محمد پێغه&zwnj;مبه&zwnj;وه (صلی الله علیه وسلم)و نوورو ڕووناکیه&zwnj;کی ئاشکراشمان بۆ دابه&zwnj;زاندوون (که قورئان)ه&zwnj;.';
$t[175] = 'جا ئه&zwnj;وانه&zwnj;ی باوه&zwnj;ڕیان به&zwnj;خوا هێناوه و شوێنی به&zwnj;رنامه&zwnj;که&zwnj;ی که&zwnj;وتوون، ئه&zwnj;وه له ئاینده&zwnj;یه&zwnj;کی نزیکدا خوا ده&zwnj;یانخاته ژێر سایه&zwnj;ی فه&zwnj;زڵ و ڕه&zwnj;حمه&zwnj;تی تایبه&zwnj;تی خۆیه&zwnj;وه و هیدایه&zwnj;ت و ڕێنمووییان ده&zwnj;کات بۆ لای خۆی له ڕێگه&zwnj;ی ڕێبازێکی ڕاست و دروسته&zwnj;وه&zwnj;.';
$t[176] = 'ئه&zwnj;ی محمد (صلی الله علیه وسلم) داوای فتوات لێده&zwnj;که&zwnj;ن ده&zwnj;رباره&zwnj;ی (کلاله&zwnj;) که بریتی یه&zwnj;: له که&zwnj;سێک باوک و نه&zwnj;وه&zwnj;ی نه&zwnj;بێت، تۆش بڵێ: خوا وه&zwnj;ڵامتان ده&zwnj;داته&zwnj;وه له&zwnj;و باره&zwnj;یه&zwnj;وه&zwnj;، ئه&zwnj;گه&zwnj;ر که&zwnj;سێک (پیاوێک) وه&zwnj;فاتی کردو منداڵی نه&zwnj;بوو، هه&zwnj;ر ته&zwnj;نها خوشکێکی هه&zwnj;بوو، ئه&zwnj;وه نیوه&zwnj;ی سامانه&zwnj;که&zwnj;ی بۆ ئه&zwnj;وه (نیوه&zwnj;که&zwnj;ی تریشی ده&zwnj;خرێته دارایی گشتی یه&zwnj;وه ئه&zwnj;گه&zwnj;ر هه&zwnj;بێت و ئه&zwnj;گه&zwnj;ر نه&zwnj;بوو له به&zwnj;رژه&zwnj;وه&zwnj;ندیه&zwnj;کی گشتیدا به&zwnj;کار ده&zwnj;هێنرێت)، (به&zwnj;ڵام ئه&zwnj;گه&zwnj;ر مردووه&zwnj;که ئافره&zwnj;ت بوو) ئه&zwnj;وا براکه&zwnj;ی هه&zwnj;موو میراتیه&zwnj;که&zwnj;ی ده&zwnj;بات، ئه&zwnj;گه&zwnj;ر ئافره&zwnj;ته&zwnj;که منداڵی له&zwnj;دوا به&zwnj;جێ نه&zwnj;مابوو، (خۆ ئه&zwnj;گه&zwnj;ر پیاوه مردووه&zwnj;که&zwnj;) دوو خوشکی له دوا به&zwnj;جێ مابوو ئه&zwnj;وه دووبه&zwnj;ش له سێ به&zwnj;شی ئه&zwnj;وه&zwnj;ی به&zwnj;جێی هێشتووه بۆ ئه&zwnj;وان ده&zwnj;بێت، خۆ ئه&zwnj;گه&zwnj;ر میراتگران پیاوان و ئافره&zwnj;تان بوون هه&zwnj;موویشیان له خوشک و برا پێکهاتبوون ئه&zwnj;وه بۆ نێرینه&zwnj;یه&zwnj;کیان ئه&zwnj;ندازه&zwnj;ی به&zwnj;شی دوو مێینه هه&zwnj;یه&zwnj;، خوای گه&zwnj;وره ئه&zwnj;م شتانه&zwnj;تان بۆ ڕوون ده&zwnj;کاته&zwnj;وه نه&zwnj;وه&zwnj;کو سرگه&zwnj;ردان ببن، خوای گه&zwnj;وره&zwnj;ش به هه&zwnj;موو شتێک زانایه&zwnj;.';
?>