<?php
$t = array();
$t[1] = 'سه&zwnj;رنجی سه&zwnj;ره&zwnj;تای سووره&zwnj;تی (البقرة) بده&zwnj;.';
$t[2] = 'ئه&zwnj;م قورئانه دابه&zwnj;زێنراوه له&zwnj;لایه&zwnj;ن په&zwnj;روه&zwnj;ردگاری خاوه&zwnj;ن به&zwnj;زه&zwnj;یی و میهره&zwnj;بانه&zwnj;وه&zwnj;.';
$t[3] = 'کتێبێکه&zwnj;؛ ئایه&zwnj;ت و فه&zwnj;رمانه&zwnj;کانی به&zwnj;ش کراوه و جیاکراوه&zwnj;ته&zwnj;وه (ئیمان وکوفر، حه&zwnj;ڵاڵ وحه&zwnj;رام چاکه وخراپه&zwnj;... هتد)، قورئانێکه به عه&zwnj;ره&zwnj;بیه&zwnj;کی ڕه&zwnj;وان بۆ که&zwnj;سانێک که ده&zwnj;یزانن و شاره&zwnj;زاییان هه&zwnj;یه (له زمانه&zwnj;وانی و زانسته&zwnj;کان).';
$t[4] = 'هه&zwnj;ر ئه&zwnj;م قورئانه مژده&zwnj;به&zwnj;خشه به&zwnj;ئیمانداران، ترسێنه&zwnj;ری کافرانه&zwnj;، که&zwnj;چی زۆربه&zwnj;ی موشریکان پشتیان تێکرد و به&zwnj;رهه&zwnj;ڵستیان کردو نایبیستن.';
$t[5] = 'وتیشیان: ئێمه دڵه&zwnj;کانمان داپۆشراوه له ئاستی ئه&zwnj;م به&zwnj;رنامه&zwnj;یه&zwnj;دا که تۆ بانگی ئێمه ده&zwnj;که&zwnj;یت بۆ لای، گو&zwnj;ێشمان که&zwnj;ڕو قورسه له ئاستیدا، له نێوان ئێمه و تۆدا په&zwnj;رده و به&zwnj;ربه&zwnj;ست هه&zwnj;یه&zwnj;، تۆ هه&zwnj;وڵ و کۆششی خۆت بده&zwnj;، بێگومان ئێمه&zwnj;ش هه&zwnj;وڵ و کۆششی خۆمان ده&zwnj;ده&zwnj;ین.';
$t[6] = 'ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر (صلی الله علیه وسلم)، پێیان بڵێ: دڵنیابن که من هه&zwnj;ر به&zwnj;شه&zwnj;رێکم وه&zwnj;ک ئێوه&zwnj;، (جیاوازیه&zwnj;که ته&zwnj;نها ئه&zwnj;وه&zwnj;یه&zwnj;): من وه&zwnj;حی ونیگام بۆ ده&zwnj;کرێت که بێگومان خوای ئێوه خوایه&zwnj;کی تاک و ته&zwnj;نهایه&zwnj;، که&zwnj;وابوو ئێوه&zwnj;ش ڕووی تێ بکه&zwnj;ن، به&zwnj;ڕاستی بیپه&zwnj;رستن؛ له ڕێبازه&zwnj;که&zwnj;ی لامه&zwnj;ده&zwnj;ن، داوای لێخۆشبوونی لێبکه&zwnj;ن و چاک بزانن که وه&zwnj;یل و سزای سه&zwnj;خت بۆ موشریک و هاوه&zwnj;ڵگه&zwnj;ران ئاماده&zwnj;یه&zwnj;.';
$t[7] = 'ئه&zwnj;وانه&zwnj;ی که زه&zwnj;کات ناده&zwnj;ن و ئه&zwnj;وانه&zwnj;ش که بێ باوه&zwnj;ڕن به ڕۆژی قیامه&zwnj;ت.';
$t[8] = 'به&zwnj;ڕاستی ئه&zwnj;وانه&zwnj;ی که ئیمان و باوه&zwnj;ڕیان هێناوه و کاروکرده&zwnj;وه چاکه&zwnj;کانیان ئه&zwnj;نجامداوه پاداشتی نه&zwnj;بڕاوه&zwnj;یان بۆ هه&zwnj;یه...';
$t[9] = 'ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر (صلی الله علیه وسلم) پێیان بڵێ: باشه ئاخر ئێوه باوه&zwnj;ڕتان به&zwnj;و زاته نیه که زه&zwnj;وی له دوو ڕۆژدا دروست کردووه&zwnj;؟ (ئاڵبه&zwnj;ته ئه&zwnj;و ڕۆژانه مه&zwnj;گه&zwnj;ر خوا خۆی بزانێت درێژی ماوه&zwnj;که&zwnj;یان چانده&zwnj;)، چه&zwnj;ند هاوه&zwnj;ڵێکیشی بۆ بڕیار ده&zwnj;ده&zwnj;ن؟! ئه&zwnj;و به&zwnj;دیهێنه&zwnj;ر و دروستکاره په&zwnj;روه&zwnj;ردگاری هه&zwnj;ر هه&zwnj;موو جیهانیان و بوونه&zwnj;وه&zwnj;ره&zwnj;.';
$t[10] = 'له&zwnj;سه&zwnj;ر ڕووکاری زه&zwnj;وی كێوه&zwnj;کانی دامه&zwnj;زراندوه (که کار ده&zwnj;که&zwnj;نه سه&zwnj;ر ئاوو هه&zwnj;وا، دیمه&zwnj;نی جوان ده&zwnj;سازێنن، له&zwnj;نگه&zwnj;ری ده&zwnj;گرن، ڕووبه&zwnj;ری فراوانتر ده&zwnj;که&zwnj;ن... هتد) به&zwnj;ره&zwnj;که&zwnj;تی زۆری تێدا دابین کردووه (وه&zwnj;ک نه&zwnj;وت وجۆره&zwnj;ها کانزا، سه&zwnj;ره&zwnj;ڕای جیهانی ڕوه&zwnj;ک و.. هتد)، هه&zwnj;موو ڕزق و ڕۆزیه&zwnj;کانی تیادا بڕیارداوه هه&zwnj;مووی له ماوه&zwnj;ی چوار ڕۆژدا به دوو ڕۆژه&zwnj;که&zwnj;ی پێشووشه&zwnj;وه (قه&zwnj;باره و ڕه&zwnj;نگ و تام و بۆن و وه&zwnj;رز و شوێنی هه&zwnj;ر میوه و ده&zwnj;غڵ و دانێک، هه&zwnj;روه&zwnj;ها گیانداران و باڵنده&zwnj;کانیش) تا نیازو داخوازیه&zwnj;کانی ئاده&zwnj;میزاد جێبه&zwnj;جێ بکات.';
$t[11] = 'ئینجا ویستی ئاسمان ڕێک بخات و دروستی بکات له&zwnj;کاتێکدا که دوکه&zwnj;ڵ بوو، جا به ئاسمان و زه&zwnj;وی فه&zwnj;رموو: فه&zwnj;رمانبه&zwnj;ردار و ملکه&zwnj;چ بن، به&zwnj;خۆشی خۆتان یان به&zwnj;زۆر بتانخه&zwnj;مه ژێر ده&zwnj;سه&zwnj;ڵاتی خۆمه&zwnj;وه&zwnj;؟! هه&zwnj;ردوولا خێرا وتیان: ملکه&zwnj;چی تۆین و چۆن ده&zwnj;فه&zwnj;رمویت وا ده&zwnj;که&zwnj;ین، (بێگومان کارێکی ئاسانه بۆ خوای به&zwnj;ده&zwnj;سه&zwnj;ڵات، زه&zwnj;وی و ئاسمان بێنێته قسه&zwnj;، چونکه ئاده&zwnj;میزاد جۆره&zwnj;ها ئامێری هێناوه&zwnj;ته قسه&zwnj;، ده&zwnj;م و زمان و لێویشیان نیه!!).';
$t[12] = 'له ماوه&zwnj;ی دوو ڕۆژدا (له&zwnj;و ڕۆژانه&zwnj;ی که هه&zwnj;ر خۆی ماوه&zwnj;یان ده&zwnj;زانێت) ئاسمانی کرده حه&zwnj;وت چین و فه&zwnj;رمانی بۆ هه&zwnj;ر چینێکیش ده&zwnj;رکرد، ئینجا ده&zwnj;فه&zwnj;رموێت: ئاسمانی دنیاشمان ڕازانده&zwnj;وه به ئه&zwnj;ستێره&zwnj;وکردمانه چراخان و کردمانه هۆی پاڕاستن، (له شه&zwnj;ڕی شه&zwnj;یتانه&zwnj;کان)، ئا ئه&zwnj;و شتانه هه&zwnj;مووی به ویست و فه&zwnj;رمانی زاتێکه که باڵاده&zwnj;ست و زانایه&zwnj;.';
$t[13] = 'جا ئه&zwnj;گه&zwnj;ر له&zwnj;گه&zwnj;ڵ ئه&zwnj;م هه&zwnj;موو باسی ده&zwnj;سه&zwnj;ڵات و توانایی و زانایی خوای گه&zwnj;وره&zwnj;دا پشتیان هه&zwnj;ڵکرد و گوێیان نه&zwnj;گرت، پێیان بڵێ: من ئاگادارتان ده&zwnj;که&zwnj;م له&zwnj;وه&zwnj;ی که شریخه و بروسکه و کاره&zwnj;ساتێکتان به&zwnj;سه&zwnj;ر بێت، وه&zwnj;ک ئه&zwnj;وه&zwnj;ی که به&zwnj;سه&zwnj;ر عاد و ثمود هات...';
$t[14] = 'کاتێک پێغه&zwnj;مبه&zwnj;رانیان بۆ ڕه&zwnj;وانه کرا له پێشتر و دواتریشدا، به&zwnj;م فه&zwnj;رمانه پیرۆزه که&zwnj;: جگه له خوا که&zwnj;سی تر مه&zwnj;په&zwnj;رستن، که&zwnj;چی وتیان: ئه&zwnj;گه&zwnj;ر په&zwnj;روه&zwnj;ردگارمان بیوستایه ڕێنمویمان بکات ئێوه&zwnj;ی نه ده&zwnj;نارد؛ به&zwnj;ڵکو فریشته&zwnj;ی داده&zwnj;به&zwnj;زان!! که&zwnj;وابوو ئێمه بێگومان بڕوا ناکه&zwnj;ین به&zwnj;و په&zwnj;یامه&zwnj;ی که به ئێوه&zwnj;دا ڕه&zwnj;وانه&zwnj;کراوه&zwnj;.';
$t[15] = 'ئه&zwnj;وسا قه&zwnj;ومی عاد به&zwnj;ناحه&zwnj;ق خۆیان به&zwnj;زل و گه&zwnj;وره&zwnj;زانی له زه&zwnj;ویدا و وتیان: کێ هه&zwnj;یه به ئه&zwnj;ندازه&zwnj;ی ئێمه هێزو ده&zwnj;سه&zwnj;ڵاتی هه&zwnj;بێت؟ باشه ئایا ئه&zwnj;وه نه&zwnj;یانزانی به&zwnj;ڕاستی ئه&zwnj;و خوایه&zwnj;ی که دروستی کردوون، ئه&zwnj;و له&zwnj;وان هێزی زۆر زیاتره (به&zwnj;ڵکو هه&zwnj;ر به&zwnj;راورد ناکرێت) و ئه&zwnj;وانه هه&zwnj;ر ئینکاری ئایه&zwnj;ت و موعجیزه&zwnj;کانی ئێمه&zwnj;یان ده&zwnj;کرد.';
$t[16] = 'ئه&zwnj;وسا ئیتر ئێمه بایه&zwnj;کی توندو زۆر ساردمان هه&zwnj;ڵکرده سه&zwnj;ریان له چه&zwnj;ند ڕۆژێکی شومدا، تا هه&zwnj;ر له ژیانی دنیادا به&zwnj;سزای سه&zwnj;رشۆڕیان بگه&zwnj;یه&zwnj;نین و تاڵاو بکه&zwnj;ین به&zwnj;گه&zwnj;روویاندا، بێگومان سزای قیامه&zwnj;ت زیاتر سه&zwnj;رشۆڕکه&zwnj;رو تاڵتره و ئه&zwnj;وانه سه&zwnj;رفراز و ڕزگار نابن.';
$t[17] = 'هۆزی - ثمود - یشمان ڕێنموویی کرد بۆ ئیمان و چاکه&zwnj;؛ که&zwnj;چی حه&zwnj;زیان به&zwnj;کوێری کرد، تا چاوساغی و هیدایه&zwnj;ت و ئیمان، هه&zwnj;ر بۆیه ئه&zwnj;وانیش سزای سه&zwnj;رشۆڕکه&zwnj;ر و سه&zwnj;رگه&zwnj;ردانکه&zwnj;ر یه&zwnj;خه&zwnj;ی پێگرتن به&zwnj;هۆی ئه&zwnj;و کارو کرده&zwnj;وانه&zwnj;ی که ده&zwnj;یانکرد.';
$t[18] = 'ئه&zwnj;وسا ئه&zwnj;وانه&zwnj;ی که ئیمان و باوه&zwnj;ڕیان هێنابوو ڕزگارمان کردن، که&zwnj;سانێکی به ته&zwnj;قواو خواناسیش بوون.';
$t[19] = 'ڕۆژێک دێت دوژمنانی خوا تێکرا کۆ ده&zwnj;کرێنه&zwnj;وه و کێش ده&zwnj;کرێن به&zwnj;ره&zwnj;و ئاگری دۆزه&zwnj;خ، به&zwnj;ر&zwnj;و دوایان پاڵه&zwnj;په&zwnj;ستۆیانه&zwnj;.';
$t[20] = 'هه&zwnj;تا کاتێک ده&zwnj;گه&zwnj;نه ئاستی ئاگره&zwnj;که گوێیان و چاوه&zwnj;کانیان و پێسته&zwnj;کانیان شایه&zwnj;تیان له&zwnj;سه&zwnj;ر ده&zwnj;دات که چ کارو کرده&zwnj;وه&zwnj;یه&zwnj;کی نادروست و خراپیان ئه&zwnj;نجام ده&zwnj;دا.';
$t[21] = 'ئه&zwnj;وسا به&zwnj;غه&zwnj;مباریه&zwnj;وه به&zwnj;پێستی خۆیان ده&zwnj;ڵێن: بۆچی شایه&zwnj;تیتان له&zwnj;سه&zwnj;رداین خۆ زه&zwnj;ره&zwnj;رتانه&zwnj;؟ له وه&zwnj;ڵامدا ده&zwnj;ڵێن، ئه&zwnj;و خوایه ئێمه&zwnj;ی هێناوه&zwnj;ته گوفتار که ده&zwnj;توانێت هه&zwnj;موو شتێک بهێنێته قسه و گفتوگۆ، هه&zwnj;ر ئه&zwnj;و زاته&zwnj;شه که یه&zwnj;که&zwnj;مجار به&zwnj;دیهێناون و هه&zwnj;ر بۆلای ئه&zwnj;ویش ده&zwnj;برێنه&zwnj;وه (بێگومان کارێکی ئاسانه بۆخوا چو&zwnj;نکه ئاده&zwnj;میزاد جۆره&zwnj;ها ئامێری هێناوه&zwnj;ته قسه&zwnj;، ده&zwnj;م و زمان و لێویشیان نیه&zwnj;!).';
$t[22] = 'ئێوه کاتی خۆی ئه&zwnj;ونده غه&zwnj;ڕڕا بوون، گوناهو تاوانه&zwnj;کانتان نه&zwnj;ده&zwnj;&zwnj;شارده&zwnj;وه&zwnj;، چونکه مه&zwnj;ترسیتان نه&zwnj;بوو که گوێتان و چاوتان و پێستتان شایه&zwnj;تیتان له&zwnj;سه&zwnj;ر بدات، به&zwnj;ڵام گومانتان وابوو که به&zwnj;ڕاستی خوا زۆر ئاگادار نیه به&zwnj;و کاروکرده&zwnj;وانه&zwnj;ی که ئه&zwnj;نجامی ده&zwnj;ده&zwnj;ن.';
$t[23] = 'ئا ئه&zwnj;وه&zwnj;بوو گومانی ناپه&zwnj;سه&zwnj;ندتان که بووتان به&zwnj;رامبه&zwnj;ر په&zwnj;روه&zwnj;ردگارتان؛ ئێستا بوو به&zwnj;هۆی داخزان وتیاچوونتان و به&zwnj;وهۆیه&zwnj;وه چوونه ڕیزی زه&zwnj;ره&zwnj;رمه&zwnj;ندانه&zwnj;وه&zwnj;.';
$t[24] = 'جا ئه&zwnj;گه&zwnj;ر به&zwnj;ناچاری خۆگری بنوێنن، ئه&zwnj;وه ئاگر جێگه&zwnj;یانه&zwnj;، ئه&zwnj;گه&zwnj;ر داوای لێبووردن و چاوپۆشی بکه&zwnj;ن چاوپۆشیان لێناکرێت و داوایان لێوه&zwnj;رناگیرێت.';
$t[25] = 'ئه&zwnj;وانه به&zwnj;هۆی تاوان و سه&zwnj;رکه&zwnj;شیانه&zwnj;وه که&zwnj;سانێکمان کرده هاوه&zwnj;ڵیان که کاروکرده&zwnj;وه&zwnj;ی ڕابردوو داهوتوویان بۆ بڕازێننه&zwnj;وه، تا سه&zwnj;رئه&zwnj;نجام بڕیاری تۆڵه ئه&zwnj;مانیشی گرته&zwnj;وه وه&zwnj;ک کۆمه&zwnj;ڵانی پێش ئه&zwnj;مان له په&zwnj;ری و ئینسانه&zwnj;کان، به&zwnj;ڕاستی هه&zwnj;ر هه&zwnj;موویان زه&zwnj;ره&zwnj;رمه&zwnj;ند و دۆڕاون.';
$t[26] = 'بێ باوه&zwnj;ڕان ده&zwnj;یانوت: گوێ مه&zwnj;گرن بۆ ئه&zwnj;م قورئانه و بیکه&zwnj;ن به&zwnj;گاڵه و هه&zwnj;را و هوریا (له&zwnj;م سه&zwnj;رده&zwnj;مه&zwnj;دا ئه&zwnj;و ڕاگه&zwnj;یاندنه&zwnj;ی به&zwnj;ده&zwnj;ست ناحه&zwnj;زانه&zwnj;وه&zwnj;یه ئه&zwnj;و ڕۆڵه ده&zwnj;بینێت) بۆ ئه&zwnj;وه&zwnj;ی ئێوه زاڵ ببن و بیبه&zwnj;نه&zwnj;وه&zwnj;.';
$t[27] = '(ئه&zwnj;و جۆره که&zwnj;سانه بۆ کوێ ده&zwnj;چن) که&zwnj;وابوو سوێند به&zwnj;خوا هه&zwnj;ر ده&zwnj;بێت تاڵاوی سزای سه&zwnj;خت بکه&zwnj;ین به&zwnj;گه&zwnj;رووی ئه&zwnj;وانه&zwnj;ی که بێ باوه&zwnj;ڕ بوون، له ئاینده&zwnj;شدا به خراپتر له&zwnj;و کارو کرده&zwnj;وانه&zwnj;ی که ده&zwnj;یانکرد تۆڵه&zwnj;یان لێ ده&zwnj;ستێنین.';
$t[28] = 'ئا ئه&zwnj;وه&zwnj;یه پاداشتی دوژمنانی خوا هه&zwnj;ر ئاگره&zwnj;که&zwnj;ی دۆزه&zwnj;خه&zwnj;، که ماڵ و نیشته&zwnj;جێی هه&zwnj;میشه&zwnj;ییانه&zwnj;، له تۆڵه&zwnj;ی ئه&zwnj;وه&zwnj;ی که سه&zwnj;رسه&zwnj;ختانه به&zwnj;ره&zwnj;نگاری ئایه&zwnj;ته&zwnj;کانی ئێمه&zwnj;یان ده&zwnj;کرد.';
$t[29] = 'له قیامه&zwnj;تدا ئه&zwnj;وانه&zwnj;ی بێ باوه&zwnj;ڕ بوون وتیان: په&zwnj;روه&zwnj;ردگارا، ئه&zwnj;و دوو ده&zwnj;سته&zwnj;یه&zwnj;مان نیشان بده له په&zwnj;ری و ئاده&zwnj;میزاد که گومڕایان کردین بیانخه&zwnj;ینه ژێر پێمانه&zwnj;وه&zwnj;، تا هه&zwnj;ر له ژێره&zwnj;وه بن.';
$t[30] = 'ئه&zwnj;وانه&zwnj;ی به&zwnj;ڕاستی وتیان: په&zwnj;روه&zwnj;ردگارمان (الله) یه&zwnj;، له&zwnj;وه&zwnj;ودوا به&zwnj;رده&zwnj;وام پابه&zwnj;ندی ئه&zwnj;و ڕێبازه ڕاسته بوون، ئه&zwnj;وه له سه&zwnj;ره&zwnj;مه&zwnj;رگدا ده&zwnj;سته ده&zwnj;سته فریشته داده&zwnj;به&zwnj;زن بۆلایان، پێیان ده&zwnj;ڵێن: هیچ ترس و بیمێکتان نه&zwnj;بێت له داهاتوو، هیچ غه&zwnj;م و په&zwnj;ژاره&zwnj;یه&zwnj;کتان نه&zwnj;بێت بۆ ڕابردوو، مژده&zwnj;تان لێ بێت به&zwnj;و به&zwnj;هه&zwnj;شته&zwnj;ی که له کاتی خۆی به&zwnj;ڵێنتان پێ ده&zwnj;درا...';
$t[31] = '(خوای گه&zwnj;وره و فریشته&zwnj;کانیش ده&zwnj;فه&zwnj;رموون): ئێمه یارو یاوه&zwnj;رو هاوکارو پشتیوانی ئێوه&zwnj;ین له ژیانی دنیا و قیامه&zwnj;تیشدا مژده&zwnj;تان لێ بێت له به&zwnj;هه&zwnj;شتدا هه&zwnj;رچی ئاره&zwnj;زووی ده&zwnj;که&zwnj;ن وداوای ده&zwnj;که&zwnj;ن بۆتان ئاماده&zwnj;یه&zwnj;...';
$t[32] = 'ئه&zwnj;و به&zwnj;هره&zwnj;یه&zwnj;ش له&zwnj;لایه&zwnj;ن په&zwnj;روه&zwnj;ردگارێکه&zwnj;وه پێشکه&zwnj;شتان ده&zwnj;کرێت که لێبوورده و میهره&zwnj;بانه&zwnj;.';
$t[33] = 'جا کێ له&zwnj;و که&zwnj;سه جوانگفتار ترو قسه&zwnj;ی به&zwnj;جێ تره که بانگه&zwnj;وازی کردووه بۆ لای خوا، کاروکرده&zwnj;وه&zwnj;ی چاکیشی ئه&zwnj;نجام داوه&zwnj;، وتوشیه&zwnj;تی: به&zwnj;ڕاستی من له ڕیزی موسڵماناندام.';
$t[34] = 'بێگومان چاکه و خراپه وه&zwnj;ک یه&zwnj;ک نین، (تۆ ئه&zwnj;ی باوه&zwnj;ڕدار) به&zwnj;جوانترین و چاکترین شێوه به&zwnj;ره&zwnj;نگاری خراپه و نادروستی بکه&zwnj;، ئه&zwnj;و کاته ئیتر ده&zwnj;بینیت ئه&zwnj;وه&zwnj;ی که له نێوان تۆ و ئه&zwnj;ودا دوژمنایه&zwnj;تی و ساردیه&zwnj;ک هه&zwnj;یه&zwnj;؛ ده&zwnj;بێته دۆستێکی دڵسۆز و گیانی به&zwnj;گیانی.';
$t[35] = 'جا که&zwnj;سیش ناتوانێت به&zwnj;و ڕه&zwnj;فتاره هه&zwnj;ستێت جگه له&zwnj;وانه&zwnj;ی که ئارامیان گرتووه&zwnj;، هه&zwnj;روه&zwnj;ها که&zwnj;سیش ناتوانێت هه&zwnj;ڵوێستی وا بنوێنێت جگه له که&zwnj;سێک نه&zwnj;بێت که خاوه&zwnj;نی به&zwnj;هره&zwnj;یه&zwnj;کی گه&zwnj;وره بێت.';
$t[36] = 'ئه&zwnj;ی ئیماندار؛ ئه&zwnj;گه&zwnj;ر له لایه&zwnj;ن شه&zwnj;یتانه&zwnj;وه خه&zwnj;ته&zwnj;ره و خه&zwnj;یاڵی نادروست له دڵ و ده&zwnj;روونتدا دروست بوو، ئه&zwnj;وه تۆ داوای یارمه&zwnj;تی له خوا بکه و په&zwnj;نای پێ بگره&zwnj;، چونکه به&zwnj;ڕاستی ئه&zwnj;و زاته بیسه&zwnj;ر و زانایه&zwnj;.';
$t[37] = 'له نیشانه و به&zwnj;ڵگه&zwnj;کانی ده&zwnj;سه&zwnj;ڵاتی په&zwnj;روه&zwnj;ردگار شه&zwnj;و و ڕۆژ و خۆر و مانگه&zwnj;، هه&zwnj;رگیز نه&zwnj;که&zwnj;ن سوژده بۆ خۆر، یان مانگ ببه&zwnj;ن، به&zwnj;ڵکو سوژده ته&zwnj;نها بۆ ئه&zwnj;و خوایه ببه&zwnj;ن که دروستی کردوون، ئه&zwnj;گه&zwnj;ر ئێوه خۆتان به به&zwnj;نده&zwnj;ی ڕاسته&zwnj;قینه&zwnj;ی ئه&zwnj;و ده&zwnj;زانن و هه&zwnj;ر ئه&zwnj;و ده&zwnj;په&zwnj;رستن.';
$t[38] = 'جا ئه&zwnj;گه&zwnj;ر بێ بڕواکان خۆیان به زل و گه&zwnj;وره بزانن، (ئه&zwnj;وه وازیان لێ بهێنه&zwnj;) چونکه ئه&zwnj;و فریشتانه&zwnj;ی له خزمه&zwnj;ت په&zwnj;روه&zwnj;ردگاری تۆدان شه&zwnj;وو ڕۆژ سه&zwnj;رگه&zwnj;رمی ته&zwnj;سبیحات و ستایشن، هه&zwnj;رگیز بێزاری ڕوویان تێ ناکات خۆیان به گه&zwnj;وره نازانن.';
$t[39] = 'له به&zwnj;ڵگه و نیشانه&zwnj;کانی خوا ئه&zwnj;وه&zwnj;یه که ده&zwnj;بینیت زه&zwnj;وی کش وماته&zwnj;، جا که&zwnj;تێک باران به&zwnj;سه&zwnj;ریدا ده&zwnj;بارێنین ده&zwnj;خرۆشێت و گه&zwnj;شه و نه&zwnj;ش و نما ده&zwnj;کات، چونکه به&zwnj;ڕاستی ئه&zwnj;و زاته&zwnj;ی که زه&zwnj;وی زیندوو کرده&zwnj;وه، مردووانیش زیندوو ده&zwnj;کاته&zwnj;وه&zwnj;، بێگومان ئه&zwnj;و خوایه ده&zwnj;سه&zwnj;ڵاتی به&zwnj;سه&zwnj;ر هه&zwnj;موو شتێکدا هه&zwnj;یه&zwnj;..';
$t[40] = 'به&zwnj;ڕاستی ئه&zwnj;وانه&zwnj;ی که سه&zwnj;رسه&zwnj;خت و بێ بڕوان و دژایه&zwnj;تی ئایه&zwnj;ت و فه&zwnj;رمانه&zwnj;کانی ئێمه ده&zwnj;که&zwnj;ن و لێی لاده&zwnj;ده&zwnj;ن. خۆیان ناشارنه&zwnj;وه له ئێمه&zwnj;، باشه&zwnj;، ئه&zwnj;وه&zwnj;ی فڕێ بدرێته ناو ئاگری دۆزه&zwnj;خه&zwnj;وه چاکتره یان ئه&zwnj;و که&zwnj;سه&zwnj;ی که به&zwnj;دڵنیایی و ئارامی و هێمنیه&zwnj;وه دێت له ڕۆژی قیامه&zwnj;تدا و به&zwnj;ره&zwnj;و به&zwnj;هه&zwnj;شت به&zwnj;ڕێزه&zwnj;وه به&zwnj;ڕێ ده&zwnj;کرێت؟! ئه&zwnj;ی بێ باوه&zwnj;ڕان هه&zwnj;رچیتان له ده&zwnj;ست دێت بیکه&zwnj;ن، چونکه له ده&zwnj;ستی خوا ده&zwnj;رناچن، دڵنیاش بن خوا بینایه به&zwnj;و کرده&zwnj;وانه&zwnj;ی ده&zwnj;یکه&zwnj;ن.';
$t[41] = 'به&zwnj;ڕاستی ئه&zwnj;وانه&zwnj;ی که باوه&zwnj;ڕیان نه&zwnj;بووه به&zwnj;و قورئانه له&zwnj;کاتێکدا بۆیان هات، بێگومان ئه&zwnj;و قورئانه&zwnj;ش کتێبێکی زۆر به&zwnj;نرخ و ناوازه و سه&zwnj;رکه&zwnj;وتووه&zwnj;...';
$t[42] = 'هیچ ناحه&zwnj;قی و نادروستی و ناته&zwnj;واویه&zwnj;ک ڕووی تێ ناکات له هیچ کاتێکدا و له&zwnj;هیچ لایه&zwnj;که&zwnj;وه ڕه&zwnj;وانه&zwnj;کراو و نێردراوی زاتێکه که خاوه&zwnj;نی حیکمه&zwnj;ت و داناییه و شایانی سوپاس و ستایشه&zwnj;.';
$t[43] = 'ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر (صلی الله علیه وسلم) شتێک به&zwnj;تۆ ناوترێت، که به پێغه&zwnj;مبه&zwnj;رانی پێش تۆ نه&zwnj;وترابێت، به&zwnj;ڕاستی په&zwnj;روه&zwnj;ردگاری تۆ خاوه&zwnj;نی لێ بووردن و لێ خۆشبوونه بۆ باوه&zwnj;ڕداران و تۆڵه&zwnj;سێنه&zwnj;ری به&zwnj;ئازار و ئێشه له بێ باوه&zwnj;ڕان.';
$t[44] = 'ئه&zwnj;گه&zwnj;ر ئه&zwnj;م قورئانه&zwnj;مان به&zwnj;زمانی گه&zwnj;لانی تر بناردایه&zwnj;، ئه&zwnj;وه بێ باوه&zwnj;ڕان ده&zwnj;یانوت: ئایا نه&zwnj;ده&zwnj;بوو ئایه&zwnj;ته&zwnj;کانی ڕوون و ئاشکرا بوایه و زمانی خۆمان بوایه&zwnj;، چۆن ده&zwnj;بێت قورئان به&zwnj;زمانی بێگانه بێت؟ له&zwnj;کاتێکدا محمد (صلی الله علیه وسلم) به عه&zwnj;ره&zwnj;بی ده&zwnj;دوێت؟! ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر (صلی الله علیه وسلم) پێیان بڵێ: ئه&zwnj;م قورئانه بۆ ئه&zwnj;وانه&zwnj;ی باوه&zwnj;ڕیان هێناوه هیدایه&zwnj;ت و ڕێنموویی و شیفایه و چاره&zwnj;سه&zwnj;ری هه&zwnj;موو ده&zwnj;رد و نه&zwnj;خۆشیه&zwnj;کان ده&zwnj;کات، جا ئه&zwnj;وانه&zwnj;ی که باوه&zwnj;ڕ ناهێنن گوێچکه&zwnj;یان که&zwnj;ڕه و به&zwnj;شتی تر ئاخنراوه&zwnj;، هه&zwnj;روه&zwnj;ها کوێرن له ئاستیدا، ئه&zwnj;وانه هه&zwnj;روه&zwnj;ک له دووره&zwnj;وه بانگیان لی بکرێت وایه (نه ده&zwnj;نگه&zwnj;که به چاکی ده&zwnj;بیسن، نه ڕه&zwnj;نگه&zwnj;که به ڕوونی ده&zwnj;بینن)';
$t[45] = 'سوێند به&zwnj;خوا به&zwnj;ڕاستی ته&zwnj;وراتمان به&zwnj;خشی به مووسا، که&zwnj;چی ناکۆکی تیادا کرا، خۆ ئه&zwnj;گه&zwnj;ر بڕیارێکی پێشتر له لایه&zwnj;ن په&zwnj;روه&zwnj;ردگارته&zwnj;وه نه&zwnj;بوایه&zwnj;، (که تۆڵه له کافران بخاته قیامه&zwnj;ت) ئه&zwnj;وه له ناوی ده&zwnj;بردن، بێگومان ئه&zwnj;وان هه&zwnj;ر له شک و گومانێکی بێ سه&zwnj;ر و بندان که خه&zwnj;ڵکیش ده&zwnj;خاته گومانه&zwnj;وه&zwnj;.';
$t[46] = 'ئه&zwnj;وه&zwnj;ی چاکه بکات پاداشتی چاکه&zwnj;که&zwnj;ی بۆ خۆیه&zwnj;تی، ئه&zwnj;وه&zwnj;ش که خراپه و تاوان بکات هه&zwnj;ر له&zwnj;سه&zwnj;ر خۆی ده&zwnj;که&zwnj;وێت، دڵنیابه که په&zwnj;روه&zwnj;ردگاری تۆ بچوکترین سته&zwnj;م له به&zwnj;نده&zwnj;کانی ناکات.';
$t[47] = 'زانینی به&zwnj;رپابوونی ڕۆژی قیامه&zwnj;ت ته&zwnj;نها لای خوایه و بۆ لای ئه&zwnj;و ده&zwnj;برێته&zwnj;وه&zwnj;، هه&zwnj;رچی به&zwnj;رو بووم تۆوێک له خونچه و چرۆکه&zwnj;یه&zwnj;وه چه&zwnj;که&zwnj;وه ده&zwnj;کات خوا ئاگاداره لێی، هیچ مێینه&zwnj;یه&zwnj;ک سکی نابێت و به&zwnj;چکه&zwnj;و به&zwnj;رهه&zwnj;مه&zwnj;که&zwnj;ی دانانێت ته&zwnj;نها به ئاگاداری خوا نه&zwnj;بێت، (ئا به&zwnj;و شێوه&zwnj;یه خوا ده&zwnj;سه&zwnj;ڵاته&zwnj;کانی خۆی ده&zwnj;خاته به&zwnj;رچاو)، که&zwnj;چی زۆربه&zwnj;ی خه&zwnj;ڵکی قه&zwnj;دری نازانن، ئه&zwnj;و ڕۆژه&zwnj;ش که بانگیان ده&zwnj;کات و لێیان ده&zwnj;پرسێت: کوان هاوه&zwnj;ڵ و هاوتاکانم؟! ئه&zwnj;وانه&zwnj;یش له وه&zwnj;ڵامدا ده&zwnj;ڵێن: په&zwnj;روه&zwnj;ردگارا، ئێمه هه&zwnj;واڵت ده&zwnj;ده&zwnj;ینێ و جاڕ ده&zwnj;ده&zwnj;ین که که&zwnj;سمان هه&zwnj;ڵه&zwnj;ی وانایه&zwnj;ت به ده&zwnj;مماندا و شایه&zwnj;تی له&zwnj;سه&zwnj;ر شتی واناده&zwnj;ین.';
$t[48] = 'ئیتر ئه&zwnj;وه&zwnj;ی که جاران ده&zwnj;یانپه&zwnj;رست و هانا و هاواریان بۆ ده&zwnj;برد و جێگه&zwnj;ی ئومێدیان بوو لێیان ون بوو و نرخی نه&zwnj;ما، دڵنیاش بوون که ئیتر هیچ چاره و ڕزگار بوونێکیان نیه&zwnj;.';
$t[49] = 'ئاده&zwnj;میزاد هه&zwnj;ر گیز بێزار نابێت له داخوازی چاکه و خۆشی و ئاسووده&zwnj;یی بۆ خۆی، خۆ ئه&zwnj;گه&zwnj;ر ناخۆشیه&zwnj;کی تووش بێت، ئه&zwnj;وه نائومێد و بێ هیوایه&zwnj;.';
$t[50] = 'سوێند به خوا ئه&zwnj;گه&zwnj;ر ئاده&zwnj;میزاد خێرو خۆشی به&zwnj;سه&zwnj;ردا بڕێژین و پێ بچێژین، له دوای ئه&zwnj;و ته&zwnj;نگانه&zwnj;یه&zwnj;ی که تووشی هاتبوو، به متمانه&zwnj;یه&zwnj;کی زۆره&zwnj;وه ده&zwnj;ڵێت: ئه&zwnj;مه به&zwnj;ره&zwnj;نجی شانی خۆم په&zwnj;یدام کردووه و خوا خۆشی ده&zwnj;وێم، وا بزانم قیامه&zwnj;ت زۆر دووره و ڕه&zwnj;نگه هه&zwnj;ر نه&zwnj;شبێت، خۆ ئه&zwnj;گه&zwnj;ر بمگێرنه&zwnj;وه بۆ لای په&zwnj;روه&zwnj;ردگارم، ئه&zwnj;وه دڵنیام که خۆش و ناز و نیعمه&zwnj;تم لای ئه&zwnj;و: بۆ ئاماده&zwnj;یه&zwnj;، بێگومان ئه&zwnj;وانه&zwnj;ی که بێ باوه&zwnj;ڕ بوون ئه&zwnj;و کارو کرده&zwnj;وه ناشیرینانه&zwnj;یان ده&zwnj;خه&zwnj;ینه ڕوو و هه&zwnj;واڵیان پێ ده&zwnj;ده&zwnj;ین، هه&zwnj;روه&zwnj;ها سزای سه&zwnj;خت و دژوارو ناخۆشیان پێ ده&zwnj;چه&zwnj;ژین له ناو دۆزه&zwnj;خدا.';
$t[51] = 'زۆربه&zwnj;ی ئاده&zwnj;میزاد کاتێک ناز و نیعمه&zwnj;تی به&zwnj;سه&zwnj;ردا بڕێژین پشت هه&zwnj;ڵده&zwnj;کات و سه&zwnj;رپێچی ده&zwnj;کات و ملکه&zwnj;چی ڕاستی نابێت و خۆ به&zwnj;زل و گه&zwnj;وره ده&zwnj;زانێت و به&zwnj;لای خۆیدا دایده&zwnj;تاشێت، کاتێکیش ناخۆشی و نه&zwnj;هامه&zwnj;تی تووشهات، ئه&zwnj;وه دۆعا و نزا و پاڕانه&zwnj;وه&zwnj;ی زۆر به&zwnj;گه&zwnj;رم و کوڵ و پان و پۆڕه&zwnj;!!';
$t[52] = 'ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر (صلی الله علیه وسلم) پێیان بڵێ: هه&zwnj;واڵم بده&zwnj;نێ، باشه&zwnj;: ئه&zwnj;گه&zwnj;ر ئه&zwnj;م قورئانه له&zwnj;لایه&zwnj;ن خواوه بێت، پاشان ئێوه&zwnj;ش بڕواتان پێیی نه&zwnj;بێت، ده&zwnj;زانن چ سه&zwnj;رئه&zwnj;نجامێکی سامناک چاوه&zwnj;ڕێتانه&zwnj;؟. جا ئیتر کێ هه&zwnj;یه له ئێوه سه&zwnj;رسه&zwnj;ختترو لاڕێتر له&zwnj;وه&zwnj;ی که ڕێگه&zwnj;ی جیاو دوور له ڕاستی گرتبێته&zwnj;به&zwnj;ر.';
$t[53] = 'ئێمه له ئاینده&zwnj;دا به&zwnj;ڵگه و نیشانه&zwnj;ی سه&zwnj;رسوڕهێننه&zwnj;ریان نیشان ده&zwnj;ده&zwnj;ین له ئاسۆکانی بوونه&zwnj;وه&zwnj;رداو له خودی خۆیشیاندا (بێگومان ئاشکرایه که له&zwnj;سه&zwnj;رده&zwnj;می زانستیدا چه&zwnj;نده نهێنیه&zwnj;کانی بوونه&zwnj;وه&zwnj;ر ئاشكرا بووه&zwnj;.. زاناکان به گشتی و زاناکانی ئه&zwnj;ستێره&zwnj;ناسی سه&zwnj;رگه&zwnj;ردانن له ورده&zwnj;کاری و دامه&zwnj;زراوی و گه&zwnj;وره&zwnj;یی و فراوانی ئه&zwnj;م گه&zwnj;ردوونه&zwnj;، هه&zwnj;روه&zwnj;ها له ئاڵۆزی جه&zwnj;سته&zwnj;یی و عه&zwnj;قڵیی و ده&zwnj;روونی ئاده&zwnj;روونی ئاده&zwnj;میزاد)، ئه&zwnj;مه&zwnj;ش بۆ ئه&zwnj;وه&zwnj;ی تا چک بۆیان ڕوون بێت و دڵنیابن که ئه&zwnj;و زاته حه&zwnj;ق و ڕاست و ڕه&zwnj;وایه&zwnj;، ئایا شایه&zwnj;تی په&zwnj;روره&zwnj;ردگارت به&zwnj;س نیه که بێگومان ئاگا و زانایه به هه&zwnj;موو شتێک؟!';
$t[54] = '(له&zwnj;گه&zwnj;ڵ هه&zwnj;موو به&zwnj;ڵگه و نیشانانه&zwnj;دا: ئاگاداربن به&zwnj;ڕاستی ئه&zwnj;وانه هه&zwnj;ر گومانیان هه&zwnj;یه له به&zwnj;یه&zwnj;کگه&zwnj;یشتن و ئاماده&zwnj;بوونی به&zwnj;رده&zwnj;م په&zwnj;روه&zwnj;رردگاریان، ئاگاداربه و دڵنیابه که ئه&zwnj;وزاته به هه&zwnj;موو شتێک زانایه و به هه&zwnj;موو شتێک ئاگاداره وده&zwnj;سه&zwnj;ڵاتی به&zwnj;سه&zwnj;ر هه&zwnj;موو شتێکدا هه&zwnj;یه و هیچ شتێک له&zwnj;و یاخی نابێت.';
?>