<?php
$t = array();
$t[1] = '(1) و (2) چه&zwnj;ند پیتێك له سه&zwnj;ره&zwnj;تای هه&zwnj;ندێ له سوره&zwnj;ته&zwnj;کانی قورئاندا هاتوون ژماره&zwnj;یان (14)پیته&zwnj;، نیوه&zwnj;ی کۆی پیته&zwnj;کانی زمانی عه&zwnj;ره&zwnj;بین، بۆ سه&zwnj;لماندنی (اعجاز) و مه&zwnj;زنی و گرنگی قورئان که له توانای هیچ که&zwnj;سدانیه به&zwnj;و پیتانه کتێبێکی ئاوا بێوێنه دابنێت به&zwnj;ڕاده&zwnj;یه&zwnj;ك هه&zwnj;تا ئه&zwnj;گه&zwnj;ر هه&zwnj;مووی گرۆی ئاده&zwnj;میزاد و په&zwnj;ری کۆ ببنه&zwnj;وه بۆ ئه&zwnj;و مه&zwnj;به&zwnj;سته&zwnj;، بێگومان چه&zwnj;نده&zwnj;ها نهێنی تریشی تیایه&zwnj;، زاناکان له هه&zwnj;ندێکی دواون، هه&zwnj;ندێکیشیان وتویانه&zwnj;: هه&zwnj;ر خوا خۆی زانایه به نهێنی ئه&zwnj;و پیتانه&zwnj;.';
$t[2] = '(1)و (2) به&zwnj;یه&zwnj;که&zwnj;وه&zwnj;یه&zwnj;.';
$t[3] = 'ئا به&zwnj;و شێوه&zwnj;یه که ده&zwnj;یزانیت خوای باڵاده&zwnj;ست و کاربه&zwnj;جێ وه&zwnj;حی و نیگا ده&zwnj;نێرێت بۆ تۆ ئه&zwnj;ی محمد صلی الله علیه وسلم و بۆ پێغه&zwnj;مبارانی پێش تۆیش ڕه&zwnj;وانه کراوه&zwnj;، له لایه&zwnj;ن خوایه&zwnj;کی باڵاده&zwnj;ست و داناوه&zwnj;.';
$t[4] = 'هه&zwnj;رچی له ئاسمانه&zwnj;کان و زه&zwnj;ویدا هه&zwnj;یه هه&zwnj;ر هه&zwnj;مووی ئه&zwnj;و زاته به&zwnj;دیهێنه&zwnj;رو خاوه&zwnj;نێتی، بێگومان ئه&zwnj;و خوایه به&zwnj;رز وبڵندو گه&zwnj;وره&zwnj;و شکۆداره&zwnj;.';
$t[5] = 'ئاسمانه&zwnj;کان لای سه&zwnj;رویانه&zwnj;وه نزیکه له&zwnj;به&zwnj;ر گه&zwnj;وره&zwnj;یی و شکۆداری ئه&zwnj;و زاته له&zwnj;ت ببن و لێك بترازێن، هاوکات فریشته&zwnj;کان سه&zwnj;رگه&zwnj;رمی ته&zwnj;سبیحات و ستایش و سوپاسگوزاریی په&zwnj;روه&zwnj;ردگاریانن، داوای لێخۆشبوونیش ده&zwnj;که&zwnj;ن بۆ ئیماندارانی سه&zwnj;رزه&zwnj;وی، دڵنیا و ئاگاداربن که به&zwnj;ڕاستی ئه&zwnj;و په&zwnj;روه&zwnj;ردگاره هه&zwnj;ر ئه&zwnj;و لێخۆشبوو میهره&zwnj;بانه&zwnj;.';
$t[6] = 'ئه&zwnj;وانه&zwnj;ش له جیاتی ئه&zwnj;و زاته که&zwnj;سانی ترو شتی تر ده&zwnj;که&zwnj;نه پاڵپشت و پشتگیر، ئه&zwnj;وانه خوا چاودێره به&zwnj;سه&zwnj;ریانه&zwnj;وه وه&zwnj;نه&zwnj;بێت تۆ ئه&zwnj;ی محمد صلی الله علیه وسلم کرابیت به سه&zwnj;رپه&zwnj;رشت به&zwnj;سه&zwnj;ریانه&zwnj;وه&zwnj;.';
$t[7] = 'ئا به&zwnj;و شێوه&zwnj;یه&zwnj;ی که ده&zwnj;یزانیت قورئانێکی عه&zwnj;ره&zwnj;بی پاراومان به وه&zwnj;حی و نیگا بۆ ناردیت، بۆ ئه&zwnj;وه&zwnj;ی شاری مه&zwnj;ککه و ده&zwnj;وروبه&zwnj;ری (که هه&zwnj;موو زه&zwnj;وی ده&zwnj;گرێته&zwnj;وه&zwnj;) ئاگادار بکه&zwnj;یت، به&zwnj;تایبه&zwnj;ت ئاگادارو هۆشیاریان بکه&zwnj;ین له ڕۆژی کۆکردنه&zwnj;وه که هیچ گومانی تێدا نیه&zwnj;، (ئه&zwnj;و ڕۆژه خه&zwnj;ڵکی ده&zwnj;بنه دوو به&zwnj;شه&zwnj;وه&zwnj;)، ده&zwnj;سته&zwnj;یه&zwnj;کیان بۆ به&zwnj;هه&zwnj;شت به&zwnj;ڕێ ده&zwnj;کرێن و ده&zwnj;چنه ناو به&zwnj;هه&zwnj;شته&zwnj;وه&zwnj;، ده&zwnj;سته&zwnj;که&zwnj;ی تریان بۆ ناو دۆزه&zwnj;خ ڕاپێچ ده&zwnj;کرێت.';
$t[8] = 'ئه&zwnj;گه&zwnj;ر خوا ویستی ببوایه هه&zwnj;موویانی ده&zwnj;کرده میلله&zwnj;تێکی یه&zwnj;ك پارچه (هه&zwnj;مووان به&zwnj;ناچاریی ئیمه&zwnj;ندارو چاك ده&zwnj;بوون) به&zwnj;ڵام ویستی وایه هه&zwnj;ر که&zwnj;س بیه&zwnj;وێت و شایسته بێت، ده&zwnj;یخاته سایه&zwnj;ی ڕه&zwnj;حمه&zwnj;ت و میهره&zwnj;بانی و به&zwnj;هه&zwnj;شتی خۆیه&zwnj;وه&zwnj;، سته&zwnj;مکارانیش هیچ پشتیوان و یاریده&zwnj;ده&zwnj;رێکیان نابێت.';
$t[9] = 'ئایا بێجگه له خوا پشتیوانی تریان بۆ خۆیان دۆزیوه&zwnj;ته&zwnj;وه&zwnj;؟ که&zwnj;وابوو هه&zwnj;ر خوا خۆی پشتیوانه&zwnj;و هه&zwnj;ر خوا خۆی مردووان زیندوو ده&zwnj;کاته&zwnj;وه و هه&zwnj;ر خۆیشی ده&zwnj;سه&zwnj;ڵاتی به&zwnj;سه&zwnj;ر هه&zwnj;موو شتێکدا هه&zwnj;یه&zwnj;!';
$t[10] = 'له&zwnj;هه&zwnj;ر شتێکدا کێشه و ڕای جیاوازتان بوو، ئه&zwnj;وه سه&zwnj;رئه&zwnj;نجامی بڕیاره&zwnj;که&zwnj;ی بۆ خوا خۆی ده&zwnj;گه&zwnj;ڕێته&zwnj;وه&zwnj;، چونکه هه&zwnj;ر ئه&zwnj;و زاته&zwnj;یه خوای په&zwnj;روه&zwnj;ردگارم هه&zwnj;ر پشت به&zwnj;و ده&zwnj;به&zwnj;ستم و هه&zwnj;ر بۆ لای ئه&zwnj;ویش ده&zwnj;گه&zwnj;ڕێمه&zwnj;وه&zwnj;.';
$t[11] = 'بێگومان هه&zwnj;ر ئه&zwnj;و زاته به&zwnj;دیهێنه&zwnj;ری ئاسمانه&zwnj;کان و زه&zwnj;ویه&zwnj;، هه&zwnj;ر له خۆتان هاوسه&zwnj;رانی بۆ سازاندوون، له ئاژه&zwnj;ڵ و مه&zwnj;ڕو ماڵاتیش جووت، نێرو مێ ی بۆ فه&zwnj;راهه&zwnj;م هێناون، له&zwnj;و ڕێگه&zwnj;یه&zwnj;وه زاووزێ ده&zwnj;که&zwnj;ن و زۆر ده&zwnj;بن بۆتان، هیچ شتێك نیه له وێنه&zwnj;ی ئه&zwnj;وزاته&zwnj;، هیچ شتێك له&zwnj;و ناچێت (چونکه خوایه و به&zwnj;دیهێنه&zwnj;ره&zwnj;، چۆن له به&zwnj;دیهێنراوه&zwnj;کانی ده&zwnj;چێت) هه&zwnj;ر ئه&zwnj;ویش بیسه&zwnj;رو بینایه&zwnj;.';
$t[12] = 'جڵه&zwnj;وی هه&zwnj;موو ئاسمانه&zwnj;کان و زه&zwnj;وی به ده&zwnj;ست ئه&zwnj;وزاته&zwnj;یه&zwnj;، ڕزق و ڕۆزی فراوان ده&zwnj;کات بۆ هه&zwnj;ر که&zwnj;س که بیه&zwnj;وێت، یان که&zwnj;می ده&zwnj;کات و سنورێکی بۆ داده&zwnj;نێت (هه&zwnj;موویشی بۆ تاقیکردنه&zwnj;وه&zwnj;یه له لایه&zwnj;که&zwnj;وه و به&zwnj;ڕێوه&zwnj;بردنی کاروباری ژیانه له&zwnj;لایه&zwnj;کی تره&zwnj;وه&zwnj;)، به&zwnj;ڕاستی ئه&zwnj;و خوایه به هه&zwnj;موو شتێك زانایه&zwnj;.';
$t[13] = 'ڕێبازی ئاین و به&zwnj;رنامه&zwnj;ی ژیانی بۆ داناون، ئه&zwnj;وه&zwnj;ی که&zwnj;فه&zwnj;رمانی پێکردووه به _نوح_، هه&zwnj;ر به&zwnj;وشێوه&zwnj;یه قورئانیشی بۆ تۆ ئه&zwnj;ی محمد صلی الله علیه وسلم وه&zwnj;حی و نیگا کردووه و به ئیبراهیم و موساو عیساشمان ڕاگه&zwnj;یاندووه&zwnj;که ڕێبازی ئاین و یه&zwnj;کخواناسی به&zwnj;رنه&zwnj;ده&zwnj;ن و له&zwnj;سه&zwnj;ری پایادار بن، ئه&zwnj;م بانگه&zwnj;وازه ڕاست و دروسته زۆر به&zwnj;لای بت په&zwnj;رست و خوانه&zwnj;ناسانه&zwnj;وه گران و زه&zwnj;حمه&zwnj;ته&zwnj;، به&zwnj;ڵام خوای دانا خۆی که&zwnj;سانی شایسته هه&zwnj;ڵده&zwnj;بژێرێت بۆ گه&zwnj;یاندنی ئاینه&zwnj;که&zwnj;ی ڕێنموویی خه&zwnj;ڵکانێك ده&zwnj;کات که بگه&zwnj;ڕێنه&zwnj;وه بۆ لای به&zwnj;رنامه&zwnj;که&zwnj;ی به&zwnj;په&zwnj;رستن و ئه&zwnj;نجامدانی کاروکرده&zwnj;وه&zwnj;ی چاك.';
$t[14] = 'میلله&zwnj;تانی پێشوو، به&zwnj;تایبه&zwnj;ت گاور وجوو جیاوازی و کێشه دووبه&zwnj;ره&zwnj;کی نه&zwnj;که&zwnj;وته نێوانیان، مه&zwnj;گه&zwnj;ر له&zwnj;دوای هاتنی زانستی و زانیاری ته&zwnj;واو بۆیان، که&zwnj;له&zwnj;به&zwnj;ر حه&zwnj;ساده&zwnj;ت و سته&zwnj;مکاری و لادانیان له ڕاسته ڕێگا بۆیان دروست بوو، خۆ ئه&zwnj;گه&zwnj;ر بڕیاری پێشووتر له&zwnj;لایه&zwnj;ن په&zwnj;روه&zwnj;ردگارته&zwnj;وه نه&zwnj;بوایه که تا کاتێکی دیاریکراو مۆڵه&zwnj;تی داون، ئه&zwnj;وه فه&zwnj;رمانی تیاچوونیان ده&zwnj;رده&zwnj;کرا، بێگومان ئه&zwnj;وانه&zwnj;ی که بوونه میراتگری کتێبی خوا و گه&zwnj;یه&zwnj;نرا به&zwnj;ده&zwnj;ستیان له نه&zwnj;وه&zwnj;کانی داهاتوو، که&zwnj;وتنه گومان و دوودڵیه&zwnj;وه&zwnj;، خه&zwnj;ڵکیشیان خسته گومانه&zwnj;وه ده&zwnj;رباره&zwnj;ی.';
$t[15] = 'که&zwnj;واته ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر صلی الله علیه وسلم تۆ بۆ ئه&zwnj;وه بانگه&zwnj;واز بکه و پایادارو دامه&zwnj;زراوبه هه&zwnj;روه&zwnj;ك فه&zwnj;رمانت پێ دراوه&zwnj;، شوێن ئاره&zwnj;زووی ئه&zwnj;وان مه&zwnj;که&zwnj;وه و به&zwnj;ئاشکرا بڵێ: من، باوه&zwnj;ڕم هێناوه به&zwnj;و کتێب و په&zwnj;یامانه&zwnj;ی خوا بۆی نا&zwnj;ردوم و فه&zwnj;رمانم پێدراوه که دادپه&zwnj;روه&zwnj;ری به&zwnj;رپا بکه&zwnj;م له نێوانتاندا، (الله&zwnj;) په&zwnj;روه&zwnj;ردگاری ئێمه&zwnj;یه و ئێوه&zwnj;شه&zwnj;، بێگومان ده&zwnj;رئه&zwnj;نجامی کرده&zwnj;ی خۆمان بۆ خۆمانه و کرده&zwnj;وه&zwnj;ی ئێوه&zwnj;ش بۆ خۆتانه&zwnj;، ئیتر هیچ کێشه&zwnj;یه&zwnj;ك نی یه له نێوان ئێمه و ئێوه&zwnj;دا (چونکه حه&zwnj;ق ده&zwnj;رکه&zwnj;وت)، ئێمه و ئێوه هه&zwnj;ر خوا خۆی کۆمان ده&zwnj;کاته&zwnj;وه و گه&zwnj;ڕانه&zwnj;وه&zwnj;ش هه&zwnj;ر بۆ لای ئه&zwnj;وه&zwnj;.';
$t[16] = 'ئه&zwnj;وانه&zwnj;ی ڕه&zwnj;خنه ده&zwnj;گرن و قسه&zwnj;ده&zwnj;که&zwnj;ن ده&zwnj;رباره&zwnj;ی ئاینی خوا، دوای ئه&zwnj;وه&zwnj;ی که وه&zwnj;ڵام دراوه&zwnj;ته&zwnj;وه و خه&zwnj;ڵکی ڕوویان تێکردووه&zwnj;، ئه&zwnj;وانه به&zwnj;ڵگه&zwnj;و ڕه&zwnj;خنه&zwnj;کانیان پووچه&zwnj;ڵه و به&zwnj;سه&zwnj;ریاندا ده&zwnj;درێته&zwnj;وه له لایه&zwnj;ن په&zwnj;روه&zwnj;ردگاریانه&zwnj;وه&zwnj;، خه&zwnj;شم و تووڕه&zwnj;یان له&zwnj;سه&zwnj;ره و سزای سه&zwnj;ختیان بۆ ئاماده&zwnj;یه&zwnj;.';
$t[17] = 'خوا ئه&zwnj;و زاته&zwnj;یه که کتێبه پیرۆزه&zwnj;کانی به حه&zwnj;ق و ڕاستی ڕه&zwnj;وانه کردووه&zwnj;، هه&zwnj;روه&zwnj;ها شه&zwnj;ریعه&zwnj;تێکی دابه&zwnj;زاندووه که وه&zwnj;کو ته&zwnj;رازوو پێوه&zwnj;ر وایه بۆ هه&zwnj;ڵسه&zwnj;نگاندنی حه&zwnj;ق و ناحه&zwnj;ق، جا چی تۆی فێر کردووه و چووزانی له&zwnj;وانه&zwnj;یه ڕۆژی قیامه&zwnj;ت و لێپرسینه&zwnj;وه نزیك بێت.';
$t[18] = 'ئه&zwnj;وانه&zwnj;ی که باوه&zwnj;ڕ به ڕۆژی قیامه&zwnj;ت ناهێنن په&zwnj;له ده&zwnj;که&zwnj;ن و گاڵته&zwnj;یان پێی دێت و ده&zwnj;ڵێن: کوا؟ که&zwnj;ی پێش دێت؟ (به&zwnj;ڵام) ئه&zwnj;وانه&zwnj;ی باوه&zwnj;ڕیان هێناوه له به&zwnj;رپابوونی ده&zwnj;ترسن، ده&zwnj;زانن دڵنیان که به&zwnj;رپابوونی حه&zwnj;قیقه&zwnj;ت و ڕاسته&zwnj;قینه&zwnj;یه&zwnj;، ئاگاداربن له ڕاستیدا ئه&zwnj;وانه&zwnj;ی موجاده&zwnj;له له&zwnj;سه&zwnj;ر قیامه&zwnj;ت ده&zwnj;که&zwnj;ن و به&zwnj;گومانن لێی، له&zwnj;بزر بوون و سه&zwnj;رلێشێواویه&zwnj;کی قووڵدا ڕۆچوون.';
$t[19] = 'خوای گه&zwnj;وره زۆر به&zwnj;سۆزو دلۆڤانه له&zwnj;گه&zwnj;ڵ به&zwnj;نده&zwnj;کانیدا، ڕزق و ڕۆزی ده&zwnj;به&zwnj;خشێت به هه&zwnj;ر که&zwnj;س بیه&zwnj;وێت (بۆ تاقیکردنه&zwnj;وه&zwnj;)، به&zwnj;ڕاستی ئه&zwnj;و زاته به&zwnj;هێز و باڵاده&zwnj;سته&zwnj;.';
$t[20] = 'جا ئه&zwnj;وه&zwnj;ی که به&zwnj;رهه&zwnj;می قیامه&zwnj;تی ده&zwnj;وێت و له دنیادا کارو کرده&zwnj;وه&zwnj;ی چاك ده&zwnj;چێنێت، ئێمه پاداشتی هه&zwnj;وڵ و کۆششه&zwnj;که&zwnj;ی به&zwnj;زیاده&zwnj;وه پێده&zwnj;به&zwnj;خشین، ئه&zwnj;وه&zwnj;ش که به&zwnj;رهه&zwnj;می دنیای ده&zwnj;وێت، هه&zwnj;ندێك له دنیای پێ ده&zwnj;به&zwnj;خشین، (به&zwnj;ڵام) له قیامه&zwnj;تدا هیچ جۆره به&zwnj;شێکی نیه&zwnj;.';
$t[21] = '(ئایا ئه&zwnj;و بێباوه&zwnj;ڕانه ڕازین به&zwnj;به&zwnj;رنامه&zwnj;ی خوا) یاخود شوێنی ئه&zwnj;و هاوه&zwnj;ڵگه&zwnj;ڕانه ده&zwnj;که&zwnj;ون که به&zwnj;ناوی دینه&zwnj;وه به&zwnj;رنامه&zwnj;یان بۆ داناون و په&zwnj;یڕه&zwnj;وییان ده&zwnj;که&zwnj;ن بێ ئه&zwnj;وه&zwnj;ی خوا مۆڵه&zwnj;تی دابێت و پێی ڕازی بێت (حه&zwnj;رام و حه&zwnj;ڵاڵیان بۆ دیاری ده&zwnj;که&zwnj;ن)، خۆ ئه&zwnj;گه&zwnj;ر بڕیاری پێشترمان نه&zwnj;بوایه به مانه&zwnj;وه&zwnj;یان تا کاتی دیاریکراو، ئه&zwnj;وه فه&zwnj;رمان ده&zwnj;درا به له&zwnj;نا&zwnj;و بردنیان، بێگومان بۆ سته&zwnj;مکاران سزای به ئێش و به&zwnj;سۆ ئاماده&zwnj;یه&zwnj;.';
$t[22] = '(له&zwnj;ڕۆژی قیامه&zwnj;ت و لێپرسینه&zwnj;وه&zwnj;دا) ده&zwnj;بینیت سته&zwnj;مکاران ده&zwnj;ترسن له&zwnj;وه&zwnj;ی که کردوویانه&zwnj;، له&zwnj;کاتێکدا سزاکه&zwnj;ی که&zwnj;وتووه به&zwnj;سه&zwnj;ریاندا و لێی ده&zwnj;رباز نابن، هاوکات ئه&zwnj;وانه&zwnj;ی که باوه&zwnj;ڕیان هێناوه و کارو کرده&zwnj;وه چاکه&zwnj;کانیان ئه&zwnj;نجامداوه له باخچه&zwnj;کانی به&zwnj;هه&zwnj;شتدا ژیانی کامه&zwnj;رانی ده&zwnj;به&zwnj;نه سه&zwnj;ر، چیان بوێت له&zwnj;لایه&zwnj;ن په&zwnj;روه&zwnj;ردگاریانه&zwnj;وه بۆیان ئاماده&zwnj;یه&zwnj;، ئا ئه&zwnj;وه&zwnj;یه خه&zwnj;ڵات و به&zwnj;خششی گه&zwnj;وره و بێ سنوور.';
$t[23] = 'ئه&zwnj;و خه&zwnj;ڵات و به&zwnj;خششه ئه&zwnj;وه&zwnj;یه که خوا مژده ده&zwnj;دات به&zwnj;و به&zwnj;ندانه&zwnj;ی که باوه&zwnj;ڕیان هێناوه و کارو کرده&zwnj;وه چاکه&zwnj;کانیان ئه&zwnj;نجامداوه&zwnj;، (ئه&zwnj;وانه&zwnj;ش به&zwnj;ده&zwnj;م بانگه&zwnj;وازه&zwnj;وه نایه&zwnj;ن)، ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر صلی الله علیه وسلم پێیان بڵێ: له&zwnj;سه&zwnj;ر ئه&zwnj;م بانگه&zwnj;وازه هیچ جۆره پاداشتێکم له ئێوه ناوێت، ته&zwnj;نها خۆشه&zwnj;ویستی و ڕێز نه&zwnj;بێت له خزمایه&zwnj;تیدا، جا ئه&zwnj;وه&zwnj;ی چاکه&zwnj;یه&zwnj;ك ئه&zwnj;نجام بدات، ئه&zwnj;وه به&zwnj;چاکتر پاداشت ده&zwnj;درێته&zwnj;وه&zwnj;، چونکه به&zwnj;ڕاستی خوا لێبوورده و سوپاسگوزاره&zwnj;.';
$t[24] = 'به&zwnj;ڵکو خوانه&zwnj;ناسان ده&zwnj;ڵێن: محمد صلی الله علیه وسلم به&zwnj;ده&zwnj;م خواوه درۆی هه&zwnj;ڵبه&zwnj;ستووه&zwnj;!! جا ئه&zwnj;گه&zwnj;ر خوا بیه&zwnj;وێت مۆر ده&zwnj;نێت به&zwnj;سه&zwnj;ر دڵت داو هیچت بۆ ناوترێت، خوایش ده&zwnj;یه&zwnj;وێت ئاسه&zwnj;واری به&zwnj;تاڵ و ناحه&zwnj;قی نه&zwnj;هێڵێت و حه&zwnj;ق و ڕاستی به&zwnj;فه&zwnj;رمانی خۆی بچه&zwnj;سپێنێت، چونکه به&zwnj;ڕاستی ئه&zwnj;و خوایه زانایه به&zwnj;وه&zwnj;ی که له توێی دڵ و ده&zwnj;روون و سینه&zwnj;کاندا شاراوه&zwnj;یه&zwnj;.';
$t[25] = 'خوا ئه&zwnj;و زاته&zwnj;یه که په&zwnj;شیمانی و ته&zwnj;وبه له به&zwnj;نده&zwnj;کانی وه&zwnj;رده&zwnj;گرێت، چاوپۆشیش ده&zwnj;کات له هه&zwnj;ڵه و گوناهه&zwnj;کانیان، به&zwnj;و کارو کرده&zwnj;وانه&zwnj;ش که ئه&zwnj;نجامی ده&zwnj;ده&zwnj;ن ده&zwnj;زانێت و به&zwnj;ئاگایه&zwnj;.';
$t[26] = 'خوای گه&zwnj;وره ئاواتی ئه&zwnj;وانه ده&zwnj;هێنێته دی که باوه&zwnj;ڕیان هێناوه و کارو کرده&zwnj;وه چاکه&zwnj;کانیان ئه&zwnj;نجامداوه&zwnj;، هه&zwnj;روه&zwnj;ها له فه&zwnj;زڵ و زیاده خه&zwnj;ڵات و به&zwnj;خششی خۆی به&zwnj;هره&zwnj;مه&zwnj;ندیان ده&zwnj;کات، بێ باوه&zwnj;ڕانیش سزای سه&zwnj;خت و به ئێشیان بۆ ئاماده&zwnj;یه&zwnj;.';
$t[27] = 'خۆ ئه&zwnj;گه&zwnj;ر خوا ڕزق و ڕۆزی به&zwnj;فراوانی به به&zwnj;نده&zwnj;کانی ببه&zwnj;خشیایه&zwnj;، ئه&zwnj;وه ده&zwnj;ستیان ده&zwnj;دایه تاوان و گوناهو سته&zwnj;م له زه&zwnj;ویدا، به&zwnj;ڵام خوا به&zwnj;حسابێکی وردو دیاریکراو ئه&zwnj;وه&zwnj;ی بیه&zwnj;وێت پێیان ده&zwnj;به&zwnj;خشێت، به&zwnj;ڕاستی ئه&zwnj;و زاته ئاگاو بینایه به کارو هه&zwnj;ڵویستی به&zwnj;نده&zwnj;کانی.';
$t[28] = 'خوا ئه&zwnj;و زاته&zwnj;یه که باران ده&zwnj;بارێنێت و ده&zwnj;گه&zwnj;یه&zwnj;نێته فریایان دوای ئه&zwnj;وه&zwnj;ی که نائومێد بوون، هه&zwnj;روه&zwnj;ها ڕه&zwnj;حمه&zwnj;ت و به&zwnj;خششی خۆی په&zwnj;خش ده&zwnj;کاته&zwnj;وه&zwnj;، له&zwnj;به&zwnj;ر ئه&zwnj;وه هه&zwnj;ر خۆی پشتیوان و فریادڕه&zwnj;سه و شایسته&zwnj;ی سوپاسه&zwnj;.';
$t[29] = 'له&zwnj;نیشانه&zwnj;و به&zwnj;ڵگه&zwnj;کانی گه&zwnj;وره&zwnj;یی و ده&zwnj;سه&zwnj;ڵاتی خوا: دروستکردنی ئاسمانه&zwnj;کان و زه&zwnj;وی و ئه&zwnj;و زینده&zwnj;وه&zwnj;رانه&zwnj;یه که تیایاندا بڵاوی کردۆته&zwnj;وه&zwnj;، هه&zwnj;ر ئه&zwnj;و زاته کاتێك بیه&zwnj;وێت تواناو ده&zwnj;سه&zwnj;ڵاتی به&zwnj;سه&zwnj;ر به&zwnj;سه&zwnj;ر کۆکردنه&zwnj;وه&zwnj;یاندا هه&zwnj;یه&zwnj;.';
$t[30] = 'هه&zwnj;ر به&zwnj;ڵاو نه&zwnj;هامه&zwnj;تیه&zwnj;کتان به&zwnj;سه&zwnj;ر هاتبێت، ئه&zwnj;وه ده&zwnj;رئه&zwnj;نجامی ئه&zwnj;و کارو کرده&zwnj;وانه&zwnj;یه که به ئاره&zwnj;زووی خۆتان ده&zwnj;تانکرد، (به&zwnj;ڵام ئه&zwnj;وه&zwnj;نده خوایه&zwnj;کی میهره&zwnj;بانه&zwnj;) له زۆربه&zwnj;ی زۆری گوناهو هه&zwnj;ڵه&zwnj;کانتان خۆش ده&zwnj;بێت.';
$t[31] = 'ئه&zwnj;ی گرۆی خوانه&zwnj;ناسان ئێوه ناتوانن خوا ده&zwnj;سه&zwnj;وسان بکه&zwnj;ن و له ده&zwnj;سه&zwnj;ڵاتی خوا ده&zwnj;رباز ببن له زه&zwnj;ویدا، جگه له خوای گه&zwnj;وره&zwnj;ش که&zwnj;س ناتوانێت پشتیوانتان بێت و سه&zwnj;رتان بخات.';
$t[32] = 'له&zwnj;نیشانه&zwnj;و به&zwnj;ڵگه&zwnj;ی ده&zwnj;سه&zwnj;ڵاتی خوا ئه&zwnj;و که&zwnj;شتیانه که وه&zwnj;ك که&zwnj;ژو کێو هاتووچۆ ده&zwnj;که&zwnj;ن به ده&zwnj;ریاکاندا، (به&zwnj;یاسایه&zwnj;کی ڕێك و پێك).';
$t[33] = 'خۆ ئه&zwnj;گه&zwnj;ر بیه&zwnj;وێت با ده&zwnj;وه&zwnj;ستێنێت و له&zwnj;سه&zwnj;ر پشتی ده&zwnj;ریاکه ده&zwnj;وه&zwnj;ستن و له کارده&zwnj;که&zwnj;ون، به&zwnj;ڕاستی ئا له&zwnj;و تواناو ده&zwnj;سه&zwnj;ڵاته&zwnj;دا به&zwnj;ڵگه&zwnj;و نیشانه&zwnj;ی زۆر هه&zwnj;ن بۆ هه&zwnj;ر که&zwnj;سێك که زۆر خۆگرو سوپاسگوزاره&zwnj;.';
$t[34] = 'یان جاری وا هه&zwnj;یه که&zwnj;شتیه&zwnj;کان به خۆیان و سه&zwnj;رنشینه&zwnj;کانیانه&zwnj;وه ده&zwnj;شکێنێت و نوقمیان ده&zwnj;کات له ئه&zwnj;نجامی تاوان و گوناهی خه&zwnj;ڵکه&zwnj;که&zwnj;وه&zwnj;، گه&zwnj;لێك جاریش چاوپۆشی (له هه&zwnj;ڵه و تاوانی) زۆر ده&zwnj;کات و ده&zwnj;یانهێڵێته&zwnj;وه&zwnj;.';
$t[35] = 'ئه&zwnj;م ڕوونکردنه&zwnj;وه&zwnj;یه&zwnj;ش بۆ ئه&zwnj;وه&zwnj;یه خوانه&zwnj;ناسان بزانن، به&zwnj;تایبه&zwnj;ت ئه&zwnj;وانه&zwnj;ی که موجاده&zwnj;له&zwnj;و ده&zwnj;مه&zwnj;ده&zwnj;مێ ده&zwnj;که&zwnj;ن و ڕه&zwnj;خنه&zwnj;یان له ئایه&zwnj;ته&zwnj;کانی ئێمه هه&zwnj;یه&zwnj;، که له کاتی ته&zwnj;نگانه&zwnj;دا که&zwnj;س په&zwnj;نایان نادات و که&zwnj;س فریایان ناکه&zwnj;وێت جگه له خوای گه&zwnj;وه&zwnj;ره&zwnj;.';
$t[36] = 'جا هه&zwnj;ر شتێکتان پێبه&zwnj;خشراوه&zwnj;، ئه&zwnj;وه زینه&zwnj;تی ژیانی ئه&zwnj;م دنیایه&zwnj;یه (که ڕێگوزه&zwnj;رێکه به&zwnj;ره&zwnj;و قیامه&zwnj;ت) به&zwnj;ڵام ئه&zwnj;و به&zwnj;شه&zwnj;ی که له&zwnj;لای خوایه چاکتر و به&zwnj;رده&zwnj;وامتره بۆ ئه&zwnj;وانه&zwnj;ی که باوه&zwnj;ڕیان هێناوه و هه&zwnj;ر پشت به په&zwnj;روه&zwnj;ردگاریان ده&zwnj;به&zwnj;ستن....';
$t[37] = 'ئه&zwnj;وانه&zwnj;ی خۆیان له گوناهی گه&zwnj;وره و تاوانی خراپ و ناشرین ده&zwnj;پارێزن، کاتێك که تووڕه ده&zwnj;بن (له&zwnj;سه&zwnj;ر هه&zwnj;ڵه&zwnj;یه&zwnj;ك که به&zwnj;رامبه&zwnj;ریان ده&zwnj;کرێت) هه&zwnj;ر ئه&zwnj;مان چاوپۆشی ده&zwnj;که&zwnj;ن و لێده&zwnj;بورن....';
$t[38] = 'ئه&zwnj;وانه&zwnj;ش به&zwnj;ده&zwnj;م بانگه&zwnj;وازی په&zwnj;روه&zwnj;ردگاریانه&zwnj;وه چوون و نوێژه&zwnj;کانیان به&zwnj;چاکی ئه&zwnj;نجام داوه و کارو باریان هه&zwnj;رده&zwnj;م به مه&zwnj;شوره&zwnj;ت و ڕاپرسی و ڕاوێژکاریه و له&zwnj;و ڕزق و ڕۆزیه&zwnj;ی که پێمان داون، ده&zwnj;به&zwnj;خشن...';
$t[39] = 'هه&zwnj;روه&zwnj;ها ئه&zwnj;وانه&zwnj;ی که ده&zwnj;ستدرێژیان کرایه سه&zwnj;ر، ده&zwnj;سه&zwnj;وسان ناوه&zwnj;ستن و به&zwnj;رگری له خۆیان ده&zwnj;که&zwnj;ن...';
$t[40] = 'جا تۆڵه&zwnj;ی هه&zwnj;ر تاوانێك به ئه&zwnj;ندازه&zwnj;ی تاوانه&zwnj;که&zwnj;یه بێ زیادو که&zwnj;م، ئه&zwnj;و که&zwnj;سه&zwnj;ی لێخۆشبوونی هه&zwnj;بێت و چاوپۆشی و چاکسازی بکات، ئه&zwnj;وه پاداشتی لای خوایه&zwnj;، به&zwnj;ڕاستی خوا سته&zwnj;مکارانی خۆش ناوێت...';
$t[41] = 'له&zwnj;ڕاستیدا ئه&zwnj;وه&zwnj;ی حه&zwnj;قی خۆی ده&zwnj;سێنێت دوای ئه&zwnj;وه&zwnj;ی که سته&zwnj;می لێکراوه&zwnj;، ئه&zwnj;وانه لێپرسراو نابن، ئه&zwnj;گه&zwnj;ر له سنوور لا نه&zwnj;ده&zwnj;ن....';
$t[42] = 'بێگومان ته&zwnj;نها ئه&zwnj;وانه به&zwnj;رپرس ده&zwnj;بن که سته&zwnj;م له خه&zwnj;ڵکی ده&zwnj;که&zwnj;ن و ده&zwnj;ستدرێژی ده&zwnj;که&zwnj;ن به ناحه&zwnj;ق له زه&zwnj;ویدا، ئا ئه&zwnj;وانه سزاو ئازاری به ئیشیان بۆ هه&zwnj;یه&zwnj;...';
$t[43] = 'له&zwnj;ڕاستیدا ئه&zwnj;وه&zwnj;ی دان به&zwnj;خۆیدا بگرێت و چاوپۆشی بکات و لێخۆشبوو بێت (له&zwnj;کاتێکدا که ده&zwnj;توانێت تۆڵه&zwnj;بسێنێت) به&zwnj;ڕاستی ئه&zwnj;وه کارێکی به&zwnj;جێ و چاك و په&zwnj;سه&zwnj;ند ئه&zwnj;نجام ده&zwnj;دات، به&zwnj;ڕاستی ئه&zwnj;وه له&zwnj;و کارانه&zwnj;یه که پێویسته بکرێت....';
$t[44] = 'ئه&zwnj;وه&zwnj;ی خوا ڕێی لێ ون بکات، ئه&zwnj;وه ئیتر که&zwnj;سی تری دڵسۆزی ده&zwnj;ست ناکه&zwnj;وێت (تا ڕێگه&zwnj;ی ڕاستی نیشان بدات)، ئه&zwnj;وسا تۆ ئه&zwnj;ی محمد صلی الله علیه وسلم له قیامه&zwnj;تدا ده&zwnj;بینیت سته&zwnj;مکاران کاتێك سزاو ئه&zwnj;شکه&zwnj;نجه&zwnj;ی دۆزه&zwnj;خ ده&zwnj;بینن، به که&zwnj;ساسیه&zwnj;وه ده&zwnj;ڵێن: ئایا هیچ ڕێگه&zwnj;یه&zwnj;ك هه&zwnj;یه بۆ گه&zwnj;ڕانه&zwnj;وه&zwnj;، تا خوا چۆن ده&zwnj;فه&zwnj;رموێت وابکه&zwnj;ین؟!.';
$t[45] = 'سه&zwnj;رئه&zwnj;نجام تاوانباران ده&zwnj;بینیت ده&zwnj;برێنه سه&zwnj;ر دۆزه&zwnj;خ، هه&zwnj;موو په&zwnj;ست و زه&zwnj;لیل ولاره مل و خه&zwnj;جاڵه&zwnj;تن، (له شه&zwnj;رمه&zwnj;زاریی و له ترسدا) له ژێره&zwnj;وه به&zwnj;تیله&zwnj;ی چاو ده&zwnj;ڕوانن بۆ ئاگره&zwnj;که&zwnj;، (ئا له&zwnj;و کات و بارودۆخه ناخۆشه&zwnj;دا) ئه&zwnj;وانه&zwnj;ی باوه&zwnj;ڕیان هێناوه ده&zwnj;ڵێن: به&zwnj;ڕاستی خه&zwnj;ساره&zwnj;تمه&zwnj;ند و زه&zwnj;ره&zwnj;رمه&zwnj;ندان ئه&zwnj;مڕۆ ئه&zwnj;وانه&zwnj;ن که خۆیان و که&zwnj;سو کارو ماڵ و منداڵیان له ده&zwnj;ستداوه له قیامه&zwnj;تدا (که&zwnj;س و کارو خزم و هاوه&zwnj;ڵانیان ئه&zwnj;گه&zwnj;ر له دۆزه&zwnj;خدان، ئه&zwnj;وه هیچ مایه&zwnj;ی دڵخۆشی نین، خۆ ئه&zwnj;گه&zwnj;ر له به&zwnj;هه&zwnj;شتدان، ئه&zwnj;مانیان هه&zwnj;ر له یاد نی یه&zwnj;)، ئینجا ڕاده&zwnj;گه&zwnj;یه&zwnj;نرێت که&zwnj;: ئاگاداربن بێگومان سته&zwnj;مکاران له ئازارو ئه&zwnj;شکه&zwnj;نجه&zwnj;ی به&zwnj;رده&zwnj;وامدان...';
$t[46] = 'ئه&zwnj;وانه هیچ دۆست و پشتیوانێکیان نی یه له جیاتی خوا که له ده&zwnj;ست سزاو تۆڵه ڕزگاریان بکات، ئه&zwnj;و که&zwnj;سه&zwnj;ش خوا سه&zwnj;ری لێ بشێوێنێت و گومڕای بکات (چونکه خۆی شایسته کردووه&zwnj;) ئیتر ڕێبازی ڕزگاری و سه&zwnj;رفرازی ده&zwnj;ستناکه&zwnj;وێت.';
$t[47] = 'خه&zwnj;ڵکینه&zwnj;، به&zwnj;پیر بانگه&zwnj;وازی په&zwnj;روه&zwnj;ردگارتانه&zwnj;وه بچن پێش ئه&zwnj;وه&zwnj;ی ڕۆژێك بێت: هیچ گه&zwnj;ڕانه&zwnj;وه&zwnj;ی له&zwnj;لایه&zwnj;ن خواوه نابێت، ئه&zwnj;و ڕۆژه هیچ په&zwnj;ناگایه&zwnj;کتان نابێت و که&zwnj;سیشتان ناتوانێت ئینکاری بکات و دان به تاوانه&zwnj;کانیدا نه&zwnj;نێت.';
$t[48] = 'ئه&zwnj;گه&zwnj;ر خوانه&zwnj;ناسان پشتیان هه&zwnj;ڵکرد و گوێیان نه&zwnj;گرت، ئه&zwnj;ی محمد صلی الله علیه وسلم داخیان بۆ مه&zwnj;خۆ، چونکه ئێمه تۆمان نه&zwnj;ناردووه چاودێر بیت به&zwnj;سه&zwnj;ریانه&zwnj;وه&zwnj;، به&zwnj;ڵکو تۆ ته&zwnj;نها گه&zwnj;یاندنی ئه&zwnj;م بانگه&zwnj;واز و قورئانه&zwnj;ت له&zwnj;سه&zwnj;ره&zwnj;، ئاشکراشه که&zwnj;: به&zwnj;ڕاستی ئێمه کاتێك نازو نیعمه&zwnj;ت و ڕه&zwnj;حمه&zwnj;تێك ده&zwnj;ڕێژین به&zwnj;سه&zwnj;ر ئاده&zwnj;میزادا زۆر که&zwnj;شخه&zwnj;ی پێوه ده&zwnj;کات، خۆ ئه&zwnj;گه&zwnj;ر به&zwnj;ڵاو ناخۆشیه&zwnj;کیان تووش بێت، به&zwnj;هۆی کاروکرده&zwnj;وه خراپه&zwnj;کانیانه&zwnj;وه&zwnj;، به&zwnj;ڕاستی ئه&zwnj;وکه&zwnj;سه زۆرسپڵه و ڕه&zwnj;خنه له خوا گره&zwnj;.';
$t[49] = 'هه&zwnj;ر بۆ خوایه خاوه&zwnj;ندارێتی ئاسمانه&zwnj;کان و زه&zwnj;وی، هه&zwnj;رچی بوێت دروستی ده&zwnj;کات، به&zwnj;هه&zwnj;رکه&zwnj;س که بیه&zwnj;وێت ته&zwnj;نها کچ ده&zwnj;به&zwnj;خشێت و به هه&zwnj;رکه&zwnj;س که بیه&zwnj;وێت کوڕ ده&zwnj;به&zwnj;خشێت...';
$t[50] = 'یاخود له هه&zwnj;ردوو جۆر.، به&zwnj;هه&zwnj;ندێك خێزان ده&zwnj;به&zwnj;خشێت له کوڕو له کچ، هه&zwnj;میشه ویستی خوا وایه هیچی نه&zwnj;دانێ و نه&zwnj;زۆك بێت، به&zwnj;ڕاستی ئه&zwnj;و زاته زانا و به&zwnj;ده&zwnj;سه&zwnj;ڵاته&zwnj;.';
$t[51] = 'که&zwnj;س بۆی نیه هیچ کات (له&zwnj;دنیادا) خوای گه&zwnj;وره وتو وێژی ڕاسته&zwnj;وخۆی له&zwnj;گه&zwnj;ڵدا بکات مه&zwnj;گه&zwnj;ر له ڕێگه&zwnj;ی وه&zwnj;حی و نیگاوه&zwnj;، یان له پشتی په&zwnj;رده&zwnj;وه&zwnj;، وه&zwnj;ك موسا، یان نێردراوێکی وه&zwnj;ك فریشته&zwnj;_جوبره&zwnj;ئیل_ده&zwnj;نێرێت که به گوێره&zwnj;ی فه&zwnj;رمان و مۆڵه&zwnj;تی خوا چی پێ سپێردراوه ده&zwnj;یگه&zwnj;یه&zwnj;نێت، به&zwnj;ڕاستی ئه&zwnj;و زاته خوایه&zwnj;کی بڵند و داناو کاربه&zwnj;جێیه&zwnj;.';
$t[52] = 'ئه&zwnj;ی محمد صلی الله علیه وسلم ئا به&zwnj;وشێوه&zwnj;یه&zwnj;ی که وتمان ئێمه قورئانمان بۆ ڕه&zwnj;وانه کردیت له لایه&zwnj;ن خۆمانه&zwnj;وه (که ژیانبه&zwnj;خشه به ڕوح و گیان و عه&zwnj;قڵ)، تۆ نه&zwnj;تده&zwnj;زانی قورئان چیه و ئیمان چیه&zwnj;، به&zwnj;ڵام ئێمه ئه&zwnj;م قورئانه&zwnj;مان کرده ڕوناکی ونورێك که هیدایه&zwnj;ت و ڕێنمویی هه&zwnj;ر که&zwnj;س که بمانه&zwnj;وێت له به&zwnj;نده&zwnj;کانمانی پێ ده&zwnj;که&zwnj;ین، (ئه&zwnj;وانه&zwnj;ی دڵی بۆ ده&zwnj;که&zwnj;نه&zwnj;وه و به&zwnj;چاکی گوێ بۆ ده&zwnj;گرن)، بێگومان تۆ ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر صلی الله علیه وسلم ڕێنیشان ده&zwnj;ریت و ڕێنموی ده&zwnj;که&zwnj;ی بۆ ڕێگه و ڕێبازێکی ڕاست و دروست...';
$t[53] = 'که ڕێگه و ڕێبازی ئه&zwnj;و خوایه&zwnj;یه&zwnj;، که&zwnj;هه&zwnj;ر بۆ ئه&zwnj;وه ئه&zwnj;وه&zwnj;ی له ئاسمانه&zwnj;کان و زه&zwnj;ویدایه&zwnj;، ئاگاداریش بن: هه&zwnj;موو شتێك و سه&zwnj;رئه&zwnj;نجامی هه&zwnj;موو کارێك بۆ لای خوا ده&zwnj;گه&zwnj;ڕێته&zwnj;وه&zwnj;.';
?>