<?php
$t = array();
$t[1] = 'سه&zwnj;رنجی سه&zwnj;ره&zwnj;تای سوره&zwnj;تی (الشوری) بده لاپه&zwnj;ره&zwnj;_483_.';
$t[2] = 'سوێند به&zwnj;م په&zwnj;یامه ڕوون و ئاشکرایه&zwnj;، مه&zwnj;به&zwnj;ست قورئانه&zwnj;.';
$t[3] = 'ئێمه ئه&zwnj;و کتێبه&zwnj;مان کردوه&zwnj;ه به قورئانێك، به&zwnj;زمانی عه&zwnj;ره&zwnj;بی، به&zwnj;ڵکو ژیری و بیروهۆشتان بخه&zwnj;نه کار (تالێی تێبگه&zwnj;ن و په&zwnj;یڕه&zwnj;وی بکه&zwnj;ن).';
$t[4] = 'ئه&zwnj;م قورئانه له _لوح المحفوظ_ دا (که&zwnj;سه&zwnj;رچاوه&zwnj;ی قورئان و هه&zwnj;موو کتێبه&zwnj;کانی تری خوایه&zwnj;) پارێزراوه که زۆر به&zwnj;رزو به&zwnj;ڕێزه لامان و پڕه له دانایی.';
$t[5] = 'باشه&zwnj;، ئایا ده&zwnj;کرێت ئێمه وازتان لێ بهێنین و هۆشیاریتان نه&zwnj;که&zwnj;ینه&zwnj;وه به&zwnj;م قورئانه له&zwnj;به&zwnj;ر ئه&zwnj;وه&zwnj;ی ئێوه هۆزێکی زیاده&zwnj;ڕه و لارو وێرن (نه&zwnj;خێر شتی وا له ئێمه ناوه&zwnj;شێته&zwnj;وه&zwnj;).';
$t[6] = 'ئێمه چه&zwnj;نده&zwnj;ها پێغه&zwnj;مبه&zwnj;رمان ناردووه بۆ نه&zwnj;وه&zwnj;کانی ڕابووردوو تا سه&zwnj;رگه&zwnj;ردان نه&zwnj;بن.';
$t[7] = 'بێگومان هیچ پێغه&zwnj;مبه&zwnj;رێکیان بۆ نه&zwnj;هاتووه که ئه&zwnj;وان گاڵته&zwnj;یان پێ نه&zwnj;کردبێت.';
$t[8] = 'که&zwnj;واته ئه&zwnj;بێت ئه&zwnj;م بێ بڕوایانه چاوه&zwnj;ڕێی چی بکه&zwnj;ن له&zwnj;کاتێکدا ئێمه دڕنده&zwnj;تر و به&zwnj;هه&zwnj;ڵمه&zwnj;تترمان له&zwnj;وان له&zwnj;ناو بردووه&zwnj;، ئیتر به&zwnj;سه&zwnj;رهات و ڕووداوی پێشینان به&zwnj;سه&zwnj;رچووه و تێپه&zwnj;ڕ یووه&zwnj;.';
$t[9] = 'خۆ ئه&zwnj;گه&zwnj;ر پرسیاریان لێ بکه&zwnj;یت: کێ ئاسمانه&zwnj;کان و زه&zwnj;وی دروست کردووه&zwnj;؟ له وه&zwnj;ڵامدا به&zwnj;دڵنیایی ده&zwnj;ڵێن: هه&zwnj;ر زاتی باڵاده&zwnj;ست و زانا دروستی کردوون.';
$t[10] = 'ئه&zwnj;و خوایه&zwnj;ی که زه&zwnj;وی کردووه به لانکه&zwnj;ی ژیانتان، ڕێگه&zwnj;وبانی تیایدا بۆ ئاسان کردوون، بۆ ئه&zwnj;وه&zwnj;ی بگه&zwnj;نه مه&zwnj;به&zwnj;ست و ژیانتان ئاسان و ئاسووده بکه&zwnj;ن.';
$t[11] = 'هه&zwnj;روه&zwnj;ها ئه&zwnj;و خوایه له ئاسمانه&zwnj;وه به ئه&zwnj;ندازه&zwnj;یه&zwnj;کی دیاریی کراو بارانی باراندووه و ده&zwnj;فه&zwnj;رموێت، وڵاتی وشك و مردوومان پێ زیندوو کردۆته&zwnj;وه&zwnj;، هه&zwnj;ر به&zwnj;و شێوه&zwnj;یه ئێوه&zwnj;ش له گۆڕه&zwnj;کانتان ده&zwnj;هێنرێنه ده&zwnj;ره&zwnj;وه&zwnj;.';
$t[12] = 'هه&zwnj;ر ئه&zwnj;و زاته هه&zwnj;موو جووته&zwnj;کانی (له جیهانی ئاده&zwnj;میزاد و زینده&zwnj;واران و ڕوه&zwnj;ك و بێ گیانه&zwnj;کاندا) دروست کردووه&zwnj;، هه&zwnj;روه&zwnj;ها که&zwnj;شتی و جۆره&zwnj;کانی ئاژه&zwnj;ڵی بۆ ڕام هێناون، که بۆ سواریی سوودی لێ وه&zwnj;ربگرن.';
$t[13] = 'بۆ ئه&zwnj;وه&zwnj;ی له&zwnj;سه&zwnj;ر پشتیان جێگیر ببن، دوایش یادی نازو نیعمه&zwnj;تی په&zwnj;روه&zwnj;ردگارتان بکه&zwnj;نه&zwnj;وه کاتێك که سه&zwnj;قامگیر ده&zwnj;بن له&zwnj;سه&zwnj;ری، (چونکه جۆره&zwnj;ها هۆکاری هاتووچۆی بۆ سازاندوون) و بشڵێن: ستایش و به&zwnj;رزی و بێگه&zwnj;ردی بۆ ئه&zwnj;و زاته&zwnj;ی که ئه&zwnj;مه&zwnj;ی بۆ ڕه&zwnj;خساندوین (خۆ ئه&zwnj;گه&zwnj;ر ئه&zwnj;و به&zwnj;میهره&zwnj;بانی خۆی بۆی ڕام نه&zwnj;کردینایه&zwnj;) ئێمه نه&zwnj;مانده&zwnj;توانی ڕامیان بهێنین.';
$t[14] = 'ئێمه بێگومان هه&zwnj;ر بۆ لای په&zwnj;روه&zwnj;ردگارمان ده&zwnj;گه&zwnj;رێینه&zwnj;وه&zwnj;. پێغه&zwnj;مبه&zwnj;ر صلی الله علیه وسلم هه&zwnj;ر جارێك سواری وڵاخه&zwnj;که&zwnj;ی ببوایه سێ جار (الله أکبر) ی ده&zwnj;فه&zwnj;رموو، دوایی ده&zwnj;یفه&zwnj;رموو: سبحان الذی سخر لنا هذا وما کنا له مقرنین وإنا الی ڕبنا لمنقلبون).';
$t[15] = 'باوه&zwnj;ڕ چه&zwnj;وته&zwnj;کان، هه&zwnj;ندێ له به&zwnj;نده&zwnj;کانی خوا، به&zwnj;خوا ده&zwnj;زانن (مه&zwnj;سیحییه&zwnj;کان ده&zwnj;ڵێن: عیسا کوڕی خوایه&zwnj;، جوو ده&zwnj;ڵێت: عوزه&zwnj;یر، بتپه&zwnj;رسته&zwnj;کان ده&zwnj;ڵێن: فریشته&zwnj;کان کچی خوان)!! به&zwnj;ڕاستی زۆربه&zwnj;ی خه&zwnj;ڵکی قه&zwnj;دری خوا نازانن و بێ باوه&zwnj;ڕێکی زۆر ئاشکران.';
$t[16] = 'ئایا ڕه&zwnj;واو ڕاسته&zwnj;، له&zwnj;وه&zwnj;ی که دروستی ده&zwnj;کات کچان بۆ خۆی بڕیار بدات و به&zwnj;خشینی کوڕانیش به&zwnj;ڕه&zwnj;وا بزانێت بۆ ئێوه&zwnj;؟! (ئه&zwnj;مانه هه&zwnj;مووی بیروباوه&zwnj;ڕی نه&zwnj;فامه&zwnj;کان بوون خوای گه&zwnj;وره ده&zwnj;یه&zwnj;وێت هه&zwnj;ر به بۆچوونی خۆیان بیروباوه&zwnj;ڕیان پووچه&zwnj;ڵ بکاته&zwnj;وه&zwnj;).';
$t[17] = 'کاتیکیش مژده بدرێت به یه&zwnj;کێکیان به&zwnj;وه&zwnj;ی که بۆ خوای میهره&zwnj;بان به ڕه&zwnj;وای زانیووه (مه&zwnj;به&zwnj;ست ئه&zwnj;وه&zwnj;یه که کچیان بووه&zwnj;)، ئه&zwnj;وه ڕووخساری ڕه&zwnj;ش هه&zwnj;ڵده&zwnj;گه&zwnj;ڕێت و له ده&zwnj;رووندا پیش ده&zwnj;خواته&zwnj;وه&zwnj;.';
$t[18] = 'ئایا که&zwnj;سێك به منداڵی خوا ڕه&zwnj;وا ده&zwnj;بینن، (که له&zwnj;باوی ئه&zwnj;واندا) هه&zwnj;رده&zwnj;م په&zwnj;روه&zwnj;رده ده&zwnj;کرێت له&zwnj;ناو خشڵ و ئارایش کردنی خۆیداو له شه&zwnj;ڕو شۆڕو جه&zwnj;نگیشدا ده&zwnj;رناکه&zwnj;وێت؟!';
$t[19] = 'ئه&zwnj;و خوانه&zwnj;ناسانه فریشته&zwnj;کان که به&zwnj;نده&zwnj;ی خوای میهره&zwnj;بانن، به&zwnj;کچی خوایان ده&zwnj;دایه قه&zwnj;ڵه&zwnj;م!! ئایا ئه&zwnj;وان ئاماده&zwnj;ی دروستکردنیان بوون؟! له ئایینده&zwnj;دا ئه&zwnj;م بوختانه&zwnj;یان له&zwnj;سه&zwnj;ر ده&zwnj;نوسرێت و پرسیاریشیان لێ ده&zwnj;کرێت.';
$t[20] = '(بۆ پینه کردنی هه&zwnj;ڵویستی ناڕه&zwnj;وایان) ده&zwnj;یانوت: ئه&zwnj;گه&zwnj;ر خوای میهره&zwnj;بان بیویستایه&zwnj;... ئێمه ئه&zwnj;مانه&zwnj;مان نه&zwnj;ده&zwnj;په&zwnj;رست، وه نه&zwnj;بێت له&zwnj;سه&zwnj;ر بنچینه&zwnj;ی هیچ زانستی و زانیاریه&zwnj;ك ئه&zwnj;و قسه&zwnj;یه بکه&zwnj;ن، ئه&zwnj;مانه ته&zwnj;نها درۆ نه&zwnj;بێت هیچی تر ناڵێن.';
$t[21] = 'ئایا ئه&zwnj;و خوانه&zwnj;ناسانه پێش ئه&zwnj;م قورئانه کتێبێکی ترمان بۆ ناردوون؟ تا پابه&zwnj;ندی بن و په&zwnj;یڕه&zwnj;وی بکه&zwnj;ن؟';
$t[22] = 'ئه&zwnj;وانه هیچی وا نیه&zwnj;، به&zwnj;ڵکو هه&zwnj;ر ته&zwnj;نها ئه&zwnj;وه&zwnj;یه که ده&zwnj;یانوت: به&zwnj;ڕاستی ئێمه باوو باپیرانی خۆمان له&zwnj;سه&zwnj;ر ئاین و به&zwnj;رنامه&zwnj;یه&zwnj;کی ئاوا دیووه&zwnj;، بێگومان ئێمه&zwnj;ش به&zwnj;شوێن ڕێبازی ئه&zwnj;واندا هه&zwnj;نگاو هه&zwnj;ڵده&zwnj;گرین و ڕێنمووییمان وه&zwnj;رگرتووه&zwnj;!!';
$t[23] = 'هه&zwnj;ر به&zwnj;و شێوه&zwnj;یه هیچ پێغه&zwnj;مبه&zwnj;رێکمان نه&zwnj;ناردووه بۆ هیچ شارێك، که خۆشگوزه&zwnj;رانه&zwnj;کان نه&zwnj;یانوتبێت: ئێمه باوو باپیرانی خۆمان بینیووه پابه&zwnj;ندی بیروباوه&zwnj;ڕێك بوون، بێگومان ئێمه&zwnj;ش چاو له&zwnj;وان ده&zwnj;که&zwnj;ین و په&zwnj;یڕه&zwnj;وی ئه&zwnj;وان ده&zwnj;که&zwnj;ین.';
$t[24] = 'پێغه&zwnj;مبه&zwnj;ره&zwnj;که&zwnj;ش وتوویه&zwnj;تی: باشه&zwnj;... ئه&zwnj;گه&zwnj;ر له ڕێگه و ڕێبازو به&zwnj;رنامه&zwnj;ی باو و باپیرانتان چاکتریشم بۆ هێنان هه&zwnj;ر شوێنیان ده&zwnj;که&zwnj;ون؟! له وه&zwnj;ڵامدا وتیان: به&zwnj;ڕاستی ئێمه به&zwnj;و به&zwnj;رنامه و په&zwnj;یامه&zwnj;ی که به ئێوه&zwnj;دا ڕه&zwnj;وانه کراوه بڕوا ناکه&zwnj;ین.';
$t[25] = 'جا ئێمه&zwnj;ش تۆڵه&zwnj;مان لێسه&zwnj;ندن، سه&zwnj;رنج بده و ببینه سه&zwnj;رئه&zwnj;نجامی ئه&zwnj;وانه&zwnj;ی بڕوایان به په&zwnj;یامی پێغه&zwnj;مبه&zwnj;ران نه&zwnj;کرد چۆن بوو.';
$t[26] = 'ئه&zwnj;و کاته بیهێنه&zwnj;وه یاد که ئیبراهیم به باوکی و هۆزه&zwnj;که&zwnj;ی خۆی وت: به&zwnj;ڕاستی من دووره په&zwnj;رێزم له&zwnj;و شتانه&zwnj;ی که ئێوه ده&zwnj;یپه&zwnj;رستن...';
$t[27] = 'جگه له&zwnj;و زاته&zwnj;ی که دروستی کردووم، هه&zwnj;ر ئه&zwnj;ویش ڕێنموویم ده&zwnj;کات و ده&zwnj;مخاته سه&zwnj;ر ڕێگه&zwnj;ی ڕاست و دروست.';
$t[28] = 'ئه&zwnj;وسا ئیتر ئه&zwnj;و به&zwnj;رنامه&zwnj;ی یه&zwnj;کخواناسیه&zwnj;ی هێشته&zwnj;وه بۆ هۆزو گه&zwnj;لانی دوای خۆی، بۆ ئه&zwnj;وه&zwnj;ی ئه&zwnj;وانیش بگه&zwnj;ڕێنه&zwnj;وه بۆ سه&zwnj;ر ئه&zwnj;و بیروباوه&zwnj;ڕه ڕاسته&zwnj;.';
$t[29] = 'خوا ده&zwnj;فه&zwnj;رموێت: جا من ئه&zwnj;وانه و باو و باپیرانیانم له خۆشگوزه&zwnj;رانیدا هێشته&zwnj;وه تا ئه&zwnj;و کاته&zwnj;ی قورئان و پێغه&zwnj;مبه&zwnj;رێکی ئاشکرایان بۆ هات (تا په&zwnj;یامی خوایان بۆ ڕوون بکاته&zwnj;وه&zwnj;).';
$t[30] = 'کاتێکیش که حه&zwnj;ق و ڕاستیان پێگه&zwnj;یشت.. وتیان: ئه&zwnj;مه سیحرو جادووه و بێگومان ئێمه بێ باوه&zwnj;ڕین پێی..';
$t[31] = 'هه&zwnj;روه&zwnj;ها بیانوویان گرت وتیان: ئه&zwnj;وه بۆ ئه&zwnj;م قورئانه دانه&zwnj;به&zwnj;زێنرا بۆ سه&zwnj;ر پیاوێکی گه&zwnj;وره له پیاوه گه&zwnj;وره&zwnj;کانی دوو شاره&zwnj;که&zwnj;؟! (مه&zwnj;به&zwnj;ستیان مه&zwnj;ککه و طائف بووه&zwnj;)';
$t[32] = 'ئایا ئه&zwnj;وانه ڕه&zwnj;حمه&zwnj;تی په&zwnj;روه&zwnj;ردگاری تۆ دابه&zwnj;ش ده&zwnj;که&zwnj;ن؟! (که&zwnj;سانی شایسته ده&zwnj;دۆزنه&zwnj;وه بۆ پێغه&zwnj;مبه&zwnj;رایه&zwnj;تی؟!) له کاتێکدا ئێمه&zwnj;ین که ڕزق و ڕۆزیمان له نێوانیاندا دابه&zwnj;ش کردووه له ژیانی دنیادا و پله&zwnj;و پایه&zwnj;ی هه&zwnj;ندێکیمان به&zwnj;رزکردۆته&zwnj;وه به&zwnj;سه&zwnj;ر هه&zwnj;ندێکیاندا، بۆ ئه&zwnj;وه&zwnj;ی هه&zwnj;مووان کاروباری یه&zwnj;کتر ته&zwnj;واوبکه&zwnj;ن و که&zwnj;ڵك له توانایی ولێهاتوویی یه&zwnj;کتر وه&zwnj;ربگرن، جا له&zwnj;گه&zwnj;ڵ ئه&zwnj;و هه&zwnj;موو به&zwnj;هره&zwnj;و به&zwnj;خششانه&zwnj;دا ڕه&zwnj;حمه&zwnj;ت و میهره&zwnj;بانی په&zwnj;روه&zwnj;ردگاری تۆ (که&zwnj;تایبه&zwnj;تی ئیماندارانه له قیامه&zwnj;تدا) له هه&zwnj;موو ئه&zwnj;و شتانه&zwnj;ی که خه&zwnj;ڵکی کۆی ده&zwnj;که&zwnj;نه&zwnj;وه (له ما&zwnj;ڵ و سامان و هۆکاره&zwnj;کانی ڕابواردن) چاکترو به&zwnj;نرختره&zwnj;.';
$t[33] = 'ئه&zwnj;گه&zwnj;ر له&zwnj;به&zwnj;رئه&zwnj;وه نه&zwnj;بوایه خه&zwnj;ڵکی هه&zwnj;موو ده&zwnj;بوون به یه&zwnj;کپارچه (له بێ باوه&zwnj;ڕی و تاوانکاریدا)، ئه&zwnj;وه هه&zwnj;ر که&zwnj;س باوه&zwnj;ڕی به&zwnj;خوای میهره&zwnj;بان نه&zwnj;بوایه سه&zwnj;قفی خانووه&zwnj;که&zwnj;یمان له زیو بۆ دروست ده&zwnj;کرد، هه&zwnj;روه&zwnj;ها قادرمه و ئاسانسوارمان بۆ ده&zwnj;کردن تا به&zwnj;سه&zwnj;ریدا سه&zwnj;ربکه&zwnj;ون و ده&zwnj;ربکه&zwnj;ون...';
$t[34] = 'هه&zwnj;روه&zwnj;ها بۆ ماڵه&zwnj;کانیان قاپی و ده&zwnj;روازه&zwnj;ی زیو و کورسی و قه&zwnj;نه&zwnj;فه&zwnj;ی ڕازاوه&zwnj;شمان پێده&zwnj;به&zwnj;خشین تا پاڵ بده&zwnj;نه&zwnj;وه و شانی له&zwnj;سه&zwnj;ر داده&zwnj;ن...';
$t[35] = 'به هه&zwnj;موو جۆره نه&zwnj;خش و نیگارێکی جوان و ڕازاوه (دارو دیوارو سه&zwnj;قف و ده&zwnj;رگا و که&zwnj;لوپه&zwnj;لی ناو ماڵه&zwnj;کانیانمان ده&zwnj;ڕازانده&zwnj;وه&zwnj;)، جا ئه&zwnj;وه هه&zwnj;ر هه&zwnj;مووی خۆشگوزه&zwnj;رانی ژیانی دنیا نه&zwnj;بێت هیچی تر نی یه&zwnj;، له کاتێکدا که ژیانی پڕ خۆشی و به&zwnj;خته&zwnj;وه&zwnj;ری نه&zwnj;بڕاوه لای په&zwnj;روه&zwnj;ردگارت بۆ خواناسان و دووره په&zwnj;رێزانه له گوناه.';
$t[36] = 'که&zwnj;سێك خۆی کوێر بکات له ئاستی قورئان و یادی خوای میهره&zwnj;بان، ئه&zwnj;وه ئێمه شه&zwnj;یتانێك ده&zwnj;که&zwnj;ینه مڵۆزمی و هه&zwnj;میشه ده&zwnj;بێته یار و یاوه&zwnj;ری و له&zwnj;گه&zwnj;ڵیدا ده&zwnj;بێت.';
$t[37] = 'بێگومان ئه&zwnj;و شه&zwnj;یتانانه&zwnj;، ڕێگه&zwnj;ی ڕاستیان لێ ده&zwnj;گرن و وایان لێده&zwnj;که&zwnj;ن که وابزانن به&zwnj;دڵنیایی له&zwnj;سه&zwnj;ر ڕێبازێکی چاك و ڕاستن.';
$t[38] = 'تا ئه&zwnj;و کاته&zwnj;ی خه&zwnj;ڵه&zwnj;تاوه&zwnj;که دێته لامان (له قیامه&zwnj;تدا خۆی و شه&zwnj;یتانه&zwnj;که&zwnj;ی ئاماده ده&zwnj;کرێن بۆ لێپرسینه&zwnj;وه&zwnj;) ئه&zwnj;وه به شه&zwnj;یتانه&zwnj;که&zwnj;ی ده&zwnj;ڵێت: خۆزگه نێوانی من و تۆ به قه&zwnj;ده&zwnj;ر ڕۆژهه&zwnj;ڵات و ڕۆژئاوا له&zwnj;یه&zwnj;ك دوور بوایه&zwnj;، ئای که هاوڕێیه&zwnj;کی ناله&zwnj;بارو خراپ بوویت.';
$t[39] = 'ئه&zwnj;مڕۆ ئه&zwnj;و قسه و گله&zwnj;یی و بناشتانه هه&zwnj;رگیز سوودتان پێ ناگه&zwnj;یه&zwnj;نن، (چونکه&zwnj;) کاتی خۆی ئێوه سته&zwnj;متان له خۆتان و خه&zwnj;ڵکیش کردووه&zwnj;، ئێسته بێگومان هه&zwnj;ردوولاتان له سزاو ئازاردا به&zwnj;شدارن.';
$t[40] = 'ئایا تۆ ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر ئه&zwnj;و بێ باوه&zwnj;ڕه&zwnj;ی که وه&zwnj;ك که&zwnj;ڕ وایه گوێبیست ده&zwnj;که&zwnj;یت؟ یان ئه&zwnj;وه&zwnj;ی کوێره هیدایه&zwnj;تی ده&zwnj;ده&zwnj;یت؟! یاخود ئه&zwnj;و که&zwnj;سه&zwnj;ی له گومڕایی ئاشکرادا ڕۆچووه ڕێنموویی ده&zwnj;که&zwnj;یت؟! (دیاره ئه&zwnj;وه حاڵیان بێت هیدایه&zwnj;تیان ئاسان نیه&zwnj;).';
$t[41] = 'جا ئه&zwnj;گه&zwnj;ر تۆ له&zwnj;ناو به&zwnj;رین و بتمرێنین، ئه&zwnj;وه ئێمه بێگومان تۆڵه&zwnj;یان هه&zwnj;ر لێده&zwnj;سێنین.';
$t[42] = 'یاخود ئه&zwnj;نجامی ئه&zwnj;وهه&zwnj;ڕه&zwnj;شه&zwnj;یه&zwnj;ی لێمان کردوون نیشانی تۆی ده&zwnj;ده&zwnj;ین، چونکه بێگومان ئێمه توانامان به&zwnj;سه&zwnj;ریاندا هه&zwnj;یه&zwnj;.';
$t[43] = 'که&zwnj;واته تۆ بێ خه&zwnj;م به&zwnj;و پابه&zwnj;ندی ئه&zwnj;وه&zwnj;به که نیگات بۆ کراوه&zwnj;، چونکه تۆ به&zwnj;ڕاستی له&zwnj;سه&zwnj;ر ڕێگه و ڕێباز و به&zwnj;رنامه&zwnj;یه&zwnj;کی ڕاست و دروستیت (له لایه&zwnj;ن په&zwnj;روه&zwnj;ردگارته&zwnj;وه ئاڕاسته ده&zwnj;کرێیت).';
$t[44] = 'به&zwnj;ڕاستی ئه&zwnj;م قورئانه یادخه&zwnj;ره&zwnj;وه&zwnj;یه بۆ خۆت و بۆ قه&zwnj;ومه&zwnj;که&zwnj;ت، له ئاینده&zwnj;شدا پرسیارتان لێده&zwnj;کرێت ده&zwnj;رباره&zwnj;ی.';
$t[45] = 'تۆ له&zwnj;و پێغه&zwnj;مبه&zwnj;رانه&zwnj;ی که ناردوومانه پێش تۆ بپرسه&zwnj;، ئایا جگه له خوای میهره&zwnj;بان خوای ترمان دیاری کردووه که به&zwnj;ندایه&zwnj;تی بۆ بکرێت. (مه&zwnj;به&zwnj;ست ئه&zwnj;وه&zwnj;یه که سه&zwnj;رچاوه&zwnj;ی هه&zwnj;موو پێغه&zwnj;مبه&zwnj;ران یه&zwnj;کێکه&zwnj;)';
$t[46] = 'سوێند به&zwnj;خوا بێگومان ئێمه موسا پێغه&zwnj;مبه&zwnj;رمان نارد به به&zwnj;ڵگه و نیشانه&zwnj;کانمانه&zwnj;وه بۆ لای فیرعه&zwnj;ون و دارو ده&zwnj;سته&zwnj;ی، پێی ڕاگه&zwnj;یاندن که&zwnj;: به&zwnj;ڕاستی من ڕه&zwnj;وانه&zwnj;کراوی په&zwnj;روه&zwnj;ردگاری هه&zwnj;موو جیهانیانم.';
$t[47] = 'که&zwnj;چی که هاته لایان به به&zwnj;ڵگه و موعجیزه&zwnj;کانمانه&zwnj;وه&zwnj;، سه&zwnj;ریان سوڕماو گاڵته و پێکه&zwnj;نینیان به موعجیزه&zwnj;کانی ده&zwnj;هات.';
$t[48] = 'هیچ موعجیزه&zwnj;یه&zwnj;کمان نیشان نه&zwnj;داون، که له&zwnj;وی تر گه&zwnj;وره&zwnj;تر نه&zwnj;بووبێت، (جا که باوه&zwnj;ڕیان نه&zwnj;هێناوه&zwnj;) گرفتاری تاڵی و ناخۆشی و سزامان کردن بۆ ئه&zwnj;وه&zwnj;ی بگه&zwnj;ڕێنه&zwnj;وه و به&zwnj;خۆدا بچنه&zwnj;وه&zwnj;.';
$t[49] = 'که&zwnj;چی به موسایان وت: ئه&zwnj;ی جادووگه&zwnj;ر!! له په&zwnj;روه&zwnj;ردگارت داوا بکه بۆمان، به&zwnj;و په&zwnj;یمانه&zwnj;ی به&zwnj;تۆی داوه بابه&zwnj;ڵاو ناخۆشیمان له&zwnj;سه&zwnj;ر هه&zwnj;ڵبگرێت بێگومان ئێمه له هیدایه&zwnj;ت دراوان ده&zwnj;بین!!';
$t[50] = 'جاهه&zwnj;ر که به&zwnj;ڵاو ناخۆشیمان له&zwnj;سه&zwnj;ر لابردن خێرا به&zwnj;ڵێن و په&zwnj;یمانه&zwnj;که&zwnj;یان هه&zwnj;ڵده&zwnj;وه&zwnj;شاند.';
$t[51] = 'فیرعه&zwnj;ون جاڕیدا به&zwnj;ناو قه&zwnj;وم و گه&zwnj;له&zwnj;که&zwnj;یدا وتی: ئه&zwnj;ی قه&zwnj;وم و گه&zwnj;لم، من پاشای میصرم و فه&zwnj;رمانڕه&zwnj;وای ئه&zwnj;م وڵاته&zwnj;م، ئه&zwnj;م ڕووبارانه&zwnj;ش به&zwnj;به&zwnj;ر کۆشکه&zwnj;کانمدا ده&zwnj;ڕوات، ئایا ئه&zwnj;م ده&zwnj;سه&zwnj;ڵاته&zwnj;ی من نابینن؟!..';
$t[52] = 'به&zwnj;ڵکو نابینن من چاکترم له&zwnj;و که&zwnj;سه&zwnj;ی ناوبانگی نی یه و که&zwnj;سایه&zwnj;تیه&zwnj;کی لاوازو نه&zwnj;ناسراوه و زمان پاراو نی یه و له&zwnj;وانه نی یه بتوانێت چاك خه&zwnj;ڵکی تێبگه&zwnj;یه&zwnj;نێت؟!';
$t[53] = 'جا ئه&zwnj;وه بۆچی بازنی ئاڵتون و زێڕی پێشکه&zwnj;ش ناکرێت (تانیشانه بێت بۆ پایه&zwnj;داری؟!)یان ئه&zwnj;وه بۆ فریشته له&zwnj;گه&zwnj;ڵیدا نه&zwnj;هاتن بۆ بانگه&zwnj;واز کردن و پشتیوانی لێکردنی؟!';
$t[54] = 'فیرعه&zwnj;ون هیچ نرخی دانه&zwnj;نا بۆ قه&zwnj;وم و گه&zwnj;له&zwnj;که&zwnj;ی، به سووکی سه&zwnj;یری ده&zwnj;کردن، ئه&zwnj;وانیش ملکه&zwnj;چ و فه&zwnj;رمانبه&zwnj;رداری بوون، به&zwnj;ڕاستی ئه&zwnj;وانه قه&zwnj;ومێکی فاسق و تاوانبار و گوناهکار بوون.';
$t[55] = 'خوا فه&zwnj;رمووی: جا کاتێك ئێمه&zwnj;یان تووڕه کردو ڕقی ئێمه&zwnj;یان هه&zwnj;ستان، ئێمه&zwnj;ش تۆڵه&zwnj;مان لێسه&zwnj;ندن، هه&zwnj;ر هه&zwnj;موویانمان له ده&zwnj;ریادا خنکاندو نوقم کرد...';
$t[56] = 'جا کردمانن به پێشه&zwnj;نگی تیاچووه&zwnj;کان و په&zwnj;ند و عیبره&zwnj;ت بۆ خه&zwnj;ڵکی تری دوای ئه&zwnj;وان.';
$t[57] = 'کاتێکیش باسی کوڕه&zwnj;که&zwnj;ی مه&zwnj;ریه&zwnj;م_عیسا_ به&zwnj;نموونه هێنرابێته&zwnj;وه&zwnj;، ده&zwnj;ستبه&zwnj;جێ قه&zwnj;وم و هۆزه&zwnj;که&zwnj;ی تۆ مله&zwnj;جه&zwnj;ڕێیان کردووه و سه&zwnj;ریان باداوه و ته&zwnj;سلیمی حه&zwnj;ق نه&zwnj;بوون (به باسکردنی سه&zwnj;غڵه&zwnj;ت بوون)....';
$t[58] = 'وتویانه ئایا په&zwnj;رستراوه&zwnj;کانی ئێمه شایانی په&zwnj;رستنن یان ئه&zwnj;و؟! واته&zwnj;_عیسا_، ئه&zwnj;و نموونانه&zwnj;یان بۆ تۆ نه&zwnj;ده&zwnj;هێنایه&zwnj;وه&zwnj;، ته&zwnj;نها بۆ موجاده&zwnj;له نه&zwnj;بێت، (وه&zwnj;نه&zwnj;بێت به&zwnj;شوێن حه&zwnj;ق و ڕاستیدا بگه&zwnj;ڕێن)، به&zwnj;ڵکو ئه&zwnj;وانه که&zwnj;سانێکن هه&zwnj;میشه حه&zwnj;ز به دوژمنایه&zwnj;تی و ئاژاوه و ململانێی بێ سه&zwnj;روبن ده&zwnj;که&zwnj;ن.';
$t[59] = 'ئه&zwnj;و عیسایه ته&zwnj;نها به&zwnj;نده&zwnj;یه&zwnj;کی ئێمه بوو که نازو نیعمه&zwnj;تی پێغه&zwnj;مبه&zwnj;رایه&zwnj;تیمان پێبه&zwnj;خشی و کردمانه نموونه&zwnj;یه&zwnj;کی چاك بۆ نه&zwnj;وه&zwnj;ی ئیسرائیل تا شوێنی بکه&zwnj;ون.';
$t[60] = 'ئه&zwnj;گه&zwnj;ر بمانویستایه ئێوه&zwnj;مان ده&zwnj;کرده فریشته وله زه&zwnj;ویدا نیشته&zwnj;جێمان ده&zwnj;کردن و هه&zwnj;موو فه&zwnj;رمانبه&zwnj;ردار ده&zwnj;بوون.';
$t[61] = 'به&zwnj;ڕاستی له دایکبوونی عیسا، به&zwnj;بێ باوك، به&zwnj;ڵگه&zwnj;یه له&zwnj;سه&zwnj;ر قیامه&zwnj;ت، که&zwnj;واته به&zwnj;گومان مه&zwnj;بن لێی و باوه&zwnj;ڕی پێبهێنن و پێیان بڵێ: هه&zwnj;ر په&zwnj;یڕه&zwnj;وی له زاتی من بکه&zwnj;ن، هه&zwnj;ر ئه&zwnj;وه&zwnj;شه ڕێگه&zwnj;ی ڕاست و دروست.';
$t[62] = 'خه&zwnj;ڵکینه هه&zwnj;وڵ بده&zwnj;ن با شه&zwnj;یتان ڕێتان پێ نه&zwnj;گرێت و له ڕێی ڕاست لاتان نه&zwnj;دات چونکه بێگومان ئه&zwnj;و دوژمنێکی ئاشکراتانه&zwnj;.';
$t[63] = 'کاتێك عیسا به موعجیزه&zwnj;ی ئاشکراوه ڕه&zwnj;وانه&zwnj;کرا بۆ نه&zwnj;وه&zwnj;ی ئیسرائیل وتی: به&zwnj;ڕاستی من حیکمه&zwnj;ت و داناییم بۆ هێناون، هه&zwnj;روه&zwnj;ها زانیاری ئه&zwnj;وه&zwnj;شم پێ یه که ئه&zwnj;و شتانه&zwnj;تان بۆ ڕوون بکه&zwnj;مه&zwnj;وه که کێشه&zwnj;تان له&zwnj;سه&zwnj;ری هه&zwnj;یه و ڕاو بۆچونتان له&zwnj;سه&zwnj;ری جیاوازه&zwnj;، جا ئێوه له خوا بترسن و گوێڕایه&zwnj;ڵی من بکه&zwnj;ن.';
$t[64] = 'به&zwnj;ڕاستی خوای گه&zwnj;وره په&zwnj;روه&zwnj;ردگاری منیش و ئێوه&zwnj;شه&zwnj;، ته&zwnj;نها ئه&zwnj;و بپه&zwnj;رستن، هه&zwnj;ر ئه&zwnj;مه&zwnj;شه ڕێگه و ڕێبازی ڕاست و دروست.';
$t[65] = 'پاشان ده&zwnj;سته و گرۆهه&zwnj;کان جیاوازی که&zwnj;وته نێوانیان له&zwnj;سه&zwnj;ر عیسا خۆی، وه&zwnj;یل و قوڕبه&zwnj;سه&zwnj;ری بۆ ئه&zwnj;وانه&zwnj;ی که سته&zwnj;میان کردووه و سته&zwnj;م ده&zwnj;که&zwnj;ن، له سزاو ئازاری ڕۆژێکی پڕ ئێش.';
$t[66] = 'ئایا ئه&zwnj;وانه جگه&zwnj;له هاتنی قیامه&zwnj;ت چاوه&zwnj;ڕوانی چی تر ده&zwnj;که&zwnj;ن، له کاتێکدا ئه&zwnj;وان هه&zwnj;ستی پێناکه&zwnj;ن.';
$t[67] = 'دۆستان و براده&zwnj;ران هه&zwnj;ر هه&zwnj;موو له&zwnj;و ڕۆژه&zwnj;دا دوژمنی یه&zwnj;کترن، هه&zwnj;موو ڕقیان له یه&zwnj;ك ده&zwnj;بێته&zwnj;وه&zwnj;، جگه له&zwnj;و که&zwnj;سانه&zwnj;ی که له&zwnj;سه&zwnj;ر بنچینه&zwnj;ی ته&zwnj;قواو خواناسین کۆ بوونه&zwnj;ته&zwnj;وه و برایه&zwnj;تییان به&zwnj;رپا کردووه&zwnj;.';
$t[68] = 'خوای گه&zwnj;وره پێیان ده&zwnj;فه&zwnj;رموێت: ئه&zwnj;ی به&zwnj;نده&zwnj;کانم ئه&zwnj;مڕۆ ئیتر ئێوه هیچ ترس و بیمێکتان نابێت وهیچ غه&zwnj;م و په&zwnj;ژاره&zwnj;یه&zwnj;ك ڕووتان تێ ناکات...';
$t[69] = 'ئه&zwnj;و به&zwnj;خته&zwnj;وه&zwnj;رانه ئه&zwnj;و که&zwnj;سانه&zwnj;ن که کاتی خۆی باوه&zwnj;ڕیان هێناوه به ئایه&zwnj;ته&zwnj;کانی ئێمه و نیشانه&zwnj;کانی ده&zwnj;سه&zwnj;ڵاتداریمان له بوونه&zwnj;وه&zwnj;ردا و ئیماندارو موسڵمانیش بوون.';
$t[70] = 'پێیان ده&zwnj;وترێت: فه&zwnj;رموون بچنه به&zwnj;هه&zwnj;شته&zwnj;وه خۆتان و هاوسه&zwnj;رانتان، خۆتان و ئه&zwnj;وانه&zwnj;ی وه&zwnj;ك ئێوه خواناس بوون و له&zwnj;وێ ژیانی کامه&zwnj;رانی و شادی و به&zwnj;خته&zwnj;وه&zwnj;ریتان پێ ده&zwnj;درێت.';
$t[71] = 'له&zwnj;سه&zwnj;ر سینی و کوپی ئاڵتون جۆره&zwnj;ها خواردن و خواردنه&zwnj;وه&zwnj;ی جوان و ڕازاوه و تام خۆش و بۆن خۆشیان به&zwnj;سه&zwnj;ردا ده&zwnj;گێڕن و له&zwnj;و به&zwnj;هه&zwnj;شته&zwnj;دا هه&zwnj;رچی نه&zwnj;فس حه&zwnj;زی لێ بکات و دڵ بیخوازێت و چاو حه&zwnj;ز به&zwnj;دیتنی بکات ئاماده&zwnj;یه و ئێوه&zwnj;، ئه&zwnj;ی خواناس و پاکان، ژیانی هه&zwnj;میشه&zwnj;یی و نه&zwnj;بڕاوه&zwnj;شی تیادا ده&zwnj;به&zwnj;نه سه&zwnj;ر.';
$t[72] = '(خوای میهره&zwnj;بان ئه&zwnj;وه&zwnj;نده&zwnj;ی تر دڵخۆشیان ده&zwnj;کات، کاتێك پێیان ده&zwnj;فه&zwnj;رموێت): ئا ئه&zwnj;و به&zwnj;هه&zwnj;شته&zwnj;ی که ئێستا ئێوه بوونه&zwnj;ته خاوه&zwnj;نی، وه&zwnj;ك میراتیی شایسته&zwnj;ی خۆتانه&zwnj;، به&zwnj;هۆی ئه&zwnj;و کار و کرده&zwnj;وانه&zwnj;ی که ده&zwnj;تانکرد.';
$t[73] = 'میوه&zwnj;هاتی زۆر و هه&zwnj;مه&zwnj;جۆر تیایدا بۆتان ئاماده&zwnj;یه و لێی ده&zwnj;خۆن (هه&zwnj;رگیز ته&zwnj;واو نابێت و که&zwnj;م ناکات).';
$t[74] = 'به&zwnj;ڕاستی تاوانبار و تاوانکاران له&zwnj;ناو ئازار و سزای دۆزه&zwnj;خدا به&zwnj;هه&zwnj;میشه&zwnj;یی ده&zwnj;مێننه&zwnj;وه&zwnj;.';
$t[75] = 'ئازارو سزا له سه&zwnj;ریان که&zwnj;م ناکرێته&zwnj;وه&zwnj;، له کاتێکدا هه&zwnj;مووشیان تیایدا نائومێد و بێ هیوان.';
$t[76] = 'جا وه&zwnj;نه&zwnj;بێت ئێمه سته&zwnj;ممان لێکردبن به&zwnj;ڵکو هه&zwnj;ر خۆیان سته&zwnj;مکار بوون.';
$t[77] = 'دۆزه&zwnj;خیه&zwnj;کان هاوار ده&zwnj;که&zwnj;ن، ئه&zwnj;ی مالك (که فریشته&zwnj;ی سه&zwnj;رپه&zwnj;رشتیاره&zwnj;): ده&zwnj;با په&zwnj;روه&zwnj;ردگارت بمانکوژێت، ده&zwnj;با بمرین و کۆتایی به&zwnj;م سه&zwnj;غڵه&zwnj;تیه&zwnj;مان بێت، له وه&zwnj;ڵامدا ده&zwnj;ڵێت: به&zwnj;ڕاستی ئه&zwnj;وه جێتانه و تیایدا بۆ هه&zwnj;میشه ده&zwnj;بێت بمێننه&zwnj;وه (ئا به&zwnj;و شێوه&zwnj;یه قورئان باسی به&zwnj;خته&zwnj;وه&zwnj;ران و باسی به&zwnj;دبه&zwnj;خت و ناپوخته&zwnj;کان ده&zwnj;خاته به&zwnj;رچاو، ئه&zwnj;وسا دێته&zwnj;وه بۆ دنیا و ده&zwnj;یه&zwnj;وێت پێش ئه&zwnj;وه&zwnj;ی ئه&zwnj;و ڕۆژه به&zwnj;رپا ببێت خه&zwnj;ڵکی فریای خۆیان بکه&zwnj;ون):';
$t[78] = 'سوێند به&zwnj;خوا به&zwnj;ڕاستی ئێمه په&zwnj;یامی حه&zwnj;ق و ڕاستیمان بۆ ئێوه ڕه&zwnj;وانه کردون که&zwnj;چی زۆربه&zwnj;تان حه&zwnj;زتان له حه&zwnj;ق و ڕاستی نیه ه لێی بێزارن.';
$t[79] = 'ئه&zwnj;وانه هه&zwnj;ر کاتێك بڕیارێك بده&zwnj;ن و پیلانێك بگێڕن دژی موسڵمانان، ئه&zwnj;وه بێگومان ئێمه پیلانه&zwnj;که به&zwnj;سه&zwnj;ریاندا ده&zwnj;شکێنینه&zwnj;وه و به زه&zwnj;ره&zwnj;ری خۆیان ته&zwnj;واوی ده&zwnj;که&zwnj;ین.';
$t[80] = 'ئایا ئه&zwnj;وانه واده&zwnj;زانن که ئێمه له نهێنی و چپه&zwnj;یان بێئاگاین و نابیستین؟! نه&zwnj;خێر وانی یه&zwnj;، ئێمه زۆر ئاگادارین و فریشته چاودێره&zwnj;کانیشمان که هه&zwnj;میشه له نزیکیانه&zwnj;وه&zwnj;ن، وردو درشتی گوفتارو کرداریان تۆمار ده&zwnj;که&zwnj;ین.';
$t[81] = 'ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر صلی الله علیه وسلم به&zwnj;و خوانه&zwnj;ناسانه بڵێ: ئه&zwnj;گه&zwnj;ر خوای میهره&zwnj;بان کوڕی ببوایه من یه&zwnj;که&zwnj;م که&zwnj;س ده&zwnj;بووم له&zwnj;و که&zwnj;سانه&zwnj;ی که ملکه&zwnj;چ ده&zwnj;بووم!!';
$t[82] = 'پاکی و بێگه&zwnj;ردی بۆ په&zwnj;روه&zwnj;ردگاری ئاسمانه&zwnj;کان و زه&zwnj;وی، بۆ په&zwnj;روه&zwnj;ردگاری عه&zwnj;رش و ته&zwnj;ختی فه&zwnj;رمانڕه&zwnj;وایه&zwnj;تی، ده&zwnj;رباره&zwnj;ی ئه&zwnj;و گوفتاره ناقوڵاو بێ سه&zwnj;رو بنانه&zwnj;ی که نه&zwnj;فامان بۆ په&zwnj;روه&zwnj;ردگار هه&zwnj;ڵی ده&zwnj;به&zwnj;ستن';
$t[83] = 'جارێ وازیان لێ بهێنه&zwnj;، با هه&zwnj;ر ڕۆبچن له قسه&zwnj;ی هه&zwnj;له&zwnj;ق و مه&zwnj;له&zwnj;قدا و با هه&zwnj;ر گه&zwnj;مه و یاری بکه&zwnj;ن، هه&zwnj;تا ده&zwnj;گه&zwnj;نه ئه&zwnj;و ڕۆژه&zwnj;ی هه&zwnj;ڕه&zwnj;شه&zwnj;یان لێکراوه ده&zwnj;رباره&zwnj;ی.';
$t[84] = 'خوا ئه&zwnj;و زاته&zwnj;یه که هه&zwnj;ر ئه&zwnj;و، خوای ئاسمانه و هه&zwnj;ر ئه&zwnj;و خوای زه&zwnj;ویشه و هه&zwnj;ر ئه&zwnj;وه که دانا و زانایه&zwnj;.';
$t[85] = 'ئه&zwnj;و زاته&zwnj;ی که خاوه&zwnj;نی ئاسمانه&zwnj;کان و زه&zwnj;وی و نێوانیانه زۆر موباره&zwnj;ك و خێربه&zwnj;خش و پاك و بێ گه&zwnj;رده و هه&zwnj;ر ئه&zwnj;و ده&zwnj;زانێت که&zwnj;ی قیامه&zwnj;ت به&zwnj;رپا ده&zwnj;بێت، بۆ لای ئه&zwnj;ویش ده&zwnj;برێنه&zwnj;وه&zwnj;.';
$t[86] = '(ئه&zwnj;وه&zwnj;ش چاك بزانن) ئه&zwnj;و ڕۆژه&zwnj;، ئه&zwnj;وانه&zwnj;ی که ئه&zwnj;وان ده&zwnj;یانپه&zwnj;رستن له جیاتی خوا تکاو شه&zwnj;فاعه&zwnj;تیان به&zwnj;ده&zwnj;ست نیه&zwnj;، مه&zwnj;گه&zwnj;ر که&zwnj;سێك که شایه&zwnj;تی ڕاستی دابێت و بزانێت کێ شایسته&zwnj;ی تکا بۆکردنه&zwnj;.';
$t[87] = 'سوێند به&zwnj;خوا ئه&zwnj;گه&zwnj;ر له&zwnj;و خوانه&zwnj;ناسانه بپرسیت: کێ دروستی کردوون، ئه&zwnj;وه به&zwnj;دڵنیاییه&zwnj;وه ده&zwnj;ڵێن (الله&zwnj;)، که&zwnj;وابوو به&zwnj;ره&zwnj;و چی و به&zwnj;ره&zwnj;و کوێ له ڕێبازی یه&zwnj;کخواناسی لاده&zwnj;درێن و شوێنی ناحه&zwnj;قی ده&zwnj;که&zwnj;ون؟!';
$t[88] = 'سوێند به گوفتاری پێغه&zwnj;مبه&zwnj;ر که وتی: په&zwnj;روه&zwnj;ردگارا، به&zwnj;ڕاستی ئه&zwnj;مانه قه&zwnj;وم و هۆزێکن، باوه&zwnj;ڕ ناهێنن بیرناکه&zwnj;نه&zwnj;وه&zwnj;.';
$t[89] = 'ده تۆش ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر صلی الله علیه وسلم له&zwnj;به&zwnj;ر ئه&zwnj;وه چاوپۆشیان لێ بکه و لێیان ببووره&zwnj;، سه&zwnj;لامیان لێ بکه و نه&zwnj;رم به له&zwnj;گه&zwnj;ڵیان، له ئاینده&zwnj;دا ده&zwnj;زانن چییان به&zwnj;سه&zwnj;ر دێت و خۆیان ده&zwnj;بیننه&zwnj;وه&zwnj;، یاخود تێده&zwnj;گه&zwnj;ن و باوه&zwnj;ڕ ده&zwnj;هێنن.';
?>