$t = array();
$t[1] = 'سه&zwnj;رنجی ئایه&zwnj;تی1 (الشوری) لاپه&zwnj;ڕه&zwnj;483بده&zwnj;.';
$t[2] = 'ئه&zwnj;م قورئانه له&zwnj;لایه&zwnj;ن خوای باڵاده&zwnj;ست و داناوه&zwnj;، هاتووه&zwnj;ته خواره&zwnj;وه&zwnj;.';
$t[3] = 'به&zwnj;ڕاستی له ئاسمانه&zwnj;کان و زه&zwnj;ویدا به&zwnj;ڵگه&zwnj;و نیشانه&zwnj;ی زۆر هه&zwnj;ن ده&zwnj;رباره&zwnj;ی گه&zwnj;وره&zwnj;یی و ده&zwnj;سه&zwnj;ڵاتی په&zwnj;روه&zwnj;ردگار بۆ که&zwnj;سانێك که بڕوادارن.';
$t[4] = 'هه&zwnj;روه&zwnj;ها له دروستکردنی ئێوه&zwnj;دا (له ئه&zwnj;ندامه&zwnj;کانی له&zwnj;شتاندا، له قوناغه&zwnj;کانی گه&zwnj;وره بوونتاندا، له جیاوازی نێرو مێتاندا... هتد)، هه&zwnj;روه&zwnj;ها له&zwnj;و هه&zwnj;موو زینده&zwnj;وه&zwnj;ره جۆراو جۆرانه&zwnj;ی که له&zwnj;سه&zwnj;ر زه&zwnj;وی و ژێر زه&zwnj;وی و ناو ئاودا هه&zwnj;ن و بڵاویان ده&zwnj;کاته&zwnj;وه&zwnj;، (له چوار پێ و باڵنده&zwnj;کان، له خشۆك و شه&zwnj;ش پێکان، له گیاندارانی هه&zwnj;مه&zwnj;جۆر له قه&zwnj;باره&zwnj;و ڕه&zwnj;نگ و شێوازدا، له ده&zwnj;وریان له خزمه&zwnj;تکردنی ئاده&zwnj;میزادا، هه&zwnj;موو ئه&zwnj;وانه&zwnj;) به&zwnj;ڵگه و نیشانه&zwnj;ن له&zwnj;سه&zwnj;ر ده&zwnj;سه&zwnj;ڵاتی خوا بۆ که&zwnj;سانێك که دڵنیان و باوه&zwnj;ڕی دامه&zwnj;زراویان هه&zwnj;یه&zwnj;.';
$t[5] = 'هه&zwnj;روه&zwnj;ها له جیاوازی شه&zwnj;وو ڕۆژدا، له&zwnj;و ڕزق و ڕۆزیه&zwnj;ی که&zwnj;له ئاسمانه&zwnj;وه خوا باراندوویه&zwnj;تی (وه&zwnj;ك باران و تیشکی خۆرو گازه&zwnj;کان و... هتد، که سه&zwnj;رچاوه&zwnj;ی گه&zwnj;شه&zwnj;کردن و پێگه&zwnj;شتنی ڕوه&zwnj;ك و هه&zwnj;موو زینده&zwnj;وه&zwnj;رانه&zwnj;)، به&zwnj;و هۆیه&zwnj;وه زه&zwnj;وی زیندوو کرده&zwnj;وه دوای وشكبوون و مردنی، هه&zwnj;روه&zwnj;ها له هاتوچۆو گۆڕان و جوڵه&zwnj;ی هه&zwnj;وادا، له هه&zwnj;ر هه&zwnj;موو ئه&zwnj;وانه&zwnj;دا به&zwnj;ڵگه ونیشانه&zwnj;ی زۆر هه&zwnj;ن بۆ که&zwnj;سانێك عه&zwnj;قڵ و بیرو هۆشیان ده&zwnj;خه&zwnj;نه کار.';
$t[6] = 'ئه&zwnj;ی محمد صلی الله علیه وسلم ئا ئه&zwnj;وانه به&zwnj;ڵگه و نیشانه&zwnj;ی ده&zwnj;سه&zwnj;ڵاته&zwnj;کانی خوان، ده&zwnj;یخوێنینه&zwnj;وه به&zwnj;سه&zwnj;ر تۆدا سه&zwnj;رتاپای حه&zwnj;ق و ڕاسته&zwnj;قینه&zwnj;یه&zwnj;، جا دوای خواو فه&zwnj;رمانه&zwnj;کانی به چ گوتار و باسێکی تر باوه&zwnj;ڕ ده&zwnj;که&zwnj;ن و ئیمان ده&zwnj;هێنن.';
$t[7] = 'هاوار و قوربه&zwnj;سه&zwnj;ری و ڕه&zwnj;نجه&zwnj;ڕۆیی ته&zwnj;نها بۆ هه&zwnj;موو ئه&zwnj;و که&zwnj;سانه&zwnj;ی که زۆر درۆزن و بوختانچی و تاوانبارن.';
$t[8] = 'هه&zwnj;یانه گوێ ده&zwnj;گرێت بۆ ئایه&zwnj;ته&zwnj;کانی خوا کاتێك به&zwnj;سه&zwnj;ریدا ده&zwnj;خوێنرێته&zwnj;وه&zwnj;، له&zwnj;وه&zwnj;ودوا سوور ده&zwnj;بێت له&zwnj;سه&zwnj;ر بێ باوه&zwnj;ڕی و خۆی به&zwnj;زل ده&zwnj;زانێت، هه&zwnj;روه&zwnj;ك نه&zwnj;یبیستبێت، جا مژده&zwnj;ی سزاو ئازارێکی به&zwnj;ئێش بده به&zwnj;و جۆره که&zwnj;سانه&zwnj;!!';
$t[9] = 'کاتێك شتێك له ئایه&zwnj;ت و فه&zwnj;رمانه&zwnj;کانی ئێمه&zwnj;ی پێ بگات و بیزانێت، ئه&zwnj;وه ده&zwnj;یکاته هۆی گاڵته&zwnj;و گه&zwnj;مه&zwnj;جاڕ، ئا ئه&zwnj;وانه سزای خه&zwnj;جاڵه&zwnj;ت ئاوه&zwnj;رو شه&zwnj;رمه&zwnj;زاری چاوه&zwnj;ڕێیانه هه&zwnj;ر له&zwnj;م دنیادا....';
$t[10] = 'له قیامه&zwnj;تیشدا، دۆزه&zwnj;خ به&zwnj;دوایانه&zwnj;وه&zwnj;یه و لێی قوتارنابن، هه&zwnj;رچیه&zwnj;کیان کردبوو فریایان ناکه&zwnj;وێت (له ماڵ و هێزو ده&zwnj;سه&zwnj;ڵات)، هه&zwnj;موو ئه&zwnj;وانه&zwnj;ش که&zwnj;له جیاتی خوا کردبوویان به&zwnj;پاڵپشتی خۆیان، جا سزایه&zwnj;کی زۆر گه&zwnj;وره و سامناك بۆیان ئاماده&zwnj;یه&zwnj;.';
$t[11] = 'ئه&zwnj;م قورئانه ڕێنموویی به&zwnj;خشه&zwnj;، به&zwnj;رچاو ڕوونکه&zwnj;ره&zwnj;وه&zwnj;یه&zwnj;، ڕێ نیشانده&zwnj;ره، ئه&zwnj;وانه&zwnj;ش که باوه&zwnj;ڕیان نه&zwnj;بوو به ئایه&zwnj;ت و فه&zwnj;رمووده&zwnj;کانی په&zwnj;روه&zwnj;ردگاریان سزایه&zwnj;کی زۆر سه&zwnj;خت و به ئێش چاوه&zwnj;ڕێیانه&zwnj;.';
$t[12] = 'خوا ئه&zwnj;و زاته&zwnj;یه که ده&zwnj;ریای بۆ ڕام کردوون تا که&zwnj;شتی به فه&zwnj;رمانی ئه&zwnj;و هاتووچۆی پیادا بکات، تا بچن به&zwnj;ده&zwnj;م به&zwnj;خشش و به&zwnj;هره&zwnj;کانیه&zwnj;وه و ده&zwnj;ستان که&zwnj;وێت (له بازرگانی و ڕاوه ماسی و ده&zwnj;رهێنانی گه&zwnj;وهه&zwnj;ر و مرواری و... هتد) بۆ ئه&zwnj;وه&zwnj;ی سوپاسگوزاری خوا بکه&zwnj;ن.';
$t[13] = 'هه&zwnj;رچی له ئاسمانه&zwnj;کان و زه&zwnj;ویدا هه&zwnj;یه بۆ ئێوه&zwnj;ی ڕام هێناوه&zwnj;، بۆ خزمه&zwnj;تگوزاری ئێوه فه&zwnj;راهه&zwnj;می هێناوه&zwnj;، هه&zwnj;موویشی تێکڕا به&zwnj;خششن له&zwnj;لایه&zwnj;ن ئه&zwnj;و زاته&zwnj;وه&zwnj;، به&zwnj;ڕاستی ئا له&zwnj;و به&zwnj;خشش و دیاردانه&zwnj;دا به&zwnj;ڵگه و نیشانه&zwnj;ی زۆرهه&zwnj;ن بۆ که&zwnj;سانێك که بیربکه&zwnj;نه&zwnj;وه و تێفکرن.';
$t[14] = 'ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر صلی الله علیه وسلم بڵێ به&zwnj;وانه&zwnj;ی که ئیمانیان هێناوه&zwnj;: چاوپۆشی بکه&zwnj;ن له&zwnj;وانه&zwnj;ی به&zwnj;ته&zwnj;مای ڕۆژه سه&zwnj;خته&zwnj;کان نین که خوا پێشی ده&zwnj;هێنێت بۆ لێپرسینه&zwnj;وه&zwnj;یان، تا پاداشتی قه&zwnj;ومی نا&zwnj;له&zwnj;بار بداته&zwnj;وه به&zwnj;گوێره&zwnj;ی ئه&zwnj;و کاروکرده&zwnj;وه&zwnj;یه&zwnj;ی که ئه&zwnj;نجامی ده&zwnj;ده&zwnj;ن...';
$t[15] = 'هه&zwnj;ر که&zwnj;سێك کاروکرده&zwnj;وه&zwnj;ی چاکی ئه&zwnj;نجام دابێت ئه&zwnj;وه سوود و قازانجه&zwnj;که&zwnj;ی بۆ خۆیه&zwnj;تی، هه&zwnj;روه&zwnj;ها ئه&zwnj;وه&zwnj;ش کاروکرده&zwnj;وه&zwnj;ی خراپی کردبێت ئه&zwnj;ویش له&zwnj;سه&zwnj;ر خۆی ده&zwnj;که&zwnj;وێت، له&zwnj;وه&zwnj;ودواش بۆ لای په&zwnj;روه&zwnj;ردگارتان ده&zwnj;گه&zwnj;ڕێنه&zwnj;وه&zwnj;.';
$t[16] = 'سوێند به خوا بێگومان ئێمه ته&zwnj;وراتمان به&zwnj;خشی به نه&zwnj;وه&zwnj;ی ئیسرائیل هاوڕێ له&zwnj;گه&zwnj;ڵ ده&zwnj;سه&zwnj;ڵاتداری بۆ چاره&zwnj;سه&zwnj;رکردنی کێشه&zwnj;ی خه&zwnj;ڵکی، له&zwnj;گه&zwnj;ڵ پێغه&zwnj;مبه&zwnj;رایه&zwnj;تیشدا له چه&zwnj;نده&zwnj;ها ڕزق و ڕۆزی چاك و پاکیش به&zwnj;هه&zwnj;روه&zwnj;رمان کردن، زیاده ڕێزیشمان دان به&zwnj;سه&zwnj;ر هه&zwnj;موو خه&zwnj;ڵکی سه&zwnj;رده&zwnj;می خۆیاندا.';
$t[17] = 'هه&zwnj;روه&zwnj;ها ئێمه چاره&zwnj;سه&zwnj;ری هه&zwnj;موو کارێکی گرنگمان بۆ ڕوونکردنه&zwnj;وه و پێمان به&zwnj;خشین (ده&zwnj;رباره&zwnj;ی عه&zwnj;قیده&zwnj;و شه&zwnj;ریعه&zwnj;ت و حه&zwnj;ڵاڵ و حه&zwnj;رام)، جا ناکۆکی و دووبه&zwnj;ره&zwnj;کیان له نێواندا به&zwnj;رپا نه&zwnj;بوو مه&zwnj;گه&zwnj;ر دوای ئه&zwnj;وه&zwnj;ی که هه&zwnj;موو شت ڕوون بۆوه و زانستیان بۆ هات، ئه&zwnj;وه&zwnj;ش به&zwnj;هۆی سته&zwnj;م و ناکۆکی و حه&zwnj;سوودی نێوان خۆیانه&zwnj;وه&zwnj;، به&zwnj;ڕاستی په&zwnj;روه&zwnj;ردگاری تۆ ئه&zwnj;ی محمد صلی الله علیه وسلم له ڕۆژی قیامه&zwnj;تدا داوه&zwnj;ری ده&zwnj;کات له نێوانیاندا ده&zwnj;رباره&zwnj;ی ئه&zwnj;و شتانه&zwnj;ی که ناکۆکیان له&zwnj;سه&zwnj;ری هه&zwnj;یه و ڕای جیاوازیان هه&zwnj;یه ده&zwnj;رباره&zwnj;ی.';
$t[18] = 'له&zwnj;وه&zwnj;ودوا ئێمه تۆمان ئه&zwnj;ی محمد صلی الله علیه وسلم خستۆته سه&zwnj;ر به&zwnj;رنامه&zwnj;یه&zwnj;کی ڕاست و ڕه&zwnj;وان له هه&zwnj;موو کارو بارێکی ئه&zwnj;م ئاینه&zwnj;دا، که&zwnj;واته شوێنی بکه&zwnj;وه و لێی لامه&zwnj;ده&zwnj;، نه&zwnj;که&zwnj;ی شوێنی هه&zwnj;واو ئاره&zwnj;زووی ئه&zwnj;وان بکه&zwnj;ویت که نازانن و نافامن.';
$t[19] = 'بێگومان ئه&zwnj;وانه هیچ کات فریات ناکه&zwnj;ون و له&zwnj;ده&zwnj;ستی خوا ڕزگارت ناکه&zwnj;ن (ئه&zwnj;گه&zwnj;ر خوا له&zwnj;خۆت بڕه&zwnj;نجێنیت و نافه&zwnj;رمانی بکه&zwnj;یت)، به&zwnj;ڕاستی سته&zwnj;م کاران یار و یاوه&zwnj;ری هه&zwnj;ندێکیانن، خوایش پشت و په&zwnj;نای خۆشه&zwnj;ویستی دیندارو چاکانه&zwnj;.';
$t[20] = 'ئه&zwnj;م قورئان و به&zwnj;رنامه&zwnj;یه چه&zwnj;نده&zwnj;ها چاو ڕۆشنیه بۆ خه&zwnj;ڵکی و هیدایه&zwnj;ت و ڕێنمونی و ڕه&zwnj;حمه&zwnj;ته بۆ قه&zwnj;وم و که&zwnj;سانێك که باوه&zwnj;ڕ بهێنن و پێی دڵنیاو دڵئارام ببن.';
$t[21] = 'ئایا ئه&zwnj;وانه&zwnj;ی که گوناهو تاوانه&zwnj;کانیان ئه&zwnj;نجام داوه&zwnj;، وایان زانیووه هه&zwnj;ر وه&zwnj;ك ئه&zwnj;وانه حسابیان بۆ ده&zwnj;که&zwnj;ین که ئیمانیان هێناوه و کاروکرده&zwnj;وه&zwnj;و چاکه&zwnj;کانیان ئه&zwnj;نجام داوه&zwnj;؟! ژیانی دنیاو مردن و دوای مردنیان وه&zwnj;ك یه&zwnj;ك ده&zwnj;بێت؟! ئای که بڕیاری خراپ بڕیار ده&zwnj;ده&zwnj;ن.';
$t[22] = 'خوا ئاسمانه&zwnj;کان و زه&zwnj;وی له&zwnj;سه&zwnj;ر بنه&zwnj;مای حه&zwnj;ق و ڕاستی داناوه&zwnj;، بۆ ئه&zwnj;وه&zwnj;ی سه&zwnj;رئه&zwnj;نجام پاداشتی هه&zwnj;موو که&zwnj;سێك به&zwnj;گوێره&zwnj;ی کاروکرده&zwnj;وه&zwnj;کانی بداته&zwnj;وه و هیچ جۆره سته&zwnj;مێکیشیان لێ ناکرێت.';
$t[23] = 'هه&zwnj;واڵم بده&zwnj;رێ ئایا ئه&zwnj;وکه&zwnj;سه&zwnj;ی که ویست و ئاره&zwnj;زووی خۆی کردۆته خوای خۆی؟! به&zwnj;هۆی ئه&zwnj;وه&zwnj;وه خوایش گومڕای کردووه که زانیوێتی شایه&zwnj;نی ئه&zwnj;وه&zwnj;یه&zwnj;، له ئه&zwnj;نجامی به&zwnj;د هه&zwnj;ڵوێستی خۆیدا و مۆری ناوه به ده&zwnj;زگای بیستن و دڵ و ده&zwnj;روونیدا و په&zwnj;رده&zwnj;شی هێناوه به&zwnj;سه&zwnj;ر چاوو ده&zwnj;زگای بینینیدا، جا ئیتر کێ هه&zwnj;یه جگه له خوا هیدایه&zwnj;ت و ڕێنمووی بکات، ئایا بیر ناکانه&zwnj;وه و ئامۆژگاری وه&zwnj;رناگرن.';
$t[24] = 'خوانه&zwnj;ناسان ده&zwnj;یانوت: ژیان ته&zwnj;نها ئه&zwnj;م ژیانی دنیایه&zwnj;یه&zwnj;، ده&zwnj;مرین و ده&zwnj;ژین به نۆره&zwnj;، هه&zwnj;ر ڕۆژگارو زه&zwnj;مانه کایگه&zwnj;رن تیاماندا و ده&zwnj;مان مرێنن و هیچی تر! جا ئه&zwnj;وانه وه&zwnj;نه&zwnj;بێت له&zwnj;سه&zwnj;ر بنچینه&zwnj;ی هیچ جۆره زانستی و زانیاریه&zwnj;ك ئه&zwnj;وه بڵێن به&zwnj;ڵکو ته&zwnj;نها پشت به&zwnj;گومان و دوودڵی ده&zwnj;به&zwnj;ستن.';
$t[25] = 'کاتێکیش ئایه&zwnj;ته&zwnj;کانی قورئانی ئێمه&zwnj;یان به&zwnj;سه&zwnj;ردا ده&zwnj;خوێنرێته&zwnj;وه (که به&zwnj;ڵگه&zwnj;ی زۆر به&zwnj;هێز و ڕوونی تێدایه له&zwnj;سه&zwnj;ر زیندووبوونه&zwnj;وه&zwnj;، هه&zwnj;موو به&zwnj;ڵگه&zwnj;یه&zwnj;کیان هاتۆته سه&zwnj;ر ئه&zwnj;وه&zwnj;ی) که ده&zwnj;ڵێن: ده&zwnj;باشه باوو باپیرانی مردوومان بۆ زیندوو بکه&zwnj;نه&zwnj;وه ئه&zwnj;گه&zwnj;ر ڕاست ده&zwnj;که&zwnj;ن؟! (شایه&zwnj;نی باسه که حه&zwnj;زره&zwnj;تی عیسا ئه&zwnj;و کاره&zwnj;ی بۆ خه&zwnj;ڵکی ئه&zwnj;نجام دا به ویستی خوا، که&zwnj;چی وتراوه&zwnj;: ئه&zwnj;مه سحروو جادووه&zwnj;)؟!';
$t[26] = 'ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر صلی الله علیه وسلم پێیان بڵێ: (ئه&zwnj;و شتانه به&zwnj;ده&zwnj;ست ئێمه نیه&zwnj;) ئه&zwnj;وه هه&zwnj;ر خوایه ژیانتان پێ ده&zwnj;به&zwnj;خشێت و پاشان ده&zwnj;تان مرێنێت و دوای ئه&zwnj;وه کۆتان ده&zwnj;کاته&zwnj;وه له ڕۆژی قیامه&zwnj;تدا که هیچ گومانی تیادانیه&zwnj;، به&zwnj;ڵام زۆربه&zwnj;ی خه&zwnj;ڵکی ئه&zwnj;مه نازانن.';
$t[27] = 'هه&zwnj;ر خوا خاوه&zwnj;نی ئاسمانه&zwnj;کان و زه&zwnj;ویه&zwnj;، ئه&zwnj;و ڕۆژه&zwnj;ی که قیامه&zwnj;ت پێش دێت، هه&zwnj;ر ئه&zwnj;و ڕۆژه هه&zwnj;رچی به&zwnj;تاڵپه&zwnj;رست و ناحه&zwnj;ق په&zwnj;رستانه له زه&zwnj;ره&zwnj;رمه&zwnj;ندیه&zwnj;کی زۆر گه&zwnj;وره&zwnj;دا ده&zwnj;بن.';
$t[28] = 'ئه&zwnj;و ڕۆژه ده&zwnj;بینیت هه&zwnj;موو خه&zwnj;ڵکی به چۆكدا هاتوون و له&zwnj;سه&zwnj;ر ئه&zwnj;ژنۆو په&zwnj;نجه&zwnj;کانیان ئاماده&zwnj;ن (هه&zwnj;ر که&zwnj;س چاوه&zwnj;ڕێ بانگ کردنه بۆ لێپرسینه&zwnj;وه&zwnj;، ئاماده&zwnj;یه ده&zwnj;ست به&zwnj;جێ ڕاپه&zwnj;ڕێت و ڕا بکات بۆ به&zwnj;رده&zwnj;م دادگای پڕ له دادی په&zwnj;روه&zwnj;ردگار) هه&zwnj;ر قه&zwnj;وم و هۆزێك بانگ ده&zwnj;کرێت بۆ لای ئه&zwnj;و کتێبه&zwnj;ی که بۆی ڕه&zwnj;وانه کراوه&zwnj;، یاخود بانگ ده&zwnj;کرێت بۆ لای نامه&zwnj;ی کرده&zwnj;وه&zwnj;کانی، ئه&zwnj;وسا پێیان ده&zwnj;وترێت: ئه&zwnj;مڕۆ ئیتر پاداشتی ئه&zwnj;و کاروکرده&zwnj;وانه&zwnj;ی که ده&zwnj;تان کرد ده&zwnj;درێته&zwnj;وه&zwnj;.';
$t[29] = 'ئه&zwnj;مه ئێتر نامه&zwnj;ی کرده&zwnj;وه&zwnj;کانتانه که تۆمارمان کردووه له&zwnj;سه&zwnj;رتان، خۆی گوایه قسه ده&zwnj;کات و حه&zwnj;ق وێژه (دیاره کرده&zwnj;وه&zwnj;کانمان تۆمار کراوه له&zwnj;سه&zwnj;ر ده&zwnj;زگای زۆر پێشکه&zwnj;وتوو) چونکه به&zwnj;ڕاستی ئێمه کاتی خۆی ئه&zwnj;وه&zwnj;ی ئێوه ده&zwnj;تان کرد له&zwnj;به&zwnj;رمان ده&zwnj;گرته&zwnj;وه&zwnj;.';
$t[30] = 'جا ئه&zwnj;و کاته ئه&zwnj;وانه&zwnj;ی باوه&zwnj;ڕیان هێناوه وه کاروکرده&zwnj;وه چاکه&zwnj;کانیان ئه&zwnj;نجام داوه&zwnj;، ئه&zwnj;وه په&zwnj;روه&zwnj;ردگاریان ده&zwnj;یانخاته ژێر سایه&zwnj;ی ڕه&zwnj;حمه&zwnj;ت و میهره&zwnj;بانی خۆیه&zwnj;وه&zwnj;، ته&zwnj;نها ئه&zwnj;وه سه&zwnj;رکه&zwnj;وتن و سه&zwnj;رفرازیه&zwnj;کی ئاشکرایه&zwnj;.';
$t[31] = 'به&zwnj;ڵام ئه&zwnj;وانه&zwnj;ی که بێ بڕواو کافر بوون پێیان ده&zwnj;وترێت: باشه&zwnj;، ئێوه ئایه&zwnj;ته&zwnj;کانی منتان به&zwnj;سه&zwnj;ردا ده&zwnj;خوێنرایه&zwnj;وه&zwnj;؟! که&zwnj;چی خۆتان به&zwnj;گه&zwnj;وره&zwnj;و زل ده&zwnj;زانی، به&zwnj;ڕاستی ئێوه هۆزێکی تاوانبارو تاوانکار بوون.';
$t[32] = 'کاتیکیش بوترایه&zwnj;: بێگومان به&zwnj;ڵێنی خوا ڕاسته و ڕۆژی قیامه&zwnj;ت هه&zwnj;ر دێت و گومانی تیادا نیه&zwnj;، ئێوه له وه&zwnj;ڵامدا ده&zwnj;تانوت: ئێمه هه&zwnj;ر نازانین ڕۆژی قیامه&zwnj;ت چیه&zwnj;؟! ئێمه هه&zwnj;ر گومان ده&zwnj;به&zwnj;ین و دڵنیا نین!!';
$t[33] = 'ئه&zwnj;و کاته له قیامه&zwnj;تدا ئه&zwnj;و کاروکرده&zwnj;وه ناشرینیانه&zwnj;ی که کردوویانه بۆیان ده&zwnj;رده&zwnj;که&zwnj;وێت و ئاشکرا ده&zwnj;بێت، ئه&zwnj;وه&zwnj;ش که گاڵته&zwnj;یان پێده&zwnj;کرد و بڕوایان پێی نه&zwnj;بوو (که سزاو ئازاری دۆزه&zwnj;خه&zwnj;) له هه&zwnj;موو لایه&zwnj;که&zwnj;وه ده&zwnj;وریان ده&zwnj;دات و ڕێگه&zwnj;ی ڕزگارییان لێ ده&zwnj;بڕێت.';
$t[34] = 'پێیان ده&zwnj;وترێت: له&zwnj;مڕۆ به&zwnj;دواوه ئیتر ئێوه فه&zwnj;رامۆش ده&zwnj;که&zwnj;ین و پشت گوێتان ده&zwnj;خه&zwnj;ین، هه&zwnj;روه&zwnj;ك چۆن ئێوه بڕوا بوونتان به&zwnj;م ڕۆژه فه&zwnj;رامۆش کرد، شوێنی نیشته&zwnj;جێ و حه&zwnj;وانه&zwnj;وه&zwnj;شتان ناو ئاگری دۆزه&zwnj;خه&zwnj;، که&zwnj;سیشتان ده&zwnj;ست ناکه&zwnj;وێت پشتیوانی و پشتگیریتان لێ بکات.';
$t[35] = 'ئه&zwnj;م سه&zwnj;رئه&zwnj;نجامه&zwnj;ش بۆیه یه&zwnj;خه&zwnj;ی پێگرتن چونکه به&zwnj;ڕاستی کاتی خۆی گاڵته&zwnj;تان به ئایه&zwnj;ت و فه&zwnj;رمانه&zwnj;کانی خوا ده&zwnj;هات، ژیانی دنیاش غه&zwnj;ڕاو گومڕای کردبوون، ئه&zwnj;مڕۆ ئیتر ڕزگاربوونیان ئه&zwnj;سته&zwnj;مه له&zwnj;و ئاگری دۆزه&zwnj;خه لێی ده&zwnj;رناهێنرێن، داوای ئه&zwnj;وه&zwnj;شیان لێناکرێت خوا له&zwnj;خۆیان ڕازی بکه&zwnj;ن.';
$t[36] = 'جا هه&zwnj;ر له بۆ خوایه سوپاس و ستایش، که په&zwnj;روه&zwnj;ردگاری ئاسمانه&zwnj;کانه&zwnj;، په&zwnj;روه&zwnj;ردگاری زه&zwnj;ویشه&zwnj;، په&zwnj;روه&zwnj;ردگاری هه&zwnj;موو جیهانیان.';
$t[37] = 'هه&zwnj;رچی گه&zwnj;وره&zwnj;یی و شکۆمه&zwnj;ندی هه&zwnj;یه له ئاسمانه&zwnj;کان و زه&zwnj;ویدا هه&zwnj;ر شایسته&zwnj;ی ئه&zwnj;وه&zwnj;، به&zwnj;ڕاستی ئه&zwnj;و زاته باڵا ده&zwnj;ست و دانایه&zwnj;.';
?>