<?php
$t = array();
$t[1] = 'ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر (صلى الله عليه وسلم) به&zwnj;ڕاستی ئێمه سه&zwnj;رکه&zwnj;وتنێکی ئاشکرامان پێبه&zwnj;خشیت (مه&zwnj;به&zwnj;ست ڕێکه&zwnj;وتنه&zwnj;که&zwnj;ی حوده&zwnj;یبیه یه که&zwnj;له&zwnj;نێوان پێغه&zwnj;مبه&zwnj;رو (صلى الله عليه وسلم) قوریشدا مۆرکرا، به&zwnj;نده&zwnj;کانی ئه&zwnj;و په&zwnj;یمانه له&zwnj;رواڵه&zwnj;تدا زه&zwnj;ره&zwnj;ری ئیمانداران بوو، به&zwnj;ڵام هه&zwnj;ر هه&zwnj;مووی گه&zwnj;ڕا به&zwnj;خێرو سه&zwnj;ربه&zwnj;رزی بۆیان.';
$t[2] = 'تا خوا له&zwnj;هه&zwnj;موو گوناهانی ڕابوردوو ئاینده&zwnj;شت خۆش ببێت و به&zwnj;خشینه&zwnj;کانی له&zwnj;سه&zwnj;رت ته&zwnj;واو بکات و بتخاته&zwnj;سه&zwnj;ر ڕێگه&zwnj;ی ڕاست و دروست.';
$t[3] = 'تا به&zwnj;و بۆنه&zwnj;یه&zwnj;وه خوا سه&zwnj;رکه&zwnj;وتنێکی به&zwnj;هێزو باڵات پێ ببه&zwnj;خشێت.';
$t[4] = 'هه&zwnj;ر ئه&zwnj;و خوایه خۆی ئارامی و هێمنی دابه&zwnj;زاندۆته سه&zwnj;ر دڵی ئیمانداران، بۆ ئه&zwnj;وه&zwnj;ی که&zwnj;ئیمانیان دامه&zwnj;زراوترو پته&zwnj;وتر بکات، سه&zwnj;ره&zwnj;ڕای ئیمانداری و خواناسیان ئیمانیان زیاد ببێت، بێگومان هه&zwnj;رچی سه&zwnj;ربازانی ئاسمانه&zwnj;کان و زه&zwnj;وی هه&zwnj;یه هه&zwnj;ر خوا خۆی خاوه&zwnj;نیانه&zwnj;، هه&zwnj;میشه و به&zwnj;رده&zwnj;وام خوای گه&zwnj;وره&zwnj;زانایه به&zwnj;هه&zwnj;موو شتێک و دانایه له&zwnj;هه&zwnj;موو کارێکدا.';
$t[5] = 'ئه&zwnj;مه&zwnj;ش هه&zwnj;موو بۆ ئه&zwnj;وه&zwnj;ی، تا ئیمانداران له&zwnj;پیاوان و له&zwnj;ئافره&zwnj;تان بخاته&zwnj;ناو به&zwnj;هه&zwnj;شتێکه&zwnj;وه که&zwnj;چه&zwnj;نده&zwnj;ها ڕووبار به&zwnj;ژێر دره&zwnj;خت و به&zwnj;به&zwnj;رده&zwnj;م کۆشک و ته&zwnj;لاره&zwnj;کانیدا ده&zwnj;ڕوات، هاوڕێ له&zwnj;گه&zwnj;ڵ ژیانی نه&zwnj;مریدا، هه&zwnj;روه&zwnj;ها بۆ ئه&zwnj;وه&zwnj;ی که&zwnj;چاوپۆشی له&zwnj;هه&zwnj;ڵه و گوناهه&zwnj;کانیان بکات و ئه&zwnj;م به&zwnj;ڵێنه له&zwnj;لایه&zwnj;ن خواوه&zwnj;ده&zwnj;ستکه&zwnj;وت و سه&zwnj;رفرازیه&zwnj;کی زۆر گه&zwnj;وره&zwnj;و بێ سنووره&zwnj;بۆ ئیمانداران....';
$t[6] = 'له&zwnj;و لاشه&zwnj;وه&zwnj;ئازار و ئه&zwnj;شکه&zwnj;نجه&zwnj;ی دووڕو و موشریکه&zwnj;کان، چ پیاوانیان، چ ئافره&zwnj;تانیان بدات، ئه&zwnj;وانه&zwnj;ی که&zwnj;تێکڕا، گومانی خراپیان ده&zwnj;رباره&zwnj;ی خوا هه&zwnj;یه&zwnj;، ئه&zwnj;و گومانه&zwnj;خراپه&zwnj;یان هه&zwnj;ر بۆ خۆیان ده&zwnj;گه&zwnj;رێته&zwnj;وه&zwnj;، ئه&zwnj;وانه&zwnj;خوا نه&zwnj;فره&zwnj;تی لێکردوون و دۆزه&zwnj;خی بۆ ئاماده&zwnj;کردوون، ئای که سه&zwnj;ره&zwnj;نجامێکی یه&zwnj;کجار سه&zwnj;خت و سامناکه&zwnj;.';
$t[7] = 'هه&zwnj;رچی سه&zwnj;ربازان هه&zwnj;یه له&zwnj;ئاسمانه&zwnj;کان و زه&zwnj;ویدا، هه&zwnj;ر خوا خۆی خاوه&zwnj;نیانه و له&zwnj;ژێر فه&zwnj;رمانی ئه&zwnj;ودان، هه&zwnj;میشه و به&zwnj;رده&zwnj;وام ئه&zwnj;و خوایه باڵاده&zwnj;ست و دانایه.';
$t[8] = 'ئێمه تۆمان ڕه&zwnj;وانه&zwnj;کردووه&zwnj;ئه&zwnj;ی محمد (صلى الله عليه وسلم) که&zwnj;شایه&zwnj;ت و مژده&zwnj;ده&zwnj;ر و به&zwnj;ئاگاهێنه&zwnj;ر بیت بۆ گرۆی ئاده&zwnj;میزاد.';
$t[9] = 'بۆ ئه&zwnj;وه&zwnj;ی ئێوه باوه&zwnj;ڕ بهێنن به&zwnj;خواو پێغه&zwnj;مبه&zwnj;ره&zwnj;که&zwnj;ی و پشتیوانی له&zwnj;پێغه&zwnj;مبه&zwnj;ر بکه&zwnj;ن و ڕێزی شایسته&zwnj;ی خۆی لێ بگرن و ته&zwnj;سبیحات و ستایشی خوا بکه&zwnj;ن له&zwnj;به&zwnj;ره&zwnj;به&zwnj;یان و عه&zwnj;سراندا.';
$t[10] = 'بێگومان ئه&zwnj;وانه&zwnj;ی په&zwnj;یمان ده&zwnj;ده&zwnj;نه تۆ ئه&zwnj;ی محمد (صلى الله عليه وسلم) له&zwnj;ڕاستیدا هه&zwnj;ر په&zwnj;یمان به&zwnj;خوا ده&zwnj;ده&zwnj;ن.. ده&zwnj;ستی پیرۆزو به&zwnj;توانای خوا له&zwnj;سه&zwnj;ر ده&zwnj;ستیانه&zwnj;وه&zwnj;یه (به&zwnj;پشتگیری و یارمه&zwnj;تی) جا ئه&zwnj;وه&zwnj;ی په&zwnj;یمانه&zwnj;که بشکێنێت و لێی پاشگه&zwnj;ز ببێته&zwnj;وه&zwnj;، ئه&zwnj;وه&zwnj;له&zwnj;سه&zwnj;ر خۆی ده&zwnj;که&zwnj;وێت، ئه&zwnj;وه&zwnj;ش که&zwnj;پیمانه&zwnj;که ده&zwnj;باته&zwnj;سه&zwnj;ر و پاشگه&zwnj;ز نابێته&zwnj;وه لێی، خوای باڵاده&zwnj;ست ئه&zwnj;جرو پاداشتی گه&zwnj;وره&zwnj;ی پێده&zwnj;به&zwnj;خشێت (له&zwnj;ئایینده&zwnj;دا).';
$t[11] = 'ئه&zwnj;و عه&zwnj;ره&zwnj;به&zwnj;کۆچه&zwnj;رییانه&zwnj;ی له&zwnj;رێکه&zwnj;وتنه&zwnj;که&zwnj;ی حوده&zwnj;یبیه به&zwnj;جێمان و به&zwnj;شداریان نه&zwnj;کرد کاتێک زانیان ئێوه به&zwnj;ره&zwnj;و غه&zwnj;زای خه&zwnj;یبه&zwnj;ر ده&zwnj;ڕۆن که&zwnj;خوا به&zwnj;ڵێنی سه&zwnj;رکه&zwnj;وتن و ده&zwnj;سکه&zwnj;وته&zwnj;کانی جه&zwnj;نگی پێدابوون دێن بۆلات و ده&zwnj;ڵێن: ماڵ و منداڵمان ئێمه&zwnj;ی سه&zwnj;رقاڵ و خه&zwnj;ریک کرد و کاتمان نه&zwnj;بوو بێین له&zwnj;گه&zwnj;ڵتان، له&zwnj;به&zwnj;ر ئه&zwnj;وه&zwnj;داوای لێخۆش بوونمان بۆ بکه&zwnj;ن لای خوا، ئه&zwnj;وه&zwnj;ی به&zwnj;زمان ده&zwnj;یڵێین له&zwnj;دڵیاندا نی یه&zwnj;، پێیان بڵێ: باشه&zwnj;، کێ ده&zwnj;توانێت به&zwnj;ری ده&zwnj;سه&zwnj;ڵاتی خوا بگرێت، ئه&zwnj;گه&zwnj;ر بیه&zwnj;وێت زیانێکتان پێ بگه&zwnj;یه&zwnj;نێت یان سوودو قازانجێکتان بۆ پێش بهێنێت، خۆ خوا به&zwnj;ته&zwnj;واوی به&zwnj;ئاگایه له&zwnj;کاروکرده&zwnj;وه&zwnj;کانتان.';
$t[12] = 'نه&zwnj;خێر پاساوو به&zwnj;هانه&zwnj;که&zwnj;تان وانی یه&zwnj;، به&zwnj;ڵکو له&zwnj;ڕاستیدا ئێوه واگومانتان ده&zwnj;برد ئیتر هه&zwnj;رگیز پێغه&zwnj;مبه&zwnj;رو باوه&zwnj;ڕداران ناگه&zwnj;رێنه&zwnj;وه بۆ ناو که&zwnj;سوکاریان و تیاده&zwnj;چن، ئه&zwnj;و گومان بردنه&zwnj;له&zwnj;دڵ و ده&zwnj;روونتاندا ڕازاێنرابۆوه و چه&zwnj;سپی بوو، به&zwnj;ڕاستی گومانی خراپ و ناقوڵاتان بردوو، ئێوه هه&zwnj;میشه هۆزو تاقمێکی بێ که&zwnj;ڵک و بێ خێرن.';
$t[13] = 'هه&zwnj;رکه&zwnj;س باوه&zwnj;ڕی به&zwnj;خواو به&zwnj;پێغه&zwnj;مبه&zwnj;ری خوا نه&zwnj;بێت (ئاینده&zwnj;ی سامناکه&zwnj;) به&zwnj;ڕاستی ئێمه سزای دۆزه&zwnj;خمان بۆ کافرو بێ باوه&zwnj;ڕان ئاماده&zwnj;کردووه&zwnj;.';
$t[14] = 'هه&zwnj;رچی له ئاسمان و زه&zwnj;ویدا هه&zwnj;یه هه&zwnj;ر خوا خۆی خاوه&zwnj;نیانه&zwnj;، له&zwnj;هه&zwnj;ر كه&zwnj;سێك كه بیوێت خۆشده&zwnj;بێت، وه سزای هه&zwnj;ركه&zwnj;سێك بدات ده&zwnj;یدات، (ئه&zwnj;ڵبه&zwnj;ته ویستی خواش بۆ پاداشت یان سزای هه&zwnj;ركه&zwnj;سێك به&zwnj;گوێره&zwnj;ی شیاوو شایسته&zwnj;ی خۆیه&zwnj;تی و له&zwnj;وپه&zwnj;ڕی دادگه&zwnj;ریدایه&zwnj;)، چونكه خوا هه&zwnj;میشه و به&zwnj;رده&zwnj;وام لێ خۆش بووه و به&zwnj;ڕه&zwnj;حم و دلۆڤانه&zwnj;.';
$t[15] = 'ئه&zwnj;وانه&zwnj;ی خۆیان خسته دواوه&zwnj;و به&zwnj;شداریان نه&zwnj;کرد له&zwnj;رێکه&zwnj;وتنه&zwnj;که&zwnj;دا، کاتێک بینیان ئێوه (به&zwnj;ره&zwnj;و غه&zwnj;زای خه&zwnj;یبه&zwnj;ر ده&zwnj;ڕۆن) بۆ کۆکردنه&zwnj;وه&zwnj;ی ده&zwnj;ستکه&zwnj;وته&zwnj;کان، وتیان: لێمان گه&zwnj;رێن شوێنتان که&zwnj;وین... ئه&zwnj;مانه&zwnj;ده&zwnj;یانه&zwnj;وێت، ویست و فه&zwnj;رمانی خوا بگۆڕن، پێیان بڵێ: ئێوه هه&zwnj;رگیز شوێنمان ناکه&zwnj;ون... چونکه&zwnj;خوایش هه&zwnj;واری فه&zwnj;رمووه&zwnj;و پێشتر هه&zwnj;روا بڕیاردراوه&zwnj;، که&zwnj;چی ئه&zwnj;وان ده&zwnj;ڵێن: نه&zwnj;خێر خوا ڕێگه&zwnj;ی له&zwnj;ئێمه نه&zwnj;گرتووه&zwnj;، به&zwnj;لکو ئێوه حه&zwnj;سوودیمان پێ ده&zwnj;به&zwnj;ن بۆیه نایه&zwnj;ڵن به&zwnj;شدار بین، (خوا ده&zwnj;فه&zwnj;رمووێت) نه&zwnj;خێر، حه&zwnj;سوودیتان پێ نابه&zwnj;ن، به&zwnj;ڵکو ئه&zwnj;وانه&zwnj;که&zwnj;مێک نه&zwnj;بێت له&zwnj;فه&zwnj;رمانی خوا تێناگه&zwnj;ن (ته&zwnj;نها چاویان بڕیوه&zwnj;ته سامان و ده&zwnj;سکه&zwnj;وته&zwnj;دنیاییه&zwnj;کان).';
$t[16] = 'ئه&zwnj;ی محمد (صلى الله عليه وسلم) بڵێ: به&zwnj;وانه&zwnj;ی که&zwnj;سه&zwnj;رپێچیان کردووه&zwnj;، له&zwnj;عه&zwnj;ره&zwnj;به&zwnj;بیابان نشینه&zwnj;کان: ئێوه له&zwnj;ئاینده&zwnj;دا بانگ ده&zwnj;کرێن بۆ جه&zwnj;نگ له&zwnj;گه&zwnj;ڵ قه&zwnj;وم و هۆزێکدا که&zwnj;ئازاو جه&zwnj;نگاوه&zwnj;رن، ئه&zwnj;وسا یان دژیان ده&zwnj;جه&zwnj;نگن، یان خۆیان (له&zwnj;ئه&zwnj;نجامی بیرکردنه&zwnj;وه و تێڕوانیندا) به&zwnj;بێ جه&zwnj;نگ موسڵمان و ته&zwnj;سلیم ده&zwnj;بن، جا ئه&zwnj;گه&zwnj;ر فه&zwnj;رمانبه&zwnj;ردار بن، خوا پاداشتی چاکتان پێده&zwnj;به&zwnj;خشێت، به&zwnj;ڵام ئه&zwnj;گه&zwnj;ر پشت هه&zwnj;ڵکه&zwnj;ن وه&zwnj;کو ئه&zwnj;وسا و خۆتان بشارنه&zwnj;وه&zwnj;و سه&zwnj;رپێچی بکه&zwnj;ن، سزایه&zwnj;کی به&zwnj;ئێشتان بۆ پێش ده&zwnj;هێنێت.';
$t[17] = 'ئه&zwnj;و که&zwnj;سه&zwnj;ی کوێره یان نه&zwnj;خۆشه&zwnj;، گوناهبار نابێت (ئه&zwnj;گه&zwnj;ر له&zwnj;غه&zwnj;زادا به&zwnj;شداری نه&zwnj;کات) ئه&zwnj;وه&zwnj;ی که&zwnj;فه&zwnj;رمانبه&zwnj;رداری خواو پێغه&zwnj;مبه&zwnj;ره&zwnj;که&zwnj;ی بکات، ده&zwnj;یخاته باخه&zwnj;کانی به&zwnj;هه&zwnj;شته&zwnj;وه&zwnj;که&zwnj;چه&zwnj;نده&zwnj;ها ڕووبار به&zwnj;ژێر دره&zwnj;خت و کۆشکه&zwnj;کانیاندا ده&zwnj;ڕوات، ئه&zwnj;و که&zwnj;سه&zwnj;ش که&zwnj;پشت هه&zwnj;ڵکات، خوای گه&zwnj;وره&zwnj;سزای به&zwnj;ئێش و ئازاری ده&zwnj;دات.';
$t[18] = 'سوێند به&zwnj;خوا به&zwnj;ڕاستی خوا ڕازیی بووه&zwnj;له&zwnj;و باوه&zwnj;ڕدارانه&zwnj;ی کاتێک په&zwnj;یمانیان له گه&zwnj;ڵدا به&zwnj;ستیت له&zwnj;ژێر دره&zwnj;خته&zwnj;که&zwnj;دا خوا خۆیشی ئاگادار بوو به&zwnj;وه&zwnj;ی له&zwnj;دڵه&zwnj;کانیاندا هه&zwnj;یه (له&zwnj;نیاز پاکی و دڵسۆزی) هه&zwnj;ر بۆیه ئارامی و هێمنی و ئاسوده&zwnj;یی دابه&zwnj;زانده&zwnj;سه&zwnj;ریان و پاداشتی به&zwnj;مژده&zwnj;ی سه&zwnj;رکه&zwnj;وتنێکی نزێک دانه&zwnj;وه&zwnj;.';
$t[19] = 'هه&zwnj;روه&zwnj;ها به&zwnj;ڵێنی چه&zwnj;ندین ده&zwnj;ستکه&zwnj;وتی زۆری پێدان، که&zwnj;له&zwnj;ئاینده&zwnj;دا ده&zwnj;ستیان ده&zwnj;که&zwnj;وێت و وه&zwnj;ری ده&zwnj;گرن، خوا هه&zwnj;میشه&zwnj;و به&zwnj;رده&zwnj;وام باڵاده&zwnj;سته به&zwnj;سه&zwnj;ر خوانه&zwnj;ناسه&zwnj;کانداو دانایه و ده&zwnj;زانێت چ کاتێک ئیمانداران سه&zwnj;ر ده&zwnj;خات.';
$t[20] = 'خوا به&zwnj;ڵێنی ده&zwnj;سکه&zwnj;وتی زۆری پێداون، له&zwnj;ئاینده&zwnj;دا ده&zwnj;ستتان ده&zwnj;که&zwnj;وێت و وه&zwnj;ری ده&zwnj;گرن، به&zwnj;ڵام جارێ ئێستا هه&zwnj;رئه&zwnj;وه&zwnj;ی پێ به&zwnj;خشیوون (مه&zwnj;به&zwnj;ست ده&zwnj;ستکه&zwnj;وته&zwnj;کانی خه&zwnj;یبه&zwnj;ره&zwnj;) ده&zwnj;ستی خه&zwnj;ڵکیشی لێ کۆتاکردن (مه&zwnj;به&zwnj;ست قوڕه&zwnj;یشه که&zwnj;به&zwnj;هۆی ڕێکه&zwnj;وتنه&zwnj;که&zwnj;ی حوده&zwnj;یبییه&zwnj;وه خوای گه&zwnj;وره&zwnj;له&zwnj;به&zwnj;گژاچوونی موسوڵمانان ده&zwnj;ستی به&zwnj;ستنه&zwnj;وه&zwnj;)، تا ئه&zwnj;م ده&zwnj;ستکه&zwnj;وت و سه&zwnj;رکه&zwnj;وتنه&zwnj;ببێته نیشانه و به&zwnj;ڵگه&zwnj;یه&zwnj;کی به&zwnj;هێز بۆ ئیمانداران (که&zwnj;خوا یاوه&zwnj;رو پشتیوانیانه&zwnj;) هه&zwnj;روه&zwnj;ها ڕێنموویتان بکات بۆ ڕێبازی ڕاست و دروست (له&zwnj;به&zwnj;ره&zwnj;و پێش بردنی بانگه&zwnj;وازدا).';
$t[21] = 'چه&zwnj;ندین ده&zwnj;ستکه&zwnj;وتی تریشیان هه&zwnj;یه که&zwnj;ئێستا ده&zwnj;ستتان به&zwnj;سه&zwnj;ریدا نه&zwnj;ڕۆیشتووه&zwnj;، به&zwnj;ڕاستی خوا خۆی چاک به&zwnj;و ده&zwnj;سکه&zwnj;وتانه&zwnj;ده&zwnj;زانێت که&zwnj;به&zwnj;ڕێوه&zwnj;یه بۆتان، هه&zwnj;میشه و به&zwnj;رده&zwnj;وام خوا ده&zwnj;سه&zwnj;ڵاتی به&zwnj;سه&zwnj;ر هه&zwnj;موو شتێکدا هه&zwnj;یه.';
$t[22] = 'خۆ ئه&zwnj;گه&zwnj;ر ئه&zwnj;وانه&zwnj;ی بێ باوه&zwnj;ڕ بوون دژی ئێوه جه&zwnj;نگیان به&zwnj;رپا بکردایه&zwnj;، ئه&zwnj;وه&zwnj;ده&zwnj;شکان و هه&zwnj;ڵده&zwnj;هاتن، پاشان دۆست و پشتگیرێکیان نه&zwnj;ده&zwnj;بوو.';
$t[23] = '(شکست و هه&zwnj;ڵاتنی کافران له&zwnj;جه&zwnj;نگی موسوڵماناندا) هه&zwnj;رده&zwnj;م به&zwnj;رنامه&zwnj;و یاسای خوا به&zwnj;ڕاستی هه&zwnj;روا بووه&zwnj;له&zwnj;زوه&zwnj;وه&zwnj;، هه&zwnj;رگیز نابینیت به&zwnj;رنامه&zwnj;و یاسای خوا گۆڕانی به&zwnj;سه&zwnj;ردا بێت.';
$t[24] = 'هه&zwnj;ر ئه&zwnj;و خوایه ده&zwnj;ستی بێ باوه&zwnj;ڕانی له&zwnj;ئێوه به&zwnj;سته&zwnj;وه (نه&zwnj;یهێشت جه&zwnj;نگ ببێت)، دوای ئه&zwnj;وه&zwnj;ی ئێوه&zwnj;ی زاڵ کرد به&zwnj;سه&zwnj;ریاندا له&zwnj;ناو جه&zwnj;رگه&zwnj;ی مه&zwnj;ککه&zwnj;دا، خوا هه&zwnj;میشه&zwnj;و به&zwnj;رده&zwnj;وام بینایه به&zwnj;وه&zwnj;ی که&zwnj;ده&zwnj;یكه&zwnj;ن.';
$t[25] = 'ئه&zwnj;و خراپه&zwnj;کارانه&zwnj;ئه&zwnj;وانه&zwnj;ن که&zwnj;بێ باوه&zwnj;ڕ بوون و ڕێگه&zwnj;شیان لێ گرتن بگه&zwnj;نه&zwnj;که&zwnj;عبه&zwnj;ی پیرۆزو نه&zwnj;یانهێشت ئه&zwnj;و مه&zwnj;ڕوماڵاته&zwnj;ی که&zwnj;گل درابوونه&zwnj;وه&zwnj;بۆ قوربانی بگاته&zwnj;جێی خۆی، خۆ ئه&zwnj;گه&zwnj;ر له&zwnj;به&zwnj;ر ئه&zwnj;و پیاوو ئافره&zwnj;تانه&zwnj;موسوڵمانانه نه&zwnj;بوایه که&zwnj;نه&zwnj;تانده&zwnj;ناسین پێشێلتان ده&zwnj;کردن (له&zwnj;ناو شاری مه&zwnj;ککه&zwnj;دا بوون و هێشتا کۆچیان نه&zwnj;کردبوو)، به&zwnj;هۆی ئه&zwnj;وه&zwnj;وه&zwnj;عه&zwnj;یب و عارو باری گرانتان تووش ده&zwnj;بوو لێیانه&zwnj;وه بێ ئه&zwnj;وه&zwnj;ی بزانن، (ئه&zwnj;گه&zwnj;ر له&zwnj;به&zwnj;ر ئه&zwnj;وه&zwnj;نه&zwnj;بووایه مۆڵه&zwnj;تی ئێوه&zwnj;مان ده&zwnj;دا بۆ کوشتنیان)، ئه&zwnj;مه&zwnj;ش بۆ ئه&zwnj;وه&zwnj;ی خوای په&zwnj;روه&zwnj;ردیگار ئه&zwnj;و که&zwnj;سانه&zwnj;ی ده&zwnj;یه&zwnj;وێت و شایسته&zwnj;ن بخاته&zwnj;ژێر سایه&zwnj;ی سۆزو به&zwnj;زه&zwnj;یی خۆیه&zwnj;وه&zwnj;، خۆ ئه&zwnj;گه&zwnj;ر موسڵمان له&zwnj;کافران جیابوونایه له&zwnj;مه&zwnj;ککه&zwnj;دا، بێگومان ئێمه به&zwnj;ده&zwnj;ستی ئێوه سزای به&zwnj;سۆی بێ باوه&zwnj;ڕانمان ده&zwnj;دا.';
$t[26] = 'سزای ئه&zwnj;وانه&zwnj;ی که&zwnj;بێ باوه&zwnj;ڕ بوون له&zwnj;به&zwnj;ر ئه&zwnj;وه&zwnj;یه ده&zwnj;مارگیریان خستۆته ناو دڵ و ده&zwnj;روونیی خۆیانه&zwnj;وه، که&zwnj;ده&zwnj;مارگیریی نه&zwnj;فامی یه&zwnj;، جا خوا ئارامی و ئاسوده&zwnj;یی دابه&zwnj;زانده&zwnj;سه&zwnj;ر پێغه&zwnj;مبه&zwnj;ره&zwnj;که&zwnj;ی و سه&zwnj;ر ئیمانداران (ئیتر دڵخۆشن به&zwnj;رێکه&zwnj;وتنه&zwnj;که و ده&zwnj;ره&zwnj;نجامه&zwnj;کانی) خوای گه&zwnj;وره&zwnj;ش پابه&zwnj;ندی خواناسی و پارێزکاری کردن، ئه&zwnj;وان شایسته&zwnj;ی ئه&zwnj;و به&zwnj;هره&zwnj;یه&zwnj;ن و مه&zwnj;ردی ئه&zwnj;و ڕێزه&zwnj;ن... دڵنیابن هه&zwnj;میشه و به&zwnj;رده&zwnj;وام خوا به&zwnj;هه&zwnj;موو شتێک زانا و ئاگایه.';
$t[27] = 'سوێند به&zwnj;خوا بێگومان خوا خه&zwnj;ونی پێغه&zwnj;مبه&zwnj;ره&zwnj;که&zwnj;ی به&zwnj;ڕاستی و وه&zwnj;ک خۆی هێناوه&zwnj;ته دی که&zwnj;ده&zwnj;فه&zwnj;رموێت: به&zwnj;م زووانه&zwnj;ئێوه به&zwnj;هه&zwnj;موو ئارامی و ئاسووده&zwnj;ییه&zwnj;که&zwnj;وه به&zwnj;ویستی خوا ده&zwnj;چنه&zwnj;ناو که&zwnj;عبه&zwnj;ی پیرۆزه&zwnj;وه&zwnj;، (عه&zwnj;مره&zwnj;به&zwnj;جێده&zwnj;هێنن) سه&zwnj;ره&zwnj;تان ده&zwnj;تاشن یان کورتی ده&zwnj;که&zwnj;نه&zwnj;وه&zwnj;بێ ئه&zwnj;وه&zwnj;ی ترس و بیمتان له&zwnj;که&zwnj;س هه&zwnj;بێت، جا ئه&zwnj;وه&zwnj;ی نه&zwnj;تانزانیووه خوا ده&zwnj;یزانی، که&zwnj;جگه&zwnj;له&zwnj;م سه&zwnj;رکه&zwnj;وتنه&zwnj;سه&zwnj;رکه&zwnj;وتنێکی نزیکی تری بۆ بڕیارداون و پێتان ده&zwnj;به&zwnj;خشێت (که&zwnj;غه&zwnj;زای خه&zwnj;یبه&zwnj;ره&zwnj;)....';
$t[28] = 'خوا ئه&zwnj;و زاته&zwnj;یه که&zwnj;پێغه&zwnj;مبه&zwnj;ری خۆی ڕه&zwnj;وانه&zwnj;کردووه&zwnj;، هاوڕێ له&zwnj;گه&zwnj;ڵ ڕێنموویی و په&zwnj;یامی ڕاست و دروستدا، بۆ ئه&zwnj;وه&zwnj;ی زاڵی بکات به&zwnj;سه&zwnj;ر هه&zwnj;موو په&zwnj;یام و به&zwnj;رنامه&zwnj;یه&zwnj;کی تردا، خوا خۆیشی به&zwnj;سه&zwnj;، بۆ شایه&zwnj;تدان و ئاشکرا کردنی ئه&zwnj;و ڕاستی یه.';
$t[29] = 'محمد (صلى الله عليه وسلم) ڕه&zwnj;وانه&zwnj;کراوی خوایه&zwnj;، ئه&zwnj;وانه&zwnj;ش که&zwnj;له&zwnj;خزمه&zwnj;تیدان و شوێنی که&zwnj;وتوون توند و تیژن به&zwnj;رامبه&zwnj;ر بێ باوه&zwnj;ڕان و به&zwnj;به&zwnj;زه&zwnj;یی و میهره&zwnj;بانن له&zwnj;نێوان خۆیاندا، ده&zwnj;یانبینیت سه&zwnj;رگه&zwnj;رمی نوێژ و خواپه&zwnj;رستین، کڕنۆش و سوژده&zwnj;ده&zwnj;به&zwnj;ن، هه&zwnj;رده&zwnj;م ئاواتیان به&zwnj;ده&zwnj;ست هێنانی ڕه&zwnj;زامه&zwnj;ندی و پایه به&zwnj;رزی یه له&zwnj;لای خوا، نیشانه&zwnj;ی چاکی و پاکییان له&zwnj;ناوچه&zwnj;وانیاندا دیاریه&zwnj;و ده&zwnj;دره&zwnj;وشێته&zwnj;وه له&zwnj;ئه&zwnj;نجامی سوژده&zwnj;بردن و خوا په&zwnj;رستییاندا، جا ئه&zwnj;و به&zwnj;ڕێزانه ئه&zwnj;وه&zwnj;نموونه&zwnj;و پێناسه&zwnj;یانه له&zwnj;ته&zwnj;وراتدا، نموونه&zwnj;و پێناسه&zwnj;شیان له&zwnj;ئینجیلیشدا وه&zwnj;ک ڕوه&zwnj;کێک وایه که&zwnj;له&zwnj;سه&zwnj;ره&zwnj;تاوه&zwnj;چه&zwnj;که&zwnj;ره&zwnj;بکات، جا چه&zwnj;که&zwnj;ره&zwnj;که&zwnj;ی به&zwnj;هێز کردبێت، پاشان ئه&zwnj;ستوور ببێت و ڕابوه&zwnj;ستێت له&zwnj;سه&zwnj;ر قه&zwnj;ده&zwnj;که&zwnj;ی، ئه&zwnj;وه&zwnj;نده&zwnj;زوو پێ بگات کشتیاره&zwnj;کان سه&zwnj;رسام بکات و پێیان سه&zwnj;یر بێت: چۆن وا پێگه&zwnj;یی؟! چۆن ئاوا خۆی گرت؟! ئه&zwnj;م پێناسه&zwnj;یه بۆ ئه&zwnj;وه&zwnj;یه خوا کافران داخ له&zwnj;دڵ بکات، جا خوای به&zwnj;ده&zwnj;سه&zwnj;ڵات به&zwnj;ڵێنی به&zwnj;وانه&zwnj;داوه&zwnj;که&zwnj;باوه&zwnj;ڕیان هێناوه و کرده&zwnj;وه&zwnj;چاکه&zwnj;کانیان ئه&zwnj;نجام داوه&zwnj;له&zwnj;وان: که&zwnj;لێخۆشبوون و پاداشتی گه&zwnj;وره&zwnj;و بێ سنووریان پێببه&zwnj;خشێت.';
?>