$t = array();
$t[1] = 'ئه&zwnj;ی ئه&zwnj;وانه&zwnj;ی باوه&zwnj;ڕتان هێناوه&zwnj;، به&zwnj;ئه&zwnj;مه&zwnj;ک و به&zwnj;وه&zwnj;فا بن به&zwnj;رامبه&zwnj;ر به&zwnj;ڵێن و په&zwnj;یمانه&zwnj;کانتان، (خواردنی گۆشتی) ماڵات (مه&zwnj;ڕو بزن و گاو وشتر) بۆتان حه&zwnj;ڵاڵ کراوه&zwnj;، بێجگه له&zwnj;وانه&zwnj;ی که به&zwnj;سه&zwnj;رتاندا ده&zwnj;خوێنرێته&zwnj;وه (له&zwnj;م سووره&zwnj;ته&zwnj;دا)، له&zwnj;کاتێکدا ڕاوتان بۆ حه&zwnj;ڵاڵ نیه که له ئیحرامدان، (ئه&zwnj;م سنوورانه فه&zwnj;رمانی خوایه&zwnj;) به&zwnj;ڕاستی خوای گه&zwnj;وره فه&zwnj;رمان ده&zwnj;دات به هه&zwnj;ر شتێک که بیه&zwnj;وێت.';
$t[2] = 'ئه&zwnj;ی ئه&zwnj;وانه&zwnj;ی باوه&zwnj;ڕتان هێناوه ده&zwnj;ستکاری دروشمه&zwnj;کانی خوا مه&zwnj;که&zwnj;ن و هه&zwnj;ڵی مه&zwnj;وه&zwnj;شێننه&zwnj;وه (به&zwnj;تایبه&zwnj;ت دروشمه&zwnj;کانی حه&zwnj;ج و عه&zwnj;مره&zwnj;) هه&zwnj;روه&zwnj;ها مانگه&zwnj;کانی حه&zwnj;رام (که&zwnj;: ڕجب، ذوالقعدة، ذوالحجة، محرم)ن، تێکی مه&zwnj;ده&zwnj;ن و ئه&zwnj;و قوربانیه&zwnj;ش که ده&zwnj;کرێت (مه&zwnj;رجه&zwnj;کانی مه&zwnj;شێوێنن) و ئه&zwnj;و ملوانکانه&zwnj;ی (ده&zwnj;کرێته ملی ئاژه&zwnj;ڵ، که نیشانه&zwnj;یه بۆ ئه&zwnj;وه&zwnj;ی خاوه&zwnj;نیان بڕیاریداوه بکرێن به&zwnj;قوربانی) لێی مه&zwnj;که&zwnj;نه&zwnj;وه و ڕێگه له&zwnj;وانه مه&zwnj;گرن که دێن بۆ (طواف)ی که&zwnj;عبه&zwnj;و له&zwnj;کاتێکدا به&zwnj;ئومێدی فه&zwnj;زڵ و ڕه&zwnj;زامه&zwnj;ندی خوان، کاتێک ئیحرام ده&zwnj;شکێنن، ئه&zwnj;وه بۆتان هه&zwnj;یه ڕاو بکه&zwnj;ن، هه&zwnj;روه&zwnj;ها دوژمنایه&zwnj;تی و ڕقه&zwnj;به&zwnj;رایه&zwnj;تی که&zwnj;سانێک که نه&zwnj;یانهێشت کاتی خۆی (طواف)ی که&zwnj;عبه بکه&zwnj;ن، مه&zwnj;که&zwnj;نه هۆی ده&zwnj;ستدرێژی و به&zwnj;رپاکردنی ناخۆشی، یارمه&zwnj;تی و کۆمه&zwnj;کیی یه&zwnj;کتر بکه&zwnj;ن له&zwnj;سه&zwnj;ر چاکه&zwnj;و خواناسی، نه&zwnj;که&zwnj;ن یارمه&zwnj;تی وکۆمه&zwnj;کی یه&zwnj;کتر بکه&zwnj;ن له&zwnj;سه&zwnj;ر گوناهو ده&zwnj;ستدرێژی، له خوا بترسن و پارێزکار بن، چونکه به&zwnj;ڕاستی خوا زۆر به&zwnj;توندی تۆڵه ده&zwnj;سێنێت.';
$t[3] = 'له&zwnj;سه&zwnj;رتان حه&zwnj;رام کراوه خواردنی گۆشتی مرداره&zwnj;وه بوو، خوێن، گۆشتی به&zwnj;راز، هه&zwnj;روه&zwnj;ها ئه&zwnj;و ماڵاته&zwnj;ش که سه&zwnj;ر ده&zwnj;بڕدرێن و ناوی غه&zwnj;یری خوای له&zwnj;سه&zwnj;ر ده&zwnj;برێت و ماڵاتی خنکاویش، ئه&zwnj;وه&zwnj;ش که به ئازارو لێدان مردار بۆته&zwnj;وه&zwnj;، ئه&zwnj;وه&zwnj;ش که ده&zwnj;که&zwnj;وێته خواره&zwnj;وه&zwnj;، ئه&zwnj;وه&zwnj;ش که به&zwnj;لێدانی ماڵاتێکی تر مردار بووبێته&zwnj;وه&zwnj;، ئه&zwnj;وانه&zwnj;ش که دڕنده پالاماری دابن و لێی خواردبن مه&zwnj;گه&zwnj;ر فریای چه&zwnj;قۆ که&zwnj;وتبن، (هه&zwnj;ر حا&zwnj;ڵه&zwnj;تێک له&zwnj;وانه&zwnj;ی باسکرا ئه&zwnj;گه&zwnj;ر به چه&zwnj;قۆدا بگات حه&zwnj;ڵاڵه&zwnj;)، هه&zwnj;روه&zwnj;ها حه&zwnj;رام کراوه خواردنی گۆشتی ئه&zwnj;و ماڵاتانه&zwnj;ی له&zwnj;و شوێنانه&zwnj;دا سه&zwnj;ر ده&zwnj;بڕدرێن که هاناو هاواری بۆ غه&zwnj;یری خوا تێدا ده&zwnj;برێت، هه&zwnj;روه&zwnj;ها حه&zwnj;رامه له&zwnj;سه&zwnj;رتان داوا کردنی چاره&zwnj;نووستان به&zwnj;هۆی (الأ&zwnj;&zwnj;زلام) وه&zwnj;، وه&zwnj;کو ئه&zwnj;و شتانه&zwnj;ی که له ڕێگه&zwnj;ی هه&zwnj;ڵدانی زاره&zwnj;وه ده&zwnj;ست بکه&zwnj;وێت و فاڵگرتنه&zwnj;وه&zwnj;، بورجه&zwnj;کان، قاوه گرتنه&zwnj;وه&zwnj;،... هتد) ئه&zwnj;و جۆره کردارو ڕه&zwnj;فتارانه&zwnj;تان سه&zwnj;رپێچی و ده&zwnj;رچوونه له (فه&zwnj;رمانی خوا)، جا ئه&zwnj;مڕۆ (که ڕۆژی عه&zwnj;ره&zwnj;فه&zwnj;یه&zwnj;) کافران نائومێد بوون له دین و ئاینی ئێوه (ئیتر ناتوانن زاڵ بن به&zwnj;سه&zwnj;رتانداو هه&zwnj;رگیز نووری خوایان بۆ ناکوژێته&zwnj;وه&zwnj;) که&zwnj;واته له&zwnj;وانه مه&zwnj;ترسن، به&zwnj;ڵکو هه&zwnj;ر له من بترسن، ئه&zwnj;مڕۆ ئاین و به&zwnj;رنامه&zwnj;که&zwnj;تانم به&zwnj;کۆتایی گه&zwnj;یاندو نازو نیعمه&zwnj;تی خۆمم بۆ ته&zwnj;واو کردن و ڕازیم به&zwnj;وه&zwnj;ی که ئیسلام ببێته به&zwnj;رنامه&zwnj;و دین و ئاینتان (جا له&zwnj;گه&zwnj;ڵ بڕیاردانی ئه&zwnj;و شتانه&zwnj;دا که حه&zwnj;راممان کردووه&zwnj;) هه&zwnj;ر که&zwnj;س ناچار کرا له گرانیی و برسێتیدا (مه&zwnj;ترسی ئه&zwnj;وه&zwnj;ی هه&zwnj;بوو بمرێت) ده&zwnj;توانێت له&zwnj;و شتانه بخوات که حه&zwnj;راممان کردووه (به&zwnj;مه&zwnj;رجێک ده&zwnj;ست درێژکار نه&zwnj;بێت و به&zwnj;ئاره&zwnj;زوو ژه&zwnj;می زۆری لێ نه&zwnj;خوات) و خۆی تووشی گوناهو تاوان نه&zwnj;کات، ئه&zwnj;وه به&zwnj;ڕاستی خوا لێخۆشبووه&zwnj;و میهره&zwnj;بانه (بۆ ئه&zwnj;و جۆره حاڵه&zwnj;تانه&zwnj;).';
$t[4] = 'ئه&zwnj;ی محمد (صلی الله علیه وسلم) پرسیارت لێده&zwnj;که&zwnj;ن چی حه&zwnj;ڵاڵکراوه بۆیان؟ پێیان بڵێ: هه&zwnj;رچی خۆراکی چاک و پاکه بۆتان حه&zwnj;ڵاڵ کراوه&zwnj;، ئه&zwnj;وه&zwnj;ش باڵنده&zwnj;و تانجی شکاری بۆتان ڕاو ده&zwnj;که&zwnj;ن و فێرتان کردوون (بێگومان خوا توانای به&zwnj;خشیووه به مرۆڤ بتوانێت مه&zwnj;شق بکات به&zwnj;گیانداران و فێریان بکات ڕاوی بۆ بکه&zwnj;ن به&zwnj;تایبه&zwnj;تی تانجی)، ده بخۆن له&zwnj;وه&zwnj;ی بۆتان ده&zwnj;گرن و ناوی خوای لێ بهێنن کاتێک به&zwnj;ڕه&zwnj;ڵای ده&zwnj;که&zwnj;ن و کاتێک لێی ده&zwnj;خۆن، هه&zwnj;ر له خوایش بترسن و پارێزکاربن (ئه&zwnj;ی ڕاوچیه&zwnj;کان)، به&zwnj;ڕاستی خوا به&zwnj;خێرایی لێپرسینه&zwnj;وه ئه&zwnj;نجام ده&zwnj;دات (ووردو درشتی کردارو ڕه&zwnj;فتار فه&zwnj;رامۆش ناکات).';
$t[5] = 'له&zwnj;مڕۆوه هه&zwnj;رچی خۆراکی چاک و پاکه بۆتان حه&zwnj;ڵاڵ کراوه و خواردنی ئه&zwnj;وانه&zwnj;ی که کتێبی ئاسمانیان پێدراوه بۆتان حه&zwnj;ڵاڵه&zwnj;، خوارده&zwnj;مه&zwnj;نی ئێوه&zwnj;ش حه&zwnj;ڵاڵه بۆ ئه&zwnj;وان، ئافره&zwnj;تانی داوێن پاک له ئیمانداران و ئافره&zwnj;تانی داوێن پاک له&zwnj;وانه&zwnj;ی کتێبیان پێدراوه پێش ئێوه (بۆتان حه&zwnj;ڵاڵه&zwnj;) کاتێک ماره&zwnj;ییان پێبده&zwnj;ن، به&zwnj;مه&zwnj;رجێک ئێوه پاک داوێن بن و داوێن پیس نه&zwnj;بن به&zwnj;ئاشکراو دووره په&zwnj;رێز بن له دۆست گرتنی نهێنی بۆ داوێن پیسی، جا ئه&zwnj;وه&zwnj;ی بێ باوه&zwnj;ڕ بێت به&zwnj;شه&zwnj;رعی خواو له&zwnj;م حه&zwnj;رامانه خۆی نه&zwnj;پارێزێت، ئه&zwnj;وه ئیتر کارو کرده&zwnj;وه چاکه&zwnj;کانیشی پووچ ده&zwnj;بێته&zwnj;وه و ئه&zwnj;و جۆره که&zwnj;سانه له قیامه&zwnj;تدا له ڕیزی خه&zwnj;ساره&zwnj;تمه&zwnj;ندو زه&zwnj;ره&zwnj;رمه&zwnj;ندو دۆڕاوه&zwnj;کاندان.';
$t[6] = 'ئه&zwnj;ی ئه&zwnj;وانه&zwnj;ی باوه&zwnj;ڕتان هێناوه&zwnj;: کاتێک ویستتان هه&zwnj;ستن بۆ نوێژ (پێش ئه&zwnj;وه ده&zwnj;ست نوێژ بگرن، به&zwnj;م شێوه&zwnj;یه&zwnj;): ئه&zwnj;وه سه&zwnj;ره&zwnj;تا ڕووخسارتان بشۆن، ئینجا ده&zwnj;سته&zwnj;کانتان له&zwnj;گه&zwnj;ڵ ئه&zwnj;نیشکتاندا، پاشان ده&zwnj;ستی ته&zwnj;ڕ بهێنن به&zwnj;سه&zwnj;ر سه&zwnj;رتاندا، قاچه&zwnj;کانیشتان بشۆن له&zwnj;گه&zwnj;ڵ قوله پێتاندا، ئه&zwnj;گه&zwnj;ر له&zwnj;شتان گران بوو، ئه&zwnj;وه خۆتان خاوێن بکه&zwnj;ن و خۆتان بشۆن، ئه&zwnj;گه&zwnj;ر نه&zwnj;خۆش بوون یان له سه&zwnj;فه&zwnj;ردا بوون یان ده&zwnj;ستان به&zwnj;ئاو گه&zwnj;یاندبوو (تاره&zwnj;تتان گرتبوو) یان له ئافره&zwnj;تی (نامه&zwnj;حره&zwnj;م) که&zwnj;وتبوون و ئاوتان ده&zwnj;ست نه&zwnj;ده&zwnj;که&zwnj;وت (بۆ خۆشتن یان بۆ ده&zwnj;ستنوێژ، ئه&zwnj;وکاته ده&zwnj;توانن) به خاکێکی پاک ته&zwnj;یه&zwnj;موم بکه&zwnj;ن (واته له&zwnj;پی ده&zwnj;ستان به&zwnj;ئاسته&zwnj;م بماڵن به&zwnj;خاکدا)و بیهێنن به ڕووخسارو ده&zwnj;ستاندا (تا ئانیشکتان)، خوا نایه&zwnj;وێت له ئایندا تووشی سه&zwnj;غڵه&zwnj;تی و ناڕه&zwnj;حه&zwnj;تیتان بکات به&zwnj;ڵکو ده&zwnj;یه&zwnj;وێت پاک و خاوێنتان بکات (له ڕواڵه&zwnj;ت و ناوه&zwnj;ڕۆکدا) و ده&zwnj;یه&zwnj;وێت نازو نیعمه&zwnj;تی خۆیتان به&zwnj;سه&zwnj;ردا ته&zwnj;واوبکات بۆ ئه&zwnj;وه&zwnj;ی سوپاسگوزار بن.';
$t[7] = 'یادی نازو نیعمه&zwnj;تی خوا بکه&zwnj;نه&zwnj;وه له&zwnj;سه&zwnj;رتان، هه&zwnj;روه&zwnj;ها ئه&zwnj;و په&zwnj;یمانه&zwnj;ش که پێویسته ئێوه پابه&zwnj;ندی بن، له بیرتان نه&zwnj;چێت که وتوتانه&zwnj;: گوێرایه&zwnj;ڵ و ملکه&zwnj;چ و فه&zwnj;رمانبه&zwnj;ردارین (که&zwnj;واته ئیتر هه&zwnj;میشه و به&zwnj;رده&zwnj;وام) له خوا بترسن و پارێزکار بن، چونکه خوا زانایه به&zwnj;نهێنی دڵ و ده&zwnj;روونه&zwnj;کان.';
$t[8] = 'ئه&zwnj;ی ئه&zwnj;وانه&zwnj;ی باوه&zwnj;ڕتان هێناوه&zwnj;، زۆر به&zwnj;چاکی هه&zwnj;ستن به&zwnj;ئه&zwnj;نجامدانی شایه&zwnj;تی دادپه&zwnj;روه&zwnj;رانه له&zwnj;به&zwnj;ر خوا، ڕق و دوژمنایه&zwnj;تی هیچ که&zwnj;س و هیج لایه&zwnj;ک هه&zwnj;ڵتان نه&zwnj;نێت که&zwnj;له دادپه&zwnj;روه&zwnj;ریی لابده&zwnj;ن، دادپه&zwnj;روه&zwnj;ربن، ئه&zwnj;وه نزیکتره له ته&zwnj;قواو خواناسیه&zwnj;وه&zwnj;، ده&zwnj;ی که&zwnj;واته له&zwnj;خوا ترس و پارێزکاربن، چونکه به&zwnj;ڕاستی خوا ئاگاداره به&zwnj;و کارو کرده&zwnj;وه&zwnj;یه&zwnj;ی که ده&zwnj;یکه&zwnj;ن.';
$t[9] = 'خوا به&zwnj;ڵێنی داوه به&zwnj;وانه&zwnj;ی باوه&zwnj;ڕیان هێناوه&zwnj;و کرده&zwnj;وه چاکه&zwnj;کانیان ئه&zwnj;نجامداوه به لێخۆشبوون و چاوبۆشی (له هه&zwnj;ڵه&zwnj;و گوناهه&zwnj;کانیان) و پاداشتی گه&zwnj;وره&zwnj;و بێ سنوور بۆ ئه&zwnj;وانه&zwnj;.';
$t[10] = 'ئه&zwnj;وانه&zwnj;ش که ڕێبازی کوفریان گرتۆته&zwnj;به&zwnj;رو بڕوایان به&zwnj;ئایه&zwnj;ته&zwnj;کانی ئێمه نه&zwnj;هێناوه&zwnj;، ئا ئه&zwnj;وانه نیشته&zwnj;جێی ناو ئاگری دۆزه&zwnj;خن.';
$t[11] = 'ئه&zwnj;ی ئه&zwnj;وانه&zwnj;ی باوه&zwnj;ڕتان هێناوه یادی نازو نیعمه&zwnj;تی خوا بکه&zwnj;نه&zwnj;وه له&zwnj;سه&zwnj;رتان کاتێک هه&zwnj;ندێک ویستیان ده&zwnj;ستدرێژی بکه&zwnj;نه سه&zwnj;رتان، خوایش ده&zwnj;ستدرێژیه&zwnj;که&zwnj;ی ڕاگرتن و (ئێوه&zwnj;ی پاڕاست)، ده&zwnj;ی که&zwnj;واته له خوا بترسن و پارێزکار بن، ده&zwnj;با ئیمانداران هه&zwnj;ر پشت به&zwnj;خوا ببه&zwnj;ستن (تا هه&zwnj;میشه فریایان بکه&zwnj;وێت و له پیلانی دوژمنان بیانپارێزێت).';
$t[12] = 'سوێند بێت بێگومان (کاتی خۆی) خوا په&zwnj;یمانی له نه&zwnj;وه&zwnj;ی ئیسرائیل وه&zwnj;رگرت و دوانزه ڕابه&zwnj;رمان تێدا هه&zwnj;ڵبژاردن، ئینجا خوا پێی فه&zwnj;رموون که&zwnj;: من به&zwnj;ڕاستی له&zwnj;گه&zwnj;ڵتاندام، سوێند بێت به خوا ئه&zwnj;گه&zwnj;ر نوێژه&zwnj;کانتان به&zwnj;چاکی ئه&zwnj;نجام بده&zwnj;ن و زه&zwnj;کات بده&zwnj;ن و باوه&zwnj;ڕبهێنن به هه&zwnj;موو پێغه&zwnj;مبه&zwnj;رانی من و پشتیوانییان لێبکه&zwnj;ن، قه&zwnj;رزی چاک و جوان و ڕێک و پێک بده&zwnj;ن به&zwnj;خه&zwnj;ڵکی له&zwnj;به&zwnj;ر خوا (له&zwnj;به&zwnj;ر ڕه&zwnj;زامه&zwnj;ندی ئه&zwnj;وزاته هه&zwnj;موو چاکه&zwnj;یه&zwnj;ک ئه&zwnj;نجام بده&zwnj;ن و چاوه&zwnj;ڕێی پاداشت له&zwnj;وبن) ئه&zwnj;وه بێگومان له گوناهو هه&zwnj;ڵه&zwnj;کانتان خۆشده&zwnj;بم و چاوپۆشیتان لێده&zwnj;که&zwnj;م و ده&zwnj;تانخه&zwnj;مه باخه&zwnj;کانی به&zwnj;هه&zwnj;شته&zwnj;وه که چه&zwnj;نده&zwnj;ها ڕووبار به&zwnj;ژێر دره&zwnj;خته&zwnj;کانیدا ده&zwnj;ڕوات، جا ئه&zwnj;وه&zwnj;ی له&zwnj;وه&zwnj;ودوا بێ باوه&zwnj;ڕ بێت له ئێوه&zwnj;، ئه&zwnj;وه مانای وایه که به&zwnj;ڕاستی ڕێگه&zwnj;و ڕێبازی ڕاست و ڕه&zwnj;وانی لێ تێکچووه&zwnj;.';
$t[13] = 'جا به&zwnj;هۆی ئه&zwnj;وه&zwnj;وه که نه&zwnj;وه&zwnj;ی ئیسرائیل به&zwnj;رده&zwnj;وام په&zwnj;یمان و به&zwnj;ڵێنه&zwnj;کانیان ده&zwnj;شکاند، نه&zwnj;فره&zwnj;تمان لێکردن و دڵه&zwnj;کانیانمان ڕه&zwnj;ق و سه&zwnj;خت و ڕه&zwnj;ش کرد (چونکه یه&zwnj;کێک له سیفه&zwnj;تیان ئه&zwnj;وه&zwnj;یه&zwnj;) که&zwnj;: قسه له جێی خۆیدا ناکه&zwnj;ن و جێی ده&zwnj;گۆڕن (به ئاره&zwnj;زووی خۆیان ده&zwnj;ستکاریی ته&zwnj;وراتیشیان کردووه&zwnj;) هه&zwnj;روه&zwnj;ها به&zwnj;شێک له&zwnj;و شتانه&zwnj;ی که یاداوه&zwnj;ری بوو بۆیان فه&zwnj;رامۆشیان کردو، به&zwnj;رده&zwnj;وام ده&zwnj;بینیت و بۆت ده&zwnj;رده&zwnj;که&zwnj;وێت که زۆربه&zwnj;یان خیانه&zwnj;تکارو دوژمنکارن، مه&zwnj;گه&zwnj;ر که&zwnj;مێکیان نه&zwnj;بێت، (له&zwnj;گه&zwnj;ڵ ئه&zwnj;وه&zwnj;شدا قه&zwnj;یناکات) هه&zwnj;ر لێیان خۆش ببه&zwnj;و چاوپۆشیان لێ بکه&zwnj;، چونکه به&zwnj;ڕاستی خوا چاکه&zwnj;کارانی خۆشده&zwnj;وێت.';
$t[14] = 'هه&zwnj;روه&zwnj;ها په&zwnj;یمانمان له&zwnj;وانه&zwnj;ش وه&zwnj;رگرتووه که ده&zwnj;یانوت: ئێمه گاورین که&zwnj;چی به&zwnj;شێک له&zwnj;و شتانه&zwnj;یان فه&zwnj;رامۆشکرد که&zwnj;پێی یاداوه&zwnj;ری کرابوون، (به&zwnj;تایبه&zwnj;تی ده&zwnj;رباره&zwnj;ی پێغه&zwnj;مبه&zwnj;رایه&zwnj;تی محمد (صلی الله علیه وسلم) ئێمه&zwnj;ش هه&zwnj;تا ڕۆژی قیامه&zwnj;ت دوژمنایه&zwnj;تی و ڕق و کینه&zwnj;مان پێوه&zwnj;لکاندن، له ئاینده&zwnj;شدا خوا هه&zwnj;موو ئه&zwnj;و کارو کرده&zwnj;وانه&zwnj;یان ده&zwnj;خاته&zwnj;وه به&zwnj;رچاوو پێیان ڕاده&zwnj;گه&zwnj;یه&zwnj;نێت.';
$t[15] = 'ئه&zwnj;ی خاوه&zwnj;نانی کتێب: دڵنیابن که پێغه&zwnj;مبه&zwnj;ری ئێمه بۆ ئێوه&zwnj;ش هاتووه تا زۆربه&zwnj;ی ئه&zwnj;و شتانه&zwnj;ی که لای ئێوه ڕوون و ئاشکرانین و ده&zwnj;یشارنه&zwnj;وه&zwnj;، له کتێبی (ته&zwnj;ورات و ئینجیلدا) بۆتان ڕوون و ئاشکرا بکات، هه&zwnj;روه&zwnj;ها له زۆرێک له&zwnj;و شتانه&zwnj;ی که شاردوتانه&zwnj;ته&zwnj;وه چاوپۆشی ده&zwnj;کات، چونکه به&zwnj;ڕاستی له&zwnj;لایه&zwnj;ن خواوه ڕووناکیه&zwnj;کی ئاشکراتان بۆهاتووه (که محمدی پێغه&zwnj;مبه&zwnj;رمانه (صلی الله علیه وسلم) هاوڕێ له&zwnj;گه&zwnj;ڵ کتێبێکی ڕوونکه&zwnj;ره&zwnj;وه&zwnj;دا (که قورئانه&zwnj;).';
$t[16] = '(بێگومان) خوا به&zwnj;و قورئانه پیرۆزه هیدایه&zwnj;ت و ڕێنموویی ئه&zwnj;و که&zwnj;سانه ده&zwnj;کات که&zwnj;: شوێن ڕه&zwnj;زامه&zwnj;ندی خوا که&zwnj;وتوون، به&zwnj;ره&zwnj;و ڕێبازه&zwnj;کانی ئاشتی و ئاسوده&zwnj;یی و له تاریکایی یه&zwnj;کان ده&zwnj;ریانده&zwnj;هێنێت و ڕزگاریان ده&zwnj;کات و ده&zwnj;یانخاته ناو ڕوناکیه&zwnj;وه به&zwnj;فه&zwnj;رمانی خۆی و ڕێنمووییان ده&zwnj;کات بۆ سه&zwnj;ر ڕێگه&zwnj;و ڕێبازی ڕاست و دروستی (دینداری و خواناسی و چاکه&zwnj;).';
$t[17] = 'سوێند بێت بێگومان ئه&zwnj;وانه بێ باوه&zwnj;ڕ بوون که وتیان: به&zwnj;ڕاستی خوا، هه&zwnj;ر مه&zwnj;سیحی کوڕی مه&zwnj;ریه&zwnj;مه&zwnj;، (ئه&zwnj;ی محمد (صلی الله علیه وسلم) پێیان) بڵێ: ئه&zwnj;گه&zwnj;ر خوا بیه&zwnj;وێت مه&zwnj;سیحی کوڕی مه&zwnj;ریه&zwnj;م و دایکی و هه&zwnj;رچی له&zwnj;سه&zwnj;ر زه&zwnj;ویدا هه&zwnj;یه له&zwnj;ناوی به&zwnj;رێت، ئه&zwnj;وه کێ یه له به&zwnj;رامبه&zwnj;ر خواوه ده&zwnj;سه&zwnj;ڵاتی شتێکی هه&zwnj;یه&zwnj;؟ (که&zwnj;واته ئه&zwnj;ویش و دایکیشی و ئه&zwnj;و خه&zwnj;ڵکه&zwnj;ش به&zwnj;دیهێنراوی خوان و له ده&zwnj;سه&zwnj;ڵاتی ده&zwnj;رناچن، ئیتر به چ به&zwnj;ڵگه&zwnj;یه&zwnj;ک قسه&zwnj;ی وا نابه&zwnj;جێ ده&zwnj;که&zwnj;ن؟!، به&zwnj;مه&zwnj;رجێک) خوا زاتێکه هه&zwnj;رچی له ئاسمانه&zwnj;کان و زه&zwnj;وی و نێوانیاندا هه&zwnj;یه هه&zwnj;ر ئه&zwnj;و خاوه&zwnj;نیانه&zwnj;، هه&zwnj;رچی بیه&zwnj;وێت دروستی ده&zwnj;کات (شتێکی ئاساییه ئه&zwnj;و زاته مه&zwnj;سیح بێ باوک دروست بکات)، چونکه خوا ده&zwnj;سه&zwnj;ڵاتی هه&zwnj;یه به&zwnj;سه&zwnj;ر هه&zwnj;موو شتێکدا (هه&zwnj;موو شت لای ئه&zwnj;و ئاسانه&zwnj;).';
$t[18] = 'جووله&zwnj;که&zwnj;و گاور ده&zwnj;یانوت: ئێمه ڕۆڵه&zwnj;ی خواو خۆشه&zwnj;ویست و نزیکی ئه&zwnj;وین، پێیان بڵێ: که&zwnj;واته بۆچی له&zwnj;سه&zwnj;ر گوناهو تاوانتان سزاتان ده&zwnj;دات (له دنیادا به&zwnj;ئاشکرا به&zwnj;ڵاو ناخۆشیتان بۆ پێش ده&zwnj;هێنێت، سزای قیامه&zwnj;تیش با له&zwnj;ولاوه بوه&zwnj;ستێت، ئێوه خۆتان ئاوا به&zwnj;گه&zwnj;وره&zwnj;و نازدار دامه&zwnj;نێن و خۆتان جیا مه&zwnj;که&zwnj;نه&zwnj;وه&zwnj;) نه&zwnj;خێر وانیه&zwnj;، چونکه ئێوه&zwnj;ش به&zwnj;شه&zwnj;ر و ئاده&zwnj;میزادێکن له&zwnj;و خه&zwnj;ڵکه&zwnj;ی که دروستی کردووه&zwnj;، ئه&zwnj;وه&zwnj;ی خوا بیه&zwnj;وێت لێی خۆش ببێت، لێی خۆشده&zwnj;بێت، ئه&zwnj;وه&zwnj;ش که بیه&zwnj;وێت سزای بدات سزای ده&zwnj;دات (ئه&zwnj;ڵبه&zwnj;ته له چوارچێوه&zwnj;ی دادپه&zwnj;روه&zwnj;ریدا) هه&zwnj;رچیش له ئاسمانه&zwnj;کان و زه&zwnj;وی و نێوانیاندا هه&zwnj;یه هه&zwnj;ر خوا خاوه&zwnj;نیانه&zwnj;، گه&zwnj;ڕانه&zwnj;وه و سه&zwnj;رئه&zwnj;نجامیش بۆ لای ئه&zwnj;و زاته&zwnj;یه&zwnj;.';
$t[19] = 'ئه&zwnj;ی خاوه&zwnj;نانی کتێب: بێگومان پێغه&zwnj;مبه&zwnj;رمان (محمد (صلی الله علیه وسلم) هاتووه بۆ لاتان تا هه&zwnj;موو شتێکتان بۆ ڕوون بکاته&zwnj;وه&zwnj;، دوای ئه&zwnj;وه&zwnj;ی که ماوه&zwnj;یه&zwnj;ک بوو هیچ پێغه&zwnj;مبه&zwnj;رێکمان ڕه&zwnj;وانه نه&zwnj;کردبوو (نێوان له دایک بوونی عیسا و ناردنی محمد سه&zwnj;لامی خوایان له&zwnj;سه&zwnj;ر بێت، زیاتر له شه&zwnj;ش سه&zwnj;ده&zwnj;یه&zwnj;) نه&zwnj;وه&zwnj;کو بڵێن که&zwnj;سمان بۆ ڕه&zwnj;وانه نه&zwnj;کرا تا مژده&zwnj;ده&zwnj;ر بێت (به&zwnj;چاکان) و ترسێنه&zwnj;ربێت بۆ (یاخیه&zwnj;کان)، ئه&zwnj;وه&zwnj;ته به&zwnj;ڕاستی مژده&zwnj;ده&zwnj;رو ترسێنه&zwnj;رتان بۆ هات (ئیتر هیچ بیانوویه&zwnj;کتان نه&zwnj;ماوه&zwnj;)، خوایش ده&zwnj;سه&zwnj;ڵاتی به&zwnj;سه&zwnj;ر هه&zwnj;موو شتێکدا هه&zwnj;یه (ئه&zwnj;گه&zwnj;ر یاخی بن سزاتان ده&zwnj;دات).';
$t[20] = 'یادیان بێنه&zwnj;وه کاتێک موسا به&zwnj;قه&zwnj;ومه&zwnj;که&zwnj;ی وت: ئه&zwnj;ی قه&zwnj;وم و عه&zwnj;شیره&zwnj;تم یادی نازو نیعمه&zwnj;تی خوا بکه&zwnj;نه&zwnj;وه له سه&zwnj;رتان که پێغه&zwnj;مبه&zwnj;رانی له&zwnj;ناو ئێوه&zwnj;دا فه&zwnj;راهه&zwnj;م هێناوه&zwnj;، هه&zwnj;روه&zwnj;ها پاشای زۆری له&zwnj;ناوتاندا هه&zwnj;ڵخستووه&zwnj;، ئه&zwnj;وه&zwnj;ی به ئێوه&zwnj;ی به&zwnj;خشیووه به&zwnj;که&zwnj;سی تری نه&zwnj;به&zwnj;خشیووه له&zwnj;و خه&zwnj;ڵکه بێ شوماره&zwnj;ی (ئه&zwnj;و سه&zwnj;رده&zwnj;مه&zwnj;).';
$t[21] = 'ئه&zwnj;ی قه&zwnj;وم و عه&zwnj;شیره&zwnj;تم بچنه ناو ئه&zwnj;و زه&zwnj;ویه پیرۆزه&zwnj;ی (القدس)، که خوا پێویستی کرد له&zwnj;به&zwnj;ر سوودی خۆتان بچنه ناوی، پاشگه&zwnj;ز مه&zwnj;بنه&zwnj;وه&zwnj;و پاشه&zwnj;کشێ مه&zwnj;که&zwnj;ن تا خه&zwnj;ساره&zwnj;تمه&zwnj;ندو زه&zwnj;ره&zwnj;رمه&zwnj;ند نه&zwnj;بن.';
$t[22] = '(که&zwnj;چی) وتیان: ئه&zwnj;ی موسا به&zwnj;ڕاستی خه&zwnj;ڵکێکی ئازاو به&zwnj;رزو دلێری تێدایه&zwnj;، بێگومان ئێمه هه&zwnj;رگیز ناچینه ناوی تا ئه&zwnj;وانی لێده&zwnj;رنه&zwnj;چن، جا ئه&zwnj;گه&zwnj;ر ئه&zwnj;وان ده&zwnj;رچوون لێی، ئه&zwnj;وه بێگومان ئێمه ده&zwnj;چینه ناوی و له&zwnj;وێ ده&zwnj;بین!!';
$t[23] = '(به&zwnj;ڵام) دوو که&zwnj;س له&zwnj;وانه&zwnj;ی که ترسی خوایان له&zwnj;دڵدا بوو و خوا نازو نیعمه&zwnj;تی ئیمان و دڵنیایی ته&zwnj;واوی پێ به&zwnj;خشیبوون وتیان: له ده&zwnj;روازه&zwnj;ی شاره&zwnj;وه بچنه سه&zwnj;ریان و (یه&zwnj;ک هێرشی گه&zwnj;وره به&zwnj;رن) هه&zwnj;ر که چوونه ئه&zwnj;و دیوه&zwnj;وه&zwnj;، ئه&zwnj;وه بێگومان ئیتر ئێوه سه&zwnj;رکه&zwnj;وتوون (دوژمنتان شکست ده&zwnj;هێنێت، جا بۆ به&zwnj;ده&zwnj;ستهێنانی ئه&zwnj;و سه&zwnj;رکه&zwnj;وتنه&zwnj;) ده&zwnj;بێت: به ته&zwnj;واویی پشت به خوا ببه&zwnj;ستن ئه&zwnj;گه&zwnj;ر ئێوه ئیماندارن.';
$t[24] = 'که&zwnj;چی وتیان: ئه&zwnj;ی موسا دڵنیابه ئێمه هه&zwnj;رگیز ناچینه ئه&zwnj;و شاره&zwnj;وه و ڕووی تێناکه&zwnj;ین ماده&zwnj;م ئه&zwnj;و جه&zwnj;نگاوه&zwnj;رانه&zwnj;ی تێدا بێت، (ئیتر تۆ که&zwnj;یفی خۆته حه&zwnj;ز ده&zwnj;که&zwnj;یت) بچۆ خۆت و په&zwnj;روه&zwnj;ردگارت جه&zwnj;نگ بکه&zwnj;ن دژیان، ئێمه به&zwnj;ڕاستی ئا لێره&zwnj;دا دانیشتووین (چاوه&zwnj;ڕێی سه&zwnj;رئه&zwnj;نجامین)!!';
$t[25] = 'ئینجا موسا وتی: په&zwnj;روه&zwnj;ردگارا به&zwnj;ڕاستی من ده&zwnj;سه&zwnj;ڵاتم نیه جگه به&zwnj;سه&zwnj;ر خۆم و براکه&zwnj;مدا نه&zwnj;بێت (ئێمه ئاماده&zwnj;ی هه&zwnj;موو فه&zwnj;رمانێکی تۆین) ئیتر داواکارین که ئێمه جیا بکه&zwnj;یته&zwnj;وه له&zwnj;و خه&zwnj;ڵکه تاوانکار و سته&zwnj;م پیشه&zwnj;یه&zwnj;...';
$t[26] = 'خوا فه&zwnj;رمووی: به&zwnj;ڕاستی ئه&zwnj;وه ئیتر شاری قدس له&zwnj;سه&zwnj;ریان حه&zwnj;رامه&zwnj;، ده&zwnj;بێت چل ساڵیش وێڵ و سه&zwnj;رگه&zwnj;ردان بن له&zwnj;سه&zwnj;ر زه&zwnj;ویدا (تا ئه&zwnj;و نه&zwnj;وه ترسنۆکه تیا بچێت و نه&zwnj;وه&zwnj;ی تازه پێبگات)، ئیتر خه&zwnj;فه&zwnj;ت مه&zwnj;خۆ بۆ گه&zwnj;ل و هۆزی تاوانبار و سته&zwnj;م پیشه&zwnj;.';
$t[27] = 'به&zwnj;سه&zwnj;رهات و هه&zwnj;واڵی دوو کوڕه&zwnj;که&zwnj;ی ئاده&zwnj;م بۆ ئه&zwnj;و خه&zwnj;ڵکه بخوێنه&zwnj;ره&zwnj;وه که&zwnj;: به&zwnj;ڕاستی ڕوویداوه&zwnj;، ئه&zwnj;وه بوو کاتێک هه&zwnj;ردووکیان قوربانییان کرد، له یه&zwnj;کێکیان وه&zwnj;رگیرا (که له&zwnj;سه&zwnj;ر حه&zwnj;ق بوو، له خوا ترس بوو) و له&zwnj;وی تریان وه&zwnj;رنه&zwnj;گیرا (چونکه ڕق ئه&zwnj;ستوورو حه&zwnj;سوودو خوانه&zwnj;ناس بوو) به&zwnj;براکه&zwnj;ی وت: هه&zwnj;ر ده&zwnj;تکوژم ئه&zwnj;ویش له وه&zwnj;ڵامی هه&zwnj;ڕه&zwnj;شه&zwnj;که&zwnj;یدا وتی: خوای گه&zwnj;وره ته&zwnj;نها له پارێزکاران قوربانی و چاکه وه&zwnj;رده&zwnj;گرێت.';
$t[28] = 'سوێند به خوا ئه&zwnj;گه&zwnj;ر تۆ ده&zwnj;ست درێژ بکه&zwnj;یت بۆ من بۆ ئه&zwnj;وه&zwnj;ی بمکوژیت، من ده&zwnj;ستدرێژ ناکه&zwnj;م بۆ تۆ بۆ ئه&zwnj;وه&zwnj;ی بتکوژم، چونکه به&zwnj;ڕاستی من له&zwnj;سزاو تۆڵه&zwnj;ی خوا، په&zwnj;روه&zwnj;ردگاری جیهانه&zwnj;کان ده&zwnj;ترسم.';
$t[29] = '(من به&zwnj;و ده&zwnj;ست درێژ نه&zwnj;کردنه&zwnj;م) به&zwnj;ڕاستی ده&zwnj;مه&zwnj;وێت به&zwnj;گوناهی من و گوناهی خۆیشته&zwnj;وه بگه&zwnj;ڕێیته&zwnj;وه&zwnj;، ئه&zwnj;وجا ده&zwnj;بیته نیشته&zwnj;جێی ناو ئاگر، هه&zwnj;ر ئه&zwnj;وه&zwnj;شه پاداشتی سته&zwnj;مکاران.';
$t[30] = 'سه&zwnj;رئه&zwnj;نجام نه&zwnj;فسی سه&zwnj;رکه&zwnj;شی هانیداو کوشتنی براکه&zwnj;ی لاساده و ئاسان کردو کوشتی، ئیتر خۆی خسته ڕیزی خه&zwnj;ساره&zwnj;تمه&zwnj;ند و زه&zwnj;ره&zwnj;رمه&zwnj;ندانه&zwnj;وه&zwnj;.';
$t[31] = 'له&zwnj;وه&zwnj;ودوا گیری خوارد به&zwnj;ده&zwnj;ست ته&zwnj;رمه&zwnj;که&zwnj;یه&zwnj;وه&zwnj;، چونکه وادیاره یه&zwnj;که&zwnj;م که&zwnj;سه کوژرا بێت و شه&zwnj;هید بوو بێت)، ئه&zwnj;وسا خوای میهره&zwnj;بان قه&zwnj;له&zwnj;ڕه&zwnj;شێکی ناردو چاڵێکی له زه&zwnj;ویدا هه&zwnj;ڵکه&zwnj;ند (بۆ قه&zwnj;له&zwnj;ره&zwnj;شێکی تری مرداره&zwnj;وه بوو) تا نیشانی بدات چۆن لاشه&zwnj;ی براکه&zwnj;ی بشارێته&zwnj;وه&zwnj;، (هه&zwnj;رکه ئه&zwnj;وه&zwnj;ی دی) وتی: هاوار له من، ئایا ئه&zwnj;وه من نه&zwnj;متوانی وه&zwnj;کو ئه&zwnj;و قه&zwnj;له&zwnj;ڕه&zwnj;شه بکه&zwnj;م و به&zwnj;قه&zwnj;ده&zwnj;ر ئه&zwnj;وم لێنه&zwnj;هات تا لاشه و عه&zwnj;یب و عاری براکه&zwnj;م بشارمه&zwnj;وه&zwnj;، ئیتر بۆ هه&zwnj;میشه چووه ڕیزی په&zwnj;شیمانانه&zwnj;وه&zwnj;.';
$t[32] = 'له&zwnj;به&zwnj;ر ئه&zwnj;وه&zwnj;ی کاره&zwnj;ساتی کوشتنی ناڕه&zwnj;وای ئاوا ڕوو نه&zwnj;دات ئێمه پێویستمان کرد له&zwnj;سه&zwnj;ر نه&zwnj;وه&zwnj;ی ئیسرائیل ئه&zwnj;وه&zwnj;ی که&zwnj;سێک به&zwnj;ناحه&zwnj;ق بکوژێت و له جیاتی کوژراوێک نه&zwnj;بێت، یان گوناهو تاوانی گه&zwnj;وره&zwnj;ی ئه&zwnj;نجام نه&zwnj;دابێت له&zwnj;زه&zwnj;ویدا (وه&zwnj;کوجه&zwnj;رده&zwnj;یی و ده&zwnj;ستدرێژی و... هتد)، ئه&zwnj;وه وه&zwnj;ک ئه&zwnj;وه وایه هه&zwnj;ر هه&zwnj;موو خه&zwnj;ڵکی کوشتبێت، ئه&zwnj;وه&zwnj;ش که&zwnj;سێک له مه&zwnj;رگ ڕزگار بکات، هه&zwnj;روه&zwnj;ک ئه&zwnj;وه وایه هه&zwnj;موو خه&zwnj;ڵکی له&zwnj;مه&zwnj;رگ ڕزگار کردبێت و ژیانی پێ به&zwnj;خشی بن، سوێند بێت به&zwnj;خوا به&zwnj;ڕاستی پێغه&zwnj;مبه&zwnj;رانمان به به&zwnj;ڵگه&zwnj;و موعجیزه&zwnj;ی ئاشکراو دیاره&zwnj;وه هاتووه بۆ لایان (تا پابه&zwnj;ندی حه&zwnj;ق و داد بن) که&zwnj;چی زۆربه&zwnj;یان دوای ئه&zwnj;و ڕێنمووییانه&zwnj;، هه&zwnj;ر زیاده&zwnj;ڕه و بوون له&zwnj;م سه&zwnj;ر زه&zwnj;مینه&zwnj;دا له سته&zwnj;م و کوشتنی ناحه&zwnj;قدا.';
$t[33] = 'بێگومان پاداشتی ئه&zwnj;وانه&zwnj;ی که ده&zwnj;نجێگن و دوژمنایه&zwnj;تی خواو پێغه&zwnj;مبه&zwnj;ره&zwnj;که&zwnj;ی ده&zwnj;که&zwnj;ن و به گه&zwnj;رمی هه&zwnj;وڵ ده&zwnj;ده&zwnj;ن بۆ بڵاوکردنه&zwnj;وه&zwnj;ی فه&zwnj;سادو به&zwnj;د ڕه&zwnj;وشتی له زه&zwnj;ویدا هه&zwnj;ر ئه&zwnj;وه&zwnj;یه&zwnj;: (ئه&zwnj;وانه&zwnj;یان که خه&zwnj;ڵکیان کوشتووه&zwnj;) ده&zwnj;بێت بکوژرێن، (ئه&zwnj;وانه&zwnj;ش که&zwnj;خه&zwnj;ڵکیان کوشتووه و جه&zwnj;رده&zwnj;ییشیان کردووه&zwnj;) ده&zwnj;بێت له خاچ بدرێن، (ئه&zwnj;وانه&zwnj;یان ته&zwnj;نها جه&zwnj;رده&zwnj;یی ده&zwnj;که&zwnj;ن) ده&zwnj;ست و قاچێکیان به&zwnj;پێچه&zwnj;وانه&zwnj;وه ببڕدرێت، (ئه&zwnj;وانه&zwnj;یان که مه&zwnj;ترسیان لێده&zwnj;کرێت) ئه&zwnj;وه دوور بخرێنه&zwnj;وه له وڵات، ئه&zwnj;وسزایه خه&zwnj;جاڵه&zwnj;تی و سه&zwnj;رشۆڕیه بۆیان له&zwnj;دنیادا، له&zwnj;قیامه&zwnj;تیشدا سزای سه&zwnj;خت و گه&zwnj;وره&zwnj;یان بۆهه&zwnj;یه&zwnj;.';
$t[34] = 'جگه له&zwnj;وانه&zwnj;یان که ته&zwnj;وبه&zwnj;یان کردووه&zwnj;، پێش ئه&zwnj;وه&zwnj;ی ده&zwnj;ستی ئێوه&zwnj;یان بگاتێ، ئه&zwnj;وه بزانن که به&zwnj;ڕاستی خوا لێخۆشبوو و میهره&zwnj;بانه (ئه&zwnj;ڵبه&zwnj;ته ئه&zwnj;گه&zwnj;ر مافی خه&zwnj;ڵکیان به&zwnj;سه&zwnj;ره&zwnj;وه نه&zwnj;بێت).';
$t[35] = 'ئه&zwnj;ی ئه&zwnj;وانه&zwnj;ی باوه&zwnj;ڕتان هێناوه له خوا بترسن و پارێزکار بن، له ڕێگه&zwnj;ی هه&zwnj;موو هۆکارو کرده&zwnj;وه&zwnj;یه&zwnj;کی چاکه&zwnj;وه هه&zwnj;وڵ بده&zwnj;ن ڕه&zwnj;زامه&zwnj;ندیی ئه&zwnj;و زاته به&zwnj;ده&zwnj;ست بهێنن، هه&zwnj;میشه هه&zwnj;وڵ و کۆشش و جیهادو خه&zwnj;بات بکه&zwnj;ن له پێناوی ڕێباز و به&zwnj;رنامه&zwnj;ی ئه&zwnj;و زاته&zwnj;دا، بۆ ئه&zwnj;وه&zwnj;ی سه&zwnj;رفراز بن (له هه&zwnj;ردوو جیهاندا).';
$t[36] = 'بێگومان ئه&zwnj;وانه&zwnj;ی بێ باوه&zwnj;ڕ بوون، ئه&zwnj;گه&zwnj;ر هه&zwnj;رچی سامانی زه&zwnj;وی هه&zwnj;یه هه&zwnj;یان بێت و ئه&zwnj;وه&zwnj;نده&zwnj;ی تریشی له&zwnj;گه&zwnj;ڵ بێت و بیانه&zwnj;وێت خۆیانی پێ ڕزگار بکه&zwnj;ن له سزای ڕۆژی قیامه&zwnj;ت، لێیان وه&zwnj;رناگیرێت، ئه&zwnj;وانه سزایه&zwnj;کی به ئێشیان بۆ ئاماده کراوه (ده&zwnj;بێت تیایدا گیر بخۆن).';
$t[37] = 'ئه&zwnj;وانه ده&zwnj;یانه&zwnj;وێت له دۆزه&zwnj;خ ده&zwnj;رچن (به&zwnj;ڵام خه&zwnj;یاڵیان خاوه&zwnj;) له&zwnj;کاتێکدا ده&zwnj;رچوونیان لێی نییه&zwnj;، ئه&zwnj;وانه سزایه&zwnj;کی به&zwnj;رده&zwnj;وامیان بۆ ئاماده&zwnj;یه&zwnj;.';
$t[38] = 'ده&zwnj;ستی پیاوی دزو ئافره&zwnj;تی دز ببڕن له پاداشتی کاری ناڕه&zwnj;وایاندا ئه&zwnj;مه&zwnj;ش تۆڵه&zwnj;یه&zwnj;که له لایه&zwnj;ن خواوه&zwnj;، خوا باڵاده&zwnj;ست و دانایه (له بڕیاردانی ئه&zwnj;و سزایه&zwnj;دا، ئه&zwnj;ڵبه&zwnj;ته ئه&zwnj;م سزایه له کۆمه&zwnj;ڵگه&zwnj;ی ئیسلامیدا جێبه&zwnj;جێ ده&zwnj;کرێت، که&zwnj;: هه&zwnj;موو که&zwnj;سێک ژیانی مسۆگه&zwnj;ره و هه&zwnj;مووان ده&zwnj;ستی یارمه&zwnj;تی بۆ یه&zwnj;کتر درێژ ده&zwnj;که&zwnj;ن، هه&zwnj;روه&zwnj;ها له ساڵانی گرانیدا ده&zwnj;ست نابڕدرێت).';
$t[39] = 'جا ئه&zwnj;وه&zwnj;ی دوای سته&zwnj;م و تاوانه&zwnj;که&zwnj;ی ته&zwnj;وبه بکات و چاکسازی له خۆیدا بکات (دڵ و ده&zwnj;روونی خاوێن بکاته&zwnj;وه&zwnj;، ماڵی خه&zwnj;ڵکی بداته&zwnj;وه و چاوی له دزێتی نه&zwnj;مێنێت)، ئه&zwnj;وه بێگومان خوا ته&zwnj;وبه&zwnj;که&zwnj;ی لێوه&zwnj;رده&zwnj;گرێت، چونکه به&zwnj;ڕاستی خوای گه&zwnj;وره لێخۆشبوو میهره&zwnj;بانه&zwnj;.';
$t[40] = '(ئه&zwnj;ی محمد (صلی الله علیه وسلم)، ئه&zwnj;ی ئیماندار) ئایا نه&zwnj;تزانیووه هه&zwnj;رچی له ئاسمانه&zwnj;کان و زه&zwnj;ویدا هه&zwnj;یه&zwnj;، به&zwnj;ڕاستی ته&zwnj;نها هه&zwnj;ر خوا خاوه&zwnj;نیانه&zwnj;، سزای هه&zwnj;ر که&zwnj;سێک که بیه&zwnj;وێت ده&zwnj;یدات (ئه&zwnj;ڵبه&zwnj;ته هه&zwnj;ڕه&zwnj;مه&zwnj;کی نیه&zwnj;، به&zwnj;ڵکو له ڕووی دادپه&zwnj;روه&zwnj;ری ته&zwnj;واوه&zwnj;وه&zwnj;)، هه&zwnj;روه&zwnj;ها خۆش ده&zwnj;بێت له&zwnj;و که&zwnj;سه&zwnj;ی که ده&zwnj;یه&zwnj;وێت و مه&zwnj;به&zwnj;ستێتی لێی خۆش ببێت، ئه&zwnj;و خوایه ده&zwnj;سه&zwnj;ڵاتی به&zwnj;سه&zwnj;ر هه&zwnj;موو شتێکدا هه&zwnj;یه (شوێنی پاداشت و سزای جێگه&zwnj;ی هه&zwnj;مووانی تێدا ده&zwnj;بێته&zwnj;وه&zwnj;).';
$t[41] = 'ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر (صلی الله علیه وسلم) با غه&zwnj;مبارت نه&zwnj;کات ئه&zwnj;وانه&zwnj;ی که به&zwnj;په&zwnj;له به&zwnj;ده&zwnj;م بێ باوه&zwnj;ڕی و بێ دینیه&zwnj;وه ده&zwnj;چن، له&zwnj;وانه&zwnj;ی که به&zwnj;ده&zwnj;م و زار ده&zwnj;ڵێن: باوه&zwnj;ڕمان هێناوه&zwnj;، به&zwnj;ڵام دڵیان باوه&zwnj;ڕی نه&zwnj;هێناوه&zwnj;، هه&zwnj;روه&zwnj;ها له&zwnj;وانه&zwnj;ش که بوونه&zwnj;ته جوله&zwnj;که و زۆر گوێگرن بۆ درۆو ده&zwnj;له&zwnj;سه&zwnj;، زۆر گوێبیستن بۆ خه&zwnj;ڵکی تریش (که خوانه&zwnj;ناسن و) نه&zwnj;هاتوون بۆ لات، قسه ده&zwnj;گۆڕن و قسه وه&zwnj;کو خۆی ناگێڕنه&zwnj;وه&zwnj;، یاخود ئاوه&zwnj;ژووی ده&zwnj;که&zwnj;ن، دوای ئه&zwnj;وه&zwnj;ی جێی خۆی گرتبوو (ئه&zwnj;وانه&zwnj;ی که خه&zwnj;ڵک ده&zwnj;نێرن بۆ لای پێغه&zwnj;مبه&zwnj;رو پێی) ده&zwnj;ڵێن: ئه&zwnj;گه&zwnj;ر ئاوا بڕیاریدا لێی وه&zwnj;ربگرن، ئه&zwnj;گه&zwnj;ر وانه&zwnj;بوو هوشیار بن، جا ئه&zwnj;وه&zwnj;ی خوای گه&zwnj;وره بیه&zwnj;وێت تووشی تاقیکردنه&zwnj;وه و سزای بکات، ئه&zwnj;وه هه&zwnj;رگیز تۆ خاوه&zwnj;نی هیچ شتێک نابیت له&zwnj;لایه&zwnj;ن خواوه بۆ ڕزگار بوونی ئه&zwnj;و، ئائه&zwnj;وانه ئه&zwnj;و که&zwnj;سانه&zwnj;ن خوا نه&zwnj;یویستووه دڵیان پاک و خاوێن بکاته&zwnj;وه (چونکه خۆشیان مه&zwnj;به&zwnj;ستیان نه&zwnj;بووه&zwnj;) ئه&zwnj;وانه له دنیادا سه&zwnj;رشۆڕیی و شه&zwnj;رمه&zwnj;زاریی به&zwnj;شیانه&zwnj;، له قیامه&zwnj;تیشدا سزای سه&zwnj;خت و گه&zwnj;وره چاوه&zwnj;ڕێیانه&zwnj;.';
$t[42] = 'ئه&zwnj;و (جوله&zwnj;کانه&zwnj;) زۆر به دڵ گه&zwnj;رمی گوێ بۆ درۆ ده&zwnj;گرن و که&zwnj;سایه&zwnj;تیان ناپوخته&zwnj;و زۆر حه&zwnj;رام خۆر و سوو خۆرن، جا ئه&zwnj;گه&zwnj;ر کێشه&zwnj;یه&zwnj;کیان هه&zwnj;بوو، هاتن بۆ لات (تۆ سه&zwnj;ربه&zwnj;ستی) داوه&zwnj;ری بکه&zwnj;یت له&zwnj;نێوانیاندا یان گوێیان پێنه&zwnj;ده&zwnj;یت، خۆ ئه&zwnj;گه&zwnj;ر گوێیان نه&zwnj;ده&zwnj;یتێ، ئه&zwnj;وه هه&zwnj;رگیز ناتوانن هیچ جۆره زیانێکت پێبگه&zwnj;یه&zwnj;نن، خۆ ئه&zwnj;گه&zwnj;ر دادوه&zwnj;ریت کرد له نێوانیاندا دادپه&zwnj;روه&zwnj;رانه ئه&zwnj;نجامی بده&zwnj;، چونکه به&zwnj;ڕاستی خوا دا&zwnj;دپه&zwnj;روه&zwnj;رو ماف په&zwnj;روه&zwnj;رانی خۆش ده&zwnj;وێت.';
$t[43] = '(سه&zwnj;یره&zwnj;)! ئه&zwnj;وانه چۆن دێن و تۆ ده&zwnj;که&zwnj;ن به دادوه&zwnj;ر، له&zwnj;کاتێکدا خۆیان ته&zwnj;وراتیان لایه&zwnj;و فه&zwnj;رمانی خوا به&zwnj;روونی تیادایه&zwnj;، پاشان پشتی تێده&zwnj;که&zwnj;ن دوای ئه&zwnj;وه&zwnj;ی که (حوکم و فه&zwnj;رمانی خوایان بۆ ده&zwnj;رده&zwnj;که&zwnj;وێت) دیاره ئه&zwnj;وانه هه&zwnj;ر ئیماندار نین (له مه&zwnj;دینه&zwnj;دا یه&zwnj;کێک له ناودارانی جوو زینای کردبوو، حوکمی زیناکه&zwnj;رێک هاوسه&zwnj;ری هه&zwnj;بێت له ته&zwnj;وراتدا به&zwnj;رد باران کردنه&zwnj;، هاتن بۆ خزمه&zwnj;تی پێغه&zwnj;مبه&zwnj;ر (صلی الله علیه وسلم)، تا به&zwnj;ڵکو حوکمه&zwnj;که&zwnj;ی له&zwnj;قورئاندا ئاسانتر بێت، به&zwnj;مه&zwnj;رجێک باوه&zwnj;ڕیان به پێغه&zwnj;مبه&zwnj;رو به قورئان نه&zwnj;بوو، کاتێک پێغه&zwnj;مبه&zwnj;ر گێڕانیه&zwnj;وه بۆ لای حوکمی ته&zwnj;ورات پشتیان هه&zwnj;ڵکرد و به&zwnj;دڵیان نه&zwnj;بوو...)';
$t[44] = 'به&zwnj;ڕاستی ئێمه کاتێک ته&zwnj;وراتمان نارده خواره&zwnj;وه ڕێنموویی تیادا بوو (بۆ هه&zwnj;موو خێرو چاکه&zwnj;یه&zwnj;ک)، نوور و ڕووناکیش بوو (له&zwnj;ناو تاریکایی یه&zwnj;کاندا)، ئه&zwnj;و پێغه&zwnj;مبه&zwnj;رانه&zwnj;ی که هه&zwnj;میشه ملکه&zwnj;چ و فه&zwnj;رمانبه&zwnj;ردار بوون حوکمی ئه&zwnj;وانه&zwnj;یان پێده&zwnj;کرد که ببونه جوله&zwnj;که&zwnj;، هه&zwnj;روه&zwnj;ها زانا په&zwnj;روه&zwnj;ردگار ناسه&zwnj;کانیان و زانا شه&zwnj;رع ناسه&zwnj;کانیشیان، حوکمیان ده&zwnj;کرد به&zwnj;وه&zwnj;ی داوایان لێکرابوو بیپارێزن، که ئه&zwnj;ویش کتێبی خوا (ته&zwnj;وراته&zwnj;)، هه&zwnj;روه&zwnj;ها شایه&zwnj;تیش بوون له&zwnj;سه&zwnj;ری (که&zwnj;واته ئه&zwnj;ی زانایانی جوله&zwnj;که&zwnj;، ئه&zwnj;ی ئیمانداران) له خه&zwnj;ڵکی مه&zwnj;ترسن به&zwnj;ڵکو ته&zwnj;نها له من بترسن، ئایه&zwnj;ت و فه&zwnj;رمانه&zwnj;کانی من به&zwnj;نرخێکی که&zwnj;م مه&zwnj;فرۆشن (ئه&zwnj;گه&zwnj;ر نرخه&zwnj;که هه&zwnj;موو دنیاش بێت هه&zwnj;ر که&zwnj;مه&zwnj;) جا ئه&zwnj;وه&zwnj;ی حوکم و داوه&zwnj;ری به&zwnj;و به&zwnj;رنامه&zwnj;یه نه&zwnj;کات که خوا دایبه&zwnj;زاندووه&zwnj;، ئائه&zwnj;وانه بێ باوه&zwnj;ڕن.';
$t[45] = 'له ته&zwnj;وراتدا پێویستمان کردووه له&zwnj;سه&zwnj;ریان که به&zwnj;ڕاستی هه&zwnj;ر که&zwnj;سێک که&zwnj;سێک بکوژێت ده&zwnj;بێت بکوژرێته&zwnj;وه و چاوی یه&zwnj;کێک کوێر بکات، ده&zwnj;بێت چاوی کوێر بکرێته&zwnj;وه و لووتی یه&zwnj;کێک عه&zwnj;یبدار بکات ده&zwnj;بێت لووتی عه&zwnj;یبدار بکرێته&zwnj;وه و گوێی یه&zwnj;کێک ببڕێت ده&zwnj;بێت گوێی ببڕدرێته&zwnj;وه و ددانی یه&zwnj;کێک بشکێنێت ده&zwnj;بێت ددانی بشکێنرێته&zwnj;وه&zwnj;و برین و زامه&zwnj;کانیش هه&zwnj;روه&zwnj;ها تۆڵه&zwnj;یان به&zwnj;گوێره&zwnj;ی خۆی بۆ ده&zwnj;سه&zwnj;نرێته&zwnj;وه&zwnj;، جائه&zwnj;وه&zwnj;ی له تاوانبار خۆش ببێت، ئه&zwnj;و چاوپۆشی و لێخۆشبوونه ده&zwnj;بێته که&zwnj;فاره&zwnj;تی گوناهانی، بێگومان ئه&zwnj;وه&zwnj;ی حوکم و داوه&zwnj;ری نه&zwnj;کات به&zwnj;و به&zwnj;رنامه&zwnj;یه&zwnj;ی که خوا دایبه&zwnj;زاندوه&zwnj;، ئائه&zwnj;وانه هه&zwnj;ر سته&zwnj;مکارن.';
$t[46] = 'ئینجا به&zwnj;شوێن ئاسه&zwnj;واری هه&zwnj;موو پێغه&zwnj;مبه&zwnj;رانی (نه&zwnj;وه&zwnj;ی ئیسرائیلدا) عیسای کوڕی مه&zwnj;ریه&zwnj;ممان هێنا، که ڕاستی ته&zwnj;وراتی ده&zwnj;رده&zwnj;خست و پشتیوانی لێده&zwnj;کردو ئینجیلیشمان پێدا، که هیدایه&zwnj;ت و نوور و ڕووناکی تێدایه و پشتگیریی ته&zwnj;وراتی پێش خۆیه&zwnj;تی، هیدایه&zwnj;ت و ڕێنموویی و ئامۆژگاریه بۆ ئه&zwnj;و که&zwnj;سانه&zwnj;ی که له خوا ده&zwnj;ترسن و پارێزکارن.';
$t[47] = 'ده&zwnj;با شوێنکه&zwnj;وتووانی ئینجیل به&zwnj;و ڕێنموویی و ئامۆژگاریانه هه&zwnj;ڵسوکه&zwnj;وت بکه&zwnj;ن که خوا له ئینجیلدا دایبه&zwnj;زاندوه (گرنگترینیان شوێنکه&zwnj;وتنی ئاینی ئیسلام و پێغه&zwnj;مبه&zwnj;ری ئیسلامه&zwnj;) ئه&zwnj;وه&zwnj;ی حوکم و داوه&zwnj;ری نه&zwnj;کات به&zwnj;و به&zwnj;رنامه&zwnj;یه&zwnj;ی که خوا دایبه&zwnj;زاندوه&zwnj;، جا ئا ئه&zwnj;وانه ناپاک و تاوانبارو له سنوور ده&zwnj;رچوون.';
$t[48] = 'ئێمه قورئانمان هاوڕێ له&zwnj;گه&zwnj;ڵ هه&zwnj;موو حه&zwnj;قیقه&zwnj;ت و ڕاستیه&zwnj;کدا بۆ تۆ دابه&zwnj;زاندووه&zwnj;، ڕاستی و دروستی کتێبه&zwnj;کانی پێش خۆیشی دیاری ده&zwnj;کات و چاودێره به&zwnj;سه&zwnj;ر هه&zwnj;موویاندا (کاتێک خاوه&zwnj;نانی کتێب له گاورو جوو گیروگرفتیان ده&zwnj;هێنن بۆ لای تۆ) به&zwnj;و به&zwnj;رنامه&zwnj;یه&zwnj;ی خوا ناردوویه&zwnj;تی داوه&zwnj;ریی بکه له نێوانیانداو شوێن ئاره&zwnj;زووه&zwnj;کانی ئه&zwnj;وان مه&zwnj;که&zwnj;وه&zwnj;و له&zwnj;و حه&zwnj;قه لامه&zwnj;ده که بۆت ڕه&zwnj;وانه کراوه&zwnj;، بۆ هه&zwnj;رلایه&zwnj;کتان (موسڵمانان و خاوه&zwnj;نانی کتێب) به&zwnj;رنامه و پڕۆگرامی تایبه&zwnj;تمان بڕیارداوه خۆ ئه&zwnj;گه&zwnj;ر خوا بیویستایه هه&zwnj;ر هه&zwnj;مووتانی ده&zwnj;کرده یه&zwnj;ک ئوممه&zwnj;ت و گه&zwnj;لێکی یه&zwnj;ک پارچه&zwnj;، به&zwnj;ڵام (ویستی وایه به&zwnj;و جیاوازیه&zwnj;) تاقیتان بکاته&zwnj;وه له&zwnj;وه&zwnj;ی پێی به&zwnj;خشیوون (تا به&zwnj;هۆی عه&zwnj;قڵ و ژیریتانه&zwnj;وه شوێنی پێغه&zwnj;مبه&zwnj;ری ئیسلام بکه&zwnj;ون)، ده&zwnj;ی که&zwnj;واته زۆر به&zwnj;په&zwnj;له&zwnj;بن و پێشبڕکێ بکه&zwnj;ن بۆ هه&zwnj;موو خێرو چاکه&zwnj;یه&zwnj;ک، دڵنیاش بن که هه&zwnj;مووتان ده&zwnj;گه&zwnj;ڕێنه&zwnj;وه بۆ لای خوا، ئه&zwnj;و کاته هه&zwnj;واڵی ته&zwnj;واوتان ده&zwnj;داتێ ده&zwnj;رباره&zwnj;ی ئه&zwnj;و شتانه&zwnj;ی که کێشه&zwnj;تان له&zwnj;سه&zwnj;ری هه&zwnj;بوو.';
$t[49] = '(کاتێک هه&zwnj;ندێک له زاناکانی جوو، هاتن بۆ لات تا داوه&zwnj;ریی بکه&zwnj;ی له نێوانیاندا)، داوه&zwnj;ری بکه به&zwnj;و به&zwnj;رنامه&zwnj;یه (قورئانه&zwnj;)ی که له&zwnj;لایه&zwnj;ن خواوه بۆت دابه&zwnj;زیووه&zwnj;، شوێنی ئاره&zwnj;زووه&zwnj;کانی ئه&zwnj;وان مه&zwnj;که&zwnj;وه&zwnj;، وریابه نه&zwnj;وه&zwnj;کو ئه&zwnj;وان ڕێت لێ هه&zwnj;ڵه بکه&zwnj;ن له هه&zwnj;ندێک له&zwnj;و ڕێنمووییانه&zwnj;ی که خوا بۆی ناردوویت (جا ئه&zwnj;گه&zwnj;ر ئه&zwnj;و جوله&zwnj;کانه ڕازیی نه&zwnj;بوون) و پشتیان هه&zwnj;ڵکرد، ئه&zwnj;وه بزانه به&zwnj;ڕاستی خوا ده&zwnj;یه&zwnj;وێت گیرۆده&zwnj;ی سه&zwnj;رئه&zwnj;نجامی هه&zwnj;ندێک له گوناهه&zwnj;کانی خۆیانیان بکات، بێگومان زۆربه&zwnj;ی خه&zwnj;ڵکی یاخی و سه&zwnj;رکه&zwnj;ش و تاوانبارن.';
$t[50] = 'ئایا ئه&zwnj;وانه حوکم و فه&zwnj;رمانڕه&zwnj;وایی جاهیلیه&zwnj;ت و نه&zwnj;فامیان ده&zwnj;وێت و ئه&zwnj;ویان لاپه&zwnj;سه&zwnj;نده&zwnj;؟! (ئاخر بۆ بیرناکه&zwnj;نه&zwnj;وه&zwnj;) کێ هه&zwnj;یه به&zwnj;قه&zwnj;ده&zwnj;ر خوا حوکمی جوان و چاک و به&zwnj;جێ بێت، (به&zwnj;تایبه&zwnj;ت) لای ئه&zwnj;و که&zwnj;سانه&zwnj;ی که به&zwnj;وردیی سه&zwnj;رنج ده&zwnj;ده&zwnj;ن و ویژدان و هه&zwnj;ستی زیندوویان هه&zwnj;یه&zwnj;.';
$t[51] = 'ئه&zwnj;ی ئه&zwnj;وانه&zwnj;ی باوه&zwnj;ڕتان هێناوه جوله&zwnj;که&zwnj;و گاور مه&zwnj;که&zwnj;نه یارو یاوه&zwnj;رو پشتیوانی خۆتان ئه&zwnj;وانه هه&zwnj;ندێکیان پشتیوان و پشتگیری یه&zwnj;کترن، ئه&zwnj;و که&zwnj;سه&zwnj;ی پشت به&zwnj;وان ببه&zwnj;ستێت، ئه&zwnj;وه هه&zwnj;ر له&zwnj;وانه و (حسابی ئه&zwnj;وانی بۆ ده&zwnj;کرێت) به&zwnj;ڕاستی خوا هیدایه&zwnj;تی قه&zwnj;ومی سته&zwnj;مکار نادات...';
$t[52] = 'به ڕوونی ده&zwnj;بینی ئه&zwnj;وانه&zwnj;ی دڵیان نه&zwnj;خۆشه و (ئیمان لاوازن) به&zwnj;په&zwnj;له ده&zwnj;چن بۆ لایان (به زمانی حاڵ) ده&zwnj;ڵێن: ده&zwnj;ترسین به&zwnj;ڵایه&zwnj;کمان تووش بێت یان کاره&zwnj;ساتێکمان به&zwnj;سه&zwnj;ر بێت، جا دوور نیه و له&zwnj;وانه&zwnj;یه به&zwnj;م نزیکانه خوا سه&zwnj;رکه&zwnj;وتن ببه&zwnj;خشێت (به ئیمانداران) یاخود به&zwnj;شێوه&zwnj;یه&zwnj;&zwnj;کی یه&zwnj;کسه&zwnj;ریی به موعجیزه&zwnj;یه&zwnj;ک له&zwnj;لایه&zwnj;ن خۆیه&zwnj;وه (سه&zwnj;رکه&zwnj;وتنیان پێببه&zwnj;خشێت) ئه&zwnj;وکاته باوه&zwnj;ڕلاوازان په&zwnj;شیمان ده&zwnj;بنه&zwnj;وه له&zwnj;سه&zwnj;ر ئه&zwnj;و نهێنی و خه&zwnj;ته&zwnj;ره و خه&zwnj;یاڵه&zwnj;ی له دڵ و ده&zwnj;روونیاندا شاردبوویانه&zwnj;وه&zwnj;.';
$t[53] = 'ئه&zwnj;وسا ئه&zwnj;وانه&zwnj;ی ئیمانیان هێناوه (له&zwnj;ناو خۆیاندا به&zwnj;سه&zwnj;رسامیه&zwnj;وه&zwnj;) ده&zwnj;ڵێن: ئایا ئه&zwnj;و دووڕووانه ئه&zwnj;وانه&zwnj;ن که سوێندیان به خوا ده&zwnj;خوارد و جه&zwnj;ختیان له&zwnj;سه&zwnj;ر ده&zwnj;کرد که گوایه به&zwnj;ڕاستی له&zwnj;گه&zwnj;ڵتانداین؟! که&zwnj;چی کاره&zwnj;کانیان پووچه&zwnj;ڵ بوه&zwnj;وه&zwnj;و خه&zwnj;ساره&zwnj;تمه&zwnj;ند و زه&zwnj;ره&zwnj;رمه&zwnj;ند و مایه پووچ بوون.';
$t[54] = 'ئه&zwnj;ی ئه&zwnj;وانه&zwnj;ی باوه&zwnj;ڕتان هێناوه&zwnj;، هه&zwnj;ر که&zwnj;س له ئێوه له ئاین و به&zwnj;رنامه&zwnj;که&zwnj;ی وه&zwnj;رگه&zwnj;ڕێت و پاشگه&zwnj;ز ببێته&zwnj;وه&zwnj;، ئه&zwnj;وه (هه&zwnj;ر خۆی زه&zwnj;ره&zwnj;ر ده&zwnj;کات) ئه&zwnj;وه له ئاینده&zwnj;دا خوا که&zwnj;سانێک ده&zwnj;هێنێته کایه&zwnj;وه (ئه&zwnj;وه&zwnj;نده ڕێک و پێک و باوه&zwnj;ڕ دامه&zwnj;زراون) په&zwnj;روه&zwnj;ردگاری مه&zwnj;زن خۆشی ده&zwnj;وێن، ئه&zwnj;وانیش ئه&zwnj;ویان خۆش ده&zwnj;وێت، هه&zwnj;روه&zwnj;ها ملکه&zwnj;چ و فه&zwnj;رمانبه&zwnj;ردارو به&zwnj;سۆزن بۆ ئیمانداران و سه&zwnj;ربڵندو نه&zwnj;خۆرو ده&zwnj;روون به&zwnj;رزن به&zwnj;سه&zwnj;ر بێ باوه&zwnj;ڕاندا، له پێناو ڕێبازی خواداو بۆ به&zwnj;ده&zwnj;ستهێنانی ڕه&zwnj;زامه&zwnj;ندی ئه&zwnj;و هه&zwnj;وڵ ده&zwnj;ده&zwnj;ن و خه&zwnj;بات ده&zwnj;که&zwnj;ن و تێده&zwnj;کۆشن، له لۆمه&zwnj;ی لۆمه&zwnj;که&zwnj;رانیش ناترسن (ئه&zwnj;م خۆشه&zwnj;ویستی و هه&zwnj;ڵبژاردن و سۆز و تێکۆشانه&zwnj;) فه&zwnj;زڵ و ڕێزی تایبه&zwnj;تی خوایه که ده&zwnj;یبه&zwnj;خشێت به هه&zwnj;ر که&zwnj;س که بیه&zwnj;وێت و (شایسته بێت) و خوا فراوانگیرو زانایه (به&zwnj;نهێنی و ئاشکرا، به&zwnj;شایسته&zwnj;یی و ناشایسته&zwnj;یی هه&zwnj;ر که&zwnj;س و هه&zwnj;ر لایه&zwnj;نێک).';
$t[55] = 'به&zwnj;ڕاستی ته&zwnj;نها خۆشه&zwnj;ویست و پشتیوانی ئێوه&zwnj;، هه&zwnj;ر خواو پێغه&zwnj;مبه&zwnj;ره&zwnj;که&zwnj;ی و ئه&zwnj;وانه&zwnj;ن باوه&zwnj;ڕیان هێناوه&zwnj;، که نوێژه&zwnj;کانیان به&zwnj;چاکی ئه&zwnj;نجام ده&zwnj;ده&zwnj;ن و زه&zwnj;کات له ماڵ و سامانیان ده&zwnj;رده&zwnj;که&zwnj;ن و هه&zwnj;میشه دیارده&zwnj;ی کڕنوش و ملکه&zwnj;چییان بۆ په&zwnj;روه&zwnj;ردگار پێوه دیاره&zwnj;.';
$t[56] = 'جا ئه&zwnj;وه&zwnj;ی خواو پێغه&zwnj;مبه&zwnj;رو ئه&zwnj;وانه&zwnj;ی بڕوایان هێناوه به&zwnj;پشتیوان و خۆشه&zwnj;ویستی خۆی قه&zwnj;بوڵ بکات، ئه&zwnj;وه با دڵنیا بێت که هه&zwnj;ر کۆمه&zwnj;ڵ و ده&zwnj;سته&zwnj;و تاقمی خوا، زاڵ و سه&zwnj;رکه&zwnj;وتوون.';
$t[57] = 'ئه&zwnj;ی ئه&zwnj;وانه&zwnj;ی باوه&zwnj;ڕتان هێناوه&zwnj;: نه&zwnj;که&zwnj;ن ئه&zwnj;وانه بکه&zwnj;نه پشتیوان و جێگه&zwnj;ی متمانه&zwnj;ی خۆتان که گاڵته&zwnj;و گه&zwnj;مه&zwnj;یان به ئاینه&zwnj;که&zwnj;تان کردووه&zwnj;، چ له خاوه&zwnj;نانی کتێبی پێش خۆتان، چ له بێ باوه&zwnj;ڕان، له خواش بترسن و پارێزکاربن ئه&zwnj;گه&zwnj;ر ئێوه باوه&zwnj;ڕدارن.';
$t[58] = 'کاتێک ئێوه بانگ ده&zwnj;ده&zwnj;ن بۆ نوێژ، بانگ و نوێژه&zwnj;که&zwnj;تان ده&zwnj;که&zwnj;نه گه&zwnj;مه&zwnj;وگاڵته&zwnj;، چونکه ئه&zwnj;وانه که&zwnj;سانێکن به&zwnj;ڕاستی ژیر نابن و نافامن.';
$t[59] = '(ئه&zwnj;ی محمد (صلی الله علیه وسلم) پێیان) بڵێ: ئه&zwnj;ی خاوه&zwnj;نانی کتێب ئه&zwnj;وه به چی عه&zwnj;یبدار و له&zwnj;که&zwnj;دارمان ده&zwnj;که&zwnj;ن و لێمان توڕه ده&zwnj;بن، جگه له&zwnj;به&zwnj;ر ئه&zwnj;وه نه&zwnj;بێت که ئێمه بڕوای ڕاست و پته&zwnj;ومان هێناوه به&zwnj;خواو به&zwnj;و قورئانه&zwnj;ی که دابه&zwnj;زیووه&zwnj;ته خواره&zwnj;وه بۆ سه&zwnj;ر ئێمه و به&zwnj;وه&zwnj;ی که پێشتر دابه&zwnj;زیووه&zwnj;؟ یان ئه&zwnj;وه&zwnj;یه که زۆربه&zwnj;تان یاخی و سه&zwnj;ره&zwnj;ڕۆ و تاوانبارن.';
$t[60] = 'پێیان بڵێ: ئایا هه&zwnj;واڵتان بده&zwnj;مێ به کردارێک که تۆڵه&zwnj;که&zwnj;ی لای خوا خراپتره له تۆڵه&zwnj;ی له&zwnj;که&zwnj;دارکردنی موسڵمانان، ئه&zwnj;وه&zwnj;ش کرداری ئه&zwnj;و که&zwnj;سه&zwnj;یه که خوا نه&zwnj;فرینی لێکردوه و جامی خه&zwnj;شم و ڕقی خۆی ڕژاندووه به&zwnj;سه&zwnj;ریداو هه&zwnj;ندێکیشی کردن به مه&zwnj;یمون و هه&zwnj;ندێکیشیان به به&zwnj;راز و هه&zwnj;ندێکیانیشی کرده تاغوت په&zwnj;رست و بت په&zwnj;رست؟! ئا ئه&zwnj;وانه خراپترین جێگه و ڕێگه&zwnj;یان هه&zwnj;یه و گومڕاترین که&zwnj;سن له ڕێبازی ڕاست و دروست.';
$t[61] = 'ئه&zwnj;وانه کاتێک دێن بۆ لاتان (به درۆوه ده&zwnj;ڵێن) ئێمه&zwnj;ش موسوڵمانین و باوه&zwnj;ڕمان هێناوه&zwnj;، له کاتێکدا به&zwnj;ڕاستی ئه&zwnj;وانه به بێ باوه&zwnj;ڕیه&zwnj;وه هاتبوونه ژووره&zwnj;وه و بێگومان هه&zwnj;ر به&zwnj;بێ باوه&zwnj;ڕیشه&zwnj;وه چوونه ده&zwnj;ره&zwnj;وه&zwnj;، جا خوا خۆی چاک ده&zwnj;زانێت که چیان حه&zwnj;شارداوه له دڵ و ده&zwnj;روونیاندا.';
$t[62] = 'زۆرێکیش له&zwnj;وانه ده&zwnj;بینیت به&zwnj;په&zwnj;له&zwnj;و به&zwnj;هه&zwnj;ڵپه&zwnj;ن له زه&zwnj;لکاوی تاوانی درۆو عه&zwnj;یبدارکردنی موسوڵمانان و ده&zwnj;ستدرێژیی ناحه&zwnj;ق و خواردنی حه&zwnj;رامیان، سوێند بێت به&zwnj;خوا ئه&zwnj;وه&zwnj;ی که ده&zwnj;یکه&zwnj;ن زۆر خراپ و نادروسته&zwnj;.';
$t[63] = 'ئایا ئه&zwnj;وه تاکه&zwnj;ی خواناس و زاناکانیان قه&zwnj;ده&zwnj;غه&zwnj;ی گوفتاری گوناهو حه&zwnj;رام خۆرییان لێناکه&zwnj;ن؟ سوێند بێت ئه&zwnj;و کرده&zwnj;وه&zwnj;و هه&zwnj;ڵس و که&zwnj;وته&zwnj;ی که ده&zwnj;یکه&zwnj;ن بۆ ڕازی کردنی خه&zwnj;ڵک زۆر نادروست و خراپ و ناپه&zwnj;سه&zwnj;نده&zwnj;.';
$t[64] = 'ئه&zwnj;و جوله&zwnj;که (نه&zwnj;فام و خوانه&zwnj;ناسانه&zwnj;) ده&zwnj;یانوت: خوا ده&zwnj;ستی ده&zwnj;ستی به&zwnj;ستراوه و خێری له&zwnj;ده&zwnj;ست نابێته&zwnj;وه&zwnj;!! ده&zwnj;ک ده&zwnj;ستیان به&zwnj;ستراو و بێ خێرو که&zwnj;له&zwnj;پچه و تووشی شه&zwnj;له&zwnj;ل بێت، ده&zwnj;ک نه&zwnj;فرینیان لێ بێت، بۆ خۆیان و قسه&zwnj;یان، نه&zwnj;خێر، وانیه به&zwnj;ڵکو دوو ده&zwnj;سته&zwnj;ی (پڕ له به&zwnj;ره&zwnj;که&zwnj;تی په&zwnj;روه&zwnj;ردگار) هه&zwnj;میشه و به&zwnj;رده&zwnj;وام کراوه&zwnj;یه&zwnj;، چۆنی بوێت و چه&zwnj;ندی بوێت ده&zwnj;یبه&zwnj;خشێت! سوێند بێت زۆربه&zwnj;یان ئه&zwnj;وه&zwnj;ی نێردراوه&zwnj;ته خواره&zwnj;وه بۆت که قورئانه له&zwnj;لایه&zwnj;ن په&zwnj;روه&zwnj;ردگارته&zwnj;وه یاخی بوون و سته&zwnj;میان زیاد ده&zwnj;کات و نغرۆیان ده&zwnj;کات له کوفردا، ئێمه&zwnj;ش هه&zwnj;تا ڕۆژی قیامه&zwnj;ت دوژمنایه&zwnj;تی و ڕق و کینه&zwnj;مان له&zwnj;نێوانیاندا به&zwnj;رپاکردووه&zwnj;، هه&zwnj;ر کاتێک ده&zwnj;یانه&zwnj;وێت جه&zwnj;نگ به&zwnj;رپا بکه&zwnj;ن و ئاگری شه&zwnj;ڕ هه&zwnj;ڵبگیرسێنن خوا ده&zwnj;یکوژێنێته&zwnj;وه و نایه&zwnj;ڵێت ته&zwnj;شه&zwnj;نه بکات، ئه&zwnj;وانه زۆر به&zwnj;په&zwnj;له و به&zwnj;گه&zwnj;رمی هه&zwnj;وڵی بڵاو بوونه&zwnj;وه&zwnj;ی فه&zwnj;سادو تاوان ده&zwnj;ده&zwnj;ن له زه&zwnj;ویدا، خوایش ئه&zwnj;و که&zwnj;سانه&zwnj;ی خۆش ناوێت که تۆوی خراپه ده&zwnj;چێنن.';
$t[65] = 'خۆ ئه&zwnj;گه&zwnj;ر به&zwnj;ڕاستی خاوه&zwnj;نانی کتێب باوه&zwnj;ڕیان بهێنایه&zwnj;و پارێزکار بوونایه&zwnj;، ئه&zwnj;وه چاوپۆشیمان له خراپه&zwnj;کانیان ده&zwnj;کردو لێیان خۆش ده&zwnj;بووین و ده&zwnj;مانخستنه ناو به&zwnj;هه&zwnj;شتی پڕ له نازو نیعمه&zwnj;ته&zwnj;وه&zwnj;.';
$t[66] = 'خۆ ئه&zwnj;گه&zwnj;ر به&zwnj;ڕاستی هه&zwnj;ستانایه به&zwnj;په&zwnj;یڕه&zwnj;و و پیاده کردنی ته&zwnj;ورات و ئینجیل (که مژده&zwnj;ی زۆری تیایه ده&zwnj;رباره&zwnj;ی ئیسلام و محمد (صلی الله علیه وسلم)و ئه&zwnj;و قورئانه&zwnj;ش که له&zwnj;لایه&zwnj;ن په&zwnj;روه&zwnj;ردگاریانه&zwnj;وه بۆیان هاتووه&zwnj;ته خواره&zwnj;وه (ئه&zwnj;و کاته به&zwnj;رووبوومی دره&zwnj;خته&zwnj;کان زۆر و زه&zwnj;به&zwnj;نده ده&zwnj;که&zwnj;ین و به&zwnj;ره&zwnj;که&zwnj;ت ده&zwnj;خه&zwnj;ینه جیهانی دانه&zwnj;وێڵه&zwnj;و سه&zwnj;وزه و خۆراکه&zwnj;کانی تره&zwnj;وه&zwnj;)، ئه&zwnj;و کاته خۆراکیان ده&zwnj;خوارد له ڕا سه&zwnj;رو له&zwnj;به&zwnj;ر پێیانه&zwnj;وه&zwnj;، ده&zwnj;سته&zwnj;یه&zwnj;ک له&zwnj;وانه که&zwnj;سانێکی ژیرو دادپه&zwnj;روه&zwnj;ر و میانه&zwnj;ڕه&zwnj;و به ویژدانن، که&zwnj;چی زۆربه&zwnj;شیان ئه&zwnj;و کاروکرده&zwnj;وه&zwnj;ی که ده&zwnj;یکه&zwnj;ن زۆر نادروست و نابه&zwnj;جێ یه&zwnj;.';
$t[67] = 'ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;رو فرستاده&zwnj;، هه&zwnj;رچی فه&zwnj;رمان و په&zwnj;یامێکت بۆ دابه&zwnj;زیووه له&zwnj;لایه&zwnj;ن په&zwnj;روه&zwnj;ردگارته&zwnj;وه (بێ دوودڵی، بێ ماتڵی) ڕای بگه&zwnj;یه&zwnj;نه&zwnj;، خۆ ئه&zwnj;گه&zwnj;ر به&zwnj;و کاره هه&zwnj;ڵنه&zwnj;سایت، ئه&zwnj;وه مانای وایه په&zwnj;یامه&zwnj;که&zwnj;یت (به&zwnj;ته&zwnj;واویی) نه&zwnj;گه&zwnj;یاندووه (دڵنیاش به که&zwnj;) خوا ده&zwnj;تپارێزێت له شه&zwnj;ڕ و خراپه&zwnj;ی خه&zwnj;ڵکی، چونکه به&zwnj;ڕاستی خوا هیدایه&zwnj;ت و ڕێنموویی قه&zwnj;وم و هۆزی بێ باوه&zwnj;ڕان نادات.';
$t[68] = '(ئه&zwnj;ی محمد (صلی الله علیه وسلم) بڵێ: ئه&zwnj;ی خاوه&zwnj;نانی کتێب، ئێوه له&zwnj;سه&zwnj;ر هیچ به&zwnj;رنامه&zwnj;و په&zwnj;یڕه&zwnj;وو پڕۆگرامێک نین، هه&zwnj;تا به&zwnj;چاکی په&zwnj;یڕه&zwnj;و و پیاده&zwnj;ی ته&zwnj;ورات و ئینجیل و ئه&zwnj;وه&zwnj;ی (له&zwnj;م دواییه&zwnj;دا، که قورئانه&zwnj;) نه&zwnj;که&zwnj;ن که له&zwnj;لایه&zwnj;ن په&zwnj;روه&zwnj;ردگارتانه&zwnj;وه بۆتان دابه&zwnj;زیووه&zwnj;، سوێند بێت به&zwnj;خوا زۆربه&zwnj;یان ئه&zwnj;وه&zwnj;ی نێردراوه&zwnj;ته خواره&zwnj;وه بۆت له لایه&zwnj;ن په&zwnj;روه&zwnj;ردگارته&zwnj;وه یاخی بوون و سته&zwnj;میان زیاد ده&zwnj;کات و نغرۆیان ده&zwnj;کات له بێ باوه&zwnj;ڕیدا (که&zwnj;چی له&zwnj;جیاتی گوێڕایه&zwnj;ڵی و ملکه&zwnj;چیی زۆربه&zwnj;یان هه&zwnj;وڵ ده&zwnj;ده&zwnj;ن شت هه&zwnj;ڵبه&zwnj;ستن بۆ ئه&zwnj;و قورئانه&zwnj;ی که بۆمان ڕه&zwnj;وانه کردوویت)، جا ئیتر خه&zwnj;فه&zwnj;ت و په&zwnj;ژاره دات نه&zwnj;گرێت بۆ ئه&zwnj;و گه&zwnj;له بێ باوه&zwnj;ڕه&zwnj;، (کاتێک: باوه&zwnj;ڕ ناهێنن، کاتێک تۆڵه&zwnj;یان لێ ده&zwnj;که&zwnj;ینه&zwnj;وه له دنیادا پێش قیامه&zwnj;ت).';
$t[69] = 'به&zwnj;ڕاستی ئه&zwnj;وانه&zwnj;ی که ئیمان و باوه&zwnj;ڕیان هێناوه (له ئوممه&zwnj;تی محمد (صلی الله علیه وسلم) و ئه&zwnj;وانه&zwnj;ی که بوون به جوو و ئه&zwnj;وانه&zwnj;ی صابئه&zwnj;ن (فریشته په&zwnj;رست و ئه&zwnj;ستێره په&zwnj;رستن) و گاوره&zwnj;کان، هه&zwnj;ر یه&zwnj;ک له&zwnj;و ده&zwnj;سته&zwnj;و تاقمانه ئه&zwnj;گه&zwnj;ر باوه&zwnj;ڕی دامه&zwnj;زراو به&zwnj;خواو به ڕۆژی دوایی بهێنێت، و کارو کرده&zwnj;وه&zwnj;ی چاک و دروست و به&zwnj;سوود ئه&zwnj;نجام بدات، ئه&zwnj;وانه نه&zwnj;ترس و بیم یه&zwnj;خه&zwnj;یان ده&zwnj;گرێت، نه&zwnj;خه&zwnj;م و په&zwnj;ژاره ڕوویان تێده&zwnj;کات (کاتێک له&zwnj;به&zwnj;ر ڕۆشنایی قورئانه&zwnj;وه ئه&zwnj;و باوه&zwnj;ڕه ده&zwnj;هێنن).';
$t[70] = 'سوێند بێت به&zwnj;خوا بێگومان ئێمه په&zwnj;یمانمان له نه&zwnj;وه&zwnj;ی ئیسرائیل وه&zwnj;رگرتووه (که له ڕێبازی یه&zwnj;کتاناسی و چاکه&zwnj;کاریی لانه&zwnj;ده&zwnj;ن)، چه&zwnj;ند پێغه&zwnj;مبه&zwnj;رێکیشمان بۆ ڕه&zwnj;وانه کردوون، هه&zwnj;رکاتێک یه&zwnj;کێک له&zwnj;و پێغه&zwnj;مبه&zwnj;رانه&zwnj;یان به&zwnj;دڵ نه&zwnj;بوو بێت (به&zwnj;هۆی لاساریی و ده&zwnj;روون ئاڵۆزیی خۆیانه&zwnj;وه یاخود به&zwnj;ئاره&zwnj;زووی ئه&zwnj;وانی نه&zwnj;کردبێت) ده&zwnj;سته&zwnj;یه&zwnj;کیان (به&zwnj;رنامه&zwnj;ی پێغه&zwnj;مبه&zwnj;رانیان) به&zwnj;درۆ زانیووه&zwnj;، ده&zwnj;سته&zwnj;یه&zwnj;کی تریشیان په&zwnj;لاماریان داون و (شه&zwnj;هیدیان کردوون).';
$t[71] = '(له&zwnj;ولاشه&zwnj;وه چونکه خۆیان به&zwnj;خۆشه&zwnj;ویستی خوا داده&zwnj;نا) به&zwnj;ته&zwnj;ما بوون که تووشی به&zwnj;ڵاو ناخۆشی و سزا نه&zwnj;بن (له سه&zwnj;رنجامی لادان و یاخی بوونیان) هه&zwnj;ر بۆیه کوێرو که&zwnj;ڕ بوون له ئاستی حه&zwnj;ق و ڕاستیدا، (دوایی که زانیان زه&zwnj;ره&zwnj;ریانه&zwnj;) ته&zwnj;وبه&zwnj;یان کرد، خوایش چاوپۆشی لێکردن، له&zwnj;وه&zwnj;ودوا (تێهه&zwnj;ڵچوونه&zwnj;وه له تاوان و لادان) و زۆربه&zwnj;یان کوێرو که&zwnj;ڕ بوونه&zwnj;وه له&zwnj;ئاستی حه&zwnj;ق و ڕاستیدا، خوای گه&zwnj;وره&zwnj;ش بینایه به&zwnj;هه&zwnj;موو ئه&zwnj;و کارانه&zwnj;ی که ئه&zwnj;نجامی ده&zwnj;ده&zwnj;ن و (به&zwnj;سه&zwnj;ریاندا تێناپه&zwnj;ڕێت).';
$t[72] = 'سوێند به&zwnj;خوا به&zwnj;ڕاستی، ئه&zwnj;وانه بێ باوه&zwnj;ڕ بوون که وتیان: خوا هه&zwnj;ر مه&zwnj;سیحی کوڕی مه&zwnj;ریه&zwnj;مه (به&zwnj;مه&zwnj;رجێک) مه&zwnj;سیح وتوویه&zwnj;تی: ئه&zwnj;ی نه&zwnj;وه&zwnj;ی ئیسرائیل، ئه&zwnj;و خوایه بپه&zwnj;رستن که په&zwnj;روه&zwnj;ردگاری من و په&zwnj;روه&zwnj;ردگاری ئێوه&zwnj;شه و دڵنیابن ئه&zwnj;وه&zwnj;ی شه&zwnj;ریک و هاوبه&zwnj;ش بۆ خوا بڕیار بدات، ئه&zwnj;وه بێگومان خوا به&zwnj;هه&zwnj;شتی له&zwnj;سه&zwnj;ر حه&zwnj;رامکردووه و شوێن و جێگاو سه&zwnj;ره&zwnj;نجامی ئاگره&zwnj;، ئه&zwnj;وسا ئه&zwnj;وسته&zwnj;مکارانه هیچ جۆره پشتیوانێکیان نی یه (که&zwnj;س ناتوانێت فریایان بکه&zwnj;وێت).';
$t[73] = 'سوێند بێت به&zwnj;خوا بێگومان ئه&zwnj;وانه&zwnj;ش بێ باوه&zwnj;ڕ بوون که وتیان: خوا یه&zwnj;کێکه له سێ دانه خوا (له حاڵه&zwnj;تێکدا): هیچ خوایه&zwnj;ک، هیچ په&zwnj;روه&zwnj;ردگارێک نیه جگه له خوایه&zwnj;کی تاکو ته&zwnj;نها، خۆ ئه&zwnj;گه&zwnj;ر له&zwnj;و گوفتاره نابه&zwnj;جێ و ناقۆڵایه&zwnj;یان کۆڵ نه&zwnj;ده&zwnj;ن، سوێند به&zwnj;خوا ئه&zwnj;وانه&zwnj;یان که بێ باوه&zwnj;ڕ بوون سزایه&zwnj;کی به&zwnj;ئێشیان تووش ده&zwnj;بێت.';
$t[74] = 'ئایا (خاوه&zwnj;نانی ئه&zwnj;و بیرو بڕوا پووچانه&zwnj;) حه&zwnj;ق نیه ته&zwnj;وبه بکه&zwnj;ن و بگه&zwnj;ڕێنه&zwnj;وه بۆ لای خواو داوای لێخۆشبوونی لێ بکه&zwnj;ن؟ له&zwnj;کاتێکدا: خوا لێخۆشبووه و میهره&zwnj;بانیشه&zwnj;!!';
$t[75] = 'مه&zwnj;سیحی کوڕی مه&zwnj;ریه&zwnj;م هیچ شتێک نی یه (نه خوایه و نه کوڕی خوایه&zwnj;) جگه پێغه&zwnj;مبه&zwnj;رێکی خوا نه&zwnj;بێت، به ڕاستی پێش ئه&zwnj;و چه&zwnj;نده&zwnj;ها پێغه&zwnj;مبه&zwnj;ری تریش ڕابوردوون، دایکیشی ئافره&zwnj;تێکی زۆر ڕاست و ڕاستگۆیه&zwnj;، هه&zwnj;ردووکیان خواردنیان ده&zwnj;خوارد (وه&zwnj;کو هه&zwnj;موو ئینسانێک) سه&zwnj;یر بکه و سه&zwnj;رنج بده (ئه&zwnj;ی محمد (صلی الله علیه وسلم)، ئه&zwnj;ی ئیماندار) چۆن هه&zwnj;موو فه&zwnj;رمان و به&zwnj;ڵگه&zwnj;یه&zwnj;کیان بۆ ڕوون ده&zwnj;که&zwnj;ینه&zwnj;وه&zwnj;، له&zwnj;وه&zwnj;ودوا ته&zwnj;ماشاکا چۆن ڕوویان له حه&zwnj;ق وه&zwnj;رده&zwnj;چه&zwnj;رخێنرێت و له&zwnj;ڕاستی لاده&zwnj;درێن!';
$t[76] = 'پێیان بڵێ: ئایا (ئه&zwnj;وه ڕاسته) له جیاتی خوا شتێک ده&zwnj;په&zwnj;رستن که نه زه&zwnj;ره&zwnj;رتانی به&zwnj;ده&zwnj;سته نه قازانج، (به&zwnj;ڵکو) هه&zwnj;ر خوا خۆی بیسه&zwnj;رو زانایه (ئه&zwnj;و شتانه&zwnj;ی ئێوه به&zwnj;ده&zwnj;وریدا دێن هیچیان له ده&zwnj;ست نایه&zwnj;ت).';
$t[77] = 'هه&zwnj;روه&zwnj;ها بڵێ: ئه&zwnj;ی خاوه&zwnj;نانی کتێب زیاده&zwnj;ڕه&zwnj;وی مه&zwnj;که&zwnj;ن له ئاینه&zwnj;که&zwnj;تاندا به&zwnj;زیاده&zwnj;ڕه&zwnj;وی نابه&zwnj;جێ و له سنووری حه&zwnj;ق و ڕاستی ده&zwnj;رمه&zwnj;چن، شوێنی ئاره&zwnj;زووی ئه&zwnj;و که&zwnj;سانه مه&zwnj;که&zwnj;ون که بێگومان پێشان گومڕا بوون و زۆر که&zwnj;سیشیان گومڕا کردو ئه&zwnj;وانیش له&zwnj;و ڕێگه&zwnj;و ڕێبازه ڕاسته لایاندا.';
$t[78] = 'نه&zwnj;فرین کراوه له&zwnj;وانه&zwnj;ی: بێ باوه&zwnj;ڕ بوون له نه&zwnj;وه&zwnj;ی ئیسرائیل له&zwnj;سه&zwnj;ر زاری داودو عیسای کوڕی مه&zwnj;ریه&zwnj;م، ئه&zwnj;مه&zwnj;ش له&zwnj;به&zwnj;ر ئه&zwnj;وه&zwnj;ی که ئه&zwnj;وان یاخی و سه&zwnj;رکه&zwnj;ش بوون و ده&zwnj;ستدرێژیان ده&zwnj;کرد.';
$t[79] = 'ئه&zwnj;وانه قه&zwnj;ده&zwnj;غه&zwnj;ی یه&zwnj;کتریان نه&zwnj;ده&zwnj;کرد له خراپه&zwnj;و تاوانێک که ده&zwnj;یانکرد، سوێند بێت به&zwnj;خوا ئه&zwnj;وه&zwnj;ی که ده&zwnj;یکه&zwnj;ن زۆر خراپ و ناپه&zwnj;سه&zwnj;نده&zwnj;!';
$t[80] = 'زۆربه&zwnj;یان ده&zwnj;بینیت ڕازین به&zwnj;خۆشه&zwnj;ویستی و پشتیوانی ئه&zwnj;وانه&zwnj;ی که بێ باوه&zwnj;ڕ بوون، سوێند به خوا ده&zwnj;ست پێشکه&zwnj;رییه&zwnj;کی خراپیان بۆ خۆیان ئه&zwnj;نجامداوه که خوایان له خۆیان تووڕه کردووه (به&zwnj;و کاره&zwnj;یان)، (له قیامه&zwnj;تیشدا) له&zwnj;ناو سزای دۆزه&zwnj;خدا ده&zwnj;بێت بۆ هه&zwnj;میشه بمێننه&zwnj;وه&zwnj;.';
$t[81] = 'خۆ ئه&zwnj;گه&zwnj;ر ئه&zwnj;وانه باوه&zwnj;ڕیان به&zwnj;خواو به&zwnj;م پێغه&zwnj;مبه&zwnj;ره&zwnj;و ئه&zwnj;و قورئانه ببوایه که بۆی دابه&zwnj;زیوه&zwnj;، ئه&zwnj;وه ئه&zwnj;وانه&zwnj;یان نه&zwnj;ده&zwnj;کرد به&zwnj;خۆشه&zwnj;ویست و سه&zwnj;رپه&zwnj;رشتیاری خۆیان، پشتیان به&zwnj;وان نه&zwnj;ده&zwnj;به&zwnj;ست، به&zwnj;ڵام زۆربه&zwnj;یان تاوانبارو لاده&zwnj;رو یاخین و (حه&zwnj;ق و ڕاستی مه&zwnj;به&zwnj;ستیان نیه&zwnj;، به&zwnj;ڵکو به&zwnj;رده&zwnj;وام دژایه&zwnj;تی ئاینی ئیسلام ده&zwnj;که&zwnj;ن).';
$t[82] = 'سوێند به&zwnj;خوا ده&zwnj;بینیت و ده&zwnj;ستت ده&zwnj;که&zwnj;وێت سه&zwnj;ر سه&zwnj;خترینی خه&zwnj;ڵک له دوژمنایه&zwnj;تیدا به&zwnj;رامبه&zwnj;ر به&zwnj;وانه&zwnj;ی که باوه&zwnj;ڕیان هێناوه&zwnj;، جوله&zwnj;که&zwnj;ن و ئه&zwnj;وانه&zwnj;ی که هاوه&zwnj;ڵیان بۆ خوا داناوه و سوێند بێت ده&zwnj;بینیت و ده&zwnj;ستت ده&zwnj;که&zwnj;وێت نزیکترینیان له سۆز و خۆشه&zwnj;ویستی بۆ ئه&zwnj;وانه&zwnj;ی که باوه&zwnj;ڕیان هێناوه ئه&zwnj;وانه&zwnj;ن که ده&zwnj;یانوت: به&zwnj;ڕاستی ئێمه نه&zwnj;صرانین، ئه&zwnj;وه&zwnj;ش له&zwnj;به&zwnj;ر ئه&zwnj;وه&zwnj;یه به&zwnj;ڕاستی هه&zwnj;ندێک له&zwnj;وان قه&zwnj;شه&zwnj;ی دیندارن و هه&zwnj;ندێکیشیان گۆشه&zwnj;گیرو که&zwnj;سانی خواپه&zwnj;رستن و بێگومان ئه&zwnj;وان خۆ به&zwnj;گه&zwnj;وره دانانێن.';
$t[83] = '(ئه&zwnj;وانه&zwnj;یان که به&zwnj;شوێن ڕاستیدا ده&zwnj;گه&zwnj;ڕێن) هه&zwnj;ر کاتێک گوێیان له قورئان ده&zwnj;بوو که بۆ پێغه&zwnj;مبه&zwnj;ر (صلی الله علیه وسلم) دابه&zwnj;زیووه&zwnj;، ده&zwnj;بینیت چاوه&zwnj;کانیان ئه&zwnj;سرینی خۆشی هه&zwnj;ڵده&zwnj;ڕێژێت، به&zwnj;هۆی ئه&zwnj;و حه&zwnj;ق و ڕاستیه&zwnj;ی که ده&zwnj;یانناسی، له&zwnj;کاتێکدا ده&zwnj;ڵێن: په&zwnj;روه&zwnj;ردگارا ئێمه ئیمان و باوه&zwnj;ڕمان هێناوه (به ئاینی ئیسلام) ده تۆش له ڕیزی (په&zwnj;یڕه&zwnj;وان) و شایه&zwnj;ته&zwnj;کاندا تۆمارمان بکه&zwnj;.';
$t[84] = 'چی ڕێمان لێده&zwnj;گرێت باوه&zwnj;ڕ نه&zwnj;هێنین به خواو به&zwnj;وه&zwnj;ی بۆمان هاتووه له حه&zwnj;ق و ڕاستی، هیواداریشین که په&zwnj;روه&zwnj;ردگارمان بمانخاته نێو ڕیزی چاکان و گونجاوان بۆ خواپه&zwnj;رستی.';
$t[85] = 'جا به&zwnj;هۆی ئه&zwnj;وگوفتاره به&zwnj;جێیه&zwnj;یان پاداشتی دانه&zwnj;وه&zwnj;، به&zwnj;وه&zwnj;ی باخه&zwnj;کانی به&zwnj;هه&zwnj;شتیان پێ ده&zwnj;به&zwnj;خشێت که چه&zwnj;نده&zwnj;ها ڕووبار به&zwnj;ژێر دره&zwnj;خته&zwnj;کانیدا ده&zwnj;ڕوات، هاوڕێ له&zwnj;گه&zwnj;ڵ ژیانی هه&zwnj;میشه&zwnj;یی و نه&zwnj;بڕاوه&zwnj;دا، هه&zwnj;ر به&zwnj;و شێوه&zwnj;یه&zwnj;شه پاداشتی چاکه&zwnj;کاران.';
$t[86] = 'ئه&zwnj;وانه&zwnj;ش باوه&zwnj;ڕیان نه&zwnj;هێناوه و ئایه&zwnj;ت و فه&zwnj;رمانه&zwnj;کانی ئێمه&zwnj;یان به درۆزانی، ئا ئه&zwnj;وانه نیشته&zwnj;جێی ناو دۆزه&zwnj;خن.';
$t[87] = 'ئه&zwnj;ی ئه&zwnj;وانه&zwnj;ی که باوه&zwnj;ڕتان هێناوه&zwnj;، ئه&zwnj;و نازو نیعمه&zwnj;ته چاکانه&zwnj;ی که خوا بۆی حه&zwnj;ڵاڵ کردوون له خۆتانی حه&zwnj;رام مه&zwnj;که&zwnj;ن و له سنوور ده&zwnj;رمه&zwnj;چن و ده&zwnj;ستدرێژیی مه&zwnj;که&zwnj;ن، چونکه به&zwnj;ڕاستی خوا ده&zwnj;ست درێژکارانی خۆشناوێت.';
$t[88] = 'له&zwnj;و ڕزق و ڕۆزییه حه&zwnj;ڵاڵ و پاکانه بخۆن که خوا پێی به&zwnj;خشیوون، له&zwnj;و خوایه بترسن که ئێوه هه&zwnj;میشه باوه&zwnj;ڕی دامه&zwnj;زراوتان پێیه&zwnj;تی (خۆتان له ڕزق و ڕۆزیی حه&zwnj;رام بپارێزن).';
$t[89] = 'خوا لێتان ناگرێت کاتێک به&zwnj;ساده&zwnj;یی سوێند به&zwnj;ده&zwnj;متاندا دێت و (له&zwnj;سه&zwnj;ری ڕاهاتوون) به&zwnj;ڵکو له&zwnj;و سوێندانه&zwnj;تان ده&zwnj;پرسێته&zwnj;وه که بیرو هۆشتان لای هه&zwnj;بووه و بۆ مه&zwnj;به&zwnj;ستێک وتووتانه&zwnj;، جا که&zwnj;فاره&zwnj;تی ئه&zwnj;و جۆره سوێندانه&zwnj;: نان و خۆراک دانه به ده که&zwnj;سی هه&zwnj;ژار به&zwnj;شێوه&zwnj;یه&zwnj;کی مامناوه&zwnj;ندی که بۆ خاو و خێزانتان سه&zwnj;رفی ده&zwnj;که&zwnj;ن، یاخود پۆشته کردنه&zwnj;وه&zwnj;یان یاخود ئازاد کردنی به&zwnj;نده&zwnj;یه&zwnj;ک (ئه&zwnj;گه&zwnj;ر بووی) ئه&zwnj;وه&zwnj;ی (توانای ئه&zwnj;و شتانه&zwnj;ی نه&zwnj;بوو) با سێ ڕۆژ به&zwnj;رۆژوو بێت، ئه&zwnj;وه که&zwnj;فاره&zwnj;تی سوێنده&zwnj;کانتانه کاتێک سوێند ده&zwnj;خۆن و نایبه&zwnj;نه سه&zwnj;ر، جا تاده&zwnj;توانن سوێنده&zwnj;کانتان بپارێزن و مه&zwnj;یانشکێنن، ئا به&zwnj;و شێوه&zwnj;یه خوا ئایه&zwnj;ته&zwnj;کانی خۆیتان بۆ ڕوون ده&zwnj;کاته&zwnj;وه بۆ ئه&zwnj;وه&zwnj;ی سوپاسگوزاری بکه&zwnj;ن.';
$t[90] = 'ئه&zwnj;ی ئه&zwnj;وانه&zwnj;ی باوه&zwnj;ڕتان هێناوه دڵنیابن که عه&zwnj;ره&zwnj;ق و قومارو گۆشتی ماڵاتی سه&zwnj;ربڕاو بۆ غه&zwnj;یری خوا له&zwnj;و شوێنانه&zwnj;ی که خه&zwnj;ڵک به&zwnj;پیرۆزی داناوه نه&zwnj;ک ئیسلام، بورج حه&zwnj;رام و حه&zwnj;ڵاڵ زانین به&zwnj;هۆی هه&zwnj;ڵدانی زاره&zwnj;وه ئه&zwnj;مانه هه&zwnj;رپیسن و له&zwnj;کاروکرده&zwnj;وه&zwnj;ی شه&zwnj;یتانن، که&zwnj;واته ئێوه&zwnj;ش خۆتانی لێ دوور بگرن و خۆتانی لێ بپارێزن، بۆ ئه&zwnj;وه&zwnj;ی سه&zwnj;رفرازیی و به&zwnj;خته&zwnj;وه&zwnj;ریی به&zwnj;ده&zwnj;ست بهێنن (له هه&zwnj;ردوو جیهاندا).';
$t[91] = 'به&zwnj;ڕاستی شه&zwnj;یتان هه&zwnj;ر ده&zwnj;یه&zwnj;وێت له ڕێگه&zwnj;ی عه&zwnj;ره&zwnj;ق و قوماره&zwnj;وه دوژمنایه&zwnj;تی و بوغزو کینه بخاته نێوانتانه&zwnj;وه و له&zwnj;یادکردنی خوا و ناوه پیرۆزه&zwnj;کانی وێڵتان بکات، هه&zwnj;روه&zwnj;ها سست و ته&zwnj;مه&zwnj;ڵتان بکات له ئه&zwnj;نجامدانی نوێژه&zwnj;کان به شێنه&zwnj;یی، ئایا ئیتر به&zwnj;س نیه&zwnj;؟ ئایا کۆڵ ناده&zwnj;ن؟! (جا هه&zwnj;ر که ئه&zwnj;م ئایه&zwnj;ته پیرۆزه هاته خواره&zwnj;وه&zwnj;، جاڕ درا به ناو شاری مه&zwnj;دینه&zwnj;دا که عه&zwnj;ره&zwnj;ق حه&zwnj;رام کراوه&zwnj;، ئه&zwnj;وسا ئیمانداران قه&zwnj;ڕابه و گۆزه و دیزه و هه&zwnj;رچی عه&zwnj;ره&zwnj;قی تیادا بوو ڕشتیانه کۆڵان).';
$t[92] = '(ئه&zwnj;ی ئیمانداران) فه&zwnj;رمانبه&zwnj;رداری خوا بن، فه&zwnj;رمانبه&zwnj;رداری پێغه&zwnj;مبه&zwnj;ریش بن و وریابن و ئاگادار بن (نه&zwnj;که&zwnj;ن سه&zwnj;رپێچی له فه&zwnj;رمان و ڕێنمووییه&zwnj;کانیان بکه&zwnj;ن) جا ئه&zwnj;گه&zwnj;ر پشت هه&zwnj;ڵکه&zwnj;ن و یاخی ببن ئه&zwnj;وه بزانن به&zwnj;ڕاستی کاری پێغه&zwnj;مبه&zwnj;ری ئێمه ته&zwnj;نها ڕاگه&zwnj;یاندنی ئاشکراو ڕوونی په&zwnj;یامه&zwnj;که&zwnj;یه&zwnj;.';
$t[93] = 'هیچ گوناهێک نیه له&zwnj;سه&zwnj;ر ئه&zwnj;وانه&zwnj;ی که باوه&zwnj;ڕیان هێناوه و کرداره چاکه&zwnj;کانیان ئه&zwnj;نجامداوه له&zwnj;وه&zwnj;ی که خواردویانه و خواردویانه&zwnj;ته&zwnj;وه (پێش حه&zwnj;رامکردنیان)، کاتێک خۆیان پاڕاستبێت له حه&zwnj;رامی ئه&zwnj;و سه&zwnj;رده&zwnj;مه و بڕوایان هه&zwnj;بوو بێت و کرداره چاکه&zwnj;کانیان ئه&zwnj;نجام دابێت و پاشان له تازه حه&zwnj;رام کراوه&zwnj;کانیش خۆیان پاڕاستبێت و باوه&zwnj;ڕیان هێنا بێت له دوایدا خۆیان پاڕاستبێت و به&zwnj;رده&zwnj;وامیش بووبن له&zwnj;سه&zwnj;ر چاکه&zwnj;کردن، خوایش چاکه&zwnj;کارانی خۆش ده&zwnj;وێت (چاوپۆشی له ڕابوردوویان ده&zwnj;کات).';
$t[94] = 'ئه&zwnj;ی ئه&zwnj;وانه&zwnj;ی باوه&zwnj;ڕتان هێناوه سوێند بێت بێگومان خوای گه&zwnj;وره تاقیتان ده&zwnj;کاته&zwnj;وه به&zwnj;شتی که&zwnj;م هه&zwnj;ندێکیان له نێچیری کێوین که دێنه پێشه&zwnj;وه لێتان و ده&zwnj;توانن به&zwnj;ده&zwnj;ست بیانگرن و به ڕمه&zwnj;کانتان ڕاویان بکه&zwnj;ن (له&zwnj;کاتی ئیحرامدا بۆ حه&zwnj;ج و عه&zwnj;مره&zwnj;) تا به ته&zwnj;واوی له جیهانی واقعدا خوا بزانێت که کێ له په&zwnj;نهانیدا ده&zwnj;ترسێت له خوا، جا ئه&zwnj;وه&zwnj;ی ده&zwnj;ستدرێژی و نافه&zwnj;رمانی کردبێت له دوای ئه&zwnj;و تاقی کردنه&zwnj;وه&zwnj;یه (له&zwnj;کاتی ئیحرامدا) ئه&zwnj;وه سزای به&zwnj;ئێشی بۆ هیه&zwnj;.';
$t[95] = 'ئه&zwnj;ی ئه&zwnj;وانه&zwnj;ی باوه&zwnj;ڕتان هێناوه له&zwnj;کاتێکدا ئێوه له ئیحرامدان (نیه&zwnj;تی حه&zwnj;ج یا عه&zwnj;مره&zwnj;تان هێناوه&zwnj;) نێچیر ڕاو مه&zwnj;که&zwnj;ن، ئه&zwnj;وه&zwnj;ی به&zwnj;ئاره&zwnj;زوو ڕاو بکات و نێچیر بکوژێت له تۆڵه&zwnj;یدا ده&zwnj;بێت قوربانیه&zwnj;کی وه&zwnj;ک ئه&zwnj;و نێچیره پێشکه&zwnj;ش بکات و ئه&zwnj;و بڕیاره&zwnj;ش ده&zwnj;بێت دووکه&zwnj;سی شاره&zwnj;زای دادپه&zwnj;روه&zwnj;ر له خۆتان بیده&zwnj;ن و قوربانیه&zwnj;که&zwnj;ش ده&zwnj;بێت پێشکه&zwnj;ش بکرێت له ده&zwnj;وروبه&zwnj;ری که&zwnj;عبه&zwnj;دا، یاخود به&zwnj;قه&zwnj;ده&zwnj;ر نرخی قۆچه&zwnj;که خۆراک پێشکه&zwnj;ش به&zwnj;هه&zwnj;ژاران بکات، یاخود به&zwnj;قه&zwnj;ده&zwnj;ر ژماره&zwnj;ی هه&zwnj;ژاران (که خۆراکیان به&zwnj;رده&zwnj;که&zwnj;وێت) به&zwnj;ڕۆژوو بێت، تا تاڵاوی سه&zwnj;رپێچی خۆی بچێژێت، له ڕابوردووشدا هه&zwnj;رچی له&zwnj;و بابه&zwnj;تانه ڕوویداوه خوا قه&zwnj;ڵه&zwnj;می چاوپۆشی به&zwnj;سه&zwnj;ردا هێناوه&zwnj;، جا ئه&zwnj;وه&zwnj;ی ئه&zwnj;م هه&zwnj;ڵانه دووباره بکاته&zwnj;وه خوا تۆڵه&zwnj;ی لێده&zwnj;سێنێت، چونکه خوا باڵاده&zwnj;ست و تۆڵه سێنه&zwnj;رێکی (به زه&zwnj;بره&zwnj;).';
$t[96] = 'ڕاوکردن و خواردنی زینده&zwnj;وه&zwnj;رانی ده&zwnj;ریایی بۆتان حه&zwnj;ڵاڵ کراوه&zwnj;، له&zwnj;به&zwnj;ر سوودی ئێوه و ئه&zwnj;و که&zwnj;سانه&zwnj;ش که له سه&zwnj;فه&zwnj;ردان و ڕاوی زینده&zwnj;وه&zwnj;رانی ده&zwnj;شتی و کێوی لێتان حه&zwnj;رامه هه&zwnj;تا له ئیحرامدا بن، له&zwnj;و خوایه بترسن که هه&zwnj;ر بۆ لای ئه&zwnj;و کۆ ده&zwnj;کرێنه&zwnj;وه (بۆ لێپرسینه&zwnj;وه&zwnj;).';
$t[97] = 'خوا، که&zwnj;عبه که (البیت الحرام)ه کردوویه&zwnj;تی به&zwnj;هۆی هه&zwnj;ستانه&zwnj;وه و بوژانه&zwnj;وه&zwnj;ی خه&zwnj;ڵکی (له هه&zwnj;ردوو دنیادا)، هه&zwnj;روه&zwnj;ها مانگه حه&zwnj;رامه&zwnj;کانیش و دیاریی و قوربانیه دیاری کراوه&zwnj;کان، ئه&zwnj;وه&zwnj;ش بۆ ئه&zwnj;وه&zwnj;یه چاک بزانن و دڵنیا بن که خوا ئاگاداره به&zwnj;هه&zwnj;رچی له ئاسمانه&zwnj;کاندایه و به&zwnj;هه&zwnj;رچی له زه&zwnj;ویدا هه&zwnj;یه&zwnj;، به&zwnj;ڕاستی خوا به هه&zwnj;موو شتێک زانایه&zwnj;.';
$t[98] = 'چاک بزانن به&zwnj;ڕاستی خوا تۆڵه&zwnj;سێنه&zwnj;رێکی توند و تیژه (له یاخیه&zwnj;کان) هه&zwnj;روه&zwnj;ها بێگومان لێخۆشبوو و میهره&zwnj;بانه (بۆ ئیمانداران).';
$t[99] = 'پێغه&zwnj;مبه&zwnj;ر هیچی له&zwnj;سه&zwnj;ر نی یه جگه له ڕاگه&zwnj;یاندنی په&zwnj;یامی خوا، هه&zwnj;ر خوا خۆیشی ده&zwnj;زانێت به&zwnj;وه&zwnj;ی ده&zwnj;ریده&zwnj;خه&zwnj;ن، به&zwnj;وه&zwnj;ش ده&zwnj;یشارنه&zwnj;وه (له کردارو نیه&zwnj;تتان).';
$t[100] = '(ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر (صلی الله علیه وسلم) پێیان بڵێ: پیس و پاک یه&zwnj;کسان نین، هه&zwnj;رچه&zwnj;نده زۆریه&zwnj;تی ئه&zwnj;و پیسه سه&zwnj;رنجی ڕاکێشا بیت، که&zwnj;واته ئه&zwnj;ی هۆشمه&zwnj;ندان له خوا بترسن و پارێزکار بن، بۆ ئه&zwnj;وه&zwnj;ی سه&zwnj;رفراز و سه&zwnj;رکه&zwnj;وتووبن.';
$t[101] = 'ئه&zwnj;ی ئه&zwnj;وانه&zwnj;ی باوه&zwnj;ڕتان هێناوه پرسیار مه&zwnj;که&zwnj;ن له هه&zwnj;ندێک شت، که ئه&zwnj;گه&zwnj;ر ئاشکرا بکرێت بۆتان سه&zwnj;غڵه&zwnj;تتان ده&zwnj;کات، خۆ ئه&zwnj;گه&zwnj;ر پرسیار بکه&zwnj;ن ده&zwnj;رباره&zwnj;یان له&zwnj;کاتی دابه&zwnj;زاندنی قورئاندا ئه&zwnj;وه بۆتان ئاشکرا ده&zwnj;کرێت، خوا چاوپۆشی لێ کردوون و (باسی نه&zwnj;کردوون) خواش لێخۆشبوو و به&zwnj;حیلم و حه&zwnj;وسه&zwnj;ڵه&zwnj;یه&zwnj;.';
$t[102] = 'بێگومان که&zwnj;سانێک پێش ئێوه پرسیاریان ده&zwnj;رباره&zwnj;یان کرد، پاشان که بۆیان ڕوون ده&zwnj;بۆوه بڕوایان پێ نه&zwnj;ده&zwnj;کرد و پێی بێ باوه&zwnj;ڕ بوون (که&zwnj;واته ئێوه وامه&zwnj;بن).';
$t[103] = 'خوا بڕیاری ئه&zwnj;وه&zwnj;ی نه&zwnj;داوه که وشتر گوێی ببڕدرێت یان به&zwnj;ڕه&zwnj;ڵا بکرێت، یان به&zwnj;رخۆله&zwnj;ی مه&zwnj;ڕه&zwnj;کان بۆ بته&zwnj;کان سه&zwnj;ر ببڕدرێت، یان وشتری نێر ئازاد بێت، به&zwnj;ڵام ئه&zwnj;وانه&zwnj;ی بێ باوه&zwnj;ڕ بوون درۆیان به&zwnj;ده&zwnj;م خواوه هه&zwnj;ڵده&zwnj;به&zwnj;ست، زۆربه&zwnj;شیان ژیر نابن (هه&zwnj;ر له خۆیانه&zwnj;وه بڕیاری نابه&zwnj;جێ ده&zwnj;ده&zwnj;ن و کرداری ناپه&zwnj;سه&zwnj;ند ده&zwnj;که&zwnj;ن).';
$t[104] = 'هه&zwnj;ر کاتێک پێیان بوترێت: وه&zwnj;رن بۆ لای ئه&zwnj;وه&zwnj;ی خوا دایبه&zwnj;زاندووه و وه&zwnj;ن بۆ لای پێغه&zwnj;مبه&zwnj;ریش (بۆ ئه&zwnj;وه&zwnj;ی داوه&zwnj;ری بکات له نێوانتاندا) ئه&zwnj;وه ده&zwnj;ڵێن: هه&zwnj;ر ئه&zwnj;و به&zwnj;رنامه&zwnj;یه&zwnj;مان به&zwnj;سه که باوو باپیرانمان له&zwnj;سه&zwnj;ری بوون و له مامۆستاو ڕابه&zwnj;رانمانه&zwnj;وه فێری بوین و بۆمان ماوه&zwnj;ته&zwnj;وه&zwnj;، ئایا ئه&zwnj;گه&zwnj;ر کاتێکیش باوان و مامۆستایانیان هیچ شتێک له حه&zwnj;رام و حه&zwnj;ڵاڵ نه&zwnj;زانن و ڕێنموویش وه&zwnj;رنه&zwnj;گرن، ئه&zwnj;وه هه&zwnj;رشوێنیان ده&zwnj;که&zwnj;ون؟!';
$t[105] = 'ئه&zwnj;ی ئه&zwnj;وانه&zwnj;ی باوه&zwnj;ڕتان هێناوه هه&zwnj;میشه&zwnj;و به&zwnj;رده&zwnj;وام سه&zwnj;رگه&zwnj;رمی په&zwnj;روه&zwnj;رده&zwnj;ی خۆتان بن و (مه&zwnj;ترسن)، چونکه ئه&zwnj;وانه&zwnj;ی گومڕان ناتوانن زیانتان لێبده&zwnj;ن ئه&zwnj;گه&zwnj;ر ئێوه به&zwnj;چاکی ڕێبازی هیدایه&zwnj;ت و خواناسیتان گرتبێته به&zwnj;ر، دڵنیابن گه&zwnj;ڕانه&zwnj;وه&zwnj;ی هه&zwnj;موو شتێک بۆلای خوایه&zwnj;، سه&zwnj;ره&zwnj;نجام هه&zwnj;واڵی هه&zwnj;موو ئه&zwnj;و کارو کرده&zwnj;وانه&zwnj;تان ده&zwnj;داتێ که ئه&zwnj;نجامی ده&zwnj;ده&zwnj;ن.';
$t[106] = 'ئه&zwnj;ی ئه&zwnj;وانه&zwnj;ی باوه&zwnj;ڕتان هێناوه&zwnj;: کاتێک نیشانه&zwnj;ی مه&zwnj;رگ و مردن له که&zwnj;سێکتان ده&zwnj;رکه&zwnj;وت، شایه&zwnj;تی نێوانتان با دووکه&zwnj;س بن له&zwnj;کاتی وه&zwnj;سیه&zwnj;ته&zwnj;که&zwnj;دا، دووکه&zwnj;سی دادپه&zwnj;روه&zwnj;ر بن له خۆتان یان دوو که&zwnj;سی تر جگه له خۆتان، ئه&zwnj;گه&zwnj;ر ئێوه بۆ بازرگانی و شتی تر سه&zwnj;فه&zwnj;رتان کرد له وڵاتداو جا مردن یه&zwnj;خه&zwnj;ی گرتن، ئه&zwnj;گه&zwnj;ر دوودڵ بوون له دوو شایه&zwnj;ته&zwnj;که ئه&zwnj;وا ڕایانگرن له دوای نوێژی عه&zwnj;سر سوێند بخۆن به&zwnj;خواو (بڵێن) ئێمه سوێنده&zwnj;که&zwnj;مان به&zwnj;هیچ نرخێک ناگۆڕینه&zwnj;وه با سوێند بۆ خوراوه&zwnj;که نزیکترین خزمیش بێت (له ڕاستی لا ناده&zwnj;ین)، ئێمه شایه&zwnj;تیه&zwnj;ک که بۆ خوا ئه&zwnj;نجاممانداوه نایشارینه&zwnj;وه و ونی ناکه&zwnj;ین، چونکه به&zwnj;ڕاستی ئه&zwnj;گه&zwnj;ر وابکه&zwnj;ین ئه&zwnj;وه که&zwnj;وابوو بێگومان له ڕیزی تاوانبارانداین.';
$t[107] = 'خۆ ئه&zwnj;گه&zwnj;ر ده&zwnj;رکه&zwnj;وت ئه&zwnj;و دوو شایه&zwnj;ته&zwnj;ی پێشوو (درۆیان کردووه&zwnj;و) خۆیان گوناهبار کردووه ئه&zwnj;وه دووکه&zwnj;سی تر له خزمی نزیکی سته&zwnj;م لێکراوی مردووه&zwnj;که له جیاتی ئه&zwnj;وان ئاماده بکرێن و ئینجا سوێند به&zwnj;خوا بخۆن و بڵێن: که به ڕاستی شایه&zwnj;تی ئێمه له شایه&zwnj;تی ئه&zwnj;وان ڕاستترو دروستتره&zwnj;، ئێمه هیچ سته&zwnj;م و ده&zwnj;ستدرێژیه&zwnj;کمان نه&zwnj;کردووه و (ته&zwnj;ماعمان له ماڵیاندا نیه&zwnj;)، خۆ ئه&zwnj;گه&zwnj;ر نیه&zwnj;تمان وابێت، ئه&zwnj;وه به&zwnj;ڕاستی که&zwnj;وابوو ئێمه&zwnj;ش له ڕیزی سته&zwnj;مکارانداین.';
$t[108] = 'ئه&zwnj;وه&zwnj;ی که باسکرا نزیکترین و شیاوترین شێوازه بۆ شایه&zwnj;تی جێبه&zwnj;جێ کردن بۆ ئه&zwnj;وه&zwnj;ی سوێند و شایه&zwnj;تیه&zwnj;کان به ڕاستی بدرێن له&zwnj;به&zwnj;ر خوا یان له ترسی ئابڕوو چوونی به&zwnj;درۆ خستنه&zwnj;وه&zwnj;ی سوێنده&zwnj;کانیان (تا مافی که&zwnj;س نه&zwnj;فه&zwnj;وتێت)، له خوا بترسن و گوێ بگرن (بۆ هه&zwnj;موو ڕێنموییه&zwnj;کی خێر) خوایش هیدایه&zwnj;ت و ڕێنموویی هۆزو قه&zwnj;ومی تاوانبارو گوناهکارو له سنوور ده&zwnj;رچوو نادات.';
$t[109] = 'رۆژێک دێت، خوا هه&zwnj;موو پێغه&zwnj;مبه&zwnj;ران کۆده&zwnj;کاته&zwnj;وه&zwnj;، ئیجا پێیان ده&zwnj;فه&zwnj;رموێت: چۆن وه&zwnj;ڵامتان درایه&zwnj;وه&zwnj;و خه&zwnj;ڵکی هه&zwnj;ڵوێستیان چۆن بوو به&zwnj;رامبه&zwnj;رتان؟ تا چ ڕاده&zwnj;یه&zwnj;ک هاتن به&zwnj;ده&zwnj;متانه&zwnj;وه&zwnj;؟ (له سامناکی ئه&zwnj;و ڕۆژه و به&zwnj;هۆی دڵنیاییان له زانیاری بێ سنووری خوایی) وتیان: ئێمه نازانین و هیچ شاره&zwnj;زاییه&zwnj;کمان نیه&zwnj;، به&zwnj;ڕاستی خوایه تۆ هه&zwnj;ر خۆت ئاگات له هه&zwnj;موو نهێنی و شاراوه&zwnj;کانه و به هه&zwnj;موو په&zwnj;نهانێک ده&zwnj;زانیت.';
$t[110] = 'ئه&zwnj;وسا (له&zwnj;ناو هه&zwnj;موو پێغه&zwnj;مبه&zwnj;راندا ڕوو به عیسای کوڕی مه&zwnj;ریه&zwnj;م) خوا فه&zwnj;رمووی: ئه&zwnj;ی عیسای کوڕی مه&zwnj;ریه&zwnj;م یادی ئه&zwnj;و نازو نیعمه&zwnj;ته&zwnj;ی من بکه&zwnj;ره&zwnj;وه که ڕژاندم به&zwnj;سه&zwnj;ر خۆت و دایکتدا، کاتێک من به&zwnj;هۆی فریشته جوبره&zwnj;ئیلی پیرۆزه&zwnj;وه پشتیوانیم لێکردیت، ئه&zwnj;وکاته&zwnj;ی له بێشکه&zwnj;داو به&zwnj;پیریش به&zwnj;ڕه&zwnj;وانی قسه&zwnj;ت بۆ خه&zwnj;ڵکی ده&zwnj;کرد، هه&zwnj;روه&zwnj;ها کاتێک فێری نووسین و دانایی و ته&zwnj;ورات و ئینجیلم کردیت، کاتێکیش له قوڕ وه&zwnj;ک شێوه&zwnj;ی باڵنده&zwnj;ت دروست ده&zwnj;کرد به&zwnj;فه&zwnj;رمانی من، ئه&zwnj;مجا فووت پیاده&zwnj;کرد، جا ده&zwnj;بوو به&zwnj;باڵده به&zwnj;فه&zwnj;رمانی من و کوێری زگماک و به&zwnj;ڵه&zwnj;کت چاک ده&zwnj;کرده&zwnj;وه به&zwnj;فه&zwnj;رمانی من، کاتێکیش مردووه&zwnj;کانت به&zwnj;زیندوویی به&zwnj;فه&zwnj;رمانی من له گۆڕ ده&zwnj;رده&zwnj;هێنایه&zwnj;وه&zwnj;، هه&zwnj;روه&zwnj;ها یادی ئه&zwnj;وه بکه&zwnj;ره&zwnj;وه که شه&zwnj;ڕی نه&zwnj;وه&zwnj;ی ئیسرائیلم لێ لادایت، کاتێک به&zwnj;به&zwnj;ڵگه ئاشکراکانه&zwnj;وه هاتی بۆ لایان، جا ئه&zwnj;وانه&zwnj;شیان که بێ باوه&zwnj;ڕ بوون وتیان: ئه&zwnj;مه جگه له&zwnj;وه&zwnj;ی جادوویه&zwnj;کی ئاشکرایه هیچ شتێکی تر نیه&zwnj;.';
$t[111] = 'هه&zwnj;روه&zwnj;ها کاتێک نیگام نارد بۆ هاوه&zwnj;ڵه دڵسۆزو تایبه&zwnj;تیه&zwnj;کانی عیسا (حه&zwnj;واریه&zwnj;کان) که ئیمان و باوه&zwnj;ڕ بهێنن به من و به پێغه&zwnj;مبه&zwnj;ره&zwnj;که&zwnj;م، ئه&zwnj;وانیش وتیان: ئیمان و باوه&zwnj;ڕی ته&zwnj;واومان هه&zwnj;یه&zwnj;، خوایه تۆش به&zwnj;شایه&zwnj;ت به که به&zwnj;ڕاستی ئێمه موسڵمان و (ملکه&zwnj;چ و دڵسۆزین).';
$t[112] = 'کاتێک حه&zwnj;واریه&zwnj;کانیش وتیان: ئه&zwnj;ی عیسای کوڕی مه&zwnj;ریه&zwnj;م، ئایا په&zwnj;روه&zwnj;ردگاری تۆ ده&zwnj;توانێت له ئاسمانه&zwnj;وه سفره و خوانێکمان بۆ دابه&zwnj;زێنێت؟ عیسا وتی: له خوا بترسن، ئه&zwnj;گه&zwnj;ر ئێوه ئیماندارن، (ئه&zwnj;مه چ داخوازیه&zwnj;که ده&zwnj;یکه&zwnj;ن؟!).';
$t[113] = '(حه&zwnj;وارییه&zwnj;کان بۆ پاساوی داواکه&zwnj;یان) وتیان: ده&zwnj;مانه&zwnj;وێت (بۆ پیرۆزی و به&zwnj;ره&zwnj;که&zwnj;ت) لێی بخۆین و دڵنیابین و چاک بزانین که بێگومان ڕاستت له&zwnj;گه&zwnj;ڵدا کردووین و له&zwnj;سه&zwnj;ریشی ببینه شایه&zwnj;ت (تا ئه&zwnj;وه&zwnj;ی باوه&zwnj;ڕی هه&zwnj;یه باوه&zwnj;ڕی دامه&zwnj;زراوتر بێت).';
$t[114] = 'ئه&zwnj;وسا عیسای کوڕی مه&zwnj;ریه&zwnj;م وتی: ئه&zwnj;ی خوای په&zwnj;روه&zwnj;ردگار له ئاسمانه&zwnj;وه سفره و خوانێکمان بۆ دابه&zwnj;زێنه تا ببێته جه&zwnj;ژن بۆ نه&zwnj;وه&zwnj;کانی ئێستا و داهاتوومان و به&zwnj;ڵگه&zwnj;یه&zwnj;ک بێت له&zwnj;لایه&zwnj;ن تۆوه بۆ پێغه&zwnj;مبه&zwnj;رایه&zwnj;تی من و ده&zwnj;سه&zwnj;ڵاتی بێ سنوورت و له ڕزق و ڕۆزی به&zwnj;هره&zwnj;وه&zwnj;رمان بکه&zwnj;، هه&zwnj;ر تۆی چاکترین ڕۆزی به&zwnj;خش.';
$t[115] = '(ئینجا) خوا فه&zwnj;رمووی: به&zwnj;ڕاستی من ئه&zwnj;و سفره و خوانه&zwnj;تان بۆ داده&zwnj;به&zwnj;زێنم، جا ئه&zwnj;وه&zwnj;تان (دوای دابه&zwnj;زاندنی سفره&zwnj;وخوانه&zwnj;که&zwnj;) بێ بڕوابێت و سوپاس ناپه&zwnj;زێربێت، ئه&zwnj;وه به&zwnj;ڕاستی من سزایه&zwnj;کی ده&zwnj;ده&zwnj;م که سزای هیچ که&zwnj;سێکم له جیهاندا ئاوانه&zwnj;دا بێت.';
$t[116] = 'کاتێکیش خوا (هه&zwnj;ر له قیامه&zwnj;تدا) فه&zwnj;رمووی: ئه&zwnj;ی عیسای کوڕی مه&zwnj;ریه&zwnj;م، ئایا ئه&zwnj;وه&zwnj;تۆ به&zwnj;خه&zwnj;ڵکیت وتوه&zwnj;: منیش و دایکیشم به دوو خوا دابنێن له خوار خوای په&zwnj;روه&zwnj;ردگاره&zwnj;وه&zwnj;؟ عیسا له وه&zwnj;ڵامدا وتی: پاکی و بێگه&zwnj;ردی و بێ هاوه&zwnj;ڵی شایسته&zwnj;ی تۆیه&zwnj;، بۆم نیه من شتێک بڵێم بۆ خۆم که مافی من نه&zwnj;بێت؟! (چۆن حه&zwnj;ق به&zwnj;خۆم ده&zwnj;ده&zwnj;م شتی وا بێت به زارمدا؟!) خۆ ئه&zwnj;گه&zwnj;ر شتی وام وتبێت ئه&zwnj;وه به&zwnj;ڕاستی تۆ ده&zwnj;یزانیت و لێت شاراوه نیه&zwnj;، چونکه تۆ ده&zwnj;زانیت به&zwnj;هه&zwnj;رچی له دڵ و ده&zwnj;روونمدا هه&zwnj;یه&zwnj;، به&zwnj;ڵام من نازانم به&zwnj;وه&zwnj;ی که له ده&zwnj;ریای علم و زانستی بێ پایانی تۆدا هه&zwnj;یه&zwnj;، چونکه به&zwnj;ڕاستی هه&zwnj;ر تۆ زانا و شاره&zwnj;زای نهێنی و په&zwnj;نهانی.';
$t[117] = '(بێگومان تۆ چاک ده&zwnj;زانیت): من هیچ شتێکم پێ نه&zwnj;وتوون جگه له&zwnj;وه&zwnj;ی که تۆ فه&zwnj;رمانت پێداوم، ئه&zwnj;ویش ئه&zwnj;وه&zwnj;یه&zwnj;: ته&zwnj;نها خوا بپه&zwnj;رستن که په&zwnj;روه&zwnj;ردگاری من و په&zwnj;روه&zwnj;ردگاری ئێوه&zwnj;شه و من شایه&zwnj;ت بووم به&zwnj;سه&zwnj;ریانه&zwnj;وه هه&zwnj;تا له&zwnj;ناویاندا بووم، ئینجا کاتێک منت مران و گیانت کێشام، ئیتر ئه&zwnj;وه هه&zwnj;ر خۆت چاودێر و ئاگادار بوویت لێیان و هه&zwnj;ر تۆش شایه&zwnj;تیت به&zwnj;سه&zwnj;ر هه&zwnj;موو شتێکه&zwnj;وه&zwnj;.';
$t[118] = 'جا ئه&zwnj;گه&zwnj;ر ئه&zwnj;شکه&zwnj;نجه&zwnj;یان بده&zwnj;یت (له&zwnj;سه&zwnj;ر لادان و تاوانیان)، ئه&zwnj;وه به&zwnj;ڕاستی به&zwnj;نده&zwnj;ی خۆتن و ئه&zwnj;گه&zwnj;ر لێشیان خۆش ببیت، ئه&zwnj;وه به&zwnj;ڕاستی تۆ خۆت خوایه&zwnj;کی باڵاده&zwnj;ست و دانایت (ده&zwnj;زانیت کێ شایسته&zwnj;ی لێخۆشبوونه&zwnj;).';
$t[119] = '(ئینجا) خوای گه&zwnj;وره فه&zwnj;رمووی: ئه&zwnj;مه ئه&zwnj;و ڕۆژه&zwnj;یه که ڕاستگۆیان ڕاستگۆییه&zwnj;که&zwnj;یان سوودیان پێده&zwnj;گه&zwnj;یه&zwnj;نێت، باخه&zwnj;کانی به&zwnj;هه&zwnj;شت بۆیان ئاماده&zwnj;یه که چه&zwnj;نده&zwnj;ها ڕووبار به&zwnj;ژێر دره&zwnj;خته&zwnj;کانیدا ده&zwnj;ڕوات و بۆ هه&zwnj;میشه&zwnj;و هه&zwnj;تاهه&zwnj;تایی له&zwnj;و به&zwnj;هه&zwnj;شته&zwnj;دا ده&zwnj;مێننه&zwnj;وه و خوای په&zwnj;روه&zwnj;ردگاریش لێیان ڕازی بووه و ئه&zwnj;وانیش له خوا ڕازی بوون، ئه&zwnj;وه&zwnj;یه سه&zwnj;رفرازیی گه&zwnj;وره و بێ سنوور.';
$t[120] = 'ئاسمانه&zwnj;کان و زه&zwnj;وی هه&zwnj;ر خوا خاوه&zwnj;نیانه&zwnj;، هه&zwnj;روه&zwnj;ها ئه&zwnj;وه&zwnj;ش که تیایاندایه و ئه&zwnj;و زاته هه&zwnj;میشه ده&zwnj;سه&zwnj;ڵاتی به&zwnj;سه&zwnj;ر هه&zwnj;موو شتێکدا هه&zwnj;یه&zwnj;.';
?>