<?php
$t = array();
$t[1] = 'چه&zwnj;ند پیتێک له&zwnj;سه&zwnj;ره&zwnj;تای هه&zwnj;ندێ له&zwnj;سووره&zwnj;ته&zwnj;کانی قورئاندا هاتوون ژماره&zwnj;یان (14) پیته&zwnj;، نیوه&zwnj;ی کۆی پیته&zwnj;کانی زمانی عه&zwnj;ره&zwnj;بین، بۆ سه&zwnj;لماندنی (اعجاز) و مه&zwnj;زنی و گرنگی قورئان که&zwnj;له&zwnj;توانای هیچ که&zwnj;سدانیه به&zwnj;و پیتانه کتێبێکی ئاوا بێوێنه دابنێت به&zwnj;راده&zwnj;یه&zwnj;ک هه&zwnj;تا هه&zwnj;موو گرۆی ئاده&zwnj;میزاد و په&zwnj;ری کۆ ببنه&zwnj;وه&zwnj;بۆ ئه&zwnj;و مه&zwnj;به&zwnj;سته&zwnj;، بێگومان چه&zwnj;نده&zwnj;ها نهێنی تریشی تیایه&zwnj;، زاناکان له&zwnj;هه&zwnj;ندێکی دواون، هه&zwnj;ندێکیشیان وتویانه&zwnj;: هه&zwnj;ر خوا خۆی زانایه به&zwnj;نهێنی ئه&zwnj;و پیتانه. سوێند به&zwnj;م قورئانه که&zwnj;خاوه&zwnj;نی ڕێز و شکۆمه&zwnj;ندی یه (تۆ ئه&zwnj;ی محمد (صلى الله عليه وسلم) نێردراوی له&zwnj;لایه خواوه&zwnj;)';
$t[2] = 'خه&zwnj;ڵکی، باوه&zwnj;ڕیان نه&zwnj;کرد، به&zwnj;ڵکو سه&zwnj;رسام بوون له&zwnj;وه&zwnj;ی که&zwnj;یه&zwnj;کێک له&zwnj;خۆیان نێردراوه تادایان بچڵه&zwnj;کێنێت و بێداریان بکاته&zwnj;وه&zwnj;، جا کافران وتیان: ئه&zwnj;مه&zwnj;شتێکی سه&zwnj;یرو سه&zwnj;رسوڕهێنه&zwnj;ره&zwnj;، بۆیه وتیان:';
$t[3] = 'ئایا کاتێک مردین و بووینه خاک زیندوو ده&zwnj;بینه&zwnj;وه؟! دوای مردن گه&zwnj;ڕانه&zwnj;وه ئه&zwnj;سته&zwnj;مه&zwnj;! (په&zwnj;روه&zwnj;ردیگاریش له&zwnj;وه&zwnj;ڵامیاندا ده&zwnj;فه&zwnj;رموێت):';
$t[4] = 'به&zwnj;ڕاستی ئێمه زانیوومانه کاتێک که&zwnj;مردن، زه&zwnj;وی چۆن به&zwnj;ره&zwnj;به&zwnj;ره&zwnj;لاشه&zwnj;یان داده&zwnj;ڕزێنێت و ده&zwnj;یکاته&zwnj;وه به&zwnj;خاک، ئێمه تۆمارگه&zwnj;ی تایبه&zwnj;تیمان هه&zwnj;یه&zwnj;، که&zwnj;هه&zwnj;موو شتێکی تێدا تۆماره و هیچ شتێک له&zwnj;بارنی ئه&zwnj;وانه&zwnj;وه&zwnj;له&zwnj;ئێمه شاراوه&zwnj;و بزر نیه.';
$t[5] = 'به&zwnj;ڵام ئه&zwnj;مانه&zwnj;دژی ڕاستین و بڕوایان پێ نه&zwnj;هێناوه&zwnj;کاتێک بۆیان هات، به&zwnj;هۆی ئه&zwnj;وه&zwnj;وه&zwnj;ئه&zwnj;وانه&zwnj;له&zwnj;سه&zwnj;رگه&zwnj;ردانی و سه&zwnj;رلێشێواوییه&zwnj;کی ئاڵۆزدا ده&zwnj;ژین (هه&zwnj;ر ڕۆژه&zwnj;ی قسه&zwnj;یه&zwnj;ک ده&zwnj;که&zwnj;ن).';
$t[6] = 'ئایا ورد نه&zwnj;بوونه&zwnj;ته&zwnj;وه&zwnj;له&zwnj;و ئاسمانه&zwnj;ی که&zwnj;به&zwnj;راسه&zwnj;ریانه&zwnj;وه&zwnj;یه&zwnj;: چۆنمان دروست کردووه&zwnj;و ڕازاندوومانه&zwnj;ته&zwnj;وه&zwnj;به&zwnj;جۆرێک هیچ که&zwnj;م و کووڕی و که&zwnj;لێنێکی تێدا نی یه؟!';
$t[7] = 'هه&zwnj;روه&zwnj;ها زه&zwnj;ویمان ڕاخستووه&zwnj;و بڵاومان کردۆته&zwnj;وه و کژو کێومان له&zwnj;سه&zwnj;ری دامه&zwnj;زراندووه&zwnj;و له&zwnj;هه&zwnj;موو جۆره ڕوه&zwnj;کێکی جوان و ڕازاوه&zwnj;مان تیادا ڕواندووه&zwnj;.';
$t[8] = 'ئه&zwnj;مانه&zwnj;هه&zwnj;ر هه&zwnj;مووی بۆ ڕۆشنکردنه&zwnj;وه&zwnj;ی دڵ و یادخستنه&zwnj;وه&zwnj;یه بۆ هه&zwnj;موو ئه&zwnj;و به&zwnj;ندانه&zwnj;ی که&zwnj;به&zwnj;لای ئێمه&zwnj;دا دێنه&zwnj;وه و ڕه&zwnj;زامه&zwnj;ندی ئێمه&zwnj;یان مه&zwnj;به&zwnj;سته&zwnj;.';
$t[9] = 'له&zwnj;ئاسمانه&zwnj;وه&zwnj;ئاوێکی به&zwnj;پیت و به&zwnj;ره&zwnj;که&zwnj;تمان هه&zwnj;رده&zwnj;م دابه&zwnj;زاندووه&zwnj;جا به&zwnj;هۆی ئه&zwnj;وه&zwnj;وه&zwnj;چه&zwnj;نده&zwnj;ها باخ و دانه&zwnj;وێڵه&zwnj;ی ئه&zwnj;و کشتوکاڵانه&zwnj;ی که&zwnj;دره&zwnj;و ده&zwnj;کرێن ئه&zwnj;روێنین.';
$t[10] = 'هه&zwnj;روه&zwnj;ها دارخورمای قه&zwnj;شه&zwnj;نگ و بڵند سه&zwnj;ری هه&zwnj;ڵدا، که&zwnj;هێشووه خورمای جوان و له&zwnj;سه&zwnj;ر یه&zwnj;ک و ڕیزکراو ده&zwnj;گرێت.';
$t[11] = 'ئه&zwnj;مه&zwnj;ش بۆ ئه&zwnj;وه&zwnj;ی ببێته ڕزق و ڕۆزی بۆ به&zwnj;نده&zwnj;کان... هه&zwnj;ر به&zwnj;و ئاوو بارانه&zwnj;شوێنه مردووه&zwnj;کان زیندووده&zwnj;که&zwnj;ینه&zwnj;وه&zwnj;، هه&zwnj;ر ئاوه&zwnj;هایش له&zwnj;دوای مردن ئێوه له&zwnj;خاک دێنه ده&zwnj;ره&zwnj;وه.';
$t[12] = 'پێش ئه&zwnj;مانیش، قه&zwnj;ومی نوح، پێغه&zwnj;مبه&zwnj;ره&zwnj;که&zwnj;یان به&zwnj;درۆزانی... هه&zwnj;روه&zwnj;ها خاون بیره&zwnj;کان و هۆزی تمود یش.';
$t[13] = 'هه&zwnj;روه&zwnj;ها قه&zwnj;ومی هود و فیرعه&zwnj;ون و براکانی لوط، (واته&zwnj;: قه&zwnj;ومه&zwnj;که&zwnj;ی).';
$t[14] = 'خاوه&zwnj;ن باخه&zwnj;چڕه&zwnj;کان و قه&zwnj;ومه&zwnj;که&zwnj;ی تبع یش، (که&zwnj;یه&zwnj;کێک بووه&zwnj;له&zwnj;پاشاکانی ده&zwnj;وڵه&zwnj;تی حمیه&zwnj;ری لم یه&zwnj;مه&zwnj;ن)... هه&zwnj;ر یه&zwnj;کێک له&zwnj;وان باوه&zwnj;ڕیان به&zwnj;پێغه&zwnj;مبه&zwnj;ره&zwnj;کانیان نه&zwnj;کرد، به&zwnj;هۆی ئه&zwnj;وه&zwnj;وه&zwnj;شایسته&zwnj;ی پێشهاتنی هه&zwnj;ڕه&zwnj;شه&zwnj;ی ئێمه بوون.';
$t[15] = 'ئایا ئێمه له&zwnj;سه&zwnj;ره&zwnj;تا و یه&zwnj;که&zwnj;م جاردا که&zwnj;ئه&zwnj;و خه&zwnj;ڵکه&zwnj;مان دروست کرد ماندوو بووین و گیروگرفت هاته&zwnj;رێمان؟! نه&zwnj;خێر ماندوو نه&zwnj;بووین، به&zwnj;ڵکو ئه&zwnj;وان له&zwnj;گوماندا به&zwnj;رامبه&zwnj;ر به دروست بوونه&zwnj;وه&zwnj;یه&zwnj;کی تازه&zwnj;.';
$t[16] = 'سوێند به&zwnj;خوا بێگومان ئێمه ئاده&zwnj;میزادمان دروست کردووه&zwnj;و ده&zwnj;زانین چی به&zwnj;دڵ و ده&zwnj;روونیدا دێت... ئێمه له&zwnj;شاڕه&zwnj;گی دڵی لێی نزیکترین.';
$t[17] = 'کاتێک دوو فریشته چاودێره&zwnj;که&zwnj;ی ئاده&zwnj;میزاد که&zwnj;له&zwnj;لای ڕاست و چه&zwnj;پیه&zwnj;وه دانیشتوون، (هه&zwnj;موو گوفتارو کردارێکی تۆمار ده&zwnj;که&zwnj;ن به&zwnj;ده&zwnj;زگای زۆر پێشکه&zwnj;وتوو).';
$t[18] = 'هه&zwnj;ر قسه&zwnj;یه&zwnj;کی له&zwnj;ده&zwnj;م ده&zwnj;رده&zwnj;چێت خێرا چاودێرێکی ئاماده&zwnj;، تۆماری ده&zwnj;کات.';
$t[19] = 'ئه&zwnj;ی ئینسان، ته&zwnj;نگانه&zwnj;ی مردن و ئازاره&zwnj;کانی هات، هاوڕێ له&zwnj;گه&zwnj;ڵ ڕاستییه&zwnj;کاندا، ئه&zwnj;وسا پێت ده&zwnj;وترێت: ئا ئه&zwnj;وه&zwnj;به&zwnj;سه&zwnj;رهات و ڕووداوه&zwnj;یه که&zwnj;خۆتت لێ لاده&zwnj;داو په&zwnj;نات لێ ده&zwnj;گرت.';
$t[20] = 'له&zwnj;وه&zwnj;ودوا فوو ده&zwnj;کرێت به&zwnj;چوردا، ئیتر ئه&zwnj;وه&zwnj;رۆژی پێشهاتنی هه&zwnj;ڕه&zwnj;شه&zwnj;وو ئاگادار کردنه&zwnj;وه&zwnj;کانه&zwnj;.';
$t[21] = 'ڕۆژی قیامه&zwnj;ت دێت و هه&zwnj;موو که&zwnj;سێک دوو که&zwnj;سی له&zwnj;گه&zwnj;ڵدایه&zwnj;، یه&zwnj;کێکیان ده&zwnj;یگه&zwnj;یه&zwnj;نێت و ئه&zwnj;وه&zwnj;ی تریشیان شایه&zwnj;ته به&zwnj;سه&zwnj;ریه&zwnj;وه.';
$t[22] = 'به&zwnj;یه&zwnj;که&zwnj;یه&zwnj;که&zwnj;ی بێ باوه&zwnj;ڕان ده&zwnj;وتڕێت: سوێند به&zwnj;خوا به&zwnj;ڕاستی تۆ له&zwnj;م به&zwnj;سه&zwnj;رهاتانه&zwnj;نا ئاگابوویت و گوێی خۆتت لێ خه&zwnj;واندبوو، ئێستا ئێمه په&zwnj;رده&zwnj;مان له&zwnj;سه&zwnj;ر چاوه&zwnj;کانت لاداوه&zwnj;و هه&zwnj;موو شتێک وه&zwnj;کو خۆی ده&zwnj;بینیت و ئه&zwnj;مرۆ چاوه&zwnj;کانت تیژه...';
$t[23] = 'فریشته&zwnj;ی هاوه&zwnj;ڵ و چاودێری ڕوو به&zwnj;په&zwnj;روه&zwnj;ردگار ده&zwnj;ڵێت: هه&zwnj;رچی کاروکرده&zwnj;وه&zwnj;ی ئه&zwnj;م ئاده&zwnj;می یه هه&zwnj;یه هه&zwnj;مووی ئه&zwnj;وه&zwnj;ته&zwnj;لای من و یاداشت و تۆمارم کردووه&zwnj;.';
$t[24] = '(دوای لێپرسینه&zwnj;وه&zwnj;، خوا فه&zwnj;رمان ده&zwnj;دات به&zwnj;دوو فریشته&zwnj;که&zwnj;) هه&zwnj;رچی کافرو بێ باوه&zwnj;ڕێکی سه&zwnj;رکه&zwnj;ش و بوغزن هه&zwnj;یه فڕێی بده&zwnj;نه&zwnj;ناو ئاگری دۆزه&zwnj;خه&zwnj;وه...';
$t[25] = 'ئه&zwnj;وه&zwnj;ی که&zwnj;هه&zwnj;میشه به&zwnj;توندی قه&zwnj;ده&zwnj;غه&zwnj;ی هه&zwnj;موو خێرو چاکه&zwnj;یه&zwnj;کی ده&zwnj;کرد، مافی خه&zwnj;ڵکی پێشێل ده&zwnj;کرد، خۆی به&zwnj;گومان بوو، خه&zwnj;ڵکیشی ده&zwnj;خسته&zwnj;گومانه&zwnj;وه&zwnj;....';
$t[26] = 'ئه&zwnj;وه&zwnj;بوو که&zwnj;هاوه&zwnj;ڵ و شه&zwnj;ریکی بۆ خوا به&zwnj;ڕه&zwnj;وا ده&zwnj;زانی... که&zwnj;واته&zwnj;ئێوه هه&zwnj;ردووکتان ئه&zwnj;و ئاده&zwnj;می یه فڕێ بده&zwnj;نه&zwnj;ناو سزای سه&zwnj;خته&zwnj;وه&zwnj;.';
$t[27] = 'هاوه&zwnj;ڵه شه&zwnj;یتانه&zwnj;که&zwnj;شی پاکانه&zwnj;ده&zwnj;کات و ده&zwnj;ڵێت: په&zwnj;روه&zwnj;ردگارا، خۆ من له&zwnj;خشته&zwnj;م نه&zwnj;بردووه&zwnj;، من سه&zwnj;رکه&zwnj;شم نه&zwnj;کردووه&zwnj;، خۆی تاوانباره&zwnj;، به&zwnj;ڵام خۆی له&zwnj;نێو گومڕایی و سه&zwnj;رلێشێواویه&zwnj;کی دووردا نقووم بوو بوو.';
$t[28] = '(جا ئه&zwnj;وکاته&zwnj;ی شه&zwnj;یتان و ئاده&zwnj;میزاد لای خوادا ده&zwnj;بێته کێشه&zwnj;یان، خوا بێ ده&zwnj;نگیان ده&zwnj;کات) ده&zwnj;فه&zwnj;رموێت: شه&zwnj;ڕه ده&zwnj;مێ له&zwnj;لای مندا مه&zwnj;که&zwnj;ن، به&zwnj;ڕاستی من پێشتر هه&zwnj;ڕه&zwnj;شه&zwnj;ی هاتنی ئه&zwnj;م ڕۆژه&zwnj;م خستبووه&zwnj;به&zwnj;رچاوتان و، پێم ڕاگه&zwnj;یاندبوون.';
$t[29] = 'ئیتر قسه&zwnj;و بڕیاری من ناگۆڕێت و هه&zwnj;ڵناوه&zwnj;شێته&zwnj;وه.... من هێچ کات سته&zwnj;مکاریش نیم له&zwnj;به&zwnj;نده&zwnj;کانم.';
$t[30] = 'ڕۆژێک دێت به&zwnj;دۆزه&zwnj;خ ده&zwnj;ڵێین: ئایا پڕ بوویت؟ ئه&zwnj;ویش له&zwnj;وه&zwnj;ڵامدا ده&zwnj;ڵێت: ئایا که&zwnj;سی تر ماوه&zwnj;خه&zwnj;ڵکی زیاتر نیه؟!';
$t[31] = 'له&zwnj;ولایشه&zwnj;وه به&zwnj;هه&zwnj;شت نزیک خراوه&zwnj;ته&zwnj;وه&zwnj;له&zwnj;خواناس و پارێزکارو باوه&zwnj;ڕداران، بێ ئه&zwnj;وه&zwnj;ی دوور بێت لێیان، ده&zwnj;وترێت...';
$t[32] = 'ئه&zwnj;مه&zwnj;ئه&zwnj;و به&zwnj;ڵێنه بوو پێتان درابوو، بۆ هه&zwnj;موو ئه&zwnj;و که&zwnj;سانه&zwnj;ی که&zwnj;زۆر ته&zwnj;وبه&zwnj;کارو خۆپارێزن له&zwnj;گوناهو نافه&zwnj;رمانی خوا.';
$t[33] = 'هه&zwnj;ر که&zwnj;س له&zwnj;خوای میهره&zwnj;بان بترسێت و له&zwnj;په&zwnj;نهاندا خۆی له&zwnj;گوناه بپارێزێت و به&zwnj;دڵێکی به&zwnj;ئاگا و ته&zwnj;وبه&zwnj;کاره&zwnj;وه&zwnj;بۆ باره&zwnj;گای خوا بگه&zwnj;ڕێته&zwnj;وه... (جا به&zwnj;و به&zwnj;خته&zwnj;وه&zwnj;ڕانه ده&zwnj;وترێت):';
$t[34] = 'بفه&zwnj;رموون بچنه ناو به&zwnj;هه&zwnj;شته&zwnj;وه له&zwnj;گه&zwnj;ڵ ئاشتی و ئارامی وهێمنیدا، ئا ئه&zwnj;وه&zwnj;ئیتر ڕۆژی نه&zwnj;مریی و نه&zwnj;بڕاوه&zwnj;یه.';
$t[35] = 'هه&zwnj;رچیش داوای ده&zwnj;که&zwnj;ن و حه&zwnj;زی لێ ده&zwnj;که&zwnj;ن بۆیان ئاماده&zwnj;یه تیایدا، زۆر له&zwnj;وه&zwnj;ش زیاترمان لایه.';
$t[36] = 'خۆ ئێمه چه&zwnj;نده&zwnj;ها هۆزو نه&zwnj;ته&zwnj;وه&zwnj;ی پێش ئه&zwnj;وانمان له&zwnj;ناو برد، که&zwnj;ئه&zwnj;وان له&zwnj;مان به&zwnj;ده&zwnj;سه&zwnj;ڵاتترو به&zwnj;تواناتر بوون... خۆ ئه&zwnj;وان به&zwnj;شاره&zwnj;کان و شوێنه&zwnj;واره&zwnj;کاندا گه&zwnj;ڕاوان و تێپه&zwnj;ر بوون، ئایا هیچ ده&zwnj;ربازبوونێک له&zwnj;سزاکانیان به&zwnj;دی کرد؟!';
$t[37] = 'به&zwnj;ڕاستی ئا له&zwnj;و به&zwnj;سه&zwnj;رهاتانه&zwnj;دا یاده&zwnj;وه&zwnj;ری هه&zwnj;یه بۆ که&zwnj;سێک دڵێکی زیندووی هه&zwnj;نێت، یاخود گوێ بۆ قورئان ڕادێرێت، له&zwnj;کاتێکدا ئاماده&zwnj;بێت بۆ فه&zwnj;رمانبه&zwnj;رداریی و کردنی هه&zwnj;موو کارێکی دروست.';
$t[38] = 'به&zwnj;ڕاستی ئێمه ئاسمانه&zwnj;کان و زه&zwnj;وی و هه&zwnj;رچی له&zwnj;نێوانیاندا هه&zwnj;یه به&zwnj;شه&zwnj;ش ڕۆژ دروستمان کردوون، هیچ ماندویه&zwnj;تیه&zwnj;کیش ڕوی تێ نه&zwnj;کردین.';
$t[39] = 'جا تۆ ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر (صلى الله عليه وسلم) دان به&zwnj;خۆتا بگره&zwnj;له&zwnj;به&zwnj;رامبه&zwnj;ر ئه&zwnj;و هه&zwnj;موو ناحه&zwnj;قی و نادروستیانه&zwnj;وه که&zwnj;ده&zwnj;یڵێن و دێته ڕێت، (بۆ به&zwnj;رگه&zwnj;گرتنی ئه&zwnj;و گیروگرفتانه&zwnj;ی که&zwnj;بۆت پێش ده&zwnj;هێنن)، پێش خۆر هه&zwnj;ڵاتن و پێش ئاوابوونی ته&zwnj;سبیحات و سوپاس و ستایشی په&zwnj;روه&zwnj;ردیگارت بکه&zwnj;.';
$t[40] = 'له&zwnj;شه&zwnj;وگاریشدا ته&zwnj;سبیحات و ستایشی خوا بکه&zwnj;، هه&zwnj;روه&zwnj;ها له&zwnj;دوای نوێژه&zwnj;کانیش.';
$t[41] = 'گوێش به&zwnj;چاکی ڕابگره&zwnj;!! ڕۆژێک دێت بانگ وێژێک بانگ ده&zwnj;کات له&zwnj;شوێنێکی نزیکه&zwnj;وه&zwnj;...';
$t[42] = 'ئه&zwnj;و ڕۆژه ده&zwnj;نگێکی زۆر به&zwnj;رز و سامناک به&zwnj;ڕاستیی ده&zwnj;بیستن، دیاره که&zwnj;ئه&zwnj;وه&zwnj;رۆژی هاتنه&zwnj;ده&zwnj;ره&zwnj;وه&zwnj;یه له&zwnj;توێی خاکدا.';
$t[43] = 'به&zwnj;ڕاستی هه&zwnj;ر ئێمه ژیان ده&zwnj;به&zwnj;خشین و ده&zwnj;شیان مرێنین، گه&zwnj;ڕانه&zwnj;وه&zwnj;ش هه&zwnj;ر بۆ لای ئێمه&zwnj;یه.';
$t[44] = 'ڕۆژێک دێت زه&zwnj;وی له&zwnj;ئاستی ئه&zwnj;وان شه&zwnj;ق ده&zwnj;بات و خه&zwnj;ڵکی له&zwnj;گۆڕه&zwnj;کانیان به&zwnj;گورجی دێنه ده&zwnj;ره&zwnj;وه&zwnj;.... ئا ئه&zwnj;وه&zwnj;کۆ کردنه&zwnj;وه&zwnj;یه&zwnj;که لای ئێمه کارێکی زۆر ئاسانه (بۆ لێپرسینه&zwnj;وه&zwnj;).';
$t[45] = 'ئه&zwnj;ی محمد (صلى الله عليه وسلم) ئێمه چاک ئاگادارین به&zwnj;وه&zwnj;ی که&zwnj;ده&zwnj;یڵێن و چۆن دژایه&zwnj;تی ئه&zwnj;م ڕاستی یه ده&zwnj;که&zwnj;ن!... خۆ تۆ زۆردارنیت به&zwnj;سه&zwnj;ریانه&zwnj;وه تا باوه&zwnj;ڕیان به&zwnj;سه&zwnj;ردا بسه&zwnj;پێنیت.. که&zwnj;واته&zwnj;تۆ ته&zwnj;نها ئه&zwnj;وه&zwnj;ت له&zwnj;سه&zwnj;ره&zwnj;که&zwnj;به&zwnj;قورئان یاداوه&zwnj;ریی ئه&zwnj;و که&zwnj;سانه&zwnj;بکه&zwnj;یت که&zwnj;له&zwnj;هه&zwnj;ڕه&zwnj;شه&zwnj;کانی ئێمه ده&zwnj;ترسن.';
?>