<?php
$t = array();
$t[1] = 'سوێند به&zwnj;و ڕه&zwnj;شه&zwnj;با و ئه&zwnj;و بایه&zwnj;ی که&zwnj;هه&zwnj;ورو هه&zwnj;ڵمی ئاو هه&zwnj;ڵده&zwnj;گرێت و په&zwnj;خشی ده&zwnj;کاته&zwnj;وه&zwnj;، یان ته&zwnj;پ و تۆز به&zwnj;رز ده&zwnj;کاته&zwnj;وه (که&zwnj;ڕۆڵی هه&zwnj;یه له&zwnj;باران بارایندا).';
$t[2] = 'ئینجا به&zwnj;هه&zwnj;ڵگرانی باری قورس و گران (له&zwnj;هه&zwnj;ڵمی ئاو).';
$t[3] = 'هه&zwnj;روه&zwnj;ها به&zwnj;و هه&zwnj;ورانه&zwnj;ک ه&zwnj;به&zwnj;ئاسانی به&zwnj;ئاسماندا له&zwnj;شوێنێکوه ده&zwnj;گوێزرێته&zwnj;وه بۆ شوێنێکی تر، یاخود سوێند به&zwnj;و که&zwnj;شتیانه&zwnj;ی به&zwnj;ئاسانی به&zwnj;سه&zwnj;ر ئاودا دێن و ده&zwnj;چن.';
$t[4] = 'ئینجا سوێند به&zwnj;و فریشتانه&zwnj;ی که&zwnj;کاروبار دابه&zwnj;ش ده&zwnj;که&zwnj;ن.';
$t[5] = 'به&zwnj;ڕاستی ئه&zwnj;و به&zwnj;ڵێنانه&zwnj;ی پێتان دراوه&zwnj;ڕاسته&zwnj;و هه&zwnj;ر دێته دی.';
$t[6] = 'بێگومان ڕۆژی لێ پرسینه&zwnj;وه&zwnj;ش هه&zwnj;ر دێت و به&zwnj;ڕێوه&zwnj;یه.';
$t[7] = 'سوێند به&zwnj;ئاسمانی خاوه&zwnj;ن ڕێگه&zwnj;و ڕێک و پێک و ڕازاوه&zwnj;.';
$t[8] = 'بێگومان ئه&zwnj;ی خوانه&zwnj;ناسان ئێوه له&zwnj;گوفتارێکی بێ سه&zwnj;روبندان که&zwnj;دووره له&zwnj;ڕاستی و حه&zwnj;قیقه&zwnj;ته&zwnj;وه.';
$t[9] = 'ئه&zwnj;و که&zwnj;سه&zwnj;ی له&zwnj;زانستی خوادا لادراو ه&zwnj;به&zwnj;هۆی تاوانیه&zwnj;وه له&zwnj;باوه&zwnj;ڕ هێنان لاده&zwnj;درێت.';
$t[10] = 'ده&zwnj;ک به&zwnj;کوشتچن درۆزن و بوختانچیه&zwnj;کان....';
$t[11] = 'ئه&zwnj;وانه&zwnj;ی که&zwnj;له&zwnj;گومڕای و تاریکیدا ڕۆچوون و پێوه&zwnj;ی سه&zwnj;رگه&zwnj;رمن.';
$t[12] = 'به&zwnj;گاڵته و ته&zwnj;ڵفیسیه&zwnj;وه ده&zwnj;ڵێن یان ده&zwnj;پرسن: ڕۆژی قیامه&zwnj;ت که&zwnj;ی پێش دێت؟!';
$t[13] = 'ئه&zwnj;و ڕۆژه که&zwnj;به&zwnj;رپا ده&zwnj;بێت به&zwnj;ئاگر ده&zwnj;برژێنرێن....';
$t[14] = 'پێیان ده&zwnj;وترێت: بچێژن ئه&zwnj;و به&zwnj;ڵایه&zwnj;ی که&zwnj;به&zwnj;سه&zwnj;ر خۆتان هێنا، ئه&zwnj;مه&zwnj;ئه&zwnj;و سزاو ئازاره&zwnj;یه که&zwnj;به&zwnj;گاڵته&zwnj;وه په&zwnj;له&zwnj;تان له&zwnj;پێشهاتنی ده&zwnj;کرد.';
$t[15] = 'به&zwnj;ڕاستی پارێزکارانیش له&zwnj;باخه&zwnj;کان و کانیاوه&zwnj;کانی به&zwnj;هه&zwnj;شتدا ژیانی خۆش ده&zwnj;به&zwnj;نه&zwnj;سه&zwnj;ر.';
$t[16] = 'ئه&zwnj;و شتانه&zwnj;ی که&zwnj;په&zwnj;روه&zwnj;ردگار بۆی ئاماده&zwnj;کردون پێیان ده&zwnj;به&zwnj;خشێت، ئه&zwnj;وانه&zwnj;یش به&zwnj;خۆشی و شادیه&zwnj;وه وه&zwnj;ریده&zwnj;گرن، به&zwnj;ڕاستی ئه&zwnj;وانه&zwnj;پێشتر و شادیه&zwnj;وه وه&zwnj;ریده&zwnj;گرن، به&zwnj;ڕاستی ئه&zwnj;وانه پێشتر له&zwnj;دنیاداچاکه&zwnj;کارو چاکه&zwnj;خواز بوون....';
$t[17] = 'له&zwnj;شه&zwnj;وگاردا زۆر که&zwnj;م ده&zwnj;خه&zwnj;وتن و ڕاده&zwnj;کشان (چونکه&zwnj;سه&zwnj;رگه&zwnj;رمی شه&zwnj;و نوێژ و ده&zwnj;ور کردنه&zwnj;وه&zwnj;ی قورئان و یادی خوا بوون)....';
$t[18] = 'له&zwnj;به&zwnj;ره&zwnj;به&zwnj;یانه&zwnj;کانیشدا داوای لێخۆشبوونیان له&zwnj;په&zwnj;روه&zwnj;ردگاریان ده&zwnj;کرد (تا لێیان ببورێت)...';
$t[19] = 'له&zwnj;ماڵ و سامانیشیاندا به&zwnj;شی هه&zwnj;ژارو نه&zwnj;داریان ته&zwnj;رخان کردبوو.';
$t[20] = 'بێگومان له&zwnj;سه&zwnj;ر گۆی زه&zwnj;ویدا به&zwnj;ڵگه و نیشانه&zwnj;ی زۆر هه&zwnj;یه (له&zwnj;سه&zwnj;ر ده&zwnj;سه&zwnj;ڵات و زانیاری خوا) بۆ که&zwnj;سانێک که&zwnj;به&zwnj;شوێن دڵنیاییدا ده&zwnj;گه&zwnj;ڕێن.';
$t[21] = 'هه&zwnj;روه&zwnj;ها له&zwnj;خودی خۆشتاندا (له&zwnj;هه&zwnj;موو خانه&zwnj;یه&zwnj;کی له&zwnj;شدا، له&zwnj;هه&zwnj;موو ئه&zwnj;ندامێکداو، له&zwnj;هه&zwnj;موو کۆئه&zwnj;ندامێکدا، له&zwnj;رۆح ونه&zwnj;فس و عه&zwnj;قڵدا... هتد به&zwnj;ڵگه&zwnj;ی بێ سنوور هه&zwnj;یه له&zwnj;سه&zwnj;ر زانایی و به&zwnj;توانایی و جوانکاری زاتی به&zwnj;دیهێنه&zwnj;ر) ئایا ئه&zwnj;وه&zwnj;بۆ بینایتان ناخه&zwnj;نه&zwnj;کار.';
$t[22] = 'سه&zwnj;رچاوه&zwnj;ی ڕزق و ڕۆزیتان له&zwnj;ئاسمانه&zwnj;وه&zwnj;یه (له&zwnj;خوا داوابکه&zwnj;ن) هه&zwnj;روه&zwnj;ها ئه&zwnj;وانیش که&zwnj;به&zwnj;ڵێنتان پێدراوه.';
$t[23] = 'سوێند به&zwnj;په&zwnj;روه&zwnj;ردگاری ئاسمان و زه&zwnj;وی، ئه&zwnj;و به&zwnj;ڵێنانه حه&zwnj;قیقه&zwnj;ت و ڕاسته&zwnj;قینه&zwnj;یه&zwnj;، هه&zwnj;روه&zwnj;ک چۆن ئێوه قسه&zwnj;ی ڕاست و دروست ده&zwnj;که&zwnj;ن.';
$t[24] = 'ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر (صلى الله عليه وسلم) ئایا هه&zwnj;واڵی میوانه به&zwnj;ڕێزه&zwnj;کانی ئیبراهیمت پێنه&zwnj;گه&zwnj;یشتووه؟';
$t[25] = 'کاتێک چوون بۆ سه&zwnj;ردانی و وتیان: سڵاو، ئه&zwnj;ویش وتی، سڵاو له&zwnj;ئێوه&zwnj;ش بێت، هه&zwnj;رچه&zwnj;نده&zwnj;ناتانناسم.';
$t[26] = 'ئه&zwnj;وسا ئیتر خۆی دزیه&zwnj;وه چوو به&zwnj;هاوکاری خێزانی گوێره&zwnj;که&zwnj;یه&zwnj;کی قه&zwnj;ڵه&zwnj;وی (سه&zwnj;ری بڕی و سووریانکرده&zwnj;وه و) هێنای.';
$t[27] = 'بردی و لێی نزیک کردنه&zwnj;وه&zwnj;،، فه&zwnj;رمووی لێکردن که&zwnj;بینی ناخۆن، وتی: ئه&zwnj;وه&zwnj;نا فه&zwnj;رموون، ئه&zwnj;وه&zwnj;ناخۆن؟!';
$t[28] = 'ئیبراهیم ترسێکی لێنیشت (چونکه&zwnj;وای زانی شتێکیان له&zwnj;ژێر سه&zwnj;ردایه&zwnj;) بۆیه خچرا وتیان: مه&zwnj;ترسه&zwnj;ئێمه فریشته&zwnj;یه&zwnj;، مژده&zwnj;شیان پێده&zwnj;به&zwnj;خشێت.';
$t[29] = 'خێزانه&zwnj;که&zwnj;ی قریوه&zwnj;یه&zwnj;کی کرد و ده&zwnj;نگی لێ به&zwnj;رز بۆوه و ده&zwnj;ستی به&zwnj;ئاسته&zwnj;م دا به&zwnj;روومه&zwnj;تی خۆیدا (وه&zwnj;کو داب و نـریتی ئافره&zwnj;تان له&zwnj;کاتی سه&zwnj;رسامیدا) وتی: پیریژنێکی نه&zwnj;زۆک، چۆن منداڵی ده&zwnj;بێت؟!';
$t[30] = 'فریشته&zwnj;کان وتیان: ئه&zwnj;وه&zwnj;فه&zwnj;رمانی په&zwnj;روه&zwnj;ردگارته&zwnj;و ئه&zwnj;و زاتێکی دانا و زانایه.';
$t[31] = 'ئینجا ئیبراهیم لێی، پرسین: بێجگه له&zwnj;و مژده&zwnj;یه چ کارێکی گرنگتان به&zwnj;ده&zwnj;سته&zwnj;وه&zwnj;یه؟!';
$t[32] = 'فریشته&zwnj;کان وتیان: بڕاستی ئێمه ڕه&zwnj;وانه&zwnj;کراوین بۆ گه&zwnj;لێکی تاوانبار و تاوانکار.';
$t[33] = 'بۆ ئه&zwnj;وه&zwnj;ی به&zwnj;رد بارانیان بکه&zwnj;ین به&zwnj;به&zwnj;ردێک که&zwnj;له&zwnj;قوڕ دروست کراوه&zwnj;.';
$t[34] = 'هه&zwnj;موو ئه&zwnj;و به&zwnj;ردانه&zwnj;نیشانه&zwnj;دارن له&zwnj;لایه&zwnj;ن په&zwnj;روه&zwnj;ردگارته&zwnj;وه&zwnj;بۆ ناپوخت و له&zwnj;سنوور درچووه&zwnj;کان.';
$t[35] = 'جا هه&zwnj;رچی له&zwnj;و ناوچه&zwnj;یه&zwnj;دا بوو له&zwnj;ئیمانداران ده&zwnj;رمانکردن و ڕزگارمان کردن....';
$t[36] = 'له&zwnj;ماڵه موسوڵمانێکیش زیاترمان به&zwnj;دی نه&zwnj;کرد.';
$t[37] = '(له&zwnj;پاشان ئه&zwnj;و شوێنه&zwnj;مان به&zwnj;شێوه&zwnj;یه&zwnj;ک کاول کرد)، کردمانه&zwnj;په&zwnj;ند و به&zwnj;ڵگه بۆ هه&zwnj;موو ئه&zwnj;و که&zwnj;سانه&zwnj;ی له&zwnj;سزای به&zwnj;ئێشی ئێمه ده&zwnj;ترسن.';
$t[38] = 'له&zwnj;به&zwnj;سه&zwnj;رهاتی موسایشدا په&zwnj;ند و ئامۆژگاری هه&zwnj;یه که&zwnj;ناردمان بۆ لای فیرعه&zwnj;ون به&zwnj;موعجزه&zwnj;یه&zwnj;کی ئاشکراو ڕوونه&zwnj;وه&zwnj;.';
$t[39] = 'ئه&zwnj;ویش پشتی هه&zwnj;لکرد به&zwnj;هۆی سه&zwnj;ربازو دارو ده&zwnj;سته&zwnj;که&zwnj;یه&zwnj;وه&zwnj;و وتی (ئه&zwnj;م فرستاده&zwnj;یه&zwnj;) جادووگه&zwnj;ره&zwnj;، یان شێته!!';
$t[40] = 'جا ئێمه&zwnj;ش خۆی و سه&zwnj;ربازه&zwnj;کانیمان گرفتار کرد و پێچاماننه&zwnj;وه و فڕێمان دانه&zwnj;ده&zwnj;ریاوه&zwnj;، له&zwnj;کاتێکدا ئه&zwnj;و لۆمه&zwnj;کراوه.';
$t[41] = 'هه&zwnj;روه&zwnj;ها له&zwnj;به&zwnj;سه&zwnj;رهاتی عادیش&zwnj;دا په&zwnj;ند و ئامۆژگاری هه&zwnj;یه&zwnj;، کاتێک له&zwnj;سه&zwnj;ره&zwnj;نجامی سه&zwnj;رکه&zwnj;شیان بایه&zwnj;کی بێ خێرمان هه&zwnj;ڵکرده سه&zwnj;ریان.';
$t[42] = 'ئه&zwnj;و بایه هه&zwnj;ر شوێنێک و هه&zwnj;ر شتێک و هه&zwnj;ر که&zwnj;سێکدا بڕۆیشتایه وازی لێ نه&zwnj;ده&zwnj;هێنا هه&zwnj;تا بێ که&zwnj;ڵکی ده&zwnj;کرد و وه&zwnj;ک شتێکی فڕێ دراوی لێ ده&zwnj;کرد.';
$t[43] = 'هه&zwnj;روه&zwnj;ها له&zwnj;به&zwnj;سه&zwnj;رهاتی پمودیشدا په&zwnj;ند و ئامۆژگاری هه&zwnj;یه&zwnj;، کاتێک پێیان وترا: تا ماوه&zwnj;یه&zwnj;ک ڕابوێرن!!';
$t[44] = 'ئه&zwnj;وانیش له&zwnj;فه&zwnj;رمانی په&zwnj;روه&zwnj;ردگاریان یاخی بوون، کتوپڕ بروسکه&zwnj;یه&zwnj;ک پێچانیه&zwnj;وه وبه&zwnj;چاوی ئه&zwnj;بڵه&zwnj;قه&zwnj;وه ده&zwnj;یانروانی.';
$t[45] = 'ئیتر نه&zwnj;یانتوانی هه&zwnj;ڵسنه&zwnj;وه و سه&zwnj;رکه&zwnj;وتووش نه&zwnj;بوون.';
$t[46] = 'پێشتریش هۆزی نوح به&zwnj;ڕاستی هه&zwnj;ر تاوانبارو خوانه&zwnj;ناس ولارو وێربوون (ئه&zwnj;وانیشمان به&zwnj;زریانه&zwnj;که له&zwnj;ناوبرد).';
$t[47] = 'ئێمه ئاسمانمان به&zwnj;ده&zwnj;ستی قودره&zwnj;تی خۆمان دروست کرد، به&zwnj;رده&zwnj;وامیش گه&zwnj;وره&zwnj;ی ده&zwnj;که&zwnj;ین و فراوانی ده&zwnj;که&zwnj;ین. (ئه&zwnj;مه&zwnj;ئاماژه&zwnj;یه بۆ ڕاستیه&zwnj;کی زانستی که&zwnj;زاناکانی گه&zwnj;ردوونناسی ده&zwnj;یسه&zwnj;لمێنن).';
$t[48] = 'زه&zwnj;ویشمان ڕاخستووه (ژیانمان له&zwnj;سه&zwnj;ری ئاسان کردووه&zwnj;) به&zwnj;ڕاستی خوا زاتێکی کارساز و کاربه&zwnj;جێیه.';
$t[49] = ': له&zwnj;هه&zwnj;موو شتێک ئێمه جووتمان دروست کردوه (له&zwnj;جیهانی ئاده&zwnj;میزاد و گیاندار و ڕوه&zwnj;ک و بێ گیاندا، له&zwnj;هه&zwnj;موو شتێکی ماددی و مه&zwnj;عنه&zwnj;ویدا) بۆ ئه&zwnj;وه&zwnj;ی بیربکه&zwnj;نه&zwnj;وه و یاده&zwnj;وه&zwnj;ری وه&zwnj;ربگرن.';
$t[50] = 'به&zwnj;په&zwnj;له&zwnj;و گورج و گۆڵ بن بۆ به&zwnj;ده&zwnj;ست هێنانی ڕه&zwnj;زامه&zwnj;ندی خوا، بۆ هه&zwnj;رچی په&zwnj;روه&zwnj;ردگار پێی خۆشه&zwnj;، دڵنیاش بن که&zwnj;من (واته&zwnj;پێغه&zwnj;مبه&zwnj;ر (صلى الله عليه وسلم) له&zwnj;لایه&zwnj;ن خواوه&zwnj;ترسێنه&zwnj;رێکی ئاشکرام ئه&zwnj;گه&zwnj;ر یاخی و سه&zwnj;رکه&zwnj;ش بن.....';
$t[51] = 'هیچ مه&zwnj;که&zwnj;ن به&zwnj;هاوه&zwnj;ڵ و هاوتای خوا، چونکه من به&zwnj;ڕاستی نێردراوێکی ترسێنه&zwnj;ری ئاشکرام بۆتان له&zwnj;لایه&zwnj;ن خواوه&zwnj;.';
$t[52] = 'هه&zwnj;ر نا به&zwnj;و شێوه&zwnj;یه هیچ پێغه&zwnj;مبه&zwnj;رێک له&zwnj;پێش ئه&zwnj;ماندا نه&zwnj;هاتووه&zwnj;، خوانه&zwnj;ناسان نه&zwnj;یانوتبێت جادووگه&zwnj;ره&zwnj;، یان شێته.';
$t[53] = 'سه&zwnj;یره! ئایا ده&zwnj;ڵێی به&zwnj;گوێی یه&zwnj;کدا خوێدویانه و یه&zwnj;کتریان ڕاسپاردووه&zwnj;، نه&zwnj;خێر، به&zwnj;ڵکو ئه&zwnj;وانه&zwnj;هه&zwnj;ر قه&zwnj;ومێکی سته&zwnj;مکارو سه&zwnj;رکه&zwnj;شن.';
$t[54] = 'تۆ ئه&zwnj;ی محمد (صلى الله عليه وسلم) گوێیان مه&zwnj;ده&zwnj;رێ چونکه تۆ لۆمه&zwnj;کراو نابیت (له&zwnj;سه&zwnj;ر باوه&zwnj;ڕ نه&zwnj;هێنانیان).';
$t[55] = 'به&zwnj;ڵکو تۆ هه&zwnj;میشه ئامۆژگاریان بکه&zwnj;، چونکه&zwnj;به&zwnj;ڕاستی ئامۆژگاری سوود به&zwnj;ئیمانداران ده&zwnj;گه&zwnj;یه&zwnj;نێت.';
$t[56] = 'بێگومان من په&zwnj;ری و ئاده&zwnj;میزاد م دروست نه&zwnj;کردووه&zwnj;ته&zwnj;نها بۆ ئه&zwnj;وه&zwnj;نه&zwnj;بێت که&zwnj;من بپه&zwnj;رستن و فه&zwnj;رمانبه&zwnj;ردارم بن.';
$t[57] = 'نه&zwnj;رۆزیم لێیان ده&zwnj;وێت نه&zwnj;خواردن و خواردنه&zwnj;وه&zwnj;.';
$t[58] = 'چونکه&zwnj;به&zwnj;ڕاستی هه&zwnj;ر خوا خۆی ڕۆزی به&zwnj;خشه&zwnj;و خاوه&zwnj;نی هێزو ده&zwnj;سه&zwnj;ڵاتی پته&zwnj;وه&zwnj;.';
$t[59] = 'بێگومان بۆ ئه&zwnj;وانه&zwnj;ی سته&zwnj;میان کردووه&zwnj;تۆڵه&zwnj;و سزای زۆرمان بۆ ئاماده&zwnj;کردوون، هه&zwnj;روه&zwnj;ک چۆن بۆ هاوه&zwnj;ڵ و هاوبیره&zwnj;کانیان ئاماده&zwnj;مانکردووه&zwnj;، ده&zwnj;با ئیتر په&zwnj;له&zwnj;م لێنه&zwnj;که&zwnj;ن (چونکه&zwnj;به&zwnj;م زوانه&zwnj;یه&zwnj;خه&zwnj;یان ده&zwnj;گرێت).';
$t[60] = 'ئینجا وه&zwnj;یل و ئاهو ناڵه بۆ ئه&zwnj;وانه&zwnj;ی که&zwnj;رێبازی کوفرو خوانه&zwnj;ناسیان گرتۆته&zwnj;به&zwnj;ر له&zwnj;و ڕۆژه&zwnj;ی که&zwnj;هه&zwnj;ڕه&zwnj;شه&zwnj;یان لێ کراوه&zwnj;و به&zwnj;م زووانه&zwnj;پێش دێت.';
?>