<?php
$t = array();
$t[1] = 'سوێند به&zwnj;کێوی _ طور _ (که&zwnj;خوای گه&zwnj;وره&zwnj;گفتوگۆی له&zwnj;گه&zwnj;ڵ موسادا له نزیکی ئه&zwnj;نجامدا).';
$t[2] = 'سوێند به&zwnj;کتێبی نوسراو که (لوح المحفوظه&zwnj;).';
$t[3] = 'که&zwnj;له&zwnj;لاپه&zwnj;ڕه&zwnj;ی ته&zwnj;نگی گرنگدایه&zwnj;';
$t[4] = ': سوێند _ به&zwnj;که&zwnj;عبه&zwnj;_ که&zwnj;هه&zwnj;میشه ئاوه&zwnj;دانه به نوێژكه&zwnj;ران، وه (ئه&zwnj;وانه&zwnj;ی بۆ حه&zwnj;ج و عه&zwnj;مره&zwnj;ده&zwnj;چن) هه&zwnj;روه&zwnj;ها (بیت المعمور) شوێنێکه له&zwnj;ئاسمانی حه&zwnj;وته&zwnj;م که&zwnj;دیاریکراوه بۆ خواپه&zwnj;رستی فریشته&zwnj;کان هه&zwnj;روه&zwnj;ک چۆن که&zwnj;عبه پیرۆزه لای ئیمانداران، (له&zwnj;فه&zwnj;رموده&zwnj;یه&zwnj;کی ڕاست و دروستدا ئاماژه&zwnj;ی بۆ کراوه&zwnj;).';
$t[5] = 'هه&zwnj;روه&zwnj;ها سوێند به&zwnj;سه&zwnj;قفی به&zwnj;رزو بڵند (که&zwnj;ئاسمان ده&zwnj;گرێته&zwnj;وه&zwnj;، خوای گه&zwnj;وره&zwnj;ده&zwnj;یه&zwnj;وێت سه&zwnj;رنجمان ڕاکێشێت بۆ لای).';
$t[6] = 'سوێند به&zwnj;ده&zwnj;ریای پڕ له&zwnj;ئاو یان ده&zwnj;ریای داگیرساو (که&zwnj;قیامه&zwnj;ت داده&zwnj;گرسێت و ده&zwnj;ته&zwnj;قێته&zwnj;وه&zwnj;).';
$t[7] = '(په&zwnj;روه&zwnj;ردگاری مه&zwnj;زن سوێند به&zwnj;و هه&zwnj;موو شتانه&zwnj;ده&zwnj;خوات، ئینجا ده&zwnj;فه&zwnj;رموێت): به&zwnj;ڕاستی سزاو ئازاری په&zwnj;روه&zwnj;ردگاری تۆ ئه&zwnj;ی محمد (صلی الله علیه وسلم) هه&zwnj;ر پێش دێت.';
$t[8] = 'که&zwnj;سیش ناتوانێت به&zwnj;ری بگرێت و ده&zwnj;ستی پێوه بنێت و نه&zwnj;هێڵێت.';
$t[9] = 'ئه&zwnj;و ڕۆژه ئه&zwnj;ستێره وهه&zwnj;سه&zwnj;ره&zwnj;کانی ئاسمان هه&zwnj;موو به&zwnj;یه&zwnj;کدا ده&zwnj;ده&zwnj;ن، (خوای گه&zwnj;وره&zwnj;تێکیان ده&zwnj;دات وهه&zwnj;ڵیان ده&zwnj;وه&zwnj;شێنێت)';
$t[10] = 'کێوه&zwnj;کانیش له&zwnj;جێی خۆیان ده&zwnj;جوڵێن و ده&zwnj;ڕۆن، (وه&zwnj;ک خۆری، وه&zwnj;ک هه&zwnj;ور، زه&zwnj;ویان پێ ته&zwnj;خت ده&zwnj;کرێت).';
$t[11] = 'که&zwnj;واته واوه&zwnj;یلا و هاوار بۆ ئه&zwnj;وانه&zwnj;ی که&zwnj;باوه&zwnj;ڕیان به&zwnj;حه&zwnj;ق و ڕاستی نه&zwnj;کرد و به&zwnj;درۆیان ده&zwnj;زانی له&zwnj;و ڕۆژه&zwnj;دا.';
$t[12] = 'ئه&zwnj;وانه&zwnj;ی که&zwnj;رۆچووه و گاڵته ده&zwnj;که&zwnj;ن به&zwnj;ئیسلام و پێغه&zwnj;مبه&zwnj;ر و قورئان.';
$t[13] = 'رۆژێک دێت به&zwnj;توندی پاڵیان پێوه ده&zwnj;نرێت بۆ ناو دۆزه&zwnj;خ (ده&zwnj;نگ له&zwnj;سینه&zwnj;وه&zwnj;یانه&zwnj;وه دێت ئه&zwnj;وه&zwnj;نده&zwnj;پاڵه&zwnj;که تونده&zwnj;).';
$t[14] = 'پێیان ده&zwnj;وترێت ئه&zwnj;مه&zwnj;ئه&zwnj;و ئاگرو دۆزه&zwnj;خه&zwnj;یه که&zwnj;کاتی خۆی بڕواتان پێی نه&zwnj;بوو به&zwnj;گاڵته&zwnj;تان گرتبوو';
$t[15] = 'باشه&zwnj;، ئه&zwnj;مه&zwnj;جادووه&zwnj;نیشانتان ده&zwnj;درێت یان ئێوه ڕاستی نابینن؟!';
$t[16] = 'ده&zwnj;بێت بچنه&zwnj;ناوی و سزا بچێژن، جا ئیتر خۆڕاده&zwnj;گرن یان خۆڕاناگرن وه&zwnj;ک یه&zwnj;که بۆتان، بێگومان یان خۆڕاناگرن وه&zwnj;ک یه&zwnj;که بۆتان، بێگومان تۆڵه&zwnj;ی ئه&zwnj;و کاروکرده&zwnj;وانه&zwnj;تان هه&zwnj;ر ده&zwnj;درێته&zwnj;وه که&zwnj;ده&zwnj;تانکرد.';
$t[17] = 'له&zwnj;ولاشه&zwnj;وه&zwnj;به&zwnj;ڕاستی ئه&zwnj;وانه&zwnj;ی که&zwnj;له&zwnj;دنیادا خواناس و دینداربوون، له&zwnj;باخه&zwnj;کانی به&zwnj;هه&zwnj;شت و ناز و نیعمه&zwnj;تدا، ژیانی پڕ له&zwnj;به&zwnj;ختیاری و کامه&zwnj;رانی ده&zwnj;به&zwnj;نه&zwnj;سه&zwnj;ر.';
$t[18] = 'زۆر که&zwnj;یف خۆش و دڵخۆش و ڕووخۆشن به&zwnj;و به&zwnj;خششانه&zwnj;ی که په&zwnj;روه&zwnj;ردگاریان پێی به&zwnj;خشیون، هه&zwnj;ر په&zwnj;روه&zwnj;ردیگاریشیان له&zwnj;سزاو تاڵاوی دۆزه&zwnj;خ پاڕاستونی.';
$t[19] = 'پێیان ده&zwnj;وترێت بخۆن و بخۆنه&zwnj;وه نۆشی گیانتان بێت له&zwnj;پاداشتی کرده&zwnj;وه&zwnj;ی چاک و ڕێک و پێکتاندا';
$t[20] = 'ئه&zwnj;و به&zwnj;خته&zwnj;وه&zwnj;رانه&zwnj;به&zwnj;که&zwnj;ماڵی ئیسراحه&zwnj;ت) پاڵیان داوه&zwnj;ته&zwnj;وه&zwnj;له&zwnj;سه&zwnj;ر کورسی و قه&zwnj;نه&zwnj;فه&zwnj;رازاوه&zwnj;کان، که&zwnj;به&zwnj;شێوه&zwnj;یه&zwnj;کی جوان و ڕێک ڕیز کراون، ئێمه هاوسه&zwnj;رانی حۆری چاوگه&zwnj;شه&zwnj;مان لێ ماره&zwnj;کردوون';
$t[21] = 'جا ئه&zwnj;وانه&zwnj;ی باوه&zwnj;ڕیان هێناوه و نه&zwnj;وه&zwnj;کانیشیان به&zwnj;ئیمان و باوه&zwnj;ڕه&zwnj;وه شوێنیان که&zwnj;وتوون، ئه&zwnj;وانه&zwnj;له&zwnj;به&zwnj;هه&zwnj;شتدا پێک شاد ده&zwnj;که&zwnj;ینه&zwnj;وه (تا کامه&zwnj;رانی و خۆشییان زیاتر بێت)، وه&zwnj;نه&zwnj;بێت ئه&zwnj;وانه&zwnj;ی که&zwnj;پله&zwnj;ی به&zwnj;رزیان ده&zwnj;ستکه&zwnj;وتووه&zwnj;دایان به&zwnj;زێنین بۆ پله&zwnj;ی نزمتر، چونکه&zwnj;ئێمه پاداشتی کرده&zwnj;وه&zwnj;ی که&zwnj;س که&zwnj;م ناکه&zwnj;ینه&zwnj;وه (ئه&zwnj;گه&zwnj;ر زیادی نه&zwnj;که&zwnj;ین) له&zwnj;هه&zwnj;مان کاتدا هه&zwnj;رکه&zwnj;سه&zwnj;بارمته&zwnj;ی کرده&zwnj;وه&zwnj;ی خۆیه&zwnj;تی';
$t[22] = 'به&zwnj;رده&zwnj;وام حه&zwnj;زیان له&zwnj;هه&zwnj;ر میوه و گۆشتێک بێت پێشکه&zwnj;شیان ده&zwnj;که&zwnj;ین';
$t[23] = 'به&zwnj;دڵ خۆشیه&zwnj;وه له&zwnj;کاسی تایبه&zwnj;تیدا شه&zwnj;راب ده&zwnj;نۆشن، جار جار بۆ خۆشی له&zwnj;یه&zwnj;کتری ده&zwnj;سێنن، دوور له&zwnj;ره&zwnj;فتاری ناشیرین و گوناهو تاوانبارکردنی یه&zwnj;کتر';
$t[24] = 'هه&zwnj;روه&zwnj;ها منداڵانی جوان و ئێسک سووک به&zwnj;ده&zwnj;وریاندا دێن و خزمه&zwnj;تیان ده&zwnj;که&zwnj;ن ده&zwnj;ڵێی مرواری پاک و بێگه&zwnj;ردن';
$t[25] = '(جا دوای ئه&zwnj;و عه&zwnj;یش و نۆش و که&zwnj;یف و ئاهه&zwnj;نگه&zwnj;) ڕووده&zwnj;که&zwnj;نه&zwnj;یه&zwnj;کترو ده&zwnj;سته&zwnj;ده&zwnj;سته&zwnj;له&zwnj;یه&zwnj;ک نزیک ده&zwnj;بنه&zwnj;وه&zwnj;، پرسیار له&zwnj;رابوردوی یه&zwnj;کتر ده&zwnj;که&zwnj;ن (دیاره هه&zwnj;رکه&zwnj;سه&zwnj;له&zwnj;و به&zwnj;یه&zwnj;کگه&zwnj;یشتنانه&zwnj;دا به&zwnj;شێک له&zwnj;سه&zwnj;رگوزه&zwnj;شته&zwnj;ی خۆی نیشانی ئیماندارانی هاوه&zwnj;ڵ و میوانی ده&zwnj;دات، له&zwnj;گه&zwnj;ڵ ڕوونکردنه&zwnj;وه&zwnj;ی پێویستدا، که&zwnj;به&zwnj;ده&zwnj;زگای زۆر پێشکه&zwnj;وتوو تۆمارکراوه&zwnj;)';
$t[26] = 'سه&zwnj;ره&zwnj;نجام هه&zwnj;موو به&zwnj;یه&zwnj;ک ده&zwnj;نگ ده&zwnj;ڵێن: به&zwnj;ڕاستی (کارێکی چاکمان ده&zwnj;کرد سوپاس بۆ خوا) چونکه&zwnj;کاتی خۆی که&zwnj;له&zwnj;ناو ماڵ و خێزانماندا بووین، ترسی لێپرسینه&zwnj;وه&zwnj;ی ڕۆژی قیامه&zwnj;تمان هه&zwnj;بوو، هه&zwnj;روه&zwnj;ها به&zwnj;به&zwnj;زه&zwnj;ییش بووین له&zwnj;ناویاندا و مه&zwnj;به&zwnj;ستمان بوو ئه&zwnj;وانیش وه&zwnj;ک ئێمه خواناس بن';
$t[27] = 'به&zwnj;هۆی ئه&zwnj;وه&zwnj;وه&zwnj;خوای میهره&zwnj;بانیش لای ڕه&zwnj;حمه&zwnj;تی لێ کردینه&zwnj;وه و منه&zwnj;تباری کردین و له&zwnj;سزای دۆزه&zwnj;خی پڕئێش پاڕاستینی';
$t[28] = 'به&zwnj;ڕاستی ئێمه کاتی خۆی هاناو هاوارمان هه&zwnj;ر بۆ ئه&zwnj;و ده&zwnj;بردو ده&zwnj;مانپه&zwnj;رست، چونکه&zwnj;بێگومان هه&zwnj;ر ئه&zwnj;و زاته&zwnj;خوایه&zwnj;کی چاکه&zwnj;خوازو میهره&zwnj;بان و دلۆڤانه&zwnj;';
$t[29] = 'ئه&zwnj;ی محمد (صلی الله علیه وسلم) ئامۆژگاری ئه&zwnj;وخه&zwnj;ڵکه بکه&zwnj;، چونکه&zwnj;به&zwnj;هۆی نازو نیعمه&zwnj;تی په&zwnj;روه&zwnj;ردگارته&zwnj;وه&zwnj;نه&zwnj;فاڵچیت، نه&zwnj;شێتی';
$t[30] = 'خوانه&zwnj;ناس و دڵڕه&zwnj;قه&zwnj;کان ده&zwnj;ڵێن: ئه&zwnj;مه&zwnj;شاعیرێکه و چاوه&zwnj;ڕوانی مه&zwnj;رگی ده&zwnj;که&zwnj;ین!!';
$t[31] = 'پێیان بڵێ: چاوه&zwnj;ڕێ بن منیش له&zwnj;گه&zwnj;ڵ ئێوه&zwnj;دا چاوه&zwnj;ڕێم (بزانین سه&zwnj;رئه&zwnj;نجام کێ سه&zwnj;رده&zwnj;که&zwnj;وێت، کێ سه&zwnj;رفرازه&zwnj;؟)';
$t[32] = 'ئایا پیاوه ژیره&zwnj;کانیان پێیان ده&zwnj;ڵێت: ئه&zwnj;و قسه&zwnj;ناڕه&zwnj;وایانه بکه&zwnj;ن، نه&zwnj;خێر ئه&zwnj;مانه&zwnj;هه&zwnj;ر خۆیان که&zwnj;سانێکی سته&zwnj;مکارن؟';
$t[33] = 'یان ده&zwnj;ڵێن: ئه&zwnj;م قورئانه&zwnj;خۆی هه&zwnj;ڵیبه&zwnj;ستووه&zwnj;و دایڕشتووه&zwnj;، نه&zwnj;خێر ئه&zwnj;وانه&zwnj;، هه&zwnj;ر ئیمان و باوه&zwnj;ڕیان نیه (ئه&zwnj;گینا چۆن وا ده&zwnj;ڵێن).';
$t[34] = '(ده&zwnj;ی که&zwnj;واته&zwnj;خۆ ئه&zwnj;وانیش قسه&zwnj;زان و لێزانن) ده&zwnj;با فه&zwnj;رمایشتێکی ئاوا وه&zwnj;ک ئه&zwnj;م قورئانه&zwnj;بهێنن ئه&zwnj;گه&zwnj;ر ڕاست ده&zwnj;که&zwnj;ن؟!';
$t[35] = 'ئایا ئه&zwnj;وانه&zwnj;به&zwnj;بێ هیچ شتێک، هیچ هۆیه&zwnj;ک، هاتوونه&zwnj;ته&zwnj;کایه&zwnj;وه؟! یان هه&zwnj;ر خۆیان دروستکارن؟!';
$t[36] = 'یان ئه&zwnj;و بێ باوه&zwnj;ڕانه ئاسمانه&zwnj;کان و زه&zwnj;ویان دروستکردووه&zwnj;؟ نه&zwnj;خێر ئه&zwnj;وانه&zwnj;هه&zwnj;ر دڵنیا نابن';
$t[37] = 'یان ئه&zwnj;وه&zwnj;یه گه&zwnj;نجینه&zwnj;ی ڕزق و ڕۆزیه&zwnj;کانی په&zwnj;روه&zwnj;ردگاری تۆیان به&zwnj;ده&zwnj;سته&zwnj;؟! یان ئه&zwnj;وه&zwnj;یه ده&zwnj;سه&zwnj;ڵاتیان بێ سنووره&zwnj;؟ (تا ئاوا بوێرن له&zwnj;خوا یاخی بن؟)';
$t[38] = 'یان ئه&zwnj;وانه&zwnj;په&zwnj;یژه&zwnj;یان هه&zwnj;یه&zwnj;و پیایدا سه&zwnj;رده&zwnj;که&zwnj;ون، تا گوێ له&zwnj;نهێنیه&zwnj;کانی ئاسمان بگرن؟! ده&zwnj;با گوێگره&zwnj;کانیان به&zwnj;ڵگه&zwnj;یه&zwnj;کی ئاشکرا په&zwnj;یدا بکه&zwnj;ن (ئه&zwnj;وسا مله&zwnj;جه&zwnj;ڕی بکه&zwnj;ن).';
$t[39] = 'ئایا ئه&zwnj;وه&zwnj;ڕاسته&zwnj;که&zwnj;کچان، نه&zwnj;وه&zwnj;ی خوابن و کوڕانیش نه&zwnj;وه&zwnj;ی ئێوه؟! (به&zwnj;مه&zwnj;رجێک خۆیان له&zwnj;کچان بێزاربوون، به&zwnj;ڵام بۆ خوا به&zwnj;ره&zwnj;وایان بینیوه؟!).';
$t[40] = 'یان ئه&zwnj;وه&zwnj;یه داوای کرێ و پاره&zwnj;یان لێ ده&zwnj;که&zwnj;یت، تا بارێکی گران بده&zwnj;ی به&zwnj;کۆڵیانداو پێیان هه&zwnj;ڵنه&zwnj;سوڕێت!!';
$t[41] = 'یان ئه&zwnj;وه&zwnj;یه که&zwnj;نهێنی و شاراوه&zwnj;کان لای ئه&zwnj;وانه&zwnj;و، ئه&zwnj;وان یاداشتی خه&zwnj;ڵکی ده&zwnj;نووسن، (یان ئه&zwnj;وان بڕیاری به&zwnj;سه&zwnj;رهاته&zwnj;کان ده&zwnj;ده&zwnj;ن)؟!';
$t[42] = 'یان ئه&zwnj;وانه&zwnj;ده&zwnj;یانه&zwnj;وێت پیلان و ته&zwnj;ڵه&zwnj;یه&zwnj;ک ڕێک بخه&zwnj;ن (دژی پێغه&zwnj;مبه&zwnj;ر و ئیمانداران، به&zwnj;ڵام خه&zwnj;یاڵیان خاوه&zwnj;) ئه&zwnj;وانه&zwnj;ی که&zwnj;کافروخوانه&zwnj;ناسن هه&zwnj;ر خۆیان که&zwnj;وتوونه&zwnj;ته&zwnj;ناو پیلانه&zwnj;وه و بون به&zwnj;ته&zwnj;ڵه&zwnj;ی خۆیانه&zwnj;وه&zwnj;، پیلانه&zwnj;کانیان به&zwnj;سه&zwnj;ر خۆیاندا شکاوه&zwnj;ته&zwnj;وه&zwnj;.';
$t[43] = 'یان ئه&zwnj;وانه&zwnj;وابیرده&zwnj;که&zwnj;نه&zwnj;وه که&zwnj;خوایه&zwnj;کی تریان جگه&zwnj;له (الله) هه&zwnj;یه؟! پاکی و بێگه&zwnj;ردی بۆ خوا (له&zwnj;بیروباوه&zwnj;ڕی چه&zwnj;وتیان)، له&zwnj;و شتانه&zwnj;ی که&zwnj;ئه&zwnj;وان ده&zwnj;یکه&zwnj;نه هاوه&zwnj;ڵ بۆ خوا.';
$t[44] = 'خۆ ئه&zwnj;گه&zwnj;ر (له&zwnj;ئه&zwnj;نجامی لادان و بیروباوه&zwnj;ڕی چه&zwnj;وتیان و کاری نادروستیان خوای گه&zwnj;وره&zwnj;سزایه&zwnj;کیان بۆ بنێرێت) و پارچه (ئه&zwnj;ستێره&zwnj;یه&zwnj;کیان) تێ بگرێت و ڕوو به&zwnj;زه&zwnj;وی دابه&zwnj;زێت ئه&zwnj;و نه&zwnj;فامانه&zwnj;ده&zwnj;ڵێن: هیچ نیه!! په&zwnj;ڵه هه&zwnj;وره&zwnj;و نیشتۆته سه&zwnj;ریه&zwnj;ک!!';
$t[45] = 'وازیان لێ بهێنه هه&zwnj;تا به&zwnj;و ڕۆژه ده&zwnj;گه&zwnj;ن که&zwnj;تایدا تیاده&zwnj;چن و لاڵ و پاڵ ده&zwnj;که&zwnj;ون';
$t[46] = 'ئه&zwnj;و ڕۆژه ئیتر فێڵ و ته&zwnj;ڵه&zwnj;که&zwnj;یان به&zwnj;هیچ جۆرێک فریایان ناکه&zwnj;وێت و سه&zwnj;رکه&zwnj;وتوو سه&zwnj;رفراز نابن';
$t[47] = 'بێگومان بۆ ئه&zwnj;وانه&zwnj;ی که&zwnj;سته&zwnj;میان کردووه&zwnj;سزای تریش هه&zwnj;یه بێجگه له&zwnj;وه&zwnj;ی که&zwnj;جار جار بۆیان پێش دێت پێش تیاچوونی یه&zwnj;کجاری، به&zwnj;ڵام زۆربه&zwnj;یان نافامن و نازانن (هه&zwnj;ست به&zwnj;ده&zwnj;ستی قه&zwnj;ده&zwnj;ر ناکه&zwnj;ن له&zwnj;رووداوه&zwnj;کاندا)';
$t[48] = 'ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر (چلی الله علیه وسلم) خۆگربه له&zwnj;به&zwnj;رامبه&zwnj;ر به&zwnj;جێهێنانی فه&zwnj;رمانی په&zwnj;روه&zwnj;ردگارته&zwnj;وه&zwnj;، دڵنیابه که&zwnj;تۆ له&zwnj;ژێر چاودێری ئێمه&zwnj;دایت، هه&zwnj;روه&zwnj;ها ته&zwnj;سبیحات و ستایش و سوپاسگوزاری په&zwnj;روه&zwnj;ردگارت بكه&zwnj;کاتێک که&zwnj;به&zwnj;ره&zwnj;به&zwnj;یان هه&zwnj;ڵده&zwnj;سێت';
$t[49] = 'له&zwnj;شه&zwnj;وگاریشدا هه&zwnj;ر بیپه&zwnj;رسته و ستایشی بکه&zwnj;، هه&zwnj;روه&zwnj;ها کاتێک ئه&zwnj;ستێره&zwnj;کان ون ده&zwnj;بن له&zwnj;چاو دیار نامێنن';
?>