$t[55] = 'جا ئیتر ئه&zwnj;ی بێ ئیمان تۆ له&zwnj;کام یه&zwnj;ک له&zwnj;نازو نیعمه&zwnj;ته&zwnj;کانی خوا دوودڵیت...';
$t[56] = 'ئه&zwnj;م پێغه&zwnj;مبه&zwnj;ره&zwnj;ش هۆشیارکه&zwnj;ره&zwnj;وه&zwnj;یه له&zwnj;هۆشیارکه&zwnj;ره&zwnj;وانی پێشوو';
$t[57] = '(ئه&zwnj;وه&zwnj;چاک بزانن) قیامه&zwnj;ت و لێپرسینه&zwnj;وه نزیک بووته&zwnj;وه (نزیکه به&zwnj;نیسبه&zwnj;ت هه&zwnj;ر ئاده&zwnj;میزادێکه&zwnj;وه هه&zwnj;ر که&zwnj;مردن ئه&zwnj;وه&zwnj;قیامه&zwnj;ت له&zwnj;و به&zwnj;رپا بووه&zwnj;، قیامه&zwnj;تی گه&zwnj;وره&zwnj;ش کاتێک به&zwnj;رپا ده&zwnj;بێت هه&zwnj;مووان واهه&zwnj;ست ده&zwnj;که&zwnj;ن که&zwnj;چه&zwnj;ند سه&zwnj;عات له&zwnj;وه&zwnj;وپێش دنیا بوو وا ئێسته&zwnj;ش قیامه&zwnj;ته&zwnj;)!!';
$t[58] = 'که&zwnj;سیش ناتونێت ئه&zwnj;و ڕۆژه ده&zwnj;ربخات و به&zwnj;رپای بکات جگه&zwnj;له&zwnj;خوای باڵاده&zwnj;ست';
$t[59] = 'جا ئایا ئێوه له&zwnj;م قسه&zwnj;یه که&zwnj;قورئانه&zwnj;سه&zwnj;رسام ده&zwnj;بن؟!';
$t[60] = 'پێ ده&zwnj;که&zwnj;نن و گریانتان نایه&zwnj;ت (بۆ ئه&zwnj;و چاره&zwnj;نووسه&zwnj;ره&zwnj;شه&zwnj;ی که&zwnj;چاوه&zwnj;ڕێتانه&zwnj;)';
$t[61] = 'ئێوه به&zwnj;رده&zwnj;وام سه&zwnj;رگه&zwnj;رمی ڕابوردنن و بێ هۆشن؟!';
$t[62] = '(ده&zwnj;ی که&zwnj;واته&zwnj;بێداربنه&zwnj;وه و داچڵه&zwnj;کێن) که&zwnj;واته&zwnj;سوژده&zwnj;بۆ خوای به&zwnj;دیهێنه&zwnj;ر ببه&zwnj;ن، هه&zwnj;ر ئه&zwnj;ویش بپه&zwnj;رستن';
?>