$t[45] = 'له ئایینده&zwnj;یه&zwnj;کی نزیکدا له&zwnj;شکرو کۆمه&zwnj;ڵیان شکست ده&zwnj;هێنێت و هه&zwnj;ڵدێن (له&zwnj;غه&zwnj;زای به&zwnj;دردا ئه&zwnj;و مژده&zwnj;یه هاته&zwnj;دی)';
$t[46] = 'جگه&zwnj;له&zwnj;وه&zwnj;ش قیامه&zwnj;ت کاتی لێ پرسینه&zwnj;وه&zwnj;یانه&zwnj;، که&zwnj;رۆژێکی زۆر سه&zwnj;ختترو تاڵ تره&zwnj;';
$t[47] = 'به&zwnj;ڕاستی تاوانبارو تاوانکاران له&zwnj;گومڕایی و شێتیه&zwnj;کی زۆردا ڕۆچوون';
$t[48] = 'ڕۆژێک دێت که&zwnj;له&zwnj;سه&zwnj;ر ڕوویان ڕاده&zwnj;کێشرێن له&zwnj;ناو ئاگری دۆزه&zwnj;خدا، پێیان ده&zwnj;وترێت: ده&zwnj;بچێژن تاڵاوی دۆزه&zwnj;خ';
$t[49] = 'بێگومان ئێمه هه&zwnj;موو شتێکمان به&zwnj;ئه&zwnj;ندازه&zwnj;و نه&zwnj;خشه&zwnj;ی دیاریکراو دروست کردووه&zwnj;';
$t[50] = 'فه&zwnj;رمانی ئێمه ته&zwnj;نها یه&zwnj;ک وشه&zwnj;یه ده&zwnj;ڵێین ببه&zwnj;ده&zwnj;ستبه&zwnj;جێ ده&zwnj;بێت وه&zwnj;ک چاو تروکاندنێک';
$t[51] = 'بێگومان ئێمه زۆر له&zwnj;پێشینانی خوانه&zwnj;ناسی وه&zwnj;ک ئێوه&zwnj;مان له&zwnj;ناو بردوه&zwnj;، جا ئایا که&zwnj;س هه&zwnj;یه ده&zwnj;رس و عیبره&zwnj;ت و په&zwnj;ندو ئامۆژگاری وه&zwnj;ربگرێت';
$t[52] = 'هه&zwnj;رچی کارو کرده&zwnj;وه&zwnj;یه&zwnj;ک که&zwnj;ئه&zwnj;نجامیان داوه&zwnj;له (لوح المحفوظ) و له&zwnj;لای فریشته چاودێره&zwnj;کان تۆمارکراوه&zwnj;';
$t[53] = 'هه&zwnj;رچی وردو درشت هه&zwnj;یه&zwnj;، له&zwnj;کارو کرده&zwnj;وه&zwnj;یان نووسراوه&zwnj;و تۆمارکراوه&zwnj;';
$t[54] = 'بێگومان پارێزکاران و خواناسان له&zwnj;ناخ و ڕوبارو تاڤگه&zwnj;کاندا ژیانی پڕ له&zwnj;به&zwnj;ختیاری و کامه&zwnj;رانی هه&zwnj;میشه&zwnj;یی ده&zwnj;به&zwnj;نه&zwnj;سه&zwnj;ر';
$t[55] = 'له شوێنی ڕاست و په&zwnj;سه&zwnj;ندان لای پادشای به&zwnj;ده&zwnj;سته&zwnj;وه&zwnj;';
?>