<?php
$t = array();
$t[1] = 'سوپاس و ستایش بۆ ئه&zwnj;و خوایه&zwnj;ی که ئاسمانه&zwnj;کان و زه&zwnj;وی دروست کردووه&zwnj;، تاریکی و ڕوناکی فه&zwnj;راهه&zwnj;م هێناوه (که&zwnj;چی له&zwnj;گه&zwnj;ڵ ئه&zwnj;و هه&zwnj;موو به&zwnj;ڵگه به&zwnj;هێزو سه&zwnj;رسام ئاوه&zwnj;رانه&zwnj;دا) پاشان ئه&zwnj;وانه&zwnj;ی بێ باوه&zwnj;ڕ بوون به&zwnj;په&zwnj;روه&zwnj;ردگاریان یه&zwnj;کێک له دروستکراوه&zwnj;کانی خوا ده&zwnj;که&zwnj;نه هاوتای خوا.';
$t[2] = 'خوا ئه&zwnj;و زاته&zwnj;یه که ئێوه&zwnj;ی له قوڕێک دروست کردووه له&zwnj;وه&zwnj;ودوا کاتێکی بڕیاریدا (که خه&zwnj;وتنه وه&zwnj;ک نیوه مه&zwnj;رگێک، به&zwnj;شێک له&zwnj;ئه&zwnj;ندامه هه&zwnj;ستیاره&zwnj;کان له&zwnj;کار ده&zwnj;که&zwnj;ون بۆ ماوه&zwnj;یه&zwnj;ک) و کاتێکی دیاریکراوی تریشی لای خۆی بڕیارداوه بۆ هه&zwnj;موو که&zwnj;سێک که کاتی ته&zwnj;واو بوونی ته&zwnj;مه&zwnj;نیه&zwnj;تی، (که&zwnj;چی پاش ئه&zwnj;و هه&zwnj;موو به&zwnj;ڵگانه بۆ ده&zwnj;سه&zwnj;ڵاتی بێ سنووری خوا) ئێوه ئینکاری زیندوو بوونه&zwnj;وه ده&zwnj;که&zwnj;ن و هه&zwnj;ر له گوماندان!!';
$t[3] = 'ئه&zwnj;وه هه&zwnj;ر الله یه په&zwnj;رستراوه له ئاسمانه&zwnj;کان و زه&zwnj;ویدا و نهێنی و ئاشکراتان ده&zwnj;زانێت، به هه&zwnj;موو کارو کرده&zwnj;وه&zwnj;یه&zwnj;کیش که ده&zwnj;یکه&zwnj;ن ئاگاداره&zwnj;.';
$t[4] = '(بێ باوه&zwnj;ڕان) هیچ به&zwnj;ڵگه&zwnj;و موعجیزه&zwnj;یه&zwnj;کی په&zwnj;روه&zwnj;ردگاریان بۆ نایه&zwnj;ت پشتی تێنه&zwnj;که&zwnj;ن و ڕووی لێ وه&zwnj;رنه&zwnj;گێڕن.';
$t[5] = 'که&zwnj;وابوو بێگومان ئه&zwnj;وانه کاتێک ئه&zwnj;و حه&zwnj;ق و ڕاستیه&zwnj;یان بۆ هات که قورئانه بڕوایان پێنه&zwnj;کرد، جا له ئاینده&zwnj;دابۆیان دێت و پێیان ده&zwnj;گات هه&zwnj;واڵی ئه&zwnj;وه&zwnj;ی که ئه&zwnj;وان گاڵته&zwnj;یان پێده&zwnj;کرد.';
$t[6] = 'ئایا نه&zwnj;یانبینیووه و نه&zwnj;یانزانیووه (به&zwnj;هۆی شوێنه&zwnj;واره&zwnj;وه&zwnj;) چه&zwnj;نده&zwnj;ها نه&zwnj;وه&zwnj;مان پێش ئه&zwnj;مان له&zwnj;ناو بردووه که پایه&zwnj;دارمان کردبوون له زه&zwnj;ویدا، به&zwnj;شێوه&zwnj;یه&zwnj;ک ئێوه&zwnj;مان ئاوا پایه&zwnj;دار نه&zwnj;کردووه و بارانی زۆرمان به&zwnj;رده&zwnj;وام بۆ ده&zwnj;باراندن (که ده&zwnj;بووه هۆی پێگه&zwnj;یشتنی جۆره&zwnj;ها ڕوه&zwnj;ک و به&zwnj;روبووم)، چه&zwnj;نده&zwnj;ها جۆگه&zwnj;و ڕووباریشمان له&zwnj;ناو کێڵگه&zwnj;و باخ و شاره&zwnj;کانیاندا بۆ ڕه&zwnj;خساندبوون خوڕه&zwnj;می ده&zwnj;کرد به&zwnj;نێو دره&zwnj;خت و کۆشک و ته&zwnj;لاریاندا، به&zwnj;ڵام به&zwnj;هۆی لادان و گوناهو تاوانیانه&zwnj;وه له&zwnj;ناومان بردن و نه&zwnj;وه&zwnj;ی ترمان له دوای ئه&zwnj;وان هێنایه کایه&zwnj;وه&zwnj;.';
$t[7] = 'خۆ ئه&zwnj;گه&zwnj;ر نووسراوێکمان بۆ تۆ له&zwnj;ناو کاغه&zwnj;زێکدا داببه&zwnj;زاندایه&zwnj;، ئه&zwnj;وجا ده&zwnj;ستیشیان لێ بدایه&zwnj;، ئه&zwnj;وه ئه&zwnj;وانه&zwnj;ی که بێ بڕوا بوون ده&zwnj;یانوت: ئه&zwnj;مه جگه له جادوویه&zwnj;کی ئاشکرا هیچ شتێکی تر نیه&zwnj;!! (هه&zwnj;ر باوه&zwnj;ڕ ناهێنن).';
$t[8] = 'هه&zwnj;روه&zwnj;ها ده&zwnj;یانوت: چی ده&zwnj;بوو ئه&zwnj;گه&zwnj;ر فریشته&zwnj;یه&zwnj;ک بۆ لای (محمد (صلی الله علیه وسلم) دابه&zwnj;زییایه و به&zwnj;چاوی خۆمان بمانبینیایه&zwnj;! جا ئه&zwnj;گه&zwnj;ر فریشته&zwnj;مان بناردایه ئه&zwnj;وه کۆتایی به&zwnj;هه&zwnj;موو شتێک ده&zwnj;هات و پاشان مۆڵه&zwnj;ت نادرێن تا ته&zwnj;وبه بکه&zwnj;ن.';
$t[9] = 'خۆ ئه&zwnj;گه&zwnj;ر فریشته&zwnj;مان بکردایه به فرستاده&zwnj;، ئه&zwnj;وه له شێوه&zwnj;ی پیاوێکدا ده&zwnj;ماننارد و هه&zwnj;مان گومان که ئێستا هه&zwnj;یانه بۆیان دروست ده&zwnj;بوو.';
$t[10] = '(ئه&zwnj;ی محمد (صلى الله عليه وسلم) دڵت له&zwnj;خۆت دانه&zwnj;مێنێت) سوێندبێت به&zwnj;خوا به&zwnj;ڕاستی گاڵته&zwnj;کراوه به پێغه&zwnj;مبه&zwnj;رانی پێش تۆ، سه&zwnj;رنجا&zwnj;م ئه&zwnj;وه&zwnj;ی ئه&zwnj;وان بڕوایان پێی نه&zwnj;بوو، گاڵته&zwnj;یان پێده&zwnj;کرد، یه&zwnj;خه&zwnj;ی پێگرتن و نغرۆی کردن (خوا تۆڵه&zwnj;ی لێسه&zwnj;ندن).';
$t[11] = 'پێیان بڵێ: بڕۆن بگه&zwnj;ڕێن به&zwnj;سه&zwnj;ر زه&zwnj;ویدا و پاشان ته&zwnj;ماشا بکه&zwnj;ن و سه&zwnj;رنج بده&zwnj;ن، بزانن سه&zwnj;ر ئه&zwnj;نجامی ئه&zwnj;وانه&zwnj;ی به&zwnj;رنامه&zwnj;ی خوایان به&zwnj;درۆزانی چۆن بووه (چیمان به&zwnj;سه&zwnj;رهێنان).';
$t[12] = '(لێیان بپرسه ئه&zwnj;ی محمد (صلى الله عليه وسلم) و بڵێ: ئه&zwnj;وه&zwnj;ی له ئاسمانه&zwnj;کان و زه&zwnj;ویدا هه&zwnj;یه هی کێ یه&zwnj;؟ (هه&zwnj;ر خۆت وه&zwnj;ڵام بده&zwnj;ره&zwnj;وه و) بڵێ: هه&zwnj;ر هی خوایه&zwnj;، که ڕه&zwnj;حم و میهره&zwnj;بانی له&zwnj;سه&zwnj;ر خۆی فه&zwnj;رز کردووه&zwnj;، سوێند بێت به&zwnj;خوا به&zwnj;ره&zwnj;و ڕۆژی قیامه&zwnj;ت که هیچ شکوگومانی تیادا نیه کۆتان ده&zwnj;کاته&zwnj;وه&zwnj;، (هه&zwnj;رچه&zwnj;نده&zwnj;) ئه&zwnj;وانه&zwnj;ی که خۆیان دۆڕاندو بیریان له قیامه&zwnj;ت نه&zwnj;کرده&zwnj;وه&zwnj;، ئه&zwnj;وه هه&zwnj;ر باوه&zwnj;ڕ ناهێنن.';
$t[13] = 'هه&zwnj;رچی کش و مات بووه له شه&zwnj;ودا و ده&zwnj;جوڵێت له ڕۆژدا هه&zwnj;ر هی خوایه&zwnj;، بێگومان ئه&zwnj;و په&zwnj;روه&zwnj;ردگاره خوایه&zwnj;کی بیسه&zwnj;رو زانایه&zwnj;.';
$t[14] = 'هه&zwnj;روها پێیان بڵێ: ئایا (به&zwnj;عه&zwnj;قڵ و ژیریی ئێوه ڕه&zwnj;وایه&zwnj;) که جگه له خوا که&zwnj;سێکی تر بکه&zwnj;مه پشت و په&zwnj;نای خۆم؟ که به&zwnj;دیهێنه&zwnj;ری ئاسمانه&zwnj;کان و زه&zwnj;ویه و هه&zwnj;ر ئه&zwnj;ویش ڕۆزی به&zwnj;خشه و بێ نیازه له هه&zwnj;موو ڕزق و ڕۆزیه&zwnj;ک، دووباره بڵێ: من به&zwnj;ڕاستی فه&zwnj;رمانم پێدراوه یه&zwnj;که&zwnj;م که&zwnj;س بم که ته&zwnj;سلیمی ئه&zwnj;و زاته ببم و موسوڵمان بم و (من پێت ڕاده&zwnj;گه&zwnj;یه&zwnj;نم) که&zwnj;: هه&zwnj;رگیز نه&zwnj;که&zwnj;یت له موشریک و هاوه&zwnj;ڵگه&zwnj;ران بیت.';
$t[15] = '(هه&zwnj;روه&zwnj;ها ئه&zwnj;ی محمد (صلى الله عليه وسلم) بڵێ: به&zwnj;ڕاستی من زۆر له سزای ڕۆژێکی گه&zwnj;وره ده&zwnj;ترسم که سزای ڕۆژی قیامه&zwnj;ته ئه&zwnj;گه&zwnj;ر یاخی بم له فه&zwnj;رمانی په&zwnj;روه&zwnj;ردگارم.';
$t[16] = 'ئه&zwnj;وه&zwnj;ی له&zwnj;و سزایه ده&zwnj;رباز بکرێت له&zwnj;و ڕۆژه&zwnj;دا، ئه&zwnj;وه به&zwnj;ڕاستی ئیتر دیاره که خوا ڕه&zwnj;حمی پێکردووه و شایسته&zwnj;ی میهره&zwnj;بانی ئه&zwnj;و بووه&zwnj;، ئه&zwnj;و ده&zwnj;رباز بوونه سه&zwnj;رفرازیه&zwnj;کی ئاشکرایه&zwnj;.';
$t[17] = '(دڵنیابه له&zwnj;وه&zwnj;ی) ئه&zwnj;گه&zwnj;ر خوا دووچاری زیان و گرفتێکت بکات، ئه&zwnj;وه هه&zwnj;رگیز لابه&zwnj;ری نیه جگه خۆی نه&zwnj;بێت، خۆ ئه&zwnj;گه&zwnj;ر بیه&zwnj;وێت تووشی خێرو خۆشیه&zwnj;کت بکات، ئه&zwnj;وه ئه&zwnj;و خوایه ده&zwnj;سه&zwnj;ڵاتی به&zwnj;سه&zwnj;ر هه&zwnj;موو شتێکدا هه&zwnj;یه&zwnj;.';
$t[18] = 'هه&zwnj;ر ئه&zwnj;و خوایه&zwnj;ش زاڵ و باڵاده&zwnj;سته به&zwnj;سه&zwnj;ر هه&zwnj;موو به&zwnj;نده&zwnj;کانیدا و هه&zwnj;ر ئه&zwnj;ویشه زاتێکی دانا و ئاگایه (به&zwnj;نهێنی و ئاشکرای به&zwnj;نده&zwnj;کانی).';
$t[19] = '(ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر (صلى الله عليه وسلم) به&zwnj;وانه بڵێ که شایه&zwnj;تیان ده&zwnj;وێت له&zwnj;سه&zwnj;ر پێغه&zwnj;مبه&zwnj;رایه&zwnj;تی تۆ) چ شتێک گه&zwnj;وره&zwnj;ترو ڕاستتره له شایه&zwnj;تیدا؟! هه&zwnj;ر خۆت بڵێ: الله ڕاسترین شایه&zwnj;ته&zwnj;، که شایه&zwnj;تی نێوان من و ئێوه&zwnj;یه&zwnj;، من ئه&zwnj;م قورئانه&zwnj;شم به&zwnj;وه&zwnj;حی و نیگا بۆ هاتووه تا ئێوه&zwnj;و هه&zwnj;موو ئه&zwnj;وانه&zwnj;ی پێیان گه&zwnj;یشتووه ئاگادارو بێدار بکه&zwnj;م، (له&zwnj;گه&zwnj;ڵ ئه&zwnj;م هه&zwnj;موو ڕوون کردنه&zwnj;وانه&zwnj;دا) ئایا ئێوه ئیتر شایه&zwnj;تی ده&zwnj;ده&zwnj;ن که به&zwnj;ڕاستی له&zwnj;گه&zwnj;ڵ خوادا چه&zwnj;ند خوایه&zwnj;کی تر هه&zwnj;بێت؟! بڵێ: (من به&zwnj;ش به&zwnj;حاڵی خۆم) شایه&zwnj;تی ناحه&zwnj;ق و ناڕه&zwnj;وای وا ناده&zwnj;م و بڵێ: به&zwnj;ڕاستی ئه&zwnj;و خوایه زاتێکی تاک و ته&zwnj;نهایه و من به&zwnj;ڕاستی به&zwnj;ریم له هه&zwnj;موو ئه&zwnj;و شتانه&zwnj;ی که ئێوه ده&zwnj;یانکه&zwnj;نه هاوه&zwnj;ڵ و هاوتای ئه&zwnj;و زاته&zwnj;).';
$t[20] = 'ئه&zwnj;وانه&zwnj;ی کتێبی (ئاسمانیمان پێداون له گاورو جوو) پێغه&zwnj;مبه&zwnj;ر (صلى الله عليه وسلم) ده&zwnj;ناسنه&zwnj;وه هه&zwnj;روه&zwnj;ک چۆن منداڵه&zwnj;کانی خۆیان ده&zwnj;ناسنه&zwnj;وه&zwnj;، (به&zwnj;ڵام) ئه&zwnj;وانه&zwnj;ی که خۆیان دۆڕاندو بڕوایان به&zwnj;محمد (صلى الله عليه وسلم) نه&zwnj;هێناوه&zwnj;، ئه&zwnj;وه هه&zwnj;ر باوه&zwnj;ڕ ناهێنن.';
$t[21] = 'جا کێ هه&zwnj;یه له&zwnj;وه سته&zwnj;مکارتر بێت، که شتی نادروست و درۆ، بۆ خوا هه&zwnj;ڵده&zwnj;به&zwnj;ستێت؟ یان ئایه&zwnj;ت و فه&zwnj;رمانه&zwnj;کانی خوا به&zwnj;درۆ ده&zwnj;زانێت؟! چونکه به&zwnj;ڕاستی ئه&zwnj;و سته&zwnj;مکارانه سه&zwnj;رفراز نابن.';
$t[22] = 'له ڕۆژێکدا که هه&zwnj;موو (خوانه&zwnj;ناس و) هاوه&zwnj;ڵگه&zwnj;ران کۆ ده&zwnj;که&zwnj;ینه&zwnj;وه به&zwnj;گشتی و پاشان ده&zwnj;ڵێین به&zwnj;وانه&zwnj;ی که هاوبه&zwnj;شیان داناوه بۆ خوا: کوان و له کوێن ئه&zwnj;و شتانه&zwnj;ی ده&zwnj;تانپه&zwnj;رستن و ده&zwnj;تانوت هاوتای خوان به&zwnj;خه&zwnj;یاڵی خۆتان؟!';
$t[23] = 'پاشان سه&zwnj;رئه&zwnj;نجامی بێ باوه&zwnj;ڕیان هیچ نه&zwnj;بوو بێجگه له&zwnj;وه&zwnj;ی که وتیان: سوێند به&zwnj;خوای په&zwnj;روه&zwnj;ردگارمان ئێمه هه&zwnj;رگیز موشریک و هاوه&zwnj;ڵگه&zwnj;ر نه&zwnj;بووین!!';
$t[24] = 'سه&zwnj;یرکه چۆن درۆ له&zwnj;گه&zwnj;ڵ خۆشیان ده&zwnj;که&zwnj;ن، چۆن بیروبڕوای پووچیان لێ ون بووه و ئاسه&zwnj;واری نه&zwnj;ماوه&zwnj;.';
$t[25] = 'هه&zwnj;ندێکیش له&zwnj;و (بێ باوه&zwnj;ڕانه&zwnj;) گوێت بۆ ده&zwnj;گرن (له&zwnj;کاتی خوێندنی قورئاندا، به&zwnj;ڵام چونکه مه&zwnj;به&zwnj;ستیان نیه شوێن حه&zwnj;قیقه&zwnj;ت بکه&zwnj;ون) ئێمه په&zwnj;رده&zwnj;مان هێنا به&zwnj;سه&zwnj;ر دڵیاندا له تێگه&zwnj;یشتنیان بۆ قورئانه&zwnj;که و گوێمان سه&zwnj;نگین و گران کردوون (وه&zwnj;ک ئه&zwnj;وه وایه که نه&zwnj;بیستن) ئه&zwnj;گه&zwnj;ر هه&zwnj;رچی نیشانه&zwnj;و به&zwnj;ڵگه&zwnj;یه&zwnj;کیش هه&zwnj;یه بیبینن ئه&zwnj;وان بڕوای پێناکه&zwnj;ن، هه&zwnj;تا کاتێکیش که دێن بۆ لات ده&zwnj;یکه&zwnj;نه موجاده&zwnj;له&zwnj;و ده&zwnj;مه&zwnj;ده&zwnj;مێ، سه&zwnj;رئه&zwnj;نجام خوانه&zwnj;ناسان ده&zwnj;ڵێن: ئه&zwnj;م قورئانه هیچ نیه بێجگه له داستانی پێشینان نه&zwnj;بێت!!';
$t[26] = 'ئه&zwnj;وانه نایه&zwnj;ڵن که&zwnj;س گوێ بۆ قورئان بگرێت و خۆیشیانی لێ دوور ده&zwnj;خه&zwnj;نه&zwnj;وه&zwnj;، (جا به&zwnj;و کاره&zwnj;یان) جگه خودی خۆیان که&zwnj;س له&zwnj;ناو نابه&zwnj;ن و هه&zwnj;ستیشی پێناکه&zwnj;ن.';
$t[27] = 'جا ئه&zwnj;گه&zwnj;ر ئه&zwnj;وانه ببینی (له قیامه&zwnj;تدا) له&zwnj;سه&zwnj;ر لێواری دۆزه&zwnj;خ ڕاوه&zwnj;ستێنراون (به&zwnj;ده&zwnj;م حه&zwnj;سره&zwnj;ت و ئاهوناڵه&zwnj;وه&zwnj;) جا ده&zwnj;ڵێن: خۆزگه جارێکی تر بیانگێڕاینایه&zwnj;ته&zwnj;وه بۆ دنیاو (له&zwnj;م سه&zwnj;رئه&zwnj;نجامه شوومه ڕزگارمان ده&zwnj;بوو) ئه&zwnj;وسا هه&zwnj;رگیز نیشانه&zwnj;و به&zwnj;ڵگه&zwnj;و ئایه&zwnj;ته&zwnj;کانی خوامان به&zwnj;درۆ نه&zwnj;ده&zwnj;زانی و، باوه&zwnj;ڕی به&zwnj;تینمان پێی ده&zwnj;بوو...';
$t[28] = 'نه&zwnj;خێر ڕاست ناکه&zwnj;ن و بڕواناهێنن، بێگومان ئه&zwnj;وه ئاواتێکی ئه&zwnj;سته&zwnj;مه&zwnj;، هه&zwnj;موو ئه&zwnj;و کاروکرده&zwnj;وه و پیلانانه&zwnj;ی که پێشتر ده&zwnj;یانشارده&zwnj;وه&zwnj;، ئێستا وائاشکرا بووه بۆیان، خۆ ئه&zwnj;گه&zwnj;ر بگێڕدرێنه&zwnj;وه بۆ (دنیا) ئه&zwnj;وه هه&zwnj;ر ده&zwnj;ست ده&zwnj;که&zwnj;نه&zwnj;وه به&zwnj;و کارو کرده&zwnj;وانه&zwnj;ی که جاران ده&zwnj;یانکرد، به&zwnj;ڕاستی ئه&zwnj;وانه هه&zwnj;ر درۆ ده&zwnj;که&zwnj;ن و درۆزنن.';
$t[29] = 'ئه&zwnj;وانه وتیان: ژیان هه&zwnj;ر ئه&zwnj;م ژیانه&zwnj;ی دنیایه&zwnj;و هیچی تر نیه&zwnj;، ئێمه زیندوو ناکرێینه&zwnj;وه (بۆ لێپرسینه&zwnj;وه&zwnj;).';
$t[30] = 'جائه&zwnj;گه&zwnj;ر کاتێک ئه&zwnj;وانه ده&zwnj;بینیت (له&zwnj;به&zwnj;رده&zwnj;م دادگای پڕ له&zwnj;دادی) په&zwnj;روه&zwnj;ردگاریاندا وه&zwnj;ستێنراون، (خوای گه&zwnj;وره پێیان) ده&zwnj;فه&zwnj;رموێت: ئایا ئه&zwnj;مه&zwnj;ی (که بۆتان پێشهاتووه&zwnj;و ده&zwnj;یبینن) ڕاست و حه&zwnj;قیقه&zwnj;ت نیه&zwnj;؟ (به&zwnj;که&zwnj;ساسی و خه&zwnj;جاڵه&zwnj;تیه&zwnj;وه&zwnj;) ده&zwnj;ڵێن: به&zwnj;ڵێ ڕاسته&zwnj;، سوێند به په&zwnj;روه&zwnj;ردگارمان (ئینجا) خوا ده&zwnj;فه&zwnj;رموێت: (ماده&zwnj;م وایه&zwnj;) سزاو تاڵاو بچێژن به&zwnj;هۆی بێ باوه&zwnj;ڕی و یاخی بوونتانه&zwnj;وه&zwnj;.';
$t[31] = 'به&zwnj;ڕاستی ئه&zwnj;وانه&zwnj;ی که بڕوایان به&zwnj;ئاماده بوونی به&zwnj;رده&zwnj;م (دادگای) خوایی نه&zwnj;بوو زه&zwnj;ره&zwnj;ریان کرد و خۆیان دۆڕاند، کاتێکیش قیامه&zwnj;ت کتوپڕ یه&zwnj;خه&zwnj;یان پێده&zwnj;گرێت، ده&zwnj;ڵێن: ئاخ و داخ وپه&zwnj;شیمانی بۆ ئه&zwnj;و ڕۆژگاره&zwnj;ی به&zwnj;که&zwnj;مته&zwnj;رخه&zwnj;می و (به&zwnj;به&zwnj;ره&zwnj;ڵایی و سه&zwnj;رکه&zwnj;شی و تاوان) بردمانه&zwnj;سه&zwnj;ر، له&zwnj;کاتێکدا ئه&zwnj;وانه هه&zwnj;موو گوناهو تاوانیان داوه به&zwnj;کۆڵیاندا، ئاگاداربن که چه&zwnj;نده خراپه&zwnj;، کۆڵ و باری وا، که هه&zwnj;ڵی ده&zwnj;گرن.';
$t[32] = 'ژیانی دنیا جگه له یاری و گه&zwnj;مه&zwnj;و گاڵته&zwnj;یه&zwnj;کی که&zwnj;م نه&zwnj;بێت هیچی تر نیه&zwnj;، بێگومان ماڵی دوایی (به&zwnj;هه&zwnj;شتی به&zwnj;رین) چاکتره بۆ ئه&zwnj;وانه&zwnj;ی که پارێزکاری و خواناسی ده&zwnj;که&zwnj;ن، ئایا ئه&zwnj;وه عه&zwnj;قڵ و ژیریتان بۆ ناخه&zwnj;نه&zwnj;کار و ژیر نابن؟';
$t[33] = 'به&zwnj;ڕاستی چاک ده&zwnj;زانین که گوفتاری نادروستی خوانه&zwnj;ناسان غه&zwnj;م و په&zwnj;ژاره&zwnj;ت بۆ پێش ده&zwnj;هێنێت، چونکه به&zwnj;ڕاستی ئه&zwnj;وانه وه&zwnj;نه&zwnj;بێت تۆ به&zwnj;درۆزن بزانن، به&zwnj;ڵکو سته&zwnj;مکاران هه&zwnj;ر داننانێن و ئینکاری ئایه&zwnj;ت و فه&zwnj;رمانه&zwnj;کانی خوا ده&zwnj;که&zwnj;ن.';
$t[34] = 'سوێند بێت بێگومان پێغه&zwnj;مبه&zwnj;رانی پێش تۆش بڕوایان پێنه&zwnj;کراوه&zwnj;، له&zwnj;گه&zwnj;ڵ ئه&zwnj;وه&zwnj;شدا ئه&zwnj;وان دانیان به&zwnj;خۆدا گرت له&zwnj;سه&zwnj;ر ئه&zwnj;و بڕوا پێ نه&zwnj;بوونه&zwnj;یان، ئازارو ئه&zwnj;شکه&zwnj;نجه&zwnj;ش دران، هه&zwnj;تا سه&zwnj;رئه&zwnj;نجام سه&zwnj;رکه&zwnj;وتنی ئێمه&zwnj;یان بۆهات و به&zwnj;هره&zwnj;وه&zwnj;ر بوون پێی و هیچ شتێک نیه بتوانێت ویست و فه&zwnj;رمانه&zwnj;کانی خوا بگۆرێت، سوێند بێت به&zwnj;خوا بێگومان هه&zwnj;واڵ و ده&zwnj;نگوباسی هه&zwnj;ندێک له پێغه&zwnj;مبه&zwnj;رانت بۆ هاتووه&zwnj;.';
$t[35] = 'خۆ ئه&zwnj;گه&zwnj;ر پشت هه&zwnj;ڵکردن و سه&zwnj;رپێچی خوانه&zwnj;ناسانت زۆر لاسه&zwnj;خته&zwnj;، جا ئه&zwnj;گه&zwnj;ر ده&zwnj;توانیت تونێلێک له زه&zwnj;ویدا هه&zwnj;ڵبکه&zwnj;نیت یان په&zwnj;یژه&zwnj;یه&zwnj;ک به&zwnj;ئاسماندا هه&zwnj;ڵپه&zwnj;سێریت تا موعجیزه&zwnj;یه&zwnj;کیان بۆ پێش بهێنیت (بێ سووده ئه&zwnj;وانه هه&zwnj;ر باوه&zwnj;ڕت پێناکه&zwnj;ن) ئه&zwnj;گه&zwnj;ر خواش بیویستایه له&zwnj;سه&zwnj;ر هیدایه&zwnj;ت و ڕێبازی دروست، ئه&zwnj;وه کۆی ده&zwnj;کردنه&zwnj;وه (به&zwnj;مه&zwnj;رجێک خۆیان مه&zwnj;به&zwnj;ستیان بێت)، که&zwnj;واته له&zwnj;و که&zwnj;سانه مه&zwnj;به که شاره&zwnj;زانین.';
$t[36] = 'به&zwnj;ڕاستی ته&zwnj;نها ئه&zwnj;وانه به&zwnj;ده&zwnj;م بانگه&zwnj;وازی خواوه ده&zwnj;چن که ده&zwnj;بیستن، (دڵ) مردووه&zwnj;کانیش (له ڕۆژی قیامه&zwnj;تدا) خوا زیندوویان ده&zwnj;کاته&zwnj;وه&zwnj;، له&zwnj;وه&zwnj;ودواش به&zwnj;ناچاریی هه&zwnj;ر ده&zwnj;بێت بۆ لای ئه&zwnj;و بگه&zwnj;ڕێنه&zwnj;وه (تا به&zwnj;سزای بێ باوه&zwnj;ڕیی خۆیانیان بگه&zwnj;یه&zwnj;نێت).';
$t[37] = '(ئه&zwnj;و خوانه&zwnj;ناسانه&zwnj;) وتیان: ئه&zwnj;وه بۆ محمد (صلى الله عليه وسلم) موعجیزه&zwnj;یه&zwnj;کی له&zwnj;لایه&zwnj;ن په&zwnj;روه&zwnj;ردگاریه&zwnj;وه بۆ نایه&zwnj;ته خواره&zwnj;وه&zwnj;؟ بڵێ: به&zwnj;ڕاستی خوا ده&zwnj;سه&zwnj;ڵاتی هه&zwnj;یه که موعجیزه بنێرێت، به&zwnj;ڵام زۆربه&zwnj;ی ئه&zwnj;وانه نافامن و نازانن.';
$t[38] = 'هیچ زینده&zwnj;وه&zwnj;رو گیانله&zwnj;به&zwnj;رێک نیه به&zwnj;سه&zwnj;ر زه&zwnj;ویدا بڕوات و هیچ باڵنده&zwnj;یه&zwnj;ک به&zwnj;باڵه&zwnj;کانی بفڕێت و ئه&zwnj;وانیش ئوممه&zwnj;ت و گه&zwnj;لێک نه&zwnj;بن وه&zwnj;ک ئێوه (دڵنیا بن) هیچ شتێک نیه پشت گوێمان خستبێت له کتێبه (تایبه&zwnj;تیه&zwnj;که&zwnj;ی لوح المحفوظ) دا له&zwnj;وه&zwnj;ودوا هه&zwnj;رهه&zwnj;موو ئه&zwnj;وانه کۆده&zwnj;کرێنه&zwnj;وه بۆلای په&zwnj;روه&zwnj;ردگاریان (بۆ ئه&zwnj;وه&zwnj;ی دادگایی بکرێن و هه&zwnj;موو که&zwnj;س مافی خۆی وه&zwnj;ربگرێت، یان تۆڵه&zwnj;ی ڕه&zwnj;وای لێ بسه&zwnj;نرێت).';
$t[39] = 'ئه&zwnj;وانه&zwnj;ی که بڕوایان نه&zwnj;کردووه به&zwnj;ئایه&zwnj;ت و فه&zwnj;رمانه&zwnj;کانی ئێمه&zwnj;، که&zwnj;ڕن حه&zwnj;ق نابیستن و لاڵن حه&zwnj;ق ناڵێن له&zwnj;ناو تاریکیه&zwnj;کاندا ژیان ده&zwnj;به&zwnj;نه&zwnj;سه&zwnj;ر، بێگومان ئه&zwnj;وه&zwnj;ی خوا بیه&zwnj;وێت گومڕای ده&zwnj;کات (چونکه خۆیشی ده&zwnj;ستپێشخه&zwnj;ره له&zwnj;و بواره&zwnj;دا) و ئه&zwnj;وه&zwnj;ش که بیه&zwnj;وێت (هیدایه&zwnj;تی بداو هیدایه&zwnj;ت وه&zwnj;ربگرێت) ده&zwnj;یخاته سه&zwnj;ر ڕێبازی ڕاست و ڕه&zwnj;وان.';
$t[40] = '(به خوانه&zwnj;ناسان) بڵێ: هه&zwnj;واڵم بده&zwnj;نێ ئه&zwnj;گه&zwnj;ر سزاو تۆڵه&zwnj;ی خوا یه&zwnj;خه&zwnj;تان پێبگرێت، یان قیامه&zwnj;ت یه&zwnj;خه&zwnj;تان پێ بگرێت، (ئا له&zwnj;و کاتانه&zwnj;دا) هاناو هاوار بۆ که&zwnj;سێک ده&zwnj;به&zwnj;ن جگه له خوا؟ ئه&zwnj;گه&zwnj;ر ئێوه ڕاستگۆن که غه&zwnj;یری خوا فریاتان ده&zwnj;که&zwnj;وێت؟!';
$t[41] = '(نه&zwnj;خێر له&zwnj;وکاته سامناکانه&zwnj;دا) هه&zwnj;ر هاناو هاوار بۆ ئه&zwnj;و زاته ده&zwnj;به&zwnj;ن (ئه&zwnj;ویش خوایه&zwnj;کی میهره&zwnj;بانه&zwnj;) به&zwnj;ڵاو ناخۆشیه&zwnj;کانتان له&zwnj;سه&zwnj;ر لاده&zwnj;بات ئه&zwnj;گه&zwnj;ر بیه&zwnj;وێت، جا له&zwnj;وکاته&zwnj;دا هه&zwnj;ر شتێک که به هاوه&zwnj;ڵ شه&zwnj;ریکی ئه&zwnj;و زاته&zwnj;ی داده&zwnj;نێن، فه&zwnj;رامۆشی ده&zwnj;که&zwnj;ن.';
$t[42] = 'سوێند بێت بێگومان ئێمه پێش تۆ پێغه&zwnj;مبه&zwnj;رانی زۆرمان ڕه&zwnj;وانه کردووه بۆ سه&zwnj;ر قه&zwnj;وم و گه&zwnj;لانی تر (جا کاتێک سه&zwnj;رکه&zwnj;ش و یاخی بوون)، تووشی تاڵاوی جه&zwnj;نگ و به&zwnj;ڵاو ته&zwnj;نگانه&zwnj;مان کردن بۆ ئه&zwnj;وه&zwnj;ی لێمان بپاڕینه&zwnj;وه&zwnj;و بلاڵێنه&zwnj;وه&zwnj;.';
$t[43] = 'ئه&zwnj;وانه کاتێک تۆڵه&zwnj;و سزای ئێمه&zwnj;یان بۆ هات، بۆ نزاو پارانه&zwnj;وه&zwnj;ی به&zwnj;کوڵیان نه&zwnj;کرد، به&zwnj;ڵام دڵیان ڕه&zwnj;ق و ڕه&zwnj;ش بوو، شه&zwnj;یتانیش ئه&zwnj;و کاروکرده&zwnj;وه ناپه&zwnj;سه&zwnj;ندانه&zwnj;ی بۆ ڕازاندبوونه&zwnj;وه که ده&zwnj;یانکرد.';
$t[44] = 'ئینجا کاتێک ئه&zwnj;و به&zwnj;رنامه و ڕێبازه&zwnj;ی که یادیان خرابۆوه فه&zwnj;رامۆشیان کرد، ئێمه&zwnj;ش ده&zwnj;روازه&zwnj;کانی هه&zwnj;موو شتێکمان بۆ واڵا کردنه&zwnj;وه و (نازو نیعمه&zwnj;تمان به&zwnj;سه&zwnj;ردا ڕژاندن) هه&zwnj;تا کاتێک دڵخۆش و شادمان بوون به&zwnj;وه&zwnj;ی که پێیان درا، ئیتر له&zwnj;ناکاو گرتمانن و هه&zwnj;موویانمان سه&zwnj;رگه&zwnj;ردان و ڕیسواکرد ئینجا ئه&zwnj;وان بێ ئومێد بوون و به غه&zwnj;م و په&zwnj;ژاره&zwnj;یه&zwnj;کی زۆره&zwnj;وه تیاچوون.';
$t[45] = 'ئینجا ئیتر ئه&zwnj;و گه&zwnj;له&zwnj;ی سته&zwnj;میان کرد و بێ بڕوا بوون بنبڕ کران، (جا ئه&zwnj;وکاته بوونه&zwnj;وه&zwnj;ر هاوڕێ له&zwnj;گه&zwnj;ڵ ئیمانداران و فریشته&zwnj;کاندا ده&zwnj;ڵێن: الحمد لله ڕب العالمین).';
$t[46] = '(ئه&zwnj;ی محمد (صلى الله عليه وسلم) به&zwnj;و بێ باوه&zwnj;ڕانه&zwnj;) بڵێ: هه&zwnj;واڵم بده&zwnj;نێ ئه&zwnj;گه&zwnj;ر خوا ده&zwnj;زگای بیستن و بینینتان لێ وه&zwnj;ربگرێته&zwnj;وه و مۆر بنێت به&zwnj;سه&zwnj;ر دڵه&zwnj;کانتاندا، ئایا خوایه&zwnj;ک هه&zwnj;یه جگه الله ئه&zwnj;و ده&zwnj;زگایانه&zwnj;تان بۆ بهێنێته&zwnj;وه&zwnj;؟ سه&zwnj;یر بکه و سه&zwnj;رنج بده چۆن چه&zwnj;نده&zwnj;ها نموونه و به&zwnj;ڵگه&zwnj;یان بۆ ده&zwnj;هێنینه&zwnj;وه&zwnj;، پاشان ئه&zwnj;وان هه&zwnj;ر مله&zwnj;جه&zwnj;ڕێ ده&zwnj;که&zwnj;ن و ڕوو وه&zwnj;رده&zwnj;گێڕن.';
$t[47] = 'پێیان بڵێ: هه&zwnj;واڵم بده&zwnj;نێ، ئه&zwnj;گه&zwnj;ر سزاو تۆڵه&zwnj;ی خوا کتوپڕ بێ پێشه&zwnj;کی یه&zwnj;خه&zwnj;ی پێگرتن، یان به ئاشکرا نیشانه&zwnj;کانی ده&zwnj;رکه&zwnj;وت و به&zwnj;ره&zwnj;و ڕووتان هات، ئایا ئه&zwnj;و کاته هه&zwnj;ر قه&zwnj;وم و ده&zwnj;سته&zwnj;ی سته&zwnj;مکاران نیه که تیاده&zwnj;چن؟';
$t[48] = 'ئێمه پێغه&zwnj;مبه&zwnj;ران بۆ هیچ شتێک نانێرین جگه بۆ مژده&zwnj;دان نه&zwnj;بێت (بۆ ئیمانداران) و ترساندن نه&zwnj;بێت (بۆ بێ باوه&zwnj;ڕان)، جا ئه&zwnj;وه&zwnj;ی ئیمان و باوه&zwnj;ڕ بهێنێت و چاکسازی بکات، ئه&zwnj;وه نه&zwnj;ترس و بیمیان له&zwnj;سه&zwnj;ره&zwnj;، نه غه&zwnj;مبار ده&zwnj;بن.';
$t[49] = 'ئه&zwnj;وانه&zwnj;ش که بڕوایان نه&zwnj;هێنا به ئایه&zwnj;ت و فه&zwnj;رمانه&zwnj;کانی ئێمه&zwnj;، سزاو ئازار یه&zwnj;خه&zwnj;یان پێ ده&zwnj;گرێت و پێیاندا ده&zwnj;چزێت به&zwnj;هۆی ئه&zwnj;وه&zwnj;ی ده&zwnj;رده&zwnj;چوون له فه&zwnj;رمانی خوا.';
$t[50] = 'ئه&zwnj;ی محمد (صلى الله عليه وسلم)  پێیان بڵێ: من پێتان ناڵێم گه&zwnj;نجینه&zwnj;کانی خوا لای منه&zwnj;، هه&zwnj;ره&zwnj;ها له&zwnj;نهێنی و شاراوه&zwnj;کانیش هیچ نازانم و ئاگادارنیم و پێتان ناڵێم به&zwnj;ڕاستی من فریشته&zwnj;م، به&zwnj;ڵکو ته&zwnj;نها شوێنی ئه&zwnj;وه ده&zwnj;که&zwnj;وم که به&zwnj;وه&zwnj;حی و نیگا پێم ده&zwnj;گات، (هه&zwnj;روه&zwnj;ها) پێیان بڵێ: ئایا کوێرو چاوساغ وه&zwnj;ک یه&zwnj;کن؟ (له ڕاستیدا بێ باوه&zwnj;ڕ وه&zwnj;ک کوێروایه که&zwnj;له تاریکایدا گیری خواردبێت و له هه&zwnj;موو تاوێکی ژیانیداو له هه&zwnj;موو هه&zwnj;نگاوێکیدا به&zwnj;ته&zwnj;مای کۆسپ و ته&zwnj;گه&zwnj;ره&zwnj;یه&zwnj;، له&zwnj;و لاشه&zwnj;وه ئیماندار بیینایه&zwnj;، چاو ساغه&zwnj;، هه&zwnj;موو شتێکی لاروونه&zwnj;، ده&zwnj;زانێت بۆچی هاتووه&zwnj;، بۆچی ده&zwnj;ژی، به&zwnj;ره&zwnj;وکوێ ده&zwnj;چێت) ئایا بۆ بیرناکه&zwnj;نه&zwnj;وه و تێنافکرن (له&zwnj;و ڕاستیانه&zwnj;).';
$t[51] = 'که&zwnj;واته&zwnj;، به قورئان ئه&zwnj;وانه ئاگادارو هۆشیار بکه که ده&zwnj;ترسن کۆبکرێنه&zwnj;وه بۆ لای په&zwnj;روه&zwnj;ردگاریان، ئه&zwnj;وکاته که&zwnj;س نیه جگه له&zwnj;و په&zwnj;روه&zwnj;ردگاره پشتیوانییان لێبکات، که&zwnj;سیش نیه تکاکاریان بێت، (ئه&zwnj;و ئاگادار کردنه&zwnj;وه&zwnj;یه&zwnj;) بۆ ئه&zwnj;وه&zwnj;یه ئه&zwnj;وان ته&zwnj;قواو پارێزکاری بکه&zwnj;ن و (له هه&zwnj;وڵی به&zwnj;ده&zwnj;ستهێنانی ڕه&zwnj;زامه&zwnj;ندیی خوادا بن).';
$t[52] = '(ئه&zwnj;ی محمد (صلى الله عليه وسلم)، ئه&zwnj;ی ئیماندار) نه&zwnj;که&zwnj;یت ئه&zwnj;وانه ده&zwnj;ربکه&zwnj;یت (له مزگه&zwnj;وت و کۆڕی خواناسین) که له به&zwnj;یانیان و ئێواراندا له په&zwnj;روه&zwnj;ردگاریان ده&zwnj;پاڕێنه&zwnj;وه و (ده&zwnj;یپه&zwnj;رستن) مه&zwnj;به&zwnj;ستیشیان ته&zwnj;نها ڕه&zwnj;زامه&zwnj;ندی ئه&zwnj;وه&zwnj;، حساب و لێپرسینه&zwnj;وه&zwnj;ی هیچ شتێکی ئه&zwnj;وان له&zwnj;سه&zwnj;ر تۆ نیه و حساب و لێپرسینه&zwnj;وه&zwnj;ی هیچ شتێکی تۆیش له&zwnj;سه&zwnj;ر ئه&zwnj;وان نیه&zwnj;، ده&zwnj;رکردنی تۆ بۆ ئه&zwnj;و موسڵمانانه ده&zwnj;بێته هۆی ئه&zwnj;وه&zwnj;ی تۆ بچیته ڕیزی سته&zwnj;مکارانه&zwnj;وه&zwnj;.';
$t[53] = 'ئێمه هه&zwnj;ر به&zwnj;و شێوه&zwnj;یه هه&zwnj;ندێکیان به هه&zwnj;ندێکیان تاقیده&zwnj;که&zwnj;ینه&zwnj;وه (کێشه بۆ یه&zwnj;کتر دروست ده&zwnj;که&zwnj;ن، ده&zwnj;بنه به&zwnj;ڵا بۆ یه&zwnj;کتر، یاخود هه&zwnj;ندێک هه&zwnj;ژار ده&zwnj;که&zwnj;ین و هه&zwnj;ندێک ده&zwnj;وڵه&zwnj;مه&zwnj;ند.. هتد) تا بێ بڕوایان بڵێن: ئایا ئائه&zwnj;وانه خوا ڕێزی لێناون له&zwnj;ناوماندا؟! (ئه&zwnj;و هه&zwnj;ژارو داماوانه که&zwnj;ی شایسته&zwnj;ی ئه&zwnj;وه&zwnj;ن خوا هه&zwnj;ڵیانبژێرێت و هیدایه&zwnj;تیان بدات) ئایا خوا زانا نیه به سوپاسگوزاران؟';
$t[54] = 'کاتێک ئه&zwnj;و که&zwnj;سانه هاتن بۆ لات که ئیمان به&zwnj;ئایه&zwnj;ت و فه&zwnj;رمانه&zwnj;کانی ئێمه ده&zwnj;هێنن (تۆ ئه&zwnj;ی محمد (صلى الله عليه وسلم)، ئه&zwnj;ی ئیماندار) بڵێ: سڵاوتان لێ بێت (ئه&zwnj;م مژده&zwnj;یه&zwnj;شیان بده&zwnj;رێ) که&zwnj;: په&zwnj;روه&zwnj;ردگارتان فه&zwnj;رزی کردووه له&zwnj;سه&zwnj;ر خۆی (که له ئاقاری ئێوه&zwnj;دا) به&zwnj;ره&zwnj;حم ومیهره&zwnj;بان و دلۆڤان بێت، ئه&zwnj;گه&zwnj;ر که&zwnj;سێکتان به&zwnj;نه&zwnj;فامیی، به&zwnj;نه&zwnj;زانیی گوناهێکی کردو له&zwnj;وه&zwnj;ودوا ته&zwnj;وبه&zwnj;ی کردو په&zwnj;شیمان بۆوه و پاش ئه&zwnj;نجامدانی خراپه&zwnj;که و چاکسازی کرد، ئه&zwnj;وه با دڵنیابێت که په&zwnj;روه&zwnj;ردگار زۆر لێخۆشبوو و میهره&zwnj;بانه&zwnj;.';
$t[55] = 'هه&zwnj;ر به&zwnj;و شێوه&zwnj;یه که&zwnj;باسکرا به&zwnj;درێژیی (به&zwnj;ڵگه&zwnj;و نیشانه&zwnj;کان) ڕوون ده&zwnj;که&zwnj;ینه&zwnj;وه تا به&zwnj;جوانی و ئاشکرا، حه&zwnj;ق ڕوون بێته&zwnj;وه و ڕێبازی تاوانبار و خوانه&zwnj;ناسان ده&zwnj;ربکه&zwnj;وێت و بناسرێت.';
$t[56] = '(به&zwnj;بێ باوه&zwnj;ڕان) بڵێ: به&zwnj;ڕاستی من قه&zwnj;ده&zwnj;غه کراوم له په&zwnj;رستنی ئه&zwnj;وانه&zwnj;ی ئێوه ده&zwnj;یانپه&zwnj;رستن له غه&zwnj;یری خوا (هه&zwnj;روه&zwnj;ها) بڵێ: من شوێن ئاره&zwnj;زووه&zwnj;کانی ئێوه ناکه&zwnj;وم (ئه&zwnj;گه&zwnj;ر وابکه&zwnj;م)، ئه&zwnj;وه ئیتر بێگومان ئه&zwnj;و کاته من سه&zwnj;ر لێشێواو ده&zwnj;بم و هه&zwnj;رگیز له هیدایه&zwnj;ت دراوان نیم.';
$t[57] = 'پێیان بڵێ: چونکه من زۆر دڵنیام و به به&zwnj;ڵگه&zwnj;وه په&zwnj;روه&zwnj;ردگارم (ده&zwnj;ناسم و ده&zwnj;یپه&zwnj;رستم) که&zwnj;چی (پێم سه&zwnj;یره&zwnj;) ئێوه باوه&zwnj;ڕتان پێی نیه&zwnj;، ئه&zwnj;وه&zwnj;ی ئێوه په&zwnj;له&zwnj;تانه بۆتان پێش بێت (له تۆڵه&zwnj;و سزای خوایی) به&zwnj;ده&zwnj;ست من نیه&zwnj;و لای من نیه&zwnj;، حوکم و فه&zwnj;رمانڕه&zwnj;وایی (هه&zwnj;مووشت) هه&zwnj;ر به&zwnj;ده&zwnj;ست خوایه&zwnj;، ئه&zwnj;و زاته ڕاستی و حه&zwnj;قیقه&zwnj;ته&zwnj;کان ده&zwnj;خاته ڕوو، چاکترین جیاکه&zwnj;ره&zwnj;وه&zwnj;شه (له نێوان حه&zwnj;ق و به&zwnj;تاڵدا).';
$t[58] = 'پێیان بڵێ: ئه&zwnj;گه&zwnj;ر به&zwnj;ڕاستی ئه&zwnj;وه&zwnj;ی ئێوه په&zwnj;له&zwnj;تانه له پێشهاتنی (سزای خوایی) لای من بوایه و به&zwnj;ده&zwnj;ست من بوایه&zwnj;، ئه&zwnj;و کاته هه&zwnj;موو شت له نێوانماندا کۆتایی ده&zwnj;هات (ئێوه له&zwnj;ناو ده&zwnj;چوون)، خوایش هه&zwnj;میشه زانایه به سته&zwnj;مکاران.';
$t[59] = 'کلیلی هه&zwnj;موو شاراوه&zwnj;کان و زانینی هه&zwnj;موو نهێنیه&zwnj;کان به&zwnj;ده&zwnj;ست خوایه و هه&zwnj;ر لای ئه&zwnj;وه&zwnj;، که&zwnj;س نایانزانێت جگه له&zwnj;و زاته هه&zwnj;رچی له&zwnj;سه&zwnj;ر وشکانیه (له گیاندارو بێ گیان و گیاو دارو زینده&zwnj;وه&zwnj;رانی وردو درشت له&zwnj;سه&zwnj;ر زه&zwnj;وی و له توێی خاکدا) خوا پێی ده&zwnj;زانێت، هه&zwnj;روه&zwnj;ها هه&zwnj;رچی له ده&zwnj;ریاکانیشدا هه&zwnj;یه (له زینده&zwnj;وه&zwnj;ری هه&zwnj;مه&zwnj;جۆرو ماسی سه&zwnj;یرو سه&zwnj;مه&zwnj;ره&zwnj;) ئاگای لێیه&zwnj;تی و هیچ گه&zwnj;ڵایه&zwnj;ک ناکه&zwnj;وێته خواره&zwnj;وه خوا پێی نه&zwnj;زانێت، هه&zwnj;روه&zwnj;ها ده&zwnj;نکێک یان تۆوێک له تاریکایه&zwnj;کانی زه&zwnj;ویدا نیه&zwnj;، ئه&zwnj;و ده&zwnj;نکه چ شێدار بێت یان وشک بێت، هیچ شتێک نیه له خوا په&zwnj;نهان بێت و له (لوح المحفوظ) دا، یاخود له دۆسیه و ده&zwnj;زگای تایبه&zwnj;تیدا تۆمار نه&zwnj;کرابێت.';
$t[60] = 'هه&zwnj;ر ئه&zwnj;و زاته&zwnj;یه به&zwnj;شه&zwnj;و خه&zwnj;وتان لێ ده&zwnj;خات و ده&zwnj;تانمرێنێت (له&zwnj;م ساڵانه&zwnj;ی دوایدا زاناکان بۆیان ده&zwnj;رکه&zwnj;وتووه که خه&zwnj;و جۆره مردنێکه&zwnj;)، هه&zwnj;روه&zwnj;ها ده&zwnj;زانێت له ڕۆژیشدا چ کارو کرده&zwnj;وه&zwnj;یه&zwnj;ک ئه&zwnj;نجام ده&zwnj;ده&zwnj;ن، (هه&zwnj;موو ڕۆژێک ژیانتان پێده&zwnj;به&zwnj;خشێت) و پاشان زیندووتان ده&zwnj;کاته&zwnj;وه بۆ ئه&zwnj;وه&zwnj;ی واده&zwnj;ی دیاری کراو که (ڕۆژانی ته&zwnj;مه&zwnj;نتانه&zwnj;) ته&zwnj;واو بکرێت و (خه&zwnj;وێکی گه&zwnj;وره&zwnj;تان لێده&zwnj;که&zwnj;وێت) له&zwnj;وه&zwnj;ودوا گه&zwnj;ڕانه&zwnj;وه&zwnj;تان هه&zwnj;ر بۆ لای ئه&zwnj;و زاته&zwnj;یه&zwnj;، پاشان هه&zwnj;واڵی ئه&zwnj;و کارو کرده&zwnj;وانه&zwnj;تان پێڕاده&zwnj;گه&zwnj;یه&zwnj;نێت که&zwnj;کاتی خۆی ئه&zwnj;نجامتان ده&zwnj;دا.';
$t[61] = 'ئه&zwnj;و زاته ده&zwnj;سه&zwnj;ڵاتداری بێ سنووره به&zwnj;سه&zwnj;ر به&zwnj;نده&zwnj;کانیدا، چه&zwnj;نده&zwnj;ها (فریشته&zwnj;ی) چاودێرو تۆمارکه&zwnj;ر ده&zwnj;نێرێت بۆ سه&zwnj;رتان هه&zwnj;تا ئه&zwnj;و کاته&zwnj;ی مه&zwnj;رگ دێت و یه&zwnj;خه به&zwnj;که&zwnj;سێکتان ده&zwnj;گرێت، ئه&zwnj;و کاته ئیتر (فریشته&zwnj;) ڕه&zwnj;وانه کراوه&zwnj;کانمان گیانی ده&zwnj;کێشن و له کاتێکدا ئه&zwnj;وان له کارو کرده&zwnj;وه&zwnj;ی خۆیان که&zwnj;مته&zwnj;رخه&zwnj;می ناکه&zwnj;ن.';
$t[62] = 'پاشان خه&zwnj;ڵکی هه&zwnj;موویان گه&zwnj;ڕێنرانه&zwnj;وه بۆ لای خوا که سه&zwnj;روه&zwnj;ریی ڕاسته&zwnj;قینه&zwnj;یانه&zwnj;، ئاگاداربن که حوکم و فه&zwnj;رمانڕه&zwnj;وایی هه&zwnj;ر به&zwnj;ده&zwnj;ست ئه&zwnj;وه&zwnj;و ئه&zwnj;و زاته خێراو له&zwnj;ماوه&zwnj;یه&zwnj;کی که&zwnj;مدا له هه&zwnj;مووان ده&zwnj;پرسێته&zwnj;وه&zwnj;.';
$t[63] = 'ئه&zwnj;ی محمد (صلى الله عليه وسلم) پێیان بڵێ: کێ فریاتان ده&zwnj;که&zwnj;وێت و ڕزگارتان ده&zwnj;کات له تاریکیه&zwnj;کانی وشکانی و ده&zwnj;ریادا، (کاتێک گیرده&zwnj;خۆن) و به که&zwnj;ساسی لێی ده&zwnj;پاڕێنه&zwnj;وه&zwnj;، به&zwnj;نهێنی هانای بۆ ده&zwnj;به&zwnj;ن، (په&zwnj;یمان ده&zwnj;ده&zwnj;ن و ده&zwnj;ڵێن) سوێند بێت به خوا ئه&zwnj;گه&zwnj;ر له&zwnj;م ته&zwnj;نگانه&zwnj;یه ڕزگارمان بکه&zwnj;یت، ئه&zwnj;وه به&zwnj;دڵنیاییه&zwnj;وه ده&zwnj;چینه ڕیزی سوپاسگوزارانه&zwnj;وه&zwnj;.';
$t[64] = 'جا له وه&zwnj;ڵامیاندا بڵێ: خوا له&zwnj;و تاریکیانه و له هه&zwnj;موو کۆسپێک ڕزگارتان ده&zwnj;کات، که&zwnj;چی پاشان (زۆربه&zwnj;تان) هاوه&zwnj;ڵ و شه&zwnj;ریکی بۆ بڕیار ده&zwnj;ده&zwnj;ن.';
$t[65] = 'پێیان بڵێ: هه&zwnj;ر ئه&zwnj;و زاته به&zwnj;توانه&zwnj;یه و ده&zwnj;توانێت سزای سه&zwnj;ختتان له&zwnj;سه&zwnj;روتانه&zwnj;وه بۆ بنێرێت یان له ژێر پێتانه&zwnj;وه یاخود بتانکاته ده&zwnj;سته ده&zwnj;سته و پارچه پارچه و تاڵاوی توندو تیژی هه&zwnj;ندێکتان بکات به&zwnj;گه&zwnj;رووی هه&zwnj;ندێکی ترتاندا، ته&zwnj;ماشا بکه چۆن چه&zwnj;نده&zwnj;ها به&zwnj;ڵگه&zwnj;ی جۆراو جۆریان بۆ ده&zwnj;هێنینه&zwnj;وه&zwnj;، بۆ ئه&zwnj;وه&zwnj;ی ئه&zwnj;وان تێبگه&zwnj;ن.';
$t[66] = '(له&zwnj;گه&zwnj;ڵ ئه&zwnj;وه&zwnj;شدا) قه&zwnj;ومه&zwnj;که&zwnj;ی تۆ ئه&zwnj;م به&zwnj;رنامه و قورئانه&zwnj;یان به&zwnj;درۆزانی، له&zwnj;کاتێکدا هه&zwnj;ر ئه&zwnj;وه&zwnj;یه حه&zwnj;قیقه&zwnj;ت و ڕاستی پێیان بڵێ: ئه&zwnj;وه ئیتر من ده&zwnj;سه&zwnj;ڵاتی ڕزگار کردنی ئێوه&zwnj;م نیه و من پشتیوانی ئێوه نیم (ئه&zwnj;گه&zwnj;ر خوا به&zwnj;ڵایه&zwnj;کتان به&zwnj;سه&zwnj;ر بهێنێت).';
$t[67] = 'بۆ هه&zwnj;موو هه&zwnj;واڵێک (که له قورئاندا هاتووه&zwnj;) کات و شوێنی به&zwnj;دی هاتنی خۆی هه&zwnj;یه&zwnj;، له ئاینده&zwnj;شدا ده&zwnj;یزانن.';
$t[68] = 'کاتێکیش ئه&zwnj;وانه&zwnj;ت بینی که ده&zwnj;م له ئایه&zwnj;ته&zwnj;کانی ئێمه&zwnj;وه ده&zwnj;ده&zwnj;ن و (ده&zwnj;یکه&zwnj;نه گاڵته&zwnj;جاڕی خۆیان) ئه&zwnj;وه تۆ پشتیان تێ بکه&zwnj;و کۆڕو کۆبوونه&zwnj;وه&zwnj;یان به&zwnj;جێ بهێڵه هه&zwnj;تا ئه&zwnj;و کاته&zwnj;ی ده&zwnj;چنه ناو باسێکی تره&zwnj;وه&zwnj;، کاتێکیش ئه&zwnj;گه&zwnj;ر شه&zwnj;یتان له بیری بردیته&zwnj;وه (که دوور بکه&zwnj;ویته&zwnj;وه لێیان) جا دوای ئه&zwnj;وه&zwnj;ی که بیرت هاته&zwnj;وه له&zwnj;گه&zwnj;ڵ قه&zwnj;ومی سته&zwnj;مکاران دامه&zwnj;نیشه&zwnj;.';
$t[69] = '(هه&zwnj;رچه&zwnj;نده&zwnj;) تاوانی نه&zwnj;فامان هیچ کار ناکاته سه&zwnj;ر پاداشتی ئه&zwnj;وانه&zwnj;ی خواناس و دیندارن، به&zwnj;ڵام (ئه&zwnj;و هه&zwnj;ڵوێسته&zwnj;ی پێشوو) یادخستنه&zwnj;وه&zwnj;یه&zwnj;، بۆ ئه&zwnj;وه&zwnj;ی خۆیان بپارێزن (له&zwnj;و کۆڕو کۆمه&zwnj;ڵانه و یاخیه&zwnj;کانیش دابچڵه&zwnj;کێن).';
$t[70] = '(ئه&zwnj;ی محمد (صلى الله عليه وسلم)، ئه&zwnj;ی ئیماندار) وازله&zwnj;وانه بهێنه که ئاینی خۆیان کردووه به&zwnj;گاڵته&zwnj;وگاڵته&zwnj;جار و ژیانی دنیا فریوی داون و سه&zwnj;ر لێشێواوی کردوون، یادیان بهێنه&zwnj;ره&zwnj;وه به&zwnj;قورئان تا که&zwnj;س تیا نه&zwnj;چێت به&zwnj;هۆی کارو کرده&zwnj;وه&zwnj;ی ناله&zwnj;باریه&zwnj;وه&zwnj;، ئه&zwnj;و کاته جگه له خوا که&zwnj;س نه&zwnj;بێت پشتیوانی بێت، که&zwnj;سیش نه&zwnj;بێت تکاکاری بێت، چونکه کابرای خوانه&zwnj;ناس هه&zwnj;رچیه&zwnj;کی ببێت و بیه&zwnj;وێت بیکاته بارمته&zwnj;و فیدیه&zwnj;ی خۆی، لێی وه&zwnj;رناگرێت، ئا ئه&zwnj;وانه کاسانێکن که گرفتار بوون و تیاچوون به ده&zwnj;ست کارو کرده&zwnj;وه&zwnj;ی ناله&zwnj;باری خۆیانه&zwnj;وه&zwnj;، بۆ ئه&zwnj;وانه خواردنه&zwnj;وه و ئاوی له&zwnj;کوڵ و سزای به&zwnj;ئێش ئاماده&zwnj;یه&zwnj;، به&zwnj;هۆی بێ باوه&zwnj;ڕ بوونیانه&zwnj;وه&zwnj;.';
$t[71] = 'پێیان بڵێ: (ئایا ڕه&zwnj;وایه&zwnj;) په&zwnj;رستن و هاناو هاوار بۆ که&zwnj;سێک یان بۆ شتێک به&zwnj;رین، که نه ده&zwnj;توانێت قازانجمان پێبگه&zwnj;یه&zwnj;نێت نه زیان، ئایا ڕاسته پاشگه&zwnj;ز بکرێینه&zwnj;وه دوای ئه&zwnj;وه&zwnj;ی خوا هیدایه&zwnj;تی داین، ئێمه وه&zwnj;کو ئه&zwnj;و که&zwnj;سه بین که شه&zwnj;یتانه&zwnj;کان ده&zwnj;ستیان لێوه&zwnj;شاند بێت و شێتیان کردبێت، به&zwnj;سه&zwnj;ر زه&zwnj;ویدا سه&zwnj;رگه&zwnj;ردان بسوڕێته&zwnj;وه و چه&zwnj;ند هاوه&zwnj;ڵێکی (دڵسۆز)ی هه&zwnj;بێت، بانگی بکه&zwnj;ن بۆ ڕێبازی هیدایه&zwnj;ت و (به&zwnj;سۆزه&zwnj;وه پێی بڵێن): وه&zwnj;ره بۆ لای ئێمه&zwnj;، (ئه&zwnj;ی محمد (صلى الله عليه وسلم)، ئه&zwnj;ی ئیماندار) تۆ بڵێ: به&zwnj;ڕاستی من دڵنیام که هه&zwnj;ر ڕێنموویی خوا (ئاینی ئیسلام) ڕێنموویی ته&zwnj;واوه&zwnj;، ئێمه فه&zwnj;رمانمان پێدراوه که ته&zwnj;سلیم و فه&zwnj;رمانبه&zwnj;رداری په&zwnj;روه&zwnj;ردگاری جیهانیان ببین، (به&zwnj;قسه&zwnj;ی شه&zwnj;یتانه&zwnj;کان نه&zwnj;که&zwnj;ین).';
$t[72] = 'هه&zwnj;روه&zwnj;ها (فه&zwnj;رمانمان پێدراوه&zwnj;): نوێژو دروشمه&zwnj;کانی به&zwnj;چاکی ئه&zwnj;نجام بده&zwnj;ن و له خه&zwnj;شم و قینی خوا خۆتان بپارێزن، ئه&zwnj;و په&zwnj;روه&zwnj;ردگاره ئه&zwnj;وه&zwnj;یه که هه&zwnj;ر بۆ لای ئه&zwnj;و کۆ ده&zwnj;کرێینه&zwnj;وه&zwnj;.';
$t[73] = 'هه&zwnj;ر ئه&zwnj;و زاته ئاسمانه&zwnj;کان و زه&zwnj;وی له&zwnj;سه&zwnj;ر بنچینه&zwnj;و بناغه&zwnj;ی حه&zwnj;ق و ڕاستی دروست کردووه&zwnj;، هه&zwnj;ر ڕۆژێک هه&zwnj;ر کاتێک فه&zwnj;رمان (به نه&zwnj;مان و له&zwnj;ناوچوونی بدات) بێ دواکه&zwnj;وتن پێش دێت، فه&zwnj;رمان و گوفتاری ئه&zwnj;وزاته هه&zwnj;میشه ڕاست و به&zwnj;جێیه و ئه&zwnj;نجامدراوه&zwnj;، له&zwnj;و ڕۆژه&zwnj;ی فوو ده&zwnj;کرێت به (صور)دا ده&zwnj;سه&zwnj;ڵات ته&zwnj;نها بۆ خوایه&zwnj;، هه&zwnj;ر ئه&zwnj;و زانای نهێنی و ئاشکرایه&zwnj;، هه&zwnj;ر ئه&zwnj;ویش خوایه&zwnj;کی دانا و ئاگایه (به هه&zwnj;موو به&zwnj;دیهێنراوه&zwnj;کانی).';
$t[74] = '(به&zwnj;سه&zwnj;رهاتی سه&zwnj;ده&zwnj;کانی ڕابوردوو بێنه&zwnj;وه یادیان) کاتێک ئیبراهیم به ئازه&zwnj;ری باوکی وت: ئایا ڕاسته چه&zwnj;ند بتێک بکه&zwnj;یته خوا؟! به&zwnj;ڕاستی من ده&zwnj;بینم تۆش و قه&zwnj;ومه&zwnj;که&zwnj;شت له&zwnj;ناو گومڕاییه&zwnj;کی ئاشکرادان.';
$t[75] = 'به&zwnj;و شێوه&zwnj;یه&zwnj;ش ده&zwnj;سه&zwnj;ڵاتی تۆکمه&zwnj;مان له ئاسمانه&zwnj;کان و زه&zwnj;ویدا نیشانی ئیبراهیم ده&zwnj;ده&zwnj;ین بۆ ئه&zwnj;وه&zwnj;ی به&zwnj;ڵگه به&zwnj;ده&zwnj;ست بێت و له ڕیزی دڵنیاکاندا بێت.';
$t[76] = 'ئینجا کاتێک شه&zwnj;و باڵی کێشا به&zwnj;سه&zwnj;ر ئیبراهیمدا ئه&zwnj;ستێره&zwnj;یه&zwnj;کی (گه&zwnj;شی گه&zwnj;وره&zwnj;ی)بینی، بۆیه وتی: ئاگاداربن، وادابنێین ئه&zwnj;مه په&zwnj;روه&zwnj;ردگارمه&zwnj;، کاتێک ئه&zwnj;ستێره&zwnj;که&zwnj;ی لێ ئاوا بوو وتی: من شتی له&zwnj;به&zwnj;رچاو ون بووم خۆش ناوێت و نایپه&zwnj;رستم.';
$t[77] = 'ئینجا کاتێک مانگی بینی هه&zwnj;ڵهات، وتی: ئاگاداربن، وادابنێین ئه&zwnj;مه&zwnj;یان په&zwnj;روه&zwnj;ردگارمه&zwnj;، کاتێک ئه&zwnj;ویش ئاوا بوو وتی: سوێند بێت به خوا ئه&zwnj;گه&zwnj;ر په&zwnj;روه&zwnj;ردگارم هیدایه&zwnj;تی نه&zwnj;دامایه&zwnj;، ئه&zwnj;وه بێگومان له ڕیزی ده&zwnj;سته&zwnj;ی گومڕاکاندا ده&zwnj;بووم.';
$t[78] = 'ئینجا کاتێک خۆری بینی هه&zwnj;ڵهات، وتی: ئاگاداربن، وادابنێین ئه&zwnj;مه&zwnj;یان په&zwnj;روه&zwnj;ردگارمه&zwnj;، ئه&zwnj;مه گه&zwnj;وره&zwnj;تریشه کاتێک ئه&zwnj;ویش ئاوابوو وتی: ئه&zwnj;ی خه&zwnj;ڵکینه&zwnj;، ئه&zwnj;ی خزمینه&zwnj;، به&zwnj;ڕاستی من به&zwnj;ریم له&zwnj;و شتانه&zwnj;ی ئێوه کردووتانه به&zwnj;هاوه&zwnj;ڵ و شه&zwnj;ریک (بۆ په&zwnj;روه&zwnj;ردگار).';
$t[79] = 'به&zwnj;ڕاستی من ڕووی خۆم و دڵم له په&zwnj;رستندا کردۆته ئه&zwnj;و زاته&zwnj;ی ئاسمانه&zwnj;کان و زه&zwnj;وی فه&zwnj;راهه&zwnj;م هێناوه&zwnj;، له&zwnj;کاتێکدا ڕووم له&zwnj;هیچ شتێکی ترناکه&zwnj;م، به&zwnj;دڵئارامی ته&zwnj;واویشه&zwnj;وه (ده&zwnj;ڵیم که&zwnj;): هه&zwnj;رگیز من له ڕیزی موشریک و هاوه&zwnj;ڵگه&zwnj;راندانیم.';
$t[80] = '(که&zwnj;چی قه&zwnj;ومه ناله&zwnj;باره&zwnj;که&zwnj;شی) که&zwnj;وتنه موجاده&zwnj;له&zwnj;و ده&zwnj;مه&zwnj;ده&zwnj;مێ دژی (له وه&zwnj;ڵامیاندا ئیبراهیم وتی): ئایا ئێوه ده&zwnj;رباره&zwnj;ی خوای (تاکو ته&zwnj;نها) له&zwnj;گه&zwnj;ڵ مندا ده&zwnj;مه&zwnj;ده&zwnj;مێ و موجاده&zwnj;له ده&zwnj;که&zwnj;ن؟ له&zwnj;کاتێکدا ئه&zwnj;و به&zwnj;ڕاستی هیدایه&zwnj;تی داوم و ڕێبازی ڕاستی نیشانداوم و ناشترسم له&zwnj;و شتانه&zwnj;ی که ئێوه ده&zwnj;یکه&zwnj;ن به&zwnj;شه&zwnj;ریکی خوا (چونکه ناتوانن زیانێکم پێ بگه&zwnj;یه&zwnj;نن) مه&zwnj;گه&zwnj;ر ویستی په&zwnj;روه&zwnj;ردگارمی له&zwnj;سه&zwnj;ربێت و بیه&zwnj;وێت شتێکم به&zwnj;سه&zwnj;ردا بهێنێت، په&zwnj;روه&zwnj;ردگارم به&zwnj;زانست و زانیاری هه&zwnj;موو شتێکی گرتۆته&zwnj;وه&zwnj;، ئایا ئه&zwnj;وه بیرناکه&zwnj;نه&zwnj;وه و یاداوه&zwnj;ری وه&zwnj;رناگرن؟';
$t[81] = 'من چۆن له&zwnj;و شتانه ده&zwnj;ترسم که ئێوه له جیاتی خوا ده&zwnj;یانپه&zwnj;رستن و چۆن ئێوه ناترسن له&zwnj;وه&zwnj;ی که شه&zwnj;ریک و هاوه&zwnj;ڵتان بۆ خوا بڕیارداوه به&zwnj;بێ هیچ به&zwnj;ڵگه&zwnj;یه&zwnj;ک که خوا ناردبێتی بۆتان، جا که&zwnj;واته کام ده&zwnj;سته شایسته&zwnj;ی ئه&zwnj;وه&zwnj;یه بێ ترس و بیم بێت و به ئاسووده&zwnj;یی بژی؟ ئه&zwnj;گه&zwnj;ر ئێوه ده&zwnj;زانن ئه&zwnj;وه پێم بڵێن؟...';
$t[82] = 'ئه&zwnj;وانه&zwnj;ی ئیمان و باوه&zwnj;ڕیان هێناوه و سته&zwnj;م و هاوه&zwnj;ڵگه&zwnj;ریان تێکه&zwnj;ڵ به ئیمانه&zwnj;که&zwnj;یان نه&zwnj;کردووه&zwnj;، ئاسووده&zwnj;یی و هێمنی هه&zwnj;ر بۆ ئه&zwnj;وانه&zwnj;یه&zwnj;، هه&zwnj;ر ئه&zwnj;وانه&zwnj;ش ڕێنموومایی کراون.';
$t[83] = 'ئه&zwnj;و وتو وێژو لێکۆڵینه&zwnj;وانه به&zwnj;ڵگه و نیشانه&zwnj;ی ئێمه بوو، کاتێک به&zwnj;خشیمانن به ئیبراهیم له&zwnj;سه&zwnj;ر گه&zwnj;له&zwnj;که&zwnj;ی و زاڵ بوو به&zwnj;سه&zwnj;ریاندا، قسه&zwnj;و بیروباوه&zwnj;ری به&zwnj;سه&zwnj;ر قه&zwnj;ومه&zwnj;که&zwnj;یدا سه&zwnj;رکه&zwnj;وت، جا هه&zwnj;ر که&zwnj;سێک بمانه&zwnj;وێت و (خۆیشی شایسته بێت) چه&zwnj;نده&zwnj;ها پله&zwnj;وپایه&zwnj;ی به&zwnj;رز ده&zwnj;که&zwnj;ینه&zwnj;وه&zwnj;، به&zwnj;ڕاستی په&zwnj;روه&zwnj;ردگاری تۆ دانا و کار به&zwnj;جێ یه و زانایه به&zwnj;هه&zwnj;موو شتێک.';
$t[84] = '(له&zwnj;وه&zwnj;ودوا) ئیسحاق و یه&zwnj;عقوبمان پێبه&zwnj;خشی، هه&zwnj;ریه&zwnj;ک له&zwnj;وانمان ڕێنموویی کرد، پێشتریش نوحمان ڕێنموویی کردبوو و له نه&zwnj;وه&zwnj;کانی (تری ئیبراهیم) داود و سلیمان و ئه&zwnj;یوب و یوسف و موسا و هاروون (هاتنه دنیاوه و بوونه ڕابه&zwnj;رو چاوساغ بۆ خه&zwnj;ڵکی) ئا به&zwnj;و شێوه&zwnj;یه پاداشتی چاکه&zwnj;خوازان ده&zwnj;ده&zwnj;ینه&zwnj;وه (نه&zwnj;وه&zwnj;ی چاکو پاک و خواناسیان پێده&zwnj;به&zwnj;خشین).';
$t[85] = '(هه&zwnj;روه&zwnj;ها) زه&zwnj;که&zwnj;ریا و یه&zwnj;حیا و عیسا و ئیلیاس هه&zwnj;ر هه&zwnj;موویان له پاک و چاکان بوون.';
$t[86] = 'ئیسماعیل و یه&zwnj;سه&zwnj;ع و یونس و لوطیش (له&zwnj;هیدایه&zwnj;ت دراوو پێغه&zwnj;مبه&zwnj;ران بوون) هه&zwnj;ریه&zwnj;که له&zwnj;وانیشمان ڕێزدارکرد به&zwnj;سه&zwnj;ر هه&zwnj;موو خه&zwnj;ڵکی جیهانی (سه&zwnj;رده&zwnj;می خۆیاندا).';
$t[87] = 'هه&zwnj;روه&zwnj;ها هه&zwnj;ندێک له باوانیان و نه&zwnj;وه&zwnj;کانیان و براکانیانمان هه&zwnj;ڵبژاردو هیدایه&zwnj;ت و ڕێنموویمان کردن بۆ ڕێگه&zwnj;و ڕێبازی ڕاست و دروست.';
$t[88] = 'ئه&zwnj;وه&zwnj;یه هیدایه&zwnj;ت و ڕێنموویی خوایی که هیدایه&zwnj;ت و ڕێنموویی هه&zwnj;ر که&zwnj;سێکی پێ ده&zwnj;دات له به&zwnj;نده&zwnj;کانی (که شایسته بێت) خۆ ئه&zwnj;گه&zwnj;ر شه&zwnj;ریک و هاوبه&zwnj;ش بڕیار بده&zwnj;ن، ئه&zwnj;وه هه&zwnj;رچی کارو کرده&zwnj;وه&zwnj;یه&zwnj;کی (چاکیشیان) هه&zwnj;یه هه&zwnj;مووی پووچ و بێ نرخ ده&zwnj;بێت.';
$t[89] = 'ئا ئه&zwnj;وانه&zwnj;ی (که ناوبران) ئه&zwnj;وانه&zwnj;ن کتێبی (ئاسمانی) و ده&zwnj;سه&zwnj;ڵات و فه&zwnj;رمانڕه&zwnj;وایی و پێغه&zwnj;مبه&zwnj;رایه&zwnj;تیمان پێ به&zwnj;خشیوون، جا ئه&zwnj;گه&zwnj;ر ئه&zwnj;وانه بڕوایان پێی نه&zwnj;بێت (له نه&zwnj;فامان)، ئه&zwnj;وه به&zwnj;ڕاستی به&zwnj;که&zwnj;سانێکی ترمان (له ژیرو هۆشمه&zwnj;نده&zwnj;کان) سپارد و بڕوایان پێیه&zwnj;تی و پشتیوانیی لێده&zwnj;که&zwnj;ن و پێی بێ باوه&zwnj;ڕ نین.';
$t[90] = 'ئه&zwnj;و (ناوبراوانه&zwnj;) ئه&zwnj;و که&zwnj;سانه&zwnj;ن که خوا (به&zwnj;تایبه&zwnj;ت) هیدایه&zwnj;ت و ڕینموویی کردوون، تۆش (ئه&zwnj;ی محمد (صلى الله عليه وسلم)، ئه&zwnj;ی ئیماندار) شوێنی هیدایه&zwnj;ت و ڕێنموویی ئه&zwnj;وانه بکه&zwnj;وه (هه&zwnj;روه&zwnj;ها به&zwnj;و خه&zwnj;ڵکه&zwnj;) بڵێ: که من هیج جۆره کرێ و پاداشتێکتان له&zwnj;سه&zwnj;ر ڕێنموویی لێ داوا ناکه&zwnj;م، ئه&zwnj;م قورئان و به&zwnj;رنامه&zwnj;یه&zwnj;ش (که به مندا ڕه&zwnj;وانه&zwnj;کراوه&zwnj;) ته&zwnj;نها یادخه&zwnj;ره&zwnj;وه&zwnj;یه بۆ هه&zwnj;موو خه&zwnj;ڵکی سه&zwnj;ر ئه&zwnj;م زه&zwnj;ویه&zwnj;.';
$t[91] = '(جوله&zwnj;که&zwnj;) وه&zwnj;کو پێویست قه&zwnj;دری خوایان نه&zwnj;گرت، ڕێزیان بۆ دانه&zwnj;نا، چونکه وتیان: خوا هیچ کتێبێکی بۆ هیچ که&zwnj;س دانه&zwnj;به&zwnj;زاندوه&zwnj;، پێیان بڵێ: باشه ئه&zwnj;ی کێ ئه&zwnj;و کتێبه&zwnj;ی دابه&zwnj;زاند که موسا هێنای (بۆتان)، له&zwnj;کاتێکدا نوورو هیدایه&zwnj;تیش بوو بۆ خه&zwnj;ڵکی، که&zwnj;چی ئێوه به&zwnj;ش به&zwnj;ش و پارچه پارچه&zwnj;ی ده&zwnj;که&zwnj;ن و ئه&zwnj;وه&zwnj;ی به&zwnj;دڵتان بێت نیشانی خه&zwnj;ڵکی ده&zwnj;ده&zwnj;ن و زۆریشی ده&zwnj;شارنه&zwnj;وه (به&zwnj;تایبه&zwnj;ت ده&zwnj;رباره&zwnj;ی پێغه&zwnj;مبه&zwnj;ر و قورئان) هه&zwnj;روه&zwnj;ها بڵێ هه&zwnj;ندێک شت فێرکران که نه ئێوه نه باو و باپیرانتان ده&zwnj;تانزانی، پێیان بڵێ: هه&zwnj;ر خوا خۆی دایبه&zwnj;زاندوه و فێریشی کردوون، له&zwnj;وه&zwnj;ودوا وازیان لێ بهێنه با له به&zwnj;تاڵ و نادروستی خۆیاندا ڕۆبچن و گه&zwnj;مه&zwnj;و یاری بکه&zwnj;ن!!';
$t[92] = 'ئه&zwnj;م (قورئانه&zwnj;) کتێبێکی پیرۆزه&zwnj;، دامان به&zwnj;زاندوه&zwnj;، ڕاستی کتێبه&zwnj;کانی پێشوو ده&zwnj;سه&zwnj;لمێنێت (به تایبه&zwnj;ت ته&zwnj;ورات و ئینجیل، که ئه&zwnj;وانیش له لایه&zwnj;ن خواوه ڕه&zwnj;وانه کراون) هه&zwnj;روه&zwnj;ها ناردوومانه تا شاره دێرینه&zwnj;که (مه&zwnj;ککه&zwnj;ی پیرۆز) و هه&zwnj;موو ده&zwnj;وروبه&zwnj;ری داچڵه&zwnj;کێنێت و (بانگیان بکات بۆ ئیسلام، که دانیشتووانی هه&zwnj;موو گۆی زه&zwnj;وی ده&zwnj;گرێته&zwnj;وه&zwnj;) جا ئه&zwnj;وانه&zwnj;ی که باوه&zwnj;ڕ به&zwnj;رۆژی دوایی ده&zwnj;هێنن، بڕوا به قورئانیش ده&zwnj;هێنن و له&zwnj;سه&zwnj;ر نوێژه&zwnj;کانیان به&zwnj;رده&zwnj;وامن و هه&zwnj;میشه له&zwnj;کاتی خۆیدا ئه&zwnj;نجامی ده&zwnj;ده&zwnj;ن و ده&zwnj;یپارێزن.';
$t[93] = 'کێ له&zwnj;وه سته&zwnj;مکارتره که درۆ بۆ خوا هه&zwnj;ڵبه&zwnj;ستێت و یاخود بڵێت: منیش وه&zwnj;حی و نیگام بۆ هاتووه&zwnj;، له ڕاستیشدا هیچی بۆ نه&zwnj;هاتبێت، هه&zwnj;روه&zwnj;ها ئه&zwnj;و که&zwnj;سه&zwnj;ش که وتویه&zwnj;تی: منیش وه&zwnj;ک قورئان کتێبێک داده&zwnj;به&zwnj;زێنم وه&zwnj;ک خوا دایبه&zwnj;زاندوه&zwnj;، خۆ ئه&zwnj;گه&zwnj;ر ئه&zwnj;و سته&zwnj;مکارانه ببینی چۆن له ئێش و ئازاری سه&zwnj;ره&zwnj;مه&zwnj;رگدا (گیرێکیان خواردووه&zwnj;) فریشته&zwnj;کانیش ده&zwnj;ستیان لێده&zwnj;که&zwnj;نه&zwnj;وه و لێیان ده&zwnj;ده&zwnj;ن و (پێیان ده&zwnj;ڵێن): ئاده&zwnj;ی گیانتان بده&zwnj;ن به&zwnj;ده&zwnj;سته&zwnj;وه&zwnj;، ده&zwnj;ی ڕۆحتان ده&zwnj;رکه&zwnj;ن، ئه&zwnj;مڕۆ له تۆڵه&zwnj;ی (خوا نه&zwnj;ناسیتاندا) سزاو ئه&zwnj;شکه&zwnj;نجه&zwnj;ی ڕیسوایی و شه&zwnj;رمه&zwnj;زاری ده&zwnj;درێن، چونکه شتی ناحه&zwnj;قتان به&zwnj;ده&zwnj;م خواوه هه&zwnj;ڵده&zwnj;به&zwnj;ست و خۆتان به&zwnj;گه&zwnj;وره ده&zwnj;زانی له به&zwnj;رامبه&zwnj;ر ئایه&zwnj;ت و فه&zwnj;رمانه&zwnj;کانی خوادا و لووت به&zwnj;رزیتان نه&zwnj;یده&zwnj;هێشت باوه&zwnj;ڕی پێ بهێنن.';
$t[94] = '(له ڕۆژی قیامه&zwnj;تدا خوای گه&zwnj;وره ڕوو به خوانه&zwnj;ناسان ده&zwnj;فه&zwnj;رموێت) سوێند به&zwnj;خوا بێگومان ئێوه به ته&zwnj;نها و تاک تاک هاتوونه&zwnj;ته لامان هه&zwnj;روه&zwnj;کو یه&zwnj;که&zwnj;م جار دروستمان کردن و هه&zwnj;رچی پێمان به&zwnj;خشی بوون و به&zwnj;کارتان ده&zwnj;هێنا به&zwnj;جێتان هێشتووه له دوایی خۆتانه&zwnj;وه و ناشبینین له&zwnj;گه&zwnj;ڵتان ئه&zwnj;و که&zwnj;س و شتانه&zwnj;ی که ئێوه ده&zwnj;تانوت تکاکارن بۆمان و فریامان ده&zwnj;که&zwnj;ون، سوێند به&zwnj;خوا به&zwnj;ڕاستی هه&zwnj;رچی په&zwnj;یوه&zwnj;ندیه&zwnj;ک هه&zwnj;بوو له نێوانتاندا بچڕاوه&zwnj;و هه&zwnj;رچی ئومێدتان پێی هه&zwnj;بوو لافتان پێوه لێده&zwnj;دا لێتان ون بوو.';
$t[95] = 'به&zwnj;ڕاستی خوا له&zwnj;تکه&zwnj;ری ده&zwnj;نک و دانه&zwnj;وێڵه&zwnj;یه بۆ ئه&zwnj;وه&zwnj;ی یپشکوێنێت و بڕوێت، زیندووش له مردوو ده&zwnj;رده&zwnj;هێنێت، ده&zwnj;رهێنه&zwnj;ری مردووشه له زیندوو، ئا ئه&zwnj;وه&zwnj;یه خوای په&zwnj;روه&zwnj;ردگارتان، جا ئیتر بۆ کوێ لاده&zwnj;درێن و بۆ کوێ وێڵ ده&zwnj;کرێن.';
$t[96] = '(هه&zwnj;رئه&zwnj;و زاته&zwnj;یه&zwnj;) به&zwnj;یانیان فه&zwnj;راهه&zwnj;م ده&zwnj;هێنێت و ئاسۆ ڕوناک ده&zwnj;کاته&zwnj;وه و شه&zwnj;وی کردووه به&zwnj;هۆی ئارامی و خامۆشی، ڕۆژو مانگیشی کردووه به&zwnj;هۆی ڕاگرتنی حساب، ئه&zwnj;و شتانه هه&zwnj;مووی به&zwnj;نه&zwnj;خشه&zwnj;ی خوای باڵاده&zwnj;ست و زانا کێشراوه&zwnj;.';
$t[97] = 'هه&zwnj;ر ئه&zwnj;و زاته&zwnj;، ئه&zwnj;ستێره&zwnj;ی بۆ فه&zwnj;راهه&zwnj;م هێناون تا ببێته هۆی ڕێنموویتان له&zwnj;ناو تاریکیه&zwnj;کانی سه&zwnj;ر ڕووکاری وشکانی و ده&zwnj;ریاکاندا، به&zwnj;ڕاستی ئێمه نیشانه و به&zwnj;ڵگه&zwnj;ی زۆرمان (له&zwnj;سه&zwnj;ر ده&zwnj;سه&zwnj;ڵاتی خۆمان و ڕێکوپێکی کارمان) ڕوون کردۆته&zwnj;وه بۆ که&zwnj;سانێک که پێی بزانن و لێی تێبگه&zwnj;ن.';
$t[98] = 'هه&zwnj;ر ئه&zwnj;و زاته ئێوه&zwnj;ی به&zwnj;دیهێناوه له تاکه که&zwnj;سێک، سه&zwnj;رده&zwnj;مێک (به&zwnj;شێکتانی له پشتی باوکتاندا) نیشته&zwnj;جێ کردبوو (که گوێزرانه&zwnj;وه بۆ ناو سکی دایکتان) ئه&zwnj;و شوێنه ئێوه&zwnj;ی گرته خۆ (تا تیایدا نه&zwnj;ش و نما بکه&zwnj;ن، یاخود بۆ ماوه&zwnj;یه&zwnj;ک ئێوه&zwnj;ی له&zwnj;سه&zwnj;ر ڕووکاری زه&zwnj;وی دانا، دوایی له توێیدا شاردنیه&zwnj;وه تا ڕۆژی قیامه&zwnj;ت) به&zwnj;ڕاستی ئێمه نیشانه و به&zwnj;ڵگه&zwnj;ی زۆرمان (له&zwnj;سه&zwnj;ر دروستکردنی ئێوه و دروستکاری خۆمان هێناوه&zwnj;ته&zwnj;وه&zwnj;) بۆ که&zwnj;سانێک که تێفکرن و وردبین بن.';
$t[99] = 'ئه&zwnj;و خوایه زاتێکه که له ئاسمانه&zwnj;وه باران ده&zwnj;بارێنێت (ده&zwnj;فه&zwnj;رموێت): جا به&zwnj;هۆیه&zwnj;وه چرۆو چه&zwnj;که&zwnj;ره&zwnj;ی هه&zwnj;موو شتێکی پێ ده&zwnj;رده&zwnj;هێنین و شینی ده&zwnj;که&zwnj;ین، ئینجا لاسکی سه&zwnj;وزی لێ په&zwnj;یدا ده&zwnj;که&zwnj;ین، زنجیره دانه&zwnj;ی سه&zwnj;فته&zwnj;کراوی لێ دروست ده&zwnj;که&zwnj;ین (وه&zwnj;ک گوڵه&zwnj;گه&zwnj;نم و چوڵتوک و گه&zwnj;نمه&zwnj;شامی.. هتد) له دارخورماش له هه&zwnj;ندێ لق و چڵه پۆپی هێشووی خورما شۆڕ ده&zwnj;بێته&zwnj;وه&zwnj;، هه&zwnj;روه&zwnj;ها باخی هه&zwnj;مه&zwnj;جۆری ڕه&zwnj;زو ترێ، زه&zwnj;یتون و هه&zwnj;ناریش، هه&zwnj;یانه له ڕواڵه&zwnj;تدا له&zwnj;یه&zwnj;ک ده&zwnj;چێت، هه&zwnj;شیانه له&zwnj;یه&zwnj;ک ناچێت، جا ته&zwnj;ماشای به&zwnj;ره&zwnj;که&zwnj;ی بکه&zwnj;ن کاتێک به&zwnj;ر ده&zwnj;گرێت، پێ ده&zwnj;گات، (سه&zwnj;ره&zwnj;تا هه&zwnj;ندێکی گوڵه و دوایی خۆی هه&zwnj;ڵده&zwnj;پێچێت، ڕه&zwnj;نگ و قه&zwnj;باره&zwnj;یه&zwnj;ک ده&zwnj;گرێته خۆی، دوای چه&zwnj;ند مانگێک قه&zwnj;باره&zwnj;شی گه&zwnj;وره&zwnj;تر ده&zwnj;بێت، ڕه&zwnj;نگیشی ده&zwnj;گۆڕێت، ئه&zwnj;وسا پێ ده&zwnj;گات و تام و بۆنی سروشتی خۆی بۆ دروست ده&zwnj;بێت) به&zwnj;ڕاستی ئا له&zwnj;و شتانه&zwnj;دا (که ئاماژه&zwnj;ی تێداکرا بۆتان) چه&zwnj;نده&zwnj;ها به&zwnj;ڵگه&zwnj;و نیشانه&zwnj;ی ته&zwnj;واون بۆ که&zwnj;سانێک که ئیمان و باوه&zwnj;ڕیان ده&zwnj;هێنن (به&zwnj;و به&zwnj;دی هێنه&zwnj;ره&zwnj;ی که ئاسه&zwnj;واری ده&zwnj;ستی قودره&zwnj;ت و به ده&zwnj;سه&zwnj;ڵاتی له هه&zwnj;موو شتێکدا به&zwnj;دی ده&zwnj;کرێت).';
$t[100] = '(له&zwnj;گه&zwnj;ڵ ئه&zwnj;و هه&zwnj;موو نیشانه&zwnj;ی ده&zwnj;سه&zwnj;ڵاتداریه&zwnj;دا نه&zwnj;فامان) په&zwnj;ری و جنۆکه&zwnj;یان کردۆته هاوبه&zwnj;ش و هاوتای خوا، له حاڵێکدا هه&zwnj;ر خوا دروستکاری په&zwnj;ری و جنۆکه&zwnj;کانه و به&zwnj;ناحه&zwnj;ق کوڕو کچ ده&zwnj;ده&zwnj;نه پاڵی، بێ هیچ جۆره زانیاریه&zwnj;ک (گاور ده&zwnj;ڵێ: عیسا کوڕی خوایه&zwnj;، جوو ده&zwnj;ڵێ: عوزه&zwnj;یر، هاوه&zwnj;ڵگه&zwnj;ران ده&zwnj;ڵێن: فریشته&zwnj;کان کچی خوان!!) پاکی و بێگه&zwnj;ردی و بڵندی بۆ ئه&zwnj;و زاته&zwnj;یه که دووره له&zwnj;و سیفه&zwnj;ته ناقۆڵاو نادروستانه&zwnj;وه که ئه&zwnj;وانه خوای پێی پێناسه ده&zwnj;که&zwnj;ن.';
$t[101] = 'بێگومان ئه&zwnj;و زاته به&zwnj;دیهێنه&zwnj;ری ئاسمانه&zwnj;کان و زه&zwnj;ویه له جوانترین و ڕێک و پێکترین شێوه&zwnj;دا، چۆن ڕۆڵه&zwnj;ی ده&zwnj;بێت؟! خۆ ئه&zwnj;وزاته هاوسه&zwnj;ری نه&zwnj;گرتووه&zwnj;؟! به&zwnj;ڵکو هه&zwnj;موو شتێکی دروست کردوه&zwnj;، به&zwnj;ڕاستی ئه&zwnj;و زاته به&zwnj;هه&zwnj;موو شتێک زانایه&zwnj;.';
$t[102] = 'ئا ئه&zwnj;و خوایه په&zwnj;روه&zwnj;ردگارتانه&zwnj;، جگه له&zwnj;ویش هیچ خوایه&zwnj;کی تر نیه&zwnj;، به&zwnj;دیهێنه&zwnj;ری هه&zwnj;موو شتێکه&zwnj;، که&zwnj;واته هه&zwnj;ر ئه&zwnj;و زاته بپه&zwnj;رستن و به&zwnj;ندایه&zwnj;تی ته&zwnj;نها ئه&zwnj;و بکه&zwnj;ن، ئه&zwnj;و خوایه ئاگادارو چاودێری هه&zwnj;موو شتێکه&zwnj;.';
$t[103] = 'ده&zwnj;زگای بینایی و (بۆچوونی ئاده&zwnj;میزاد) ناتوانێت ئه&zwnj;و زاته ببینێت، زانیاری ئه&zwnj;و خوایه هه&zwnj;موو شتێکی گرتۆته&zwnj;وه&zwnj;، ئاگاداریشه به&zwnj;هه&zwnj;موو ده&zwnj;زگاکانی بینایی، هه&zwnj;ر ئه&zwnj;وه وردبین و ئاگایه&zwnj;.';
$t[104] = 'بێگومان چه&zwnj;نده&zwnj;ها به&zwnj;ڵگه&zwnj;ی ڕووناکبیرتان له لایه&zwnj;ن په&zwnj;روه&zwnj;ردگارتانه&zwnj;وه بۆ هاتووه&zwnj;، جا ئه&zwnj;وه&zwnj;ی خۆی ڕوناکبیر بکات و له ڕاستیه&zwnj;کان تێفکرێت و تێبگات و گۆڕانکاری له بیرو هۆش و ژیانیدا ئه&zwnj;نجام بدات، ئه&zwnj;وه هه&zwnj;رخۆی قازانج ده&zwnj;کات، ئه&zwnj;و که&zwnj;سه&zwnj;ی چاو ده&zwnj;نوقێنێت له ئاستی ڕوناکبیریه خواییه&zwnj;کاندا و له&zwnj;ڕاستی لاده&zwnj;دات، ئه&zwnj;ویش هه&zwnj;ر له&zwnj;سه&zwnj;ر خۆی ده&zwnj;که&zwnj;وێت و خۆی زه&zwnj;ره&zwnj;ر ده&zwnj;کات و من (واته پێغه&zwnj;مبه&zwnj;ر (صلى الله عليه وسلم) به&zwnj;هیچ شێوه&zwnj;یه&zwnj;ک چاودێر نیم به&zwnj;سه&zwnj;رتانه&zwnj;وه&zwnj;.';
$t[105] = 'هه&zwnj;ر به&zwnj;و شێوه&zwnj;یه (ئه&zwnj;ی محمد (صلى الله عليه وسلم) ئایه&zwnj;تی جۆراو جۆرت بۆ ڕه&zwnj;وانه ده&zwnj;که&zwnj;ین له هه&zwnj;موو باره&zwnj;یه&zwnj;که&zwnj;وه&zwnj;، ده با هه&zwnj;ر بڵێن خوێندووته و به&zwnj;ده&zwnj;رس پێت وتراوه (ئێمه ده&zwnj;مانه&zwnj;وێت) به&zwnj;ڕاستی ڕوونی بکه&zwnj;ینه&zwnj;وه بۆ که&zwnj;سانێک که بزانن و تێبگه&zwnj;ن.';
$t[106] = '(تۆ ئه&zwnj;ی محمد (صلى الله عليه وسلم) شوێنی ئه&zwnj;و به&zwnj;رنامه&zwnj;یه بکه&zwnj;وه که له&zwnj;لایه&zwnj;ن په&zwnj;روه&zwnj;ردگارته&zwnj;وه به نیگا و (وحی) پێت ڕاگه&zwnj;یه&zwnj;نراوه&zwnj;، جگه له&zwnj;و هیچ خوایه&zwnj;کی تر نیه&zwnj;، گوێ مه&zwnj;ده&zwnj;رێ و پشت هه&zwnj;ڵکه له موشریک و هاوه&zwnj;ڵگه&zwnj;ران.';
$t[107] = 'خۆ ئه&zwnj;گه&zwnj;ر په&zwnj;روه&zwnj;ردگارت (بیویستایه و شایسته بوونایه هیدایه&zwnj;تی ده&zwnj;دان) هاوه&zwnj;ڵگه&zwnj;ر نه&zwnj;ده&zwnj;بوون، خۆ ئێمه تۆمان نه&zwnj;کردووه&zwnj;ته چاودێری به&zwnj;سه&zwnj;ر ئه&zwnj;وانه&zwnj;وه و تۆ ده&zwnj;سه&zwnj;ڵاتت به&zwnj;سه&zwnj;ریاندا نیه (تا به&zwnj;زۆر هیدایه&zwnj;تیان به&zwnj;سه&zwnj;ردا بسه&zwnj;پێنیت).';
$t[108] = '(ئه&zwnj;ی ئیمانداران) نه&zwnj;که&zwnj;ن جوێن بده&zwnj;ن به&zwnj;و بت و شتانه&zwnj;ی که (نه&zwnj;فامان) هاواری لێده&zwnj;که&zwnj;ن و ده&zwnj;یپه&zwnj;رستن بێجگه له&zwnj;خوا، نه&zwnj;وه&zwnj;کو ئه&zwnj;وانیش به&zwnj;ناحه&zwnj;ق و به&zwnj;نه&zwnj;فامی جوێن بده&zwnj;ن به&zwnj;خوا، (چونکه ئه&zwnj;وانه&zwnj;) نایناسن و قه&zwnj;دری نازانن، ئا به&zwnj;و شێوه&zwnj;یه ئێمه بۆ هه&zwnj;ر گه&zwnj;ل و ئوممه&zwnj;تێک کرده&zwnj;وه&zwnj;که&zwnj;یانمان ڕازاندۆته&zwnj;وه&zwnj;، له&zwnj;وه&zwnj;ودوا گه&zwnj;ڕانه&zwnj;وه&zwnj;یان هه&zwnj;ر بۆ لای په&zwnj;روه&zwnj;ردگاریانه&zwnj;، ئه&zwnj;وسا ئاگاداریان ده&zwnj;کاته&zwnj;وه به&zwnj;و کاروکرده&zwnj;وانه&zwnj;ی که ده&zwnj;یانکرد.';
$t[109] = 'موشریک و هاوه&zwnj;ڵگه&zwnj;ران سوێندی گه&zwnj;وره&zwnj;یان به&zwnj;خوا ده&zwnj;خوارد: که سوێند بێت ئه&zwnj;گه&zwnj;ر موعجیزه&zwnj;یه&zwnj;کیان بۆ بێت ئه&zwnj;وه بێگومان باوه&zwnj;ڕی پێ ده&zwnj;هێنن، پێیان بڵێ: به&zwnj;ڕاستی هه&zwnj;موو به&zwnj;ڵگه&zwnj;و موعجیزه&zwnj;کان ته&zwnj;نها لای خوایه و به&zwnj;ده&zwnj;ست ئه&zwnj;وه&zwnj;، جا ئێوه چووزانن، چونکه له&zwnj;وانه&zwnj;یه ئه&zwnj;و داخوازی و موعجیزه و به&zwnj;ڵگانه&zwnj;شیان بۆ پێش بهێنین هه&zwnj;ر باوه&zwnj;ڕ ناهێنن.';
$t[110] = 'ئه&zwnj;و کاته دڵ و چاوه&zwnj;کانیان هه&zwnj;ڵده&zwnj;گێڕینه&zwnj;وه (له&zwnj;نێوان جۆره&zwnj;ها لێکدانه&zwnj;وه و ئه&zwnj;گه&zwnj;ردا) هه&zwnj;روه&zwnj;کو چۆن یه&zwnj;که&zwnj;مجار له سه&zwnj;ره&zwnj;تاوه باوه&zwnj;ڕیان نه&zwnj;هێنابوو (به&zwnj;ده&zwnj;سه&zwnj;ڵاتی بێ سنووری خواو ئه&zwnj;م هه&zwnj;موو موعجیزه بێ شوماره که ده&zwnj;وری داون) ئێمه جارێ وازیان لێ ده&zwnj;هێنین تا له سته&zwnj;م و سه&zwnj;رکه&zwnj;شی خۆیاندا سه&zwnj;رگه&zwnj;ردان بن و ڕۆبچن (بۆ کوێ ده&zwnj;چن له ده&zwnj;ستمان ده&zwnj;رناچن).';
$t[111] = 'خۆ ئه&zwnj;گه&zwnj;ر (داخوازی هاوه&zwnj;ڵگه&zwnj;رانمان جێبه&zwnj;جێ بکردایه&zwnj;) و به&zwnj;ڕاستی ئێمه فریشته&zwnj;مان دابه&zwnj;زاندانایه بۆ لایان و مردووه&zwnj;کان قسه&zwnj;یان له&zwnj;گه&zwnj;ڵدا بکردنایه&zwnj;، هه&zwnj;موو شتێکیشمان له&zwnj;به&zwnj;ر ده&zwnj;میاندا کۆ بکردایه&zwnj;ته&zwnj;وه&zwnj;، نه&zwnj;ده&zwnj;بوون به&zwnj;بڕوادار و بڕوایان نه&zwnj;ده&zwnj;هێنا، مه&zwnj;گه&zwnj;ر خوا بیه&zwnj;وێت، به&zwnj;ڵام زۆربه&zwnj;یان نافامن و نازانن (بیر ناکه&zwnj;نه&zwnj;وه و تێنافکرن، هه&zwnj;تا ئه&zwnj;مڕۆش گا په&zwnj;رست، شه&zwnj;یتان په&zwnj;رست و، بت په&zwnj;رستان هه&zwnj;ر ماون، هه&zwnj;یانه خاوه&zwnj;نی بڕوانامه&zwnj;ی به&zwnj;رزیشن، به&zwnj;ڵام له ڕووی ئاینداریه&zwnj;وه له&zwnj;وپه&zwnj;ڕی دواکه&zwnj;وتن و کۆنه&zwnj;په&zwnj;رستیدان).';
$t[112] = 'هه&zwnj;ر به&zwnj;و شێوه&zwnj;یه (کێشمه کێشی ئیمانداران و خوانه&zwnj;ناسان به&zwnj;رده&zwnj;وامه&zwnj;) جا بۆ هه&zwnj;موو پێغه&zwnj;مبه&zwnj;رێک (بۆ ئیماندارانیش) دوژمنێکمان له شه&zwnj;یتانه&zwnj;کانی ئاده&zwnj;میزاد و په&zwnj;ری سازاندوه&zwnj;، که به&zwnj;رده&zwnj;وام قسه&zwnj;ی زل و ڕازاوه ده&zwnj;ده&zwnj;ن به&zwnj;گوێی یه&zwnj;کداو (خۆیان و خه&zwnj;ڵکی) پێ سه&zwnj;رگه&zwnj;ردان و گومڕا ده&zwnj;که&zwnj;ن، خۆ ئه&zwnj;گه&zwnj;ر په&zwnj;روه&zwnj;ردگارت بیویستایه (نه&zwnj;یانده&zwnj;توانی) شتی وابکه&zwnj;ن، ده وازیان لێ بهێنه له&zwnj;گه&zwnj;ڵ ئه&zwnj;و درۆو ده&zwnj;له&zwnj;سه&zwnj;یه&zwnj;ی که هه&zwnj;ڵی ده&zwnj;به&zwnj;ستن (بۆ کوێ ده&zwnj;چن، له ده&zwnj;ست سزای خوایی ڕزگار نابن).';
$t[113] = '(هه&zwnj;روه&zwnj;ها به&zwnj;و قسه جوان و بریقه&zwnj;دارانه ئه&zwnj;یاندا به&zwnj;گوێی یه&zwnj;کدا) بۆئه&zwnj;وه&zwnj;ی ئه&zwnj;وانه&zwnj;ی باوه&zwnj;ڕیان به&zwnj;قیامه&zwnj;ت نیه دڵی بده&zwnj;نێ و پێی ڕازی بن و بۆ ئه&zwnj;وه&zwnj;ی ئه&zwnj;وتاوانانه بکه&zwnj;ن که ئه&zwnj;وان به&zwnj;رده&zwnj;وام ده&zwnj;یکه&zwnj;ن.';
$t[114] = 'ئایا ڕه&zwnj;وایه (بۆ منی پێغه&zwnj;مبه&zwnj;ر، بۆ منی ئیماندار) جگه له خوا که&zwnj;سێکی تر بکه&zwnj;مه حه&zwnj;که&zwnj;م و دادوه&zwnj;ر، له&zwnj;کاتێکدا ئه&zwnj;و زاته کتێبی پیرۆزی خۆی که (قورئانه&zwnj;) بۆ دابه&zwnj;زاندوون که هه&zwnj;موو دروست و نادروستێکی تیادا جیاکردۆته&zwnj;وه&zwnj;، ئه&zwnj;وانه&zwnj;ش که کتێبی (ئاسمانیمان) پێداون (له گاورو جوو) چاک ده&zwnj;زانن به&zwnj;ڕاستی ئه&zwnj;و قورئانه له&zwnj;لایه&zwnj;ن په&zwnj;روه&zwnj;ردگارته&zwnj;وه به&zwnj;ڕاست و ڕه&zwnj;وا دابه&zwnj;زێنراوه&zwnj;، که&zwnj;وابوو ئیتر تۆ هه&zwnj;رگیز مه&zwnj;چۆره ڕیزی گوماندارو دوو دڵانه&zwnj;وه&zwnj;.';
$t[115] = 'فه&zwnj;رمان و فه&zwnj;رمووده&zwnj;کانی په&zwnj;روه&zwnj;ردگارت (ئه&zwnj;ی محمد (صلى الله عليه وسلم) له&zwnj;وپه&zwnj;ڕی ڕاستی و دادپه&zwnj;روه&zwnj;ریدا به&zwnj;ئه&zwnj;نجام گه&zwnj;یشتووه&zwnj;، هه&zwnj;رگیز شتێک نیه بیانگۆرێت، هه&zwnj;ر ئه&zwnj;ویش بیسه&zwnj;ره به&zwnj;گوفتاری هه&zwnj;مووان، زانایه به&zwnj;هه&zwnj;موو نهێنی و ئاشکرایه&zwnj;ک.';
$t[116] = 'خۆ ئه&zwnj;گه&zwnj;ر فه&zwnj;رمانبه&zwnj;ردارو ملکه&zwnj;چی زۆربه&zwnj;ی خه&zwnj;ڵکی سه&zwnj;رزه&zwnj;وی بیت ئه&zwnj;وه له ڕێبازی خوا وێڵت ده&zwnj;که&zwnj;ن، چونکه شوێنی هیچ شتێک ناکه&zwnj;ون جگه له گومان و ته&zwnj;نها گۆتره&zwnj;کاری ده&zwnj;که&zwnj;ن.';
$t[117] = 'به&zwnj;ڕاستی په&zwnj;روه&zwnj;ردگاری تۆ چاک زانایه به&zwnj;وه&zwnj;ی که&zwnj;: کێ له ئاین و ڕێبازی ئه&zwnj;و وێڵ و گومڕا ده&zwnj;بێت و هه&zwnj;ر خوایش زانایه به&zwnj;وانه&zwnj;ی که هیدایه&zwnj;ت و ڕێنموویی وه&zwnj;رگرن.';
$t[118] = 'جا بخۆن له&zwnj;و زینده&zwnj;وه&zwnj;ره سه&zwnj;ر بڕاوانه&zwnj;ی که ناوی خوا له&zwnj;کاتی سه&zwnj;ربڕینیاندا براوه&zwnj;، ئه&zwnj;گه&zwnj;ر ئێوه باوه&zwnj;ڕتان به&zwnj;رێنموونی و ئایه&zwnj;ته&zwnj;کانی خوا هه&zwnj;یه (بسم الله&zwnj;، الله اکبر، پێویسته له&zwnj;کاتی سه&zwnj;ربڕینی ماڵاتدا بوترێت).';
$t[119] = 'به&zwnj;ڵگه&zwnj;تان چیه که (له گۆشتی ئه&zwnj;و ئاژه&zwnj;ڵانه&zwnj;) ناخۆن که ناوی خوا براوه له سه&zwnj;ریان (له&zwnj;کاتی سه&zwnj;ر بڕینیاندا) له&zwnj;کاتێکدا به&zwnj;ڕاستی ئه&zwnj;وه&zwnj;ی حه&zwnj;رام کراوه له سه&zwnj;رتان خوا بۆی جیا کردونه&zwnj;ته&zwnj;وه مه&zwnj;گه&zwnj;ر ناچار بکرێن بۆی، به&zwnj;ڕاستی زۆربه&zwnj;یان خه&zwnj;ڵکی سه&zwnj;رگه&zwnj;ردان و گومڕا ده&zwnj;که&zwnj;ن به&zwnj;هۆی ئاره&zwnj;زووه&zwnj;کانیانه&zwnj;وه نه&zwnj;ک به&zwnj;هۆی زانسته&zwnj;وه (هه&zwnj;ر له&zwnj;خۆیانه&zwnj;وه شت حه&zwnj;رام ده&zwnj;که&zwnj;ن) بێگومان په&zwnj;روه&zwnj;ردگارت خۆی چاک ئه&zwnj;و که&zwnj;سانه ده&zwnj;ناسێت که ده&zwnj;ست درێژکارن و خۆشیان و خه&zwnj;ڵکیش له سنوور ده&zwnj;ترازێنن.';
$t[120] = '(ئه&zwnj;ی گرۆی ئیمانداران) واز بهێنن له هه&zwnj;موو گوناهو هه&zwnj;ڵه&zwnj;یه&zwnj;کی ئاشکراو نادیار (له&zwnj;و گوناهانه به دووربن که به&zwnj;دڵ و ده&zwnj;روون ده&zwnj;کرێت، هه&zwnj;روه&zwnj;ها له&zwnj;وانه&zwnj;ش که به&zwnj;ده&zwnj;م و ده&zwnj;ست و ئه&zwnj;ندامه&zwnj;کان ده&zwnj;کرێت) به&zwnj;ڕاستی ئه&zwnj;وانه&zwnj;ی گوناهو تاوان ئه&zwnj;نجام ده&zwnj;ده&zwnj;ن له&zwnj;ئاینده&zwnj;دا سزاو تۆڵه&zwnj;ی گوناهو تاوانه&zwnj;کانیان یه&zwnj;خه&zwnj;یان پێ ده&zwnj;گرێت.';
$t[121] = 'له&zwnj;و ئاژه&zwnj;ڵه سه&zwnj;ر بڕاوانه&zwnj;ش مه&zwnj;خۆن که ناوی خوا له&zwnj;کاتی سه&zwnj;ر بڕینیاندا نه&zwnj;براوه و به&zwnj;ڕاستی ئه&zwnj;وه لادان و گوناهه&zwnj;، دڵنیابن که شه&zwnj;یتانه&zwnj;کان شت ده&zwnj;خه&zwnj;نه دڵی لایه&zwnj;نگره&zwnj;کانیانه&zwnj;وه تا موجاده&zwnj;له&zwnj;تان له&zwnj;گه&zwnj;ڵ بکه&zwnj;ن و (به&zwnj;رگری له به&zwnj;تاڵیان بکه&zwnj;ن) جا ئه&zwnj;گه&zwnj;ر ئێوه فه&zwnj;رمانبه&zwnj;رداری ئه&zwnj;و هاوه&zwnj;ڵگه&zwnj;رو شه&zwnj;یتانانه بن، ئه&zwnj;وه بێگومان مانای وایه ئێوه&zwnj;ش موشریک و هاوه&zwnj;ڵگه&zwnj;رن.';
$t[122] = 'ئایا ئه&zwnj;و که&zwnj;سه&zwnj;ی که مردوو بوو (به&zwnj;هۆی بێ باوه&zwnj;ڕیه&zwnj;وه&zwnj;)، ئینجا (به&zwnj;هۆی ئیمانه&zwnj;وه&zwnj;) زیندوومان کرده&zwnj;وه و نوورو ڕوناکیه&zwnj;کمان بۆ ساز کرد له ژیانیدا (که قورئانه&zwnj;) له&zwnj;ناو خه&zwnj;ڵکیدا پێی ده&zwnj;ڕوات و هه&zwnj;ڵس و که&zwnj;وتی پێده&zwnj;کات (هانده&zwnj;رێتی بۆ هه&zwnj;موو چاکه&zwnj;یه&zwnj;ک و ده&zwnj;یگێڕێته&zwnj;وه له هه&zwnj;موو خراپه&zwnj;یه&zwnj;ک، ئایا ئه&zwnj;و که&zwnj;سه&zwnj;) وه&zwnj;کو ئه&zwnj;وه وایه که له&zwnj;ناو تاریکیه&zwnj;کاندا گیری خواردوه و لێی ده&zwnj;رناچێت، هه&zwnj;روه&zwnj;ها ئا به&zwnj;و شێوه&zwnj;یه که ده&zwnj;بینرێت ئه&zwnj;و کارو کرده&zwnj;وه&zwnj;ی که ده&zwnj;یکه&zwnj;ن بۆیان ڕازێنراوه&zwnj;ته&zwnj;وه&zwnj;.';
$t[123] = 'ئا به&zwnj;و شێوه&zwnj;یه&zwnj;، له&zwnj;ناو هه&zwnj;موو شارو شارۆچکه&zwnj;یه&zwnj;کدا گه&zwnj;وره&zwnj;ترینی تاوانباریانمان باڵاده&zwnj;ست کردوه تا پیلان و ته&zwnj;ڵه&zwnj;که بگێڕن تیایدا، (بێ ئه&zwnj;وه&zwnj;ی بزانن) که&zwnj;: هیچ فێڵ و ته&zwnj;ڵه&zwnj;که&zwnj;یه&zwnj;کیش ناکه&zwnj;ن به&zwnj;زیانی خۆیان ته&zwnj;واو نه&zwnj;بێت، له&zwnj;کاتێکدا هه&zwnj;ست به&zwnj;وه ناکه&zwnj;ن، پیلان دژی خۆیان ده&zwnj;گێڕن و به&zwnj;سه&zwnj;ر خۆیاندا ده&zwnj;شکێته&zwnj;وه (به&zwnj;ڵام) هه&zwnj;ستی پێناکه&zwnj;ن (چونکه غافڵ و بێ ئاگان که به&zwnj;ره&zwnj;و لێواری مه&zwnj;رگ هه&zwnj;نگاو هه&zwnj;ڵده&zwnj;گرن و له سزای په&zwnj;روه&zwnj;ردگار ده&zwnj;رباز نابن).';
$t[124] = '(بێ باوه&zwnj;ڕان) کاتێک ئایه&zwnj;تێکی قورئانیان بۆ بێت ده&zwnj;ڵێن: ئێمه هه&zwnj;رگیز باوه&zwnj;ڕ ناهێنین هه&zwnj;تا وێنه&zwnj;ی ئه&zwnj;وه&zwnj;ی که بۆ پێغه&zwnj;مبه&zwnj;ران هاتووه به ئێمه نه&zwnj;به&zwnj;خشرێت، خوا زانایه به&zwnj;و شوێن و دڵ و ده&zwnj;روونه&zwnj;ی که شایسته&zwnj;یه په&zwnj;یامی خۆی تێدا دابنێت، له&zwnj;ئاینده&zwnj;یه&zwnj;کی نزیکیشدا ئه&zwnj;وانه&zwnj;ی که تاوانیان کردووه له&zwnj;لایه&zwnj;ن خواوه سووکی و ڕیسوای و بێ نرخی یه&zwnj;خه&zwnj;یان پێده&zwnj;گرێت، هاوڕێ له&zwnj;گه&zwnj;ڵ سزای پڕ ئێشدا له پاداشتی ئه&zwnj;و پیلان و ته&zwnj;ڵه&zwnj;که&zwnj;یه&zwnj;دا که ده&zwnj;یانگێڕا.';
$t[125] = 'جا ئه&zwnj;و که&zwnj;سه&zwnj;ی (خێری تێدا بێت) و خوا بیه&zwnj;وێت هیدایه&zwnj;تی بدات، ئه&zwnj;وه سینه&zwnj;ی ساف و پاک و ئاماده ده&zwnj;کات بۆ ئاینی ئیسلام، ئه&zwnj;و که&zwnj;سه&zwnj;ش خوا بیه&zwnj;وێت گومڕای بکات، ئه&zwnj;وه دڵی ده&zwnj;گوشێت و سنگی توند ده&zwnj;کات و هه&zwnj;ناسه&zwnj;ی سوار ده&zwnj;بێت، هه&zwnj;ر وه&zwnj;ک ئه&zwnj;وه&zwnj;ی به&zwnj;ره&zwnj;و ئاسمان به&zwnj;رز بێته&zwnj;وه (چۆن تووشی ته&zwnj;نگه نه&zwnj;فه&zwnj;سی و دڵه ته&zwnj;پێ و خوێن به&zwnj;ر بوون ده&zwnj;بێت)، ئا به&zwnj;و شێوه&zwnj;یه خوا گومڕایی و پیسی هاوه&zwnj;ڵگه&zwnj;ری له&zwnj;سه&zwnj;ر دڵی ئه&zwnj;وانه داده&zwnj;نێت که باوه&zwnj;ڕ ناهێنن.';
$t[126] = 'ئا ئه&zwnj;م قورئانه ڕێگه&zwnj;و ڕێبازو به&zwnj;رنامه&zwnj;ی په&zwnj;روه&zwnj;ردگاری تۆیه که ڕاست و دروسته&zwnj;، بێگومان ئێمه هه&zwnj;موو به&zwnj;ڵگه و فه&zwnj;رمانه&zwnj;کانمان ڕوون کردۆته&zwnj;وه بۆ که&zwnj;سانێک که یاداوه&zwnj;ری وه&zwnj;رده&zwnj;گرن.';
$t[127] = 'ئه&zwnj;وانه به&zwnj;هه&zwnj;شتی پڕ له ئاشتی و هێمنیان بۆ ئاماده&zwnj;یه لای په&zwnj;روه&zwnj;ردگاریان، هه&zwnj;ر ئه&zwnj;و زاته&zwnj;ش پشتیوان و خۆشه&zwnj;ویستیانه به&zwnj;هۆی ئه&zwnj;و کارو کرده&zwnj;وه چاکانه&zwnj;ی که ئه&zwnj;نجامیان ده&zwnj;دا.';
$t[128] = 'ڕۆژێک دێت خوا هه&zwnj;مووانیان کۆ ده&zwnj;کاته&zwnj;وه و (به&zwnj;خه&zwnj;شم و قینه&zwnj;وه ده&zwnj;فه&zwnj;رموێت): ئه&zwnj;ی ده&zwnj;سته&zwnj;ی په&zwnj;ریه&zwnj;کان به&zwnj;ڕاستی ئێوه زۆر که&zwnj;ستان له ئینسانه&zwnj;کان گومڕاکرد، له&zwnj;ولاشه&zwnj;وه گوێ له مشته&zwnj;کانیان، (له خه&zwnj;ڵکی نه&zwnj;گبه&zwnj;ت و ڕه&zwnj;نجه&zwnj;ڕۆ) ده&zwnj;ڵێن: په&zwnj;روه&zwnj;ردگارا هه&zwnj;ندێکمان سوودمان له هه&zwnj;ندێکیان وه&zwnj;رگرت (سوودی ئینسانه&zwnj;کان ڕابواردن سامان کۆکردنه&zwnj;وه بوو، سوودی په&zwnj;ریه&zwnj;کانیش پێڕی خۆیانیان زۆرکرد به&zwnj;گومڕاکردنی خه&zwnj;ڵکێکی زۆر)، ئێستاش ئه&zwnj;وه&zwnj;ته گه&zwnj;یشتووینه&zwnj;ته ئه&zwnj;وکات و ساته&zwnj;ی تۆ بۆت دیاری کردووین، خوایش فه&zwnj;رمووی: (ماده&zwnj;م وایه&zwnj;) ئه&zwnj;م ئاگره جێگه&zwnj;تانه نه&zwnj;مرن تیادا، مه&zwnj;گه&zwnj;ر خوا ویستی له&zwnj;سه&zwnj;ر بێت (به سزایه&zwnj;کی تر سزایان بدات)، به&zwnj;ڕاستی په&zwnj;روه&zwnj;ردگاری تۆ (ئه&zwnj;ی محمد (صلى الله عليه وسلم) ئه&zwnj;ی ئیماندار) داناو زانایه&zwnj;.';
$t[129] = 'هه&zwnj;ر به&zwnj;و شێوه&zwnj;یه&zwnj;ش سته&zwnj;مکاران ده&zwnj;که&zwnj;ینه پشتیوان و خۆشه&zwnj;ویست و هاوکاری یه&zwnj;کتر، به&zwnj;هۆی ئه&zwnj;و کارو کرده&zwnj;وانه&zwnj;ی که ده&zwnj;یانکرد.';
$t[130] = '(له ڕۆژی قیامه&zwnj;تدا خوا ده&zwnj;فه&zwnj;رموێت): ئه&zwnj;ی ده&zwnj;سته&zwnj;ی په&zwnj;ری و ئینسان، ئایا پێغه&zwnj;مبه&zwnj;رانتان له خۆتان بۆ نه&zwnj;هات، که ئایه&zwnj;ته&zwnj;کانی من و ڕووداوه&zwnj;کان به&zwnj;سه&zwnj;رتاندا بخوێننه&zwnj;وه و ئاگادارتان بکه&zwnj;ن و بتانترسێنن له&zwnj;وه&zwnj;ی که ئه&zwnj;م ڕۆژه&zwnj;تان بۆ پێش دێت؟! (هه&zwnj;موو به&zwnj;یه&zwnj;کده&zwnj;نگ) ده&zwnj;ڵێن: ڕاسته ئێمه خۆمان شایه&zwnj;تی له&zwnj;سه&zwnj;ر خۆمان ده&zwnj;ده&zwnj;ین (به&zwnj;ڵام ئه&zwnj;وانه کاتی خۆی) ژیانی دنیا سه&zwnj;ری لێشێواندن و هه&zwnj;ڵی خه&zwnj;ڵه&zwnj;تاندن و شایه&zwnj;تیان له&zwnj;سه&zwnj;ر خۆیاندا که به&zwnj;ڕاستی کاتی خۆی بێ باوه&zwnj;ڕ بوون.';
$t[131] = 'ئه&zwnj;و ناردنی پێغه&zwnj;مبه&zwnj;رانه بۆ ئه&zwnj;وه&zwnj;یه چونکه په&zwnj;روه&zwnj;ردگاری تۆ (نه&zwnj;یکردووه به&zwnj;عاده&zwnj;ت) که له&zwnj;ناوبه&zwnj;ری شارو شوێنه&zwnj;کان بێت به ناحه&zwnj;ق، له&zwnj;کاتێکدا خه&zwnj;ڵکه&zwnj;که&zwnj;ی غافڵ و بێ ئاگا بن و په&zwnj;یامی خوایان پێ ڕانه&zwnj;گه&zwnj;یه&zwnj;نرابێت.';
$t[132] = 'بۆ هه&zwnj;ر یه&zwnj;کێک له بێ باوه&zwnj;ڕ و موسڵمان چه&zwnj;نده&zwnj;ها پله&zwnj;وپایه هه&zwnj;یه له پاداشتی کرده&zwnj;وه&zwnj;کانی، په&zwnj;روه&zwnj;ردگاریشت بێئاگا نیه له&zwnj;و کاروکرده&zwnj;وه&zwnj;ی که ده&zwnj;یکه&zwnj;ن.';
$t[133] = 'په&zwnj;روه&zwnj;ردگاری تۆ زۆر ده&zwnj;وڵه&zwnj;مه&zwnj;ندو بێ نیازه&zwnj;، هه&zwnj;روه&zwnj;ها خاوه&zwnj;نی سۆزو میهره&zwnj;بانیشه&zwnj;، ئه&zwnj;گه&zwnj;ر بیه&zwnj;وێت ئه&zwnj;وه ئێوه لاده&zwnj;بات و له&zwnj;ناوتان ده&zwnj;بات، هه&zwnj;رچی و هه&zwnj;ر که&zwnj;سیشی مه&zwnj;به&zwnj;ست بێت دوای ئێوه جێنشینی ده&zwnj;کات، هه&zwnj;ر وه&zwnj;کو ئێوه&zwnj;ی له نه&zwnj;وه&zwnj;ی که&zwnj;سانێکی تر هێنایه کایه&zwnj;وه&zwnj;.';
$t[134] = 'به&zwnj;ڕاستی ئه&zwnj;و به&zwnj;ڵێنانه&zwnj;ی که ده&zwnj;تاندرێتێ (ده&zwnj;رباره&zwnj;ی قیامه&zwnj;ت و لێپرسینه&zwnj;وه&zwnj;) به&zwnj;ڕێوه&zwnj;یه و بێگومان هه&zwnj;ر دێت و ئێوه ده&zwnj;سه&zwnj;وسانکه&zwnj;رنین.';
$t[135] = '(ئه&zwnj;ی محمد (صلى الله عليه وسلم) پێیان) بڵی: ئه&zwnj;ی قه&zwnj;وم و هۆزم چیتان بۆ ده&zwnj;کرێت ئه&zwnj;نجامی بده&zwnj;ن له&zwnj;سه&zwnj;ر شێوازی خۆتان، ئه&zwnj;وه به&zwnj;ڕاستی منیش به&zwnj;رده&zwnj;وامم له&zwnj;سه&zwnj;ر کاری خۆم (له هه&zwnj;وڵ و تێکۆشان بۆ هیدایه&zwnj;ت و ئیمان)، چونکه له&zwnj;ئاینده&zwnj;دا ده&zwnj;زانن که سه&zwnj;ئه&zwnj;نجامی ماڵی قیامه&zwnj;ت بۆ کێ ده&zwnj;بێت، دڵنیاش بن که سته&zwnj;مکاران سه&zwnj;رفراز نابن.';
$t[136] = '(ئه&zwnj;و هاوه&zwnj;ڵگه&zwnj;رو نه&zwnj;فامانه&zwnj;) هه&zwnj;ندێک له&zwnj;وه&zwnj;ی که خوا دروستی کردووه له به&zwnj;رو بوومی کشتوکاڵ و ماڵاتیان کردووه به به&zwnj;شی خواو وتویانه&zwnj;: ئه&zwnj;مه به&zwnj;شی خوایه به&zwnj;خه&zwnj;یاڵی خۆیان! (هه&zwnj;ندێکی تریشیان جیا کردۆته&zwnj;وه و وتویانه&zwnj;) ئه&zwnj;مه&zwnj;ش بۆ بته&zwnj;کانمانه&zwnj;! جا ئه&zwnj;وه&zwnj;ی بۆ بته&zwnj;کانیان بڕیاریان داوه ئه&zwnj;وه به&zwnj;خوا ناگات، (واته قبوڵ و په&zwnj;سه&zwnj;ند نیه&zwnj;، ئه&zwnj;وه&zwnj;ش که به&zwnj;خه&zwnj;یاڵی ئه&zwnj;وان) بۆ خوا بڕیاریانداوه&zwnj;، ئه&zwnj;وه ده&zwnj;گه&zwnj;یشته لای بته&zwnj;کان و بۆ ئه&zwnj;وان بوو، ئای که دادوه&zwnj;ریه&zwnj;کی خراپ و نادروست ده&zwnj;که&zwnj;ن.';
$t[137] = 'وه&zwnj;ک ئه&zwnj;و دابه&zwnj;شکردنه دوور له دادوه&zwnj;ریه&zwnj;، بت و په&zwnj;رستراوه&zwnj;کانیان کوشتنی منداڵه&zwnj;کانیان بۆ زۆربه&zwnj;ی هاوه&zwnj;ڵگه&zwnj;ران جوان و ڕازاوه کردبوو (نه&zwnj;فامانی عه&zwnj;ره&zwnj;ب کچانیان زینده به&zwnj;چاڵ ده&zwnj;کرد)، بۆ ئه&zwnj;وه&zwnj;ی له&zwnj;ناویان به&zwnj;رن و ئاینی یه&zwnj;کتاپه&zwnj;رستیان لێ بشێوێنن، خۆ ئه&zwnj;گه&zwnj;ر خوا بیویستایه ئه&zwnj;وه نه&zwnj;یانده&zwnj;توانی کاری وابکه&zwnj;ن (ئازادی لێ ده&zwnj;سه&zwnj;ندنه&zwnj;وه&zwnj;)، که&zwnj;وابوو وازیان لێ بهێنه خۆیان و ئه&zwnj;و درۆو ده&zwnj;له&zwnj;سه&zwnj;یه&zwnj;ی که ده&zwnj;یکه&zwnj;ن.';
$t[138] = 'هاوه&zwnj;ڵگه&zwnj;ران به بڕیاری نادروستی خۆیان ده&zwnj;یانوت: ئه&zwnj;مانه چه&zwnj;ند ماڵات و کێڵگه&zwnj;یه&zwnj;کن قه&zwnj;ده&zwnj;غه کراون، که&zwnj;س لێیان ناخوات مه&zwnj;گه&zwnj;ر ئێمه بمانه&zwnj;وێت و ڕازی بین، هه&zwnj;ندێک ماڵاتی تریش حه&zwnj;رام کرابوو پشتیان بۆ سواریی و بۆبار به&zwnj;کار بهێنرێت، ماڵاتێکی تریش هه&zwnj;ن ناوی خوای له&zwnj;سه&zwnj;ر نابه&zwnj;ن (ناوی بته&zwnj;کان ده&zwnj;به&zwnj;ن له&zwnj;کاتی سه&zwnj;ر بڕینیاندا) ئه&zwnj;م کاره&zwnj;یان درۆ هه&zwnj;ڵبه&zwnj;ستن بوو به ده&zwnj;م خواوه (گوایه به&zwnj;و کاره&zwnj;یان ڕازیه&zwnj;)، بێگومان خوا له ئاینده&zwnj;دا تۆڵه&zwnj;ی هه&zwnj;موو ئه&zwnj;و شتانه&zwnj;یان لێ ده&zwnj;ستێنێت که به&zwnj;ناحه&zwnj;ق هه&zwnj;ڵیانده&zwnj;به&zwnj;ست.';
$t[139] = '(نه&zwnj;فامیه&zwnj;کی تر که په&zwnj;یڕه&zwnj;وی ده&zwnj;که&zwnj;ن ئه&zwnj;وه&zwnj;یه&zwnj;) ده&zwnj;یانوت: هه&zwnj;رچی له سکی ئه&zwnj;م ماڵاتانه&zwnj;دا بێت (له به&zwnj;چکه و کۆرپه&zwnj;له&zwnj;) تایبه&zwnj;تیه بۆ نێرینه&zwnj;کانمان و حه&zwnj;رامکراوه له هاوسه&zwnj;رانمان، خۆ ئه&zwnj;گه&zwnj;ر به مرداره&zwnj;وه بووی (به&zwnj;چکه&zwnj;یه&zwnj;ک یان کۆرپرله&zwnj;یه&zwnj;ک بوو) ئه&zwnj;وا هه&zwnj;مووان تیایدا هاوبه&zwnj;شن و (پیاوان و ئافره&zwnj;تان لێی ده&zwnj;خۆن) له ئاینده&zwnj;دا خوا پاداشتی ئه&zwnj;م هه&zwnj;موو (سنووره خۆ کردانه و) باس و خواس و پێناسانه&zwnj;یان ده&zwnj;داته&zwnj;وه&zwnj;، چونکه به&zwnj;ڕاستی خوا حه&zwnj;کیم و داناو کاربه&zwnj;جێیه و زانا و ئاگاداره به&zwnj;هه&zwnj;موو شتێک (ده&zwnj;یه&zwnj;وێت هه&zwnj;موو هه&zwnj;ڵسوکه&zwnj;وتی خه&zwnj;ڵکی ڕێک و پێک بێت).';
$t[140] = 'به&zwnj;ڕاستی ئه&zwnj;وانه&zwnj;ی که منداڵه&zwnj;کانی خۆیان به&zwnj;ناحه&zwnj;ق و ناڕه&zwnj;واو نه&zwnj;زانی و نه&zwnj;فامی کوشت زه&zwnj;ره&zwnj;ریان کرد، هه&zwnj;روه&zwnj;ها زه&zwnj;ره&zwnj;ریان کرد ئه&zwnj;وانه&zwnj;ی ئه&zwnj;وه&zwnj;ی خوا پێی به&zwnj;خشیبوون خۆیانیان لێ بێ به&zwnj;ش کردو له&zwnj;ناویان برد، هه&zwnj;مووشی به&zwnj;هۆی درۆ هه&zwnj;ڵبه&zwnj;ستنه&zwnj;وه به&zwnj;ده&zwnj;م خواوه&zwnj;، به&zwnj;ڕاستی ئه&zwnj;وانه گومڕا بوون و هیدایه&zwnj;ت دراو نه&zwnj;بوون.';
$t[141] = 'خوا هه&zwnj;ر ئه&zwnj;و زاته&zwnj;یه که باخاتی به&zwnj;رزی لق و پۆپداری به&zwnj;رپا کردوه و باخاتی نزمی په&zwnj;رش و بڵاویشی به&zwnj;دی هێناوه و خورماو کشتوکاڵی جۆراوجۆریش که شێوه&zwnj;و قه&zwnj;باره&zwnj;و ڕه&zwnj;نگ و تام و بۆنیان له خواردندا جیایه&zwnj;، له زه&zwnj;یتوون و هه&zwnj;ناری له&zwnj;یه&zwnj;ک چوو، هه&zwnj;روه&zwnj;ها له&zwnj;یه&zwnj;ک نه&zwnj;چوو، بخۆن له به&zwnj;رو بوومه&zwnj;که&zwnj;ی کاتێک ده&zwnj;یگرێت و ڕۆژی چنین و دروێنه مافی (هه&zwnj;ژارانی) لێبده&zwnj;ن و زیاده&zwnj;ڕه&zwnj;وی مه&zwnj;که&zwnj;ن، چونکه خوا له سنوور ده&zwnj;رچوان و زیاده&zwnj;ڕه&zwnj;وانی خۆش ناوێت.';
$t[142] = '(هه&zwnj;ر خوا) هه&zwnj;ندێک له ماڵاتی بۆ ئێوه ڕام هێناوه که بۆ بار بردن به&zwnj;کاریان ده&zwnj;هێنن (له خوری و مووی هه&zwnj;ندێکی تریان) فه&zwnj;رش و ڕایه&zwnj;خ دروست ده&zwnj;که&zwnj;ن و بۆ سواریش هه&zwnj;ندێکی تریان به&zwnj;کار ده&zwnj;هێنن، ده بخۆن له&zwnj;و ڕزق و ڕۆزییه&zwnj;ی که خوا پێی به&zwnj;خشیوون، نه&zwnj;که&zwnj;ن شوێن هه&zwnj;نگاو و (نه&zwnj;خشه و پیلانه&zwnj;کانی) شه&zwnj;یتان بکه&zwnj;ون، چونکه به&zwnj;ڕاستی ئه&zwnj;و دوژمنێکی ئاشکرای ئێوه&zwnj;یه&zwnj;.';
$t[143] = '(خوای گه&zwnj;وره&zwnj;) هه&zwnj;شت جووت ماڵاتی (بۆ خزمه&zwnj;تی ئاده&zwnj;میزاد دروستکردووه&zwnj;) له&zwnj;مه&zwnj;ڕ جوتێک و له&zwnj;بزنیش جوتێک (ئه&zwnj;ی محمد (صلى الله عليه وسلم) به&zwnj;و نه&zwnj;فامانه بڵێ که هه&zwnj;ر له خۆیانه&zwnj;وه شت حه&zwnj;رام ده&zwnj;که&zwnj;ن) ئایا خواردنی گۆشتی نێره&zwnj;کانی حه&zwnj;رام کردووه یان مێیه&zwnj;کان یان ئه&zwnj;وه&zwnj;ی که&zwnj;له سکی مێیه&zwnj;کاندا هه&zwnj;یه&zwnj;؟! ئه&zwnj;گه&zwnj;ر ئێوه ڕاست ده&zwnj;که&zwnj;ن و به&zwnj;ڵگه&zwnj;یه&zwnj;کی زانستیتان هه&zwnj;یه&zwnj;، بۆمان ڕوون بکه&zwnj;نه&zwnj;وه&zwnj;؟!';
$t[144] = 'هه&zwnj;روه&zwnj;ها له وشتر جووتێک و له ڕه&zwnj;شه وڵاخیش جوتێک، (دووباره پێیان) بڵێ: ئایا خواردنی گۆشتی نێره&zwnj;کانی حه&zwnj;رام کردووه یان مێیه&zwnj;کان، یان ئه&zwnj;وه&zwnj;ی که&zwnj;له سکی مێیه&zwnj;کاندا هه&zwnj;یه&zwnj;؟! یاخود ئێوه (وا دیاره&zwnj;) شایه&zwnj;ت بوون و له&zwnj;وێ بوون کاتێک خوا بڕیاری به&zwnj;و جۆره&zwnj;ی بۆ دان؟!! جا ئیتر کێ له&zwnj;وه سته&zwnj;مکارتره که درۆی به ده&zwnj;م خواوه هه&zwnj;ڵبه&zwnj;ستووه تا به&zwnj;بێ زانستی، خه&zwnj;ڵکی سه&zwnj;رلێشێواو بکات، به&zwnj;ڕاستی خوا هیدایه&zwnj;تی ده&zwnj;سته&zwnj;ی سته&zwnj;مکاران نادات.';
$t[145] = '(ئه&zwnj;ی محمد (صلى الله عليه وسلم) پییان بڵێ: من نابینم و ده&zwnj;ستم ناکه&zwnj;وێت له&zwnj;وه&zwnj;ی که وه&zwnj;حی و نیگام بۆ کراوه هیچ خواردنێکی حه&zwnj;رامکراو له&zwnj;سه&zwnj;ر ئه&zwnj;و که&zwnj;سه&zwnj;ی که بیخوات، مه&zwnj;گه&zwnj;ر خواردنی مرداره&zwnj;وه بوو، یان خوێنێکی ڕژێنراو، یان گۆشتی به&zwnj;راز، به&zwnj;ڕاستی ئه&zwnj;و قه&zwnj;ده&zwnj;غه کراوه هه&zwnj;ر پیسه&zwnj;، یاخود (ماڵاتێک) که&zwnj;له سنووری شه&zwnj;رع ده&zwnj;رچووبێت له سه&zwnj;ر بڕینه&zwnj;که&zwnj;یدا به&zwnj;وه&zwnj;ی که ناوی غه&zwnj;یری خوای له&zwnj;سه&zwnj;ر برابێت، جا که&zwnj;سێک ئه&zwnj;گه&zwnj;ر ناچارکرا (به&zwnj;هۆی گرانی یان زۆر لێکردن) له&zwnj;کاتێکدا داواکارو ده&zwnj;رچوو نه&zwnj;بێت و به&zwnj;رده&zwnj;وام نه&zwnj;بێت و دووباره&zwnj;ی نه&zwnj;کاته&zwnj;وه&zwnj;، ئه&zwnj;وه به&zwnj;ڕاستی په&zwnj;روه&zwnj;ردگاری تۆ لێخۆشبوو میهره&zwnj;بانه (بۆ بڕاوادارانی ناچار).';
$t[146] = 'له&zwnj;سه&zwnj;ر ئه&zwnj;وانه&zwnj;ش که بوونه&zwnj;ته جوله&zwnj;که هه&zwnj;رچی زینده&zwnj;وه&zwnj;ری چڕنوکداره حه&zwnj;راممان کردو له (گۆشتی) ڕه&zwnj;شه وڵاخ و له بزن و مه&zwnj;ڕیش پیوه&zwnj;که&zwnj;مان لێ حه&zwnj;رام کردن مه&zwnj;گه&zwnj;ر ئه&zwnj;وه&zwnj;ی که به&zwnj;بڕبڕاگه&zwnj;ی پشتیانه&zwnj;وه بێت، یاخود ئه&zwnj;وه&zwnj;ی ده&zwnj;وری ڕیخۆڵه&zwnj;یانی دابێت، یان ئه&zwnj;وه&zwnj;ی تێکه&zwnj;ڵی ئێسقان بووبێت، ئه&zwnj;و (حه&zwnj;رام کردنانه&zwnj;) به&zwnj;هۆی لادان و سته&zwnj;میانه&zwnj;وه&zwnj;، تۆڵه&zwnj;یه&zwnj;ک بوو لێمانسه&zwnj;ندن، به&zwnj;ڕاستی هه&zwnj;ر ئێمه ڕاستگۆین (تا جوله&zwnj;که خۆیان بناسن و بزانن که ئه&zwnj;م قورئانه فه&zwnj;رمووده&zwnj;ی خوایه و ئاگاداره به&zwnj;هه&zwnj;موو شتێکیان).';
$t[147] = 'ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر (صلى الله عليه وسلم) جا ئه&zwnj;گه&zwnj;ر باوه&zwnj;ڕیان پێ نه&zwnj;کردیت ئه&zwnj;وه بڵێ: که په&zwnj;روه&zwnj;ردگارتان خاوه&zwnj;نی ڕه&zwnj;حمه&zwnj;ت و میهره&zwnj;بانی فراوان و بێ سنووره (ئه&zwnj;گه&zwnj;ر باوه&zwnj;ڕ بهێنن و له&zwnj;پیلان و ته&zwnj;ڵه&zwnj;که به دوور بن، لێتان خۆش ده&zwnj;بێت) هێزی خه&zwnj;شمی خوا له ده&zwnj;سته&zwnj;ی سته&zwnj;مکاران به&zwnj;رگری ناکرێت (له ده&zwnj;رئه&zwnj;نجامی بێ باوه&zwnj;ڕیان).';
$t[148] = 'له&zwnj;مه&zwnj;ودوا موشریک و هاوه&zwnj;ڵگه&zwnj;ران ده&zwnj;ڵێن: ئه&zwnj;گه&zwnj;ر خوا بیویستایه هاوه&zwnj;ڵمان بۆ بڕیار نه&zwnj;ده&zwnj;دا!! باوو باپیرانیشمان موشریک نه&zwnj;ده&zwnj;بوون!! هیچ شتێکیشمان له خۆمان حه&zwnj;رام نه&zwnj;ده&zwnj;کرد!! (ئه&zwnj;مانه هه&zwnj;مووی قسه&zwnj;ی به&zwnj;تاڵه و هه&zwnj;رگیز خوای گه&zwnj;وره زۆر له که&zwnj;س ناکات بۆ باوه&zwnj;ڕ هێنان یان بێ باوه&zwnj;ڕی) هه&zwnj;ر به&zwnj;و شێوه&zwnj;یه ئه&zwnj;وانه&zwnj;ی پێش ئه&zwnj;مانیش درۆ و بوختانیان هه&zwnj;ڵده&zwnj;به&zwnj;ست، هه&zwnj;تا تووشی به&zwnj;ڵای ئێمه بوون و سزای ئێمه&zwnj;یان چه&zwnj;شت، پێیان بڵێ: ئایا هیچ زانیاری و زانستیه&zwnj;کی خوایتان له&zwnj;لایه&zwnj;، تا نیشانمان بده&zwnj;ن و بۆمان ده&zwnj;ربخه&zwnj;ن؟ ئێوه شوێنی هیچ شتێکی تر ناکه&zwnj;ون جگه له&zwnj;گومان و ئێوه هیچ شتێکی تر ناکه&zwnj;ن مه&zwnj;گه&zwnj;ر ئه&zwnj;وه نه&zwnj;بێت که درۆ ده&zwnj;که&zwnj;ن.';
$t[149] = 'ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر (صلى الله عليه وسلم) پێیان بڵێ: که&zwnj;وابوو هه&zwnj;ر بۆ خوایه خۆی به&zwnj;ڵگه تێرو ته&zwnj;واوه&zwnj;کان، جا ئه&zwnj;گه&zwnj;ر بیویستایه ئه&zwnj;وه هه&zwnj;مووتانی به&zwnj;ته&zwnj;واوی هیدایه&zwnj;ت ده&zwnj;داو به ناچاریی هه&zwnj;مووان بڕوادار ده&zwnj;بوون (به&zwnj;ڵام ڕێزی لێگرتوون و سه&zwnj;رپشکی کردوون).';
$t[150] = 'پێیان بڵێ: ئاده&zwnj;ی ئه&zwnj;و شایه&zwnj;تانه&zwnj;تان بهێنن که شایه&zwnj;تی ده&zwnj;ده&zwnj;ن به&zwnj;ڕاستی خوا ئه&zwnj;م شتانه&zwnj;ی حه&zwnj;رام کردووه&zwnj;، خۆ ئه&zwnj;گه&zwnj;ر ویستیان شایه&zwnj;تی بده&zwnj;ن، ئه&zwnj;وه تۆ له&zwnj;گه&zwnj;ڵ ئه&zwnj;واندا مه&zwnj;به و شوێنی ئاره&zwnj;زووی ئه&zwnj;وانه مه&zwnj;که&zwnj;وه که باوه&zwnj;ڕیان به ئایه&zwnj;ت و فه&zwnj;رمانه&zwnj;کانی ئێمه نه&zwnj;کردووه و شوێن ئاره&zwnj;زووی ئه&zwnj;وانه&zwnj;ش مه&zwnj;که&zwnj;وه که باوه&zwnj;ڕ به&zwnj;رۆژی دوایی ناهێنن و ئه&zwnj;وانه&zwnj;ش که هاوتا بۆ په&zwnj;روه&zwnj;ردگاریان داده&zwnj;نێن.';
$t[151] = '(ئه&zwnj;ی محمد (صلى الله عليه وسلم) پێیان بڵێ: (خه&zwnj;ڵکینه&zwnj;) وه&zwnj;رن با ئه&zwnj;و شتانه&zwnj;تان بۆ بخوێنمه&zwnj;وه که په&zwnj;روه&zwnj;ردگارتان حه&zwnj;رامی کردووه له&zwnj;سه&zwnj;رتان، (یه&zwnj;که&zwnj;میان ئه&zwnj;وه&zwnj;یه&zwnj;) که هیچ شتێک نه&zwnj;که&zwnj;ن به&zwnj;شه&zwnj;ریک و هاوه&zwnj;ڵ بۆی، (دووهه&zwnj;میان ئه&zwnj;وه&zwnj;یه&zwnj;) که چاک ڕه&zwnj;فتار بن له&zwnj;گه&zwnj;ڵ دایک و باوکتاندا، (سێهه&zwnj;میان) منداڵانی خۆتان له&zwnj;به&zwnj;ر برسێتی و نه&zwnj;داری مه&zwnj;کوژن، چونکه ئێمه (په&zwnj;کتان ناخه&zwnj;ین) ڕزق و ڕۆزی ئێوه&zwnj;ش و ئه&zwnj;وانیش ده&zwnj;ده&zwnj;ین، (چوارهه&zwnj;میان) نزیکی گوناهو تاوانه&zwnj;کان مه&zwnj;که&zwnj;ون، چ ئه&zwnj;وه&zwnj;ی که ئاشکراو بووه و چ ئه&zwnj;وه&zwnj;ی که شاراوه و په&zwnj;نهانه بووه&zwnj;، (پێنجه&zwnj;میان) هیچ که&zwnj;سیش مه&zwnj;کوژن که خوا حه&zwnj;رامی کردووه مه&zwnj;گه&zwnj;ر به&zwnj;حه&zwnj;ق نه&zwnj;بێت، ئه&zwnj;وه&zwnj;ی که خوێندمه&zwnj;وه بۆتان خوا فه&zwnj;رمانی پێکردوون بۆ ئه&zwnj;وه&zwnj;ی ژیرو هۆشمه&zwnj;ند بن.';
$t[152] = '(شه&zwnj;شه&zwnj;میان) نزیک ماڵ و سامانی هه&zwnj;تیو مه&zwnj;که&zwnj;ون مه&zwnj;گه&zwnj;ر به&zwnj;چاکی و له قازانجی نه&zwnj;بێت، هه&zwnj;تا ئه&zwnj;و کاته&zwnj;ی گه&zwnj;وره ده&zwnj;بێت و به&zwnj;ته&zwnj;واوی پێ ده&zwnj;گات، (حه&zwnj;وته&zwnj;میان) کێشان و پێوان به&zwnj;ڕێک و پێکی به&zwnj;کار بهێنن به&zwnj;دادگه&zwnj;ری ئه&zwnj;نجامی بده&zwnj;ن و (فێڵی تێدا مه&zwnj;که&zwnj;ن)، جا ئێمه داخوازی له که&zwnj;س ناکه&zwnj;ین مه&zwnj;گه&zwnj;ر به&zwnj;گوێره&zwnj;ی توانای خۆی نه&zwnj;بێت (هه&zwnj;شته&zwnj;میان) کاتێکیش قسه&zwnj;و گوفتارتان کرد ئه&zwnj;وه دادوه&zwnj;ر بن و (لایه&zwnj;نی ناحه&zwnj;ق مه&zwnj;گرن) ئه&zwnj;گه&zwnj;ر خزمی نزیکیش بێت، (نۆهه&zwnj;میان) وه&zwnj;فابکه&zwnj;ن به&zwnj;و په&zwnj;یمانه&zwnj;ی که ده&zwnj;یبه&zwnj;ستن له&zwnj;گه&zwnj;ڵ خوادا به&zwnj;و به&zwnj;ڵێنه&zwnj;ی که به په&zwnj;روه&zwnj;ردگاری ده&zwnj;ده&zwnj;ن، ئه&zwnj;و شتانه&zwnj;ش په&zwnj;روه&zwnj;ردگارتان فه&zwnj;رمانی پێداون، بۆ ئه&zwnj;وه&zwnj;ی یاداوه&zwnj;ری وه&zwnj;ربگرن.';
$t[153] = 'بێگومان ئه&zwnj;مه&zwnj;ی (که باسکرا) ڕێبازی منه که ڕاسته ده ئێوه&zwnj;ش شوێنی بکه&zwnj;ون (ده&zwnj;هه&zwnj;م) شوێنی ڕێچکه&zwnj;کانی تر مه&zwnj;که&zwnj;ون چونکه جیاتان ده&zwnj;کاته&zwnj;وه له ڕێبازی خوا، ئه&zwnj;وه&zwnj;ش په&zwnj;روه&zwnj;ردگارتان فه&zwnj;رمانی پێ کردوون، بۆ ئه&zwnj;وه&zwnj;ی خواناس و پارێزکار بن.';
$t[154] = '(دوای ئه&zwnj;و (١٠) ئامۆژگاریه&zwnj;، یادتان ده&zwnj;هێنمه&zwnj;وه&zwnj;): پاشان کتێبی ته&zwnj;وراتمان به موسا به&zwnj;خشی بۆ ئه&zwnj;وه&zwnj;ی نازو نیعمه&zwnj;تی ته&zwnj;واو بڕژین به&zwnj;سه&zwnj;ر ئه&zwnj;و که&zwnj;سه&zwnj;دا که به&zwnj;چاکی پیاده&zwnj;ی ده&zwnj;کات و جیاکه&zwnj;ره&zwnj;وه و ڕوون که&zwnj;ره&zwnj;وه&zwnj;ی هه&zwnj;موو شتێک بێت و ڕێنموویی و میهره&zwnj;بانیش بێت، بۆ ئه&zwnj;وه&zwnj;ی ئه&zwnj;وانه باوه&zwnj;ڕی دامه&zwnj;زراو بهێنن به&zwnj;گه&zwnj;یشتنه&zwnj;وه&zwnj;یان به په&zwnj;روه&zwnj;ردگاریان (بۆ لێپرسینه&zwnj;وه و پاداشتی کارو کرده&zwnj;وه&zwnj;یان).';
$t[155] = 'ئه&zwnj;م قورئانه&zwnj;ش کتێبێکی پیرۆزه دامانبه&zwnj;زاند (بۆ سه&zwnj;رجه&zwnj;م خه&zwnj;ڵکی، له هه&zwnj;موو کات و شوێنێکدا) ده&zwnj;ئێوه&zwnj;ش شوێنی بکه&zwnj;ون و خۆتان له یاخی بوون دوور بگرن، بۆ ئه&zwnj;وه&zwnj;ی ڕه&zwnj;حمتان پێبکرێت...';
$t[156] = 'نه&zwnj;ک بڵێن: به&zwnj;ڕاستی کتێبی ئاسمانی هه&zwnj;ر بۆ دوو ده&zwnj;سته&zwnj;ی (گاورو جوو) پێش ئێمه دابه&zwnj;زێنراوه&zwnj;، له&zwnj;کاتێکدا بێگومان ئێمه له خوێندنه&zwnj;وه و لێکۆڵینه&zwnj;وه&zwnj;ی کتێبه&zwnj;کانیان بێ ئاگاین و نه&zwnj;خوێنده&zwnj;وارین...';
$t[157] = 'یان نه&zwnj;ک بڵێن: ئه&zwnj;گه&zwnj;ر به&zwnj;ڕاستی ئه&zwnj;و کتێبه بۆ ئێمه داببه&zwnj;زێنرایه ئه&zwnj;وه ئێمه له&zwnj;وان چاکتر په&zwnj;یڕه&zwnj;ویمان ده&zwnj;کرد، جا بێگومان خۆ ئه&zwnj;وه&zwnj;ته ئێوه&zwnj;ش به&zwnj;ڵگه&zwnj;ی ئاشکراو (قورئانی ڕه&zwnj;وانتان) له&zwnj;لایه&zwnj;ن په&zwnj;روه&zwnj;ردگارتانه&zwnj;وه بۆ هاتووه هاوڕێ له&zwnj;گه&zwnj;ڵ هیدایه&zwnj;ت و ڕه&zwnj;حمه&zwnj;ت و ڕێنموویدا (که&zwnj;چی باوه&zwnj;ڕی پێ ناهێنن!!)، جا کێ له&zwnj;وه سته&zwnj;مکارتره که بڕوای به&zwnj;ئایه&zwnj;ت و فه&zwnj;رمانه&zwnj;کانی خوا نه&zwnj;هێناوه و به&zwnj;فێڵ و پیلان ڕووی خه&zwnj;ڵکیان لێ وه&zwnj;رده&zwnj;گێڕێت، له&zwnj;ئاینده&zwnj;یه&zwnj;کی نزیکدا تۆڵه له&zwnj;وانه ده&zwnj;سێنین که ڕووی خۆیان و خه&zwnj;ڵکیش وه&zwnj;رده&zwnj;گێڕن له ئایه&zwnj;ته&zwnj;کانی ئێمه و به&zwnj;سزای توندو پڕ ئێش و ئازار گرفتاریان ده&zwnj;که&zwnj;ین، به&zwnj;هۆی ئه&zwnj;وه&zwnj;ی ڕووی خۆیان و خه&zwnj;ڵکیان وه&zwnj;رده&zwnj;چه&zwnj;رخاند له ئایینی خوا.';
$t[158] = 'ئایا ئه&zwnj;وانه چاوه&zwnj;ڕێی هیچ شتێک ده&zwnj;که&zwnj;ن جگه له&zwnj;وه&zwnj;ی که فریشته&zwnj;ی گیان کێشانیان بۆ بێت؟ یان په&zwnj;روه&zwnj;ردگارت خۆی یه&zwnj;کسه&zwnj;ر بێت بێ چه&zwnj;ند و چوون؟ یان هه&zwnj;ندێک له نیشانه&zwnj;کانی په&zwnj;روه&zwnj;ردگارت ده&zwnj;ربکه&zwnj;وێت (که به&zwnj;ڵگه&zwnj;یه له&zwnj;سه&zwnj;ر به&zwnj;رپا بوونی قیامه&zwnj;ت)؟ له ڕۆژی هاتنی هه&zwnj;ندێک له نیشانه&zwnj;کانی په&zwnj;روه&zwnj;ردگارت (وه&zwnj;کو هه&zwnj;ڵهاتنی ڕۆژ له خۆرئاوا...)، ئه&zwnj;و که&zwnj;سه&zwnj;ی ئه&zwnj;و ڕۆژه ئیمان و باوه&zwnj;ڕ ده&zwnj;هێنێت، ئه&zwnj;و ئیمان و باوه&zwnj;ڕه&zwnj;ی سوودی بۆی نابێت ئه&zwnj;گه&zwnj;ر پێشتر ئیمانی نه&zwnj;هێنابێت، یان کرده&zwnj;وه&zwnj;ی چاکی هاوڕێ له&zwnj;گه&zwnj;ڵ ئیمانه&zwnj;که&zwnj;یدا ئه&zwnj;نجام نه&zwnj;دابێت، پێیان بڵێ: چاوه&zwnj;ڕێ بکه&zwnj;ن (بزانین خوا چیتان به&zwnj;سه&zwnj;ر ده&zwnj;هێنێت) ئێمه&zwnj;ش چاوه&zwnj;ڕوانین.';
$t[159] = 'به&zwnj;ڕاستی ئه&zwnj;وانه&zwnj;ی ئاینه&zwnj;که&zwnj;یان به&zwnj;ش به&zwnj;ش کردو بوونه ده&zwnj;سته ده&zwnj;سته و پارچه پارچه (بڕوایان به&zwnj;هه&zwnj;ندێکی قورئان هه&zwnj;یه و هه&zwnj;ندێکی تری پشت گوێ ده&zwnj;خه&zwnj;ن)، ئه&zwnj;وه له هیچ شتێکدا تۆ له&zwnj;وان نیت، به&zwnj;ڵکو تۆ له&zwnj;وان به&zwnj;ریت، به&zwnj;ڕاستی ئه&zwnj;وانه کاروباریان بۆ لای خوایه و تۆڵه&zwnj;ی لادانیان لێده&zwnj;سێنێته&zwnj;وه&zwnj;، له&zwnj;وه&zwnj;ودوا هه&zwnj;واڵی هه&zwnj;موو ئه&zwnj;و کارو کرده&zwnj;وانه&zwnj;یان پێ ده&zwnj;دات که ئه&zwnj;نجامی ده&zwnj;ده&zwnj;ن.';
$t[160] = 'ئه&zwnj;وه&zwnj;ی به&zwnj;یه&zwnj;ک خێرو چاکه&zwnj;وه هاتبێت ئه&zwnj;وه ده چه&zwnj;ندانه&zwnj;ی چاکه&zwnj;که پاداشتی بۆ هه&zwnj;یه&zwnj;، ئه&zwnj;وه&zwnj;ش به گوناهو هه&zwnj;ڵه&zwnj;یه&zwnj;که&zwnj;وه هاتبێت، ئه&zwnj;وه پاداشت نادرێته&zwnj;وه جگه به&zwnj;ئه&zwnj;ندازه&zwnj;ی تاوانه&zwnj;که&zwnj;ی نه&zwnj;بێت و ئه&zwnj;وانه سته&zwnj;میان لێناکرێت.';
$t[161] = '(ئه&zwnj;ی محمد (صلى الله عليه وسلم) پێیان) بڵێ: به&zwnj;ڕاستی من په&zwnj;روه&zwnj;ردگارم هیدایه&zwnj;ت و ڕێنمووی کردووم بۆ ڕێگه و ڕێبازی ڕاست و دروست، ئاینێک که ڕاگری ژیانی خه&zwnj;ڵکه&zwnj;، ئه&zwnj;ویش ئاینی ئیبراهیمه که دووره له هه&zwnj;موو لادان و ناڕێکیه&zwnj;که&zwnj;وه و ئه&zwnj;و هه&zwnj;رگیز له ڕیزی موشریک و هاوه&zwnj;ڵگه&zwnj;راندا نه&zwnj;بووه&zwnj;.';
$t[162] = '(ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر (صلى الله عليه وسلم)، ئه&zwnj;ی ئیماندار) بڵێ: به&zwnj;ڕاستی نوێژو حه&zwnj;ج و دروشمه&zwnj;کانی و (سه&zwnj;رجه&zwnj;م چاکه و به&zwnj;ندایه&zwnj;تیم) هه&zwnj;روه&zwnj;ها ژیان و مردنم تایبه&zwnj;ته بۆ خوای په&zwnj;روه&zwnj;ردگاری جیهانیانه&zwnj;وه&zwnj;.';
$t[163] = 'ئه&zwnj;و زاته هیچ هاوه&zwnj;ڵ و هاوبه&zwnj;ش و شه&zwnj;ریکێکی نیه و من به&zwnj;وه فه&zwnj;رمانم پێدراوه و من یه&zwnj;که&zwnj;م که&zwnj;سی موسوڵمانانم.';
$t[164] = 'هه&zwnj;روه&zwnj;ها بڵێ: ئایا ڕه&zwnj;وایه من جگه له خوا بگه&zwnj;ڕێم په&zwnj;روه&zwnj;ردگارێکی تر بکه&zwnj;مه په&zwnj;روه&zwnj;ردگاری خۆم له&zwnj;کاتێکدا که ئه&zwnj;و زاته خاوه&zwnj;ن و په&zwnj;روه&zwnj;ردگاری هه&zwnj;موو شتێکه&zwnj;؟ هیچ که&zwnj;سێک هیچ گوناهێک ناکات له&zwnj;سه&zwnj;ر خۆی نه&zwnj;که&zwnj;وێت، هیچ که&zwnj;سێک باری گرانی تاوانی که&zwnj;سی تر هه&zwnj;ڵناگرێت، له&zwnj;وه&zwnj;ودوا گه&zwnj;ڕانه&zwnj;وه&zwnj;تان هه&zwnj;ر بۆ لای په&zwnj;روه&zwnj;ردگارتانه&zwnj;، ئینجا هه&zwnj;واڵی هه&zwnj;موو ئه&zwnj;و شتانه&zwnj;تان پێ ده&zwnj;دات که جیاوازی و کێشه&zwnj;تان تێدا هه&zwnj;یه&zwnj;.';
$t[165] = 'خوا هه&zwnj;ر ئه&zwnj;و زاته&zwnj;یه&zwnj;، که ئێوه&zwnj;ی کردووه به&zwnj;جێنشینی (گه&zwnj;لانی ڕابووردوو) له&zwnj;م زه&zwnj;ویه&zwnj;دا، چه&zwnj;نده&zwnj;ها پله&zwnj;وپایه&zwnj;ی هه&zwnj;ندێکتانی به&zwnj;سه&zwnj;ر هه&zwnj;ندێکی ترتاندا به&zwnj;رز کردۆته&zwnj;وه (له ڕووی زانستی یان سامان یان هێز... هید)، بۆ ئه&zwnj;وه&zwnj;ی تاقیتان بکاته&zwnj;وه به&zwnj;وه&zwnj;ی که پێی به&zwnj;خشیوون، دڵنیاش به که په&zwnj;روه&zwnj;ردگاری تۆ له تۆڵه&zwnj;سه&zwnj;ندنه&zwnj;وه&zwnj;دا خێرایه (له&zwnj;وانه&zwnj;ی که&zwnj;له تاقی کردنه&zwnj;وه&zwnj;که&zwnj;دا ده&zwnj;رناچن) بێگومان ئه&zwnj;و زاته لێخۆش بووه و میهره&zwnj;بانیشه (بۆ سه&zwnj;رفرازان).';
?>