$t = array();
$t[1] = 'هه&zwnj;رچی له ئاسمانه&zwnj;کان و هه&zwnj;رچی له زه&zwnj;ویدا هه&zwnj;یه یادی خوا به&zwnj;پاکی و بێگه&zwnj;ردی ده&zwnj;که&zwnj;ن، ده&zwnj;سه&zwnj;ڵات هه&zwnj;ر بۆ ئه&zwnj;وه و هه&zwnj;ر بۆ ئه&zwnj;ویش سوپاس، هه&zwnj;ر ئه&zwnj;ویش ده&zwnj;سه&zwnj;ڵاتداره به&zwnj;سه&zwnj;ر هه&zwnj;موو شتێکدا.';
$t[2] = 'ئه&zwnj;وزاته ئه&zwnj;وه&zwnj;یه ئێوه&zwnj;ی دروست کردووه&zwnj;، جا هه&zwnj;تانه بێ باوه&zwnj;ڕه&zwnj;و هه&zwnj;تانه باوه&zwnj;ڕدا، خوایش بینایه به&zwnj;هه&zwnj;موو ئه&zwnj;و کاروکرده&zwnj;وانه&zwnj;ی که ئه&zwnj;نجامی ده&zwnj;ده&zwnj;ن.';
$t[3] = 'ئه&zwnj;و خوایه له&zwnj;سه&zwnj;ر بنچینه&zwnj;ی حه&zwnj;ق و ڕاستی ئاسمانه&zwnj;کان و زه&zwnj;وی دروست کردووه&zwnj;، وێنه&zwnj;ی ڕووخساری ئێوه&zwnj;شی کێشاوه به&zwnj;جوانترین شێوه&zwnj;، گه&zwnj;رانه&zwnj;وه&zwnj;و سه&zwnj;رئه&zwnj;نجامیش هه&zwnj;ر بۆ لای ئه&zwnj;وه&zwnj;.';
$t[4] = 'هه&zwnj;رچی له ئاسمانه&zwnj;کان و زه&zwnj;ویدا هه&zwnj;یه و ده&zwnj;گوزه&zwnj;رێت، خوا ئاگاو زانایه پێی، هه&zwnj;روه&zwnj;ها ده&zwnj;زانێت چی په&zwnj;نهان ده&zwnj;که&zwnj;ن و چی ئاشکرا ده&zwnj;که&zwnj;ن، هه&zwnj;میشه ئه&zwnj;و خوایه زانایه به هه&zwnj;رچی له پێچ و په&zwnj;نای سینه&zwnj;کاندا حه&zwnj;شاردراوه&zwnj;.';
$t[5] = 'ئایا هه&zwnj;واڵی ئه&zwnj;وانه&zwnj;تان پێ نه&zwnj;گه&zwnj;یشتووه که پێشتر بێ بڕوا بوون، چۆن سزاو ئازاری کارو کرده&zwnj;وه&zwnj;کانیان چه&zwnj;شت له دنیادا، له قیامه&zwnj;تیشدا سزای به&zwnj;ئێش و ئازار بۆتان ئاماده&zwnj;یه (چونکه تاوانی بێ باوه&zwnj;ڕی زۆر گه&zwnj;وره&zwnj;یه و سه&zwnj;رچاوه&zwnj;یه بۆ هه&zwnj;موو گوناهـ و تاوانێک).';
$t[6] = 'ئه&zwnj;وه&zwnj;ش بۆیه پێشهات چونکه پێغه&zwnj;مبه&zwnj;ران به&zwnj;جۆره&zwnj;ها به&zwnj;ڵگه&zwnj;و موعجیزه&zwnj;، په&zwnj;یامی خوایان پێڕاگه&zwnj;یاندن که&zwnj;چی وتیان: ئه&zwnj;مانه به&zwnj;شه&zwnj;رن وه&zwnj;کو ئێمه&zwnj;، چۆن ده&zwnj;بنه پێغه&zwnj;مبه&zwnj;رو چاوساغمان؟ به&zwnj;و بیانووه ڕێبازی بێ باوه&zwnj;ڕیان هه&zwnj;ڵبژاردو پشتیان هه&zwnj;ڵکرد، خوایش پێویستی به&zwnj;ئیمانی ئه&zwnj;وان نیه چونکه خوا خۆی ده&zwnj;وڵه&zwnj;مه&zwnj;نده&zwnj;و بێنیازه و شایسته&zwnj;ی سوپاسه&zwnj;.';
$t[7] = 'ئه&zwnj;وانه&zwnj;ی بێ باوه&zwnj;ڕ بوون به بۆچوونی خۆیان وتیان: هه&zwnj;رگیز زیندوو ناکرێنه&zwnj;وه&zwnj;، پێیان بڵێ: وا نیه&zwnj;، سوێند به په&zwnj;روه&zwnj;ردگارم، به&zwnj;ڕاستی هه&zwnj;ر زیندوو ده&zwnj;کرێنه&zwnj;وه&zwnj;، پاشان ئاگادار ده&zwnj;کرێن له هه&zwnj;موو ئه&zwnj;و کارو کرده&zwnj;وانه&zwnj;ی که ئه&zwnj;نجامتان ده&zwnj;دا، ئه&zwnj;وه&zwnj;ش بۆ خوا زۆر ئاسان و ساده&zwnj;یه&zwnj;.';
$t[8] = 'که&zwnj;واته تا زووه باوه&zwnj;ڕ بهێنن به&zwnj;خواو به پێغه&zwnj;مبه&zwnj;ره&zwnj;که&zwnj;ی و به&zwnj;و نوور و ڕۆشناییه&zwnj;ی که ناردوومانه&zwnj;ته خواره&zwnj;وه (که قورئانه&zwnj;)، دڵنیاش بن که خوا ئاگایه به هه&zwnj;موو ئه&zwnj;و کاروکرده&zwnj;وانه&zwnj;ی که ئه&zwnj;نجامی ده&zwnj;ده&zwnj;ن.';
$t[9] = 'ڕۆژێک دێت خوا هه&zwnj;مووانتان کۆده&zwnj;کاته&zwnj;وه&zwnj;، بۆ ڕۆژی کۆبوونه&zwnj;وه&zwnj;که&zwnj;، ئه&zwnj;وه ڕۆژی زیان باریه&zwnj;، جا ئه&zwnj;وه&zwnj;ی ئیمان و باوه&zwnj;ر بهێنێت به خواو کرده&zwnj;وه&zwnj;ی چاکیش ئه&zwnj;نجام بدات، ئه&zwnj;وه خوا چاوپۆشی ده&zwnj;کات له گوناهو هه&zwnj;ڵه&zwnj;کانی و ده&zwnj;یخاته باخه&zwnj;کانی به&zwnj;هه&zwnj;شته&zwnj;وه که چه&zwnj;نده&zwnj;ها ڕوبار به&zwnj;ژێر دره&zwnj;خته&zwnj;کانیدا ده&zwnj;ڕوات، هاوڕێ له&zwnj;گه&zwnj;ڵ ژیانی نه&zwnj;بڕاوه&zwnj;دا بۆ هه&zwnj;میشه&zwnj;و هه&zwnj;تا هه&zwnj;تایی، ئه&zwnj;وه&zwnj;یه سه&zwnj;رکه&zwnj;وتن و سه&zwnj;رفرازی گه&zwnj;وره&zwnj;و بێسنوور.';
$t[10] = 'ئه&zwnj;وانه&zwnj;ش که بێ باوه&zwnj;ڕ بوون و ئایه&zwnj;ت و فه&zwnj;رمانه&zwnj;کانی ئێمه&zwnj;یان به&zwnj;درۆزانی، ئا ئه&zwnj;وانه نیشته&zwnj;جێی ناو ئاگرن و ژیانی نه&zwnj;بڕاوه تیایدا ده&zwnj;بنه سه&zwnj;ر، ئای که سه&zwnj;رئه&zwnj;نجامێکی زۆر ناخۆش و ناله&zwnj;باره&zwnj;.';
$t[11] = 'به&zwnj;فه&zwnj;رمانی خوا نه&zwnj;بێت هیچ خۆشی و ناخۆشیه&zwnj;ك پێش نه&zwnj;هاتووه&zwnj;، جا ئه&zwnj;وه&zwnj;ی باوه&zwnj;ڕی ته&zwnj;واو بهێنێت، خوا دڵی ڕێنموویی ده&zwnj;کات بۆ لای خۆی، (هه&zwnj;ر بارودۆخێکی به&zwnj;سه&zwnj;ردا بێت، له خۆشی و ناخۆشی به&zwnj;تاقیکردنه&zwnj;وه&zwnj;ی ده&zwnj;زانێت) خوای گه&zwnj;وره&zwnj;ش هه&zwnj;میشه به&zwnj;هه&zwnj;موو شتێک زانایه&zwnj;.';
$t[12] = 'فه&zwnj;رمانبه&zwnj;رداریی خوا بکه&zwnj;ن و فه&zwnj;رمانبه&zwnj;رداریی پێغه&zwnj;مبه&zwnj;ریش (صلی الله علیه وسلم) بکه&zwnj;ن، جا ئه&zwnj;گه&zwnj;ر پشت هه&zwnj;ڵکه&zwnj;ن و گوێڕایه&zwnj;ڵ نه&zwnj;بن ئه&zwnj;وه خۆتان زه&zwnj;ره&zwnj;ر ده&zwnj;که&zwnj;ن، ئه&zwnj;گینا ته&zwnj;نها گه&zwnj;یاندنی ڕوون و ئاشکرایی په&zwnj;یـامه&zwnj;که&zwnj;ی، ئه&zwnj;رکی سه&zwnj;رشانی پێغه&zwnj;مبه&zwnj;ره&zwnj;که&zwnj;مانه&zwnj;.';
$t[13] = '-الله&zwnj;- خوایه&zwnj;که جگه له خوایه&zwnj;کی تر نیه که دروستکار بێت و خاوه&zwnj;نی فه&zwnj;رمان بێت، ده&zwnj;با ئه&zwnj;وانه&zwnj;ی ئیماندارن هه&zwnj;ر پشت به&zwnj;و خوایه ببه&zwnj;ستن و داوای یارمه&zwnj;تی هه&zwnj;ر له&zwnj;و بکه&zwnj;ن.';
$t[14] = 'ئه&zwnj;ی ئه&zwnj;وانه&zwnj;ی باوه&zwnj;ڕتان هێناوه&zwnj;.. هه&zwnj;ندێك له هاوسه&zwnj;رانتان یان هه&zwnj;ندێک له ڕۆڵه&zwnj;و نه&zwnj;وه&zwnj;کانتان دوژمنتانن.. جا ئێوه&zwnj;ش وریابن و خۆتان بپارێزن لێیان، جا ئه&zwnj;گه&zwnj;ر چاوپۆشی له هه&zwnj;ڵه&zwnj;یان بکه&zwnj;ن و واز له ئازاردانیان بهێنن و لێیان ببورن، ئه&zwnj;وه چاك بزانن که خوا لێخۆسبووه و میهره&zwnj;بانه&zwnj;.';
$t[15] = 'دڵنیابن که ماڵ و سامان و ڕۆڵه و نه&zwnj;وه&zwnj;کانتان بۆ تاقی کردنه&zwnj;وه&zwnj;تانن، (ئه&zwnj;گه&zwnj;ر له&zwnj;و بوارانه&zwnj;دا سه&zwnj;رکه&zwnj;وتن به&zwnj;ده&zwnj;ست بهێنن)، خوای میهره&zwnj;بان پاداشتی گه&zwnj;وره&zwnj;و و زۆری له لایه بۆ خۆگرو لێبورده&zwnj;کان.';
$t[16] = 'که&zwnj;وابوو هه&zwnj;تا ده&zwnj;توانن به&zwnj;ته&zwnj;قواو خواناس بن، گوێڕایه&zwnj;ڵ و فه&zwnj;رمانبه&zwnj;ردار بن، خێرو چاکه&zwnj;ش بکه&zwnj;ن، ئه&zwnj;وانه هه&zwnj;مووی بۆ خۆتان چاکه&zwnj;، جا ئه&zwnj;وه&zwnj;ی خۆی بپارێزێت له ڕه&zwnj;زیلی و ڕژدی نه&zwnj;فسی خۆی، ئا ئه&zwnj;وانه هه&zwnj;ر خۆیان سه&zwnj;رفرازن.';
$t[17] = 'ئه&zwnj;گه&zwnj;ر قه&zwnj;رز بده&zwnj;ن به خوا، به&zwnj;شێوه&zwnj;کی چاك (مه&zwnj;به&zwnj;ست به&zwnj;خشینی ماڵ و سامانه له پێناوه خوادا) ئه&zwnj;وه خوا چه&zwnj;ند به&zwnj;رابه&zwnj;ری ده&zwnj;کات بۆتان، هه&zwnj;روه&zwnj;ها لێتان خۆش ده&zwnj;بێت خوایش سوپاسگوزاره (له&zwnj;سه&zwnj;ر به&zwnj;خشن که هه&zwnj;ر به&zwnj;خششی خۆیه&zwnj;تی) و خۆڕاگره (مۆڵه&zwnj;تی ڕه&zwnj;زیله&zwnj;کانیش ده&zwnj;دات تا کاتی خۆی).';
$t[18] = 'ئه&zwnj;و زاته ئاگاداری نهێنی و ئاشکرایه&zwnj;، خوایه&zwnj;کی باڵاده&zwnj;ست و دانایه&zwnj;.';
