$t[1] = 'ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر تۆ بۆچی شتێک له خۆت حه&zwnj;رام ده&zwnj;که&zwnj;یت که خوا بۆی حه&zwnj;ڵاڵ کردویت، ده&zwnj;ته&zwnj;وێت به&zwnj;و کاره هاوسه&zwnj;ره&zwnj;کانت ڕازی بکه&zwnj;یت؟! له&zwnj;کاتێکدا هه&zwnj;ر خوا لێخۆشبوو میهره&zwnj;بانه&zwnj;.';
$t[2] = 'به&zwnj;ڕاستی خوا هه&zwnj;ڵوه&zwnj;شاندنه&zwnj;وه&zwnj;ی سوێده&zwnj;کانتانی بۆ بڕیارداون (به&zwnj;وه&zwnj;ی که&zwnj;فاره&zwnj;تی بده&zwnj;ن)، ئه&zwnj;و خوایه گه&zwnj;وره&zwnj;تانه و پشتیوان و یارمه&zwnj;تی ده&zwnj;رتانه&zwnj;، هه&zwnj;ر ئه&zwnj;ویش زاتێکی زاناو دانایه&zwnj;.';
$t[3] = 'کاتێك پێغه&zwnj;مبه&zwnj;ر (صلی الله علیه وسلم) قسه&zwnj;یه&zwnj;کی نهێنی لای هه&zwnj;ندێك له خێزانه&zwnj;کانی درکان (که ئه&zwnj;ویش دایکه حفصه بوو)، جا کاتێک ئه&zwnj;ویش باسه&zwnj;که&zwnj;ی گه&zwnj;یانده دایکه عائیشه خوایش پێغه&zwnj;مبه&zwnj;ری ئاگادارکرد له ڕازو نیازیان و باسی هه&zwnj;ندیك له ڕازه&zwnj;که&zwnj;ی کردو باسی هه&zwnj;ندێکی نه&zwnj;کرد، (پێغه&zwnj;مبه&zwnj;ر (صلی الله علیه وسلم) گله&zwnj;یی له دایکه حفصه کرد، ده&zwnj;رباره&zwnj;ی درکاندنی نهێنیکه&zwnj;) دایکه حفصه پرسی: کێ ئه&zwnj;م هه&zwnj;واڵه&zwnj;ی پێگه&zwnj;یاندیت؟ پێغه&zwnj;مبه&zwnj;ر (صلی الله علیه وسلم) وتی: خوای زاناو ئاگا منی ئاگادار کرد، (ئه&zwnj;م چه&zwnj;ند ئایه&zwnj;ته ده&zwnj;رباره&zwnj;ی ڕووداوێکی تایبه&zwnj;تیه له ژیانی پێغه&zwnj;مبه&zwnj;ر (صلی الله علیه وسلم) که له&zwnj;کتێبه&zwnj;کانی سیره&zwnj;دا باس کراوه&zwnj;).';
$t[4] = '(بۆیه خوا گه&zwnj;وره پێیان ده&zwnj;فه&zwnj;رموێت:) ئه&zwnj;گه&zwnj;ر هه&zwnj;ردووکتان داوای ته&zwnj;وبه و په&zwnj;شیمانی بکه&zwnj;ن و بگه&zwnj;ڕێنه&zwnj;وه بۆ لای خوا، ئه&zwnj;وه باشه&zwnj;، چونکه دڵه&zwnj;کانتان له&zwnj;خشته چوو بوو و ترازا بوو، خۆ ئه&zwnj;گه&zwnj;ر پشتی یه&zwnj;کتر بگرن دژ به پێغه&zwnj;مبه&zwnj;ر (صلی الله علیه وسلم)، ئه&zwnj;وه چاک بزانن که خوا خۆی یاریده&zwnj;ده&zwnj;رێتی و هه&zwnj;روه&zwnj;ها جوبره&zwnj;ئیل و ئیمانداره چاکه&zwnj;کان، فریشته&zwnj;کانیش له&zwnj;وه&zwnj;ودوا پشتیوانن بۆی.';
$t[5] = 'ئه&zwnj;گه&zwnj;ر پیغه&zwnj;مبه&zwnj;ر (صلی الله علیه وسلم) ته&zwnj;ڵاقتان بدات، ئه&zwnj;وه نزیکه خوا هاوسه&zwnj;رانی چاکتر له ئێوه&zwnj;ی پی ببه&zwnj;خشتێت، موسڵمان و باوه&zwnj;ڕدار، ملکه&zwnj;چی په&zwnj;روه&zwnj;ردگار، زوو له هه&zwnj;ڵه په&zwnj;شیمان، خواپه&zwnj;رست و دیندار، ڕۆژووه&zwnj;وان، یان بیرو هۆشیان له&zwnj;کاردایه و سه&zwnj;رنجی دروست کراوانی خوا ده&zwnj;ده&zwnj;ن، هه&zwnj;روه&zwnj;ها ئه&zwnj;و ئافره&zwnj;تانه هه&zwnj;یانه بێوه&zwnj;ن و هه&zwnj;یانه کچن...';
$t[6] = 'ئه&zwnj;ی ئه&zwnj;و کاسانه&zwnj;ی که باوه&zwnj;ڕتان هێناوه خۆتان و که&zwnj;سوکارتان بپارێزن له ئاگرێك که سووته&zwnj;مه&zwnj;نیه&zwnj;که&zwnj;ی خه&zwnj;ڵکی و به&zwnj;رده&zwnj;، سه&zwnj;رپه&zwnj;رشتیاری ئه&zwnj;و دۆزه&zwnj;خ و ئاگره&zwnj;، جۆره فریشته&zwnj;یه&zwnj;کن که زۆر دڵڕه&zwnj;ق و توندو تیژن، نافه&zwnj;رمانی خوا ناکه&zwnj;ن له هیچ شتێکدا که فه&zwnj;رمانیان پێ بدات و هه&zwnj;رچی فه&zwnj;رمانێکیان پێ بدرێت ئه&zwnj;نجامی ده&zwnj;ده&zwnj;ن.';
$t[7] = 'ئه&zwnj;ی ئه&zwnj;وانه&zwnj;ی که بی باوه&zwnj;ڕ بوون ئه&zwnj;مڕۆ هیچ پاکانه&zwnj;یه&zwnj;ک مه&zwnj;که&zwnj;ن، چونکه ئێوه به&zwnj;ڕاستی ته&zwnj;نها پاداشتی ئه&zwnj;و کارو کرده&zwnj;وانه&zwnj;تان ده&zwnj;درێته&zwnj;وه که ئه&zwnj;نجامتان ده&zwnj;دا.';
$t[8] = 'ئه&zwnj;ی ئه&zwnj;و که&zwnj;سانه&zwnj;ی که باوه&zwnj;ڕتان هێناوه&zwnj;، ئێوه به ته&zwnj;وبه&zwnj;و په&zwnj;شیمانیه&zwnj;کی په&zwnj;خت و دڵسۆزانه&zwnj;وه ڕوو بکه&zwnj;نه&zwnj;وه خوا، نزیکه په&zwnj;روه&zwnj;ردگارتان له هه&zwnj;ڵه و گوناهه&zwnj;کانتان خۆش ببێت و بتانخاته باخه&zwnj;کانی به&zwnj;هه&zwnj;شته&zwnj;وه که چه&zwnj;نداها و ڕوبار به&zwnj;ژێر دره&zwnj;خت و کۆشکه&zwnj;کانیدا ده&zwnj;ڕوات، ڕۆژێك دێت که خوا پێغه&zwnj;مبه&zwnj;ر و ئه&zwnj;وانه&zwnj;ی له خزمه&zwnj;تیدا خه&zwnj;جاڵه&zwnj;ت و شه&zwnj;رمه&zwnj;زار ناکات، نوورو ڕوناکیان ده&zwnj;دره&zwnj;وشێته&zwnj;وه&zwnj;و دێت و ده&zwnj;چێت له به&zwnj;رده&zwnj;م و لای ڕاستیانه&zwnj;وه و ده&zwnj;ڵێن: په&zwnj;روه&zwnj;ردگارا ئه&zwnj;م نوورو ڕوناکیه&zwnj;مان بۆ زیاد بکه&zwnj;و لێمان خۆش ببه&zwnj;، چونکه به&zwnj;ڕاستی تۆ ده&zwnj;سه&zwnj;ڵاتت به&zwnj;سه&zwnj;ر هه&zwnj;موو شتێکدا هه&zwnj;یه&zwnj;.';
$t[9] = 'ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر (صلی الله علیه وسلم) کۆشش بکه و بجه&zwnj;نگه دژی کافر و دووڕوه&zwnj;کان و توندو تیژبه له&zwnj;سه&zwnj;ریان، ئه&zwnj;وانه شوێنیان دۆزه&zwnj;خه&zwnj;، ئای که سه&zwnj;رنجامێکی خواپه&zwnj;.';
$t[10] = 'خوا نموونه&zwnj;ی هێناوه&zwnj;ته&zwnj;وه به ژنی نوح و لوط بۆ ئه&zwnj;وانه&zwnj;ی که بێ باوه&zwnj;ڕ بوون، که ئه&zwnj;و دوو ئافره&zwnj;ته هاوسه&zwnj;ری دوو به&zwnj;نده&zwnj;ی چاك و پاکی ئێمه بوون، که&zwnj;چی خیانه&zwnj;تیان لێ کردن (باوه&zwnj;ڕیان پێیان نه&zwnj;بوو، غه&zwnj;یبه&zwnj;تیان ده&zwnj;کردن، گاڵته&zwnj;یان پێده&zwnj;کردن...)، ئیتر ئه&zwnj;و پێغه&zwnj;مبه&zwnj;رانه فریای هاوسه&zwnj;ره&zwnj;کانیان نه&zwnj;که&zwnj;وتن، له قیامه&zwnj;تدا به&zwnj;و ژنانه ده&zwnj;وترێت: بچنه ناو ئاگری دۆزه&zwnj;خه&zwnj;وه له&zwnj;گه&zwnj;ڵ ئه&zwnj;وانه&zwnj;دا که ده&zwnj;چنه ناوی.';
$t[11] = 'هه&zwnj;روه&zwnj;ها خوا نموونه&zwnj;ی هێناوه&zwnj;ته&zwnj;وه به هاوسه&zwnj;ره&zwnj;که&zwnj;ی فیرعه&zwnj;ون بۆ ئه&zwnj;وانه&zwnj;ی که باوه&zwnj;ڕیان هێناوه&zwnj;، کاتێك وتی: په&zwnj;روه&zwnj;ردگارا له&zwnj;لای خۆت له به&zwnj;هه&zwnj;شتدا ماڵێکم بۆ دروست بکه (تا به&zwnj;رده&zwnj;وام به&zwnj;دیدارت شاد ببم) و له ده&zwnj;ست فیرعه&zwnj;ون و کاروکرده&zwnj;وه&zwnj;ی ڕزگارم بکه و له&zwnj;ده&zwnj;ست ئه&zwnj;م قه&zwnj;ومه سته&zwnj;مکاره&zwnj;ش ده&zwnj;ربازم بکه&zwnj;.';
$t[12] = 'هه&zwnj;روه&zwnj;ها نموونه&zwnj;ی هێناوه&zwnj;ته&zwnj;وه به مه&zwnj;ریه&zwnj;می کچی عیمرانیش که داوێنی خۆی به&zwnj;پاکی ڕاگرت، ئێمه&zwnj;ش له&zwnj;و ڕۆحه&zwnj;ی که به&zwnj;ده&zwnj;ستمانه&zwnj;، فوومان پێداکرد، ئه&zwnj;ویش باوه&zwnj;ڕی ته&zwnj;واوی هه&zwnj;بوو به گوفتارو فه&zwnj;رمانه&zwnj;کانی په&zwnj;روه&zwnj;ردگاری و هه&zwnj;میشه و به&zwnj;رده&zwnj;وام ملکه&zwnj;چ و فه&zwnj;رمانبه&zwnj;ردارو خواناس و نزاکار بوو.';
