$t = array();
$t[1] = 'سه&zwnj;رنجی سه&zwnj;ره&zwnj;تای سووره&zwnj;تی (البقرة) بده&zwnj;.';
$t[2] = '(ئه&zwnj;م قورئانه) کتێبێکه و بۆت هاتوه&zwnj;ته خواره&zwnj;وه&zwnj;، که&zwnj;وابوو با سه&zwnj;غڵه&zwnj;تیی له دڵ و ده&zwnj;روونتدا نه&zwnj;ببێت (له&zwnj;گه&zwnj;یاندنیدا) بۆ ئه&zwnj;وه&zwnj;ی خه&zwnj;ڵکی پێ بێدار بکه&zwnj;یته&zwnj;وه&zwnj;، هه&zwnj;روه&zwnj;ها یاده&zwnj;وه&zwnj;ریشه بۆ ئیمانداران.';
$t[3] = '(خه&zwnj;ڵکینه&zwnj;) شوێنی ئه&zwnj;و (به&zwnj;رنامه&zwnj;یه) بکه&zwnj;ون که له&zwnj;لایه&zwnj;ن په&zwnj;روه&zwnj;ردگارتانه&zwnj;وه بۆتان هێنراوه&zwnj;ته خواره&zwnj;وه&zwnj;، جگه له&zwnj;و زاته شوێنی هیچ پشتیوان و خۆشه&zwnj;ویستێکی تر مه&zwnj;که&zwnj;ون، که&zwnj;م یاداوه&zwnj;ری و په&zwnj;ند وه&zwnj;رده&zwnj;گرن و بیرده&zwnj;که&zwnj;نه&zwnj;وه&zwnj;!';
$t[4] = 'چه&zwnj;نده&zwnj;ها شارو دێهاتمان وێران و کاول کرد له کاتێکدا له شه&zwnj;ودا خه&zwnj;وتبوون، یان له&zwnj;کاتی ئیسراحه&zwnj;تی نیوه&zwnj;ڕۆدا بوون کتوپڕ سزاو تۆڵه&zwnj;ی ئێمه یه&zwnj;خه&zwnj;ی پێگرتن.';
$t[5] = 'ئه&zwnj;و کاته که سزای ئێمه یه&zwnj;خه&zwnj;ی پێگرتن ئینجا قسه&zwnj;و گوفتار و دۆعا و نزایان هیچ نه&zwnj;بوو ته&zwnj;نها ئه&zwnj;وه نه&zwnj;بێت ده&zwnj;یانوت: به&zwnj;ڕاستی ئێمه سته&zwnj;مکار بووین، به&zwnj;ڕاستی ئێمه یاخی بوین.';
$t[6] = 'ئینجا سوێند بێت به خوا بێگومان ئێمه پرسیار ده&zwnj;که&zwnj;ین له&zwnj;وانه&zwnj;ی که پێغه&zwnj;مبه&zwnj;رانیان بۆ ڕه&zwnj;وانه کراوه&zwnj;، سوێند بێت به خوا له پێغه&zwnj;مبه&zwnj;رانیش ده&zwnj;پرسینه&zwnj;وه (ده&zwnj;رباره&zwnj;ی هه&zwnj;ڵوێستی ئه&zwnj;و خه&zwnj;ڵکه به&zwnj;رامبه&zwnj;ریان).';
$t[7] = 'ئینجا سوێند بێت به خوا به&zwnj;ڕاستی هه&zwnj;موو به&zwnj;سه&zwnj;رهاته&zwnj;کان له&zwnj;سه&zwnj;ر بنچینه&zwnj;ی زانیاری و ئاگاداری ته&zwnj;واو ده&zwnj;خه&zwnj;ینه ڕوو و ده&zwnj;یڵێینه&zwnj;وه بۆیان، ئێمه بێ ئاگا نه&zwnj;بووین له هه&zwnj;ڵس و که&zwnj;وتیان.';
$t[8] = 'ته&zwnj;رازوو پێوه&zwnj;ری ڕاست و دروست له&zwnj;و ڕۆژه&zwnj;دایه&zwnj;، ئینجا ئه&zwnj;وه&zwnj;ی تای ته&zwnj;رازووی (خێر و چاکه&zwnj;کانی) سه&zwnj;نگین بێت، ئه&zwnj;وه ئیتر ئه&zwnj;و جۆره که&zwnj;سانه سه&zwnj;رکه&zwnj;وتوو سه&zwnj;رفرازن.';
$t[9] = '(به&zwnj;ڵام) ئه&zwnj;وه&zwnj;ی تای ته&zwnj;رازوی (خێرو چاکه&zwnj;کانی سوک بێت) ئه&zwnj;و جۆره که&zwnj;سانه ئه&zwnj;وانه&zwnj;ن که خۆیان دۆڕان و خۆیان له&zwnj;ده&zwnj;ستدا به&zwnj;هۆی ئه&zwnj;وه&zwnj;ی که بڕوایان به&zwnj;به&zwnj;ڵگه و ئایه&zwnj;ته&zwnj;کانی ئێمه نیه و سته&zwnj;م ده&zwnj;که&zwnj;ن.';
$t[10] = 'سوێند به&zwnj;خوا به&zwnj;ڕاستی ئێمه ئێوه&zwnj;مان نیشته&zwnj;جێ کرد له&zwnj;م زه&zwnj;ویه&zwnj;دا و ده&zwnj;سه&zwnj;ڵاتمان پێبه&zwnj;خشین و جۆره&zwnj;ها هۆکاری ژیانمان تیایدا بۆ سازاندوون (له خواردن و خواردنه&zwnj;وه&zwnj;و هۆکانی گواستنه&zwnj;وه&zwnj;و پۆشاک و ناوماڵ و ئاوو هه&zwnj;واو... هتد، که&zwnj;چی له&zwnj;به&zwnj;رامبه&zwnj;ر ئه&zwnj;و هه&zwnj;موو نازو نیعمه&zwnj;ته بێ شوماره&zwnj;) سوپاسی که&zwnj;می نیعمه&zwnj;ته&zwnj;کان ده&zwnj;که&zwnj;ن یان که&zwnj;متان سوپاسگوزارن.';
$t[11] = 'سوێند به&zwnj;خوا به&zwnj;ڕاستی ئێمه (باوه گه&zwnj;وره&zwnj;ی) ئێوه&zwnj;مان دروست کردو نه&zwnj;خش و نیگاری ڕواڵه&zwnj;ت و ڕوخساری ئێوه&zwnj;مان کێشا، پاشان وتمان به فریشته&zwnj;کان: ئاده&zwnj;ی سوژده به&zwnj;رن بۆ ئاده&zwnj;م (سوژده&zwnj;ی ڕێزو به&zwnj;فه&zwnj;رمانی خوا) جا هه&zwnj;موو فریشته&zwnj;کان سوژده&zwnj;یان برد جگه له ئیبلیس (که فه&zwnj;رمانی خوای شکاندو) له سوژده&zwnj;به&zwnj;ران نه&zwnj;بوو.';
$t[12] = 'خوا فه&zwnj;رمووی: با&zwnj;شه&zwnj;، چی به&zwnj;رگری کردیت ئه&zwnj;ی ئیبلیس که سوژده نه&zwnj;به&zwnj;یت کاتێک فه&zwnj;رمانم پێدایت؟ شه&zwnj;یتان وتی: من له&zwnj;و چاکترم، (چونکه) تۆ منت له ئاگر دروست کردوه و ئه&zwnj;وت له قوڕ دروستکردووه&zwnj;.';
$t[13] = 'خوا فه&zwnj;رمووی: ده&zwnj;ی که&zwnj;واته دابه&zwnj;زه و له به&zwnj;هه&zwnj;شت یان له ئاسماندا یان له پله&zwnj;و پایه&zwnj;ی به&zwnj;ندایه&zwnj;تی، چونکه تۆ بۆت نیه له&zwnj;وێدا فیز بکه&zwnj;یت و خۆت به&zwnj;گه&zwnj;وره دابنێیت، جا تا زووه ده&zwnj;رچۆ، چونکه به&zwnj;ڕاستی تۆ له ڕیسواو بچوک و بێ نرخه&zwnj;کانیت.';
$t[14] = 'شه&zwnj;یتان وتی: داواکارم که مۆڵه&zwnj;تم بده&zwnj;یت تا ئه&zwnj;و ڕۆژه&zwnj;ی که هه&zwnj;موو نه&zwnj;وه&zwnj;ی ئاده&zwnj;م زیندوو ده&zwnj;کرێنه&zwnj;وه&zwnj;.';
$t[15] = 'خوا فه&zwnj;رمووی: به&zwnj;ڕاستی تۆ له مۆڵه&zwnj;ت دراوانیت.';
$t[16] = 'شه&zwnj;یتان وتی: به&zwnj;هۆی ئه&zwnj;وه&zwnj;ی که تۆ منت گومڕاو سه&zwnj;رگه&zwnj;ردان کرد، ئه&zwnj;وه سوێند بێت منیش له ڕێگه ڕاسته&zwnj;که&zwnj;تدا بۆیان داده&zwnj;نیشم (لایان بده&zwnj;م).';
$t[17] = 'پاشان سوێند به&zwnj;خوا له به&zwnj;رده&zwnj;میانه&zwnj;وه&zwnj;، له پشتیانه&zwnj;وه&zwnj;، له لای ڕاستیانه&zwnj;وه&zwnj;، له لای چه&zwnj;پیانه&zwnj;وه&zwnj;، بۆیان دێم و (دنیایان لاخۆشه&zwnj;ویست ده&zwnj;که&zwnj;م و گومانیان ده&zwnj;رباره&zwnj;ی قیامه&zwnj;ت لادروست ده&zwnj;که&zwnj;م) و ده&zwnj;ستت ناکه&zwnj;وێت زۆربه&zwnj;یان سوپاسگوزار بن.';
$t[18] = 'خوا فه&zwnj;رمووی: له به&zwnj;هه&zwnj;شت ده&zwnj;رچۆره ده&zwnj;ره&zwnj;وه به&zwnj;سه&zwnj;رشۆڕی و ڕیسوایی و ده&zwnj;رکراوی، سوێند به خوا ئه&zwnj;و که&zwnj;سه&zwnj;ی شوێنت که&zwnj;وت له&zwnj;وان، ئه&zwnj;وه بێگومان دۆزه&zwnj;خ پڕ ده&zwnj;که&zwnj;م له ئێوه هه&zwnj;موو.';
$t[19] = '(تۆش) ئه&zwnj;ی ئاده&zwnj;م، خۆت و هاوسه&zwnj;ره&zwnj;که&zwnj;ت له&zwnj;به&zwnj;هه&zwnj;شتدا نیشته&zwnj;جێ بن، جا له هه&zwnj;ر کوێ ئاره&zwnj;زووتان لێ بوو (خوارده&zwnj;مه&zwnj;نی و میوه&zwnj;یه&zwnj;) بخۆن، (به&zwnj;ڵام) نزیکی ئه&zwnj;م دره&zwnj;خته مه&zwnj;که&zwnj;ون (دره&zwnj;ختێکی تایبه&zwnj;تی به&zwnj;رداربوو، بۆ تاقی کردنه&zwnj;وه&zwnj;یان خوای گه&zwnj;وره بڕیاریدا که هه&zwnj;ر نزیکی نه&zwnj;که&zwnj;ون، چ جای له به&zwnj;رو بوومی بخۆن) ئه&zwnj;و کاته ده&zwnj;چنه ڕیزی سته&zwnj;مکارانه&zwnj;وه&zwnj;.';
$t[20] = 'شه&zwnj;یتان (ئه&zwnj;و سنووره&zwnj;ی بۆ ئاده&zwnj;م و حه&zwnj;وا به&zwnj;فرسه&zwnj;ت زانی) و ئینجا که&zwnj;وته فڕێدانی وه&zwnj;سوه&zwnj;سه&zwnj;و خه&zwnj;ته&zwnj;ره و خه&zwnj;یاڵ بۆ ناو دڵ و ده&zwnj;روونیان تا ئه&zwnj;و عه&zwnj;یب و عاره&zwnj;یان ده&zwnj;ربخات که شاراوه&zwnj;بوو لێیان (دوایی خۆی ئاشکراکرد) و پێی وتن: په&zwnj;روه&zwnj;ردگارتان ئه&zwnj;م دره&zwnj;خته&zwnj;ی لێ قه&zwnj;ده&zwnj;غه نه&zwnj;کردوون ته&zwnj;نها له&zwnj;به&zwnj;ر ئه&zwnj;وه&zwnj;یه نه&zwnj;وه&zwnj;کو ببنه فریشته&zwnj;، یاخود بۆ ئه&zwnj;وه&zwnj;یه نه&zwnj;وه&zwnj;کو له نه&zwnj;مران بن!!';
$t[21] = '(ئه&zwnj;وسا شه&zwnj;یتان زانی ده&zwnj;ترسن و دوودڵن) سوێندی بۆ خواردن که من به&zwnj;ڕاستی له ئامۆژگارانم بۆ ئێوه&zwnj;و دڵسۆزتانم!!';
$t[22] = 'ئیتر شه&zwnj;یتان به&zwnj;فێڵ و پیلان فریوی دان (به&zwnj;ره&zwnj;ولای دره&zwnj;خته&zwnj;که&zwnj;ی بردن) جاکاتێک تامی به&zwnj;رو بوومی دره&zwnj;خته&zwnj;که&zwnj;یان چه&zwnj;شت، هه&zwnj;رچی عه&zwnj;یب و عاریان هه&zwnj;یه که&zwnj;وته ده&zwnj;ره&zwnj;وه&zwnj;، (ناچار به&zwnj;په&zwnj;له&zwnj;) ده&zwnj;ستیاندایه گه&zwnj;ڵای دره&zwnj;خته&zwnj;کانی به&zwnj;هه&zwnj;شت عه&zwnj;یب و عاری خۆیان پێ دائه&zwnj;پۆشی و په&zwnj;روه&zwnj;ردگاریان بانگی کردن و فه&zwnj;رمووی: ئایا من قه&zwnj;ده&zwnj;غه&zwnj;ی ئه&zwnj;و دره&zwnj;خته&zwnj;م لێنه&zwnj;کردن و پێم نه&zwnj;وتن، که به&zwnj;ڕاستی شه&zwnj;یتان بۆ ئێوه دوژمنێکی ئاشکرایه&zwnj;؟!';
$t[23] = '(ئه&zwnj;وسا ئیتر هه&zwnj;ردووکیان هه&zwnj;ستیان به&zwnj;هه&zwnj;ڵه&zwnj;ی خۆیان کردو) وتیان: په&zwnj;روه&zwnj;ردگارا ئێمه سته&zwnj;ممان له خۆمان کرد، خۆ ئه&zwnj;گه&zwnj;ر لێمان خۆش نه&zwnj;بیت و به&zwnj;زه&zwnj;ییت پێماندا نه&zwnj;یه&zwnj;ته&zwnj;وه&zwnj;، ئه&zwnj;وه به&zwnj;ڕاستی ئێمه سوێند به خوا له خه&zwnj;ساره&zwnj;تمه&zwnj;ند و زه&zwnj;ره&zwnj;رمه&zwnj;ندانین.';
$t[24] = 'خوای گه&zwnj;وره فه&zwnj;رمووی: ئیتر دابه&zwnj;زن هه&zwnj;ندێکتان دوژمنی هه&zwnj;ندێکتانن (شه&zwnj;یتان و دارو ده&zwnj;سته&zwnj;ی دوژمنی ئێوه&zwnj;ن و نه&zwnj;وه&zwnj;ی ئاده&zwnj;میش به&zwnj;هۆی پیلانی شه&zwnj;یتانه&zwnj;وه دوژمنایه&zwnj;تی یه&zwnj;کتر ده&zwnj;که&zwnj;ن)، بۆتان هه&zwnj;یه له زه&zwnj;ویدا نیشته&zwnj;جێ بوون و هۆی ڕابواردن و ژیان تا ماوه&zwnj;یه&zwnj;ک.';
$t[25] = 'خوا فه&zwnj;رمووی (پێیان): هه&zwnj;ر له زه&zwnj;ویدا ده&zwnj;ژین و له&zwnj;وێش ده&zwnj;مرن و (تێکه&zwnj;ڵ به&zwnj;خاکی ده&zwnj;بن، له کاتی دیاری کراویشدا) هه&zwnj;ر له ناخیه&zwnj;وه ده&zwnj;هێنرێنه ده&zwnj;ره&zwnj;وه ( بۆ لێپرسینه&zwnj;وه&zwnj;).';
$t[26] = 'ئه&zwnj;ی نه&zwnj;وه&zwnj;ی ئاده&zwnj;م، ئێمه به&zwnj;ڕاستی پۆشاکی له&zwnj;بارمان بۆ فه&zwnj;راهه&zwnj;م هێناون، عه&zwnj;وره&zwnj;ت و ئه&zwnj;وه&zwnj;ی پێتان ناخۆشه ده&zwnj;ربکه&zwnj;وێت دای ئه&zwnj;پۆشێت، هه&zwnj;روه&zwnj;ها جل و به&zwnj;رگی جوانیش که خۆتانی پێ بڕازێننه&zwnj;وه (به&zwnj;ڵام بیرتان نه&zwnj;چێت) که پۆشاکی ته&zwnj;قواو خواناسی چاکتر و خێردارتر و به&zwnj;فه&zwnj;ڕتره&zwnj;، ئه&zwnj;و پۆشاکه (ماددی و مه&zwnj;عنه&zwnj;ویانه&zwnj;) له نیشانه و به&zwnj;ڵگه&zwnj;کانی خوای گه&zwnj;وره&zwnj;ن بۆ ئه&zwnj;وه&zwnj;ی یاداوه&zwnj;ری وه&zwnj;ربگرن و (یادی به&zwnj;هه&zwnj;شت بکه&zwnj;ن).';
$t[27] = 'ئه&zwnj;ی نه&zwnj;وه&zwnj;ی ئاده&zwnj;م: شه&zwnj;یتان، ئێوه&zwnj;ش له خشته نه&zwnj;بات، هه&zwnj;روه&zwnj;ک باوک و دایکتانی له خشته بردو له به&zwnj;هه&zwnj;شتدا به ده&zwnj;رکردنی دان، له&zwnj;کاتێکدا ده&zwnj;ستی کرد به&zwnj;داماڵینی پۆشاکه&zwnj;کانیان تا عه&zwnj;وره&zwnj;تیان پیشانی یه&zwnj;کتربدات، بێگومان شه&zwnj;یتان و دارو ده&zwnj;سته&zwnj;ی ئێوه ده&zwnj;بینن، له شوێنێکدا که (دنیایه&zwnj;) ئێوه ئه&zwnj;وان نابینن، جا دڵنیابن که ئێمه شه&zwnj;یتانه&zwnj;کانمان کردووه به یارو یاوه&zwnj;ری ئه&zwnj;وانه&zwnj;ی باوه&zwnj;ڕ ناهێنن.';
$t[28] = 'کاتێکیش (بێ باوه&zwnj;ڕان) تاوان و گوناهێک ئه&zwnj;نجام بده&zwnj;ن ده&zwnj;ڵێن: باوو باپیرانیشمان دیوه ئه&zwnj;م کاره&zwnj;یان کردوه&zwnj;و خوای گه&zwnj;وره خۆی فه&zwnj;رمانی پێداوین بیکه&zwnj;ین!! پێیان بڵێ: (نه&zwnj;خێر وانییه&zwnj;) چونکه به&zwnj;ڕاستی خوا فه&zwnj;رمان به&zwnj;گوناهو تاوان و کاری نابه&zwnj;جێ نادات، چۆن (جه&zwnj;ساره&zwnj;ت ده&zwnj;که&zwnj;ن) و شتێک هه&zwnj;ڵده&zwnj;به&zwnj;ستن بۆ خوا که نازانن وتویه&zwnj;تی یان نا.';
$t[29] = '(ئه&zwnj;ی محمد(صلى الله عليه وسلم) بڵێ: په&zwnj;روه&zwnj;ردگارم فه&zwnj;رمانی به دادپه&zwnj;روه&zwnj;ری داوه&zwnj;، له&zwnj;کاتی هه&zwnj;رپه&zwnj;رستن و نوێژێکدا، له هه&zwnj;موو مزگه&zwnj;وتێکدا، ڕووی دڵ و ڕوخسارتان به&zwnj;ڕاستی له په&zwnj;روه&zwnj;ردگارتان بکه&zwnj;ن و بیپه&zwnj;رستن و لێی بپاڕێنه&zwnj;وه&zwnj;، له&zwnj;کاتێکدا دینداری بێگه&zwnj;ردو دڵسۆزانه هه&zwnj;ر بۆ ئه&zwnj;و زاته&zwnj;یه&zwnj;، چونکه هه&zwnj;ر چۆن دروستی کردوون له سه&zwnj;ره&zwnj;تاوه&zwnj;، له ئاینده&zwnj;شدا هه&zwnj;ر بۆ لای ئه&zwnj;و ده&zwnj;گه&zwnj;ڕێنه&zwnj;وه&zwnj;...';
$t[30] = '(جا ئه&zwnj;و کاته ده&zwnj;بنه دوو ده&zwnj;سته&zwnj;) ده&zwnj;سته&zwnj;یه&zwnj;ک هیدایه&zwnj;ت و ڕێنمووی وه&zwnj;رگرتووه&zwnj;، ده&zwnj;سته&zwnj;یه&zwnj;کیش بڕیاری گومڕای و سه&zwnj;ر لێشێواوی به&zwnj;سه&zwnj;ردا چه&zwnj;سپاوه&zwnj;، چونکه به&zwnj;ڕاستی ئه&zwnj;وانه بێجگه له خوا شه&zwnj;یتانه&zwnj;کانیشیان کرده پشتیوان و خۆشه&zwnj;ویستی خۆیان، واش ده&zwnj;زانن که ڕێگه&zwnj;ی ڕاست و دروستیان گرتۆته&zwnj;به&zwnj;ر.';
$t[31] = 'ئه&zwnj;ی نه&zwnj;وه&zwnj;ی ئاده&zwnj;م، خۆتان بڕازێننه&zwnj;وه به پۆشاک له&zwnj;به&zwnj;ر کردن، له&zwnj;کاتی هه&zwnj;موو نوێژو چوونه ناو مزگه&zwnj;وتێکدا، بخۆن و بخۆنه&zwnj;وه (له نازو نیعمه&zwnj;ته&zwnj;کان) به&zwnj;ڵام زیاده&zwnj;ڕه&zwnj;وی مه&zwnj;که&zwnj;ن، چونکه به&zwnj;ڕاستی خوا له سنوور ده&zwnj;رچووان و زیاده&zwnj;ڕه&zwnj;وانی خۆش ناوێت.';
$t[32] = '(ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر(صلى الله عليه وسلم) پێیان) بڵێ: ئه&zwnj;وه کێیه پۆشاک و زینه&zwnj;ت و جوانیه خواییه&zwnj;کانی له به&zwnj;نده&zwnj;کانی حه&zwnj;رام کردوه که له بنه&zwnj;ڕه&zwnj;تدا خۆی بۆی به&zwnj;دیهێناون؟ هه&zwnj;روه&zwnj;ها ئه&zwnj;وه کێیه ڕزق و ڕۆزیه چاک و پاکه&zwnj;کانی حه&zwnj;رام کردوه&zwnj;؟! به&zwnj;و خه&zwnj;ڵکه بڵێ: هه&zwnj;موو ئه&zwnj;و شتانه بۆ ئیماندارانه له ژیانی دنیاداو له کاتێکدا پاڵفته&zwnj;ن له ڕۆژی قیامه&zwnj;تدا، ئا به&zwnj;و شێوه&zwnj;یه ئایه&zwnj;ت و فه&zwnj;رمانه&zwnj;کان ڕوون ده&zwnj;که&zwnj;ینه&zwnj;وه بۆ که&zwnj;سانێک که بزانن و هوشیار بن.';
$t[33] = 'پێیان بڵێ: به&zwnj;ڕاستی په&zwnj;روه&zwnj;ردگارم هه&zwnj;رچی گوناهو خراپه&zwnj;یه حه&zwnj;رامی کردووه چ ئه&zwnj;وانه&zwnj;ی ئاشکران، چ ئه&zwnj;وانه&zwnj;ی په&zwnj;نهانن، هه&zwnj;روه&zwnj;ها هه&zwnj;موو گوناهو داخوازی و ده&zwnj;ستدرێژیه&zwnj;کی به&zwnj;ناحه&zwnj;قی حه&zwnj;رام کردووه&zwnj;، شتێک بکه&zwnj;ن به&zwnj;شه&zwnj;ریک و هاوه&zwnj;ڵ بۆ خوا که هیچ ده&zwnj;سه&zwnj;ڵاتی پێنه&zwnj;داوه&zwnj;، هه&zwnj;روه&zwnj;ها حه&zwnj;رامی کردووه له خۆتانه&zwnj;وه به&zwnj;ناوی خواوه&zwnj;، دوور له هه&zwnj;موو زانیاری و زانستێک شت بڵێن.';
$t[34] = 'بۆ هه&zwnj;موو نه&zwnj;ته&zwnj;وه&zwnj;یه&zwnj;ک ته&zwnj;مه&zwnj;نێکی سنووردار هه&zwnj;یه&zwnj;، (له&zwnj;کاتی دیاریکراودا کۆتایی به&zwnj;ده&zwnj;وری ده&zwnj;هێنرێت)، جا کاتێک کۆتایی ته&zwnj;مه&zwnj;نیان هات، ئه&zwnj;وه نه تاوێک دوا ده&zwnj;که&zwnj;ون نه تاوێکیش پێش ده&zwnj;که&zwnj;ون.';
$t[35] = 'ئه&zwnj;ی نه&zwnj;وه&zwnj;ی ئاده&zwnj;م، ئه&zwnj;گه&zwnj;ر پێغه&zwnj;مبه&zwnj;رانتان له خۆتان بۆ هات که ئایه&zwnj;ت و فه&zwnj;رمانه&zwnj;کانی منیان به&zwnj;سه&zwnj;ردا ده&zwnj;خوێننه&zwnj;وه&zwnj;، جا ئه&zwnj;و که&zwnj;سه&zwnj;ی به ته&zwnj;قواو له خوا ترس بێت ئه&zwnj;وه ترسیان له&zwnj;سه&zwnj;ر نیه و دڵگران و غه&zwnj;مبار نابن.';
$t[36] = '(به&zwnj;ڵام) ئه&zwnj;وانه&zwnj;ش بڕوایان به&zwnj;ئایه&zwnj;ت و فه&zwnj;رمانه&zwnj;کانی ئێمه نه&zwnj;بووه&zwnj;، لووت به&zwnj;رز بوون به&zwnj;رامبه&zwnj;ریان ئا ئه&zwnj;وانه نیشته&zwnj;جێی ناو ئاگری دۆزه&zwnj;خن و به&zwnj;هه&zwnj;میشه&zwnj;یی تیایدان.';
$t[37] = 'جا کێ له&zwnj;وه سته&zwnj;مکارتره که به&zwnj;ده&zwnj;م خواوه درۆ هه&zwnj;ڵده&zwnj;به&zwnj;ستێت، یان بڕوای به&zwnj;ئایه&zwnj;ته&zwnj;کانی نه&zwnj;هێناوه&zwnj;، ئا ئه&zwnj;وانه ئه&zwnj;وه&zwnj;ی که بۆیان بڕیار دراوه له دنیادا ده&zwnj;ستیان ده&zwnj;که&zwnj;وێت، هه&zwnj;تا ئه&zwnj;و کاته&zwnj;ی فریشته نێردراوه&zwnj;کانمان بۆیان دێن گیانیان ده&zwnj;کێشن، ئه&zwnj;وسا پێیان ده&zwnj;ڵێن: کوان ئه&zwnj;وانه&zwnj;ی جگه&zwnj;له خوا هانا و هاوارتان بۆ ده&zwnj;بردن و ده&zwnj;تانپه&zwnj;رستن؟! (به&zwnj;سه&zwnj;رسامیه&zwnj;وه&zwnj;) ده&zwnj;ڵێن: (نازانین) لێمان ون بوون، (ئه&zwnj;وسا به&zwnj;ناچاریی) شایه&zwnj;تی له خۆیان ده&zwnj;ده&zwnj;ن که به&zwnj;ڕاستی ئه&zwnj;وان هه&zwnj;ر بێ باوه&zwnj;ڕ بوون.';
$t[38] = '(ئه&zwnj;وسا خوا فه&zwnj;رمانیدا) و فه&zwnj;رمووی: ده بچنه ڕیزی ئه&zwnj;و کۆمه&zwnj;ڵانه&zwnj;وه که پێش ئێوه هه&zwnj;بوون (له&zwnj;گومڕاکان) له&zwnj;ده&zwnj;سته&zwnj;ی په&zwnj;ری و ئاده&zwnj;میزاده&zwnj;کان له&zwnj;ناو دۆزه&zwnj;خدا، هه&zwnj;ر کاتێک هه&zwnj;ر کۆمه&zwnj;ڵ و ده&zwnj;سته&zwnj;یه&zwnj;ک ده&zwnj;چنه ناوی نه&zwnj;فرین ده&zwnj;که&zwnj;ن له هاوئاین و هاو بیرانیان، هه&zwnj;تا ئه&zwnj;و کاته&zwnj;ی هه&zwnj;موویان کۆ ده&zwnj;کرێنه&zwnj;وه و به&zwnj;سه&zwnj;ریه&zwnj;کدا ده&zwnj;درێن له ناویدا، شوێنکه&zwnj;وته&zwnj;کان ڕوو به&zwnj;گه&zwnj;وره&zwnj;کانیان ده&zwnj;ڵێن: په&zwnj;روه&zwnj;ردگارا ئا ئه&zwnj;وانه ئێمه&zwnj;یان گومڕاکرد، ده تۆش له&zwnj;ناو دۆزه&zwnj;خدا سزای دوو به&zwnj;رامبه&zwnj;ریان بده&zwnj;!! (خوای گه&zwnj;وره&zwnj;ش فه&zwnj;رمووی): بۆ هه&zwnj;موو لایه&zwnj;ک (سزاو تۆڵه&zwnj;) دوو به&zwnj;رامبه&zwnj;ره به&zwnj;ڵام ئێوه پێی نازانن.';
$t[39] = '(پاشان) ده&zwnj;سته پێشوه&zwnj;کان به دوایینه&zwnj;کان ده&zwnj;ڵێن: وه&zwnj;نه&zwnj;بێت ئێوه هیچ ڕێزو فه&zwnj;زڵێکتان هه&zwnj;بێت به&zwnj;سه&zwnj;ر ئێمه&zwnj;دا، ده ئێوه&zwnj;ش بچێژن سزاو ئازار به&zwnj;هۆی ئه&zwnj;و کارو کرده&zwnj;وه ناله&zwnj;بارانه&zwnj;وه که ئه&zwnj;نجامتان ده&zwnj;دا.';
$t[40] = 'بێگومان ئه&zwnj;وانه&zwnj;ی بڕوایان به ئایه&zwnj;ت و فه&zwnj;رمانه&zwnj;کانی ئێمه نه&zwnj;هێناوه و خۆیان له ئاستیدا به&zwnj;گه&zwnj;وره داناوه&zwnj;، ئه&zwnj;وانه ده&zwnj;روازه&zwnj;کانی ئاسمانیان بۆ ناکرێته&zwnj;وه و ناچنه به&zwnj;هه&zwnj;شته&zwnj;وه هه&zwnj;تا وشتر ئه&zwnj;چێت به&zwnj;ناو کونی ده&zwnj;رزیدا، ئا به&zwnj;و شێوه&zwnj;یه ئێمه پاداشتی تاوانباران ده&zwnj;ده&zwnj;ینه&zwnj;وه و تۆڵه&zwnj;یان لێده&zwnj;سێنین.';
$t[41] = 'ئه&zwnj;وانه (له&zwnj;ناو دۆزه&zwnj;خدا) ڕایه&zwnj;خی ئاگرینیان هه&zwnj;یه&zwnj;، له سه&zwnj;روشیانه&zwnj;وه&zwnj;(سه&zwnj;قف و به&zwnj;تانی و لێفه&zwnj;ی) ئاگرین داپۆشه&zwnj;ریانه&zwnj;، (سه&zwnj;رو خواریان ئاگره&zwnj;) بێگومان هه&zwnj;ر به&zwnj;و شێوه&zwnj;یه پاداشتی سته&zwnj;مکاران ده&zwnj;ده&zwnj;ینه&zwnj;وه و تۆڵه&zwnj;یان لێ ده&zwnj;سێنین.';
$t[42] = '(له لایه&zwnj;کی تره&zwnj;وه&zwnj;) ئه&zwnj;وانه&zwnj;ی که باوه&zwnj;ڕیان هێناوه&zwnj;و کاروکرده&zwnj;وه چاکه&zwnj;کانیان ئه&zwnj;نجامداوه (به&zwnj;مه&zwnj;رجێک) ئێمه له توانای هه&zwnj;رکه&zwnj;س زیاتر داخوازی ناکه&zwnj;ین، ئه&zwnj;وانه خاوه&zwnj;ن و نیشته&zwnj;جێی به&zwnj;هه&zwnj;شتن و ژیانیان به&zwnj;رده&zwnj;وام و هه&zwnj;میشه&zwnj;ییه تیایدا.';
$t[43] = '(هه&zwnj;ر له ده&zwnj;روازه&zwnj;ی به&zwnj;هه&zwnj;شتدا) هه&zwnj;رچی بوغزو کینه&zwnj;یه&zwnj;ک له دڵ و ده&zwnj;روون و سینه&zwnj;یاندا بوو ئێمه دامان ڕێی (تا به سینه&zwnj;یه&zwnj;کی ساف و دڵ و ده&zwnj;روونێکی بێگه&zwnj;رده&zwnj;وه بچنه ناو به&zwnj;هه&zwnj;شته&zwnj;وه&zwnj;) که&zwnj;چه&zwnj;نده&zwnj;ها ڕووبار به&zwnj;ژێر دره&zwnj;خته&zwnj;کانیداو به به&zwnj;رده&zwnj;م کۆشکه&zwnj;کانیدا ڕه&zwnj;وان و جاری ده&zwnj;بێت، (ئه&zwnj;وسا ئیتر هه&zwnj;مووان به&zwnj;یه&zwnj;ک ده&zwnj;نگ و یه&zwnj;ک ئاواز) ده&zwnj;ڵێن: سوپاس و ستایش بۆ ئه&zwnj;و خوایه&zwnj;ی که هیدایه&zwnj;ت و ڕێنموویی کردین بۆ ئه&zwnj;م جێگه&zwnj;و ڕێگه خۆشه&zwnj;، ئه&zwnj;گه&zwnj;ر ئه&zwnj;و خوایه هیدایه&zwnj;ت و ڕێنموویی نه&zwnj;کردینایه هه&zwnj;رگیز ڕێگای هیدایه&zwnj;تمان نه&zwnj;ده&zwnj;زانی و ئه&zwnj;م پاداشته&zwnj;مان وه&zwnj;رنه&zwnj;ده&zwnj;گرت، سوێند به&zwnj;خوا به&zwnj;ڕاستی پێغه&zwnj;مبه&zwnj;رانی په&zwnj;روه&zwnj;ردگارمان به حه&zwnj;ق و ڕاستیه&zwnj;وه هاتن، ئه&zwnj;وسا بانگیان لێکرا: ئه&zwnj;وه ئه&zwnj;و به&zwnj;هه&zwnj;شته&zwnj;یه که پێتان دراوه به&zwnj;هۆی ئه&zwnj;و کارو کرده&zwnj;وانه که ئه&zwnj;نجامتان ده&zwnj;دا.';
$t[44] = '(پاشان) نیشته&zwnj;جێیانی به&zwnj;هه&zwnj;شت بانگی نیشته&zwnj;جێکانی دۆزه&zwnj;خیان کرد: به&zwnj;ڕاستی ئه&zwnj;و به&zwnj;ڵێنه&zwnj;ی که په&zwnj;روه&zwnj;ردگارمان پێی دابووین ده&zwnj;ستمانکه&zwnj;وت و پێی به&zwnj;خشین، ئایا ئێوه&zwnj;ش ئه&zwnj;و به&zwnj;ڵێنه&zwnj;ی که په&zwnj;روه&zwnj;دگارتان دابووی به&zwnj;ڕاستی ده&zwnj;ستانکه&zwnj;وت؟! (له وه&zwnj;ڵامدا به&zwnj;دڵ شکاوی و غه&zwnj;م و په&zwnj;ژاره&zwnj;یه&zwnj;کی زۆره&zwnj;وه&zwnj;) وتیان: به&zwnj;ڵێ.. ئه&zwnj;وسا جاڕچیه&zwnj;ک جاڕیدا له نێوانیاندا و هاواری کرد: نه&zwnj;فره&zwnj;تی خوا له سته&zwnj;مکاران بێت...';
$t[45] = 'ئه&zwnj;وانه&zwnj;ی که به نه&zwnj;رمه بڕی به&zwnj;رهه&zwnj;ڵستی ڕێبازی خوا ده&zwnj;که&zwnj;ن و کۆسپ و ته&zwnj;گه&zwnj;ره ده&zwnj;خه&zwnj;نه ڕێگه&zwnj;ی و ڕێبازی خوایان به خوارو خێچی ده&zwnj;وێت، له&zwnj;کاتێکدا هه&zwnj;میشه ئه&zwnj;وان بێ بڕوان به زیندوو بوونه&zwnj;وه و لێپرسینه&zwnj;وه&zwnj;.';
$t[46] = 'له&zwnj;نێوان به&zwnj;هه&zwnj;شتی و دۆزه&zwnj;خیه&zwnj;کاندا دیوارو په&zwnj;رده&zwnj;یه&zwnj;ک هه&zwnj;یه&zwnj;، به&zwnj;سه&zwnj;ر شوێنه به&zwnj;رزه&zwnj;کانیشه&zwnj;وه که&zwnj;سانێکی مه&zwnj;رد هه&zwnj;ن هه&zwnj;ردوولا به&zwnj;چاکی ده&zwnj;ناسن به&zwnj;هۆی ناونیشانه&zwnj;کانیانه&zwnj;وه (کاتێک ڕوو ده&zwnj;که&zwnj;نه به&zwnj;هه&zwnj;شت) بانگ ده&zwnj;که&zwnj;ن له نیشته&zwnj;جێکانی و ده&zwnj;ڵێن: سڵاوتان لێ بێت (خۆشی له خۆتان)، ئه&zwnj;وانه هێشتا نه&zwnj;چوونه&zwnj;ته ناوی باڵام به&zwnj;هیوان و ئومێدیان هه&zwnj;یه&zwnj;.';
$t[47] = 'کاتێکیش ڕوویان به&zwnj;ره&zwnj;و ڕووی دۆزه&zwnj;خیان وه&zwnj;رده&zwnj;چه&zwnj;رخێنرێت ده&zwnj;ڵێن: په&zwnj;روه&zwnj;دگارا مه&zwnj;مانگێڕه له&zwnj;گه&zwnj;ڵ ده&zwnj;سته&zwnj;ی سته&zwnj;مکاراندا.';
$t[48] = 'ئه&zwnj;وانه&zwnj;ی که له&zwnj;سه&zwnj;ر شوێنه به&zwnj;رزه&zwnj;کانن بانگی که&zwnj;سانێک ده&zwnj;که&zwnj;ن (له دۆزه&zwnj;خیان) که به&zwnj;ڕواڵه&zwnj;ت و سیمایاندا ده&zwnj;یانناسن و پێیان ده&zwnj;ڵێن: (خۆ ئیستا لاتان ڕوونه&zwnj;) که دارو ده&zwnj;سته&zwnj;و هێزو توانا و ماڵ و سامانتان فریاتان نه&zwnj;که&zwnj;وت؟! هه&zwnj;روه&zwnj;ها فیز و لووت به&zwnj;رزی و خۆ فش کردنه&zwnj;وه&zwnj;تان به&zwnj;هاناتانه&zwnj;وه نه&zwnj;هات!!';
$t[49] = 'ئایا ئه&zwnj;وانه ئه&zwnj;و که&zwnj;سانه&zwnj;ن که&zwnj;کاتی خۆی سوێندتان ده&zwnj;خوارد ڕه&zwnj;حمه&zwnj;تی خوا نایانگرێته&zwnj;وه&zwnj;! (ئه&zwnj;وه&zwnj;ته خوا پێیان ده&zwnj;فه&zwnj;رموێت): ده بچنه به&zwnj;هه&zwnj;شته&zwnj;وه&zwnj;، نه ترس و بیمتان له&zwnj;سه&zwnj;ره نه&zwnj;غه&zwnj;م و په&zwnj;ژاره&zwnj;ش ده&zwnj;خۆن.';
$t[50] = '(دۆزه&zwnj;خیان به&zwnj;ده&zwnj;م ئاهو ناڵه&zwnj;و غه&zwnj;م و په&zwnj;ژاره&zwnj;وه&zwnj;) هاواریان له به&zwnj;هه&zwnj;شتیان کردو لێیان پاڕانه&zwnj;وه&zwnj;و وتیان: نه&zwnj;ختێک ئاومان به&zwnj;سه&zwnj;ردا بڕێژن، یان به&zwnj;شمان بده&zwnj;ن له&zwnj;و ڕزق و ڕۆزیانه&zwnj;ی که خوا پێی به&zwnj;خشیوون (به&zwnj;هه&zwnj;شتیه&zwnj;کان له وه&zwnj;ڵامیاندا) وتیان: به&zwnj;ڕاستی خوا خواردن و خواردنه&zwnj;وه&zwnj;ی به&zwnj;هه&zwnj;شتی له&zwnj;سه&zwnj;ر بێ باوه&zwnj;ڕان حه&zwnj;رام کردووه&zwnj;...';
$t[51] = 'ئه&zwnj;وانه&zwnj;ی که&zwnj;کاتی خۆی ئاینه&zwnj;که&zwnj;یان به&zwnj;گه&zwnj;مه&zwnj;و گاڵته گرتبوو، ژیانی دنیا غه&zwnj;ڕاو سه&zwnj;رگه&zwnj;ردانی کردبوون و خه&zwnj;ڵه&zwnj;تاندنی، جا ئێمه ئه&zwnj;مڕۆ ئه&zwnj;وانه فه&zwnj;رامۆش ده&zwnj;که&zwnj;ین، هه&zwnj;روه&zwnj;کو ئه&zwnj;وان ئه&zwnj;م ڕۆژه&zwnj;یان فه&zwnj;رامۆش کرد و له&zwnj;بیر خۆیان بردبۆوه&zwnj;، هه&zwnj;روه&zwnj;ها به&zwnj;هۆی ئه&zwnj;وه&zwnj;شه&zwnj;وه که دژی ئایه&zwnj;ته&zwnj;کانی ئێمه ده&zwnj;وه&zwnj;ستان و ئینکاری و به&zwnj;ربه&zwnj;ره&zwnj;کانیان ده&zwnj;کرد.';
$t[52] = 'سوێند بێت به&zwnj;خوا به&zwnj;ڕاستی ئێمه کتێبێکمان بۆ هێنان هه&zwnj;موو شتێکمان له&zwnj;سه&zwnj;ر بنچینه&zwnj;ی زانستی و زانیاری تێدا ڕوون کردۆته&zwnj;وه&zwnj;، هیدایه&zwnj;ت و ڕه&zwnj;حمه&zwnj;ت و ڕێنموویشه بۆ ئه&zwnj;و که&zwnj;سانه&zwnj;ی که باوه&zwnj;ڕی پێده&zwnj;هێنن.';
$t[53] = 'ئایا (بێ باوه&zwnj;ڕان) چاوه&zwnj;ڕێی چی ده&zwnj;که&zwnj;ن جگه له پێشهاتن و ڕوودانی هه&zwnj;ڕه&zwnj;شه قورئانیه&zwnj;کان نه&zwnj;بێت؟ جا ئه&zwnj;و ڕۆژه&zwnj;ی که (به&zwnj;ڵێن و هه&zwnj;ڕه&zwnj;شه&zwnj;کان) دێته دی، ئه&zwnj;وانه&zwnj;ی پێشتر فه&zwnj;رامۆشیان کردبوو ده&zwnj;ڵێن: به&zwnj;ڕاستی پێغه&zwnj;مبه&zwnj;رانی په&zwnj;روه&zwnj;ردگارمان حه&zwnj;قیقه&zwnj;ت و ڕاسته&zwnj;قینه&zwnj;یان هێنابوو (که&zwnj;چی ئێمه بڕوامان پێنه&zwnj;کردن و به&zwnj;که&zwnj;ساسیه&zwnj;وه ده&zwnj;ڵێن): ئایا ئه&zwnj;وه که&zwnj;سێک نیه تکامان بۆ بکات؟ (به&zwnj;ڵکو خوا بمانبه&zwnj;خشێت)، یاخود بگه&zwnj;ڕێینه&zwnj;وه بۆ دنیاو کارو کرده&zwnj;وه&zwnj;ی چاک بکه&zwnj;ین نه&zwnj;ک ئه&zwnj;وه&zwnj;ی که ئه&zwnj;وسا ده&zwnj;مانکرد، به&zwnj;ڕاستی ئه&zwnj;وانه خۆیان دۆڕاندو هه&zwnj;رچیان هه&zwnj;ڵبه&zwnj;ستبوو و باوه&zwnj;ڕیان پێی هه&zwnj;بوو، لێیان ون بوو.';
$t[54] = 'بێگومان په&zwnj;روه&zwnj;ردگارتان (الله&zwnj;)یه که دروستکاری ئاسمانه&zwnj;کان و زه&zwnj;ویه له ماوه&zwnj;ی شه&zwnj;ش ڕۆژدا (مه&zwnj;گه&zwnj;ر هه&zwnj;ر خۆیشی بزانێت ئه&zwnj;و ڕۆژانه ماوه&zwnj;یان چه&zwnj;نده&zwnj;)، پاشان له&zwnj;سه&zwnj;ر عه&zwnj;رش و ته&zwnj;ختی فه&zwnj;رمانڕه&zwnj;وایی وه&zwnj;ستا (ئه&zwnj;ڵبه&zwnj;ته چۆنیه&zwnj;تیی وه&zwnj;ستان و ته&zwnj;ختی فه&zwnj;رمانڕه&zwnj;وایه&zwnj;تی و گه&zwnj;لێ شتی له&zwnj;و بابه&zwnj;تانه له&zwnj;راده&zwnj;ی بۆچوونی ئێمه به&zwnj;ده&zwnj;ره&zwnj;، ته&zwnj;فسیری ئه&zwnj;م ئایه&zwnj;ته&zwnj;یان له پێشه&zwnj;وا مالیک و ئه&zwnj;حمه&zwnj;د پرسیووه&zwnj;، له وه&zwnj;ڵامدا وتوویانه&zwnj;: وه&zwnj;ستانه&zwnj;که حه&zwnj;قیقه&zwnj;تی هه&zwnj;یه&zwnj;، چۆنیه&zwnj;تیه&zwnj;که&zwnj;ی شاراوه&zwnj;یه&zwnj;، پرسیار کردن ده&zwnj;رباره&zwnj;ی بیدعه&zwnj;یه&zwnj;، باوه&zwnj;ڕ بوون پێی واجبه&zwnj;)، به&zwnj;شه&zwnj;و، ڕۆژ داده&zwnj;پۆشێت؛ له&zwnj;کاتێکدا به&zwnj;شێنه&zwnj;یی عه&zwnj;وداڵه به&zwnj;دوایدا، خۆرو مانگ و ئه&zwnj;ستێره&zwnj;کان (هه&zwnj;ر هه&zwnj;موویان) به فه&zwnj;رمانی خوا که&zwnj;وتوونه&zwnj;ته گه&zwnj;ڕو له فه&zwnj;رمانی ده&zwnj;رناچن، ئاگادار بن و بزانن و تێفکرن که&zwnj;: هه&zwnj;رچی دروستکردن و بڕیاردانه هه&zwnj;ر بۆ ئه&zwnj;وه&zwnj;، (که&zwnj;واته ده&zwnj;با هه&zwnj;مووان فه&zwnj;رمانبه&zwnj;رداری ئه&zwnj;و بین و له فه&zwnj;رمانی ده&zwnj;رنه&zwnj;چین)، موباره&zwnj;ک و پیرۆزو گه&zwnj;وره&zwnj;یه زاتی (الله&zwnj;) په&zwnj;روه&zwnj;ردگاری جیهانیان.';
$t[55] = 'هاناو هاوار بکه&zwnj;ن له په&zwnj;روه&zwnj;ردگارتان به&zwnj;پاڕانه&zwnj;وه&zwnj;ی به&zwnj;کوڵ، به&zwnj;نهێنی (له&zwnj;کات و شوێنی له&zwnj;باردا) چونکه به&zwnj;ڕاستی ئه&zwnj;و زاته ده&zwnj;ست درێژکارانی خۆش ناوێت ( مه&zwnj;به&zwnj;ست ئه&zwnj;وانه&zwnj;ن که هاناو هاوار بۆ غه&zwnj;یری خوا ده&zwnj;به&zwnj;ن).';
$t[56] = '(خه&zwnj;ڵکینه&zwnj;) فه&zwnj;ساد و گوناه و تاوان ئه&zwnj;نجام مه&zwnj;ده&zwnj;ن له زه&zwnj;ویدا دوای ئه&zwnj;وه&zwnj;ی چاکه&zwnj;کاریی ڕه&zwnj;نگی گرتووه&zwnj;و (خه&zwnj;ڵکی خه&zwnj;ریکن خووی پێوه ده&zwnj;گرن)، به&zwnj;رده&zwnj;وام هاناو هاوار بۆ خوا به&zwnj;رن له ترسی سزای دۆزه&zwnj;خ و به&zwnj;ئومێدی به&zwnj;ده&zwnj;ستهێنانی به&zwnj;هه&zwnj;شت، به&zwnj;ڕاستی ڕه&zwnj;حمه&zwnj;تی خوا نزیکه له چاکه&zwnj;کاران و چاکه&zwnj;خوازانه&zwnj;وه&zwnj;.';
$t[57] = 'هه&zwnj;ر ئه&zwnj;و خوایه&zwnj;یه که بای شه&zwnj;ماڵ (هه&zwnj;ڵگری هه&zwnj;ڵمی ئاو) ده&zwnj;نێرێت له&zwnj;کاتێکدا مژده&zwnj;یه (بۆ خه&zwnj;ڵک) له&zwnj;نێوان دووده&zwnj;ستی ڕه&zwnj;حمه&zwnj;تی په&zwnj;روه&zwnj;ردگاردا، هه&zwnj;تا وای لێدێت که ئه&zwnj;و بایه هه&zwnj;وری سه&zwnj;نگین هه&zwnj;ڵده&zwnj;گرێت، ئه&zwnj;وسا ده&zwnj;ینێرین بۆ ناوچه&zwnj;و وڵاتێکی مردوو، جا به&zwnj;هۆی ئه&zwnj;و ئاوه&zwnj;وه هه&zwnj;رچی به&zwnj;روبوومه ده&zwnj;ری ده&zwnj;هێنین، جا هه&zwnj;ر ئابه&zwnj;و شێوه&zwnj;یه مردووانیش له&zwnj;ناخی زه&zwnj;وی ده&zwnj;رده&zwnj;هێنین و زیندوو ده&zwnj;که&zwnj;ینه&zwnj;وه&zwnj;، بۆ ئه&zwnj;وه&zwnj;ی یاداوه&zwnj;ری وه&zwnj;ربگرن.';
$t[58] = 'وڵاتی به&zwnj;پیت و چاک و ئاوه&zwnj;دان، دارو دره&zwnj;خت و ڕووه&zwnj;کی تێدا ده&zwnj;ڕوێت به&zwnj;فه&zwnj;رمانی په&zwnj;روه&zwnj;ردگاری، ئه&zwnj;وه&zwnj;ش که زۆنگ و پیس و بێ که&zwnj;ڵک بێت، شتی بێ سوود و بێ فه&zwnj;ڕ نه&zwnj;بێت، هیچی لێ په&zwnj;یدا نابێت، ئا به&zwnj;و شێوه&zwnj;یه به&zwnj;ڵگه&zwnj;و نیشانه&zwnj;ی جۆراوجۆر ده&zwnj;هێنینه&zwnj;وه بۆ که&zwnj;سانێک که دڵسۆزن و سوپاسی خوا ده&zwnj;که&zwnj;ن.';
$t[59] = 'سوێند به خوا به&zwnj;ڕاستی ئێمه - نوح - مان ڕه&zwnj;وانه کرد بۆ سه&zwnj;ر قه&zwnj;ومه&zwnj;که&zwnj;ی، جا ئه&zwnj;ویش پێی وتن: ئه&zwnj;ی گه&zwnj;ل و هۆزم ئێوه هه&zwnj;ر خوا بپه&zwnj;رستن، چونکه جگه له&zwnj;و زاته خوایه&zwnj;کی ترتان نیه&zwnj;، به&zwnj;ڕاستی من ده&zwnj;ترسم له&zwnj;رۆژێکی گه&zwnj;وره&zwnj;دا تووشی به&zwnj;ڵاو سزایه&zwnj;کی زۆر سه&zwnj;خت ببن.';
$t[60] = 'ده&zwnj;سته&zwnj;ی ده&zwnj;سه&zwnj;ڵاتداری قه&zwnj;ومه&zwnj;که&zwnj;ی وتیان: به&zwnj;ڕاستی ئێمه تۆ له&zwnj;نێو گومڕاییه&zwnj;کی ئاشکرادا ده&zwnj;بینین!!';
$t[61] = 'نوح وتی: ئه&zwnj;ی قه&zwnj;وم و هۆزم من هیچ گومڕاییه&zwnj;کم نیه به&zwnj;ڵکو من پێغه&zwnj;مبه&zwnj;رێکم و له لایه&zwnj;ن په&zwnj;روه&zwnj;ردگاری جیهانیانه&zwnj;وه ڕه&zwnj;وانه&zwnj;کراوم...';
$t[62] = 'په&zwnj;یامه&zwnj;کانی په&zwnj;روه&zwnj;ردگارمتان پێ ڕاده&zwnj;گه&zwnj;یه&zwnj;نم، من دڵسۆزیتان له&zwnj;گه&zwnj;ڵ ده&zwnj;که&zwnj;م، ئه&zwnj;وه&zwnj;ی من ده&zwnj;یزانم ده&zwnj;رباره&zwnj;ی گه&zwnj;وره&zwnj;یی و باڵاده&zwnj;ستی خوا، ئێوه نایزانن...';
$t[63] = 'ئایا ئێوه سه&zwnj;رسام بوون له&zwnj;هاتنی به&zwnj;رنامه و یادخه&zwnj;ره&zwnj;وه&zwnj;یه&zwnj;ک له&zwnj;لایه&zwnj;ن په&zwnj;روه&zwnj;ردگارتانه&zwnj;وه بۆ سه&zwnj;ر پیاوێکی (ڕاستگۆ) له خۆتان؟ تا بێدارتان بکاته&zwnj;وه&zwnj;و دینداری و خواناسی بکه&zwnj;ن، بۆ ئه&zwnj;وه&zwnj;ی سه&zwnj;رئه&zwnj;نجام ڕه&zwnj;حمتان پێ بکرێت.';
$t[64] = '(دوای ٩٥٠ ساڵ هه&zwnj;وڵ و کۆشش) هه&zwnj;ر به&zwnj;رنامه&zwnj;که&zwnj;یان به درۆزانی و بڕوایان پێ نه&zwnj;کرد، جا ئێمه&zwnj;ش خۆی و ئه&zwnj;و ئیماندارانه&zwnj;ی له&zwnj;گه&zwnj;ڵیدا بوون له که&zwnj;شتیه&zwnj;که&zwnj;دا ڕزگارمان کردن و ئه&zwnj;وانه&zwnj;ی بڕوایان نه&zwnj;هێنا به&zwnj;ئایه&zwnj;ت و فه&zwnj;رمانه&zwnj;کانمان نوقمی زریانه&zwnj;که&zwnj;مان کردن، چونکه به&zwnj;ڕاستی ئه&zwnj;وان گه&zwnj;لێکی کوێرو نابینا بوون (له ئاستی ڕاستیه&zwnj;کاندا).';
$t[65] = 'بۆ قه&zwnj;ومی (عاد)یش (هود)ی برایانمان نارد (هه&zwnj;روه&zwnj;کو نوح) وتی: ئه&zwnj;ی گه&zwnj;ل و عه&zwnj;شیره&zwnj;تم هه&zwnj;ر خوا بپه&zwnj;رستن چونکه جگه له&zwnj;و زاته خوایه&zwnj;کی ترتان نیه&zwnj;، ئایا ئه&zwnj;وه له خوا ناترسن؟!';
$t[66] = 'ئه&zwnj;و ده&zwnj;سه&zwnj;ڵاتدارانه&zwnj;ی که بێ بڕوا بوون له قه&zwnj;ومه&zwnj;که&zwnj;ی وتیان: به&zwnj;ڕاستی ئێمه تۆ له نێو نه&zwnj;فامیدا ده&zwnj;بینین ( تۆ ناگۆڕێیت و پێش ناکه&zwnj;ویت) و به&zwnj;ڕاستی ئێمه واده&zwnj;زانین تۆ یه&zwnj;کێکیت له درۆزنه&zwnj;کان.';
$t[67] = '(هود) فه&zwnj;رمووی: ئه&zwnj;ی قه&zwnj;وم و عه&zwnj;شیره&zwnj;تم من هیچ گێلی و نه&zwnj;فامیه&zwnj;کم پێوه نیه&zwnj;، به&zwnj;ڵکو من فرستاده&zwnj;یه&zwnj;کم له&zwnj;لایه&zwnj;ن په&zwnj;روه&zwnj;ردگاری هه&zwnj;موو جیهانیانه&zwnj;وه&zwnj;...';
$t[68] = 'په&zwnj;یامه&zwnj;کانی په&zwnj;روه&zwnj;ردگاری خۆمتان پێ ڕاده&zwnj;گه&zwnj;یه&zwnj;نم و من بۆ ئێوه دڵسۆزو ئه&zwnj;مینم.';
$t[69] = 'ئایا ئێوه سه&zwnj;رسام بوون له هاتنی به&zwnj;رنامه و یادخه&zwnj;ره&zwnj;وه&zwnj;یه&zwnj;ک له&zwnj;لایه&zwnj;ن په&zwnj;روه&zwnj;ردگارتانه&zwnj;وه بۆ سه&zwnj;ر پیاوێکی (ڕاستگۆ) له خۆتان؟ تا بێدارتانبکاته&zwnj;وه&zwnj;، جا یادی ئه&zwnj;و کاته بکه&zwnj;نه&zwnj;وه که خوا ئێوه&zwnj;ی کرده جێنشین له دوای قه&zwnj;ومی نوح، له ڕووی جه&zwnj;سته&zwnj;وه زیاد له&zwnj;وان گه&zwnj;وره و تێرو ته&zwnj;واو به&zwnj;دیهێنان که&zwnj;واته یادی نازو نیعمه&zwnj;ته&zwnj;کانی خوا بکه&zwnj;ن بۆ ئه&zwnj;وه&zwnj;ی سه&zwnj;رفراز بن.';
$t[70] = '(که&zwnj;چی قه&zwnj;ومی ناله&zwnj;بار له وه&zwnj;ڵامیدا) وتیان: ئایا تۆ هاتویته لامان بۆ ئه&zwnj;وه&zwnj;ی ته&zwnj;نها خوا بپه&zwnj;رستین و واز له&zwnj;و په&zwnj;رستراوانه بهێنین (که ساڵه&zwnj;هایه&zwnj;) باو و باپیرانمان ده&zwnj;یانپه&zwnj;رستن؟! (به گاڵته و بێ باوه&zwnj;ڕیه&zwnj;وه وتیان): که&zwnj;وابوو ئه&zwnj;گه&zwnj;ر تۆ له ڕاستگۆیانیت ئه&zwnj;و به&zwnj;ڵێنانه&zwnj;ی که پێمان ده&zwnj;ده&zwnj;یت بۆمان پێش بهێنه&zwnj;!!';
$t[71] = 'هود فه&zwnj;رمووی: ئیتر ئه&zwnj;وه به&zwnj;ڵاو ناخۆشی و خه&zwnj;شمی خوا که&zwnj;وته سه&zwnj;رتان، ئایا ئه&zwnj;وه ڕاسته ئێوه موجاده&zwnj;له&zwnj;و ده&zwnj;مه ده&zwnj;مێم له&zwnj;گه&zwnj;ڵ ده&zwnj;که&zwnj;ن له&zwnj;سه&zwnj;ر کۆمه&zwnj;ڵێک بت و په&zwnj;یکه&zwnj;ر که خۆتان و باوو باپیرانتان ناوتان ناون؟ بێگومان هیچ په&zwnj;یوه&zwnj;ندیه&zwnj;کیان به خواوه نیه&zwnj;، هیچ به&zwnj;ڵگه&zwnj;یه&zwnj;ک له&zwnj;لایه&zwnj;ن خواوه دانه&zwnj;به&zwnj;زیوه که ئه&zwnj;وانه بچوکترین کاریان به&zwnj;ده&zwnj;ست بێت، ئێوه چاوه&zwnj;ڕێی بن، منیش له&zwnj;گه&zwnj;ڵتاندا له چاوه&zwnj;ڕوانانم.';
$t[72] = 'ئینجا (دوای دابه&zwnj;زینی خه&zwnj;شمی خوایی) هودو ئه&zwnj;و ئیماندارانه&zwnj;ی له&zwnj;گه&zwnj;ڵی بوون، به&zwnj;ره&zwnj;حمه&zwnj;تێکی تایبه&zwnj;تی خۆمان، ڕزگارمان کردن، ئه&zwnj;وانه&zwnj;ش که ئایه&zwnj;ت و هه&zwnj;ڕه&zwnj;شه&zwnj;کانی ئێمه&zwnj;یان به&zwnj;درۆ ده&zwnj;زانی دوابڕاومان کردن و ئه&zwnj;وانه&zwnj;ش باوه&zwnj;ڕدار نه&zwnj;بوون به مژده&zwnj;کانمان.';
$t[73] = 'بۆ سه&zwnj;ر قه&zwnj;ومی (ثمود)یش (صاڵح)ی برایانمان نارد (ئه&zwnj;ویش پێی وتن): ئه&zwnj;ی گه&zwnj;له&zwnj;که&zwnj;م خوا بپه&zwnj;رستن چونکه جگه له&zwnj;و خوایه&zwnj;کی ترتان نیه&zwnj;، ئه&zwnj;وه&zwnj;تا به&zwnj;ڕاستی موعجیزه&zwnj;ی ئاشکراتان له&zwnj;لایه&zwnj;ن په&zwnj;روه&zwnj;ردگارتانه&zwnj;وه بۆ هاتووه&zwnj;، ئه&zwnj;مه وشترێکه له&zwnj;لایه&zwnj;ن خواوه به&zwnj;تایبه&zwnj;ت بۆ ئێوه&zwnj;ی به&zwnj;دیهێناوه (له&zwnj;سه&zwnj;ر داخوازیی خۆتان) که موعجیزه&zwnj;یه&zwnj;کی ئاشکرایه&zwnj;، وازی لێ بهێنن با بله&zwnj;وه&zwnj;ڕێت له زه&zwnj;وی خواداو ده&zwnj;ست درێژی مه&zwnj;که&zwnj;نه سه&zwnj;رو ئازاری مه&zwnj;ده&zwnj;ن (چونکه هه&zwnj;ر ئازارتاندا) سزاو به&zwnj;ڵایه&zwnj;کی به&zwnj;ئێش و ئازار داوێن گیرتان ده&zwnj;بێت.';
$t[74] = 'یادی ئه&zwnj;و کاته&zwnj;ش بکه&zwnj;نه&zwnj;وه که خوا ئێوه&zwnj;ی کرده جێنشین له دوای قه&zwnj;ومی عاد، ژیانی له&zwnj;سه&zwnj;ر زه&zwnj;ویدا بۆ ئاسان کردن، له ده&zwnj;شته&zwnj;کاندا کۆشک و ته&zwnj;لار دروست ده&zwnj;که&zwnj;ن، له شاخ و کێوه&zwnj;کانیشدا ژوور داده&zwnj;تاشن، که&zwnj;واته یادی نازو نیعمه&zwnj;ته&zwnj;کانی خوا بکه&zwnj;ن و تۆوی خراپه و فه&zwnj;سادو گوناه له&zwnj;سه&zwnj;ر زه&zwnj;ویدا مه&zwnj;چێنن.';
$t[75] = '(دوای ئه&zwnj;و یادخستنه&zwnj;وانه&zwnj;ی صاڵح پێغه&zwnj;مبه&zwnj;ر) ده&zwnj;سته&zwnj;ی ده&zwnj;سه&zwnj;ڵاتداران، ئه&zwnj;وانه&zwnj;ی لووت به&zwnj;رزیان کرد له قه&zwnj;ومه&zwnj;که&zwnj;ی وتیان به&zwnj;وانه&zwnj;ی که چه&zwnj;وسێنراونه&zwnj;ته&zwnj;وه&zwnj;، له&zwnj;وانه&zwnj;یان که باوه&zwnj;ڕیان هێناوه&zwnj;: باشه ئێوه ده&zwnj;زانن که صاڵح له&zwnj;لایه&zwnj;ن په&zwnj;روه&zwnj;ردگاریه&zwnj;وه ڕه&zwnj;وانه کراوه&zwnj;؟! ئیمانداران وتیان: به دڵنیاییه&zwnj;وه ئێمه باوه&zwnj;ڕدارین به&zwnj;و په&zwnj;یامه&zwnj;ی که به&zwnj;ودا ڕه&zwnj;وانه کراوه&zwnj;.';
$t[76] = '(به&zwnj;ڵام) ئه&zwnj;وانه&zwnj;ی که خۆیان به&zwnj;زل و گه&zwnj;وره ده&zwnj;زانی وتیان: به&zwnj;ڕاستی ئێمه بێ بڕواین به&zwnj;وه&zwnj;ی ئێوه بڕواتان پێ هێناوه&zwnj;!';
$t[77] = 'یه&zwnj;کسه&zwnj;ر (چوون) وشتره&zwnj;که&zwnj;یان سه&zwnj;ربڕی و له فه&zwnj;رمانی په&zwnj;روه&zwnj;ردگاریان یاخی بوون و وتیان: ئه&zwnj;ی صاڵح ئه&zwnj;گه&zwnj;ر تۆ له پێغه&zwnj;مبه&zwnj;رانیت ئاده&zwnj;ی ئه&zwnj;و هه&zwnj;ڕه&zwnj;شانه&zwnj;ی که لێمان ده&zwnj;یکه&zwnj;یت بۆمان پێش بهێنه&zwnj;.';
$t[78] = 'ئینجا یه&zwnj;کسه&zwnj;ر (دوای سه&zwnj;ربڕینی وشتره&zwnj;که&zwnj;) بوومه&zwnj;له&zwnj;رزه&zwnj;یه&zwnj;ک و ده&zwnj;نگێکی سامناک گرتنی و پێچانیه&zwnj;وه و دوا به دوای ئه&zwnj;و هه&zwnj;موو له ماڵه&zwnj;کانیاندا به&zwnj;چۆکدا هاتن و هێزی ئه&zwnj;ژنۆیان شکاو تیاچوون.';
$t[79] = '(جا صاڵح پێغه&zwnj;مبه&zwnj;ر، پشتی لێهه&zwnj;ڵکردن) و وتی: ئه&zwnj;ی قه&zwnj;وم و عه&zwnj;شیره&zwnj;تم سوێند به خوا به&zwnj;ڕاستی په&zwnj;یامی په&zwnj;روه&zwnj;ردگارم به&zwnj;چاکی پێڕاگه&zwnj;یاندن و دڵسۆزیم له&zwnj;گه&zwnj;ڵ کردن، به&zwnj;ڵام (به&zwnj;داخه&zwnj;وه&zwnj;) ئێوه دڵسۆزانتان خۆش ناوێت.';
$t[80] = 'یادی (لوت پێغه&zwnj;مبه&zwnj;ریش) بکه کاتێک به قه&zwnj;ومه&zwnj;که&zwnj;ی وت: چۆن ئێوه گوناهو تاوانێکی وا ده&zwnj;که&zwnj;ن که تا ئێستا هیچ که&zwnj;س پێش ئێوه له جیهاندا گوناهو تاوانی وای نه&zwnj;کردوه&zwnj;؟!';
$t[81] = 'چونکه به&zwnj;ڕاستی ئێوه ئاره&zwnj;زووی (جنسی) خۆتان له&zwnj;گه&zwnj;ڵ پیاواندا ئه&zwnj;نجام ده&zwnj;ده&zwnj;ن له&zwnj;با&zwnj;تی ژنانی هاوسه&zwnj;رتان، نه&zwnj;خێر ئێوه قه&zwnj;ومێکی ناپوخت و سنوور به&zwnj;زێن و زیاده&zwnj;ڕه&zwnj;ون.';
$t[82] = 'وه&zwnj;ڵامی قه&zwnj;ومه ناله&zwnj;باره&zwnj;که&zwnj;ی ته&zwnj;نها ئه&zwnj;وه&zwnj;بوو که وتیان: ئه&zwnj;وانه شار به&zwnj;ده&zwnj;رکه&zwnj;ن چونکه ئه&zwnj;وانه که&zwnj;سانێکن خۆیان پاک ڕاده&zwnj;گرن (داوێن پاکن و وه&zwnj;ک ئێمه ناکه&zwnj;ن)!!';
$t[83] = 'ئێمه&zwnj;ش لوت و خانه&zwnj;واده&zwnj;که&zwnj;یمان ڕزگار کرد، جگه له هاوسه&zwnj;ره&zwnj;که&zwnj;ی، چونکه ئه&zwnj;ویش له&zwnj;و که&zwnj;سانه بوو که تیاچوون (له&zwnj;به&zwnj;ر ئه&zwnj;وه&zwnj;ی باوه&zwnj;ڕی به لوت پێغه&zwnj;مبه&zwnj;ر نه&zwnj;بوو).';
$t[84] = 'ئه&zwnj;وسا (به&zwnj;ردمان) به&zwnj;سه&zwnj;رداباراندن، جا ته&zwnj;ماشا بکه&zwnj;و سه&zwnj;رنج بده&zwnj;و په&zwnj;ند وه&zwnj;رگره&zwnj;و بزانه&zwnj;، سه&zwnj;رئه&zwnj;نجامی تاوانباران وبێ ڕه&zwnj;وشتان چۆن بوو.';
$t[85] = 'بۆ هۆزی مه&zwnj;دیه&zwnj;نیش (شعیب)ی برایانمان نارد، ئه&zwnj;ویش پێی وتن: ئه&zwnj;ی قه&zwnj;وم و هۆزو گه&zwnj;لم ته&zwnj;نها خوا بپه&zwnj;رستن، چونکه جگه له&zwnj;و زاته خوایه&zwnj;کی ترتان نیه&zwnj;، ئه&zwnj;وه&zwnj;ته به&zwnj;ڕاستی به&zwnj;ڵگه&zwnj;و نیشانه&zwnj;ی ڕوونتان له&zwnj;لایه&zwnj;ن په&zwnj;روه&zwnj;ردگارتانه&zwnj;وه بۆ هاتووه&zwnj;، که&zwnj;وابوو له کێشانه و پێوانه&zwnj;دا (فێڵ و ته&zwnj;ڵه&zwnj;که مه&zwnj;که&zwnj;ن) و مافی خۆی بده&zwnj;نێ، شتی خه&zwnj;ڵکی سووک مه&zwnj;که&zwnj;ن و بێ نرخ ته&zwnj;ماشای مه&zwnj;که&zwnj;ن، تۆوی فه&zwnj;ساد و تاوانباری و گوناه له زه&zwnj;ویدا مه&zwnj;چێنن دوای چاکساز بوونی، ئه&zwnj;وانه چاکتره بۆتان ئه&zwnj;گه&zwnj;ر ئێوه باوه&zwnj;ڕدارن.';
$t[86] = 'هه&zwnj;روه&zwnj;ها له&zwnj;سه&zwnj;ر هیچ ڕێگه&zwnj;یه&zwnj;ک دامه&zwnj;نیشن هه&zwnj;ڕه&zwnj;شه له خه&zwnj;ڵکی بکه&zwnj;ن و ئه&zwnj;وانه&zwnj;ی که باوه&zwnj;ڕیان هێناوه به&zwnj;خوا قه&zwnj;ده&zwnj;غه&zwnj;یان بکه&zwnj;ن له&zwnj;گرتنی ڕێبازی خواو بتانه&zwnj;وێت ڕێبازی خوا به&zwnj;لاری و له&zwnj;وێری پیشان بدرێت و پیاده بکه&zwnj;ن، یادی ئه&zwnj;و کاته&zwnj;ش بکه&zwnj;ن که که&zwnj;م و لاواز بوون، ئینجا خوا زۆرو به&zwnj;هێزی کردن، ته&zwnj;ماشا بکه&zwnj;ن و سه&zwnj;رنج بده&zwnj;ن و بزانن چۆن بوو سه&zwnj;ره&zwnj;ئه&zwnj;نجامی ئه&zwnj;وانه&zwnj;ی چێنه&zwnj;ری تۆوی خراپه&zwnj;ن.';
$t[87] = 'خۆ ئه&zwnj;گه&zwnj;ر ده&zwnj;سته&zwnj;یه&zwnj;ک له ئێوه باوه&zwnj;ڕی هێنابێت به&zwnj;و به&zwnj;رنامه&zwnj;یه&zwnj;ی که پێی ڕه&zwnj;وانه کراوم و ئه&zwnj;گه&zwnj;ر ده&zwnj;سته&zwnj;یه&zwnj;کیش باوه&zwnj;ڕیان نه&zwnj;هێنا بوو، ئه&zwnj;وه خۆتان بگرن و چاوه&zwnj;ڕێ بن هه&zwnj;تا خوا خۆی داوه&zwnj;ری ده&zwnj;کات له نێوانمانداو هه&zwnj;ر ئه&zwnj;و زاته خۆی چاکترینی دادوه&zwnj;رانه&zwnj;.';
$t[88] = 'ده&zwnj;سته&zwnj;ی ده&zwnj;سه&zwnj;ڵاتداران، ئه&zwnj;وانه&zwnj;ی لووت به&zwnj;رزیان کرد له قه&zwnj;ومه&zwnj;که&zwnj;ی وتیان: ئه&zwnj;ی شوعه&zwnj;یب سوێند به خوا خۆت و ئه&zwnj;وانه&zwnj;ی باوه&zwnj;ڕیان هێناوه له گه&zwnj;ڵتدا له شاره&zwnj;که&zwnj;ماندا ده&zwnj;رتان ده&zwnj;که&zwnj;ین، یان به ئاره&zwnj;زووی خۆتان بگه&zwnj;ڕێنه&zwnj;وه نێو ئاینه&zwnj;که&zwnj;مان، ئیمانداران وتیان: ئایا ئه&zwnj;گه&zwnj;ر حه&zwnj;زیشی لێ نه&zwnj;که&zwnj;ین به&zwnj;دڵیشمان نه&zwnj;بێت؟!';
$t[89] = 'به&zwnj;ڕاستی ئێمه درۆمان به&zwnj;ناوی خواوه هه&zwnj;ڵبه&zwnj;ستووه&zwnj;، ئه&zwnj;گه&zwnj;ر په&zwnj;شیمان ببینه&zwnj;وه و بگه&zwnj;ڕێینه&zwnj;وه ناو ئاینه&zwnj;که&zwnj;ی ئێوه&zwnj;، دوای ئه&zwnj;وه&zwnj;ی که خوا له ده&zwnj;ست (ئه&zwnj;و خورافاته&zwnj;) ڕزگاری کردین، ئێمه بۆمان نیه بگه&zwnj;ڕێینه&zwnj;وه بۆ ناو ئاینی ئێوه مه&zwnj;گه&zwnj;ر ویستی خوای په&zwnj;روه&zwnj;ردگارمانی له&zwnj;سه&zwnj;ر بێت، زانست و زانیاری په&zwnj;روه&zwnj;ردگارمان فراوانه&zwnj;و هه&zwnj;موو شتێکی گرتۆته&zwnj;وه&zwnj;، هه&zwnj;ر به&zwnj;خوا پشت و په&zwnj;نامان به&zwnj;ستووه&zwnj;، په&zwnj;روه&zwnj;ردگارا خۆت داوه&zwnj;ری بکه له&zwnj;سه&zwnj;ر بنچینه&zwnj;ی حه&zwnj;ق وڕاستی له&zwnj;نێوان ئێمه و قه&zwnj;وم و گه&zwnj;لماندا، چونکه تۆ چاکترینی دادوه&zwnj;رانی.';
$t[90] = 'ده&zwnj;سته&zwnj;ی ده&zwnj;سه&zwnj;ڵاتداران له گه&zwnj;لی شوعه&zwnj;یب ئه&zwnj;وانه&zwnj;ی که بێ باوه&zwnj;ڕ بوون وتیان: سوێند به خوا ئه&zwnj;گه&zwnj;ر ئێوه شوێنی شوعه&zwnj;یب بکه&zwnj;ون، به&zwnj;ڕاستی ئه&zwnj;و کاته ئێوه له زه&zwnj;ره&zwnj;رمه&zwnj;ندانن.';
$t[91] = 'ئینجا یه&zwnj;کسه&zwnj;ر بوومه&zwnj;له&zwnj;رزه&zwnj;یه&zwnj;ک و ده&zwnj;نگێکی سامناک گرتنی و پێچانیه&zwnj;وه و دوابه&zwnj;دوای ئه&zwnj;وه هه&zwnj;موو له ماڵه&zwnj;کانیاندا به چۆکدا هاتن و هێزی ئه&zwnj;ژنۆیان شکاو تیاچوون.';
$t[92] = 'ئه&zwnj;وانه&zwnj;ی که به&zwnj;رنامه&zwnj;ی شوعه&zwnj;یبیان به&zwnj;درۆزانی و باوه&zwnj;ڕیان پێی نه&zwnj;بوو (له&zwnj;ناو بران) هه&zwnj;روه&zwnj;ک له&zwnj;و ناوه&zwnj;دا نه&zwnj;بووبن و نه&zwnj;ژیابن وابوو، ئه&zwnj;وانه&zwnj;ی که شوعه&zwnj;یبیان به&zwnj;درۆزانی و باوه&zwnj;ڕیان پێی نه&zwnj;بوو ته&zwnj;نها ئه&zwnj;وان زه&zwnj;ره&zwnj;رمه&zwnj;ند و ماڵوێران بوون.';
$t[93] = 'جا شوعه&zwnj;یب پێغه&zwnj;مبه&zwnj;ر، پشتی لێهه&zwnj;ڵکردن و وتی: ئه&zwnj;ی قه&zwnj;وم و عه&zwnj;شیره&zwnj;تم سوێند به خوا به&zwnj;ڕاستی په&zwnj;یامه&zwnj;کانی په&zwnj;روه&zwnj;ردگارم به&zwnj;چاکی پێڕاگه&zwnj;یاندن و دڵسۆزیم له&zwnj;گه&zwnj;ڵ کردن، ئیتر من چۆن خه&zwnj;م بخۆم بۆ قه&zwnj;ومێک که بێ باوه&zwnj;ڕن.';
$t[94] = 'له هیچ شارو وڵاتێکدا هیچ پێغه&zwnj;مبه&zwnj;رێکمان نه&zwnj;ناردوه ( که خه&zwnj;ڵکه&zwnj;که&zwnj;ی بدرۆیان زانی بێت و به&zwnj;گوێیان نه&zwnj;کردبێت) مه&zwnj;گه&zwnj;ر خه&zwnj;ڵکی ئه&zwnj;و شوێنه&zwnj;مان به&zwnj;ته&zwnj;نگانه و نه&zwnj;خۆشی گیرۆده کردوه&zwnj;، بۆ ئه&zwnj;وه&zwnj;ی (دابچڵه&zwnj;کێن و تێفکرن) و ده&zwnj;ست بکه&zwnj;ن به&zwnj;نزاو پاڕانه&zwnj;وه&zwnj;و لاڵانه&zwnj;وه&zwnj;.';
$t[95] = 'پاشان شوێنی ته&zwnj;نگانه&zwnj;و ناخۆشیه&zwnj;که&zwnj;مان گۆڕی به&zwnj;شوێنی خۆشی و کامه&zwnj;رانی، هه&zwnj;تا به&zwnj;ته&zwnj;واوی له هه&zwnj;موو ڕویه&zwnj;که&zwnj;وه زیادیان کردو که&zwnj;وتنه خۆشیه&zwnj;وه و (وایان لێهات که خۆشی و ناخۆشی به&zwnj;تاقیکردنه&zwnj;وه دانه&zwnj;نێن) جا وتیان: به&zwnj;ڕاستی ناخۆشی و خۆشی تووشی باوو باپیرانیشمان بووه (ئه&zwnj;مه شتێکی ئاساییه&zwnj;!!) ئێمه&zwnj;ش کتوپڕ گرتمانن له&zwnj;کاتێکدا ئه&zwnj;وان هه&zwnj;ستیان به خۆیان نه&zwnj;کرد له&zwnj;ناومان بردن و ته&zwnj;خت و تاراجیانمان تێکدا.';
$t[96] = 'خۆ ئه&zwnj;گه&zwnj;ر خه&zwnj;ڵکی شاره&zwnj;کان به&zwnj;ڕاستی باوه&zwnj;ڕیان بهێنایه&zwnj;و خۆیان بپاڕاستایه&zwnj;، ئه&zwnj;وا بێگومان ئێمه ده&zwnj;رگای فه&zwnj;ڕو به&zwnj;ره&zwnj;که&zwnj;تی ئاسمان و زه&zwnj;وییان له&zwnj;سه&zwnj;ر ده&zwnj;کردنه&zwnj;وه&zwnj;، به&zwnj;ڵام ئه&zwnj;وان که په&zwnj;یام و پێغه&zwnj;مبه&zwnj;رانی ئێمه&zwnj;یان به&zwnj;درۆزانی، جا ئێمه&zwnj;ش به&zwnj;هۆی ئه&zwnj;و کاروکرده&zwnj;وانه&zwnj;ی که ده&zwnj;یانکرد گرتمانن و له&zwnj;ناومان بردن.';
$t[97] = 'جا ئایا خه&zwnj;ڵکی شاره&zwnj;کان له&zwnj;وه دڵنیان و ناترسن که تۆڵه&zwnj;ی ئێمه بێت بۆیان له شه&zwnj;ودا له کاتێکدا که ئه&zwnj;وان نوستوون؟!';
$t[98] = 'ئایا خه&zwnj;ڵکی شاره&zwnj;کان دڵنیاشن و ناترسن له&zwnj;وه&zwnj;ی که تۆڵه&zwnj;ی ئێمه بێت بۆیان له چێشته&zwnj;نگاودا، له&zwnj;کاتێکدا ئه&zwnj;وان سه&zwnj;رگه&zwnj;رمی یاری و گه&zwnj;مه&zwnj;ن.';
$t[99] = 'جا ئایا دڵنیاو بێباک بوون له نه&zwnj;خشه&zwnj;و پلانی خوا (له&zwnj;سه&zwnj;ر ئاستی تاک و کۆمه&zwnj;ڵ یان له&zwnj;سه&zwnj;ر ئاستی حوکمه&zwnj;ت و گه&zwnj;لان)؟! جا که&zwnj;س بێباک نابێت له نه&zwnj;خشه و پلانی خوا جگه له قه&zwnj;ومی خه&zwnj;ساره&zwnj;تمه&zwnj;ندان نه&zwnj;بێت.';
$t[100] = 'ئایا ده&zwnj;ریشنه&zwnj;که&zwnj;وت بۆ ئه&zwnj;وانه&zwnj;ی دێن به&zwnj;دوای ده&zwnj;سته&zwnj;و چینه&zwnj;کانی پێش خۆیان پاش له&zwnj;ناوچوونی ئه&zwnj;وانه&zwnj;ی که تیایدا نیشته&zwnj;جێ بوون که ئه&zwnj;گه&zwnj;ر بمانویستایه&zwnj;، ئه&zwnj;وه به&zwnj;هۆی گوناهه&zwnj;کانیانه&zwnj;وه تووشمان ده&zwnj;کردن (وه&zwnj;ک چۆن خه&zwnj;ڵکانی پێش خۆیانمان له&zwnj;ناو برد)و مۆرمان ده&zwnj;نا به&zwnj;سه&zwnj;ر دڵه&zwnj;کانیاندا، جا ئه&zwnj;وان نابیستن.';
$t[101] = '(ئه&zwnj;ی محمد(صلى الله عليه وسلم) ئه&zwnj;و شارو ناوچانه هه&zwnj;ندێک له هه&zwnj;واڵ و به&zwnj;سه&zwnj;رهاته&zwnj;کانیان بۆ ده&zwnj;گێڕینه&zwnj;وه&zwnj;، سوێند به&zwnj;خوا بێگومان پێغه&zwnj;مبه&zwnj;رانیان بۆ هات، هاوڕێ له&zwnj;گه&zwnj;ڵ چه&zwnj;نده&zwnj;ها موعجیزه&zwnj;دا، ئیتر وه&zwnj;نه&zwnj;بێت ئه&zwnj;وانه باوه&zwnj;ڕیان هێنابێت به&zwnj;وه&zwnj;ی که پێشتر بڕوایان پێی نه&zwnj;بوو، ئا به&zwnj;و شێوه&zwnj;یه خوا مۆر ده&zwnj;نێت به&zwnj;سه&zwnj;ر دڵی بێ باوه&zwnj;ڕاندا.';
$t[102] = 'به&zwnj;شی زۆری ئه&zwnj;و خه&zwnj;ڵکه سه&zwnj;رگه&zwnj;ردانه&zwnj;مان ده&zwnj;ست نه&zwnj;که&zwnj;وت که په&zwnj;یمان شکێن نه&zwnj;بن و به&zwnj;ڕاستی زۆربه&zwnj;ی ئه&zwnj;وانه&zwnj;مان به&zwnj;ده&zwnj;رچوو له ئاینی خوا ده&zwnj;ستکه&zwnj;وت.';
$t[103] = 'پاشان له دوای ئه&zwnj;و (پێغه&zwnj;مبه&zwnj;رانه&zwnj;ی که باسکران) موسامان هاوڕێ له&zwnj;گه&zwnj;ڵ موعجیزه&zwnj;ی جۆراو جۆردا بۆ سه&zwnj;ر فیرعه&zwnj;ون و دارو ده&zwnj;سته&zwnj;که&zwnj;ی ڕه&zwnj;وانه&zwnj;کرد، که&zwnj;چی سته&zwnj;میان له&zwnj;خۆیان کردو پێی بێ باوه&zwnj;ڕ بوون، جا ته&zwnj;ماشا بکه و ببینه بزانه چۆن بوو سه&zwnj;رئه&zwnj;نجامی ئه&zwnj;وانه&zwnj;ی که چێنه&zwnj;ری تۆوی خراپه بوون.';
$t[104] = 'موسا وتی: ئه&zwnj;ی فیرعه&zwnj;ون چاک بزانه و دڵنیابه که من ڕه&zwnj;وانه کراوو فرستاده&zwnj;م له&zwnj;لایه&zwnj;ن په&zwnj;روه&zwnj;ردگاری جیهانیانه&zwnj;وه&zwnj;...';
$t[105] = 'هه&zwnj;میشه سوورم له&zwnj;سه&zwnj;ر ئه&zwnj;وه&zwnj;ی هیچ شتێک نه&zwnj;ڵێم به&zwnj;ناوی خواوه جگه له&zwnj;وه&zwnj;ی حه&zwnj;ق و ڕاست نه&zwnj;بێت، به&zwnj;ڕاستی به&zwnj;به&zwnj;ڵگه&zwnj;یه&zwnj;کی ڕوونه&zwnj;وه له&zwnj;لایه&zwnj;ن په&zwnj;روه&zwnj;ردگاره&zwnj;وه هاتووم بۆ لاتان: که&zwnj;واته (ئه&zwnj;ی فیرعه&zwnj;ون) داواکارم نه&zwnj;وه&zwnj;ی ئیسرائیل ئازاد بکه&zwnj;یت، با له&zwnj;گه&zwnj;ڵ مندا بن و (ده&zwnj;ست مه&zwnj;هێنه ڕێگه&zwnj;یان).';
$t[106] = '(فیرعه&zwnj;ون بڕوای نه&zwnj;کرد) و وتی: ئه&zwnj;گه&zwnj;ر به&zwnj;ڵگه&zwnj;یه&zwnj;کت هێناوه&zwnj;، ده بیخه&zwnj;ره ڕوو، ئه&zwnj;گه&zwnj;ر تۆ له ڕاستگۆیانیت.';
$t[107] = 'ئینجا موسا دارعه&zwnj;ساکه&zwnj;ی فڕێداو ده&zwnj;ستبه&zwnj;جێ بوو به&zwnj;مارێکی گه&zwnj;وره و ئاشکرا.';
$t[108] = 'ده&zwnj;ستیشی (خسته بن باڵی)وده&zwnj;ری هێنایه&zwnj;وه خێرابوو به (کافوری) سپی و بریقه&zwnj;ی ده&zwnj;دایه&zwnj;وه بۆ ته&zwnj;ماشاکه&zwnj;ران.';
$t[109] = 'هه&zwnj;ندێک له ده&zwnj;سه&zwnj;ڵاتدارانی دارو ده&zwnj;سته&zwnj;ی فیرعه&zwnj;ون وتیان: به&zwnj;ڕاستی ئه&zwnj;مه جادووگه&zwnj;رێکی زۆر زانایه&zwnj;!!';
$t[110] = '(به&zwnj;م شتانه&zwnj;) ده&zwnj;یه&zwnj;وێت له&zwnj;وڵات و نیشتمانتان ده&zwnj;رتانپه&zwnj;ڕێنێت، جا چ فه&zwnj;رمانێک ده&zwnj;ده&zwnj;ن؟!';
$t[111] = 'وتیان: جارێ وازی لێبهێنه له&zwnj;گه&zwnj;ڵ براکه&zwnj;یدا و چه&zwnj;ند پیاوێک بنێره له شاره&zwnj;کاندا تا هه&zwnj;موو جادووگه&zwnj;ران کۆ بکه&zwnj;نه&zwnj;وه&zwnj;...';
$t[112] = 'هه&zwnj;وڵ بده&zwnj;ن هه&zwnj;موو جادووگه&zwnj;رێکی زاناو شاره&zwnj;زات بۆ په&zwnj;یدا بکه&zwnj;ن.';
$t[113] = '(دوای چه&zwnj;ند ڕۆژێک) جادووگه&zwnj;ران هاتن بۆ لای فیرعه&zwnj;ون وتیان: پاداشتێک، شاباشێکمان ده&zwnj;ست ده&zwnj;که&zwnj;وێت ئه&zwnj;گه&zwnj;ر ئێمه (له&zwnj;م ململانێیه&zwnj;دا) بردمانه&zwnj;وه&zwnj;؟!';
$t[114] = '(فیرعه&zwnj;ون) وتی: به&zwnj;ڵێ (ئه&zwnj;ی چۆن) ئێوه له نزیکان و به&zwnj;ڕێزان ده&zwnj;بن.';
$t[115] = 'ئه&zwnj;وسا جادووگه&zwnj;ران وتیان: ئه&zwnj;ی موسا، یان ئه&zwnj;وه&zwnj;یه تۆ ده&zwnj;ست به&zwnj;کار ده&zwnj;بیت و دایده&zwnj;به&zwnj;زێنیت یان ئێمه ده&zwnj;ستبه&zwnj;کار ده&zwnj;بین هه&zwnj;میشه دایده&zwnj;به&zwnj;زێنین.';
$t[116] = 'موسا وتی: ده ئێوه ده&zwnj;ست به&zwnj;کاربن و دایبه&zwnj;زێنن، جا کاتێک دایانبه&zwnj;زاند، جادوویان له چاوی خه&zwnj;ڵکی کردو ویستیان بیانترسێن و جادوویه&zwnj;کی زۆر گه&zwnj;وره&zwnj;یان پێش هێناو به&zwnj;رپاکرد.';
$t[117] = 'ئێمه&zwnj;ش نیگامان نارد بۆ موسا که دارعه&zwnj;ساکه&zwnj;ت فڕێ بده&zwnj;، جا ده&zwnj;ستبه&zwnj;جێ (هه&zwnj;ر که فڕێیدا) ئه&zwnj;و فڕو فێڵه&zwnj;ی که ده&zwnj;یانکرد هه&zwnj;مووی هه&zwnj;ڵلووشی.';
$t[118] = 'ئه&zwnj;وسا ئیتر حه&zwnj;ق چه&zwnj;سپاو ده&zwnj;رکه&zwnj;وت و پووچه&zwnj;ڵیش بوه&zwnj;وه ئه&zwnj;وه&zwnj;ی ئه&zwnj;وان ده&zwnj;یانکرد.';
$t[119] = 'ئیتر له&zwnj;و گۆڕه&zwnj;پانه&zwnj;دا (فیرعه&zwnj;ون و داروده&zwnj;سته&zwnj;که&zwnj;ی) شکستیان پێهێنراو به&zwnj;سه&zwnj;ر شۆڕیی و شه&zwnj;رمه&zwnj;زاری گه&zwnj;ڕانه&zwnj;وه&zwnj;.';
$t[120] = '(کاتێک دڵنیابوون ئه&zwnj;وه موعجیزه&zwnj;یه&zwnj;و له&zwnj;ده&zwnj;سه&zwnj;ڵاتی ئاده&zwnj;میزادا نیه&zwnj;) جادووگه&zwnj;ران خران به&zwnj;روودا سوژده&zwnj;به&zwnj;ر بوون بۆ خوا.';
$t[121] = 'جادووگه&zwnj;ران وتیان: ئێمه باوه&zwnj;ڕێکی پته&zwnj;ومان هێناوه به په&zwnj;روه&zwnj;ردگاری جیهانیان...';
$t[122] = 'که په&zwnj;روه&zwnj;ردگاری موسا و هاروونه&zwnj;.';
$t[123] = 'فیرعه&zwnj;ون وتی: ئایا باوه&zwnj;ڕتان هێنا به په&zwnj;روه&zwnj;ردگاری موسا و هاروون، پێش ئه&zwnj;وه&zwnj;ی من مۆڵه&zwnj;ت و ڕووخسه&zwnj;تتان بده&zwnj;م؟! به&zwnj;ڕاستی دیاره که ئه&zwnj;مه پیلانێکه سازتانداوه و گێڕاوتانه له شاره&zwnj;که&zwnj;دا تا خه&zwnj;ڵکه&zwnj;که&zwnj;ی لێ ده&zwnj;ربه&zwnj;ده&zwnj;ر بکه&zwnj;ن، جا له&zwnj;مه&zwnj;ولا ده&zwnj;زانن (چیتان به&zwnj;سه&zwnj;ر ده&zwnj;هێنم)...';
$t[124] = 'سوێند بێت ده&zwnj;ست و قاچه&zwnj;کانتان ڕاست و چه&zwnj;پ ده&zwnj;بڕم، پاشان سوێند بێت هه&zwnj;مووتان هه&zwnj;ڵده&zwnj;واسم و له خاچتان ده&zwnj;ده&zwnj;م.';
$t[125] = '(جادووگه&zwnj;رانی ئه&zwnj;وسا که ئیماندارانی ئێسته&zwnj;ن) وتیان: (ئێمه باکمان نیه&zwnj;) به&zwnj;ڕاستی ئێمه هه&zwnj;ر بۆ لای په&zwnj;روه&zwnj;ردگارمان ده&zwnj;گه&zwnj;ڕێینه&zwnj;وه&zwnj;...';
$t[126] = 'تۆ هیچ بێزاریه&zwnj;کت له ئێمه نیه جگه له&zwnj;وه که باوه&zwnj;ڕمان به&zwnj;به&zwnj;ڵگه و موعجیزه&zwnj;کانی په&zwnj;روه&zwnj;ردگارمان هێناوه&zwnj;، کاتێک بۆمان هات، (پاشان ده&zwnj;ستی نزاو پاڕانه&zwnj;وه&zwnj;یان به&zwnj;رز کرده&zwnj;وه و وتیان) په&zwnj;روه&zwnj;ردگارا خۆگری و ئارامیمان به&zwnj;سه&zwnj;ردا بڕێژه&zwnj;و به موسوڵمانێتی بمانمرێنه&zwnj;.';
$t[127] = 'هه&zwnj;ندێک له ده&zwnj;سه&zwnj;ڵاتدارانی دارو ده&zwnj;سته&zwnj;ی فیرعه&zwnj;ون وتیان: ئایا (هه&zwnj;روا به&zwnj;ئاسانی) واز له موساو قه&zwnj;ومه&zwnj;که&zwnj;ی ده&zwnj;هێنیت بۆ ئه&zwnj;وه&zwnj;ی خراپه و تاوان بچێنن له&zwnj;م زه&zwnj;ویه&zwnj;داو واز له خۆت و خواکانت بهێنن؟! فیرعه&zwnj;ون وتی: (چۆن وازیان لێ ده&zwnj;هێنین) له&zwnj;مه&zwnj;ودوا به&zwnj;زۆریی کوڕه&zwnj;کانیان ده&zwnj;کوژین، ئافره&zwnj;تانیشیان به&zwnj;زیندوویی ده&zwnj;هێڵینه&zwnj;وه (بۆ که&zwnj;نیزه&zwnj;کی) و به&zwnj;ڕاستی ئێمه به&zwnj;سه&zwnj;ریاندا زاڵین و چاوپۆشیان لێناکه&zwnj;ین.';
$t[128] = 'موسا به&zwnj;قه&zwnj;ومه&zwnj;که&zwnj;ی وت: داوای کۆمه&zwnj;کی و پشتیوانی له خوا بکه&zwnj;ن و دان به&zwnj;خۆدا بگرن و ئارامگر بن، به&zwnj;ڕاستی زه&zwnj;وی موڵکی خوایه&zwnj;و ده&zwnj;یسپێرێت به&zwnj;هه&zwnj;رکه&zwnj;س که بیه&zwnj;وێت له به&zwnj;نده&zwnj;کانی، سه&zwnj;رئه&zwnj;نجامیش هه&zwnj;ر بۆ پارێزکارانه&zwnj;.';
$t[129] = '(قه&zwnj;ومه&zwnj;که&zwnj;ی موسا دیاره زۆر له ناخۆشیدا بوون) بۆیه وتیان: پێش ئه&zwnj;وه&zwnj;ی تۆ بێیته ناومان و دوای ئه&zwnj;وه&zwnj;ش که هاتوویت (بارودۆخی ئێمه نه&zwnj;گۆڕاوه&zwnj;) هه&zwnj;ر ئازاردراو بووین، موسا وتی: ئومێد وایه په&zwnj;روه&zwnj;ردگارتان دوژمنه&zwnj;که&zwnj;تان له&zwnj;ناو ببات و بتانکاته جێنشین له زه&zwnj;ویدا، ئه&zwnj;وسا سه&zwnj;یرتان ده&zwnj;کات که ئێوه چۆن کارده&zwnj;که&zwnj;ن!!';
$t[130] = 'سوێند بێت به&zwnj;خوا به&zwnj;ڕاستی ئێمه شوێنکه&zwnj;وتوه&zwnj;کان و گه&zwnj;لی فیرعه&zwnj;ونمان گیرۆده&zwnj;ی چه&zwnj;ند ساڵێکی گرانی و نه&zwnj;هاتی و که&zwnj;م بو&zwnj;نه&zwnj;وه&zwnj;ی ڕزق و ڕۆزیی و به&zwnj;روبوومه&zwnj;کان کرد، بۆ ئه&zwnj;وه&zwnj;ی ئه&zwnj;وان دابچڵه&zwnj;کێن و په&zwnj;ند وه&zwnj;رگرن و بیربکه&zwnj;نه&zwnj;وه&zwnj;.';
$t[131] = 'جا ئه&zwnj;وانه کاتێک خێرو چاکه&zwnj;یه&zwnj;کیان بۆ بێت ئه&zwnj;وه ده&zwnj;ڵێن: ئه&zwnj;مه حه&zwnj;قی خۆمانه و ئێمه شایسته&zwnj;ین، خۆ ئه&zwnj;گه&zwnj;ر ناخۆشیه&zwnj;کیان تووش بێت ئۆباڵ و شوومیان ده&zwnj;ده&zwnj;نه پاڵ موساو ئه&zwnj;وانه&zwnj;ی له&zwnj;گه&zwnj;ڵیدا بوون، ئاگاداربن به&zwnj;ڕاستی هه&zwnj;رچی ڕه&zwnj;شبینی و گه&zwnj;شبینیان هه&zwnj;یه هه&zwnj;مووی له&zwnj;لایه&zwnj;ن خواوه&zwnj;یه&zwnj;، به&zwnj;ڵام زۆربه&zwnj;یان ئه&zwnj;م ڕاستیانه تێ ناگه&zwnj;ن و نایزانن.';
$t[132] = '(فیرعه&zwnj;ون و دارو ده&zwnj;سته&zwnj;ی) وتیان: هه&zwnj;ر چیمان بۆ بهێنیت له به&zwnj;ڵگه&zwnj;و موعجیزه تا چاوبه&zwnj;ستمان لێبکه&zwnj;ی پێی، ئه&zwnj;وه ئێمه هه&zwnj;ر بڕوادارنین به تۆ!!';
$t[133] = 'جا ئێمه&zwnj;ش به&zwnj;ڵای لافاوو کولله و ئه&zwnj;سپێ و بۆقی جۆراو جۆرو خوێنمان نارده سه&zwnj;ریان له کاتێکدا ئه&zwnj;وانه چه&zwnj;نده&zwnj;ها به&zwnj;ڵگه&zwnj;ی ڕوون بوون (له&zwnj;سه&zwnj;ر تاوانباریان)، که&zwnj;چی ئه&zwnj;وان هه&zwnj;ر لووت به&zwnj;رزیان کردو خۆیان به&zwnj;زل و گه&zwnj;وره&zwnj;زانی، به&zwnj;ڕاستی قه&zwnj;ومێکی تاوانبارو تاوانکار بوون.';
$t[134] = 'جا کاتێکیش هه&zwnj;ر به&zwnj;ڵایه&zwnj;ک له&zwnj;و به&zwnj;ڵایانه به&zwnj;سه&zwnj;ریاندا هات (په&zwnj;نایان ده&zwnj;برد بۆ موسا) و ده&zwnj;یانوت: ئه&zwnj;ی موسا (فریامان بکه&zwnj;وه&zwnj;) له په&zwnj;روه&zwnj;ردگارت بپاڕێره&zwnj;وه بۆمان، به&zwnj;و په&zwnj;یمانه&zwnj;ی که خوا لای داناویت، سوێند به&zwnj;خوا ئه&zwnj;گه&zwnj;ر ئه&zwnj;م به&zwnj;ڵایانه&zwnj;مان له&zwnj;سه&zwnj;ر هه&zwnj;ڵگری ئه&zwnj;وسا بێگومان باوه&zwnj;ڕت پێ ده&zwnj;که&zwnj;ین و نه&zwnj;وه&zwnj;ی ئیسرائیلیش ئازاد ده&zwnj;که&zwnj;ین و له&zwnj;گه&zwnj;ڵتا ده&zwnj;نێرین.';
$t[135] = 'جاکاتێک به&zwnj;ڵاکانمان له&zwnj;سه&zwnj;ر لاده&zwnj;بردن بۆ کاتێکی دیاریکراو که ئه&zwnj;وان پێی ده&zwnj;گه&zwnj;یشتن، ئه&zwnj;وه خێرا په&zwnj;یمانه&zwnj;کانیان ده&zwnj;شکاندو له سنوور ده&zwnj;ترازان و (ده&zwnj;چوونه&zwnj;وه دۆخی جاران).';
$t[136] = 'جا ئێمه&zwnj;ش تۆڵه&zwnj;مان لێسه&zwnj;ندن و نوقمی ده&zwnj;ریامان کردن، چونکه ئه&zwnj;وان بڕوایان به به&zwnj;ڵگه و موعجیزه&zwnj;کانی ئێمه نه&zwnj;بوو به درۆیان داده&zwnj;نا و، ئه&zwnj;وان لێی بێئاگا بوون.';
$t[137] = 'ئه&zwnj;وسا ئه&zwnj;و قه&zwnj;وم و گه&zwnj;له&zwnj;ی لاواز ده&zwnj;کران و ده&zwnj;چه&zwnj;وسێنرانه&zwnj;وه له هه&zwnj;موو خۆرهه&zwnj;ڵات و خۆرئاوای ئه&zwnj;و وڵاته&zwnj;ی که فه&zwnj;ڕو به&zwnj;ره&zwnj;که&zwnj;تمان به&zwnj;سه&zwnj;ردا ڕژاند بوو کرده جێنشین و به&zwnj;ڵێنی ڕاست و جوانی په&zwnj;روه&zwnj;ردگارت (ئه&zwnj;ی محمد(صلى الله عليه وسلم) جێبه&zwnj;جێ بوو بۆ نه&zwnj;وه&zwnj;ی ئیسرائیل به&zwnj;هۆی ئه&zwnj;وه&zwnj;ی که ئارامیان له&zwnj;سه&zwnj;رگرت، (که&zwnj;واته ئه&zwnj;گه&zwnj;ر ئوممه&zwnj;تی تۆیش ئاوا خؤگربن بێگومان هه&zwnj;مان ئه&zwnj;نجام به&zwnj;ده&zwnj;ست ده&zwnj;هێنن)، هه&zwnj;رچیش فیرعه&zwnj;ون و قه&zwnj;ومه&zwnj;که&zwnj;ی دروستیان ده&zwnj;کردو ئه&zwnj;و کۆشک و ته&zwnj;لارسازیه&zwnj;ی که ده&zwnj;یانکرد، هه&zwnj;ر هه&zwnj;موویمان کاول و وێران کرد.';
$t[138] = 'له&zwnj;وه&zwnj;ودوا نه&zwnj;وه&zwnj;ی ئیسرائیلمان له ده&zwnj;ریاکه په&zwnj;ڕانده&zwnj;وه&zwnj;، دوای تاوێک ڕێیان که&zwnj;وت له خه&zwnj;ڵکێک که به&zwnj;ده&zwnj;وری بته&zwnj;کانیاندا ده&zwnj;خولانه&zwnj;وه&zwnj;و ده&zwnj;یانپه&zwnj;رستن، (ده&zwnj;سته&zwnj;یه&zwnj;کیان) وتیان: ئه&zwnj;ی موسا خوایه&zwnj;کیش بۆ ئێمه ساز بکه وه&zwnj;کو ئه&zwnj;وانه چه&zwnj;ند خوایه&zwnj;کیان هه&zwnj;یه&zwnj;!! موسا پێی وتن: به&zwnj;ڕاستی ئێوه قه&zwnj;ومێکن نه&zwnj;ده&zwnj;زانن و نه&zwnj;ده&zwnj;فامن...';
$t[139] = 'بێگومان ئائه&zwnj;وانه ئه&zwnj;وه&zwnj;ی تێدا ده&zwnj;ژین له&zwnj;ناوچووه و، پووچه&zwnj;ڵه ئه&zwnj;وه&zwnj;ی ئه&zwnj;وان ده&zwnj;یانکرد.';
$t[140] = 'موسا وتی: ئایا که&zwnj;ی ڕه&zwnj;وایه بێجگه له خوا خوایه&zwnj;کی ترتان بۆ بخوازم، له&zwnj;کاتێکدا که ئه&zwnj;و زاته له&zwnj;ناو خه&zwnj;ڵکی ئه&zwnj;م جیهانه&zwnj;دا هه&zwnj;ڵی بژاردوون و ڕێزی زیاتری لێناوون.';
$t[141] = 'یادی ئه&zwnj;و کاته&zwnj;ش بکه&zwnj;ن که ڕزگارمان کردن له دارو ده&zwnj;سته&zwnj;ی فیرعه&zwnj;ون کاتێک که ناسۆرترین ئه&zwnj;شکه&zwnj;نجه&zwnj;یان ده&zwnj;دان، به&zwnj;شێوه&zwnj;یه&zwnj;کی دڕندانه زۆرینه&zwnj;ی کوڕه&zwnj;کانی ئێوه&zwnj;یان ده&zwnj;کوشت وئافره&zwnj;ته&zwnj;کانی ئێوه&zwnj;شیان ده&zwnj;هێشته&zwnj;وه (بۆ که&zwnj;نیزه&zwnj;کی)، له&zwnj;وه&zwnj;شدا به&zwnj;ڵاو تاقیکردنه&zwnj;وه&zwnj;یه&zwnj;کی گه&zwnj;وره هه&zwnj;یه بۆتان له&zwnj;لایه&zwnj;ن په&zwnj;روه&zwnj;ردگارتانه&zwnj;وه (بۆچی زوو فه&zwnj;رامۆشتان کرد).';
$t[142] = 'به&zwnj;ڵێنی سی شه&zwnj;ومان به موسادا (که خواپه&zwnj;رستی تێدا بکات، تا ئاماده بێت بۆ وه&zwnj;رگرتنی ته&zwnj;ورات) و به ده شه&zwnj;وی تر ماوه&zwnj;که&zwnj;مان ته&zwnj;واوکرد، ئینجا کاتی گفتوگۆی موسا له&zwnj;گه&zwnj;ڵ په&zwnj;روه&zwnj;ردگاریدا ته&zwnj;واو بوو که گه&zwnj;یشته چل شه&zwnj;و، موسا (پێش ڕۆیشتنی بۆ کێوی طور) به هارونی برای وت: ئیتر تۆ له جیاتی من به له&zwnj;ناو قه&zwnj;وم و عه&zwnj;شره&zwnj;تمدا ده&zwnj;وری چاکسازی ببینه و نه&zwnj;که&zwnj;یت شوێن ڕێبازی ئه&zwnj;وانه بکه&zwnj;ویت که چێنه&zwnj;ری تۆوی خراپه&zwnj;ن.';
$t[143] = 'جا کاتێک موسا هات بۆ کات و شوێنی دیاریکراومان و په&zwnj;روه&zwnj;ردگاریشی گفتوگۆی ڕاسته&zwnj;وخۆی له&zwnj;گه&zwnj;ڵدا ئه&zwnj;نجامدا (دیاره له حاڵه&zwnj;تێکی ڕۆحی و ده&zwnj;روونی زۆر به&zwnj;رزدا بووه&zwnj;، که&zwnj;وتۆته جیهانێکی زۆر سه&zwnj;یرو خۆشه&zwnj;وه که قه&zwnj;ڵه&zwnj;م ناتوانێت باسی بکات و زمان ناتوانێت وه&zwnj;سفی بکات، ئاخر ناحه&zwnj;قی نیه&zwnj;، په&zwnj;روه&zwnj;ردگاری ئه&zwnj;م بوونه&zwnj;وه&zwnj;ره گفتوگۆ له&zwnj;گه&zwnj;ڵ به&zwnj;نده&zwnj;یه&zwnj;کی خۆیدا ئه&zwnj;نجام بدات، با پێغه&zwnj;مبه&zwnj;ریش بێت، بۆیه له خۆشیدا هاته گفت و) وتی: (تو خوا، خوایه گیان، ئه&zwnj;ی په&zwnj;روه&zwnj;ردگاری ئازیزم) خۆتم نیشان بده تا تێر ته&zwnj;ماشات بکه&zwnj;م، خوای گه&zwnj;وره&zwnj;ش فه&zwnj;رمووی: هه&zwnj;رگیز (له دنیادا) نامبینیت (بۆ دڵنیاکردنی له&zwnj;وه&zwnj;ی که به&zwnj;رگه&zwnj;ی ته&zwnj;جه&zwnj;لای زاتی په&zwnj;روه&zwnj;ردگار ناگرێت) پێی فه&zwnj;رموو: به&zwnj;ڵام (ئه&zwnj;گه&zwnj;ر هه&zwnj;ر حه&zwnj;ز ده&zwnj;که&zwnj;یت) سه&zwnj;یری ئه&zwnj;و چیایه بکه&zwnj;، جا ئه&zwnj;گه&zwnj;ر مایه&zwnj;وه له جێگه&zwnj;ی خۆیدا ئه&zwnj;وا له&zwnj;وه&zwnj;ودوا تۆش من ده&zwnj;بینیت، جا کاتێک په&zwnj;روه&zwnj;ردگاری خۆی نیشانی کێوه&zwnj;که&zwnj;داو ته&zwnj;جه&zwnj;لای کرد، کێوه&zwnj;که&zwnj;ی سووتاند و ورد و خاشی کرد، موساش که&zwnj;وت و له هۆش خۆی چوو، ئینجا کاتێک هاته&zwnj;وه هۆش خۆی (زانی داخوازیه&zwnj;که&zwnj;ی زۆر گه&zwnj;وره بووه&zwnj;) وتی: ستایش و بێگه&zwnj;ردی و که&zwnj;ماڵ هه&zwnj;ر سایشته&zwnj;ی تۆیه&zwnj;، من په&zwnj;شیمانم له داخوازیه&zwnj;که&zwnj;م و گه&zwnj;ڕامه&zwnj;وه بۆ لای تۆ و من یه&zwnj;که&zwnj;م که&zwnj;سم له ئیمانداران.';
$t[144] = '(خوای گه&zwnj;وره&zwnj;) فه&zwnj;رمووی: ئه&zwnj;ی موسا به&zwnj;ڕاستی من تۆم هه&zwnj;ڵبژاردوه له&zwnj;ناو خه&zwnj;ڵکیدا به&zwnj;هۆی په&zwnj;یامه&zwnj;کانمه&zwnj;وه و گفتوگۆکردنیشم له&zwnj;گه&zwnj;ڵ تۆدا، جا هه&zwnj;رچیه&zwnj;کم پێبه&zwnj;خشیوویت به توندو تۆڵی بیگره&zwnj;و په&zwnj;یڕه&zwnj;وی بکه&zwnj;و ڕای بگه&zwnj;یه&zwnj;نه و هه&zwnj;میشه و به&zwnj;رده&zwnj;وام له سوپاسگوزاران به&zwnj;.';
$t[145] = 'له تابلۆکانی (ته&zwnj;وراتیشدا) له هه&zwnj;موو بابه&zwnj;تێکمان بۆ تۆمار کردبوو، ئامۆژگاری تێرو ته&zwnj;واو (بۆ هه&zwnj;موو چالاکیه&zwnj;کانی ژیان)، هه&zwnj;روه&zwnj;ها ڕوون کردنه&zwnj;وه بۆ هه&zwnj;موو شتێک و هه&zwnj;موو بابه&zwnj;تێک، که&zwnj;وابوو ئه&zwnj;ی موسا: به&zwnj;توندی بیانگره و په&zwnj;یڕه&zwnj;ویان بکه&zwnj;و فه&zwnj;رمان به قه&zwnj;ومه&zwnj;که&zwnj;شت بده&zwnj;: که به&zwnj;جوانترین شێوه وه&zwnj;ریان بگرن و په&zwnj;یڕه&zwnj;ویان بکه&zwnj;ن، له ئاینده&zwnj;دا خانووبه&zwnj;ره و شوێنی تاوانبارو له سنوور ده&zwnj;رچوانتان نیشان ده&zwnj;ده&zwnj;م (چۆن وێرانی ده&zwnj;که&zwnj;ین).';
$t[146] = 'ئه&zwnj;و که&zwnj;سانه&zwnj;ی که به&zwnj;ناحه&zwnj;ق فیز ده&zwnj;که&zwnj;ن له زه&zwnj;ویداو خۆیان به&zwnj;گه&zwnj;وره&zwnj;تر ده&zwnj;نوێنن (له خه&zwnj;ڵکی)، له&zwnj;مه&zwnj;ودوا ڕووی دڵیان وه&zwnj;رده&zwnj;چه&zwnj;رخێنم له تێگه&zwnj;یشتنی ئایه&zwnj;ت و فه&zwnj;رمانه&zwnj;کانم، خۆ ئه&zwnj;وانه ئه&zwnj;گه&zwnj;ر هه&zwnj;ر به&zwnj;ڵگه و نیشانه&zwnj;یه&zwnj;ک ببینن ئه&zwnj;وه باوه&zwnj;ڕی پێناکه&zwnj;ن، ئه&zwnj;گه&zwnj;ر ڕێبازی به&zwnj;رژه&zwnj;وه&zwnj;ندی دنیاو قیامه&zwnj;ت ببینن ئه&zwnj;وه نایگرنه&zwnj;به&zwnj;ر، خۆ ئه&zwnj;گه&zwnj;ر ڕێبازی ئاره&zwnj;زوو په&zwnj;رستی و گومڕایی ببینن، ئه&zwnj;وه ده&zwnj;یگرنه به&zwnj;رو په&zwnj;یڕه&zwnj;وی ده&zwnj;که&zwnj;ن، ئه&zwnj;وه له&zwnj;به&zwnj;ر ئه&zwnj;وه&zwnj;یه چونکه به&zwnj;ڕاستی ئه&zwnj;وان بڕوایان به&zwnj;ئایه&zwnj;ت و فه&zwnj;رمانه&zwnj;کانی ئێمه نه&zwnj;بوو لێیان بێئاگا بوون.';
$t[147] = 'جا ئه&zwnj;وانه&zwnj;ی که ئایه&zwnj;ت و فه&zwnj;رمانه&zwnj;کانی ئێمه و به یه&zwnj;کگه&zwnj;یشنه&zwnj;وه&zwnj;یان له دوارۆژدا به&zwnj;درۆزانی، ئه&zwnj;وانه هه&zwnj;موو کاروکرده&zwnj;وه&zwnj;کانیان پووچ و بێ نرخ و بێ سوود بوو، ئایا پاداشتی هیچ شتێکیان ده&zwnj;درێته&zwnj;وه جگه له&zwnj;وه&zwnj;ی که ده&zwnj;یانکرد؟!';
$t[148] = 'جا له دوای چوونی موسا (بۆ کێوی طور) قه&zwnj;ومه&zwnj;که&zwnj;ی په&zwnj;یکه&zwnj;ری گوێره&zwnj;که&zwnj;یه&zwnj;کیان له خشڵه&zwnj;کانیان دروست کرد، که بۆڕه&zwnj;یه&zwnj;کی هه&zwnj;بوو!! ئایا ئه&zwnj;وان نه&zwnj;یانزانی که ئه&zwnj;وه به&zwnj;ڕاستی قسه&zwnj;یان له&zwnj;گه&zwnj;ڵدا ناکات و ڕێنمووییان ناکات بۆ هیچ ڕێیه&zwnj;ک، (سه&zwnj;یره له&zwnj;گه&zwnj;ڵ ئه&zwnj;و هه&zwnj;موو که&zwnj;م و کووڕیانه&zwnj;شدا) کردیان به&zwnj;خوای خۆیان، ئه&zwnj;وانه سته&zwnj;مکار بوون.';
$t[149] = 'کاتێکیش که له ده&zwnj;ستیان چوو و په&zwnj;شیمان بوونه&zwnj;وه و (ڕاستییان بۆ ده&zwnj;رکه&zwnj;وت و گوێره&zwnj;که بوو به په&zwnj;ند له&zwnj;به&zwnj;ر چاویان) زانیان و دڵنیا بوون که به&zwnj;ڕاستی گومڕا بوون و وتیان: سوێند به خوا ئه&zwnj;گه&zwnj;ر په&zwnj;روه&zwnj;ردگارمان به&zwnj;زه&zwnj;یی پیاماندا نه&zwnj;یه&zwnj;ته&zwnj;وه و لێمان خۆش نه&zwnj;بێت، ئه&zwnj;وه به&zwnj;ڕاستی له زه&zwnj;ره&zwnj;رمه&zwnj;ندانین.';
$t[150] = 'جا کاتێک موسا گه&zwnj;ڕایه&zwnj;وه بۆ لای قه&zwnj;ومه&zwnj;که&zwnj;ی (زانی بووی خه&zwnj;ریکی گوێره&zwnj;که په&zwnj;رستین) زۆر به&zwnj;توڕه&zwnj;یی و په&zwnj;ستی و غه&zwnj;مباریه&zwnj;وه وتی: به&zwnj;ڕاستی دوای ڕۆشتنی من جێنشینیه&zwnj;کی خراپی منتان کرد، ئه&zwnj;وه په&zwnj;له&zwnj;تان کرد له واده&zwnj;ی په&zwnj;روه&zwnj;ردگارتاندا؟ (ئیتر له&zwnj;داخی ئه&zwnj;و لادان و سه&zwnj;رگه&zwnj;ردانیه&zwnj;) تابلۆکانی (ته&zwnj;ورات)ی فڕێداو په&zwnj;لاماری قژی (هاروون)ی برای داو ڕای کێشا بۆ لای خۆی، (هاروون) وتی: کوڕی دایکم (خه&zwnj;تای من نه&zwnj;بوو، من زۆر هه&zwnj;وڵمدا به&zwnj;ڵام بێ سوود بوو) به ڕاستی ئه&zwnj;و خه&zwnj;ڵکه زۆریان بۆ هێنام و هێزیان لێسه&zwnj;ندم، خه&zwnj;ریک بوو بشمکوژن (کاکه گیان) که&zwnj;وابوو دوژمنانم پێ شاد مه&zwnj;که و له&zwnj;گه&zwnj;ڵ قه&zwnj;ومی سته&zwnj;مکاردا حسابم بۆ مه&zwnj;که&zwnj;.';
$t[151] = '(ئه&zwnj;وسا ئیتر موسا به&zwnj;خۆیدا هاته&zwnj;وه و) وتی: په&zwnj;روه&zwnj;ردگارا له خۆم و براکه&zwnj;شم خۆش ببه و هه&zwnj;ردووکمان بخه&zwnj;ره نێو ڕه&zwnj;حمه&zwnj;ت و میهره&zwnj;بانی خۆته&zwnj;وه و تۆ له هه&zwnj;موو که&zwnj;س به&zwnj;سۆزتر و میهره&zwnj;بانتریت.';
$t[152] = 'به&zwnj;ڕاستی ئه&zwnj;وانه&zwnj;ی که گوێره&zwnj;که&zwnj;که&zwnj;یان په&zwnj;رست، له&zwnj;ئاینده&zwnj;دا له&zwnj;لایه&zwnj;ن په&zwnj;روه&zwnj;ردگاریانه&zwnj;وه خه&zwnj;شم و قینیان ده&zwnj;گاتێ و زه&zwnj;لیلی و خه&zwnj;جاڵه&zwnj;تییش به&zwnj;شیان ده&zwnj;بێت له ژیانی دنیادا، هه&zwnj;ر به&zwnj;و شێوه&zwnj;یه&zwnj;ش پاداشتی ئه&zwnj;وانه ده&zwnj;ده&zwnj;ینه&zwnj;وه که درۆ به&zwnj;ده&zwnj;م خواوه هه&zwnj;ڵده&zwnj;به&zwnj;ستن.';
$t[153] = 'ئه&zwnj;وانه&zwnj;ش که کرداره خراپه&zwnj;کانیان ئه&zwnj;نجامداوه و پاشان له دوای کرداره خراپه&zwnj;کان ته&zwnj;وبه&zwnj;یان کردو په&zwnj;شیمان بوونه&zwnj;وه و باوه&zwnj;ڕێکی دامه&zwnj;زراویان هێنا، ئه&zwnj;وه بێگومان په&zwnj;روه&zwnj;ردگاری تۆ (ئه&zwnj;ی محمد(صلى الله عليه وسلم) له دوای ئه&zwnj;نجامدانی ئه&zwnj;وکارانه لێخۆشبوو میهره&zwnj;بانه&zwnj;.';
$t[154] = 'کاتێک تووڕه&zwnj;یی موسا هێور بۆوه&zwnj;، تابلۆکانی (ته&zwnj;ورات)ی هه&zwnj;ڵگرته&zwnj;وه&zwnj;، که&zwnj;له توێیدا ڕێنموویی و ڕه&zwnj;حمه&zwnj;ت و میهره&zwnj;بانی تیادا ده&zwnj;دره&zwnj;وشایه&zwnj;وه بۆ ئه&zwnj;وانه&zwnj;ی که له په&zwnj;روه&zwnj;ردگاریان ده&zwnj;ترسن و مه&zwnj;به&zwnj;ستیانه لێیان نه&zwnj;ڕه&zwnj;نجێت.';
$t[155] = '(دوای ماوه&zwnj;یه&zwnj;ک) موسا حه&zwnj;فتا پیاوی له قه&zwnj;ومه&zwnj;که&zwnj;ی هه&zwnj;ڵبژارد (تا په&zwnj;شیمانی ته&zwnj;واو ده&zwnj;ربڕن له&zwnj;گوێره&zwnj;که په&zwnj;رستی، له&zwnj;کاتی دیاری کراودا بردنی بۆ لای کێوی طور) جا کاتێک تووشی بوومه&zwnj;له&zwnj;رزه&zwnj;که بوون و گرتنی، موسا وتی: په&zwnj;روه&zwnj;ردگارا خۆ ئه&zwnj;گه&zwnj;ر بتویستایه پێشتر منیش و ئه&zwnj;وانیشت له&zwnj;ناو ده&zwnj;برد، ئایا ئێمه له&zwnj;ناو ده&zwnj;به&zwnj;یت به&zwnj;هۆی ئه&zwnj;وه&zwnj;ی که ئه&zwnj;و نه&zwnj;فامانه&zwnj;مان کردویانه&zwnj;، ئه&zwnj;مه هیچ شتێک نیه ته&zwnj;نها تاقیکردنه&zwnj;وه&zwnj;یه&zwnj;کی زاتی تۆ نه&zwnj;بێت که ئه&zwnj;وه&zwnj;ی بته&zwnj;وێت پێگومڕا ده&zwnj;که&zwnj;یت، ئه&zwnj;وه&zwnj;ش بته&zwnj;وێت هیدایه&zwnj;تی ده&zwnj;ده&zwnj;یت، هه&zwnj;ر تۆ پشت و په&zwnj;نای ئێمه&zwnj;ی، جا (داواکارین) لێمان خۆش ببیت و به&zwnj;زه&zwnj;ییت پیاماندا بێته&zwnj;وه&zwnj;، تۆ چاکترینی چاوپۆشیکه&zwnj;رانی.';
$t[156] = 'داواکارین که له دنیادا چاکه&zwnj;مان بۆ پێش بهێنیت و له قیامه&zwnj;تیشدا، ئێمه به&zwnj;ڕاستی ڕێنموویی کراوین بۆ لای زاتی تۆ، (خوای گه&zwnj;وره له وه&zwnj;ڵامیاندا) فه&zwnj;رمووی: سزاو تۆڵه&zwnj;م تووشی که&zwnj;سێک ده&zwnj;که&zwnj;م که بمه&zwnj;وێت و (شایسته بێت)، ڕه&zwnj;حمه&zwnj;ت و میهره&zwnj;بانیم هه&zwnj;موو شتێکی گرتۆته&zwnj;وه&zwnj;، جا له&zwnj;مه&zwnj;ودوا ئه&zwnj;و ڕه&zwnj;حمه&zwnj;ت و میهره&zwnj;بانیه&zwnj;م تۆمار ده&zwnj;که&zwnj;م و بڕیار ده&zwnj;ده&zwnj;م بۆ ئه&zwnj;و که&zwnj;سانه&zwnj;ی که ته&zwnj;قواو پارێزکاری ده&zwnj;که&zwnj;ن و زه&zwnj;کات ده&zwnj;ده&zwnj;ن و ئه&zwnj;وانیش که باوه&zwnj;ڕی ته&zwnj;واو به&zwnj;ئایه&zwnj;ت و فه&zwnj;رمانه&zwnj;کانی ئێمه ده&zwnj;هێنن...';
$t[157] = 'ئه&zwnj;وانه&zwnj;ی که شوێنی ئه&zwnj;م (محمد(صلى الله عليه وسلم) پێغه&zwnj;مبه&zwnj;ره هه&zwnj;واڵده&zwnj;ره نه&zwnj;خوێنده&zwnj;واره ده&zwnj;که&zwnj;ون ئه&zwnj;وه&zwnj;ی که (گاورو جوو، ناوو نیشانیان) ده&zwnj;ستیان ده&zwnj;که&zwnj;وێت نووسراوه لای ئه&zwnj;وان له ته&zwnj;ورات و ئینجیلدا، فه&zwnj;رمانیان پێده&zwnj;دات به&zwnj;چاکه&zwnj;و قه&zwnj;ده&zwnj;غه&zwnj;ی گوناهو خراپه&zwnj;یان لێده&zwnj;کات و هه&zwnj;رچیه&zwnj;ک پاکو چاکه بۆیان حه&zwnj;ڵاڵ ده&zwnj;کات و هه&zwnj;رچی پیس و ناپوخته لێیان حه&zwnj;رام ده&zwnj;کات و ئه&zwnj;رکه قورس و سه&zwnj;نگینه&zwnj;کان له&zwnj;سه&zwnj;رشانیان لاده&zwnj;بات، ئه&zwnj;و کۆت و زنجیرانه&zwnj;ش که له&zwnj;سه&zwnj;ریان بوو لای ده&zwnj;بات (وه&zwnj;ک خۆ کۆشتن له&zwnj;کاتی ته&zwnj;وبه&zwnj;دا... هتد)، جا ئه&zwnj;وانه&zwnj;ی باوه&zwnj;ڕیان پێ هێناوه و پشتیوانیان لێکردوه و پشتگیریان کردووه و شوێنی ئه&zwnj;و نووره که&zwnj;وتوون (که قورئانه&zwnj;) و هاوڕێ له&zwnj;گه&zwnj;ڵیدا ڕه&zwnj;وانه کراوه&zwnj;، هه&zwnj;ر ئه&zwnj;وانه سه&zwnj;رفرازو ڕزگارن.';
$t[158] = '(ئه&zwnj;ی محمد(صلى الله عليه وسلم)  بڵێ: ئه&zwnj;ی خه&zwnj;ڵکینه به&zwnj;ڕاستی من پێغه&zwnj;مبه&zwnj;رو فرستاده&zwnj;ی خوام بۆ ئێوه به&zwnj;گشتی، ئه&zwnj;و خوایه&zwnj;ی که هه&zwnj;ر بۆئه&zwnj;وه خاوه&zwnj;ندارێتی ئاسمانه&zwnj;کان و زه&zwnj;وی&zwnj;، هیچ خوایه&zwnj;ک نیه جگه له&zwnj;و زاته&zwnj;، ده&zwnj;ژێنێت و ده&zwnj;مرێنێت، جا که&zwnj;وابوو باوه&zwnj;ڕی پته&zwnj;و بهێنن به&zwnj;و خوایه&zwnj;و به&zwnj;پێغه&zwnj;مبه&zwnj;ره&zwnj;که&zwnj;ی، که هه&zwnj;واڵده&zwnj;رێکی نه&zwnj;خوێنده&zwnj;واره&zwnj;، (تاهیچ که&zwnj;س نه&zwnj;ڵێت پیاوێکی خوێنده&zwnj;وار بووه و خۆی قورئانی داناوه&zwnj;) ئه&zwnj;و پێغه&zwnj;مبه&zwnj;ره&zwnj;ی که خۆی باوه&zwnj;ڕ ده&zwnj;هێنێت به خواو به فه&zwnj;رمووده&zwnj;کانی، ده ئێوه&zwnj;ش شوێنی بکه&zwnj;ون، بۆ ئه&zwnj;وه&zwnj;ی ئێوه ڕێنموویی وه&zwnj;رگرن.';
$t[159] = 'له قه&zwnj;ومی موسا که&zwnj;سانێک هه&zwnj;ن (ژیرو هۆشمه&zwnj;ندن و شوێنی حه&zwnj;ق ده&zwnj;که&zwnj;ون و) ڕێنموویی خه&zwnj;ڵک ده&zwnj;که&zwnj;ن و شاره&zwnj;زایان ده&zwnj;که&zwnj;ن به&zwnj;حه&zwnj;ق و ڕاستی، هه&zwnj;ر به&zwnj;و حه&zwnj;قه خه&zwnj;ڵکی ڕاست ده&zwnj;که&zwnj;نه&zwnj;وه (له لادان و سه&zwnj;رکه&zwnj;شی).';
$t[160] = 'ئێمه نه&zwnj;وه&zwnj;ی ئیسرائیلمان کرد به دوانزه تیره&zwnj;و تایه&zwnj;فه (دوای ئه&zwnj;وه&zwnj;ی به&zwnj;بیابانی سینادا کۆچیان ده&zwnj;کرد تووشی تینویه&zwnj;تیه&zwnj;کی زۆر بوون) و وه&zwnj;حی و نیگامان بۆ موسا نارد کاتیک قه&zwnj;ومه&zwnj;که&zwnj;ی داوای ئاویان لێکرد که&zwnj;: به دار عاساکه&zwnj;ت بماڵه به&zwnj;به&zwnj;رده&zwnj;که&zwnj;دا ( هه&zwnj;ر که ئه&zwnj;و کاره&zwnj;ی کرد) به&zwnj;رده&zwnj;که دوانزه کانی لێ هه&zwnj;ڵقوڵا، بێگومان هه&zwnj;ر تیره&zwnj;و تایه&zwnj;فه&zwnj;یه&zwnj;ک کانیاوی خۆی زانی و هه&zwnj;وریشمان کرد به سێبه&zwnj;ر به&zwnj;سه&zwnj;ریانه&zwnj;وه (تا گه&zwnj;رما زۆریان بۆ نه&zwnj;هێنێت)و گه&zwnj;زۆ و باڵنده&zwnj;ی شه&zwnj;لاقه&zwnj;شمان به&zwnj;سه&zwnj;ردا باراندن، ده بخۆن له&zwnj;و هه&zwnj;موو ڕزق و ڕۆزیه پاکانه&zwnj;ی که پێمان به&zwnj;خشیوون، جا وه&zwnj;نه&zwnj;بێت ئه&zwnj;وانه سته&zwnj;میان له ئێمه کردبێت (به ناشوکری کردن و بێزاری ده&zwnj;ربڕین) به&zwnj;ڵکو سته&zwnj;میان هه&zwnj;ر له خۆیان ده&zwnj;کرد.';
$t[161] = 'یادی ئه&zwnj;و کاته بکه&zwnj;ره&zwnj;وه که پێیان وترا: له&zwnj;م شاره&zwnj;دا که (قودسه&zwnj;) نیشته&zwnj;جێ ببن و له&zwnj;و به&zwnj;روبوومانه&zwnj;ی که به ده&zwnj;ورو پشتیدا هه&zwnj;یه له هه&zwnj;ر شوێنێک ویستان به ئاره&zwnj;زووی خۆتان لێی بخۆن و داوای لێخۆشبوون بکه&zwnj;ن و بڵێن په&zwnj;روه&zwnj;ردگارا؛ لێمان خۆش ببه&zwnj;و گوناهه&zwnj;کانمان لێ هه&zwnj;ڵوه&zwnj;رێنه&zwnj;، جا له ده&zwnj;روازه&zwnj;ی شاره&zwnj;وه به کڕنووشه&zwnj;وه بچنه ژووره&zwnj;وه&zwnj;، ئه&zwnj;وه ئیتر ئێمه له هه&zwnj;ڵه و گوناهه&zwnj;کانتان خۆش ده&zwnj;بین، له&zwnj;ئاینده&zwnj;دا چاکه&zwnj;کاران پاداشتی زیاتریان ده&zwnj;ده&zwnj;ینه&zwnj;وه (له هه&zwnj;ردوو جیهاندا).';
$t[162] = 'جا ئه&zwnj;وانه&zwnj;یان که سته&zwnj;میان کردبوو فه&zwnj;رمووده&zwnj;که&zwnj;یان گۆڕی به&zwnj;شتێک ئه&zwnj;وه نه&zwnj;بوو که پێیان وترا بوو، جائێمه&zwnj;ش سزاو به&zwnj;ڵایه&zwnj;کمان له ئاسمانه&zwnj;وه به&zwnj;سه&zwnj;ردا ناردن به&zwnj;هۆی ئه&zwnj;و سته&zwnj;مه&zwnj;وه که ده&zwnj;یانکرد.';
$t[163] = 'پرسیار له جووه&zwnj;کان بکه ده&zwnj;رباره&zwnj;ی ئه&zwnj;و شاره&zwnj;ی که له&zwnj;سه&zwnj;ر قه&zwnj;راغی ده&zwnj;ریا بوو، له&zwnj;کاتێکدا ئه&zwnj;وان له ڕۆژی شه&zwnj;ممه&zwnj;دا سنووریان ده&zwnj;شکاند (که بڕیار بوو ڕۆژی شه&zwnj;ممه ته&zwnj;رخان بێت ته&zwnj;نها بۆ خواپه&zwnj;رستی) کاتێک له ڕۆژی شه&zwnj;ممه&zwnj;که&zwnj;یاندا ماسیه گه&zwnj;وره&zwnj;کانیان به&zwnj;سه&zwnj;ر ئاوه&zwnj;وه بۆ ده&zwnj;هاتن، ئه&zwnj;و ڕۆژه&zwnj;ش که شه&zwnj;ممه&zwnj;یان نه&zwnj;ده&zwnj;بوو ماسیه&zwnj;کانیان بۆ نه ده&zwnj;هات، ئا به&zwnj;و شێوه&zwnj;یه به&zwnj;هۆی یاخی بوون و لادانیانه&zwnj;وه تاقیمان کردنه&zwnj;وه&zwnj;.';
$t[164] = '(دیاره که هه&zwnj;ندێک له&zwnj;و جوله&zwnj;کانه فه&zwnj;رمانی خوایان شکاندووه&zwnj;، به&zwnj;هه&zwnj;ر فێڵێک بووه ڕاوی خۆیان کردوه&zwnj;، بۆیه ده&zwnj;سته&zwnj;یه&zwnj;ک له پیاو چاکانیان که&zwnj;وتوونه&zwnj;ته ئامۆژگاری کردنی ده&zwnj;ست درێژکاران) که&zwnj;چی کاتێکیش ده&zwnj;سته&zwnj;یه&zwnj;ک له&zwnj;وان وتیان: بۆچی ئامۆژگاری که&zwnj;سانێک ده&zwnj;که&zwnj;ن که خوا هه&zwnj;ر له&zwnj;ناوبه&zwnj;ریانه&zwnj;، یان سزاده&zwnj;ریانه به&zwnj;سزایه&zwnj;کی توند ( له وه&zwnj;ڵامیاندا) وتیان: ئاخر بابه&zwnj;ڵگه&zwnj;یه&zwnj;کی دڵسۆزی و غه&zwnj;مخۆری بێت بۆ لای په&zwnj;روه&zwnj;ردگارتان و به&zwnj;و هیوایه ئه&zwnj;وان له خوا بترسن.';
$t[165] = 'جا کاتێک فه&zwnj;رامۆشیان کرد ئه&zwnj;وه&zwnj;ی ئامۆژگاریان پێی کرابوو، ئێمه&zwnj;ش ئه&zwnj;وانه&zwnj;ی که قه&zwnj;ده&zwnj;غه&zwnj;ی خراپه&zwnj;یان ده&zwnj;کرد ڕزگارمان کردن و ئه&zwnj;وانه&zwnj;ش که سته&zwnj;میان کردو هه&zwnj;وڵیاندا بۆ کردنی ئه&zwnj;وکاره ناڕه&zwnj;وایه&zwnj;، به&zwnj;سزایه&zwnj;کی سه&zwnj;خت گیرۆده&zwnj;مان کردن، به&zwnj;هۆی ئه&zwnj;وه&zwnj;ی له سنوور ده&zwnj;رچوو بوون.';
$t[166] = 'جا کاتێک هه&zwnj;ر سه&zwnj;رکه&zwnj;شییان کرد له&zwnj;وه&zwnj;ی که قه&zwnj;ده&zwnj;غه کرابوو لێیان، ئێمه&zwnj;ش پێمان وتن: ببنه کۆمه&zwnj;ڵه مه&zwnj;یمونێکی سووک و ڕیسواو دوور له ڕه&zwnj;حمی خوا.';
$t[167] = 'یادی ئه&zwnj;و کاته&zwnj;ش بکه په&zwnj;روه&zwnj;ردگارت (ئه&zwnj;ی محمد(صلى الله عليه وسلم) بڕیاریدا و ڕایگه&zwnj;یاند به&zwnj;ڕاستی که&zwnj;سانێک ده&zwnj;نێرێته سه&zwnj;ریان تا ڕۆژی قیامه&zwnj;ت، ئه&zwnj;شکه&zwnj;نجه&zwnj;و ئازاری سه&zwnj;ختیان بدات، چونکه به&zwnj;ڕاستی په&zwnj;روه&zwnj;ردگارت زۆر به&zwnj;خێرایی تۆڵه ده&zwnj;سێنێت ( له سته&zwnj;مکاران)، هه&zwnj;روه&zwnj;ها بێگومان لێخۆشبوو میهره&zwnj;بانیشه ( بۆ ته&zwnj;وبه&zwnj;کاران و ئیمانداران).';
$t[168] = 'سه&zwnj;رئه&zwnj;نجامیش نه&zwnj;وه&zwnj;ی ئیسرائیلمان کرد به&zwnj;چه&zwnj;ند به&zwnj;شه&zwnj;وه له&zwnj;سه&zwnj;ر زه&zwnj;ویدا، هه&zwnj;یانه چاک و له خواترسه&zwnj;، هه&zwnj;شیانه وا نیه و له&zwnj;پله&zwnj;یه&zwnj;کی نزمدایه&zwnj;و به&zwnj;چه&zwnj;نده&zwnj;ها خۆشی و ناخۆشی تاقیمان کردنه&zwnj;وه بۆ ئه&zwnj;وه&zwnj;ی ئه&zwnj;وان بگه&zwnj;ڕێنه&zwnj;وه (بۆ ڕێبازی ڕاست و دروست).';
$t[169] = 'جا له دوای ئه&zwnj;وان نه&zwnj;وه&zwnj;یه&zwnj;کی نوێ جێگه&zwnj;ی ئه&zwnj;وانی گرته&zwnj;وه ته&zwnj;وراتیان به&zwnj;میراتی وه&zwnj;رگرت، ئه&zwnj;وانه (به په&zwnj;رۆشه&zwnj;وه&zwnj;) په&zwnj;لاماری که&zwnj;لوپه&zwnj;لی (حه&zwnj;رامی) ئه&zwnj;م دنیایه ده&zwnj;ده&zwnj;ن و ده&zwnj;شڵێن چاوپۆشیمان لێده&zwnj;کرێت، خۆ ئه&zwnj;گه&zwnj;ر که&zwnj;لوپه&zwnj;لێکی تریان بۆ بێت وه&zwnj;ک ئه&zwnj;و، ئه&zwnj;وه وه&zwnj;ریده&zwnj;گرن و ده&zwnj;یماشنه&zwnj;وه (چاویان تێرنابێت)، باشه&zwnj;، ئایا ئه&zwnj;وانه له&zwnj;ڕێگه&zwnj;ی ته&zwnj;وراته&zwnj;وه په&zwnj;یمانیان لێ وه&zwnj;رنه&zwnj;گیراوه که به&zwnj;ناوی خواوه شتێک نه&zwnj;ڵێن جگه&zwnj;له حه&zwnj;ق و ڕاستی؟ خۆ هه&zwnj;رچی له ته&zwnj;وراتدا هه&zwnj;یه&zwnj;، ئه&zwnj;وانه خوێندویانه&zwnj;و ده&zwnj;یزانن!!، بێگومان ماڵی قیامه&zwnj;ت، به&zwnj;هه&zwnj;شتی به&zwnj;رین، چاکتره بۆ ئه&zwnj;وانه&zwnj;ی که خواناسی و دینداری ده&zwnj;که&zwnj;نه کاڵای باڵایان، باشه ئه&zwnj;وه تاکه&zwnj;ی ژیرنابن؟! (بۆ ئه&zwnj;وه&zwnj;نده دنیا په&zwnj;رستن)؟!';
$t[170] = 'ئه&zwnj;وانه&zwnj;ش که به ته&zwnj;وراته&zwnj;وه پابه&zwnj;ندی ده&zwnj;که&zwnj;ن (که هانیان ده&zwnj;دات بۆ باوه&zwnj;ڕهێنان به&zwnj;محمد(صلى الله عليه وسلم)و نوێژه&zwnj;کانیان به&zwnj;چاکی ئه&zwnj;نجام ده&zwnj;دا، بێگومان ئێمه پاداشتی چاکسازان به&zwnj;زایه ناده&zwnj;ین.';
$t[171] = 'یادی ئه&zwnj;و کاته&zwnj;ش بکه&zwnj;ره&zwnj;وه که کێوی طورمان له جێی خۆی هه&zwnj;ڵکه&zwnj;ندو له شوێنی خۆی به&zwnj;رزمان کرده&zwnj;وه به&zwnj;سه&zwnj;ریاندا وه&zwnj;کو په&zwnj;ڵه&zwnj;هه&zwnj;ورێک، جا ئه&zwnj;وکاته (به&zwnj;ته&zwnj;واوی ترسیان لێنیشت و) وایانزانی که ده&zwnj;که&zwnj;وێت به&zwnj;سه&zwnj;ریاندا، (ئه&zwnj;وسا پێمان وتن) ده ئیتر ئه&zwnj;وه&zwnj;ی پێمان به&zwnj;خشیوون به&zwnj;توندی بیگرن و له سنوور ده&zwnj;رمه&zwnj;چن و ئه&zwnj;و فه&zwnj;رمانانه&zwnj;ی که پێمانداون هه&zwnj;میشه له یادتان بێت، بۆ ئه&zwnj;وه&zwnj;ی ئێوه خۆتان بپارێزن.';
$t[172] = 'یادی ئه&zwnj;وکاته&zwnj;ش بکه&zwnj;ره&zwnj;وه که په&zwnj;روه&zwnj;ردگارت پشتاو پشتی نه&zwnj;وه&zwnj;ی ئاده&zwnj;می کۆ کرده&zwnj;وه (له جیهانی په&zwnj;نهان و شاراوه&zwnj;دا په&zwnj;یمانی لێ وه&zwnj;رگرتن) و کردنی به شایه&zwnj;ت به&zwnj;سه&zwnj;ر خۆیانه&zwnj;وه و (پێی فه&zwnj;رموون): ئایا من په&zwnj;روه&zwnj;ردگاری ئێوه نیم؟! هه&zwnj;موو وتیان: نابێت تۆ خوای ئێمه نه&zwnj;بیت، به&zwnj;ڵێ شایه&zwnj;تیمان داوه (که تۆ په&zwnj;روه&zwnj;ردگارمانیت)، ئه&zwnj;مه&zwnj;ش بۆ ئه&zwnj;وه&zwnj;ی نه&zwnj;وه&zwnj;کو له ڕۆژی قیامه&zwnj;تدا بڵێن: به&zwnj;ڕاستی ئێمه له&zwnj;مه بێئاگا بوین!!';
$t[173] = 'یاخود نه&zwnj;وه&zwnj;ک بڵێن: جا ئێمه چی بکه&zwnj;ین، به&zwnj;ڕاستی باوو باپیرانمان له پێشدا هاوه&zwnj;ڵیان بۆ خوا داناوه و ئێمه&zwnj;ش نه&zwnj;وه&zwnj;یه&zwnj;ک بووین دوای ئه&zwnj;وان هاتین و شوێنیان که&zwnj;وتین، جا ئایا تۆ له&zwnj;ناومان ده&zwnj;به&zwnj;یت به&zwnj;هۆی ئه&zwnj;وه&zwnj;ی که پێشێلکه&zwnj;رانی حه&zwnj;ق کردوویانه&zwnj;؟';
$t[174] = 'ئا به&zwnj;وشێوه&zwnj;یه ئایه&zwnj;ت و فه&zwnj;رمانه&zwnj;کان ڕوونده&zwnj;که&zwnj;ینه&zwnj;وه (تالێی ورد ببنه&zwnj;وه&zwnj;) و بۆ ئه&zwnj;وه&zwnj;ی بگه&zwnj;ڕێنه&zwnj;وه (بۆ سه&zwnj;ر ڕاسته شه&zwnj;قامی ئیمان و خواناسین).';
$t[175] = '(ئه&zwnj;ی محمد(صلى الله عليه وسلم) هه&zwnj;واڵی ئه&zwnj;و که&zwnj;سه&zwnj;یان به&zwnj;سه&zwnj;ردا بخوێنه&zwnj;ره&zwnj;وه که فه&zwnj;رمانه&zwnj;کانی خۆمانمان پێبه&zwnj;خشی بوو، که&zwnj;چی ئه&zwnj;ویش خۆی لێ داماڵی و خۆی لێ دووره په&zwnj;رێز گرت، ئه&zwnj;وسا ئیتر شه&zwnj;یتان شوێنی که&zwnj;وت و دایه&zwnj;پێش و چووه ڕیزی سه&zwnj;ر لێشێواوانه&zwnj;وه&zwnj;.';
$t[176] = 'خۆ ئه&zwnj;گه&zwnj;ر بمانویستایه ئه&zwnj;وه به&zwnj;و ئایه&zwnj;ت و فه&zwnj;رمانانه به&zwnj;رزمان ده&zwnj;کرده&zwnj;وه&zwnj;، به&zwnj;ڵام ئه&zwnj;و خۆی چه&zwnj;سپان به&zwnj;زه&zwnj;ویه&zwnj;وه و (ده&zwnj;ستی کرد به&zwnj;دنیا په&zwnj;رستی و) شوێنی ئاره&zwnj;زووی خۆی که&zwnj;وت، جا نموونه&zwnj;ی (ئه&zwnj;و جۆره که&zwnj;سانه&zwnj;) وه&zwnj;کو نموونه&zwnj;ی سه&zwnj;گ وایه ئه&zwnj;گه&zwnj;ر ده&zwnj;ری بکه&zwnj;یت و پیایدا هه&zwnj;ڵبشاخێیت هه&zwnj;ناسه بڕکێیه&zwnj;تی (زمان ده&zwnj;رده&zwnj;کێشێت)، یاخود ئه&zwnj;گه&zwnj;ر وازی لێ بهێنیت ئه&zwnj;وه هه&zwnj;ر هه&zwnj;ناسه بڕکێیه&zwnj;تی، ئه&zwnj;وه نموونه&zwnj;ی ئه&zwnj;و که&zwnj;سانه&zwnj;یه که ئایه&zwnj;ته&zwnj;کانی ئێمه&zwnj;یان به&zwnj;درۆ زانیوه و بڕوایان پێی نه&zwnj;بووه&zwnj;، که&zwnj;وابوو ئه&zwnj;م جۆره به&zwnj;سه&zwnj;رهاتانه بگێڕه&zwnj;ره&zwnj;وه بۆ ئه&zwnj;وه&zwnj;ی (دابچڵه&zwnj;کێن و) بیربکه&zwnj;نه&zwnj;وه&zwnj;.';
$t[177] = 'ئای که چه&zwnj;نده نموونه&zwnj;یه&zwnj;کی ناشرینه&zwnj;، نموونه&zwnj;ی ئه&zwnj;و که&zwnj;سانه&zwnj;ی که به&zwnj;ڵگه&zwnj;و فه&zwnj;رمانه&zwnj;کانی ئێمه&zwnj;یان به&zwnj;درۆ زانیوه&zwnj;و بڕوایان پێ نه&zwnj;کردوه&zwnj;، ئه&zwnj;وانه هه&zwnj;ر سته&zwnj;میان له خۆیان ده&zwnj;کرد.';
$t[178] = 'ئه&zwnj;وه&zwnj;ی خوا ڕێنموویی بکات ئه&zwnj;وه هه&zwnj;ر ئه&zwnj;و ڕێنموویی وه&zwnj;رگره&zwnj;، ئه&zwnj;وه&zwnj;ش که&zwnj;گومڕای بکات ئه&zwnj;وه هه&zwnj;ر ئه&zwnj;وانه زه&zwnj;ره&zwnj;رمه&zwnj;ندن.';
$t[179] = 'سوێند به خوا بێگومان ئێمه زۆر له په&zwnj;ری و ئاده&zwnj;میزادمان بۆ دۆزه&zwnj;خ دروست کردوه&zwnj;، ئه&zwnj;وانه ده&zwnj;زگای دڵیان هه&zwnj;یه و که&zwnj;چی حه&zwnj;قی پێ تێناگه&zwnj;ن، چاویشیان هه&zwnj;یه که&zwnj;چی حه&zwnj;قی پێ نابینن، گوێیان هه&zwnj;یه که&zwnj;چی حه&zwnj;قی پێ نابیستن، ئا ئه&zwnj;وانه وه&zwnj;کو ئاژه&zwnj;ڵ وان ( له شوێنکه&zwnj;وتنی ئاره&zwnj;زوودا) به&zwnj;ڵکو ئه&zwnj;وانه وێڵ تریشن، ئا ئه&zwnj;وانه هه&zwnj;ر غافڵ و بێ ئاگان له حه&zwnj;ق.';
$t[180] = 'هه&zwnj;ر بۆ خوایه هه&zwnj;موو ناوه جوان و پیرۆزه&zwnj;کان، جا (ئه&zwnj;ی ئیمانداران) به&zwnj;و ناوانه دۆعا و نزا بکه&zwnj;ن و هاناو هاوار بۆ ئه&zwnj;و زاته به&zwnj;رن، واز بهێنن له&zwnj;وانه&zwnj;ی که له ناوه پیرۆزه&zwnj;کانی خوادا لادان ده&zwnj;که&zwnj;ن و (سیفه&zwnj;ت و ناوه&zwnj;کانی خوا به&zwnj;کارده&zwnj;هێنن بۆ غه&zwnj;یری خوا)، له ئاینده&zwnj;دا پاداشتی ئه&zwnj;و کارو کرده&zwnj;وانه&zwnj;یان وه&zwnj;رده&zwnj;گرن که ئه&zwnj;نجامیان ده&zwnj;دا.';
$t[181] = 'له&zwnj;و خه&zwnj;ڵکه&zwnj;ش که دروستمان کردوون که&zwnj;سانێک هه&zwnj;ن که (ژیرو هۆشمه&zwnj;ندن و شوێنی حه&zwnj;ق ده&zwnj;که&zwnj;ون و) ڕێنموویی خه&zwnj;ڵک ده&zwnj;که&zwnj;ن و شاره&zwnj;زایان ده&zwnj;که&zwnj;ن به حه&zwnj;ق و ڕاستی، هه&zwnj;ر به&zwnj;وحه&zwnj;قه خه&zwnj;ڵکی ڕاست ده&zwnj;که&zwnj;نه&zwnj;وه (له لادان و سه&zwnj;رکه&zwnj;شی).';
$t[182] = 'ئه&zwnj;وانه&zwnj;ش که به&zwnj;ڵگه و نیشانه و به&zwnj;رنامه&zwnj;ی ئێمه به&zwnj;درۆ ده&zwnj;زانن، له&zwnj;ئاینده&zwnj;دا ورده ورده به&zwnj;ره&zwnj;و تیاچوون ده&zwnj;یانبه&zwnj;ین له کات و شوێنێکدا که ئه&zwnj;وان هه&zwnj;ستی پێناکه&zwnj;ن و نایزانن.';
$t[183] = 'من مۆڵه&zwnj;تیشیان ده&zwnj;ده&zwnj;م، به&zwnj;ڵام که تۆڵه&zwnj;م لێسه&zwnj;ندن به&zwnj;ڕاستی نه&zwnj;خشه&zwnj;و پلانی من توندو به&zwnj;هێزه&zwnj;.';
$t[184] = 'باشه&zwnj;، ئه&zwnj;وه (ئه&zwnj;و خه&zwnj;ڵکه&zwnj;) بیریان نه&zwnj;کردۆته&zwnj;وه (له گوفتارو ڕه&zwnj;فتاری محمد(صلى الله عليه وسلم) که ئه&zwnj;و هاوه&zwnj;ڵه&zwnj;یان هیچ نیشانه&zwnj;یه&zwnj;کی شێتی پێوه نیه&zwnj;، به&zwnj;ڵکو ئه&zwnj;و (به&zwnj;ڕێزه&zwnj;) هیچ شتێک نیه جگه بێدارکه&zwnj;ره&zwnj;وه&zwnj;یه&zwnj;کی ئاشکرا نه&zwnj;بێت.';
$t[185] = 'ئایا بۆ سه&zwnj;رنجیان نه&zwnj;داوه له&zwnj;و هه&zwnj;موو ده&zwnj;سه&zwnj;ڵاته گه&zwnj;وره&zwnj;یه&zwnj;ی خوا له ئاسمانه&zwnj;کان و زه&zwnj;ویداو ئه&zwnj;و هه&zwnj;موو شتانه&zwnj;ی که خوا به&zwnj;دی هێناون؟ (بۆ ئه&zwnj;وه&zwnj;نده بێئاگان؟) خۆ به&zwnj;ڕاستی نزیک بۆته&zwnj;وه ئاکامیان، جا به چ گوفتارێکی تر له دوای قورئان باوه&zwnj;ڕ ده&zwnj;هێنن؟';
$t[186] = 'ئه&zwnj;و که&zwnj;سه&zwnj;ی خوا گومڕای بکات (به&zwnj;هۆی لاساری و بێ دینی خۆیه&zwnj;وه&zwnj;) ئه&zwnj;وه هیچ ڕێنموویکه&zwnj;رێکی نیه و خوایش لێیان ده&zwnj;گه&zwnj;ڕێ له ناو سه&zwnj;رگه&zwnj;ردانی و سه&zwnj;رکه&zwnj;شی خۆیاندا بسوڕێنه&zwnj;وه&zwnj;.';
$t[187] = '(ئه&zwnj;ی محمد(صلى الله عليه وسلم) پرسیارت لێده&zwnj;که&zwnj;ن ده&zwnj;رباره&zwnj;ی قیامه&zwnj;ت: که&zwnj;ی به&zwnj;رپا ده&zwnj;بێت؟ پێیان بڵێ: به&zwnj;ڕاستی زانینی کاتی به&zwnj;رپا بوونی هه&zwnj;ر لای په&zwnj;روه&zwnj;ردگارمه&zwnj;، له&zwnj;کاتێکدا که&zwnj;س له سات و کاتی خۆیدا ده&zwnj;ری ناخات و به&zwnj;رپای ناکات زاتی په&zwnj;روه&zwnj;ردگار نه&zwnj;بێت، به&zwnj;رپا بوونی قیامه&zwnj;ت زۆر گرانه و زۆر ده&zwnj;که&zwnj;وێت له&zwnj;سه&zwnj;ر دانیشتوانی ئاسمانه&zwnj;کان و زه&zwnj;وی (چونکه شیرازه&zwnj;ی هه&zwnj;مووی تێکده&zwnj;چێت و لێکهه&zwnj;ڵده&zwnj;وه&zwnj;شێت) ڕۆژی قیامه&zwnj;ت به&zwnj;رپا نابێت کوتوپڕ نه&zwnj;بێت، له پڕ یه&zwnj;خه&zwnj;تان پێده&zwnj;گرێت، وا پرسیارت لێده&zwnj;که&zwnj;ن هه&zwnj;ر وه&zwnj;ک تۆ زۆر عه&zwnj;وداڵی زانینی هاتنی ڕۆژی قیامه&zwnj;ت بیت و پرسیاری زۆرت له باره&zwnj;یه&zwnj;وه کردبێت!! پێیان بڵێ: به&zwnj;ڕاستی زانینی به&zwnj;رپا بوونی قیامه&zwnj;ت ته&zwnj;نها لای خوایه&zwnj;، به&zwnj;ڵام زۆربه&zwnj;ی خه&zwnj;ڵکیش (ئه&zwnj;مه&zwnj;) نازانن.';
$t[188] = '(ئه&zwnj;ی محمد(صلى الله عليه وسلم) پێیان بڵێ: که من ناتوانم هیچ قازانج یان زه&zwnj;ره&zwnj;رێک به&zwnj;خۆم بگه&zwnj;یه&zwnj;نم مه&zwnj;گه&zwnj;ر ویستی خوای له&zwnj;سه&zwnj;ربێت، خۆ ئه&zwnj;گه&zwnj;ر غه&zwnj;یب و شاراوه&zwnj;کانم بزانیایه ئه&zwnj;وه چاکه&zwnj;ی زۆرم کۆده&zwnj;کرده&zwnj;وه بۆخۆم و هیچ ناخۆشیه&zwnj;کیشم تووش نه&zwnj;ده&zwnj;بوو، من هیچ شتێک نیم ته&zwnj;نیا ترسێنه&zwnj;رێک (بۆ یاخیه&zwnj;کان) و مژده&zwnj;ده&zwnj;ریشم بۆ که&zwnj;سانێک که باوه&zwnj;ڕ ده&zwnj;هێنن.';
$t[189] = 'خوا ئه&zwnj;و زاته&zwnj;یه که ئێوه&zwnj;ی له&zwnj;یه&zwnj;ک نه&zwnj;فس و له&zwnj;یه&zwnj;ک شت دروست کردوه&zwnj;و هه&zwnj;ر له&zwnj;ویش جووته&zwnj;که&zwnj;ی به&zwnj;دی هێنا، بۆئه&zwnj;وه&zwnj;ی ئارامی له&zwnj;لا بگرێت، جا کاتێک پیاوه&zwnj;که چووه لای هاوسه&zwnj;ره&zwnj;که&zwnj;ی، ئه&zwnj;ویش سکی پڕبوو به&zwnj;کۆرپه&zwnj;له&zwnj;یه&zwnj;کی سووک، (تا ماوه&zwnj;ی ١٢٠ ڕۆژ) ئافره&zwnj;ته&zwnj;که&zwnj;ش تێپه&zwnj;ڕی کرد به&zwnj;و ماوه&zwnj;یه&zwnj;دا، ئینجا کاتێک که باری به&zwnj;ره&zwnj;و سه&zwnj;نگینی ده&zwnj;چێت ئه&zwnj;و ژن و مێرده له خوا ده&zwnj;پاڕێنه&zwnj;وه&zwnj;و ده&zwnj;ڵێن: خوایه سوێند به تۆ، ئه&zwnj;گه&zwnj;ر منداڵێکی باش و بێ عه&zwnj;یبمان پێببه&zwnj;خشیت، ئه&zwnj;وه به&zwnj;ڕاستی له سوپاسگوزارانی تۆ ده&zwnj;بین.';
$t[190] = 'که&zwnj;چی کاتێک نه&zwnj;وه&zwnj;ی پاک و بێ عه&zwnj;یبی پێ به&zwnj;خشین ئه&zwnj;وان هاوه&zwnj;ڵیان بۆ خوا دانا له&zwnj;وه&zwnj;ی که خوا پێی دابوون (به&zwnj;خشینی منداڵ ده&zwnj;ده&zwnj;نه پاڵ هۆکاره&zwnj;کان، نه&zwnj;ک ده&zwnj;سه&zwnj;ڵاتی خوا)، خوا به&zwnj;رزتره له&zwnj;وه&zwnj;ی که ئه&zwnj;وان هاوبه&zwnj;شی بۆ بڕیار ده&zwnj;ده&zwnj;ن.';
$t[191] = 'ئایا که&zwnj;سێک یان شتێک ده&zwnj;که&zwnj;نه شه&zwnj;ریک و هاوبه&zwnj;ش که ناتوانێت هیچ شتێک دروست بکات، له&zwnj;کاتێکدا خۆشیان دروستکراون و (هیچیان به&zwnj;ده&zwnj;ست نیه&zwnj;)...';
$t[192] = 'ئه&zwnj;وانه ناتوانن سه&zwnj;رکه&zwnj;وتن به&zwnj;وان ببه&zwnj;خشن و ناشتوانن یارمه&zwnj;تی خۆیان بده&zwnj;ن و به&zwnj;رگریی له خۆیشیان بکه&zwnj;ن.';
$t[193] = 'ئه&zwnj;گه&zwnj;ر بانگیشیان بکه&zwnj;ن بۆ ڕێبازی هیدایه&zwnj;ت، ئه&zwnj;وه شوێنتان ناکه&zwnj;ون، چوونیه&zwnj;کیشه بۆتان ئایا بانگتان کردبن، یان ئێوه بێ ده&zwnj;نگ بن (ناتوانن بیانهه&zwnj;ژێنن ئه&zwnj;گه&zwnj;ر خوا ویستی له&zwnj;سه&zwnj;ر نه&zwnj;بێت).';
$t[194] = 'به&zwnj;ڕاستی ئه&zwnj;وانه&zwnj;ی ئێوه هاواریان لێده&zwnj;که&zwnj;ن جگه&zwnj;له خواو لێیان ده&zwnj;پاڕێنه&zwnj;وه&zwnj;، چه&zwnj;ند به&zwnj;نده&zwnj;یه&zwnj;کی وه&zwnj;ک ئێوه&zwnj;ن (هیچیان به&zwnj;ده&zwnj;ست نیه&zwnj;) ده هاواریان لێ بکه&zwnj;ن، بابێن به&zwnj;ده&zwnj;متانه&zwnj;وه&zwnj;، ئه&zwnj;گه&zwnj;ر ئێوه ڕاست ده&zwnj;که&zwnj;ن (له&zwnj;و داوایه&zwnj;تاندا).';
$t[195] = 'ئایا ئه&zwnj;و په&zwnj;رستراوانه قاچیان هه&zwnj;یه پێی بڕۆن به&zwnj;ڕێوه&zwnj;؟ یان ده&zwnj;ستیان هه&zwnj;یه ڕای بوه&zwnj;شێنن و ئازاری نه&zwnj;یاره&zwnj;کانی پێ بده&zwnj;ن؟ یاخود چاویان هه&zwnj;یه پێی ببینن؟ یاخود گوێیان هه&zwnj;یه پێی ببیستن؟ ئه&zwnj;ی محمد(صلى الله عليه وسلم) پێیان بڵێ: داوا له په&zwnj;رستراوه&zwnj;کانتان بکه&zwnj;ن و چیتان له ده&zwnj;ست دێت له پیلان گێڕان بیکه&zwnj;ن و مۆڵه&zwnj;تم مه&zwnj;ده&zwnj;ن...';
$t[196] = '(هه&zwnj;روه&zwnj;ها بڵێ): به&zwnj;ڕاستی پشت و په&zwnj;نای من ئه&zwnj;و خوایه&zwnj;یه که قورئانی دابه&zwnj;زاندوه&zwnj;و له&zwnj;کاتێکدا ئه&zwnj;و زاته سه&zwnj;رپه&zwnj;رشتی چاکان و پاکان ده&zwnj;کات.';
$t[197] = 'ئه&zwnj;وانه&zwnj;ی که ئێوه جگه له خوا هاناو هاواریان بۆ ده&zwnj;به&zwnj;ن ناتوانن سه&zwnj;رکه&zwnj;وتوتان بکه&zwnj;ن، (ته&zwnj;نانه&zwnj;ت) ناتوانن یارمه&zwnj;تی خۆیشیان بده&zwnj;ن.';
$t[198] = 'خۆ ئه&zwnj;گه&zwnj;ر بانگیان بکه&zwnj;یت بۆ ڕێبازی هیدایه&zwnj;ت، ئه&zwnj;وه (بانگه&zwnj;وازت) نابیستن (چ جای ئه&zwnj;وه&zwnj;ی یارمه&zwnj;تیشت بده&zwnj;ن)، تۆ ده&zwnj;یانبینیت ئه&zwnj;وان سه&zwnj;یرت ده&zwnj;که&zwnj;ن، له&zwnj;کاتێکدا که نابینن.';
$t[199] = 'کاری ئاسان له&zwnj;خه&zwnj;ڵکی داوا بکه&zwnj;و چاوپۆشی له هه&zwnj;ڵه&zwnj;و که&zwnj;موکوڕیه&zwnj;کانیان بکه&zwnj;و، فه&zwnj;رمان بده به چاکه&zwnj;و کرده&zwnj;وه&zwnj;یه&zwnj;ک که په&zwnj;سه&zwnj;ندی شه&zwnj;رعه&zwnj;، ڕووش وه&zwnj;رگێڕه&zwnj;و پشت بکه له نه&zwnj;فام و تێ نه&zwnj;گه&zwnj;یشتوان.';
$t[200] = 'ئه&zwnj;گه&zwnj;ر وه&zwnj;سوه&zwnj;سه&zwnj;یه&zwnj;کت له&zwnj;لایه&zwnj;ن شه&zwnj;یتانه&zwnj;وه بۆ پێشهات و ویستی له خشته&zwnj;ت به&zwnj;رێت و تووشی هه&zwnj;ڵه&zwnj;ت بکات، جا ئه&zwnj;وه تۆ په&zwnj;نا بگره به&zwnj;خواو پشت به&zwnj;و ببه&zwnj;سته&zwnj;، چونکه به&zwnj;ڕاستی ئه&zwnj;و زاته بیسه&zwnj;ر و زانایه&zwnj;.';
$t[201] = 'به&zwnj;ڕاستی ئه&zwnj;وانه&zwnj;ی که پارێزکار و ته&zwnj;قوادار بوون، کاتێک خه&zwnj;ته&zwnj;ره&zwnj;و خه&zwnj;یاڵێک له&zwnj;لایه&zwnj;ن شه&zwnj;یتانه&zwnj;وه تووشیان بێت، ئه&zwnj;وه خێرا بێدار ده&zwnj;بنه&zwnj;وه و خوای خۆیان بیر ده&zwnj;که&zwnj;وێته&zwnj;وه و هۆشیار ده&zwnj;بنه&zwnj;وه&zwnj;، ئیتر ده&zwnj;ستبه&zwnj;جێ بینا ده&zwnj;بن و به&zwnj;رچاویان ڕوون ده&zwnj;بێت (رێبازی ڕاست به&zwnj;دی ده&zwnj;که&zwnj;ن و لێیان ناشێوێت).';
$t[202] = 'برا شه&zwnj;یتانه&zwnj;کانیشیان کۆمه&zwnj;کیان ده&zwnj;که&zwnj;ن له گومڕایی کردنیان به&zwnj;ڕازاندنه&zwnj;وه و جوانکردنی گوناهو تاوانه&zwnj;کان، پاشان کۆڵ ناده&zwnj;ن و کول نابن (له گومڕایی کردنیاندا).';
$t[203] = 'کاتێکیش تۆ (له&zwnj;سه&zwnj;ر داخوازیان)، به&zwnj;ڵگه&zwnj;و موعجیزه&zwnj;یه&zwnj;کیان بۆ نه&zwnj;هێنیت، ئه&zwnj;وه خوانه&zwnj;ناسان (به&zwnj;گاڵته پێکردنه&zwnj;وه&zwnj;) ده&zwnj;ڵێن: ده&zwnj;ی بۆ هه&zwnj;ڵت نه&zwnj;به&zwnj;ست و داتنه&zwnj;هێنا؟! بڵێ (ئه&zwnj;ی محمد(صلى الله عليه وسلم) من له&zwnj;و که&zwnj;سانه نیم که خۆم شت هه&zwnj;ڵبه&zwnj;ستم) به&zwnj;ڕاستی من ته&zwnj;نها شوێنکه&zwnj;وته&zwnj;ی ئه&zwnj;و نیگایه&zwnj;م که له&zwnj;لایه&zwnj;ن په&zwnj;روه&zwnj;ردگارمه&zwnj;وه بۆم دێت، ئه&zwnj;م قورئانه&zwnj;ی (که ئێوه باوه&zwnj;ڕتان پێی نیه&zwnj;) چه&zwnj;نده&zwnj;ها به&zwnj;ڵگه&zwnj;و به&zwnj;رچاو ڕوونیه بۆ دڵه&zwnj;کان له لایه&zwnj;ن په&zwnj;روه&zwnj;ردگارتانه&zwnj;وه&zwnj;، هیدایه&zwnj;ت به&zwnj;خش و ڕه&zwnj;حمه&zwnj;تیشه بۆ ئه&zwnj;و که&zwnj;سانه&zwnj;ی که باوه&zwnj;ڕ ده&zwnj;هێنن.';
$t[204] = 'کاتێکیش که قورئان ده&zwnj;خوێندرێت، ئه&zwnj;وه گوێی بۆ بگرن و دڵی بده&zwnj;نێ و بێ ده&zwnj;نگ بن، بۆ ئه&zwnj;وه&zwnj;ی ئێوه ڕه&zwnj;حمتان پێ بکرێت و په&zwnj;روه&zwnj;ردگار به&zwnj;زه&zwnj;یی پێتاندا بێته&zwnj;وه (گوێ گرتن به&zwnj;دڵ و بێده&zwnj;نگی کار کردی خۆی هه&zwnj;یه له&zwnj;سه&zwnj;ر بیسه&zwnj;ر، بێجگه له پاداشتی بێ سنوور).';
$t[205] = 'یادی په&zwnj;روه&zwnj;ردگارت له دڵ و ده&zwnj;روونی خۆتدا به لاڵانه&zwnj;وه&zwnj;و ترسه&zwnj;وه بکه&zwnj;و، بێ ده&zwnj;نگ به&zwnj;رز کردنه&zwnj;وه ( له نێوان چپه و ئاشکرادا)، له به&zwnj;یانیان و ده&zwnj;مه و ئێواراندا، له&zwnj;بێئاگایان مه&zwnj;به (ئه&zwnj;وانه&zwnj;ی دڵیان غافڵه&zwnj;).';
$t[206] = 'به&zwnj;ڕاستی ئه&zwnj;وانه&zwnj;ی لای په&zwnj;روه&zwnj;ردگارتن خۆیان به&zwnj;گه&zwnj;وره ناگرن له په&zwnj;رستنی ئه&zwnj;و زاته&zwnj;داو به&zwnj;رده&zwnj;وام ته&zwnj;سبیحات و ستایشی ده&zwnj;که&zwnj;ن و سوژده و کڕنووشی بۆ ده&zwnj;به&zwnj;ن (حه&zwnj;ق وایه سه&zwnj;رجه&zwnj;م ئاده&zwnj;میزاده&zwnj;کانیش چاو له&zwnj;وان بکه&zwnj;ن).';
?>