$t[7] = 'ئه&zwnj;و به&zwnj;خته&zwnj;وه&zwnj;رانه&zwnj;، یه&zwnj;کێك له سیفاتیان ئه&zwnj;وه&zwnj;یه&zwnj;: وه&zwnj;فا به&zwnj;نه&zwnj;زر ده&zwnj;که&zwnj;ن، یاخود به&zwnj;ڵێنێکیاندا وه&zwnj;فادارن و ئه&zwnj;نجامی ده&zwnj;ده&zwnj;ن و بیمیان هه&zwnj;یه له ڕۆژێك که شه&zwnj;ڕی لێ ده&zwnj;بارێت و پرشو بڵاوه&zwnj;...';
$t[8] = 'هه&zwnj;روه&zwnj;ها نان و خۆراك ده&zwnj;به&zwnj;خشن، ئه&zwnj;گه&zwnj;رچی خۆشه&zwnj;ویستیش بێت لایان، به&zwnj;هه&zwnj;ژارو هه&zwnj;تیوو دیله&zwnj;کان.';
$t[9] = 'ده&zwnj;ڵێن: بێگومان ئێمه ئه&zwnj;م خۆراکه&zwnj;تان هه&zwnj;ر له&zwnj;به&zwnj;ر خواو بۆ به&zwnj;ده&zwnj;ستهێنانی ڕه&zwnj;زامه&zwnj;ندی ئه&zwnj;و پێده&zwnj;به&zwnj;خشین، نه پاداشتمان لێتان ده&zwnj;وێت نه سوپاس.';
$t[10] = 'هه&zwnj;روه&zwnj;ها ده&zwnj;ڵێن: ئه&zwnj;م کاره بۆیه ده&zwnj;که&zwnj;ین چونکه له ڕۆژێکی تاڵ و گرژو ڕه&zwnj;ش و تاریك ده&zwnj;ترسین، که په&zwnj;روه&zwnj;ردگارمان پێشی ده&zwnj;هێنێت.';
$t[11] = 'جا خوای گه&zwnj;وره&zwnj;ش له شه&zwnj;ڕو ناخۆشی و نه&zwnj;هامه&zwnj;تی ئه&zwnj;و ڕۆژه پاڕاستنی و ڕووی گه&zwnj;ش و شادمانی پێبه&zwnj;خشین.';
