$t[15] = 'هه&zwnj;روه&zwnj;ها له سوراحی و دۆلکه&zwnj;ی زیودا شه&zwnj;رابیان بۆ ده&zwnj;گێڕن که ده&zwnj;کرێته ئه&zwnj;و کوپ و په&zwnj;رداخانه&zwnj;وه که له شووشه&zwnj;و کریستاڵی تایبه&zwnj;ت دروستکراون.';
$t[16] = 'هه&zwnj;موو دام و ده&zwnj;زگای خواردن و خواردنه&zwnj;وه&zwnj;کان له شووشه&zwnj;ی زیوی دروستکراون به&zwnj;شێوه&zwnj;یه&zwnj;کی جوان و دڵڕفێن.';
$t[17] = 'هه&zwnj;روه&zwnj;ها شه&zwnj;رابێکی تریان پێشکه&zwnj;ش ده&zwnj;کرێت که ئاوێته&zwnj;که&zwnj;ی زه&zwnj;نجه&zwnj;بیله&zwnj;.';
$t[18] = 'که سه&zwnj;رچاوه&zwnj;یه&zwnj;که له به&zwnj;هه&zwnj;شتدا به سه&zwnj;لسه&zwnj;بیل ناو ده&zwnj;برێت.';
$t[19] = 'ئینجا نه&zwnj;وجه&zwnj;وانانی هه&zwnj;میشه لاوو ڕووخۆش و جوان و پاك، به&zwnj;ناویاندا ده&zwnj;گه&zwnj;ڕێن (قسه&zwnj;ی خۆش و سروودی به&zwnj;تام ده&zwnj;ڵێن) کاتێك ده&zwnj;یانبینیت وا ده&zwnj;زانیت مروارین و به&zwnj;و ناوه&zwnj;دا بڵاو بوونه&zwnj;ته&zwnj;وه&zwnj;.';
$t[20] = 'کاتێکیش به&zwnj;هه&zwnj;ر لایه&zwnj;کدا چاوده&zwnj;گێڕیت، ئه&zwnj;وه نازو نیعمه&zwnj;تی نه&zwnj;بڕاوه&zwnj;و بێ ئه&zwnj;ندازو ده&zwnj;سه&zwnj;ڵاتێکی گه&zwnj;وره ده&zwnj;بینیت له باخ و باخات و کۆشك و ته&zwnj;لارو دیمه&zwnj;نی جوان و فراوانی بێ سنوور.';
$t[21] = 'پۆشاکی به&zwnj;خته&zwnj;وه&zwnj;ران ئاوریشمی سه&zwnj;وزی ته&zwnj;نکه که به&zwnj;سه&zwnj;ر پۆشاکی ئاوریشمی ئه&zwnj;ستووردا له&zwnj;به&zwnj;ریان کردووه&zwnj;، به بازنی زیوی ڕازێنراونه&zwnj;ته&zwnj;وه&zwnj;، ئینجا له&zwnj;لایه&zwnj;ن په&zwnj;روه&zwnj;ردگاریانه&zwnj;وه شه&zwnj;رابی پاك و بێگه&zwnj;رد و خاوێنیان پێشکه&zwnj;ش کراوه&zwnj;.';
$t[22] = 'پاشان په&zwnj;روه&zwnj;ردگاریان پێیان ده&zwnj;فه&zwnj;رموێت: به&zwnj;ڕاستی ئه&zwnj;م پاداشته شایسته&zwnj;ی ئێوه&zwnj;یه&zwnj;، هه&zwnj;وڵ و کۆشش و کارو کرده&zwnj;وه&zwnj;ی ئێوه شایانی سوپاس و ڕێزه&zwnj;.. (بێگومان خوای گه&zwnj;وره به&zwnj;م سوپاسگوزاریه ئه&zwnj;وه&zwnj;نده&zwnj;ی تر به&zwnj;خته&zwnj;وه&zwnj;ران و دڵخۆش ده&zwnj;کات).';
$t[23] = 'دڵنیاش به ئه&zwnj;ی محمد صلی الله علیه وسلم هه&zwnj;ر ئێمه قورئانمان بۆ تۆ دابه&zwnj;زاندووه به&zwnj;ش به&zwnj;ش (تا خه&zwnj;ڵکی پێ بێدار بکه&zwnj;یته&zwnj;وه&zwnj;).';
