<?php
$t = array();
$t[1] = '(ئه&zwnj;ی محمد(صلى الله عليه وسلم) پرسیارت لێ ده&zwnj;که&zwnj;ن ده&zwnj;رباره&zwnj;ی ده&zwnj;سکه&zwnj;وته&zwnj;کان، تۆ بڵێ: هه&zwnj;موو ده&zwnj;سکه&zwnj;وته&zwnj;کان هی خواو پێغه&zwnj;مبه&zwnj;رن (هه&zwnj;ر ئه&zwnj;وان بۆیان هه&zwnj;یه بڕیاری چۆنیه&zwnj;تی دابه&zwnj;ش کردنی بده&zwnj;ن)، که&zwnj;واته له خوا بترسن و چاکسازی نێوانی خۆتان بکه&zwnj;ن (مه&zwnj;یه&zwnj;ڵن له&zwnj;سه&zwnj;ر ته&zwnj;ماعی دنیا نێوانتان بشێوێت) و فه&zwnj;رمانبه&zwnj;رداری خوا و پێغه&zwnj;مبه&zwnj;ره&zwnj;که&zwnj;ی بن، ئه&zwnj;گه&zwnj;ر ئێوه ئیماندارن.';
$t[2] = 'بێگومان ئیماندارانی ڕاسته&zwnj;قینه ته&zwnj;نها ئه&zwnj;وانه&zwnj;ن: (یه&zwnj;که&zwnj;م) که کاتێک ناوی خوا برا، ئه&zwnj;وه دڵه&zwnj;کانیان ده&zwnj;ترسێت و ده&zwnj;هه&zwnj;ژێت، (دووهه&zwnj;م) کاتێکیش ئایه&zwnj;ت و فه&zwnj;رمانه&zwnj;کانی ئێمه&zwnj;یان به&zwnj;سه&zwnj;ردا بخوێنرێته&zwnj;وه&zwnj;، ئه&zwnj;وه ئیمان و باوه&zwnj;ڕیان زیاد ده&zwnj;کات، (سێهه&zwnj;م) (له هه&zwnj;موو کارو بارێکیشیاندا) هه&zwnj;ر پشت به په&zwnj;روه&zwnj;ردگاری خۆیان ده&zwnj;به&zwnj;ستن...';
$t[3] = '(چواره&zwnj;م) ئه&zwnj;وانه&zwnj;ن که نوێژه&zwnj;کانیان به&zwnj;چاکی ئه&zwnj;نجام ده&zwnj;ده&zwnj;ن، (پێنجه&zwnj;م) هه&zwnj;ندێک له&zwnj;و ڕزق و ڕۆزیه&zwnj;ی پێمانداون، ده&zwnj;به&zwnj;خشن و سه&zwnj;رفی ده&zwnj;که&zwnj;ن...';
$t[4] = 'ئا ئه&zwnj;وانه هه&zwnj;ر خۆیان ئیماندارانی ڕاسته&zwnj;قینه&zwnj;ن، چه&zwnj;نده&zwnj;ها پله&zwnj;وپایه&zwnj;ی به&zwnj;رزیان بۆ هه&zwnj;یه لای په&zwnj;روه&zwnj;ردگاریان و لێخۆش بوون و ڕزق و ڕۆزی هه&zwnj;میشه&zwnj;یی له&zwnj;گه&zwnj;ڵ ڕێزدا.';
$t[5] = '(شێوه&zwnj;ی دابه&zwnj;ش کردنی ده&zwnj;سکه&zwnj;وته&zwnj;کانی غه&zwnj;زای به&zwnj;در هه&zwnj;ندێ موسڵمان پێی دڵگران بوون) وه&zwnj;ک چۆن په&zwnj;روه&zwnj;ردگارت به حه&zwnj;ق و ڕاستی له نێو ماڵه&zwnj;که&zwnj;ت ده&zwnj;ری هێنایت له&zwnj;کاتێکدا به&zwnj;ڕاستی کۆمه&zwnj;ڵێک له بڕواداران پێی دڵگران بوون.';
$t[6] = 'ئه&zwnj;و کۆمه&zwnj;ڵه ئیمانداره موجاده&zwnj;له&zwnj;ت له&zwnj;گه&zwnj;ڵ ده&zwnj;که&zwnj;ن له شتی ڕاست و دروستدا، دوای ڕوون بونه&zwnj;وه&zwnj;ی (جه&zwnj;نگیان پێ ناخۆشه&zwnj;) وه&zwnj;ک بدرێنه به&zwnj;ر بۆ مردن له&zwnj;کاتێکدا به&zwnj;چاوی خۆیان هۆکاری مه&zwnj;رگ ببینن.';
$t[7] = 'یادی ئه&zwnj;و کاته بکه&zwnj;نه&zwnj;وه خوا به&zwnj;ڵێنی پێدابوون که یه&zwnj;کێک له&zwnj;و دوو ده&zwnj;سته&zwnj;یه به&zwnj;ڕاستی بۆ ئێوه ده&zwnj;بێت (یان کاروانه&zwnj;که یان سه&zwnj;رکه&zwnj;وتن به&zwnj;سه&zwnj;ر له&zwnj;شکره&zwnj;که&zwnj;دا) ئێوه&zwnj;ش به&zwnj;ڕاستی ئاواته خوازن و حه&zwnj;ز ده&zwnj;که&zwnj;ن کاروانه بێ چه&zwnj;ک و ئاسانه&zwnj;که بۆ ئێوه بێت، خوایش ده&zwnj;یه&zwnj;وێت حه&zwnj;ق و ڕاستی به&zwnj;فه&zwnj;رمانه&zwnj;کانی خۆی بچه&zwnj;سپێنێت و کافرانیش بنه&zwnj;بڕ بکات و ڕیشه&zwnj;که&zwnj;نیان بکات...';
$t[8] = 'بۆ ئه&zwnj;وه&zwnj;ی حه&zwnj;ق و ڕاستی بچه&zwnj;سپێنێت و به&zwnj;تاڵ و ناحه&zwnj;قیش پووچ بکاته&zwnj;وه و له&zwnj;ناوی ببات، هه&zwnj;رچه&zwnj;نده تاوانبارو تاوانکاران پێیان ناخۆشه&zwnj;.';
$t[9] = 'یادی ئه&zwnj;وکاته بکه&zwnj;نه&zwnj;وه که&zwnj;له په&zwnj;روه&zwnj;ردگارتان ده&zwnj;پاڕانه&zwnj;وه و داوای یارمه&zwnj;تیتان لێ ده&zwnj;کرد، خوایش خێرا هات به هاناتانه&zwnj;وه و فه&zwnj;رمووی به&zwnj;ڕاستی من به&zwnj;هه&zwnj;زار فریشته&zwnj;ی تر یارمه&zwnj;تی و کۆمه&zwnj;کیتان ده&zwnj;که&zwnj;م به&zwnj;شوێن ئه&zwnj;وانی تردا.';
$t[10] = 'ئه&zwnj;م یارمه&zwnj;تیه&zwnj;ش جگه&zwnj;له مژده&zwnj;یه&zwnj;کی خوایی شتێکی تر نه&zwnj;بوو، تا دڵه&zwnj;کانیشتان ئارام بگرێت و دڵنیابن به&zwnj;و یارمه&zwnj;تیه&zwnj;، ئه&zwnj;گینا سه&zwnj;رکه&zwnj;وتن ته&zwnj;نها له لایه&zwnj;ن خواوه نه&zwnj;بێت نیه&zwnj;، چونکه به&zwnj;ڕاستی هه&zwnj;ر خوا خۆی باڵاده&zwnj;ست و دانایه&zwnj;.';
$t[11] = 'ئه&zwnj;و مژده و ئارامی دڵه کاتێک بوو که خوای گه&zwnj;وره به&zwnj;وه&zwnj;نه&zwnj;وز دایگرتن، بۆ ئه&zwnj;وه&zwnj;ی هێمنیه&zwnj;ک بێت بۆتان له&zwnj;لایه&zwnj;ن خۆیه&zwnj;وه و ئاوێکیشی له ئاسمانه&zwnj;وه به&zwnj;سه&zwnj;رتاندا باراند بۆ ئه&zwnj;وه&zwnj;ی خاوێنتان بکاته&zwnj;وه پێی و پۆخڵی شه&zwnj;یتانتان لێ دوور بخاته&zwnj;وه (تا له ڕووی جه&zwnj;سته و دڵ و ده&zwnj;روونه&zwnj;وه خاوێن بن)و دڵه&zwnj;کانتان به&zwnj;هێزو دامه&zwnj;زراو بکات و بیانبه&zwnj;ستێت به خواوه و به&zwnj;و ئاوه پێیه&zwnj;کانیشتان جێگیرو قایم بکات.';
$t[12] = 'ئه&zwnj;ی محمد(صلى الله عليه وسلم) یادی ئه&zwnj;وه بکه&zwnj;ره&zwnj;وه کاتێک په&zwnj;روه&zwnj;ردگارت وه&zwnj;حی و نیگای ده&zwnj;کرد بۆ فریشته&zwnj;کان که&zwnj;: به&zwnj;ڕاستی من یارو یاوه&zwnj;رتانم، که&zwnj;وابوو ئێوه&zwnj;ش دڵی ئیمانداران پابه&zwnj;ندو دامه&zwnj;زراو بکه&zwnj;ن، له&zwnj;مه&zwnj;ودوا منیش ترس و بیم ده&zwnj;خه&zwnj;مه دڵی ئه&zwnj;وانه&zwnj;ی که بێ باوه&zwnj;ڕ بوون، ده ئێوه&zwnj;ش له سه&zwnj;رووی گه&zwnj;ردنی ناپاکان بده&zwnj;ن و ده&zwnj;ست بوه&zwnj;شێنن له هه&zwnj;موو سه&zwnj;رپه&zwnj;نجه&zwnj;کانیان (تا کافره&zwnj;کان شمشێریان پێ نه&zwnj;گیرێت).';
$t[13] = 'ئه&zwnj;وه له&zwnj;به&zwnj;ر ئه&zwnj;وه بوو چونکه به&zwnj;ڕاستی ئه&zwnj;وان دژایه&zwnj;تی و سه&zwnj;رپێچی خواو پێغه&zwnj;مبه&zwnj;ره&zwnj;که&zwnj;یان کرد، هه&zwnj;ر که&zwnj;سیش دژایه&zwnj;تی و سه&zwnj;رپێچی خواو پێغه&zwnj;مبه&zwnj;ره&zwnj;که&zwnj;ی بکات، ئه&zwnj;وه به&zwnj;ڕاستی خوا تۆڵه&zwnj;سێنه&zwnj;رێکی به&zwnj;زه&zwnj;بره&zwnj;.';
$t[14] = 'ئه&zwnj;وه&zwnj;ته (تۆڵه&zwnj;ی خواتان) جا ئێوه&zwnj;ش بچێژن و بێگومان سزای ئاگری دۆزه&zwnj;خ هه&zwnj;میشه هه&zwnj;ر بۆ بێ باوه&zwnj;ڕانه&zwnj;.';
$t[15] = 'ئه&zwnj;ی ئه&zwnj;وانه&zwnj;ی که باوه&zwnj;ڕتان هێناوه کاتێک به&zwnj;ره&zwnj;نگاری ئه&zwnj;وانه بوونه&zwnj;وه که بێ باوه&zwnj;ڕ بوون له&zwnj;کاتی له&zwnj;شکرکێشیدا ئه&zwnj;وه پشتیان تێمه&zwnj;که&zwnj;ن و ڕامه&zwnj;که&zwnj;ن...';
$t[16] = 'هه&zwnj;رکه&zwnj;س له&zwnj;و ڕۆژه&zwnj;دا ڕابکاته دواوه و پشتیان تێبکات ئه&zwnj;وه به&zwnj;ڕاستی له&zwnj;گه&zwnj;ڵ خه&zwnj;شم و قینی خوادا گه&zwnj;ڕاوه&zwnj;ته&zwnj;وه و سه&zwnj;رئه&zwnj;نجام جێ و ڕێگه&zwnj;ی دۆزه&zwnj;خه که ناخۆشترین جێگه و سه&zwnj;رئه&zwnj;نجامه مه&zwnj;گه&zwnj;ر مه&zwnj;به&zwnj;ستی به&zwnj;کارهێنانی هونه&zwnj;ری جه&zwnj;نگ بێت یاخود مه&zwnj;به&zwnj;ستی شوێن گۆڕین و جێ گۆڕین و جێ گۆڕکێ بێت بۆ لای ده&zwnj;سته&zwnj;یه&zwnj;کی تر.';
$t[17] = 'که&zwnj;وابوو ئه&zwnj;ی ئیمانداران: وه&zwnj;نه&zwnj;بێت ئێوه کافرانتان کوشتبێت، به&zwnj;ڵکو خوا کوشتنی و له&zwnj;ناوی بردوون، تۆش ئه&zwnj;ی محمد(صلى الله عليه وسلم) کاتێک مشتێک خۆڵت ڕوو به&zwnj;کافران فڕێدا ئه&zwnj;و کاره ته&zwnj;نها تۆ نه&zwnj;تکرد، به&zwnj;ڵکو خوا فڕێیداو (خۆڵی کرده چاوی کافرانه&zwnj;وه&zwnj;)، تا ئیمانداران تاقی بکاته&zwnj;وه به تاقی کردنه&zwnj;وه&zwnj;یه&zwnj;کی چاک، چونکه به&zwnj;ڕاستی خوا بیسه&zwnj;ری (دوعاو نزایانه&zwnj;)، زاناشه (به&zwnj;نیه&zwnj;تی دڵیان).';
$t[18] = 'ئه&zwnj;و سه&zwnj;رکه&zwnj;وتن و پشتیوانیه بۆ ئێوه&zwnj;ی ئیمانداره&zwnj;، دڵنیاش بن که خوا ڕیسواکه&zwnj;ری پیلانی بێ باوه&zwnj;ڕانه&zwnj;.';
$t[19] = 'ئیمانداران ئه&zwnj;گه&zwnj;ر ئێوه داوای یارمه&zwnj;تی و سه&zwnj;رکه&zwnj;وتن ده&zwnj;که&zwnj;ن ئه&zwnj;وه به&zwnj;ڕاستی سه&zwnj;رکه&zwnj;وتنتان بۆ هات و ئه&zwnj;گه&zwnj;ر وازبهێنن له سه&zwnj;رپێچی فه&zwnj;رمانه&zwnj;کانی پێغه&zwnj;مبه&zwnj;ر ئه&zwnj;وه چاکتره بۆتان، خۆ ئه&zwnj;گه&zwnj;ر بگه&zwnj;ڕێنه&zwnj;وه بۆ سه&zwnj;رپێچی کردن، ئێمه&zwnj;ش ده&zwnj;گه&zwnj;ڕێینه&zwnj;وه بۆ شکست پێتانداتان، ئه&zwnj;وکاته ده&zwnj;سته&zwnj;و تاقمتان هه&zwnj;رگیز هیچ سوودێکتان پێناگه&zwnj;یه&zwnj;نێت هه&zwnj;رچه&zwnj;ند زۆریش بێت (ژماره&zwnj;تان) چونکه له ڕاستیشدا هه&zwnj;میشه خوا له&zwnj;گه&zwnj;ڵ ئیماندارانی دامه&zwnj;زراو و لێبڕاودایه&zwnj;.';
$t[20] = 'ئه&zwnj;ی ئه&zwnj;وانه&zwnj;ی باوه&zwnj;ڕتان هێناوه&zwnj;، فه&zwnj;رمانبه&zwnj;رداری خواو پێغه&zwnj;مبه&zwnj;ره&zwnj;که&zwnj;ی بن و له به&zwnj;رنامه&zwnj;ی ئه&zwnj;وزاته پشت هه&zwnj;ڵمه&zwnj;که&zwnj;ن، له&zwnj;کاتێکدا ئێوه تێده&zwnj;گه&zwnj;ن و ده&zwnj;بیسن.';
$t[21] = 'وه&zwnj;ک ئه&zwnj;و که&zwnj;سانه&zwnj;ش مه&zwnj;بن (به&zwnj;سه&zwnj;ر زاره&zwnj;کی) ده&zwnj;یانوت: تێگه&zwnj;یشتین و بیستمان، گوێڕایه&zwnj;ڵین، له&zwnj;کاتێکدا ئه&zwnj;وان نابیستن و تێناگه&zwnj;ن (چونکه دڵ و مێشکی خۆیان داخستووه&zwnj;).';
$t[22] = 'به&zwnj;ڕاستی خراپترین زینده&zwnj;وه&zwnj;ران لای خوا ئه&zwnj;و که&zwnj;سانه&zwnj;ن که که&zwnj;ڕن (حه&zwnj;ق نابیستن) و لاڵن (حه&zwnj;ق بێژ نین)، ئه&zwnj;وانه&zwnj;ی که ژیر نابن (بیرو هۆشیان ناخه&zwnj;نه&zwnj;کارو تێنافکرن و بیرو مێشکیان قفڵ داوه&zwnj;).';
$t[23] = 'خۆ ئه&zwnj;گه&zwnj;ر خوا خێری تیادا به&zwnj;دی بکردنایه&zwnj;، ئه&zwnj;وه وای لێده&zwnj;کردن که حه&zwnj;ق و ڕاستی ببیستن، خۆ ئه&zwnj;گه&zwnj;ر حه&zwnj;قیشیان پێ ببیستنایه (که ئاماده&zwnj;نین بۆ وه&zwnj;رگرتنی) ئه&zwnj;وه هه&zwnj;ر پشت له به&zwnj;رنامه&zwnj;ی خوا ده&zwnj;که&zwnj;ن له&zwnj;کاتێکدا ئه&zwnj;وان هه&zwnj;میشه نه&zwnj;یارن.';
$t[24] = 'ئه&zwnj;ی ئه&zwnj;وانه&zwnj;ی باوه&zwnj;ڕتان هێناوه&zwnj;، به&zwnj;ده&zwnj;م بانگه&zwnj;وازی خواو پێغه&zwnj;مبه&zwnj;ره&zwnj;وه بچن کاتێک بانگتان ده&zwnj;کات بۆ به&zwnj;رنامه و ڕێبازێک که ده&zwnj;تانژێنێته&zwnj;وه و ژیان به&zwnj;خشه&zwnj;، چاک بزانن به&zwnj;ڕاستی خوا (زۆر به&zwnj;ئاسانی) ده&zwnj;توانێت ده&zwnj;سه&zwnj;ڵاتی بکه&zwnj;وێته نێوان ئاده&zwnj;میزاد و (ئاواته&zwnj;کانی) دڵیه&zwnj;وه (به&zwnj;گۆڕینیان)، (بیرتان نه&zwnj;چێت) به&zwnj;ڕاستی هه&zwnj;ر بۆ لای ئه&zwnj;ویش کۆده&zwnj;کرێنه&zwnj;وه&zwnj;.';
$t[25] = 'هه&zwnj;روه&zwnj;ها خۆتان بپارێزن له به&zwnj;ڵایه&zwnj;ک تووشی ئه&zwnj;وانه&zwnj;تان نابێت به&zwnj;تایبه&zwnj;ت که سته&zwnj;میان کردوه (به&zwnj;ڵکو هه&zwnj;مووان ده&zwnj;گرێته&zwnj;وه&zwnj;)، بشزانن که به&zwnj;ڕاستی خوا تۆڵه&zwnj;ی زۆر به زه&zwnj;بره&zwnj;.';
$t[26] = 'یادی ئه&zwnj;وکاته بکه&zwnj;نه&zwnj;وه که ئێوه زۆر که&zwnj;م و لاوازکراوو بێ هێزبوون له&zwnj;زه&zwnj;ویدا، ده&zwnj;ترسان که خه&zwnj;ڵکی بتانڕفێنن (بتانکوژن یان ئازارتان بده&zwnj;ن) جا خوای باڵاده&zwnj;ست جێ و ڕێی بۆ سازکردن و به&zwnj;هێزی کردن به&zwnj;سه&zwnj;رکه&zwnj;وتن و یارمه&zwnj;تی خۆی و له ڕزق و ڕۆزیی و نازو نیعمه&zwnj;ته پاکه&zwnj;کان به&zwnj;هره&zwnj;وه&zwnj;ری کردن بۆ ئه&zwnj;وه&zwnj;ی سوپاسگوزاری بکه&zwnj;ن. (ئه&zwnj;مه&zwnj;ش مژده&zwnj;یه&zwnj;کی به&zwnj;رده&zwnj;وامه و پشت به&zwnj;خوا به&zwnj;م زووانه دێته&zwnj;وه دی).';
$t[27] = 'ئه&zwnj;ی ئه&zwnj;وانه&zwnj;ی باوه&zwnj;ڕتان هێناوه&zwnj;: خیانه&zwnj;ت مه&zwnj;که&zwnj;ن له خواو له پێغه&zwnj;مبه&zwnj;ر(صلى الله عليه وسلم) (به&zwnj;وه&zwnj;ی که&zwnj;م ته&zwnj;رخه&zwnj;میتان له فه&zwnj;رمانبه&zwnj;رداریاندا)، هه&zwnj;روه&zwnj;ها خیانه&zwnj;تیش مه&zwnj;که&zwnj;ن له ئه&zwnj;مانه&zwnj;ت و سپارده&zwnj;کانتان که پێتان ده&zwnj;سپێردرێت، له&zwnj;کاتێکدا که ئێوه ده&zwnj;زانن (ئه&zwnj;و کاره&zwnj;تان خیانه&zwnj;ته&zwnj;).';
$t[28] = 'هه&zwnj;روه&zwnj;ها بزانن به&zwnj;ڕاستی ماڵ و سامان و منداڵتان بۆ تاقیکردنه&zwnj;وه&zwnj;یه&zwnj;، به&zwnj;ڕاستی خوای میهره&zwnj;بانیش پاداشتی زۆرو بێ سنووری له لایه (بۆ ئه&zwnj;و که&zwnj;سانه&zwnj;ی که نایه&zwnj;ڵن ماڵ و سامان و منداڵیان ببێته کۆسپ له ڕێگه&zwnj;ی جیهاد و هه&zwnj;وڵ و خه&zwnj;بات و کۆششیاندا).';
$t[29] = 'ئه&zwnj;ی ئه&zwnj;و که&zwnj;سانه&zwnj;ی باوه&zwnj;ڕتان هێناوه&zwnj;: ئه&zwnj;گه&zwnj;ر ته&zwnj;قوای خوا بکه&zwnj;ن و پارێزکاری بکه&zwnj;ن، ئه&zwnj;وه خوا به&zwnj;رچاو ڕۆشنییه&zwnj;کی ته&zwnj;واوتان بۆ فه&zwnj;راهه&zwnj;م ده&zwnj;هێنێت (که بتوانن حه&zwnj;ق و ناحه&zwnj;قی به&zwnj;ئاسانی له&zwnj;یه&zwnj;ک پێ جیابکه&zwnj;نه&zwnj;وه&zwnj;) هه&zwnj;روه&zwnj;ها هه&zwnj;ڵه&zwnj;و گوناهه&zwnj;کانیشتان داده&zwnj;پۆشێت و لێتان خۆش ده&zwnj;بێت، (چونکه&zwnj;)خوا خاوه&zwnj;نی فه&zwnj;زڵ و ڕێزی گه&zwnj;وره&zwnj;و بێ سنووره&zwnj;.';
$t[30] = '(ئه&zwnj;ی محمد(صلى الله عليه وسلم) یادی ئه&zwnj;و کاته بکه&zwnj;ره&zwnj;وه&zwnj;) کاتێک ئه&zwnj;وانه&zwnj;ی بێ باوه&zwnj;ڕ بوون پیلانیان ده&zwnj;گێڕا دژی تۆ بۆ ئه&zwnj;وه&zwnj;ی به&zwnj;ندت بکه&zwnj;ن، یان بتکوژن، یان ده&zwnj;رت بکه&zwnj;ن، ئه&zwnj;وان پیلانی خۆیان ده&zwnj;گێڕن و خوایش پیلانی خۆی ده&zwnj;خاته&zwnj;کار (به&zwnj;پووچ کردنه&zwnj;وه&zwnj;ی کاره&zwnj;کانیان)، دڵنیابن خوای گه&zwnj;وره چاکترینی هه&zwnj;موو پیلانسازو نه&zwnj;خشه سازانه (نایه&zwnj;ڵێ پیلانیان سه&zwnj;ربگرێ).';
$t[31] = 'کاتێکیش ئایه&zwnj;ت و فه&zwnj;رمانه&zwnj;کانی ئێمه به&zwnj;سه&zwnj;ر بێ باوه&zwnj;ڕاندا ده&zwnj;خوێنرێته&zwnj;وه&zwnj;، ئه&zwnj;وه ده&zwnj;ڵێن: به&zwnj;ڕاستی ئێمه بیستمان و ئه&zwnj;گه&zwnj;ر بمانویستایه ئه&zwnj;وه وێنه&zwnj;ی ئه&zwnj;وه&zwnj;مان ده&zwnj;گوت!!، ئه&zwnj;م بابه&zwnj;تانه جگه له داستانی پێشینان شتێکی تر نیه&zwnj;!!';
$t[32] = 'یادی ئه&zwnj;و کاته بکه&zwnj;ره&zwnj;وه که بێ باوه&zwnj;ڕان وتیان: خوایه ئه&zwnj;گه&zwnj;ر ئه&zwnj;م دین و به&zwnj;رنامه و قورئانه ڕاسته&zwnj;، له&zwnj;لایه&zwnj;ن تۆیشه&zwnj;وه ڕه&zwnj;وانه کراوه&zwnj;، ئه&zwnj;وه به&zwnj;رد له ئاسمانه&zwnj;وه ببارێنه به&zwnj;سه&zwnj;رماندا، یان به&zwnj;ڵاو سزایه&zwnj;کی به&zwnj;ئێشمان بۆ بهێنه&zwnj;!!.';
$t[33] = 'بێگومان خوای گه&zwnj;وره (به&zwnj;قسه&zwnj;ی ئه&zwnj;وان ناکات) به&zwnj;ڵاو سزا نادات به&zwnj;سه&zwnj;ریاندا، له&zwnj;کاتێکدا تۆ له&zwnj;ناویاندا بیت (به ئومێدی ئه&zwnj;وه&zwnj;ی که هه&zwnj;ندێکیان باوه&zwnj;ڕ بهێنن)، هه&zwnj;روه&zwnj;ها خوایش سزاده&zwnj;ر نیه له&zwnj;کاتێکدا ئه&zwnj;وان داوای لێخۆشبوون بکه&zwnj;ن.';
$t[34] = 'ئایا چی هه&zwnj;یه بۆ ئه&zwnj;وان که خوا سزایان نه&zwnj;دات؟! له&zwnj;کاتێکدا که ئه&zwnj;وان ڕێگری ده&zwnj;که&zwnj;ن و نایه&zwnj;ڵن خه&zwnj;ڵکی ڕووبکاته مزگه&zwnj;وتی حه&zwnj;رام (که&zwnj;عبه&zwnj;)، ئه&zwnj;وانه هه&zwnj;ر شایسته&zwnj;ی ئه&zwnj;وه نین که سه&zwnj;رپه&zwnj;رشتیاری بن، هیچ تاقمێک شایسته&zwnj;ی سه&zwnj;رپه&zwnj;رشتیاری که&zwnj;عبه نیه بێجگه له خواناس و پارێزکاران، به&zwnj;ڵام زۆربه&zwnj;شیان (له&zwnj;و ڕاستیانه&zwnj;) بێئاگان.';
$t[35] = 'ئه&zwnj;وانه نزا و پاڕانه&zwnj;وه&zwnj;شیان له&zwnj;لای ماڵی خوادا (که&zwnj;عبه&zwnj;ی پیرۆز) جگه له فیکه&zwnj;کێشان و چه&zwnj;پڵه لێدان شتێکی تر نه&zwnj;بووه&zwnj;، که&zwnj;وابوو بچێژن سزای کوشتن و دیلی (له غه&zwnj;زای به&zwnj;دردا) به&zwnj;وه&zwnj;ی که ئێوه بێ باوه&zwnj;ڕو له خوا یاخی ده&zwnj;بوون.';
$t[36] = 'بێگومان ئه&zwnj;وانه&zwnj;ی بێ باوه&zwnj;ڕن، ماڵ و سامانی خۆیان به&zwnj;خت ده&zwnj;که&zwnj;ن بۆ ئه&zwnj;وه&zwnj;ی به&zwnj;شێنه&zwnj;یی به&zwnj;ربه&zwnj;ست دانێن و ڕێگری بکه&zwnj;ن له به&zwnj;رده&zwnj;م ئاین و ڕێبازی خوادا، جا ماڵ و سامانیان له ئاینده&zwnj;دا بۆ ئه&zwnj;و مه&zwnj;به&zwnj;سته خه&zwnj;رج ده&zwnj;که&zwnj;ن! پاشان ده&zwnj;بێته په&zwnj;شیمانی و داخ و حه&zwnj;سره&zwnj;ت له&zwnj;سه&zwnj;ریان، له&zwnj;دوایدا شکست ده&zwnj;هێنن، بێگومان ئه&zwnj;وانه&zwnj;ش که بێ باوه&zwnj;ڕ بوون بۆ دۆزه&zwnj;خ کۆ ده&zwnj;کرێنه&zwnj;وه و ڕاپێچ ده&zwnj;کرێن.';
$t[37] = '(به&zwnj;سه&zwnj;رهات و ڕووداوه&zwnj;کان) بۆ ئه&zwnj;وه&zwnj;یه که: خوا که&zwnj;سانی ده&zwnj;روون پیس و ناپاک له&zwnj;که&zwnj;سانی ده&zwnj;روون خاوێن و پاک جیا بکاته&zwnj;وه و پیس و ناپاکه&zwnj;کانیش له&zwnj;سه&zwnj;ریه&zwnj;ک که&zwnj;ڵه&zwnj;که بکات و هه&zwnj;موویان بدات به مل یه&zwnj;کدا بۆ ئه&zwnj;وه&zwnj;ی کۆیان بکاته&zwnj;وه به&zwnj;گشتی، ئینجا فڕێیان بداته ناو دۆزه&zwnj;خه&zwnj;وه&zwnj;، ئا ئه&zwnj;وانه هه&zwnj;ر خۆیان خه&zwnj;ساره&zwnj;تمه&zwnj;ند و زه&zwnj;ره&zwnj;رمه&zwnj;ندن.';
$t[38] = 'ئه&zwnj;ی محمد(صلى الله عليه وسلم) به&zwnj;کافران بڵێ: ئه&zwnj;گه&zwnj;ر کۆڵ بده&zwnj;ن و واز بهێنن و(باوه&zwnj;ڕبهێنن)، ئه&zwnj;وه بێگومان چاوپۆشی له گوناهی ڕابردوویان ده&zwnj;کرێت، خۆ ئه&zwnj;گه&zwnj;ر بگه&zwnj;ڕێنه&zwnj;وه&zwnj;و به&zwnj;به&zwnj;رده&zwnj;وامی دژایه&zwnj;تی (ئاینی خوا) بکه&zwnj;ن، جا ئه&zwnj;وه به&zwnj;ڕاستی یاسای نه&zwnj;ته&zwnj;وه یاخی بووه پێشینه&zwnj;کانیان به&zwnj;سه&zwnj;ردا ده&zwnj;سه&zwnj;پێنرێت.';
$t[39] = '(ئه&zwnj;ی ئیمانداران) ئێوه&zwnj;ش بجه&zwnj;نگن دژ به&zwnj;کافرو خوانه&zwnj;ناسان تا فیتنه و ئاشووب و ئازاردانی موسڵمان نه&zwnj;مێنێت و (ئه&zwnj;و خه&zwnj;ڵکه سه&zwnj;ربه&zwnj;ست بن له هه&zwnj;ڵبژاردنی به&zwnj;رنامه و ئایندا)، تا دین و فه&zwnj;رمانبه&zwnj;رداری هه&zwnj;ر هه&zwnj;مووی بۆ خوا بێت، جا ئه&zwnj;گه&zwnj;ر بێ باوه&zwnj;ڕان کۆڵ بده&zwnj;ن، ئه&zwnj;وه بابزانن به&zwnj;ڕاستی خوا بینایه به&zwnj;هه&zwnj;موو ئه&zwnj;و کارو کرده&zwnj;وانه&zwnj;ی که ده&zwnj;یکه&zwnj;ن.';
$t[40] = 'خۆ ئه&zwnj;گه&zwnj;ر ئه&zwnj;و خوانه&zwnj;ناسانه هه&zwnj;ر پشتهه&zwnj;ڵکه&zwnj;ن و واز نه&zwnj;هێنن، ئه&zwnj;وه بزانن که خوا پشتیوان و سه&zwnj;رپه&zwnj;رشتیارتانه که پشتیوانێکی چاک و یارمه&zwnj;تی ده&zwnj;رێکی چاکه&zwnj;.';
$t[41] = '(ئه&zwnj;ی ئیمانداران) ئاگاداربن و بزانن که&zwnj;: به&zwnj;ڕاستی هه&zwnj;رچیه&zwnj;کتان ده&zwnj;ستکه&zwnj;وت (له جه&zwnj;نگی بێ باوه&zwnj;ڕاندا) ئه&zwnj;وه بێگومان پێنج یه&zwnj;کی بۆ خواو پێغه&zwnj;مبه&zwnj;رو خزمانی نزیکی پێغه&zwnj;مبه&zwnj;رو هه&zwnj;تیوان و هه&zwnj;ژاران و ڕێبوارانه&zwnj;، (چوار به&zwnj;شه&zwnj;که&zwnj;ی تریشی بۆ موجاهیدانه که له&zwnj;و غه&zwnj;زایه&zwnj;دا به&zwnj;شدارییان کردووه&zwnj;) ئه&zwnj;گه&zwnj;ر ئێوه باوه&zwnj;ڕتان هه&zwnj;یه به خواو به&zwnj;وه&zwnj;ی که دامانبه&zwnj;زاندوه بۆ سه&zwnj;ر به&zwnj;نده&zwnj;مان (محمد(صلى الله عليه وسلم) له ڕۆژی جیاکردنه&zwnj;وه&zwnj;که&zwnj;دا (که غه&zwnj;زای به&zwnj;دره&zwnj;) ئه&zwnj;و ڕۆژه&zwnj;ی که هه&zwnj;ردوو له&zwnj;شکر به&zwnj;ره&zwnj;نگاری یه&zwnj;ک بوون، خوایش به&zwnj;سه&zwnj;ر هه&zwnj;موو شتێکدا ده&zwnj;سه&zwnj;ڵاتداره&zwnj;...';
$t[42] = 'کاتێک ئێوه له دامێنی خواری شیوه&zwnj;که و نزیکتر بوون به مه&zwnj;دینه و ئه&zwnj;وانیش له سه&zwnj;ره&zwnj;وه&zwnj;ی شیوه&zwnj;که&zwnj;وه بوون و دوورتر له مه&zwnj;دینه&zwnj;، کاروانه&zwnj;که&zwnj;ش له&zwnj;خوارترتانه&zwnj;وه بوو، خۆ ئه&zwnj;گه&zwnj;ر بڕیارو به&zwnj;ڵێنتان بدایه به&zwnj;یه&zwnj;کتر بگه&zwnj;ن له&zwnj;وکات و شوێنه&zwnj;دا ئه&zwnj;وه دوو به&zwnj;ره&zwnj;کی له&zwnj;نێوانتاندا دروست ده&zwnj;بوو، به&zwnj;ڵام خوا ده&zwnj;یه&zwnj;وێت بارودۆخێک پێش بهێنێت که بڕیاری له&zwnj;سه&zwnj;ردراوه (که سه&zwnj;رکه&zwnj;وتنی موسوڵمانانه و شکستی بێ باوه&zwnj;ڕانه&zwnj;)، بۆ ئه&zwnj;وه&zwnj;ی له&zwnj;ناو بچێت و به&zwnj;هانه&zwnj;ی نه&zwnj;مێنێت ئه&zwnj;وه&zwnj;ی که له زانستی خوادا له&zwnj;ناوچووه و بۆ ئه&zwnj;وه&zwnj;ی بژی له&zwnj;سه&zwnj;ر به&zwnj;رچاو ڕوونی و به&zwnj;ڵگه&zwnj;، ئه&zwnj;وه&zwnj;ی له زانستی خوادا ده&zwnj;ژی له&zwnj;سه&zwnj;ر دینداری (سه&zwnj;رئه&zwnj;نجامی شه&zwnj;ڕی به&zwnj;در بۆ ئه&zwnj;وه بوو تا باوه&zwnj;ڕو بێ باوه&zwnj;ڕی بۆ هه&zwnj;ردوولا ڕوون بێت)و بێگومان خوا بیسه&zwnj;رو زانایه&zwnj;.';
$t[43] = 'کاتێک خوا ئه&zwnj;وانی له خه&zwnj;ونتدا به&zwnj;که&zwnj;م نیشان ئه&zwnj;دایت، خۆ ئه&zwnj;گه&zwnj;ر ئه&zwnj;وانی به&zwnj;ژماره&zwnj;یه&zwnj;کی زۆر نیشانی بدایتایه ئه&zwnj;وه ئێوه لاواز ده&zwnj;بوون و تووشی نه&zwnj;هامه&zwnj;تی و کێشه ده&zwnj;بوون له باره&zwnj;ی جه&zwnj;نگ کردنه&zwnj;وه&zwnj;، به&zwnj;ڵام خوا به سه&zwnj;لامه&zwnj;تی و سه&zwnj;رکه&zwnj;وتوویی له&zwnj;و ترس و کێشه&zwnj;یه ڕزگاری کردن، چونکه به&zwnj;ڕاستی ئه&zwnj;و زاته زانایه به&zwnj;هه&zwnj;رچی له&zwnj;ناو دڵ و ده&zwnj;روون و سینه&zwnj;کاندا حه&zwnj;شار دراوه&zwnj;.';
$t[44] = 'کاتێکیش به&zwnj;یه&zwnj;کگه&zwnj;یشتن له گۆڕه&zwnj;پانی جه&zwnj;نگدا له&zwnj;به&zwnj;رچاوی ئێوه&zwnj;، خوا ئه&zwnj;وانی به&zwnj;که&zwnj;م نیشان ده&zwnj;دان و ژماره&zwnj;ی ئێوه&zwnj;شی له&zwnj;به&zwnj;رچاوی ئه&zwnj;وان به که&zwnj;م ده&zwnj;نواند، بۆ ئه&zwnj;وه&zwnj;ی هه&zwnj;ر ئه&zwnj;وه پێش بێت که خوا بڕیاری له&zwnj;سه&zwnj;رداوه&zwnj;، هه&zwnj;ر بۆ لای خوا سه&zwnj;ره&zwnj;نجامی هه&zwnj;موو کارو کرده&zwnj;وه&zwnj;یه&zwnj;ک ده&zwnj;گێردرێته&zwnj;وه&zwnj;.';
$t[45] = 'ئه&zwnj;ی ئه&zwnj;و که&zwnj;سانه&zwnj;ی ئیمان و باوه&zwnj;ڕتان هێناوه&zwnj;: کاتێک ده&zwnj;گه&zwnj;نه ده&zwnj;سته&zwnj;یه&zwnj;ک و ڕوو به ڕووتان ده&zwnj;بنه&zwnj;وه&zwnj;، ئه&zwnj;وه ده&zwnj;بێت خۆڕاگربن و سه&zwnj;نگه&zwnj;ر چۆڵ نه&zwnj;که&zwnj;ن و زۆر یادی خوا بکه&zwnj;ن و لێی بپاڕێنه&zwnj;وه&zwnj;، بۆ ئه&zwnj;وه&zwnj;ی ئێوه سه&zwnj;رفرازو سه&zwnj;رکه&zwnj;وتوو بن...';
$t[46] = 'گوێڕایه&zwnj;ڵی خواو پێغه&zwnj;مبه&zwnj;ره&zwnj;که&zwnj;شی بکه&zwnj;ن له هه&zwnj;موو کردارو گوفتارێکداو ئاشووب و ئاژاوه مه&zwnj;گێڕن و مه&zwnj;چن به&zwnj;گژ یه&zwnj;کدا، خۆ ئه&zwnj;گه&zwnj;ر بچن به&zwnj;گژ یه&zwnj;کداو یه&zwnj;کتر بشکێنن، ئه&zwnj;وه شکست و لاوازی ڕووتان تێده&zwnj;کات و هه&zwnj;یبه&zwnj;ت و شکۆداری و ده&zwnj;سه&zwnj;ڵاتتان له&zwnj;به&zwnj;ین ده&zwnj;چێت و ده&zwnj;بێت خۆگرو نه&zwnj;فس درێژ بن (هه&zwnj;م له مه&zwnj;یدانی جه&zwnj;نگدا، هه&zwnj;م له&zwnj;ناو یه&zwnj;کتردا) چونکه به&zwnj;ڕاستی خوا یارو یاوه&zwnj;ری خۆگرو ئارامگرانه&zwnj;.';
$t[47] = 'نه&zwnj;که&zwnj;ن وه&zwnj;ک ئه&zwnj;و جۆره که&zwnj;سانه بن که له وڵات و شوێنی خۆیان ده&zwnj;رچوون له&zwnj;به&zwnj;ر که&zwnj;ش و فش کردن و خۆ نواندن له&zwnj;به&zwnj;ر چاوی خه&zwnj;ڵک و ده&zwnj;یانه&zwnj;وێت کۆسپ بخه&zwnj;نه به&zwnj;رده&zwnj;م ڕێبازو ئاینی خوا، له&zwnj;کاتێکدا که خوای گه&zwnj;وره&zwnj;ش به&zwnj;ئاگایه به سه&zwnj;ره&zwnj;تاو سه&zwnj;ره&zwnj;نجامی هه&zwnj;موو ئه&zwnj;و کاروکرده&zwnj;وانه&zwnj;ی که ده&zwnj;یکه&zwnj;ن.';
$t[48] = 'کاتێکیش شه&zwnj;یتان (به&zwnj;وه&zwnj;سوه&zwnj;سه و خه&zwnj;یاڵ) هه&zwnj;موو کرده&zwnj;وه ناپه&zwnj;سه&zwnj;ندو ناڕه&zwnj;واکانی بۆ ڕازاندنه&zwnj;وه&zwnj;، وتی: ئه&zwnj;مڕۆ هیچ که&zwnj;س ده&zwnj;ره&zwnj;قه&zwnj;تی ئێوه نایه&zwnj;ت و دڵنیابن منیش پشت و په&zwnj;نام بۆ ئێوه&zwnj;، جا کاتێک ئه&zwnj;و دوو له&zwnj;شکره یه&zwnj;کتریان بینی، ده&zwnj;ستبه&zwnj;جێ شه&zwnj;یتان پاشه&zwnj;و پاش گه&zwnj;ڕایه&zwnj;وه&zwnj;و وتی: به&zwnj;ڕاستی من به&zwnj;ریم له ئێوه&zwnj;! چونکه به&zwnj;ڕاستی ئه&zwnj;وه&zwnj;ی من ده&zwnj;یبینم ئێوه نایبینن، (دیاره که شه&zwnj;یتان بینیوویه&zwnj;تی چۆن فریشته ده&zwnj;سته ده&zwnj;سته داده&zwnj;به&zwnj;زن بۆ یارمه&zwnj;تی ئیمانداران، بۆیه له ترسا وتی): بێگومان من له خوا ده&zwnj;ترسم، خوایش زۆر سه&zwnj;خت و به زه&zwnj;بره له تۆڵه&zwnj;دا.';
$t[49] = 'کاتێک دووڕووه&zwnj;کان و ئه&zwnj;وانه&zwnj;ی له دڵیاندا نه&zwnj;خۆشی (بێ ئیمانی) تیا جێگیر بوو (به ئیماندارانیان) ده&zwnj;وت: ئا ئه&zwnj;وانه ئاینه&zwnj;که&zwnj;یان له خۆبایی کردوون (ئه&zwnj;گینا به&zwnj;و ژماره که&zwnj;مه&zwnj;وه چۆن خۆیان ده&zwnj;گرن)، (خوایش ده&zwnj;فه&zwnj;رمووێت) ئه&zwnj;وه&zwnj;ی پشت به&zwnj;خوا ببه&zwnj;ستێت، ئه&zwnj;وه به&zwnj;ڕاستی خوا باڵاده&zwnj;ست و دانایه&zwnj;.';
$t[50] = 'ئه&zwnj;گه&zwnj;ر ده&zwnj;تبینی کاتێک فریشته&zwnj;کان گیانی ئه&zwnj;وانه&zwnj;یان ده&zwnj;کێشا که بێ باوه&zwnj;ڕ بوون! که ده&zwnj;یانکێشا به&zwnj;ده&zwnj;م و چاو و پشتیاندا و (ڕاپێچیان ده&zwnj;کردن بۆ دۆزه&zwnj;خ و، پێیان ده&zwnj;وتن): ده بچێژن سزایه&zwnj;کی سوتێنه&zwnj;ر...';
$t[51] = '(هه&zwnj;روه&zwnj;ها پێیان ده&zwnj;وتن) ئه&zwnj;و لێدان و سزایه هه&zwnj;ر به&zwnj;هۆی ئه&zwnj;وه&zwnj;وه&zwnj;یه که خۆتان به ده&zwnj;ستی خۆتان پێشتان خستووه و چونکه به&zwnj;ڕاستی خوا به&zwnj;هیچ شێوه&zwnj;یه&zwnj;ک سته&zwnj;مکار نیه له به&zwnj;نده&zwnj;کانی.';
$t[52] = 'کارو کرده&zwnj;وه&zwnj;ی (خوانه&zwnj;ناسان له هه&zwnj;موو سه&zwnj;رده&zwnj;مێکدا) وه&zwnj;کو داب و نه&zwnj;ریتی فیرعه&zwnj;ون و ئه&zwnj;و که&zwnj;سانه وایه که له (سه&zwnj;رده&zwnj;مه&zwnj;) پێشووه&zwnj;کاندا هاتوون پێش ئه&zwnj;مان، ئه&zwnj;وانه بڕوایان نه&zwnj;بوو به&zwnj;ئایه&zwnj;ت و فه&zwnj;رمانه&zwnj;کانی خوا، جا خوایش خه&zwnj;شمی لێ گرتن و له&zwnj;ناوی بردن به&zwnj;هۆی گوناهو تاوانیانه&zwnj;وه&zwnj;، به&zwnj;ڕاستی خوا به تواناو به&zwnj;زه&zwnj;بره له تۆڵه&zwnj;دا.';
$t[53] = 'ئه&zwnj;و سزاو له&zwnj;ناو بردنه له&zwnj;به&zwnj;ر ئه&zwnj;وه&zwnj;یه که (بڕیاری خوا وایه&zwnj;) بێگومان خوا هیچ نازو نیعمه&zwnj;تێکی نه&zwnj;گۆڕیوه که به&zwnj;خشیبێتی به&zwnj;هه&zwnj;ر قه&zwnj;وم و گه&zwnj;لێک، هه&zwnj;تا ئه&zwnj;وان ئه&zwnj;وه&zwnj;ی به&zwnj;خۆیان ده&zwnj;کرێت نه&zwnj;یگۆڕن و نه&zwnj;یکه&zwnj;ن، چونکه به&zwnj;ڕاستیش خوای گه&zwnj;وره بیسه&zwnj;ری (گوفتاره&zwnj;کانه&zwnj;) زانایه به&zwnj;کرداره&zwnj;کان.';
$t[54] = '(ره&zwnj;وشتی ئه&zwnj;مانه&zwnj;) هه&zwnj;ر وه&zwnj;کو داب و نه&zwnj;ریتی دارو ده&zwnj;سته&zwnj;ی فیرعه&zwnj;ون و ئه&zwnj;وانه&zwnj;ی پێشیان وایه&zwnj;، که به&zwnj;ڵگه&zwnj;و فه&zwnj;رمانه&zwnj;کانی په&zwnj;روه&zwnj;ردگاریان به&zwnj;درۆ ده&zwnj;زانی و باوه&zwnj;ڕیان پێی نه&zwnj;بوو، له ئه&zwnj;نجامدا ئێمه&zwnj;ش به&zwnj;هۆی گوناهو تاوانیانه&zwnj;وه له&zwnj;ناومان بردن و دارو ده&zwnj;سته&zwnj;ی فیرعه&zwnj;ونیشمان نوقمی ده&zwnj;ریا کرد، هه&zwnj;مووشیان سته&zwnj;مکار بوون.';
$t[55] = 'به&zwnj;ڕاستی خراپترینی زینده&zwnj;وه&zwnj;ران لای خوا ئه&zwnj;وانه&zwnj;ن که بێ باوه&zwnj;ڕ بوون، چونکه ئه&zwnj;وان باوه&zwnj;ڕ ناهێنن...';
$t[56] = 'ئه&zwnj;وانه&zwnj;ی که په&zwnj;یمانت له&zwnj;گه&zwnj;ڵدا به&zwnj;ستن (که خیانه&zwnj;تت لێ ناکه&zwnj;ن) که&zwnj;چی پاشان له هه&zwnj;موو جارێکدا هه&zwnj;ر په&zwnj;یمانه&zwnj;که&zwnj;یان ده&zwnj;شکاند و ئه&zwnj;وان له خوا ناترسن و پارێزکاری ناکه&zwnj;ن.';
$t[57] = 'جا هه&zwnj;رکاتێک ڕووبه&zwnj;ڕووی (خوانه&zwnj;ناسان) بویته&zwnj;وه له جه&zwnj;نگدا، ئه&zwnj;وه وایان لێ بکه ئه&zwnj;وانه&zwnj;ی له دوایانه&zwnj;وه&zwnj;ن و یارمه&zwnj;تیان ده&zwnj;ده&zwnj;ن په&zwnj;رش و بڵاو ببن، به&zwnj;و هیوایه&zwnj;ی ئه&zwnj;وان په&zwnj;ندو عیبره&zwnj;ت و ئامۆژگاریی وه&zwnj;ربگرن.';
$t[58] = 'خۆ ئه&zwnj;گه&zwnj;ر بێگومان ترسایت له ده&zwnj;سته&zwnj;یه&zwnj;ک (که په&zwnj;یمانت له&zwnj;گه&zwnj;ڵدا به&zwnj;ستوون) په&zwnj;یمانه&zwnj;که هه&zwnj;ڵوه&zwnj;شێننه&zwnj;وه&zwnj;و خیانه&zwnj;تت لێ بکه&zwnj;ن ئه&zwnj;وه تۆیش له&zwnj;م کاته&zwnj;دا په&zwnj;یمانه&zwnj;که بده&zwnj;ره&zwnj;وه به&zwnj;روویاندا به&zwnj;شێوه&zwnj;یه&zwnj;کی ڕێک و ڕاست، (پێیان بڵێ که&zwnj;) په&zwnj;یمانه&zwnj;که&zwnj;یان نرخی نه&zwnj;ماوه&zwnj;، چونکه به&zwnj;ڕاستی خوا که&zwnj;سانی په&zwnj;یمان شکێن و ناپاکی خۆش ناوێت.';
$t[59] = 'با ئه&zwnj;وانه&zwnj;ی که بێ بڕوا بوون وا نه&zwnj;زانن که له ده&zwnj;ستمان ده&zwnj;رچوون، به&zwnj;ڕاستی ئه&zwnj;وانه ناتوانن ده&zwnj;سه&zwnj;وسانمان بکه&zwnj;ن و له ده&zwnj;ستمان ده&zwnj;رناچن.';
$t[60] = '(خوا فه&zwnj;رمان ده&zwnj;دات به&zwnj;کار به&zwnj;ده&zwnj;ستانی ده&zwnj;وڵه&zwnj;تی ئیسلام): هه&zwnj;رچی له تواناتاندا هه&zwnj;یه بۆ به&zwnj;هێزکردنی سوپای ئیسلام بیخه&zwnj;نه گه&zwnj;ڕو ئاماده&zwnj;یی بکه&zwnj;ن (هێزی ئاسمانی و ده&zwnj;ریایی و وشکانی... هتد) و له ئاماده&zwnj;کردنی هه&zwnj;موو جۆره ئه&zwnj;سپ و (هۆکانی تری گواستنه&zwnj;وه&zwnj;) که&zwnj;مته&zwnj;رخه&zwnj;می مه&zwnj;که&zwnj;ن تا دوژمنانی خواو دوژمنانی خۆتانی پێ چاوترسێن بکه&zwnj;ن، با که&zwnj;سانی تریش جگه له&zwnj;م دوژمنانه&zwnj;ی ئێستا، پێی چاوترسێن ببن که ئێسته ئێوه نازانن کێن و نایانناسن، به&zwnj;ڵکو هه&zwnj;ر خوا خۆی ده&zwnj;یانناسێت و هه&zwnj;ر شتێک ببه&zwnj;خشن له پێناوی خوادا (بۆ به&zwnj;هێز کردنی سوپای ئیسلام)، ئه&zwnj;وه به&zwnj;زایه ناچێت و ده&zwnj;ستان ده&zwnj;که&zwnj;وێته&zwnj;وه&zwnj;و ئێوه سته&zwnj;متان لێناکرێت.';
$t[61] = 'جا ئه&zwnj;گه&zwnj;ر (دوژمنان) به&zwnj;لای ئاشتیدا دایانشکاند و حه&zwnj;زیان پێکرد تۆش (ئه&zwnj;ی محمد(صلى الله عليه وسلم)، ئه&zwnj;ی پێشه&zwnj;وا) ئاماده&zwnj;یی خۆت ده&zwnj;ربڕه و پشت به&zwnj;خوا ببه&zwnj;سته چونکه به&zwnj;ڕاستی ئه&zwnj;و زاته بیسه&zwnj;رو زانایه (له پیلان و نه&zwnj;خشه&zwnj;ی دوژمنان ئاگاداره&zwnj;).';
$t[62] = 'خۆ ئه&zwnj;گه&zwnj;ر خوانه&zwnj;ناسان (به داخوازی ڕێکه&zwnj;وتن و ئاشتی) ویستیان بتخه&zwnj;ڵه&zwnj;تێنن، ئه&zwnj;وه دڵنیابه که پشتیوانیی خوا به&zwnj;سه (که نه&zwnj;یه&zwnj;ڵێت بکه&zwnj;ویته ناو پیلان و نه&zwnj;خشه&zwnj;یانه&zwnj;وه&zwnj;) چونکه هه&zwnj;ر ئه&zwnj;و زاته کاتی خۆی پشتیوانیی لێکردیت و سه&zwnj;ری خستیت و ئیماندارانیشی کرده هۆیه&zwnj;ک بۆ به&zwnj;ده&zwnj;ستهێنانی سه&zwnj;رکه&zwnj;وتن...';
$t[63] = 'دڵه&zwnj;کانیشیانی به&zwnj;ست به&zwnj;یه&zwnj;که&zwnj;وه&zwnj;، ئه&zwnj;گه&zwnj;ر هه&zwnj;رچی دارایی زه&zwnj;وی هه&zwnj;یه&zwnj;، هه&zwnj;ر هه&zwnj;موویت خه&zwnj;رج بکردایه تا دڵه&zwnj;کانیانت په&zwnj;یوه&zwnj;ند بکردایه به یه&zwnj;که&zwnj;وه&zwnj;، ئه&zwnj;وه نه&zwnj;تده&zwnj;توانی ئه&zwnj;و دڵانه په&zwnj;یوه&zwnj;ست بکه&zwnj;یت به&zwnj;یه&zwnj;که&zwnj;وه&zwnj;، به&zwnj;ڵام خوا (به&zwnj;هۆی ئاینی ئیسلامه&zwnj;وه&zwnj;) خۆشی و یه&zwnj;کێتی و برایه&zwnj;تی له نێوانیاندا به&zwnj;رپاکرد، چونکه به&zwnj;ڕاستی ئه&zwnj;و زاته باڵاده&zwnj;ست و دانایه&zwnj;.';
$t[64] = 'ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر(صلى الله عليه وسلم): ته&zwnj;نها خوا به&zwnj;سه بۆ خۆت و ئه&zwnj;وانه&zwnj;ی شوێنت که&zwnj;وتوون له ئیمانداران که پشتگیر و یاریده&zwnj;ده&zwnj;رتان بێت.';
$t[65] = 'ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر(صلى الله عليه وسلم): هانی ئیمانداران بده له&zwnj;سه&zwnj;ر جه&zwnj;نگ (دژی ئه&zwnj;وانه&zwnj;ی که کۆسپن له ڕێبازی گه&zwnj;یاندنی په&zwnj;یامی خوادا) ئه&zwnj;گه&zwnj;ر بیست که&zwnj;سی خۆڕاگر هه&zwnj;بێت له ئێوه&zwnj;، ئه&zwnj;وه زاڵ ده&zwnj;بن به&zwnj;سه&zwnj;ر دووسه&zwnj;د که&zwnj;سدا، خۆ ئه&zwnj;گه&zwnj;ر سه&zwnj;د که&zwnj;س له ئێوه هه&zwnj;بن، ئه&zwnj;وه به&zwnj;سه&zwnj;ر هه&zwnj;زار که&zwnj;س له&zwnj;وانه&zwnj;ی که بێ باوه&zwnj;ڕن زاڵ ده&zwnj;بن، چونکه به&zwnj;ڕاستی ئه&zwnj;وانه که&zwnj;سانێکن که تێناگه&zwnj;ن (چ هێزێک پشتیوانیی له ئێوه ده&zwnj;کات).';
$t[66] = '(ئه&zwnj;ی ئیمانداران): ئێسته ئیتر خوا ئه&zwnj;رکی له&zwnj;سه&zwnj;ر که&zwnj;مکردنه&zwnj;وه و ده&zwnj;یزانی که لاوازیتان تێدایه (له ڕووی مادی و مه&zwnj;عنه&zwnj;ویه&zwnj;وه&zwnj;، له&zwnj;به&zwnj;رئه&zwnj;وه&zwnj;): جا ئه&zwnj;گه&zwnj;ر سه&zwnj;د که&zwnj;سی خۆڕاگر هه&zwnj;بێت له ئێوه&zwnj;، ئه&zwnj;وه زاڵ ده&zwnj;بن به&zwnj;سه&zwnj;ر دووسه&zwnj;د که&zwnj;سداو ئه&zwnj;گه&zwnj;ر هه&zwnj;زار که&zwnj;ستان هه&zwnj;بێت، ئه&zwnj;وه زاڵ ده&zwnj;بن به&zwnj;سه&zwnj;ر دوو هه&zwnj;زاردا، ئه&zwnj;ویش به&zwnj;ویست و یارمه&zwnj;تی خوا، خوایش یارو یاوه&zwnj;ری خۆڕاگرانه&zwnj;.';
$t[67] = 'بۆ هیچ پێغه&zwnj;مبه&zwnj;رێک نه&zwnj;بووه که دیلی هه&zwnj;بێت، هه&zwnj;تا کاتێک چاک پایه&zwnj;دارو جێگیر ده&zwnj;بێت له زه&zwnj;ویدا، (مه&zwnj;گه&zwnj;ر) که&zwnj;لوپه&zwnj;ل و شتومه&zwnj;کی دنیاتان ده&zwnj;وێت؟! له&zwnj;کاتێکدا خوا (سه&zwnj;رفرازیی) ئه&zwnj;و جیهانی ده&zwnj;وێت و قیامه&zwnj;تی مه&zwnj;به&zwnj;سته و خوا باڵاده&zwnj;ست و دانایه (سه&zwnj;رکه&zwnj;وتن به&zwnj;ئیمانداران ده&zwnj;به&zwnj;خشێت و دانایه له تاقی کردنه&zwnj;وه&zwnj;یاندا).';
$t[68] = 'خۆ ئه&zwnj;گه&zwnj;ر بڕیارێک له&zwnj;لایه&zwnj;ن خواوه پێش نه&zwnj;که&zwnj;وتایه (که مرۆڤ هه&zwnj;تا په&zwnj;یامی خوای پێنه&zwnj;گات خوا سزای نادات) له&zwnj;سه&zwnj;ر ئه&zwnj;و شتانه&zwnj;ی که وه&zwnj;رتان گرت (له به&zwnj;رامبه&zwnj;ر ئازادکردنی دیله&zwnj;کانه&zwnj;وه&zwnj;) ئه&zwnj;وه سزایه&zwnj;کی زۆر گه&zwnj;وره&zwnj;تان تووش ده&zwnj;بوو.';
$t[69] = 'که&zwnj;وابوو بخۆن له&zwnj;و شتانه&zwnj;ی که ده&zwnj;ستتان که&zwnj;وتووه&zwnj;، به&zwnj;حه&zwnj;ڵالێکی چاک و پاک، له خوا بترسن و دینداربن، چونکه به&zwnj;ڕاستی ئه&zwnj;و خوایه لێخۆشبوو میهره&zwnj;بانه&zwnj;.';
$t[70] = 'ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر(صلى الله عليه وسلم): به&zwnj;و دیلانه بڵێ که له&zwnj;به&zwnj;ر ده&zwnj;ستاندان، ئه&zwnj;گه&zwnj;ر خوا خێر له دڵه&zwnj;کانتاندا به&zwnj;دی بکات، ئه&zwnj;وه زۆر له&zwnj;وه باشترتان پێ ده&zwnj;به&zwnj;خشێت که لێتان وه&zwnj;رگیراوه&zwnj;، (ئه&zwnj;گه&zwnj;ر باوه&zwnj;ڕ بهێنن) لێتان خۆش ده&zwnj;بێت، خوایش لێخۆشبوو میهره&zwnj;بانه&zwnj;.';
$t[71] = 'خۆ ئه&zwnj;گه&zwnj;ر بیانه&zwnj;وێت خیانه&zwnj;ت و پیلان دژ به تۆ (ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر (صلی الله علیه وسلم)) بگێڕن، ئه&zwnj;وه به&zwnj;ڕاستی ئه&zwnj;وانه پێشتر خیانه&zwnj;تیان له خوا کردووه (به&zwnj;وه&zwnj;ی که شه&zwnj;ریک و هاوه&zwnj;ڵیان بۆ بڕیارداوه&zwnj;)، ئینجا خوا ده&zwnj;سه&zwnj;ڵاتی لێسه&zwnj;ندن و ئێوه&zwnj;ی زاڵ کرد به&zwnj;سه&zwnj;ریاندا، بێگومان خوا زانایه (به پیلانیان) و دانایه (به چۆنیه&zwnj;تی سنوور دانان بۆیان).';
$t[72] = 'به&zwnj;ڕاستی ئه&zwnj;وانه&zwnj;ی که باوه&zwnj;ڕیان هێناوه و کۆچیان کردووه&zwnj;و تێکۆشاون له پێناوی ئاینی خوادا به خۆیان و ماڵ و سامانیانه&zwnj;وه&zwnj;، ئه&zwnj;وانه&zwnj;ش که پێشوازیان له کۆچبه&zwnj;ران کردو له&zwnj;ماڵ و حاڵی خۆیاندا جێیان کردنه&zwnj;وه و پشتگیریان کردن، ئا ئه&zwnj;وانه هه&zwnj;ندێکیان پشتیوانی هه&zwnj;ندێکی تریانن، ئه&zwnj;وانه&zwnj;ش که باوه&zwnj;ڕیان هێناوه&zwnj;و کۆچیان نه&zwnj;کردووه&zwnj;، ئه&zwnj;وه بۆتان نیه پشتگیریان بکه&zwnj;ن له&zwnj;هیچ شتێکدا هه&zwnj;تا ئه&zwnj;وانیش کۆچ ده&zwnj;که&zwnj;ن (بۆ به&zwnj;هێزکردنی ده&zwnj;وڵه&zwnj;تی ئیسلام)، خۆ ئه&zwnj;گه&zwnj;ر ئه&zwnj;و ئیماندارانه&zwnj;ی (که کۆچیان نه&zwnj;کردووه&zwnj;) داوای یارمه&zwnj;تی و پشتگیریان له ئێوه کرد بۆ سه&zwnj;رخستنی ئاینی ئیسلام، ئه&zwnj;وه پێویسته له&zwnj;سه&zwnj;رتان بکه&zwnj;ن، جگه له&zwnj;سه&zwnj;ر ئه&zwnj;و عه&zwnj;شره&zwnj;ت و لایه&zwnj;نه&zwnj;ی که&zwnj;له نێوان ئێوه و ئه&zwnj;واندا په&zwnj;یمانێک هه&zwnj;بێت (ئه&zwnj;و په&zwnj;یمانه مه&zwnj;شکێنن)، خوای گه&zwnj;وره&zwnj;ش بینایه به&zwnj;و کارو کرده&zwnj;وانه&zwnj;ی که ده&zwnj;یکه&zwnj;ن.';
$t[73] = 'ئه&zwnj;وانه&zwnj;ش که بێ باوه&zwnj;ڕ بوون، هه&zwnj;ندێکیان پشتیوانی هه&zwnj;ندێکی تریانن و پشتیوانی له یه&zwnj;کتر ده&zwnj;که&zwnj;ن، خۆ ئه&zwnj;گه&zwnj;ر ئێوه&zwnj;ی ئیماندار کۆمه&zwnj;ک به&zwnj;یه&zwnj;کتر نه&zwnj;که&zwnj;ن و پشتی یه&zwnj;کتر نه&zwnj;گرن ئه&zwnj;وه له&zwnj;سه&zwnj;ر زه&zwnj;ویدا ناخۆشی و ناکۆکی و گوناهو تاوانی گه&zwnj;وره پێش دێت (ئازار و ناخۆشی بۆ ئێوه&zwnj;ی ئیماندار پێش دێت).';
$t[74] = 'ئه&zwnj;وانه&zwnj;ش باوه&zwnj;ڕیان هێناوه و کۆچیان کردوه و تێکۆشاون له پێناوی خوادا، ئه&zwnj;وانه&zwnj;ش جێگه&zwnj;ی کۆچبه&zwnj;رانیان کرده&zwnj;وه و (پێشوازیان کردن) و یارمه&zwnj;تیان دان، هه&zwnj;ر ئا ئه&zwnj;وانه بڕواداری ڕاسته&zwnj;قینه&zwnj;ن، (له به&zwnj;هه&zwnj;شتی به&zwnj;ریندا) بۆیان ئاماده&zwnj;یه لێخۆشبوون و ڕزق و ڕۆزی به&zwnj;پێزو پڕ به&zwnj;ره&zwnj;که&zwnj;ت و جوان و ڕازاوه&zwnj;.';
$t[75] = 'ئه&zwnj;وانه&zwnj;ش که باوه&zwnj;ڕیان هێناوه له دوای کۆچ کردنی یه&zwnj;که&zwnj;م و کۆچیان کردو هه&zwnj;وڵ و کۆششیاندا (له پێناوی گه&zwnj;یاندنی به&zwnj;رنامه&zwnj;ی خوا) له&zwnj;گه&zwnj;ڵ ئێوه&zwnj;دا، ئا ئه&zwnj;وانه&zwnj;ش له ئێوه&zwnj;ن (پله&zwnj;ی ئێوه&zwnj;یان هه&zwnj;یه&zwnj;)، ئه&zwnj;وانه&zwnj;ش که (سه&zwnj;ره&zwnj;ڕای په&zwnj;یوه&zwnj;ندی ئیمانی) په&zwnj;یوه&zwnj;ندی خزمایه&zwnj;تیشیان هه&zwnj;یه&zwnj;، هه&zwnj;ندێکیان شایسته&zwnj;ترن به&zwnj;هه&zwnj;ندێکی تریانه&zwnj;وه له به&zwnj;رنامه&zwnj;ی خوادا (واته ئه&zwnj;وان شایسته&zwnj;ترن له میرات به&zwnj;ریدا بۆ یه&zwnj;کتری)، به&zwnj;ڕاستی خوا به هه&zwnj;موو شتێک زاناو ئاگایه&zwnj;.';
?>