<?php
$t = array();
$t[1] = 'ئه&zwnj;مه به&zwnj;ری بوونێکی گه&zwnj;وره و گرنگه له&zwnj;لایه&zwnj;ن خواو پێغه&zwnj;مبه&zwnj;ره&zwnj;که&zwnj;یه&zwnj;وه بۆ ئه&zwnj;وانه&zwnj;ی که په&zwnj;یمانتان له&zwnj;گه&zwnj;ڵدا به&zwnj;ستبوون له هاوه&zwnj;ڵگه&zwnj;ران (واته پسان و نه&zwnj;مانی ئه&zwnj;و په&zwnj;یمانانه&zwnj;یه که له&zwnj;گه&zwnj;ڵ هاوه&zwnj;ڵگه&zwnj;راندا به&zwnj;ستراوه&zwnj;، چونکه ئه&zwnj;وان ده&zwnj;ست پێشخه&zwnj;ر بوون له شکاندنی په&zwnj;یمانه&zwnj;که&zwnj;دا).';
$t[2] = 'جا که&zwnj;واته ئه&zwnj;ی هاوه&zwnj;ڵگه&zwnj;ران بۆتان هه&zwnj;یه چوار مانگ به&zwnj;سه&zwnj;ر زه&zwnj;ویدا بگه&zwnj;ڕێن (تا شوێنێک بۆ نیشته&zwnj;جێ بوونی خۆتان ده&zwnj;دۆزنه&zwnj;وه&zwnj;)، بشزانن به&zwnj;ڕاستی ئێوه ده&zwnj;سه&zwnj;وسانکه&zwnj;ری خوا نین و له ده&zwnj;ست ئه&zwnj;و ده&zwnj;رناچن، به&zwnj;ڕاستیش خوای گه&zwnj;وره ڕیسواکه&zwnj;ری بێ باوه&zwnj;ڕانه&zwnj;.';
$t[3] = 'ئه&zwnj;مه&zwnj;ش بانگه&zwnj;وازو ڕاگه&zwnj;یاندنێکه له&zwnj;لایه&zwnj;ن خواو پێغه&zwnj;مبه&zwnj;ره&zwnj;که&zwnj;یه&zwnj;وه بۆ هه&zwnj;موو خه&zwnj;ڵکی، له ڕۆژی حه&zwnj;جی گه&zwnj;وره&zwnj;دا که&zwnj;: به&zwnj;ڕاستی خواو پێغه&zwnj;مبه&zwnj;ره&zwnj;که&zwnj;ی به&zwnj;رین له موشریک و هاوه&zwnj;ڵگه&zwnj;ران، جا ئه&zwnj;گه&zwnj;ر ئێوه ته&zwnj;وبه بکه&zwnj;ن و بگه&zwnj;ڕێنه&zwnj;وه له بێ باوه&zwnj;ڕی ئه&zwnj;وه چاکتره بۆتان، خۆ ئه&zwnj;گه&zwnj;ر هه&zwnj;ر یاخی بن و پشت هه&zwnj;ڵکه&zwnj;ن، ئه&zwnj;وه چاک بزانن به&zwnj;ڕاستی ئێوه ده&zwnj;سه&zwnj;وسانکه&zwnj;ری خوا نین و له ده&zwnj;سه&zwnj;ڵاتی ده&zwnj;رناچن و مژده بده به&zwnj;وانه&zwnj;ی که بێ باوه&zwnj;ڕ بوون، به&zwnj;سزایه&zwnj;کی پڕ ئێش و ئازار!!...';
$t[4] = 'به&zwnj;ڵام ئه&zwnj;و هاوه&zwnj;ڵگه&zwnj;رانه&zwnj;ی که په&zwnj;یمانتان له&zwnj;گه&zwnj;ڵیاندا به&zwnj;ستووه&zwnj;، پاشان پابه&zwnj;ندی په&zwnj;یمانه&zwnj;که بوون و هیچیان لێ که&zwnj;م نه&zwnj;کردووه و یارمه&zwnj;تی که&zwnj;سیشیان نه&zwnj;داوه دژتان، ئه&zwnj;وه ئێوه په&zwnj;یمانه&zwnj;که&zwnj;یان بۆ به&zwnj;رنه سه&zwnj;رو ته&zwnj;واوی بکه&zwnj;ن، تا کاتی دیاری کراویان، چونکه به&zwnj;ڕاستی خوا پارێزکارانی خۆش ده&zwnj;وێت.';
$t[5] = 'جا کاتێک مانگه حه&zwnj;رامه&zwnj;کان به&zwnj;سه&zwnj;رچوون (که بریتیه له&zwnj;: ذوالقعدة، ذوالحجة، محرم، ڕجب) ئه&zwnj;وه هاوه&zwnj;ڵگه&zwnj;رانی په&zwnj;یمان شکێن بکوژن له&zwnj;هه&zwnj;ر کوێ ده&zwnj;ستان لێیان گیر بوو، یاخود به&zwnj;دیل بیانگرن، سنوور دانێن بۆ چالاکیان، هه&zwnj;روه&zwnj;ها که&zwnj;مین و بۆسه&zwnj;یان له هه&zwnj;موو شوێنێکی له&zwnj;باردا بۆ دانێن، جا ئه&zwnj;گه&zwnj;ر (پێش ئه&zwnj;وه&zwnj;ی ده&zwnj;ستی ئێوه&zwnj;یان بگاتێ) ته&zwnj;وبه&zwnj;یان کردو وازیان هێنا له&zwnj;و کاره&zwnj;یان و نوێژیان کردو زه&zwnj;کاتیاندا، ئه&zwnj;وه وازیان لێبهێنن و ڕێیان بۆ چۆڵ بکه&zwnj;ن، چونکه به&zwnj;ڕاستی خوا لێخۆشبوو دلۆڤانه (ئه&zwnj;گه&zwnj;ر ڕاست بکه&zwnj;ن، خۆ ئه&zwnj;گه&zwnj;ر به ڕاستیشیان نه&zwnj;بێت ئێوه حسابی ڕواڵه&zwnj;ت بکه&zwnj;ن).';
$t[6] = 'خۆ ئه&zwnj;گه&zwnj;ر که&zwnj;سێک له هاوه&zwnj;ڵگه&zwnj;ران داوای په&zwnj;نای لێ کردیت (ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر (صلى الله عليه وسلم)، ئه&zwnj;ی ئیماندار)، ئه&zwnj;وه تۆیش په&zwnj;نای بده هه&zwnj;تا گوێی له فه&zwnj;رمووده&zwnj;که&zwnj;ی خوا (قورئان) ده&zwnj;بێت، پاشان بیگه&zwnj;یه&zwnj;نه به&zwnj;و شوێنه&zwnj;ی ده&zwnj;یه&zwnj;وێت و (ماڵ و گیان و ژیانی) تیایدا پارێزراوه&zwnj;، ئه&zwnj;م میهره&zwnj;بانیه له&zwnj;به&zwnj;ر ئه&zwnj;وه&zwnj;یه ئه&zwnj;وانه که&zwnj;سانێکن نازانن (خواناسی چیه&zwnj;).';
$t[7] = 'چۆن په&zwnj;یمان ده&zwnj;بێت بۆ هاوه&zwnj;ڵگه&zwnj;ران (ئه&zwnj;وانه&zwnj;ی که چه&zwnj;نده&zwnj;ها جار په&zwnj;یمان ده&zwnj;شکێنن)، لای خواو لای پێغه&zwnj;مبه&zwnj;ره&zwnj;که&zwnj;ی (په&zwnj;یمان نابێت)؟! جگه له&zwnj;وانه&zwnj;ی که له پاڵ مزگه&zwnj;وتی حه&zwnj;رام و (که&zwnj;عبه&zwnj;دا) په&zwnj;یمانتان (له&zwnj;گه&zwnj;ڵیاندا مۆرکرد) هه&zwnj;تا ئه&zwnj;وان پابه&zwnj;ند و ڕاست و دروست بن له&zwnj;سه&zwnj;ر په&zwnj;یمانه&zwnj;که&zwnj;یان به&zwnj;رامبه&zwnj;ر ئێوه&zwnj;وه&zwnj;، ئه&zwnj;وه ئێوه&zwnj;ش پابه&zwnj;ندو ڕاست و ڕه&zwnj;وان بن بۆیان، چونکه به&zwnj;ڕاستی خوا پارێزکارانی خۆش ده&zwnj;وێت.';
$t[8] = 'چۆن (پابه&zwnj;ندی په&zwnj;یمان ده&zwnj;بن له&zwnj;گه&zwnj;ڵ ئێوه&zwnj;دا، کاری چاک له&zwnj;وانه ناوه&zwnj;شێته&zwnj;وه&zwnj;) خۆ ئه&zwnj;گه&zwnj;ر له کاتێکدا ده&zwnj;ستیان به&zwnj;سه&zwnj;رتاندا بڕوات ئه&zwnj;وه هیچ سنوورێک ناپارێزن، نه&zwnj;خزمایه&zwnj;تی ده&zwnj;فامن، نه&zwnj;په&zwnj;یمان، به&zwnj;ده&zwnj;م و زاریان ڕازیتان ده&zwnj;که&zwnj;ن، دڵه&zwnj;کانیشیان دژو بێزارن له&zwnj;وه&zwnj;ی که به&zwnj;ده&zwnj;م ده&zwnj;یڵێن (دژی ڕاگرتنی په&zwnj;یمانه&zwnj;کانیانن) زۆربه&zwnj;شیان له سنوور ده&zwnj;رچوون و په&zwnj;یمان شکێنن.';
$t[9] = '(هاوه&zwnj;ڵگه&zwnj;ران نرخیان بۆ ئایه&zwnj;ته&zwnj;کانی خوای گه&zwnj;وره دانه&zwnj;ناوه&zwnj;) ئه&zwnj;وانه به ئایه&zwnj;ته&zwnj;کانی خوا پاره&zwnj;و ده&zwnj;ستمایه&zwnj;یه&zwnj;کی که&zwnj;می دنیایان کڕی و گۆڕیانه&zwnj;وه (ئایه&zwnj;ته&zwnj;کانی خوایان گۆڕیه&zwnj;وه به&zwnj;نرخێکی که&zwnj;م و هه&zwnj;رزان) و ئینجا پشتیان هه&zwnj;ڵکرد له ڕێگه&zwnj;ی خوا، ڕێگری خه&zwnj;ڵکیشیان لێ ده&zwnj;کرد، به&zwnj;ڕاستی ئه&zwnj;و (خوا نه&zwnj;ناسانه&zwnj;) ناپه&zwnj;سه&zwnj;ند بوو ئه&zwnj;و کارو کرده&zwnj;وه&zwnj;یه&zwnj;ی که ده&zwnj;یانکردو ئه&zwnj;نجامیان ده&zwnj;دا.';
$t[10] = 'ئه&zwnj;وانه ده&zwnj;رباره&zwnj;ی هیچ ئیماندارێک سنووری خزمایه&zwnj;تی و په&zwnj;یمان ڕه&zwnj;چاو ناکه&zwnj;ن و ناپارێزن، هه&zwnj;ر ئا ئه&zwnj;وانه&zwnj;شن ده&zwnj;ستدرێژکاران.';
$t[11] = 'جا ئه&zwnj;گه&zwnj;ر ته&zwnj;وبه بکه&zwnj;ن و په&zwnj;شیمانی ده&zwnj;ربڕن و نوێژه&zwnj;کانیان ئه&zwnj;نجام بده&zwnj;ن و زه&zwnj;کات له ماڵ و سامانیان ده&zwnj;رکه&zwnj;ن، ئه&zwnj;وه برای دینی ئێوه&zwnj;ن، ئێمه&zwnj;یش ئایه&zwnj;ت و فه&zwnj;رمانه&zwnj;کان ڕوون ده&zwnj;که&zwnj;ینه&zwnj;وه بۆ که&zwnj;سانێک که ده&zwnj;یزانن.';
$t[12] = 'خۆ ئه&zwnj;گه&zwnj;ر ئه&zwnj;وانه په&zwnj;یمانه&zwnj;کانیان بشکێنن له&zwnj;دوای به&zwnj;ستنی و گاڵته&zwnj;و تانه&zwnj;وته&zwnj;شه&zwnj;ریان له ئاین و به&zwnj;رنامه&zwnj;که&zwnj;تان ده&zwnj;دا، ئه&zwnj;وه بجه&zwnj;نگن دژی سه&zwnj;رانی کوفر، به&zwnj;ڕاستی ئه&zwnj;وان هیچ په&zwnj;یمانێکیان نیه&zwnj;، بۆ ئه&zwnj;وه&zwnj;ی کۆڵ بده&zwnj;ن و واز له بێ باوه&zwnj;ڕی خۆیان بهێنن.';
$t[13] = 'باشه&zwnj;، ئایا ئێوه ناجه&zwnj;نگن دژی که&zwnj;سانێک که به&zwnj;ڵێن و په&zwnj;یمانه&zwnj;کانیان هه&zwnj;ڵوه&zwnj;شاندوه و هه&zwnj;وڵیشیاندا که پێغه&zwnj;مبه&zwnj;ر ده&zwnj;رکه&zwnj;ن له شاری (مه&zwnj;ککه&zwnj;) و هه&zwnj;ر ئه&zwnj;وانیش یه&zwnj;که&zwnj;مجار ده&zwnj;ستیان کرد به دژایه&zwnj;تیتان، ئایا له&zwnj;وان ده&zwnj;ترسن؟ جا خۆ هه&zwnj;ر خوا شایسته&zwnj;یه که لێی بترسن ئه&zwnj;گه&zwnj;ر ئێوه ئیماندارن.';
$t[14] = '(ئه&zwnj;ی ئیمانداران) بجه&zwnj;نگن دژی بێ باوه&zwnj;ڕان، ئه&zwnj;وه خوایه که به&zwnj;ده&zwnj;ستی ئێوه سزایان ده&zwnj;دات و سه&zwnj;رشۆڕو خه&zwnj;جاڵه&zwnj;تیان ده&zwnj;کات و ئێوه به&zwnj;سه&zwnj;ریاندا سه&zwnj;ر ده&zwnj;خات سینه&zwnj;ی گرۆی ئیماندارانیش گوشادو ئاسووده ده&zwnj;کات و دڵ و ده&zwnj;روونیان ساڕێژ ده&zwnj;کات (چونکه کاتی خۆی کافره&zwnj;کانی مه&zwnj;ککه زۆر ئازارو ئه&zwnj;شکه&zwnj;نجه&zwnj;ی ئیماندارانیان ده&zwnj;داو بێ به&zwnj;زه&zwnj;ییانه هه&zwnj;ندێکیان لێ شه&zwnj;هید کردن).';
$t[15] = 'هه&zwnj;رچی ئازارو کینه&zwnj;ش هه&zwnj;یه له&zwnj;دڵی ئیمانداراندا لای ببات و نه&zwnj;یهێڵێت، خوای گه&zwnj;وره&zwnj;ش ته&zwnj;وبه&zwnj;و په&zwnj;شیمانی له&zwnj;و که&zwnj;سه وه&zwnj;رده&zwnj;گرێت که خۆی ده&zwnj;یه&zwnj;وێت و (شایسته&zwnj;یه&zwnj;)، خوای گه&zwnj;وره زاناو دانایه&zwnj;.';
$t[16] = '(ئه&zwnj;ی ئیمانداران) ئایا پێتان وابوو و واتان ده&zwnj;زانی هه&zwnj;روا وازتان لێده&zwnj;هێنرێت (بێ تاقیکردنه&zwnj;وه&zwnj;) له&zwnj;کاتێکدا هێشتا خوا ده&zwnj;ری نه&zwnj;خستووه ئه&zwnj;وانه&zwnj;ی جیهادیان کردووه&zwnj;و تێکۆشاون له ئێوه&zwnj;و جگه&zwnj;له خواو پێغه&zwnj;مبه&zwnj;ره&zwnj;که&zwnj;ی و ئیمانداران که&zwnj;سانی تریان نه&zwnj;کردووه به&zwnj;جێی متمانه&zwnj;و ڕازو نهێنی خۆیان، خوایش هه&zwnj;میشه ئاگاداره به هه&zwnj;موو ئه&zwnj;و کارو کرده&zwnj;وانه&zwnj;ی که ئه&zwnj;نجامی ده&zwnj;ده&zwnj;ن (به&zwnj;ڵام ده&zwnj;یه&zwnj;وێت هه&zwnj;موو ڕاستیه&zwnj;کان ده&zwnj;رکه&zwnj;ون و بێنه&zwnj;روو).';
$t[17] = 'شیاو وشایسته نه&zwnj;بووه بۆ موشریک و هاوه&zwnj;ڵگه&zwnj;ران که مزگه&zwnj;وته&zwnj;کانی خوا ئاوه&zwnj;دان بکه&zwnj;نه&zwnj;وه&zwnj;، له&zwnj;کاتێکدا خۆیان شایه&zwnj;تن له&zwnj;سه&zwnj;ر خۆیان که بێ باوه&zwnj;ڕن، ئه&zwnj;وانه هه&zwnj;موو کارو کرده&zwnj;وه&zwnj;کانیان بێ نرخه و پووچه&zwnj;ڵ بۆته&zwnj;وه&zwnj;، ئه&zwnj;وان له&zwnj;ناو ئاگری دۆزه&zwnj;خدا (له ژیانی کوله مه&zwnj;رگیدا) به&zwnj;رده&zwnj;وامن.';
$t[18] = 'به&zwnj;ڕاستی ته&zwnj;نها ئه&zwnj;و که&zwnj;سه (به&zwnj;دڵ و به&zwnj;گیان) مزگه&zwnj;وته&zwnj;کانی خوا ئاوه&zwnj;دان ده&zwnj;کاته&zwnj;وه (به خواپه&zwnj;رستی و نوێکردنه&zwnj;وه&zwnj;ی) که باوه&zwnj;ڕی پته&zwnj;وی به&zwnj;خواو به&zwnj;رۆژی دوایی هێناوه و نوێژه&zwnj;کانی به&zwnj;چاکی ئه&zwnj;نجام داوه&zwnj;و زه&zwnj;کاتیشی (له ماڵ و سامانی) ده&zwnj;رکردووه و له که&zwnj;سیش نه&zwnj;ترساوه جگه له خوای باڵاده&zwnj;ست، جا ئومێده ئه&zwnj;و جۆره که&zwnj;سانه له ڕیزی ڕێنموویی کراواندا بن.';
$t[19] = 'ئایا ئێوه به&zwnj;ڕه&zwnj;واو دروستان دانا ئاوبه&zwnj;خشین به&zwnj;حاجیان و ئاوه&zwnj;دان کردنه&zwnj;وه&zwnj;ی مزگه&zwnj;وتی حه&zwnj;رام (که&zwnj;عبه&zwnj;)، وه&zwnj;ک ئه&zwnj;و که&zwnj;سه وابێت که باوه&zwnj;ڕی پته&zwnj;وی به&zwnj;خواو به&zwnj;رۆژی دوایی هێناوه&zwnj;و تێکۆشاوه له پێناوی ڕێبازی خوادا؟ ئه&zwnj;و دوو ده&zwnj;سته&zwnj;یه وه&zwnj;ک یه&zwnj;کنین و له&zwnj;یه&zwnj;ک ئاستدا نین لای خوا، خوای گه&zwnj;وره&zwnj;ش هیدایه&zwnj;ت و ڕێنموویی قه&zwnj;ومی سته&zwnj;مکار نادات.';
$t[20] = 'ئه&zwnj;وانه&zwnj;ی باوه&zwnj;ڕیان هێناوه و کۆچیان کردووه و جیهادو هه&zwnj;وڵ و کۆششیان ئه&zwnj;نجامداوه له پێناوی ڕێبازو ڕه&zwnj;زامه&zwnj;ندیی خوادا به ماڵیان و به&zwnj;گیانیان، پله&zwnj;پایه&zwnj;و پاداشتیان لای خوا بێ سنوور و گه&zwnj;وره&zwnj;تره&zwnj;، ئا ئه&zwnj;وانه&zwnj;ش که باسکران هه&zwnj;ر ئه&zwnj;وانه سه&zwnj;رفرازو به&zwnj;خته&zwnj;وه&zwnj;رن...';
$t[21] = '(جا ئه&zwnj;وانه&zwnj;) په&zwnj;روه&zwnj;ردگاریان مژده&zwnj;یان ده&zwnj;داتێ به ڕه&zwnj;حمه&zwnj;ت و میهره&zwnj;بانی تایبه&zwnj;تی له لایه&zwnj;ن خۆیه&zwnj;وه&zwnj;، هاوڕێ له&zwnj;گه&zwnj;ڵ ڕه&zwnj;زامه&zwnj;ندی و خۆشنوودی و به&zwnj;هه&zwnj;شت و باخی بێ شومار، له کاتێکدا بۆ ئه&zwnj;وان هه&zwnj;یه له&zwnj;و به&zwnj;هه&zwnj;شته&zwnj;دا نازو نیعمه&zwnj;تی نه&zwnj;بڕاوه که پێشکه&zwnj;شیان ده&zwnj;کرێت.';
$t[22] = 'له&zwnj;هه&zwnj;مان کاتدا له&zwnj;و به&zwnj;هه&zwnj;شته&zwnj;دا بۆ هه&zwnj;میشه و به&zwnj;رده&zwnj;وام ژیان ده&zwnj;به&zwnj;نه سه&zwnj;ر، به&zwnj;ڕاستی خوای به&zwnj;خشنده پاداشتی گه&zwnj;وره و بێ سنووری له&zwnj;لایه&zwnj;.';
$t[23] = 'ئه&zwnj;ی ئه&zwnj;و که&zwnj;سانه&zwnj;ی باوه&zwnj;ڕتان هێناوه&zwnj;، باو و باپیران و براکانتان مه&zwnj;که&zwnj;نه پشتیوان و خۆشه&zwnj;ویستی خۆتان، ئه&zwnj;گه&zwnj;ر ئه&zwnj;وان بێ باوه&zwnj;ڕیان خۆش ویست و هه&zwnj;ڵیانبژارد به&zwnj;سه&zwnj;ر ئیمان و باوه&zwnj;ڕدا، هه&zwnj;رکه&zwnj;س له ئێوه پشت به&zwnj;وان ببه&zwnj;ستێت و خۆشی بوێن، ئائه&zwnj;وانه هه&zwnj;ر سته&zwnj;مکارن.';
$t[24] = '(ئه&zwnj;ی محمد (صلى الله عليه وسلم) پێیان بڵێ: ئه&zwnj;گه&zwnj;ر باوو باپیرانتان و براکانتان و هاوسه&zwnj;رانتان و عه&zwnj;شره&zwnj;ت و هۆزتان و ماڵ و سامانێک که به&zwnj;ده&zwnj;ستان هێناوه&zwnj;، یاخود بازرگانیه&zwnj;ک ده&zwnj;ترسن که شکان و بێ بازاڕی ڕووی تێبکات و خانووبه&zwnj;ره و کۆشک و ته&zwnj;لارێک که حه&zwnj;ز ده&zwnj;که&zwnj;ن لێتان تێک نه&zwnj;چێت و دڵخۆشن پێی، لاتان خۆشه&zwnj;ویستترو نازدارتر بێت له خواو پێغه&zwnj;مبه&zwnj;ره&zwnj;که&zwnj;ی و جیهادو تێکۆشان له&zwnj;پێناو ڕێبازی ئه&zwnj;و زاته&zwnj;دا، ده ئه&zwnj;وه چاوه&zwnj;ڕوان بن، هه&zwnj;تا خوای گه&zwnj;وره دێت به فه&zwnj;رمانه&zwnj;کانیه&zwnj;وه و (تۆڵه&zwnj;تان لێ ده&zwnj;سێنێت)، خوای په&zwnj;روه&zwnj;ردگاریش هیدایه&zwnj;ت و ڕێنموویی قه&zwnj;ومی یاخی و له سنوور ده&zwnj;رچوو نادات.';
$t[25] = 'سوێند بێت بێگومان خوای گه&zwnj;وره له زۆر شوێندا سه&zwnj;ری خستن (به&zwnj;سه&zwnj;ر بێ باوه&zwnj;ڕاندا)، له ڕۆژی حونه&zwnj;ینیش کاتێک که زۆریی ژماره&zwnj;ی خۆتان سه&zwnj;رسامی کردبوون، جا (بینیتان) ئه&zwnj;و زۆریه هیچ فریاتان نه&zwnj;که&zwnj;وت و بێ نیازی نه&zwnj;کردن، زه&zwnj;ویش چه&zwnj;نده فراوانه لێتان هاته&zwnj;وه یه&zwnj;ک و ته&zwnj;نگ بوه&zwnj;وه&zwnj;، له&zwnj;وه&zwnj;ودوا هه&zwnj;موو پشتان هه&zwnj;ڵکردو ڕاتانکرد.';
$t[26] = 'پاشان خوای گه&zwnj;وره ئارامی و ئاسووده&zwnj;یی دابه&zwnj;زانده سه&zwnj;ر پێغه&zwnj;مبه&zwnj;ره&zwnj;که&zwnj;ی و سه&zwnj;ر ئیمانداران و سه&zwnj;ربازانێکی دابه&zwnj;زان که نه&zwnj;تاندیووه&zwnj;، سزای ئه&zwnj;وانه&zwnj;شیدا که بێ باوه&zwnj;ڕان بوون، ئه&zwnj;وه&zwnj;ش سزای و پاداشتی بێ باوه&zwnj;ڕانه&zwnj;.';
$t[27] = 'ئینجا دوای ئه&zwnj;وه خوای گه&zwnj;وره له هه&zwnj;ر که&zwnj;سێک که بیه&zwnj;وێت و شایسته بێت ته&zwnj;وبه و په&zwnj;شیمانی وه&zwnj;رده&zwnj;گرێت، خوا لێخۆشبوو میهره&zwnj;بانه&zwnj;.';
$t[28] = 'ئه&zwnj;ی ئه&zwnj;و که&zwnj;سانه&zwnj;ی باوه&zwnj;ڕتان هێناوه چاک بزانن که موشریک و هاوه&zwnj;ڵگه&zwnj;ران ناپوخت و پیسن، ئیتر له&zwnj;م ساڵه&zwnj;یان به&zwnj;دواوه نابێت نزیکی مزگه&zwnj;وتی حه&zwnj;رام (که&zwnj;عبه&zwnj;ی پیرۆز) بکه&zwnj;ون، خۆ ئه&zwnj;گه&zwnj;ر ده&zwnj;ترسن له هه&zwnj;ژاری و نه&zwnj;داری و بێ بازاڕی ئه&zwnj;وه بزانن که&zwnj;له ئاینده&zwnj;دا خوای گه&zwnj;وره له فه&zwnj;زڵ و به&zwnj;خشنده&zwnj;یی خۆی ده&zwnj;وڵه&zwnj;مه&zwnj;ندتان ده&zwnj;کات هه&zwnj;ر کاتێک بیه&zwnj;وێت (بێگومان سامانی نه&zwnj;وت و کانزاکان به&zwnj;شێکن له&zwnj;و مژده&zwnj;یه&zwnj;) به&zwnj;ڕاستی خوا زاناو دانایه&zwnj;.';
$t[29] = '(ئه&zwnj;ی ئیمانداران) دژی ئه&zwnj;وانه&zwnj;ی که باوه&zwnj;ڕی (ڕاست و دروستیان) به&zwnj;خواو به&zwnj;رۆژی قیامه&zwnj;ت نیه و ئه&zwnj;و شتانه&zwnj;ی خواو پێغه&zwnj;مبه&zwnj;ره&zwnj;که&zwnj;ی به&zwnj;حه&zwnj;رامیان داناوه به&zwnj;حه&zwnj;رامی نازانن و په&zwnj;یڕه&zwnj;وی دینی حه&zwnj;ق نابن، له&zwnj;وانه&zwnj;ی که کتێبیان پێدراوه له (گاورو جوو) بجه&zwnj;نگن، هه&zwnj;تا ئه&zwnj;و کاته&zwnj;ی به&zwnj;ناچاری باج پێشکه&zwnj;ش به&zwnj;ده&zwnj;وڵه&zwnj;تی ئیسلام ده&zwnj;که&zwnj;ن، (له&zwnj;جیاتی ئه&zwnj;وه&zwnj;ی که گیان و ماڵ و سامانیان پارێزراوه و به&zwnj;شداری جه&zwnj;نگ ناکه&zwnj;ن).';
$t[30] = 'جوله&zwnj;که وتیان: عوزه&zwnj;یر کوڕی خوایه&zwnj;، گاوره&zwnj;کانیش وتیان: مه&zwnj;سیح کوڕی خوایه&zwnj;!! ئا ئه&zwnj;و بوختانه (نابه&zwnj;جێیانه&zwnj;) قسه&zwnj;ی ئه&zwnj;وانه و به&zwnj;ده&zwnj;م ده&zwnj;یڵێن و لێی تێناگه&zwnj;ن (بیرێک ناکه&zwnj;نه&zwnj;وه&zwnj;، تیافکرن) ئه&zwnj;و گوفتاره&zwnj;یان لاسایی کردنه&zwnj;وه&zwnj;ی ئه&zwnj;وانه&zwnj;یه که پێشتر بێ باوه&zwnj;ڕ بوون (له&zwnj;وه&zwnj;دا که ده&zwnj;یانوت فریشته&zwnj;کان کچی خوان)، ده&zwnj;ک به&zwnj;کوشت چن و خوا له&zwnj;ناویان به&zwnj;رێت، ئاخر ئه&zwnj;م بوختانه ناڕه&zwnj;وایانه چۆن هه&zwnj;ڵده&zwnj;به&zwnj;ستن.';
$t[31] = '(گاورو جوو) حاخام و قه&zwnj;شه&zwnj;کانی خۆیان کردۆته په&zwnj;روه&zwnj;ردگاری خۆیان له جیاتی خوا (ملکه&zwnj;چ و فه&zwnj;رمانبه&zwnj;رداریانن، ئه&zwnj;وانیش به&zwnj;ئاره&zwnj;زووی خۆیان شتیان بۆ حه&zwnj;ڵاڵ یان حه&zwnj;رام ده&zwnj;که&zwnj;ن) هه&zwnj;روه&zwnj;ها مه&zwnj;سیحی کوڕی مه&zwnj;ریه&zwnj;میش (به&zwnj;کوڕی خوا ده&zwnj;زانن) له&zwnj;کاتێکدا که فه&zwnj;رمانیان پێنه&zwnj;دراوه جگه له په&zwnj;رستنی تاکه خوایه&zwnj;ک، بێگومان جگه له&zwnj;وزاته خوایه&zwnj;کی تر نیه&zwnj;، پاکی و بێگه&zwnj;ردی و ستایش بۆ ئه&zwnj;و زاته&zwnj;یه&zwnj;، که (ئه&zwnj;و نه&zwnj;فامانه&zwnj;) هاوه&zwnj;ڵی بۆ بڕیار ده&zwnj;ده&zwnj;ن.';
$t[32] = '(ئه&zwnj;وانه به&zwnj;و بیروباوه&zwnj;ڕه پووچانه&zwnj;یانه&zwnj;وه&zwnj;) ده&zwnj;یانه&zwnj;وێت نوورو ئاینی خوا به&zwnj;قاڵه&zwnj;ی ده&zwnj;میان بکوژێننه&zwnj;وه و خامۆشی بکه&zwnj;ن (به&zwnj;ڵام خه&zwnj;یاڵیان خاوه&zwnj;) خوا ناهێڵێت ئاواتیان بێته دی و هیچ شتێکی تری ناوێت جگه له&zwnj;وه نه&zwnj;بێت ئاین و نووری خۆی سه&zwnj;ربخات و نووری خۆی ته&zwnj;واو بکات و ده&zwnj;ری بخات، هه&zwnj;رچه&zwnj;نده کافرو بێ باوه&zwnj;ڕان پێی سه&zwnj;غڵه&zwnj;ت و دڵته&zwnj;نگ بن.';
$t[33] = 'هه&zwnj;ر ئه&zwnj;و زاته بۆیه پێغه&zwnj;مبه&zwnj;ره&zwnj;که&zwnj;ی هاوڕێ له&zwnj;گه&zwnj;ڵ هیدایه&zwnj;ت و ڕێنموویی و ئایینی ڕاست و دروستدا ڕه&zwnj;وانه کردووه تا سه&zwnj;ری بخات به&zwnj;سه&zwnj;ر هه&zwnj;موو به&zwnj;رنامه و ئاینه&zwnj;کانی تردا، هه&zwnj;رچه&zwnj;نده موشریک و هاوه&zwnj;ڵگه&zwnj;ران پێی سه&zwnj;غڵه&zwnj;ت و دڵته&zwnj;نگ بن.';
$t[34] = 'ئه&zwnj;ی ئه&zwnj;و که&zwnj;سانه&zwnj;ی باوه&zwnj;ڕتان هێناوه&zwnj;، چاک بزانن که زۆربه&zwnj;ی حاخام و مالومه&zwnj;کانی جوو و قه&zwnj;شه&zwnj;ی گاوران، ماڵ و سامانی خه&zwnj;ڵکی به&zwnj;ناحه&zwnj;ق ده&zwnj;خۆن، به&zwnj;رهه&zwnj;ڵستی ڕێباز و ئاینی خوا ده&zwnj;که&zwnj;ن، جا ئه&zwnj;وانه&zwnj;ی که ئاڵتون و زیو کۆ ده&zwnj;که&zwnj;نه&zwnj;وه و قایمی ده&zwnj;که&zwnj;ن و له پێناوی ڕێبازی خوادا و بۆ ڕه&zwnj;زامه&zwnj;ندی ئه&zwnj;و، نایبه&zwnj;خشن، ئه&zwnj;وه مژده&zwnj;ی سزایه&zwnj;کی پڕ ئێشیان بده&zwnj;رێ!!';
$t[35] = '(ئه&zwnj;و سزا سه&zwnj;خته&zwnj;) له ڕۆژێکدا ده&zwnj;بێت ئه&zwnj;و پاره و سامانه&zwnj;ی کۆیان کردۆته&zwnj;وه&zwnj;، له دۆزه&zwnj;خدا سوور ده&zwnj;کرێته&zwnj;وه&zwnj;، ناوچاو و لاته&zwnj;نیشت و پشتیانی پێ داخ ده&zwnj;کرێت، ئینجا پێیان ده&zwnj;وترێت: ئه&zwnj;مه ئه&zwnj;و سامانه&zwnj;یه که کۆتان ده&zwnj;کرده&zwnj;وه بۆ خۆتان، ده بچێژن سزای ئه&zwnj;وه&zwnj;ی که بۆ خۆتان کۆده&zwnj;کرده&zwnj;وه&zwnj;.';
$t[36] = 'بێگومان ژماره&zwnj;ی مانگه&zwnj;کان لای خوا دوانزه مانگه&zwnj;، له&zwnj;کتێبی خوادا له (لوح المحفوظ) دا دیاریکراوه&zwnj;، هه&zwnj;ر له&zwnj;و ڕۆژه&zwnj;وه که ئاسمانه&zwnj;کان و زه&zwnj;وی دروست کردووه&zwnj;، چوار مانگی به حه&zwnj;رام داناوه (واته جه&zwnj;نگ تیایدا نابێت به&zwnj;رپا ببێت)، هه&zwnj;ر ئه&zwnj;وه&zwnj;یه ئایین و به&zwnj;رنامه&zwnj;ی ڕاست و دروست، که&zwnj;وابوو له&zwnj;و مانگانه&zwnj;دا سته&zwnj;م له خۆتان مه&zwnj;که&zwnj;ن و جه&zwnj;نگ به&zwnj;رپا مه&zwnj;که&zwnj;ن، به&zwnj;ڵکو ته&zwnj;نها دژی موشریکانی (شه&zwnj;ڕه&zwnj;نگێز) هه&zwnj;مووتان بجه&zwnj;نگن به گشتی هه&zwnj;روه&zwnj;کو ئه&zwnj;وان دژی ئێوه ده&zwnj;جه&zwnj;نگن به&zwnj;گشتی، ئینجا بشزانن که خوا یارو یاوه&zwnj;ری ئه&zwnj;و که&zwnj;سانه&zwnj;یه که لێی ده&zwnj;ترسن و پارێزکارن.';
$t[37] = 'به&zwnj;ڕاستی دواخستن و ده&zwnj;ستکاری کردنی ئه&zwnj;و مانگانه&zwnj;ی که جه&zwnj;نگ تیایاندا حه&zwnj;رامه&zwnj;، بۆ کاتێکی تر؛ سه&zwnj;رکه&zwnj;شی و ڕۆچونێکی زیاتره له کوفرو خوانه&zwnj;ناسیدا، ئه&zwnj;وانه&zwnj;ی که بێ بڕوا بوون پێی گومڕا ده&zwnj;کرێن، ساڵێک ئه&zwnj;و ده&zwnj;ستکاریه به&zwnj;حه&zwnj;ڵاڵ داده&zwnj;نێن و ساڵێکیش به&zwnj;حه&zwnj;رام، بۆ ئه&zwnj;وه&zwnj;ی ئه&zwnj;و مانگانه ڕێک بکه&zwnj;ون له&zwnj;گه&zwnj;ڵ ژماره&zwnj;ی ئه&zwnj;و مانگانه&zwnj;ی که خوا حه&zwnj;رامی کردوون، به&zwnj;م جۆره ئه&zwnj;وه&zwnj;ی خوا حه&zwnj;رامی کردبوو حه&zwnj;ڵاڵیان ده&zwnj;کرد، ئه&zwnj;وانه کرداره ناپه&zwnj;سه&zwnj;نده&zwnj;کانی خۆیان لا جوان کرابوو، خوایش هیدایه&zwnj;ت و ڕێنموویی هۆزی بێ باوه&zwnj;ڕان ناکات.';
$t[38] = 'ئه&zwnj;ی ئه&zwnj;و که&zwnj;سانه&zwnj;ی ئیمان و باوه&zwnj;ڕتان هێناوه&zwnj;: ئه&zwnj;وه بۆچی کاتێک پێتان ده&zwnj;وترێت ڕاپه&zwnj;ڕن و ئاماده&zwnj;بن و هێرش به&zwnj;رن له پێناوی ئاینی خوادا (دژی ئه&zwnj;و خوانه&zwnj;ناسانه&zwnj;ی که خۆیان ئاماده کردووه بۆ کوژاندنه&zwnj;وه&zwnj;ی نووری خوا) ئێوه له&zwnj;شتان داده&zwnj;هێزرێت و ده&zwnj;لکێن به&zwnj;زه&zwnj;ویداو زه&zwnj;مین گیرده&zwnj;بن (ئه&zwnj;ڵبه&zwnj;ته ئه&zwnj;م بانگه&zwnj;وازه هه&zwnj;رکاتێک له&zwnj;لایه&zwnj;ن سه&zwnj;رکردایه&zwnj;تی حه&zwnj;کیم و داناو بڕوا پێ کراوه&zwnj;وه بڕیار درا، که هه&zwnj;ڵقوڵاوی ناو کۆمه&zwnj;ڵگه&zwnj;ی ئیماندارانه و ئه&zwnj;زموونی ساڵه&zwnj;ها جیهادو خه&zwnj;باتی ژێر زه&zwnj;مینی هه&zwnj;یه&zwnj;، ده&zwnj;بێت له&zwnj;لایه&zwnj;ن هه&zwnj;موو ئیماندارانه&zwnj;وه په&zwnj;یڕه&zwnj;وی بکرێت) ئایا ئێوه ڕازین به ژیانی دنیاو به&zwnj;گرنگتری ده&zwnj;زانن له ژیانی نه&zwnj;بڕاوه&zwnj;ی قیامه&zwnj;ت له به&zwnj;هه&zwnj;شتی به&zwnj;رین، بێگومان ژیانی دنیا به به&zwnj;راورد له&zwnj;گه&zwnj;ڵ ژیانی پڕ له شادیی قیامه&zwnj;تدا زۆر زۆر که&zwnj;م ته&zwnj;مه&zwnj;ن و که&zwnj;م خۆشی یه&zwnj;.';
$t[39] = 'ئه&zwnj;گه&zwnj;ر (بۆ جیهاد) ڕانه&zwnj;په&zwnj;ڕن و ده&zwnj;ستبه&zwnj;کار نه&zwnj;بن، ئه&zwnj;وه سزایه&zwnj;کی زۆر به&zwnj;ئێشتان ده&zwnj;دات و که&zwnj;سانێکی تر ده&zwnj;هێنێته کایه&zwnj;وه له ئێوه نه&zwnj;بن، وه&zwnj;نه&zwnj;بێت ئێوه بتوانن هیچ زه&zwnj;ره&zwnj;ر و زیانێک به خوا بگه&zwnj;یه&zwnj;نن، (به&zwnj;نه&zwnj;چوونتان به&zwnj;ده&zwnj;م بانگه&zwnj;وازی خواوه&zwnj;، به&zwnj;ڵکو نافه&zwnj;رمانی و دنیاپه&zwnj;رستی هه&zwnj;ر زیان له خۆتان ده&zwnj;دات)، خوایش هه&zwnj;میشه به&zwnj;سه&zwnj;ر هه&zwnj;موو شتێکدا ده&zwnj;سه&zwnj;ڵاتداره&zwnj;.';
$t[40] = 'ئه&zwnj;گه&zwnj;ر ئێوه پشتگیری پێغه&zwnj;مبه&zwnj;ر (صلى الله عليه وسلم) نه&zwnj;که&zwnj;ن و یارمه&zwnj;تی نه&zwnj;ده&zwnj;ن، ئه&zwnj;وه به&zwnj;ڕاستی خوا پشتگیری کردو سه&zwnj;ری خست، کاتێک ئه&zwnj;وانه&zwnj;ی بێ باوه&zwnj;ڕ بوون ده&zwnj;ریان کرد له شاری مه&zwnj;ککه و یه&zwnj;کێک بوو له&zwnj;و دوو که&zwnj;سه کاتێک هه&zwnj;ردووکیان له&zwnj;ناو ئه&zwnj;شکه&zwnj;وته&zwnj;که&zwnj;دا بوون (کاتێک بێ باوه&zwnj;ڕه&zwnj;کان گه&zwnj;شتبوونه ده&zwnj;وروبه&zwnj;ری ئه&zwnj;شکه&zwnj;وته&zwnj;که ئه&zwnj;بوبه&zwnj;کری هاوه&zwnj;ڵی خه&zwnj;می لێهات) پێغه&zwnj;مبه&zwnj;ریش (صلى الله عليه وسلم) دڵی دایه&zwnj;وه و پێی وت: خه&zwnj;فه&zwnj;ت مه&zwnj;خۆ، دڵنیابه خوامان له&zwnj;گه&zwnj;ڵه&zwnj;، ئه&zwnj;وسا ئیتر خوا ئاسووده&zwnj;ی و ئارامی خۆی دابه&zwnj;زانده سه&zwnj;ری و پشتگیری و به&zwnj;هێزی کرد به سه&zwnj;ربازانێک که ئێوه نه&zwnj;تانده&zwnj;بینین و به&zwnj;رنامه&zwnj;و ئاینی بێ باوه&zwnj;ڕانی دانه&zwnj;واند و ڕیسواتری کرد، به&zwnj;رنامه&zwnj;و ئاینی خوایش هه&zwnj;میشه به&zwnj;رزو بڵندتره&zwnj;، خوایش هه&zwnj;میشه باڵاده&zwnj;ست و دانایه&zwnj;.';
$t[41] = '(ئه&zwnj;ی ئیمانداران هه&zwnj;ر کاتێک داواکران بۆ غه&zwnj;زا له&zwnj;لایه&zwnj;ن سه&zwnj;رکردایه&zwnj;تیه&zwnj;کی متمانه پێکراوه&zwnj;وه&zwnj;): ئێوه به&zwnj;چه&zwnj;کی سووک و سه&zwnj;نگین و به&zwnj;سواره و پیاده&zwnj;تانه&zwnj;وه ڕاپه&zwnj;ڕن و ئاماده&zwnj;بن و جیهاد و تێکۆشان بکه&zwnj;ن به&zwnj;ماڵ و سامان و گیانتان له پێناوی خوادا، ئه&zwnj;وه چاکتره بۆتان ئه&zwnj;گه&zwnj;ر ئێوه بزانن و تێبگه&zwnj;ن.';
$t[42] = 'ئه&zwnj;گه&zwnj;ر (غه&zwnj;زای ته&zwnj;بووک) ده&zwnj;ستکه&zwnj;وتێکی ئاسان و نزیک بوایه&zwnj;، یا سه&zwnj;فه&zwnj;رێکی مامناوه&zwnj;ندی بوایه&zwnj;، ئه&zwnj;وه دووڕووه&zwnj;کان شوێنت ده&zwnj;که&zwnj;وتن، به&zwnj;ڵام ڕێگه&zwnj;ی دوورو پڕ مه&zwnj;ینه&zwnj;ت بۆ ئه&zwnj;وان زه&zwnj;حمه&zwnj;ته&zwnj;، (جا له&zwnj;وه&zwnj;ودوا دێن) و سوێند به&zwnj;زاتی خوا ده&zwnj;خۆن و ده&zwnj;ڵێن: ئه&zwnj;گه&zwnj;ر بمانتوانیایه ئه&zwnj;وه له&zwnj;گه&zwnj;ڵ ئێوه&zwnj;دا ده&zwnj;رده&zwnj;چووین و ده&zwnj;هاتین بۆ غه&zwnj;زا، ئه&zwnj;وانه به&zwnj;و ڕه&zwnj;فتارو درۆیانه&zwnj;یان، خۆیان تیاده&zwnj;به&zwnj;ن، خوایش ده&zwnj;زانێت به&zwnj;ڕاستی ئه&zwnj;وانه درۆزنن.';
$t[43] = 'خوا لێت خۆش بێت (ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر (صلى الله عليه وسلم)! بۆ مۆڵه&zwnj;تت دانێ (نه&zwnj;ده&zwnj;بوایه مۆڵه&zwnj;تیان بده&zwnj;یتێ) هه&zwnj;تا به&zwnj;چاکی بۆت ده&zwnj;ربکه&zwnj;وێت، کێ ڕاسته و باوه&zwnj;ڕ دامه&zwnj;زراوه&zwnj;، کێش درۆزن و دوورووه&zwnj;.';
$t[44] = '(به&zwnj;ڵام) ئه&zwnj;وانه&zwnj;ی باوه&zwnj;ڕیان به&zwnj;خواو به ڕۆژی دوایی هه&zwnj;یه مۆڵه&zwnj;تت لێ ناخوازن (خۆ نادزنه&zwnj;وه له جیهادو له غه&zwnj;زا)، چونکه ئاماده&zwnj;ن ماڵ و سامان و گیانیان به&zwnj;خت بکه&zwnj;ن له پێناوی خوادا، خوایش زانایه به خاوه&zwnj;ن ته&zwnj;قواو پارێزکاران.';
$t[45] = '(به&zwnj;ڵکو) به&zwnj;ڕاستی ته&zwnj;نها ئه&zwnj;وانه مۆڵه&zwnj;تت لێ ده&zwnj;خوازن که باوه&zwnj;ڕ به&zwnj;خواو به ڕۆژی دوایی ناهێنن، ئه&zwnj;وانه هه&zwnj;میشه دوودڵن و دڵه ڕاوکێیانه&zwnj;، به&zwnj;رده&zwnj;وام له گومانی خۆیاندا سه&zwnj;رگه&zwnj;ردان و وێڵن و دێن و ده&zwnj;ڕۆن.';
$t[46] = 'ئه&zwnj;گه&zwnj;ر ئه&zwnj;و دووڕووانه بیانویستایه ده&zwnj;رچن (بۆ غه&zwnj;زا) ئه&zwnj;وه خۆیان بۆ ئاماده ده&zwnj;کرد، به&zwnj;ڵام خوا چالاکی نواندن و ده&zwnj;رچوونی ئه&zwnj;وانی نه&zwnj;ویست (چونکه دڵ و ده&zwnj;روون نه&zwnj;خۆش بوون)، له&zwnj;به&zwnj;ر ئه&zwnj;وه سستی کردن و له&zwnj;شی قورس کردن پێیان وترا: دانیشن بۆ خۆتان له&zwnj;گه&zwnj;ڵ پیرو مناڵ و په&zwnj;ککه&zwnj;وته&zwnj;کاندا.';
$t[47] = 'ئه&zwnj;وانه ئه&zwnj;گه&zwnj;ر ده&zwnj;رچوونایه و له&zwnj;ناوتاندا بوونایه هیچیان بۆ ئێوه زیاد نه&zwnj;ده&zwnj;کرد بێجگه له سستی نه&zwnj;بێت (له ئاشووب و گێره شێوێنی زیاتریان له ده&zwnj;ست نه&zwnj;ده&zwnj;هات) و ده&zwnj;ستیان ده&zwnj;کرد به چرپ و چاپ له&zwnj;ناوتاندا و ده&zwnj;گه&zwnj;ران بۆ دۆزینه&zwnj;وه&zwnj;ی که&zwnj;لێنێک، به&zwnj;ئاواتی ئاشووب هه&zwnj;ڵگیرساندن له نێوانتاندا، که&zwnj;سانێکیش هه&zwnj;ن له&zwnj;نێو ئێوه&zwnj;دا، گوێیان بۆ ده&zwnj;گرن، خوایش چاک ئاگاداره به کاروکرده&zwnj;وه&zwnj;ی سته&zwnj;مکاران.';
$t[48] = 'سوێند به خوا (ئه&zwnj;و دووڕووانه&zwnj;) پێشتریش هه&zwnj;وڵیان داوه فیتنه و ئاشووب (له نێوان ئیمانداراندا) به&zwnj;رپا بکه&zwnj;ن و چه&zwnj;نده&zwnj;ها فه&zwnj;رمان و نه&zwnj;خشه&zwnj;یان گۆڕی بۆ ئه&zwnj;وه&zwnj;ی فێڵت لێ بکه&zwnj;ن و وره&zwnj;ت بڕوخێنن، هه&zwnj;تا خوا کردی حه&zwnj;ق هات و سه&zwnj;رکه&zwnj;وت و دینی خوا پایه&zwnj;دار بوو، له&zwnj;کاتێکدا ئه&zwnj;وان هه&zwnj;میشه سه&zwnj;غڵه&zwnj;تن (به پایه&zwnj;داری ئیسلام).';
$t[49] = '(له&zwnj;و دووڕووه بێ چاوو ڕووانه&zwnj;) که&zwnj;سانێک هه&zwnj;ن که ده&zwnj;ڵێن: مۆڵه&zwnj;تم بده&zwnj;و تووشی گوناهو سه&zwnj;رپێچیم مه&zwnj;که&zwnj;!! (به&zwnj;بیانووی ئه&zwnj;وه&zwnj;ی له غه&zwnj;زادا، له&zwnj;وانه&zwnj;یه تووشی گوناهو هه&zwnj;ڵه ببێت!!) له حاڵێکدا ئه&zwnj;و جۆره که&zwnj;سانه با چاک بزانن که له گوناهو تاواندا ڕۆچوون، بێگومان دۆزه&zwnj;خیش گه&zwnj;مارۆی بێ باوه&zwnj;ڕانی داوه و (ڕزگاربوونیان ئه&zwnj;سته&zwnj;مه&zwnj;).';
$t[50] = '(ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر (صلى الله عليه وسلم) ئه&zwnj;گه&zwnj;ر خێرو چاکه&zwnj;یه&zwnj;کت تووش بێت، ئه&zwnj;وه ئه&zwnj;وان پێی دڵته&zwnj;نگ و سه&zwnj;غڵه&zwnj;ت ده&zwnj;بن، خۆ ئه&zwnj;گه&zwnj;ر به&zwnj;ڵاو ناخۆشیه&zwnj;کت تووش بێت ئه&zwnj;وه ده&zwnj;ڵێن: ئێمه پێشتر ده&zwnj;ستی خۆمانمان وه&zwnj;شاند (باشمان کرد نه&zwnj;چووین بۆ جه&zwnj;نگ، پێشبینی ئه&zwnj;مه&zwnj;مان ده&zwnj;کرد)، ئه&zwnj;وسا پشت هه&zwnj;ڵده&zwnj;که&zwnj;ن له کاتێکدا ئه&zwnj;وان دڵخۆش و شادمانن!...';
$t[51] = '(ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر (صلى الله عليه وسلم) تۆش بڵێ: هه&zwnj;رگیز له&zwnj;وه زیاترمان بۆ پێش نایه&zwnj;ت جگه&zwnj;له&zwnj;وه&zwnj;ی که خوا بۆی نوسیووین (هه&zwnj;رچیش بۆمان پێش دێت هه&zwnj;ر خێره بۆمان)، ئه&zwnj;وزاته هه&zwnj;میشه یارو پشتیوانمانه&zwnj;، ده&zwnj;با ئیمانداران هه&zwnj;ر پشت به&zwnj;و خوایه ببه&zwnj;ستن.';
$t[52] = '(به دووڕووه&zwnj;کان و بێ باوه&zwnj;ڕان) بڵێ: ئایا ئێوه له یه&zwnj;کێک له&zwnj;و دوو سه&zwnj;رئه&zwnj;نجامه چاکه زیاتر چاوه&zwnj;ڕوانی چیمان بۆ ده&zwnj;که&zwnj;ن، له&zwnj;کاتێکدا هه&zwnj;ردووکیان چاکن و ئاواتیان بۆ ده&zwnj;خوازین (که&zwnj;: یان سه&zwnj;رکه&zwnj;وتنه&zwnj;، یان شه&zwnj;هید بوون) ئێمه&zwnj;ش هه&zwnj;میشه چاوه&zwnj;ڕێی ئه&zwnj;وه ده&zwnj;که&zwnj;ین خوای گه&zwnj;وره به&zwnj;ڵاو سزایه&zwnj;کی تایبه&zwnj;تی خۆیتان به&zwnj;سه&zwnj;ردا ببارێنێت، یان به&zwnj;ده&zwnj;ستی ئێمه تووشی شکستی و نه&zwnj;هامه&zwnj;تی ببن، ده ئیتر ئێوه خۆتان مه&zwnj;ڵاس بده&zwnj;ن و چاوه&zwnj;ڕێ بن، به&zwnj;ڕاستی ئێمه&zwnj;ش له&zwnj;گه&zwnj;ڵتاندا چاوه&zwnj;ڕێین.';
$t[53] = 'به دووڕووه&zwnj;کان (که ده&zwnj;یانه&zwnj;وێت له ماڵ به&zwnj;خشین خۆیان بشارنه&zwnj;وه&zwnj;) بڵێ: هه&zwnj;رچی ده&zwnj;به&zwnj;خشن به ڕه&zwnj;زامه&zwnj;ندیی یان به&zwnj;ناچاریی هه&zwnj;رگیز لێتان وه&zwnj;رناگیرێت، چونکه به&zwnj;ڕاستی ئێوه که&zwnj;سانێکی گوناهبارو باوه&zwnj;ڕ دانه&zwnj;مه&zwnj;زراون.';
$t[54] = 'هه&zwnj;ر بۆیه&zwnj;ش ئه&zwnj;وه&zwnj;ی که ده&zwnj;یبه&zwnj;خشن لێیان وه&zwnj;رناگیرێت، چونکه ئه&zwnj;وانه باوه&zwnj;ڕیان به&zwnj;خواو به&zwnj;پێغه&zwnj;مبه&zwnj;ره&zwnj;که&zwnj;ی (صلى الله عليه وسلم) نیه و نایه&zwnj;ن بۆ نوێژ، مه&zwnj;گه&zwnj;ر به&zwnj;له&zwnj;ش به&zwnj;باری ته&zwnj;مه&zwnj;ڵی نه&zwnj;بێت، ماڵ و سامانیش نابه&zwnj;خشن مه&zwnj;گه&zwnj;ر له&zwnj;کاتێکدا که ئه&zwnj;وان نابه&zwnj;دڵ و سه&zwnj;خڵه&zwnj;تن.';
$t[55] = '(ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر (صلى الله عليه وسلم) ئه&zwnj;ی ئیماندار) نه&zwnj;ماڵ و سامان و نه نه&zwnj;وه&zwnj;یان سه&zwnj;رسامت نه&zwnj;کات به&zwnj;باشیان بزانیت، چونکه به&zwnj;ڕاستی خوا هه&zwnj;ر ده&zwnj;یه&zwnj;وێت له ژیانی دنیادا تووشی به&zwnj;ڵاو سزایان بکات له ڕێگه&zwnj;ی ماڵ و سامان و نه&zwnj;وه&zwnj;یانه&zwnj;وه و گیانیان ده&zwnj;ربێت، له&zwnj;کاتێکدا که ئه&zwnj;وان بێ باوه&zwnj;ڕن.';
$t[56] = 'ئه&zwnj;وانه سوێندیش به خوا ده&zwnj;خۆن که به&zwnj;ڕاستی له ئێوه&zwnj;ن، (به&zwnj;ڵام درۆ ده&zwnj;که&zwnj;ن) ئه&zwnj;وانه هیچ کاتێک له ئێوه نین، به&zwnj;ڵکو ئه&zwnj;وانه که&zwnj;سانێکن که له ترسا خه&zwnj;ریکن زیڕه بکه&zwnj;ن و ترسنۆکن.';
$t[57] = 'خۆئه&zwnj;گه&zwnj;ر په&zwnj;نایه&zwnj;ک یان چه&zwnj;ند ئه&zwnj;شکه&zwnj;وتێک یان ژێر زه&zwnj;مینێکیان ده&zwnj;ست بکه&zwnj;وێت ئه&zwnj;وه به&zwnj;په&zwnj;له ڕووی تێده&zwnj;که&zwnj;ن و له&zwnj;کاتێکدا که ئه&zwnj;وان هیچ شتێک جڵه&zwnj;ویان ناگرێت (ئه&zwnj;وه&zwnj;نده له به&zwnj;شداربوونی غه&zwnj;زا ده&zwnj;ترسن).';
$t[58] = 'هه&zwnj;یانه تانه&zwnj;و ته&zwnj;شه&zwnj;ر له تۆ ده&zwnj;دات له دابه&zwnj;شکردنی زه&zwnj;کات و ده&zwnj;ستکه&zwnj;وتدا (پڕوپاگه&zwnj;نده ده&zwnj;کات که گوایه عه&zwnj;داله&zwnj;ت و دادی تێدا نیه&zwnj;)، جا ئه&zwnj;وانه ئه&zwnj;گه&zwnj;ر به&zwnj;شیان بدرێت ڕازین، ئه&zwnj;گه&zwnj;ر بێ به&zwnj;ش بکرێن لێی ئه&zwnj;وه خێرا ئه&zwnj;وان ڕق هه&zwnj;ڵده&zwnj;گرن و کینه له سینه ده&zwnj;گرن.';
$t[59] = 'خۆ ئه&zwnj;گه&zwnj;ر ئه&zwnj;وانه ڕازی بوونایه به&zwnj;و به&zwnj;شه&zwnj;ی خوا به نسیبی کردوون و پێغه&zwnj;مبه&zwnj;ر (صلى الله عليه وسلم) پێی به&zwnj;خشیوون و به&zwnj;دڵ بیانوتایه&zwnj;: خوامان به&zwnj;سه (که لێمان ڕازی بێت)، ئێمه فه&zwnj;رامۆش ناکات له&zwnj;ئاینده&zwnj;دا هه&zwnj;ر پێمان ده&zwnj;به&zwnj;خشێت له فه&zwnj;زڵ و زیاده ڕێزی خۆی، پێغه&zwnj;مبه&zwnj;ره&zwnj;که&zwnj;شی له&zwnj;وه&zwnj;ی ده&zwnj;ستی ده&zwnj;که&zwnj;وێت پێمان ده&zwnj;به&zwnj;خشێت، به&zwnj;ڕاستی ئێمه ئاوات و مه&zwnj;به&zwnj;ستمان گه&zwnj;ڕانه&zwnj;وه&zwnj;یه بۆ لای خواو به&zwnj;ده&zwnj;ستهێنانی ڕه&zwnj;زامه&zwnj;ندی ئه&zwnj;وه (ئه&zwnj;و کاته پاداشتی هه&zwnj;ردوو جیهانیان بۆ خۆیان مسۆگه&zwnj;ر ده&zwnj;کرد).';
$t[60] = 'بێگومان زه&zwnj;کات ته&zwnj;نها بۆ هه&zwnj;ژارو نه&zwnj;داران و ئه&zwnj;و که&zwnj;سانه&zwnj;ی که&zwnj;کارمه&zwnj;ندن به&zwnj;سه&zwnj;ریه&zwnj;وه و به&zwnj;و موسڵمانه نوێیانه&zwnj;ی که هێشتا ئیمان جێگیر نه&zwnj;بووه له&zwnj;دڵیاندا و بۆ ئازادکردنی کۆیله&zwnj;کان و بۆ (یارمه&zwnj;تیدانی) قه&zwnj;رزداره&zwnj;کان و بۆ گه&zwnj;یاندنی ئاینی خوا (هه&zwnj;ر بوارێک که خزمه&zwnj;ت به&zwnj;بانگه&zwnj;وازی ئیسلام بکات و ئاینی خوا به&zwnj;ره&zwnj;و پێش و سه&zwnj;رکه&zwnj;وتن ببات) هه&zwnj;روه&zwnj;ها بۆ ئه&zwnj;و ڕێبوارانه&zwnj;ش که پاره&zwnj;یان لێ ده&zwnj;بڕێت و نه&zwnj;دار ده&zwnj;که&zwnj;ون، (دابه&zwnj;شکردنی زه&zwnj;کات به&zwnj;و شێوه&zwnj;یه&zwnj;) فه&zwnj;رزه له لایه&zwnj;ن خواوه له&zwnj;گه&zwnj;ردنی ئیمانداراندا، خواش زاناو دانایه (له سامان به&zwnj;خشین و فه&zwnj;رزکردنی زه&zwnj;کاتدا).';
$t[61] = 'هه&zwnj;ندێکی تر (له دووڕووه&zwnj;کان) ئه&zwnj;وه&zwnj;نده نه&zwnj;فامن، ئازاری دڵی پێغه&zwnj;مبه&zwnj;ر (صلى الله عليه وسلم) ده&zwnj;ده&zwnj;ن، به&zwnj;وه&zwnj;ی که ده&zwnj;ڵێن: (محمد) گوێ بۆ هه&zwnj;موو که&zwnj;س ده&zwnj;گرێت، پێیان بڵێ: گوێ بیستی خێره بۆتان، باوه&zwnj;ڕی پته&zwnj;وی به&zwnj;خوا هه&zwnj;یه&zwnj;، باوه&zwnj;ڕی (به ڕاستگۆی) ئیماندارانیش هه&zwnj;یه&zwnj;، ڕه&zwnj;حمه&zwnj;تیشه بۆ ئه&zwnj;وانه&zwnj;تان که باوه&zwnj;ڕیان هێناوه&zwnj;، (به&zwnj;ڵام) ئه&zwnj;وانه&zwnj;ی ئازاری پێغه&zwnj;مبه&zwnj;ری خوا ده&zwnj;ده&zwnj;ن سزایه&zwnj;کی به&zwnj;ئێش بۆیان ئاماده&zwnj;یه&zwnj;.';
$t[62] = 'دووڕووه&zwnj;کان، سوێندتان به خوا بۆ ده&zwnj;خۆن تا بڕواتان پێبکه&zwnj;ن و ڕازیتان بکه&zwnj;ن، له&zwnj;کاتێکدا که حه&zwnj;ق وایه خواو پێغه&zwnj;مبه&zwnj;ره&zwnj;که&zwnj;ی ڕازی بکه&zwnj;ن ئه&zwnj;گه&zwnj;ر ئه&zwnj;وانه باوه&zwnj;ڕدارن...';
$t[63] = 'مه&zwnj;گه&zwnj;ر ئه&zwnj;وانه نازانن ئه&zwnj;و که&zwnj;سه&zwnj;ی که دژایه&zwnj;تی خواو پێغه&zwnj;مبه&zwnj;ره&zwnj;که&zwnj;ی بکات، ئه&zwnj;وه به ڕاستی ئاگری دۆزه&zwnj;خ به&zwnj;شیه&zwnj;تی و بۆی ئاماده&zwnj;یه هاوڕێ له&zwnj;گه&zwnj;ڵ ژیانی نه&zwnj;بڕاوه&zwnj;شدا، ئا ئه&zwnj;وه ڕیسوایی و خه&zwnj;جاڵه&zwnj;تیه&zwnj;کی زۆر گه&zwnj;وره&zwnj;یه&zwnj;.';
$t[64] = 'دووڕووه&zwnj;کان ده&zwnj;ترسن له&zwnj;وه&zwnj;ی سووره&zwnj;تێک دابه&zwnj;زێنرێته سه&zwnj;ریان، هه&zwnj;واڵیان بداتێ به&zwnj;وه&zwnj;ی له دڵ و ده&zwnj;روونیاندایه&zwnj;، پێیان بڵێ: گاڵته بکه&zwnj;ن! بێگومان ئه&zwnj;وه&zwnj;ی لێی ده&zwnj;ترسن خوا هه&zwnj;ر ئاشکرای ده&zwnj;کات (بۆ خۆتان و بۆ خه&zwnj;ڵکیش).';
$t[65] = '(کاتێک تۆ ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر (صلى الله عليه وسلم) له&zwnj;گوفتاری نابه&zwnj;جێیان ئاگادار ده&zwnj;بیت) و لێیان ده&zwnj;پرسیت (ئه&zwnj;وه خه&zwnj;ریکی چین؟!) ئه&zwnj;وه بێگومان ده&zwnj;ڵێن: به&zwnj;ڕاستی ئێمه ته&zwnj;نها ده&zwnj;مه&zwnj;ته&zwnj;قێ و گه&zwnj;مه&zwnj;و گاڵته&zwnj;مان ده&zwnj;کرد، پێیان بڵێ: ئایا ئێوه به خواو ئایه&zwnj;ت و فه&zwnj;رمانه&zwnj;کانی و پێغه&zwnj;مبه&zwnj;ره&zwnj;که&zwnj;ی گاڵته&zwnj;تان ده&zwnj;کرد؟!';
$t[66] = '(ئیتر بێ سووده&zwnj;) پاکانه مه&zwnj;که&zwnj;ن، چونکه به&zwnj;ڕاستی ئێوه بێ باوه&zwnj;ڕ بوون دوای ئه&zwnj;وه&zwnj;ی له ڕواڵه&zwnj;تدا باوه&zwnj;ڕتان هێنابوو ئه&zwnj;گه&zwnj;ر له ده&zwnj;سته&zwnj;یه&zwnj;کتان خۆش ببین (دوای ته&zwnj;وبه&zwnj;ی ڕاسته&zwnj;قینه&zwnj;) ده&zwnj;سته&zwnj;یه&zwnj;کتان هه&zwnj;ر ئازار ده&zwnj;ده&zwnj;ین (به&zwnj;هۆی کاری نابه&zwnj;جێیه&zwnj;وه&zwnj;) چونکه به&zwnj;ڕاستی ئه&zwnj;وان تاوانبارو تاوانکار بوون.';
$t[67] = 'دووڕووه&zwnj;کان له پیاوان و له ژنان سه&zwnj;ر به&zwnj;یه&zwnj;کترن و وه&zwnj;ک یه&zwnj;کن له&zwnj;م ڕه&zwnj;فتارانه&zwnj;دا: فه&zwnj;رمان به&zwnj;گوناهو خراپه و تاوان ده&zwnj;ده&zwnj;ن و قه&zwnj;ده&zwnj;غه&zwnj;ی خێرو چاکه ده&zwnj;که&zwnj;ن و ده&zwnj;ستیان ده&zwnj;نوقێنن (ماڵ و سامان به&zwnj;شی که&zwnj;س ناده&zwnj;ن)، ئه&zwnj;وانه خوایان فه&zwnj;رامۆش کردووه&zwnj;، خوایش ئه&zwnj;وانی پشت گوێ خستووه&zwnj;، به&zwnj;ڕاستی دووڕووه&zwnj;کان هه&zwnj;رخۆیان یاخی و له ئایین ده&zwnj;رچوون.';
$t[68] = 'خوا (هه&zwnj;ڕه&zwnj;شه&zwnj;ی کردوه و) به&zwnj;ڵێنی ئاگری دۆزه&zwnj;خی به دووڕووه&zwnj;کان له پیاوان و ژنان و به&zwnj;بێ باوه&zwnj;ڕانیش داوه&zwnj;، که به&zwnj;رده&zwnj;وام و نه&zwnj;مرن تیایدا، ئه&zwnj;و دۆزه&zwnj;خه&zwnj;یان به&zwnj;سه و کاڵا به&zwnj;قه&zwnj;د باڵایه&zwnj;و خوا نه&zwnj;فره&zwnj;تی لێکردوون و سزای به&zwnj;رده&zwnj;وامیش به&zwnj;شیانه و بۆیان ئاماده&zwnj;یه&zwnj;.';
$t[69] = '(ئێوه&zwnj;ش ئه&zwnj;ی بێ باوه&zwnj;ڕان) وه&zwnj;ک ئه&zwnj;و بێ باوه&zwnj;ڕانه&zwnj;ن له پێش ئێوه&zwnj;دا بوون، که ئه&zwnj;وان له ئێوه به&zwnj;هێزتر بوون و ماڵ و منداڵیشیان له ئێوه زیاتر بوو، ئینجا ماوه&zwnj;یه&zwnj;ک به&zwnj;و به&zwnj;شه&zwnj;ی که هه&zwnj;یانبوو ڕایانبوارد، ئێوه&zwnj;ش ماوه&zwnj;یه&zwnj;ک ڕاتانبوارد به به&zwnj;شه&zwnj;که&zwnj;ی خۆتان، هه&zwnj;روه&zwnj;ک چۆن ئه&zwnj;وانه&zwnj;ی پێش ئێوه ڕایانبوارد به به&zwnj;شه&zwnj;کانی خۆیان!! ئێوه&zwnj;ش ڕۆچوون له ئاره&zwnj;زووبازیدا وه&zwnj;ک چۆن ئه&zwnj;وان ڕۆچوون له ئاره&zwnj;زووبازیدا، ئا ئه&zwnj;وانه هه&zwnj;موو کارو کرده&zwnj;وه&zwnj;یه&zwnj;کیان پووچه&zwnj;ڵ بۆته&zwnj;وه و به&zwnj;که&zwnj;ڵکیان نه&zwnj;هات له دنیاوقیامه&zwnj;تدا ئا ئه&zwnj;وانه هه&zwnj;ر خۆیان ڕه&zwnj;نج به&zwnj;خه&zwnj;سارن.';
$t[70] = 'ئایا ئه&zwnj;وانه هه&zwnj;واڵی گرنگی قه&zwnj;ومه&zwnj;کانی پێش خۆیان پێ نه&zwnj;گه&zwnj;یشتووه (به&zwnj;تایبه&zwnj;تی) قه&zwnj;ومی نوح و عادو ثمود، قه&zwnj;ومی ئیبراهیم و نیشته&zwnj;جێکانی مه&zwnj;دیه&zwnj;ن (قه&zwnj;ومی شوعه&zwnj;یب)، هه&zwnj;روه&zwnj;ها ڕه&zwnj;فتار نزم و بێ ڕه&zwnj;وشته&zwnj;کان (قه&zwnj;ومی لوط)، جا هه&zwnj;موو ئه&zwnj;و نه&zwnj;ته&zwnj;وانه پێغه&zwnj;مبه&zwnj;ره&zwnj;کانیان به&zwnj;به&zwnj;ڵگه&zwnj;و نیشانه&zwnj;ی ئاشکرایان بۆ هێنان، جا به&zwnj;ڕاستی وه&zwnj;نه&zwnj;بێت خوا سته&zwnj;می لێکردبن، به&zwnj;ڵکو ئه&zwnj;وان سته&zwnj;میان له خۆیان کرد.';
$t[71] = '(له&zwnj;و لاشه&zwnj;وه&zwnj;) ئیمانداران له پیاوان و له ئافره&zwnj;تان هه&zwnj;ندێکیان خۆشه&zwnj;ویست و پشتگیرو هاوکاری یه&zwnj;کترن (که ئه&zwnj;مه&zwnj;ش سیفه&zwnj;تیانه&zwnj;): فه&zwnj;رمان به&zwnj;چاکه ده&zwnj;ده&zwnj;ن و قه&zwnj;ده&zwnj;غه له خراپه ده&zwnj;که&zwnj;ن و نوێژه&zwnj;کانیان به&zwnj;چاکی ئه&zwnj;نجام ده&zwnj;ده&zwnj;ن و زه&zwnj;کاتی ماڵیشیان ده&zwnj;به&zwnj;خشن و گوێڕایه&zwnj;ڵ و فه&zwnj;رمانبه&zwnj;رداری خواو پێغه&zwnj;مبه&zwnj;ره&zwnj;که&zwnj;ی ده&zwnj;که&zwnj;ن، ئا ئه&zwnj;وانه له ئاینده&zwnj;دا خوا ڕه&zwnj;حمیان پێ ده&zwnj;کات و سۆزی ده&zwnj;بێت بۆیان، به&zwnj;ڕاستی خوا باڵاده&zwnj;ست و دانایه&zwnj;.';
$t[72] = 'خوای گه&zwnj;وره به&zwnj;ڵێنی داوه به ئیمانداران له پیاوان و له ئافره&zwnj;تان، به&zwnj;وه&zwnj;ی که باخه&zwnj;کانی به&zwnj;هه&zwnj;شتیان پێ ده&zwnj;به&zwnj;خشێت که چه&zwnj;نده&zwnj;ها ڕوبار به&zwnj;ژێر دره&zwnj;خته&zwnj;کانیدا ده&zwnj;ڕوات و جاریه به به&zwnj;رده&zwnj;م کۆشکه&zwnj;کانیاندا، هاوڕێ له&zwnj;گه&zwnj;ڵ ژیانی هه&zwnj;میشه&zwnj;یی و نه&zwnj;بڕاوه تیایدا، هه&zwnj;روه&zwnj;ها چه&zwnj;نده&zwnj;ها جێگه و ڕێگه و کۆشک و ته&zwnj;لاری خۆش و ڕازاوه&zwnj;ی تایبه&zwnj;تی له به&zwnj;هه&zwnj;شتداو له&zwnj;ناو باخی چڕوپڕی (عه&zwnj;دن) دا، له&zwnj;گه&zwnj;ڵ ڕه&zwnj;زامه&zwnj;ندی خوای گه&zwnj;وره&zwnj;دا، که له هه&zwnj;موو نازو نیعمه&zwnj;ته&zwnj;کان به&zwnj;نرخترو گرنگتره&zwnj;، ئه&zwnj;وه خۆی سه&zwnj;رفرازیه&zwnj;کی زۆر گه&zwnj;وره و بێ ئه&zwnj;ندازه&zwnj;یه&zwnj;.';
$t[73] = 'ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر (صلى الله عليه وسلم) دژی کافرو دووڕووه&zwnj;کان بجه&zwnj;نگه و تێ بکۆشه و توند و تیژبه له&zwnj;سه&zwnj;ریان، ئه&zwnj;وانه سه&zwnj;رئه&zwnj;نجام و مه&zwnj;نزڵگه&zwnj;یان دۆزه&zwnj;خه&zwnj;، ئای که سه&zwnj;رئه&zwnj;نجامێکی خراپ و ناشرینه&zwnj;.';
$t[74] = '(دووڕووه&zwnj;کان) سوێند به&zwnj;خوا ده&zwnj;خۆن که قسه&zwnj;یه&zwnj;کی نابه&zwnj;جێیان نه&zwnj;کردووه&zwnj;، که&zwnj;چی سوێند به&zwnj;خوا به&zwnj;ڕاستی ئه&zwnj;وانه درۆ ده&zwnj;که&zwnj;ن، به&zwnj;ڵکو گوفتاری کوفرو بێ باوه&zwnj;ڕی هات به&zwnj;ده&zwnj;میاندا، ئه&zwnj;وانه دوای موسڵمانبوونیان بێ باوه&zwnj;ڕ بوونه&zwnj;وه&zwnj;، خه&zwnj;ریکی (پیلان)و نه&zwnj;خشه&zwnj;یه&zwnj;ک بوون به&zwnj;ڵام بۆیان نه&zwnj;لواو پێی نه&zwnj;گه&zwnj;یشتن (که شه&zwnj;هید کردنی پێغه&zwnj;مبه&zwnj;ر بوو (صلى الله عليه وسلم)، هیچ شتێک ئه&zwnj;وانی تووڕه نه&zwnj;کردووه&zwnj;، ته&zwnj;نها ئه&zwnj;وه نه&zwnj;بێت: خواو پێغه&zwnj;مبه&zwnj;ره&zwnj;که&zwnj;ی (صلى الله عليه وسلم) ئیماندارانیان ده&zwnj;وڵه&zwnj;مه&zwnj;ند و به&zwnj;هره&zwnj;وه&zwnj;ر کرد له فه&zwnj;زڵ و زیاده ڕێزی خۆی، جا ئه&zwnj;و دووڕووانه ئه&zwnj;گه&zwnj;ر ته&zwnj;وبه بکه&zwnj;ن و له ڕه&zwnj;فتاری نادروستیان پاشگه&zwnj;ز ببنه&zwnj;وه ئه&zwnj;وه چاکتره بۆیان، خۆ ئه&zwnj;گه&zwnj;ر پشت هه&zwnj;ڵکه&zwnj;ن و (هه&zwnj;ر خه&zwnj;ریکی نه&zwnj;خشه&zwnj;و پیلان بن) ئه&zwnj;وه بێگومان خوا سزای به ئازاریان ده&zwnj;دات له دنیاو قیامه&zwnj;تدا، که&zwnj;سیش یارو یاوه&zwnj;رو پشتیوانیان نابێت له زه&zwnj;ویدا.';
$t[75] = '(له دووڕووه&zwnj;کان) که&zwnj;سانێک هه&zwnj;ن که په&zwnj;یمان و به&zwnj;ڵێنی به&zwnj;خوا داوه&zwnj;: سوێند به&zwnj;خوا ئه&zwnj;گه&zwnj;ر خوا ماڵ و سامان و دارایمان پێببه&zwnj;خشێت، به&zwnj;ڕاستی زه&zwnj;کات و خێری لێده&zwnj;که&zwnj;ین و بێگومان له ڕیزی که&zwnj;سانی چاک و خواناسیشدا ده&zwnj;بین.';
$t[76] = 'جا کاتێک خوا له فه&zwnj;زڵ و به&zwnj;ره&zwnj;که&zwnj;تی خۆی پێیدان و به&zwnj;هره&zwnj;وه&zwnj;ری کردن، ئه&zwnj;وان ڕه&zwnj;زیلیان تێدا کردو پشتیان له به&zwnj;ڵێنه&zwnj;که&zwnj;یان کرد له&zwnj;کاتێکدا که ئه&zwnj;وان هه&zwnj;میشه له به&zwnj;ڵێن و په&zwnj;یمان پشت هه&zwnj;ڵکه&zwnj;رن.';
$t[77] = 'ئه&zwnj;وسا ئیتر به&zwnj;هۆی ئه&zwnj;و به&zwnj;ڵێن شکاندنه&zwnj;یانه&zwnj;وه&zwnj;، خوا نه&zwnj;خۆشی دووڕوویی له دڵ و ده&zwnj;روونیاندا چه&zwnj;سپاندو دامه&zwnj;زراند تا ئه&zwnj;و ڕۆژه&zwnj;ی ئاماده ده&zwnj;بن (له&zwnj;به&zwnj;رده&zwnj;م دادگای خوایدا) بۆ لێپرسینه&zwnj;وه&zwnj;، چونکه نافه&zwnj;رمانی و سه&zwnj;رپێچیان کردووه له&zwnj;و به&zwnj;ڵێنه&zwnj;ی که دایان به خواو و به&zwnj;هۆی ئه&zwnj;وه&zwnj;شه&zwnj;وه که درۆیان ده&zwnj;کرد.';
$t[78] = 'مه&zwnj;گه&zwnj;ر ئایا ئه&zwnj;وانه نه&zwnj;یانده&zwnj;زانی به&zwnj;ڕاستی خوای گه&zwnj;وره ئاگاداره به&zwnj;نهێنی و چپه&zwnj;و سرته&zwnj;یان؟ ئایا نه&zwnj;شیانده&zwnj;زانی که به&zwnj;ڕاستی خوا به&zwnj;ته&zwnj;واوی زاناو ئاگایه به&zwnj;هه&zwnj;موو نهێنی و شاراوه&zwnj;کان؟';
$t[79] = 'ئه&zwnj;و بێ باوه&zwnj;ڕانه&zwnj;ی که پلار ده&zwnj;گرنه ئه&zwnj;و ئیماندارانه&zwnj;ی که زیاده به&zwnj;خشن له خێر کردندا به&zwnj;ویستی خۆیان له ڕێگه&zwnj;ی خوادا و پلاریش له ئیماندارانی هه&zwnj;ژار ده&zwnj;گرن که بێجگه له ڕه&zwnj;نجی شانی خۆیان هیچی تریان ده&zwnj;ست ناکه&zwnj;وێت، ئینجا هه&zwnj;ر گاڵته&zwnj;شیان پێده&zwnj;که&zwnj;ن، ئه&zwnj;وانه خوا تۆڵه&zwnj;یان لێ ده&zwnj;سێنێت و ڕیسوایان ده&zwnj;کات، به&zwnj;هۆی ئه&zwnj;و گاڵته کردنه&zwnj;یانه&zwnj;وه و سزای به ئێش و ئازاریش به&zwnj;شیانه&zwnj;.';
$t[80] = 'ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر (صلى الله عليه وسلم) داوای لێخۆش بوون بۆ ئه&zwnj;و دووڕووانه بکه&zwnj;یت یان داوای لێخۆش بوونیان بۆ نه&zwnj;که&zwnj;یت چوونیه&zwnj;که&zwnj;، ته&zwnj;نانه&zwnj;ت ئه&zwnj;گه&zwnj;ر حه&zwnj;فتا جاریش داوای لێخۆشبوونیان بۆ بکه&zwnj;یت، ئه&zwnj;وه خوا هه&zwnj;رگیز لێیان خۆش نابێت، ئه&zwnj;وه چونکه به ڕاستی ئه&zwnj;وان باوه&zwnj;ڕیان به خواو پێغه&zwnj;مبه&zwnj;ره&zwnj;که&zwnj;ی نه&zwnj;بوو، خوایش هیدایه&zwnj;ت و ڕێنموویی که&zwnj;سانی گوناهکارو له سنوور ده&zwnj;رچوو ناکات.';
$t[81] = '(دووڕووه&zwnj;کان)، ئه&zwnj;وانه&zwnj;ی که به&zwnj;جێهێڵران له&zwnj;ناو مه&zwnj;دینه&zwnj;دا شادمان بوون به&zwnj;دانیشتنیان و دواکه&zwnj;وتنیان له پێغه&zwnj;مبه&zwnj;رو نه&zwnj;چوونیان بۆ غه&zwnj;زا، حه&zwnj;زیان نه&zwnj;کرد جیهادو تێکۆشان به خۆیان و ماڵیان له پێناوی خوادا بکه&zwnj;ن و به ئیماندارانیان وت: له&zwnj;م گه&zwnj;رمایه&zwnj;دا ده&zwnj;رمه&zwnj;چن بۆ جیهاد، ئه&zwnj;ی محمد (صلى الله عليه وسلم) پێیان بڵێ: ئاگری دۆزه&zwnj;خ به&zwnj;تینتر و به&zwnj;هێزتره له&zwnj;گه&zwnj;رمیدا ئه&zwnj;گه&zwnj;ر تێ بگه&zwnj;ن و بیرو هۆشیان بخه&zwnj;نه&zwnj;کار.';
$t[82] = 'جا (وازیان لێ بهێنه&zwnj;) باکه&zwnj;مێک پێبکه&zwnj;نن (له دنیادا) و ده&zwnj;بێت زۆر بگرین (له قیامه&zwnj;تدا) له پاداشتی ئه&zwnj;و کارو کرده&zwnj;وه&zwnj;یه&zwnj;ی ده&zwnj;یکه&zwnj;ن.';
$t[83] = 'ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر (صلى الله عليه وسلم) جا ئه&zwnj;گه&zwnj;ر خوا (له غه&zwnj;زای ته&zwnj;بووک) تۆی گه&zwnj;ڕانده&zwnj;وه بۆ مه&zwnj;دینه&zwnj;، پاشان ده&zwnj;سته&zwnj;یه&zwnj;ک له&zwnj;و (دووڕوو دنیا په&zwnj;رستانه&zwnj;) مۆڵه&zwnj;تیان لێ خواستیت تا له ئاینده&zwnj;دا به&zwnj;شداری غه&zwnj;زا بکه&zwnj;ن و ده&zwnj;ربچن له&zwnj;گه&zwnj;ڵ ئێوه&zwnj;دا، ئه&zwnj;وه تۆش پێیان بڵێ: ئیتر هه&zwnj;رگیز له&zwnj;گه&zwnj;ڵ مندا ده&zwnj;رناچن و هه&zwnj;رگیز نابێت له&zwnj;گه&zwnj;ڵ مندا بجه&zwnj;نگن دژ به دوژمن، چونکه به&zwnj;ڕاستی ئێوه له سه&zwnj;ره&zwnj;تاوه ڕازی بوون به&zwnj;دانیشتن و نه&zwnj;هاتن، ده ئیتر هه&zwnj;ر دانیشن له&zwnj;گه&zwnj;ڵ ئه&zwnj;وانه&zwnj;دا که مانه&zwnj;وه&zwnj;و دواکه&zwnj;وتن و نه&zwnj;هاتن.';
$t[84] = 'هیچ کات له&zwnj;سه&zwnj;ر هیچ که&zwnj;س له&zwnj;و دووڕووانه نوێژ نه&zwnj;که&zwnj;ی که مرد، له&zwnj;سه&zwnj;ر گۆڕه&zwnj;که&zwnj;شی مه&zwnj;وه&zwnj;سته&zwnj;، چونکه به&zwnj;ڕاستی ئه&zwnj;وانه باوه&zwnj;ڕیان به خواو به پێغه&zwnj;مبه&zwnj;ره&zwnj;که&zwnj;ی نه&zwnj;بوو و مردیشن له&zwnj;کاتێکدا له دین ده&zwnj;رچوو بوون.';
$t[85] = '(ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر (صلى الله عليه وسلم) ئه&zwnj;ی ئیماندار) ماڵ و سامان و نه&zwnj;وه&zwnj;یان سه&zwnj;رسامت نه&zwnj;کات به&zwnj;باشیان بزانیت، چونکه به&zwnj;ڕاستی خوا هه&zwnj;ر ده&zwnj;یه&zwnj;وێت له ژیانی دنیادا تووشی به&zwnj;ڵاو سزایان بکات له ڕێگه&zwnj;ی ماڵ و سامان و نه&zwnj;وه&zwnj;یانه&zwnj;وه و گیانیان ده&zwnj;ربێت، له&zwnj;کاتێکدا که ئه&zwnj;وان بێ باوه&zwnj;ڕن.';
$t[86] = 'کاتێکیش سووره&zwnj;تێک له قورئان دابه&zwnj;زێنرێت و (داوایان لێ بکات) که باوه&zwnj;ڕ به&zwnj;خوا بهێنن و له&zwnj;گه&zwnj;ڵ پێغه&zwnj;مبه&zwnj;ره&zwnj;که&zwnj;یدا جیهاد بکه&zwnj;ن و تێ بکۆشن و بچن بۆ غه&zwnj;زا، ئه&zwnj;وه ده&zwnj;سه&zwnj;ڵاتدارو سه&zwnj;رمایه&zwnj;داره&zwnj;کانیان (دووڕووه&zwnj;کان) مۆڵه&zwnj;تت لێ ده&zwnj;خوازن و ده&zwnj;ڵێن: وازمان لێ بێنه تا له&zwnj;گه&zwnj;ڵ په&zwnj;ککه&zwnj;وته و دانیشتووه&zwnj;کاندا بین.';
$t[87] = '(سه&zwnj;یره&zwnj;) ئه&zwnj;و ـ به&zwnj;ناو پیاوانه ـ ڕازی بوون که له&zwnj;گه&zwnj;ڵ پیره ژناندا بن و بمێننه&zwnj;وه&zwnj;و دڵیان مۆری به&zwnj;سه&zwnj;ردا نرابوو، ئه&zwnj;وانه (له حه&zwnj;قیقه&zwnj;تی ئاین و پاداشتی جیهاد) حاڵی نین و تێناگه&zwnj;ن.';
$t[88] = 'به&zwnj;ڵام پێغه&zwnj;مبه&zwnj;ر (صلى الله عليه وسلم) و ئه&zwnj;وانه&zwnj;ی که له&zwnj;گه&zwnj;ڵیدا باوه&zwnj;ڕیان هێناوه&zwnj;، جیهادو تێکۆشانیان کردووه (بۆ چه&zwnj;سپاندنی ئاینی خوا) به ماڵ و سامان و گیانیانه&zwnj;وه&zwnj;، ئا ئه&zwnj;وانه هه&zwnj;رچی خێرو خۆشی هه&zwnj;یه به&zwnj;شیانه و ئه&zwnj;وانه سه&zwnj;رفرازو سه&zwnj;ربه&zwnj;رز و ڕزگارن.';
$t[89] = '(ئه&zwnj;و به&zwnj;خته&zwnj;وه&zwnj;رانه&zwnj;) خوا چه&zwnj;نده&zwnj;ها باخی به&zwnj;هه&zwnj;شتی بۆ ئاماده کردوون که چه&zwnj;نده&zwnj;ها ڕوبار به&zwnj;ژێر (دره&zwnj;خته&zwnj;کانی و به&zwnj;به&zwnj;رده&zwnj;م کۆشکه&zwnj;کانیدا) ده&zwnj;ڕوات، هاوڕێ له&zwnj;گه&zwnj;ڵ ژیانی هه&zwnj;میشه&zwnj;یی و نه&zwnj;بڕاوه تیایدا، ئه&zwnj;و سه&zwnj;رئه&zwnj;نجامه سه&zwnj;رکه&zwnj;وتن و سه&zwnj;رفرازیه&zwnj;کی زۆر گه&zwnj;وره&zwnj;یه&zwnj;.';
$t[90] = '(له&zwnj;کاتی غه&zwnj;زای ته&zwnj;بووکدا) پاکانه&zwnj;که&zwnj;ران له بیابان نشینانی عه&zwnj;ره&zwnj;ب هاتنه (خزمه&zwnj;تی پێغه&zwnj;مبه&zwnj;ر (صلى الله عليه وسلم) بۆ ئه&zwnj;وه&zwnj;ی مۆڵه&zwnj;تیان بدرێت که نه&zwnj;چن بۆ جیهاد، هه&zwnj;روه&zwnj;ها ئه&zwnj;وانیش که درۆیان له&zwnj;گه&zwnj;ڵ خواو پێغه&zwnj;مبه&zwnj;ره&zwnj;که&zwnj;یدا ده&zwnj;کرد دانیشتن و به&zwnj;شداری غه&zwnj;زاکه&zwnj;یان نه&zwnj;کرد، ئه&zwnj;وانه&zwnj;ی که بێ باوه&zwnj;ڕ بوون له&zwnj;ناو ئه&zwnj;وانه&zwnj;دا له&zwnj;ئاینده&zwnj;دا سزایه&zwnj;کی پڕ ئێش و ئازاریان تووش ده&zwnj;بێت.';
$t[91] = 'لاوازو نه&zwnj;خۆش و ئه&zwnj;وانه&zwnj;ش که خه&zwnj;رجییان ده&zwnj;ست ناکه&zwnj;وێت، هیچ گوناهو گله&zwnj;ییه&zwnj;کیان له&zwnj;سه&zwnj;ر نیه (له به&zwnj;شداری نه&zwnj;کردنی غه&zwnj;زادا) کاتێک دڵسۆز بن بۆ خواو پێغه&zwnj;مبه&zwnj;ره&zwnj;که&zwnj;ی، له&zwnj;سه&zwnj;ر ئه&zwnj;و چاکه&zwnj;خواز و پاکانه هیچ جۆره گله&zwnj;یی و ڕه&zwnj;خنه&zwnj;یه&zwnj;ک نیه&zwnj;، خوای گه&zwnj;وره لێخۆش بووه و میهره&zwnj;بانیشه&zwnj;.';
$t[92] = 'هه&zwnj;روه&zwnj;ها گله&zwnj;یی و ڕه&zwnj;خنه نیه له&zwnj;سه&zwnj;ر ئه&zwnj;و که&zwnj;سانه&zwnj;ی که دێن بۆ لای تۆ بۆ ئه&zwnj;وه&zwnj;ی وڵاخی سواریان بۆ په&zwnj;یدا بکه&zwnj;یت و بێن بۆ جیهاد، تۆش ده&zwnj;ڵێیت: هیچ شک نابه&zwnj;م تا بتانده&zwnj;مێ بۆ سواری (ئیتر به نائومێدی) گه&zwnj;ڕانه&zwnj;وه&zwnj;و چاویان فرمێسکی لێ ده&zwnj;ڕژا له خه&zwnj;فه&zwnj;تدا ئه&zwnj;سرینی لێ ده&zwnj;باری چونکه هیچ شک نابه&zwnj;ن بیبه&zwnj;خشن و خه&zwnj;رجی بکه&zwnj;ن (له پێناوی خوادا، چۆن له غه&zwnj;زا دواکه&zwnj;وتن)!!.';
$t[93] = 'به&zwnj;ڵکو ته&zwnj;نها گله&zwnj;یی و ڕه&zwnj;خنه له&zwnj;سه&zwnj;ر ئه&zwnj;وانه&zwnj;یه که مۆڵه&zwnj;تت لێ ده&zwnj;خوازن و خۆ ده&zwnj;دزنه&zwnj;وه&zwnj;، له کاتێکدا که ده&zwnj;وڵه&zwnj;مه&zwnj;ند و ده&zwnj;ست ڕۆیشتوویشن، ڕازین له&zwnj;گه&zwnj;ڵ پیره ژنان و په&zwnj;ککه&zwnj;وتواندابن و بمێننه&zwnj;وه&zwnj;، خوایش مۆری ناوه به&zwnj;سه&zwnj;ر دڵیاندا، بۆیه ئه&zwnj;وانه نازانن (خێر له چیدایه&zwnj;).';
$t[94] = 'کاتێک ئێوه له غه&zwnj;زا ده&zwnj;گه&zwnj;ڕێنه&zwnj;وه دووڕووه&zwnj;کان دێن بۆ لاتان و داوای لێ بووردنتان لێ ده&zwnj;که&zwnj;ن!! پێیان بڵێ: داوای لێ بووردن مه&zwnj;که&zwnj;ن، هه&zwnj;رگیز باوه&zwnj;ڕتان پێناکه&zwnj;ین، خوایش ئاگاداری کردووین له هه&zwnj;موو هه&zwnj;واڵ و هه&zwnj;ڵس و که&zwnj;وت و گوفتار و نیه&zwnj;تێکتان، له ئاینده&zwnj;دا خوا و پێغه&zwnj;مبه&zwnj;ره&zwnj;که&zwnj;ی کارو کرده&zwnj;وه&zwnj;تان ده&zwnj;بینن (له&zwnj;سه&zwnj;ر کوفر ده&zwnj;مێننه&zwnj;وه یاخود په&zwnj;شیمان ده&zwnj;بنه&zwnj;وه&zwnj;؟)، پاشان ده&zwnj;برێنه&zwnj;وه بۆ لای ئه&zwnj;و زاته&zwnj;ی که زانا و ئاگای په&zwnj;نهان و ئاشکرایه&zwnj;، ئینجا ئاگادارتان ده&zwnj;کاته&zwnj;وه به&zwnj;و کارو کرده&zwnj;وه&zwnj;یه&zwnj;ی که ده&zwnj;یکه&zwnj;ن.';
$t[95] = 'که ئێوه گه&zwnj;ڕانه&zwnj;وه (له غه&zwnj;زا) بۆ لایان سوێندتان بۆ ده&zwnj;خۆن به خوا بۆ ئه&zwnj;وه&zwnj;ی وازیان لێ بهێنن و سه&zwnj;رزه&zwnj;نشتیان نه&zwnj;که&zwnj;ن، جا ئێوه&zwnj;ش وازیان لێ بهێنن و پشتیان تێبکه&zwnj;ن، چونکه به&zwnj;ڕاستی ئه&zwnj;وانه (دڵ و ده&zwnj;روون) پیسن، شوێن و جێگه&zwnj;یان دۆزه&zwnj;خه له پاداشتی ئه&zwnj;و کارو کرده&zwnj;وه&zwnj;ی که ده&zwnj;یانکرد.';
$t[96] = 'هه&zwnj;ر سوێندتان بۆ ده&zwnj;خۆن تا لێیان ڕازی بن و چاوپۆشییان لێبکه&zwnj;ن، خۆ ئه&zwnj;گه&zwnj;ر ئێوه ڕازی بن لێیان، ئه&zwnj;وه بێگومان خوا له که&zwnj;سانی له ئایین ده&zwnj;رچوو ڕازی نابێت.';
$t[97] = 'زۆربه&zwnj;ی عه&zwnj;ره&zwnj;به کۆچه&zwnj;ری و دێهاتی و بیابان نشینه&zwnj;کان (له عه&zwnj;ره&zwnj;به شارنشینه&zwnj;کان) دڵ ڕه&zwnj;قترن له باره&zwnj;ی کوفرو دووڕووییه&zwnj;وه (چونکه دوورن له سه&zwnj;رچاوه&zwnj;کانی زانست و مه&zwnj;ده&zwnj;نیه&zwnj;ته&zwnj;وه&zwnj;)، شایسته&zwnj;ترن ئه&zwnj;و سنوورانه نه&zwnj;زانن که&zwnj;: خوا ناردوویه&zwnj;تیه خواره&zwnj;وه بۆ پێغه&zwnj;مبه&zwnj;ره&zwnj;که&zwnj;ی (ده&zwnj;رباره&zwnj;ی حه&zwnj;ڵاڵ و حه&zwnj;رام) خوای گه&zwnj;وره&zwnj;ش زانا و دانایه (به به&zwnj;نده&zwnj;کانی).';
$t[98] = 'هه&zwnj;ر له&zwnj;و عه&zwnj;ره&zwnj;به بیابان نشینانه&zwnj;، که&zwnj;سانێک هه&zwnj;یه که ئه&zwnj;وه&zwnj;ی ده&zwnj;یبه&zwnj;خشێت به&zwnj;سه&zwnj;رانه و غه&zwnj;رامه&zwnj;ی ده&zwnj;زانێت چاوه&zwnj;ڕێی ئه&zwnj;وه ده&zwnj;که&zwnj;ن چه&zwnj;نده&zwnj;ها به&zwnj;ڵاو ناخۆشی بێته سه&zwnj;رتان، یاخوا مه&zwnj;ینه&zwnj;تی خراپ به&zwnj;سه&zwnj;ر ئه&zwnj;واندا بێت، خوایش هه&zwnj;میشه بیسه&zwnj;ره (به گوفتاریان)، زانایه (به نیه&zwnj;تیان).';
$t[99] = 'له&zwnj;و عه&zwnj;ره&zwnj;به بیابان نشینانه که&zwnj;سانی تریان هه&zwnj;یه&zwnj;که ئیمان و باوه&zwnj;ڕ به&zwnj;خواو به&zwnj;ڕۆژی دوایی ئه&zwnj;هێنن و ئه&zwnj;وه&zwnj;ی خه&zwnj;رجی ده&zwnj;که&zwnj;ن و ده&zwnj;یبه&zwnj;خشن به&zwnj;چه&zwnj;نده&zwnj;ها هه&zwnj;نگاوی نزیک بوونه&zwnj;وه&zwnj;ی لای خوای داده&zwnj;نێن و بۆ ئه&zwnj;وه&zwnj;ی به&zwnj;هره&zwnj;مه&zwnj;ند بن له چه&zwnj;نده&zwnj;ها دۆعای خێری پێغه&zwnj;مبه&zwnj;ر (صلی الله علیه وسلم)، - ئاگاداربن- به&zwnj;ڕاستی ئه&zwnj;و به&zwnj;خشینه&zwnj;یان مایه&zwnj;ی سه&zwnj;رفرازی و نزیک بوونه&zwnj;وه&zwnj;یانه له خواوه&zwnj;، سه&zwnj;رئه&zwnj;نجام خوا ده&zwnj;یانخاته ژێر سایه&zwnj;ی ڕه&zwnj;حمه&zwnj;تیه&zwnj;وه&zwnj;، به&zwnj;ڕاستی لێخۆشبوو میهره&zwnj;بانه&zwnj;.';
$t[100] = 'خوای میهره&zwnj;بان له&zwnj;وانه&zwnj;ی که پێشکه&zwnj;وتن و یه&zwnj;که&zwnj;م که&zwnj;س بوون له ئیسلام بوون و خزمه&zwnj;ت کردنی ئیسلامدا له&zwnj;کاروانی کۆچبه&zwnj;ران و پشتیوانان و ئه&zwnj;وانه&zwnj;ی که به ڕێکوپێکی شوێنیان که&zwnj;وتوون، ڕازی بووه&zwnj;و ئه&zwnj;وانیش له&zwnj;و ڕازی بوون، باخه&zwnj;کانی به&zwnj;هه&zwnj;شتی بۆ ئاماده کردوون که چه&zwnj;نده&zwnj;ها ڕووبار به به&zwnj;رده&zwnj;م کۆشکه&zwnj;کانی و به&zwnj;ژێر دره&zwnj;خته&zwnj;کانیدا ده&zwnj;ڕوات، هاوڕێ له&zwnj;گه&zwnj;ڵ نه&zwnj;مریدا بۆ هه&zwnj;میشه&zwnj;یش تیایدا ده&zwnj;مێننه&zwnj;وه&zwnj;، ئه&zwnj;و سه&zwnj;رئه&zwnj;نجامه سه&zwnj;ر که&zwnj;وتنێکی زۆر گه&zwnj;وره&zwnj;یه&zwnj;.';
$t[101] = 'له&zwnj;و ناوچانه&zwnj;ی ده&zwnj;وورو&zwnj;به&zwnj;رتان هه&zwnj;ندێ عه&zwnj;ره&zwnj;بی بیابان نشینی دووڕوو هه&zwnj;ن و هه&zwnj;ندێ له دانیشتوانی شار (المدینة) ڕاهاتوون له&zwnj;سه&zwnj;ر دووڕووی و زرنگن له&zwnj;و باره&zwnj;یه&zwnj;وه&zwnj;، تۆ (ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر صلی الله علیه وسلم) نایانناسیت، به&zwnj;ڵکو ئێمه ده&zwnj;یانناسین، له&zwnj;مه&zwnj;ودوا ئه&zwnj;وانه هه&zwnj;ر له دنیادا دوو جار ئازاریان ده&zwnj;ده&zwnj;ین، پاشان ده&zwnj;برێنه&zwnj;وه به&zwnj;ره&zwnj;و سزاو ئازارێکی زۆر سه&zwnj;خت و دژوار.';
$t[102] = '(بێجگه له&zwnj;وانه&zwnj;) که&zwnj;سانێکی تریش هه&zwnj;ن که دان به&zwnj;گوناهو هه&zwnj;ڵه&zwnj;کانیاندا ده&zwnj;نێن و کرداری چاک و کرداری خراپیشیان تێکه&zwnj;ڵ کردووه&zwnj;، ئه&zwnj;مانه نزیکه خوا ته&zwnj;وبه&zwnj;یان لێ وه&zwnj;ربگرێت، چونکه به&zwnj;ڕاستی خوا لێخۆشبوو میهره&zwnj;بانه&zwnj;.';
$t[103] = 'له هه&zwnj;ندێ ماڵ و سامانی ئه&zwnj;و (ئیماندارانه&zwnj;) زه&zwnj;کات و خێر وه&zwnj;ربگره&zwnj;، چونکه ده&zwnj;بێته هۆی خاوێن کردنه&zwnj;وه&zwnj;ی ماڵه&zwnj;کانیان و پاککردنه&zwnj;وه&zwnj;ی دڵ و ده&zwnj;روونیان و دۆعای دابارینی ڕه&zwnj;حمه&zwnj;ت بکه به&zwnj;سه&zwnj;ریاندا و ئه&zwnj;گه&zwnj;ر مردیشن نوێژیان له&zwnj;سه&zwnj;ر بکه&zwnj;، چونکه به&zwnj;ڕاستی دۆعاو نوێژ کردنی تۆ مایه&zwnj;ی دڵنیایی و ئارامی&zwnj;یه بۆیان، خوایش هه&zwnj;میشه بیسه&zwnj;ر و زانایه&zwnj;.';
$t[104] = 'ئایا نه&zwnj;یانزانیووه که به&zwnj;ڕاستی خوا خۆی ته&zwnj;وبه قبوڵ ده&zwnj;کات له به&zwnj;نده&zwnj;کانی و هه&zwnj;موو خێر و سه&zwnj;ده&zwnj;قه&zwnj;یه&zwnj;کیشیان لێ وه&zwnj;رده&zwnj;گرێت و به&zwnj;ڕاستی خوا خۆی ته&zwnj;وبه وه&zwnj;رگرو میهره&zwnj;بانه&zwnj;.';
$t[105] = 'به&zwnj;و خه&zwnj;ڵکه بڵێ: ئێوه هه&zwnj;رچی ده&zwnj;توانن له کارو کرده&zwnj;وه ئه&zwnj;نجامی بده&zwnj;ن، چونکه خوا کارو کرده&zwnj;وه&zwnj;تان ده&zwnj;بینێت و (ئاگاداره به نهێنی و ئاشکرای)، هه&zwnj;روه&zwnj;ها پێغه&zwnj;مبه&zwnj;ره&zwnj;که&zwnj;شی (ئاگادار ده&zwnj;کات کاتێک له ژیاندایه، دوای وه&zwnj;فاتیشی بۆ خوای گه&zwnj;وره ئاسانه که نیشانی بدات)، هه&zwnj;روه&zwnj;ها ئیماندارانیش (ئاسه&zwnj;وارو ده&zwnj;رئه&zwnj;نجامی ده&zwnj;بینن)، پاشان ده&zwnj;گه&zwnj;ڕێنرێنه&zwnj;وه بۆ لای ئه&zwnj;و خوایه&zwnj;ی که زانایه به&zwnj;نهێنی و ئاشکرا، ئه&zwnj;وسا ئاگادارتان ده&zwnj;کات له هه&zwnj;موو ئه&zwnj;و کارو کرده&zwnj;وانه&zwnj;ی که ئه&zwnj;نجامی ده&zwnj;ده&zwnj;ن.';
$t[106] = 'که&zwnj;سانێکی تریش هه&zwnj;ن (له به&zwnj;جێ ماوه&zwnj;کان) ئه&zwnj;نجامی کرده&zwnj;وه&zwnj;یان دواخراوه بۆ فه&zwnj;رمانی خوا، یان سزایان ده&zwnj;دات (ئه&zwnj;گه&zwnj;ر به&zwnj;رده&zwnj;وام بن له&zwnj;سه&zwnj;ر دووڕووی) یاخود ته&zwnj;وبه&zwnj;یان لێ وه&zwnj;رده&zwnj;گرێت (ئه&zwnj;گه&zwnj;ر په&zwnj;شیمان بن)، خوای گه&zwnj;وره&zwnj;ش هه&zwnj;میشه زاناو دانایه&zwnj;.';
$t[107] = 'ئه&zwnj;و (دووڕووانه&zwnj;ی) که مزگه&zwnj;وتێکیان دروستکرد له&zwnj;به&zwnj;ر زه&zwnj;ره&zwnj;ردان و بڵاوکردنه&zwnj;وه&zwnj;ی بێ باوه&zwnj;ڕی و دووبه&zwnj;ره&zwnj;کی به&zwnj;رپاکردن له&zwnj;نێوان ئیمانداراو بۆ چاوه&zwnj;ڕوان کردنی هاتنی ئه&zwnj;و که&zwnj;سانه&zwnj;ی که پێشتر دووژمنایه&zwnj;تی و جه&zwnj;نگیان دژی خواو پێغه&zwnj;مبه&zwnj;ره&zwnj;که&zwnj;ی ده&zwnj;کرد و بێگومان سوێند ده&zwnj;خۆن که&zwnj;: ئێمه ته&zwnj;نها مه&zwnj;به&zwnj;ستمان چاکه بووه&zwnj;، (له کاتێکدا) خوایش شایه&zwnj;تی ده&zwnj;دات که به&zwnj;ڕاستی ئه&zwnj;وانه درۆزنن.';
$t[108] = 'هه&zwnj;رگیز له&zwnj;و مزگه&zwnj;وته&zwnj;دا ڕامه&zwnj;وه&zwnj;سته بۆ نوێژ کردن (چونکه&zwnj;) به&zwnj;ڕاستی مزگه&zwnj;وتێک شایسته&zwnj;یه که تۆ تیایدا بوه&zwnj;ستیت بۆ نوێژ کردن تیایدا که هه&zwnj;ر له یه&zwnj;که&zwnj;م ڕۆژه&zwnj;وه له&zwnj;سه&zwnj;ر بنچینه&zwnj;ی ته&zwnj;قواو خواناسی دروست کراوه&zwnj;، (چونکه&zwnj;) له&zwnj;و مزگه&zwnj;وته&zwnj;دا چه&zwnj;ند که&zwnj;سانێکی مه&zwnj;ردی تیادایه که زۆر حه&zwnj;ز ده&zwnj;که&zwnj;ن خۆیان خاوێن ڕابگرن (له ده&zwnj;روون و ڕواڵه&zwnj;تدا)، خوایش به&zwnj;رده&zwnj;وام ئه&zwnj;وانه&zwnj;ی خۆش ده&zwnj;وێت که خۆیان پاک ڕاده&zwnj;گرن.';
$t[109] = 'جا ئایا ئه&zwnj;و که&zwnj;سه&zwnj;ی که مزگه&zwnj;وت و کارو شوێنی له&zwnj;سه&zwnj;ر بنچینه&zwnj;ی ته&zwnj;قواو له خوا ترسان دروست کرد بێت و ڕه&zwnj;زامه&zwnj;ندی خوای مه&zwnj;به&zwnj;ست بێت چاکتره&zwnj;؟! یاخود ئه&zwnj;و که&zwnj;سه&zwnj;ی که بینای مزگه&zwnj;وت شوێن و به&zwnj;رنامه و کاری له&zwnj;سه&zwnj;ر لێواری که&zwnj;نده&zwnj;ڵانێکی بێ هێز و ڕووخاو دامه&zwnj;زراند بێت، ئینجا له&zwnj;گه&zwnj;ڵ خۆیدا بڕوخێته ناو ئاگری دۆزه&zwnj;خه&zwnj;وه&zwnj;، خوای گه&zwnj;وره&zwnj;ش ڕێنموویی ئه&zwnj;و ده&zwnj;سته سته&zwnj;مکاره ناکات.';
$t[110] = 'ئه&zwnj;و بینایه&zwnj;ی دووڕووه&zwnj;کان دروستیان کرد به&zwnj;رده&zwnj;وام هۆی گومان و زیاد بوونی دڵه ڕاوکێیه له دڵیاندا، هه&zwnj;تا ئه&zwnj;و کاته&zwnj;ی که دڵیان ده&zwnj;پچڕێت و ده&zwnj;مرن، بێگومان خوا زانایه (به پیلانی دووڕووه&zwnj;کان) دانایه (له هه&zwnj;ڵوه&zwnj;شاندنه&zwnj;وه و پووچ کردنه&zwnj;وه&zwnj;یاندا).';
$t[111] = 'به&zwnj;ڕاستی خوا، گیان و ماڵی له ئیمانداران کڕیوه به&zwnj;وه&zwnj;ی که به&zwnj;هه&zwnj;شت بۆ ئه&zwnj;وانه&zwnj;و به&zwnj;شیانه (به&zwnj;ڕاستی په&zwnj;روه&zwnj;ردگارێکی به&zwnj;خشنده&zwnj;یه&zwnj;، هه&zwnj;ر خۆی به&zwnj;خشێنه&zwnj;ری گیان و ماڵ و سامانه&zwnj;، که&zwnj;چی ئاوا مامه&zwnj;ڵه ده&zwnj;کات له&zwnj;گه&zwnj;ڵ ئیماندارانداو نرخێکی نه&zwnj;بڕاوه&zwnj;، نرخێکی زۆر گه&zwnj;وره&zwnj;ی بۆ بڕیارداوه&zwnj;، کاتێک له پێناوی ئه&zwnj;ودا ده&zwnj;یبه&zwnj;خشن) ئه&zwnj;وانه ده&zwnj;جه&zwnj;نگن له پێناوی ئاینی خوادا، دوژمنانی ئاینی خوا ده&zwnj;کوژن (له گۆڕه&zwnj;پانی جه&zwnj;نگدا)، یان ده&zwnj;کوژرێن و (شه&zwnj;هید ده&zwnj;کرێن)، ئه&zwnj;و پاداشت و به&zwnj;ڵێنه به&zwnj;ڕاستی له&zwnj;سه&zwnj;ر خوایه و باس کراوه له ته&zwnj;ورات و ئینجیل و قورئاندا، جا کێ هه&zwnj;یه به قه&zwnj;ده&zwnj;ر خوا به&zwnj;وه&zwnj;فاو به&zwnj;ئه&zwnj;مه&zwnj;ک بێت بۆ جێ به&zwnj;جێکردنی به&zwnj;ڵێنه&zwnj;کانی، که&zwnj;واته زۆر دڵخۆش و کامه&zwnj;ران بن به&zwnj;و مامه&zwnj;ڵه&zwnj;یه&zwnj;ی که ئه&zwnj;نجامتانداوه و به&zwnj;و فرۆشتن و کڕینه&zwnj;ی که له&zwnj;سه&zwnj;ری ڕێککه&zwnj;وتوون، بێگومان هه&zwnj;ر ئه&zwnj;وه&zwnj;شه سه&zwnj;رفرازی و سه&zwnj;رکه&zwnj;وتنی نه&zwnj;بڕاوه و بێ سنوور.';
$t[112] = '(جا ئه&zwnj;وانه&zwnj;ی ئه&zwnj;و مامه&zwnj;ڵه&zwnj;یه ده&zwnj;که&zwnj;ن پێشتر به&zwnj;م سیفه&zwnj;ته پیرۆزانه خۆیان ڕازاندۆته&zwnj;وه&zwnj;): ته&zwnj;وبه&zwnj;کارن و له&zwnj;هه&zwnj;ڵه&zwnj;کانیان په&zwnj;شیمانن، خواپه&zwnj;رستن (له هه&zwnj;موو هه&zwnj;ڵس و که&zwnj;وتیاندا)، سوپاسگوزارن (له&zwnj;سه&zwnj;ر هه&zwnj;&zwnj;موو نازو نیعمه&zwnj;تێک)، گه&zwnj;ڕۆکن (له&zwnj;سه&zwnj;ر زه&zwnj;ویدا، ئه&zwnj;ندێشه&zwnj;و بیرو هۆش به&zwnj;کارده&zwnj;هێنن بۆ بیر کردنه&zwnj;وه له بونه&zwnj;وه&zwnj;رو ده&zwnj;وروبه&zwnj;ر)، سوژده&zwnj;به&zwnj;رو کڕنوشبه&zwnj;رو نوێژ خوێنن، فه&zwnj;رمانده&zwnj;رن به&zwnj;هه&zwnj;موو چاکه&zwnj;یه&zwnj;ک، قه&zwnj;ده&zwnj;غه&zwnj;ی خراپه&zwnj;و هه&zwnj;موو نادروستیه&zwnj;ک ده&zwnj;که&zwnj;ن و پارێزه&zwnj;رن بۆ سنووره&zwnj;کانی خواو قه&zwnj;ده&zwnj;غه&zwnj;کراوه&zwnj;کانی، ئینجا مژده بده به&zwnj;ئیمانداران (ئه&zwnj;وانه&zwnj;ی ئه&zwnj;و سیفاتانه له خۆیاندا ده&zwnj;هێننه دی).';
$t[113] = 'بۆ پێغه&zwnj;مبه&zwnj;رو ئه&zwnj;وانه&zwnj;ی که باوه&zwnj;ڕیان هێناوه&zwnj;، ڕه&zwnj;وا نیه داوای لێ خۆشبوون بۆ موشریک و هاوه&zwnj;ڵگه&zwnj;ڕان بکه&zwnj;ن هه&zwnj;ر چه&zwnj;نده ئه&zwnj;وانه خزمی نزیکیش بن، دوای ئه&zwnj;وه&zwnj;ی که بۆیان ڕوون بۆته&zwnj;وه که به&zwnj;ڕاستی ئه&zwnj;وانه نیشته&zwnj;جێی دۆزه&zwnj;خن (به&zwnj;هۆی بێ دینی و تاوان و گوناهیانه&zwnj;وه&zwnj;).';
$t[114] = 'جا دوای لێخۆش بوونی ئیبراهیم بۆ باوکی له&zwnj;به&zwnj;ر به&zwnj;ڵێنێک بوو که پێی دابوو، کاتێک بۆی ده&zwnj;ر&zwnj;که&zwnj;وت به&zwnj;ڕاستی باوکی دووژمنی خوایه&zwnj;، ئیتر خۆی لێ به&zwnj;ری کرد، به&zwnj;ڕاستی ئیبراهیم زۆر به&zwnj;سۆزو دڵ نه&zwnj;رم بوو و زۆر خاوه&zwnj;ن حیلم بوو.';
$t[115] = 'هه&zwnj;رگیز خوا که&zwnj;سانێک گومڕا ناکات دوای ئه&zwnj;وه&zwnj;ی که هیدایه&zwnj;تی داون، هه&zwnj;تا بۆیان ڕوون نه&zwnj;کاته&zwnj;وه که ده&zwnj;بێت له&zwnj;چی خۆیان بپارێزن، به&zwnj;ڕاستی خوا به هه&zwnj;موو شتێک زانایه&zwnj;.';
$t[116] = 'به&zwnj;ڕاستی هه&zwnj;ر بۆ خوایه خاوه&zwnj;نداریه&zwnj;تی ئاسمانه&zwnj;کان و زه&zwnj;وی، ئه&zwnj;و خوایه ژیان ده&zwnj;به&zwnj;خشێت و مردنیش پێش ده&zwnj;هێنێت، هه&zwnj;رگیز جگه له خوا که&zwnj;ستان ده&zwnj;ست ناکه&zwnj;وێت که یارو یاوه&zwnj;رتان بێت و پشتیوانیتان لێ بکات.';
$t[117] = 'سوێند به&zwnj;خوا به&zwnj;ڕاستی خوا ته&zwnj;وبه&zwnj;ی قبوڵ کرد له پێغه&zwnj;مبه&zwnj;رو کۆچبه&zwnj;ران و پشتیوانان، ئه&zwnj;وانه&zwnj;ی که شوێنی پێغه&zwnj;مبه&zwnj;ر که&zwnj;وتن له&zwnj;کاتی سه&zwnj;غڵه&zwnj;تی و ته&zwnj;نگانه&zwnj;دا (له غه&zwnj;زای ته&zwnj;بووکدا)، دوای ئه&zwnj;وه&zwnj;ی که خه&zwnj;ریک بوو دڵی ده&zwnj;سته&zwnj;یه&zwnj;کیان له خشته بچێت و (بێزاری ده&zwnj;ربڕێت)، له&zwnj;وه&zwnj;ودوا ته&zwnj;وبه&zwnj;و په&zwnj;شیمانی ئه&zwnj;وانیشی قبوڵ کرد، چونکه به&zwnj;ڕاستی ئه&zwnj;و په&zwnj;روه&zwnj;ردگاره به&zwnj;سۆزو میهره&zwnj;بانه له&zwnj;گه&zwnj;ڵیاندا.';
$t[118] = 'هه&zwnj;روه&zwnj;ها ته&zwnj;وبه&zwnj;ی ئه&zwnj;و سێ که&zwnj;سه&zwnj;شی وه&zwnj;رگرت که (له غه&zwnj;زای ته&zwnj;بووک) دواخران دوای ئه&zwnj;وه&zwnj;ی که زه&zwnj;وی چه&zwnj;نده فراوانه لێیان ته&zwnj;نگ بۆوه&zwnj;، خۆیشیان له خۆیان بێزار بوون، دڵنیاش بوون که هیج په&zwnj;نایه&zwnj;ک نیه (له&zwnj;خه&zwnj;شم و قینی خوا بیانپارێزێت) جگه له&zwnj;وه&zwnj;ی خۆیان بده&zwnj;نه په&zwnj;نای ئه&zwnj;و زاته&zwnj;، پاشان ته&zwnj;وبه&zwnj;ی لێ&zwnj;وه&zwnj;رگرتن، تا ته&zwnj;وبه بکه&zwnj;ن، به&zwnj;ڕاستی هه&zwnj;ر خوا ته&zwnj;وبه وه&zwnj;رگرو میهره&zwnj;بانه&zwnj;. (له غه&zwnj;زای ته&zwnj;بووکدا سیان له هاوه&zwnj;ڵان بێ هیچ بیانوویه&zwnj;ک خۆیان دواخست که بریتی بوون له {که&zwnj;عبی کوڕی مالیک} و {مه&zwnj;راره&zwnj;ی کوڕی ڕه&zwnj;بیع} و {هیلالی کوڕی ئومه&zwnj;ییه&zwnj;}، کاتێک پێغه&zwnj;مبه&zwnj;ر صلی الله علیه وسلم گه&zwnj;ڕایه&zwnj;وه&zwnj;، دووڕووه&zwnj;کان ده&zwnj;هاتن و هه&zwnj;ریه&zwnj;ک به بیانویه&zwnj;ک پاکانه&zwnj;یان ده&zwnj;کرد، به&zwnj;ڵام ئه&zwnj;و سێ ئیمانداره هیچ بیانویه&zwnj;کیان نه&zwnj;بوو، دانیان به&zwnj;هه&zwnj;ڵه&zwnj;ی خۆیاندا نا، پێغه&zwnj;مبه&zwnj;ریش صلی الله علیه وسلم فه&zwnj;رمانیدا که&zwnj;که&zwnj;س قسه&zwnj;یان له&zwnj;گه&zwnj;ڵدا نه&zwnj;کات و ده&zwnj;بێت نه&zwnj;چن به&zwnj;لای خێزانه&zwnj;کانیاندا، ئه&zwnj;م ته&zwnj;مێ کردنه&zwnj;ش، په&zwnj;نجا شه&zwnj;و&zwnj;ی خایاند.';
$t[119] = 'ئه&zwnj;ی ئه&zwnj;وانه&zwnj;ی ئیمان و باوه&zwnj;ڕتان هێناوه له&zwnj;خوا بترسن و پارێزکار بن، هه&zwnj;وڵ بده&zwnj;ن هه&zwnj;میشه له&zwnj;گه&zwnj;ك ڕاستگۆیان و ڕاستاندا بن.';
$t[120] = 'ڕه&zwnj;وا نه&zwnj;بوو بۆ دانیشتوانی مه&zwnj;دینه و بیابان نشینانی ده&zwnj;وروبه&zwnj;ری دوا بکه&zwnj;ون (له جیهاد و غه&zwnj;زا) له خزمه&zwnj;تی پێغه&zwnj;مبه&zwnj;ری خوا صلی الله علیه وسلم و خۆیان خۆشتر بوێت له&zwnj;و، چونکه ئه&zwnj;وه به&zwnj;ڕاستی هیچ تینوویه&zwnj;تی و ماندوو بوون و برسێتیه&zwnj;کیان تووش نه&zwnj;ده&zwnj;هات له&zwnj;پێناوی خوادا (به&zwnj;زایه بچێت) و نه&zwnj;ده&zwnj;چوونه هیچ شوێنێک و پێیان نه&zwnj;ده&zwnj;خسته هیچ جێیه&zwnj;ک بێ باوه&zwnj;ڕان پێی سه&zwnj;غڵه&zwnj;ت بن و ده&zwnj;ستیان له هیچ دوژمنێک نه&zwnj;ده&zwnj;وه&zwnj;شان، که نه&zwnj;بێته هۆی ئه&zwnj;وه&zwnj;ی به&zwnj;کارو کرده&zwnj;وه&zwnj;یه&zwnj;کی چاک بۆیان تۆمار نه&zwnj;کرێت، به&zwnj;ڕاستی خوا پاداشتی چاکه&zwnj;کاران به زایه نادات.';
$t[121] = 'هه&zwnj;روه&zwnj;ها هیچ به&zwnj;خششێکیان پێشکه&zwnj;ش نه&zwnj;ده&zwnj;کرد بچوک یان گه&zwnj;وره و به&zwnj;هیچ دۆل و شیوێکدا تێ نه&zwnj;ده&zwnj;په&zwnj;ڕین، به چاکه بۆیان تۆمار نه&zwnj;کرێت، تا خوا به&zwnj;چاکترین شێوه پاداشتی ئه&zwnj;و کرده&zwnj;وانه&zwnj;یان بداته&zwnj;وه که ده&zwnj;یانکرد.';
$t[122] = 'جا ڕه&zwnj;وا نیه هه&zwnj;رچی ئیمانداران هه&zwnj;یه به&zwnj;جارێک بچن بۆ غه&zwnj;زا (یان بۆ هه&zwnj;رکارێکی پێویست)، ده&zwnj;ی بۆچی له هه&zwnj;ر هۆزو ده&zwnj;سته&zwnj;یه&zwnj;ک، که&zwnj;سانێک نه&zwnj;چوون بۆ خوێندن تا شاره&zwnj;زابن له ئایین تێبگه&zwnj;ن و فێر ببن و قه&zwnj;ومه&zwnj;که&zwnj;شیان تێبگه&zwnj;یه&zwnj;نن و فێریان بکه&zwnj;ن کاتێک که گه&zwnj;ڕانه&zwnj;وه بۆ لایان، بۆ ئه&zwnj;وه&zwnj;ی ئه&zwnj;وان هوشیار ببنه&zwnj;وه خۆیان بپارێزن له گوناهو خوا له خۆ نه&zwnj;ڕه&zwnj;نجێنن.';
$t[123] = 'ئه&zwnj;ی ئه&zwnj;وانه&zwnj;ی باوه&zwnj;ڕتان هێناوه بجه&zwnj;نگن دژی ئه&zwnj;و بێ باوه&zwnj;ڕانه&zwnj;ی که&zwnj;لێتان نزیک ده&zwnj;بنه&zwnj;وه به&zwnj;ده&zwnj;وروبه&zwnj;رتاندا (نیازی شه&zwnj;ڕ فرۆشتنیان هه&zwnj;یه&zwnj;) و باتوندو تیژی و هێزو تواناتان تیادا به&zwnj;دی بکه&zwnj;ن، بشزانن به&zwnj;ڕاستی خوا یارو یاوه&zwnj;ری پارێزکارانه&zwnj;.';
$t[124] = 'کاتێکیش سوره&zwnj;تێک له قورئان دابه&zwnj;زێنرا، هه&zwnj;ندێک له (دووڕووه&zwnj;کان به&zwnj;گاڵته پێ&zwnj;کردنه&zwnj;وه&zwnj;) ده&zwnj;ڵێن: کێتان ئه&zwnj;م سوره&zwnj;ته باوه&zwnj;ڕی زیاد کردووه&zwnj;؟! جا ئه&zwnj;وانه&zwnj;ی که باوه&zwnj;ڕیان هێنا بوو ئه&zwnj;وه&zwnj;نده&zwnj;ی تر ئیمان و باوه&zwnj;ڕی پته&zwnj;و تر کردن، ئه&zwnj;وانیش مژده به&zwnj;یه&zwnj;کتر ده&zwnj;ده&zwnj;ن (ده&zwnj;رباره&zwnj;ی سه&zwnj;رکه&zwnj;وتن و خۆشی هه&zwnj;ردوو جیهان).';
$t[125] = 'ئه&zwnj;وانه&zwnj;ش که نه&zwnj;خۆشی بێ دینی و دووڕوویی له دڵیاندایه&zwnj;، ئه&zwnj;وه سه&zwnj;رباری پیسی و ناپوختی خۆیان ئه&zwnj;وه&zwnj;نده&zwnj;ی تر سوره&zwnj;ته&zwnj;که پیسی و ناپوختیان زیاد ده&zwnj;کات، ده&zwnj;شمرن له&zwnj;کاتێکدا که بێ باوه&zwnj;ڕن.';
$t[126] = 'ئایا (دووڕووه&zwnj;کان) نابینن و نازانن به&zwnj;ڕاستی ئه&zwnj;وان له هه&zwnj;موو ساڵێکدا جارێک یان دوو جار تاقی ده&zwnj;کرێنه&zwnj;وه&zwnj;، پیلان و نه&zwnj;خشه&zwnj;یان ده&zwnj;رده&zwnj;که&zwnj;وێت و (سووک و ڕیسوا ده&zwnj;بن)، پاشان ته&zwnj;وبه ناکه&zwnj;ن و په&zwnj;شیمان نابنه&zwnj;وه&zwnj;و یاداوه&zwnj;ریش وه&zwnj;رناگرن و ئه&zwnj;وان بیرناکه&zwnj;نه&zwnj;وه&zwnj;.';
$t[127] = 'کاتێکیش سوره&zwnj;تێک له قورئان دابه&zwnj;زێنرا (ئه&zwnj;وان سه&zwnj;غڵه&zwnj;تن)، ئه&zwnj;وه هه&zwnj;ندێکیان ته&zwnj;ماشای هه&zwnj;ندێکی تریان ده&zwnj;که&zwnj;ن (حه&zwnj;ز ده&zwnj;که&zwnj;ن زوو ئه&zwnj;و شوێنه جێ بهێڵن، به&zwnj;چپه یان به&zwnj;ئیشاره&zwnj;ت) ده&zwnj;ڵێن: ئایا هیچ که&zwnj;س ده&zwnj;تانبینێت (له ئیمانداران)، پاشان به&zwnj;نهێنی خۆیان دزیه&zwnj;وه&zwnj;و ڕۆیشتن، له&zwnj;کاتێکدا خوای باڵاده&zwnj;ستیش دڵی ئه&zwnj;وانی وه&zwnj;رچه&zwnj;رخاندووه&zwnj;، چونکه به&zwnj;ڕاستی ئه&zwnj;وانه که&zwnj;سانێکن که تێ&zwnj;ناگه&zwnj;ن وحاڵی نین (ناشیانه&zwnj;وێت تێبگه&zwnj;ن).';
$t[128] = '(ئه&zwnj;ی خه&zwnj;ڵکینه&zwnj;) سوێند به خوا بێگومان پێغه&zwnj;مبه&zwnj;رێکتان بۆ ڕه&zwnj;وانه کراوه که هه&zwnj;ر یه&zwnj;کێکه له خۆتان، زۆر سه&zwnj;خته به&zwnj;لایه&zwnj;وه تووش بوونتان به&zwnj;به&zwnj;ڵاو ناخۆشی، زۆر به&zwnj;ته&zwnj;نگیشتانه&zwnj;وه&zwnj;یه و په&zwnj;رۆشه له&zwnj;سه&zwnj;رتان، زۆر دڵسۆزی ئیماندارانه و میهره&zwnj;بان و دلۆڤانه بۆیان.';
$t[129] = 'جا ئه&zwnj;گه&zwnj;ر ئه&zwnj;وانه (له&zwnj;گه&zwnj;ڵ ئه&zwnj;و هه&zwnj;موو دڵسۆزیی و میهره&zwnj;بانیه&zwnj;ی تۆدا) هه&zwnj;ر پشت هه&zwnj;ڵکه&zwnj;ن و گوێت بۆ نه&zwnj;گرن، ئه&zwnj;وه ئیتر تۆ بڵێ: من خوام به&zwnj;سه یارو یاوه&zwnj;رم بێت، که پشتگیرو یاریده&zwnj;ده&zwnj;رم بێت، هیچ خوایه&zwnj;ک نیه جگه له&zwnj;و، هه&zwnj;ر پشت به&zwnj;و ده&zwnj;به&zwnj;ستم، ئه&zwnj;و زاته&zwnj;ش په&zwnj;روه&zwnj;ردگاری عه&zwnj;رشی گه&zwnj;وره&zwnj;یه&zwnj;.';
?>