$t[86] = 'Moise se &icirc;ntoarse la poporul său, m&acirc;nios şi m&acirc;hnit. El spuse: &ldquo;O, popor al meu! Domnul vostru nu v-a făcut o frumoasă făgăduială? Legăm&acirc;ntul vi s-a părut prea lung? Ori aţi dori să se abată asupra voastră m&acirc;nia Domnului vostru, căci v-aţi &icirc;ncălcat făgăduiala faţă de mine?&rdquo;';
$t[87] = 'Ei spuseră: &ldquo;Noi nu am &icirc;ncălcat făgăduiala făcută Ţie cu de la noi putere, ci am fost &icirc;ncărcaţi cu poveri de podoabe ale poporului. Noi le-am aruncat şi aşa s-a lepădat de ele şi Samiri.&rdquo;';
$t[88] = 'El le-a scos apoi un viţel ca un chip care mugea. Ei spuseră atunci: &ldquo;Acesta este dumnezeul vostru şi dumnezeul lui Moise care a uitat!&rdquo;';
$t[89] = 'Ei nu vedeau că nu le răspundea şi nici nu-i păgubea şi nici nu le folosea?';
$t[90] = 'Aaron le spusese mai &icirc;nainte: &ldquo;O, popor al meu! Aţi fost puşi la &icirc;ncercare prin el. Domnul vostru este Milostivul. Urmaţi-mă şi daţi ascultare poruncii mele.&rdquo;';
$t[91] = 'Ei spuseră: &ldquo;Nu vom &icirc;nceta să ne &icirc;nchinăm lui p&acirc;nă c&acirc;nd nu se va &icirc;ntoarce Moise la noi.&rdquo;';
$t[92] = 'Moise spuse: &ldquo;O, Aaron! Ce te-a &icirc;mpiedicat c&acirc;nd i-ai văzut rătăcindu-se';
$t[93] = 'să mă fi urmat? Tu mi-ai nesocotit porunca.';
$t[94] = 'El spuse: &ldquo;O, fiu al mamei mele! Nu mă apuca nici de barbă şi nici de cap. M-am temut că &icirc;mi vei spune: &ldquo;Tu i-ai dezbinat pe fiii lui Israel şi nu ai luat &icirc;n seamă cuv&acirc;ntul meu.&rdquo;';
$t[95] = 'Moise spuse: &ldquo;Ce ai de spus, Samiri?&rdquo;';
$t[96] = 'Acesta spuse: &ldquo;Eu am văzut ceea ce ei nu au văzut. Am luat o m&acirc;nă de ţăr&acirc;nă de pe urma trimisului şi am aruncat-o. Aşa mi-a dat ghes sufletul meu.&rdquo;';
$t[97] = 'Moise spuse: &ldquo;Pleacă! Şi &icirc;n viaţă &icirc;ţi este dat să spui: &lsquo;Nu mă atingeţi! Şi vei avea o &icirc;nt&acirc;lnire pe care nu o vei &icirc;ncălca. Uită-te la dumnezeul tău căruia i te-ai &icirc;nchinat, noi &icirc;l vom arde şi-i vom &icirc;mprăştia rămăşiţele &icirc;n mare!&rsquo;&rdquo;';
$t[98] = 'Dumnezeul vostru este Dumnezeu, Unul. Nu este dumnezeu afară de El! El cuprinde fiece lucru &icirc;ntru ştiinţă.';
$t[99] = 'Noi &icirc;ţi povestim din istoriile ce s-au petrecut &icirc;nainte şi Noi &icirc;ţi aducem o amintire de la Noi.';
$t[100] = 'Cei care se &icirc;ntorc vor căra o povară grea &icirc;n Ziua &Icirc;nvierii,';
$t[101] = 'pe care o vor purta de-a pururi. Ur&acirc;tă povară pentru Ziua &Icirc;nvierii,';
$t[102] = 'pentru Ziua c&acirc;nd se va sufla &icirc;n tr&acirc;mbiţă! &Icirc;n Ziua aceea &icirc;i vom aduna pe nelegiuiţii cum sunt cei cu ochii albăstriţi,';
$t[103] = 'iar ei &icirc;şi vor şopti unii altor: &ldquo;N-aţi stat dec&acirc;t zece zile!&rdquo;';
$t[104] = 'Noi ştim prea bine ce vor spune, iar unul dintre ei cu o purtare pilduitoare va spune: &ldquo;Voi n-aţi stat dec&acirc;t o zi!&rdquo;';
$t[105] = 'Ei se vor &icirc;ntreba despre munţi. Spune: &ldquo;Domnul meu &icirc;i va face făr&acirc;me';
$t[106] = 'şi va lăsa &icirc;n urma lor un şes neted';
$t[107] = '&icirc;n care nu vei vedea nici văluriri şi nici ad&acirc;ncituri.&rdquo;';
$t[108] = '&Icirc;n Ziua aceea, ei &icirc;l vor urma pe cel care &icirc;i va chema. Glasurile se vor potoli &icirc;naintea Milostivului şi nu vei auzi dec&acirc;t un murmur uşor.';
$t[109] = '&Icirc;n Ziua aceea, mijlocirea nu-i va sluji dec&acirc;t celui căruia Milostivul &icirc;i &icirc;ngăduie şi a cărui spusă &icirc;i face plăcere.';
$t[110] = 'El ştie ceea ce se afla &icirc;naintea şi &icirc;napoia lor, &icirc;nsă ei nu-L pot cuprinde pe El &icirc;ntru ştiinţă.';
$t[111] = 'Feţele se vor smeri &icirc;naintea Celui viu, a Celui ce este. Cel care poartă o nedreptate va fi pierdut,';
$t[112] = '&icirc;nsă cel care a săv&acirc;rşit fapte bune, credincios fiind, nu are a se teme nici de nedreptate, nici de mistuire.';
$t[113] = 'Noi am pogor&acirc;t astfel un Coran arab &icirc;n care am pus şi ameninţări. Poate se vor teme ori poate le va fi &icirc;mprospătată amintirea!';
$t[114] = '&Icirc;nălţare lui Dumnezeu, Regele, Adevărul! Nu te pripi cu Coranul &icirc;nainte ca dezvăluirea lui să se &icirc;mplinească. Spune: &ldquo;Domnul meu! &Icirc;nmulţeşte-mi ştiinţa!&rdquo;';
$t[115] = 'Odinioară am &icirc;ncheiat un legăm&acirc;nt cu Adam, &icirc;nsă el l-a uitat şi nu am aflat la el nici o tărie.';
$t[116] = 'C&acirc;nd le-am spus &icirc;ngerilor: &ldquo;Prosternaţi-vă &icirc;naintea lui Adam&rdquo;, ei se prosternară &icirc;n afară de Iblis care a refuzat.';
$t[117] = 'Noi am spus: &ldquo;O, Adam! Acesta &icirc;ţi este vrăjmaş ţie şi soţiei tale. Să nu vă izgonească din Grădină, căci veţi fi năpăstuiţi.';
$t[118] = 'Aici nu-ţi va fi foame şi nu vei fi nici gol.';
$t[119] = 'Nu-ţi va fi sete şi nu vei suferi de arşiţă.&rdquo;';
$t[120] = 'Diavolul &icirc;l ispiti spun&acirc;nd: &ldquo;O, Adame! &Icirc;ţi voi arăta Copacul nemuririi şi-al unei &icirc;mpărăţii nepieritoare!&rdquo;';
$t[121] = 'Am&acirc;ndoi m&acirc;ncară şi-şi văzură goliciunea şi atunci se apucară să &icirc;nşire pe ei frunze din grădină. Adam L-a nesocotit pe Domnul său, şi astfel se rătăci.';
$t[122] = 'Domnul său l-a ales, apoi se &icirc;ntoarse la el şi-l călăuzi.';
$t[123] = 'El spuse: &ldquo;Cobor&acirc;ţi am&acirc;ndoi din grădină, şi vrăjmaşi unul altuia să fiţi! De la Mine vă va veni o călăuzire, şi cine va urma călăuzirea Mea nu se va rătăci şi nu va fi năpăstuit.';
$t[124] = 'Cine se va &icirc;mpotrivi &icirc;nsă amintirii Mele va avea un trai mohor&acirc;t, apoi orb &icirc;l vom &icirc;nfăţişa &icirc;n Ziua &Icirc;nvierii.&rdquo;';
$t[125] = 'El va spune: &ldquo;Domnul meu! De ce m-ai &icirc;nfăţişat aici orb, căci &icirc;nainte vedeam limpede?&rdquo;';
$t[126] = 'Dumnezeu va spune: &ldquo;Precum tu ai uitat semnele Noastre după ce au ajuns la tine, tot aşa vei fi dat şi tu uitării astăzi.&rdquo;';
$t[127] = 'Aşa &icirc;l răsplătim pe cel necumpătat ce tăgăduieşte semnele Domnului său. Os&acirc;nda de Apoi este mult mai de temut şi va dura o veşnicie.';
$t[128] = 'Ei nu-şi dau seama c&acirc;te leaturi am nimicit &icirc;naintea lor, prin ale căror locuinţe ei umblă acum! &Icirc;ntru aceasta sunt semne pentru cei dăruiţi cu minte.';
$t[129] = 'Dacă un Cuv&acirc;nt de la Domnul tău, nu va veni &icirc;nainte de această Zi, os&acirc;nda va fi fără tăgadă la soroc.';
$t[130] = 'Rabdă ceea ce spun. Preamăreşte-L pe Domnul tău cu laude &icirc;nainte de răsăritul soarelui şi &icirc;nainte de asfinţitul lui, şi la vreme de noapte, preamăreşte-L şi la capetele zilei. Poate &Icirc;i vei fi pe plac!';
$t[131] = 'Nu-ţi lungi privirile asupra bucuriilor trecătoare pe care Noi le dăruim unora dintre ei, căci ele sunt numai o floare a Vieţii de Acum, făcută să-i punem la &icirc;ncercare. Zestrea de la Domnul tău este mai bună şi durează o vecie.';
$t[132] = 'Porunceşte alor tăi să se roage şi stăruie tu &icirc;nsuţi &icirc;n rugăciune. Noi nu-ţi cerem zestre, ba Noi te &icirc;nzestrăm pe tine. Sf&acirc;rşitul (fericit) va fi al celor temători.';
$t[133] = 'Ei au spus: &ldquo;Dacă ne-ar aduce măcar un semn de la Domnul său!&rdquo; Dovada de netăgăduit din &icirc;nt&acirc;ile Scripturi nu a ajuns şi la ei?';
$t[134] = 'Dacă i-am fi nimicit cu o os&acirc;ndă &icirc;naintea lui, ar fi spus: &ldquo;Domnul nostru! Dacă ne-ai fi trimis un trimis, am fi urmat semnele Tale &icirc;nainte de a fi umiliţi şi batjocoriţi.&rdquo;';
$t[135] = 'Spune: &ldquo;Fiecare aşteaptă. Aşteptaţi, aşadar! Cur&acirc;nd veţi şti cine sunt cei de pe Calea cea Netedă şi cine este bine călăuzit.&rdquo;';
?>