$t[8] = 'E pastaj than&euml;: &ldquo;Pse t&euml; mos i zbret atij (Muhammedit) nj&euml; engj&euml;ll (t&euml; na thot&euml; p&euml;r Muhammedin se &euml;sht&euml; i d&euml;rguar)&rdquo;. E sikur t&euml; zbritnim Ne nj&euml; engj&euml;ll, &ccedil;&euml;shtja do t&euml; merrte fund duke mos u dh&euml;n&euml; atyre nj&euml; afat.';
$t[9] = 'Sikur ta b&euml;nim Ne at&euml; (t&euml; d&euml;rguarin) engj&euml;ll, at&euml; do ta b&euml;nim (n&euml; form&euml;) njeriu e do t&rsquo;ua p&euml;rzienim (ngat&euml;rronim) atyre at&euml; q&euml; i p&euml;rzien ata vetes s&euml; tyre.';
$t[10] = 'Edhe t&euml; d&euml;rguarit t&euml; tjer&euml; para teje jan&euml; p&euml;rqeshur, e ata q&euml; u tall&euml;n p&euml;suan (d&euml;nim) p&euml;r shkak se talleshin.';
$t[11] = 'Thuaj: &ldquo;Ecni n&euml;p&euml;r tok&euml; e mandej shikoni se si qe p&euml;rfundimi i g&euml;njeshtar&euml;ve.';
$t[12] = 'Tuaj: &ldquo;E kujt &euml;sht&euml; e t&euml;r&euml; kjo n&euml; qiej e tok&euml;?&rdquo; Thuaj: &ldquo;Vet&euml;m e All-llahut&rdquo;. Ai ia p&euml;raktoi m&euml;shir&euml;n Vetes. Ai do t&rsquo;ju tuboj&euml; n&euml; dit&euml;n e gjykimit, p&euml;r t&euml; cil&euml;n gj&euml; nuk ka dyshim. Ata q&euml; i shkaktuan humbje vetvetes, ata nuk besojn&euml;.';
$t[13] = 'Vet&euml;m e Tij &euml;sht&euml; gjith&euml;&ccedil;ka pushon e l&euml;viz nat&euml;n e dit&euml;n. Ai gjith&euml;&ccedil;ka d&euml;gjon dhe gjith&euml;&ccedil;ka di.';
$t[14] = 'Thuaj: &ldquo;A pos All-llahut, krijues i qiejvedhe i tok&euml;s, q&euml; ushqen t&euml; tjer&euml;t, nd&euml;rsa vet&euml; nuk ushqehet, t&euml; pranoj Zot tjet&euml;r?&rdquo; Thuaj: &ldquo;Un&euml; jam urdh&euml;ruar t&euml; jem i pari q&euml; bindem dhe (urdh&euml;rohem): t&euml; mos b&euml;hem kurrsesi nga idhujtar&euml;t.';
$t[15] = 'Thuaj: &ldquo;V&euml;rtet, un&euml; i frik&euml;sohem d&euml;nimit t&euml; dit&euml;s s&euml;madhe, n&euml;se nuk i p&euml;rulem Zotit tim&rdquo;.';
$t[16] = 'Kush m&euml;njanohet at&euml; dit&euml; nga ai (d&euml;nimi), at&euml; Ai e ka m&euml;shiruar, dhe ai &euml;sht&euml; shp&euml;tim i qart&euml;.';
$t[17] = 'N&euml;se All-llahu t&euml; godet me ndonj&euml; t&euml; keqe, s&rsquo;ka kush q&euml; ta largoj&euml; at&euml; pos Tij, e n&euml;se t&euml; dhuron ndonj&euml; t&euml; mir&euml;, duhet ditur se Ai &euml;sht&euml; i gjith&euml;fuqish&euml;m p&euml;r &ccedil;do send.';
$t[18] = 'Ai &euml;sht&euml; mbizot&euml;rues ndaj rob&euml;rve t&euml; Tij. Ai holl&euml;sisht di pun&euml;t e Tij dhe t&euml; rob&euml;rve t&euml; Vet.';
$t[19] = 'Thuaj: &ldquo;Cili send ka d&euml;shmin&euml; m&euml; t&euml; madhe?&rdquo; Thuaj: &ldquo;All-llahu &euml;sht&euml; d&euml;shmues mes meje e jush, e mua m&euml; &euml;sht&euml; shpallur ky Kur&rsquo;an q&euml; me t&euml; t&rsquo;ju t&euml;rheq&euml; v&euml;rejtjen juve dhe atij q&euml; i komunikohet (dhe t&euml; gjith&euml; atyre q&euml; vijn&euml; pas jush deri n&euml; dit&euml;n e kijametit). Ju po d&euml;shmoni se All-llahut ka edhe zota tjer&euml;, a?&rdquo; Thuaj: &ldquo;Un&euml; nuk d&euml;shmoj!&rdquo; Ai &euml;sht&euml; vet&euml;m nj&euml; Zot dhe un&euml; jam i past&euml;r nga ajo q&euml; j u i shoq&euml;roni!&rdquo;';
$t[20] = 'Atyre q&euml; Ne u kemi dh&euml;n&euml; librin, e njohin at&euml; (Muhammedin) sikurse i njohin bijt&euml; e tyre. Jan&euml; ata q&euml; asgj&euml;suan vetveten, andaj nuk besojn&euml;.';
$t[21] = 'Kush &euml;sht&euml; m&euml; mizor se ai q&euml; ndaj All-llahut shpif g&euml;njeshtra apo ka p&euml;rg&euml;njeshtruar faktet e Tij? S&rsquo;ka dyshim, shpif&euml;sit dhe g&euml;njeshtar&euml;tnuk kan&euml; shp&euml;tim.';
$t[22] = 'P&euml;rkujto dit&euml;n kur Ne i tubojm&euml; t&euml; gjith&euml; dhe u themi atyre q&euml; i shoq&euml;ruan: &ldquo;Ku jan&euml; ata t&euml; shoq&euml;ruarit tuaj, q&euml; i trillonit?&rdquo;';
$t[23] = 'At&euml;her&euml; (pas atij sprovimi) p&euml;rgjigje tjet&euml;r s&rsquo;u mbetetp&euml;rve&ccedil; t&euml; thon&euml;: &ldquo;P&euml;r All-llahun, o zoti yn&euml;, ne nuk ishim dhujtar&euml;!&rdquo;';
$t[24] = 'Shih, se si b&euml;jn&euml; g&euml;njeshtra kund&euml;r vetes dhe si u shkoi huq ajo q&euml; trillonin!';
$t[25] = 'Ka prej atyre q&euml; ty t&euml; d&euml;gjon (kur lexon Kur&rsquo;anin). Po Ne kemi krijuar mbules&euml; mbi zemrat e tyre q&euml; t&euml; mos e kuptojn&euml; at&euml; dhe n&euml; vesh&euml;t e tyre sajuam shurdhim, dhe edhe dikur t&rsquo;i shohim t&euml; gjitha faktet. ata nuk besojn&euml;, derisa kur te ti e t&euml; polemizojn&euml; ata q&euml; mohuan thon&euml;: &ldquo;Nuk &euml;sht&euml; tjet&euml;r ky (Kur&rsquo;ani) vet&euml;m se mit i hersh&euml;m.';
$t[26] = 'Ata ndalojn&euml; (t&euml; tjer&euml;t) nga ai dhe vet&euml; largohen prej atij (Kur&rsquo;anit), e me at&euml; nuk shkat&euml;rrojn&euml; tjer&euml;t vet&euml;m vetveten, dhe nuk e hetojn&euml;.';
$t[27] = 'E sikur t&rsquo;i shihje ata kur jan&euml; ndalur pran&euml; zjarrit e thon&euml;: &ldquo;Ah sikr t&euml; ktheheshim (n&euml; dynja), t&euml; mos g&euml;njejm&euml; faktet e Zotit to&euml; e t&euml; b&euml;hemi nga besimtar&euml;t!&rdquo;';
$t[28] = 'Jo, (s&rsquo;&euml;sht&euml; ashtu) po atyre u doli n&euml; shesh ajo q&euml; e mbanin fsheht&euml; m&euml; par&euml;, prandaj edhe sikur t&euml; ktheheshin ata do t&euml; p&euml;rs&euml;ritnin at&euml; q&euml; e kishin t&euml; ndaluar, e s&rsquo;ka dyshim, ata jan&euml; g&euml;njeshtar&euml;.';
$t[29] = 'Ata than&euml;: &ldquo;Nuk ka tjet&euml;r, vet&euml;m kjo jeta jon&euml; n&euml; k&euml;t&euml; bot&euml;, dhe ne nuk do t&euml; rigjallemi.';
$t[30] = 'Dhe, sikur t&rsquo;i kishte par&euml; ata kur t&euml; ndalohen para Zotit t&euml; tyre e u thuhet atyre: &ldquo;A nuk &euml;sht&euml; kjo (ringjalja) e v&euml;rtet&euml;?&rdquo; Ata thon&euml;: &ldquo;Po, p&euml;r zotin ton&euml;!&rdquo; thot&euml;: &ldquo;Shijoni pra d&euml;nimin p&euml;r at&euml; q&euml; mohonit!&rdquo;';
$t[31] = 'Ata q&euml; p&euml;rg&euml;njeshtruan se do t&euml; takohen me All-llahun (n&euml; dit&euml;n e gjykimit) kan&euml; humbur p&euml;rderisa t&rsquo;u vij&euml; momenti (kijameti) befas e t&euml; thon&euml;: &ldquo;t&euml; mjer&euml;t ne p&euml;r at&euml; q&euml; l&euml;shuam&rdquo; (nga pun&euml;t e mira n&euml; dynja), e duke i bartur gabimet e veta n&euml; shpin&euml;, dhe, e sh&euml;mtuar &euml;sht&euml; ajo q&euml; bartin.';
$t[32] = 'Jeta e k&euml;saj bote nuk &euml;sht&euml; gj&euml; tjet&euml;r vet&euml;m se nj&euml; p&euml;rjetim e mashtrim. S&rsquo;ka dyshim se bota tjet&euml;r &euml;sht&euml; m&euml; e dobishmja p&euml;r ata q&euml; ruhen. A nuk logjikoni?';
$t[33] = 'Ne dim&euml; se ty t&euml; brengos ajo q&euml; thon&euml;, e ata nuk t&euml; g&euml;njejn&euml;ty, por ata mizor&euml; mohojn&euml; argumentet e All-llahut.';
$t[34] = 'Jan&euml; p&euml;rg&euml;njeshtruar t&euml; d&euml;rguar para teje, duruan at&euml; g&euml;njesht&euml;r, dhe u torturuan p&euml;rderisa u erdhi ndihma Jon&euml;, prandaj s&rsquo;ka kush q&euml; ndryshon fjal&euml;t (premtimet) e All-llahut. E ti je i njohur me disa nga ngjarjet e t&euml; d&euml;rgarve (edhe ti do t&euml; ndihmohesh sikurse ata).';
$t[35] = 'Por, n&euml;se refuzimi i tyre t&euml; &euml;sht&euml; b&euml;r&euml; breng&euml; e madhe, ti k&euml;rkoje, n&euml;se ke mund&euml;si, ndonj&euml; vrim&euml; n&euml; tok&euml; ose n&euml; qiell e t&rsquo;u sjell&euml;sh atyre ndonj&euml; argument (po ti s&rsquo;mund ta b&euml;sh k&euml;t&euml;). T&euml; kishte dashur All-llahu i kishte tubuar n&euml; rrug&euml; t&euml; drejt&euml;; po ti mos u b&euml; nga t&euml; padijshmit (dhe mos u brengos).';
$t[36] = 'vet&euml;m ata q&euml; kan&euml; d&euml;gjim t&euml; sh&euml;ndosh i p&euml;rgjigjen (ftes&euml;s). T&euml; vdekurit (ata q&euml; d&euml;gjojn&euml; dhe nuk besojn&euml;) All-llahu i ringjall, e pastaj te Ai kthehen (u jepat&euml; q&euml; e meritojn&euml;).';
$t[37] = 'Dhe than&euml;: &ldquo;P&euml;rse t&euml; mos i zbret atij (Muhammedit) nj&euml; mrekulli nga Zoti i tij?&rdquo; Thuaj: &ldquo;S&rsquo;ka dyshim se All-llahu ka fuqi t&rsquo;ia zbres&euml; nj&euml; mrekulli, por shumica e tyre nuk e din&euml; (se &ccedil;ka do t&rsquo;i gjente m&euml; pas).';
$t[38] = 'Nuk asnj&euml; gjalles&euml; n&euml; tok&euml; dhe as shpend&euml; q&euml; fluturojn&euml; me dy krah&euml; e q&euml; nuk jan&euml; t&euml; nara n&euml; grupe (t&euml; ndryshme), sikurse edhe ju (Zoti i krijoi, i pajisi si juve). Asgj&euml; nuk kemi l&euml;n&euml; pas dore nga evidenca. M&euml; n&euml; fund te Zoti i tyre do t&euml; tubohen.';
$t[39] = 'E ata q&euml; i b&euml;n&euml; t&euml; rreme faktet Tona jan&euml; shurdmemec&euml; t&euml; mbetur n&euml; err&euml;sira. All-llahu e humb k&euml; t&euml; doj&euml; e v&euml; n&euml; rrug&euml; t&euml; drejt&euml; at&euml; q&euml; do.';
$t[40] = 'Thuaju: &ldquo;N&euml;se u vjen d&euml;nimi nga All-llahu ose u vjen kijameti, m&euml; tregoni, n&euml;se jeni t&euml; sinqert&euml;, a do t&euml; thirrni k&euml; pos All-llahut (p&euml;r ndihm&euml;?&rdquo;';
$t[41] = 'Jo, vet&euml;m Atij (All-llahut) do t&rsquo;i luteshi, e n&euml;se do Ai, ju heq at&euml; p&euml;r &ccedil;ka i luteni, e do t&rsquo;i harronit ata (zota) q&euml; ia shoq&euml;ronit.';
$t[42] = 'Ne kemi d&euml;rguar (pejgamber&euml;) edhe te popjt parateje (e derisa nuk d&euml;gjuan), i d&euml;nuam m&euml; skamje emjerim, ashtu q&euml; t&euml; p&euml;rulen.';
$t[43] = 'E pse t&euml; mos p&euml;ruleshin kur ju erdhi atyre d&euml;nimijon&euml;? Por zemrat e tyre ishin ngurosor, po edhe djalli ua hjeshoi at&euml; q&euml; b&euml;nin.';
$t[44] = 'Meq&euml; lan&euml; pas dore at&euml; me &ccedil;ka u k&euml;shilluan (t&rsquo;i i drejtohen Zotit), Ne ua hap&euml;m dyrt e &ccedil;do gj&euml;je (begatie) derisa kur u g&euml;zuan p&euml;r at&euml; q&euml; ju kishte dh&euml;n&euml;, i kap&euml;m befas, e ata mbet&euml;n t&euml; zhg&euml;njyer.';
$t[45] = 'Ndaj u zhduk mbeturina e popullit mizor, pra Fal&euml;nderim i qoft&euml; Zotitt&euml; botave.';
$t[46] = 'Thuaj: &ldquo;N&euml;se All-llahu ua merr t&euml; d&euml;gjuarit, t&euml; pamurit dhe ua mbyll zemrat, pos All-llahut cili zot tjet&euml;r do t&rsquo;ju sjell&euml; at&euml;?&rdquo;. Shih sesi ua shqarojm&euml; faktet e megjithk&euml;t&euml; ata nuk ua v&euml;n&euml; veshin!';
$t[47] = 'Thuaj: &ldquo;M&euml; tregoni n&euml;se d&euml;nimi nga All-llahut u vjen befas ose me parashenja, a shkat&euml;rrohet kush tjet&euml;r pos popullit mizor?&rdquo;';
$t[48] = 'Ne t&euml; d&euml;rguarit nuk i d&euml;rgojm&euml; ndryshe vet&euml;m si p&euml;rg&euml;zues dhe qortues. E kush besoi dhe u p&euml;rmir&euml;sua, ata nuk kan&euml; as frik&euml; as breng&euml;.';
$t[49] = 'Ata q&euml; i p&euml;rg&euml;njeshtruan faktet tona, ata i kap d&euml;nimi ngase nuk respektuan normat.';
$t[50] = 'Thuaj: &ldquo;Un&euml; nuk u them juve se i kam n&euml; kompetenc&euml; depot&euml; e All-llahut (e t&rsquo;ju sjell&euml; mrekulli), as nuk pretendoj se i di fsheht&euml;sit&euml; (e t&rsquo;ju tregoj&euml; se kur do t&rsquo;ju vij&euml; d&euml;nimi), as nuk u them se un&euml; jam engj&euml;ll. Un&euml; ndjek vet&euml;m at&euml; q&euml; m&euml; shpallet mua. Thuaj: &ldquo;A jan&euml; baras i verb&euml;ti dhe ai q&euml; sheh?&rdquo; A nuk mendoni?';
$t[51] = 'Ti t&euml;rhiqju v&euml;rejtjen me k&euml;t&euml; (Kur&rsquo;an) atyre q&euml; i frik&euml;sohen tubimit para zotit t&euml; tyre, n&euml; m&euml;nyr&euml; q&euml; t&euml; ruhen, sepse pos Tij nuk do t&euml; ket&euml; as mbrojt&euml;s as nd&euml;rmjet&euml;sues.';
$t[52] = 'Dhe mos i p&euml;rze ata q&euml; adhurojn&euml; Zotin e tyre pa pra m&euml;ngjes e mbr&euml;mje, duke qen&euml; t&euml; sinqert&euml; ndaj Tij. Ti nukp&euml;rgjigjesh asgj&euml; nga llogaria e tyre e as ata nuk kan&euml; kurrfar&euml; p&euml;rgjegj&euml;sie nga llogaria jote, e po i d&euml;bove ata do t&euml; b&euml;hesh nga t&euml; padrejtit.';
$t[53] = 'K&euml;shtu Ne i sprovojm&euml; disa me disa tjer&euml; (pasanikun me varfnjakun, at&euml; me autoritet me at&euml; pa t&euml;) ashtu q&euml; t&euml; thon&euml;: &ldquo;A k&euml;to jan&euml; mes nesh q&euml;All-llahu i dhuroi?&rdquo; A nuk &euml;sht&euml; All-llahu m&euml; i dijshmi p&euml;r ata q&euml; jan&euml; mir&euml;njoh&euml;s?!';
$t[54] = 'E kur t&euml; vijn&euml; ty ata q&euml; i besojn&euml; ajetet tona, thuaju: &ldquo;Selamu alejkum, Zoti juaj ia ngjeshi vetes m&euml;shir&euml;n. Kuh b&euml;n prej jush ndonj&euml; &euml; keqe pa dije, e mandej pas asaj (t&euml; keqe) pehdohet dhe p&euml;rmir&euml;sohet, s&rsquo;ka dyshim se All-llahu &euml;sht&euml; q&euml; fal&euml; shum&euml; dhe &euml;sht&euml; M&euml;shirues.';
$t[55] = 'Ja, k&euml;shtu Ne i sqarojm&euml; argumentet, n&euml; m&euml;nyr&euml; q&euml; t&euml; dal&euml; n&euml; shesh rruga e kriminel&euml;ve.';
$t[56] = 'Thuaj: &ldquo;Un&euml; jam i ndaluar t&euml; adhuroj ata q&euml; adhuroni ju, pos All-llahut! thuaj: &ldquo;Un&euml; nuk njeki d&euml;shirat tuaja, pse do t&euml; ishte at&euml;her&euml; do te isha i humbur e jo prej t&euml; udh&euml;zuarve (n&euml; rrug&euml; t&euml; drejt&euml;).';
$t[57] = 'Thuaj: &ldquo;Un&euml; i p&euml;rmbahem t&euml; v&euml;rtet&euml;s (q&euml; m&euml; erdhi) nga Zoti im. Ju e g&euml;njyet at&euml;, e p&euml;r at&euml; q&euml; nguteni ju (d&euml;nimin), nuk e b&euml;j un&euml;, vendimi i takon vet&euml;m All-llahut; Ai e rr&euml;fen t&euml; v&euml;rtet&euml;n dhe Ai &euml;sht&euml; m&euml; i miri i gjykat&euml;sve.';
$t[58] = 'Thuaj: &ldquo;Sikur t&euml; ishte ajo p&euml;r t&euml; cil&euml;n nguteni j te un&euml;, &ccedil;&euml;shtja mes meje dhe jush do t&euml; ishte e p&euml;rfunduar. All-llahu m&euml; s&euml; miri i di p&euml;r mizor&euml;t.';
$t[59] = '&ccedil;el&euml;sat e fsheht&euml;sive jan&euml; vet&euml;m te Ai, at&euml; (fsheht&euml;sin&euml;) nuk e di kush pos Tij. Ai e di &ccedil;ka ka n&euml; tok&euml; dhe n&euml; det, Ai e di p&euml;r &ccedil;do gjeth q&euml; bie dhe s&rsquo;ka kok&euml;rr n&euml; thell&euml;si t&euml; tok&euml;s, s&rsquo;ka t&euml; njom&euml; dhe s&rsquo;ka t&euml; that&euml; q&euml; nuk &euml;sht&euml; (sh&euml;nuar) n&euml; librin e qart&euml; (LehviMahfud).';
$t[60] = 'Ai &euml;sht&euml; q&euml; ju v&euml; n&euml; gjum&euml; nat&euml;n dhe e di &ccedil;ka vepruat dit&euml;n, pastaj ju ngjall-zgjon n&euml; t&euml; (dit&euml;n) p&euml;r deri n&euml; afatin e caktuar (vdekje). Pastaj do t&euml; ktheheni e do t&rsquo;ju njohtoj&euml; me at&euml; q&euml; keni pas vepruar.';
$t[61] = 'Vet&euml;m Ai &euml;sht&euml; mbizot&euml;rues ndaj rob&euml;rve t&euml; vet. Ndaj juve d&euml;rgon roj&euml; (cakton engj&euml;j) derisa kur t&rsquo;i vij&euml; ndonj&euml;rit prej jush vdekja, atij ia marrin shpirtin t&euml; d&euml;rguarit tan&euml; (t&euml; tjer&euml;) dhe ata nuk b&euml;jn&euml; kurrfar&euml; l&euml;shimi.';
$t[62] = 'Pastaj i kthehen All-llahut, Snduesit t&euml; v&euml;rtet&euml; t&euml; tyre. Vet&euml;m i tij &euml;sht&euml; sundimi (gjykimi) dhe Ai &euml;sht&euml; m&euml; i shpejti i llogarit&euml;sve.';
$t[63] = 'Thuaj: &ldquo;Kush ju sp&euml;ton prej err&euml;sirave (trishtuese) t&euml; tok&euml;s e t&euml; detit e (nga trishtimi) ju e lutni At&euml; haptas e fshehtas (duke th&euml;n&euml;), n&euml;se Ai na shp&euml;ton prej k&euml;saj, ne do t&euml; jemi (besimtar&euml;) mir&euml;njoh&euml;s?&rdquo;';
$t[64] = 'Thuaj: &ldquo;All-llahu ju shp&euml;tin nga ajo dhe nga &ccedil;do brengosje. Megjith&euml;k&euml;t&euml; ju Atij i p&euml;rshkruani shok.';
$t[65] = 'Thuaj: &ldquo;Ai ka fuqi (t&rsquo;ju shp&euml;toj&euml;, por edhe) t&rsquo;ju sjell&euml; d&euml;nim prej s&euml; larti ose prej s&euml; poshti n&euml;n k&euml;mb&euml;t tuaja apo t&rsquo;ju ndaj n&euml; grupe e ta luftoni nj&euml;ri-tjetrin. Shih se si i sqarojm&euml; faktet n&euml; m&euml;nyr&euml; q&euml; t&euml; kptojn&euml;&rdquo;.';
$t[66] = 'E populli yt e konsideroi at&euml; (Kur&rsquo;anin) t&euml; rrem&euml;, porse ai (Kur&rsquo;ani) &euml;sht&euml; i v&euml;rtet&euml;. Thuaj: &ldquo;Un&euml; nuk jam roj&euml; e juaj&rdquo;.';
$t[67] = '&ccedil;do lajm e ka afatin e realizimit, e m&euml; von&euml;, ju do t&euml; kuptoni.';
$t[68] = 'Kur sheh ata se jan&euml; thelluar (me tallje) n&euml; &ccedil;&euml;shtjet Tona, largohu prej tyre derisa t&euml; kalojn&euml; n&euml; bised&euml; tjet&euml;r. N&euml;se djalli t&euml; b&euml;n t&euml; harrosh (e rri me ta), pasi t&euml; bie nd&euml;r mend, mos rri me popullin mizor.';
$t[69] = 'P&euml;r ata q&euml; ruhen (p&euml;r besimtar&euml;t) s&rsquo;ka kurrfar&euml; p&euml;rgjegj&euml;sie (q&euml; tallen), por duhet t&rsquo;ju p&euml;rkujtojm&euml; n&euml; m&euml;nyr&euml; q&euml; ruhen edhe ata (q&euml; tallen).';
$t[70] = 'Hiqu atyre q&euml; e marrin fen&euml; (n&euml; vend q&euml; ta resprktojn&euml;) p&euml;r loj&euml; e d&euml;frim dhe i ka mashtruar jeta e k&euml;saj bote. Ti p&euml;rkujto me t&euml; (Kur&rsquo;anin) q&euml; t&euml; mos bjer&euml; njeriu viktim&euml; e asaj q&euml; ka veprruar, e q&euml; s&rsquo;ka mbrojt&euml;s as nd&euml;rmjet&euml;sues p&euml;r te pos All-llahut. je ai (njeri) edhe n&euml;se jep, &ccedil;do lloj shpagimi nuk i pranohet. T&euml; till&euml;t jan&euml; ata q&euml; ran&euml; viktim&euml; e asaj q&euml; punuan. Ata, p&euml;r skak se mohuan, p&euml;r pije kan&euml; uj&euml; t&euml; vluar e d&euml;nim t&euml; idh&euml;t.';
$t[71] = 'Thuaj: &ldquo;A pos All-llahut t&euml; adhurojm&euml; &ccedil;ka nuk na sjell as dobi as d&euml;m dhe t&euml; kthehemi mbrapa (n&euml; kufur) pasi q&euml; All-llahu na vuri n&euml; rrug&euml; t&euml; drejt&euml;? Dhe at&euml;her&euml; t&euml; b&euml;hemi sikur ai, t&euml; cilin djall&euml;zit e kan&euml; rr&euml;mbyer (e kan&euml; hedhur) n&euml; tok&euml; (n&euml; nj&euml; humner&euml;) e l&euml;n&euml; t&euml; hutuar q&euml;, edhe pse ai ka shok&euml; q&euml; e th&euml;rrasin n&euml; rrug&euml; t&euml; drejt&euml; (i thon&euml;): &ldquo;Eja te ne&rdquo; (ai nuk p&euml;rgjigjet). Thuaj: &ldquo;I vetmi udh&euml;zim &euml;sht&euml; ai udh&euml;zimi i All-llahut, dhe se jemi urdh&euml;ruar q&euml; t&rsquo;i dor&euml;zohemi zotit t&euml; bot&euml;ve.';
$t[72] = '(Na &euml;sht&euml; th&euml;n&euml;) Dhe falne nmazin dhe kini frik&euml; prej Tij, se Ai &euml;sht&euml; te i cili do t&euml; tubohemi.';
$t[73] = 'E Ai &euml;sht&euml; q&euml; krijoi qiejt dhe tok&euml;n me q&euml;llim t&euml; caktuar, e (ruajuni d&euml;nimit) dit&euml;n kur thot&euml;: &ldquo;B&euml;hu&rdquo;! Ajo b&euml;het. Fjala e Tij &euml;sht&euml; e nj&euml;mendt&euml; dhe Atij i takon sundimi dit&euml;n kur i fryhet &ldquo;Surit&rdquo; (kjo &euml;sht&euml; hera e dyt&euml;). Ai e di t&euml; fsheht&euml;n dhe konkret&euml;n, &euml;sht&euml; m&euml; i urti q&euml; di p&euml;r &ccedil;do gj&euml; n&euml; holl&euml;si.';
$t[74] = 'P&euml;rkujtoju (o i d&euml;rguar) kur Ibrahimi i tha v&euml;llaut t&euml; vet Azerit: &ldquo;A statuja (idhuj) adhuroni p&euml;r zota? Un&euml; po t&euml; shoh ty dhe popullin t&euml;nd n&euml; nj&euml; humbje t&euml; sigurt.';
$t[75] = 'E k&euml;shtu Ibrahimit ia mund&euml;suam t&rsquo;i shoh&euml; madh&euml;sit&euml; e qiejve e t&euml; tok&euml;s p&euml;r t&rsquo;u b&euml;r&euml; edhe m&euml; i bindur.';
$t[76] = 'E kur at&euml; e mbuloi nata, ai e pa nj&euml; yll e tha: &ldquo;Ky &euml;sht&euml; Zoti im!&rdquo; E kur u zhduk ai (per&euml;ndoi) tha:&rdquo; Un&euml; nuk i dua ata q&euml; humb&euml;n&rdquo;.';
$t[77] = 'Kur e pa h&euml;n&euml;n t&euml; posa tha: &ldquo;Ky &euml;sht&euml; Zoti im! e kur per&euml;ndoi ajo, tha: N&euml;se Zoti im nuk m&euml; udh&euml;zon, un&euml; do t&euml; jem prej njer&euml;zve t&euml; humbur!&rdquo;';
$t[78] = 'Kur e pa diellin t&euml; lindur, tha: &ldquo;Ky &euml;sht&euml; Zoti im, ky &euml;sht&euml; i madh!&rdquo; e kur ai per&euml;ndoi, tha: O populli im, un&euml; jam i past&euml;rnga ajo q&euml; ju i shoq&euml;roni!&rdquo;';
$t[79] = 'Un&euml; me veten time i drejtohem Atij q&euml; krijoi qiejt e tok&euml;n, larg besimeve t&euml; tjera; un&euml; nuk jam prej atyre q&euml; i p&euml;rshkruajn&euml; shok!';
$t[80] = 'Po at&euml; e (me Ibrahimin) polemizoi populli i tij e ai tha: &ldquo;A polemizoni me mua, rreth All-llahut e Ai m&euml; udh&euml;zoi?&rdquo; Un&euml; nuk u frikohem atyre q&euml; ju ia b&euml;ni shok, vet&euml;m n&euml;se Zoti im do ndonj&euml; send (t&euml; m&euml; godas, ai m&euml; godet). Me dijen e Tij Zoti im ka p&euml;rfshir&euml; &ccedil;do send, a nuk e merrni me mend?&rdquo;';
$t[81] = 'E si t&rsquo;ju frikohem atyre q&euml; ju ia shoq&euml;ruat, e ju frikoheni p&euml;r at&euml; q&euml; i shoq&euml;ruat All-llahut pa pasur kurrfar&euml; argumenti. E cili grup, pra, &euml;sht&euml; m&euml; e drejt&euml; t&euml; jet&euml; i sigurt, n&euml;se jeni q&euml; kuptoni?';
$t[82] = 'Ata q&euml; besuan dhe besimin e tyre nuk e ngat&euml;rruan me besim t&euml; kot&euml;, atyre u takon t&euml; jen&euml; t&euml; sigurt dhe atajan&euml; n&euml; rrug&euml; t&euml; drejt&euml;.';
$t[83] = 'K&euml;to jan&euml; argumentet tona q&euml; ia dham&euml; Ibrahimit kund&euml;r popullit t&euml; tij. Ne ngrisim n&euml; shkall&euml; t&euml; lart&euml; at&euml; q&euml; duam. Zoti yt &ccedil;do send e v&euml; n&euml; vendin e vet, asgj&euml; nuk mund t&rsquo;i fshehet.';
$t[84] = 'Ne atij (Ibrahimit) i fal&euml;m Is-hakun dhe Jakubin dhe secilin prej tyre e udh&euml;zuam. M&euml; par&euml; edhe Nuhun e pat&euml;m udh&euml;zuar. E nga pasardh&euml;sit e tij (t&euml; Ibrahimit) udh&euml;zuam Davudin, Sulejmanin, Ejjubin, Jusufin, Musain dhe Harunin. K&euml;shtu i shp&euml;rblejm&euml; bamir&euml;sat.';
$t[85] = '(shp&euml;rblyem) Edhe Zekerijan&euml;, Jahjan&euml;, Isain, Iliajasin t&euml; gjith&euml; prej t&euml; mir&euml;ve t&euml; p&euml;rsosur.';
$t[86] = '(shp&euml;rbylem) Edhe Ismailin, Eljesan&euml;, Junusin dhe Lutin. T&euml; gjith&euml; k&euml;ta i ve&ccedil;uam mbi njer&euml;zit tjer&euml;.';
$t[87] = 'Edhe disa nga prind&euml;rit, nga pasardh&euml;sit dhe nga v&euml;llez&euml;rit e tyre i zgjodh&euml;m (p&euml;r pejgamber&euml;) dhe i udh&euml;zuam n&euml; rrug&euml; t&euml; v&euml;rtet&euml;.';
$t[88] = 'Ky &euml;sht&euml; udh&euml;zim i All-llahut, udh&euml;zon me t&euml; k&euml; t&euml; doj&euml; nga rob&euml;t e Tij. E sikur t&rsquo;i p&euml;rshkruajn&euml; shok Zotit (edhe ndonj&euml; nga k&euml;ta) kishte p&euml;r t&rsquo;iu shkuar huq ajo q&euml; kan&euml; vepuar.';
$t[89] = 'Ata shin q&euml; u pat&euml;m dh&euml;n&euml; librin, urt&euml;sin&euml; e pejgamberll&euml;kun; e n&euml;se k&euml;ta (idhujtar&euml;t mekas) e refuzojn&euml; k&euml;t&euml; (pejgamberll&euml;kun t&euml;nd), Ne e kemi siguruar k&euml;t&euml; me nj&euml; popull q&euml; nuk e refuzon.';
$t[90] = 'Ata (t&euml; d&euml;rguarit e p&euml;rmedur) ishin q&euml; All-llahu i vuri n&euml; rrug&euml; t&euml; drejt&euml;, andaj ti merri shembull n&euml; udh&euml;zim. Thuaj: &ldquo;Un&euml; nuk k&euml;rkoj p&euml;r k&euml;t&euml; (komunikimin e Kur&rsquo;anit) shp&euml;rblim prej jush. Ky nuk &euml;sht&euml; tjet&euml;r, p&euml;rve&ccedil; nj&euml; k&euml;shill&euml; p&euml;r mbar&euml; njer&euml;zit.';
$t[91] = 'Ata (mohuesit) nuk e njoh&euml;n All-llahun sa duhet njohur At&euml; kur than&euml;; &ldquo;All-llahu nuk i shpalli gj&euml; asnj&euml; njeriu!&rdquo; Thuaj: &ldquo;Kush e zbriti librin, me t&euml; cilin erdhi Musai e q&euml; ishte drit&euml; e udh&euml;rr&euml;fyes p&euml;r njer&euml;z, e t&euml; cilin ju e b&euml;ni t&euml; shp&euml;rndar&euml; n&euml; letra, q&euml; disa i prezentoni, kurse shumic&euml;n e fshehni. Dhe u m&euml;suat &ccedil;ka nuk e dinit ju as prind&euml;rit tuaj? Thuaj: (e zbriti) All-llahu&rdquo;. Mandej l&euml;ri ata q&euml; luajn&euml; n&euml; at&euml; kot&euml;sun&euml; e tyre.';
$t[92] = 'Edhe ky (Kur&rsquo;an) &euml;sht&euml; lib&euml;r q&euml; e zbrit&euml;m; &euml;sht&euml; i bekuar, v&euml;rtetues i t&euml; m&euml;parshmes, e q&euml; t&rsquo;i t&euml;rheqesh v&euml;rejtjen n&euml;n&euml;s s&euml; fshatrave (t&euml; banor&euml;ve mekas) dhe atyre p&euml;rrreth saj (mbar&euml; bot&euml;s). Ata q&euml; e besojn&euml; Ahiretin, besojn&euml; n&euml; t&euml; (Kur&rsquo;anin), ata edhe e falin namazin rregullisht.';
$t[93] = 'E kush &euml;sht&euml; m&euml; gabimtar i madh se sa ai q&euml; trillon rren&euml; ndaj Zotit, ose thot&euml;: &ldquo;Mua po m&euml; shpallet e nuk i &euml;sht&euml; shpallur asgj&euml;, ose se sa ai q&euml; thot&euml;: &ldquo;Do t&euml; thur di&ccedil;ka t&euml; ngjajshme me at&euml; q&euml; e ka zbritur All-llahu&rdquo;. E, sikur t&rsquo;i shihje mizor&euml;t kur jan&euml; n&euml; agoni t&euml; vdekjes, e engj&euml;jt kan&euml; shtrir&euml; duart e veta (me nd&euml;shkim) e (u thon&euml;): &ldquo;Shp&euml;tonie pra vetveten (n&euml;se mundeni)&rdquo;. &ldquo;Tash p&euml;rjetoni d&euml;nimin e turpsh&euml;m p&euml;r shkak se e thoshit t&euml; pav&euml;rtet&euml;n p&euml;r All-llahun, dhe ndaj argumrnteve t&euml; Tij ishit kryene&ccedil;.';
$t[94] = 'N&euml; t&euml; v&euml;rtet&euml; ju na erdh&euml;t nj&euml; nga nj&euml; (t&euml; vetmuar), ashtu si ju krijuam s&euml; pari; keni l&euml;n&euml; prpa shpin&euml;s at&euml; q&euml; u pat&euml;m dh&euml;n&euml;, e nuk po shohim se i keni me vete nd&euml;rmjet&euml;suesit tuaj, t&euml; cil&euml;t i mendonit se p&euml;r ju jan&euml; ndihm&euml;tar&euml; (i shoq&euml;ronit All-llahut). S&rsquo;ka dyshim, &euml;sht&euml; k&euml;putur lidhja mes jush, dhe mbaroi ajo &ccedil;ka mendonit (nd&euml;rmjjet&euml;sues apo zot&euml;ra tjer&euml;).';
$t[95] = 'S&rsquo;ka dyshim, All-llahu &euml;sht&euml; zb&euml;rthyes i far&euml;s (i kokrr&euml;s s&euml; saj) dhe i b&euml;rtham&euml;s (s&euml; pem&euml;s). Ai nxjerr t&euml; gjallin nga i vdekuri dhe Ai&euml;sht&euml; nxjerr&euml;s i t&euml; vdekurit nga i gjalli. Ky &euml;sht&euml; All-llahu, e si at&euml;her&euml; shmangeni (nga besimi)?';
$t[96] = 'Ai &euml;sht&euml; krijues i drit&euml;s s&euml; m&euml;ngjesit. Nat&euml;n e b&euml;ri koh&euml; pushimi, e diellin dhe h&euml;n&euml;n p&euml;r llogaritje t&euml; koh&euml;s. Ky (rregull) &euml;sht&euml; caktim i plotfuqishmit, i gjith&euml;dijshmit.';
$t[97] = 'Ai &euml;sht&euml;, q&euml; krijoi yjet p&euml;r ju q&euml; me ta t&euml; orientoheni n&euml; err&euml;sira kur jeni n&euml; tok&euml; ose n&euml; det. V&euml;rtet Ne i shpjeguam argumentet tona p&euml;r nj&euml; popull q&euml; di t&euml; mendoj&euml;.';
$t[98] = 'Ai &euml;sht&euml; q&euml; ju krijoi (filloi) prej nj&euml; njeriu; aty (mbi tok&euml;) jetoni (q&euml;ndroni) dhe n&euml;n te do t&euml; pushoni. Ne i sqaruam argumentet nj&euml; populli q&euml; di t&euml; kuptoj&euml;.';
$t[99] = 'Ai &euml;sht&euml; q&euml; l&euml;shoi nga lart&euml; shiun e me t&euml; nxori bim&euml;n e &ccedil;do sendi dhe prej bim&euml;s gjelb&euml;rim dhe prej tij (prej gjelb&euml;rimit) kokrra t&euml; dendura n&euml; kallinj. E nga hurmet, nga sythat tyre kalavesh&euml; t&euml; af&euml;rt (p&euml;r t&rsquo;i vjel&euml;). Edhe kopshtie me hardhi, ullinj e sheg&euml; t&euml; ngjajshme (n&euml; dukje) e t&euml; llojllojshme (n&euml; shije). Shikoni pra frutat e tyre kur i formojn&euml; dhe kur piqen (t&euml; gjitha k&euml;to nga shiu). Edhe n&euml; k&euml;to ka fakte p&euml;r njer&euml;zit q&euml; besojn&euml;.';
$t[100] = 'E xhin&euml;t ia b&euml;jn&euml; shok&euml; All-llahut, e n&euml; t&euml; v&euml;rtet&euml; Ai i krijoi ata (xhin&euml;t) dhe duke mos patur kurrfar&euml; dije, ata shpif&euml;n se Ai (Zoti) ka djem e vajza. Larg (shpifjeve) &euml;sht&euml; madh&euml;ria e Tij e lart&euml;.';
$t[101] = 'Ai (All-llahu) &euml;sht&euml; q&euml; krijo (pa kurrfar&euml; shembulli) qiejt dhe tok&euml;n (e duke qen&euml; i till&euml;), e si do t&euml; ket&euml; Ai f&euml;mij&euml; kur nuk pati bashk&euml;shorte? &ccedil;do send e krijoi Ai, dhe &euml;sht&euml; m&euml; i dijshmi p&euml;r t&euml; gjitha sendet e krijuara.';
$t[102] = 'Ky &euml;sht&euml; All-llahu, Zoti juaj, nuk ka t&euml; adhuruar p&euml;rve&ccedil; Tij, Krijues i &ccedil;do sendi, pra adhuronie At&euml;; Ai &euml;sht&euml; mbik&euml;qyr&euml;s ndaj &ccedil;do sendi.';
$t[103] = 'T&euml;par&euml;t (e njer&euml;zve) nuk mund ta p&euml;rfshin&euml; At&euml;, e Ai i &euml;rfshin&euml; t&euml; ar&euml;t. Ai &euml;sht&euml; shum&euml; i kujdessh&euml;m, holl&euml;sisht i njohur.';
$t[104] = 'Juve ju erdh&euml;n argumente t&euml; qarta nga Zoti i juaj e kush i sheh (kupton) ai e ka p&euml;r vete, e kush verb&euml;rohet, ai e ka p&euml;r t&euml; zez&euml;n e vet. E n&euml; (pejgamberi) nuk jam&euml;roj&euml; e juaj.';
$t[105] = 'e k&euml;shtu Ne i sqarojm&euml; d&euml;shmit&euml;, ashtu q&euml; ata thon&euml;: &ldquo;Ke m&euml;suar ti&rdquo; (nga librat, po fjal&euml;s s&euml; tyre nuk i vihet veshi0 dhe q&euml; p&euml;r t&rsquo;ia b&euml;r&euml; edhe m&euml; t&euml; qarta nj&euml; populli q&euml; di t&euml; dalloj&euml; (t&euml; v&euml;rtet&euml;n nga e kota).';
$t[106] = 'Ti (Muhammed) praktoko at&euml; q&euml; t&rsquo;u shpall nga Zoti yt. S&rsquo;ka Zot, p&euml;rve&ccedil; Tij. Largohu prej idhujtar&euml;ve.';
$t[107] = 'E sikur t&euml; donte All-llahu, ata nnuk do t&euml; ishin idhujtar&euml;. Ne nuk t&euml; b&euml;m&euml; p&euml;rcjell&euml;s t&euml; tyre e as q&euml; je mbik&euml;qyr&euml;s i tyre.';
$t[108] = 'Ju mos ua shani ata (zota) q&euml; u luten (idhujtar&euml;t), pos All-llahut, e (si hakmarrje) t&euml; fyejn&euml; All-llahun nga armiq&euml;sia, duke mos ditur (p&euml;r madh&euml;rin&euml; e Tij). K&euml;shtu Ne i kemi zbukuruar &ccedil;do populli veprimin e vet, mandej e ardhmja e tyre &euml;sht&euml; te Zoti i tyre, e Ai i shp&euml;rble p&euml;r at&euml; q&euml; vepruan.';
$t[109] = 'Ata u betuan me nj&euml; betim t&euml; fort&euml; n&euml; All-llahun, se n&euml;se uvjen atyre ndonj&euml; mrekulli, do ta besojn&euml;. Thuaj: &ldquo;&ccedil;&euml;shtja e atyre mrekullive &euml;sht&euml; te All-llahu&rdquo;. E ku e dini ju, ndoshta kur t&euml; vijn&euml; ato nuk u besojn&euml;.';
$t[110] = 'Ne i rrotullojm&euml; zemrat e t&euml; par&euml;t e tyre (prej besimit) ashtu sikund&euml;r nuk e besuan at&euml; (Kur&rsquo;anin) p&euml;r her&euml; t&euml; par&euml;, dhe i l&euml;m&euml; t&euml; bredhin t&euml; hutuar n&euml; at&euml; mashtrimin e tyre.';
$t[111] = 'E edhe sikur t&rsquo;ju zbritnim Ne atyre engj&euml;jt, t&rsquo;ju flitnin t&euml; vdekurit, t&rsquo;ju tubonin atyre &ccedil;do send (gjalles&euml;) konkretisht, ata nuk kishin p&euml;r t&euml; besuar, vet&euml;m po t&euml; donte All-llahu, por shumica e tyre nuk din&euml; (se besimi &euml;sht&euml; dhurat&euml; nga Zoti).';
$t[112] = 'dhe k&euml;shtu (sikurse edhe ty) &ccedil;do pejgamberi i b&euml;m&euml; armiq disa nga njer&euml;zit dhe nga xhin&euml;t e djall&euml;zuar, q&euml; me fjal&euml; t&euml; shk&euml;lqyeshme n&euml; m&euml;nyr&euml; t&euml; fsheht&euml; nxit nj&euml;ri-tjetrin n&euml; mashtrime. E sikur t&euml; donte Zoti yt, ata nuk do t&euml; b&euml;nin at&euml; (armiq&euml;si), po ti l&euml;ri ata me ato trillime.';
$t[113] = 'Dhe q&euml; t&euml; anojn&euml; (te ato fjal&euml; mashtruese) zemrat e atyre q&euml; nuk besojn&euml; bo&euml;n e ardhshme dhe q&euml; t&euml; k&euml;naqen e ngarkohen me at&euml; q&euml; jan&euml; duke u ngarkuar (ty nuk t&euml; d&euml;mtojn&euml; asgj&euml;).';
$t[114] = '(Thuaj) A pos All-llahut t&euml; k&euml;rkoj un&euml; gjykat&euml;s (mes meje dhe juve)? kur Ai &euml;sht&euml; q&euml; zbriti librin n&euml; m&euml;nyr&euml; t&euml; shkoqitur? Atyre q&euml; u dham&euml; librin e din&euml; se ai (Kur&rsquo;ani) &euml;sht&euml; i zbritur prej Zotit t&euml;nd sakt&euml;sisht, pra mos u b&euml; prej atyre q&euml; dyshojn&euml;.';
$t[115] = 'Fjal&euml;t e Zotit t&euml;nd jan&euml; plot t&euml; v&euml;rteta (&ccedil;ka lajm&euml;rojn&euml;) dhe plot t&euml; drejta (&ccedil;ka gjykojn&euml;). S&rsquo;ka kush q&euml; t&euml; ndryshoj fjal&euml;t (vendimet) e Tij. Ai &euml;sht&euml; q&euml; d&euml;gjon e di.';
$t[116] = 'N&euml; qoft&euml; se u bindesh shumic&euml;s (mohuese q&euml; jan&euml;) n&euml; tok&euml;, ata do t&euml; largojn&euml; ty nga rruga e All-llahut. Ata nuk ndjekin tjet&euml;r vet&euml;m supozime dhe nuk jan&euml; tjet&euml;r vet&euml;m se rrenacak&euml;.';
$t[117] = 'S&rsquo;ka dyshim, Zoti yt e di m&euml; s&euml; miri p&euml;r at&euml; q&euml; &euml;sht&euml; larguar rruga e Tijdhe Ai &euml;sht&euml; m&euml; i dijshmi p&euml;r t&euml; udh&euml;zuarit.';
$t[118] = 'Ju (besimtar&euml;) hani nga ajo q&euml; (&euml;sht&euml; therrur dhe) dhe &euml;sht&euml; p&euml;rmendur emri i All-llahut, po qe se jeni t&euml; bindur n&euml; faktet e Tij.';
$t[119] = '&ccedil;&rsquo;keni ju q&euml; t&euml; mos hani nga ajo p&euml;r t&euml; cil&euml;n &euml;sht&euml; p&euml;rmendur emri i All-llahut, e Ai ju sqaroi juve se &ccedil;ka &euml;sht&euml; ndaluar p&euml;r ju, p&euml;rve&ccedil;kur jeni detyruar (at&euml;her&euml; edhe harami &euml;sht&euml; hallall). Nj&euml; shumic&euml; (e mohusve), duke mos pasur kurrfar&euml; dije, por vet&euml;m nga pasionet e tyre, duan t&rsquo;i largojn&euml; nga e v&euml;rteta (njer&euml;zit). Po Zoti yt di m&euml; s&euml; mirip&euml;r ata q&euml; i shkelin dispozitat.';
$t[120] = 'Dhe mos b&euml;ni m&euml;kate as haptas as fshehtas. Ata q&euml; b&euml;jn&euml; m&euml;katin do t&euml; nd&euml;shkohen p&euml;r m&euml;katin e b&euml;r&euml;.';
$t[121] = 'Dhe mos hani nga ajo q&euml; (para therjes s&euml; saj) nuk &euml;sht&euml; p&euml;rmendur emri i All-llahut, v&euml;rtet ajo (ngr&euml;nia) &euml;sht&euml; m&euml;kat. Djall&euml;zit i nxisin miqt&euml; e vet q&euml; t&rsquo;ju polemizojn&euml; juve, e n&euml;se i d&euml;gjoni ata, at&euml;her&euml; jeni si ata (idhujtar&euml;).';
$t[122] = 'Vall&euml;, a &euml;sht&euml; ai q&euml; qe i vdekur kurse Ne e ngjall&euml;m dhe idham&euml; drit&euml;, me t&euml; cil&euml;n ec&euml;n mes njer&euml;zve, si ai q&euml; ka mbetur n&euml; err&euml;sir&euml; (i humbur) dhe nuk mund t&euml; shp&euml;toj&euml; nga ajo? Ja, k&euml;shtu (si k&euml;tij n&euml; err&euml;sir&euml;) iu dket mir&euml; mohusve ajo q&euml; veprojn&euml;.';
$t[123] = 'Dhe ashtu (sikurse n&euml; Mek&euml;) n&euml; &ccedil;do qytet kemi b&euml;r&euml; kriminel&euml;t e tij pari, n&euml; m&euml;nyr&euml; q&euml; t&euml; b&euml;jn&euml; dredhi n&euml; t&euml;, por nuk mashtrojn&euml; tjet&euml;r k&euml; pos veten e tyre dhe prap&euml;seprap&euml; nuk kuptojn&euml;.';
$t[124] = 'E kur uvjen atyre ndonj&euml; argument i prer&euml; (p&euml;r Muhammedin), ata thon&euml;: &ldquo;Kurrsesi nuk e besojm&euml; at&euml; (d&euml;rges&euml;n e Muhammedit) derisa t&euml; mos na jepet edhe neve ngjajsh&euml;m me at&euml; q&euml; iu pat dh&euml;n&euml; t&euml; d&euml;rguarve t&euml; All-llahut&rdquo;! M&euml; s&euml; miri All-llahu e di ku ta v&euml;r&euml; d&euml;rges&euml;n (risalen) e vet. Ata q&euml; b&euml;n krim do t&rsquo;igodas&euml; poshtrimi dhe d&euml;nimi i fort&euml; te All-llahu p&euml;r shkak se vazhdimisht b&euml;nin hile.';
$t[125] = 'At&euml; q&euml; All-llahu d&euml;shiron ta udh&euml;zoj&euml;, ia zgjeron zemr&euml;n p&euml;r (t&euml; pranuar) islamin. At&euml; q&euml; d&euml;shiron ta l&euml;r&euml; t&euml; humbur, zemr&euml;n e tij ia b&euml;n shum&euml; t&euml; ngusht si t&euml;ngjitej n&euml; qiell. K&euml;shtu All-llahu l&euml;shon d&euml;nimin mbi ata q&euml; nuk besojn&euml;.';
$t[126] = 'Kjo &euml;sht&euml; rruga e Zotit t&euml;nd, &euml;sht&euml; e drejt&euml;, Ne shpjeguam argumentet p&euml;r njer&euml;z q&euml; p&euml;rkujtojn&euml;.';
$t[127] = 'Ata e kan&euml; vendin e shp&euml;timit (Xhennetin) te Zoti i tyre; Ai &euml;sht&euml; mbrojt&euml;s i tyre, p&euml;r at&euml; q&euml; ata vepruan.';
$t[128] = 'P&euml;rkujto dit&euml;n kur Ai i tbon ata t&euml; gjith&euml; (e u thot&euml;): &ldquo;O grumbull i xhinve, ju mashtruat shum&euml; njer&euml;z!&rdquo; E nga njer&euml;zit q&euml; ishin miq t&euml; tyre (t&euml;xhinve) thon&euml;: &ldquo;Zoti yn&euml; ne p&euml;rfituam nj&euml;ri prej tjetrit, dhe e arrit&euml;m afatin t&euml; cilin e caktove!&rdquo; Tha: &ldquo;(Zoti) Zjarri &euml;sht&euml; vendi juaj, p&euml;rgjithmon&euml; jeni n&euml; t&euml;, pos (koh&euml;s) &ccedil;ka do All-llahu&rdquo; V&euml;rtet Zotit yt &euml;sht&euml; m&euml; i urti, m&euml; i dijshmi.';
$t[129] = 'PO ashtu (sikur u dham&euml; xhinve dhe njer&euml;zve p&euml;rjetim), Ne i b&euml;jm&euml; sundues disa mizor&euml; mbi mizor&euml;t e tjer&euml; p&euml;r shkak t&euml; asaj q&euml; fituan (vepruan).';
$t[130] = 'O grumbull i xhinve dhe i njer&euml;zve! A nuk ju erdh&euml;n nga mesi juaj t&euml; d&euml;rguar t&rsquo;ju rr&euml;fejn&euml; argumentet e Mia dhe t&rsquo;ju t&euml;rheqin v&euml;rejtjen p&euml;r takimin tuaj n&euml; k&euml;t&euml; dit&euml;? Ata thon&euml;: &ldquo;D&euml;shmojm&euml;kund&euml;r vetvetes&rdquo;. I pat mashtruar ata jeta e k&euml;saj bote dhe ashtu (t&euml; detyruar) d&euml;shmuan kund&euml;r vetvetes se me t&euml; v&euml;rtet&euml; e refuzonin (t&euml; v&euml;rtet&euml;n).';
$t[131] = 'K&euml;t&euml; (d&euml;rgimin e t&euml; d&euml;rguarve) ngase Zoti yt, nuk &euml;sht&euml; q&euml; p&euml;r shkak t&euml; m&euml;katit t&euml; shkat&euml;rroj&euml; nj&euml; vend, e banor&euml;t etij t&euml; jen&euml; t&euml; painformuar.';
$t[132] = 'Po p&euml;r secili (veprues) ka shkall&euml; (q&euml; do t&rsquo;i arrij&euml;) sipas asaj q&euml; vepruan. All-llahu nuk &euml;sht&euml; i pakujdessh&euml;m ndaj asaj q&euml; veprojn&euml;.';
$t[133] = 'Zoti yt nuk ka nevoj&euml; p&euml;r asgj&euml;, Ai &euml;sht&euml; m&euml;shirues. N&euml;se do Ai, juve ju zhduk dhe sjell k&euml; t&euml; doj&euml; pas jush, ashtu sikurse ju solli juve nga pasardh&euml;sit e atyre q&euml; ishin para jush.';
$t[134] = 'Ajo q&euml; iu k&euml;rc&euml;nohet (kijameti, ringjallja, llogaria, p&euml;rgjegj&euml;sia) pa tjet&euml;r do t&euml; vij&euml;, e ju nuk mund ta pengoni. (Nuk mund ta menjanoni caktimin e Zotit).';
$t[135] = 'Thuaj: &ldquo;O populli im (kurejshit)! veproni n&euml; at&euml; q&euml; jeni, e un&euml; do ta vazhdoj t&euml; veproj n&euml; at&euml; q&euml; jam, e m&euml; von&euml; do ta dini se kujt do t&rsquo;i takoj&euml; e ardhmja e lavdishme. &Euml;sht&euml; e ditur se zullumqar&euml;t nuk gjejn&euml; shp&euml;tim.';
$t[136] = 'Dhe nga aj q&euml; krijoi Si prej t&euml; lashtave dhe prej kafsh&euml;ve sht&euml;pake, ata (idhujtar&euml;t) ndan&euml; nj&euml; pjes&euml; p&euml;r All-llahun dhe than&euml;: &ldquo;Kjo &euml;sht&euml; p&euml;r All-llahun, si mendonin ata, e kjo &euml;sht&euml; e idhujve tan&euml;&rdquo;. Ajo q&euml; ishte p&euml;r idhujt, nuk shkon te All-llahu, e ajo q&euml; &euml;sht&euml; p&euml;r All-llahun, shkon te idhujt e tyre. sa gjykim i sh&euml;mtuar &euml;sht&euml; ai q&euml; gjykojn&euml; (pjes&euml;n e idhujve e plot&euml;sonin nga ajo e All-llahut, n&euml; rast nevoje, e t&euml; All-llahut jo).';
$t[137] = 'K&euml;shtu shumic&euml;s s&euml; idhujtar&euml;ve, idhujt ua hijeshuan atyre mbyten e f&euml;mij&euml;ve t&euml; vet, p&euml;r t&rsquo;i shkat&euml;rruar (me mashtrime) dhe p&euml;r &lsquo;ua ngat&euml;rruar fen&euml; (q&euml; e kishin pasur t&euml; Ismailit). Po sikur t&euml; donte All-llahu, ata nuk do t&euml; b&euml;nin at&euml;, andaj hiqu tyre dhe asaj q&euml; shpifin.';
$t[138] = 'Dhe sipas bindjes s&euml; tyre ata thoshin: &ldquo;K&euml;to kafsh&euml; dhe k&euml;to bim&euml; jan&euml; t&euml; ndaluara, nuk mund t&euml; ushqehet me to, p&euml;rve&ccedil; ata, t&euml; cil&euml;vene ua lejom&euml;; k&euml;to jan&euml; kafsh&euml; q&euml; &euml;sht&euml; ndaluar t&rsquo;u hipet; k&euml;to jan&euml; kafsh&euml; jat&euml; therrjes s&euml; tyre nuk p&euml;rmendin emrin e All-llahut, duke shpifur ndaj Tij. Ai do t&rsquo;i nd&euml;shkoj&euml; ata p&euml;r shpifjet e tyre.';
$t[139] = 'Madje ata thonin: &ldquo;&ccedil;ka &euml;sht&euml; n&euml; barqet e k&euml;tyre kafsh&euml;ve &euml;sht&euml; vet&euml;m p&euml;r mashkujt tan&euml;, e ndaluar p&euml;r grat&euml; tona. E n&euml;se ishte e ngordht&euml; (fruti n&euml; bark) ata (mashkuj e femra) ishin t&euml; barabart&euml; n&euml; t&euml;. Do t&euml; nd&euml;shkoj&euml; Ai cil&euml;simin &euml; tyre t&euml; rrejsh&euml;m. Ai &euml;sht&euml; i p&euml;rsosur n&euml; pun&euml;t e Tij, i dijsh&euml;m p&euml;r krijesat e Tij.';
$t[140] = 'S&rsquo;ka dyshim se kan&euml; d&euml;shtuar keq ata q&euml; mbyt&euml;n f&euml;mij&euml;t e tyre nga mendjeleht&euml;sia e pa kurrfar&euml; dije dhe ata q&euml; duke i shpifur Zotit, shpall&euml;n t&euml; ndaluar at&euml; q&euml; Zoti u kishte dhuruar. Ata kan&euml; humbur rrug&euml;n e drejt&euml; dhe prej fillimit nuk ishin n&euml; udh&euml;zim.';
$t[141] = 'Ai (All-llahu) &euml;sht&euml; q&euml; krijoi kopshtie (bim&euml;t e t&euml; cilave) t&euml; ngritura lart (n&euml; shtylla) dhe t&euml; rrafshta (t&euml; shtrira n&euml; tok&euml;), edhe hurmet dhe drith&euml;rat me frute (shije) t&euml; ndryshme; (krijoi) ullinjt&euml; dhe sheg&euml;t e ngjashme (nga shija). Hani frutat e tyre kur t&euml; piqen dhe dit&euml;n e korrjes (t&euml; vjeljes) s&euml; frutave jepne at&euml; pjes&euml; q&euml; &euml;sht&euml; obligim (t&euml; varf&euml;rve e nevojlive) dhe (hani-jepni) mos teproni, pse Ai nuk i do shkap&euml;rderdh&euml;sit.';
$t[142] = 'Nga kafsh&euml;t (sht&euml;piake krijoi) edhe aso p&euml;r ngarkim (p&euml;r hipje) dhe aso, prodhimi i t&euml; cilave p&euml;rdoret p&euml;r shtroj&euml; (leshi i tyre ose p&euml;r therrje. Haninga ajo q&euml; All-llahu ju dhuroi, e mos ndiqni gjurm&euml;t (rrug&euml;t) e shejtanit, pse ai &euml;sht&euml; armik juaj i hap&euml;t.';
$t[143] = 'Ai (krijoi) tet&euml; lloje (nga kafsh&euml;t sht&euml;piake): prej deleve dy (dash e dele), prej dhive dy (cjap e dhi). Thuaj: &ldquo;A jan&euml; t&euml; ndaluar (haram) dy meshkuj apo dy femra apo &ccedil;ka mban&euml; (pjell) mitra e dy (llojeve t&euml; tyre) femrave?&rdquo; M&euml; tregoni, pra, me fakte t&euml; ditura (e jo me trillime) n&euml;se jeni t&euml; sinqert&euml;.';
$t[144] = 'Ai (krijoi) edhe nga devet dy (lloje) dhe nga lop&euml;t dy (lloje). Thuaj: &ldquo;A dy meshkuj i ka ndaluar (haram), a dy femrat, pse &ccedil;ka mban mitra e dy (llojeve t&euml; tyre) femrave?&rdquo; A mos ishit t&euml; pranish&euml;m kur All-llahu ju porositi me k&euml;t&euml; (hallall apo haram)?&rdquo; E kush &euml;sht&euml; m&euml; mizor se ai q&euml; trillon rren&euml; p&euml;r All-llahun dhe ashtu t&rsquo;i humb&euml; njer&euml;zit n&euml; munges&euml; t&euml; dijes. All-llahu nuk v&euml; n&euml; rrug&euml; t&euml; drejt&euml; mizor&euml;t.';
$t[145] = 'Thuaj: &ldquo;N&euml; at&euml; q&euml; m&euml; &euml;sht&euml; shpallur mua (n&euml; Kur&rsquo;an) nuk po gjej di&ccedil;ka t&euml; ndaluar nga ushqimi, p&euml;rve&ccedil; n&euml; qoft&euml; se ai (ushqimi) &euml;sht&euml;: coftin&euml;, gjak i derdhur ose mish derri, ai i ndyt&euml;, dhe pos asaj q&euml; &euml;sht&euml; therrur jo n&euml; em&euml;r t&euml; All-llahut (por t&euml; ndonj&euml; idhulli) e q&euml; &euml;sht&euml; m&euml;kat. E kush detyrohet (t&rsquo;i haj&euml; k&euml;to t&euml; ndaluara), por duke mos pasur p&euml;r q&euml;llim shijen dhe duke mos e tepruar; Zoti yt &euml;sht&euml; q&euml; fal&euml; e m&euml;shiron shum&euml;.';
$t[146] = 'E (posaq&euml;risht) ndaj atyre q&euml;jan&euml; jehudi Ne kemi ndaluar &ccedil;do (kafsh&euml;) thundrake: nga lop&euml;t dhe delet u kemi ndaluar dhjmin e tyre, pos atij (dhjami) n&euml; shpin&euml;n dhe n&euml; zorr&euml;t e tyre dhe pos atij t&euml; p&euml;rzier me ndonj&euml; asht. K&euml;t&euml; (mas&euml;-ndales&euml;) e mor&euml;m si nd&euml;shkim ndaj m&euml;katit t&euml; tyre. S&rsquo;ka dyshim, Ne jemi t&euml; v&euml;rtet&euml; (n&euml; &ccedil;ka ju rr&euml;fejm&euml;).';
$t[147] = 'PO, n&euml; qoft&euml; se (p&euml;r k&euml;t&euml; jehudit&euml;) t&euml; p&euml;rg&euml;njeshtrojn&euml; Thuaj: &ldquo;Sam&euml;shirues i madh &euml;sht&euml; ky Zoti juaj, po (mos u mashtroni) d&euml;nimi i Tij p&euml;r njer&euml;zit kriminel&euml;nuk mund t&euml; prap&euml;sohet!&rdquo;';
$t[148] = 'E ata q&euml; i shoq&euml;ruan Zotit, do t&euml; thoshin: &ldquo;Sikur t&euml; donte All-llahu nuk do t&rsquo;i b&euml;im shok (nuk do t&euml; ishim idhujtar&euml;), as ne, as prind&euml;rit tan&euml;, e as nuk do t&euml; ndalonim asnj&euml; send.&rdquo; K&euml;shtu pat&euml;ng&euml;njyer edhe ata q&euml; ishin para tyre derisa (p&euml;r shkak t&euml; m&euml;katit) p&euml;rjetuan d&euml;nimin ton&euml; t&euml; ashp&euml;r. Thuaj: &ldquo;A mos keni ndonj&euml; fakt e t&euml; na prezentoni at&euml; neve?&rdquo; Ju i mb&euml;shteteni vet&euml;m hamendjes, n&euml; t&euml; v&euml;rtet&euml;, vet&euml;m g&euml;njeni.';
$t[149] = 'Thuaj: &ldquo;All-llahu ka argumentin m&euml; t&euml; plot&euml; dhe sikur t&euml; donte Ai do t&rsquo;ju vinte n&euml; rrug&euml; t&euml; drejt&euml; t&euml; gjith&euml;ve.';
$t[150] = 'Thuaj: &ldquo;Sillni d&euml;shmitar&euml;t, t&euml; cil&euml;t d&euml;shmojn&euml; se All-llahu ndaloi (b&euml;ri haram) ato (q&euml; i ndalonin vet&euml;). N&euml;seda t&euml; d&euml;shmojn&euml; rrejsh&euml;m, ti mos d&euml;shmo bashk&euml; me ta dhe mos shko pas d&euml;shirave t&euml; atyre q&euml; argmentet Tona i b&euml;n&euml; t&euml; rremedhe pas atyre q&euml; nuk besojn&euml; jet&euml;n e q&euml; largohen prej Zotit t&euml; tyre (adhurojn&euml; tjet&euml;r).';
$t[151] = 'Thuaj: &ldquo;Ejani t&rsquo;ju lexoj&euml; at&euml; q&euml; me t&euml; v&euml;rtet&euml; ju ndaloi Zoti juaj: t&euml; mos i shoq&euml;roni Atij asnj&euml; send, t&euml; silleni mir&euml; me prind&euml;rit, t&euml; mos i mbytni f&euml;mij&euml;t tuaj p&euml;r shkak t&euml; varf&euml;ris&euml;, sepse Ne u ushqejm&euml; juve dhe ata, t&euml; mos u afroheni m&euml;kateve t&euml; hapta apo t&euml; fshehta, mos e mbtni njeriun sepse mbytjen e tij e ndaloi All-llahu, p&euml;rpos kur &euml;sht&euml; me vend. K&euml;to jan&euml; porosit&euml; e Tij, k&euml;shtu t&euml; mendoni thell&euml;.';
$t[152] = 'Mos iu afronu pasuris&euml; s&euml; jetimit derisa ai t&euml; arrij&euml; pjekurin&euml;, (mund t&rsquo;i afroheni) vet&euml;m n&euml; m&euml;nyr&euml; m&euml; t&euml; mir&euml;, zbatoni me drejt&euml;si mas&euml;n dhe peshoj&euml;n, Ne nuk ngarkojm&euml; asnj&euml; njeri p&euml;rtej mund&euml;sive t&euml; tij. Kur t&euml; flitni (t&euml; d&euml;shmoni), duhet t&euml; jeni t&euml; drejt&euml; edhe &euml;sht&euml; &ccedil;&euml;shtja p&euml;r (kund&euml;r) t&euml; af&euml;rmit, dhe zotimin e dh&euml;n&euml; All-llahut plot&euml;sonie. K&euml;to jan&euml; me &ccedil;ka Ai ju porosit&euml; k&euml;shtu q&euml; t&euml; p&euml;rkujtoni.';
$t[153] = 'Dhe se kjo&euml;sht&euml; rruga (feja) Ime e drejt&euml; (q&euml; e caktova p&euml;r ju), pra p&euml;rmbajuni k&euml;saj, e mos ndiqni rrug&euml; t&euml; tjerae t&rsquo;ju ndajn&euml; nga rruga e Tij. K&euml;to jan&euml; porosit&euml; e Tij p&euml;r ju, ashtu q&euml; &euml; ruheni.';
$t[154] = 'E Musait i dham&euml; librin plot&euml;sim (i t&euml; mirave) p&euml;r at&euml; q&euml; tregon bindje t&euml; mir&euml;, dhe sqarues p&euml;r &ccedil;do send, udherr&euml;fyes e m&euml;shir&euml;, ashtu q&euml; t&euml; besojn&euml; se do t&euml; takohen me Zotin e tyre.';
$t[155] = 'Dhe ky lib&euml;r, dobi prur&euml;s, Ne e zbrit&euml;m, p&euml;rmbanju k&euml;tij, ruajuni shtu q&euml; t&euml; m&euml;shiroheni.';
$t[156] = '(E zbrit&euml;m Kur&rsquo;anin) Q&euml; t&euml; (mos) thoni: &ldquo;Libri u zbrit vet&euml;n dy grupeve para nesh (jehudive e t&euml; krishter&euml;ve) dhe se ne ishim t&euml; panjohur me librat (me m&euml;simet) e tyre.';
$t[157] = 'Dhe q&euml; t&euml; (mos) thoni: &ldquo;Sikur t&euml; na kishte zbritur neve libri, (sikurse u zbriti atyre dy grupeve) ne do t&euml; ishim edhe n&euml; t&euml; udh&euml;zuar se ata. Juve ju erdhi nga Zoti juaj argumenti (Kur&rsquo;ani), ju erdhi udh&euml;zimi, m&euml;shira. E kush &euml;sht&euml; m&euml; zullumqar se ai q&euml; argumentet eZOtit i b&euml;n t&euml; rreme dhe ua k&euml;then shpin&euml;n atyre? Ne do t&rsquo;i nd&euml;shkojm&euml; me nd&euml;shkim m&euml; t&euml; r&euml;nd&euml; ata q&euml; ua kthyen shpin&euml;n argumenteve Tona, p&euml;r shkak t&euml; prap&euml;simit q&euml; b&euml;n&euml;.';
$t[158] = 'A mos jan&euml; duke pritur tjet&euml;r ata (pas k&euml;tyre fakteve) vet&euml;m t&rsquo;ju vj&euml; engj&euml;jt (t&rsquo;ua marrin shpirtin), t&euml; vij&euml; Zoti yt (urdh&euml;ri p&euml;r shkat&euml;rrim) ose t&euml; vijn&euml; disa nga shenjat e Zotit t&euml;nd, (n&euml;) dit&euml;n kur vijn&euml; disa shenja t&euml; Zotit t&euml;nd, asnj&euml; njeriu nuk ivlen besimi i Tij n&euml;se nuk ka besuar m&euml; par&euml; ose nuk ka b&euml;r&euml; n&euml; besimin e tij kurfar&euml; t&euml; mire. Thuaj: &ldquo;Pritni, edhe ne jemi duke pritur!&rdquo;';
$t[159] = 'V&euml;rtet at q&euml; e p&euml;rqan&euml; fen&euml; e tyre dhe u ndan&euml; n&euml; grupe, ti (Muhammed) nuk ke kurfar&euml; p&euml;rgjegj&euml;sie. &ccedil;&euml;shtja e tyre &euml;sht&euml; vet&euml;m te All-llahu, Ai do t&rsquo;i njoh&euml; me at&euml; q&euml; punuan.';
$t[160] = 'Kush vjen me nj&euml; (pun&euml;) t&euml; mir&euml;, ai (n&euml; dit&euml;n e gjykimit) shp&euml;rblehet dhjet&euml; fish, e kush vjen me (vep&euml;r) t&euml; keqe, ai nd&euml;shkohet vet&euml;m p&euml;r t&euml;. Atyre nuk u b&euml;het e padrejt&euml;.';
$t[161] = 'Thuaj: &ldquo;V&euml;rtet Zoti im m&euml; udh&euml;zoi n&euml; rrug&euml; t&euml; drejt&euml;, q&euml; &euml;sht&euml; fe e drejt&euml;, fe e Ibrahimit, q&euml; ishte larg besimeve t&euml; kota. Ai (Ibrahimi) nuk ishte idhujtar!';
$t[162] = 'Thuaj: &ldquo;Namazi im, kurbani im dhe vdekja ime jan&euml; thjesht p&euml;r All-llahun, Zotin e bot&euml;ve.';
$t[163] = 'Ai nuk ka shok (nuk adhuroj tjet&euml;r). Me k&euml;t&euml; (thjesht&euml;si t&euml; adhurimit vet&euml;m p&euml;r Zotin) jam i urdh&euml;ruar dhe jam i pari i musliman&euml;ve (i pari q&euml; pranoj dhe bindem)!';
$t[164] = 'Thuaj: &ldquo;A t&euml; k&euml;rkoj&euml; Zot pos All-llahut, e Ai &euml;sht&euml; Zot i &ccedil;do sendi (ekzistues) dhe d&euml;mi i secilit person &euml;sht&euml; kund&euml;r vetes. Askush nuk do t&euml; bart&euml; barr&euml;n e tjetrit. Mandej, kthimi juaj &euml;sht&euml; te All-llahu; e Ai ju njohton p&euml;r at&euml; q&euml; p&euml;r&ccedil;aheshit.';
$t[165] = 'Ai &euml;sht&euml; q&euml; ju b&euml;ri sundues (z&euml;vend&euml;sues) n&euml; tok&euml; (pas shkat&euml;rrimit t&euml; atyre q&euml; ishin m&euml; par&euml;) dhe lart&euml;soi n&euml; nj&euml; shkall&euml; m&euml; t&euml; lart&euml;disa nga ju mbi t&euml; tjer&euml;t, p&euml;r t&rsquo;ju sprovuar n&euml; at&euml; q&euml; u dha. All-llahu &euml;sht&euml; nd&euml;shkues i shpejt&euml;, &euml;sht&euml; q&euml; fal&euml; e M&euml;shirues.';
?>