$t[3] = 'Dhe se ti, padyshim do t&euml; kesh shp&euml;rblim t&euml; pand&euml;rprer&euml;!';
$t[4] = 'V&euml;rtet, ti je n&euml; nj&euml; shkall&euml; t&euml; lart&euml; morali!';
$t[5] = 'M&euml; von&euml; ti do t&euml; shoh&euml;sh, edhe ata do t&euml; shohin.';
$t[6] = 'Se cili prej jush &euml;sht&euml; i &ccedil;mendur?';
$t[7] = 'S&rsquo;ka dyshim se Zoti yt &euml;sht&euml; Ai q&euml; e di m&euml; s&euml; miri se kush &euml;sht&euml; ai q&euml; ka humbur prej rrug&euml;s s&euml; Tij dhe Ai e di m&euml; s&euml; miri p&euml;r t&euml; udh&euml;zuarit.';
$t[8] = 'Andaj, ti mos u pajto me g&euml;njeshtar&euml;t!';
$t[9] = 'Ata kan&euml; d&euml;shir&euml; q&euml; ti t&euml; b&euml;sh lajka.';
$t[10] = 'Mos e respekto ask&euml; q&euml; betohet shum&euml; dhe &euml;sht&euml; i posht&euml;r!';
$t[11] = 'q&euml; &euml;sht&euml; p&euml;rgojues e ban fjal&euml; nd&euml;r njer&euml;z.';
$t[12] = 'Shum&euml; koprrac ndaj rrug&euml;s s&euml; mbar&euml;, i pafrenuesh&euml;m, m&euml;katar&euml;,';
$t[13] = 'q&euml; &euml;sht&euml; shum&euml; i vrazhd&euml;, m&euml; n&euml; fund edhe kopil (nuk dihet baba).';
$t[14] = '(Mos e respekto) Vet&euml;m pse ka pasuri e djem!';
$t[15] = 'Kur atij i lexohen ajetet Tona, ai thot&euml;: &ldquo;Legjenda t&euml; t&euml; par&euml;ve!&rdquo;';
$t[16] = 'Ne do ti d&euml;mkosim at&euml; n&euml; turi (n&euml; hund&euml;).';
$t[17] = 'Ne i sprovuam ata (mekasit) sikurse i kemi pas sprovuar pronar&euml;t e kopshtit. Kur u betuan se do t&euml; vjelin at&euml; her&euml;t n&euml; agim.';
$t[18] = 'E nuk than&euml; (kur u betuan): &ldquo;N&euml; dasht&euml; All-llahu - inshaell -llahu!&rdquo;';
$t[19] = 'E, sa qen&euml; ata fjetur, at&euml; (kopshtin) e goditi nj&euml; bela nga Zoti yt.';
$t[20] = 'Dhe ai gdhiu si t&euml; ishte i vjel&euml;.';
$t[21] = 'Ata n&euml; m&euml;ngjes th&euml;rritnin nj&euml;ri-tjetrin.';
$t[22] = 'Ngrihuni her&euml;t te prodhimet e tuaja, n&euml;se doni t&rsquo;i vilni!';
$t[23] = 'Dhe ata vajt&euml;nduke p&euml;shp&euml;ritur n&euml; mes vete (t&euml; mos i d&euml;gjojn&euml; kush).';
$t[24] = 'T&euml; mos lejojn&euml; t&euml; afrohet sot n&euml; t&euml; asnj&euml; i varf&euml;r.';
$t[25] = 'T&euml; mos lejojn&euml; t&euml; afrohet sot n&euml; t&euml; asnj&euml; i varf&euml;r.';
$t[26] = 'E kur e pan&euml; at&euml;, than&euml;: &ldquo;S&rsquo;ka dyshim, ne kemi humbur (rrug&euml;n)&rdquo;.';
$t[27] = 'Jo, (ne nuk kemi humbur rrug&euml;n) por pakemi mbetur pa t&euml;!';
$t[28] = 'Ai me maturi prej tyre tha: &ldquo;A nuk ju thah&euml; p&euml;rse nuk e p&euml;rmendi Zotin?&rdquo;';
$t[29] = 'At&euml;her&euml; ata than&euml;: &ldquo;I pafajsh&euml;m &euml;sht&euml; Zoti yn&euml;, ne ishim t&euml; padrejt&euml;&rdquo;';
$t[30] = 'Dhe u kthyen e po e qortoj&euml;n nj&euml;ri-tjetrin.';
$t[31] = 'Than&euml;: &ldquo;T&euml; mjer&euml;t ne, v&euml;rtet ne ishim jasht&euml; rrug&euml;s!&rdquo;';
$t[32] = 'Shpresohet se Zoti yt do na z&euml;vendsoj&euml; me ndonj&euml; edhe m&euml; t&euml; mir&euml; se ai; ne n&euml; t&euml; vet&euml;m te Zoti yn&euml; mbajm&euml; shpres&euml;n.';
$t[33] = 'Ja, k&euml;shtu do t&euml; jen&euml; d&euml;nimi (i mekasve), nd&euml;rsa d&euml;nimi i bot&euml;s tjet&euml;r do t&euml; jet&euml; shum&euml; m&euml; i ashp&euml;r, sikur t&euml; jen&euml; q&euml; e kuptojn&euml;.';
$t[34] = '&Euml;sht&euml; e sigurt se p&euml;r t&euml; devotshmit do t&euml; ket&euml; te Zoti i tyre kopshte t&euml; begatshme.';
$t[35] = 'A do ti b&euml;jm&euml; krminel&euml;t t&euml; barabart&euml; me myliman&euml;t?';
$t[36] = '&ccedil;&rsquo;&euml;sht&euml; me ju, si gjykoni ashtu?';
$t[37] = 'A mos keni ndonj&euml; lib&euml;r, e prej saj m&euml;soni?';
$t[38] = 'E ju do t&euml; keni atje &ccedil;ka t&euml; d&euml;shironi?';
$t[39] = 'Amos keni ndonj&euml; zotim t&euml; fort&euml; prej Nesh q&euml; vazhdon deri n&euml; kijamet, se do ta rrini at&euml; q&euml; d&euml;shironi?';
$t[40] = 'Pyeti ata se cili &euml;sht&euml; prej tyre q&euml; u garanton at&euml;?';
$t[41] = 'Apo ata kan&euml; ortak&euml; (zota) q&euml; u garantojn&euml;, pra le t&rsquo;i sjellin ortak&euml;t e vet, n&euml;se e thon&euml; t&euml; v&euml;tet&euml;n?';
$t[42] = '(P&euml;rkujto) Dit&euml;n kur ashp&euml;rsohet &ccedil;shtja deri n&euml; kulm (n&euml; kijamet), e ata do t&euml; ftohen p&euml;r t&euml; b&euml;r&euml; sexhde, po nuk mund&euml;n.';
$t[43] = 'Shikimet e tyre jan&euml; t&euml; p&euml;rulura dhe ata i kaplon posht&euml;rimi, kur dihet se ata kan&euml; qen&euml; t&euml; ftuar t&euml; b&euml;jn&euml; sexhde sa ishin t&euml; sh&euml;ndosh&euml; (e ata - talleshin).';
$t[44] = 'Pra m&euml; l&euml; Mua dhe ata q&euml; e mohojn&euml; k&euml;t&euml; Kur&rsquo;an. Ne do t&rsquo;i afrojm&euml; ata dal&euml;ngadal&euml; te d&euml;nimi prej nga nuk presin.';
$t[45] = 'Dhe atyre Un&euml; u jap afat, sepse nd&euml;shkimi Im &euml;st&euml; shum&euml; i fort&euml;.';
$t[46] = 'A mos k&euml;rkon ti prej tyre shp&euml;rblim, e ata jan&euml; t&euml; ngarkuar me dh&euml;niet?';
$t[47] = 'A mos te ta &euml;sht&euml; e fshehta (e Lehvi Mahfudhit), e ata prej atu trumbetojn&euml; (se jan&euml; t&euml; mir&euml;)?';
$t[48] = 'Po ti, b&euml;hu i duruesh&euml;m ndaj caktimit t&euml; Zotit t&euml;nd, e mos u b&euml; si ai peshkut n&euml; koh&euml;n kur pat thirrur (Zotin) dhe ishte i mllefosur.';
$t[49] = 'Sikur t&euml; mos i afrohej atij m&euml;shir&euml; nga Zoti i tij, do t&euml; hidhej n&euml; shkret&euml;rir&euml; i demoralizuar.';
$t[50] = 'Po Zoti i tij, e b&euml;ri at&euml; t&euml; zgjedhur prej t&euml; mir&euml;ve.';
$t[51] = 'E ata q&euml; nuk besuan gati sa t&euml; zhdukin me shikimet e tyre, kur d&euml;gjuan Kur&rsquo;anin, e (nga inati) than&euml;: &ldquo;Ai &euml;sht&euml; i marr&euml;!&rdquo;';
$t[52] = 'Po ai (Kur&rsquo;ani) nuk &euml;sht&euml; tjet&euml;r vet&euml;m se k&euml;shill&euml; p&euml;r bot&euml;n (e njer&euml;zve e t&euml; xhin&euml;ve).';
