$t[56] = 'FAST&Auml;N VI l&auml;t [Farao] se alla V&aring;ra tecken, f&ouml;rkastade han dem och v&auml;grade att tro [p&aring; V&aring;ra budskap].';
$t[57] = 'Han sade: &quot;Har du, Moses, kommit hit f&ouml;r att driva bort oss fr&aring;n v&aring;rt land med dina trollkonster';
$t[58] = 'Vi skall sannerligen visa dig lika goda trollkonster som dina. N&auml;mn d&auml;rf&ouml;r tid f&ouml;r ett m&ouml;te fr&aring;n vilket vi inte skall utebli och du inte heller f&aring;r [utebli] och en [f&ouml;r oss alla] l&auml;mplig plats.&quot;';
$t[59] = '[Moses] svarade: &quot;Det m&ouml;te som du [vill kalla till] skall &auml;ga rum p&aring; er stora h&ouml;gtidsdag och l&aring;t folket samlas redan p&aring; f&ouml;rmiddagen.&quot;';
$t[60] = 'Farao drog sig tillbaka [med sina r&aring;dgivare] f&ouml;r att planera hur de skulle g&aring; till v&auml;ga; och [n&auml;r tiden f&ouml;r m&ouml;tet kom] infann han sig.';
$t[61] = 'Moses sade till dem: &quot;Den av er som s&auml;tter ihop l&ouml;gner om Gud drar olycka &ouml;ver sig; ja, Han skall f&ouml;rinta er med [Sitt] straff! Den som s&auml;tter ihop l&ouml;gner om Gud &auml;r d&ouml;md att misslyckas.&quot;';
$t[62] = 'De &ouml;verlade i avskildhet om vad som var att g&ouml;ra';
